update verses set fr="Et assurément ton Seigneur – Il est le Puissant, le Miséricordieux." where chapterNumber=26 and ayatNumber=160;
update verses set fr="Le peuple de Lot rejeta les Messagers," where chapterNumber=26 and ayatNumber=161;
update verses set fr="Quand leur frère Lot leur dit : « Ne voulez-vous pas devenir des gens droits ?" where chapterNumber=26 and ayatNumber=162;
