update verses set fr="Et dans les dernières heures de la nuit, ils imploraient le pardon \;" where chapterNumber=51 and ayatNumber=19;
update verses set fr="Et dans leurs biens, il y avait une part pour le mendiant et pour l’indigent qui ne mendie pas." where chapterNumber=51 and ayatNumber=20;
