update verses set fr="Et de chaque chose Nous avons créé deux éléments formant une paire, afin que vous réfléchissiez." where chapterNumber=51 and ayatNumber=50;
update verses set fr="Courez donc vers Allāh. Assurément, je suis pour vous, de Sa part, un Avertisseur qui avertit clairement." where chapterNumber=51 and ayatNumber=51;
update verses set fr="Et n’associez à Allāh aucun autre dieu \; je suis pour vous, de Sa part, un Avertisseur qui avertit clairement." where chapterNumber=51 and ayatNumber=52;
