update verses set fr="Et Je n’ai créé les djinns et les hommes que pour qu’ils M’adorent." where chapterNumber=51 and ayatNumber=57;
update verses set fr="Je ne désire aucune subsistance d’eux, et Je ne désire pas non plus qu’ils Me nourrissent." where chapterNumber=51 and ayatNumber=58;
