update verses set fr="Sur fin parchemin déployé \;" where chapterNumber=52 and ayatNumber=4;
update verses set fr="Et <em>par</em> la Maison toujours fréquentée \;" where chapterNumber=52 and ayatNumber=5;
update verses set fr="Et <em>par</em> la Voûte élevée \;" where chapterNumber=52 and ayatNumber=6;
update verses set fr="Et <em>par</em> l’océan gonflé \;" where chapterNumber=52 and ayatNumber=7;
