update verses set fr="Se hâtant vers le Convocateur. Les mécréants diront : « Ceci est un jour pénible. »" where chapterNumber=54 and ayatNumber=9;
update verses set fr="Le peuple de Noé rejeta <em>la vérité</em> avant eux \; ainsi ils traitèrent Notre serviteur de menteur et dirent : « Un insensé et un rejeté ! »" where chapterNumber=54 and ayatNumber=10;
update verses set fr="Alors, il pria son Seigneur <em>ainsi</em> : « Je suis accablé, accorde-<em>moi Ton</em> aide ! »" where chapterNumber=54 and ayatNumber=11;
