update verses set fr="Et Nous le transportâmes sur ce <em>qui avait été</em> fait de planches et de clous." where chapterNumber=54 and ayatNumber=14;
update verses set fr="Qui vogua sous Nos yeux \; une récompense pour celui qui avait été rejeté." where chapterNumber=54 and ayatNumber=15;
