update verses set fr="Et ils dirent : « Comment ! Un homme, d’entre nous-mêmes, un homme <em>tout</em> seul, devons-nous le suivre ? Alors, en vérité, nous serions dans l’erreur et dans la folie." where chapterNumber=54 and ayatNumber=25;
