update verses set fr="<em>Ils seront</em> accoudés sur des coussins verts <em>allongés</em> sur de beaux tapis." where chapterNumber=55 and ayatNumber=77;
update verses set fr="Lequel donc, des bienfaits de votre Seigneur nierez-vous, vous deux ?" where chapterNumber=55 and ayatNumber=78;
update verses set fr="Béni est le nom de ton Seigneur, le Maître de la Gloire et de l’Honneur." where chapterNumber=55 and ayatNumber=79;
update verses set fr="Au nom d’Allāh, le Gracieux. le Miséricordieux." where chapterNumber=56 and ayatNumber=1;
update verses set fr="Quand l’Evénement arrivera –" where chapterNumber=56 and ayatNumber=2;
update verses set fr="On ne peut dénier son avènement –" where chapterNumber=56 and ayatNumber=3;
update verses set fr="Il abaissera <em>certains</em>, et élèvera <em>d’autres</em>." where chapterNumber=56 and ayatNumber=4;
update verses set fr="Quand la terre sera secouée d’une secousse violente," where chapterNumber=56 and ayatNumber=5;
update verses set fr="Et que les montagnes seront totalement pulvérisées," where chapterNumber=56 and ayatNumber=6;
update verses set fr="Alors, elles deviendront <em>toutes</em> comme de la poussière éparpillée," where chapterNumber=56 and ayatNumber=7;
update verses set fr="Et vous serez <em>divisés en</em> trois groupes :" where chapterNumber=56 and ayatNumber=8;
update verses set fr="<em>Premièrement</em>, ceux du côté droit – que sont ceux du côté droit ? –" where chapterNumber=56 and ayatNumber=9;
update verses set fr="<em>Deuxièmement</em>, ceux du côté gauche – que sont ceux du côté gauche ? –" where chapterNumber=56 and ayatNumber=10;
update verses set fr="<em>Troisièmement</em>, les plus en avant \; ils sont les plus en avant \;" where chapterNumber=56 and ayatNumber=11;
update verses set fr="Ceux-là seront les plus rapprochés <em>d'Allāh</em>." where chapterNumber=56 and ayatNumber=12;
update verses set fr="<em>Ils seront</em> dans les Jardins de la Félicité –" where chapterNumber=56 and ayatNumber=13;
update verses set fr="Un grand groupe provenant du peuple précédent," where chapterNumber=56 and ayatNumber=14;
update verses set fr="Et un petit nombre provenant du peuple des Derniers Temps," where chapterNumber=56 and ayatNumber=15;
update verses set fr="Sur des canapés incrustés <em>d'or et de pierres précieuses</em>," where chapterNumber=56 and ayatNumber=16;
update verses set fr="<em>Ils seront allongés</em> là-dessus, accoudés <em>sur des coussins</em>, se faisant face." where chapterNumber=56 and ayatNumber=17;
update verses set fr="Des adolescents qui ne vieilliront pas circuleront <em>parmi eux</em> pour les servir," where chapterNumber=56 and ayatNumber=18;
update verses set fr="Portant des gobelets, des aiguières et des coupes <em>remplis</em> à une source ruisselante –" where chapterNumber=56 and ayatNumber=19;
update verses set fr="Ils n’en auront pas mal à la tête et n’<em>en</em> seront point enivrés –" where chapterNumber=56 and ayatNumber=20;
update verses set fr="Et <em>portant</em> des fruits de leur choix –" where chapterNumber=56 and ayatNumber=21;
update verses set fr="Et de la chair d’oiseaux comme ils le désireront." where chapterNumber=56 and ayatNumber=22;
update verses set fr="Et <em>il s’y trouvera</em> de belles jeunes filles aux yeux grands et beaux," where chapterNumber=56 and ayatNumber=23;
update verses set fr="Pareilles à des perles bien cachées <em>et protégées</em>," where chapterNumber=56 and ayatNumber=24;
update verses set fr="Comme récompense pour ce qu’ils auront fait." where chapterNumber=56 and ayatNumber=25;
update verses set fr="Ils n’y entendront aucune parole futile ou coupable," where chapterNumber=56 and ayatNumber=26;
update verses set fr="Sauf la parole <em>de salutation</em> : « Paix ! Paix ! »" where chapterNumber=56 and ayatNumber=27;
update verses set fr="Et ceux du côté droit – que sont ceux du côté droit ? –" where chapterNumber=56 and ayatNumber=28;
update verses set fr="<em>Ils seront</em> au milieu des jujubiers sans épines," where chapterNumber=56 and ayatNumber=29;
update verses set fr="Et des régimes de bananes," where chapterNumber=56 and ayatNumber=30;
update verses set fr="Et de l’ombre étendue," where chapterNumber=56 and ayatNumber=31;
update verses set fr="Et de l’eau courante," where chapterNumber=56 and ayatNumber=32;
update verses set fr="Et des fruits abondants," where chapterNumber=56 and ayatNumber=33;
update verses set fr="Qui ne manqueront pas, ni ne seront-ils interdits," where chapterNumber=56 and ayatNumber=34;
update verses set fr="Et <em>ils auront</em> des épouses nobles –" where chapterNumber=56 and ayatNumber=35;
update verses set fr="En vérité, Nous les avons créées d’une façon excellente," where chapterNumber=56 and ayatNumber=36;
update verses set fr="Et Nous les avons faites vierges," where chapterNumber=56 and ayatNumber=37;
update verses set fr="Affectueuses, du même âge," where chapterNumber=56 and ayatNumber=38;
update verses set fr="Pour ceux du côté droit \;" where chapterNumber=56 and ayatNumber=39;
update verses set fr="Un grand groupe de parmi le peuple précédent." where chapterNumber=56 and ayatNumber=40;
update verses set fr="Et un grand groupe de parmi le peuple des Derniers Temps." where chapterNumber=56 and ayatNumber=41;
update verses set fr="Mais <em>quant à</em> ceux du côté gauche – que sont ceux du côté gauche ?" where chapterNumber=56 and ayatNumber=42;
update verses set fr="<em>Ils seront</em> au milieu des vents brûlants et d’eau bouillante," where chapterNumber=56 and ayatNumber=43;
update verses set fr="Et sous l’ombre d’une fumée noire," where chapterNumber=56 and ayatNumber=44;
update verses set fr="Ni fraîche, ni bienfaisante." where chapterNumber=56 and ayatNumber=45;
update verses set fr="Avant ceci, ils menaient une vie d’aisance et d’abondance," where chapterNumber=56 and ayatNumber=46;
update verses set fr="Et s’obstinaient dans le péché extrême." where chapterNumber=56 and ayatNumber=47;
update verses set fr="Et ils avaient coutume de dire : « Comment ! Quand nous serons morts, et que nous serons devenus poussière et ossements, serons-nous vraiment ressuscités ?" where chapterNumber=56 and ayatNumber=48;
update verses set fr="Et nos ancêtres d’antan <em>aussi</em> ? »" where chapterNumber=56 and ayatNumber=49;
update verses set fr="Dis : « <em>Oui</em>, les premiers, et ceux qui sont venus plus tard," where chapterNumber=56 and ayatNumber=50;
update verses set fr="Seront <em>tous</em> rassemblés à l’heure fixée d’un jour désigné." where chapterNumber=56 and ayatNumber=51;
update verses set fr="Alors, ô vous qui êtes égarés et avez rejeté <em>la vérité</em>," where chapterNumber=56 and ayatNumber=52;
update verses set fr="Vous mangerez sûrement de l’arbre de Zaqqūm," where chapterNumber=56 and ayatNumber=53;
update verses set fr="Et en remplirez le ventre." where chapterNumber=56 and ayatNumber=54;
update verses set fr="Et boirez là-dessus de l’eau bouillante," where chapterNumber=56 and ayatNumber=55;
update verses set fr="Buvant comme le chameau perpétuellement assoiffé. »" where chapterNumber=56 and ayatNumber=56;
update verses set fr="Ceci sera leur accueil au Jour du Jugement." where chapterNumber=56 and ayatNumber=57;
update verses set fr="C’est Nous Qui vous avons créés. Pourquoi donc ne voulez-vous pas reconnaître <em>cela</em> ?" where chapterNumber=56 and ayatNumber=58;
update verses set fr="Que pensez-vous de ce que vous éjaculez ?" where chapterNumber=56 and ayatNumber=59;
update verses set fr="Est-ce vous qui l’avez créé, ou <em>en</em> sommes-Nous le Créateur ?" where chapterNumber=56 and ayatNumber=60;
update verses set fr="Nous avons décrété la mort pour vous tous \; et rien ne peut Nous empêcher" where chapterNumber=56 and ayatNumber=61;
update verses set fr="De changer votre forme <em>présente</em>, et <em>de</em> vous susciter sous une forme dont vous n’avez aucune idée." where chapterNumber=56 and ayatNumber=62;
update verses set fr="Et vous avez certainement connu la première création. Pourquoi donc ne méditez-vous pas <em>sur cela</em> ?" where chapterNumber=56 and ayatNumber=63;
update verses set fr="Voyez-vous ce que vous semez ?" where chapterNumber=56 and ayatNumber=64;
update verses set fr="Est-ce vous qui le faites pousser, ou est-ce Nous Qui <em>le</em> faisons pousser ?" where chapterNumber=56 and ayatNumber=65;
update verses set fr="Si Nous le voulions, Nous pourrions le réduire entièrement en menue paille, <em>et</em> alors vous ne cesseriez de vous lamenter :" where chapterNumber=56 and ayatNumber=66;
update verses set fr="« Nous sommes ruinés !" where chapterNumber=56 and ayatNumber=67;
update verses set fr="Et même <em>pire,</em> nous sommes <em>totalement</em> démunis ! »" where chapterNumber=56 and ayatNumber=68;
update verses set fr="Voyez-vous l’eau que vous buvez ?" where chapterNumber=56 and ayatNumber=69;
update verses set fr="Est-ce vous qui la faites descendre des nuages, ou est-ce Nous Qui <em>la</em> faisons descendre ?" where chapterNumber=56 and ayatNumber=70;
update verses set fr="Si Nous le voulions, Nous pourrions la rendre amère. Pourquoi donc n’êtes-vous pas reconnaissants ?" where chapterNumber=56 and ayatNumber=71;
update verses set fr="Voyez-vous le feu que vous allumez ?" where chapterNumber=56 and ayatNumber=72;
update verses set fr="Est-ce vous qui produisez l’arbre <em>pour ce feu</em>, ou est-ce Nous le Producteur ?" where chapterNumber=56 and ayatNumber=73;
update verses set fr="Nous en avons fait un rappel et un avantage pour les voyageurs." where chapterNumber=56 and ayatNumber=74;
update verses set fr="Alors glorifie le nom de ton Seigneur, le Grand." where chapterNumber=56 and ayatNumber=75;
update verses set fr="<em>Non</em>, mais je cite comme preuve <em>les galaxies</em> - les amarres des étoiles –" where chapterNumber=56 and ayatNumber=76;
update verses set fr="Et, en vérité, c’est là une très grande preuve, si seulement vous saviez –" where chapterNumber=56 and ayatNumber=77;
update verses set fr="Que ceci est en vérité un noble Coran," where chapterNumber=56 and ayatNumber=78;
update verses set fr="Dans un Livre bien protégé," where chapterNumber=56 and ayatNumber=79;
update verses set fr="Que nul ne touchera sauf ceux qui sont purifiés." where chapterNumber=56 and ayatNumber=80;
update verses set fr="<em>C’est</em> une révélation de la part du Seigneur des mondes." where chapterNumber=56 and ayatNumber=81;
update verses set fr="Aborderez-vous ce discours <em>divin</em> avec hypocrisie ?" where chapterNumber=56 and ayatNumber=82;
update verses set fr="Et faites-vous de son rejet votre <em>gagne</em>-pain ?" where chapterNumber=56 and ayatNumber=83;
update verses set fr="Pourquoi donc, lorsque <em>l’âme du mourant</em> arrive à<em> sa</em> gorge," where chapterNumber=56 and ayatNumber=84;
update verses set fr="Et qu’à ce moment-là vous regardez –" where chapterNumber=56 and ayatNumber=85;
update verses set fr="Et Nous sommes plus près de lui que vous, mais vous ne voyez pas –" where chapterNumber=56 and ayatNumber=86;
update verses set fr="Pourquoi alors, si vous n’avez pas de compte à rendre," where chapterNumber=56 and ayatNumber=87;
update verses set fr="Vous ne pouvez pas la ramener si vous êtes véridiques ?" where chapterNumber=56 and ayatNumber=88;
update verses set fr="Or si <em>le mourant</em> est de ceux qui sont les rapprochés <em>d</em>'<em>Allāh</em>," where chapterNumber=56 and ayatNumber=89;
update verses set fr="Alors, <em>pour lui</em> il y a du confort et du parfum <em>du bonheur</em>, et un Jardin de Félicité \;" where chapterNumber=56 and ayatNumber=90;
update verses set fr="Et s’il est de ceux du côté droit," where chapterNumber=56 and ayatNumber=91;
update verses set fr="Alors « Paix soit sur toi, qui es de ceux du côté droit. »" where chapterNumber=56 and ayatNumber=92;
update verses set fr="Mais s’il est de ceux qui rejettent <em>la vérité</em> et qui sont égarés," where chapterNumber=56 and ayatNumber=93;
update verses set fr="Alors <em>pour lui il y aura</em> un accueil d’eau bouillante," where chapterNumber=56 and ayatNumber=94;
update verses set fr="Et <em>il sera</em> brûlé dans la Fournaise <em>de l’Enfer</em>." where chapterNumber=56 and ayatNumber=95;
update verses set fr="Assurément, ceci est une vérité certaine." where chapterNumber=56 and ayatNumber=96;
update verses set fr="Aussi, glorifie le nom de ton Seigneur, le Grand." where chapterNumber=56 and ayatNumber=97;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=57 and ayatNumber=1;
update verses set fr="Tout ce qui est aux cieux et sur la terre glorifie Allāh \; et Il est le Puissant, le Sage." where chapterNumber=57 and ayatNumber=2;
update verses set fr="A Lui est le royaume des cieux et de la terre \; Il donne la vie et Il cause la mort \; et Il a le pouvoir de faire tout ce qu’Il veut." where chapterNumber=57 and ayatNumber=3;
update verses set fr="Il est le Premier et le Dernier, et le Manifeste et le Caché, et Il connaît toutes choses très bien." where chapterNumber=57 and ayatNumber=4;
update verses set fr="C’est Lui Qui a créé les cieux et la terre en six périodes, ensuite Il S’est établi sur le Trône. Il sait ce qui entre dans la terre, et ce qui en sort et ce qui descend du ciel, et ce qui y monte. Et Il est avec vous où que vous soyez. Et Allāh voit bien tout ce que vous faites." where chapterNumber=57 and ayatNumber=5;
update verses set fr="A Lui est le royaume des cieux et de la terre, et c’est à Allāh que seront ramenées toutes les affaires <em>pour Sa décision</em>." where chapterNumber=57 and ayatNumber=6;
update verses set fr="Il fait pénétrer la nuit dans le jour, et Il fait pénétrer le jour dans la nuit \; et Il connaît très bien tout ce qui est dans les poitrines." where chapterNumber=57 and ayatNumber=7;
update verses set fr="Croyez en Allāh et en Son Messager, et dépensez <em>dans la voie d’Allāh</em> de ce dont Il vous a fait héritiers. Et ceux d’entre vous qui croient et qui dépensent, auront une grande récompense." where chapterNumber=57 and ayatNumber=8;
update verses set fr="Qu’avez-vous que vous ne croyez pas en Allāh, alors que le Messager vous appelle à croire en votre Seigneur, et qu’Il a déjà reçu un engagement de vous, si vraiment vous êtes croyants." where chapterNumber=57 and ayatNumber=9;
update verses set fr="C’est Lui Qui révèle des Signes manifestes à Son serviteur, pour qu’Il vous fasse sortir <em>de toutes sortes</em> de ténèbres <em>pour vous amener</em> vers la lumière. Et en vérité, Allāh est Compatissant <em>et</em> Miséricordieux envers vous." where chapterNumber=57 and ayatNumber=10;
update verses set fr="Qu’avez-vous que vous ne dépensez pas dans la voie d’Allāh alors que c’est à Allāh qu’appartient l’héritage des cieux et de la terre ? Ceux d’entre vous qui ont dépensé et qui ont combattu avant la Victoire ne sont pas égaux. Ceux-là sont supérieurs en rang à ceux qui ont dépensé et qui ont combattu après. Et Allāh a promis à tous ce qui est meilleur <em>comme récompense</em>. Et Allāh est Très-Conscient de ce que vous faites." where chapterNumber=57 and ayatNumber=11;
update verses set fr="Quel est celui qui fera un bon prêt à Allāh ? Alors Il le multipliera pour lui et il aura une récompense généreuse." where chapterNumber=57 and ayatNumber=12;
update verses set fr="<em>Pense</em> au jour où tu verras les croyants et les croyantes, leur lumière jaillissant devant eux et à leur droite, <em>et il leur sera dit</em> : « Bonne nouvelle pour vous aujourd’hui ! Des jardins sous lesquels coulent des rivières, et dans lesquels vous demeurerez éternellement. C’est là le triomphe suprême. »" where chapterNumber=57 and ayatNumber=13;
update verses set fr="Le jour où les hommes hypocrites et les femmes hypocrites diront à ceux qui croient, « Jetez un regard sur nous pour que nous prenions un peu de votre lumière, » il <em>leur</em> sera dit : « Retournez en arrière <em>si vous le pouvez</em> et cherchez de la lumière. » Ensuite il sera élevé entre eux une muraille avec une porte dedans. L’intérieur en sera <em>toute</em> miséricorde, et par devant à l’extérieur, sera le châtiment." where chapterNumber=57 and ayatNumber=14;
update verses set fr="Ils leur crieront, « N’étions-nous pas avec vous ? » Ils répondront : « Si ! mais vous vous êtes laissés aller à la tentation et vous avez hésité et douté, et vos vains désirs vous ont trompés jusqu’à ce que vînt le décret d’Allāh. Et le Trompeur vous a trompés à l’égard d’Allāh." where chapterNumber=57 and ayatNumber=15;
update verses set fr="Alors aujourd’hui aucune rançon ne sera acceptée de vous, ni de ceux qui n’ont pas cru. Votre demeure <em>finale</em> est le Feu \; c’est là votre ami \; et quelle mauvaise destination ! »" where chapterNumber=57 and ayatNumber=16;
update verses set fr="Le moment n’est-il pas venu pour ceux qui croient, que leur cœur s’humilie au souvenir d’Allāh et à la vérité qui est descendue, et qu’ils ne deviennent pas pareils à ceux à qui le Livre avait été donné avant <em>eux</em>, et pour qui le terme avait été prolongé, mais leur cœur s’était endurci, et beaucoup d’entre eux devinrent des rebelles ?" where chapterNumber=57 and ayatNumber=17;
update verses set fr="Sachez <em>donc</em> qu’Allāh ranime la terre après sa mort. Nous avons rendu clairs les Signes pour vous, afin que vous compreniez." where chapterNumber=57 and ayatNumber=18;
update verses set fr="<em>Quant aux</em> hommes qui font l’aumône, et <em>aux</em> femmes qui font l’aumône, et ceux qui font un bon prêt à Allāh – il sera multiplié pour eux, et il y aura <em>aussi</em> pour eux une récompense honorable –" where chapterNumber=57 and ayatNumber=19;
update verses set fr="Et ceux qui croient en Allāh et en Ses Messagers, ils sont les Véridiques et les Témoins <em>de la vérité</em> aux yeux de leur Seigneur \; ils auront leur récompense et leur lumière. Mais <em>quant à</em> ceux qui refusent de croire, et qui rejettent Nos Signes, ceux-là sont les habitants de l’Enfer." where chapterNumber=57 and ayatNumber=20;
update verses set fr="Sachez donc que la vie de ce monde n’est qu’un jeu et un amusement, et un ornement, et une<em> source de</em> vantardise entre vous-mêmes, et de rivalité à multiplier richesses et enfants. <em>Cette vie</em> est pareille à de la pluie qui produit de la végétation qui fait réjouir les cultivateurs. Ensuite elle se dessèche, et tu la vois jaunir \; puis elle devient des particules de paille brisée. Et dans l’au-delà il y a un châtiment rigoureux, et <em>aussi le</em> pardon de la part d’Allāh, et <em>Son</em> plaisir. Et la vie de ce monde n’est qu’une jouissance temporaire trompeuse." where chapterNumber=57 and ayatNumber=21;
update verses set fr="Hâtez-vous à qui mieux mieux vers un pardon de votre Seigneur et un Jardin dont l’étendue équivaut à celle du ciel et de la terre <em>et</em> qui a été préparé pour ceux qui croient en Allāh et en Ses Messagers. C’est là la grâce d’Allāh \; Il l’accorde à qui Il veut, et Allāh est le Seigneur de la grâce immense." where chapterNumber=57 and ayatNumber=22;
update verses set fr="Il ne survient aucun malheur ni sur la terre, ni en vos <em>propres</em> personnes, qui ne soit <em>inscrit</em> dans un Livre avant que Nous l’amenions en existence - assurément, cela est facile pour Allāh -" where chapterNumber=57 and ayatNumber=23;
update verses set fr="Afin que vous ne vous désoliez pas de ce qui vous a échappé, et que vous n’exultiez pas non plus à cause de ce qu’Il vous a donné. Et Allāh n’aime pas les vantards infatués," where chapterNumber=57 and ayatNumber=24;
update verses set fr="Qui sont avares et enjoignent <em>aussi</em> aux hommes d’être avares. Et quiconque tourne le dos, alors sûrement, Allāh est Celui Qui Se suffit à Lui-Même, le Digne de louange." where chapterNumber=57 and ayatNumber=25;
update verses set fr="En vérité, Nous envoyâmes Nos Messagers avec des Signes manifestes, et Nous envoyâmes avec eux le Livre et la Balance, afin que les gens puissent agir avec justice \; et Nous fîmes descendre pour vous le fer, dans lequel il y a <em>le matériau pour</em> la guerre violente, ainsi que <em>beaucoup</em> d’avantages pour l’humanité, et afin qu’Allāh puisse distinguer ceux qui L’aident et <em>qui aident</em> Ses Messagers bien qu’Il reste Invisible. Assurément, Allāh est Fort, Puissant," where chapterNumber=57 and ayatNumber=26;
update verses set fr="Et en vérité, Nous avions <em>aussi</em> envoyé Noé et Abraham, et Nous avions établi le prophétat – <em>nubuwwah</em> - parmi leur progéniture et <em>leur avions donné</em> le Livre. Alors certains d’entre eux ont suivi la direction, mais beaucoup d’entre eux furent rebelles." where chapterNumber=57 and ayatNumber=27;
update verses set fr="Ensuite Nous avons fait marcher sur leurs pas Nos Messagers, et Nous avons fait que Jésus, fils de Marie, <em>les</em> suive, et Nous lui avons donné l’Evangile. Et Nous avons mis de la compassion et de la miséricorde dans le cœur de ceux qui le suivaient. Et le monachisme qu’ils inventèrent - Nous ne l’avions pas prescrit pour eux – seulement pour rechercher le plaisir d’Allāh \; mais ils ne l’observèrent pas dans l’esprit où c’était voulu. Néanmoins, Nous avons donné à ceux qui croyaient d’entre eux la récompense <em>qui leur était due,</em> mais beaucoup d’entre eux furent des rebelles." where chapterNumber=57 and ayatNumber=28;
update verses set fr="Ô vous qui croyez ! Craignez Allāh et croyez en Son Messager \; Il vous donnera une double part de Sa miséricorde, et vous accordera une lumière dans laquelle vous marcherez, et Il vous pardonnera – et Allāh est Très-Pardonnant, Miséricordieux –" where chapterNumber=57 and ayatNumber=29;
update verses set fr="Afin que les Gens du Livre ne pensent pas qu’eux–<em>les Musulmans</em> –ne méritent aucune part dans la grâce d’Allāh \; tandis que la grâce est <em>entièrement</em> entre les mains d’Allāh. Il l’accorde à qui Il veut. Et Allāh est le Maître de la grâce immense." where chapterNumber=57 and ayatNumber=30;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=58 and ayatNumber=1;
update verses set fr="Allāh a en effet entendu la prière de celle qui discute avec toi au sujet de son mari, et qui se plaint à Allāh. Et Allāh a entendu votre dialogue. En vérité, Allāh entend tout, <em>et</em> voit tout ." where chapterNumber=58 and ayatNumber=2;
update verses set fr="Ceux d’entre vous qui mettent à l’écart leurs épouses en les appelant mères – elles ne deviennent pas leurs mères \; seule est leur mère celle qui leur a donné naissance \; et assurément ils prononcent des mots qui sont blâmables et faux \; mais assurément Allāh est Effaceur de péchés <em> et est</em> Très-Pardonnant." where chapterNumber=58 and ayatNumber=3;
update verses set fr="<em>Quant à</em> ceux qui mettent à l’écart leurs épouses en les appelant mères, et qui voudraient ensuite revenir sur ce qu’ils ont dit, <em>ils doivent au préalable</em> affranchir un esclave avant qu’ils se touchent. Vous êtes exhortés à agir ainsi. Et Allāh est Très-Conscient de ce que vous faites." where chapterNumber=58 and ayatNumber=4;
update verses set fr="Mais quiconque n’<em>en</em> trouve pas, il doit jeûner pendant deux mois consécutifs, avant qu’ils se touchent. Et quiconque ne peut pas faire<em>cela</em>, doit donner à manger à soixante indigents. Ceci <em>est ainsi</em>, afin que vous croyiez <em>vraiment</em> en Allāh et en Son Messager. Et celles-ci sont les limites <em>prescrites</em> par Allāh, et pour les mécréants il y a un châtiment douloureux." where chapterNumber=58 and ayatNumber=5;
update verses set fr="Ceux qui s’opposent à Allāh et à Son Messager seront sûrement humiliés, comme leurs devanciers ont été humiliés \; et Nous avons déjà envoyé des Signes clairs. Et les mécréants auront un châtiment humiliant." where chapterNumber=58 and ayatNumber=6;
update verses set fr="Le jour où Allāh les ressuscitera tous ensemble, Il les informera de ce qu’ils faisaient. Allāh en a tenu le compte, tandis qu’eux, ils l’ont oublié. Et Allāh est Témoin de toutes choses." where chapterNumber=58 and ayatNumber=7;
update verses set fr="Ne vois-tu pas qu’Allāh sait tout ce qui est dans les cieux et tout ce qui est sur la terre ! Il n’y a aucun entretien secret à trois, qu’Il n’en soit le quatrième, ni de cinq qu’Il n’en soit le sixième, ni de moins ni de plus que cela, sans qu’Il ne soit avec eux où qu’ils puissent être. Puis, au Jour de la Résurrection, Il les informera de ce qu’ils faisaient. Assurément, Allāh connaît toutes choses très bien." where chapterNumber=58 and ayatNumber=8;
update verses set fr="N’as-tu pas vu ceux à qui les entretiens secrets ont été interdits, et qui reviennent à ce qui leur a été interdit, et qui confèrent secrètement pour le péché et la transgression, et pour la désobéissance au Messager ? Et lorsqu’ils viennent à toi, ils te saluent <em>d’une salutation</em> dont Allāh ne t’a pas salué \; mais ils se disent entre eux : « Pourquoi Allāh ne nous punit-Il pas pour ce que nous disons ? » L’Enfer leur suffit, dans lequel ils brûleront \; et quelle mauvaise destination !" where chapterNumber=58 and ayatNumber=9;
update verses set fr="Ô vous qui croyez ! lorsque vous conférez ensemble en secret, ne conférez donc pas pour <em>commettre</em> le péché et la transgression, et pour la désobéissance au Messager, mais conférez pour <em>atteindre</em> la vertu et la piété, et craignez Allāh auprès de Qui vous serez <em>tous</em> rassemblés." where chapterNumber=58 and ayatNumber=10;
update verses set fr="<em>La tenue de</em> réunions secrètes <em>à de mauvaises fins</em> ne provient que de Satan, afin qu’il puisse occasionner de l’affliction à ceux qui croient \; mais cela ne peut leur nuire nullement, sauf par la permission d’Allāh. Et c’est en Allāh que les croyants doivent placer leur confiance." where chapterNumber=58 and ayatNumber=11;
update verses set fr="Ô vous qui croyez ! lorsqu’on vous dit : « Faites place ! » dans <em>vos</em> réunions, alors faites place \; Allāh fera amplement de la place pour vous. Et lorsqu’on dit : « Levez-vous ! » alors levez-vous \; Allāh élèvera à des rangs <em>éminents</em> ceux d’entre vous qui croient, et ceux à qui la connaissance a été donnée. Et Allāh est Très-Conscient de ce que vous faites." where chapterNumber=58 and ayatNumber=12;
update verses set fr="Ô vous qui croyez ! quand vous consultez le Messager en particulier, faites une offrande avant votre consultation. Cela est plus avantageux pour vous, et plus pur <em>aussi</em>. Mais si vous ne trouvez rien <em> à donner</em>, alors Allāh est Très-Pardonnant, Miséricordieux." where chapterNumber=58 and ayatNumber=13;
update verses set fr="Avez-vous peur de faire des offrandes avant votre consultation particulière ? Et quand vous ne <em>le</em>s faites pas, et qu’Allāh vous pardonne, alors <em>il sera suffisant pour vous</em> que vous observiez la Prière, et payiez la Zakāt, et obéissiez à Allāh et à Son Messager. Et Allāh est Très-Conscient de ce que vous faites." where chapterNumber=58 and ayatNumber=14;
update verses set fr="N’as-tu pas vu ceux qui se lient d’amitié avec un peuple contre qui Allāh est courroucé ! Ils ne sont ni des vôtres ni des leurs, et ils jurent des mensonges alors qu’ils savent." where chapterNumber=58 and ayatNumber=15;
update verses set fr="Allāh a préparé pour eux un châtiment rigoureux. En vérité mauvais est ce qu’ils faisaient." where chapterNumber=58 and ayatNumber=16;
update verses set fr="Ils ont fait de leurs serments un bouclier <em>pour leurs méfaits</em>, et ils empêchent <em>les gens</em> de suivre la voie d’Allāh \; il y aura donc pour eux un châtiment humiliant." where chapterNumber=58 and ayatNumber=17;
update verses set fr="Ni leurs biens ni leurs enfants ne leur serviront le moindrement contre Allāh. Ceux-là sont les habitants du Feu où ils demeureront longtemps." where chapterNumber=58 and ayatNumber=18;
update verses set fr="Le jour où Allāh les ressuscitera tous ensemble, ils Lui jureront comme ils vous jurent, et ils s’imagineront avoir quelque chose <em>sur quoi s’appuyer</em>. Or, ce sont eux les menteurs." where chapterNumber=58 and ayatNumber=19;
update verses set fr="Satan a réussi à les dominer, et à leur faire oublier le souvenir d’Allāh. Ils sont les partisans de Satan. Or, ce sont les partisans de Satan qui sont les perdants." where chapterNumber=58 and ayatNumber=20;
update verses set fr="Certainement, ceux qui s’opposent à Allāh et à Son Messager, ceux-là sont parmi les plus vils." where chapterNumber=58 and ayatNumber=21;
update verses set fr="Allāh a décrété : « Je prévaudrai très certainement, Moi et Mes Messagers. » En vérité, Allāh est Fort, Puissant" where chapterNumber=58 and ayatNumber=22;
update verses set fr="Tu ne trouveras pas des gens qui croient en Allāh et au Jour Dernier éprouver de l'affection pour ceux qui s’opposent à Allāh et à Son Messager, même si c’était leur père, ou leurs fils ou leurs frères, ou les membres de leur tribu. Ce sont ceux-là dans le cœur de qui Allāh a inscrit la <em>vraie</em> foi, et qu’Il a fortifiés par une parole émanant de Lui. Et Il les fera entrer dans des Jardins sous lesquels coulent des ruisseaux. Ils y demeureront éternellement. Allāh est très content d’eux, et ils sont très contents de Lui. Ceux-là sont les partisans d’Allāh. Ecoutez bien, <em>ô vous les gens</em> ! ce sont les partisans d’Allāh qui réussiront." where chapterNumber=58 and ayatNumber=23;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=59 and ayatNumber=1;
update verses set fr="Tout ce qui est dans les cieux et tout ce qui est sur la terre glorifient Allāh \; et Il est le Puissant, le Sage." where chapterNumber=59 and ayatNumber=2;
update verses set fr="C’est Lui Qui, lors du premier bannissement, fit partir de leurs habitations ceux d’entre les Gens du Livre qui étaient mécréants. Vous ne croyiez pas qu’ils partiraient et ils croyaient que leurs forteresses les défendraient contre Allāh. Mais Allāh les frappa d’où ils ne s’y attendaient pas, et jeta de la terreur dans leurs cœurs, de sorte qu’ils détruisirent leurs maisons de leurs propres mains et des mains des croyants. Tirez <em>en</em> donc une leçon \; ô vous qui avez des yeux !" where chapterNumber=59 and ayatNumber=3;
update verses set fr="Et n’eût été le fait qu’Allāh avait décrété le bannissement pour eux, Il les aurait assurément punis <em>d’une autre façon</em> dans ce monde. Et dans l’au-delà, ils auront le châtiment du Feu." where chapterNumber=59 and ayatNumber=4;
update verses set fr="C’est parce qu’ils se sont opposés à Allāh et à Son Messager \; et quiconque s’oppose à Allāh – alors sûrement Allāh est sévère en châtiment." where chapterNumber=59 and ayatNumber=5;
update verses set fr="Quelque dattier que vous ayez abattu ou laissé debout sur ses racines, c’était avec la permission d’Allāh, et pour qu’Il couvre les rebelles d’ignominie." where chapterNumber=59 and ayatNumber=6;
update verses set fr="Et tout ce qu’Allāh a accordé d’eux comme butin à Son Messager, pour cela vous n’avez poussé ni chevaux ni chameaux, mais Allāh accorde à Son Messager le pouvoir sur qui Il veut \; et Allāh a le pouvoir de faire tout ce qu’Il veut." where chapterNumber=59 and ayatNumber=7;
update verses set fr="Tout ce qu'Allāh a accordé à Son Messager comme butin des habitants des villes, est pour Allāh et pour le Messager, et pour les proches parents et les orphelins, et les nécessiteux et le voyageur, afin que cela ne circule pas <em>uniquement</em> parmi ceux qui sont riches d’entre vous. Et quoi que ce soit que le Messager vous accorde, prenez-le \; et quoi que ce soit qu’il vous interdise, abstenez-vous <em>en</em>. Et craignez Allāh \; assurément Allāh est sévère en châtiment." where chapterNumber=59 and ayatNumber=8;
update verses set fr="Le butin est pour les Réfugiés pauvres qui ont été chassés de leurs habitations et de leurs biens, alors qu’ils recherchaient quelque grâce et plaisir de la part d'Allāh et qu'ils aidaient Allāh et Son Messager. Ceux-là sont les véridiques <em>dans leur foi</em> \;" where chapterNumber=59 and ayatNumber=9;
update verses set fr="Et <em>pour</em> ceux qui avaient établi domicile <em>dans cette ville</em> avant eux et <em>avaient accepté</em> la Foi, qui aiment ceux qui sont venus à eux pour chercher refuge, et ils ne trouvent dans leurs poitrines aucune envie pour ce qui est accordé <em>aux Réfugiés</em>, mais ils donnent préférence aux autres sur eux-mêmes, bien qu’ils soient eux-mêmes dans l’indigence. Et quiconque est débarrassé de la convoitise de sa propre âme – ce sont ceux-là qui prospéreront." where chapterNumber=59 and ayatNumber=10;
update verses set fr="Et <em>le butin est aussi pour</em> ceux qui sont venus après eux. Ils disent : « Notre Seigneur, pardonne-nous, et à nos frères qui nous ont devancés dans la foi, et ne laisse dans notre cœur aucune rancune contre ceux qui croient. Notre Seigneur ! En vérité, Tu es Compatissant, Miséricordieux. »" where chapterNumber=59 and ayatNumber=11;
update verses set fr="N’as-tu pas vu ceux qui sont hypocrites ? Ils disent à leurs frères qui refusent de croire parmi les Gens du Livre : « Si vous êtes chassés, nous sortirons assurément avec vous, et nous n’obéirons jamais à qui que ce soit contre vous \; et si on se bat contre vous, nous vous aiderons assurément. » Mais Allāh témoigne que ce sont certainement des menteurs." where chapterNumber=59 and ayatNumber=12;
update verses set fr="S’ils étaient chassés, ils ne sortiraient jamais avec eux \; et si on se battait contre eux, ils ne les aideraient jamais. Et même s’ils les aidaient, ils s’enfuiraient assurément \; et alors ils ne seraient pas aidés." where chapterNumber=59 and ayatNumber=13;
update verses set fr="En vérité, ils ont dans le cœur une crainte plus grande de vous que d’Allāh. C’est parce qu’ils sont un peuple qui ne comprend pas." where chapterNumber=59 and ayatNumber=14;
update verses set fr="Ils ne se battront pas ensemble contre vous, sauf dans des villes fortifiées ou de derrière des murailles. Leur guerre entre eux-mêmes est violente ! Tu les crois unis mais leurs cœurs sont divisés. C’est parce qu’ils sont un peuple qui ne raisonne pas." where chapterNumber=59 and ayatNumber=15;
update verses set fr="<em>Leur cas est</em> pareil à celui de ceux qui, peu de temps avant eux, ont goûté les mauvaises conséquences de leur conduite. Et pour eux il y a un châtiment douloureux." where chapterNumber=59 and ayatNumber=16;
update verses set fr="<em>Ou c’est</em> comme le cas de Satan, lorsqu’il dit à l’homme : « Ne crois pas. » Mais quand il est devenu mécréant, il dit : « Je n’ai rien à faire avec toi \; certainement je crains Allāh, le Seigneur des mondes. »" where chapterNumber=59 and ayatNumber=17;
update verses set fr="Et la fin des deux sera qu’ils seront tous deux dans le Feu, y demeurant longtemps. Telle est la rétribution de ceux qui font le mal." where chapterNumber=59 and ayatNumber=18;
update verses set fr="Ô vous qui croyez ! craignez Allāh \; et que chaque âme s’occupe de ce qu’elle envoie en avant pour le lendemain. Et craignez Allāh \; en vérité, Allāh est Très-Conscient de ce que vous faites." where chapterNumber=59 and ayatNumber=19;
update verses set fr="Et ne soyez pas comme ceux qui ont oublié Allāh et par conséquent, Il leur a fait oublier leur propre âme. Ce sont eux qui sont les rebelles." where chapterNumber=59 and ayatNumber=20;
update verses set fr="Les habitants du Feu, et les habitants du Jardin ne sont pas égaux. Ce sont les habitants du Jardin qui triompheront." where chapterNumber=59 and ayatNumber=21;
update verses set fr="Si Nous avions envoyé ce Coran sur une montagne, tu l’aurais certainement vu s’humilier <em>et</em> se fendre en morceaux par crainte d’Allāh. Et ce sont des similitudes que Nous proposons aux hommes afin qu’ils réfléchissent." where chapterNumber=59 and ayatNumber=22;
update verses set fr="Il est Allāh \; nul n’est digne d’adoration excepté Lui, Celui Qui connaît l’invisible et le visible. Il est le Gracieux, le Miséricordieux." where chapterNumber=59 and ayatNumber=23;
update verses set fr="Il est Allāh \; et nul n’est digne d’adoration excepté Lui, le Souverain, le Très-Saint, la Source de Paix, Celui Qui accorde la Sécurité, le Protecteur, le Puissant, Celui Qui remet en bon état, le Possesseur de la Majesté. Saint est Allāh, <em>bien</em> au-dessus de ce qu’ils <em>Lui</em> associent." where chapterNumber=59 and ayatNumber=24;
update verses set fr="Il est Allāh, le Créateur, Celui Qui forme, Celui Qui façonne. A Lui appartiennent les plus beaux noms. Tout ce qui est dans les cieux et sur la terre Le glorifie, et Il est le Puissant, le Sage." where chapterNumber=59 and ayatNumber=25;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=60 and ayatNumber=1;
update verses set fr="Ô vous qui croyez ! ne prenez pas Mon ennemi et votre ennemi comme amis, leur offrant de l’affection, alors qu’ils ne croient pas à la vérité qui vous est venue \; ils expulsent le Messager ainsi que vous-mêmes <em>de vos habitations, simplement</em> parce que vous croyez en Allāh, votre Seigneur. Si vous sortez pour lutter pour Ma cause et rechercher Mon plaisir, <em>ne les prenez pas comme amis</em>, leur faisant parvenir en cachette <em>des messages</em> d’amitié, alors que Je sais le mieux ce que vous dissimulez et ce que vous révélez. Et quiconque d’entre vous fait cela, s’est sûrement égaré du droit chemin." where chapterNumber=60 and ayatNumber=2;
update verses set fr="Ce n’est que s’ils l’emportent sur vous, qu’ils se comporteront <em>ouvertement</em> envers vous en ennemis, et ils tendront vers vous leurs mains et leur langue pour <em>vous faire</em> du mal \; et ils désirent ardemment que vous deveniez mécréants." where chapterNumber=60 and ayatNumber=3;
update verses set fr="Ni vos liens de parenté ni vos enfants ne vous seront d’aucun profit au Jour de la Résurrection. Il jugera entre vous. Et Allāh voit bien tout ce que vous faites." where chapterNumber=60 and ayatNumber=4;
update verses set fr="Il y a pour vous un excellent modèle en Abraham et ceux qui étaient avec lui, lorsqu’ils dirent à leur peuple : « Nous n’avons rien à faire avec vous, ni avec ce que vous adorez en dehors d’Allāh. Nous vous renions. L'inimitié et la haine sont devenues manifestes entre nous et vous pour toujours, jusqu’à ce que vous croyiez en Allāh Seul » – sauf qu’Abraham dit à son père : « Je demanderai assurément pardon pour toi, quoique je n’aie aucun pouvoir <em>de prévaloir</em> en ta faveur auprès d’Allāh. » « Notre Seigneur, c’est en Toi que nous plaçons notre confiance, et c’est vers Toi que nous nous tournons <em>repentants</em>, et vers Toi qu’est la destination <em>finale</em>." where chapterNumber=60 and ayatNumber=5;
update verses set fr="Notre Seigneur, ne fais pas de nous une épreuve pour ceux qui ne croient pas, et pardonne-nous, notre Seigneur \; car Toi Seul es le Puissant, le Sage. »" where chapterNumber=60 and ayatNumber=6;
update verses set fr="Assurément, il y a en eux un excellent exemple pour vous – pour <em>tous</em> ceux qui espèrent en Allāh et au Jour Dernier. Et quiconque se détourne – en vérité, Allāh est Celui Qui Se suffit à Lui-Même, le Digne de louange." where chapterNumber=60 and ayatNumber=7;
update verses set fr="Il se peut qu’Allāh mette de l’affection entre vous et ceux d’entre eux avec qui vous êtes <em>maintenant</em> en inimitié, et Allāh est Tout-Puissant \; et Allāh est Très-Pardonnant, Miséricordieux." where chapterNumber=60 and ayatNumber=8;
update verses set fr="A l’égard de ceux qui ne se sont pas battus contre vous à cause de <em>votre</em> religion, et qui ne vous ont pas chassés de vos habitations, Allāh ne vous interdit pas d’être bienveillants, et d’agir équitablement envers eux. Assurément Allāh aime ceux qui sont équitables." where chapterNumber=60 and ayatNumber=9;
update verses set fr="Allāh vous interdit seulement, à l’égard de ceux qui se sont battus contre vous à cause de <em>votre</em> religion, et qui vous ont chassés de vos habitations, et qui ont aidé à votre expulsion, de vous lier d’amitié avec eux, et quiconque se lie d’amitié avec eux – ce sont ceux-là qui sont les injustes." where chapterNumber=60 and ayatNumber=10;
update verses set fr="Ô vous qui croyez ! lorsque des croyantes viennent à vous comme réfugiées, mettez-les à l’épreuve. Allāh connaît le mieux leur foi. Si alors vous les trouvez <em>de vraies</em> croyantes, ne les renvoyez pas aux mécréants. Ces <em>femmes</em> ne sont pas licites pour eux, et eux – <em>les mécréants</em> – ne sont pas licites non plus pour elles. Mais payez <em>à leurs maris mécréants</em> ce qu’ils auront dépensé <em>pour elles</em>. Et ce n’est pas un pêché de votre part de les épouser, lorsque vous leur aurez donné leur dot. Et ne conservez pas <em>vos</em> liens conjugaux avec les mécréantes, mais réclamez<em>le remboursement de</em> ce que vous aurez dépensé, et que <em>les mécréants</em> réclament ce qu’ils auront dépensé. C’est là le jugement d’Allāh. Il juge entre vous. Et Allāh est Omniscient, Sage." where chapterNumber=60 and ayatNumber=11;
update verses set fr="Et si l’une de vos femmes vous quitte pour les mécréants, alors, lorsque vous vous usez de représailles <em>et que vous obtenez du butin des mécréants</em>, donnez à ces croyants dont les femmes sont parties l’équivalent de ce qu’ils avaient dépensé <em>pour leurs femmes</em>. Et craignez Allāh en Qui vous croyez." where chapterNumber=60 and ayatNumber=12;
update verses set fr="Ô Prophète ! lorsque des croyantes viennent te prêter serment d’allégeance qu’elles n’associeront rien à Allāh, et qu’elles ne voleront pas, et ne commettront pas l’adultère, ni ne tueront leurs enfants, ni ne porteront de calomnie qu’elles auront elles mêmes inventée délibérément, ni ne te désobéiront en ce qui est juste, alors accepte leur allégeance et demande à Allāh de leur pardonner. En vérité, Allāh est Très-Pardonnant, Miséricordieux." where chapterNumber=60 and ayatNumber=13;
update verses set fr="Ô vous qui croyez ! ne vous liez pas d’amitié avec un peuple contre qui Allāh est courroucé \; ils ont en effet désespéré de l’au-delà, comme les mécréants ont désespéré de ceux qui sont dans les tombes." where chapterNumber=60 and ayatNumber=14;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=61 and ayatNumber=1;
update verses set fr="Tout ce qui est dans les cieux et tout ce qui est sur la terre glorifient Allāh \; et Il est le Puissant, le Sage." where chapterNumber=61 and ayatNumber=2;
update verses set fr="Ô vous qui croyez ! pourquoi dites-vous ce que vous ne faites pas ?" where chapterNumber=61 and ayatNumber=3;
update verses set fr="C’est extrêmement odieux aux yeux d'Allāh que ce que vous dites vous ne le faites pas." where chapterNumber=61 and ayatNumber=4;
update verses set fr="En vérité, Allāh aime ceux qui combattent pour Sa cause en rangs <em>serrés</em>, comme s’ils étaient une structure solide soudée au plomb." where chapterNumber=61 and ayatNumber=5;
update verses set fr="Et <em>souvenez-vous</em> quand Moïse dit à son peuple : « Ô mon peuple, pourquoi me maltraitez-vous, alors que vous savez que je suis pour vous le Messager d’Allāh ? » Alors, quand ils dévièrent <em>du droit chemin</em>, Allāh fit dévier leurs cœurs, car Allāh ne guide pas le peuple rebelle." where chapterNumber=61 and ayatNumber=6;
update verses set fr="Et <em>souvenez-vous</em> quand Jésus, fils de Marie, dit : « Ô Enfants d’Israël, assurément je suis pour vous le Messager d’Allāh, accomplissant ce qui <em>a été mentionné</em> dans la Torah avant moi, et annonçant la bonne nouvelle d’un Messager qui viendra après moi. Son nom sera Ahmad. » Et lorsqu’il vint à eux avec des preuves évidentes, ils dirent : « Ceci est <em>de la</em> magie manifeste. »" where chapterNumber=61 and ayatNumber=7;
update verses set fr="Mais qui peut être plus injuste que celui qui invente un mensonge contre Allāh, alors qu’il est appelé à l’Islam ? Allāh ne guide pas le peuple qui agit injustement." where chapterNumber=61 and ayatNumber=8;
update verses set fr="Ils désirent éteindre la lumière d’Allāh par <em>le souffle de</em> leurs bouches, mais Allāh rendra parfaite Sa lumière, même si les mécréants <em>le</em> haïssent." where chapterNumber=61 and ayatNumber=9;
update verses set fr="C’est Lui Qui a envoyé Son Messager avec la direction et la Religion de la Vérité, afin de la faire prévaloir sur toutes les <em>autres</em> religions, même si ceux qui attribuent des associés <em>à Allāh le</em> haïssent." where chapterNumber=61 and ayatNumber=10;
update verses set fr="Ô vous qui croyez ! Vous indiquerai-Je un marché qui vous sauvera d’un châtiment douloureux ?" where chapterNumber=61 and ayatNumber=11;
update verses set fr="Que vous croyiez en Allāh et en Son Messager, et luttiez pour la cause d’Allāh avec vos biens et vos personnes. Cela vaut mieux pour vous, si seulement vous saviez." where chapterNumber=61 and ayatNumber=12;
update verses set fr="Il vous pardonnera vos péchés, et vous fera entrer dans des Jardins sous lesquels coulent des ruisseaux, et dans des habitations pures <em> et agréables situées</em> dans les Jardins d’Eternité. C’est là le triomphe suprême." where chapterNumber=61 and ayatNumber=13;
update verses set fr="Et <em>Il vous accordera</em> une autre <em>faveur</em> que vous aimez : le secours provenant d’Allāh et une victoire proche. Annonce donc la bonne nouvelle aux croyants." where chapterNumber=61 and ayatNumber=14;
update verses set fr="Ô vous qui croyez ! soyez les aides <em>dans la cause</em> d’Allāh, comme disait Jésus, fils de Marie, à ses disciples : « Qui sont mes aides pour<em> la cause d’</em>Allāh ? » Les disciples dirent : « Nous sommes les aides d’Allāh. » Alors un groupe des Enfants d’Israël crut, alors qu’un <em>autre</em> groupe refusa de croire. Puis, Nous aidâmes ceux qui crurent contre leur ennemi, et ils devinrent victorieux." where chapterNumber=61 and ayatNumber=15;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=62 and ayatNumber=1;
update verses set fr="Tout ce qui est dans les cieux et tout ce qui est sur la terre glorifient Allāh, le Souverain, le Très-Saint, le Puissant, le Sage." where chapterNumber=62 and ayatNumber=2;
update verses set fr="C’est Lui Qui a suscité parmi les Illettrés un Messager <em>qui est</em> des leurs, qui leur récite Ses Signes, et les purifie, et leur enseigne le Livre et la Sagesse, bien qu’ils eussent été auparavant dans un égarement manifeste." where chapterNumber=62 and ayatNumber=3;
update verses set fr="Et <em>Il le suscitera parmi</em> d’autres des leurs qui ne se sont pas encore joints à eux. Il est le Puissant, le Sage." where chapterNumber=62 and ayatNumber=4;
update verses set fr="C’est là la grâce d’Allāh \; Il l’accorde à qui Il veut \; et Allāh est le Maître de la grâce immense." where chapterNumber=62 and ayatNumber=5;
update verses set fr="L’exemple de ceux qui étaient chargés de <em>la loi de</em> la Torah mais ne l’observaient pas, est pareil à l’exemple d’un âne chargé de livres. Mauvais est l’exemple des hommes qui rejettent les Signes d’Allāh, et Allāh ne guide pas le peuple qui agit injustement." where chapterNumber=62 and ayatNumber=6;
update verses set fr="Dis : « Ô vous qui êtes Juifs, si vous croyez être les amis d’Allāh à l’exclusion de <em>tous</em> les autres hommes, alors, souhaitez la mort si vous êtes <em>vraiment</em> véridiques. »" where chapterNumber=62 and ayatNumber=7;
update verses set fr="Mais ils ne la souhaiteront jamais, à cause de ce que leurs mains ont envoyé devant<em> eux</em>. Et Allāh connaît très bien les injustes." where chapterNumber=62 and ayatNumber=8;
update verses set fr="Dis : « La mort que vous fuyez vous rattrapera sûrement. Alors vous serez ramenés à Celui Qui connaît l’invisible et le visible, et Il vous informera de ce que vous faisiez. »" where chapterNumber=62 and ayatNumber=9;
update verses set fr="Ô vous qui croyez ! lorsque 1’appel à la Prière est fait le vendredi, hâtez-vous vers le souvenir d’Allāh, et laissez de côté toute <em> autre</em> affaire. Cela vaut mieux pour vous, si seulement vous saviez." where chapterNumber=62 and ayatNumber=10;
update verses set fr="Et lorsque la Prière est terminée, alors dispersez-vous dans le pays, et recherchez quelque grâce de la part d’Allāh, et souvenez-vous beaucoup d’Allāh, afin que vous prospériez." where chapterNumber=62 and ayatNumber=11;
update verses set fr="Mais lorsqu’ils voient quelque commerce ou divertissement, ils s'y précipitent et te laissent debout <em>seul</em>. Dis : « Ce qui est avec Allāh vaut mieux que le divertissement et le commerce, et Allāh est le Meilleur des pourvoyeurs. »" where chapterNumber=62 and ayatNumber=12;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=63 and ayatNumber=1;
update verses set fr="Quand les hypocrites viennent à toi, ils disent : « Nous témoignons que tu es en effet le Messager d’Allāh. » Et Allāh sait que tu es en effet Son Messager, mais Allāh atteste que les hypocrites sont sûrement des menteurs." where chapterNumber=63 and ayatNumber=2;
update verses set fr="Ils ont fait de leurs serments un bouclier \; c’est ainsi qu’ils empêchent <em>les gens</em> de suivre la voie d’Allāh. Mauvais est assurément ce qu’ils faisaient !" where chapterNumber=63 and ayatNumber=3;
update verses set fr="C’est parce qu’ils ont <em>d’abord</em> cru, et qu’ensuite ils ont mécru. Aussi, un sceau a été mis sur leur cœur, donc ils ne comprennent pas." where chapterNumber=63 and ayatNumber=4;
update verses set fr="Et quand tu les vois, leur apparence physique te plaît et s’ils parlent, tu écoutes leur discours \; comme si ils étaient des bûches dressées <em>se supportant les unes les autres</em>. Ils s’imaginent que chaque calamité va les frapper. Ce sont eux l’ennemi \; prends donc garde à eux. Que la malédiction d’Allāh soit sur eux ! Comme ils sont détournés !" where chapterNumber=63 and ayatNumber=5;
update verses set fr="Et lorsqu’il leur est dit : « Venez, afin que le Messager d’Allāh implore le pardon pour vous, » ils détournent la tête, et tu les vois rester en arrière, pleins d’orgueil." where chapterNumber=63 and ayatNumber=6;
update verses set fr="Cela leur est égal que tu implores le pardon pour eux, ou que tu n’implores pas le pardon pour eux. Allāh ne leur pardonnera jamais. Assurément, Allāh ne guide pas le peuple rebelle." where chapterNumber=63 and ayatNumber=7;
update verses set fr="Ce sont eux qui disent : « Ne dépensez rien pour ceux qui sont avec le Messager d’Allāh jusqu’à ce qu’ils se dispersent <em>et l’abandonnent</em>, » alors que c’est à Allāh qu’appartiennent les trésors des cieux et de la terre \; mais les hypocrites ne comprennent pas." where chapterNumber=63 and ayatNumber=8;
update verses set fr="Ils disent : « Si nous retournons à Médine, le plus honorable en chassera assurément le plus méprisable \; » alors que l’honneur <em>réel</em> appartient à Allāh et à Son Messager et aux croyants \; mais les hypocrites ne savent pas." where chapterNumber=63 and ayatNumber=9;
update verses set fr="Ô vous qui croyez ! que vos biens et vos enfants ne vous distraient pas du souvenir d’Allāh. Et quiconque agit ainsi – ce sont eux qui sont les perdants." where chapterNumber=63 and ayatNumber=10;
update verses set fr="Et dépensez de ces dons <em>et richesses</em> que Nous vous avons accordés, avant que la mort ne surprenne l’un d’entre vous, et qu’il ne dise : « Mon Seigneur ! si seulement tu voulais m’accorder du répit pendant quelque temps, pour que je puisse faire l’aumône, et puisse être du nombre des justes ! »" where chapterNumber=63 and ayatNumber=11;
update verses set fr="Et Allāh n’accordera jamais de répit à une âme lorsque son terme déterminé est arrivé \; et Allāh est Très-Conscient de ce que vous faites." where chapterNumber=63 and ayatNumber=12;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=64 and ayatNumber=1;
update verses set fr="Tout ce qui est dans les cieux et tout ce qui est sur la terre glorifient Allāh \; à Lui est le royaume, et à Lui les louanges, et Il a le pouvoir de faire tout ce qu’Il veut." where chapterNumber=64 and ayatNumber=2;
update verses set fr="C’est Lui Qui vous a créés, puis <em>certains</em> d’entre vous deviennent des mécréants, et <em>certains</em> d’entre vous deviennent des croyants \; et Allāh voit bien tout ce que vous faites." where chapterNumber=64 and ayatNumber=3;
update verses set fr="Il a créé les cieux et la terre en toute vérité, et Il vous a donné votre forme, et Il a rendu harmonieuses vos apparences, et vers Lui est la destination <em>finale</em>." where chapterNumber=64 and ayatNumber=4;
update verses set fr="Il connaît tout ce qui est dans les cieux et sur la terre, et Il sait ce que vous dissimulez et ce que vous révélez \; et Allāh sait très bien <em>tout</em> ce qui est dans les poitrines." where chapterNumber=64 and ayatNumber=5;
update verses set fr="La nouvelle ne vous est-elle pas parvenue de ceux qui ont refusé de croire auparavant ? Alors ils ont goûté les mauvaises conséquences de leur conduite, et ils ont eu un châtiment douloureux." where chapterNumber=64 and ayatNumber=6;
update verses set fr="C’est parce que leurs Messagers sont venus à eux avec des Signes manifestes, mais qu’ils dirent : « Est-ce que ce sont des humains qui nous guideront ? » Aussi refusèrent-ils de croire, et ils tournèrent le dos, mais Allāh est Indépendant <em>d’eux</em> \; et Allāh Se suffit à Lui-Même, <em>et est</em> Digne de louange." where chapterNumber=64 and ayatNumber=7;
update verses set fr="Ceux qui refusent de croire affirment qu’ils ne seront pas ressuscités. Dis : « Si fait, par mon Seigneur, vous serez sûrement ressuscités, alors vous serez sûrement informés de ce que vous avez fait. Et cela est facile pour Allāh. »" where chapterNumber=64 and ayatNumber=8;
update verses set fr="Croyez donc en Allāh et en Son Messager, et à la Lumière que Nous avons fait descendre. Et Allāh est Très-Conscient de tout ce que vous faites." where chapterNumber=64 and ayatNumber=9;
update verses set fr="Le jour où Il vous rassemblera pour le Jour du Rassemblement ce sera là le jour des pertes <em>et des gains.</em> Et quiconque croit en Allāh et fait de bonnes œuvres – Il lui enlèvera ses méfaits, et Il l’admettra dans des Jardins sous lesquels coulent des ruisseaux, pour y demeurer éternellement. C’est là le triomphe suprême." where chapterNumber=64 and ayatNumber=10;
update verses set fr="Mais <em>quant à</em> ceux qui refusent <em>de croire</em> et rejettent Nos Signes, ceux-là seront les habitants du Feu, ils y demeureront longtemps \; et quelle mauvaise destination !" where chapterNumber=64 and ayatNumber=11;
update verses set fr="Toute affliction ne survient qu’avec la permission d’Allāh. Et quiconque croit en Allāh – Il guide son cœur <em>sur le droit chemin</em> Et Allāh connaît très bien toutes les choses." where chapterNumber=64 and ayatNumber=12;
update verses set fr="Et obéissez à Allāh, et obéissez au Messager. Mais si vous tournez le dos, alors Notre Messager n’est responsable que de la transmission claire du Message." where chapterNumber=64 and ayatNumber=13;
update verses set fr="Allāh ! Il n’y a de dieu que Lui, donc que les croyants placent leur confiance en Allāh !" where chapterNumber=64 and ayatNumber=14;
update verses set fr="Ô vous qui croyez ! sûrement parmi vos femmes et vos enfants <em>il y en a qui sont</em> vos ennemis \; Donc prenez garde à eux. Et si vous passez dessus et vous détournez votre regard et vous pardonnez, alors assurément Allāh est Très-Pardonnant, Miséricordieux." where chapterNumber=64 and ayatNumber=15;
update verses set fr="En vérité, vos biens et vos enfants ne sont qu’une épreuve \; mais auprès d’Allāh il y a une immense récompense." where chapterNumber=64 and ayatNumber=16;
update verses set fr="Craignez donc Allāh autant que vous pouvez, et écoutez, et obéissez, et dépensez <em>pour Sa cause</em> \; cela sera plus avantageux pour vos âmes. Et quiconque est débarrassé de sa propre avarice – ce sont ceux-là qui prospéreront." where chapterNumber=64 and ayatNumber=17;
update verses set fr="Si vous faites un bon prêt à Allāh, Il vous le multipliera, et Il vous pardonnera \; et Allāh est Très-Appréciateur, Indulgent," where chapterNumber=64 and ayatNumber=18;
update verses set fr="<em>Il est</em> le Connaisseur de l’invisible et du visible, le Puissant, le Sage." where chapterNumber=64 and ayatNumber=19;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=65 and ayatNumber=1;
update verses set fr="Ô Prophète ! lorsque vous divorcez d’avec les femmes, divorcez d’avec elles selon leur période d’attente <em>prescrite</em>, et tenez compte de la période \; et craignez Allāh, votre Seigneur. Ne les chassez pas de leurs habitations, et elles ne doivent pas <em>en</em> partir <em>d’elles-mêmes</em> non plus, à moins qu’elles ne commettent une indécence manifeste. Et ce sont là les limites <em>fixées</em> par Allāh \; et quiconque transgresse les limites d’Allāh, nuit assurément à sa propre âme. Tu ne sais pas, il se peut que par la suite Allāh fasse arriver quelque chose de nouveau." where chapterNumber=65 and ayatNumber=2;
update verses set fr="Puis, lorsqu’elles atteignent le terme <em>prescrit</em>, gardez-les d’une manière bienveillante, ou séparez-vous d’elles d’une manière bienveillante, et appelez à témoin deux personnes justes d’entre vous \; et acquittez-vous du témoignage pour <em>l’amour d’</em>Allāh. Voilà ce à quoi est exhorté celui qui croit en Allāh et au Jour Dernier. Et celui qui craint Allāh – Il ménagera une issue pour lui," where chapterNumber=65 and ayatNumber=3;
update verses set fr="Et Il pourvoira à ses besoins d’où il ne s<em>’y</em> attend pas. Et celui qui met sa confiance en Allāh – Il est suffisant pour lui. En vérité, Allāh accomplira sûrement Sa décision. A toute chose, Allāh a prescrit une mesure." where chapterNumber=65 and ayatNumber=4;
update verses set fr="Et si vous êtes dans le doute au sujet de celles de vos femmes qui n’espèrent plus de règles, alors <em>sachez que</em> la période prescrite pour elles est de trois mois, et <em>la même chose pour</em> celles qui n’ont pas <em>encore</em> eu leurs règles. Et <em>quant à</em> celles qui sont enceintes, leur période sera jusqu’à ce qu’elles soient délivrées de leur fardeau. Et quiconque craint Allāh, Il lui facilitera ses affaires." where chapterNumber=65 and ayatNumber=5;
update verses set fr="C’est là le commandement d’Allāh qu’Il vous a révélé. Et quiconque craint Allāh – Il lui enlèvera ses méfaits et lui augmentera grandement <em>sa</em> récompense." where chapterNumber=65 and ayatNumber=6;
update verses set fr="Logez-les <em>pendant la période prescrite</em> dans les maisons où vous demeurez, <em>le mieux possible</em> selon vos moyens \; et ne les tourmentez pas pour leur rendre la vie difficile. Et si elles sont enceintes, dépensez pour elles jusqu’à ce qu’elles soient délivrées de leur fardeau. Et si elles allaitent <em>l’enfant</em> pour vous, donnez-leur leur récompense, et décidez de vos affaires ensemble en vous consultant <em>l’un l’autre</em> d’une façon juste \; mais si vous rencontrez des difficultés entre vous, alors une autre <em>femme</em> allaitera <em>l’enfant</em> pour lui - <em>le père.</em>" where chapterNumber=65 and ayatNumber=7;
update verses set fr="Que celui qui a une abondance de biens dépense de son abondance. Et que celui dont les ressources sont restreintes, dépense selon ce qu’Allāh lui a donné. Allāh ne charge aucune âme au-delà de ce qu’Il lui a donné. Allāh apportera bientôt l’aisance après les difficultés." where chapterNumber=65 and ayatNumber=8;
update verses set fr="Que de villes se sont révoltées contre le commandement de leur Seigneur et de Ses Messagers, et Nous leur avons sévèrement demandé des comptes, et Nous les avons châtiées d’un châtiment très pénible !" where chapterNumber=65 and ayatNumber=9;
update verses set fr="Elles ont donc goûté les mauvaises conséquences de leur conduite, et la fin de leur affaire a été la ruine." where chapterNumber=65 and ayatNumber=10;
update verses set fr="Allāh leur a préparé un châtiment rigoureux, craignez donc Allāh, ô vous hommes <em>doués</em> d’intelligence, qui avez cru. Allāh vous a en effet envoyé un Rappel –" where chapterNumber=65 and ayatNumber=11;
update verses set fr="Un Messager, qui vous récite les Signes manifestes d’Allāh, afin de faire sortir ceux qui croient et font de bonnes œuvres, de <em>toutes sortes de</em> ténèbres <em>et les amener</em> vers la lumière, et quiconque croit en Allāh et fait de bonnes œuvres – Il le fera entrer dans des Jardins sous lesquels coulent des ruisseaux, pour y demeurer éternellement. Allāh a en vérité fait pour lui une excellente provision." where chapterNumber=65 and ayatNumber=12;
update verses set fr="Allāh est Celui Qui a créé sept cieux, et en a fait autant pour la terre. Le commandement <em>divin</em> descend en abondance au milieu d’eux afin que vous sachiez qu’Allāh a le pouvoir de faire tout ce qu’Il veut, et qu’Allāh embrasse toutes choses dans <em>Sa</em> connaissance." where chapterNumber=65 and ayatNumber=13;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=66 and ayatNumber=1;
update verses set fr="Ô Prophète ! pourquoi t’interdis-tu ce qu’Allāh t’a rendu licite ? Tu cherches à plaire à tes épouses ? Et Allāh est Très Pardonnant, Miséricordieux." where chapterNumber=66 and ayatNumber=2;
update verses set fr="En vérité, Allāh vous a ordonné de vous libérer de vos serments <em>ayant trait à l’affaire susmentionnée</em>, et Allāh est votre Gardien \; et Il est l’ Omniscient, le Sage." where chapterNumber=66 and ayatNumber=3;
update verses set fr="Et lorsque le Prophète confia une affaire à une de ses épouses, et qu’ensuite elle la divulgua, et qu’Allāh en informa <em>le Prophète</em>, il en fit connaître une partie <em>à son épouse</em> et passa sur une partie. Et lorsqu’il l’en informa, elle dit : « Qui t’en a informé ? » Il répondit : « Le <em>Dieu</em> Omniscient, Très-Conscient m’ <em>en</em> a informé. »" where chapterNumber=66 and ayatNumber=4;
update verses set fr="<em>Maintenant</em>, si vous vous tournez toutes deux repentantes vers Allāh, <em>cela sera meilleur pour vous</em>, et votre cœur <em>y</em> est déjà porté. Mais si vous vous soutenez l’une l’autre contre lui, assurément Allāh est son Aide, ainsi que Gabriel et les justes d’entre les croyants \; et en outre, <em>tous</em> les <em>autres</em> anges <em>aussi</em> sont <em>ses</em> aides." where chapterNumber=66 and ayatNumber=5;
update verses set fr="S’il divorce d’avec vous, il se peut que son Seigneur lui accorde à votre place des épouses qui soient meilleures que vous – soumises, croyantes, obéissantes, repentantes, dévotes, portées aux jeûnes, des veuves <em>aussi bien que</em> des vierges." where chapterNumber=66 and ayatNumber=6;
update verses set fr="Ô vous qui croyez ! Préservez vos propres personnes ainsi que vos familles d’un Feu, dont le combustible est <em>fait</em> d’hommes et de pierres, <em>et</em> auquel sont préposés des anges durs <em>et</em> sévères qui ne désobéissent pas à Allāh en ce qu’Il leur commande, et font ce qu’il leur est commandé." where chapterNumber=66 and ayatNumber=7;
update verses set fr="Ô vous qui ne croyez pas ! ne vous excusez point aujourd’hui. Vous ne serez payés que pour ce que vous avez fait." where chapterNumber=66 and ayatNumber=8;
update verses set fr="Ô vous qui croyez ! tournez-vous vers Allāh avec un repentir sincère. Il se peut que votre Seigneur vous enlève vos méfaits, et qu’Il vous fasse entrer dans des Jardins sous lesquels coulent des ruisseaux, le jour où Allāh ne couvrira pas d’ignominie le Prophète ni ceux qui ont cru avec lui. Leur lumière courra devant eux et sur leur côté droit. Ils diront : « Notre Seigneur, rends notre lumière parfaite pour nous et pardonne-nous \; assurément Tu as le pouvoir de faire tout ce que Tu veux. »" where chapterNumber=66 and ayatNumber=9;
update verses set fr="Ô Prophète ! Lutte <em>vigoureusement</em> contre les mécréants et les hypocrites \; et sois sévère envers eux. Leur habitation est l’Enfer, et quelle mauvaise destination <em>que celle-là</em> !" where chapterNumber=66 and ayatNumber=10;
update verses set fr="Allāh propose à l’intention des mécréants l’exemple de la femme de Noé et de la femme de Lot. Elles étaient sous deux de Nos serviteurs pieux, mais elles agirent déloyalement envers eux. Aussi, ils ne leur furent d’aucun recours contre Allāh, et il <em>leur</em> fut dit : « Entrez dans le Feu, vous deux, avec ceux qui entrent. »" where chapterNumber=66 and ayatNumber=11;
update verses set fr="Et Allāh propose aux croyants l’exemple de la femme de Pharaon, lorsqu’elle dit : « Mon Seigneur ! bâtis pour moi une maison auprès de Toi dans le Jardin \; et délivre-moi de Pharaon et de ses œuvres, et délivre-moi du peuple injuste : »" where chapterNumber=66 and ayatNumber=12;
update verses set fr="Et <em>l’exemple</em> de Marie, la fille d‘Imrān, qui protégea sa chasteté – puis Nous avons insufflé en <em>l’enfant</em> lui-<em>même</em>, de Notre Esprit – et elle accomplit <em>en sa personne la prophétie transmise par</em> les Paroles de son Seigneur, et <em>contenue dans</em> Ses Livres, et elle fut du nombre des obéissants. " where chapterNumber=66 and ayatNumber=13;
update verses set fr=" Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=67 and ayatNumber=1;
update verses set fr=" Béni est Celui entre les mains de Qui est le royaume, et Il a le pouvoir de faire tout ce qu’Il veut \;" where chapterNumber=67 and ayatNumber=2;
update verses set fr=" Qui a créé la mort et la vie pour qu’Il vous éprouve – lequel d’entre vous est le meilleur en conduite \; et Il est le Puissant, le Très-Pardonnant," where chapterNumber=67 and ayatNumber=3;
update verses set fr=" Qui a créé sept cieux superposés. Dans la création du <em>Dieu</em> Gracieux, tu ne peux voir aucune discordance. Puis, regarde de nouveau \; vois-tu quelque imperfection ?" where chapterNumber=67 and ayatNumber=4;
update verses set fr=" Puis regarde de nouveau, et encore de nouveau, ton regard <em>ne</em> fera <em>que</em> retourner à toi frustré et fatigué." where chapterNumber=67 and ayatNumber=5;
update verses set fr=" Et en vérité, Nous avons orné le ciel le plus bas avec des lampes, et Nous les avons faites pour chasser les satans, et pour eux Nous avons préparé le châtiment de la Fournaise." where chapterNumber=67 and ayatNumber=6;
update verses set fr=" Et pour ceux qui refusent de croire en leur Seigneur, il y a le châtiment de l’Enfer, et quelle mauvaise destination que c’est !" where chapterNumber=67 and ayatNumber=7;
update verses set fr=" Quand ils y seront jetés, ils l’entendront ronfler pendant qu’il bouillonne." where chapterNumber=67 and ayatNumber=8;
update verses set fr=" Peu s’en faudrait qu’il n’éclatât de fureur. Toutes les fois qu’une troupe <em>de mécréants</em> y sera jetée, les gardiens <em>du Feu</em> leur demanderont : « Un Avertisseur n’est-il pas venu à vous ? »" where chapterNumber=67 and ayatNumber=9;
update verses set fr=" Ils diront : « Oui, certainement un Avertisseur est venu à nous, mais nous <em>l</em>’avons traité de menteur, et nous avons dit : « Allāh n’a rien envoyé \; vous n’êtes que dans un grave égarement.’ »" where chapterNumber=67 and ayatNumber=10;
update verses set fr=" Et ils diront : « Si seulement nous avions écouté, ou avions raisonné, nous ne nous serions pas trouvés parmi les habitants de la Fournaise <em>de l’Enfer</em>. »" where chapterNumber=67 and ayatNumber=11;
update verses set fr=" Alors avoueront-ils leurs péchés \; damnés soient les habitants du Feu !" where chapterNumber=67 and ayatNumber=12;
update verses set fr=" En vérité, ceux qui craignent leur Seigneur alors qu’Il reste caché <em>d'eux</em>, pour eux est <em>le</em> pardon ainsi qu'une grande récompense." where chapterNumber=67 and ayatNumber=13;
update verses set fr=" Et que vous dissimuliez ce que vous dites, ou que vous le divulguiez, Il connaît très bien ce qui est dans les poitrines." where chapterNumber=67 and ayatNumber=14;
update verses set fr=" Celui Qui a créé ne<em> le</em> sait-Il pas ? Alors qu'Il est le Connaisseur de toutes les subtilités, le Très-Conscient." where chapterNumber=67 and ayatNumber=15;
update verses set fr=" C’est Lui Qui a rendu la terre dans un état de soumission pour vous \; marchez donc dans ses chemins, et mangez de Sa provision. Et vers Lui sera la Résurrection." where chapterNumber=67 and ayatNumber=16;
update verses set fr=" Vous sentez-vous en sécurité contre Celui Qui est au ciel, qu’Il ne vous fera pas engloutir par la terre ? Et voilà qu’elle tremble !" where chapterNumber=67 and ayatNumber=17;
update verses set fr=" Vous sentez-vous en sécurité contre Celui Qui est au ciel, qu’Il n’enverra pas un ouragan de pierres contre vous ? C’est alors que vous saurez combien <em>terrible</em> était Mon avertissement !" where chapterNumber=67 and ayatNumber=18;
update verses set fr=" Et en vérité, leurs devanciers ont <em>aussi</em> traité de menteurs <em>Mes Messagers</em> \; alors combien <em>terrible</em> fut Ma réprobation !" where chapterNumber=67 and ayatNumber=19;
update verses set fr=" N’ont-ils pas vu les oiseaux au-dessus d’eux, déployant et repliant leurs ailes ? Nul ne les retient excepté le <em>Dieu</em> Gracieux. En vérité, Il voit bien toutes choses." where chapterNumber=67 and ayatNumber=20;
update verses set fr=" Ceux qui constituent votre armée peuvent-ils vous venir en aide contre le <em>Dieu</em> Gracieux ? Les mécréants ne sont que dans une illusion." where chapterNumber=67 and ayatNumber=21;
update verses set fr=" Ou, qui est celui qui pourvoira à vos besoins s’Il devait retenir Sa provision ? <em>Non</em>, mais ils persistent dans la rébellion et dans l’aversion." where chapterNumber=67 and ayatNumber=22;
update verses set fr=" Comment ! est-il mieux guidé, celui qui avance en rampant sur le visage, ou celui qui marche debout sur un droit chemin ?" where chapterNumber=67 and ayatNumber=23;
update verses set fr=" Dis : « C’est Lui Qui vous a amenés en existence, et Qui a fait pour vous des oreilles, des yeux et un cœur \; <em>mais</em> vous Lui <em>en</em> êtes bien peu reconnaissants. »" where chapterNumber=67 and ayatNumber=24;
update verses set fr=" Dis : « C’est Lui Qui vous a multipliés sur la terre, et c’est auprès de Lui que vous serez rassemblés. »" where chapterNumber=67 and ayatNumber=25;
update verses set fr=" Et ils disent : « Quand cette promesse s’accomplira-t-elle, si <em>en fait</em> vous êtes véridiques ? »" where chapterNumber=67 and ayatNumber=26;
update verses set fr=" Dis : « La connaissance <em>en</em> est avec Allāh Seul, et moi, je ne suis qu’un Avertisseur qui avertit clairement. »" where chapterNumber=67 and ayatNumber=27;
update verses set fr=" Mais lorsqu’ils la verront toute proche, le visage de ceux qui refusent de croire sera affligé, et il sera dit : « Voici ce que vous réclamiez. »" where chapterNumber=67 and ayatNumber=28;
update verses set fr=" Dis : « Dites-<em>moi</em>, si Allāh devait nous anéantir, moi et ceux qui sont avec moi, ou s’Il devait nous montrer de la miséricorde, qui protégerait les mécréants contre un châtiment douloureux ? »" where chapterNumber=67 and ayatNumber=29;
update verses set fr=" Dis : « Il est le <em>Dieu</em> Gracieux \; en Lui avons-nous cru et en Lui avons-nous placé notre confiance. Et vous saurez bientôt qui est dans une erreur manifeste. »" where chapterNumber=67 and ayatNumber=30;
update verses set fr=" Dis : « Dites-moi, si toute votre eau devait disparaître dans les profondeurs <em>de la terre</em>, qui donc vous apporterait de l’eau <em>claire et</em> vive ? »" where chapterNumber=67 and ayatNumber=31;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=68 and ayatNumber=1;
update verses set fr="Nūn. Par la plume et <em>par</em> ce qu’ils écrivent," where chapterNumber=68 and ayatNumber=2;
update verses set fr="Par la grâce de ton Seigneur. tu n’es pas un insensé." where chapterNumber=68 and ayatNumber=3;
update verses set fr="Et pour toi, assurément, il y a une récompense non interrompue." where chapterNumber=68 and ayatNumber=4;
update verses set fr="Et tu possèdes assurément de hautes qualités morales." where chapterNumber=68 and ayatNumber=5;
update verses set fr="Et tu verras bientôt, et ils verront <em>aussi</em>" where chapterNumber=68 and ayatNumber=6;
update verses set fr="Lequel d’entre vous est frappé de folie." where chapterNumber=68 and ayatNumber=7;
update verses set fr="Sûrement, ton Seigneur sait le mieux qui sont ceux qui s’égarent de Sa voie, et Il sait le mieux ceux qui sont <em>bien</em> guidés." where chapterNumber=68 and ayatNumber=8;
update verses set fr="N’obéis donc pas à ceux qui rejettent <em>la vérité.</em>" where chapterNumber=68 and ayatNumber=9;
update verses set fr="Ils veulent que tu transiges afin qu’eux <em>aussi</em>, ils puissent transiger." where chapterNumber=68 and ayatNumber=10;
update verses set fr="Et ne cède à aucun fieffé jureur méprisable," where chapterNumber=68 and ayatNumber=11;
update verses set fr="Médisant, grand colporteur de calomnies," where chapterNumber=68 and ayatNumber=12;
update verses set fr="Empêcheur du bien, transgresseur, pécheur," where chapterNumber=68 and ayatNumber=13;
update verses set fr="Mal élevé et, de plus, de paternité douteuse." where chapterNumber=68 and ayatNumber=14;
update verses set fr="Tout cela parce qu’il possède des biens et de <em>nombreux</em> enfants." where chapterNumber=68 and ayatNumber=15;
update verses set fr="Lorsque Nos Signes lui sont récités, il dit : « Des fables des anciens ! »" where chapterNumber=68 and ayatNumber=16;
update verses set fr="Nous le marquerons sur le museau." where chapterNumber=68 and ayatNumber=17;
update verses set fr="En vérité, Nous les éprouverons comme Nous avons éprouvé les propriétaires du jardin lorsqu’ils jurèrent que certainement ils en feraient la récolte le <em>lendemain</em> matin," where chapterNumber=68 and ayatNumber=18;
update verses set fr="Et ils ne firent aucune exception, <em> ils ne dirent pas, « S'il plaît à Allāh.</em> »" where chapterNumber=68 and ayatNumber=19;
update verses set fr="Alors une calamité de ton Seigneur tomba dessus pendant qu’ils dormaient \;" where chapterNumber=68 and ayatNumber=20;
update verses set fr="Et le matin le trouva comme si tout avait été rasé." where chapterNumber=68 and ayatNumber=21;
update verses set fr="Alors, <em>au petit</em> matin ils s’interpellèrent :-" where chapterNumber=68 and ayatNumber=22;
update verses set fr="« Partez tôt le matin à votre champ, si vous désirez <em>en</em> cueillir les fruits. »" where chapterNumber=68 and ayatNumber=23;
update verses set fr="Et ils se mirent en route, se parlant à voix basse," where chapterNumber=68 and ayatNumber=24;
update verses set fr="<em> Ils dirent :</em> « Qu’aucun pauvre ne soit permis d’y entrer aujourd’hui <em>de peur que cela ne soit</em> contre vos intérêts. »" where chapterNumber=68 and ayatNumber=25;
update verses set fr="Ainsi s’en allèrent-ils tôt le matin, <em>d’un pas</em> décidé." where chapterNumber=68 and ayatNumber=26;
update verses set fr="Mais lorsqu’ils le virent, ils dirent : « Nous nous sommes sûrement égarés !" where chapterNumber=68 and ayatNumber=27;
update verses set fr="Pire \; nous avons été privés <em>de tout</em> ! »" where chapterNumber=68 and ayatNumber=28;
update verses set fr="Le meilleur d’entre eux dit : « Ne vous avais-je pas dit : « Pourquoi ne rendez-vous pas gloire <em>à Allāh</em> ? »" where chapterNumber=68 and ayatNumber=29;
update verses set fr="Ils dirent <em>alors</em> : « Gloire à notre Seigneur ! Assurément nous avons été injustes. »" where chapterNumber=68 and ayatNumber=30;
update verses set fr="Ensuite ils se tournèrent les uns vers les autres, se faisant mutuellement des reproches." where chapterNumber=68 and ayatNumber=31;
update verses set fr="Ils dirent : « Malheur à nous ! Nous avons en effet été des rebelles." where chapterNumber=68 and ayatNumber=32;
update verses set fr="Peut-être que notre Seigneur nous donnera un meilleur <em>jardin</em> que celui-ci \; nous nous tournons vers notre Seigneur avec empressement. »" where chapterNumber=68 and ayatNumber=33;
update verses set fr="Tel est le châtiment <em>de ce monde</em>. Et assurément le châtiment de l’au-delà est plus grand. Si seulement ils savaient !" where chapterNumber=68 and ayatNumber=34;
update verses set fr="En vérité, pour les vertueux il y a les Jardins de la Félicité auprès de leur Seigneur." where chapterNumber=68 and ayatNumber=35;
update verses set fr="Traiterons-Nous donc ceux qui se soumettent <em>à Notre volonté</em>, comme <em>Nous traitons</em> les coupables ?" where chapterNumber=68 and ayatNumber=36;
update verses set fr="Qu’avez-vous ? Comment jugez-vous !" where chapterNumber=68 and ayatNumber=37;
update verses set fr="Auriez-vous un Livre dans lequel vous lisez," where chapterNumber=68 and ayatNumber=38;
update verses set fr="Que vous y trouverez sûrement tout ce que vous choisirez ?" where chapterNumber=68 and ayatNumber=39;
update verses set fr="Ou, auriez-vous un pacte qui Nous engagerait jusqu’au Jour de la Résurrection, que vous aurez sûrement tout ce que vous commanderez ?" where chapterNumber=68 and ayatNumber=40;
update verses set fr="Demande-leur lequel d’entre eux s’en portera garant," where chapterNumber=68 and ayatNumber=41;
update verses set fr="Ou auraient-ils quelques « associés » <em>d'Allāh</em> ? Qu’ils produisent donc <em>ces</em> « associés » des leurs, s’ils disent la vérité." where chapterNumber=68 and ayatNumber=42;
update verses set fr="Le jour où la calamité les surprendra, et où ils seront invités à se prosterner, ils ne pourront pas <em>le faire</em> \;" where chapterNumber=68 and ayatNumber=43;
update verses set fr="Leurs regards seront abaissés, <em>et</em> l’humiliation les couvrira, et ils avaient en effet été invités à se prosterner, lorsqu’ils étaient sains et saufs, <em>mais ils n’avaient pas obéi</em>." where chapterNumber=68 and ayatNumber=44;
update verses set fr="Laisse-Moi donc <em>M’occuper</em> de ceux qui rejettent ce discours. Nous les ferons s’approcher pas à pas <em>de l’anéantissement</em>, par où ils ne savent pas." where chapterNumber=68 and ayatNumber=45;
update verses set fr="Et Je leur accorde du répit \; car Mon dessein est sûr." where chapterNumber=68 and ayatNumber=46;
update verses set fr="Leur réclames-tu une récompense, pour qu’ils paraissent comme écrasés sous <em>son</em> poids ?" where chapterNumber=68 and ayatNumber=47;
update verses set fr="L’invisible est-il avec eux, de sorte qu’ils puissent <em>l</em>’écrire ?" where chapterNumber=68 and ayatNumber=48;
update verses set fr="Aussi, sois constant dans l’<em>exécution du</em> commandement de ton Seigneur et ne sois pas comme l’homme du Poisson, lorsqu’il supplia <em>son Seigneur</em> alors qu’il suffoquait<em> de chagrin</em>." where chapterNumber=68 and ayatNumber=49;
update verses set fr="Si une faveur de son Seigneur ne lui était parvenue, il aurait assurément été jeté dans un endroit désolé, et il aurait été honni <em>par son peuple</em>." where chapterNumber=68 and ayatNumber=50;
update verses set fr="Mais son Seigneur le choisit et fit de lui un des justes." where chapterNumber=68 and ayatNumber=51;
update verses set fr="Et peu s’en faut que ceux qui ne croient pas ne te délogent <em>de ton rang élevé</em> avec leurs regards <em>rageurs</em>, lorsqu’ils entendent <em>réciter</em> le Rappel \; et ils disent : « Assurément, il est fou. »" where chapterNumber=68 and ayatNumber=52;
update verses set fr="Non, ce n’est rien d’autre qu’un Rappel pour tous les mondes." where chapterNumber=68 and ayatNumber=53;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=69 and ayatNumber=1;
update verses set fr="L’Inéluctable !" where chapterNumber=69 and ayatNumber=2;
update verses set fr="Qu’est-ce que l’Inéluctable ?" where chapterNumber=69 and ayatNumber=3;
update verses set fr="Et qu’est-ce qui te fera savoir, ce qu’est l’Inéluctable ?" where chapterNumber=69 and ayatNumber=4;
update verses set fr="Les Thamūd et les ‘Ād traitèrent de mensonge la catastrophe écrasante." where chapterNumber=69 and ayatNumber=5;
update verses set fr="Alors, quant aux Thamūd, ils furent anéantis par un souffle d’une violence inouïe." where chapterNumber=69 and ayatNumber=6;
update verses set fr="Et quant aux ‘Ād, ils furent anéantis par un vent glacial, rugissant et furieux," where chapterNumber=69 and ayatNumber=7;
update verses set fr="Qu’Il fit souffler contre eux, pendant sept nuits et huit jours consécutifs, de sorte que tu aurais pu voir les habitants gisant prostrés, comme s’ils étaient des troncs de dattiers tombés par terre." where chapterNumber=69 and ayatNumber=8;
update verses set fr="Vois-tu quelque trace d’eux ?" where chapterNumber=69 and ayatNumber=9;
update verses set fr="Et Pharaon, et ceux qui l’avaient devancé, et les villes renversées avaient commis des péchés <em>avec persistance</em>." where chapterNumber=69 and ayatNumber=10;
update verses set fr="Et ils désobéirent au Messager de leur Seigneur \; alors Il les saisit d’une étreinte qui continue à se resserrer." where chapterNumber=69 and ayatNumber=11;
update verses set fr="En vérité, lorsque les eaux débordèrent, Nous vous transportâmes sur le bateau," where chapterNumber=69 and ayatNumber=12;
update verses set fr="Afin que Nous en fassions un rappel pour vous, et que les oreilles qui retiennent puissent le retenir." where chapterNumber=69 and ayatNumber=13;
update verses set fr="Et lorsque d’un seul souffle l’on sonnera de la trompe," where chapterNumber=69 and ayatNumber=14;
update verses set fr="Et <em>que</em> la terre et les montagnes seront soulevées, puis écrasées d’un seul coup en morceaux," where chapterNumber=69 and ayatNumber=15;
update verses set fr="Ce jour-là aura lieu le <em>grand</em> Evénement." where chapterNumber=69 and ayatNumber=16;
update verses set fr="Et le ciel se fendra, et ce jour-là il deviendra fragile." where chapterNumber=69 and ayatNumber=17;
update verses set fr="Et les anges <em>se tiendront</em> sur ses côtés, et au-dessus d’eux ce jour-là, huit <em>anges</em> porteront le Trône de ton Seigneur." where chapterNumber=69 and ayatNumber=18;
update verses set fr="Ce jour-là vous serez présentés <em>devant Dieu</em> \; <em>et</em> aucun de vos secrets ne restera caché." where chapterNumber=69 and ayatNumber=19;
update verses set fr="Alors, quant à celui à qui son dossier sera remis en sa main droite, il dira : « Venez, lisez mon dossier." where chapterNumber=69 and ayatNumber=20;
update verses set fr="Assurément, je savais que je serais confronté à mon compte. »" where chapterNumber=69 and ayatNumber=21;
update verses set fr="Il aura donc une vie agréable," where chapterNumber=69 and ayatNumber=22;
update verses set fr="Dans un Jardin élevé," where chapterNumber=69 and ayatNumber=23;
update verses set fr="Dont les fruits seront à portée de la main." where chapterNumber=69 and ayatNumber=24;
update verses set fr="« Mangez donc et buvez avec plaisir, pour les <em>bonnes</em> œuvres que vous avez faites dans les jours déjà passés. »" where chapterNumber=69 and ayatNumber=25;
update verses set fr="Mais quant à celui à qui son dossier sera remis en sa main gauche, il dira : « Hélas ! Si seulement mon dossier ne m’eût pas été donné !" where chapterNumber=69 and ayatNumber=26;
update verses set fr="Et que je n’aie pas non plus su quel était mon compte !" where chapterNumber=69 and ayatNumber=27;
update verses set fr="Ô que je voudrais que <em>le jugement</em> m’eût fait goûter la mort <em> une bonne fois</em> !" where chapterNumber=69 and ayatNumber=28;
update verses set fr="Mes richesses ne m’ont été d’aucun secours \;" where chapterNumber=69 and ayatNumber=29;
update verses set fr="Ma suprématie a été détruite et m’a abandonné. »" where chapterNumber=69 and ayatNumber=30;
update verses set fr="« Attrapez-le et enchaînez-le," where chapterNumber=69 and ayatNumber=31;
update verses set fr="Puis, jetez-le dans la Fournaise <em>de l’Enfer</em>." where chapterNumber=69 and ayatNumber=32;
update verses set fr="Ensuite, mettez-le dans une chaîne dont la longueur est de soixante-dix coudées \;" where chapterNumber=69 and ayatNumber=33;
update verses set fr="En vérité, il ne croyait pas en Allāh, le Grand," where chapterNumber=69 and ayatNumber=34;
update verses set fr="Et il n’exhorta pas de nourrir l’indigent." where chapterNumber=69 and ayatNumber=35;
update verses set fr="Aussi n’a-t-il pas d’ami dévoué ici aujourd’hui \;" where chapterNumber=69 and ayatNumber=36;
update verses set fr="Ni d’autre nourriture que de l’eau qui a lavé des plaies," where chapterNumber=69 and ayatNumber=37;
update verses set fr="Que seuls les pécheurs mangeront. »" where chapterNumber=69 and ayatNumber=38;
update verses set fr="Mais non, en vérité, Je jure par ce que vous voyez," where chapterNumber=69 and ayatNumber=39;
update verses set fr="Et<em> par</em> ce que vous ne voyez pas," where chapterNumber=69 and ayatNumber=40;
update verses set fr="Que c’est assurément la parole <em>apportée</em> par un noble Messager \;" where chapterNumber=69 and ayatNumber=41;
update verses set fr="Et ce n’est pas la parole d’un poète \; comme vous croyez peu !" where chapterNumber=69 and ayatNumber=42;
update verses set fr="Et ce n’est pas non plus la parole d’un devin \; comme vous prenez peu de note !" where chapterNumber=69 and ayatNumber=43;
update verses set fr="<em>C’est</em> une révélation de la part du Seigneur des mondes." where chapterNumber=69 and ayatNumber=44;
update verses set fr="Et, s’il Nous avait faussement attribué <em>ne serait-ce que</em> quelques paroles insignifiantes," where chapterNumber=69 and ayatNumber=45;
update verses set fr="Nous l’aurions assurément saisi par la main droite," where chapterNumber=69 and ayatNumber=46;
update verses set fr="Et puis assurément Nous lui aurions tranché la veine jugulaire," where chapterNumber=69 and ayatNumber=47;
update verses set fr="Et nul parmi vous n’aurait pu le protéger <em>contre Nous</em>." where chapterNumber=69 and ayatNumber=48;
update verses set fr="Et en vérité, c’est un rappel pour les vertueux." where chapterNumber=69 and ayatNumber=49;
update verses set fr="Et, assurément, Nous savons qu’il y a certains parmi vous qui rejettent <em>Nos Signes</em>." where chapterNumber=69 and ayatNumber=50;
update verses set fr="Et, en vérité, ce sera un<em> sujet de</em> regret pour les mécréants." where chapterNumber=69 and ayatNumber=51;
update verses set fr="Et assurément, c’est la certitude absolue." where chapterNumber=69 and ayatNumber=52;
update verses set fr="Aussi glorifie le nom de ton Seigneur, le Grand." where chapterNumber=69 and ayatNumber=53;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=70 and ayatNumber=1;
update verses set fr="Un interrogateur a interrogé sur un châtiment, qui ne manquera pas de tomber" where chapterNumber=70 and ayatNumber=2;
update verses set fr="Sur les mécréants, <em>châtiment</em> que personne ne peut repousser." where chapterNumber=70 and ayatNumber=3;
update verses set fr="<em>C’est</em> de la part d’Allāh, le Seigneur de toutes les ascensions." where chapterNumber=70 and ayatNumber=4;
update verses set fr="Les anges et l’Esprit montent vers Lui en un jour dont la durée est de cinquante mille ans." where chapterNumber=70 and ayatNumber=5;
update verses set fr="Aussi, exerce une patience sereine." where chapterNumber=70 and ayatNumber=6;
update verses set fr="Ils le voient comme étant éloigné," where chapterNumber=70 and ayatNumber=7;
update verses set fr="Mais Nous le voyons comme étant proche." where chapterNumber=70 and ayatNumber=8;
update verses set fr="Le jour où le ciel deviendra comme du cuivre fondu," where chapterNumber=70 and ayatNumber=9;
update verses set fr="Et les montagnes deviendront comme de la laine cardée," where chapterNumber=70 and ayatNumber=10;
update verses set fr="Et aucun ami dévoué ne s’enquerra de quelque ami dévoué." where chapterNumber=70 and ayatNumber=11;
update verses set fr="Ils seront placés en vue les uns des autres et le coupable aimerait pouvoir se racheter du châtiment de ce jour-là en <em>offrant</em> ses enfants," where chapterNumber=70 and ayatNumber=12;
update verses set fr="Et sa femme et son frère," where chapterNumber=70 and ayatNumber=13;
update verses set fr="Et ses proches parents qui lui avaient donné asile," where chapterNumber=70 and ayatNumber=14;
update verses set fr="Et <em>en offrant</em> tous ceux qui sont sur la terre, si <em>seulement</em> cela pouvait le sauver ainsi." where chapterNumber=70 and ayatNumber=15;
update verses set fr="Mais non ! sûrement, c’est une langue de flamme sans fumée," where chapterNumber=70 and ayatNumber=16;
update verses set fr="Arrachant la peau." where chapterNumber=70 and ayatNumber=17;
update verses set fr="Il appellera celui qui tournait le dos, et se retirait," where chapterNumber=70 and ayatNumber=18;
update verses set fr="Et amassait <em>des richesses</em>, et <em>les</em> retenait." where chapterNumber=70 and ayatNumber=19;
update verses set fr="En vérité, l’homme est né avare." where chapterNumber=70 and ayatNumber=20;
update verses set fr="Lorsque le malheur le touche, il est plein de lamentations," where chapterNumber=70 and ayatNumber=21;
update verses set fr="Mais lorsque le bien lui arrive, il est pingre." where chapterNumber=70 and ayatNumber=22;
update verses set fr="Excepté ceux qui prient," where chapterNumber=70 and ayatNumber=23;
update verses set fr="Ceux qui sont constants dans leur Prière," where chapterNumber=70 and ayatNumber=24;
update verses set fr="Et ceux dans les richesses de qui il y a une part déterminée" where chapterNumber=70 and ayatNumber=25;
update verses set fr="Pour le mendiant et pour le destitué <em>qui ne mendie pas</em>," where chapterNumber=70 and ayatNumber=26;
update verses set fr="Et ceux qui attestent que le Jour du Jugement est une réalité," where chapterNumber=70 and ayatNumber=27;
update verses set fr="Et ceux qui redoutent le châtiment de leur Seigneur –" where chapterNumber=70 and ayatNumber=28;
update verses set fr="En vérité, le châtiment de leur Seigneur n’épargne pas." where chapterNumber=70 and ayatNumber=29;
update verses set fr="Et ceux qui préservent leurs parties intimes," where chapterNumber=70 and ayatNumber=30;
update verses set fr="Sauf de leurs femmes et de celles que possède leur main droite, car assurément ils ne sont pas à blâmer \;" where chapterNumber=70 and ayatNumber=31;
update verses set fr="Mais ceux qui recherchent <em>quoi que ce soit</em> au-delà de cela, ceux-là sont les transgresseurs –" where chapterNumber=70 and ayatNumber=32;
update verses set fr="Et ceux qui veillent sur les dépôts qui leur sont confiés et sur leurs engagements," where chapterNumber=70 and ayatNumber=33;
update verses set fr="Et ceux qui sont intègres dans leurs témoignages," where chapterNumber=70 and ayatNumber=34;
update verses set fr="Et ceux qui surveillent leur Prière." where chapterNumber=70 and ayatNumber=35;
update verses set fr="Ceux-là seront dans des Jardins, honorés <em>comme il se doit</em>." where chapterNumber=70 and ayatNumber=36;
update verses set fr="Mais qu’arrive-t-il aux mécréants, pour qu’ils accourent vers toi," where chapterNumber=70 and ayatNumber=37;
update verses set fr="De droite et de gauche, en diverses bandes ?" where chapterNumber=70 and ayatNumber=38;
update verses set fr="Chacun d’entre eux espère-t-il entrer au Jardin de <em>la</em> Félicité ?" where chapterNumber=70 and ayatNumber=39;
update verses set fr="Jamais ! Nous les avons créés de ce qu’ils savent." where chapterNumber=70 and ayatNumber=40;
update verses set fr="Mais non ! Je jure par le Seigneur des orients et des occidents, que certainement Nous avons le pouvoir" where chapterNumber=70 and ayatNumber=41;
update verses set fr="D’amener à leur place d’autres meilleurs qu’eux, et Nous ne pouvons être entravé <em>dans Nos desseins,</em>" where chapterNumber=70 and ayatNumber=42;
update verses set fr="Laisse-les donc à leurs vains discours, s’amuser jusqu’à ce qu’ils rencontrent ce jour des leurs qui leur est promis," where chapterNumber=70 and ayatNumber=43;
update verses set fr="Le jour où ils sortiront précipitamment de leur tombe, comme s’ils couraient vers l’endroit de <em>leur propre</em> sacrifice," where chapterNumber=70 and ayatNumber=44;
update verses set fr="Les yeux abaissés \; et l’humiliation les couvrira. Tel est le jour qui leur est promis." where chapterNumber=70 and ayatNumber=45;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=71 and ayatNumber=1;
update verses set fr="Nous envoyâmes Noé à son peuple, <em>en lui disant</em> : « Avertis ton peuple avant qu’il ne leur survienne un châtiment douloureux." where chapterNumber=71 and ayatNumber=2;
update verses set fr="Il dit : « Ô mon peuple ! en vérité, je suis pour vous un Avertisseur qui avertit clairement." where chapterNumber=71 and ayatNumber=3;
update verses set fr="Adorez Allāh et craignez-Le et obéissez-moi !" where chapterNumber=71 and ayatNumber=4;
update verses set fr="Il vous pardonnera vos péchés, et vous accordera un répit jusqu’à un terme désigné. En vérité le terme fixé par Allāh ne peut pas être différé lorsqu’il vient, si seulement vous saviez ! »" where chapterNumber=71 and ayatNumber=5;
update verses set fr="Il dit : « Mon Seigneur, j’ai appelé mon peuple <em>vers Toi</em> nuit et jour," where chapterNumber=71 and ayatNumber=6;
update verses set fr="Mais mon appel n’a fait que les faire fuir davantage." where chapterNumber=71 and ayatNumber=7;
update verses set fr="Et, en vérité, toutes les fois que je les ai appelés pour que Tu leur pardonnes, ils se sont mis les doigts dans les oreilles, et se sont enveloppés de leurs vêtements, et ils ont persisté <em>dans leurs iniquités</em>, et ils se sont comportés avec une arrogance excessive." where chapterNumber=71 and ayatNumber=8;
update verses set fr="Ensuite, je les ai appelés ouvertement <em>à Ton chemin</em>." where chapterNumber=71 and ayatNumber=9;
update verses set fr="Ensuite, j’ai fait des proclamations à leur intention, et je leur ai <em>aussi</em> parlé en particulier." where chapterNumber=71 and ayatNumber=10;
update verses set fr="Et j’ai dit : 'Recherchez le pardon de votre Seigneur \; car Il est Extrêmement-Pardonnant." where chapterNumber=71 and ayatNumber=11;
update verses set fr="Il fera descendre pour vous de la pluie en abondance," where chapterNumber=71 and ayatNumber=12;
update verses set fr="Et Il accroîtra vos richesses et le nombre de vos enfants, et Il vous donnera des jardins, et Il vous donnera des rivières. »" where chapterNumber=71 and ayatNumber=13;
update verses set fr="Qu’avez-vous, que vous n’attribuez pas de la dignité à Allāh ?" where chapterNumber=71 and ayatNumber=14;
update verses set fr="Et Il vous a certainement créés par étapes." where chapterNumber=71 and ayatNumber=15;
update verses set fr="N’avez-vous pas vu comment Allāh a créé sept cieux superposés," where chapterNumber=71 and ayatNumber=16;
update verses set fr="Et y a placé la lune comme une lumière, et a fait le soleil comme une lampe ?" where chapterNumber=71 and ayatNumber=17;
update verses set fr="Et Allāh vous a fait croître de la terre comme <em>croît</em> la végétation." where chapterNumber=71 and ayatNumber=18;
update verses set fr="Ensuite Il vous y fera retourner, et Il vous <em>en</em> fera sortir d’une façon spéciale." where chapterNumber=71 and ayatNumber=19;
update verses set fr="Et pour vous, Allāh a fait de la terre une vaste étendue" where chapterNumber=71 and ayatNumber=20;
update verses set fr="Afin que vous puissiez parcourir ses voies spacieuses. «" where chapterNumber=71 and ayatNumber=21;
update verses set fr="Noé dit : « Mon Seigneur, certainement ils m’ont désobéi, et ils ont suivi quelqu’un dont les biens et les enfants n’ont fait qu’ajouter à <em>sa</em> perte." where chapterNumber=71 and ayatNumber=22;
update verses set fr="Et ils ont tramé un grand complot." where chapterNumber=71 and ayatNumber=23;
update verses set fr="Et ils <em>se</em> disent <em>les uns aux autres</em> : 'N’abandonnez <em>en aucune circonstance</em> vos dieux. Et n’abandonnez ni Waḍd, ni Souwā’, ni Ya‘ghōūth, ni Ya'ōūq, ni Nasr.'" where chapterNumber=71 and ayatNumber=24;
update verses set fr="Et ils ont égaré bien des hommes, aussi ne fais croître les injustes qu’en égarement. »" where chapterNumber=71 and ayatNumber=25;
update verses set fr="A cause de leurs péchés ils furent noyés et on les fit entrer dans un Feu. Et ils ne trouvèrent pas pour eux-mêmes d’aides contre Allāh." where chapterNumber=71 and ayatNumber=26;
update verses set fr="Et Noé dit : « Mon Seigneur, ne laisse sur la terre aucun habitant qui soit au nombre des mécréants \;" where chapterNumber=71 and ayatNumber=27;
update verses set fr="En vérité, si Tu les laissais, ils <em>ne</em> feraient <em>qu</em> ’égarer Tes serviteurs, et n’engendreraient que des pécheurs <em>et</em> des ingrats." where chapterNumber=71 and ayatNumber=28;
update verses set fr="Mon Seigneur ! pardonne-moi et <em>pardonne</em> à mes parents, et à celui qui entre dans ma maison comme un croyant, et <em>pardonne</em> aux croyants et aux croyantes \; et ne fais croître les injustes que dans la perdition. » " where chapterNumber=71 and ayatNumber=29;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=72 and ayatNumber=1;
update verses set fr="Dis : « Il m’a été révélé qu’un groupe de djinns écoutèrent avec attention, ensuite ils dirent : 'En vérité, nous avons entendu un Coran qui est merveilleux," where chapterNumber=72 and ayatNumber=2;
update verses set fr="Il guide vers la droiture \; alors nous y avons cru, et nous n’associerons personne à notre Seigneur." where chapterNumber=72 and ayatNumber=3;
update verses set fr="Et <em>nous croyons</em> que la majesté de notre Seigneur est fort exaltée. Il ne S’est pris ni épouse ni fils." where chapterNumber=72 and ayatNumber=4;
update verses set fr="Et c’est vrai qu’un imbécile parmi nous proférait des mensonges outranciers sur Allāh." where chapterNumber=72 and ayatNumber=5;
update verses set fr="Et nous avions pensé que les hommes et les djinns ne proféreraient jamais de mensonge sur Allāh." where chapterNumber=72 and ayatNumber=6;
update verses set fr="Et en vérité, il y avait des hommes parmi les gens ordinaires qui cherchaient la protection de certains hommes parmi ceux considérés comme des grands, et ils ont ainsi fait ces derniers persister davantage dans leurs mauvaises actions et <em>leur</em> imbécillité," where chapterNumber=72 and ayatNumber=7;
update verses set fr="Et, en vérité, ils pensaient, tout comme vous le pensez, qu’Allāh ne susciterait jamais quelqu’un <em>en tant que Messager</em>." where chapterNumber=72 and ayatNumber=8;
update verses set fr="Et, en vérité, nous avons cherché à atteindre le ciel, mais nous l’avons trouvé rempli de gardes solides et de flammes de feu," where chapterNumber=72 and ayatNumber=9;
update verses set fr="Et, en vérité, nous avions l’habitude de nous asseoir dans ses endroits propices à écouter. Mais quiconque écoute maintenant, trouve une flamme de feu en embuscade pour lui." where chapterNumber=72 and ayatNumber=10;
update verses set fr="Nous ne savons pas si le mal est destiné à ceux qui sont sur la terre, ou si leur Seigneur Se propose de <em>bien</em> les guider." where chapterNumber=72 and ayatNumber=11;
update verses set fr="Et certains d’entre nous sont pieux, et certains d’entre nous ne le sont pas \; et nous nous trouvons divisés en sectes, ayant des opinions divergentes." where chapterNumber=72 and ayatNumber=12;
update verses set fr="Et nous savons que nous ne pouvons faire échouer <em>les desseins d’</em>Allāh sur la terre et que nous ne pouvons Le tenir en échec en nous enfuyant \;" where chapterNumber=72 and ayatNumber=13;
update verses set fr="Et lorsque nous avons entendu <em>l’appel à</em> la direction, nous y avons cru. Et celui qui croit en son Seigneur ne craint aucune perte ni aucun tort." where chapterNumber=72 and ayatNumber=14;
update verses set fr="Et certains d’entre nous se soumettent <em>à Allāh</em>, et certains d’entre nous sont injustes.' « Et ceux qui se soumettent <em>à Allāh</em> – ceux-là qui cherchent la bonne voie," where chapterNumber=72 and ayatNumber=15;
update verses set fr="Et quant aux injustes, ils sont le combustible de l’Enfer." where chapterNumber=72 and ayatNumber=16;
update verses set fr="Et s’ils se maintiennent sur le <em>droit</em> chemin, Nous leur donnerons certainement de l’eau à boire en abondance," where chapterNumber=72 and ayatNumber=17;
update verses set fr="Pour que Nous les éprouvions par là. Et quiconque se détourne du souvenir de son Seigneur – Il le poussera dans un châtiment sans cesse croissant." where chapterNumber=72 and ayatNumber=18;
update verses set fr="Et, en vérité, tous les lieux d’adoration appartiennent à Allāh \; n’invoquez donc personne en dehors d’Allāh." where chapterNumber=72 and ayatNumber=19;
update verses set fr="Et lorsque le Serviteur d’Allāh se tient debout L’adorant, ils se ruent sur lui, l’étouffant presque." where chapterNumber=72 and ayatNumber=20;
update verses set fr="Dis : « J’invoque mon Seigneur seulement, et je ne Lui associe personne. »" where chapterNumber=72 and ayatNumber=21;
update verses set fr="Dis : « En vérité je n’ai aucun pouvoir de vous faire du mal ou du bien.’’" where chapterNumber=72 and ayatNumber=22;
update verses set fr="Dis : « Assurément, nul ne peut me protéger contre Allāh, et je ne puis non plus trouver de refuge en dehors de Lui." where chapterNumber=72 and ayatNumber=23;
update verses set fr="Sauf <em>je peux</em> transmettre <em>la révélation qui vient</em> de la part d’Allāh, ainsi que Ses Messages. » Et pour ceux qui désobéissent à Allāh et à Son Messager, il y a le Feu de l’Enfer, dans lequel ils demeureront longtemps." where chapterNumber=72 and ayatNumber=24;
update verses set fr="<em>Ils persisteront à ne pas croire</em> jusqu’à ce qu’ils voient ce qui leur est promis, et bientôt ils sauront qui sont les plus faibles en alliés et les moindres en nombres." where chapterNumber=72 and ayatNumber=25;
update verses set fr="Dis : « Je ne sais pas si ce qui vous est promis est proche, ou si mon Seigneur a fixé pour cela un terme éloigné. »" where chapterNumber=72 and ayatNumber=26;
update verses set fr="<em>Il est le</em> Connaisseur de l’invisible \; et Il n’accorde à personne la prédominance sur Son<em> domaine de l’</em>invisible," where chapterNumber=72 and ayatNumber=27;
update verses set fr="Sauf à celui qu’Il choisit en tant que <em>Son</em> Messager. Et Il le fait précéder et suivre d’une escorte <em>d’anges,</em> " where chapterNumber=72 and ayatNumber=28;
update verses set fr="Afin qu’Il sache que <em>Ses Messagers</em> ont transmis les Messages de leur Seigneur. Et Il embrasse <em>tout</em> ce qui est avec eux et Il tient le compte de toutes choses." where chapterNumber=72 and ayatNumber=29;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=73 and ayatNumber=1;
update verses set fr="Ô toi qui <em>t’</em>es enveloppé dans <em>ta</em> robe !" where chapterNumber=73 and ayatNumber=2;
update verses set fr="Tiens-toi debout <em>en Prière</em> la nuit, à l’exception d’une petite partie –" where chapterNumber=73 and ayatNumber=3;
update verses set fr="Sa moitié, ou réduis-en un<em> petit</em> peu," where chapterNumber=73 and ayatNumber=4;
update verses set fr="Ou ajoutes-y <em>un petit peu</em> – et récite le Coran lentement et clairement." where chapterNumber=73 and ayatNumber=5;
update verses set fr="En vérité, Nous allons te charger d’une lourde Parole." where chapterNumber=73 and ayatNumber=6;
update verses set fr="En vérité, se lever la nuit <em>pour prier</em> est le moyen le plus efficace pour maîtriser <em>le moi</em>, et <em>le moyen</em> pour rendre la parole plus ferme." where chapterNumber=73 and ayatNumber=7;
update verses set fr="En vérité, tu as pendant la journée une longue série d’obligations." where chapterNumber=73 and ayatNumber=8;
update verses set fr="Aussi souviens-toi du nom de ton Seigneur, et dévoue-toi à Lui d’un dévouement total." where chapterNumber=73 and ayatNumber=9;
update verses set fr="<em>Il est</em> le Seigneur de l’Orient et de l’Occident \; il n’y a de dieu que Lui \; prends-Le donc pour <em>ton</em> Protecteur." where chapterNumber=73 and ayatNumber=10;
update verses set fr="Et endure avec patience tout ce qu’ils disent \; et quitte-les d’une manière convenable." where chapterNumber=73 and ayatNumber=11;
update verses set fr="Et laisse-Moi Seul avec ceux qui rejettent <em>la vérité</em>, possesseurs de l’aisance et du confort \; et donne-leur un peu de répit." where chapterNumber=73 and ayatNumber=12;
update verses set fr="Assurément, avec Nous il y a des chaînes <em>lourdes</em> et une Fournaise," where chapterNumber=73 and ayatNumber=13;
update verses set fr="Et de la nourriture qui étouffe, et un châtiment douloureux –" where chapterNumber=73 and ayatNumber=14;
update verses set fr="Le jour où la terre et les montagnes trembleront, et où les montagnes deviendront comme des dunes qui s’effondrent." where chapterNumber=73 and ayatNumber=15;
update verses set fr="En vérité, Nous vous avons envoyé un Messager qui est un témoin sur vous, tout comme Nous avions envoyé un Messager à Pharaon \;" where chapterNumber=73 and ayatNumber=16;
update verses set fr="Mais Pharaon désobéit au Messager \; alors Nous l’avons saisi d’une étreinte terrible." where chapterNumber=73 and ayatNumber=17;
update verses set fr="Comment donc, si vous êtes mécréants vous garderez-vous contre un jour qui fera grisonner les enfants ?" where chapterNumber=73 and ayatNumber=18;
update verses set fr="<em>Et</em> qui fera que le ciel se fende en morceaux \; Sa promesse sera accomplie." where chapterNumber=73 and ayatNumber=19;
update verses set fr="Ceci, assurément, est un Rappel. Alors, que celui qui le veut, prenne une voie vers son Seigneur." where chapterNumber=73 and ayatNumber=20;
update verses set fr="Assurément, ton Seigneur sait que tu restes debout <em>enprière</em> pendant presque les deux tiers de la nuit, et <em>parfois pendant</em> la moitié ou le tiers, ainsi qu’une partie de ceux qui sont avec toi. Et Allāh décide de la durée de la nuit et du jour. Il sait que vous ne pouvez observer cela<em> assidûment de cette façon</em> \; c’est pourquoi Il S’est tourné vers vous avec clémence. Récitez donc ce qui <em>vous</em> est possible du Coran. Il sait qu’il y aura certains parmi vous qui pourraient être malades, et d’autres qui pourraient être en voyage dans le pays à la recherche de la munificence d’Allāh, et d’autres qui pourraient être appelés à combattre pour la cause d’Allāh. Aussi, récitez-en ce qui est possible <em>pour vous</em> et observez la Prière et payez la Zakāt, et prêtez à Allāh un bon prêt. Et quelque bien que vous envoyiez devant vous pour votre âme, vous le retrouverez auprès d’Allāh. Il sera meilleur et plus grand en récompense. Et implorez le pardon d’Allāh. En vérité, Allāh est Très-Pardonnant, Miséricordieux." where chapterNumber=73 and ayatNumber=21;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=74 and ayatNumber=1;
update verses set fr="Ô toi qui t’es enveloppé dans<em> ton</em> manteau !" where chapterNumber=74 and ayatNumber=2;
update verses set fr="Lève-toi et avertis," where chapterNumber=74 and ayatNumber=3;
update verses set fr="Et <em>quant à</em> ton Seigneur, célèbre <em>Sa</em> grandeur," where chapterNumber=74 and ayatNumber=4;
update verses set fr="Et <em>quant à</em> tes vêtements, purifie-<em>les</em>," where chapterNumber=74 and ayatNumber=5;
update verses set fr="Et éloigne-toi de toute impureté," where chapterNumber=74 and ayatNumber=6;
update verses set fr="Et n’accorde pas de faveurs en cherchant d’en avoir plus <em>en retour</em> \;" where chapterNumber=74 and ayatNumber=7;
update verses set fr="Et pour<em> l’amour de</em> ton Seigneur endure patiemment." where chapterNumber=74 and ayatNumber=8;
update verses set fr="Et lorsque l’on sonnera du clairon," where chapterNumber=74 and ayatNumber=9;
update verses set fr="Alors, ce jour-là sera un jour difficile \;" where chapterNumber=74 and ayatNumber=10;
update verses set fr="Pour les mécréants ce ne sera pas facile." where chapterNumber=74 and ayatNumber=11;
update verses set fr="Laisse-Moi Seul avec celui que J’ai créé." where chapterNumber=74 and ayatNumber=12;
update verses set fr="Et Je lui ai donné des biens en abondance," where chapterNumber=74 and ayatNumber=13;
update verses set fr="Et des enfants – garçons et filles – demeurant en sa présence," where chapterNumber=74 and ayatNumber=14;
update verses set fr="Et J’ai fait <em>la terre</em> un excellent berceau pour son soutien <em>en vue de son développement</em>." where chapterNumber=74 and ayatNumber=15;
update verses set fr="Cependant il désire que Je <em>lui en</em> donne davantage." where chapterNumber=74 and ayatNumber=16;
update verses set fr="Non certes ! car il a été hostile à Nos Signes." where chapterNumber=74 and ayatNumber=17;
update verses set fr="Je lui infligerai bientôt une épreuve accablante." where chapterNumber=74 and ayatNumber=18;
update verses set fr="Voilà ! il a réfléchi et a calculé !" where chapterNumber=74 and ayatNumber=19;
update verses set fr="Que la ruine le saisisse ! Comme il a calculé !" where chapterNumber=74 and ayatNumber=20;
update verses set fr="Que la ruine le saisisse encore une fois ! Comme il a calculé !" where chapterNumber=74 and ayatNumber=21;
update verses set fr="Puis il a regardé <em>autour de lui</em>," where chapterNumber=74 and ayatNumber=22;
update verses set fr="Puis il s’est renfrogné, et il a grimacé avec mépris," where chapterNumber=74 and ayatNumber=23;
update verses set fr="Puis il a tourné le dos et a été dédaigneux," where chapterNumber=74 and ayatNumber=24;
update verses set fr="Et il a dit : « Ceci n’est que <em>de la</em> magie héritée <em>du passé</em> \;" where chapterNumber=74 and ayatNumber=25;
update verses set fr="Ceci n’est que la parole d’un humain. »" where chapterNumber=74 and ayatNumber=26;
update verses set fr="Bientôt Je le jetterai dans le « Saqar »." where chapterNumber=74 and ayatNumber=27;
update verses set fr="Et qui t’informera du « Saqar » ?" where chapterNumber=74 and ayatNumber=28;
update verses set fr="Il n’épargne rien et il ne laisse rien." where chapterNumber=74 and ayatNumber=29;
update verses set fr="Il roussit la peau." where chapterNumber=74 and ayatNumber=30;
update verses set fr="Au-dessus,il y a dix-neuf <em>anges</em>." where chapterNumber=74 and ayatNumber=31;
update verses set fr="Et Nous n’avons désigné personne <em>d’autre</em> que des anges pour être les gardiens du Feu. Et Nous n’avons fixé leur nombre que pour l’épreuve des mécréants, afin que ceux à qui le Livre a été donné puissent atteindre la certitude, et que les croyants puissent augmenter <em>leur</em> foi, et que ceux à qui le Livre a été donné ainsi que les croyants ne doutent pas, et que ceux dans le cœur de qui il y a une maladie, et les mécréants, puissent dire : « Qu’est-ce qu’Allāh entend par cet exemple ? » C’est ainsi qu’Allāh juge égaré qui Il veut, et guide qui Il veut. Et personne ne connaît les armées de ton Seigneur sauf Lui- <em>Même</em>. Et ceci n’est qu’un Rappel pour l’homme." where chapterNumber=74 and ayatNumber=32;
update verses set fr="Non mais, par la lune," where chapterNumber=74 and ayatNumber=33;
update verses set fr="Et <em>par</em> la nuit lorsqu’elle se retire," where chapterNumber=74 and ayatNumber=34;
update verses set fr="Et <em>par</em> l’aube lorsqu’elle pointe," where chapterNumber=74 and ayatNumber=35;
update verses set fr="En vérité, c’est une des plus grandes <em>calamités</em>." where chapterNumber=74 and ayatNumber=36;
update verses set fr="Un avertissement à l’homme," where chapterNumber=74 and ayatNumber=37;
update verses set fr="A celui d’entre vous qui veut avancer ou rester en arrière." where chapterNumber=74 and ayatNumber=38;
update verses set fr="Chaque âme est un gage pour ce qu’elle a acquis \;" where chapterNumber=74 and ayatNumber=39;
update verses set fr="Sauf ceux du côté droit." where chapterNumber=74 and ayatNumber=40;
update verses set fr="<em>Ils seront</em> dans des Jardins, s’interrogeant" where chapterNumber=74 and ayatNumber=41;
update verses set fr="Sur les coupables," where chapterNumber=74 and ayatNumber=42;
update verses set fr="« Qu’est-ce qui vous a fait entrer dans <em>le</em> ‘Saqar’ ? »" where chapterNumber=74 and ayatNumber=43;
update verses set fr="Ils diront : « Nous n’étions pas de ceux qui faisaient la Prière," where chapterNumber=74 and ayatNumber=44;
update verses set fr="Et nous ne nourrissions pas les pauvres," where chapterNumber=74 and ayatNumber=45;
update verses set fr="Et nous nous complaisions dans de vains discours avec ceux qui s’y complaisent." where chapterNumber=74 and ayatNumber=46;
update verses set fr="Et nous niions le Jour du Jugement," where chapterNumber=74 and ayatNumber=47;
update verses set fr="Jusqu’à ce que la mort nous frappât. »" where chapterNumber=74 and ayatNumber=48;
update verses set fr="Aussi l’intercession des intercesseurs ne leur sera d’aucun profit." where chapterNumber=74 and ayatNumber=49;
update verses set fr="Qu’est-ce qu’il leur arrive pour qu’ils se détournent de l’Exhortation," where chapterNumber=74 and ayatNumber=50;
update verses set fr="Comme s’ils étaient des ânes apeurés," where chapterNumber=74 and ayatNumber=51;
update verses set fr="Fuyant devant un lion ?" where chapterNumber=74 and ayatNumber=52;
update verses set fr="<em>Non</em>, mais chacun d’entre eux désire que des pages ouvertes <em>de révélation</em> lui soient accordées." where chapterNumber=74 and ayatNumber=53;
update verses set fr="Jamais de la vie ! En vérité, ils ne craignent pas l’au-delà." where chapterNumber=74 and ayatNumber=54;
update verses set fr="Jamais de la vie ! En vérité, ceci est une exhortation." where chapterNumber=74 and ayatNumber=55;
update verses set fr="Alors, que celui qui le veut, s’en souvienne." where chapterNumber=74 and ayatNumber=56;
update verses set fr="Et ils ne s<em>’en</em> souviendront pas, à moins qu’Allāh ne le veuille. Lui <em>Seul</em> est le Maître de la droiture et le Maître du pardon." where chapterNumber=74 and ayatNumber=57;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=75 and ayatNumber=1;
update verses set fr="<em>Non</em>, mais ! Je cite comme témoin le Jour de la Résurrection." where chapterNumber=75 and ayatNumber=2;
update verses set fr="Et je cite comme témoin la conscience qui s’accuse sans cesse." where chapterNumber=75 and ayatNumber=3;
update verses set fr="Est-ce que l’homme s’imagine que Nous ne rassemblerons pas ses os ?" where chapterNumber=75 and ayatNumber=4;
update verses set fr="Mais si, <em>en vérité</em> Nous sommes capable de reconstituer même les extrémités de ses doigts." where chapterNumber=75 and ayatNumber=5;
update verses set fr="Mais l’homme désire continuer à pécher au fur et à mesure qu’il avance." where chapterNumber=75 and ayatNumber=6;
update verses set fr="Il demande : « Quand sera le Jour de la Résurrection ? »" where chapterNumber=75 and ayatNumber=7;
update verses set fr="Quand l’oeil sera ébloui," where chapterNumber=75 and ayatNumber=8;
update verses set fr="Et la lune sera éclipsée," where chapterNumber=75 and ayatNumber=9;
update verses set fr="Et le soleil et la lune seront réunis ensemble," where chapterNumber=75 and ayatNumber=10;
update verses set fr="Ce jour-là l’homme dira : « Où fuir ? »" where chapterNumber=75 and ayatNumber=11;
update verses set fr="Point du tout ! Aucun refuge !" where chapterNumber=75 and ayatNumber=12;
update verses set fr="Avec ton Seigneur <em>Seul</em> sera le lieu de repos ce jour-là." where chapterNumber=75 and ayatNumber=13;
update verses set fr="Ce jour-là l’homme sera informé de ce qu’il aura envoyé en avant et laissé en arrière." where chapterNumber=75 and ayatNumber=14;
update verses set fr="<em>Non</em>, mais l’homme est un témoin très perspicace contre lui-même," where chapterNumber=75 and ayatNumber=15;
update verses set fr="Même s’il présente ses excuses." where chapterNumber=75 and ayatNumber=16;
update verses set fr="Ne remue pas ta langue avec <em>cette révélation</em> afin de te hâter pour la<em> préserver</em>." where chapterNumber=75 and ayatNumber=17;
update verses set fr="Assurément, c’est à Nous qu’incombent son recueil et sa récitation." where chapterNumber=75 and ayatNumber=18;
update verses set fr="Alors, quand Nous le récitons, suis sa récitation." where chapterNumber=75 and ayatNumber=19;
update verses set fr="Ensuite c’est à Nous qu’incombe son interprétation." where chapterNumber=75 and ayatNumber=20;
update verses set fr="<em>Non</em>, mais vous aimez ce qui est passager \;" where chapterNumber=75 and ayatNumber=21;
update verses set fr="Et vous délaissez l’au-delà." where chapterNumber=75 and ayatNumber=22;
update verses set fr="Ce jour-là il y aura des visages resplendissants," where chapterNumber=75 and ayatNumber=23;
update verses set fr="Regardant <em>ardemment</em> vers leur Seigneur \;" where chapterNumber=75 and ayatNumber=24;
update verses set fr="Et ce jour-là il y aura des visages attristés," where chapterNumber=75 and ayatNumber=25;
update verses set fr="Etant sûrs qu’une calamité écrasante va leur arriver." where chapterNumber=75 and ayatNumber=26;
update verses set fr="Oui, <em>en vérité</em>, quand <em>l’âme du mourant</em> monte jusqu’à <em>sa</em> gorge," where chapterNumber=75 and ayatNumber=27;
update verses set fr="Et il est dit : « Quel est l’enchanteur <em>qui peut le sauver</em> ? »" where chapterNumber=75 and ayatNumber=28;
update verses set fr="Et il est sûr que c’est <em>l’heure de</em> la séparation \;" where chapterNumber=75 and ayatNumber=29;
update verses set fr="Et un tibia pressera l’autre tibia <em>en agonie \;</em>" where chapterNumber=75 and ayatNumber=30;
update verses set fr="Et c’est vers ton Seigneur ce jour-là, qu’il sera poussé." where chapterNumber=75 and ayatNumber=31;
update verses set fr="Car il n’accepta pas <em>la Vérité</em> ni disait <em>sa</em> Prière \;" where chapterNumber=75 and ayatNumber=32;
update verses set fr="Mais il rejeta <em>la Vérité</em> et tourna le dos \;" where chapterNumber=75 and ayatNumber=33;
update verses set fr="Ensuite il se rendit chez les siens, tout en se pavanant." where chapterNumber=75 and ayatNumber=34;
update verses set fr="« Malheur à toi ! et malheur encore !" where chapterNumber=75 and ayatNumber=35;
update verses set fr="Et encore, malheur à toi ! et malheur encore ! »" where chapterNumber=75 and ayatNumber=36;
update verses set fr="L’homme s’imagine-t-il qu’on le laissera sans contrôle ?" where chapterNumber=75 and ayatNumber=37;
update verses set fr="N’était-il pas une goutte de sperme émise ?" where chapterNumber=75 and ayatNumber=38;
update verses set fr="Ensuite il devint un caillot adhérent, ensuite Il <em>le</em> façonna et <em>le</em> rendit parfait." where chapterNumber=75 and ayatNumber=39;
update verses set fr="Ensuite Il fit de lui un couple, le mâle et la femelle." where chapterNumber=75 and ayatNumber=40;
