update verses set fr="Ils entreront dans un Feu brûlant \;" where chapterNumber=88 and ayatNumber=5;
update verses set fr="On leur fera boire d’une source bouillante \;" where chapterNumber=88 and ayatNumber=6;
update verses set fr="Ils n’auront aucune nourriture autre que celle consistant d’une herbe sèche, amère et épineuse," where chapterNumber=88 and ayatNumber=7;
update verses set fr="Qui ni ne fera grossir, ni ne satisfera la faim." where chapterNumber=88 and ayatNumber=8;
update verses set fr="Ce jour-là, des visages seront joyeux," where chapterNumber=88 and ayatNumber=9;
update verses set fr="Satisfaits de leurs efforts," where chapterNumber=88 and ayatNumber=10;
update verses set fr="Dans un Jardin élevé," where chapterNumber=88 and ayatNumber=11;
update verses set fr="Où tu n’entendras aucun vain propos \;" where chapterNumber=88 and ayatNumber=12;
update verses set fr="Il s’y trouve une source ruisselante," where chapterNumber=88 and ayatNumber=13;
update verses set fr="Il s’y trouve des canapés élevés," where chapterNumber=88 and ayatNumber=14;
update verses set fr="Et des gobelets posés <em>avec art</em>," where chapterNumber=88 and ayatNumber=15;
update verses set fr="Et des coussins <em>agréablement</em> rangés," where chapterNumber=88 and ayatNumber=16;
update verses set fr="Et des tapis étalés <em>avec goût</em>." where chapterNumber=88 and ayatNumber=17;
update verses set fr="Ne regardent-ils donc pas les chameaux, comment ils sont créés ?" where chapterNumber=88 and ayatNumber=18;
update verses set fr="Et le ciel, comme il est élevé <em>haut</em> ?" where chapterNumber=88 and ayatNumber=19;
update verses set fr="Et les montagnes, comme elles sont enforcées solidement <em>dans la terre</em> ?" where chapterNumber=88 and ayatNumber=20;
update verses set fr="Et la terre, comme elle est étendue ?" where chapterNumber=88 and ayatNumber=21;
update verses set fr="Donc fais entendre le Rappel, car tu n’es qu’un Rappelleur \;" where chapterNumber=88 and ayatNumber=22;
update verses set fr="Tu n’a pas été chargé de les surveiller." where chapterNumber=88 and ayatNumber=23;
update verses set fr="Mais quiconque se détourne et refuse de croire," where chapterNumber=88 and ayatNumber=24;
update verses set fr="Allāh le châtiera du plus grand châtiment." where chapterNumber=88 and ayatNumber=25;
update verses set fr="En vérité, auprès de Nous est leur retour," where chapterNumber=88 and ayatNumber=26;
update verses set fr="Alors, assurément, c’est à Nous qu’il incombe <em>de régler</em> leurs comptes." where chapterNumber=88 and ayatNumber=27;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=89 and ayatNumber=1;
