update verses set fr="Et <em>par</em> les Dix Nuits," where chapterNumber=89 and ayatNumber=3;
update verses set fr="Et <em>par</em> le Pair et l’Impair," where chapterNumber=89 and ayatNumber=4;
update verses set fr="Et <em>par</em> la Nuit lorsqu’elle s'écoule," where chapterNumber=89 and ayatNumber=5;
update verses set fr="Ne s’y trouve-t-il pas une preuve solide pour un homme doué d’intelligence ?" where chapterNumber=89 and ayatNumber=6;
