update verses set fr="Qui transgressèrent dans les pays," where chapterNumber=89 and ayatNumber=12;
update verses set fr="Et y commirent beaucoup d’iniquités." where chapterNumber=89 and ayatNumber=13;
update verses set fr="Alors ton Seigneur fit tomber sur eux <em>le</em> fouet du châtiment." where chapterNumber=89 and ayatNumber=14;
update verses set fr="Assurément, ton Seigneur veille," where chapterNumber=89 and ayatNumber=15;
update verses set fr="Quant à l’homme, lorsque son Seigneur l’éprouve et l’honore et lui accorde des faveurs, il dit : « Mon Seigneur m’a honoré. »" where chapterNumber=89 and ayatNumber=16;
update verses set fr="Mais lorsqu’Il l’éprouve et lui restreigne sa provision, il dit : « Mon Seigneur m’a humilié. »" where chapterNumber=89 and ayatNumber=17;
update verses set fr="Non, mais vous n’honorez pas l’orphelin," where chapterNumber=89 and ayatNumber=18;
update verses set fr="Et vous ne vous exhortez pas les uns les autres à nourrir les pauvres," where chapterNumber=89 and ayatNumber=19;
update verses set fr="Et vous dévorez complètement l’héritage <em>d’autrui</em>," where chapterNumber=89 and ayatNumber=20;
update verses set fr="Et vous aimez les richesses d’un amour excessif." where chapterNumber=89 and ayatNumber=21;
update verses set fr="Non, mais lorsque la terre sera complètement broyée \;" where chapterNumber=89 and ayatNumber=22;
update verses set fr="Et que ton Seigneur viendra ainsi que les anges, rang par rang \;" where chapterNumber=89 and ayatNumber=23;
update verses set fr="Et que ce jour-là, on amènera l’Enfer, ce jour-là l’homme se souviendra, mais de quel secours sera son souvenir ?" where chapterNumber=89 and ayatNumber=24;
update verses set fr="Il dira : « Hélas si seulement j’avais envoyé de l’avant <em>quelques bonnes œuvres</em> pour ma vie <em>d’ici</em> ! »" where chapterNumber=89 and ayatNumber=25;
update verses set fr="Aussi, ce jour-là, nul ne peut châtier comme Son châtiment," where chapterNumber=89 and ayatNumber=26;
update verses set fr="Et nul ne peut garrotter comme Il le fait." where chapterNumber=89 and ayatNumber=27;
update verses set fr="Ô âme en paix !" where chapterNumber=89 and ayatNumber=28;
update verses set fr="Retourne auprès de ton Seigneur, satisfaite <em>de Lui et</em> Lui satisfait <em>de toi</em>." where chapterNumber=89 and ayatNumber=29;
update verses set fr="Entre donc parmi Mes serviteurs," where chapterNumber=89 and ayatNumber=30;
update verses set fr="Et entre dans Mon Jardin." where chapterNumber=89 and ayatNumber=31;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=90 and ayatNumber=1;
update verses set fr="<em>Non</em>, mais Je prends à témoin cette cité –" where chapterNumber=90 and ayatNumber=2;
update verses set fr="Alors <em>qu’un jour,</em> tu vas descendre dans cette cité –" where chapterNumber=90 and ayatNumber=3;
update verses set fr="Et <em>Je prends à témoin</em> l’engendreur et l’engendré," where chapterNumber=90 and ayatNumber=4;
update verses set fr="Nous avons assurément créé l’homme pour mener une vie de dur labeur," where chapterNumber=90 and ayatNumber=5;
update verses set fr="S’imagine-t-il que personne n’a de pouvoir sur lui ?" where chapterNumber=90 and ayatNumber=6;
update verses set fr="Il dit : « J’ai gaspillé d'immenses richesses. »" where chapterNumber=90 and ayatNumber=7;
update verses set fr="S’imagine-t-il que personne ne le voit ?" where chapterNumber=90 and ayatNumber=8;
update verses set fr="Ne lui avons-Nous pas donné deux yeux," where chapterNumber=90 and ayatNumber=9;
update verses set fr="Et une langue et deux lèvres ?" where chapterNumber=90 and ayatNumber=10;
update verses set fr="Et Nous lui avons indiqué les deux grandes voies de l'Ascension." where chapterNumber=90 and ayatNumber=11;
update verses set fr="Mais il ne s’est pas engagé avec rigueur dans Al-̒Aqabah." where chapterNumber=90 and ayatNumber=12;
update verses set fr="Et qu’est-ce qui te ferait savoir ce qu’est Al-̒Aqabah ?" where chapterNumber=90 and ayatNumber=13;
update verses set fr="<em>C’est</em> la remise en liberté d’un esclave," where chapterNumber=90 and ayatNumber=14;
update verses set fr="Ou, par un jour de famine, de nourrir" where chapterNumber=90 and ayatNumber=15;
update verses set fr="Un orphelin qui est proche parent," where chapterNumber=90 and ayatNumber=16;
update verses set fr="Ou un indigent gisant dans la poussière." where chapterNumber=90 and ayatNumber=17;
update verses set fr="De plus, il aurait dû être de ceux qui croient et s’exhortent les uns les autres à la persévérance, et s’exhortent les uns les autres à la miséricorde." where chapterNumber=90 and ayatNumber=18;
update verses set fr="Ceux-là sont les gens du côté droit." where chapterNumber=90 and ayatNumber=19;
update verses set fr="Mais ceux qui refusent de croire à Nos Signes, ils sont les gens du côté gauche." where chapterNumber=90 and ayatNumber=20;
update verses set fr="Un feu renfermé les couvrira." where chapterNumber=90 and ayatNumber=21;
update verses set fr="Au nom d’Allāh, le Gracieux, le Miséricordieux." where chapterNumber=91 and ayatNumber=1;
update verses set fr="Par le soleil et<em> par</em> le moment où il se met à rayonner," where chapterNumber=91 and ayatNumber=2;
update verses set fr="Et <em>par</em> la lune lorsqu’elle le suit," where chapterNumber=91 and ayatNumber=3;
update verses set fr="Et <em>par</em> le jour lorsqu’il révèle la gloire <em>du soleil</em>," where chapterNumber=91 and ayatNumber=4;
update verses set fr="Et <em>par</em> la nuit lorsqu’elle tire un voile sur lui," where chapterNumber=91 and ayatNumber=5;
update verses set fr="Et <em>par</em> le ciel et la façon dont Il l’a créé !" where chapterNumber=91 and ayatNumber=6;
update verses set fr="Et <em>par</em> la terre et la façon dont Il l’a étendue," where chapterNumber=91 and ayatNumber=7;
update verses set fr="Et <em>par</em> l’âme et comment Il l’a façonnée !" where chapterNumber=91 and ayatNumber=8;
