update verses set fr="Et à Nous appartient l’au-delà aussi bien que ce monde," where chapterNumber=92 and ayatNumber=14;
update verses set fr="Je vous avertis donc d’un Feu flambant." where chapterNumber=92 and ayatNumber=15;
update verses set fr="Nul n’y entrera excepté le plus malchanceux <em>de par sa dépravation</em>," where chapterNumber=92 and ayatNumber=16;
