
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) بار بار رحم کرنے والا ہے (پڑھتا ہوں) ۔" WHERE ayatNumber = 1 AND chapterNumber = 1;
UPDATE verses SET ts = "ہر (قسم کی) تعریف کا اللہ (ہی) مستحق ہے (جو) تمام جہانوں کا رب (ہے)۔ " WHERE ayatNumber = 2 AND chapterNumber = 1;
UPDATE verses SET ts = "بے حد کرم کرنے والا‘ بار بار رحم کرنے والا۔ " WHERE ayatNumber = 3 AND chapterNumber = 1;
UPDATE verses SET ts = "(اور) جزا سزا کے وقت کا مالک ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 1;
UPDATE verses SET ts = "(اے خدا)! ہم تیری ہی عبادت کرتے ہیں اور تجھ سے ہی مدد مانگتے ہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 1;
UPDATE verses SET ts = "ہمیں سیدھے راستے پر چلا۔ " WHERE ayatNumber = 6 AND chapterNumber = 1;
UPDATE verses SET ts = "ان لوگوں کے راستے پر جن پر تو نے انعام کیا ہے جن پر نہ تو تیرا (بعد) میں غضب نازل ہوا (ہے) اور نہ وہ (بعد میں) گمراہ (ہو گئے) ہیں۔ " WHERE ayatNumber = 7 AND chapterNumber = 1;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں۔) " WHERE ayatNumber = 1 AND chapterNumber = 2;
UPDATE verses SET ts = "میں اللہ سب سے زیادہ جاننے والا ہوں۔ " WHERE ayatNumber = 2 AND chapterNumber = 2;
UPDATE verses SET ts = "یہی کامل کتاب ہے‘ اس (امر) میں کوئی شک نہیں‘ متقیوں کو ہدایت دینے والی ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 2;
UPDATE verses SET ts = "جو غیب پر ایمان لاتے اور نماز کو قائم رکھتے ہیں اور جو (کچھ) ہم نے انہیں دیا ہے اس میں سے خرچ کرتے رہتے ہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 2;
UPDATE verses SET ts = "اور جو تجھ پر نازل کیا گیا ہے یا جو تجھ سے پہلے نازل کیا گیا تھا اس پر ایمان لاتے ہیں اورآئندہ ہونے والی (موعود باتوں) پر( بھی ) یقین رکھتے ہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 2;
UPDATE verses SET ts = "یہ لوگ (یقیناً) اس ہدایت پر (قائم) ہیں جو ان کے رب کی طرف سے (آئی) ہے اور یہی لوگ کامیاب ہونے والے ہیں۔ " WHERE ayatNumber = 6 AND chapterNumber = 2;
UPDATE verses SET ts = "ایسے لوگ جنہوں نے کفر کیا ہے (اور) تیرا ان کو ڈرانا یا نہ ڈرانا ان کے لئے یکساں (اثر پیدا کرتا) ہے (جب تک وہ اس حالت کو نہ بدلیں) ایمان نہیں لائیں گے۔ " WHERE ayatNumber = 7 AND chapterNumber = 2;
UPDATE verses SET ts = "اللہ نے ان کے دلوں پر اور ان کے کانوں پر مہر کر دی ہے اور ان کی آنکھوں پر پردہ (پڑا ہوا) ہے اور ان کے لئے ایک بڑا عذاب (مقدر) ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 2;
UPDATE verses SET ts = "اور بعض لوگ ایسے بھی ہیں جو کہتے ہیں کہ ہم اللہ پر اور آنے والے دن پر ایمان رکھتے ہیں‘ حالانکہ وہ ہرگز ایمان نہیں رکھتے۔ " WHERE ayatNumber = 9 AND chapterNumber = 2;
UPDATE verses SET ts = "وہ اللہ کو اور ان لوگوں کو جو ایمان لائے ہیں دھوکا دینا چاہتے ہیں‘ مگر (واقعہ میں) وہ اپنے سوا کسی کو دھوکہ نہیں دیتے۔ اور وہ سمجھتے نہیں۔ " WHERE ayatNumber = 10 AND chapterNumber = 2;
UPDATE verses SET ts = "ان کے دلوں میں ایک بیماری تھی پھر اللہ نے ان کی بیماری کو (اور بھی) بڑھا دیا اور انہیں ایک دردناک عذاب پہنچ رہا ہے کیونکہ وہ جھوٹ بولا کرتے تھے۔ " WHERE ayatNumber = 11 AND chapterNumber = 2;
UPDATE verses SET ts = "اور جب ان سے کہا جائے (کہ) زمین میں فساد نہ کرو‘ تو کہتے ہیں‘ ہم تو صرف اصلاح کرنے والے ہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 2;
UPDATE verses SET ts = "(کان کھول کر) سنو! یہی لوگ بلاشبہ فساد کرنے والے ہیں‘ مگر وہ (اس حقیقت کو) سمجھتے نہیں۔ " WHERE ayatNumber = 13 AND chapterNumber = 2;
UPDATE verses SET ts = "اور جب انہیں کہا جائے کہ (اسی طرح) ایمان لائو‘ جس طرح (دوسرے) لوگ ایمان لائے ہیں تو کہتے ہیں کہ کیا ہم (اس طرح) ایمان لائیں جس طرح بیوقوف (لوگ) ایمان لائے ہیں یاد رکھو! (یہ جھوٹ بول رہے ہیں) وہ خود ہی بیوقوف ہیں مگر (اس بات کو) جانتے نہیں۔ " WHERE ayatNumber = 14 AND chapterNumber = 2;
UPDATE verses SET ts = "اور جب وہ ان لوگوں سے ملیں جو ایمان لائے ہیں تو کہہ دیتے ہیں کہ ہم تو (اس رسول کو) مانتے ہیں اور جب وہ اپنے سرغنوں سے علیحدگی میں ملیں تو کہہ دیتے ہیں کہ ہم یقیناً تمہارے ساتھ ہیں ہم تو صرف (ان مومنوں سے) ہنسی کر رہے تھے۔ " WHERE ayatNumber = 15 AND chapterNumber = 2;
UPDATE verses SET ts = "اللہ انہیں (ان کی) ہنسی کی سزا دے گا اور انہیں اپنی سرکشیوں میں بہکتے ہوئے چھوڑ دے گا۔ " WHERE ayatNumber = 16 AND chapterNumber = 2;
UPDATE verses SET ts = "یہ وہ لوگ ہیں جنہوں نے ہدایت کو چھوڑ کر گمراہی کو اختیار کر لیا جس کا نتیجہ یہ ہوا کہ نہ تو انہیں دنیوی فائدہ پہنچا اور نہ انہوں نے ہدایت پائی۔ " WHERE ayatNumber = 17 AND chapterNumber = 2;
UPDATE verses SET ts = "ان کی حالت اس شخص کی حالت کی طرح ہے جس نے آگ جلائی پھر جب اس (آگ) نے اس کے ارد گرد (کے علاقہ) کو روشن کر دیا تو اللہ ان کی روشنی کو لے گیا اور اس نے انہیں (قسم قسم کے) اندھیروں میں (اس حال میں) چھوڑ دیا (کہ) وہ (کوئی راہ نجات) نہیں دیکھتے۔ " WHERE ayatNumber = 18 AND chapterNumber = 2;
UPDATE verses SET ts = "وہ بہرے ہیں گونگے ہیں اندھے ہیں پس وہ لوٹیں گے نہیں۔ " WHERE ayatNumber = 19 AND chapterNumber = 2;
UPDATE verses SET ts = "یا (ان کا حال) اس بارش کی طرح ہے جو (گھٹا ٹوپ) بادل سے (برس رہی) ہو (ایسی بارش) جس کے ساتھ (قسم قسم کے) اندھیرے اور گرج اور بجلی ہوتی ہے‘ وہ اپنی انگلیوں کو کڑک کی وجہ سے موت کے ڈر سے کانوں میں ڈال لیتے ہیں حالانکہ اللہ تمام کافروں کو تباہ کرنے والا ہے۔ " WHERE ayatNumber = 20 AND chapterNumber = 2;
UPDATE verses SET ts = "قریب ہے کہ بجلی ان کی بینائی کو اچک کر لے جائے۔ جب بھی وہ ان پر چمکتی ہے تو وہ اس (کی روشنی) میں چلنے لگتے ہیں اور جب ان پر اندھیرا کر دیتی ہے تو کھڑے ہو جاتے ہیں اور اگر اللہ چاہتا تو یقیناً ان کی شنوائی اور ان کی بینائی کو ضائع کر دیتا اللہ ہر (اس) امر پر (جس کا وہ ارادہ کرے) یقیناً پوری طرح قادر ہے۔ " WHERE ayatNumber = 21 AND chapterNumber = 2;
UPDATE verses SET ts = "اے لوگو! اپنے (اس) رب کی عبادت کرو جس نے تمہیں (بھی) اور انہیں (بھی) جو تم سے پہلے گزرے ہیں پیدا کیا ہے تا کہ تم (ہر قسم کی آفات سے) بچو۔ " WHERE ayatNumber = 22 AND chapterNumber = 2;
UPDATE verses SET ts = "(وہی ہے) جس نے تمہارے لئے زمین کو بچھونے اور آسمان کو چھت کے طور پر بنایا ہے اور بادلوں سے پانی اتارا ہے‘ پھر اس (پانی) کے ذریعہ سے میووں کی قسم کا رزق تمہارے لئے نکالا ہے۔ پس تم سمجھتے بوجھتے ہوئے اللہ کے ہمسر نہ بنائو۔ " WHERE ayatNumber = 23 AND chapterNumber = 2;
UPDATE verses SET ts = "اور اگر اس (کلام) کے سبب سے جو ہم نے اپنے بندے پر اتارا ہے تم کسی (قسم کے) شک میں (مبتلا ہو گئے) ہو تو اگر تم سچے ہو تو اس جیسی ایک سورۃ لے آئو اور اپنے غیر اللہ مددگاروں کو (بھی اپنی مدد کے لئے) بلالو۔ " WHERE ayatNumber = 24 AND chapterNumber = 2;
UPDATE verses SET ts = "اور اگر تم نے ایسا نہ کیا اور تم ہرگز (ایسا) نہ کر سکو گے تو اس آگ سے بچو جس کا ایندھن آدمی اور پتھر ہیں۔ وہ کافروں کے لئے تیارکی گئی ہے۔ " WHERE ayatNumber = 25 AND chapterNumber = 2;
UPDATE verses SET ts = "اور تو ان  لوگوں کو جو ایمان لائے ہیں اور انہوں نے نیک عمل کیے ہیں خوشخبری دے کہ ان کے لئے (ایسے) باغ ہیں جن کے نیچے نہریں بہتی ہیں۔ جب بھی ان (باغوں) کے پھل میں سے کچھ رزق انہیں دیا جائے گا‘ وہ کہیں گے یہ تو وہی (رزق) ہے جو ہمیں اس سے پہلے دیا گیا تھا اور ان کے پاس وہ (رزق) ملتا جلتا لایا جائے گا اور ان کے لئے ان (باغوں) میں پاک جوڑے ہونگے اور وہ ان (باغوں) کے اندر (ہمیشہ) بسیں گے۔ " WHERE ayatNumber = 26 AND chapterNumber = 2;
UPDATE verses SET ts = "اللہ ہرگز نہیں رکتا کسی بات کے بیان کرنے سے خواہ وہ مچھر کے برابر ہو یا اس سے بھی بڑھ کر۔ پھر جو لوگ ایمان رکھتے ہیں وہ (تو) جان لیتے ہیں کہ وہ ان کے رب کی طرف سے بالکل حق (بات) ہے اور جو لوگ کافر ہوئے وہ کہتے ہیں کہ (آخر) اللہ کا اس بات کے بیان کرنے سے منشا کیا ہے؟ (اصل بات یہ ہے کہ) وہ بہت (سے لوگوں) کو اس (قرآن) کے ذریعہ سے گمراہ قرار دیتا ہے اور بہت (سے لوگوں) کو اس (قرآن) کے ذریعہ سے ہدایت دیتا ہے اور وہ اس کے ذریعہ سے ان نافرمانوں کے سوا کسی کو گمراہ قرار نہیں دیتا۔ " WHERE ayatNumber = 27 AND chapterNumber = 2;
UPDATE verses SET ts = "جو اللہ کے عہد کو اس کے پختہ کرنے کے بعد توڑ دیتے ہیں اور اس چیز کو جسے ملانے کا اللہ نے حکم دیا ہے کاٹتے ہیں اور زمین میں فساد کرتے ہیں‘ وہی لوگ نقصان اٹھانے والے ہیں۔ " WHERE ayatNumber = 28 AND chapterNumber = 2;
UPDATE verses SET ts = "(اے لوگو)! تم کس طرح اللہ (کی باتوں) کا انکار کرتے ہو؟ حالانکہ تم بے جان تھے۔ پھر اس نے تمہیں جان دار بنایا‘ پھر (ایک دن آئے گا کہ) وہ تمہیں مارے گا پھر تمہیں زندہ کرے گا جس کے بعد تمہیں اس کی طرف لوٹایا جائے گا۔ " WHERE ayatNumber = 29 AND chapterNumber = 2;
UPDATE verses SET ts = "وہ (خدا) وہی (تو) ہے جس نے ان تمام چیزوں کو جو زمین میں ہیں تمہارے (فائدہ کے) لئے پیدا کیا۔ پھر وہ آسمانوں کی طرف متوجہ ہوا اور انہیں مکمل بنا دیا سات آسمان‘ اور وہ ہر ایک بات (کی حقیقت) کو خوب جانتا ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 2;
UPDATE verses SET ts = "اور (اے انسان! تو اس وقت کو یاد کر) جب تیرے رب نے ملائکہ سے کہا کہ میں زمین میں ایک خلیفہ بنانے والا ہوں (اس پر) انہوں نے کہا کہ کیا تو اس میں (ایسے شخص بھی) پیدا کرے گا جو اس میں فساد کریں گے اور خون بہائیں گے اور ہم (تو وہ ہیں جو) تیری حمد کے ساتھ (ساتھ) تیری تسبیح (بھی) کرتے ہیں اور تجھ میں سب بڑائیوں کے پائے جانے کا اقرار کرتے ہیں (اس پر اللہ نے) فرمایا میں یقیناً وہ کچھ جانتا ہوں جو تم نہیں جانتے۔ " WHERE ayatNumber = 31 AND chapterNumber = 2;
UPDATE verses SET ts = "اور (اللہ نے) آدم کو سب نام سکھائے پھر (جن کے وہ نام تھے) ان کو ملائکہ کے سامنے پیش کرکے فرمایا کہ اگر تم درست بات کہہ رہے ہو تو تم مجھے ان کے نام بتائو۔ " WHERE ayatNumber = 32 AND chapterNumber = 2;
UPDATE verses SET ts = "انہوں نے کہا تو بے عیب ہے جو (کچھ) تو نے ہمیں سکھایا ہے اس کے سوا ہمیں کسی قسم کا علم نہیں ہے یقیناً تو ہی کامل علم والا (اور ہر قول و فعل میں) حکمت کو مدنظر رکھنے والا ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 2;
UPDATE verses SET ts = "(اس پر اللہ نے) فرمایا۔ اے آدم! ان فرشتوں کو ان کے نام بتا۔ پھر جب اس (یعنی آدم) نے ان کو ان کے نام بتائے (تو) فرمایا‘ کیا میں نے تمہیں نہیں کہا تھا کہ میں یقیناً آسمانوں اور زمین کی چھپی باتیں جانتا ہوں اور میں (اسے بھی) جانتا ہوں جو تم ظاہر کرتے ہو اور (اسے بھی) جو تم چھپاتے ہو۔ " WHERE ayatNumber = 34 AND chapterNumber = 2;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جب ہم نے فرشتوں سے کہا کہ آدم کی فرمانبرداری کرو۔ اس پر انہوں نے تو فرمانبرداری کی مگر ابلیس (نے نہ کی اس) نے انکار کیا اور تکبر کیا اور وہ (پہلے سے ہی) کافروں میں سے تھا۔ " WHERE ayatNumber = 35 AND chapterNumber = 2;
UPDATE verses SET ts = "اور ہم نے (آدم سے) کہا (کہ) اے آدم! تو اور تیری بیوی جنت میں رہو اور اس میں سے جہاں سے چاہو بافراغت کھائو‘ مگر اس (یعنی فلاں) درخت کے قریب نہ جانا۔ ورنہ تم ظالموں میں سے ہو جائو گے۔ " WHERE ayatNumber = 36 AND chapterNumber = 2;
UPDATE verses SET ts = "اور (اس کے بعد یوں ہوا کہ) شیطان نے اس (درخت) کے ذریعہ سے ان (دونوں) کو (ان کے مقام سے) ہٹا دیا اور (اس طرح) اس نے انہیں اس (حالت) سے جس میں وہ تھے نکال دیا اور ہم نے (انہیں) کہا (کہ یہاں سے) نکل جائو۔ تم میں سے بعض بعض کے دشمن ہیں اور (یاد رکھو کہ) تمہارے لئے ایک (مقررہ) وقت تک اسی زمین میں جائے رہائش اور سامان معیشت (مقدر) ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 2;
UPDATE verses SET ts = "اس کے بعد آدم نے اپنے رب سے کچھ (دعائیہ) کلمات سیکھے (اور ان کے مطابق دعا کی) تو وہ اس کی طرف (پھر فضل کے ساتھ) متوجہ ہوا۔ یقیناً وہی (بندوں کی مصیبت کے وقت) بہت ہی توجہ کرنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 38 AND chapterNumber = 2;
UPDATE verses SET ts = "(تب) ہم نے کہا (جائو) سب کے سب اس میں سے نکل جائو (اور یاد رکھو کہ) اگر پھر کبھی تمہارے پاس میری طرف سے کوئی ہدایت آئے تو جو لوگ میری ہدایت کی پیروی کریں گے انہیں نہ کوئی (آئندہ کا) خوف ہوگا اور نہ وہ (سابق کوتاہی پر) غمگین ہوں گے۔ " WHERE ayatNumber = 39 AND chapterNumber = 2;
UPDATE verses SET ts = "اور جو (لوگ) کفر کریں گے اور ہماری آیات کو جھٹلائیں گے وہ (ضرور) دوزخ (میں پڑنے) والے ہیں۔ وہ اس میں رہتے چلے جائیں گے۔ " WHERE ayatNumber = 40 AND chapterNumber = 2;
UPDATE verses SET ts = "اے بنی اسرائیل! میرے اس احسان کو یاد کرو‘ جو میں تم پر کر چکا ہوں اور تم (نے) میرے (ساتھ جو) عہد (کیا تھا اس) کو پورا کرو۔ تب میں نے جو عہد تمہارے ساتھ کیا تھا میں اس کو پورا کروں گا اور مجھ (ہی) سے (ڈرو) پھر (میں کہتا ہوں کہ) مجھ (ہی) سے ڈرو۔ " WHERE ayatNumber = 41 AND chapterNumber = 2;
UPDATE verses SET ts = "اور اس (کلام) پر ایمان لائو جو میں نے (اب) اتارا ہے (اور) جو اس (کلام) کو جو تمہارے پاس ہے سچا کرنے والا ہے اور تم اس کے (سب سے) پہلے کافر نہ بنو‘ اور میری آیتوں کے بدلے میں تھوڑی قیمت مت لو۔ اور مجھ (ہی) سے (ڈرو) پھر (میں کہتا ہوں کہ) مجھ (ہی) سے ڈرو۔ " WHERE ayatNumber = 42 AND chapterNumber = 2;
UPDATE verses SET ts = "اور جانتے بوجھتے ہوئے حق کو باطل کے ساتھ نہ ملائو اور نہ حق کو (جانتے بوجھتے ہوئے) چھپائو۔ " WHERE ayatNumber = 43 AND chapterNumber = 2;
UPDATE verses SET ts = "اور نماز کو قائم رکھو اور زکٰوۃ دو‘ اور خدا کی خالص پرستش کرنے والوں کے ساتھ مل کر خدا کی خالص پرستش کرو۔ " WHERE ayatNumber = 44 AND chapterNumber = 2;
UPDATE verses SET ts = "کیا تم (دوسرے) لوگوں کو (تو) نیکی (کرنے) کے لئے کہتے ہو اور اپنے آپ کو فراموش کر دیتے ہو؟  حالانکہ تم کتاب (یعنی تورات) پڑھتے ہو پھر (بھی) کیا تم عقل سے کام نہیں لیتے۔ " WHERE ayatNumber = 45 AND chapterNumber = 2;
UPDATE verses SET ts = "اور صبر اور دعا کے ذریعہ سے (اللہ سے) مدد مانگو اور بے شک فروتنی اختیار کرنے والوں کے سوا (دوسروں کے لئے) یہ (امر) مشکل ہے۔ " WHERE ayatNumber = 46 AND chapterNumber = 2;
UPDATE verses SET ts = "(وہ فروتن) جو (اس بات پر) یقین رکھتے ہیں کہ وہ اپنے رب سے ملنے والے ہیں اور اس بات پر بھی کہ وہ اسی کی طرف لوٹ کر جانے والے ہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 2;
