UPDATE verses SET ts = "وہ اس (لعنت) میں رہیں گے‘ نہ (تو) ان (پر) سے عذاب ہلکا کیا جائے گا اور نہ انہیں ڈھیل دی جائے گی۔ " WHERE ayatNumber = 89 AND chapterNumber = 3;
