UPDATE verses SET ts = "کیا ان کا حکومت میں کوئی حصہ ہے؟ تب تو وہ لوگوں کو کھجور کی گٹھلی کے سوراخ کے برابر بھی نہ دیں گے۔ " WHERE ayatNumber = 54 AND chapterNumber = 4;
UPDATE verses SET ts = "یا جو (کچھ) اللہ نے اپنے فضل سے لوگوں کو دیا ہے (کیا) وہ اس کی بنا پر ان سے حسد کرتے ہیں؟ (اگر یہ بات ہے) تو ہم نے ابراہیم کی اولاد کو (بھی) کتاب اور حکمت دی تھی۔ اور (نیز) ہم نے ان کو بڑی حکومت دی تھی۔ " WHERE ayatNumber = 55 AND chapterNumber = 4;
UPDATE verses SET ts = "پھر ان میں سے بعض تو اس (نئی کتاب) پر ایمان لے آئے اور بعض ان میں سے اس سے رک رہے اور جہنم تمازت میں بہت زیادہ ہے۔ " WHERE ayatNumber = 56 AND chapterNumber = 4;
UPDATE verses SET ts = "جن لوگوں نے ہماری آیات کا انکار کیا ہے ہم جلدی (ہی) انہیں آگ میں ڈالیں گے جب کبھی بھی ان کی کھالیں گل جائیں گی۔ ہم انہیں ان کے سوا اور کھالیں بدل کر دے دیں گے (اور ہم یہ) اس لئے (کریں گے) تا (کہ) وہ عذاب (کا مزا) چکھیں اللہ یقیناً غالب (اور) حکمت والا ہے۔ " WHERE ayatNumber = 57 AND chapterNumber = 4;
UPDATE verses SET ts = "اور جو لوگ ایمان لائے ہیں اور انہوں نے نیک اعمال کیے ہیں انہیں ہم ضرور ایسے باغات میں داخل کریں گے جن کے نیچے نہریں بہتی ہیں وہ ہمیشہ ان میں رہتے چلے جائیں گے۔ ان کے لئے ان میں پاک بیویاں ہوں گی۔ اور ہم انہیں گھنے سایہ میں رہنے کے لئے داخل کریں گے۔ " WHERE ayatNumber = 58 AND chapterNumber = 4;
UPDATE verses SET ts = "اللہ تمہیں یقیناً (اس بات کا) حکم دیتا ہے کہ تم امانتیں ان کے مستحقوں کے سپرد کرو۔ اور (یہ کہ) جب تم لوگوں کے درمیان فیصلہ کرو تو عدل سے فیصلہ کرو اللہ جس بات کی تمہیں نصیحت کرتا ہے وہ یقیناً بہت (ہی) اچھی ہے۔ اللہ یقیناً بہت سننے والا (اور) دیکھنے والا ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 4;
UPDATE verses SET ts = "اے ایماندارو! اللہ کی اطاعت کرو اور رسول کی اور اپنے فرمانروائوں کی بھی اطاعت کرو‘ پھر اگر تم (حکام سے) کسی امر میں اختلاف کرو تو اگر تم اللہ اور پیچھے آنے والے دن پر ایمان رکھتے ہو تو اسے اللہ اور رسول کی طرف لوٹا دو (اور ان کے حکم کی روشنی میں معاملہ طے کرو( یہ (بات) بہتر اور انجام کے لحاظ سے زیادہ اچھی ہے۔ " WHERE ayatNumber = 60 AND chapterNumber = 4;
UPDATE verses SET ts = "کیا تو نے ان لوگوں کا حال نہیں دیکھا جو دعویٰ کرتے ہیں کہ جو (کچھ) تیری طرف نازل کیا گیا ہے اور (نیز) جو تجھ سے پہلے نازل کیا گیا تھا وہ اس پر ایمان لے آئے ہیں اور باوجود اس کے کہ ان کو حکم دیا گیا تھا کہ سرکشوں سے فیصلہ نہ کرائیں اور ان کے کہے پر نہ چلیں وہ چاہتے ہیں کہ انہی سے فیصلہ کرائیں کیونکہ شیطان چاہتا ہے کہ انہیں خطرناک گمراہی میں ڈال دے۔ " WHERE ayatNumber = 61 AND chapterNumber = 4;
UPDATE verses SET ts = "اور جب ان سے کہا جاتا ہے کہ جو کچھ اللہ نے اتارا ہے‘ اس کی طرف اور رسول کی طرف آئو تو تو منافقوں کو دیکھتا ہے کہ وہ تجھ سے بالکل پیچھے ہٹ جاتے ہیں۔ " WHERE ayatNumber = 62 AND chapterNumber = 4;
UPDATE verses SET ts = "پھر (یہ) کیوں (ہوتا ہے کہ) جب ان پر ان کے افعال کے نتیجہ میں کوئی مصیبت آتی ہے تو وہ (گھبرا جاتے ہیں اور) تیرے پاس اللہ کی قسمیں کھاتے ہوئے آتے ہیں کہ ہم نے تو صرف نیک سلوک اور صلح کرانے کا ارادہ کیا تھا۔ " WHERE ayatNumber = 63 AND chapterNumber = 4;
UPDATE verses SET ts = "یہ وہ لوگ ہیں کہ جو (کچھ) ان کے دلوں میں ہے اللہ اسے (خوب) جانتا ہے۔ پس تو ان سے اعراض کر۔ اور انہیں نصیحت کر۔ اور انہیں ان کے نفوس کے متعلق موثر بات کہہ۔ " WHERE ayatNumber = 64 AND chapterNumber = 4;
UPDATE verses SET ts = "اور ہم نے کوئی رسول (بھی) سوائے اس غرض کے کہ اللہ کے حکم سے اس کی اطاعت کی جائے نہیں بھیجا اور جب انہوں نے اپنی جانوں پر ظلم کیا تھا اگر (اس وقت) وہ تیرے پاس آتے اور اللہ سے بخشش چاہتے اور رسول (بھی) ان کے حق میں بخشش طلب کرتا تو وہ ضرور اللہ کو بہت شفقت کرنے والا (اور) باربار رحم کرنے والا پاتے۔ " WHERE ayatNumber = 65 AND chapterNumber = 4;
UPDATE verses SET ts = "پس تیرے رب کی قسم ہے کہ جب تک وہ (ہر) اس بات میں جس کے متعلق ان میں جھگڑا ہو جائے تجھے حکم نہ بنائیں (اور) پھر جو فیصلہ تو کرے اس سے اپنے نفوس میں کسی قسم کی تنگی نہ پائیں اور پورے طور پر فرمانبردار (نہ) ہو جائیں ہرگز ایمان دار نہ ہوں گے۔ " WHERE ayatNumber = 66 AND chapterNumber = 4;
UPDATE verses SET ts = "اور اگر ہم انہیں (یہ) حکم دیتے  کہ تم اپنے آپ کو قتل کر دو یا یہ کہ اپنے گھروں سے نکل جائو تو ان میں سے چند کے سوا (باقی) یہ (کام) نہ کرتے اور اگر وہ جس (کام) کی انہیں نصیحت کی جاتی اسے کر لیتے تو ان کے لئے بہتر اور زیادہ پختگی کا موجب ہوتا۔ " WHERE ayatNumber = 67 AND chapterNumber = 4;
UPDATE verses SET ts = "اور اس صورت میں ہم انہیں یقیناً اپنے پاس سے (بہت) بڑا بدلہ دیتے۔ " WHERE ayatNumber = 68 AND chapterNumber = 4;
UPDATE verses SET ts = "اور (نیز) ہم انہیں ضرور سیدھا راستہ دکھاتے۔ " WHERE ayatNumber = 69 AND chapterNumber = 4;
