UPDATE verses SET ts = "اور اس شخص سے بڑھ کر کس کا دین اچھا (ہو سکتا) ہے جس نے (خوب) اچھی طرح عمل کرتے ہوئے اپنے آپ کو اللہ کے سپرد کر دیا ہو اور ابراہیم کے دین کی‘ جو سلامت رو تھے پیروی (اختیار) کی ہو اور اللہ نے ابراہیم کو (اپنا) خاص دوست بنایا تھا۔ " WHERE ayatNumber = 126 AND chapterNumber = 4;
UPDATE verses SET ts = "اور جو کچھ آسمانوں اور جو کچھ زمین میں ہے (سب) اللہ ہی کا ہے۔ اور اللہ ہر (ایک) چیز کا پورا علم رکھتا ہے۔ " WHERE ayatNumber = 127 AND chapterNumber = 4;
UPDATE verses SET ts = "اور لوگ تجھ سے (ایک سے زیادہ) عورتوں (سے نکاح) کے متعلق (احکام) دریافت کرتے ہیں۔ تو (ان سے) کہہ کہ اللہ تمہیں ان کے متعلق اجازت دے چکا ہے اور جو (حکم) (اس) کتاب میں (دوسری جگہ) تمہیں پڑھ کرسنایا گیا ہے وہ ان یتیم عورتوں کے متعلق ہے جنہیں تم ان کے مقرر کردہ حق ادا نہیں کرتے مگر ان سے نکاح کرنا چاہتے ہو اور (نیز) کمزور بچیوں کے متعلق ہے اور (تمہیں یہ حکم دیا گیا تھا) کہ یتیموں کے ساتھ انصاف کا معاملہ کرتے رہو۔ اور جو نیک کام بھی تم کرو (یاد رکھو کہ) اللہ اسے یقیناً خوب جانتا ہے۔ " WHERE ayatNumber = 128 AND chapterNumber = 4;
UPDATE verses SET ts = "اور اگر کسی عورت کو اپنے خاوند کی طرف سے بدمعاملگی یا عدم توجہی کا اندیشہ ہو تو ان دونوں پر کوئی گناہ نہیں کہ وہ کسی طریق پر آپس میں صلح کر لیں اور صلح (سب سے) بہتر ہے۔ اور لوگوں کے نفسوں میں بخل (کا خیال) پیدا کر دیا گیا ہے اور اگر تم نیک کام کرو اور تقویٰ اختیار کرو تو (یاد رکھو کہ) جو کچھ تم کرتے ہو اس سے اللہ یقیناً آگاہ ہے۔ " WHERE ayatNumber = 129 AND chapterNumber = 4;
UPDATE verses SET ts = "اور خواہ تم (عدل کرنے کی کتنی بھی) خواہش کرو (تو بھی) تم عورتوں کے درمیان عدل نہیں کر سکتے۔ پس تم بالکل (ایک ہی کی طرف) نہ جھک جائو (جس کا نتیجہ یہ ہو) کہ اس (دوسری) کو (درمیان میں) لٹکتی ہوئی (چیز) کی مانند چھوڑ دو۔ اور اگر تم (آپس میں) موافقت پیدا کرو اور تقویٰ اختیار کرو تو (یاد رکھو کہ) اللہ یقیناً بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 130 AND chapterNumber = 4;
UPDATE verses SET ts = "اور اگر وہ (دونوں) علیحدگی اختیار کر لیں تو اللہ (ان میں سے) ہر ایک کو اپنی طرف کشائش دے کر غنی بنا دے گا اور اللہ وسعت دینے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 131 AND chapterNumber = 4;
UPDATE verses SET ts = "اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے (سب) اللہ ہی کا ہے اور جن لوگوں کو تم سے پہلے کتاب دی گئی تھی ہم نے انہیں (بھی) اور تمہیں (بھی) تاکیدی حکم دیا (ہوا) ہے کہ اللہ کا تقویٰ اختیار کرو۔ اور اگر تم انکار کرو گے تو (یاد رکھو کہ) جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے (سب) اللہ ہی کا ہے اور اللہ بے نیاز (اور) ان گنت تعریفوں کا مستحق ہے۔ " WHERE ayatNumber = 132 AND chapterNumber = 4;
UPDATE verses SET ts = "اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین ہے (سب) اللہ ہی کا ہے اور اللہ ہر ضروری حفاظت کرنے والا ہے۔ " WHERE ayatNumber = 133 AND chapterNumber = 4;
