UPDATE verses SET ts = "اور جو لوگ اللہ اور اس کے (تمام) رسولوں پر ایمان لے آئے اور انہوں نے ان (رسولوں) میں سے کسی ایک کے درمیان (بھی) تفریق نہیں کی۔ وہ لوگ ایسے ہیں کہ وہ انہیں جلد ہی ان کے اجر دے گا۔ اور اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 153 AND chapterNumber = 4;
UPDATE verses SET ts = "اہل کتاب تجھ سے سوال کرتے ہیں کہ تو ان پر آسمان سے ایک کتاب نازل کرے (اس پر تعجب نہ کر کیونکہ) انہوں نے موسیٰ سے اس سے بھی بڑا سوال کیا تھا۔ انہوں نے اس سے کہا تھا کہ تو ہمیں اللہ ظاہر میں دکھا دے۔ تب ان کے ظلم کے سبب سے انہیں مہلک عذاب نے پکڑ لیا اور انہوں  نے بچھڑے کو جبکہ ان کے پاس کھلے (کھلے) نشانات آچکے تھے (اپنا معبود) بنا لیا۔ پھر ہم نے اس (قصور) سے (بھی) درگزر کیا۔ اور ہم نے موسیٰ کو کھلا (کھلا) غلبہ دیا۔ " WHERE ayatNumber = 154 AND chapterNumber = 4;
UPDATE verses SET ts = "اور ہم نے ان سے (پختہ) عہد لیتے ہوئے ان پر طور کو بلند کیا اور ہم نے ان سے کہا کہ اس دروازہ میں پوری فرمانبرداری کرتے ہوئے داخل ہو۔ اور ہم نے ان سے (یہ بھی) کہا کہ کہ سبت (کے معاملہ) میں زیادتی نہ کرو۔ اور ہم نے ان سے ایک پختہ عہد لیا۔ " WHERE ayatNumber = 155 AND chapterNumber = 4;
UPDATE verses SET ts = "پھر ان کے اپنے عہد کو توڑ دینے (کے سبب سے) اور ان کے اللہ کی آیتوں کا انکار اور ان کے نبیوں کو قتل کرنے کی بلاوجہ کوشش (کے سبب سے) اور یہ (بات) کہنے کے سبب سے کہ ہمارے دل پردوں میں ہیں (پردوں میں) نہیں بلکہ اللہ نے ان کے کفر کے سبب سے ان (کے دلوں) پر مہر کر دی ہے اس لئے وہ بالکل ایمان نہیں لاتے۔ " WHERE ayatNumber = 156 AND chapterNumber = 4;
UPDATE verses SET ts = "نیز ان کے کفر (کے سبب سے) اور ان کے مریم پر (ایک بہت) بڑا بہتان باندھنے کے سبب سے۔ " WHERE ayatNumber = 157 AND chapterNumber = 4;
UPDATE verses SET ts = "اور ان کے (یہ بات) کہنے کے سبب سے کہ اللہ کے رسول مسیح عیسیٰ ابن مریم کو ہم نے یقیناً قتل کر دیا ہے (یہ سزا انکو ملی ہے) حالانکہ نہ انہوں نے اسے قتل کیا اور نہ انہوں نے اسے صلیب پر لٹکا کر مارابلکہ وہ ان کے لئے (مصلوب کے) مشابہ بنادیا گیا اور جن لوگوں نے اس (یعنی مسیح کے صلیب سے زندہ اتارے جانے ) میں اختلاف کیا ہے وہ یقیناً اس (کے زندہ اتارے جانے کی وجہ سے) شک میں(پڑے ہوئے) ہیں۔ انہیں اس کے متعلق کوئی بھی (یقینی) علم نہیں ہے۔ ہاں (صرف ایک) وہم کی پیروی (کر رہے ہیں) اور انہوں نے اس (واقعہ کی اصلیت) کو پوری طرح نہیں سمجھا (اور جو سمجھا ہے غلط سمجھا ہے)۔" WHERE ayatNumber = 158 AND chapterNumber = 4;
UPDATE verses SET ts = "واقعہ یہ ہے کہ اللہ نے اسے اپنے حضور میںعزت (ورفعت)دی تھی(اور وہ صلیب پر مر نہیں گیا تھا) کیونکہ اللہ غالب اور حکمت والا ہے۔ " WHERE ayatNumber = 159 AND chapterNumber = 4;
UPDATE verses SET ts = "اہل کتاب میں سے ایک بھی نہیں جو اس (واقعہ) پر اپنی موت سے پہلے ایمان نہ لاتا رہے۔ اور وہ قیامت کے دن ان پر گواہ ہوگا۔ " WHERE ayatNumber = 160 AND chapterNumber = 4;
UPDATE verses SET ts = "پس (اس) ظلم کی وجہ سے جو یہودیوں کی طرف سے ظہور میں آیا ہم نے وہ پاکیزہ اشیاء جو (پہلے) ان کے لئے حلال کی گئی تھیں‘ ان پر حرام کر دیں اور (نیز) اللہ کی راہ سے بہتوں کو روکنے کے سبب سے (ان کو یہ سزا ملی) " WHERE ayatNumber = 161 AND chapterNumber = 4;
UPDATE verses SET ts = "اور ان کے سودی کاروبار کرنے کی وجہ سے بھی حالانکہ اس سے ان کو روکا گیا تھا (نیز) لوگوں کے اموال کو ان کے ناحق کھانے کے سبب سے (ان کو یہ سزا ملی) اور ہم نے ان میں سے کافرں کے لئے دردناک عذاب تیار کیا ہے۔ " WHERE ayatNumber = 162 AND chapterNumber = 4;
UPDATE verses SET ts = "لیکن ان (یہود) میں سے جو لوگ علم میں پختہ ہیں وہ اور (نیز) مومن (مسلم) اس (کلام الٰہی) پر جو تجھ پر اتار گیا ہے اور جو (کچھ) تجھ سے پہلے اتارا گیا تھا ایمان لاتے ہیں اور (خصوصاً) نماز کو ٹھیک طور پر پڑھنے والے اور زکٰوۃ دینے والے اور اللہ پر اور پیچھے آنے والے دن پر ایمان لانے والے لوگوں کو ہم ضرور (بہت) بڑا اجر دیں گے۔ " WHERE ayatNumber = 163 AND chapterNumber = 4;
UPDATE verses SET ts = "جس طرح ہم نے نوح اور اس کے بعد (دوسرے) تمام انبیاء پر وحی (نازل) کی تھی‘ یقیناً تجھ پر (بھی) ہم نے وحی (نازل) کی ہے اور ہم نے ابراہیم اور اسماعیل اور اسحاق اور یعقوب اور (اس کی) اولاد اور عیسیٰ اور ایوب اور یونس اور ہارون اور سلیمان پر (بھی) وحی (نازل) کی تھی اور ہم نے داو‘د کو (بھی) ایک کتاب دی تھی۔ " WHERE ayatNumber = 164 AND chapterNumber = 4;
UPDATE verses SET ts = "اور کئی ایسے رسول ہیں جن کی خبر ہم (اس سے) پہلے تجھے دے چکے ہیں اور کئی ایسے رسول ہیں جن کا ذکر ہم نے تجھ سے نہیں کیا اور اللہ نے موسیٰ سے خوب اچھی طرح کلام کیا تھا۔ " WHERE ayatNumber = 165 AND chapterNumber = 4;
UPDATE verses SET ts = "ہم نے انہیں (یعنی موسیٰ کے ساتھ ذکر کئے گئے رسولوں کو) بشارت دینے والے اور ڈرانے والے رسول بنا کر بھیجا تھا تا لوگوں کا ان رسولوں کے (مبعوث ہونے کے) بعد اللہ پر کوئی الزام نہ رہے اور اللہ غالب (اور) حکمت والا ہے۔ " WHERE ayatNumber = 166 AND chapterNumber = 4;
UPDATE verses SET ts = "لیکن اللہ اس (کلام) کے ذریعہ سے جو اس نے تجھ پر اتارا ہے گواہی دیتا ہے کہ اس نے اسے اپنے علم پر مشتمل کرکے اتارا ہے اور ملائکہ (بھی) گواہی دیتے ہیں اور اللہ کی گواہی سب پر مقدم ہے۔ " WHERE ayatNumber = 167 AND chapterNumber = 4;
