UPDATE verses SET ts = "کیا وہ یہ نہیں سوچتے کہ ان کے صاحب (یعنی رسول کریم صلی اللہ علیہ وسلم) کو جنون نہیں ہے۔ وہ تو صرف ایک کھلا کھلا ہوشیار کرنے والا ہے۔ " WHERE ayatNumber = 185 AND chapterNumber = 7;
UPDATE verses SET ts = "کیا وہ آسمانوں اور زمین کی بادشاہت پر غور نہیں کرتے اور ہر اس چیز پر جس کو اللہ نے پیدا کیا ہے (اور اس بات پر بھی کہ)  شاید ان کی تباہی کی مدت قریب آگئی ہو پھر وہ اس کھلی بات کے بعد کس بات کے ذریعہ ایمان لائیں گے۔ " WHERE ayatNumber = 186 AND chapterNumber = 7;
UPDATE verses SET ts = "جس کو اللہ گمراہ قرار دے اس کو ہدایت دینے والا کوئی نہیں اور وہ ان کو اپنی سرکشیوں میں بہکتے ہوئے چھوڑ دیتا ہے۔ " WHERE ayatNumber = 187 AND chapterNumber = 7;
UPDATE verses SET ts = "(اے رسول)! تیرے مخالف تجھ سے قیامت کے متعلق سوال کرتے ہیں کہ وہ کب آئے گی‘ تو کہہ دے کہ اس کا علم صرف میرے رب کو ہے اس کو اپنے وقت پر صرف وہی ظاہر کرے گا (ہاں) وہ بھاری ہوگی آسمانوں میں بھی اور زمین  میں بھی (اور) تمہارے پاس صرف اچانک آئے گی۔ وہ تجھ سے قیامت کے متعلق اس طرح سوال کرتے ہیں گویا تجھے بھی اس کے وقت کی دریافت کی لو لگی ہوئی ہے۔ تو کہہ دے (میرے لئے تو اتنا کافی ہے) کہ اس کا علم صرف اللہ کو ہے لیکن اکثر لوگ اسے جانتے نہیں۔ " WHERE ayatNumber = 188 AND chapterNumber = 7;
UPDATE verses SET ts = "(پھر) تو کہہ دے کہ میں اپنی جان کے لئے نہ کسی نفع کا مالک ہوں نہ نقصان کا۔ ہاں! (مجھ کو وہی پہنچے گا) جو اللہ چاہے گا اور اگر میں غیب کا واقف ہوتا‘ تو بھلائیوں میں سے اکثر اپنے لئے جمع کر لیتا اور مجھے کبھی کوئی تکلیف نہ پہنچتی۔ مگر میں تو صرف مومنوں کے لئے ہشیار کرنے والا اور خوش خبری  دینے والا ہوں۔ " WHERE ayatNumber = 189 AND chapterNumber = 7;
UPDATE verses SET ts = "وہی ہے جس نے تم کو ایک جان سے پیدا کیا ہے اور اسی کی قسم سے اس کا جوڑا بنایا ہے تا کہ وہ اس کی طرف رغبت کرکے سکون پائے۔ پس جب وہ اسے ڈھانپ لیتا ہے تو وہ خفیف سا بوجھ اٹھا لیتی ہے اور اسے لیے پھرتی ہے پھر جب وہ (کچھ) بوجھل ہو جاتی ہے دونوں (مرد و عورت) اپنے اللہ سے جو ان کا رب ہے دعا کرتے ہیں کہ اگر تو ہم کو ایک تندرست بچہ دے گا تو ہم شکر گذار بندے بن جائیں گے۔ " WHERE ayatNumber = 190 AND chapterNumber = 7;
UPDATE verses SET ts = "مگر جب وہ ان کو تندرست بچہ عطا کرتا ہے تو وہ دونوں اس اولاد میں جو خدا نے ان کو دی ہوتی ہے اس کے شریک مقرر کرنے لگ جاتے ہیں۔ اور اللہ ان کے شرک کرنے سے بہت بالا ہے۔ " WHERE ayatNumber = 191 AND chapterNumber = 7;
UPDATE verses SET ts = "کیا وہ ان کو (خدا کا) شریک قرار دیتے ہیں جو کچھ بھی پیدا نہیں کرتے بلکہ خود پیدا کیے جاتے ہیں۔ " WHERE ayatNumber = 192 AND chapterNumber = 7;
UPDATE verses SET ts = "اور ان (شرک کرنے والوں) کی مدد کرنے کی ذرا بھی طاقت نہیں رکھتے اور نہ اپنی جانوں کی مدد کر سکتے ہیں۔ " WHERE ayatNumber = 193 AND chapterNumber = 7;
UPDATE verses SET ts = "اور اگر تو ان جھوٹے معبودوں کو ہدایت کی طرف بلائے تو وہ تمہاری اتباع نہیں کرسکیں گے تمہارا ان کو بلانا یا چپ رہنا ان کے لئے برابر ہے۔ " WHERE ayatNumber = 194 AND chapterNumber = 7;
UPDATE verses SET ts = "جن لوگوں کو تم اللہ کے سوا بلاتے ہو وہ تمہاری طرح کے بندے ہیں پس تم انہیں بلاتے جائو (اور) اگر تم سچے ہو تو وہ تمہاری پکار کا جواب دے کر دکھائیں۔ " WHERE ayatNumber = 195 AND chapterNumber = 7;
UPDATE verses SET ts = "کیا ان کے پائوں ہیں جن سے وہ چلتے ہیں؟ یا ان کے ہاتھ ہیں جن سے وہ پکڑتے ہیں؟ یا ان کی آنکھیں ہیں جن سے وہ دیکھتے ہیں؟ یا ان کے کان ہیں جن سے وہ سنتے ہیں۔ تو ان سے کہہ دے کہ اپنے سب شریکوں کو بلا لو پھر سب مل کر میرے خلاف منصوبہ کرو اور مجھے کوئی ڈھیل نہ دو۔ " WHERE ayatNumber = 196 AND chapterNumber = 7;
UPDATE verses SET ts = "میرا دوست یقیناً وہ خدا ہے جس نے یہ کامل کتاب اتاری ہے اور وہ نیکوں کا ساتھ دیتا ہے۔ " WHERE ayatNumber = 197 AND chapterNumber = 7;
UPDATE verses SET ts = "اور وہ لوگ جن کو تم اس کے سوا پکارتے ہو وہ نہ تو تمہاری مدد کی توفیق رکھتے ہیں اور نہ اپنی جانوں کی مدد کرتے ہیں۔ " WHERE ayatNumber = 198 AND chapterNumber = 7;
UPDATE verses SET ts = "اور اگر تم ان کو ہدایت کی طرف بلائو تو وہ سن نہیں سکتے اور تو ان کو دیکھتا ہے کہ گویا وہ تجھے دیکھ رہے ہیں حالانکہ وہ (تجھے) نہیں دیکھ رہے۔ " WHERE ayatNumber = 199 AND chapterNumber = 7;
UPDATE verses SET ts = "اے نبی! ہمیشہ درگزر سے کام لے اور مطابق فطرت باتوں کا حکم دیتا رہ اور جاہل لوگوں سے منہ پھیرلے۔ " WHERE ayatNumber = 200 AND chapterNumber = 7;
UPDATE verses SET ts = "اور اگر شیطان کی طرف سے تجھ کو کوئی صدمہ پہنچے تو تو اللہ سے پناہ مانگ جو بہت سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 201 AND chapterNumber = 7;
UPDATE verses SET ts = "یقیناً وہ لوگ جنہوں نے اس وقت تقویٰ اختیار کیا جب ان کو شیطان کی طرف سے آنے والا کوئی خیال محسوس ہوا اور وہ ہوشیار ہو گئے اور ان کی آنکھیں کھل گئیں  (وہ ہدایت پاجاتے ہیں) ۔" WHERE ayatNumber = 202 AND chapterNumber = 7;
UPDATE verses SET ts = "اور ان (کافروں) کے بھائی تو انہیں گمراہی کی طرف کھینچتے ہیں پھر کسی قسم کی کمی نہیں چھوڑتے۔ " WHERE ayatNumber = 203 AND chapterNumber = 7;
UPDATE verses SET ts = "اور جب تو ان کے پاس کوئی کھلی نشانی نہ لائے تو کہتے ہیں کہ تو نے اس (نشانی) کو کیوں نہ کھینچ بلایا۔ تو کہہ دے کہ میں تو صرف اپنے رب کی طرف سے اپنے پر نازل ہونے والی وحی کی اتباع کرتا ہوں۔ یہ (وحی) تمہارے رب کی طرف سے دلائل سے پر ہے اور مومنوں کے لئے ہدایت بھی ہے اور رحمت بھی ہے۔ " WHERE ayatNumber = 204 AND chapterNumber = 7;
UPDATE verses SET ts = "اور (اے لوگو)! جب قرآن پڑھا جائے‘ تو اس کو سنا کرو اور چپ رہا کرو۔ تا کہ تم پر رحم کیا جائے۔ " WHERE ayatNumber = 205 AND chapterNumber = 7;
UPDATE verses SET ts = "اور (اے نبی)!  تو اپنے نفس میں اپنے رب کو عجز اور خوف کے ساتھ یاد کرتے رہا کر اور دھیمی آواز سے صبح بھی اور شام بھی (ایسا کیا کر) اور کبھی غفلت کرنے والوں میں شامل نہ ہو۔ " WHERE ayatNumber = 206 AND chapterNumber = 7;
UPDATE verses SET ts = "جو لوگ تیرے رب کے پاس ہیں یقیناً اپنے رب کی عبادت سے اپنے  آپ کو بڑا نہیں سمجھتے اور اس کی تسبیح کرتے رہتے ہیں اور اس کے سامنے سجدے کرتے رہتے ہیں۔ " WHERE ayatNumber = 207 AND chapterNumber = 7;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 8;
UPDATE verses SET ts = "(اے رسول)! لوگ تجھ سے اموال غنیمت کے متعلق سوال کرتے ہیں تو (ان سے) کہہ دے کہ اموال غنیمت اللہ اور اس کے رسول کے ہیں پس اللہ کا تقویٰ اختیار کرو اور آپس میں اصلاح کی کوشش کرو اور اگر تم مومن ہو تو اللہ اور اس کے رسول کی اطاعت کرو۔ " WHERE ayatNumber = 2 AND chapterNumber = 8;
UPDATE verses SET ts = "مومن تو صرف وہی ہیں کہ جب (ان کے سامنے) اللہ کا ذکر کیا جائے تو ان کے دل ڈر جائیں اور جب ان کے سامنے اس کی آیات پڑھی جائیں تو وہ ان کے ایمان کو بڑھا دیں۔ نیز (مومن وہ ہیں) جو اپنے رب پر توکل کرتے ہیں۔ " WHERE ayatNumber = 3 AND chapterNumber = 8;
UPDATE verses SET ts = "(اسی طرح حقیقی مومن وہ ہیں) جو نمازوں کو (شرائط کے مطابق) ادا کرتے ہیں۔ اور جو (کچھ) ہم نے ان کو دیا ہے اس میں سے خرچ کرتے ہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 8;
UPDATE verses SET ts = "یہ (مذکورہ بالا صفات رکھنے والے) ہی سچے مومن ہیں ان کے رب کے پاس ان کے لئے (بڑے بڑے) مدارج اور بخشش کا سامان اور معزز رزق ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 8;
UPDATE verses SET ts = "یہ (انعام ان پر) اس وجہ سے (ہوگا) کہ تجھ کو تیرے رب نے تیرے گھر سے ایک خاص مقصد کے ماتحت نکالا ہے اور مومنوں میں سے ایک فریق اسے بہت ہی ناپسند کرتا تھا۔ " WHERE ayatNumber = 6 AND chapterNumber = 8;
UPDATE verses SET ts = "وہ تجھ سے حق ظاہر ہونے کے بعد اس طرح بحث کرتے ہیں گویا (اسلام کی دعوت سے) ان کو موت کی طرف دھکیلا جا رہا ہے اور (یوں معلوم ہوتا ہے کہ) وہ (اس موت کو آنکھوں سے) دیکھ رہے ہیں۔ " WHERE ayatNumber = 7 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (اس وقت کو یاد کرو) جبکہ اللہ تم سے دو گروہوں میں سے ایک کا وعدہ کرتا تھا کہ وہ تم کو دیا جائے گا اور تم چاہتے تھے کہ وہ گروہ جس کے پاس ہتھیار نہیں ہیں تم کو ملے اور اللہ چاہتا تھا کہ وہ حق کو اپنے احکام کے ذریعہ سے پورا کردے اور کافروں کی جڑ کاٹ دے۔ " WHERE ayatNumber = 8 AND chapterNumber = 8;
UPDATE verses SET ts = "تا کہ وہ اس طرح حق کو قائم کر دے اور باطل کو تباہ کر دے خواہ مجرم اسے ناپسند ہی کیوں نہ کرتے ہوں۔ " WHERE ayatNumber = 9 AND chapterNumber = 8;
UPDATE verses SET ts = "(اور اس وقت کو بھی یاد کرو) جبکہ تم اپنے رب سے التجائیں کرتے تھے۔ اس پر تمہارے رب نے تمہاری دعائوں کو سنا (اور کہا کہ) میں تمہاری مدد ہزاروں فرشتوں سے کروں گا جن کا لشکر کے بعد لشکر بڑھ رہا ہوگا۔ " WHERE ayatNumber = 10 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اللہ نے اس خبر کو محض ایک بشارت کے طور پر نازل کیا تھا تا کہ اس کے ذریعہ سے تمہارے دل مطمئن ہو جائیں اور مدد صرف اللہ کے پاس سے آتی ہے (فرشتے تو محض ایک علامت ہیں) اللہ یقیناً بہت غالب (اور) حکمت والا ہے۔ " WHERE ayatNumber = 11 AND chapterNumber = 8;
UPDATE verses SET ts = "(یہ نشان اس وقت ظاہر ہوا) جبکہ خدا (اپنی طرف سے) امن اور تسکین کی خوشخبری دینے کے لئے تم پر اونگھ نازل کر رہا تھا اور تم پر بادلوں سے پانی اتار رہا تھا تا کہ اس کے ذریعہ سے تمہیں پاک کردے اور شیطان کی گندگی (یعنی اس کے خوف) کو تم سے دور کر دے اور تا کہ تمہارے دلوں کو مضبوط کرے اور اس (بارش) کے ذریعہ سے تمہارے قدموں کو مضبوط کردے۔ " WHERE ayatNumber = 12 AND chapterNumber = 8;
UPDATE verses SET ts = "(یہ وہ وقت تھا) جب تیرا رب ملائکہ کو بھی وحی کر رہا تھا کہ میں تمہارے ساتھ ہوں پس مومنوں کو ثابت قدم بنائو۔ میں کفار کے دلوں میں رعب ڈالوں گا۔ پس (اے مومنو)! تم ان کی گردنوں پر حملے کرتے جائو اور ان کی پورپور پر ضربیں لگاتے جائو۔ " WHERE ayatNumber = 13 AND chapterNumber = 8;
UPDATE verses SET ts = "یہ اس لئے ہوگا کہ انہوں نے اللہ اور اس کے رسول کی مخالفت کی ہے اور جو کوئی اللہ اور اس کے رسول کی مخالفت کرتا ہے (اسے سمجھ لینا چاہیئے کہ) اللہ بڑی سخت سزا دینے والا ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 8;
UPDATE verses SET ts = "(اے لوگو)! (سنو! اللہ کی سزا) ایسی ہی ہوتی ہے پس اسے چکھو اور (یاد رکھو کہ) کافروں کو یقیناً آگ کا عذاب پہنچنے والا ہے۔ " WHERE ayatNumber = 15 AND chapterNumber = 8;
UPDATE verses SET ts = "اے مومنو! جب تم کفار سے ایک لشکر کی صورت میں ملو‘ تو کبھی ان کو پیٹھ نہ دکھایا کرو۔ " WHERE ayatNumber = 16 AND chapterNumber = 8;
UPDATE verses SET ts = "اور جو کوئی ایسے موقعہ پر پیٹھ دکھائے گا سوائے اس کے کہ لڑائی کے لئے جگہ بدل رہا ہو یا کسی (مسلمان) گروہ کی طرف (اس کی مدد کے لئے) جا رہا ہو‘ تو وہ اللہ کا غضب لے کر لوٹے گا اور اس کا ٹھکانا جہنم ہوگا اور رہنے کے لحاظ سے وہ جگہ بہت بری ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 8;
UPDATE verses SET ts = "پس (یاد رکھو کہ) ان (کافروں) کو تم نے نہیں مارا تھا بلکہ ان کو اللہ نے مارا تھا۔ اور جب تو نے پتھر پھینکے تھے تو تو نے نہیں پھینکے تھے بلکہ اللہ نے پھینکے تھے جس کا نتیجہ یہ ہوا کہ اس کے ذریعہ سے مومنوں پر ایک بڑا احسان کیا اور اللہ یقیناً بہت سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 8;
UPDATE verses SET ts = "یہ (امر) اسی طرح ہوگا (جس طرح ہم نے کہا تھا) اور اللہ یقیناً کافروں کی تدبیر کو کمزور کرنے والا ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 8;
UPDATE verses SET ts = "(اے کفار مکہ)! اگر تم فتح کا نشان مانگتے تھے تو لو تمہارے پاس فتح آگئی اور اگراب بھی تم باز آجائو تو یقیناً یہ تمہارے لئے اچھا ہوگا اور اگر تم (شرارت کی طرف) لوٹو تو ہم بھی (سزا کی طرف)  لوٹیں گے اور تمہارا جتھا خواہ وہ کتنا ہی مضبوط ہو ہرگز تمہیں کوئی فائدہ نہیں دے گا اور اللہ یقیناً مومنوں کے ساتھ ہے۔ " WHERE ayatNumber = 20 AND chapterNumber = 8;
UPDATE verses SET ts = "اے مومنو! اللہ اور اس کے رسول کی اطاعت کرو‘ اور ان میں سے کسی سے منہ نہ پھیرو‘ اس حالت میں کہ تم (اس کا حکم) سن رہے ہو۔ " WHERE ayatNumber = 21 AND chapterNumber = 8;
UPDATE verses SET ts = "اور ان لوگوں کی طرح مت بنو جنہوں نے یہ کہا تھا کہ ہم سنتے ہیں‘ مگر وہ سنتے نہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 8;
UPDATE verses SET ts = "اللہ کے نزدیک حیوانات سے بھی بدتر وہ لوگ ہیں جو بہرے اور گونگے ہیں جو کچھ بھی عقل نہیں رکھتے۔ " WHERE ayatNumber = 23 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اگر اللہ ان (کفار) میں کوئی بھی خیر دیکھتا تو ان کو (قرآن) سنا دیتا اور اگر ان کو (موجودہ حالت میں قرآن) سنا دیتا تب بھی وہ پیٹھ پھیر دیتے اور (قرآن سے) روگردانی اختیار کرتے۔" WHERE ayatNumber = 24 AND chapterNumber = 8;
UPDATE verses SET ts = "اے مومنو! اللہ اور اس کے رسول کی بات سنو جب کہ وہ تمہیں زندہ کرنے کے لئے پکارے اور جان لو کہ اللہ انسان اور اس کے دل میں حائل ہے اور یہ کہ تم اسی کی طرف زندہ کرکے لوٹائے جائو گے۔ " WHERE ayatNumber = 25 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اس فتنہ سے ڈرتے رہو جو کہ تم میں سے صرف ظالموں کو نہیں پہنچے گا اور یاد رکھو کہ اللہ کا عذاب یقیناً سخت ہوتا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 8;
UPDATE verses SET ts = "اور یاد کرو جب کہ تم تھوڑے تھے اور زمین میں کمزور سمجھے جاتے تھے اور ڈرتے تھے کہ لوگ تم کو اچک کر نہ لے جائیں۔ پھر باوجود اس کے اس نے تم کو (مدینہ میں) جگہ دی اور اپنی مدد سے تمہاری تائید کی اور پاک چیزوں سے تمہیں رزق بخشا تا کہ تم شکر کرو۔ " WHERE ayatNumber = 27 AND chapterNumber = 8;
UPDATE verses SET ts = "اے مومنو! اللہ اور اس کے رسول کی خیانت نہ کرو اور نہ اپنی امانتوں کی خیانت کرو‘ اس حالت میں کہ تم جانتے بوجھتے ہو۔ " WHERE ayatNumber = 28 AND chapterNumber = 8;
UPDATE verses SET ts = "اور یاد رکھو کہ تمہارے مال اور تمہاری اولادیں صرف ایک فتنہ ہیں اور یہ کہ اللہ وہ ذات ہے جس کے پاس بڑا اجر ہے۔ " WHERE ayatNumber = 29 AND chapterNumber = 8;
UPDATE verses SET ts = "اے مومنو! اگر تم اللہ کا تقویٰ اختیار کرو گے تو وہ تمہارے لئے ایک بڑے امتیاز کا سامان پیدا کردے گا اور تمہاری کمزوریوں کو دور کر دے گا اور تمہیں بخش دے گا اور اللہ بڑے فضل والا ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (اے رسول اس وقت کو یاد کر) جب کہ کفار تیرے متعلق تدبیریں کر رہے تھے‘ تا کہ تجھے (ایک جگہ) محصور کر دیں یا تجھ کو قتل کر دیں‘ یا تجھ کو نکال دیں اور وہ بھی تدبیریں کر رہے تھے اور اللہ بھی تدبیریں کر رہا تھا اور اللہ تدبیر کرنے والوں میں سے سب سے بہتر تدبیر کرنے والا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 8;
UPDATE verses SET ts = "اور جب ان کے سامنے ہماری آیتیں پڑھ کر سنائی جاتی ہیں تو وہ کہتے ہیں‘ (بس بس) ہم نے تمہاری بات سن لی اگر ہم چاہیں تو ہم بھی اس قسم کا کلام بنا (کر پیش کر) سکتے ہیں۔ یہ (قرآن) تو صرف پہلوں کی باتیں ہیں۔ " WHERE ayatNumber = 32 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (یاد کر) جب انہوں نے کہا‘ اے اللہ! اگر تیری طرف سے یہی (دین) حق ہے تو ہم پر آسمان سے پتھر برسا‘ یا ہمیں کوئی اور دکھ سے بھرا ہوا عذاب دے۔ " WHERE ayatNumber = 33 AND chapterNumber = 8;
UPDATE verses SET ts = "لیکن اللہ انہیں اس حالت میں عذاب نہیں دے سکتا تھا جب کہ تو ان میں تھا اور نہ اللہ ان کو ایسی حالت میں عذاب دے سکتا تھا جبکہ وہ استغفار کر رہے ہوں۔ " WHERE ayatNumber = 34 AND chapterNumber = 8;
UPDATE verses SET ts = "اور ان کو کیا (مقام حاصل) ہے جس کی وجہ سے باوجود اس کے کہ وہ عزت والی مسجد (یعنی خانہ کعبہ) سے (لوگوں کو) روکتے ہیں۔ اللہ ان کو عذاب نہیں دے گا اور وہ درحقیقت اس کے متولی نہیں اس کے (حقیقی) متولی تو صرف متقی ہیں‘ لیکن ان (کفار) میں سے اکثر اس بات کو جانتے نہیں۔ " WHERE ayatNumber = 35 AND chapterNumber = 8;
UPDATE verses SET ts = "اور خانہ کعبہ کے پاس ان کی نماز سوائے سیٹیاں اور تالیاں بجانے کے اور ہے کیا۔ پس (اے بے دینو)! اپنے کفر کی وجہ سے عذاب کو چکھو۔ " WHERE ayatNumber = 36 AND chapterNumber = 8;
UPDATE verses SET ts = "جنہوں نے کفر کیا ہے وہ یقیناً اپنے مال اللہ کے راستہ سے لوگوں کو روکنے کے لئے خرچ کرتے ہیں۔ وہ اسی طرح ان مالوں کو خرچ کرتے جائیں گے پھر آخر (یہ خرچ) ان کے لئے حسرت کا موجب بن جائے گا اور وہ مغلوب کر دیئے جائیں گے اور جن لوگوں نے کفر کیا ہے ان کو اکٹھا کرکے جہنم کی طرف لے جایا جائے گا۔ " WHERE ayatNumber = 37 AND chapterNumber = 8;
UPDATE verses SET ts = "تا کہ اللہ خبیث کو طیب سے ممتاز کر دے اور خبیث چیزوں کے بعض حصوں کو بعض کے اوپر رکھ دے‘ پھر سب کو ایک ڈھیر کی صورت میں بنا دے اور پھر اس (سارے ڈھیر) کو جہنم میں جھونک دے۔ (سنو) یہی (لوگ) گھاٹا پانے والے ہیں۔ " WHERE ayatNumber = 38 AND chapterNumber = 8;
UPDATE verses SET ts = "تو کافروں سے کہہ دے کہ اگر وہ باز آجائیں ‘ تو جو (قصور) ان سے پہلے ہو چکا ہے انہیں معاف کر دیا جائے گا اور اگر وہ پھر (انہی کرتوتوں کی طرف) لوٹیں گے تو پہلے لوگوں کی (جو) سنت گزر چکی ہے (وہی) ان سے معاملہ ہوگا۔ " WHERE ayatNumber = 39 AND chapterNumber = 8;
UPDATE verses SET ts = "اور ان (کفار) سے لڑتے جائو۔ یہاں تک کہ جبر کا نام و نشان باقی نہ رہے اور دین سب کا سب اللہ ہی کے لئے ہو جائے اور اگر وہ رک جائیں تو اللہ یقیناً ان کے اعمال کو دیکھتا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اگر وہ پیٹھ پھیر لیں تو جان لو کہ اللہ یقیناً تمہارا حامی ہے۔ وہ بہترین حامی اور بہترین مددگار ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 8;
UPDATE verses SET ts = "اور جان لو کہ جو کچھ بھی تم کو غنیمت میں ملے اس میں سے اللہ اور اس کے رسول کے لئے اور (رسول سے) قرب رکھنے والوں کے لئے اور یتیموں اور مسکینوں کے لئے اور مسافروں کے لئے پانچواں حصہ ہے۔ اگر تم اللہ پر ایمان لاتے ہو اور اس پر بھی جو ہم نے اپنے بندہ پر حق و باطل میں فیصلہ کر دینے والے دن میں نازل کیا تھا جس دن کہ دونوں لشکر جمع ہوئے تھے (تو اس پر عمل کرو) اور اللہ ہر ایک چیز پر قادر ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 8;
UPDATE verses SET ts = "(اس دن) جبکہ تم (میدان جنگ کے) ورلے کنارہ پر تھے اور وہ (کافر) پرلے کنارہ پر تھے اور قافلہ تم سے نیچے کی طرف تھا اور اگر تم ان سے وعدہ بھی کرتے  تو تم (جنگ کے) وقت کے بارہ میں ان سے اختلاف کرتے لیکن (خدا نے تم کو جمع کر دیا) تا کہ وہ اس بات کو پورا کر دے جس کے کرنے کا اس نے فیصلہ کر دیا تھا (اور یہ نشان اس لئے بھی دکھایا گیا) تا کہ وہ جو دلیل کے ذریعہ سے ہلاک ہو چکا ہے ہلاک ہو جائے اور جودلیل کے ذریعہ سے زندہ ہو چکا ہے زندہ ہو جائے اور اللہ یقیناً بہت سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 43 AND chapterNumber = 8;
UPDATE verses SET ts = "(یہ واقعہ اس وقت کا ہے) جبکہ اللہ نے تیری خواب میں تجھے انہیں کم کرکے دکھایا تھا اور اگر تجھے وہ (کفار) بڑی تعداد میں دکھائے جاتے تو تم ضرور کمزوری دکھاتے اور اس معاملہ میں (یعنی لڑائی کے بارہ میں) آپس میں جھگڑتے (کہ لڑائی کی جائے یا نہ) لیکن اللہ نے (تم کو) محفوظ رکھا۔ وہ دلوں تک کی باتوں کو خوب جانتا ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (یاد کرو) جبکہ وہ ان (کفار) کو تمہاری نظر میں لڑائی کے وقت کمزور دکھاتا تھا اور تم کو ان کی نظر میں کمزور دکھاتا تھا۔ تا کہ اللہ وہ بات پوری کر دے جس کا وہ فیصلہ کر چکا ہے اور اللہ ہی کی طرف سب باتیں لوٹائی جائیں گی۔ " WHERE ayatNumber = 45 AND chapterNumber = 8;
UPDATE verses SET ts = "اے مومنو! جب تم (کافروں کی) کسی فوج کے مقابل پر آئو تو قدم جمائے رکھو اور اللہ کو بہت یاد کیا کرو تا کہ تم کامیاب ہو جائو۔ " WHERE ayatNumber = 46 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اللہ اور اس کے رسول کی اطاعت کرتے رہا کرو اور آپس میں اختلاف نہ کیا کرو (اگر ایسا کرو گے) تو دل چھوڑ بیٹھو گے اور تمہاری طاقت جاتی رہے گی‘ اور صبر کرتے رہو اللہ یقیناً صبر کرنے والوں کے ساتھ ہے۔ " WHERE ayatNumber = 47 AND chapterNumber = 8;
UPDATE verses SET ts = "اوران لوگوں کی طرح مت بنو جو اپنے گھروں سے  لوگوں کو (اپنی بہادری اور دینی غیرت) دکھانے کے لئے تکبر کرتے ہوئے نکلے۔ اور جو اللہ کے راستہ سے (لوگوں کو) روکتے ہیں۔ اللہ ایسے لوگوں کے اعمال کو تباہ کرنے کا فیصلہ کر چکا ہے۔ " WHERE ayatNumber = 48 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (یاد کرو) جب شیطان نے کافروں کو ان کے اعمال اچھے کرکے دکھائے اور کہا کہ کہ آج لوگوں میں سے کوئی تم پر غالب نہیں آسکتا اور میں تمہاری پشت پناہ ہوں۔ پھر جب دونوں لشکر آمنے سامنے ہوئے تو وہ اپنی ایڑیوں کے بل پھر گیا اور اس نے کہا میں تم سے بیزار ہوں۔ میں وہ کچھ دیکھتا ہوں جو تم نہیں دیکھتے میں اللہ سے ڈرتا ہوں اور اللہ کا عذاب سخت ہوتا ہے۔ " WHERE ayatNumber = 49 AND chapterNumber = 8;
UPDATE verses SET ts = "نیز (یاد کرو) جب منافق اور جن کے دلوں میں مرض تھی کہتے تھے کہ ان مسلمانوں کو ان کے دین نے مغرور کر دیا ہے۔ حالانکہ جو اللہ پر توکل کرتا ہے وہ دیکھ لیتا ہے کہ اللہ بڑا غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 50 AND chapterNumber = 8;
UPDATE verses SET ts = "کاش کہ تو اس وقت کا تصور کرے جب ملائکہ کافروں کی روح قبض کرتے ہیں۔ اور ان کے مونہوں اور پیٹھوں پر ضربیں لگاتے جاتے ہیں اور (یہ کہتے جاتے ہیں کہ) ایک جلنے والا عذاب چکھو۔ " WHERE ayatNumber = 51 AND chapterNumber = 8;
UPDATE verses SET ts = "یہ (عذاب) تمہارے ہاتھوں کی گزشتہ کرتوتوں کا نتیجہ ہے اور (سمجھ لو کہ) اللہ اپنے بندوں پر ذرا سا بھی ظلم نہیں کرتا۔ " WHERE ayatNumber = 52 AND chapterNumber = 8;
UPDATE verses SET ts = "فرعون کی قوم کے طریق کے مطابق اور ان لوگوں کے (طریق کے مطابق) جو ان سے پہلے گزرے ہیں (تمہارا حشر ہوگا) ان سب نے اللہ کی آیتوں کا انکار کیا تھا۔ اس لئے اللہ نے ان کے گناہوں کے سبب سے ان کو پکڑ لیا۔ اللہ یقیناً بڑی طاقت والا (اور) سخت سزا دینے والا ہے۔ " WHERE ayatNumber = 53 AND chapterNumber = 8;
UPDATE verses SET ts = "یہ (اس لئے ہوگا) کہ اللہ جب کبھی کسی قوم پر کوئی نعمت نازل کرتا ہے تو اس نعمت کو بدلتا نہیں‘ جب تک کہ وہ قوم اپنے دل کی حالت کو نہ بدل دے اور اللہ یقیناً بہت سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 8;
UPDATE verses SET ts = "(اے منکرو! تمہارا حال بھی) فرعون کی قوم اور ان سے پہلے لوگوں کی طرح (ہوگا) جنہوں نے اپنے رب کی آیات کا انکار کیا تھا۔ تب ہم نے ان کو ان کے گناہوں کے سبب سے ہلاک کر دیا۔ (اور ان کے انعامات اس لئے چھین لئے گئے کہ انہوں نے خدا سے منہ پھیر لیا) اور ہم نے فرعون کی قوم کو غرق کر دیا تھا اور وہ سب ظالم تھے۔ " WHERE ayatNumber = 55 AND chapterNumber = 8;
UPDATE verses SET ts = "اللہ کے نزدیک وہ (لوگ) جانوروں سے بھی بدتر ہیں جنہوں نے (خدا کی آیتوں کا) انکار کیااور وہ ایمان نہیں لاتے۔ " WHERE ayatNumber = 56 AND chapterNumber = 8;
UPDATE verses SET ts = "وہ لوگ جن سے تو نے عہد کیا مگر وہ ہر دفعہ اپنا عہد توڑ دیتے ہیں اور (خدا کا) تقویٰ اختیار نہیں کرتے۔ " WHERE ayatNumber = 57 AND chapterNumber = 8;
UPDATE verses SET ts = "پس اگر تو لڑائی میں ان پر قابو پالے تو ان کے ذریعہ سے جو ان کے پیچھے (اور لشکر) ہیں ان کو بھی بھگا دے تا کہ وہ نصیحت حاصل کریں۔ " WHERE ayatNumber = 58 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اگر تو کسی قوم سے عہد شکنی کا ڈر رکھتا ہو تو تو اس طرح ان کا عہد ختم کر دے جس سے وہ سمجھ لیں کہ اب تم دونوں (فریق اپنی پابندیوں سے) آزاد ہو۔ اللہ خیانت کرنے والوں کو پسند نہیں کرتا۔ " WHERE ayatNumber = 59 AND chapterNumber = 8;
UPDATE verses SET ts = "اور کافر کبھی یہ خیال نہ کریں کہ وہ (اپنی دھوکا بازیوں کے ذریعہ سے) آگے بڑھ گئے ہیں وہ کبھی (بھی مومنوں کو) بے بس نہیں بنا سکتے۔ " WHERE ayatNumber = 60 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (اے مسلمانو! چاہیے کہ) تم ان (لڑنے والوں) کے لئے جس حد تک ممکن ہو‘ اپنی طاقتیں جمع کرو (ملکی انتظام کے ذریعہ سے بھی) اور سرحدوں پر چھائونیاں بنانے کے ذریعہ سے بھی ان (چھائونیوں) کے ذریعہ تم اللہ کے دشمنوں اور اپنے دشمنوں کو ڈراتے ہو۔ اور ان کے سوا اور (دشمنوں) کو بھی جو ان (سرحدی دشمنوں) کے پرے ہیں تم ان کو نہیں جانتے لیکن اللہ ان کو جانتا ہے اور تم جو کچھ بھی اللہ کے راستہ میں خرچ کرو گے وہ تم کو اس کا پورا پورا اجر دے گا اور تم سے بے انصافی کا معاملہ نہیں کیا جائے گا۔ " WHERE ayatNumber = 61 AND chapterNumber = 8;
UPDATE verses SET ts = "اور (اگر تمہاری تیاریوں کو دیکھ کر) وہ (کافر) صلح کی طرف مائل ہوں تو (اے رسول)! تو بھی صلح کی طرف مائل ہو اور اللہ پر توکل کر۔ اللہ یقیناً بہت دعائیں سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 62 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اگر وہ اس بات کا ارادہ رکھتے ہوں کہ بعد میں تجھے دھوکا دیں تو (یاد رکھ کہ) اللہ تیرے لئے یقیناً کافی ہے وہی ہے جس نے تجھ کو مومنوں کے ذریعہ اور اپنی مدد کے ذریعہ مضبوط کیا۔ " WHERE ayatNumber = 63 AND chapterNumber = 8;
UPDATE verses SET ts = "اور ان کے دلوں کو آپس میں باندھ دیا (یہاں تک کہ صحابہ تیرے پسینہ کی جگہ اپنا خون بہانے کے لئے تیار ہو گئے) اگر تو جو کچھ بھی زمین میں ہے ان پر خرچ کر دیتا تو بھی ان کے دلوں کو اس طرح باندھ نہیں سکتا تھا۔ لیکن اللہ نے ان میں باہمی محبت (اور تیرے ساتھ بھی محبت) قائم کر دی وہ یقیناً غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 64 AND chapterNumber = 8;
UPDATE verses SET ts = "اے نبی! اللہ اور وہ مومن جو تیرے تابع ہو گئے ہیں تیرے لئے کافی ہیں (جو مخالف ہیں ان کی پروا نہ کر) ۔" WHERE ayatNumber = 65 AND chapterNumber = 8;
UPDATE verses SET ts = "اے نبی! مومنوں کو (کافروں سے) لڑنے کی باربار زور سے تحریک کرتا رہ اگر تم میں سے بیس(۲۰) ثابت قدم رہنے والے (مومن) ہوں گے تو وہ دو سو (کافروں) پر غالب آجائیں گے اور اگر سو (ثابت قدم رہنے والے مومن) ہوں گے تو وہ ایک ہزار کافروں پر غالب آجائیں گے کیونکہ وہ ایسی قوم ہیں جو سمجھتے نہیں (جبکہ مومن سمجھ بوجھ کر اپنے ایمان پر قائم ہیں) ۔" WHERE ayatNumber = 66 AND chapterNumber = 8;
UPDATE verses SET ts = "ابھی اللہ نے تم سے بوجھ کو ہلکا کر دیا ہے اور جان لیا ہے کہ تم میں ابھی کچھ کمزوری ہے (یعنی سب مومن انتہائی درجہ کے مومن نہیں ہوچکے)  پس چاہیے کہ اگر تم میں سے سو ثابت قدم رہنے والے (مومن) ہوں تو دو سو (کافروں) پر غالب آجائیں اور اگر تم میں سے ہزار ثابت قدم رہنے والے مومن ہوں تو دو ہزار (کافروں) پر اللہ کے حکم سے غالب آجائیں اور اللہ ثابت قدم رہنے والے لوگوں کے ساتھ ہے۔ " WHERE ayatNumber = 67 AND chapterNumber = 8;
UPDATE verses SET ts = "کسی نبی کی یہ شان نہیں کہ وہ قیدی بنائے۔ جب تک کہ وہ ملک میں خون ریزی نہ کرلے۔ (اگر تم بغیر باقاعدہ جنگ کے قیدی پکڑو۔تو) تم دنیوی اموال کے طالب قرار پائو گے۔ حالانکہ اللہ تمہارے لئے آخرت کی نعمتیں چاہتا ہے اللہ بڑا غالب (اور) بڑی حکمتوں والا ہے۔ " WHERE ayatNumber = 68 AND chapterNumber = 8;
UPDATE verses SET ts = "(اور) اگر اللہ کی طرف سے ایک حکم صریح پہلے سے نہ گزر چکا ہوتا تو جو کچھ تم نے (قیدیوں کا) فدیہ لیا تھا اس کی وجہ سے تم کو بڑا عذاب پہنچتا۔ " WHERE ayatNumber = 69 AND chapterNumber = 8;
UPDATE verses SET ts = "پس (چونکہ فدیہ کے جواز کا حکم پہلے سے اتر چکا ہے) جو کچھ تم کو غنیمت میں سے ملے اور (وہ الٰہی حکم کے ماتحت) حلال اور طیب ہو اسے کھائو اور اللہ کا تقویٰ اختیار کرو۔ اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 70 AND chapterNumber = 8;
UPDATE verses SET ts = "اے نبی! جو لوگ تمہارے ہاتھوں میں (جنگی) قیدیوں کی حیثیت میں ہیں ان سے کہہ دے کہ اگر اللہ تمہارے دلوں میں نیکی دیکھے گا تو جو کچھ تم سے (تاوان جنگ کے طور پر) لیا گیا ہے اس سے بہتر تم کو دے دے گا اور (علاوہ ازیں) تمہارے گناہ بھی معاف کر دے گا اور اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 8;
UPDATE verses SET ts = "اور اگر وہ (آزاد ہونے پر) تجھ سے خیانت کرنے کا ارادہ کریں تو اس سے پہلے وہ اللہ سے بھی خیانت کر چکے ہیں پھر بھی اس نے ان کو تمہارے قبضہ میں دے دیا اور اللہ بہت جاننے والا (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 72 AND chapterNumber = 8;
UPDATE verses SET ts = "وہ لوگ جو کہ ایمان لائے ہیں اور جنہوں نے ہجرت کی ہے اور اللہ کے راستہ میں اپنی جانوں اور مالوں کے ذریعہ سے جہاد کیا ہے اور جنہوں نے (ہجرت کرنے والوں کو اپنے گھروں میں) جگہ دی ہے اور (ان کی) مدد کی ہے ان میں سے بعض بعض کے دلی دوست ہیں اور وہ لوگ جو ایمان لائے ہیں اور انہوں نے ہجرت نہیں کی ان سے دلی دوستی کرنا تمہارا کام نہیں جب تک کہ وہ ہجرت نہ کریں اور اگر وہ تم سے دین کے بارہ میں مدد مانگیں تو تم پر ان کی مدد کرنا فرض ہے مگر اس قوم کے خلاف نہیں کہ جن کے اور تمہارے درمیان کوئی عہد ہو اور اللہ تمہارے اعمال کو دیکھ رہا ہے۔ " WHERE ayatNumber = 73 AND chapterNumber = 8;
UPDATE verses SET ts = "اور جن لوگوں نے کفر کیا ہے وہ آپس میں ایک دوسرے کے دوست ہیں اگر تم وہی کچھ نہ کرو جس کا ہم نے حکم دیا ہے تو زمین میں بڑا فساد اور فتنہ پیدا ہو جائے گا۔ " WHERE ayatNumber = 74 AND chapterNumber = 8;
UPDATE verses SET ts = "اور وہ لوگ جو ایمان لائے ہیں اور جنہوں نے ہجرت کی ہے اور جنہوں نے اللہ کے راستہ میں جہاد کیا ہے اور جنہوں نے (ہجرت کرنے والوں کو) اپنے گھروں میں جگہ دی ہے اور (ان کی) مدد کی ہے وہی سچے مومن ہیں ان کو گناہوں کی معافی بھی حاصل ہوگی‘ اور معزز رزق ملے گا۔ " WHERE ayatNumber = 75 AND chapterNumber = 8;
UPDATE verses SET ts = "اور جو لوگ (موجودہ وقت) کے بعد ایمان لائیں گے اور ہجرت بھی کریں گے اور تمہارے ساتھ مل کر جہاد کریں گے وہ بھی تمہاری جماعت میں سے سمجھے جائیں گے اور بعض رحمی رشتہ دار بعض کی نسبت اللہ کی کتاب کی رو سے زیادہ قریبی ہوتے ہیں۔ اللہ ہر چیز کو خوب جانتا ہے۔ " WHERE ayatNumber = 76 AND chapterNumber = 8;
UPDATE verses SET ts = "اللہ اور اس کے رسول کی طرف سے (ان آیات میں) ان مشرکوں (کے الزام) سے برأ ت کا اعلان کیا جاتا ہے جن سے تم نے شرط باندھی تھی (کہ تمہاری فتح ہوگی اور ان کی شکست) ۔" WHERE ayatNumber = 1 AND chapterNumber = 9;
UPDATE verses SET ts = "چنانچہ ملک عرب میں چار مہینے پھر کر دیکھ لو اور جان لو کہ تم اللہ کو ہرا نہیں سکتے اور یہ (بھی جان لو) کہ اللہ کفار کو رسوا کرکے چھوڑے گا۔ " WHERE ayatNumber = 2 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ اور اس کے رسول کی طرف سے تمام لوگوں میں حج اکبر کے دن یہ اعلان (کیا جاتا) ہے کہ اللہ اور (اسی طرح) اس کا رسول بھی مشرکوں (کے سب الزاموں) سے بری ہو چکے ہیں (اور مکہ فتح ہو چکا ہے) سو اگر (اس نشان کو دیکھ کر) تم توبہ کرو تو یہ تمہارے لئے بہتر ہوگا اور اگر تم پیٹھ پھیر لو تو جان لو کہ تم ہرگز اللہ کو ہرا نہیں سکتے اور تو کفار کو خبر دے دے کہ ان کے لئے (ایک) دردناک عذاب (مقدر) ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 9;
UPDATE verses SET ts = "ہاں مشرکوں میں سے جن سے تم نے عہد کیا ہے پھر انہوں نے تم سے (بالکل) عہد شکنی نہیں کی‘ اور تمہارے خلاف کسی کی مدد نہیں کی تم ان کے عہد کو ان (عہدوں) کی مدت (مقررہ) تک نباہو (اور ان کو ملک سے نہ نکالو) اللہ یقیناً متقیوں کو پسند کرتا ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 9;
UPDATE verses SET ts = "پس جب وہ چار مہینے گزر جائیں جن میں (عرب کے کافروں سے اوپر کی آیات میں) لڑائی سے منع کیا گیا تھا (مگر پھر بھی وہ معاہدہ کی طرف راغب نہیں ہوئے حالانکہ وہ اس سے پہلے مسلمانوں سے لڑ رہے تھے) تو مشرکوں کے اس خاص گروہ کو جہاں بھی پائو قتل کرو۔ اور ان کو گرفتار کرلو اور ان کو (ان کے قلعوں میں) محصور کر دو اور ہر گھات کی جگہ پر ان کے لئے بیٹھو۔ پس اگر وہ توبہ کریں اور نماز قائم کریں اور زکٰوۃ دیں تو انکا راستہ کھول دو اللہ یقیناً بڑا بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 9;
UPDATE verses SET ts = "اور مشرکوں سے اگر کوئی تجھ سے پناہ مانگے تو اس کو پناہ دے یہاں تک کہ وہ اللہ کا کلام سن لے۔ پھر اس کو اس کے امن کی جگہ تک پہنچا دے۔ کیونکہ وہ ایسی قوم ہے جو (حقیقت کو) نہیں جانتی۔ " WHERE ayatNumber = 6 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ اور اس کے رسول مشرکوں سے کس طرح عہدوپیمان کر سکتے ہیں۔ سوائے  ان (مشرکوں) کے جن کے ساتھ تم نے مسجد حرام کے پاس عہد کیا تھا۔ پس جب تک وہ (تمہارے مقابلہ پر) اپنے عہد پر قائم رہیں‘ تم بھی ان کے ساتھ معاہدہ پر قائم رہو۔ اللہ (عہد توڑنے سے) بچنے والوں کو ہی پسند کرتا ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 9;
UPDATE verses SET ts = "(ہاں اس قسم کے مشرکوں کو کوئی رعایت) کس طرح (دی جا سکتی ہے) کیونکہ وہ اگر تم پر غالب آجائیں تو تمہاری کسی رشتہ داری یا معاہدہ کی پروا نہیں کریں گے وہ تم کو اپنے  منہ (کی باتوں) سے خوش رکھتے ہیں۔ حالانکہ ان کے دل (ان باتوں سے) انکار کرتے ہیں اور ان میں سے اکثر عہد و پیمان کو توڑنے والے ہوتے ہیں۔ " WHERE ayatNumber = 8 AND chapterNumber = 9;
UPDATE verses SET ts = "انہوں نے اللہ کی آیات کے بدلہ میں ایک حقیر سی قیمت وصول کی ہے اور اس کے راستہ سے (لوگوں کو) روکا ہے۔ یقیناً ان کے اعمال بہت برے ہیں۔ " WHERE ayatNumber = 9 AND chapterNumber = 9;
UPDATE verses SET ts = "کسی مومن کے بارہ میں بھی وہ رشتہ داری کا لحاظ نہیں کرتے اور نہ عہدو پیمان کا۔ اور وہ حد سے بڑھے ہوئے ہیں۔ " WHERE ayatNumber = 10 AND chapterNumber = 9;
UPDATE verses SET ts = "پس اگر وہ توبہ کر لیں‘ اور نمازوں کو قائم کریں اور زکٰوۃ دیں تو دین میں تمہارے بھائی ہیں اور ہم (اپنی) آیات کو علم والی قوم کے لئے کھول کھول کر بیان کرتے ہیں۔ " WHERE ayatNumber = 11 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اگر (یہ لوگ) اپنے عہد و پیمان کے بعد اپنی قسموں کو توڑ دیں  اور تمہارے دین پر طعنہ کریں‘ تو (ایسے) سردارانِ کفر سے لڑائی کرو۔ تا کہ وہ شرارتوں سے باز آجائیں کیونکہ ان کی قسموں کا کوئی اعتبار نہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 9;
UPDATE verses SET ts = "(اے مومنو)! کیا تم اس قوم سے نہیں لڑو گے جنہوں نے اپنی قسمیں توڑ دیں اور رسول کو (اس کے گھر سے) نکالنے کا فیصلہ کر لیا اور تم سے (جنگ چھیڑنے میں) انہوں نے (ہی) ابتدا کی کیا تم ان سے ڈرتے ہو؟ (اگر ایسا ہے تو) اگر تم مومن ہو تو سمجھ لو کہ اللہ اس بات کا زیادہ حق دار ہے کہ تم اس سے ڈرو۔ " WHERE ayatNumber = 13 AND chapterNumber = 9;
UPDATE verses SET ts = "ان سے لڑو۔ اللہ ان کو تمہارے ہاتھوں سے عذاب دلوائے گا اور ان کو رسوا کرے گا اور تمہیں ان پر غلبہ دے گا اور اس (ذریعہ) سے مومن قوم کے دلوں کو (صدمہ اور خوف سے) نجات دے گا۔ " WHERE ayatNumber = 14 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ان کے دلوں کے غصہ کو دور کر دے گا اور اللہ جس پر چاہتا ہے فضل کرتا ہے اور اللہ بہت جاننے والا (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 15 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا تم خیال کرتے ہو کہ تم کو یونہی چھوڑ دیا جائے گا‘ حالانکہ اب تک اللہ نے ان لوگوں کو جنہوں نے تم میں سے جہاد کیا ہے (ان کے مقابلہ میں جنہوں نے جہاد نہیں کیا) ظاہر نہیں کیا۔ جو خدا اور اس کے رسول اور مومنوں کے مقابلہ میں کفار سے خفیہ جوڑ توڑ نہیں رکھتے اور اللہ تمہارے اعمال سے اچھی طرح واقف ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 9;
UPDATE verses SET ts = "(ایسے) مشرکوں کو (کوئی) حق نہیں پہنچتا کہ اللہ کی مسجدوں کو آباد کریں جبکہ وہ اپنی جانوں پر (خود) کفر کی گواہی دے رہے ہیں۔ یہی لوگ ہیں جن کے اعمال اکارت چلے گئے اور وہ آگ میں ایک لمبے عرصہ تک رہتے چلے جائیں گے۔ " WHERE ayatNumber = 17 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ کی مسجدوں کو تو وہی آباد کرتا ہے جو اللہ اور یوم آخرت پر ایمان لاتا ہے۔ اور نمازوں کو قائم کرتا ہے اور زکٰوۃ دیتا ہے اور اللہ کے سوا کسی سے نہیں ڈرتا سو قریب ہے کہ ایسے لوگ کامیابی کی طرف لے جائے جائیں۔ " WHERE ayatNumber = 18 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا تم نے حاجیوں کو پانی پلانے اور خانہ کعبہ کو آباد رکھنے (کے کام) کو اس شخص (کے کام) کی طرح سمجھ لیا ہے جو اللہ اور یوم آخرت پر ایمان لایا اور اس نے اللہ کے راستہ میں جہاد کیا یہ (دونوں گروہ) اللہ کے نزدیک (ہرگز) برابر نہیں اور اللہ ظالم قوم کو ہرگز کامیابی کی طرف نہیں لے جاتا۔ " WHERE ayatNumber = 19 AND chapterNumber = 9;
UPDATE verses SET ts = "(وہ لوگ) جو (کہ) ایمان لائے اور جنہوں نے ہجرت کی اور (پھر) اللہ کے راستہ میں اپنے مالوں (کے ذریعہ سے بھی) اور جانوں کے ذریعہ سے (بھی) جہاد کیا۔ اللہ کے نزدیک درجہ میں بہت بلند ہیں اور وہی لوگ کامیاب ہونے والے ہیں۔ " WHERE ayatNumber = 20 AND chapterNumber = 9;
UPDATE verses SET ts = "ان کا رب ان کو اپنی عظیم الشان رحمت کی خبر دیتا ہے اور (اپنی) رضا مندی اور ایسی جنتوں کی بھی جن میں ان کے لئے دائمی نعمت ہوگی۔ " WHERE ayatNumber = 21 AND chapterNumber = 9;
UPDATE verses SET ts = "(وہ) ان میں بستے چلے جائیں گے (یاد رکھو کہ) اللہ کے پاس یقیناً بہت بڑا اجر ہے۔ " WHERE ayatNumber = 22 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! اپنے باپ دادوں اور بھائیوں کو (اپنا حقیقی) دوست نہ بنائو اگر وہ ایمان کے مقابلہ میں کفر سے زیادہ محبت کرتے ہوں اور تم میں سے جو لوگ ان سے ایسی دوستی کریں گے وہ یقیناً ظالم ہوں گے۔ " WHERE ayatNumber = 23 AND chapterNumber = 9;
UPDATE verses SET ts = "تو (مومنوں سے) کہہ دے کہ اگر تمہارے باپ دادے اور تمہارے بیٹے اور تمہارے بھائی اور تمہاری بیویاں اور تمہارے (دوسرے) رشتہ دار اور وہ مال جو تم نے کمائے ہیں اور وہ تجارتیں جن کے نقصان سے تم ڈرتے ہو اور وہ مکان جن کو تم پسند کرتے ہو‘ تم کو اللہ اور اس کے رسول اور اس کے راستہ میں جہاد کرنے کی نسبت زیادہ پیارے ہیں تو تم انتظار کرو یہاں تک کہ اللہ اپنے فیصلہ کو ظاہر کردے اور اللہ اطاعت سے نکلنے والی قوم کو کبھی (کامیابی کا) راستہ نہیں دکھاتا۔ " WHERE ayatNumber = 24 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے بہت سے مواقع پر تمہاری مدد کی ہے (خصوصاً) حنین (کی جنگ) کے دن جبکہ تمہاری کثرت نے تم کو متکبر بنا دیا تھا۔ پھر وہ (کثرت) تمہارے کسی کام نہ آئی اور زمین باوجود فراخی کے تم پر تنگ ہوگئی۔ اور تم نے پیٹھ دکھاتے ہوئے منہ پھیر لیا۔ " WHERE ayatNumber = 25 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اللہ نے اپنی سکینت اپنے رسول اور مومنوں پر اتاری اور ایسے لشکر اتارے جن کو تم نہیں دیکھ رہے تھے اور کفار کو عذاب دیا اور کفار کی یہی جزا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اللہ ایسی سزا کے بعد جس پر چاہتا ہے رحم کر دیتا ہے اور اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! مشرک لوگ حقیقتاً گندے (اور ناپاک) ہیں پس وہ اس سال کے بعد مسجد حرام (یعنی خانہ کعبہ) کے قریب مت آئیں اور اگر تم کو غربت کا خطرہ ہو تو اللہ اگر اس نے ایسا چاہا تم کو اپنے فضل سے ضرور غنی بنا دے گا۔ اللہ یقیناً بہت جاننے والا (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 9;
UPDATE verses SET ts = "جو لوگ اللہ پرایمان نہیں لاتے اور نہ یوم آخرت پر اور نہ اسے جسے اللہ اور اس کے رسول نے حرام قرار دیا ہے حرام قرار دیتے ہیں اور نہ سچے دین کو اختیار کرتے ہیں۔ یعنی وہ لوگ جن کو کتاب دی گئی ہے ان سے جنگ کرو جب تک کہ وہ اپنی مرضی سے جزیہ ادا نہ کریں اور وہ تمہارے ماتحت نہ آجائیں۔ " WHERE ayatNumber = 29 AND chapterNumber = 9;
UPDATE verses SET ts = "اور یہودی کہتے ہیں کہ عزیر اللہ کا بیٹا ہے اور نصرانی کہتے ہیں کہ مسیح اللہ کا بیٹا ہے یہ (بات) صرف ان کے منہ کی لاف ہے وہ (صرف) اپنے سے پہلے کفار کی باتوں کی نقل کر رہے ہیں۔ اللہ ان کو ہلاک کرے وہ (حقیقت سے) کیسے دور جا رہے ہیں۔ " WHERE ayatNumber = 30 AND chapterNumber = 9;
UPDATE verses SET ts = "انہوں نے احبار اور رہبان کو اللہ کے سوا اپنا رب بنا لیا ہے۔ اسی طرح مسیح ابن مریم کو بھی۔ حالانکہ ان کو صرف یہ حکم دیا گیا تھا کہ وہ ایک خدا کی عبادت کریں‘ جس کے سوا کوئی معبود نہیں وہ ان کے شرک سے پاک ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 9;
UPDATE verses SET ts = "وہ چاہتے ہیں کہ اللہ کے نور کو اپنے منہ (کی پھونکوں) سے بجھا دیں اور اللہ اپنے نور کو پورا کرنے کے سوا دوسری ہر بات سے انکار کرتا ہے خواہ کفار کو کتنا ہی برا لگے۔ " WHERE ayatNumber = 32 AND chapterNumber = 9;
UPDATE verses SET ts = "وہی ہے جس نے اپنے رسول کو ہدایت اور دین حق دے کر بھیجا تا کہ (باقی) تمام دینوں پر اسے غالب کر دے گو مشرکوں کو یہ بات بہت ہی بری لگے۔ " WHERE ayatNumber = 33 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! بہت سے احبار اور رہبان لوگوں کے مالوں کو ناواجب طور پر کھاتے ہیں اور (لوگوں کو) اللہ کے راستہ سے روکتے ہیں۔ اور وہ لوگ (بھی) جو سونے اور چاندی کو جمع رکھتے ہیں۔ اور اللہ کے راستہ میں خرچ نہیں کرتے۔ ان کو دردناک عذاب کی خبر دے۔ " WHERE ayatNumber = 34 AND chapterNumber = 9;
UPDATE verses SET ts = "(یہ عذاب) اس دن (ہوگا) جبکہ اس (جمع شدہ سونے اور چاندی) پر جہنم کی آگ بھڑکائی جائے گی پھر اس (سونے اور چاندی) سے ان کے ماتھوں اور پہلوئوں اور پیٹھوں کو داغ لگائے جائیں گے (اور کہا جائے گا کہ) یہ وہ چیز ہے جس کو تم اپنی جانوں کے لئے جمع کرتے تھے۔ پس جن چیزوں کو تم جمع کرتے تھے ان کے مزہ کو چکھو۔ " WHERE ayatNumber = 35 AND chapterNumber = 9;
UPDATE verses SET ts = "یقیناً مہینوں کی گنتی اللہ کے نزدیک بارہ مہینے (ہی) ہوتی ہے۔ یہ اللہ کا قانون ہے۔ اس دن سے کہ آسمانوں اور زمین کو اس نے پیدا کیا ہے ان (مہینوں) میں سے چار عزت کے مہینے (کہلاتے) ہیں۔ یہ مضبوط دین ہے۔ پس (چاہیے کہ) ان مہینوں میں اپنی جانوں پر ظلم نہ کیا کرو۔ اور تمام مشرکوں سے لڑو۔ جس طرح کہ وہ سب کے سب تم سے لڑتے ہیں اور یاد رکھو کہ اللہ متقیوں کے ساتھ ہے۔ " WHERE ayatNumber = 36 AND chapterNumber = 9;
UPDATE verses SET ts = "نَسِیْ ء   صرف کفر کے زمانے کی زیادتی ہے جس کے ذریعہ سے کافر لوگ گمراہ ہوتے رہتے ہیں وہ اسے ایک سال حلال قرار دیتے ہیں اور دوسرے سال حرام قرار دیتے ہیں تا کہ وہ مہینوں کو سال کی مقررہ گنتی کے برابر کر دیں اور ان مہینوں کے مطابق کر دیں جن میں جنگ منع ہے (اور اپنی پیدا کردہ تبدیلی کی وجہ سے جو اختلاف ہو گیا ہے اسے پورا کردیں) ان اعمال کی خرابی (شیطان کی طرف سے) ان کو خوبصورت کرکے دکھا دی گئی ہے اور اللہ کافر قوم کو کامیابی کا راستہ نہیں دکھاتا۔ " WHERE ayatNumber = 37 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! تم کو کیا ہو گیا ہے کہ جب تم سے کہا جاتا ہے کہ اللہ کے راستہ میں لڑنے کے لئے (سب مل کر) نکلو تو تم لوگ اپنے ملک (کی محبت) کی طرف جھک جاتے ہو کیا تم آخرت کے مقابلہ میں دنیا کی زندگی پسند کرتے ہو؟ (اگر ایسا ہے) تو یاد رکھو کہ دنیا کی زندگی کا سامان آخرت کے مقابلہ میں صرف ایک حقیر چیز ہے۔ " WHERE ayatNumber = 38 AND chapterNumber = 9;
UPDATE verses SET ts = "اگر تم (سب مل کر اللہ کے راستہ میں) لڑنے کے لئے نکلو گے تو وہ تم کو دردناک عذاب دے گا اور تمہارے سوا ایک اور قوم کو بدل کر لے آئے گا اور تم اسے (یعنی اللہ کو) کوئی نقصان نہیں پہنچا سکو گے اور اللہ ہر چیز پر (جس کے کرنے کا وہ ارادہ کرے) قادر ہے۔ " WHERE ayatNumber = 39 AND chapterNumber = 9;
UPDATE verses SET ts = "اگر تم اس رسول کی مدد نہ کرو تو (یاد رکھو کہ) اللہ اس کی اس وقت بھی مدد کر چکا ہے جبکہ اسے کافروں نے دو میں سے ایک کی صورت میں نکال دیا تھا جبکہ وہ دونوں غار میں تھے اور جبکہ وہ اپنے ساتھی (ابوبکر) سے کہہ رہا تھا کہ کسی گذشتہ بھول چوک پر غم نہ کرو۔ اللہ یقیناً ہمارے ساتھ ہے۔ پس اللہ نے اس پر اپنی سکینت نازل کی۔ اور اس کی ایسے لشکروں سے مدد کی جن کو تم نہیں دیکھتے تھے اور ان لوگوں کی بات کو نیچا کر دیا جنہوں نے کفر کیا تھا اور اللہ ہی کی بات اونچی ہو کر رہتی ہے اور اللہ بڑا غالب (اور) حکمت والا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! جہاد کے لئے نکل کھڑے ہو خواہ تم بے سازوسامان ہو‘ یا باسازوسامان۔ اور اپنے مالوں اور اپنی جانوں کے ساتھ اللہ کے راستہ میں جہاد کرو۔ یہ تمہارے لئے اگر تم جانتے ہو بہت بہتر ہوگا۔ " WHERE ayatNumber = 41 AND chapterNumber = 9;
UPDATE verses SET ts = "اگر قریب میں ملنے والا فائدہ ہوتا یا چھوٹا سفر ہوتا تو یہ لوگ تیرے پیچھے چل پڑتے لیکن انہیں مسافت دور معلوم ہوئی۔ لیکن (اب وہ تیری واپسی کے بعد) اللہ کی قسمیں کھا کر کہیں گے کہ اگر ہماری طاقت میں ہوتا تو ہم ضرور تمہارے ساتھ نکل کھڑے ہوتے (یہ لوگ) اپنی جانوں کو ہلاک کرتے ہیں اور اللہ جانتا ہے کہ یہ جھوٹے ہیں۔ " WHERE ayatNumber = 42 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے تیری غلطی کے بداثر کو مٹا دیا اور تجھے عزت دی۔ لیکن آخر تم نے کیوں ان (اجازت مانگنے والوں) کو پیچھے رہنے کی اجازت دی تھی (تم ان کے جانے پر اصرار کرتے) یہاں تک کہ سچ بولنے والے تجھ پر ظاہر ہو جاتے اور تو جھوٹوں کو بھی جان لیتا۔ " WHERE ayatNumber = 43 AND chapterNumber = 9;
UPDATE verses SET ts = "جو اللہ اور یوم آخرت پر ایمان لاتے ہیں‘ وہ اپنے مالوں اور جانوں کے ساتھ جہاد کرنے سے بچنے کی اجازت نہیں مانگتے۔ اللہ متقیوں سے خوب واقف ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 9;
UPDATE verses SET ts = "پیچھے رہنے کی اجازت صرف وہ مانگتے ہیں جو کہ اللہ اور یوم آخرت پر ایمان نہیں لاتے اور ان کے دلوں میں شبہات پیدا ہو گئے ہیں پس وہ اپنے شبہات کی وجہ سے کبھی ادھر ہوتے ہیں کبھی ادھر۔ " WHERE ayatNumber = 45 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اگر وہ (جنگ کے لئے) نکلنے کا پختہ ارادہ رکھتے تو اس کے لئے کوئی تیاری بھی کرتے۔ لیکن اللہ نے ان کے (جنگ کے لئے) نکلنے  کو پسند نہیں کیا۔ پس ان کو اپنی جگہ پر ہی بٹھا دیا اور ان سے یہ کہہ دیا گیا (یعنی ان کے کافر دوستوں نے کہا) کہ جو لوگ بیٹھ رہے ہیں انہی کے ساتھ تم بھی بیٹھ رہو۔ " WHERE ayatNumber = 46 AND chapterNumber = 9;
UPDATE verses SET ts = "اگر وہ تمہارے ساتھ مل کر نکلتے تو خرابی پیدا کرنے کے سوا تمہاری کچھ مدد نہ کرتے اور وہ تمہارے درمیان (فساد کرنے کے لئے) خوب گھوڑے دوڑاتے پھرتے (اور) تمہارے اندر فتنہ پیدا کرنے کی خواہش  کرتے اور تم میں (بھی) کچھ ایسے (لوگ پائے جاتے) ہیں جو ان تک پہنچانے کے لئے باتیں سنتے ہیں اور اللہ ظالموں کو خوب جانتا ہے۔ " WHERE ayatNumber = 47 AND chapterNumber = 9;
UPDATE verses SET ts = "انہوں نے اس سے پہلے بھی فتنہ (پیدا کرنا) چاہا تھا اور تیرے لئے حالات کو کئی کئی طرح بدلا تھا۔ یہاں تک کہ حق آگیا اور اللہ کا فیصلہ ظاہر ہو گیا اور وہ (اس فیصلہ کو) ناپسند کرتے تھے۔ " WHERE ayatNumber = 48 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ان میں سے بعض (منافق) ایسے بھی ہیں جو کہتے ہیں کہ ہمیں (پیچھے رہنے کی) اجازت دیجئے اور ہم کو (جنگ پر جانے) کی آزمائش میں نہ ڈالیئے۔ سن رکھو۔ یہ لوگ فتنہ میں پہلے ہی سے پڑ چکے ہیں اور جہنم کافروں کو یقیناً تباہ کرنے والی ہے۔ " WHERE ayatNumber = 49 AND chapterNumber = 9;
UPDATE verses SET ts = "اگر تجھے کوئی فائدہ پہنچے تو ان کو برا لگتا ہے اور اگر تجھ پر کوئی مصیبت آجائے تو وہ کہتے ہیں‘ ہم نے تو پہلے ہی سے اپنے پیش آنے والے دنوں کا انتظام کر لیا تھا اور وہ خوشی کے مارے پیٹھ پھیر کر چلے جاتے ہیں۔ " WHERE ayatNumber = 50 AND chapterNumber = 9;
UPDATE verses SET ts = "تو (ان سے) کہہ دے‘ ہم کو تو وہی پہنچتا ہے جو اللہ نے ہمارے لئے مقرر کر چھوڑا ہے وہ ہمارا کارساز ہے اور مومنوں کو چاہیے کہ وہ اللہ پر ہی توکل رکھیں۔ " WHERE ayatNumber = 51 AND chapterNumber = 9;
UPDATE verses SET ts = "تو (ان سے) کہدے کہ دو بھلائیوں میں سے ایک کے سوا تم ہمارے لئے کسی بات کا انتظار نہیں کرتے اور ہم تمہارے لئے صرف اس بات کا انتظار کرتے ہیں کہ اللہ تم کو اپنے پاس سے یا ہمارے ہاتھوں سے عذاب پہنچائے گا پس تم بھی انتظار کرو‘ ہم بھی تمہارے ساتھ انتظار کریں گے۔ " WHERE ayatNumber = 52 AND chapterNumber = 9;
UPDATE verses SET ts = "تو (ان سے) کہہ دے کہ خواہ خوشی سے خرچ کرو خواہ ناخوشی سے۔ تم  سے کسی صورت میں (تمہارا صدقہ) قبول نہ کیا جائے گا کیونکہ تم تو اطاعت سے نکل جانے والی قوم ہو۔ " WHERE ayatNumber = 53 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اللہ اور اس کے رسول کے انکار کرنے کے سوا اور اس بات کے سوا کہ وہ نماز بہت سستی سے پڑھتے تھے اور خدا کی راہ میں ناخوشی سے خرچ کرتے تھے ان کے صدقات کے قبول کرنے کو کس نے روکا ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 9;
UPDATE verses SET ts = "پس تجھے ان کے مال اور ان کی اولادیں تعجب میں نہ ڈالیں۔ اللہ صرف یہ چاہتا ہے کہ ان (مالوں اور اولادوں) کے ذریعہ سے ان کو اس دنیا کی زندگی میں عذاب دے اور یہ کہ ان کی جانیں ایسے وقت میں نکلیں کہ وہ منکر ہی ہوں۔ " WHERE ayatNumber = 55 AND chapterNumber = 9;
UPDATE verses SET ts = "اور وہ اس بات پر اللہ کی قسمیں کھاتے ہیں کہ وہ تم میں سے ہیں حالانکہ وہ تم میں سے نہیں ہیں بلکہ وہ ایک ایسی جماعت ہیں جو سخت بزدل ہے۔ " WHERE ayatNumber = 56 AND chapterNumber = 9;
UPDATE verses SET ts = "اگر وہ کوئی پناہ کی جگہ یا چھپ رہنے کے لئے غار یا بیٹھ رہنے کے لئے کوئی ٹھکانہ پائیں تو وہ پیٹھ پھیر کر دوڑتے ہوئے ادھر چلے جائیں گے۔ " WHERE ayatNumber = 57 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ان میں سے کچھ (منافق) ایسے ہیں جو صدقات کے بارہ میں تجھ پر الزام لگاتے ہیں۔ اگر ان صدقات میں سے کچھ ان کو دے دیا جائے تو وہ راضی ہو جاتے ہیں‘ اور اگر ان میں سے انہیں کچھ نہ دیا جائے تو فوراً خفا ہو جاتے ہیں۔ " WHERE ayatNumber = 58 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اگر وہ اللہ اور اس کے رسول کی عطا پر خوش ہو جاتے اور یہ کہتے کہ اللہ ہی ہمارے لئے کافی ہے (اگر ہمیں تنگی ہوگی تو) اللہ اپنے فضل سے ہمیں دے گا اور (اسی طرح) اس کا رسول بھی۔ ہم تو اپنے اللہ کی طرف جھکنے والے ہیں (تو یہ ان کے لئے بہتر ہوتا) " WHERE ayatNumber = 59 AND chapterNumber = 9;
UPDATE verses SET ts = "صدقات تو صرف فقراء اور مساکین کے لئے ہیں اور ان کے لئے جو ان صدقات کے جمع کرنے کے لئے مقرر کیے گئے ہیں۔ نیز ان کے لئے جن کے دلوں کو (اپنے ساتھ) جوڑنا مطلوب ہو اور اسی طرح قیدیوں اور قرض داروں کے لئے اور (ان کے لئے جو) اللہ کے راستہ میں (جنگ کرتے ہیں) اور مسافروں کے لئے یہ فرض اللہ  کا مقرر کردہ ہے اور اللہ بہت جاننے والا (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 60 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ان میں سے بعض ایسے (منافق) بھی ہیں جو نبی کو دکھ دیتے ہیں اور کہتے ہیں کہ وہ تو (کان ہی) کان ہے تو کہہ دے کہ وہ تمہارے لئے بھلائی سننے کے کان رکھتا ہے وہ اللہ پر ایمان لاتا ہے اور جو تم میں سے مومن ہوں ان (کے وعدوں) پر (بھی) یقین رکھتا ہے اور مومنوں کے لئے رحمت کا موجب ہے‘ اور وہ لوگ جو اللہ کے رسول کو دکھ پہنچاتے ہیں ان کے لئے دردناک عذاب ہے۔ " WHERE ayatNumber = 61 AND chapterNumber = 9;
UPDATE verses SET ts = "وہ تمہارے خوش کرنے کے لئے اللہ کی قسمیں کھاتے ہیں۔ حالانکہ اللہ بھی اور اس کا رسول بھی زیادہ حق دار ہیں کہ اس کو خوش کیا جائے۔ بشرطیکہ یہ (منافق) سچے مومن ہوں۔ " WHERE ayatNumber = 62 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا ان کو معلوم نہیں کو جو کوئی اللہ اور اس کے رسول کی مخالفت کرتا ہے اس کے لئے جہنم کی آگ (مقدر) ہے وہ اس میں رہتا چلا جائے گا اور یہ بڑی بھاری رسوائی ہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 9;
UPDATE verses SET ts = "منافق (دکھاوے کے طور پر) ڈر کا اظہار کرتے ہیں کہ ان کے خلاف کوئی سورۃ نازل نہ ہو جائے جو انہیں (اور مسلمانوں کو) ان باتوں سے خبردار کردے جو ان کے دلوں میں ہیں۔ تو کہہ دے کہ ہنسی کرتے جائو۔ اللہ (حقیقتا) اس بات کو ظاہر کر دے گا۔ جس (کے ظاہر کرنے) سے تم (بناوٹ سے) ڈرتے ہو۔ " WHERE ayatNumber = 64 AND chapterNumber = 9;
UPDATE verses SET ts = "اور اگر تو ان سے پوچھے (کہ تم ایسی باتیں کیوں کرتے ہو) تو وہ ضرور یہی جواب دیں گے ہم تو صرف مذاق اور ہنسی کرتے تھے۔ تو ان کو جواب دیجیو کہ کیا اللہ اور اس کی آیات اور اس کے رسول سے مذاق اور ہنسی کرتے تھے۔ " WHERE ayatNumber = 65 AND chapterNumber = 9;
UPDATE verses SET ts = "اب کوئی عذر نہ کرو۔ تم نے ایمان لاکر کفر کیا (پس اس کی سزا پائو) اگر ہم تم میں سے ایک گروہ کو معاف کر دیں اور ایک دوسرے گروہ کو عذاب دے دیں اس لئے کہ وہ مجرم تھے (تو یہ ہمارا کام ہے) " WHERE ayatNumber = 66 AND chapterNumber = 9;
UPDATE verses SET ts = "منافق مرد اور منافق عورتیں آپس میں ایک دوسرے کے ساتھ تعلق رکھتے ہیں وہ بری باتوں کا حکم دیتے ہیں اور اچھی باتوں کے خلاف تعلیم دیتے ہیں اور اپنے ہاتھوں کو (خدا کی راہ میں خرچ کرنے سے) روکتے ہیں۔ انہوں نے اللہ کو ترک کر دیا‘ سو اللہ نے بھی ان کو ترک کردیا۔ منافق یقیناً اطاعت سے نکلنے والے ہیں۔ " WHERE ayatNumber = 67 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے منافق مردوں اور منافق عورتوں اور کفار سے جہنم کی آگ کا وعدہ کیا ہے‘ وہ اس میں رہتے چلے جائیں گے۔ وہی ان (کی پوری توجہ کھینچنے) کے لئے کافی ہے (اور اس کے علاوہ) اللہ نے ان کو (اپنی درگاہ سے) دھتکار بھی دیا ہے اور ان کے لئے ایک قائم رہنے والا عذاب (مقدر) ہے۔ " WHERE ayatNumber = 68 AND chapterNumber = 9;
UPDATE verses SET ts = "(اے منافقو! یہ عذاب) ان لوگوں (کی سزا) کی طرح (ہوگا) جو تم سے پہلے گذر چکے ہیں۔ وہ تم سے زیادہ طاقتور تھے وہ زیادہ مال دار (تھے) اور زیادہ اولاد رکھتے تھے پس انہوں نے اپنے حصہ کے مطابق فائدہ اٹھایا اور تم نے اپنے حصہ کے مطابق فائدہ اٹھایا۔ جس طرح کہ تم سے پہلے لوگوں نے اپنے حصہ کے مطابق فائدہ اٹھایا اور تم نے اسی طرح ہنسی مذاق کیا جس طرح ان لوگوں نے ہنسی مذاق کیا تھا۔ ان کے عمل دنیا کے متعلق بھی اور آخرت کے متعلق بھی ضائع ہو گئے اور وہ لوگ نقصان اٹھانے والے (لوگوں میں شامل) ہو گئے۔ " WHERE ayatNumber = 69 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا ان کے پاس  ان لوگوں کی خبریں نہیں آئیں جو ان سے پہلے گزر چکے ہیں (یعنی) نوح اور عاد کی (قوم کی) اور ثمود کی (قوم کی‘ جو صالح کی قوم تھے) اور ابراہیم کی قوم کی۔ اور مدین کے لوگوں کی۔ اور الٹائی گئی بستیوں کے لوگوں (یعنی لوط کی قوم) کی۔ ان کے پاس ان کے رسول کھلے نشان لے کر آئے تھے (مگر انہوں نے انکار کیا اور سزا پائی)  اللہ نے ان پر ظلم نہیں کیا بلکہ وہ اپنی جانوں پر (خود) ظلم کر رہے تھے۔ " WHERE ayatNumber = 70 AND chapterNumber = 9;
UPDATE verses SET ts = "اور مومن مرد اور مومن عورتیں آپس میں ایک دوسرے کے دوست ہیں۔ وہ نیک باتوں کا حکم دیتے ہیں اور بری باتوں سے روکتے ہیں۔ اور نماز کو قائم کرتے ہیں اور زکٰوۃ دیتے ہیں۔ اور اللہ اور اس کے رسول کی اطاعت کرتے ہیں۔ یہ ایسے لوگ ہیں کہ اللہ ضرور ان پر رحم کرے گا۔ اللہ غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے مومن مردوں اور مومن عورتوں سے ایسی جنات کے وعدے کیے ہیں جن کے نیچے نہریں بہتی ہیں وہ ان میں ہمیشہ رہیں گے اور ہمیشہ رہنے والی جنتوں میں پاک رہائش گاہوں کا (بھی وعدہ کیا ہے) اور ان کے علاوہ اللہ کی رضا مندی سب سے بڑا انعام ہے (جو ان کو ملے گا)۔ (اور) اس کا ملنا (بہت) بڑی کامیابی ہے۔ " WHERE ayatNumber = 72 AND chapterNumber = 9;
UPDATE verses SET ts = "اے نبی! کفار اور منافقوں سے جہاد کرو۔ اور (پکا انتظام کرکے) ان پر سختی (سے حملہ) کرو۔ ان کا ٹھکانا جہنم ہے اور وہ (رہنے کے لحاظ سے) بہت بری جگہ ہے۔ " WHERE ayatNumber = 73 AND chapterNumber = 9;
UPDATE verses SET ts = "وہ اللہ کی قسمیں کھاتے ہیں کہ انہوں نے کوئی (بری) بات نہیں کہی۔ حالانکہ انہوں نے کفر کی بات کہی ہے اور اسلام لانے کے بعد کفر کیا ہے۔ اور (اسلام کے خلاف) ایسی (مکروہ) باتوں کا ارادہ کیا ہے جن کو وہ حاصل نہیں کر سکتے اور انہوں نے مسلمانوں سے صرف اس لئے دشمنی کی کہ اللہ اور اس کے رسول نے ان کو اپنے فضل سے مال دار بنا دیا تھا۔ پس اگر وہ توبہ کریں تو ان کے لئے اچھا ہوگا اور اگر وہ پیٹھ پھیر کر چلے جائیں تو اللہ ان کو دنیا میں بھی اور آخرت میں (بھی) دردناک عذاب دے گا اور اس جہان میں نہ کوئی ان کا دوست ہوگا اور نہ مددگار۔ " WHERE ayatNumber = 74 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ان میں سے (کچھ افراد) ایسے بھی ہیں جو اللہ سے یہ عہد کرتے ہیں کہ اگر وہ (اللہ) ہمیں اپنے فضل سے کچھ دے گا تو ہم ضرور اس کی راہ میں صدقہ کریں گے اور ہم ضرور نیک بن جائیں گے۔ " WHERE ayatNumber = 75 AND chapterNumber = 9;
UPDATE verses SET ts = "اور جب اس (خدا) نے ان کو اپنے فضل سے مال عطا فرمایا تو انہوں نے اس کی راہ میں اسے خرچ کرنے سے بخل کیا اور (اپنے پرانے طریقوں کی طرف) لوٹ گئے اور (خدا اور رسول کی باتوں سے) روگردانی کرتے ہوئے پیٹھ پھیر لی۔ " WHERE ayatNumber = 76 AND chapterNumber = 9;
UPDATE verses SET ts = "پس (نتیجہ یہ ہوا کہ) اس نے ان کے دلوں میں اس دن تک کے لئے جب وہ اس سے ملیں گے نفاق کا سلسلہ چلا دیا کیونکہ انہوں نے جو خدا سے وعدہ کیا تھا اس کی خلاف ورزی کی۔ اور بوجہ اس کے کہ وہ جھوٹ بولتے تھے۔ " WHERE ayatNumber = 77 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا ان کو معلوم نہیں تھا کہ اللہ ان کے مخفی مشوروں (کو بھی جانتا ہے) اور ان کے کھلے مشوروں کو (بھی) جانتا ہے۔ اور یہ کہ اللہ پورے طور پر غیب کی باتوں سے واقف ہے۔ " WHERE ayatNumber = 78 AND chapterNumber = 9;
UPDATE verses SET ts = "یہ (منافق ہی) ہیں جو مومنوں میں سے خوشی سے بڑھ بڑھ کر صدقے دینے والوں پر طنز کرتے ہیں اور ان پر بھی جو کہ سوائے اپنی (محنت کی) کمائی کے (کوئی) طاقت نہیں رکھتے۔ سو (باوجود اس قربانی کے) یہ (منافق) ان پر ہنسی کرتے ہیں۔  اللہ ان (میں سے اشد مخالفوں) کو ہنسی کی سزا دے گا اور ان کو دردناک عذاب پہنچے گا۔ " WHERE ayatNumber = 79 AND chapterNumber = 9;
UPDATE verses SET ts = "تو ان کے لئے استغفار کر یا نہ کر (ان کے لئے برابر ہے) اگر تو ان کے لئے ستر بار بھی استغفار کرے گا تو اللہ ان کو کبھی معاف نہیں کرے گا۔ یہ اس لئے ہوگا کہ انہوں نے اللہ اور اس کے رسول کا انکار کیا اور اللہ اطاعت سے نکل جانے والی قوم کو کبھی کامیابی کا راستہ نہیں دکھاتا۔ " WHERE ayatNumber = 80 AND chapterNumber = 9;
UPDATE verses SET ts = "(جہاد سے) پیچھے چھوڑے ہوئے (منافق) اللہ کے رسول کے (حکم کے) خلاف (چل کر) اپنی جگہ بیٹھ رہنے پر بہت خوش ہیں اور انہوں نے اپنی جانوں اور اپنے مالوں کے ساتھ جہاد کرنے کو برا سمجھا تھا اور (ایک دوسرے سے) کہا تھا کہ ایسی (شدید) گرمی میں جنگ کے لئے (اکٹھے ہو کر) مت نکلو۔ تو (ان سے) کہہ دے کہ جہنم کی آگ (اس گرمی سے) زیادہ سخت ہے۔ کاش کہ وہ سمجھتے۔ " WHERE ayatNumber = 81 AND chapterNumber = 9;
UPDATE verses SET ts = "پس چاہیے  کہ (اپنی اس فریب دہی پر) وہ تھوڑا ہنسیں اور اپنے عمل کی جزا پر زیادہ روئیں۔ " WHERE ayatNumber = 82 AND chapterNumber = 9;
UPDATE verses SET ts = "پھر اگر اللہ تجھ کو ان میں سے ایک گروہ کی طرف لوٹا لائے۔ اور وہ لوگ (کسی آئندہ) جنگ کے لئے جانے کی تجھ سے اجازت مانگیں تو تو ان سے کہہ دے کہ تم کو کبھی بھی (آئندہ) میرے ساتھ (جنگ پر) جانے کی اجازت نہیں ہوگی اور کبھی بھی تم دشمن سے میرے ہمراہ ہو کر لڑنے نہیں پائو گے (کیونکہ) تم پہلی دفعہ (پیچھے) بیٹھ رہنے پر راضی ہو گئے تھے۔ پس (آئندہ ہمیشہ) پیچھے رہ جانے والوں کے ساتھ بیٹھ رہا کرو۔ " WHERE ayatNumber = 83 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ان میں سے اگر کوئی مر جائے تو اس پر نماز (جنازہ) نہ پڑھا کر۔ اور نہ اس کی قبر پر (دعا کے لئے) کھڑا ہوا کر۔ کیوں کہ انہوں نے اللہ اور اس کے رسول کا انکار کیا اور ایسی حالت میں مرے ہیں‘ جب کہ وہ اطاعت سے خارج ہو رہے تھے۔ " WHERE ayatNumber = 84 AND chapterNumber = 9;
UPDATE verses SET ts = "پس تجھے ان کے مال اور ان کی اولادیں تعجب میں نہ ڈالیں۔ اللہ صرف یہ چاہتا ہے کہ ان (مالوں اور اولادوں) کے ذریعہ سے ان کو اس دنیا میں عذاب دے اور یہ کہ ان کی جانیں ایسے وقت میں نکلیں کہ وہ منکر ہی ہوں۔ " WHERE ayatNumber = 85 AND chapterNumber = 9;
UPDATE verses SET ts = "اور جب کوئی سورۃ (اس حکم کے ساتھ) نازل ہوتی ہے کہ اللہ پر ایمان لائو اور اس کے رسول کے ساتھ مل کر جہاد کرو۔ تو ان میں سے مال دار لوگ تجھ سے اجازت مانگنے لگ جاتے ہیں اور کہتے ہیں۔ ہمیں پیچھے چھوڑ جائیں تا کہ ہم ان لوگوں کے ساتھ رہیں جو کہ پیچھے بیٹھنے والے ہوں گے۔ " WHERE ayatNumber = 86 AND chapterNumber = 9;
UPDATE verses SET ts = "وہ اس بات پر خوش ہیں کہ پیچھے بیٹھ رہنے والے قبائل میں شامل ہو جائیں اور ان کے دلوں پر مہر کر دی گئی ہے۔ پس وہ (اپنے بداعمال کی وجہ سے) سمجھتے نہیں۔ " WHERE ayatNumber = 87 AND chapterNumber = 9;
UPDATE verses SET ts = "لیکن یہ رسول (یعنی آنحضرت صلی اللہ علیہ وسلم) اور جو اس کے ساتھ ہو کر (خدا پر) ایمان لائے ہیں اور جنہوں نے اپنے مال اور اپنی جانوں کے ذریعہ سے جہاد کیا ہے۔ ان کے لئے ہر قسم کی بھلائیاں ہیں اور وہی (آخر) کامیاب ہونے والے ہیں۔ " WHERE ayatNumber = 88 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے ان کے لئے ایسی جنتیں تیار کی ہیں‘ جن کے نیچے نہریں بہتی ہیں۔ وہ ان باغوں میں ہمیشہ رہتے چلے جائیں گے۔ یہ بہت بڑی کامیابی ہے۔" WHERE ayatNumber = 89 AND chapterNumber = 9;
UPDATE verses SET ts = "اور مدینہ کے اردگرد جنگلوں (اور چھوٹے گائوں) میں رہنے والے لوگوں میں سے بہانہ ساز (لوگ) آکر کہتے ہیں کہ انہیں (بھی) پیچھے رہنے کی اجازت دی جائے اور وہ لوگ جنہوں نے اللہ اور اس کے رسول سے جھوٹ بولا ہے (بغیر اجازت کے ہی) پیچھے ٹھہر گئے ہیں‘ ان میں سے جو کافر ہیں ان کو یقیناً دردناک عذاب پہنچے گا۔ " WHERE ayatNumber = 90 AND chapterNumber = 9;
UPDATE verses SET ts = "(لیکن اے رسول)! جو واقعی کمزور ہیں اور مریض ہیں۔ اور جو لوگ زاد راہ نہیں پاتے ان پر (پیچھے رہ جانے کی وجہ سے) کوئی گرفت نہیں ہوگی۔ جبکہ وہ دلی طور پر اللہ اور اس کے رسول کے مخلص ہیں (یہ لوگ محسن ہیں) اور محسنوں پر کوئی الزام نہیں اور اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 91 AND chapterNumber = 9;
UPDATE verses SET ts = "اور نہ ان لوگوں پر (کوئی الزام ہے) جو تیرے پاس اس وقت آئے جب جنگ کا اعلان کیا گیا تھا۔ اس لئے کہ تو ان کو کوئی سواری مہیا کر دے۔ تو تو نے جواب دیا کہ میرے پاس کوئی چیز نہیں ہے جس پر تمہیں سوار کرائوں اور (یہ جواب سن کر) وہ چلے گئے اور (اس) غم سے ان کی آنکھوں سے آنسو بہ رہے تھے کہ افسوس ان کے پاس کچھ نہیں جسے (خدا کی راہ میں) خرچ کریں۔ " WHERE ayatNumber = 92 AND chapterNumber = 9;
UPDATE verses SET ts = "الزام صرف ان لوگوں پر ہے جو اس حالت میں اجازت مانگتے ہیں کہ وہ مال دار ہوتے ہیں۔ وہ پیچھے بیٹھ رہنے والے قبائل کے ساتھ (بیٹھ رہنے پر) راضی ہو گئے اور اللہ نے ان کے دلوں پر مہر کر دی۔ مگر وہ (ایسے ہیں کہ) سمجھتے ہی نہیں۔ " WHERE ayatNumber = 93 AND chapterNumber = 9;
UPDATE verses SET ts = "جب تم جنگ سے ان کی طرف واپس لوٹ کر آتے ہو تو وہ تمہارے پاس آکر (قسم قسم کے) عذر کرتے ہیں‘ تو ان سے کہہ دے کہ عذر نہ کرو۔ ہم (تمہارے عذروں کو) ہرگز تسلیم نہیں کریں گے۔ اللہ نے تمہارے حالات سے ہم کو خبر دے دی ہے اور اللہ اور اس کا رسول تمہارے عملوں کو بھانپتے رہیں گے۔ پھر تم حاضر و غائب کو جاننے والے خدا کے حضور لوٹائے جائو گے۔ پس وہ تمہارے عملوں کی حقیقت سے تم کو خبردار کرے گا۔ " WHERE ayatNumber = 94 AND chapterNumber = 9;
UPDATE verses SET ts = "جب تم ان کی طرف لوٹو گے تو وہ تمہارے سامنے اللہ کی قسمیں کھائیں گے تا تم ان سے درگزر کرو (سو ہم بھی تم سے کہتے ہیں کہ) ان سے درگذر ہی کرو۔ کیونکہ وہ گندے ہیں اور ان کا ٹھکانا ان کے اعمال کے بدلہ میں پہلے ہی سے جہنم مقرر ہو چکا ہے۔ " WHERE ayatNumber = 95 AND chapterNumber = 9;
UPDATE verses SET ts = "وہ تمہارے سامنے قسمیں کھائیں گے کہ تم ان سے راضی ہو جائو۔ پس اگر تم ان سے راضی بھی ہو جائو تو اللہ اطاعت سے نکل جانے والے لوگوں سے راضی نہیں ہوگا۔ " WHERE ayatNumber = 96 AND chapterNumber = 9;
UPDATE verses SET ts = "دیہات (اور جنگلوں) میں رہنے والے عرب کفر اور نفاق میں (سب عربوں سے) بڑھے ہوئے ہیں۔ اور (جہالت کی وجہ سے) اس کے مستحق ہیں کہ جو اللہ نے اپنے رسول پر اتارا ہے اس کی حدود کو نہ پہچانیں اور اللہ بہت جاننے والا (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 97 AND chapterNumber = 9;
UPDATE verses SET ts = "اور دیہاتیوں (اور جنگل کے رہنے والوں) میں سے کچھ لوگ ایسے بھی ہیں (جو) خدا کی راہ میں خرچ کئے ہوئے مال کو (ایک) چٹی سمجھتے ہیں اور تمہارے لئے (آسمانی) گردشوں کی انتظار میں رہتے ہیں۔ (سنو)! بری گردش انہی پر آئے گی اور اللہ خوب سننے والا (اور) جاننے والا ہے۔ " WHERE ayatNumber = 98 AND chapterNumber = 9;
UPDATE verses SET ts = "اور دیہاتیوں  اور (جنگل میں رہنے والے عربوں) میں سے کچھ لوگ ایسے بھی ہیں جو اللہ پر اور یوم آخرت پر (سچا) ایمان لاتے ہیں اور (خدا کی راہ میں) جو کچھ خرچ کرتے ہیں‘ اسے (خداتعالیٰ کی) قربت اور رسول کی دعائوں کا ذریعہ سمجھتے ہیں۔ سنو! یہ فعل ضرور ان کے لئے (خدا کی) قربت کا ذریعہ ہوگا۔ اللہ ضرور ان کو اپنی رحمت میں داخل کرے گا کیونکہ اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 99 AND chapterNumber = 9;
UPDATE verses SET ts = "اور جو مہاجرین اور انصار میں سے سبقت لے جانے والے ہیں اور وہ لوگ بھی جو کہ کامل اطاعت دکھاتے ہوئے ان کے پیچھے چلے۔ اللہ ان سے راضی ہو گیا اور وہ اللہ سے راضی ہو گئے۔ اس نے ان کے لئے ایسی جنتیں تیار کی ہیں جن کے نیچے نہریں بہتی ہیں وہ ان میں ہمیشہ رہتے چلے جائیں گے۔ یہ بہت بڑی کامیابی ہے۔ " WHERE ayatNumber = 100 AND chapterNumber = 9;
UPDATE verses SET ts = "اور گنواروں (اور جنگل میں رہنے والوں) میں سے جو تمہارے اردگرد رہتے ہیں منافق بھی ہیں اور مدینہ کے رہنے والوں میں سے بھی (کچھ لوگ ایسے ہیں) جو نفاق پر مصر ہیں‘ تو ان کو نہیں جانتا مگر ہم ان کو جانتے ہیں‘ ہم ان کو دو دفعہ عذاب دیں گے‘ پھر وہ ایک بہت بڑے عذاب کی طرف لوٹائے جائیں گے۔ " WHERE ayatNumber = 101 AND chapterNumber = 9;
UPDATE verses SET ts = "اور کچھ اور لوگ ہیں جنہوں نے اپنے گناہوں کا اقرار کیا انہوں نے نیک عملوں کو کچھ اور عملوں سے جو برے تھے ملا دیا تھا۔ قریب ہے کہ اللہ ان پر فضل کر دے اللہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 102 AND chapterNumber = 9;
UPDATE verses SET ts = "(اے رسول)! ان کے مالوں میں سے صدقہ لے تا کہ تو انہیں پاک کرے اور ان کی ترقی کے سامان مہیا کرے اور ان کے لئے دعائیں بھی کرتا رہ۔ کیونکہ تیری دعا ان کی تسکین کا موجب ہے اور اللہ تیری دعائوں کو بہت سننے والا (اور حالات کو) جاننے والا ہے۔ " WHERE ayatNumber = 103 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا ان کو معلوم نہیں کہ اللہ ہی ہے‘ جو اپنے بندوں کی توبہ قبول کرتا اور ان کے صدقات لیتا ہے (رسول نہیں لیتا) اور اللہ ہی ہے جو بہت توبہ قبول کرنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 104 AND chapterNumber = 9;
UPDATE verses SET ts = "اور (ان سے) کہہ دے کہ اپنی جگہ کام کرے جائو۔ اللہ اور اس کا رسول اور مومن ضرور تمہارے کاموں کی حقیقت کو دیکھتے رہیں گے اور تم ضرور حاضر و غائب جاننے والے خدا کی طرف لوٹائے جائو گے اور وہ تمہارے عملوں کی حقیقت تمہیں بتائے گا۔ " WHERE ayatNumber = 105 AND chapterNumber = 9;
UPDATE verses SET ts = "اور کچھ اور لوگ ہیں جو خدا کے حکم کی انتظار میں چھوڑے گئے ہیں (اس کو اختیار ہے کہ) خواہ انکو عذاب دے یا انکی توبہ قبول کرے اور اللہ جاننے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 106 AND chapterNumber = 9;
UPDATE verses SET ts = "اور وہ لوگ جنہوںنے ایک مسجد نقصان پہنچانے اور کفر کی تبلیغ کرنے اور مومنوں میں تفرقہ پیدا کرنے کے لئے بنائی ہے اور جو شخص اللہ اور اس کے رسول سے لڑ چکا ہے اس کے لئے کمین گاہ مہیا کرنے کے لئے۔ وہ ضرور قسم کھائیں گے (کہ) اس مسجد کے بنانے سے ہمارا ارادہ صرف نیکی کرنا تھا اور اللہ گواہی دیتا ہے کہ وہ یقیناً جھوٹ بول رہے ہیں۔ " WHERE ayatNumber = 107 AND chapterNumber = 9;
UPDATE verses SET ts = "(اے نبی)! تو اس (مسجد) میں (کبھی) کھڑا نہ ہو۔ وہ مسجد جس کی بنیاد پہلے دن سے تقویٰ پر رکھی گئی ہے زیادہ حقدار ہے کہ تو اس میں (جماعت کرانے کے لئے) کھڑا ہو۔ اس میں (آنے والے) ایسے لوگ ہیں جو خواہش رکھتے ہیں کہ بالکل پاک ہو جائیں۔ اور اللہ کامل پاکیزگی اختیار کرنے والوں کو پسند کرتا ہے۔ " WHERE ayatNumber = 108 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا وہ شخص جو اپنی عمارت کی بنیاد اللہ کے تقویٰ اور رضامندی پر رکھتا ہے زیادہ اچھا ہے یا وہ جو اپنی عمارت کی بنیاد ایک پھسلنے والے کنارے پر رکھتا ہے جو گر رہا ہوتا ہے پھر وہ کنارہ اس عمارت سمیت جہنم کی آگ میں گر جاتا ہے اور اللہ ظالم قوم کو (کامیابی کا) راستہ نہیں دکھاتا۔ " WHERE ayatNumber = 109 AND chapterNumber = 9;
UPDATE verses SET ts = "وہ بنیاد جو انہوں نے بنائی تھی ہمیشہ ان کے دلوں میں خلش کا موجب رہے گی سوائے اس کے کہ ان کے دل ٹکڑے ٹکڑے ہو جائیں (اور وہ مر جائیں) اور اللہ بہت جاننے والا (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 110 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے مومنوں سے ان کی جانوں اور ان کے مالوں کو (اس وعدہ کے ساتھ) خرید لیا ہے کہ ان کو جنت ملے گی (کیونکہ) وہ اللہ کے راستہ میں لڑتے ہیں۔ پس  (یا تو وہ) اپنے دشمنوں کو مار لیتے ہیں یا خود مارے جاتے ہیں یہ ایک ایسا وعدہ ہے جو اس پر لازم ہے (اور) تورات اور انجیل (میں بھی بیان کیا گیا ہے) اور قرآن میں (بھی) اور اللہ سے زیادہ اپنے وعدہ کو پورا کرنے والا کون ہے پس (اے مومنو)! اپنے اس سودے پر خوش ہو جائو جو تم نے کیا ہے اور یہی وہ بڑی کامیابی ہے (جس کا مومنوں کو وعدہ دیا گیا ہے۔) " WHERE ayatNumber = 111 AND chapterNumber = 9;
UPDATE verses SET ts = "جو (لوگ) توبہ کرنے والے ہیں‘ عبادت کرنے والے ہیں (خدا کی) حمد کرنے والے ہیں (خدا کی راہ میں) سفر کرنے والے ہیں‘ رکوع کرنے والے ہیں‘ سجدہ کرنے والے ہیں نیک باتوں کا حکم دینے والے ہیں اور ُبری باتوں سے روکنے والے ہیں اور اللہ کی حدود کی حفاظت کرنے والے ہیں۔ ایسے مومنوں کو تو بشارت دے دے۔ " WHERE ayatNumber = 112 AND chapterNumber = 9;
UPDATE verses SET ts = "نبی اور مومنوں کی شان کے خلاف تھا کہ مشرکوں کے لئے استغفار کرتے‘ خواہ وہ قریبی رشتہ دار کیوں نہ ہوں بعد اس کے کہ ان پر ظاہر ہو گیا کہ وہ دوزخی ہیں۔ " WHERE ayatNumber = 113 AND chapterNumber = 9;
UPDATE verses SET ts = "اور ابراہیم کا استغفار اپنے باپ کے لئے صرف اس وجہ سے تھا کہ اس نے اس سے ایک وعدہ کیا تھا۔ مگر جب اس پر ظاہر ہو گیا کہ وہ اللہ کا دشمن ہے تو وہ اس وعدہ سے پوری طرح  دست بردار ہو گیا۔ ابراہیم بہت ہی نرم دل اور عقل مند تھا۔ " WHERE ayatNumber = 114 AND chapterNumber = 9;
UPDATE verses SET ts = "اور یہ اللہ کی شان کے خلاف ہے کہ کسی قوم کو ہدایت دینے کے بعد اسے گمراہ قرار دے جب تک کہ وہ ان کے سامنے وہ (امور) نہ بیان کر دے جن سے ان کو بچنا چاہیے  اللہ ہر ایک چیز کو جانتا ہے۔ " WHERE ayatNumber = 115 AND chapterNumber = 9;
UPDATE verses SET ts = "آسمانوں اور زمین کی بادشاہت یقیناً اللہ کی ہے۔ وہ زندہ بھی کرتاہے اور مارتا بھی ہے اور اللہ کے سوا نہ کوئی تمہارا دوست ہے اور نہ مددگار۔ " WHERE ayatNumber = 116 AND chapterNumber = 9;
UPDATE verses SET ts = "اللہ نے نبی اور مہاجرین اور انصار پر بڑا فضل کیا ہے (یعنی ان لوگوں پر) جنہوں نے اس (نبی) کی تکلیف کی گھڑی میں جبکہ ان میں سے ایک گروہ کے دل کسی قدر شک میں پڑ گئے تھے اتباع کی۔ پھر اس نے ان (کمزوروں) پر بھی فضل کر دیا۔ وہ ان (مومنوں) سے یقیناً محبت کرنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 117 AND chapterNumber = 9;
UPDATE verses SET ts = "اسی طرح ان تینوں پر بھی (اس نے فضل کیا) جو کہ پیچھے چھوڑے گئے تھے‘ یہاں تک کہ جب زمین باوجود فراخی کے ان پر تنگ ہو گئی اور ان کے اپنے نفس بھی ان پر بار بن گئے اور انہوں نے خیال کیا کہ اللہ کے غضب سے بچنے کے لئے سوائے اس کے اور کوئی پناہ نہیں تو ان کی حالت دیکھ کر اللہ نے ان پر فضل کیا تا کہ وہ بھی توبہ کریں۔ اللہ یقیناً باربار توبہ قبول کرنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 118 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! اللہ کا تقویٰ اختیار کرو اور صادقوں (کی جماعت) کے ساتھ شامل ہو جائو۔ " WHERE ayatNumber = 119 AND chapterNumber = 9;
UPDATE verses SET ts = "اہل مدینہ اور جو ان کے ارد گرد دیہاتی (اورجنگلی) رہتے ہیں ان کو مناسب نہ تھا کہ اللہ کے رسول کو (اکیلا) چھوڑ کر آپ پیچھے رہ جاتے اور نہ (یہ کہ) اس کی جان سے بے پرواہ ہو کر اپنی جانوں کی فکر میں لگ جاتے۔ یہ (فیصلہ) اس لئے (کیا جاتا) ہے کہ کوئی پیاس یا تھکان یا بھوک کی گھڑی ان پر اللہ کے رستہ  میں نہیں آتی اور نہ وہ کسی زمین پر قدم مارتے ہیں جو کفار کو غصہ دلاتا ہو اور نہ وہ دشمن پر کوئی فتح پاتے ہیں کہ ان کے لئے اس کے بدلہ میں نیک عمل نہ لکھا جاتا ہو۔ اللہ محسنوں کا اجر کبھی ضائع نہیں کرتا۔ " WHERE ayatNumber = 120 AND chapterNumber = 9;
UPDATE verses SET ts = "اور وہ اللہ کی راہ میں کوئی چھوٹا سا خرچ بھی نہیں کرتے اور نہ بڑا اور نہ کسی وادی کو طے کرتے ہیں مگر (معاً ان کے اعمال میں وہ نیک کام) لکھ لیا جاتا ہے تا کہ اللہ ان کے عملوں کا (اعلیٰ سے) اعلیٰ بدلہ دے۔ " WHERE ayatNumber = 121 AND chapterNumber = 9;
UPDATE verses SET ts = "اور مومنوں کے لئے ممکن نہ تھا کہ وہ سب کے سب (اکٹھے ہو کر تعلیم دین کے لئے) نکل پڑیں۔ پس کیوں نہ ہوا کہ ان کی جماعت میں سے ایک گروہ نکل پڑتا۔ تا کہ وہ دین پوری طرح سیکھتے اور اپنی قوم  کو واپس لوٹ کر (بے دینی سے) ہوشیار کرتے۔ تا کہ وہ (گمراہی سے) ڈرنے لگیں۔ " WHERE ayatNumber = 122 AND chapterNumber = 9;
UPDATE verses SET ts = "اے مومنو! ان کفار سے جنگ کرو جو تمہارے پہلو میں رہتے ہیں اور چاہیے  کہ وہ تم میں پختگی محسوس کریں۔ اور جان لو کہ اللہ متقیوں کے ساتھ ہے۔ " WHERE ayatNumber = 123 AND chapterNumber = 9;
UPDATE verses SET ts = "اور جب کبھی کوئی سورۃ نازل ہوتی ہے تو ان میں سے بعض (منافق) کہتے ہیں کہ اس (سورۃ) نے تم میں سے کس کا ایمان بڑھایا ہے؟ سو (یاد رکھو کہ) جو لوگ مومن ہیں (ان کے پہلے ایمان کے نتیجہ میں) ان کے ایمان کو اس (سورۃ) نے بڑھایا ہے اور وہ خوشی حاصل کر رہے ہیں۔ " WHERE ayatNumber = 124 AND chapterNumber = 9;
UPDATE verses SET ts = "اور جن کے دل میں بیماری ہے اس (سورۃ) نے ان کے (پہلے) گند پر اور گند چڑھا دیا ہے۔ یہاں تک کہ وہ ایسی حالت میںمریں گے کہ وہ کافر ہوں گے۔ " WHERE ayatNumber = 125 AND chapterNumber = 9;
UPDATE verses SET ts = "کیا وہ دیکھتے نہیں کہ ان کی ہر سال میں ایک یا دو دفعہ آزمائش کی جاتی ہے‘ پھر بھی وہ توبہ نہیں کرتے اور نہ نصیحت حاصل کرتے ہیں۔ " WHERE ayatNumber = 126 AND chapterNumber = 9;
UPDATE verses SET ts = "اور جب کوئی سورۃ نازل ہوتی ہے تو ان میں سے کچھ لوگ دوسروں کی طرف دیکھنے لگتے ہیں تا معلوم کریں کہ کیا کوئی شخص تم کو دیکھ تو نہیں رہا۔ پھر (تسلی کرکے) یہ (لوگ مجلس سے) چلے جاتے ہیں اللہ نے ان کے دلوں کو پھیر دیا ہے کیونکہ وہ ایسے لوگ ہیں جو سمجھتے نہیں۔ " WHERE ayatNumber = 127 AND chapterNumber = 9;
UPDATE verses SET ts = "(اے مومنو)! تمہارے پاس تمہاری ہی قوم کا ایک فرد رسول ہو کر آیا ہے تمہارا تکلیف میں پڑنا اس پر شاق گذرتا ہے اور وہ تمہارے لئے خیر کا بہت بھوکا ہے۔ اور مومنوں کے ساتھ محبت کرنے والا (اور) بہت کرم کرنے والا ہے۔ " WHERE ayatNumber = 128 AND chapterNumber = 9;
UPDATE verses SET ts = "پس اگر وہ پھر جائیں تو تو کہہ دے کہ اللہ میرے لئے کافی ہے اس کے سوا کوئی معبود نہیں۔ میں اسی پر توکل کرتا ہوں اور وہ عرش عظیم کا رب ہے۔ " WHERE ayatNumber = 129 AND chapterNumber = 9;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے(پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 10;
UPDATE verses SET ts = "میں اللہ دیکھنے والا ہوں۔ یہ (یعنی اس سورۃ کی آیتیں) کامل (اور) پرحکمت کتاب کی آیتیں ہیں۔ " WHERE ayatNumber = 2 AND chapterNumber = 10;
UPDATE verses SET ts = "کیا لوگوں کے نزدیک ہمارا ان  میں سے ایک شخص پر (یہ) وحی کرنا کہ لوگوں کو ہوشیار کر اور جو لوگ ایمان لائے ہیں انہیں بشارت دے کہ ان کے لئے ان کے رب کے حضور میں ایک ظاہر و باطن طور پر کامل درجہ ہے (ایسا) عجیب (امر) تھا (کہ) ان کافروں نے کہہ دیا کہ یہ (شخص) ضرور ہی کھلا کھلا دھوکہ باز ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 10;
UPDATE verses SET ts = "تمہارا رب یقیناً اللہ ہے جس نے آسمانوں اور زمین کو چھ وقتوں میں پیدا کیا۔ پھر وہ عرش پر قرار فرما ہوا وہ ہر امر کا انتظام کرتا ہے (اس کے حضور میں) کوئی بھی (کسی کا) شفیع نہیں (ہوسکتا) سوائے اس کی اجازت کے (سنو)! یہ اللہ (مذکورہ بالا صفات والا ہے)  (اور وہی) تمہارا رب ہے۔ اس لئے تم اسی کی عبادت کرو۔ کیا تم (باوجود ان باتوں کے) پھر بھی نصیحت حاصل نہیں کرو گے۔ " WHERE ayatNumber = 4 AND chapterNumber = 10;
UPDATE verses SET ts = "اسی کی طرف تم سب کو لوٹ کر جانا ہے (یہ) اللہ کا وعدہ ہے (جو) پورا ہو کر رہنے والا (ہے) وہ یقیناً مخلوق کو پیدا کرتا ہے۔ پھر اسے لوٹاتا ہے تا کہ  جو لوگ ایمان لائے اور انہوں نے نیک (اور مناسب حال) کام کئے انہیں اجر میں کامل حصہ دے اور جن لوگوں نے کفر (اختیار) کیا ان کے لئے کھولتا ہوا پانی پینے کے لئے ہوگا اور ایک دردناک عذاب ہوگا کیونکہ وہ کفر کرتے (چلے جاتے ) تھے۔ " WHERE ayatNumber = 5 AND chapterNumber = 10;
UPDATE verses SET ts = "وہی ہے جس نے سورج کو ذاتی روشنی (والا) اور چاند کو نور (والا) بنایا ہے اور ایک اندازہ کے مطابق اس کی منزلیں بنائی ہیں تا کہ تمہیں سالوں کی گنتی اور حساب معلوم ہو۔ اس (سلسلہ) کو اللہ نے حق (وحکمت) کے ساتھ ہی پیدا کیا ہے وہ ان آیات کو علم والے لوگوں کے لئے تفصیل کے ساتھ بیان کرتا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 10;
UPDATE verses SET ts = "رات اور دن کے آگے پیچھے آنے اور جو کچھ اللہ نے آسمانوں اور زمین میں پیدا کیا ہے (اس میں) متقی لوگوں کے لئے یقینی طور پر بہت سے نشان ہیں۔ " WHERE ayatNumber = 7 AND chapterNumber = 10;
UPDATE verses SET ts = "جو لوگ ہم سے ملنے کی امید نہیں رکھتے اور اس ورلی زندگی پر راضی ہو گئے ہیں اور اس پر انہوں نے اطمینان پکڑ لیا ہے اور (پھر) جو لوگ ہمارے نشانوں (کی طرف) سے غافل ہو گئے ہیں۔ " WHERE ayatNumber = 8 AND chapterNumber = 10;
UPDATE verses SET ts = "ان (سب) کا ٹھکانا ان کی (اپنی) کمائی کی وجہ سے یقیناً (دوزخ کی) آگ ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 10;
UPDATE verses SET ts = "جو لوگ ایمان لائے اور انہوں نے نیک (اور مناسب حال) عمل کئے انہیں ان کا رب ان کے ایمان کی وجہ سے (کامیابی کے راستہ کی طرف) ہدایت دے گا (اور) آسائش والی جنتوں میں انہی کے (تصرف کے) نیچے نہریں بہتی ہوں گی۔ " WHERE ayatNumber = 10 AND chapterNumber = 10;
UPDATE verses SET ts = "ان (جنتوں) میں (خدا کے حضور) ان کی پکار (یہ) ہوگی اے اللہ! تو پاک ہے اور ان کی (ایک دوسرے کے لئے) دعا یہ ہوگی (کہ تم پر ہمیشہ کے لئے) سلامتی ہو اور سب سے آخر میں وہ بلند آواز سے یہ کہہ دیں گے کہ اللہ ہی سب تعریفوں کا مستحق ہے جو تمام جہانوں کا رب ہے۔ " WHERE ayatNumber = 11 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اگر اللہ لوگوں پر (ان کے اعمال کی) بدی (کا نتیجہ) ان کے مال کو جلد چاہنے کی طرح جلد وارد کرتا تو ان کی (زندگی کے اختتام کی) میعاد ان پر لائی جا چکی ہوتی۔ (مگر چونکہ ہم نے ایسا پسند نہیںکیا) اس لئے ہم ان لوگوں کو جو ہماری ملاقات کی امید نہیں رکھتے اس حالت میں چھوڑ رہے ہیں کہ وہ اپنی سرکشی میں سرگرداں پھر رہے ہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جب انسان کو تکلیف پہنچتی ہے تو وہ اپنے پہلو کے بل (لیٹا ہوا) یا بیٹھا ہوا یا کھڑا ہوا ہمیں پکارتا ہے۔ پھر جب ہم اس کی تکلیف کو اس سے دور کر دیتے ہیں تو وہ (اس طرح سے کتراکر) گذر جاتا ہے (کہ) گویا اس نے کسی تکلیف (کے دور کرنے) کے لئے جو اسے پہنچی تھی ہمیں پکارا ہی نہیں تھا۔ اسی طرح تمام حد سے بڑھ جانے والوں کو جو کچھ وہ کیا کرتے ہیں خوبصورت کرکے دکھلایا گیا ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ہم یقینی طور پر تم سے پہلے (بھی) قوموں کے بعد قوموں کو جبکہ انہوں نے باوجود اس کے کہ ان کے پاس ان کے رسول کھلے نشان لے کر آئے تھے ظلم کیا تھا اور ایمان نہ لائے تھے ہلاک کر چکے ہیں ہم مجرم لوگوں کو اسی طرح بدلہ دیا کرتے ہیں۔ " WHERE ayatNumber = 14 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر ان کے بعد ہم نے تمہیں زمین میں (ان کا) جانشین بنایا‘ تا کہ ہم دیکھیں کہ تم کیسے عمل کرتے ہو۔ " WHERE ayatNumber = 15 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جب انہیں ہماری روشن آیات پڑھ کر سنائی جاتی ہیں تو جو لوگ ہمارے ملنے کی امید نہیں رکھتے وہ کہہ دیتے ہیں کہ (اے محمد)! تو اس کے سوا کوئی اور قرآن لے آ۔ یا اس میں (ہی کچھ) تغیر (وتبدل) کر دے۔ تو (انہیں) کہہ (کہ یہ) میرا کام نہیں کہ میں اس میں اپنی طرف سے (کوئی) تغیر (وتبدل) کردوں۔ میں (تو) جو (کچھ) مجھ پر وحی (سے حکم نازل) کیا جاتا ہے‘ اسی کی پیروی کرتا ہوں اور اگر میں اپنے رب کی نافرمانی کروں تو میں ایک بڑے (ہولناک) دن کے عذاب سے ڈرتا ہوں (کہ مجھے نہ آپکڑے) ۔" WHERE ayatNumber = 16 AND chapterNumber = 10;
UPDATE verses SET ts = "(اور) تو (انہیں) کہہ کہ اگر اللہ کی (یہی) مشیت ہوتی (کہ اس کی جگہ کوئی اور تعلیم دی جائے) تو میں اسے تمہارے سامنے پڑھ کر نہ سناتا اور نہ وہ (ہی) تمہیں اس (تعلیم) سے آگاہ کرتا۔ چنانچہ اس سے پہلے میں ایک عرصہ دراز تم میں گزار چکا ہوں کیا پھر (بھی) تم عقل سے کام نہیں لیتے۔ " WHERE ayatNumber = 17 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر (تم ہی بتائو کہ) جو اللہ پر بہتان باندھے یا اس کے نشانات کو جھٹلائے اس سے بڑھ کر کون ظالم ہوگا (غرض) یہ یقینی بات ہے کہ مجرم لوگ کامیاب نہیں ہوتے۔ " WHERE ayatNumber = 18 AND chapterNumber = 10;
UPDATE verses SET ts = "اور یہ (لوگ) اللہ کو چھوڑ کر ایسی چیز کی پرستش کرتے ہیں جو نہ انہیں نقصان دیتی ہے اور نہ نفع پہنچاتی ہے اور کہتے ہیں (کہ) یہ (ہمارے معبود) اللہ کے حضور میں ہمارے شفیع ہیں۔ تو (انہیں) کہہ (کہ) کیا تم اللہ کو وہ (بات) بتاتے ہو جس کے متعلق نہ آسمانوں میں (پائے جانے کا) اسے علم ہے اور نہ (ہی) زمین میں (کہیں اس کے وجود کا کوئی پتا ہے) وہ پاک ہے اور ان کے شریک ٹھہرانے سے وہ بالاتر ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 10;
UPDATE verses SET ts = "اور (تمام) لوگ ایک ہی گروہ (بنے ہوئے) تھے۔ پھر انہوں نے آپس میں اختلاف (پیدا) کر لیا اور جو بات تیرے رب کی طرف سے پہلے (بصورت وعدہ) آچکی ہے اگر وہ (مانع) نہ ہوتی تو جس (امر) میں وہ اختلاف کر رہے ہیں  اس کے متعلق ان کے درمیان (کبھی کا) فیصلہ (صادر) کیا جا چکا ہوتا۔ " WHERE ayatNumber = 20 AND chapterNumber = 10;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ اس (رسول) پر اس کے رب کی طرف سے کوئی نشان کیوں نہیں اتارا گیا تو (انہیں) کہہ (کہ) ہر غیب (کی بات کا علم) اللہ ہی کو حاصل ہے اس لئے تم (اس کا) انتظار کرو‘ میں (بھی) تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔ " WHERE ayatNumber = 21 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جب لوگوں کو کسی تکلیف کے بعد جو انہیں پہنچی ہو ہم (کسی قدر اپنی) رحمت (کا مزہ) چکھاتے ہیں تو جھٹ ہمارے نشانوں کے متعلق ان کی طرف سے کوئی (نہ کوئی مخالفانہ) تدبیر ہونے لگتی ہے۔ تو (انہیں) کہہ (کہ اس کے مقابل پر) اللہ کی تدبیر تو بہت ہی جلد (کارگر) ہوا کرتی ہے (اور) تم جو تدابیر (بھی) کرتے ہو ہمارے فرستادے انہیں لکھتے رہتے ہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 10;
UPDATE verses SET ts = "وہ (خدائے کریم) ہی ہے جو تمہیں (توفیق دیکر) خشکی اور تری میں چلاتا ہے۔ یہاں تک کہ جب تم لوگ کشتیوں میں (سوار) ہوتے ہو اور وہ عمدہ ہوا کے ذریعہ سے ان (لوگوں) کو بھی لیکر چل رہی ہوتی ہیں اور وہ ان پر اِترا رہے ہوتے ہیں تو ان پر ایک (تندو) تیز ہوا آجاتی ہے اور ہر طرف سے موج (پر موج) ان پر (چڑھ) آتی ہے اور وہ سمجھنے لگتے ہیں کہ (اب) وہ ہلاکت (کے منہ) میں آگئے ہیں تو (ایسے وقت میں) وہ (اپنی) اطاعت کو خدا کے لئے خالص کرتے  ہوئے اس کو پکارتے ہیں (اور کہتے ہیں کہ اے اللہ)! اگر تو نے ہمیں اس (مصیبت) سے نجات دی تو ہم ضرور ہی (تیرے) شکر گذاروں (کے زمرہ) میں (داخل) ہو جائیں گے۔ " WHERE ayatNumber = 23 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر جب وہ انہیں (اس عذاب سے) نجات دیکر خشکی پر پہنچاتا ہے تو وہ جھٹ زمین میں ناحق سرکشی کرنے لگتے ہیں (اے لوگو)! تمہارا صرف ورلی زندگی کو چاہنا تمہارے نفسوں پر (وبال بن کر) پڑے گا۔ پھر ہماری طرف تمہاری واپسی ہوگی۔ تب جو (کچھ کہ) تم کرتے رہے ہوگے‘ ہم اس سے تمہیں آگاہ کریں گے۔ " WHERE ayatNumber = 24 AND chapterNumber = 10;
UPDATE verses SET ts = "اس ورلی زندگی کی حالت (تو) اس پانی کی طرح ہے‘ جسے ہم نے بادل سے برسایا‘ پھر اس کے ساتھ زمین کی روئیدگی جسے آدمی اور چارپائے کھاتے ہیں مل (کر یک جان ہو) گئی۔ یہاں تک کہ جب زمین نے (اس کے ذریعہ سے) اپنی کمال درجہ کی زینت کو پالیا ور خوب صورت ہوگئی اور اس کے مالکوں نے سمجھ لیا کہ (اب) وہ اس پر قابو یافتہ ہیں تو اس پر دن یا رات کو (عذاب کے متعلق) ہمارا حکم آگیا۔ اور ہم نے اسے ایک کٹے ہوئے کھیت کی طرح کر دیا گویا یہاں کل (کچھ) تھا (ہی) نہیں۔ (غرض) جو لوگ سوچ سے کام لیتے ہیں ان کے لئے ہم اسی طرح (اپنی) آیات کو کھول کر بیان کرتے ہیں۔ " WHERE ayatNumber = 25 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اللہ سلامتی کے گھر کی طرف بلاتا ہے اور جسے پسند کرتا ہے (اسے) ایک سیدھی راہ پر چلا (کر منزل مقصود تک پہنچا) دیتا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 10;
UPDATE verses SET ts = "ان لوگوں کے لئے جنہوں نے نیکو کاری اختیار کی بہترین انجام ہوگا اور (اس پر) مزید (انعامات بھی)ـــ-- ان کے چہروں پر نہ غبار چھائے گا اور نہ ذلت (کے آثار ہوں گے) یہ لوگ جنت کے مکین ہیں (اور) اس میں رہتے چلے جائیں گے۔ " WHERE ayatNumber = 27 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جنہوں نے بدیاں کی ہوں گی (ان کے لئے) بدی کا بدلہ اس (بدی کے) برابر ہوگا اور انہیں ذلت پہنچے گی اور کوئی بھی انہیں اللہ (کے عذاب) سے بچانے والا نہیں ہوگا (اور ان کی حالت ایسی ہوگی) گویا ان کے مونہوں پر رات کے کئی (کئی) تاریک ٹکڑے ڈال دئے گئے ہیں۔ یہ (لوگ) آگ (میں رہنے) والے ہیں۔ اس میں لمبے عرصہ تک رہتے چلے جائیں گے۔ " WHERE ayatNumber = 28 AND chapterNumber = 10;
UPDATE verses SET ts = "اور (اے لوگو! اس دن کو یاد کرو) جس دن ہم ان سب کو جمع کریں گے پھر جنہوں نے شرک کیا (ہوگا) انہیں ہم کہیں گے (کہ پرے ہٹ کر) اپنی جگہ پر (کھڑے رہو) تم بھی اور تمہارے (بنائے ہوئے خدائی کے) حصہ دار (بھی) پھر ہم ان میں آپس میں (بھی) جدائی ڈال دیں گے اور ان کے (بنائے ہوئے خدا کے) شریک (انہیں) کہیں گے (کہ) تم ہماری عبادت (توہرگز) نہیں کرتے تھے۔ " WHERE ayatNumber = 29 AND chapterNumber = 10;
UPDATE verses SET ts = "پس تمہارے درمیان اور ہمارے درمیان (خود) اللہ کافی گواہ ہے۔ ہم تمہاری پرستش سے قطعاًبے خبر تھے۔ " WHERE ayatNumber = 30 AND chapterNumber = 10;
UPDATE verses SET ts = "(تب) وہاں ہر ایک شخص جو کچھ اس نے (اپنے لئے) بویا ہوگا اس کا مزہ چکھے گا اور انہیں ان کے سچے مالک اللہ کی طرف لوٹا (کر) لایا جائے گا اور جو کچھ وہ (اپنے پاس سے) گھڑتے تھے وہ (سب) انہیں بھول جائے گا۔ " WHERE ayatNumber = 31 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (ان سے) کہہ (کہ بتائو) آسمان اور زمین سے تمہیں کون روزی دیتا ہے یا (یہ کہ) کانوں اور آنکھوں پر کون قابو رکھتا ہے اور کون (ایک) مردہ (چیز) میں سے زندہ (چیز) نکالتا اور زندہ (چیز) میں سے مردہ (چیز) نکالتا ہے اور کون (اس) تمام امر کا انتظام کرتا ہے۔ اس پر وہ ضرور کہیں گے کہ اللہ (کرتا ہے) تب (انہیں) کہہ (کہ پھر وجہ) کیا (ہے کہ) تم تقویٰ اختیار نہیں کرتے۔ " WHERE ayatNumber = 32 AND chapterNumber = 10;
UPDATE verses SET ts = "وہ اللہ ہی (کی ہستی) ہے (جو ایسا کرتی ہے) (اور وہی) تمہارا حقیقی رب (ہے) اور حق کو چھوڑ کر گمراہی کے سوا کیا (حاصل ہو سکتا) ہے۔ پس (بتائو تو سہی کہ) کس طرح تم (اور اور طرف) پھیرے جا رہے ہو۔ " WHERE ayatNumber = 33 AND chapterNumber = 10;
UPDATE verses SET ts = "اسی طرح جن لوگوں نے فسق اختیار کیا ہے ان پر تیرے رب کا فرمودہ پورا ہوا ہے کہ وہ ایمان نہیں لاتے۔ " WHERE ayatNumber = 34 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) کیا تمہارے (قرار دادہ) شریکوں میں سے کوئی (بھی ایسا) ہے جو پہلی بار پیدا کرتا ہو (اور) پھر اس (پیدائش) کو دوہراتا ہو۔ تو (انہیں) کہہ (کہ) اللہ (ہی ہے جو) پہلی بار پیدا کرتا ہے (اور) پھر اس (پیدائش) کو دوہراتا ہے۔ پس (بتائو کہ باوجود اس کے) تمہیں کس طرف کو پھیرا جا رہا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (ان سے یہ بھی) کہہ (کہ) کیا تمہارے (بنائے ہوئے) شریکوں میں سے کوئی (بھی ایسا) ہے جو حق کی طرف (لوگوں) کی رہنمائی کرتا ہو (وہ تو اس سوال کا کیا جواب دیں گے) تو (خود ہی ان سے) کہہ دے (کہ) اللہ (ہی ہے جو) حق کی طرف (لوگوں کی) رہنمائی کرتا ہے پس کیا وہ (خدا) جو حق کی طرف رہنمائی کرتا ہے اس بات کا زیادہ مستحق ہے کہ اس (کے احکام) کی پیروی کی جائے یا وہ (فرضی خدا) جو کہ سوائے اس (صورت) کے کہ اسے (ہدایت کا) راستہ دکھایا جائے (خود بھی) راہ نہیں پاتا پھر بتائو تمہیں کیا (ہوگیا) ہے؟ تم کیسے فیصلے کرتے ہو؟ " WHERE ayatNumber = 36 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ان میں سے اکثر (اپنے) وہم کے سوا (اور کسی چیز کی) پیروی نہیں کرتے (حالانکہ) وہم حق کی جگہ کچھ بھی کام نہیں دیتا۔ جو کچھ یہ (لوگ) کرتے ہیں (اسے) اللہ یقیناً خوب جانتا ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اس قرآن کا اللہ کے سوا (کسی اور) کی طرف سے جھوٹے طور پر بنا لیا جانا (ممکن ہی) نہیں ہو سکتا۔ بلکہ یہ (تو) اس (کلام الٰہی) کی تصدیق (کرتا) ہے جو اس سے پہلے (موجود) ہے اور کتاب (الٰہی میں جو کچھ پایا جانا چاہیے اس) کی تفصیل (بیان کرتا ہے اور) اس میں بھی کوئی شک نہیں کہ یہ تمام جہانوں کے رب کی طرف سے ہے۔ " WHERE ayatNumber = 38 AND chapterNumber = 10;
UPDATE verses SET ts = "کیا وہ کہتے ہیں کہ اس (شخص) نے اسے (اپنی طرف سے) گھڑ لیا ہے؟ تو (انہیں) کہہ (کہ) اگر تم (اس بیان میں) سچے ہو تو اس (کی سورتوں) جیسی کوئی ایک (ہی) سورۃ لے آئو اور اللہ کے سوا جس (کسی کو بھی بلانے) کی تمہیں طاقت ہو (اپنی مدد کے لئے) بلالو۔ " WHERE ayatNumber = 39 AND chapterNumber = 10;
UPDATE verses SET ts = "(مگر ان کا یہ خیال درست نہیں) بلکہ (حقیقت میں) انہوں نے (ایک) ایسی چیز کو جھٹلا دیا ہے جس کا انہوں نے پورا علم حاصل ہی نہیں کیا تھا۔ اور نہ (ہی) ابھی اس کی حقیقت ان پر ظاہر ہوئی تھی۔ جو لوگ ان سے پہلے تھے انہوں نے (بھی) اسی طرح جھٹلایا تھا پھر دیکھ (کہ) ان ظالموں کا کیا انجام ہوا تھا۔ " WHERE ayatNumber = 40 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ان میں سے بعض ایسے ہیں جو اس پر ایمان لائیں گے اور بعض ایسے ہیں جو اس پر ایمان نہیں لائیں گے اور تیرا رب فساد کرنے والوں کو خوب جانتا ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اگر وہ تجھے جھٹلائیں تو (انہیں) کہہ (کہ) میرا عمل (خود) میرے لئے (مفید یا مضر) ہوگا اور تمہارا عمل تمہارے لئے (مفید یا مضر ہوگا) جو کچھ میں کرتا ہوں اس سے تم بری (الذمہ) ہو۔ اور جو کچھ تم کرتے ہو اس سے میں بری (الذمہ) ہوں۔ " WHERE ayatNumber = 42 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ان (لوگوں) میں سے (بعض ایسے) ہیں جو تیری (باتوں کی) طرف (ہروقت) کان رکھتے ہیں۔ (تو) کیا تو ایسے بہروں کو اگرچہ وہ عقل سے کام (ہی) نہ لیتے ہوں (اپنی بات) سنوالے گا؟ " WHERE ayatNumber = 43 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ان میں سے بعض ایسے (بھی) ہیں جو تیری طرف (نظریں گاڑ کر) دیکھتے (رہتے) ہیں (تو) کیا تو ان اندھوں کی اگرچہ وہ بصیرت (بھی) نہ رکھتے ہوں راہنمائی کرلے گا۔ " WHERE ayatNumber = 44 AND chapterNumber = 10;
UPDATE verses SET ts = "اللہ (کی شان) یقیناً (ایسی ہے کہ وہ) لوگوں پر کچھ بھی ظلم نہیں کرتا‘ بلکہ لوگ اپنی جانوں پر (آپ ہی) ظلم کرتے ہیں۔ " WHERE ayatNumber = 45 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جس دن وہ انہیں ایسی حالت میں جمع کرے گا کہ (وہ محسوس کرتے ہوں گے کہ) گویا وہ دن کی ایک ساعت کے سوا (دنیا میں) نہیں رہے تھے (اس دن) وہ ایک دوسرے (کی حالت) کو معلوم کر لیں گے (یاد رکھو کہ) جن لوگوں نے اللہ کے حضور پیش ہونے (کے وعدہ) کو جھٹلایا اور وہ ہدایت کو قبول کرنے والے نہیں بنے‘ انہوں نے نقصان (ہی) اٹھایا۔ " WHERE ayatNumber = 46 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جس (عذاب کے بھیجنے) کا ہم ان سے وعدہ کرتے ہیں اگر ہم اس کا کوئی حصہ (تیرے سامنے بھیج کر) تجھے دکھادیں (توتوبھی دیکھ لے گا) اور اگر ہم (اس گھڑی سے پہلے) تجھے وفات دے دیں تو (تجھے مابعدالموت اس کی حقیقت معلوم ہو جائے گی اور) بہرحال (چونکہ) انہوں نے ہماری طرف لوٹنا ہے (انہیں بھی وہاں اس کی حقیقت معلوم ہو جائے گی) اور (یاد رکھو کہ) جو کچھ وہ کرتے ہیں اللہ اس سے خوب آگاہ ہے۔ " WHERE ayatNumber = 47 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ہر ایک قوم کے لئے ایک (نہ ایک) رسول (کا آنا ضروری ہوتا) ہے۔ پس جب ان کا رسول آتا ہے تو ان کے درمیان انصاف کے ساتھ فیصلہ کر دیا جاتا ہے اور ان پر کوئی ظلم نہیں کیا جاتا۔ " WHERE ayatNumber = 48 AND chapterNumber = 10;
UPDATE verses SET ts = "اور وہ کہتے ہیں (کہ) اگر تم سچے ہو تو یہ وعدہ کب (پورا) ہوگا؟ " WHERE ayatNumber = 49 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) میں (تو) اللہ کی مشیت کے سوا (خود) اپنے حق میں (بھی) نہ کسی نقصان پر قابو رکھتا ہوں اور نہ کسی نفع پر (ہاں یہ درست ہے کہ) ہر ایک قوم کے (مستحق عذاب ہونے کے) لئے ایک میعاد مقرر ہوتی ہے‘ جب وہ آجاتی ہے تو (اس وقت) وہ نہ کوئی گھڑی (اس سے) پیچھے رہ (کہ اس سے بچ) سکتی ہے اور نہ (ہی) آگے بڑھ (کر اس سے خلاصی پا) سکتی ہے۔ " WHERE ayatNumber = 50 AND chapterNumber = 10;
UPDATE verses SET ts = "ُتو (اُنہیں) کہہ دے (کہ بھلا) بتائو (تو سہی کہ) اگر اس کا عذاب رات کو یا دن کو (تمہارے دیکھتے دیکھتے) تم پر آجائے‘ تو مجرم لوگ اس سے کیونکر بھاگ سکیں گے۔ " WHERE ayatNumber = 51 AND chapterNumber = 10;
UPDATE verses SET ts = "کیا پھر جب وہ آجائے گا (تو اس وقت) تم اس پر ایمان لائو گے؟ (اس کا کچھ فائدہ نہیں ہو سکتا۔ اس وقت تو تم سے کہا جائے گا کہ) کیا اب (تم ایمان لاتے ہو) حالانکہ (اس کے آنے تک) تم اس کے جلد آنے کا مطالبہ کرتے رہے ہو۔ " WHERE ayatNumber = 52 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر جن لوگوں نے ظلم کیا ہوگا انہیں کہا جائے گا (کہ اب) تم قائم رہنے والا عذاب پائو۔ کیا تمہیں اپنے عمل کے سوا کسی اور چیز کی جزا دی جا رہی ہے۔ " WHERE ayatNumber = 53 AND chapterNumber = 10;
UPDATE verses SET ts = "اور وہ تجھ سے دریافت کرتے ہیں (کہ) کیا وہ (عذاب سچ مچ) واقع ہوگا۔ ُتو (انہیں) کہہ دے (کہ) ہاں! مجھے اپنے رب کی قسم ہے (کہ) وہ یقیناً واقع ہونے والا ہے۔ اور تم (ایسا کرنے سے خدا کو) عاجز نہیں کر سکتے۔ " WHERE ayatNumber = 54 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اگر ایسے شخص کو جو ظالم بنا ہے وہ سب کچھ مل جاتا جو زمین میں موجود ہے تو وہ اس کو دے کر اپنے نفس کو چھڑانے میں کوتاہی نہ کرتا (یعنی اگر ممکن ہو سکتا مگر ایسا نہ ہو سکے گا) اور جب وہ عذاب کو دیکھیں گے تو وہ اپنی شرمندگی کو چھپائیں گے اور ان کے درمیان انصاف سے فیصلہ کر دیا جائے گا اور ان پر (کوئی) ظلم نہیں کیا جائے گا۔ " WHERE ayatNumber = 55 AND chapterNumber = 10;
UPDATE verses SET ts = "سنو! جو کچھ آسمانوں اور زمین میں (پایا جاتا) ہے وہ سب (کا سب) یقیناً اللہ (ہی) کا ہے۔ اور اللہ کا وعدہ یقیناً پورا ہونے والا ہے مگر ان میں سے اکثر (لوگ) نہیں جانتے۔ " WHERE ayatNumber = 56 AND chapterNumber = 10;
UPDATE verses SET ts = "وہ زندہ کرتا ہے اور مارتا ہے اور اسی کی طرف تمہیں لوٹایا جائے گا۔ " WHERE ayatNumber = 57 AND chapterNumber = 10;
UPDATE verses SET ts = "اے لوگو! تمہارے پاس تمہارے رب کی طرف سے یقیناً ایک ایسی کتاب آگئی ہے جو سراسر نصیحت ہے اور وہ (ہر) اس (بیماری) کے لئے جو سینوں میں (پائی جاتی) ہو شفا (دینے والی) ہے اور ایمان لانے والوں کے لئے ہدایت اور رحمت ہے۔ " WHERE ayatNumber = 58 AND chapterNumber = 10;
UPDATE verses SET ts = "تو ان سے کہہ دے (کہ یہ سب کچھ) اللہ کے فضل اور اس کی رحمت سے (وابستہ) ہے پس اسی پر انہیں خوشی منانا چاہیے جو (مال) وہ جمع کر رہے ہیں اس سے یہ (نعمت کہیں) زیادہ بہتر ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (ان سے) کہہ (کہ) کیا تم نے اس بات کو (بھی کبھی سوچ کر) دیکھا ہے کہ اللہ نے تمہارے لئے (آسمان سے) رزق اتارا ہے پھر تم نے اس میں سے (کچھ) حرام اور (کچھ) حلال ٹھہرا دیا ہے تو (ان سے) کہہ (کہ) کیا اللہ نے تمہیں (اس بات) کی اجازت دی ہے یا تم اللہ پر (محض) افترا کرتے ہو۔ " WHERE ayatNumber = 60 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جو لوگ اللہ پر جھوٹ باندھتے ہیں ان کا قیامت کے دن کے متعلق کیا خیال ہے۔ اللہ لوگوں پر یقیناً (بہت ہی بڑے) انعام کرنے والا ہے۔ مگر ان میں سے اکثر (لوگ) شکر نہیں کرتے۔ " WHERE ayatNumber = 61 AND chapterNumber = 10;
UPDATE verses SET ts = "اور تو نہ (کبھی) کسی کام میں (مشغول) ہوتا ہے اور نہ تو اس (کتاب) میں سے کوئی حصۂ قرآن پڑھتا ہے اور نہ (ہی) تم (سب لوگ) کوئی (اور) کام کرتے ہو مگر (ان سب صورتوں میں) جب تم (اپنے کام میں پوری) توجہ سے مشغول ہوتے ہو ہم تمہیں دیکھ رہے ہوتے ہیں اور زمین یا آسمان میں کوئی (ایک) ذرہ بھر چیز (بھی) تیرے رب (کی نظر) سے پوشیدہ نہیں ہوتی اور نہ (ہی ذرہ سے) کوئی چھوٹی چیز یا بڑی چیز ہے جو (ہر ایک حقیقت کو) روشن کر دینے  والی ایک کتاب میں (مذکور اور موجود) نہ ہو۔ " WHERE ayatNumber = 62 AND chapterNumber = 10;
UPDATE verses SET ts = "سنو! جو (لوگ) اللہ سے سچی محبت رکھنے والے ہیں ان پر نہ کوئی خوف (مستولی ہوتا) ہے اور نہ وہ غمگین ہوتے ہیں۔ " WHERE ayatNumber = 63 AND chapterNumber = 10;
UPDATE verses SET ts = "(یعنی وہ لوگ) جو ایمان لائے اور تقویٰ کو (ہمیشہ) لازم حال رکھتے تھے۔ " WHERE ayatNumber = 64 AND chapterNumber = 10;
UPDATE verses SET ts = "ان کے لئے (اس) ورلی زندگی میں (بھی خدا کی طرف سے)  بشارت (پانے کا انعام مقرر) اور بعد والی (زندگی) میں بھی۔ اللہ کی (فرمودہ) باتوں میں (قطعاً)کوئی تبدیلی نہیں (ہوسکتی) یہی (وہ کامیابی ہے جو بڑی) عظیم الشان کامیابی (کہلا سکتی) ہے۔ " WHERE ayatNumber = 65 AND chapterNumber = 10;
UPDATE verses SET ts = "اور (چاہیے کہ) ان کی (کوئی مخالفانہ) بات تمہیں غمگین نہ کرنے پائے (کیونکہ) غلبہ بکلی اللہ کو حاصل ہے (اور) وہ خوب سننے والا (اور) خوب جاننے والا ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 10;
UPDATE verses SET ts = "سنو! (مخلوق کا) جو (فرد بھی) آسمانوں کے اندر (پایا جاتا) اور زمین میں (موجود) ہے (ہرایک) اللہ ہی کا ہے اور جو لوگ اللہ کے سوا (دوسری چیزوں کو) پکارتے ہیں وہ (دراصل خدا کے) شریکوں کی پیروی نہیں کرتے (بلکہ حق یہ ہے کہ) وہ صرف (اپنے) وہم کی پیروی کرتے ہیں اور وہ صرف تخمینوں (اور ڈھکونسلوں) سے کام لیتے ہیں۔ " WHERE ayatNumber = 67 AND chapterNumber = 10;
UPDATE verses SET ts = "وہ (لاشریک ہستی) ہی ہے جس نے تمہارے لئے رات کو اس لئے (تاریک) بنایا ہے کہ تم اس میں آرام پائو۔ اور اس کے بالمقابل دن کو (کام کاج کے لئے) روشن (بنایا ہے) جو لوگ (حق بات کو) سنتے (اور اس سے فائدہ اٹھاتے) ہیں ان کے لئے اس (نظام) میں یقیناً کئی ایک نشان ہیں۔ " WHERE ayatNumber = 68 AND chapterNumber = 10;
UPDATE verses SET ts = "(اور) انہوں نے (تو یہاں تک) کہہ دیا ہے (کہ) اللہ نے (بھی اپنے لئے) اولاد اختیار کی ہے (حالانکہ ہم تو اسے) اس بات سے پاک قرار دیتے ہیں۔ وہ نہایت (ہی) بے نیاز ہے جو کچھ آسمانوں میں (پایا جاتا) ہے اور جو کچھ زمین میں (موجود) ہے (سب) اسی کا ہے۔ اس (دعویٰ) کا (جوتم کرتے ہو) تمہارے پاس کوئی بھی ثبوت نہیں ہے (پھر) کیا تم اللہ کی طرف وہ (بات) منسوب کرتے ہو جس کی بابت تم (کچھ بھی) علم نہیں رکھتے۔ " WHERE ayatNumber = 69 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (ان سے) کہہ (کہ) جو (لوگ) اللہ پر جھوٹ باندھتے ہیں‘ وہ ہرگز کامیاب نہیں ہوتے۔ " WHERE ayatNumber = 70 AND chapterNumber = 10;
UPDATE verses SET ts = "دنیا میں (ان کا حصہ صرف چند روز کے لئے) نفع اٹھانا ہے پھر انہیں ہماری طرف لوٹنا ہوگا۔ پھر اس وجہ سے کہ وہ کفر کرتے (چلے جاتے) ہیں ہم انہیں سخت عذاب (کا مزہ) چکھائیں گے۔ " WHERE ayatNumber = 71 AND chapterNumber = 10;
UPDATE verses SET ts = "اور تو انہیں نوح کا حال (بھی) سنا کیونکہ اس نے (بھی) اپنی قوم سے کہا تھا (کہ) اے میری قوم! اگر تمہیں  میرا (خداداد) مرتبہ اور اللہ کے نشانوں کے ذریعہ سے تمہیں (تمہارا فرض) یاد دلانا ناگوار (گذرتا) ہے تو یاد رکھو کہ صرف اللہ کی ذات پر میں بھروسا رکھتا ہوں۔ تم اپنے تجویز کردہ شریکوں سمیت اپنی بات کے (متعلق سب پختگی کے سامانوں) کو جمع کر لو (اور) نیز چاہیے کہ تمہاری بات تم پر (کسی پہلو سے) مشتبہ نہ رہے‘ پھر اسے مجھ پر نافذ کردو اور مجھے (کوئی موقع اور) مہلت نہ دو۔ " WHERE ayatNumber = 72 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر بھی اگر تم پھر جائو تو اس میں میرا کوئی نقصان نہیں (بلکہ تمہارا ہی ہے) کیونکہ میں نے تم سے (اس کے بدلہ میں) کوئی اجر نہیں مانگا۔ میرا اجر اللہ کے سوا اور کسی پر نہیں ہے اور مجھے حکم دیا گیا ہے کہ میں (اس کے) کامل فرمانبرداروں میں سے بنوں۔ " WHERE ayatNumber = 73 AND chapterNumber = 10;
UPDATE verses SET ts = "اس پر بھی انہوں نے اسے جھٹلا دیا۔ تب ہم نے اسے اور (نیز) انہیں جو کشتی میں اس کے ساتھ (سوار) تھے بچا لیا۔ اور انہیں ہم نے پہلے لوگوں کا جانشین بنا دیا اور جن لوگوں نے ہمارے نشانوں کو جھٹلایا (تھا) انہیں ہم نے غرق کر دیا۔ سو دیکھو (کہ) جن لوگوں کو (اس عذاب سے) آگاہ کر دیا گیا تھا ان کا انجام کیسا ہوا؟ " WHERE ayatNumber = 74 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر اس کے بعد ہم نے اور (بھی کئی) رسول اپنی (اپنی) قوم کی طرف بھیجے اور وہ ان کے پاس روشن نشانات لے کر آئے تو وہ (لوگ) اس سبب سے کہ (اس سے پہلے) اس (صداقت) کو جھٹلا چکے تھے (اس پر) ایمان نہ لائے۔ ہم حد سے بڑھنے والوں کے دلوں پر اسی طرح مہر لگایا کرتے ہیں۔ " WHERE ayatNumber = 75 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر ان کے بعد ہم نے موسیٰ اور ہارون کو اپنے نشان دیکر فرعون اور اس کی قوم کے بڑے لوگوں کی طرف بھیجا‘ تو انہوں نے تکبر اختیار کیا اور  وہ (پہلے ہی سے) ایک مجرم قوم تھے۔ " WHERE ayatNumber = 76 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر جب ہماری طرف سے ان کے پاس حق آیا تو انہوں نے کہہ دیا (کہ) یہ ضرور ہی ایک (تعلقات کو) کاٹ دینے والا فریب ہے۔ " WHERE ayatNumber = 77 AND chapterNumber = 10;
UPDATE verses SET ts = "(اس پر) موسیٰ نے ان سے کہا (کہ) کیا تم حق کی نسبت (ایسا کہتے ہو (اور وہ بھی اس وقت) جبکہ وہ تمہارے پاس آگیا ہے کیا یہ فریب (ہو سکتا) ہے؟ حالانکہ فریب دینے والے کامیاب نہیں ہوتے۔ " WHERE ayatNumber = 78 AND chapterNumber = 10;
UPDATE verses SET ts = "انہوں نے کہا‘ کیا تو (اس لئے) ہمارے پاس آیا ہے کہ جس بات پر ہم نے اپنے باپ دادوں کو پایا ہے اس سے ہمیں ہٹا دے اور تم دونوں کو ملک میں بڑائی حاصل ہو جائے اور ہم تم پر ہرگز ایمان نہیں لائیں گے۔ " WHERE ayatNumber = 79 AND chapterNumber = 10;
UPDATE verses SET ts = "اور فرعون نے (اپنے لوگوں سے) کہا (کہ) تم میرے پاس (ملک بھر کے) ہر ایک کامل واقفیت رکھنے والے ساحر کو لے آئو۔ " WHERE ayatNumber = 80 AND chapterNumber = 10;
UPDATE verses SET ts = "پس جب ساحر (لوگ) آئے تو موسیٰ نے انہیں کہا (کہ) جو کچھ تم نے ڈالنا ہے ڈالو۔ " WHERE ayatNumber = 81 AND chapterNumber = 10;
UPDATE verses SET ts = "اس پر جب انہوں نے (جو کچھ ڈالنا تھا) ڈال دیا۔ تو موسیٰ نے کہا (کہ) جو کچھ تم لوگوں نے پیش کیا ہے (پورا) پورا فریب ہے۔ (اور) اللہ ضرور اسے مٹا دے گا۔ اللہ مفسدوں کی کارروائیوں کو کبھی کامیاب نہیں ہونے دیتا۔ " WHERE ayatNumber = 82 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اللہ اپنے کلمات کے ذریعہ سے حق کو قائم کرتا ہے گو مجرم (لوگ اس بات کو) ناپسند ہی کریں۔ " WHERE ayatNumber = 83 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر (بھی) اس کی قوم کے چند نوجوان ہی اس پر ایمان لائے باقی لوگوں نے فرعون (کے ڈر سے) اور اپنی قوم کے بڑے لوگوں کے خوف سے کہ وہ انہیں کسی مصیبت میں (نہ) ڈال دے موسیٰ کی فرمانبرداری (اختیار) نہ کی اور فرعون یقینی طور پر چیرہ دستی کرنے والا تھا اور حد سے بڑھ جانے والوں میں سے تھا۔ " WHERE ayatNumber = 84 AND chapterNumber = 10;
UPDATE verses SET ts = "اور موسیٰ نے (اپنی قوم سے) کہا (کہ) اے میری قوم! اگر یہ بات (درست) ہے کہ تم اللہ پر ایمان لائے ہو‘ تو اگر (اس کے ساتھ) تم (اس کے) سچے فرمانبردار (بھی) ہو تو اسی پر بھروسا کرو۔ " WHERE ayatNumber = 85 AND chapterNumber = 10;
UPDATE verses SET ts = "اس پر انہوں  نے کہا (کہ) ہم اللہ پر ہی بھروسا رکھتے ہیں۔ اے ہمارے رب! ہمیں (ان) ظالم لوگوں کے لئے فتنہ (کا موجب) نہ بنا۔ " WHERE ayatNumber = 86 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اپنی رحمت سے ہمیں کافر لوگوں (کے ظلم) سے بچالے۔ " WHERE ayatNumber = 87 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ہم نے موسیٰ اور اس کے بھائی کی طرف وحی بھیجی کہ تم مصر میں چند مکانوں (کی جگہ) کو اپنی قوم  کے (رہنے کے) لئے مخصوص کردو۔ اور تم سب لوگ اپنے (اپنے) گھر آمنے سامنے بنائو اور (ان میں) عمدگی سے نماز ادا (کیا) کرو۔ اور (یہ وحی بھی کی کہ اے موسیٰ)! تو مومنوں کو (کامیابی کی) بشارت دے۔ " WHERE ayatNumber = 88 AND chapterNumber = 10;
UPDATE verses SET ts = "اور موسیٰ نے کہا (کہ) اے ہمارے رب! تو نے فرعون (کو) اور اس کی قوم کے بڑے لوگوں کو (اس) ورلی زندگی میں زینت (کے سامان) اور اموال دے رکھے ہیں مگر اے ہمارے رب! نتیجہ یہ نکل رہا ہے کہ وہ تیرے رستہ سے (لوگوں کو) برگشتہ کر رہے ہیں۔ پس اے ہمارے رب! ان کے مالوں کو برباد کر دے اور ان کے دلوں پر بھی سزا نازل کر جس کا یہ نتیجہ نکلے کہ جب تک وہ دردناک عذاب نہ دیکھ لیں ایمان نہ لائیں۔ " WHERE ayatNumber = 89 AND chapterNumber = 10;
UPDATE verses SET ts = "(اس پر اللہ نے) فرمایا‘ تمہاری دعا قبول کر لی گئی۔ پس تم دونوں ثابت قدمی دکھائو۔ اور جو لوگ علم نہیں رکھتے ان کے راہ کی پیروی ہرگز نہ کرو۔ " WHERE ayatNumber = 90 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ہم نے بنی اسرائیل کو سمندر سے (پار) گذارا‘ تو فرعون اور اس کی فوجوں نے سرکشی اور ظلم (کی راہ) سے ان کا پیچھا کیا۔ حتیٰ کہ جب غرق ہونے کی آفت نے اسے (اور اس کی فوج کو) آپکڑا تو اس نے کہا کہ میں ایمان لاتا ہوں کہ جس (مقتدر ہستی) پر بنی اسرائیل ایمان لائے ہیں اس کے سوا کوئی بھی معبود نہیں ہے اور میں (سچی) فرمانبرداری اختیار کرنے والوں میں سے (ہوتا) ہوں۔ " WHERE ayatNumber = 91 AND chapterNumber = 10;
UPDATE verses SET ts = "(ہم نے کہا) کیا (تو) اب (ایمان لاتا ہے) حالانکہ پہلے تو نے نافرمانی کی۔ اور تو مفسدوں میں سے تھا۔ " WHERE ayatNumber = 92 AND chapterNumber = 10;
UPDATE verses SET ts = "پس اب ہم تیرے بدن (کے بقا) کے ذریعہ سے تجھے (ایک جزوی) نجات دیتے ہیں تا کہ جو لوگ تیرے پیچھے آنے والے ہیں ان کے لئے تو ایک نشان ہو اور لوگوں میں سے بہت سے افراد ہمارے نشانوں سے بلاشبہ بے خبر ہیں۔ " WHERE ayatNumber = 93 AND chapterNumber = 10;
UPDATE verses SET ts = "اور ہم نے بلاشک و شبہ بنی اسرائیل کو ظاہری اور باطنی (ہرقسم کی) خوبی والی جگہ دی تھی اور (ہر قسم کی) پسندیدہ چیزیں (بھی) انہیں دی تھیں۔ پھر اس وقت تک کہ ان کے  پاس صحیح علم آگیا۔ انہوں نے (کسی امر میں) اختلاف نہ کیا۔ تیرا رب ان کے درمیان اس امر کے بارہ میں جس میں وہ اب اختلاف کر رہے ہیں‘ یقیناً قیامت کے دن فیصلہ کر دے گا۔ " WHERE ayatNumber = 94 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر اگر تو (اے قرآن کے پڑھنے والے) اس (کلام) کی وجہ سے جو ہم نے تیری طرف نازل کیا ہے کسی شک (وشبہ) میں (مبتلا) ہے تو تو ان لوگوں سے جو تجھ سے پہلے اس کتاب کو پڑھ رہے ہیں دریافت کر (اور تجھے معلوم ہوگا کہ) بلاشک و شبہ (ایک) کامل صداقت تیرے رب کی طرف سے تیری طرف آئی ہے پس تو شک کرنے والوں میں سے نہ بن۔ " WHERE ayatNumber = 95 AND chapterNumber = 10;
UPDATE verses SET ts = "اور تو ان (لوگوں) میں سے ہرگز نہ بن جنہوں نے اللہ کے نشانوں کو جھٹلا دیا ہے ورنہ تو نقصان اٹھانے والوں میں سے ہو جائے گا۔ " WHERE ayatNumber = 96 AND chapterNumber = 10;
UPDATE verses SET ts = "جن لوگوں کے متعلق تیرے رب (کی طرف سے ہلاکت) کی خبر آچکی ہے وہ ہرگز ایمان نہیں لائیں گے۔ " WHERE ayatNumber = 97 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اگر ان کے پاس ہر قسم کے نشان بھی آجائیں تب بھی وہ اس وقت تک ایمان نہیں لائیں گے جب تک دردناک عذاب نہ دیکھ لیں۔ " WHERE ayatNumber = 98 AND chapterNumber = 10;
UPDATE verses SET ts = "اور کیوں یونس کی قوم کے سوا کوئی (اور ایسی) بستی نہ ہوئی جو (سب کی سب) ایمان لاتی اور اس کا ایمان لانا اسے نفع دیتا۔ جب وہ (یعنی یونس کی قوم کے لوگ) ایمان لائے تو ہم نے ان (پر) سے اس ورلی زندگی میں (بھی) رسوائی  کا عذاب دور کر دیا اور انہیں ایک وقت تک (ہرطرح کا) سامان عطا کیا۔ " WHERE ayatNumber = 99 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اگر اللہ (ہدایت کے معاملہ میں) اپنی (ہی) مشیت کو نافذ کرتا تو (جس قدر) لوگ زمین پر موجود ہیں وہ سب کے سب ایمان لے آتے (پس جب خدا بھی مجبور نہیں کرتا) تو کیا تو لوگوں کو (اتنا) مجبور کرے گا کہ وہ مومن بن جائیں۔ " WHERE ayatNumber = 100 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اللہ کے دئے ہوئے اذن کے سوا کسی شخص کے اختیار میں نہیں کہ وہ ایمان لے آئے اور وہ اپنا غضب ان لوگوں پر (نازل) کرتا ہے جو عقل رکھتے ہوئے اس سے کام نہیں لیتے۔ " WHERE ayatNumber = 101 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) دیکھو (تو) آسمانوں اور زمین میں کیا (ہورہا) ہے اور کسی قسم کے نشانات (بھی) خواہ امن کے ہوں خواہ ڈرانے والی خبریں ان لوگوں کو فائدہ نہیں دیتیں جو ایمان نہ لانے پرُ مصر ہوں۔ " WHERE ayatNumber = 102 AND chapterNumber = 10;
UPDATE verses SET ts = "پھر کیا جو لوگ ان سے پہلے گذر چکے ہیں وہ ان کے ایام کے مشابہ ایام کے سوا کسی اور چیز کا انتظار کر رہے ہیں؟ تو ان سے کہہ (کہ) اچھا (اگر وہی نمونہ دیکھنا ہے تو) پھر تم (لوگ کچھ) انتظار کرو۔ میں (بھی) یقیناً تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔ " WHERE ayatNumber = 103 AND chapterNumber = 10;
UPDATE verses SET ts = "اور (جب وہ عذاب آجائے گا تو اس وقت) ہم اپنے رسولوں کو اور جو لوگ ان پر ایمان لائے ہیں ان سب کو بچالیں گے۔ اسی طرح ہمارے ذمہ (خود اپنا قائم کیا ہوا) ایک حق ہے کہ ہم مومنوں کو ضرور بچا لیا کرتے ہیں۔ " WHERE ayatNumber = 104 AND chapterNumber = 10;
UPDATE verses SET ts = "تو کہہ کہ اے لوگو! اگر تم میرے دین کے متعلق کسی قسم کے شک (وشبہ) میں ہو تو (سن لو کہ) اللہ کے سوا جن (معبودوں) کی تم پرستش کرتے ہو میں ان کی پرستش نہیں کرتا بلکہ میں اللہ کی پرستش کرتا ہوں جو تم کو وفات دے گا۔ اور مجھے حکم دیا گیا ہے کہ میں ایمان لانے والوں میں سے بنوں۔ " WHERE ayatNumber = 105 AND chapterNumber = 10;
UPDATE verses SET ts = "اور (اس فرمان کے پہنچانے کا بھی حکم دیا گیا ہے) کہ (اے مخاطب) تو ہر کجی سے پاک ہوتے ہوئے اپنی توجہ کو ہمیشہ کے واسطے دین کے لئے (وقف) کر دے اور تو مشرکوں میں سے ہرگز نہ بن۔ " WHERE ayatNumber = 106 AND chapterNumber = 10;
UPDATE verses SET ts = "اور تو اللہ کے سوا (کسی چیز) کو جو تجھے نہ (کوئی) نفع پہنچاتی ہے اور نہ (کوئی) نقصان پہنچاتی ہے نہ پکار۔ اور اگر تو نے (ایسا) کیا تو اس صورت میں تو یقیناً ظالموں میں شمار ہوگا۔ " WHERE ayatNumber = 107 AND chapterNumber = 10;
UPDATE verses SET ts = "اور اگر اللہ تجھے کوئی تکلیف پہنچائے تو اس کے سوا کوئی بھی اسے دور کرنے والا نہیں (ہو سکتا) اور اگر وہ تیرے لئے کوئی بہتری چاہے تو اس کے فضل کو روکنے والا (بھی قطعاً) کوئی نہیں (ہوسکتا) وہ اپنے بندوں میں سے جسے پسند کرتا ہے اسے (اپنا فضل) پہنچا دیتا ہے اور وہ بہت ہی بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 108 AND chapterNumber = 10;
UPDATE verses SET ts = "تو (ان سے) کہہ (کہ) اے لوگو! تمہارے پاس تمہارے رب کی طرف سے حق آ گیا ہے پس (اب) جو کوئی (اس کی بتائی ہوئی) ہدایت کو اختیار کرے تو وہ اپنی جان ہی (کے فائدہ) کے لئے ہدایت کو اختیار کرتا ہے۔ اور جو اس راہ سے بھٹک جائے تو اس کا بھٹکنا (بھی) اس کی جان پر ہی (ایک وبال) ہوگا۔ اور میں تمہارا کوئی ذمہ وار نہیں ہوں۔ " WHERE ayatNumber = 109 AND chapterNumber = 10;
UPDATE verses SET ts = "اور جو کچھ تیری طرف وحی کیا جاتا ہے تو اس کی پیروی کر اور صبر سے کام لے یہاں تک کہ اللہ فیصلہ صادر کر دے اور وہ سب فیصلہ کرنے والوں سے بہتر فیصلہ کرنے والا ہے۔ " WHERE ayatNumber = 110 AND chapterNumber = 10;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 11;
UPDATE verses SET ts = "میں اللہ دیکھنے والا ہوں۔ (یہ) ایسی کتاب ہے کہ اس کی آیات کو محکم کیا گیا ہے اور نیز انہیں کھول کر بیان کیا گیا ہے (اور یہ) حکیم اور خبیر (ہستی) کی طرف سے ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 11;
UPDATE verses SET ts = "(اور) اس تعلیم پر مشتمل ہے کہ تم اللہ کے سوا (کسی) کی عبادت نہ کرو۔ اور یہ کہ تم اپنے رب سے بخشش مانگو (اور) اس کی طرف (سچا) رجوع کرو (تب) وہ تمہیں ایک مقررہ میعاد تک اچھی طرح سے سامان عطا کرے گا۔ اور نیز ہر ایک فضیلت والے (شخص) کو اپنا فضل عطا کرے گا اور اگر تم پھر جائو گے تو میں یقیناً تم پر ایک بڑے (ہولناک) دن کے عذاب (کے آنے) سے ڈرتا ہوں۔ کیونکہ میں خدا کی طرف سے بلاشک و شبہ تمہارے لئے ہوشیار کرنے والا اور اہم خبریں دینے والا (بنا کر بھیجا گیا) ہوں۔" WHERE ayatNumber = 3 AND chapterNumber = 11;
UPDATE verses SET ts = "(اور) اس تعلیم پر مشتمل ہے کہ تم اللہ کے سوا (کسی) کی عبادت نہ کرو۔ اور یہ کہ تم اپنے رب سے بخشش مانگو (اور) اس کی طرف (سچا) رجوع کرو (تب) وہ تمہیں ایک مقررہ میعاد تک اچھی طرح سے سامان عطا کرے گا۔ اور نیز ہر ایک فضیلت والے (شخص) کو اپنا فضل عطا کرے گا اور اگر تم پھر جائو گے تو میں یقیناً تم پر ایک بڑے (ہولناک) دن کے عذاب (کے آنے) سے ڈرتا ہوں۔ کیونکہ میں خدا کی طرف سے بلاشک و شبہ تمہارے لئے ہوشیار کرنے والا اور اہم خبریں دینے والا (بنا کر بھیجا گیا) ہوں۔ " WHERE ayatNumber = 4 AND chapterNumber = 11;
UPDATE verses SET ts = "اللہ کی طرف تم (سب) کو واپس لوٹنا ہے اور وہ ہر چیز پر کامل طور پر قدرت رکھنے والا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 11;
UPDATE verses SET ts = "سنو! وہ یقیناً اپنے سینوں کو اس لئے موڑتے رہتے ہیں کہ اس سے چھپے رہیں۔ سنو! جس وقت وہ اپنے کپڑے اوڑھتے ہیں (تو اس وقت بھی) جو کچھ وہ چھپاتے ہیں اور جو کچھ وہ ظاہر کرتے ہیں اسے وہ جانتا (ہوتا) ہے۔ وہ یقیناً سینوں کی باتوں کو (بھی) خوب جانتا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 11;
UPDATE verses SET ts = "اور زمین میں ایسا کوئی بھی جاندار نہیں ہے جس کا رزق اللہ کے ذمہ نہ ہو۔ وہ اس کی عارضی رہائش کی جگہ کو اور اس کی مستقل رہائش کی جگہ کو جانتا ہے (یہ) سب (کچھ) ایک واضح کر دینے والی کتاب  میں (موجود) ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 11;
UPDATE verses SET ts = "اور وہی ہے جس نے آسمانوں اور زمین کو چھ وقتوں میں پیدا کیا ہے تا کہ وہ تمہارا امتحان کرے (کہ) تم میں سے کس کے عمل زیادہ اچھے ہیں۔ اور اس کا عرش پانی پر ہے۔ اور یہ یقینی امر ہے کہ اگر تو (ان سے) کہے (کہ) تم مرنے کے بعد ضرور اٹھائے جائو گے تو جن لوگوں نے انکار کیا ہے (قسمیں کھا کھا کر) کہیں گے (کہ) یہ (دعویٰ) صرف ایک دھوکا ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 11;
UPDATE verses SET ts = "اور یہ (بھی) قطعی امر ہے کہ اگر ہم  اس عذاب کو ایک اندازہ کی ہوئی مدت تک ان سے پیچھے ہٹائے رکھیں تو وہ ضرور کہیں گے (کہ) کونسی بات اسے روک رہی ہے سنو! جس وقت وہ ان پر آئے گا تو ان سے ہٹایا نہیں جائے گا اور جس عذاب پر وہ ہنسی کرتے تھے وہ انہیں گھیر لے گا۔ " WHERE ayatNumber = 9 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اگر ہم انسان کو اپنی طرف سے (کسی قسم کی) رحمت کا (مزا) چکھائیں (اور) پھر ہم اس سے اسے ہٹالیں تو وہ بہت ہی ناامید (اور) منکر احسان ہو جاتا ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اگر ہم کسی مصیبت کے بعد جو اسے پہنچی ہو اسے کسی (بڑی) نعمت (کا مزہ) چکھائیں تو سوائے ان لوگوں کے جو صبر اختیار کریں اور نیک اعمال کریں‘ وہ کہنے لگتا ہے کہ (اب) میری سب تکلیفیں مجھ سے دور ہو گئی ہیں۔ یقیناً وہ بہت ہی اترانے والا (اور) بہت ہی فخر کرنے والا ہے۔ یہی (صابر لوگ) ہیں جن کے لئے بخشش اور (بہت) بڑا اجر (مقدر) ہے۔" WHERE ayatNumber = 11 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اگر ہم کسی مصیبت کے بعد جو اسے پہنچی ہو اسے کسی (بڑی) نعمت (کا مزہ) چکھائیں تو سوائے ان لوگوں کے جو صبر اختیار کریں اور نیک اعمال کریں‘ وہ کہنے لگتا ہے کہ (اب) میری سب تکلیفیں مجھ سے دور ہو گئی ہیں۔ یقیناً وہ بہت ہی اترانے والا (اور) بہت ہی فخر کرنے والا ہے۔ یہی (صابر لوگ) ہیں جن کے لئے بخشش اور (بہت) بڑا اجر (مقدر) ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 11;
UPDATE verses SET ts = "پس شاید (کفار تجھ سے یہ توقع رکھتے ہیں کہ) تو اس (کلام) کا جو تجھ پر وحی کیا جاتا ہے کچھ حصہ (لوگوں کو پہنچانے کی بجائے) چھوڑ دینے پر تیار ہو جائے (مگر ایسا کبھی نہیں ہو سکتا) اور (وہ یہ بھی توقع کر رہے ہیں کہ) ان کے اس اعتراض کی وجہ سے کہ اس پر کیوں کوئی خزانہ نہیں اترا یا اس پر کوئی فرشتہ نہیں آیا تیرا دل تنگ ہو جائے تو صرف (ہوشیار اور) آگاہ کرنے والا ہے اور اللہ ہر بات کا کارساز ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 11;
UPDATE verses SET ts = "کیا وہ کہتے ہیں (کہ) اس نے اس کتاب کو اپنے پاس سے بنا لیا ہے تو  (انہیں) کہہ اگر تم (اس بیان میں) سچے ہو تو تم بھی اس جیسی دس سورتیں اپنے پاس سے بنا لائو اور اللہ کے سوا جس (کو بھی اپنی مدد کے لئے لانے) کی تمہیں طاقت ہو اسے بلالو۔ " WHERE ayatNumber = 14 AND chapterNumber = 11;
UPDATE verses SET ts = "پس اگر وہ تمہاری (یہ) بات قبول نہ کریں۔ تو جان لو کہ جو (کلام) تم پر اتار گیا ہے اللہ کے (خاص) علم پر مشتمل ہے اور یہ کہ اس کے سوا کوئی (ہستی) بھی پرستش کے لائق نہیں پس کیا تم کامل فرمانبردار بنو گے (یا نہیں)؟ " WHERE ayatNumber = 15 AND chapterNumber = 11;
UPDATE verses SET ts = "جو (لوگ اس) ورلی زندگی (کے سامان) اور اس کی زینت کو (اپنا) مقصود بنائیں گے انہیں ہم ان کے اعمال (کے پھل) اسی (زندگی) میں پورے پورے دے دیں گے اور انہیں اس میں سے کم نہیں دیا جائے گا۔ " WHERE ayatNumber = 16 AND chapterNumber = 11;
UPDATE verses SET ts = "یہی وہ (لوگ) ہیں جن کے لئے آخرت میں (دوزخ) کی آگ کے سوا (اور) کچھ نہیں ہوگا اور جو کچھ انہوں نے اس (ورلی زندگی) کی خاطر کیا ہوگا وہ اس میں بالکل بے سود ہو جائے گا۔ اور جو کچھ وہ کرتے رہے ہوں گے وہ تباہ ہو جائے گا۔ " WHERE ayatNumber = 17 AND chapterNumber = 11;
UPDATE verses SET ts = "پس کیا وہ (شخص) جو اپنے رب کی طرف سے ایک روشن دلیل پر (قائم) ہے اور جس کے پیچھے بھی اس کی طرف سے ایک گواہ آئے گا (جو اس کا فرمانبردار ہوگا) اور اس سے پہلے بھی موسیٰ کی کتاب (آچکی) ہے (جو اس کی تائید کر رہی تھی اور) جو (اس کے کلام سے پہلے) لوگوں کے لئے امام اور رحمت تھی (ایک جھوٹے مدعی جیسا ہو سکتا ہے) وہ (یعنی موسیٰ کے سچے پیرو) اس پر (ایک دن ضرور) ایمان لے آئیں گے۔ اور ان مخالف گروہوں میں سے جو کوئی انکار کرتا رہے گا‘ دوزخ اس کا موعودہ ٹھکانا ہے۔ پس (اے مخاطب) تو اس کے متعلق کسی (قسم کے) شک میں نہ پڑ۔ وہ یقیناً حق ہے (اور) تیرے رب کی طرف سے (ہے) لیکن اکثر  لوگ ایمان نہیں لاتے۔ " WHERE ayatNumber = 18 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اس سے زیادہ کون ظالم (ہوسکتا) ہے جو اللہ پر جھوٹ باندھے ایسے لوگ اپنے رب کے سامنے پیش کئے جائیں گے اور تمام گواہ کہیں گے (کہ) یہ وہ لوگ ہیں جنہوں نے اپنے رب پر جھوٹ باندھا۔ سنو! ان ظالموں پر اللہ کی لعنت ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 11;
UPDATE verses SET ts = "(یہ وہ لوگ ہیں) جو اللہ (کی طرف پہنچنے) کی راہ سے (لوگوں کو) روکتے ہیں اور اس میں کجی پیدا کرنا چاہتے ہیں اور یہی لوگ پیچھے آنے والی (گھڑی) کے (سب سے بڑے) منکر ہیں۔ " WHERE ayatNumber = 20 AND chapterNumber = 11;
UPDATE verses SET ts = "یہ (لوگ) ملک میں (الٰہی سلسلوں کو) کمزور نہیں کر سکتے اور نہ (ہی) اللہ کو چھوڑ کر ان کا کوئی دوست ہوتا ہے۔ ان کو دوہرا عذاب دیا جاتا ہے (دنیا میں بھی اور آخرت میں بھی) نہ وہ (کچھ) سن سکتے ہیں۔ اور نہ (کچھ) دیکھ سکتے ہیں۔ " WHERE ayatNumber = 21 AND chapterNumber = 11;
UPDATE verses SET ts = "یہی (لوگ) ہیں جنہوں نے اپنی جانوں کو گھاٹے میں ڈالا اور جس (مدعا) کے لئے وہ (اللہ پر) جھوٹ باندھتے تھے وہ ان (کے ہاتھ) سے جاتا رہے گا۔ " WHERE ayatNumber = 22 AND chapterNumber = 11;
UPDATE verses SET ts = "یہ قطعی (بات) ہے کہ آخرت میں وہی (سب سے) زیادہ گھاٹا پانے والے ہوں گے۔ " WHERE ayatNumber = 23 AND chapterNumber = 11;
UPDATE verses SET ts = "جو لوگ ایمان لائے اور انہوں نے نیک (اور مناسب حال) عمل کیے‘ اور اپنے رب کی طرف جھک گئے وہ لازما بہشت (میں رہنے) والے ہیں۔ وہ اس میں رہتے چلے جائیں گے۔ " WHERE ayatNumber = 24 AND chapterNumber = 11;
UPDATE verses SET ts = "ان دونوں گروہوں کی حالت ایک اندھے اور آنکھوں والے اور بہرے اور خوب سننے والے کی (حالت کی) طرح ہے۔ کیا ان (دونوں) کی حالت برابر ہو سکتی ہے؟  کیا پھر (بھی) تم نہیںسمجھتے۔ " WHERE ayatNumber = 25 AND chapterNumber = 11;
UPDATE verses SET ts = "اور ہم نے ہی نوح کو اس کی قوم کی طرف (رسول بنا کر) بھیجا تھا (جس پر اس نے انہیںکہا تھا) کہ خوب سن لو کہ میں تمہاری طرف کھول (کھول) کر آگاہ (اور ہوشیار) کرنے والا (بنا کر بھیجا گیا) ہوں۔ " WHERE ayatNumber = 26 AND chapterNumber = 11;
UPDATE verses SET ts = "(اس پیغام کے ساتھ) کہ تم اللہ کے سوا (کسی ہستی) کی پرستش  نہ کرو۔ میں یقیناً تم پر ایک (بڑے) تکلیف (دینے) والے دن کے عذاب (کے آنے) سے ڈرتا ہوں۔ " WHERE ayatNumber = 27 AND chapterNumber = 11;
UPDATE verses SET ts = "اس پر ان بڑے لوگوں نے جنہوں نے اس کی قوم میں سے (اس کا) انکار کیا تھا (اسے) کہا (کہ) ہم تجھے اپنے جیسے ایک آدمی کے سوا (کچھ) نہیں سمجھتے۔ اور نہ ہم (یہ) دیکھتے ہیں کہ سوائے ان لوگوں کے جو سرسری نظر میں ہم سے حقیر ترین (نظر آتے) ہیں کسی نے تیری پیروی (اختیار) کی ہو۔ اور ہم اپنے اوپر تمہاری (کسی قسم کی) کوئی فضیلت نہیں دیکھتے بلکہ ہم یقین رکھتے ہیں کہ تم جھوٹے ہو۔ " WHERE ayatNumber = 28 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا اے میری قوم! (ذرا) بتائو (تو سہی کہ) اگر (ثابت ہو جائے کہ) میں (اپنے دعویٰ کی بنا) اپنے رب کی طرف سے (عطا شدہ) کسی کھلے نشان پر (رکھتا) ہوں اور اس نے اپنے حضور سے مجھے اپنی ایک بہت بڑی رحمت عطا کی ہے اور وہ تم پر مشتبہ رہی ہے (تو تمہارا کیا حال ہوگا؟) کیا ہم اس (روشن نشان) کو تم سے جبرا منوالیں گے خواہ تم اسے ناپسند ہی کرتے ہو۔ " WHERE ayatNumber = 29 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! میں اس کی بابت تم سے کوئی مال نہیں مانگتا۔ میرا اجر اللہ کے سوا (اور) کسی پر نہیں اور میں ان لوگوں کو جو (مجھ پر)ایمان لائے ہیں ہرگز نہیں دھتکاروں گا وہ (تو) اپنے رب سے ملنے (کا شرف پانے) والے ہیں لیکن ان کو ذلیل سمجھنا مجھ پر یہ روشن کر دیتا ہے کہ تم جاہل لوگ ہو۔ " WHERE ayatNumber = 30 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! اگر میں ان کو رد کر دوں تو (اس فعل پر) اللہ کی طرف سے (آنے والی سزا سے مجھے بچانے کے لئے) کون میری مدد کرے گا۔ کیا تم (پھر بھی) نہیں سمجھتے۔ " WHERE ayatNumber = 31 AND chapterNumber = 11;
UPDATE verses SET ts = "اور میں تم سے (یہ) نہیں کہتا (کہ) اللہ کے خزانے میرے پاس ہیں اور نہ یہ کہ میں غیب کا علم رکھتا ہوں اور نہ میں (یہ) کہتا ہوں کہ میں فرشتہ ہوں۔ اور نہ میں ان (لوگوں) کے متعلق جنہیں تمہاری آنکھیں حقارت کی نگاہ سے دیکھتی ہیں (یہ) کہتا ہوں (کہ) اللہ انہیں (کبھی) کوئی بھلائی نصیب نہیں کرے گا جو کچھ ان کے نفسوں میں ہے اسے اللہ (ہی سب سے) بہتر جانتا ہے اگر میں تمہارا ہم زبان ہو جائوں تو میں یقیناً ظالموں میں شامل ہو جائوں گا۔ " WHERE ayatNumber = 32 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اے نوح! تو ہم سے بحث کر چکا ہے‘ بلکہ خوب بحث کر چکا ہے اب یہی رہ گیا ہے کہ اگر تو راستبازوں میں سے ہے تو جس (عذاب) سے تو ہمیں ڈراتا ہے اسے ہم پر لے آ۔ " WHERE ayatNumber = 33 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا۔ اللہ ہی اگر چاہے گا تو اسے لائے گا اور تم (اسے اس کے لانے سے) ہرگز عاجز نہیں کر سکتے۔ " WHERE ayatNumber = 34 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اگر میں ذاتی طور پر تم سے خلوص (کا تعلق) رکھنا چاہوں (بھی) تو میرا (تم سے) خلوص رکھنا تمہیں (اللہ کے عذاب سے بچنے کے لئے کوئی) نفع نہیں پہنچائے گا۔ اگر اللہ (یہ) چاہتا ہو کہ تمہیں ہلاک کرے تو وہ تمہارا رب ہے اور اسی کی طرف تم لوٹائے جائو گے۔ " WHERE ayatNumber = 35 AND chapterNumber = 11;
UPDATE verses SET ts = "کیا وہ کہتے ہیں (کہ) اس نے اس (عذاب کے وعدہ) کو اپنے پاس سے گھڑ لیا ہے تو انہیں کہہ اگر میں نے اسے اپنے پاس سے گھڑ لیا ہے تو میرا یہ خطرناک جرم (ضرور) مجھ پر (ہی وبال بن کر) پڑے گا اور (تمہارے جرموں کا وبال مجھ پر نہیں ہوگا کیونکہ) جو خطرناک جرم تم کرتے ہو ان سے میں بیزار ہوں۔ " WHERE ayatNumber = 36 AND chapterNumber = 11;
UPDATE verses SET ts = "اور نوح کی طرف (یہ بھی) وحی کی گئی تھی کہ جو (لوگ) ایمان لا چکے ہیں ان کے سوا تیری قوم میں سے (اب) کوئی (اور شخص تجھ پر) ہرگز ایمان نہیں لائے گا۔ اس لئے جو (کچھ) وہ کر رہے ہیں اس کی وجہ سے تو افسوس نہ کر۔ " WHERE ayatNumber = 37 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تو ہماری آنکھوں کے (سامنے) اور ہماری وحی کے مطابق کشتی بنا اور جن لوگوں نے ظلم (کا شیوہ اختیار) کیا ہے ان کے متعلق مجھ سے (کوئی) بات نہ کر وہ ضرور (ہی) غرق کیے جائیں گے۔ " WHERE ayatNumber = 38 AND chapterNumber = 11;
UPDATE verses SET ts = "اور وہ (یعنی نوح) ہمارے حکم کے مطابق کشتی بناتا چلا  جاتا تھا اور جب بھی اس کی قوم میں سے کوئی بڑے لوگوں کی جماعت اس کے پاس سے گذرتی تھی تو وہ اس پر ہنستی تھی (جس پر) اس نے (ان سے) کہا (کہ) اگر (آج) تم (لوگ) ہم پر ہنستے ہو تو (کل) ہم (بھی) تم پر ہنسیں گے جیسا کہ (آج) تم (ہم پر) ہنستے ہو۔ " WHERE ayatNumber = 39 AND chapterNumber = 11;
UPDATE verses SET ts = "پھر جلد تمہیں معلوم ہو جائے گا (کہ) وہ کون (سا فریق) ہے جس پر ایسا عذاب آرہا ہے جو اسے رسوا کر دے گا اور جس پر مستقل عذاب نازل ہو رہا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 11;
UPDATE verses SET ts = "یہاں تک کہ جب ہمارا (عذاب کا) حکم آجائے اور چشمے پھوٹ کر بہ پڑیں (تو) ہم کہیں گے کہ ہر ایک (قسم کے جانوروں) میں سے ایک جوڑا یعنی دو (ہم جنس فردوں) کو اور اپنے اہل (وعیال) کو (بھی) سوائے اس (فرد) کے جس (کی ہلاکت) کے متعلق (اس عذاب کے آنے سے) پہلے (ہی ہمارا  قطعی) فرمان جاری ہو چکا ہے اور نیز ان کو جو تجھ پر ایمان لائے ہیں اس میں سوار کرادے اور اس پر سوائے قلیل تعداد کے کوئی ایمان نہ لایا تھا۔ " WHERE ayatNumber = 41 AND chapterNumber = 11;
UPDATE verses SET ts = "چنانچہ (جب طوفان آگیا تو) اس نے (اپنے ساتھیوں کو) کہا (کہ) اس میں سوار ہو جائو اس کا چلنا اور اس کا ٹھہرایا جانا اللہ کے نام کی برکت سے ہی ہوگا۔ میرا رب یقیناً بہت ہی بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 11;
UPDATE verses SET ts = "اور وہ پہاڑوں کی طرح کی (اونچی) موجوں میں انہیں لئے جا رہی تھی اور (اسی اثنا میں) نوح نے اپنے بیٹے کو جبکہ وہ (اس سے علیحدہ) ایک اور جانب کی طرف چلا گیا تھا پکارا (کہ) اے میرے بیٹے! ہمارے ساتھ سوار ہو جائو اور کافروں کے ساتھ نہ ہو۔ " WHERE ayatNumber = 43 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا کہ میں ابھی کسی پہاڑ پر جا ٹھہروں گا (اور پناہ لوں گا) جو اس پانی سے مجھے بچالے گا۔ اس نے کہا (کہ) اللہ کے (اس عذاب کے) حکم سے آج کوئی بھی (کسی کو) بچانے والا نہیں (ہوسکتا) سوائے اس کے کہ جس پر وہ (آپ) رحم کردے۔ اور (اسی اثنا میں) پانی کی لہر ان دونوں کے درمیان حائل ہو گئی اور وہ غرق کیے جانے والوںمیں شامل ہو گیا۔ " WHERE ayatNumber = 44 AND chapterNumber = 11;
UPDATE verses SET ts = "اس کے بعد (زمین سے بھی) کہہ دیا گیا (کہ) اے زمین تو (اب) اپنے پانی کو نگل جا اور (آسمان سے بھی کہ) اے آسمان (اب) تو (برسنے سے) تھم جا۔ اور پانی کو جذب کر دیا گیا اور (یہ) معاملہ ختم کر دیا گیا۔ اور وہ کشتی جودی پر (جاکر) ٹھہر گئی اور کہہ دیا گیا کہ (اے عذاب کے فرشتو)! ظالم لوگوں کے لئے ہلاکت مقدر کردو۔ " WHERE ayatNumber = 45 AND chapterNumber = 11;
UPDATE verses SET ts = "اور نوح نے اپنے رب کو پکارا اور کہا اے میرے رب! میرا بیٹا یقیناً میرے اہل میں سے ہے اور تیرا وعدہ (بھی) نہایت سچا ہے اور تو سب فیصلہ کرنے والوں سے بڑھ کر (بہتر اور درست) فیصلہ کرنے والا ہے۔ " WHERE ayatNumber = 46 AND chapterNumber = 11;
UPDATE verses SET ts = "فرمایا اے نوح! وہ تیرے اہل میں سے ہرگز نہیں کیونکہ وہ یقیناً برے عمل کرنے والا ہے پس تو مجھ سے ایسی دعا نہ کر جس کے بارہ میں تجھے (میری طرف سے) علم نہ دیا گیا ہو اور میں تجھے نصیحت کرتا ہوں کہ جاہلوں کی طرح کبھی کام نہ کیجیئو۔ " WHERE ayatNumber = 47 AND chapterNumber = 11;
UPDATE verses SET ts = "(نوح نے) کہا اے میرے رب! میں اس بارہ میں تیری پناہ چاہتا ہوں کہ تجھ سے کوئی ایسا سوال کروں جس کے متعلق مجھے حقیقی علم حاصل نہ ہو اور اگر تو میری گزشتہ غفلت کو معاف نہ کرے اور رحم نہ کرے تو میں نقصان اٹھانے والوں میں سے ہو جائوں گا۔ " WHERE ayatNumber = 48 AND chapterNumber = 11;
UPDATE verses SET ts = "(اس پر اسے) کہا گیا (کہ) اے نوح! تو ہماری طرف سے (عطا شدہ) سلامتی اور طرح طرح کی برکات کے ساتھ جو تجھ پر اور ان امتوں پر جو تیرے ساتھ ہیں (نازل کی گئی ہیں) سفر کر۔ اوربعض جماعتیں ایسی بھی ہیں جنہیں ہم ضرور (دنیا کا عارضی) سامان عطا کریں گے (مگر) پھر ان پر ہماری طرف سے دردناک عذاب آئے گا۔ " WHERE ayatNumber = 49 AND chapterNumber = 11;
UPDATE verses SET ts = "یہ (انذاری بیان) غیب کی اہم خبروں میں سے ہے جنہیں ہم تجھ پر وحی (کے ذریعے سے نازل) کرتے ہیں۔ نہ تو ان کو اس سے پہلے جانتا تھا اور نہ تیری قوم (جانتی تھی) پس تو صبر سے کام لے (اچھا) انجام یقیناً تقویٰ اختیار کرنے والوں کا (ہی) ہوتا ہے۔ " WHERE ayatNumber = 50 AND chapterNumber = 11;
UPDATE verses SET ts = "اور عاد کی طرف (ہم نے) ان کے بھائی ہود کو (رسول بنا کر بھیجا تھا اور) اس نے انہیں کہا تھا کہ اے میری قوم! تم اللہ کی عبادت کرو۔ اس کے سوا تمہارا کوئی بھی معبود نہیں ہے (اس کے شریک مقرر کرنے میں) تم محض افتراء کرنے والے ہو۔ " WHERE ayatNumber = 51 AND chapterNumber = 11;
UPDATE verses SET ts = "اے میری قوم! میں اس (کام) کا تم سے کوئی اجر نہیں مانگتا۔ میرا اجر اس ہستی کے ذمہ ہے جس نے مجھے پیدا کیا ہے۔ کیا پھر (بھی) تم عقل سے کام نہیں لیتے۔ " WHERE ayatNumber = 52 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! تم اپنے رب سے بخشش مانگو۔ پھر اس کی طرف (کامل) رجوع اختیار کرو۔ جس کے نتیجہ میں وہ تم پر خوب برسنے والا بادل بھیجے گا اور تم کو طاقت کے بعد طاقت بخشے گا اور مجرم بن کر خدا سے منہ نہ پھیرو۔ " WHERE ayatNumber = 53 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا‘ اے ہود! تو ہمارے پاس (اپنے دعویٰ کا) کوئی روشن ثبوت نہیں لایا اور ہم (محض) تیرے کہہ دینے سے اپنے معبودوں کو چھوڑ نہیں سکتے اور نہ ہی ہم تجھ پر ایمان لائیں گے۔ " WHERE ayatNumber = 54 AND chapterNumber = 11;
UPDATE verses SET ts = "(تیرے متعلق) ہم سوائے اس کے (کچھ) نہیں کہتے کہ ہمارا کوئی معبود بد ارادوں سے تیرے پیچھے پڑ گیا ہے۔ اس نے کہا‘ میں اللہ کو (اس بات کا) گواہ ٹھہراتا ہوں اور تم بھی گواہ رہو۔ " WHERE ayatNumber = 55 AND chapterNumber = 11;
UPDATE verses SET ts = "کہ جس کسی کو تم اللہ کا شریک قرار دیتے ہو میں اس سے بیزار ہوں (سو اگر میں غلطی پرہوں) تو سب مل کر میرے خلاف منصوبہ کرو۔ اور مجھے کوئی ڈھیل نہ دو۔ " WHERE ayatNumber = 56 AND chapterNumber = 11;
UPDATE verses SET ts = "میں اللہ پر جو میرا (بھی) رب (ہے) اور تمہارا (بھی) رب ہے بھروسہ کرتا ہوں (اور کہتا ہوں کہ روئے زمین پر) کوئی بھی چلنے والا (جاندار) ایسا نہیں کہ وہ (خدا) اس کی پیشانی کو پکڑے ہوئے نہ ہو۔ میرا رب یقیناً مومنوں کی مدد کرنے کے لئے سیدھی راہ پر کھڑا ہے (اور اپنی طرف آنے والوں کی حفاظت کر رہا ہے) " WHERE ayatNumber = 57 AND chapterNumber = 11;
UPDATE verses SET ts = "پس اگر تم میری طرف سے پیٹھ پھیر لو تو (اس میں میرا کوئی نقصان نہیں کیونکہ) جو (تعلیم) دے کر مجھے تمہاری طرف بھیجا گیا ہے وہ میں نے تمہیں پہنچا دی (ہوئی) ہے اور (اگر تم پیٹھ پھیر لو گے تو) میرا رب تمہارے سوا کسی اور قوم کو (پہلوں کا) جانشین بنا دے گا اور تم اس کو کچھ (بھی) نقصان نہیں پہنچا سکو گے۔ میرا رب یقیناً ہر چیز کا محافظ ہے۔ " WHERE ayatNumber = 58 AND chapterNumber = 11;
UPDATE verses SET ts = "اور جب ہمارا (عذاب کے متعلق) حکم آگیا تو (اس وقت) ہم نے ہود کو (بھی) اور جو لوگ اس کے ساتھ ایمان لائے تھے ان کو (بھی) (اس عذاب سے) اپنی (خاص) رحمت کے ذریعہ سے نجات دی اور ہم نے انہیں ایک سخت عذاب سے بچا لیا۔ " WHERE ayatNumber = 59 AND chapterNumber = 11;
UPDATE verses SET ts = "اور یہ (مغرور) عاد (ہی تھے) جنہوں نے (دیدہ و دانستہ) اپنے رب کے نشانوں کا انکار کر دیا تھا۔ اور اسکے رسولوں کی نافرمانی کی تھی۔ اور ہر ایک سرکش (اور) حق کے دشمن (شخص) کے حکم کی پیروی کرنے لگ پڑے تھے۔ " WHERE ayatNumber = 60 AND chapterNumber = 11;
UPDATE verses SET ts = "اس دنیا میں (بھی) لعنت ان کے پیچھے لگا دی گئی ہے اور قیامت کے دن (بھی لگا دی جائے گی) سنو! عاد نے یقیناً اپنے رب (کے احسانوں) کی ناشکری کی تھی۔ سنو! (ہم عذاب کے فرشتوں سے کہتے ہیں) عاد یعنی قوم ہود کے لئے لعنت مقدر کردو۔ " WHERE ayatNumber = 61 AND chapterNumber = 11;
UPDATE verses SET ts = "اور ثمود کی طرف (ہم نے) ان کے بھائی صالح کو بھیجا تھا۔ اس نے (انہیں) کہا‘ اے میری قوم! تم اللہ کی عبادت کرو۔ اس کے سوا تمہارا کوئی بھی معبود نہیں۔اسی نے تمہیں زمین سے اٹھایا (اور بلندی بخشی) اوراس میں تمہیں آباد کیا۔ اس لئے تم اس سے بخشش طلب کرو۔ اور اس کی طرف کامل رجوع اختیار کرو۔ میرا رب یقیناً قریب ہے (اور دعائیں) قبول کرنے والا ہے۔ " WHERE ayatNumber = 62 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا۔ اے صالح! اس سے پہلے (تو) تو ہمارے درمیان (آئندہ کے لئے) امید کی جگہ (سمجھا جاتا) تھا (اب) کیا تو (باوجود اس عقل و دانش کے) ہمیں اس بات سے روکتا ہے کہ ہم ایسی چیز کی عبادت کریں جس کی عبادت ہمارے باپ (دادے) کرتے آئے ہیں۔ اور (سچ تو یہ ہے کہ) جس بات کی طرف تو ہمیں بلاتا ہے اس کے متعلق ہم ایک بے چین کر دینے والے شک میں (پڑے ہوئے) ہیں۔ " WHERE ayatNumber = 63 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا اے میری قوم! بتائو اگر میں (فی الواقعہ اپنے دعویٰ کی بنا) اپنے رب کی طرف سے (عطا شدہ) کسی روشن ثبوت پر (رکھتا) ہوں۔ اور اس نے مجھے اپنی جناب سے ایک (خاص) رحمت عطا کی ہے تو (باوجود اس کے) اگر میں اس کی نافرمانی کروں تو اللہ کے مقابل پر کون میری مدد کرے گا۔ پھر (اس وقت تو) تم مجھے سوائے تباہی میں ڈالنے کے (اور) کسی بات میں نہیں بڑھائو گے۔ " WHERE ayatNumber = 64 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! یہ اونٹنی (وہ ہے) جسے اللہ نے تمہارے لئے ایک نشان بنایا ہے اس لئے تم اسے آزاد پھرنے دو‘ تا کہ یہ اللہ کی زمین میں (چل پھر کر) کھائے (پیئے) اور اسے کوئی تکلیف نہ پہنچائو ورنہ تمہیں ایک جلد آنے والا عذاب پکڑ لے گا۔ " WHERE ayatNumber = 65 AND chapterNumber = 11;
UPDATE verses SET ts = "اس پر انہوں نے (تلوار سے) اس کی ٹانکیں کاٹ دیں‘ جس پر اس نے (ان سے) کہا تم تین روز (تک) اپنے گھروں میں (اپنے حاصل شدہ سامانوں سے) فائدہ اٹھالو۔ یہ (وعدہ) ایسا وعدہ ہے جو جھوٹا نہیں ہوگا۔ " WHERE ayatNumber = 66 AND chapterNumber = 11;
UPDATE verses SET ts = "پھر جب ہمارا حکم آ گیا (یعنی عذاب کا) تو ہم نے صالح کو اور اس کے ساتھ جو اس پر ایمان لائے تھے انہیں اپنی خاص رحمت کے ذریعہ ہر بلا سے اور خصوصاً اس دن کی رسوئی سے بچا لیا۔ یقیناً تیرا رب ہی (تمام تر) قوت والا (اور) غلبہ والا ہے۔ " WHERE ayatNumber = 67 AND chapterNumber = 11;
UPDATE verses SET ts = "اور جنہوں نے ظلم (کا ارتکاب) کیا تھا انہیں اس عذاب نے پکڑ لیا اور وہ اپنے اپنے گھروں میں (عذاب کی وجہ سے) زمین سے چمٹے ہوئے ہو گئے۔ " WHERE ayatNumber = 68 AND chapterNumber = 11;
UPDATE verses SET ts = "گویا وہ اس (ملک) میں کبھی بسے ہی نہ تھے۔ سنو! ثمود نے اپنے رب (کے احسانوں) کی ناشکری کی تھی۔ سنو! (عذاب کے فرشتوں کو حکم دیا گیا کہ) ثمود کے لئے لعنت مقدر کر دو۔ " WHERE ayatNumber = 69 AND chapterNumber = 11;
UPDATE verses SET ts = "اور ہمارے فرستادے یقیناً ابراہیم کے پاس خوشخبری لائے تھے (اور) کہا تھا کہ (ہماری طرف سے آپ کو) سلام ہو۔ اس نے کہا (تمہارے لئے بھی ہمیشہ کی) سلامتی ہو۔ پھر (وہ) ایک بھنا ہوا بچھڑا جلدی سے لے آیا۔ " WHERE ayatNumber = 70 AND chapterNumber = 11;
UPDATE verses SET ts = "پس جب اس نے ان کے ہاتھوں کو دیکھا کہ اس (کھانے) تک نہیں پہنچتے تو (اس نے) ان کے (اس) فعل کو غیر معمولی سمجھا اور اس (فعل) سے خطرہ محسوس کیا۔ (اس پر) انہوں نے کہا (کہ) تو خوف نہ کر۔ ہمیں تو لوط کی قوم کی طرف بھیجا گیا ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اس کی بیوی (بھی پاس ہی) کھڑی تھی۔ اس پر وہ بھی گھبرائی تب ہم نے اس کی تسلی کے لئے اس کو اسحاق کی اور اسحاق کے بعد یعقوب (کی پیدائش) کی بشارت دی۔ " WHERE ayatNumber = 72 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا‘ ہائے میری ذلت! کیا میں (بچہ) جنوں گی حالانکہ میں بوڑھی (ہوچکی) ہوں اور میرا خاوند بھی بڑھاپے کی حالت میں ہے۔ یہ یقیناً عجیب بات ہے۔ " WHERE ayatNumber = 73 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا کیا تو اللہ کی بات پر تعجب کرتی ہے‘ اے اس گھر والو تم پر (تو) اللہ کی رحمت اور اس کی (ہرقسم کی) برکات (نازل ہو رہی) ہیں (پس تمہارے لئے تو یہ بات عجیب نہیں ہونی چاہیے) وہ یقیناً بہت ہی تعریف والا (اور) بزرگ شان والا ہے۔ " WHERE ayatNumber = 74 AND chapterNumber = 11;
UPDATE verses SET ts = "پس جب ابراہیم سے گھبراہٹ دور ہو گئی اور اسے خوشخبری ملی تو (اس وقت) وہ لوط کی قوم کے متعلق ہمارے ساتھ جھگڑنے لگا۔ " WHERE ayatNumber = 75 AND chapterNumber = 11;
UPDATE verses SET ts = "ابراہیم بہت ہی بردبار (اور) دردمند (دل رکھنے والا) (اور ہمارے حضور) باربار جھکنے والا تھا۔ " WHERE ayatNumber = 76 AND chapterNumber = 11;
UPDATE verses SET ts = "(اس پر ہم نے اسے کہا) اے ابراہیم! تو اس (سفارش) سے رک جا۔ کیونکہ تیرے رب کا (آخری) حکم آچکا ہے اور ان کی کفار کی ایسی حالت ہے کہ ان پر نہ ٹلنے والا عذاب آکر رہے گا۔ " WHERE ayatNumber = 77 AND chapterNumber = 11;
UPDATE verses SET ts = "اور جب ہمارے فرستادے لوط کے پاس آئے تو ان کی وجہ سے اسے غم ہوا اور اس نے دل میں تنگی محسوس کی اور کہا آج کا دن (بہت) سخت معلوم ہوتا ہے۔ " WHERE ayatNumber = 78 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اس کی قوم (غصہ سے) اس کی طرف بھاگتی ہوئی آئی اور (یہ پہلا موقعہ نہ تھا) پہلے (بھی) وہ (لوگ نہایت خطرناک) بدیاں کرتے تھے۔ اس نے کہا اے میری قوم یہ میری بیٹیاں (جو تمہارے ہی گھروں میں بیاہی ہوئی) ہیں۔ وہ تمہارے لئے (اور تمہاریآبرو کے بچانے کے لئے) نہایت پاک (دل اور پاک خیال) ہیں پس تم اللہ کا تقویٰ اختیار کرو اور میرے مہمانوں (کی موجودگی) میں مجھے رسوا نہ کرو۔ کیا تم میں سے کوئی (بھی) سمجھدار (آدمی) نہیں ہے؟ " WHERE ayatNumber = 79 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا تو یقیناً معلوم کر چکا ہے کہ تیری لڑکیوں کے متعلق ہمیں کوئی بھی حق (حاصل) نہیں ہے اور جو (کچھ) ہم چاہتے ہیں اسے تو جانتا ہے۔ " WHERE ayatNumber = 80 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا کاش مجھے تمہارے مقابلہ میں (کسی قسم کی) قوت (حاصل) ہوتی (تومیں تمہارا مقابلہ کرتا) لیکن اگر یہ نہیں تو یہی صورت باقی ہے کہ میں ایک زبردست جائے پناہ کی طرف جھکوں۔ " WHERE ayatNumber = 81 AND chapterNumber = 11;
UPDATE verses SET ts = "(اس پر) انہوں نے کہا (یعنی مہمانوں نے) کہ اے لوط! ہم یقیناً تیرے رب کے فرستادہ ہیں اور ہمیں معلوم ہے کہ وہ تجھ تک ہرگز نہیں پہنچیں گے (ان کی تباہی کا وقت آچکا ہے) اس لئے تو رات کے کسی حصے میں اپنے گھر والوں کو لیکر تیزی سے (یہاں سے) چلا جا اور تم میں سے کوئی (فرد بھی) ادھر ادھر نہ دیکھے (اس طرح سے تم محفوظ رہو گے) ہاں تیری بیوی ایسی ہے کہ جو (عذاب) ان پر آیا (ہوا ہے) وہ اس پر بھی یقیناً آنے والا ہے۔ ان کا مقررہ وقت (آئندہ) صبح ہے (اور) کیا صبح قریب نہیں ہے؟ " WHERE ayatNumber = 82 AND chapterNumber = 11;
UPDATE verses SET ts = "پھر جب ہمارا حکم آگیا تو ہم نے اس (بستی) کے اوپر والے حصہ کو نیچے والا (حصہ) بنا دیا اور اس پر سوکھی مٹی کے بنے ہوئے پتھروں کی یکے بعد دیگرے بارش برسائی۔ " WHERE ayatNumber = 83 AND chapterNumber = 11;
UPDATE verses SET ts = "جو تیرے رب کی تقدیر میں (ان کے لئے ہی) مقدر (اور نامزد) کیے ہوئے تھے۔ اور ان ظالموں سے (بھی) یہ عذاب دور نہیں۔ " WHERE ayatNumber = 84 AND chapterNumber = 11;
UPDATE verses SET ts = "اور مدین کی طرف (ہم نے) ان کے بھائی شعیب کو (نبی بنا کر بھیجا) اس نے (انہیں) کہا اے میری قوم! تم اللہ کی عبادت کرو۔ اس کے سوا تمہارا کوئی معبود نہیں اور ماپ اور تول کو کم نہ کیا کرو۔ میں (اس وقت) یقیناً تمہیں اچھی حالت میں دیکھتا ہوں اور (ساتھ ہی) میںتمہاری نسبت ایک تباہ کن دن کے عذاب سے ڈر رہا ہوں۔ " WHERE ayatNumber = 85 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! تم ماپ اور تول کو انصاف کے ساتھ پورا کیا کرو۔ اور لوگوں کو ان کی چیزیں کم نہ دیا کرو۔ اور فسادی بن کر زمین میں خرابی مت پھیلائو۔ " WHERE ayatNumber = 86 AND chapterNumber = 11;
UPDATE verses SET ts = "اگر تم (سچے) مومن ہو تو (یقین جانو کہ) اللہ کا (تمہارے پاس) باقی چھوڑا ہوا (مال ہی) تمہارے لئے بہتر (اور مبارک) ہے اور میں تم پر کوئی محافظ (بناکر) نہیں (بھیجا گیا۔ صرف ایک ناصح ہوں) " WHERE ayatNumber = 87 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا‘ اے شعیب! کیا تیری نماز تجھے حکم دیتی ہے کہ جس چیز کی ہمارے باپ دادا پرستش کرتے (آئے) ہیں اسے ہم چھوڑ دیں یا اس بات کو (ترک کر دیں) کہ اپنے مالوں کے متعلق ہم جو چاہیں کریں (اگر ایسا ہے) تو تو یقیناً بڑا ہی عقلمند (اور) سمجھ دار (آدمی) ہے۔ " WHERE ayatNumber = 88 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا‘ اے میری قوم! (بھلا) بتائو (تو سہی) اگر (ثابت ہوا کہ) میں (اپنے دعویٰ کی بنا) اپنے رب کی طرف سے (عطا شدہ) کسی روشن دلیل پر (رکھتا) ہوں اور اس نے اپنے حضور سے مجھے اچھا (اور پسندیدہ) رزق دیا ہے (تو کل خدا کے حضور کیا جواب دو گے؟) اور میں نہیں چاہتا کہ جس بات سے تمہیں روکوں (اس سے تم رک جائو اور خود میں) تمہارے خلاف اسی (بات) کا قصد کروں میں تو سوائے اس (حد تک) اصلاح کے جس کی مجھے طاقت ہے کچھ نہیں چاہتا اور میرا توفیق پانا اللہ ہی (کے فضل اور رحم) سے (وابستہ) ہے اسی پر میرا بھروسہ ہے اور اسی کی طرف میں باربار جھکتا ہوں۔ " WHERE ayatNumber = 89 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! (دیکھنا کہیں تمہاری) مجھ سے دشمنی تمہیں (اس بات پر) نہ اکسادے کہ تم ویسی (ہی) مصیبت سہیڑ لو جیسی کہ نوح کی قوم یا ہود کی قوم یا صالح کی قوم پر مصیبت آئی تھی۔ اور لوط کی قوم (تو) تم سے کچھ (ایسی) دور (بھی) نہیں ہے۔ " WHERE ayatNumber = 90 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تم اپنے رب سے بخشش طلب کرو (اور) پھر اس کی طرف کامل رجوع اختیار کرو میرا رب یقیناً باربار رحم کرنے والا (اور) بہت ہی محبت کرنے والا ہے۔ " WHERE ayatNumber = 91 AND chapterNumber = 11;
UPDATE verses SET ts = "انہوں نے کہا‘ اے شعیب! جو کچھ تو کہتا ہے اس میں سے بہت سا (حصہ) ہماری سمجھ میں نہیں آتا۔ اور ہم تجھے اپنے درمیان ایک کمزور آدمی سمجھتے ہیں اور اگر تیرا گروہ نہ ہوتا تو ہم تجھے سنگسار کر دیتے اور تو (بذات خود) ہماری نظر میں کوئی قابل عزت وجود نہیں۔ " WHERE ayatNumber = 92 AND chapterNumber = 11;
UPDATE verses SET ts = "اس نے کہا اے میری قوم! کیا میرا گروہ اللہ کی نسبت تمہاری نظر میں زیادہ قابل عزت ہے اور اسے تم نے اپنی پیٹھ کے پیچھے ڈالا ہوا ہے جو کچھ تم کرتے ہو اسے میرا رب خوب جانتا ہے۔ " WHERE ayatNumber = 93 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اے میری قوم! تم اپنی جگہ پر (اپنے) کام کیے جائو‘ میں (بھی اپنی جگہ پر) اپنا کام کر رہا ہوں عنقریب تمہیں معلوم ہو جائے گا کہ وہ کون ہے جس پر رسوا کر دینے والا عذاب آتا ہے اور کون جھوٹا ہے (اور کون سچا) اور تم (بھی اپنے اور میرے انجام کا) انتظار کرو۔ میں بھی یقیناً تمہارے ساتھ (نتیجہ) کا منتظر رہوں گا۔ " WHERE ayatNumber = 94 AND chapterNumber = 11;
UPDATE verses SET ts = "اور جب ہمارا حکم (عذاب کے متعلق) آ گیا‘ تو ہم نے شعیب کو اور ان (لوگوں) کو جو اس کے ساتھ ایمان لائے تھے اپنی (خاص) رحمت سے (اس عذاب سے) بچالیا اور جنہوں نے ظلم (کا شیوہ اختیار) کیا تھا انہیں اس عذاب نے پکڑ لیا اور وہ اپنے (اپنے) گھروں میں زمین سے چمٹے ہوئے تباہ ہو گئے۔ " WHERE ayatNumber = 95 AND chapterNumber = 11;
UPDATE verses SET ts = "گویا وہ ان میں (کبھی) رہے (ہی) نہ تھے۔ سنو! مدین کے لئے بھی (خدا نے) لعنت مقدر کی تھی جیسا کہ ثمود کے لئے (خدا نے) لعنت مقدر کی تھی۔ " WHERE ayatNumber = 96 AND chapterNumber = 11;
UPDATE verses SET ts = "اور یقیناً ہم نے موسیٰ کو ہر قسم کے نشان اور روشن دلیل دے کر فرعون اور اس کی قوم کے بڑے لوگوں کی طرف بھیجا تھا۔ لیکن انہوں نے (موسیٰ کو چھوڑ کر) فرعون کے حکم کی پیروی کی اور فرعون کا حکم ہرگز درست نہ تھا۔" WHERE ayatNumber = 97 AND chapterNumber = 11;
UPDATE verses SET ts = "اور یقیناً ہم نے موسیٰ کو ہر قسم کے نشان اور روشن دلیل دے کر فرعون اور اس کی قوم کے بڑے لوگوں کی طرف بھیجا تھا۔ لیکن انہوں نے (موسیٰ کو چھوڑ کر) فرعون کے حکم کی پیروی کی اور فرعون کا حکم ہرگز درست نہ تھا۔ " WHERE ayatNumber = 98 AND chapterNumber = 11;
UPDATE verses SET ts = "وہ قیامت کے دن اپنی قوم کے آگے (آگے) چلے گا اور ان کو (دوزخ) کی آگ میں (جا) اتارے گا۔ پھر وہ گھاٹ بھی اور اس میں اترنے والے بھی برے ہوں گے۔ " WHERE ayatNumber = 99 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اس دنیا میں (بھی) ان کے پیچھے لعنت لگا دی گئی اور قیامت کے دن بھی (لگا دی جائے گی) یہ انعام جو انہیں دیا جانے والا ہے بہت ہی برا ہے۔ " WHERE ayatNumber = 100 AND chapterNumber = 11;
UPDATE verses SET ts = "یہ (تباہ شدہ) بستیوں کی خبروں میں سے ایک حصہ ہے ہم اسے تیرے سامنے بیان کرتے ہیں ان میں سے بعض (بستیاں ابھی تک موجود) کھڑی ہیں (یعنی ان کے نشان موجود ہیں) اور بعض تباہ شدہ حالت میں ہیں (یعنی ان کے نشان تک مٹ گئے ہیں) ۔" WHERE ayatNumber = 101 AND chapterNumber = 11;
UPDATE verses SET ts = "اور ہم نے ان پر کوئی ظلم نہیں کیا (تھا) بلکہ انہوں نے (خود ہی) اپنی جانوں پر ظلم کیا تھا پھر جب تیرے رب (کے عذاب) کا حکم آ گیا تو ان کے معبودوں نے جنہیں وہ اللہ کے سوا پکارا کرتے تھے انہیں کچھ بھی فائدہ نہ دیا اور سوائے تباہی میں ڈالنے کے انہوں نے (کسی بات میں) انہیں نہ بڑھایا۔ " WHERE ayatNumber = 102 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تیرے رب کی گرفت جب وہ بستیوں کو اس حالت میں کہ وہ ظلم پر ظلم کر رہی ہوں پکڑتا ہے‘ اسی طرح (یعنی اتمام حجت کے بعد) ہوا کرتی ہے۔ اس کی گرفت بڑی ہی دردناک (اور) سخت ہوتی ہے۔ " WHERE ayatNumber = 103 AND chapterNumber = 11;
UPDATE verses SET ts = "جو آخرت کے عذاب سے ڈرتا ہو‘ اس کے لئے (خدا کی) اس (گرفت) میں یقیناً ایک (عبرت انگیز) نشان (پایا جاتا) ہے۔ یہ ایک ایسا دن (آنے والا) ہے جس کے لئے لوگوں کو جمع کیا جائے گا اور یہ ایسا دن ہوگا جسے سب لوگ دیکھیں گے۔ " WHERE ayatNumber = 104 AND chapterNumber = 11;
UPDATE verses SET ts = "اور ہم اسے صرف ایک مقررہ میعاد تک پیچھے ڈال رہے ہیں۔ " WHERE ayatNumber = 105 AND chapterNumber = 11;
UPDATE verses SET ts = "جس وقت وہ آئے گا کوئی شخص اس کے (یعنی خدائے برتر کے) اذن کے سوا کلام نہیں کر سکے گا۔ پھر ان میں سے (بعض تو) بدبخت (ثابت) ہوں گے اور (بعض) خوش نصیب ہوں گے۔ " WHERE ayatNumber = 106 AND chapterNumber = 11;
UPDATE verses SET ts = "پس جو بدبخت (ثابت) ہوں گے وہ آگ میں (داخل) ہوں گے۔ اس میں (کسی وقت تو)ان کے (درد سے) لمبے سانس (نکل رہے) ہوں گے اور (کسی وقت) ہچکی کی حالت کے مشابہ سانس (نکل رہے ہوں گے) " WHERE ayatNumber = 107 AND chapterNumber = 11;
UPDATE verses SET ts = "وہ اس میں اس وقت تک رہتے چلے جائیں گے جب تک کہ آسمان اور زمین قائم ہیں سوائے اس (عرصہ) کے جو تیرا رب چاہے‘ تیرا رب جو چاہتا ہے اسے کرکے رہتا ہے۔ " WHERE ayatNumber = 108 AND chapterNumber = 11;
UPDATE verses SET ts = "اور جو خوش نصیب ثابت ہوں گے وہ جنت میں ہوں گے وہ اس میں اس وقت تک رہتے چلے جائیں گے جب تک کہ آسمان اور زمین قائم ہیں۔ سوائے اس وقت کہ جو تیرا رب چاہے (یہ ایسی) عطا ہے جو (کبھی) کاٹی نہیں جائے گی۔ " WHERE ayatNumber = 109 AND chapterNumber = 11;
UPDATE verses SET ts = "پس (اے مخاطب) جو عبادت یہ (لوگ) کرتے ہیں۔ اس کے (باطل ہونے کے) متعلق تو کسی شک (اور شبہ) میں نہ پڑ۔ یہ اسی طرح کی عبادت کرتے ہیں جس طرح کی عبادت (ان سے) پہلے ان کے باپ دادے کرتے تھے اور ہم یقیناً انہیں (بھی) ان کا حصہ پورا پورا دیں گے۔ جس میں سے (ہرگز) کچھ کم نہیں کیا جائے گا۔ " WHERE ayatNumber = 110 AND chapterNumber = 11;
UPDATE verses SET ts = "اور ہم نے (اختلافات مٹانے کے لئے) یقیناً موسیٰ کو (بھی) کتاب (یعنی تورات) دی تھی پھر (کچھ مدت کے بعد) اس کے متعلق (بھی) اختلاف کیا گیا اور اگر وہ (رحمت کے وعدہ والی) بات جو تیرے رب کی طرف سے پہلے (سے) نازل ہو چکی ہے (مانع) نہ ہوتی تو ان کے درمیان (کبھی) کا فیصلہ کیا جا چکا ہوتا۔ اور اب تو وہ اس (کتاب یعنی قرآن) کے متعلق (بھی) ایک بے چین کر دینے والے شک میں (پڑے ہوئے) ہیں۔ " WHERE ayatNumber = 111 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تیرا رب یقیناً ان سب کو ان کے اعمال (کے پھل) پورے (پورے) دے گا وہ جو کچھ کرتے ہیں۔ اسے وہ خوب جانتا ہے۔ " WHERE ayatNumber = 112 AND chapterNumber = 11;
UPDATE verses SET ts = "پس (اے رسول)! تو ان (لوگوں) سمیت جنہوں نے تیرے ساتھ ہو کر (ہماری طرف رجوع کیا ہے (اس طرح پر) جس طرح تجھے حکم دیا گیا ہے سیدھی راہ پر قائم رہ۔ اور (اے مومنو)! تم کبھی حد سے نہ بڑھنا۔ جو کچھ تم کرتے ہو وہ اسے دیکھ رہا ہے۔ " WHERE ayatNumber = 113 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تم ان لوگوں کی طرف جنہوں نے ظلم (کا شیوہ اختیار) کیا ہے نہ جھکنا۔ ورنہ تمہیں (بھی جہنم کی) آگ (کی لپٹ) پہنچے گی اور (اس وقت) اللہ کے سوا تمہارا کوئی دوست (اور مددگار) نہ ہوگا اور تمہیں (کسی طرف سے بھی) مدد نہیں ملے گی۔ " WHERE ayatNumber = 114 AND chapterNumber = 11;
UPDATE verses SET ts = "اور (اے مخاطب)! تو دن کی دونوں طرفوں نیز رات کے (متعدد اور مختلف) اوقات میں عمدگی سے نماز ادا کیا کر۔ یقیناً نیکیاں بدیوں کو دور کر دیتی ہیں۔ یہ (تعلیم اللہ کی) یاد رکھنے والوں کے لئے ایک نصیحت ہے۔ " WHERE ayatNumber = 115 AND chapterNumber = 11;
UPDATE verses SET ts = "اور صبر (اور استقلال) سے کام لے کیونکہ اللہ نیکو کاروں کے اجر کو ہرگز ضائع نہیں (کیا) کرتا۔ " WHERE ayatNumber = 116 AND chapterNumber = 11;
UPDATE verses SET ts = "پھر کیوں ان قوموں میں سے جو تم سے پہلے (زمانہ میں) تھیں ایسے عقلمند (لوگ) نہ نکلے جو (لوگوں کو) ملک میں بگاڑ پیدا کرنے سے روکتے۔ سوائے چند ایک کے جنہیں ہم نے (ان کے بدیوں سے رکنے اور روکنے کی وجہ سے) بچا لیا۔ اور (باقی لوگ) جنہوں نے ظلم (کا شیوہ اختیار) کیا تھا اس (مال و متاع کی لذت) میں مشغول ہو گئے جس میں انہیں آسودگی بخشی گئی تھی۔ اور مجرم ہو گئے۔ " WHERE ayatNumber = 117 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تیرا رب ایسا ہرگز نہیں ہے کہ (ملک کی) آبادیوں کو باوجود اس کے کہ ان کے رہنے والے اصلاح (کے کام) کرنے والے ہوں ہلاک کر دے۔ " WHERE ayatNumber = 118 AND chapterNumber = 11;
UPDATE verses SET ts = "اور اگر تیرا رب اپنی ہی مشیت نافذ کرتا تو تمام لوگوں کو ایک ہی جماعت بنا دیتا اور (چونکہ اس نے ایسا نہیں کیا اور انہیں ان کی عقل پر چھوڑ دیا ہے) وہ ہمیشہ اختلاف کرتے رہیں گے۔" WHERE ayatNumber = 119 AND chapterNumber = 11;
UPDATE verses SET ts = "سوائے ان کے جن پر تیرے رب نے رحم کیا ہے۔ اور اسی (رحم کا مورد بنانے کے) لئے اس نے انہیں پیدا کیا ہے۔ اور تیرے رب کا یہ فرمودہ ضرور پورا ہوگا (کہ) میں جہنم کو یقیناً (ان سب) جنوں اور انسانوں سے (جو اختلاف کا موجب بنتے ہیں) پر کروں گا۔ " WHERE ayatNumber = 120 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تیرے دل کو مضبوط کرنے کے لئے رسولوں کی تمام اہم خبریں ہم تیرے سامنے بیان کرتے ہیں اور اس (سورۃ) میں وہ ساری باتیں تجھ پر نازل ہوگئی ہیں جو حق و حکمت سے پر ہیں۔ اور نصیحت کرنے والی اور مومنوں کو ان کے فرائض یاد دلانے والی ہیں۔ " WHERE ayatNumber = 121 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تو ان لوگوں سے جو ایمان نہیں لاتے۔ کہہ دے کہ تم اپنے حسب حال عمل کرو۔ ہم بھی (اپنے حسب حال) عمل کریں گے۔ " WHERE ayatNumber = 122 AND chapterNumber = 11;
UPDATE verses SET ts = "اور تم انتظار کرو‘ ہم بھی انتظار کر رہے ہیں۔ " WHERE ayatNumber = 123 AND chapterNumber = 11;
UPDATE verses SET ts = "اور آسمانوں اور زمین کا غیب صرف اللہ کو حاصل ہے اور تمام باتیں (انجام کے لحاظ سے) آخر اسی کی طرف لوٹ کر جاتی ہیں۔ پس تو اسی کی عبادت کر اور اسی پر بھروسا کر اور تیرا رب ان اعمال سے ہرگز غافل نہیں جو تم کرتے ہو۔ " WHERE ayatNumber = 124 AND chapterNumber = 11;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 12;
UPDATE verses SET ts = "میں اللہ دیکھنے والا ہوں۔ یہ (حقائق کو) روشن کرنے والی کتاب کی آیات ہیں۔ " WHERE ayatNumber = 2 AND chapterNumber = 12;
UPDATE verses SET ts = "اپنے مطالب کو خوب واضح کرنے والے قرآن کو ہم نے اتارا ہے تا کہ تم (اس میں) عقل (اور تدبر) سے کام لو۔ " WHERE ayatNumber = 3 AND chapterNumber = 12;
UPDATE verses SET ts = "ہم تیرے پاس (ہر امر کو) بہترین طور پر بیان کرتے ہیں‘ کیونکہ ہم نے اس قرآن کو تیری طرف (حقائق پر مشتمل) وحی (کے ذریعہ سے نازل) کیا ہے اور اس سے پہلے تو (ان حقائق سے) بے خبر لوگوں میں شامل تھا۔ " WHERE ayatNumber = 4 AND chapterNumber = 12;
UPDATE verses SET ts = "(تو اس وقت کو یاد کر) جب یوسف نے اپنے باپ سے کہا (تھا) کہ اے میرے باپ! (یقین مانیئے) میں نے گیارہ ستاروں کو اور سورج اور چاند کو (بھی رئویا میں) دیکھا ہے (اور مزید تعجب اس پر ہے کہ) میں نے ان کو اپنے سامنے سجدہ کرتے دیکھا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا‘ اے میرے پیارے بیٹے! اپنی (یہ) رئویا اپنے بھائیوں کے پاس نہ بیان کیجیئو۔ ورنہ وہ تیرے متعلق (ضرور) کوئی (مخالفانہ) تدبیر کریں گے۔ شیطان انسان کا یقیناً کھلا دشمن ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (جیسا کہ تو نے دیکھا ہے) اسی طرح تیرا رب تجھے برگزیدہ کرے گا اور (الٰہی) باتوں کا علم تجھے بخشے گا اور تجھ پر اور یعقوب کی تمام (حقیقی) آل پر (اسی طرح) اپنے انعام کو پورا کرے گا جیسا کہ  اس نے اس سے پہلے تیرے دو بزرگوں ابراہیم اور اسحاق پر پورا کیا تھا۔ تیرا رب یقیناً بہت جاننے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 12;
UPDATE verses SET ts = "یوسف اور اس کے بھائیوں (کے واقعات) میں (حق کے) طالبوں کے لئے یقیناً کئی نشان (پائے جاتے) ہیں۔ " WHERE ayatNumber = 8 AND chapterNumber = 12;
UPDATE verses SET ts = "(یعنی اس وقت کے واقعات میں) جب انہوں نے (یعنی یوسف کے بھائیوں نے) (ایک دوسرے سے) کہا (کہ) یوسف اور اس کا بھائی یقیناً ہماری نسبت ہمارے باپ کو زیادہ پیارے ہیں حالانکہ ہم ایک منضبط جماعت ہیں (اس معاملہ میں) ہمارا باپ (کھلی) کھلی غلطی میں (پھنسا ہوا) ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 12;
UPDATE verses SET ts = "(اس لئے یا تو) یوسف کو قتل کر دو یا اسے کسی اور ملک میں (دور) پھینک دو (ایسا کرو گے تو) تمہارے باپ کی توجہ تمہارے لئے فارغ ہو جائے گی اور (اس فعل سے ڈرنے کی کوئی وجہ نہیں کیونکہ) اس کے بعد (توبہ کرکے) تم (پھر) ایک نیک گروہ بن سکو گے۔ " WHERE ayatNumber = 10 AND chapterNumber = 12;
UPDATE verses SET ts = "(اس پر) ان میں سے ایک بولنے والے نے کہا (کہ) تم یوسف کو قتل نہ کرو اور اگر تم نے (کچھ) کرنا (ہی) ہے تو اسے (کسی) بائولی کی تہ میں ڈال دو۔ کسی قافلہ کا کوئی شخص اسے (دیکھ کر) اٹھالے گا (اور بغیر جان لینے کے تمہارا مقصد پورا ہو جائے گا) " WHERE ayatNumber = 11 AND chapterNumber = 12;
UPDATE verses SET ts = "(چنانچہ) انہوں نے (باپ سے جاکر) کہا‘ اے ہمارے باپ! آپ کو (ہمارے متعلق) کیا (خدشہ) ہے کہ یوسف کے متعلق آپ ہم پر اعتماد نہیں کرتے حالانکہ ہم اس سے (دلی) خلوص رکھتے ہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 12;
UPDATE verses SET ts = "کل اسے ہمارے ساتھ (سیر کے لئے باہر) بھیجئے وہ (وہاں) کھلا کھائے (پیئے) گا اور کھیلے گا اور ہم اس کی حفاظت کریں گے۔ " WHERE ayatNumber = 13 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے (یعنی یعقوب نے) کہا‘ تمہارا اسے (اپنے ساتھ) لے جانا مجھے یقیناً فکر مند کرتا ہے اور میں (اس بات سے بھی) ڈرتا ہوں کہ کہیں ایسی حالت میں کہ تم اس سے غافل ہو اسے (کوئی) بھیڑیا (ہی آکر نہ) کھا جائے۔ " WHERE ayatNumber = 14 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا‘ اگر اس (بات) کے باوجود (بھی) کہ ہم ایک مضبوط جماعت ہیں اسے بھیڑیا کھا جائے تو (خدا کی قسم) اس صورت میں ہم یقیناً گھاٹے میں پڑنے والے ہوں گے۔ " WHERE ayatNumber = 15 AND chapterNumber = 12;
UPDATE verses SET ts = "پھر جب وہ اسے لے گئے اور (جاکر) اسے (کسی) بائولی کی تہ میں ڈالنے کا متفقہ فیصلہ کر لیا (تو ادھر انہوں نے اپنا ارادہ پورا کیا) اور (ادھر) ہم نے اس کی طرف وحی کے (ذریعہ سے یہ بشارت نازل) کی کہ تو (محفوظ رہے گا اور) انہیںان کے اس کام سے آگاہ کرے گا اور وہ (اس بات کو) نہیں سمجھتے تھے۔ " WHERE ayatNumber = 16 AND chapterNumber = 12;
UPDATE verses SET ts = "اور عشا کے وقت وہ روتے ہوئے اپنے باپ کے پاس آئے۔ " WHERE ayatNumber = 17 AND chapterNumber = 12;
UPDATE verses SET ts = "(اور) کہا (کہ) اے ہمارے باپ! (یقین جانیئے) ہم جاکر (کھیلنے اور)  ایک دوسرے سے بڑھ بڑھ کر دوڑنے لگے۔ اور یوسف کو ہم اپنے سامان کے پاس چھوڑ گئے تو (خدا کا کرنا ایسا ہوا کہ) اسے (ایک) بھیڑیا کھا گیا اور (یہ تو ہم جانتے ہیں کہ) آپ ہماری بات (کو درست) نہیں ماننے کے گو ہم (اس میں بالکل) سچے (ہی کیوں نہ) ہوں۔ " WHERE ayatNumber = 18 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (اسے یقین دلانے کے لئے) وہ اس کے کرتے پر جھوٹا خون لگا لائے (جسے دیکھ کر) اس نے کہا (یہ بات درست نہیں) بلکہ تمہارے نفسوں نے تمہارے لئے ایک (ایسی بری) بات کو خوبصورت کرکے دکھلایا ہے (جسے تم کر گذرے ہو) اب اچھی طرح صبر کرنا (ہی میرے لئے مناسب) ہے اور جو بات تم بیان کرتے ہو اس (کے تدارک) کے لئے اللہ ہی سے مدد مانگی جا سکتی ہے (اور اسی سے مانگی جائے گی)۔ " WHERE ayatNumber = 19 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (اتنے میں) ایک قافلہ آیا اور انہوں نے اپنے پانی لانے والے (آدمی) کو بھیجا۔ اور اس نے (اس بائولی پر جاکر) اپنا ڈول ڈالا (اور جب اسے بائولی میں ایک لڑکا نظر آیا) تو اس نے (قافلہ والوں سے) کہا‘ اے (قافلہ والو! لو) خوشخبری (سنو! اور دیکھو مجھے)  یہ ایک لڑکا (ملا) ہے اور انہوں نے (یعنی قافلہ والوں نے) اسے ایک تجارتی مال سمجھتے ہوئے چھپا لیا اور جو کچھ وہ کرتے تھے اسے اللہ خوب جانتا تھا۔ " WHERE ayatNumber = 20 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (اس کے بعد جب برادران یوسف کو یوسف کے پکڑے جانے کا علم ہوا تو) انہوں نے اسے (اپنا غلام بتاکر) کچھ تھوڑی (سی) قیمت یعنی چند گنتی کے درہموں کے بدلے (اسی قافلہ والوں کے پاس اسے) بیچ دیا اور وہ اس (حقیر قیمت) سے بالکل بے رغبت تھے۔ " WHERE ayatNumber = 21 AND chapterNumber = 12;
UPDATE verses SET ts = "اور مصر (کے باشندوں) میں سے جس شخص نے اسے خریدا اس نے اپنی بیوی سے کہا کہ اس کی رہائش کی جگہ باعزت بنا۔ امید ہے کہ یہ (لڑکا) ہمارے لئے نفع رساں (ثابت) ہوگا۔ یا ہم اسے (اپنا) بیٹا (ہی) بنا لیں گے۔ اور اس طرح سے ہم نے یوسف کو اس ملک میں (قدرو) منزلت بخشی اور (ہم نے اسے یہ عزت کا مقام) اس لئے (بھی دیا) تا کہ ہم تعبیر رئویا کی حقیقت کا علم اسے دیں اور اللہ اپنی بات (کو پورا کرنے) پر (کامل) اقتدار رکھتا ہے لیکن اکثر لوگ (اس حقیقت کو) جانتے نہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب وہ اپنی قوت (اور مضبوطی کی عمر) کو پہنچا تو ہم نے اسے فیصلہ (کرنے کی فراست) اور (خاص) علم بخشا۔ اور (حقیقی) نیکو کاروں کو ہم اسی طرح جزا دیا کرتے ہیں۔ " WHERE ayatNumber = 23 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جس (عورت) کے گھر میں وہ (رہتا) تھا اس نے اس سے اس کی مرضی کے خلاف (ایک) فعل کروانا چاہا۔ اور (اس مکان کے) تمام دروازے بند کر دئیے اور کہا (میری طرف) آجا۔ اس نے کہا (میں ایسا کرنے سے) اللہ کی پناہ (چاہتا ہوں) وہ یقیناً میرا رب ہے۔ اس نے (ہی) میری رہائش کی جگہ اچھی بنائی ہے۔ بات یہی ہے کہ ظالم کامیاب نہیں ہوا کرتے۔ " WHERE ayatNumber = 24 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس (عورت) نے اس کے متعلق (اپنا) ارادہ پختہ کر لیا اور اس (یوسف) نے بھی اس کے متعلق (اپنا) ارادہ (یعنی اس سے محفوظ رہنے کا) پختہ کر لیا (اور) اگر اس نے اپنے رب کا روشن نشان نہ دیکھا ہوتا (تو وہ ایسا عزم نہ کر سکتا) اسی طرح پر (ہوا) تا کہ ہم اس سے (ہرایک) بدی اور بے حیائی (کی بات) کو دور کردیں۔ (اور) وہ یقیناً ہمارے برگزیدہ (اور پاک کئے ہوئے) بندوں میں سے تھا۔ " WHERE ayatNumber = 25 AND chapterNumber = 12;
UPDATE verses SET ts = "اور وہ (دونوں) دروازہ کی طرف دوڑے اور اس (کشمکش میں اس عورت) نے اس کے کرتے کو پیچھے سے پھاڑ دیا۔ اور (جب وہ دروازے تک پہنچے تو) انہوں نے اس (عورت) کے خاوند کو دروازہ کے پاس (کھڑا) پایا (جس پر) اس (عورت) نے (اپنے خاوند سے) کہا جو (شخص) آپ کے اہل سے بدی (کرنا) چاہے۔ اس کی سزا سوائے اس کے (کوئی) نہیں (ہونی چاہیے) کہ اسے قید کر دیا جائے یا (اسے) کوئی دردناک عذاب (دیا جائے) " WHERE ayatNumber = 26 AND chapterNumber = 12;
UPDATE verses SET ts = "اس (یعنی یوسف) نے کہا (بات یہ نہیں بلکہ) اس نے مجھ سے میری مرضی کے خلاف (ایک) فعل کروانا چاہا تھا اور اس (عورت ہی) کے کنبہ میں سے ایک گواہ نے گواہی دی (کہ اس عورت کے کپڑے صحیح سلامت ہیں اور اس آدمی کا کرتہ تازہ پھٹا ہوا ہے پس) اگر اس کا کرتہ آگے سے پھاڑا گیا ہے تو اس  عورت نے سچ کہا ہے اور وہ (آدمی) یقیناً جھوٹا ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اگر اس (مرد) کا کرتہ پیچھے سے پھاڑا گیا ہے تو اس (عورت) نے جھوٹ بولا ہے اور وہ (مرد) یقیناً سچا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 12;
UPDATE verses SET ts = "پس جب اس (کے خاوند) نے اس کے (یعنی یوسف کے) کرتے کو دیکھا کہ پیچھے سے پھاڑا گیا ہے تو اس نے (اپنی بیوی سے) کہا یہ (جھگڑا) یقیناً تمہاری چالاکی سے (پیدا ہوا) ہے۔ یقیناً تم عورتوں کی چالاکی بہت بڑی ہے۔" WHERE ayatNumber = 29 AND chapterNumber = 12;
UPDATE verses SET ts = "اے یوسف! تو اس (عورت کی شرارت) سے چشم پوشی کر اور تو (اے عورت) اپنے قصور کی بخشش طلب کر۔ یقیناً تو ظالموں (اور خطاکاروں) میں سے ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس شہر کی بعض عورتوں نے (ایک دوسری سے) کہا (کہ) عزیز کی عورت اپنے غلام سے اس کی مرضی کے خلاف (برا) فعل کروانا چاہتی ہے (اور) اس کی محبت نے اس کے دل کی گہرائیوں میں گھر کر لیا ہے۔ ہم (اس معاملہ میں) اسے کھلی (کھلی) غلطی پر دیکھتی ہیں۔ " WHERE ayatNumber = 31 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب اس نے ان کی ان سرگوشیوں کی خبر سنی تو انہیں (دعوت) کا پیغام بھیجا اور ان کے لئے ایک (خاص) مسند تیار کی اور (جب وہ آئیں تو) ان میں سے ہر ایک کو (کھانے کے کاٹنے کے لئے ایک ایک) چھری دی اور (یوسف سے) کہا (کہ) ان کے سامنے آ۔ پس جب انہوں نے اسے دیکھا تو اسے (بہت) بڑی شان کا انسان پایا اور (اسے دیکھ کر حیرت سے) اپنے ہاتھ کاٹے اور کہا (کہ یہ شخص محض) اللہ کے لئے (بدی کے ارتکاب سے) ڈرا ہے۔ یہ بشر (ہے ہی) نہیں۔ یہ (تو) صرف ایک معزز فرشتہ ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 12;
UPDATE verses SET ts = "(اس پر) اس (عورت) نے انہیں کہا۔ یہ وہی (شخص) ہے۔ جس کے متعلق تم نے مجھے ملامت کی ہے اور میں نے اس سے اس کی مرضی کے خلاف (ایک برا) فعل کروانے کی کوشش ضرور کی تھی (مگر اس پر (بھی) یہ بچا رہا۔ اور اگر اس نے وہ بات جس کے لئے میں اسے حکم دیتی ہوں نہ کی‘ تو یقیناً اسے قید کر دیا جائے گا۔ اور یقیناً وہ ذلیل ہوگا۔ " WHERE ayatNumber = 33 AND chapterNumber = 12;
UPDATE verses SET ts = "(یہ سن کر) اس نے (دعا کرتے ہوئے) کہا (کہ) اے میرے رب! جس بات کی طرف وہ مجھے بلاتی ہے اس کی نسبت قید خانہ میں جانا مجھے زیادہ پسند ہے اور اگر ان کی تدبیر (کے بدنتیجہ) کو تو مجھ سے نہیں ہٹائے گا تو میں ان کی طرف جھک جائوں گا۔ اور جاہلوں میں سے ہو جائوں گا۔ " WHERE ayatNumber = 34 AND chapterNumber = 12;
UPDATE verses SET ts = "پس اس کے رب نے اس کی دعا سن لی اور ان کی تدبیر (کے بدنتیجہ) کو اس سے ہٹا دیا۔ یقیناً وہی ہے جو بہت ہی (دعائیں) سننے والا (اور لوگوں کے حالات کو) خوب جاننے والا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 12;
UPDATE verses SET ts = "پھر ان (لوگوں) کی (یعنی قوم کے سرداروں کی) (ان) آثار کو دیکھنے کے بعد (یہ) رائے  ہوگئی۔ کہ (بدنامی کو دور کرنے کے لئے) وہ اسے (کم سے کم) کچھ وقت کے لئے ضرور قید کردیں۔ " WHERE ayatNumber = 36 AND chapterNumber = 12;
UPDATE verses SET ts = "اور قید خانہ میں اس کے ساتھ دو اور جوان (بھی) داخل ہوئے جن میں سے ایک نے (تو اس سے یہ) کہا (کہ) میں (خواب میں) اپنے آپ کو (اس حالت میں) دیکھتا ہوں کہ میں انگور نچوڑ رہا ہوں۔ اور دوسرے نے کہا (کہ) میں (خواب میں) اپنے آپ کو (اس حالت میں) دیکھتا ہوں کہ میں اپنے سر پر روٹیاں اٹھائے ہوئے ہوں جن میں سے پرندے کھا رہے ہیں (اور ان  دونوں نے اس سے کہا کہ) آپ ہمیں اس کی حقیقت سے آگاہ کریں۔ ہم آپ کو یقیناً نیکو کاروں میں سے سمجھتے ہیں۔ " WHERE ayatNumber = 37 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا اس وقت کا کھانا ابھی نہیں  آئے گا کہ میں تمہیں اس کے آنے سے پہلے اس (خواب) کی حقیقت بتادوں گا۔ یہ (تعبیر رئویا کی اہلیت مجھ میں) اس وجہ سے (پائی جاتی) ہے کہ میرے رب نے مجھے علم بخشا ہے۔ میں نے ان لوگوں کے دین کو جو اللہ پر ایمان نہیںرکھتے اور وہ آخرت کے (بھی) منکر ہیں چھوڑ دیا ہوا ہے۔ " WHERE ayatNumber = 38 AND chapterNumber = 12;
UPDATE verses SET ts = "اور میں نے اپنے باپ دادوں یعنی ابراہیم اور اسحاق اور یعقوب کے طریق کی پیروی اختیار کی ہے۔ ہمیں کسی چیز کو بھی اللہ کا شریک ٹھہرانے کا حق نہیں ہے یہ (توحید کی تعلیم کا ملنا) ہم پر اور دوسرے لوگوں پر اللہ کا (اس کے خاص فضلوں میں سے) ایک فضل ہے‘ لیکن اکثر لوگ (اس کے احسانوں کا) شکر نہیں کرتے۔ " WHERE ayatNumber = 39 AND chapterNumber = 12;
UPDATE verses SET ts = "اے میرے قید خانہ کے ساتھیو! کیا (ایک دوسرے سے) اختلاف رکھنے والے خدا بہتر ہیں یا اللہ جو یکتا (اور) کامل غلبہ رکھنے والا ہے؟ " WHERE ayatNumber = 40 AND chapterNumber = 12;
UPDATE verses SET ts = "تم اسے چھوڑ کر سوائے چند (فرضی) ناموں کے جو (خود) تم نے اور تمہارے باپ دادوں نے بنا رکھے ہیں (اور) جن کی بابت اللہ نے (تمہاری تائید میں) کوئی بھی حجت نہیں اتاری (کسی کی) عبادت نہیں کرتے (یاد رکھو) فیصلہ کرنا اللہ کے سوا کسی کے اختیار میں نہیں ہے (اور) اس نے یہ حکم دیا ہے کہ تم اس کے سوا کسی کی عبادت نہ کرو۔ یہی درست مذہب ہے۔ لیکن اکثر لوگ جانتے نہیں۔ " WHERE ayatNumber = 41 AND chapterNumber = 12;
UPDATE verses SET ts = "اے میرے قید خانہ کے دونوں ساتھیو! (اب اپنی اپنی خواب کی تعبیر سنو) تم میں سے ایک تو اپنے آقا کو شراب پلایا کرے گا اور دوسرے کو سولی دے کر مارا جائے گا۔ پھر پرندے اس کے سر پر سے (گوشت وغیرہ) کھائیں گے‘ (لو) جس امر کے متعلق تم پوچھ رہے ہو  اس کا فیصلہ کر دیا گیا ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 12;
UPDATE verses SET ts = "اوران میں سے اس سے جس کے متعلق اس نے یہ سمجھا تھا کہ وہ مخلصی پا جانے والا ہے  اس نے کہا (کہ) اپنے آقا کے پاس میرا (بھی) ذکر کرنا۔ پھر شیطان نے اس (آزاد شدہ قیدی) کو اس کے آقا سے (یہ) ذکر کرنا فراموش کرادیا اور وہ (یعنی یوسف) کئی سال قید خانہ میں (پڑا) رہا۔ " WHERE ayatNumber = 43 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (کچھ عرصہ کے بعد) بادشاہ نے (اپنے درباریوں سے) کہا (کہ) میں (خواب میں) سات موٹی گائیں دیکھتا ہوں۔ جنہیں سات دُبلی (گائیں) کھا رہی ہیں اور سات (تروتازہ اور) سبز بالیں (دیکھتا ہوں) اور چند اور (بالیں بھی جو) خشک (ہیں) اے سرکردہ لوگو! اگر تم رئویا کی تعبیر (کیا) کرتے ہو تو مجھے میری (اس) رئویا کے متعلق صحیح حکم بتائو۔ " WHERE ayatNumber = 44 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ یہ تو) پراگندہ خوابیں ہیں۔ اور ہم (لوگ) ایسی پراگندہ خوابوں کی حقیقت نہیں جانتے۔ " WHERE ayatNumber = 45 AND chapterNumber = 12;
UPDATE verses SET ts = "اور ان (دو قیدیوں) میں سے اس نے جس نے مخلصی پائی تھی اور (جس نے) ایک عرصہ کے بعد (یوسف کے ساتھ جو اس کا معاملہ گذرا تھا اسے) یاد کیا۔ کہا (کہ) میں تمہیں اس کی حقیقت سے آگاہ کروں گا۔ پس تم (اس کی حقیقت دریافت کرنے کے لئے) مجھے بھیجو۔ " WHERE ayatNumber = 46 AND chapterNumber = 12;
UPDATE verses SET ts = "(اور اس نے یوسف سے جاکر کہا کہ) اے یوسف! (ہاں) اے راستباز! ہمیں ان سات موٹی گائیوں (کو رئویا میں دیکھنے) کے متعلق جنہیں سات دُبلی (گائیں) کھا جائیں اور (نیز) سات سبز بالوں اور (ان کے مقابل پر) چند اور خشک (بالوں کو دیکھنے) کے متعلق تشریح بتایئے تا کہ میں ان لوگوں کے پاس جائوں کہ ان کو (تعبیر خواب کا) علم ہو جائے۔ " WHERE ayatNumber = 47 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا (کہ) تم سات برس مسلسل جدوجہد سے کاشت (کا کام) کرو گے پس (اس عرصہ میں) جو (کچھ) تم کاٹو اس (سب) کو سوائے (اس) تھوڑے سے حصہ کے جو تم کھالو‘ اس کی بالوں میں (ہی) رہنے دینا۔ " WHERE ayatNumber = 48 AND chapterNumber = 12;
UPDATE verses SET ts = "پھر اس کے بعد سات سخت (تنگی کے سال) آئیں گے (اور) سوائے اس قلیل مقدار کے جسے تم پس انداز کر لو وہ اس (تمام غلہ) کو جو تم نے ان کے لئے پہلے سے جمع کر چھوڑا ہوگا کھا جائیں گے۔ " WHERE ayatNumber = 49 AND chapterNumber = 12;
UPDATE verses SET ts = "پھر اس کے بعد ایک (ایسا) سال آئے گا‘ جس میں لوگوں کی فریاد سنی جائے گی اور وہ (خوشحال) ہو جائیں گے اور اس حالت میں ایک دوسرے کو تحفے دیں گے۔ " WHERE ayatNumber = 50 AND chapterNumber = 12;
UPDATE verses SET ts = "اور بادشاہ نے (یہ بات سن کر ان سے) کہا (کہ) تم اسے میرے پاس لے آئو۔ پس جب (بادشاہ کا) پیغام رساں اس کے پاس آیا‘ تو اس نے (یعنی یوسف نے اس سے) کہا (کہ) تو اپنے آقا کے پاس واپس جا اور اس سے پوچھ کہ جن عورتوں نے اپنے ہاتھ کاٹے تھے ان کی (اس وقت) کیا کیفیت ہے۔ میرا رب ان کے منصوبے کو یقیناً خوب جاننے والا ہے۔ " WHERE ayatNumber = 51 AND chapterNumber = 12;
UPDATE verses SET ts = "(یہ پیغام سن کر) اس نے (یعنی بادشاہ نے) ان (عورتوں) سے کہا (کہ) تمہارا (وہ) معاملہ جبکہ تم نے یوسف سے اس کی مرضی کے خلاف (ایک برا) فعل کروانے کی کوشش کی تھی (اصل میں) کیا تھا؟ انہوں نے کہا کہ وہ اللہ کی خاطر (بدی کے ارتکاب سے) ڈرا تھا۔ (اور) ہم نے اس میں کوئی بھی برائی (کی بات) نہیں معلوم کی تھی۔ (یہ سن کر) عزیز کی بیوی نے کہا (کہ) اب سچائی بالکل کھل گئی ہے۔ میں نے (ہی) اس سے اس کی مرضی کے خلاف (برا) فعل کرانے کی کوشش کی تھی اور وہ یقیناً راستبازوں میں سے ہے۔ " WHERE ayatNumber = 52 AND chapterNumber = 12;
UPDATE verses SET ts = "(اور یوسف نے اسے یہ بھی کہا کہ) یہ (بات میں نے) اس لئے کہی ہے کہ اس کو (یعنی عزیز کو) علم ہو جائے کہ میں نے (اس کی) غیر موجودگی میں اس کے حق میں خیانت نہیں کی اور یہ کہ (میرا ایمان ہے کہ) خیانت کرنے والوں کی تدبیر کو اللہ کامیاب نہیں کرتا۔ " WHERE ayatNumber = 53 AND chapterNumber = 12;
UPDATE verses SET ts = "اور میں اپنے نفس کو (ہر قسم کی غلطی سے) بری قرار نہیں دیتا کیونکہ (انسانی) نفس سوائے اس کے جس پر میرا رب رحم کرے بری باتوں کا حکم دینے پر بہت دلیر ہے میرا رب (کمزوریوں پر) بہت پردہ ڈالنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 12;
UPDATE verses SET ts = "اور بادشاہ نے حکم دیا کہ اس کو (یعنی یوسف کو) میرے پاس لائو‘ تا کہ  میں اسے اپنے (خاص کاموں کے) لئے منتخب کرلوں جب (یوسف آئے اور) اس نے (یعنی بادشاہ نے) اس سے بات چیت کی تو (ان کو ہر طرح قابل پا کر یوں) بولا کہ تو آج (سے) ہمارے ہاں معزز مرتبہ والا (اور) قابل اعتماد آدمی (شمار) ہوگا۔ " WHERE ayatNumber = 55 AND chapterNumber = 12;
UPDATE verses SET ts = "(اس پر یوسف نے) کہا کہ مجھے ملک کے خزانوں پر افسر مقرر کر دیں۔ کیونکہ میں یقیناً (خزانوں کی) بہترین حفاظت کرنے والا اور (ان کے خرچ کے وجوہ کو) خوب سمجھنے والا ہوں۔ " WHERE ayatNumber = 56 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس طرح (مناسب حالات پیدا کرکے) ہم نے یوسف کو (اس) ملک میں ایک بااختیار عہدہ عطا کیا وہ (اپنی مرضی کے مطابق) جہاں (کہیں) چاہتا ٹھہرتا ہم جسے چاہتے ہیں (اس دنیا میں ہی) اپنی رحمت سے (حصہ) دیتے ہیں اور ہم نیکو کاروں کا اجر ضائع نہیں کیا کرتے۔" WHERE ayatNumber = 57 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (اس دنیوی اجر کے علاوہ) آئندہ (زندگی) کا بدلہ ایمان لانے والوں اور اللہ کا تقویٰ اختیار کرنے والوں کے لئے (کہیں) بڑھ (چڑھ) کرہوگا۔ " WHERE ayatNumber = 58 AND chapterNumber = 12;
UPDATE verses SET ts = "اور (اس قحط کے زمانہ میں) یوسف کے بھائی (بھی اس ملک میں) آئے اور اس کے حضور میں حاضر ہوئے اور اس نے انہیں (دیکھتے ہی) پہچان لیا مگر وہ اسے نہ پہچان سکے۔ " WHERE ayatNumber = 59 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب اس نے انہیں ان کے سامان دیکر (واپسی کے لئے) تیار کیا تو (ان سے) کہا (کہ) تمہارے باپ کی طرف سے جو تمہارا ایک بھائی ہے (اب کے) اسے (بھی اپنے ساتھ) میرے پاس لانا۔ کیا تم دیکھتے نہیں کہ میں ماپ پورا دیتا ہوں اور (نیز) میں مہمان نوازوں میں سے بہترین (مہمان نواز) ہوں۔ " WHERE ayatNumber = 60 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اگر تم اسے میرے پاس نہ لائے تو (سمجھ لو کہ) میرے پاس تمہیں تول کر دینے کے لئے کچھ نہیں ہوگا اور (اس صورت میں) تم میرے قریب نہ پھٹکنا۔ " WHERE ayatNumber = 61 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا‘ ہم ضرور اس کے متعلق اس کے باپ کو پھسلانے کی کوشش کریں گے اور ہم یقیناً (یہ کام) کرکے رہیں گے۔ " WHERE ayatNumber = 62 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس نے اپنے غلاموں سے کہہ دیا کہ ان کی پونجی (واپس) ان کے بوروں میں رکھ دو۔ شاید جب وہ لوٹ کر اپنے گھر والوں کے پاس جائیں تو اس (احسان) کو مانیں (اور) شاید وہ (اسی سبب سے) پھر واپس آئیں۔ " WHERE ayatNumber = 63 AND chapterNumber = 12;
UPDATE verses SET ts = "پس جب وہ اپنے باپ کے پاس واپس گئے تو کہا (کہ) اے ہمارے باپ ہمیں (آئندہ کے لئے) غلہ سے محروم کر دیا گیا ہے اس لئے (اب) ہمارے بھائی (بن یامین) کو (بھی) ہمارے ساتھ بھیج کہ ہم پھر غلہ لے سکیں۔ اور ہم یقیناً اس کی حفاظت کریں گے۔ " WHERE ayatNumber = 64 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا (تم ہی بتائو) کیا میں اس کو تمہارے سپرد کر سکتا ہوں سوائے اس کے کہ اس کا وہی نتیجہ نکلے جو اس سے پہلے میرے اس کے بھائی کو تمہارے سپرد کرنے کا نتیجہ نکلا تھا۔ پس (میں اسے تمہارے سپرد تو کرتا ہوں مگر اس یقین سے کہ تم نہیں بلکہ) اللہ ہی بہتر حفاظت کرنے والا ہے۔ اور وہی سب سے زیادہ رحم کرنے والا ہے۔ " WHERE ayatNumber = 65 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب انہوں نے اپنا سامان کھولا تو دیکھا کہ ان کی پونجی ان کی طرف واپس کر دی گئی ہے (اس پر) انہوں نے (اپنے باپ سے) کہا (کہ) اے ہمارے باپ (اس سے بڑھ کر) ہم اور کیا خواہش کر سکتے ہیں (دیکھئے) یہ ہماری پونجی ہے اسے (بھی) ہماری طرف واپس کر دیا گیا ہے اور (اگر ہمارا بھائی ہمارے ساتھ جائے گا تو) ہم اپنے گھر والوں کو خوراک کا سامان لا دیں گے اور اپنے بھائی کی (ہر طرح سے) حفاظت کریں گے اور ایک بار شتر زیادہ لائیں گے۔ یہ وزن (جو ہمیں مفت ملے گا) بڑی نعمت ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا‘ میں اسے تمہارے ساتھ کبھی نہیں بھیجوں گا جب تک تم مجھ سے اللہ کی طرف سے مقرر شدہ (یعنی اس قسم سے موکد) یہ عہد نہ کرو کہ تم اسے ضرور میرے پاس (واپس) لائو گے۔ سوائے اس (صورت) کے کہ تم (خود کسی مصیبت میں) گھر جائو۔ پھر جب انہوں نے اسے اپنا پختہ قول دے دیا تو اس نے کہا جو (کچھ) ہم (اس وقت) کہہ رہے ہیں اللہ اس کا نگران ہے۔ " WHERE ayatNumber = 67 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس نے (ان سے) کہا (کہ) اے میرے بیٹو! تم شہر کے ایک ہی دروازہ سے اکٹھے اندر نہ جانا‘ بلکہ الگ الگ دروازوں سے اندر جانا۔ اور میں اللہ کی (گرفت) سے بچانے کے لئے کچھ بھی تمہارے کام نہیں آسکتا۔ فیصلہ کرنا (دراصل) اللہ ہی کا کام ہے اسی پر میں نے بھروسا کیا ہے اور تمام بھروسا کرنے والوں کو اسی پر بھروسا کرنا چاہیئے۔ " WHERE ayatNumber = 68 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب اس طریق کے مطابق جس کا حکم ان کے باپ نے انہیں دیا تھا وہ داخل ہوئے تو وہ غرض پوری ہو گئی جس کے لئے انہیں یہ حکم دیا گیا تھا مگر یعقوب اپنی تدبیر سے اُن  (یعنی بیٹوں) کو خدائی تدبیر سے بچا نہیں سکتا تھا۔ ہاں مگر یعقوب کے دل میں ایک خواہش تھی جسے اس نے (اس طرح) پورا کر لیا اور چونکہ ہم نے اس کو (یعنی یعقوب کو) علم دیا تھا۔ وہ بڑا علم والا تھا لیکن اکثر لوگ (اس حقیقت کو) نہیں جانتے۔" WHERE ayatNumber = 69 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب وہ یوسف کے حضور حاضر ہوئے تو اس نے اپنے بھائی کو اپنے پاس جگہ دی اور (اس سے) کہا (کہ) یقیناً میں ہی تیرا (مفقود) بھائی ہوں۔ پس جو کچھ وہ کرتے رہے ہیں اس کی وجہ سے (اب) تو غمگین نہ ہو۔ " WHERE ayatNumber = 70 AND chapterNumber = 12;
UPDATE verses SET ts = "پھر جب اس نے انہیں ان کا سامان دے کر (واپسی کے لئے) تیار کیا‘ تو اس نے (پانی پینے کا ایک) کٹورا (بھی) اپنے بھائی کے بورے میں رکھ دیا پھر (ایسا ہوا کہ) کسی اعلان کرنے والے (یعنی شاہی کارندہ) نے اعلان کیا‘ (کہ) اے قافلہ والو تم یقیناً چور ہو۔ " WHERE ayatNumber = 71 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے (یعنی برادران یوسف نے) ان (شاہی کارندوں) کی طرف رخ کرکے کہا (کہ) تم کیا چیز گم پاتے ہو؟۔ " WHERE ayatNumber = 72 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) ہم غلہ ماپنے کا شاہی پیمانہ گم پاتے ہیں اور جو شخص اسے (تلاش کرکے) لے آئے تو ایک اونٹ کے بوجھ کے برابر (غلہ) اس کا (انعام) ہوگا اور (اعلان کرنے والے نے یہ بھی کہا کہ) میں اس (انعام) کا ذمہ وار ہوں۔ " WHERE ayatNumber = 73 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اللہ کی قسم! تم جانتے ہو کہ ہم (یہاں) اس لئے نہیں آئے کہ ملک میں فساد کریں اور نہ (ہی) ہم چور ہیں۔ " WHERE ayatNumber = 74 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اگر تم جھوٹے (ثابت) ہوئے تو اس (فعل یعنی چوری) کی سزا کیا ہوگی؟ " WHERE ayatNumber = 75 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اس کی سزا یہ ہے کہ جس شخص کے سامان میں وہ (برتن) پایا جائے وہ (خود ہی) اس (فعل) کا بدلہ ہو ہم (لوگ) ظالموں کو اسی طرح سزا دیا کرتے ہیں۔ " WHERE ayatNumber = 76 AND chapterNumber = 12;
UPDATE verses SET ts = "پس اس نے (یعنی اعلان کرنے والے نے) اس کے (یعنی یوسف کے) بھائی کے بورے سے پہلے ان (دوسروں) کے بوروں کو (دیکھنا) شروع کیا۔ پھر اس کے بھائی کے بورے (کو دیکھا اور اس میں اس برتن کو پاکر اس) میں سے اسے نکالا۔ اس طرح ہم نے یوسف کے لئے (ایک) تدبیر کی (ورنہ) وہ بادشاہ کے قانون کے اندر (رہتے ہوئے) اپنے بھائی کو اللہ کی تدبیر کے بغیر روک نہیں سکتا تھا۔ ہم جس کے چاہتے ہیں درجات بلند کرتے ہیں اور (حقیقت یہ ہے کہ) ہر علم والے کے اوپر (اس سے) زیادہ علم والی ہستی (پائی جاتی) ہے۔ " WHERE ayatNumber = 77 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے (یعنی اس کے بھائیوں نے) کہا (کہ) اگر اس نے چوری کی ہو تو (کچھ عجب نہیں کیونکہ) اس کا ایک بھائی (بھی) پہلے چوری کر چکا ہے۔ اس پر یوسف نے (اپنے دل کی بات کو) اپنے دل میں پوشیدہ رکھا۔ اور ان پر اسے ظاہر نہ کیا (ہاں دل میں اتنا) کہا (کہ) تم (لوگ) بڑے بدبخت ہو اور جو بات تم کہتے ہو اسے اللہ (ہی) بہتر جانتا ہے۔ " WHERE ayatNumber = 78 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اے سردار! اس کا ایک بہت بوڑھا باپ ہے (اسے اس کے صدمہ سے بچانے کے لئے) اس کی بجائے آپ ہم میں سے کسی ایک کو پکڑ لیجیئے۔ ہم آپ کو یقیناً محسنوں میں سے سمجھتے ہیں۔ " WHERE ayatNumber = 79 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا (کہ ہم) اس شخص کے سوا جس کے پاس ہم نے اپنا سامان پایا ہے کسی اور کو پکڑنے سے خدا کی پناہ چاہتے ہیں اگر ہم ایسا کریں تو ہم یقیناً ظالموں میں سے ہوں گے۔ " WHERE ayatNumber = 80 AND chapterNumber = 12;
UPDATE verses SET ts = "پس جب وہ اس سے (یعنی یوسف سے) ناامید ہو گئے تو آپس میں باتیں کرتے ہوئے (لوگوں سے) الگ ہوگئے (تب) ان میں سے بڑے نے کہا (کہ) کیا تمہیں معلوم نہیں کہ تمہارے باپ نے تم سے پکا قول لیا (ہوا) ہے جو اللہ (کی قسم) سے (موکد) ہے اور یہ کہ (اس سے) پہلے تم یوسف کے بارہ میں بھی کوتاہی کر چکے ہو اس لئے جب تک میرا باپ مجھے (خاص طور پر) اجازت (نہ) دے یا (خود) اللہ میرے حق میں فیصلہ (کی کوئی راہ پیدا نہ) کرے میں اس ملک کو نہیں چھوڑوں گا اور وہ (اللہ) سب فیصلہ کرنے والوں سے بہتر (فیصلہ کرنے والا) ہے۔ " WHERE ayatNumber = 81 AND chapterNumber = 12;
UPDATE verses SET ts = "تم اپنے باپ کی طرف لوٹ جائو اور (اس سے جاکر) کہو (کہ) اے ہمارے باپ آپ  کے (چھوٹے) بیٹے نے ضرور چوری کی ہے۔ اور ہم نے (آپ سے) وہی کہا ہے جس کا ہمیں (ذاتی) علم ہے اور ہم (اپنی) نظر سے پوشیدہ بات کی حفاظت نہیں کر سکتے تھے۔ " WHERE ayatNumber = 82 AND chapterNumber = 12;
UPDATE verses SET ts = "اور آپ (بے شک) ان لوگوں سے (بھی) دریافت کر لیں جن میں ہم (رہتے) تھے اور اس قافلہ سے (بھی) جس کے ساتھ ہم آئے ہیں اور یقین جانیئے کہ ہم (اس بات میں) سچے ہیں۔ " WHERE ayatNumber = 83 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے (یعنی یعقوب نے) کہا (کہ یہ بات درست) نہیں (معلوم ہوتی) بلکہ (معلوم ہوتا ہے کہ) تمہارے نفسوں نے کوئی بات خوبصورت کرکے تمہیں دکھائی ہے۔ پس اب میرے لئے یہی رہ گیا ہے کہ اچھی طرح صبر کروں (کچھ) بعید نہیں کہ اللہ ان سب کو میرے پاس لے آئے۔ یقیناً وہی ہے جو خوب جاننے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 84 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس نے ان کی طرف سے اپنا رخ پھیر لیا اور (الگ جاکر دعا کی اور) کہا اے (میرے خدا)! یوسف کے واقعہ پر میں پھر فریاد کرتا ہوں۔ اور غم کی وجہ سے اس کی آنکھوں میں آنسو بھر آئے مگر وہ اپنے غم کو (ہمیشہ اپنے دل کے) اندر (ہی) دبائے رکھتا تھا۔ " WHERE ayatNumber = 85 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اللہ کی قسم (یوں معلوم ہوتا ہے کہ) آپ اس وقت تک یوسف کا ذکر کرتے رہیں گے جب تک آپ بیمار نہ پڑ جائیں یا فوت نہ ہو جائیں۔ " WHERE ayatNumber = 86 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا (کہ) میں اپنی پریشانی اور غم کی فریاد اللہ کے حضور کرتا ہوں اور میں اللہ کی طرف سے وہ علم رکھتا ہوں جو تم نہیں رکھتے۔ " WHERE ayatNumber = 87 AND chapterNumber = 12;
UPDATE verses SET ts = "اے میرے بیٹو! جائو اور (جاکر) یوسف اور اس کے بھائی کی جستجو کرو۔ اور اللہ کی رحمت سے ناامید مت ہو (اصل) بات یہی ہے کہ اللہ کی رحمت سے کافر لوگوں کے سوا کوئی (انسان) ناامید نہیں ہوتا۔ " WHERE ayatNumber = 88 AND chapterNumber = 12;
UPDATE verses SET ts = "پس جب وہ واپس ہو کر پھر) اس کے (یعنی یوسف کے) حضور حاضر ہوئے تو (اس سے) کہا (کہ) اے سردار! ہمیں اور ہمارے (تمام) کنبہ کو (سخت) تنگی پہنچی (ہوئی) ہے اور ہم (بالکل) تھوڑی سی پونجی لائے ہیں پس آپ (محض احسان کے طور پر) ہمیں (مطالبہ کے مطابق) غلہ دے دیں اور صدقہ (کے طور پر حق سے بھی کچھ زیادہ) دیں۔ اللہ صدقہ دینے والوں کو یقیناً (بڑا) اجر دیتا ہے۔ " WHERE ayatNumber = 89 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا (کہ) کیا تمہیں (اپنا وہ سلوک) معلوم ہے جو تم نے یوسف اور اس کے بھائی کے ساتھ کیا تھا جبکہ تم (اپنے فعل کی برائی سے) ناواقف تھے۔ " WHERE ayatNumber = 90 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا (کہ) کیا واقعہ میں آپ (ہی) یوسف ہیں؟ اس نے کہا (کہ) ہاں میں ہی یوسف ہوں اور یہ میرا بھائی ہے۔ اللہ نے ہم پر فضل کر دیا ہے حق بات یہی ہے کہ جو (شخص) تقویٰ اختیار کرے اور صبر کرے تو اللہ ایسے محسنوں کا اجر کبھی ضائع نہیں کرتا۔ " WHERE ayatNumber = 91 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا‘ ہمیں اللہ کی قسم ہے۔ اللہ نے آپ کو ہم پر یقیناً فضیلت دی ہے اور ہم بلاشبہ خطا کار تھے۔ " WHERE ayatNumber = 92 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا‘ اب تم پر کسی قسم کی ملامت نہ ہوگی (اور) اللہ (بھی) تمہیں بخش دے گا اور وہ سب رحم کرنے والوں سے بڑھ کر رحم کرنے والا ہے۔ " WHERE ayatNumber = 93 AND chapterNumber = 12;
UPDATE verses SET ts = "تم میرا یہ کرتہ لے جانا اور اسے میرے باپ کے سامنے (جا) رکھنا (اس سے) وہ (میرے متعلق) سب امور سے واقف ہو جائیں گے اور تم اپنا سارا کنبہ (بھی) میرے پاس لے آنا۔ " WHERE ayatNumber = 94 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب (ان کا) قافلہ (مصر سے) چل پڑا۔ تو ان کے باپ نے (لوگوں سے) کہا (کہ) ایسا نہ ہو کہ تم مجھے جھٹلانے لگو تو (میں ضرور کہوں گا کہ) مجھے یوسف کی خوشبو ضرور آرہی ہے۔ " WHERE ayatNumber = 95 AND chapterNumber = 12;
UPDATE verses SET ts = "انہوں نے کہا کہ تو یقیناً اپنی پرانی غلطی میں (پڑا ہوا) ہے۔ " WHERE ayatNumber = 96 AND chapterNumber = 12;
UPDATE verses SET ts = "پس جونہی کہ (یوسف کے مل جانے) کی بشارت دینے والا (شخص حضرت یعقوب کے پاس) آیا۔ اس نے اس (کرتے) کو اس کے سامنے رکھ دیا جس پر وہ ساری بات سمجھ گیا (اور ان سے) کہا کیا میں نے تم سے نہیں کہا تھا کہ میں اللہ کی طرف سے (علم پاکر یقیناً وہ کچھ) جانتا ہوں جو تم نہیں جانتے۔ " WHERE ayatNumber = 97 AND chapterNumber = 12;
UPDATE verses SET ts = "(تب) انہوں نے (یعنی حضرت یوسف کے بھائیوں نے) کہا اے ہمارے باپ! آپ ہمارے حق میں (خدا سے) ہمارے گناہوں کی بخشش طلب کریں ہم یقیناً خطاکار ہیں۔ " WHERE ayatNumber = 98 AND chapterNumber = 12;
UPDATE verses SET ts = "اس نے کہا‘ میں (ضرور) تمہارے لئے اپنے رب سے بخشش طلب کروں گا یقیناً وہی (ہے جو) بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 99 AND chapterNumber = 12;
UPDATE verses SET ts = "پھر جب وہ (سب) یوسف کے پاس پہنچے تو اس نے اپنے ماں باپ کو اپنے پاس جگہ دی اور (سب سے) کہا کہ اللہ کی مشیئت کے مطابق تم سب امن (اور سلامتی) کے ساتھ مصر میں داخل ہو جائو۔ " WHERE ayatNumber = 100 AND chapterNumber = 12;
UPDATE verses SET ts = "اور اس نے اپنے ماں باپ کو اپنے تخت پر بٹھایا اور وہ (سب) اس کی وجہ سے (خدا کا شکر کرتے ہوئے) سجدہ میں گر گئے اور اس نے (یعنی یوسف نے) کہا اے میرے باپ! یہ میرے پہلے سے (خواب میں) دیکھی ہوئی بات کی تعبیر ہے۔اللہ نے اسے پورا کر دیا ہے اور اس نے مجھ پر (بہت بڑا) فضل کیا ہے کیونکہ اس نے (پہلے) مجھے قیدخانہ سے نکالا اور (مجھے اس عزت کے مقام پر پہنچا کر اس کے بعد) وہ تمہیں جنگل (علاقہ) سے (نکال کر اس وقت میرے پاس) لایا جب شیطان نے مجھ میں اور میرے بھائیوں میں بگاڑ پیدا کر دیا تھا۔ میرا رب جس سے چاہتا ہے لطف (واحسان) کا معاملہ کرتا ہے۔ یقینا وہی (ہے جو) خوب جاننے والا (اور) حکمت والا ہے۔" WHERE ayatNumber = 101 AND chapterNumber = 12;
UPDATE verses SET ts = "اے میرے رب! تو نے مجھے حکومت کا ایک حصہ (بھی) عطا کیا ہے اور تعبیر الرئویا کا بھی کچھ علم تو نے مجھے بخشا ہے۔ (اے) آسمانوں اور زمین کو پیدا کرنے والے تو (ہی) دنیا اور آخرت (دونوں) میں میرا مددگار ہے (جب بھی میری موت کا وقت آئے) مجھے اپنی کامل فرمانبرداری کی حالت میں وفات دے اور صالحین (کی جماعت) کے ساتھ ملا دے۔ " WHERE ayatNumber = 102 AND chapterNumber = 12;
UPDATE verses SET ts = "(اے ہمارے رسول)! یہ (بیان) غیب کی خبروں میں سے ہے ہم اسے تجھ پر وحی (کے ذریعہ سے ظاہر) کرتے ہیں اور جب انہوں نے (یعنی تیرے دشمنوں نے تیرے خلاف) تدبیریں کرتے ہوئے آخر آپس میں اتفاق کر لیا تو تو (اس وقت) ان کے پاس (موجود) نہیں تھا۔ " WHERE ayatNumber = 103 AND chapterNumber = 12;
UPDATE verses SET ts = "اور خواہ تو (کتنا ہی) چاہے (کہ سب لوگ ہدایت پا جائیں) اکثر لوگ ہرگز ایمان نہیں لائیں گے۔ " WHERE ayatNumber = 104 AND chapterNumber = 12;
UPDATE verses SET ts = "اور تو اس (تبلیغ و تعلیم) کے متعلق ان سے کوئی اجر نہیں مانگتا۔ یہ تو تمام جہانوں (اور سب لوگوں) کے لئے سراسر شرف (کا موجب) ہے۔ " WHERE ayatNumber = 105 AND chapterNumber = 12;
UPDATE verses SET ts = "اور آسمانوں اور زمین میں بہت سے نشان (موجود) ہیں جن کے پاس سے یہ لوگ ان سے اعراض کرتے ہوئے گذر جاتے ہیں۔ " WHERE ayatNumber = 106 AND chapterNumber = 12;
UPDATE verses SET ts = "اور ان میں سے اکثر (لوگ) اللہ پر ایمان نہیں لاتے مگر اس حالت میں کہ وہ (ساتھ) ساتھ شرک بھی کرتے جاتے ہیں۔ " WHERE ayatNumber = 107 AND chapterNumber = 12;
UPDATE verses SET ts = "تو کیا یہ (لوگ) اس بات سے (محفوظ اور) بے خوف ہو گئے ہیں کہ ان پر اللہ کے عذابوں میں سے کوئی سخت عذاب آجائے یا اچانک ان پر وہ گھڑی آجائے (جس کی پہلے سے خبر دی جا چکی ہے) اور انہیں پتہ بھی نہ لگے۔ " WHERE ayatNumber = 108 AND chapterNumber = 12;
UPDATE verses SET ts = "تو کہہ (کہ) یہ میرا طریق ہے میں (تو) اللہ کی طرف بلاتا ہوں اور جنہوں نے (سچے طور پر) میری پیروی اختیار کی ہے (میں اور وہ سب) بصیرت پر قائم ہیں اور اللہ (سب قسم کے نقائص سے) پاک ہے اور میں مشرکوں میں سے نہیں ہوں۔ " WHERE ayatNumber = 109 AND chapterNumber = 12;
UPDATE verses SET ts = "اور تجھ سے پہلے (بھی) ہم (لوگوں کی رہنمائی کے لئے ہمیشہ) انہی (دنیا کی) بستیوں کے رہنے والے مردوں ہی کو جن پر ہم (اپنی) وحی نازل کرتے تھے رسالت دے کر بھیجتے رہے ہیں تو کیا یہ (لوگ) زمین میں نہیں پھرے تا دیکھتے (کہ) جو (لوگ) ان سے پہلے (انبیاء کے منکر) تھے ان کا انجام کیسا ہوا تھا؟ اور آخرت کا گھر ان لوگوں کے لئے جنہوں نے  تقویٰ اختیار کیا یقیناً زیادہ بہتر ہے پھر کیا تم عقل سے کام نہیں لیتے۔ " WHERE ayatNumber = 110 AND chapterNumber = 12;
UPDATE verses SET ts = "اور جب (ایک طرف تو) رسول (ان کی جانب سے) ناامید ہو گئے اور (دوسری طرف) ان (منکروں) کا (یہ) پختہ خیال ہو گیا کہ ان سے (وحی کے نام سے) جھوٹی باتیں کہی جا رہی ہیں تو (اس وقت) ان (رسولوں) کے پاس ہماری مدد آگئی۔ اور جنہیں ہم بچانا چاہتے تھے (انہیں) بچا لیا گیا۔ اور مجرم لوگوں سے ہمارا عذاب (ہرگز) نہیں ہٹایا جاتا۔ " WHERE ayatNumber = 111 AND chapterNumber = 12;
UPDATE verses SET ts = "ان (لوگوں) کے ذکر میں عقل مندوں کے لئے ایک عبرت (کا نمونہ موجود) ہے۔ یہ ایسی بات (ہرگز) نہیں ہے جو (اپنے پاس سے) گھڑی گئی ہو۔ بلکہ (یہ) اپنے سے پہلے (کلام الٰہی کی پیشگوئیوں) کو کامل طور پر پورا کرنے والی ہے اور ہر بات کی پوری تفصیل بیان کرنے والی ہے اور جو لوگ ایمان لاتے ہیں ان کے لئے ہدایت اور رحمت ہے۔ " WHERE ayatNumber = 112 AND chapterNumber = 12;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 13;
UPDATE verses SET ts = "میں اللہ جاننے والا اور دیکھنے والا ہوں۔ یہ کامل کتاب کی آیات ہیں۔ اور جو کلام تجھ پر تیرے رب کی طرف سے اتارا گیا ہے وہ بالکل حق ہے۔ لیکن اکثر لوگ ایمان نہیں لاتے۔ " WHERE ayatNumber = 2 AND chapterNumber = 13;
UPDATE verses SET ts = "اللہ وہ ہے جس نے آسمانوں کو بغیر ستونوں کے بلند کیا ہے۔ جیسا کہ تم دیکھ ہی رہے ہو (اور) پھر وہ عرش پر قائم ہوا ہے اور سورج اور چاند کو اس نے (تمہاری) خدمت پر لگا رکھا ہے (چنانچہ) ہر ایک (سیارہ) ایک معین میعاد تک (اپنی گردش کے مطابق) چل رہا ہے۔ وہ (اللہ) ہر امر کا انتظام کرتا ہے۔ (اور) اپنی آیات کو کھول کر بیان کرتا ہے تا کہ  تم (لوگ) اپنے رب سے ملنے کا یقین رکھو۔ " WHERE ayatNumber = 3 AND chapterNumber = 13;
UPDATE verses SET ts = "اور وہی ہے جس نے زمین کو پھیلایا ہے‘ اور اس میں استحکام کے ساتھ ٹھہرے رہنے والے پہاڑ اور (نیز) دریا بنائے ہیں اور اس میں تمام (اقسام) کے پھلوں سے دونوں قسمیں (یعنی نرومادہ) بنائی ہیں۔ وہ رات کو دن پر لا ڈالتا ہے۔ جو لوگ سوچتے ہیں ان کے لئے (بلاشک وشبہ) اس (بات) میں کئی نشان (پائے جاتے) ہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 13;
UPDATE verses SET ts = "اور زمین میں ایک دوسرے کے پاس پاس کئی (اقسام کے) قطعات ہیں اور کئی طرح کے انگوروں کے باغات اور (کئی قسم کی) کھیتی اور (طرح طرح کے) کھجور کے درخت (جن میں سے بعض) ایک ایک جڑ سے کئی کئی نکلنے والے (ہوتے ہیں) اور (بعض) ایک ایک جڑ سے کئی کئی نکلنے والوں کے خلاف (ایک ہی تنے کے ہوتے) ہیں۔ جنہیں ایک ہی (طرح کے) پانی سے سیراب کیا جاتا ہے اور (باوجود اس کے) پھل کے لحاظ سے ہم ان میں سے بعض (درختوں) کو بعض پر فضیلت دیتے ہیں۔ اس میں بھی ان لوگوں کے لئے جو عقل سے کام لیتے ہیں کئی نشان (موجود) ہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 13;
UPDATE verses SET ts = "اور (اے مخاطب) اگر تجھے (ان منکرین حق پر) تعجب آئے تو (وہ بجا ہے کیونکہ) ان کا (یہ) کہنا (کہ) کیا جب ہم مر کر مٹی ہو جائیں گے تو ہمیں واقعہ میں (پھر) کسی نئے جنم میں آنا ہوگا۔ تیری بات سے بھی زیادہ عجیب بات ہے یہ وہ لوگ ہیں جنہوں نے اپنے رب کا انکار کر دیا ہے اور یہ وہ لوگ ہیں جن کی گردنوں میں طوق (پڑے) ہوں گے۔ اور یہ (لوگ دوزخ کی) آگ (میں پڑنے) والے ہیں۔ وہ اس میں رہتے چلے جائیں گے۔ " WHERE ayatNumber = 6 AND chapterNumber = 13;
UPDATE verses SET ts = "اور وہ سزا کو نیک جزا سے جلد تر لانے کا تجھ سے مطالبہ کر رہے ہیں۔ حالانکہ ان سے پہلے (ایسے لوگوں پر) تمام (قسم کے) عبرتناک عذاب آچکے ہیں اور تیرا رب لوگوں کو ان کے ظلم کے باوجود (بھی) بلاشک و شبہ (بہت ہی) بخشنے والا ہے اور (اسی طرح) تیرا رب یقیناً سخت سزا دینے والا (بھی ہے)۔ " WHERE ayatNumber = 7 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جن لوگوں نے انکار کیا ہے وہ کہتے ہیں (کہ) اس (شخص) پر اس کے رب کی طرف سے کوئی نشان کیوں نہیں اتارا گیا (حالانکہ) تو صرف آگاہ (اور ہوشیار) کرنے والا ہے اور ہر ایک قوم کے لئے (خدا کی طرف سے) ایک راہنما (بھیجا جاچکا) ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 13;
UPDATE verses SET ts = "اللہ (خوب) جانتا ہے اسے (بھی) جو ہر مادہ اٹھاتی ہے اور جسے رحم ناقص کر (کے گرا) دیتے ہیں۔ اور (اسے بھی) جسے وہ بڑھاتے ہیں۔ اور ہر ایک چیز اس کے پاس ضرورت کے مطابق موجود ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 13;
UPDATE verses SET ts = "وہ غائب اور حاضر (دونوں) کا جاننے والا ہے۔ بڑے مرتبہ والا (اور) بڑی شان والا ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 13;
UPDATE verses SET ts = "جو تم میں سے بات چھپاتا ہے اور وہ بھی جو اسے ظاہر کرتا ہے (اس کے علم کے لحاظ سے دونوں) برابر ہیں۔ نیز وہ بھی جو رات کو چھپ رہتا ہے اور جو دن کو چلتا ہے۔ " WHERE ayatNumber = 11 AND chapterNumber = 13;
UPDATE verses SET ts = "اس کی طرف سے اس (رسول) کے آگے بھی اور اس کے پیچھے بھی (ایک دوسرے کے ساتھ ساتھ) آنے والی (ملائکہ کی) ایک جماعت (حفاظت کے لئے) مقرر ہے جو اللہ کے حکم سے اس کی حفاظت کر رہی ہے۔ اللہ کبھی بھی کسی قوم کی حالت کو نہیں بدلتا جب تک کہ وہ اپنی اندرونی حالت کو نہ بدلے اور جب اللہ کسی قوم کے متعلق عذاب کا فیصلہ کر لیتا ہے تو اس (عذاب) کو ہٹانے والا کوئی نہیں ہوتا اور اس کے سوا ان کا اور کوئی (بھی) مددگار نہیں (ہوسکتا۔) " WHERE ayatNumber = 12 AND chapterNumber = 13;
UPDATE verses SET ts = "وہی ہے جو تمہیں بجلی (کی چمک) دکھاتا ہے۔ خوف کے لئے (بھی) اور طمع کے لئے (بھی) اور بھاری بادل اٹھاتا ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 13;
UPDATE verses SET ts = "اور کڑک اس کی تعریف کے ساتھ (ساتھ) اس کی پاکیزگی کا اظہار بھی کرتی ہے اور فرشتے بھی اس کے خوف کے سبب سے (ایسا ہی کرتے ہیں) اور گرنے والی بجلیاں بھی بھیجتا ہے پھر جن پر چاہتا ہے انہیں گراتا ہے اور وہ اللہ کے بارہ میں جھگڑ رہے ہیں۔ حالانکہ وہ سخت عذاب دینے والا ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 13;
UPDATE verses SET ts = "نہ ٹلنے والا بلاوا اسی کا ہے۔ اور جنہیں وہ اس کے سوا پکارتے ہیں۔ وہ ان (کی دعا) کا کوئی جواب نہیں دیتے۔ (ہاں) مگر (ان کا فعل) اس (شخص) کی طرح ہے جو اپنے دونوں ہاتھ پانی کی طرف پھیلا رہا ہو۔ تا وہ اس کے منہ تک پہنچ جائے‘ لیکن وہ (پانی) اس تک کبھی نہیں پہنچے گا اور کافروں کی (چیخ اور) پکار ضائع ہو جائے گی۔ " WHERE ayatNumber = 15 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جو (وجود) آسمانوں میں ہیں یا زمین میں ہیں اور ان کے سائے بھی خوشی سے یا کراہت سے (ہر) صبح اور شام اللہ ہی کو سجدہ کرتے ہیں۔ " WHERE ayatNumber = 16 AND chapterNumber = 13;
UPDATE verses SET ts = "تو ان سے کہہ (کہ بتائو) آسمانوں اور زمین کا رب کون ہے؟ (اس کا جواب وہ تو کیا دیں گے) تو (خود ہی) کہہ دے (کہ) اللہ‘ (اور پھر تو ان سے) کہہ‘ کیا پھر (بھی) تم نے اس کے سوا (اپنے) ایسے مددگار تجویز کر رکھے ہیں جو خود اپنے لئے (بھی) کسی نفع (کو حاصل کرنے) کی قدرت نہیں رکھتے۔ اور نہ کسی نقصان کو روکنے کی (اور ان سے) کہہ (کہ) کیا اندھا اور دیکھنے والا برابر (ہوسکتا) ہے؟ یا کیا تاریکی اور روشنی برابر (ہوسکتی) ہے؟ کیا انہوں نے اللہ کے ایسے شریک تجویز کئے ہیں جنہوں نے اس کی طرح (کچھ) مخلوق پیدا کی ہے۔ جس کی وجہ سے (اس کی اور دوسروں کی) مخلوق ان کے لئے مشتبہ ہو گئی ہے تو ان سے کہہ (کہ) اللہ (ہی) ہر ایک چیز کا خالق ہے اور وہ کامل طور پر یکتا (اور ہر ایک چیز پر) کامل اقتدار رکھنے والا ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 13;
UPDATE verses SET ts = "اس نے آسمان سے کچھ پانی اتارا‘ پھر (اس سے) کئی وادیاں اپنی (اپنی) گنجائش کے مطابق بہ نکلیں‘ اور اس سیلاب نے اوپر آجانے والی جھاگ کو اٹھالیا اور جس (دھات) کو وہ کسی زیور یا اور کسی گھریلو استعمال کے سامان بنانے کے لئے آگ میں تپاتے ہیں اس میں (بھی) اس (سیلاب) جیسا ایک جھاگ (ہوتا)ہے اسی طرح اللہ حق اور باطل (کے فرق) کو بیان کرتا ہے۔ پھر جھاگ تو پھینکا جاکر تباہ ہو جاتا ہے‘ اور جو چیز لوگوں کو نفع دینے والی ہوتی ہے۔ وہ زمین میں ٹھہری رہتی ہے۔ اللہ تمام باتوں کو اسی طرح (کھول کر) بیان کرتا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 13;
UPDATE verses SET ts = "جنہوں نے اپنے رب کا کہا مانا ان کے لئے کامیابی ہے اور جنہوں نے اس کا کہا نہیں مانا (ان کی یہ حالت ہوگی کہ) جو کچھ بھی زمین میں ہے اگر (سب) ان کا ہوتا اور اس کے برابر اور بھی‘ تو وہ اس سب مال کو دیکر (اپنے آپ کو عذاب سے) چھڑانے کی کوشش کرتے ان کے لئے برے انجام والا حساب (مقدر) ہے اور ان کا ٹھکانا جہنم ہے اور وہ رہنے کے لحاظ سے بہت بری (جگہ) ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 13;
UPDATE verses SET ts = "جو شخص جانتا ہے کہ جو (کلام) تمہارے رب کی طرف سے تمہاری طرف اتارا گیا ہے وہ بالکل حق ہے کیا وہ اس شخص جیسا (ہوسکتا) ہے جو اندھا ہے (نہیں‘ کیونکہ) عقل رکھنے والے ہی نصیحت پکڑتے ہیں۔ " WHERE ayatNumber = 20 AND chapterNumber = 13;
UPDATE verses SET ts = "ایسے لوگ جو اللہ کے (ساتھ کئے ہوئے) عہد کو پورا کرتے ہیں اور اس پختہ عہد کو نہیں توڑتے۔ " WHERE ayatNumber = 21 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جو (لوگ) ان تعلقات کو ہمیشہ قائم رکھتے ہیں جن کے قائم رکھنے کا اللہ نے حکم دیا ہے اور اپنے رب سے ڈرتے ہیں اور برے (انجام والے) حساب سے خوف رکھتے ہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جنہوں نے اپنے رب کی رضا کی طلب میں ثابت قدمی سے کام لیا ہے اور نماز کو عمدگی سے ادا کیا ہے اور جو کچھ ہم نے انہیں دیا ہے اس میں سے چھپ کر (بھی) اور ظاہر (بھی ہماری راہ میں) خرچ کیا ہے اور (جو) بدی کو نیکی کے ذریعہ سے دور کرتے (رہتے) ہیں‘ انہی کے لئے اس گھر کا (بہترین) انجام (مقدر) ہے۔ " WHERE ayatNumber = 23 AND chapterNumber = 13;
UPDATE verses SET ts = "یعنی مستقل رہائش کے باغات جن میں وہ (خود بھی) داخل ہوں گے اور ان کے بڑوں اور ان کی بیویوں اور ان کی نسلوں میں سے بھی۔ (وہ لوگ) جنہوں نے نیکی اختیار کی ہوگی (اس میں داخل ہوں گے) اور فرشتے ہر دروازہ سے ان کے پاس آئیں گے۔ " WHERE ayatNumber = 24 AND chapterNumber = 13;
UPDATE verses SET ts = "(اور کہیں گے) تمہارے لئے سلامتی ہے کیونکہ تم ثابت قدم رہے پس (اب دیکھو کہ تمہارے لئے) اس گھر کا کیا ہی اچھا انجام ہے۔ " WHERE ayatNumber = 25 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جو اللہ کے (ساتھ کیے ہوئے) پختہ عہد کو پکے اقرار کے بعد توڑتے ہیں اور جس تعلق کے قائم کرنے کا اللہ نے حکم دیا تھا اسے پارہ پارہ کرتے ہیں اور زمین میں فساد کرتے ہیں ان کے لئے (اللہ کی طرف سے) لعنت مقدر ہے اور ان کے لئے ایک برا گھر (بھی) مقرر ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 13;
UPDATE verses SET ts = "اللہ جس کے لئے پسند کرتا ہے (اس کے لئے) رزق کو فراخ کر دیتا ہے اور (جس کے لئے چاہتا ہے اس کے لئے) تنگ کر دیتا ہے اور یہ (لوگ) اس ورلی زندگی پر (ہی) خوش ہوگئے ہیں۔ حالانکہ یہ ورلی زندگی آخرت کے مقابلہ میں محض ایک وقتی سامان ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جن لوگوں نے (تمہارا) انکار کیا ہے وہ کہتے ہیں کہ اس پر اس کے رب کی طرف سے کوئی نشان کیوں نہیں اتارا گیا۔ تو کہہ اللہ جسے چاہتا ہے ہلاک کر دیتا ہے اور جو (اس کی طرف) مائل ہو اس کی اپنی طرف رہنمائی کرتا ہے۔" WHERE ayatNumber = 28 AND chapterNumber = 13;
UPDATE verses SET ts = "یعنی جو ایمان لائے ہوں اور ان کے دل اللہ کی یاد سے اطمینان پاتے ہوں (ان کو ہدایت دیتا ہے) پس سمجھ لو کہ اللہ کی یاد ہی سے دل اطمینان پاتے ہیں۔ " WHERE ayatNumber = 29 AND chapterNumber = 13;
UPDATE verses SET ts = "جو لوگ ایمان لائے ہیں اور انہوں نے نیک (اور مناسب حال) عمل کئے ہیں ان کے لئے (بڑی) قابل رشک حالت اور بہترین واپسی کی جگہ (مقدر) ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 13;
UPDATE verses SET ts = "اسی (قسم کا انجام پانے کے) لئے ہم نے تجھے ایک ایسی قوم میں جس سے پہلے کئی قومیں (آنے والے کی راہ دیکھتی) گذر چکی تھیں بھیجا ہے تا کہ  جو (کلام) ہم نے تیری طرف وحی کیا ہے تو وہ انہیں پڑھ کر سنائے کیونکہ وہ رحمن (کے فیضان) کا انکار کر رہے ہیں تو کہہ یہ میرا رب ہے اس کے سوا کوئی معبود نہیں اسی پر میں نے بھروسا کیا ہے اور اسی کی طرف ہر آن میرا رجوع ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 13;
UPDATE verses SET ts = "اور اگر کوئی ایسا قرآن ہو جس کے ذریعہ سے (نشان کے طور پر) پہاڑوں کو (ان کی جگہ سے ہٹا کر) چلایا گیا ہو یا اس کے ذریعہ سے زمین کو ٹکڑے (ٹکڑے) کیا گیا ہو یا اس کے ذریعہ سے مردوں سے باتیں کی گئی ہوں (تو کیا یہ لوگ اس پر ایمان لے آئیں گے؟ ہرگز نہیں) بلکہ (ایمان لانے کا) فیصلہ پورے طور پر اللہ کے ہاتھ میں ہے۔ کیا جو (لوگ) ایمان لائے ہیں انہیں (اب تک) معلوم نہیں ہوا کہ اگر اللہ چاہتا تو سب لوگوں کو ہدایت دے دیتا اور (اے رسول)! جن لوگوں نے تمہارا انکار کیا ہے ان کے اس عمل کی وجہ سے ہمیشہ کوئی (نہ کوئی) سخت آفت ان پر آتی رہے گی۔ یا ان کے گھر کے قریب نازل ہوتی رہے گی یہاں تک کہ اللہ کا (آخری) وعدہ (یعنی فتح مکہ) آجائے گا۔ اللہ اس وعدہ کے خلاف ہرگز نہیں کرے گا۔ " WHERE ayatNumber = 32 AND chapterNumber = 13;
UPDATE verses SET ts = "اور تجھ سے پہلے رسولوں سے (بھی) استہزا کیا گیا تھا جس پر میں نے ان لوگوں کو جنہوں نے انکار کیا تھا (کچھ عرصہ تک کے لئے) ڈھیل دی پھر میں نے ان کو تباہ کر دیا۔ اب دیکھو میری سزا کیسی (سخت) تھی۔ " WHERE ayatNumber = 33 AND chapterNumber = 13;
UPDATE verses SET ts = "تو کیا وہ (خدا) جو ہر ایک شخص کے اعمال پر نگران ہے (ان سے نہ پوچھے گا) اور انہوں نے تو اللہ کے کئی ایک شریک (بھی) بنائے (ہوئے) ہیں (ان سے) کہو۔ تم ان (بناوٹی خدائوں) کے نام (تو) بتائو۔ یا کیا تم (لوگ) اس (خدا) کو کوئی ایسی بات بتائو گے جو زمین پر موجود تو ہے لیکن وہ (اسے) نہیں جانتا۔ یا تم صرف منہ سے یہ باتیں کرتے ہو بلکہ جن لوگوں نے انکار کیا ہے ان کو ان کی (اپنی ہی) فریب کاری خوبصورت (شکل میں) دکھائی گئی ہے اور انہیں درست راستہ سے ہٹا دیا گیا ہے اور جسے اللہ ہلاک کرے اسے راہ دکھانے والا کوئی نہیں (مل سکتا) ۔" WHERE ayatNumber = 34 AND chapterNumber = 13;
UPDATE verses SET ts = "ان کے لئے ایک عذاب (تو) اس ورلی زندگی میں (ہی مقدر) ہے۔ اور آخرت کا عذاب یقیناً اور (بھی) سخت ہوگا۔ اور انہیں اللہ (کے عذاب) سے کوئی بھی بچانے والا نہیں ہوگا۔ " WHERE ayatNumber = 35 AND chapterNumber = 13;
UPDATE verses SET ts = "اس جنت کا مثالی بیان جس کا پرہیز گاروں کو وعدہ دیا گیا ہے (یہ ہے کہ) اس کے (درختوں کے سایہ) تلے نہریں بہتی ہوں گی اس کا پھل بھی ہمیشہ رہنے والا ہوگا اور اس کا سایہ (بھی) یہ ان (لوگوں) کا انجام ہوگا جنہوں نے تقویٰ اختیار کیا اور انکار کرنے والوں کا انجام (دوزخ کی) آگ ہے۔ " WHERE ayatNumber = 36 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جن لوگوں کو ہم نے کتاب دی ہے وہ اس (کلام الٰہی) سے جو تجھ پر نازل کیا گیا ہے خوش ہوتے ہیں اور ان (مختلف) گروہوں میں سے (بعض) ایسے (بھی) ہیں جو اس کے بعض (حصہ) کا انکار کرتے ہیں۔ تم کہو‘ مجھے (تو) یہی حکم دیا گیا ہے کہ میں اللہ کی عبادت کروں اور (کسی کو) اس کا شریک نہ ٹھہرائوں۔ میں اس کی طرف (تم) کو بلاتا ہوں اور اسی طرف میں (بھی) رجوع کرتا ہوں۔ " WHERE ayatNumber = 37 AND chapterNumber = 13;
UPDATE verses SET ts = "اور اسی طرح ہم نے اسے ایک مفصل حکم کی صورت میں اتارا ہے اور (اے مخاطب) اگر تو نے اس علم کے بعد جو تجھے حاصل ہو چکا ہے ان (کفار) کی خواہشات کی پیروی کی‘ تو اللہ کے مقابلہ میں نہ (تو) تیرا کوئی دوست ہوگا اور نہ کوئی بچانے والا (ہوگا) ۔" WHERE ayatNumber = 38 AND chapterNumber = 13;
UPDATE verses SET ts = "اور ہم نے تجھ سے پہلے (بھی) یقیناً کئی  رسول بھیجے تھے اور انہیں بیویاں اور بچے بھی دئیے تھے اور کسی رسول کے لئے ممکن نہ تھا کہ وہ اللہ کے اذن کے سوا (اپنی قوم کے پاس) کوئی نشان لاتا۔ ہر ایک پروگرام کے لئے ایک میعاد مقرر ہے۔ " WHERE ayatNumber = 39 AND chapterNumber = 13;
UPDATE verses SET ts = "جس چیز کو اللہ چاہتا ہے مٹاتا ہے اور (جسے چاہتا ہے) قائم کرتا ہے اور اسی کے پاس (تمام) احکام کی اصل (اور جڑ) ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جس (عذاب کے بھیجنے) کا ہم ان سے وعدہ کرتے ہیں اگر ہم اس کا کوئی حصہ تیرے سامنے بھیج کر تجھے دکھا دیں (تو تو بھی ان کا انجام دیکھ لے گا) اور (اگر) ہم (اس گھڑی سے پہلے) تجھے وفات دے دیں (تو تجھے مابعدالموت اس کی حقیقت معلوم ہو جائے گی کیونکہ) تیرے ذمہ (ہمارے حکم اور پیغام کا) صرف پہنچا دینا ہے اور ان کا حساب لینا ہمارے ذمہ ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 13;
UPDATE verses SET ts = "اور کیا انہوں  نے دیکھا نہیں کہ ہم ملک کو اس کی تمام اطراف سے کم کرتے چلے آرہے ہیں۔ اور فیصلہ (تو) اللہ کرتا ہے۔ کوئی اس کے فیصلہ کو تبدیل کرنے والا نہیں۔ اور وہ جلد حساب لینے والا ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جو لوگ ان سے پہلے تھے‘ انہوں نے (بھی انبیاء کے خلاف) تدبیریں کی تھیں (مگر ان کی کوئی پیش نہ گئی) پس تدبیر کرنا تو کلی طور پر اللہ ہی کے اختیار میں ہے۔ ہر شخص جو کچھ (بھی) کرتا ہے وہ (اللہ) اسے جانتا ہے اور ان کافروں کو جلد ہی معلوم ہو جائے گا کہ اس آنے والے گھر کا (اچھا) انجام کس کے لئے ہے۔ " WHERE ayatNumber = 43 AND chapterNumber = 13;
UPDATE verses SET ts = "اور جن لوگوں نے تیرا انکار کیا ہے وہ کہتے ہیں کہ تو خدا کا بھیجا ہوا نہیں ہے تو انہیں کہہ (کہ) اللہ میرے درمیان اور تمہارے درمیان کافی گواہ ہے۔ اور (اسی طرح پر) وہ (شخص بھی گواہ) ہے جس کے پاس اس (مقدس) کتاب کا علم (آچکا) ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 13;
UPDATE verses SET ts = "(میں) اللہ کا نام لیکر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 14;
UPDATE verses SET ts = "میں اللہ دیکھنے والا ہوں۔ (یہ) ایک کتاب ہے جسے ہم نے تجھ پر اس لئے اتارا ہے کہ تو تمام لوگوں کو ان کے رب کے حکم سے ظلمات سے نکال کر نور کی طرف لے آئے۔ یعنی غالب اور تعریفوں والے خدا کے راستہ کی طرف۔ " WHERE ayatNumber = 2 AND chapterNumber = 14;
UPDATE verses SET ts = "(وہ غالب اور تعریفوں والا خدا) اللہ (ہی ہے) (اور) جو کچھ آسمانوں میں ہے اور جو زمین میں ہے سب اس کا ہے اور (اس کا) انکار کرنے والوں کے لئے ایک سخت عذاب (مقدر ہے) ۔" WHERE ayatNumber = 3 AND chapterNumber = 14;
UPDATE verses SET ts = "جو (انکار کرنے والے) آخرت کے مقابلہ میں اس ورلی زندگی کو ترجیح دیتے ہیں اور (دوسرے لوگوں کو بھی) اللہ کے راستہ سے روکتے ہیں اور اسے کجی اختیار کرکے (حاصل کرنا) چاہتے ہیں۔ یہ لوگ دور کی گمراہی میں (پڑے ہوئے) ہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 14;
UPDATE verses SET ts = "اور ہر ایک رسول کو ہم نے اس کی قوم کی زبان میں ہی (وحی دے کر) بھیجا ہے تا کہ وہ انہیں (ہماری باتیں) کھول کر بتائے۔ پھر (اس تدبیر کے بعد) اللہ جسے (ہلاک کرنا) چاہتا ہے ہلاک کرتا ہے اور جسے (کامیاب کرنا) چاہتا ہے اسے منزل مقصود پر پہنچا دیتا ہے اور وہ کامل طور پر غالب (اور) صاحب حکمت ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 14;
UPDATE verses SET ts = "اور (تجھ سے پہلے) ہم نے موسیٰ کو (بھی) اپنے نشانات کے ساتھ یہ (حکم دے کر) بھیجا تھا کہ اپنی قوم کو ظلمات سے نکال کر نور کی طرف لا۔ اور انہیں اللہ کے انعام اور اس کے عذاب یاد دلا۔ (کیونکہ) بلاشبہ اس میں ہر ایک پورے صابر (اور) پورے شکر گذار کے لئے کئی نشان (پائے جاتے) ہیں۔ " WHERE ayatNumber = 6 AND chapterNumber = 14;
UPDATE verses SET ts = "اور (اے مخاطب تو اس وقت کو بھی یاد کر) جب موسیٰ نے اپنی قوم سے کہا تھا (کہ اے میری قوم)! تم اپنے آپ پر اللہ کا (اس وقت کا) انعام یاد کرو جب اس نے تمہیں فرعون کے ساتھیوں سے اس حالت میں بچایا تھا کہ وہ تمہیں سخت عذاب دیتے تھے تمہارے بیٹوں کو مار دیتے تھے اور تمہاری عورتوں کوزندہ رکھتے تھے اور اس میں تمہارے رب کی طرف سے (تمہارے لئے) بڑا (بھاری) امتحان تھا۔ " WHERE ayatNumber = 7 AND chapterNumber = 14;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جب تمہارے رب نے (انبیاء کے ذریعہ سے) اعلان کیا تھا کہ (اے لوگو)! اگر تم شکر گذار بنے تو میں تمہیں اور بھی زیادہ دوں گا اور اگر تم نے ناشکری کی تو (یاد رکھو) میرا عذاب یقیناً سخت (ہوا کرتا) ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 14;
UPDATE verses SET ts = "اور موسیٰ نے (اپنی قوم سے یہ بھی) کہا تھا (کہ) اگر تم اور جو (دوسرے لوگ) زمین میں بستے ہیں سب (کے سب) کفر اختیار کر لو۔ تو (اس میں خدا کا کوئی نقصان نہیں ہو سکتا کیونکہ) اللہ یقیناً بے نیاز (اور) بہت ہی تعریفوں والا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 14;
UPDATE verses SET ts = "کیا جو لوگ تم سے پہلے تھے یعنی نوح کی قوم اور عاد اور ثمود اور جو ان کے بعد ہوئے ان کی نسبت دلوں کو ہلا دینے والی خبر تمہیں نہیں پہنچی (وہ ایسے نابود ہوئے اور مٹائے گئے کہ) اللہ کے سوا (اب) انہیں کوئی (بھی) نہیں جانتا۔ (جب) ان کے پاس ان کے رسول (ہمارے) روشن نشان لیکر آئے‘ تو انہوں نے ان کی بات نہ مانی اور کہا (کہ) جس (تعلیم) کے ساتھ تمہیں بھیجا گیا ہے اس کا (تو) ہم انکار کر چکے ہیں اور جس بات کی طرف تم ہمیں بلاتے ہو اس کے متعلق ہم ایک بے چین کر دینے والے شک میں (پڑے ہوئے) ہیں۔ " WHERE ayatNumber = 10 AND chapterNumber = 14;
UPDATE verses SET ts = "ان کے پیغمبروں نے (انہیں) کہا (کہ) کیا تمہیں اللہ کے متعلق کوئی شک  ہے جو آسمانوں اور زمین کو پیدا کرنے والا ہے وہ تمہیں اس لئے بلا رہا ہے تا کہ وہ تمہارے گناہوں میں سے بعض بخش دے اور ایک مقررہ میعاد تک تمہیں تاخیر دے انہوں نے کہا (کہ) تم (تو) ہماری ہی طرح کے انسان ہو۔ تم چاہتے ہو کہ جس چیز کی ہمارے باپ دادا پرستش کرتے چلے آئے ہیں اس سے ہمیں ہٹا دو۔ پس (اگر اس معاملہ میں تم حق پر ہو تو) ہمارے پاس کوئی روشن نشان لائو۔ " WHERE ayatNumber = 11 AND chapterNumber = 14;
UPDATE verses SET ts = "ان کے پیغمبروں نے انہیں کہا (کہ یہ سچ ہے کہ) ہم تمہاری (ہی) طرح کے بشر ہیں لیکن (ساتھ ہی یہ بھی سچ ہے کہ) اللہ اپنے بندوں میں سے جس پر چاہتا ہے (خاص) احسان کرتا ہے اور یہ بات ہمارے اختیار میں نہیں ہے کہ اللہ کے حکم کے سوا تمہارے پاس کوئی نشان لائیں اور مومنوں کو اللہ پر ہی توکل رکھنا چاہیئے۔ " WHERE ayatNumber = 12 AND chapterNumber = 14;
UPDATE verses SET ts = "اور ہمیں (ہوا) کیا ہے کہ ہم اللہ پر توکل نہ کریں‘ حالانکہ اس نے ہمارے (مناسب حال) راستے ہمیں دکھائے ہیں اور جو دکھ تم نے ہمیں دے رکھا ہے اس پر ہم یقیناً صبر کرتے چلے جائیں گے اور بھروسا کرنے والوں کو تو اللہ پر ہی بھروسہ کرنا چاہیے۔ " WHERE ayatNumber = 13 AND chapterNumber = 14;
UPDATE verses SET ts = "اور جن لوگوں نے کفر اختیار کیا۔ انہوں نے اپنے (اپنے زمانہ کے) پیغمبروں سے کہا کہ ہم تمہیں ضرور اپنے ملک سے نکال دیں گے یا تم (مجبور ہو کر) ہمارے مذہب میں واپس آجائو گے (تو ان تکلیفوں سے بچ سکو گے) جس پر ان کے رب نے ان پر وحی نازل کی (کہ) ہم ان ظالموں کو یقیناً ہلاک کر دیں گے۔ " WHERE ayatNumber = 14 AND chapterNumber = 14;
UPDATE verses SET ts = "اور ان (کی ہلاکت) کے بعد اس ملک میں ضرور تمہیں آباد کر دیں گے۔ یہ (وعدہ) اس کے حق میں ہے جو میرے مقام سے ڈرے اور نیز میری انذاری پیشگوئیوں سے ڈرے۔ " WHERE ayatNumber = 15 AND chapterNumber = 14;
UPDATE verses SET ts = "اور انہوں نے (اپنی) فتح کے لئے دعا کی اور (نتیجہ یہ ہوا کہ) ہر ایک سرکش (اور) حق کا دشمن ناکام رہا۔ " WHERE ayatNumber = 16 AND chapterNumber = 14;
UPDATE verses SET ts = "اس (دنیوی عذاب) کے بعد (اس کے لئے) جہنم (کا عذاب بھی مقدر) ہے اور (وہاں) اسے تیز گرم پانی پلایا جائے گا۔ " WHERE ayatNumber = 17 AND chapterNumber = 14;
UPDATE verses SET ts = "وہ اسے تھوڑا تھوڑا کرکے پیئے گا اور اسے آسانی سے نگل نہیں سکے گا اور ہر جگہ (اور ہر طرف سے) اس پر موت آئے گی اور وہ مرے گا نہیں اور اس کے علاوہ بھی (اس کے لئے) ایک سخت عذاب (مقرر) ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 14;
UPDATE verses SET ts = "ان لوگوں کی مثال جنہوں نے اپنے رب (کے احکام) کا انکار کیا ہے (یہ ہے کہ) انکے اعمال اس راکھ کی طرح ہیں جسے ایک تیز آندھی والے دن ہوا تیزی سے (اڑا) لے گئی ہو جو کچھ انہوں نے (اپنے مستقبل کے لئے) کمایا ہے۔ اس میں سے کوئی حصہ (بھی) ان کے ہاتھ نہیں آئے گا۔ یہی پرلے درجہ کی تباہی ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 14;
UPDATE verses SET ts = "(اے مخاطب)! کیا تو نے دیکھا نہیں کہ اللہ نے آسمانوں اور زمین کو حق و حکمت کے ساتھ پیدا کیا ہے اگر وہ چاہے تو تمہیں ہلاک کر دے اور (تمہاری جگہ پر) کوئی اور نئی مخلوق لے آئے۔ " WHERE ayatNumber = 20 AND chapterNumber = 14;
UPDATE verses SET ts = "اور یہ بات اللہ کے لئے کوئی مشکل نہیں ہے۔ " WHERE ayatNumber = 21 AND chapterNumber = 14;
UPDATE verses SET ts = "اور وہ سب اللہ کے حضور آکھڑے ہوں گے‘ تب (ان میں سے) کمزور (سمجھے جانے والے) تکبر کرنے والوں کو کہیں گے(کہ) ہم تو تمہارے پیچھے چلنے والے تھے۔ پس کیا تم اللہ کے عذاب میں سے (اس وقت) کچھ ہم سے دور کر سکتے ہو۔ وہ (جواب میں) کہیں گے کہ اگر اللہ ہمیں ہدایت دیتا تو ہم (بھی) تمہیں ہدایت دیتے (لیکن اب کیا ہو سکتا ہے) ہمارا بے صبری دکھانا یا ہمارا صبر کرنا (اس وقت) ہمارے لئے یکساں ہے‘ (اور) ہمارے لئے بچائو کی کوئی صورت نہیں ہے۔ " WHERE ayatNumber = 22 AND chapterNumber = 14;
UPDATE verses SET ts = "اور جب تمام معاملہ کا فیصلہ کیا جا چکے گا تو شیطان (لوگوں سے) کہے گا (کہ) اللہ نے یقیناً تم سے اٹل وعدہ کیا تھا اور میں نے (بھی) تم سے (ایک) وعدہ کیا تھا مگر میں نے وہ (وعدہ) تم سے پورا نہ کیا اور میرا تم پر کوئی تسلط نہ تھا ہاں میں نے تمہیں اپنی طرف بلایا اور تم نے میرا کہنا مان لیا اس لئے (اب) مجھے ملامت نہ کرو بلکہ اپنے آپ کو ملامت کرو۔ (اس وقت) نہ میں تمہاری فریاد سن سکتا ہوں  اور نہ تم میری فریاد سن سکتے ہو۔ تم نے جو مجھے اللہ کا شریک بنا رکھا تھا۔ میں تمہاری اس بات کا پہلے سے انکار کر چکا ہوں (اس پر خدا فرماتا ہے اس قسم کا شرک کرنے والے) ظالموں کے لئے یقیناً دردناک عذاب (مقدر) ہے۔ " WHERE ayatNumber = 23 AND chapterNumber = 14;
UPDATE verses SET ts = "اور جو لوگ ایمان لائے ہوں گے اور انہوں نے (نیک اور) مناسب حال عمل کیے ہونگے انہیں ان کے رب کے حکم سے ایسے باغوں میں جن کے (سایوں کے) نیچے نہریں بہتی ہوں گی داخل کیا جائے گا (اور) وہ ان (جنتوں) میں بستے چلے جائیں گے اور وہاں ان کی (ایک دوسرے کے لئے یہ) دعا ہوگی (کہ تم پر) سلامتی (ہو) ۔" WHERE ayatNumber = 24 AND chapterNumber = 14;
UPDATE verses SET ts = "(اے مخاطب)! کیا تو نے دیکھا نہیں (کہ) اللہ نے کس طرح ایک کلام پاک کے متعلق حقیقت حال کو بیان کیا ہے وہ ایک پاک درخت کی طرح ہوتا ہے جس کی جڑ (مضبوطی کے ساتھ) قائم ہوتی ہے اور اس کی (ہر ایک) شاخ آسمان کی بلندی میں (پہنچی ہوتی) ہے۔ " WHERE ayatNumber = 25 AND chapterNumber = 14;
UPDATE verses SET ts = "وہ ہر وقت اپنے رب کے اذن سے اپنا (تازہ) پھل دیتا ہے اور اللہ لوگوں کے لئے (ان کی ضرورت کی) تمام باتیں بیان کرتا ہے تا کہ وہ نصیحت حاصل کریں۔ " WHERE ayatNumber = 26 AND chapterNumber = 14;
UPDATE verses SET ts = "اور بری بات کا حال برے درخت کی طرح ہے جس کو زمین سے اکھاڑ (کر پھینک) دیا گیا ہو (اور) جسے (کہیں بھی) قرار (حاصل) نہ ہو۔ " WHERE ayatNumber = 27 AND chapterNumber = 14;
UPDATE verses SET ts = "جو لوگ ایمان لائے ہیں انہیں اللہ اس قائم رہنے والی (اور پاک) بات کے ذریعہ سے (اس) ورلی زندگی میں (بھی) ثبات بخشتا ہے اور آخرت (کی زندگی) میں بھی (بخشے گا) اور ظالموں کو اللہ ہلاک کرتا ہے اور اللہ جو چاہتا ہے کرتا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 14;
UPDATE verses SET ts = "(اے مخاطب)! کیا تو نے ان لوگوں (کی حالت) کو (غور کی نظر سے) نہیں دیکھا جنہوں نے ناشکری سے اللہ کی نعمت کو بدل ڈالا (اور آپ بھی ہلاک ہوئے) اور اپنی قوم کو (بھی) ہلاکت کے گھر میں (لا) اتارا۔ " WHERE ayatNumber = 29 AND chapterNumber = 14;
UPDATE verses SET ts = "یعنی جہنم میں‘ وہ اس میں داخل ہوں گے اور وہ جگہ (رہنے کے لحاظ سے) بہت بری ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 14;
UPDATE verses SET ts = "اور انہوں نے اللہ کے ہم مرتبہ شریک بنا لیے ہیں تا (لوگوں کو) اس کی راہ سے برگشتہ کریں۔ تو (انہیں) کہہ (کہ اچھا‘ کچھ دن) عارضی فائدہ اٹھالو۔ پھر تمہیں یقیناً (دوزخ کی) آگ کی طرف جانا ہوگا۔ " WHERE ayatNumber = 31 AND chapterNumber = 14;
UPDATE verses SET ts = "(اے رسول)! میرے ان بندوں سے جو ایمان لا چکے ہیں کہہ کہ وہ اس دن کے آنے سے پہلے جس میں نہ کوئی بیع (وشرائ) ہوگی اور نہ ہی کوئی گہری دوستی۔ نماز کو عمدگی سے ادا کیا کریں اور جو کچھ ہم نے انہیں دیا ہے اس میں سے پوشیدگی میں (بھی) اور ظاہر میں (بھی ہماری راہ میں) خرچ کیا کریں۔ " WHERE ayatNumber = 32 AND chapterNumber = 14;
UPDATE verses SET ts = "اللہ وہ (ہستی) ہے جس نے آسمان اور زمین کو پیدا کیا ہے اور بادلوں سے پانی اتار کر اس کے ذریعہ سے تمہارے لئے پھلوں (کی قسم) سے رزق پیدا کیا ہے اور اس نے کشتیوں کو (چلانے والی ہوا کو) بلا اُجرت تمہاری خدمت پر لگایا (ہوا) ہے تا کہ وہ اس کے حکم سے سمندر میں چلیں۔ اور (اسی طرح) دریائوں کو (بھی) اس نے بلا اُجرت تمہاری خدمت پر لگا رکھا ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 14;
UPDATE verses SET ts = "اور سورج اور چاند کو (بھی) وہ (دونوں) بلا وقفہ (اپنا مفوضہ) کام کرتے ہیں اور اس نے رات اور دن کو (بھی) بلا اُجرت تمہاری خدمت پر لگا رکھا ہے۔ " WHERE ayatNumber = 34 AND chapterNumber = 14;
UPDATE verses SET ts = "اور جوکچھ (بھی) تم نے اس سے مانگا اس نے تمہیں دیا ہے اور اگر تم اللہ کے احسان گننے لگو تو ان کا شمار نہیں کر سکو گے۔ انسان یقیناً بڑا ہی ظالم (اور) بڑا ہی ناشکر گذار ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 14;
UPDATE verses SET ts = "اور (اے مخاطب اس وقت کو یاد کر) جب ابراہیم نے (دعا کرتے ہوئے) کہا تھا (کہ) اے میرے رب! اس شہر (یعنی مکہ) کو امن والی (جگہ) بنا اور مجھے اور میرے بیٹوں کو اس بات سے دور رکھ کہ ہم معبودان باطلہ کی پرستش کریں۔ " WHERE ayatNumber = 36 AND chapterNumber = 14;
UPDATE verses SET ts = "اے میرے رب! انہوں نے یقیناً بہت سے لوگوں کو گمراہ کر رکھا ہے۔ پس جس نے میری پیروی کی وہ (تو) مجھ سے (تعلق رکھتا) ہے اور جس نے میری نافرمانی کی (اس کے متعلق بھی میری یہی عرض ہے کہ اس کو بخش دینا کیونکہ) تو یقیناً بڑا ہی بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 14;
UPDATE verses SET ts = "اے ہمارے رب! میں نے اپنی اولاد میں سے بعض کو تیرے معزز گھر کے پاس ایک ایسی وادی میں جس میں کوئی کھیتی نہیں ہوتی لا بسایا ہے اے میرے رب! (میں نے ایسا اس لئے کیا ہے) تا وہ عمدگی سے نماز ادا کریں۔ پس تو لوگوں کے دل ان کی طرف جھکا دے اور انہیں مختلف پھلوں سے رزق دیتا رہ تا کہ وہ (ہمیشہ تیرا) شکر کرتے رہیں۔ " WHERE ayatNumber = 38 AND chapterNumber = 14;
UPDATE verses SET ts = "اے ہمارے رب! جو کچھ ہم چھپاتے ہیں اور جو کچھ ہم ظاہر کرتے ہیں تو یقیناً سب کچھ ہی جانتا ہے اور اللہ سے کوئی چیز نہ زمین میں چھپی رہ سکتی ہے اور نہ آسمان میں۔ " WHERE ayatNumber = 39 AND chapterNumber = 14;
UPDATE verses SET ts = "ہر (قسم کی) تعریف کا اللہ ہی مستحق ہے۔ جس نے (میرے) بڑھاپے کے باوجود مجھے (دو بیٹے) اسماعیل اور اسحاق عطا کیے ہیں۔ میرا رب (بہت ہی) دعائیں سننے والا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 14;
UPDATE verses SET ts = "(اے) میرے رب! مجھے اور میری اولاد (میں سے ہر ایک) کو عمدگی سے نماز ادا کرنے والا بنا۔ (اے) ہمارے رب! (ہم پر فضل کر) اور میری دعا قبول فرما۔ " WHERE ayatNumber = 41 AND chapterNumber = 14;
UPDATE verses SET ts = "(اے) ہمارے رب! جس دن حساب ہونے لگے اس دن مجھے اور میرے والدین کو اور تمام مومنوں کو بخش دیجیئو۔ " WHERE ayatNumber = 42 AND chapterNumber = 14;
UPDATE verses SET ts = "اور (اے مخاطب) یہ ظالم (مکہ والے) جو کچھ کر رہے ہیں اس سے تو اللہ کو ہرگز بے خبر نہ سمجھ۔ وہ انہیں صرف اس دن تک ڈھیل دے رہا ہے جس دن (ان کی) آنکھیں (حیرت سے) پھٹی کی پھٹی رہ جائیں گی۔ " WHERE ayatNumber = 43 AND chapterNumber = 14;
UPDATE verses SET ts = "(وہ) اپنے سروں کو اوپر اٹھائے ہوئے خوف زدہ ہو کر دوڑ رہے ہوں گے (اور) ان کی نظریں  (لوٹ کر) واپس نہیں آئیں گی اور ان کے دل (امیدوں سے) خالی ہوں گے۔ " WHERE ayatNumber = 44 AND chapterNumber = 14;
UPDATE verses SET ts = "اور تو ان لوگوں کو اس دن سے  ڈرا جب ان پر وہ عذاب آئے گا (جس کا وعدہ کیا گیا ہے) جن لوگوں نے ظلم (کا شیوہ اختیار) کیا ہوگا (اس وقت) کہیں گے (کہ اے) ہمارے رب! ہمارے معاملہ کو کسی (اور) قریب میعاد تک (کے لئے) پیچھے ڈال دے کہ ہم تیری طرف سے آئی ہوئی دعوت کو قبول کریں گے اور (تیرے) رسولوں کی پیروی کریں گے (جس پر انہیں جواب ملے گا کہ کیا ابھی اتمام حجت میں کوئی کسر باقی ہے؟) اور کیا تم نے پہلے قسم (پر قسم) نہیں کھائی تھی کہ تم پر کسی طرح کا زوال نہیں (آئے گا) ۔" WHERE ayatNumber = 45 AND chapterNumber = 14;
UPDATE verses SET ts = "حالانکہ تم نے ان لوگوں کے گھروں کو اپنا گھر بنایا ہوا ہے جنہوں نے (تم سے پہلے) اپنی جانوں پر ظلم کیا تھا اور تم پر یہ بات خوب روشن ہو چکی تھی کہ ان کے ساتھ ہم نے کیسا معاملہ کیا تھا اور ہم تمام باتیں تمہارے لئے کھول کر بیان کر چکے ہیں۔ " WHERE ayatNumber = 46 AND chapterNumber = 14;
UPDATE verses SET ts = "اور یہ (لوگ) اپنی (ہر ایک) تدبیر عمل میں لا چکے ہیں۔ اور ان کی (ہر) تدبیر اللہ کے ہاں (محفوظ) ہے اور خواہ ان کی تدبیر  ایسی ہو کہ اس کے نتیجہ میں پہاڑ (بھی اپنی جگہ سے) ٹل جائیں (یہ تیرا کوئی نقصان نہیں کر سکتے۔ " WHERE ayatNumber = 47 AND chapterNumber = 14;
UPDATE verses SET ts = "پس (اے مخاطب) تو اللہ کو اپنے رسولوں سے اپنے وعدہ کے خلاف (معاملہ) کرنے والا ہرگز نہ سمجھ۔ اللہ یقیناً غالب (اور برے کاموں کی) سزا دینے والا ہے۔ " WHERE ayatNumber = 48 AND chapterNumber = 14;
UPDATE verses SET ts = "(اور وہ دن ضرور آنے والا ہے) جس دن زمین و آسمان بدل کر دوسرے زمین و آسمان قائم کیے جائیں گے۔ اور یہ (لوگ) اللہ کے سامنے ہوں گے جو واحد (اور ہر ایک چیز پر) کامل غلبہ رکھنے والا ہے۔ " WHERE ayatNumber = 49 AND chapterNumber = 14;
UPDATE verses SET ts = "اور اس دن تو ان مجرموں کو زنجیروں میں جکڑے ہوئے دیکھے گا۔ " WHERE ayatNumber = 50 AND chapterNumber = 14;
UPDATE verses SET ts = "ان کے کرتے (گویا) تارکول کے بنے ہوئے (کالے سیاہ) ہوں گے اور (دوزخ کی) آگ ان کے مونہوں کو ڈھانپ رہی ہوگی۔ " WHERE ayatNumber = 51 AND chapterNumber = 14;
UPDATE verses SET ts = "(یہ اس لئے ہوگا) تا اللہ ہر شخص کو جو کچھ اس نے (اپنے لئے) کیا ہوگا اس کا بدلہ دے۔ اللہ یقیناً جلد حساب لینے والا ہے۔ " WHERE ayatNumber = 52 AND chapterNumber = 14;
UPDATE verses SET ts = "یہ (ذکر) لوگوں کے (نصیحت حاصل کرنے کے) لئے کافی ہے اور اس بات کے لئے (بھی) کہ انہیں (آنے والے عذاب سے پورے طور پر) ہوشیار کیا جائے اور اس لئے(بھی) کہ انہیں معلوم ہو جائے کہ اللہ ہی صرف ایک حقیقی معبود ہے اور اس لئے (بھی) کہ عقل والے (لوگ) نصیحت حاصل کریں۔ " WHERE ayatNumber = 53 AND chapterNumber = 14;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 15;
UPDATE verses SET ts = "میں اللہ دیکھنے والا ہوں۔ یہ (ایک) کامل کتاب اور اپنے (مطالب کو) واضح کر دینے والے قرآن کی آیات ہیں۔ " WHERE ayatNumber = 2 AND chapterNumber = 15;
UPDATE verses SET ts = "جن لوگوں نے (اس کا) انکار کیا ہے‘ وہ بسا اوقات آرزو کیا کرتے ہیں کہ کاش وہ (بھی اس کی) فرمانبرداری اختیار کرنے والے ہوتے۔ " WHERE ayatNumber = 3 AND chapterNumber = 15;
UPDATE verses SET ts = "تو ان کو کھانے پینے اور وقتی سامانوں سے نفع اٹھانے میں مشغول چھوڑ دے اور (انہیں چھوڑے دے کہ ان کی جھوٹی) امیدیں انہیں غافل کرتی رہیں کیونکہ وہ جلد (ہی حقیقت) معلوم کر لیں گے۔ " WHERE ayatNumber = 4 AND chapterNumber = 15;
UPDATE verses SET ts = "اور ہم نے کبھی کسی بستی کو بغیر اس کے کہ اس کے متعلق (پہلے سے) ایک معلوم فیصلہ ہو چکا ہو ہلاک نہیں کیا۔ " WHERE ayatNumber = 5 AND chapterNumber = 15;
UPDATE verses SET ts = "کوئی قوم بھی اپنی (ہلاکت کی) میعاد سے بھاگ (کر بچ) نہیں سکتی اور نہ ہی پیچھے رہ (کر اس سے بچ) سکتی ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 15;
UPDATE verses SET ts = "اور انہوں نے (بڑے زور سے) کہا (کہ) اے وہ شخص جس پر یہ ذکر اتارا گیا ہے‘ تو یقیناً دیوانہ ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 15;
UPDATE verses SET ts = "اگر تو سچا ہے تو کیوں ملائکہ کو ہمارے پاس نہیں لاتا۔ " WHERE ayatNumber = 8 AND chapterNumber = 15;
UPDATE verses SET ts = "(کیا انہیں معلوم نہیں کہ) ہم (جب بھی) فرشتوں کو (اتارے ہیں تو) حق کے مطابق اتارتے ہیں اور (جب انہیں کافروں کے لئے اتارتے ہیں تو) اس وقت انہیں (یعنی کفار کو ذرہ بھی) مہلت نہیں دی جاتی۔ " WHERE ayatNumber = 9 AND chapterNumber = 15;
UPDATE verses SET ts = "اس ذکر (یعنی قرآن) کو ہم نے ہی اتارا ہے اور ہم یقیناً اس کی حفاظت کریں گے۔ " WHERE ayatNumber = 10 AND chapterNumber = 15;
UPDATE verses SET ts = "اور ہم نے اگلے (زمانہ کے) لوگوں کی جماعتوں میں (بھی) تجھ سے پہلے رسول بھیجے تھے۔ " WHERE ayatNumber = 11 AND chapterNumber = 15;
UPDATE verses SET ts = "اور جو رسول بھی ان کے پاس آتا تھا‘ وہ اس کی ہنسی اڑاتے تھے۔ " WHERE ayatNumber = 12 AND chapterNumber = 15;
UPDATE verses SET ts = "اسی طرح ہم اس (عادت استہزا) کو مجرموں کے دلوں میں راسخ کرتے ہیں۔ " WHERE ayatNumber = 13 AND chapterNumber = 15;
UPDATE verses SET ts = "یہ (لوگ) اس (قرآن) پر ایمان نہیں لاتے حالانکہ پہلوں (کے متعلق اللہ) کی سنت گذر چکی ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 15;
UPDATE verses SET ts = "اور اگر (بالفرض) ہم ان پر (شناخت کی) کوئی آسمانی راہ کھول (بھی) دیتے اور وہ اس سے (فائدہ اٹھا کر) حقیقت کو سمجھنے بھی لگتے۔ " WHERE ayatNumber = 15 AND chapterNumber = 15;
UPDATE verses SET ts = "تو (بھی) وہ (یہی) کہتے (کہ) محض ہماری نظروں پر پردہ ڈالا گیا ہے (ورنہ حقیقت کچھ نہیں) بلکہ ہم (لوگوں) پر (کوئی) جادو کر دیا گیا ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 15;
UPDATE verses SET ts = "اور یقیناً ہم نے آسمان میں (ستاروں کی) کئی منزلیں بنائی ہیں اور ہم نے اسے دیکھنے والوں کے لئے خوبصورت بنایا ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 15;
UPDATE verses SET ts = "اور (نیز) ہم نے اسے ہر ایک سرکش (اور) دھتکارے ہوئے (وجود کی رسائی) سے محفوظ کر دیا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 15;
UPDATE verses SET ts = "مگر جو شخص چوری سے (وحی الٰہی کی) کوئی بات سن لے۔ (اور اُسے بگاڑ کر پھیلائے) تو اس کے پیچھے ایک روشن شعلہ لگا دیا جاتا ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 15;
UPDATE verses SET ts = "اور ہم نے زمین کو پھیلایا ہے اور اس میں ہم نے محکم پہاڑ قائم کیے ہیں اور (نیز) ہم نے اس میں ہر قسم کی موزون چیزوں کو (پیدا کیا اور) بڑھایا ہے۔ " WHERE ayatNumber = 20 AND chapterNumber = 15;
UPDATE verses SET ts = "اور اس میں ہم نے تمہارے لئے اور (ہر) اس (مخلوق) کے لئے جسے تم رزق نہیں دیتے معیشت کے سامان پیدا کیے ہیں۔ " WHERE ayatNumber = 21 AND chapterNumber = 15;
UPDATE verses SET ts = "اور کوئی چیز ایسی نہیں جس کے (غیر محدود) خزانے ہمارے پاس نہ ہوں۔ لیکن ہم اسے ایک معین اندازے سے ہی اتارا کرتے ہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 15;
UPDATE verses SET ts = "اور ہم نے (بخارات کو) اٹھانے والی ہوائیں (بھی) چھوڑ رکھی ہیں اور (ان کے ذریعہ سے) ہم نے بادلوں سے پانی اتارا ہے۔ پھر وہ تمہیں پلایا ہے اور تم خود اسے محفوظ نہیں رکھ سکتے تھے (لیکن اس ذریعہ سے ہم نے اسے محفوظ کر دیا ہے) " WHERE ayatNumber = 23 AND chapterNumber = 15;
UPDATE verses SET ts = "اور یقیناً ہم ہی زندہ کرتے اور مارتے ہیں اور ہم ہی (سب کے) وارث ہیں۔ " WHERE ayatNumber = 24 AND chapterNumber = 15;
UPDATE verses SET ts = "اور ہم تم میں سے آگے نکل جانے والوں کو (بھی) جانتے ہیں۔ اور (اسی طرح) ہم (تم میں سے) پیچھے رہ جانے والوں کو (بھی) جانتے ہیں۔ " WHERE ayatNumber = 25 AND chapterNumber = 15;
