UPDATE verses SET ts = "اور (یاد رکھ کہ) جزا (وسزا) کے دن تک تجھ پر (میری) لعنت رہے گی۔ " WHERE ayatNumber = 36 AND chapterNumber = 15;
UPDATE verses SET ts = "اس نے کہا (کہ) اے میرے رب (اگر تو فوراً مجھے سزا نہیں دیتا تو) تو مجھے ان کے (دوبارہ) اٹھائے جانے کے دن تک مہلت دے۔ " WHERE ayatNumber = 37 AND chapterNumber = 15;
