UPDATE verses SET ts = "اور (اس کے علاوہ) جب تم انہیں چرا کر شام کو (ان کے تھانوں کی طرف) واپس لاتے ہو تو اس میں ایک قسم کا زینت کا سامان ہوتا ہے اسی طرح اس وقت جب تم انہیں (صبح کو) چرنے کے لئے (آزاد) چھوڑتے ہو (تو اس میں بھی تمہارے لئے زینت اور بڑائی کا سامان ہوتا ہے) " WHERE ayatNumber = 7 AND chapterNumber = 16;
