UPDATE verses SET ts = "اس کا تسلط صرف ان لوگوں پر (ہوتا) ہے۔ جو اس سے دوستی رکھتے ہیں اور جو اس کی وجہ سے شرک کرتے ہیں۔ " WHERE ayatNumber = 101 AND chapterNumber = 16;
UPDATE verses SET ts = "اور جب ہم کسی نشان کی جگہ پر کوئی اور نشان لاتے ہیں اور (اس میں کیا شک ہے کہ) اللہ جو کچھ اتارتا ہے اس (کی ضرورت) کو وہ (سب سے) بہتر جانتا ہے تو (اس موقعہ پر مخالفین) کہتے ہیں کہ تو مفتری ہے (مگر حقیقت یوں) نہیں بلکہ ان میں سے اکثرعلم نہیں رکھتے۔ " WHERE ayatNumber = 102 AND chapterNumber = 16;
UPDATE verses SET ts = "تو (ایسے معترض سے) کہہ (کہ) روح القدس نے اسے تیرے رب کی طرف سے حق (وحکمت) کے ساتھ اتارا ہے تا کہ جو لوگ ایمان لائے ہیں انہیں وہ (ایمان پر) ہمیشہ کے لئے قائم کر دے اور نیز (اس نے) فرمانبرداروں کی (مزید) رہنمائی کے لئے اور (انہیں) بشارت دینے کے لئے (اسے اتارا ہے) ۔" WHERE ayatNumber = 103 AND chapterNumber = 16;
UPDATE verses SET ts = "اور ہم یقیناً جانتے ہیں کہ وہ کہتے ہیں (کہ یہ وحی الٰہی نہیں بلکہ) ایک آدمی اسے سکھاتا ہے (مگر وہ نہیں سمجھتے کہ) جس شخص کی طرف وہ (اشارہ کرتے ہیں اور ان کے ذہن اس کی طرف) مائل ہوتے ہیں اس کی زبان اعجمی ہے اور یہ (قرآنی زبان تو خوب) روشن (کرکے دکھانے والی) عربی زبان ہے۔ " WHERE ayatNumber = 104 AND chapterNumber = 16;
UPDATE verses SET ts = "جو لوگ اللہ کے نشانوں پر ایمان نہیں لاتے۔ اللہ انہیں ہدایت نہیں دیتا۔ اور ان کے لئے دردناک عذاب (مقدر) ہے۔ " WHERE ayatNumber = 105 AND chapterNumber = 16;
UPDATE verses SET ts = "جھوٹ وہی لوگ باندھا کرتے ہیں جو اللہ کے نشانوں پر ایمان نہیں رکھتے۔ اوریہی لوگ پکے جھوٹے ہوتے ہیں۔ " WHERE ayatNumber = 106 AND chapterNumber = 16;
UPDATE verses SET ts = "جو لوگ (بھی) اپنے ایمان لانے کے بعد اللہ کا انکار کریں سوائے ان کے جنہیں (کفر پر) مجبور کیا گیا ہو لیکن ان کا دل ایمان پر مطمئن ہو (وہ گرفت میں نہ آئیں گے) ہاں وہ جنہوں نے (اپنا) سینہ کفر کے لئے کھول دیا ہو ان پر اللہ کا (بہت) بڑا غضب (نازل) ہوگا۔ اور ان کے لئے بھاری عذاب (مقدر) ہے۔ " WHERE ayatNumber = 107 AND chapterNumber = 16;
UPDATE verses SET ts = "اور ایسا اس سبب سے ہوگا کہ انہوں نے اس ورلی زندگی سے محبت کرکے اسے آخرت پر مقدم کر لیا اور (نیز اس وجہ سے کہ) اللہ کفر اختیار کرنے والے لوگوں کو ہدایت نہیں دیتا۔ " WHERE ayatNumber = 108 AND chapterNumber = 16;
