UPDATE verses SET ts = "(اس جزا کا ظہور خصوصیت سے اس دن ہوگا) جس دن ہر شخص اپنی جان کے متعلق جھگڑتا ہوا آئے گا اور ہر شخص نے جو کچھ کیا ہو گا (اس کا اجر) اسے پورا پورا دیا جائے گا۔ اور اُن پر (کسی رنگ میں بھی) ظلم نہ کیا جائے گا۔ " WHERE ayatNumber = 112 AND chapterNumber = 16;
UPDATE verses SET ts = "اور اللہ (تمہیں سمجھانے کے لئے) ایک بستی کا حال بیان کرتا ہے۔ جسے (ہر طرح سے) امن حاصل ہے (اور) اطمینان نصیب ہے ہر طرف سے اس کا رزق اسے بافراغت پہنچ رہا ہے۔ پھر (بھی) اس نے اللہ کی نعمتوں کی ناشکری کی ہے اس (کی اس ناشکری) پر اللہ نے اس (کے باشندوں) پر ان کے اپنے (گھنائونے) عمل کی وجہ سے بھوک اور خوف کا لباس نازل کیا ہے۔ " WHERE ayatNumber = 113 AND chapterNumber = 16;
UPDATE verses SET ts = "اور یقیناً ان کے پاس انہی میں سے (ہمارا) ایک رسول آچکا ہے مگر انہوں نے اسے جھٹلایا۔ جس پر اس حالت میں کہ وہ ظلم کر رہے تھے (ہمارے) عذاب نے انہیں آپکڑا۔ " WHERE ayatNumber = 114 AND chapterNumber = 16;
UPDATE verses SET ts = "پس جو حلال (اور) طیب (مال) اللہ نے تمہیں دیا ہے تم اس میں سے کھائو اور اللہ کی نعمت کا اگر تم اسی کی عبادت کرتے ہو شکر کرو۔ " WHERE ayatNumber = 115 AND chapterNumber = 16;
UPDATE verses SET ts = "اس نے تم پر صرف مردار کو اور خون کو اور سئور کے گوشت کو اور (ہر) اس چیز کو حرام کیا ہے جس پر اللہ کے سوا کسی اور کا نام لیا گیا ہو اور جو شخص (ان میں سے کسی چیز کے کھانے پر) مجبور کیا جائے بحالیکہ وہ (شریعت کا) مقابلہ کرنے والا نہ ہو اور نہ حد سے بڑھنے والا ہو تو (یاد رہے کہ) اللہ بہت ہی بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 116 AND chapterNumber = 16;
UPDATE verses SET ts = "اور اپنی زبانوں کے جھوٹے بیانوں کے سبب سے (یہ) مت کہو کہ یہ حلال ہے اور یہ حرام ہے (تا ایسا نہ ہو) کہ تم اللہ پر جھوٹ باندھنے والے بن جائو۔ جو لوگ اللہ پر جھوٹ باندھتے ہیں وہ ہرگز کامیاب نہیں ہوتے۔ " WHERE ayatNumber = 117 AND chapterNumber = 16;
UPDATE verses SET ts = "(یہ دنیا) تھوڑا سا عارضی سامان ہے اور (اس جھوٹ کے نتیجہ میں) ان کے لئے دردناک عذاب (مقدر) ہے (پس اس سے بچنا چاہیئے) " WHERE ayatNumber = 118 AND chapterNumber = 16;
UPDATE verses SET ts = "اور جن لوگوں نے یہودی مذہب اختیار کیا تھا ان پر (بھی) ہم نے اس سے پہلے وہ (تمام) چیزیں حرام کی تھیں جن کا ذکر ہم نے تجھ سے کیا ہے اور ہم نے ان پر (یہ احکام دیکر) ظلم نہیں کیا تھا بلکہ وہ (ان احکام کو توڑ کر) اپنی جانوں پر ظلم کیا کرتے تھے۔ " WHERE ayatNumber = 119 AND chapterNumber = 16;
UPDATE verses SET ts = "پھر (یاد رکھو کہ) جن لوگوں نے بے خبری کی حالت میں (کوئی) برائی کی ہو (اور) پھر اس کے بعد (وہ اس سے) توبہ کر لیں۔ اور (اپنی غلطی کی) اصلاح (بھی) کریں‘ ان کے حق میں تیرا رب ان (شرائط کے پورا کرنے) کے بعد بہت ہی بخشنے والا (اور) باربار رحم کرنے والا (ثابت) ہوگا۔ " WHERE ayatNumber = 120 AND chapterNumber = 16;
UPDATE verses SET ts = "ابراہیم یقیناً ہر (اِک) خیر کا جامع اللہ کے لئے تذلل اختیار کرنے والا (اور) ہمیشہ خدا کی کامل فرماں برداری کرنے والا تھا‘ اور وہ مشرکوں میں سے نہیں تھا۔ " WHERE ayatNumber = 121 AND chapterNumber = 16;
UPDATE verses SET ts = "(وہ) اس کے انعاموں کا شکر گذار تھا‘ اس (کے رب) نے اسے برگزیدہ کیا تھا اور ایک سیدھی راہ کی طرف اس کی راہنمائی کی تھی۔ " WHERE ayatNumber = 122 AND chapterNumber = 16;
UPDATE verses SET ts = "اور ہم نے اُسے اس دنیا میں (بھی بڑی) کامیابی بخشی تھی‘ اور وہ آخرت میں (بھی) یقیناً صالح لوگوں میں سے ہوگا۔ " WHERE ayatNumber = 123 AND chapterNumber = 16;
UPDATE verses SET ts = "اور (اے رسول) ہم نے تجھے وحی کے ذریعہ سے حکم دیا ہے کہ (ہماری) کامل فرمانبرداری پر ہمیشہ قائم رہنے والے ابراہیم کے طریق کی پیروی کر۔ اور (اے مکہ والو جانتے ہوکہ) وہ مشرکوں میں سے نہیں تھا۔ " WHERE ayatNumber = 124 AND chapterNumber = 16;
UPDATE verses SET ts = "سبت (کا وبال) انہی لوگوں پر ڈالا گیا تھا جنہوں نے اس میں اختلاف کیا تھا اور تیرا رب اس امر کے متعلق جس میں وہ اختلاف کرتے تھے یقیناً قیامت کے دن فیصلہ کرے گا۔ " WHERE ayatNumber = 125 AND chapterNumber = 16;
UPDATE verses SET ts = "(اور اے رسول) تو (لوگوں کو) حکمت اور اچھی نصیحت کے ذریعہ سے اپنے رب کی طرف بلا۔ اور اس طریق سے جو سب سے اچھا ہو۔ ان سے (ان کے اختلافات کے متعلق) بحث کر۔ تیرا رب ان کو (بھی) جو اس کی راہ سے بھٹک گئے ہوں (سب سے) بہتر جانتا ہے اور ان کو بھی جو ہدایت پاتے ہیں۔ " WHERE ayatNumber = 126 AND chapterNumber = 16;
UPDATE verses SET ts = "اگر تم (لوگ زیادتی کرنے والوں کو) سزا دو تو جتنی تم پر زیادتی کی گئی ہو تم اتنی (ہی) سزا دو۔ اور (ہمیں اپنی ذات کی قسم ہے کہ) اگر تم صبر کرو گے تو صبر کرنے والوں کے حق میں وہ (یعنی صبر کرنا) بہتر ہوگا۔ " WHERE ayatNumber = 127 AND chapterNumber = 16;
UPDATE verses SET ts = "اور (اے رسول) تو صبر سے کام لے اور تیرا صبر کرنا اللہ (کی مدد) سے ہی (ہوسکتا) ہے اور تو ان (لوگوں کی حالت) پر غم نہ کھا۔ اور جو تدبیریں وہ کرتے ہیں ان کی وجہ سے تکلیف محسوس نہ کر۔ " WHERE ayatNumber = 128 AND chapterNumber = 16;
UPDATE verses SET ts = "اور یاد رکھ کہ اللہ یقیناً ان لوگوں کے ساتھ ہوتا ہے جنہوں نے تقویٰ (کا طریق) اختیار کیا ہو اور جو نیکو کار ہوں۔ " WHERE ayatNumber = 129 AND chapterNumber = 16;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 17;
UPDATE verses SET ts = "(میں) اُس (خدا) کی پاکیزگی (بیان کرتا ہوں) جو رات کے وقت اپنے بندے کو (اس) حرمت والی مسجد سے (اُس) دُور والی مسجد تک جس کے اردگرد کو (بھی) ہم نے برکت دی ہے (اس لئے) لے گیا کہ تاہم اسے اپنے بعض نشان دکھلائیں یقیناً وہی (خدا) ہے جو (اپنے بندوں کی پکار کو) خوب سننے والا (اور ان کی حالتوں کو) خوب دیکھنے والا ہے۔" WHERE ayatNumber = 2 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے موسیٰ کو کتاب (یعنی تورات) دی تھی اور اس کو ہم نے بنی اسرائیل کے لئے ہدایت (کا ذریعہ) بنایا تھا (اور اس میں انہیں حکم دیا تھا) کہ تم میرے سوا کسی کو (اپنا) کارساز نہ ٹھہرائو۔ " WHERE ayatNumber = 3 AND chapterNumber = 17;
UPDATE verses SET ts = "(اور یہ بھی کہا تھا کہ اے) ان لوگوں کی نسل! جنہیں ہم نے نوح کے ساتھ (کشتی پر) سوار کیا تھا (یاد رکھو کہ) وہ یقیناً (ہمارا) نہایت شکر گذار بندہ تھا (پس تم بھی شکر گذار بنو) ۔" WHERE ayatNumber = 4 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے اس کتاب میں بنی اسرائیل کو یہ بات (کھول کر) پہنچا دی تھی کہ تم یقیناً اس ملک میں دو بار فساد کرو گے اور یقیناً تم بہت بڑی سرکشی (اختیار) کرو گے۔ " WHERE ayatNumber = 5 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب ان دو (بار کے فسادوں) میں سے پہلی (بار) کا وعدہ (پورا ہونے کا وقت) آیا تو ہم نے بعض اپنے ایسے بندوں کو (تمہاری سرکوبی کے لئے) تم پر (مستولی کرکے) کھڑا کر دیا جو سخت جنگجو تھے۔ اور وہ تمہارے گھروں کے اندر جا گھسے اور یہ وعدہ (بہرحال) پورا ہو کر رہنے والا تھا۔ " WHERE ayatNumber = 6 AND chapterNumber = 17;
UPDATE verses SET ts = "(اس کے بعد) پھرہم نے تمہیں (دشمن پر) حملہ کی طاقت عطا کی اورہم نے مالوں اور بیٹوں کے ذریعہ سے تمہاری مدد کی اورہم نے تمہیں جتھے کے لحاظ سے بھی (پہلے سے) زیادہ کردیا۔ " WHERE ayatNumber = 7 AND chapterNumber = 17;
UPDATE verses SET ts = "(سنو)! اگر تم نیکو کار بنو گے تو نیکو کار بن کر اپنی جانوں کو ہی فائدہ پہنچائو گے‘ اور اگر تم برے اعمال کرو گے تو (بھی) ان (یعنی اپنی جانوں) کے لئے (برا کرو گے) پھر جب دوسری بار والا وعدہ (پورا ہونے کا وقت) آگیا‘ تا کہ وہ (یعنی تمہارے دشمن) تمہارے معزز لوگوں سے ناپسندیدہ معاملہ کریں اور (اسی طرح) مسجد میں داخل ہوں جس طرح وہ اس میں پہلی بار داخل ہوئے تھے اور جس چیز پر غلبہ پائیں اُسے بالکل تباہ (اور برباد) کرکے رکھ دیں (تو یہ بات بھی پوری ہوگئی) " WHERE ayatNumber = 8 AND chapterNumber = 17;
UPDATE verses SET ts = "(اب بھی) کچھ بعید نہیں (بلکہ عین ممکن ہے) کہ تمہارا رب تم پر رحم کر دے اور اگر تم (پھر اپنے غلط رویہ کی طرف) لوٹے تو ہم بھی (اپنی سزا کی طرف) لوٹیں گے اور (یاد رکھو کہ) جہنم کو ہم نے کافروں کے لئے قید خانہ بنایا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 17;
UPDATE verses SET ts = "یہ قرآن یقیناً اس (راہ کی) طرف راہنمائی کرتا ہے جوسب سے زیادہ درست ہے اور مومنوں کوجو مناسب حال کام کرتے ہیں بشارت دیتاہے کہ ان کیلئے (بہت) بڑااجر (مقدر) ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (قرآن) یہ (بھی کہتا ہے) کہ جو لوگ آخرت پر ایمان نہیں لاتے ان کے لئے ہم نے دردناک عذاب تیار کیا ہے۔ " WHERE ayatNumber = 11 AND chapterNumber = 17;
UPDATE verses SET ts = "اور انسان شر کو (اسی جوش سے) بلاتا ہے جس جوش سے اللہ اس (بندہ) کو خیر کی طرف بلا رہا ہوتا ہے اور انسان بڑا ہی جلد باز (واقع ہوا) ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے رات اور دن دو نشان بنائے ہیں۔ اس طرح پر کہ رات والے نشان کے اثر کو تو ہم نے مٹا دیا اور دن والے نشان کو ہم نے بینائی بخشنے والا بنا دیا تا کہ تم خدا کے فضل کو تلاش کرو اور (آسانی سے) سالوں کی گنتی اور حساب معلوم کر سکو‘ اور ہم نے ہر ایک چیز کو خوب کھول کھول کر بیان کر دیا ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے ہر انسان کی گردن میں اس کے عمل کو باندھ دیا ہے اور ہم قیامت کے دن اس (کے اعمال) کی ایک کتاب نکال کر اس کے سامنے رکھ دیں گے‘ جسے وہ (بالکل) کھلی ہوئی پائے گا۔ " WHERE ayatNumber = 14 AND chapterNumber = 17;
UPDATE verses SET ts = "(اور اسے کہا جائے گا) اپنی کتاب (خود ہی) پڑھ (کر دیکھ لے) آج تیرا نفس ہی تیرا حساب لینے کے لئے کافی ہے۔ " WHERE ayatNumber = 15 AND chapterNumber = 17;
UPDATE verses SET ts = "(پس یاد رکھو کہ) جو ہدایت کو قبول کرے گا اس کا ہدایت پانا اسی کی ذات کے فائدہ کے لئے ہے جو (اس ہدایت کو رد کرکے) گمراہ ہوگا۔ اس کا گمراہ ہونا اسی کے (نفس کے) خلاف پڑے گا اور کوئی بوجھ اٹھانے والی جان کسی دوسری (جان) کا بوجھ نہیں اٹھائے گی اور ہم  (کسی قوم پر) ہرگز عذاب نہیں بھیجتے جب تک (ان کی طرف) کوئی رسول نہ بھیج لیں۔ " WHERE ayatNumber = 16 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب ہم کسی بستی کو ہلاک کرنے کا ارادہ کریں تو (پہلے) ہم اس کے آسودہ حال لوگوں کو (نیکی کا) حکم دیتے ہیں جس پر وہ (الٹا) اس (بستی) میں نافرمانی (کی راہ) اختیار کر لیتے ہیں۔ تب اس بستی کے متعلق ہمارا کلام پورا ہو جاتا ہے اور ہم اسے پوری طرح تباہ کر دیتے ہیں۔ " WHERE ayatNumber = 17 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (اسی قانون کے مطابق) ہم نے نوح (کی قوم کو اور اس) کے بعد (یکے بعد دیگرے) بہت سی نسلوں کو ہلاک کیا اور تیرا رب اپنے بندوں کے گناہوں پر (اچھی طرح) آگاہی رکھنے والا (ہے اور انہیں) خوب دیکھتا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 17;
UPDATE verses SET ts = "جو شخص (صرف) دنیا کا خواہاں ہو۔ ہم اس قسم کے لوگوں میں سے جس کو چاہتے ہیں‘ اس دنیا میں کچھ جلدی والا (دنیوی) فائدہ دے دیتے ہیں۔ اس کے بعد اس کے لئے جہنم (کا عذاب) مقرر کر دیتے ہیں جس میں وہ الزام لگوا کر اور دھتکارا جاکر داخل ہو جاتا ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جن لوگوں نے ایمان کی حالت میں آخرت کی خواہش کی اور اس کے لئے اس کے مطابق کوشش (بھی) کی تو (یاد رکھو) ایسے ہی لوگ ہیں جن کی کوشش کی قدر کی جائے گی۔" WHERE ayatNumber = 20 AND chapterNumber = 17;
UPDATE verses SET ts = "ہم سبھی کو مدد دیتے ہیں ان کو بھی (یعنی دین والوں کو بھی) اور ان کو بھی (یعنی دنیا والوں کو بھی) (اور یہ مدد) تیرے رب کی عطا میں سے ہے اور تیرے رب کی عطا (کسی خاص گروہ سے) روکی نہیں جاتی۔ " WHERE ayatNumber = 21 AND chapterNumber = 17;
UPDATE verses SET ts = "دیکھ کس طرح ہم نے (دنیوی سامانوں کی رو سے بھی) ان میں سے بعض کو بعض پر فضیلت دی (ہوئی) ہے اور آخرت کی (زندگی) تو یقیناً (اور بھی) بڑے درجات والی اور زیادہ فضیلت والی (زندگی) ہوگی۔ " WHERE ayatNumber = 22 AND chapterNumber = 17;
UPDATE verses SET ts = "پس (اے مخاطب) اللہ کے ساتھ کوئی دوسرا معبود نہ بنا‘ ورنہ تو الزام لگوا کر (اور) نصرت (الٰہی) سے محروم ہو کر بیٹھ جائے گا۔ " WHERE ayatNumber = 23 AND chapterNumber = 17;
UPDATE verses SET ts = "تیرے رب نے (اس بات کا) تا کیدی حکم دیا ہے کہ تم اس کے سوا کسی کی عبادت نہ کرو اور (نیز یہ کہ اپنے) ماں باپ سے اچھا سلوک کرو۔ اگر ان میں سے کسی ایک پر یا ان دونوں پر تیری زندگی میں بڑھاپا آجائے‘ تو انہیں (ان کی کسی بات پر ناپسندیدگی کا اظہار کرتے ہوئے) اُف تک نہ کہہ اور نہ انہیں جھڑک اور ان سے (ہمیشہ) نرمی سے بات کر۔ " WHERE ayatNumber = 24 AND chapterNumber = 17;
UPDATE verses SET ts = "اور رحم کے جذبہ کے ماتحت ان کے سامنے عاجزانہ رویہ اختیار کر اور (ان کے لئے دعا کرتے وقت) کہا کر (کہ اے) میرے رب! ان پر مہربانی فرما کیونکہ انہوں نے بچپن کی حالت میں میری پرورش کی تھی۔ " WHERE ayatNumber = 25 AND chapterNumber = 17;
UPDATE verses SET ts = "تمہارا رب جو کچھ (بھی) تمہارے دلوں میں ہو اسے (سب سے) بہتر جانتا ہے (اور) اگر تم نیک ہوگے تو (یاد رکھو کہ) وہ باربار رجوع کرنے والوں کو بہت ہی بخشنے والا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 17;
UPDATE verses SET ts = "اور قرابت والے (رشتہ دار) کو اور مسکین کو اور مسافر کو اس کا حق دو اور اسراف کسی رنگ میں (بھی) نہ کرو۔ " WHERE ayatNumber = 27 AND chapterNumber = 17;
UPDATE verses SET ts = "اسراف کرنے والے لوگ یقیناً شیطانوں کے بھائی ہوتے ہیں اور شیطان اپنے رب کا بہت ہی ناشکر گذار ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 17;
UPDATE verses SET ts = "اور اپنے رب کی کسی عظیم الشان رحمت کے حصول کے لئے جس کی تجھے امید لگی ہوئی ہو اگر تو ان (رشتہ داروں) سے اعراض کرے (تو اس صورت میں اعراض جائز ہے مگر) تب بھی ان سے تلطف والے طریق پر بات کر۔ " WHERE ayatNumber = 29 AND chapterNumber = 17;
UPDATE verses SET ts = "اور تو نہ (تو بخل سے) اپنے ہاتھ کو باندھ کر اپنی گردن میں ڈال لے اور نہ (اسراف میں پڑ کر) اسے بالکل کھول دے ورنہ (یاتو) تو ملامت کا نشانہ بن کر یا تھکان کے اثر سے بیٹھ جائے گا۔ " WHERE ayatNumber = 30 AND chapterNumber = 17;
UPDATE verses SET ts = "تیرا رب جس کے لئے چاہتا ہے رزق کو وسیع کر دیتا ہے اور (جس کے لئے چاہتا ہے) تنگ کرتا ہے۔ وہ یقیناً اپنے بندوں (کے حالات) کو جاننے والا (اور) دیکھنے والا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 17;
UPDATE verses SET ts = "اور تم مفلسی کے خوف سے اپنی اولاد کو قتل مت کرو انہیں (بھی) ہم ہی رزق دیتے ہیں اور تمہیں بھی (ہم ہی دیتے ہیں) انہیں قتل کرنا یقیناً (بہت) بڑی خطا ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 17;
UPDATE verses SET ts = "اور زنا کے قریب (بھی) نہ جائو‘ وہ یقیناً ایک کھلی بے حیائی اور بہت برا راستہ ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جس جان کو (مارنا) اللہ نے حرام ٹھہرایا ہے اسے (شرعی) حق کے سوا قتل نہ کرو۔  اور جو شخص مظلوم مارا جائے اس کے وارث کو ہم نے (قصاص) کا اختیار دیا ہے پس (اس کے لئے یہ ہدایت ہے کہ) وہ (قاتل کو) قتل کرنے میں (ہماری مقرر کردہ) حد سے آگے نہ بڑھے (اگر وہ حد کے اندر رہے گا) تو یقیناً (ہماری) مدد اس کے شامل حال ہوگی۔ " WHERE ayatNumber = 34 AND chapterNumber = 17;
UPDATE verses SET ts = "اور تم اس طریق کے سوا جو (یتیم کے حق میں) زیادہ اچھا ہو (کسی اور طرح) یتیم کے مال کے پاس نہ پھٹکو۔ یہاں تک کہ وہ اپنی مضبوطی کی عمر کو پہنچ جائے اور (اپنے) عہد کو پورا کرو۔ (کیونکہ) ہر عہد کی نسبت یقیناً (ایک نہ ایک دن) جواب طلبی ہوگی۔ " WHERE ayatNumber = 35 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب تم (کسی کو کچھ) ماپ کر دینے لگو تو ماپ پورا دیا کرو اور (جب تول کر دو تو بھی) سیدھے ترازو کے ساتھ تول کر دیا کرو۔ یہ بات سب سے بہتر اور انجام کے لحاظ سے سب سے اچھی ہے۔ " WHERE ayatNumber = 36 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (اے مخاطب) جس بات کا تجھے علم نہ ہو اس کی اتباع نہ کیا کر (کیونکہ) کان اور آنکھ اور دل ان سب کے متعلق (تجھ سے) پوچھا جائے گا۔ " WHERE ayatNumber = 37 AND chapterNumber = 17;
UPDATE verses SET ts = "اور زمین پر اکڑ کر مت چل‘ کیونکہ نہ تو تو اس طرح ملک کی انتہا کو پہنچ سکتا ہے‘ اور نہ تو (اس طرح قوم کے) سرداروں کے بلند درجہ کو پا سکتا ہے۔ " WHERE ayatNumber = 38 AND chapterNumber = 17;
UPDATE verses SET ts = "ان (حکموں) میں سے ہر ایک (فعل) کی بری صورت تیرے رب کے نزدیک ناپسندیدہ ہے۔ " WHERE ayatNumber = 39 AND chapterNumber = 17;
UPDATE verses SET ts = "یہ (شاندار تعلیم) اس (علم اور) حکمت کا (ایک حصہ) ہے جو تیرے رب نے وحی کے ذریعہ سے تیری طرف بھیجی ہے اور تو اللہ کے ساتھ کوئی اور معبود مت بنا‘ ورنہ تو ملامت کا نشانہ بن کر (اور) دھتکارا جا کر دوزخ میں ڈال دیا جائے گا۔ " WHERE ayatNumber = 40 AND chapterNumber = 17;
UPDATE verses SET ts = "کیا تمہارے رب نے تم کو لڑکوں (کی نعمت) سے مخصوص کر دیا ہے اور (خود) اس نے بعض فرشتوں کو (اپنی) لڑکیاں بنا لیا ہے۔ یقیناً تم (یہ) بڑی (خطرناک) بات کہتے ہو۔ " WHERE ayatNumber = 41 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے اس قرآن میں (ہر ایک بات کو) اس لئے بار بار بیان کیا ہے کہ وہ (اس سے) نصیحت حاصل کریں لیکن (باوجود اس کے) وہ (قرآن) انہیں (عجب اور) نفرت ہی میں بڑھا رہا ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 17;
UPDATE verses SET ts = "تو کہہ (کہ) اگر ان کے قول کے مطابق اس (اللہ) کے ساتھ (کوئی) اور معبود (بھی) ہوتے تو اس صورت میں وہ (مشرکین ان معبودوں کی مدد سے) عرش والے (خدا) تک (پہنچنے کا) کوئی راستہ ضرور ڈھونڈ لیتے۔ " WHERE ayatNumber = 43 AND chapterNumber = 17;
UPDATE verses SET ts = "وہ ان (مشرکین) کی شرک کی باتوں سے پاک اور بہت ہی بالا ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 17;
UPDATE verses SET ts = "ساتوں آسمان اور زمین اور جو ان میں (بسنے والے) ہیں اس کی تسبیح کرتے ہیں اور (دنیا کی) ہر چیز اس کی تعریف کرتی ہوئی (اس کی) تسبیح کرتی ہے لیکن تم ان (اشیائ) کی تسبیح کو نہیں سمجھتے۔ وہ یقیناً پردہ پوشی کرنے والا (اور) بہت (ہی) بخشنے والا ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب تو قرآن کو پڑھتا ہے تو (اس وقت) ہم تیرے درمیان اور ان لوگوں کے درمیان جو آخرت پر ایمان نہیں رکھتے ایک مخفی (اور عام نظروں سے پوشیدہ) پردہ پیدا کر دیتے ہیں۔ " WHERE ayatNumber = 46 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم ان کے دلوں پر کئی پردے ڈال دیتے ہیں تا کہ وہ اس (سچائی) کو سمجھ نہ سکیں اور ان کے کانوں میں بہرا پن (پیدا کر دیا) ہے اور جب تو قرآن میں اپنے رب کو جو ایک ہی ہے یاد کرتا ہے تو وہ نفرت سے اپنی پیٹھیں پھیر کر چل پڑتے ہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 17;
UPDATE verses SET ts = "(اور) جب وہ (بظاہر) تیری باتیں سن رہے ہوتے ہیں تو جس غرض کے ماتحت وہ تیری باتیں سن رہے ہوتے ہیں ہم اس کی حقیقت کو خوب جانتے ہیں اور (نیز) جب وہ باہم سرگوشی کر رہے ہوتے ہیں (اس کا بھی ہمیں علم ہوتا ہے اور) جب وہ ظالم (ایک دوسرے سے) کہہ رہے ہوتے ہیں (کہ) تم ایک فریب خوردہ شخص ہی کی پیروی کر رہے ہو (تو اس وقت بھی ہم سن رہے ہوتے ہیں) " WHERE ayatNumber = 48 AND chapterNumber = 17;
UPDATE verses SET ts = "دیکھ انہوں نے تیرے متعلق کس طرح باتیں بنائی  ہیں جس کے نتیجہ میں وہ گمراہ ہو گئے ہیں اور (اب) وہ (ہدایت کے حاصل کرنے کے لئے) کوئی راہ نہیں پا سکتے۔ " WHERE ayatNumber = 49 AND chapterNumber = 17;
UPDATE verses SET ts = "اور انہوں نے (یہ بھی) کہا ہے (کیا) جب ہم (مرکر) ہڈیاں ہو جائیں گے اور کچھ عرصہ کے بعد گل کر ہڈیوں کا (بھی) چورا بن جائیں گے (تو ہمیں از سر نو زندہ کیا جائے گا اور) کیا واقعی ہمیں ایک نئی مخلوق کی صورت میں اٹھایا جائے گا؟ " WHERE ayatNumber = 50 AND chapterNumber = 17;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) تم (خواہ) پتھر بن جائو یا لوہا۔ " WHERE ayatNumber = 51 AND chapterNumber = 17;
UPDATE verses SET ts = "یا کوئی اور ایسی مخلوق جو تمہارے دلوں میں ان سے بھی سخت نظر آتی ہو (تب بھی تم کو دوبارہ زندہ کیا جائے گا) (یہ سن کر) وہ ضرور کہیں گے (کہ) کون ہمیں دوبارہ (زندہ کرکے وجود میں) لائے گا۔ تو (انہیں) کہہ (کہ) وہی (خدا) جس نے تمہیں پہلی بار پیدا کیا تھا۔ اس پر وہ لازماً تعجب سے تمہاری طرف (دیکھتے ہوئے) سر ہلائیں گے اور کہیں گے (کہ) یہ (زندہ کیے جانے کا معاملہ) کب ہوگا؟ (جب وہ ایسا کہیں تو) تو (ان سے) کہہ (کہ) بالکل ممکن ہے کہ وہ (وقت اب) قریب (آچکا) ہو۔ " WHERE ayatNumber = 52 AND chapterNumber = 17;
UPDATE verses SET ts = "(یہ وعدہ اس دن پورا ہوگا) جس دن وہ تمہیں بلائے گا تو تم اس کی تعریف کرتے ہوئے اس کا حکم مانو گے (اور فوراً حاضر ہو جائو گے) اور تم سمجھ رہے ہوگے کہ تم (دنیا میں) تھوڑا ہی ٹھہرے تھے۔ " WHERE ayatNumber = 53 AND chapterNumber = 17;
UPDATE verses SET ts = "اور تو میرے بندوں سے کہہ (کہ) وہی بات کہا کریں جو (سب سے) زیادہ اچھی ہو (کیونکہ) شیطان یقیناً ان کے درمیان فساد ڈالتا رہتا ہے۔ شیطان انسان کا کھلا (کھلا) دشمن ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 17;
UPDATE verses SET ts = "تمہارا رب تمہیں (سب سے) زیادہ جانتا ہے اگر وہ چاہے گا تو تم پر رحم کرے گا اور اگر وہ چاہے گا تو تمہیں عذاب دے گا اور (اے رسول)! ہم نے تجھے ان کا ذمہ وار بنا کر نہیں بھیجا۔ " WHERE ayatNumber = 55 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جو (وجود بھی) آسمانوں اور زمین میں (بسنے والے) ہیں انہیں تمہارا رب (سب سے) زیادہ جانتا ہے اور ہم نے یقیناً انبیاء میں سے بعض کو بعض (دوسرے انبیائ) پر فضیلت دی ہے اور دائود کو (بھی) ہم نے زبور دی تھی۔ " WHERE ayatNumber = 56 AND chapterNumber = 17;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) جن لوگوں کے متعلق تمہارا دعویٰ ہے کہ وہ اس کے سوا الوہیت رکھتے ہیں انہیں (اپنی مدد کے لئے) پکارو (تو تمہیں معلوم ہو جائے گا کہ) نہ وہ (تمہاری کسی) تکلیف کو تم سے دور کرنے کا اختیار رکھتے ہیں اور نہ (تمہاری حالت میں) کوئی تبدیلی پیدا کرنے کی (طاقت رکھتے ہیں) ۔" WHERE ayatNumber = 57 AND chapterNumber = 17;
UPDATE verses SET ts = "وہ لوگ جنہیں وہ پکارتے ہیں وہ بھی اپنے رب کے قرب کے لئے کوئی ذریعہ تلاش کرتے ہیں (یعنی یہ دیکھتے رہتے ہیں) کہ کون خدا کے زیادہ قریب ہے (تا کہ ہم اس کی مدد حاصل کریں) اور وہ ہمیشہ اس کی رحمت کے امیدوار رہتے ہیں اور اس کے عذاب سے ڈرتے رہتے ہیں۔ تیرے رب کا عذاب یقیناً ایسا ہے جس سے خوف کیا جاتا ہے۔  " WHERE ayatNumber = 58 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (روئے زمین پر) کوئی ایسی بستی نہیں (ہوگی) جسے ہم قیامت کے دن سے پہلے ہلاک نہ کر دیں یا اسے بہت سخت عذاب نہ دیں۔ یہ بات تقدیر (الٰہی) میں (پہلے سے) لکھی ہوئی ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 17;
UPDATE verses SET ts = "کیا ہمیں نشانات کے بھیجنے میں اس کے سوا اور کوئی (امر) مانع ہو سکتا ہے کہ پہلے لوگوں نے (ان  نشانات کو) جھٹلا دیا تھا (اور ان سے کوئی فائدہ نہ اٹھایا تھا لیکن یہ کوئی وجہ نشان بھیجنے سے رکنے کی نہیں) چنانچہ (جب) ہم نے ثمود (کی قوم) کو ایک اونٹنی ایک روشن نشان کے طور پر دی تو انہوں نے اس پر ظلم کیا اور ہم نشانوں کو (بدانجام سے) ڈرانے کے لئے ہی بھیجا کرتے ہیں۔ " WHERE ayatNumber = 60 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب ہم نے تجھے کہا تھا (کہ) تمہارا رب ضرور ان لوگوں کو ہلاک (کرنے کا فیصلہ) کر چکا ہے (تب بھی انہوں نے فائدہ نہیں اٹھایا) اور جو رئویا ہم نے تجھے دکھائی تھی اسے (بھی) اور اس درخت کو (بھی) جسے قرآن میں ملعون قرار دیا گیا ہے ہم نے لوگوں کے لئے صرف امتحان کا ذریعہ بنایا تھا اور(باوجود اس کے کہ) ہم انہیں ڈراتے (چلے جاتے) ہیں پھر (بھی) وہ (یعنی ہمارا ڈرانا) انہیں ایک خطرناک سرکشی میں بڑھا رہا ہے۔ " WHERE ayatNumber = 61 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جب ہم نے فرشتوں کو کہا تھا (کہ) تم آدم کے ساتھ (مل) کر سجدہ کرو تو انہوں نے (اس حکم کے مطابق آدم کے ساتھ مل کر) سجدہ کیا تھا۔ مگر ابلیس نے (نہ کیا تھا) اس نے کہا تھا (کہ) کیا میں اس (بشر) کے ساتھ (مل کر) سجدہ کروں جسے تو نے کیچڑ سے پیدا کیا ہے۔ " WHERE ayatNumber = 62 AND chapterNumber = 17;
UPDATE verses SET ts = "(نیز) اس نے کہا (کہ اے اللہ! تو ہی مجھے) بتا کہ کیا یہ (میرا مطاع ہو سکتا) ہے جسے تو نے مجھ پر شرف دے دیا ہے اگر تو نے مجھے قیامت کے دن تک مہلت دی تو (مجھے تیری ہی ذات کی قسم ہے کہ) میں اس کی (تمام) اولاد کو قابو میں کرلوں گا۔ سوائے تھوڑے سے لوگوں کے (جنہیں تو بچالے) ۔" WHERE ayatNumber = 63 AND chapterNumber = 17;
UPDATE verses SET ts = "(اللہ نے) فرمایا‘ چل (دُور ہو) کیونکہ تیری بھی اور ان میں سے جو تیری پیروی کریں‘ (ان کی بھی) سزا جہنم ہے (اور یہ) پورا پورا بدلہ (ہے) " WHERE ayatNumber = 64 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (ہم نے کہا جا) ان میں سے جس پر تیرا بس چلے اسے اپنی آواز سے فریب دے کر (اپنی طرف) بلا اور اپنے سواروں اور پیادوں کو ان پر چڑھالا۔ اور (ان کے) مالوں اور اولادوں میں ان کا حصہ دار بن اور ان سے (جھوٹے) وعدے کر (اور پھر اپنی کوششوں کا نتیجہ دیکھ) اور شیطان جو وعدے بھی کرتا ہے فریب کی نیت ہی سے کرتا ہے۔ " WHERE ayatNumber = 65 AND chapterNumber = 17;
UPDATE verses SET ts = "جو میرے بندے ہیں‘ ان پر ہرگز تیرا تسلط نہیں (ہوسکتا) اور (اے میرے بندے)! تیرا رب کارسازی میں (تیرے لئے) کافی ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 17;
UPDATE verses SET ts = "(اور اے میرے بندو)! تمہارا رب (تو) وہ ہے جو تمہارے لئے کشتیوں کو سمندر میں چلاتا ہے تا کہ تم اس کے فضل کو ڈھونڈو۔ وہ یقیناً تم پر باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 67 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب سمندر میں (طغیانی پیدا ہونے کی وجہ سے) تمہیں تکلیف پہنچے تو اس کے سوا (دوسرے وجود) جن کو تم پکارتے ہو (تمہارے ذہنوں سے) غائب ہو جاتے ہیں۔ پھر جب وہ تمہیں بچا کر خشکی پر لاتا ہے تو تم (اس کی طرف سے) اعراض کر لیتے ہو اور انسان بہت ہی ناشکر گذار ہے۔ " WHERE ayatNumber = 68 AND chapterNumber = 17;
UPDATE verses SET ts = "کیا پھر تم (یہ سمجھتے ہو کہ تم) اس بات سے بالکل امن میں ہو کہ وہ (یاتو) تمہیں خشکی کے کنارہ پر (زمین میں) دھنسا دے یا تم پر سنگریزے نازل کرے اور پھر تم اپنا کوئی کارساز (اور چارہ گر) نہ پائو۔ " WHERE ayatNumber = 69 AND chapterNumber = 17;
UPDATE verses SET ts = "یا تم اس بات سے بے خوف ہو کہ وہ تمہیں (پھر) دوسری بار اس (سمندر) میں لوٹا لائے اور تم پر ایک تند ہوا چھوڑدے اور تمہارے کفر کی وجہ سے تمہیں غرق کر دے (اور) پھر اس (عذاب) پر تم ہمارے خلاف اپنا کوئی مددگار نہ پائو۔ " WHERE ayatNumber = 70 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے بنی آدم کو (بہت) شرف بخشا ہے اور ان کے لئے خشکی اور تری میں سواری کا سامان پیدا کیا ہے اور انہیں پاکیزہ چیزوں سے رزق دیا ہے اور جو مخلوق ہم نے پیدا کی ہے اس میں سے ایک کثیر حصہ پر ہم نے انہیں بڑی فضیلت دی ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 17;
UPDATE verses SET ts = "اور (اس دن کو یاد کرو) جس دن ہم ہر ایک گروہ کو ان کے پیشوا سمیت بلائیں گے پھر جن کے دائیں ہاتھ میں ان (کے اعمال) کی کتاب دی جائے گی‘ وہ (بڑے شوق سے) اپنی کتاب کو پڑھیں گے اور ان پر ذرہ بھر (بھی) ظلم نہیں کیا جائے گا۔ " WHERE ayatNumber = 72 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جو اس (دنیا) میں اندھا رہے گا وہ آخرت میں (بھی) اندھا ہوگا اور (اسی طرح وہ) اپنے (طور اور) طریق میں سب سے بڑھ کر بھٹکا ہوا ہوگا۔ " WHERE ayatNumber = 73 AND chapterNumber = 17;
UPDATE verses SET ts = "اور قریب تھا کہ اس (کلام) کی وجہ سے جو ہم نے تجھ پر وحی سے نازل کیا ہے وہ تجھے (سخت سے سخت) عذاب میں مبتلا کرتے تا کہ تو (ان سے ڈر کر) اس (کلام) کے سوا کچھ اور (کلام اپنے پاس سے) بنا کر ہماری طرف منسوب کردے اور (اگر تو ایسا کرتا تو) اس صورت میں وہ یقیناً تجھے (اپنا) گہرا دوست بنا لیتے۔ " WHERE ayatNumber = 74 AND chapterNumber = 17;
UPDATE verses SET ts = "اور اگر ہم تجھے (قرآن دے کر) ثبات نہ بخش چکے ہوتے تو پھر بھی (یہی ہوتا کہ) تو (بغیر الہام کے بھی) ان کی طرف بہت کم باتوں میں مائل ہوتا (مگر اب تو تجھے وحی الٰہی نے صحیح راستہ بتا دیا ہے) ۔" WHERE ayatNumber = 75 AND chapterNumber = 17;
UPDATE verses SET ts = "(اور اگر جیسا کہ ان کا خیال ہے تو ہم پر افترا باندھنے والا ہوتا) تو اس صورت میں ہم تجھے زندگی کا بڑا عذاب اور موت کا بڑا عذاب چکھاتے (اور) پھر تو ہمارے مقابل پر اپنا کوئی (بھی) مددگار نہ پاتا۔ " WHERE ayatNumber = 76 AND chapterNumber = 17;
UPDATE verses SET ts = "اور وہ یقیناً تجھے اس ملک سے نکالنے کے مختلف حیلے کرتے رہتے ہیں تا کہ تجھے ڈرا کر ملک سے باہر نکال دیں۔ مگر (ایسا ہوا تو) وہ (خود بھی) تیرے بعد تھوڑا عرصہ ہی (محفوظ) رہیں گے (اور جلد تباہ ہو جائیں گے) ۔" WHERE ayatNumber = 77 AND chapterNumber = 17;
UPDATE verses SET ts = "یہ سلوک ہمارے اس سلوک کے مطابق ہوگا جو ہم نے تجھ سے پہلے (گذرے ہوئے) اپنے رسولوں (کی قوموں) کے ساتھ کیا تھا اور تو ہمارے طریق عمل میں کوئی فرق نہیں پائے گا۔ " WHERE ayatNumber = 78 AND chapterNumber = 17;
UPDATE verses SET ts = "تو سورج کے ڈھلنے (کے وقت) سے لیکر رات کے خوب تاریک ہو جانے (کے وقت) تک (مختلف گھڑیوں میں) نماز کو عمدگی سے ادا کیا کر اور صبح کے وقت (قرآن) کے پڑھنے کو بھی (لازم سمجھ) صبح کے وقت (قرآن) کا پڑھنا یقیناً (اللہ کے حضور میں ایک) مقبول عمل ہے۔ " WHERE ayatNumber = 79 AND chapterNumber = 17;
UPDATE verses SET ts = "اور رات کو بھی تو اس (قرآن) کے ذریعہ سے کچھ سو لینے کے بعد شب بیداری کیا کر‘ جو تجھ پر ایک زائد انعام ہے (اس طرح پر) بالکل متوقع ہے کہ تیرا رب تجھے حمد والے مقام پر کھڑا کر دے۔ " WHERE ayatNumber = 80 AND chapterNumber = 17;
UPDATE verses SET ts = "اور کہہ (کہ) اے میرے رب! مجھے نیک طور پر (دوبارہ مکہ میں) داخل کر اور ذکر نیک چھوڑنے والے طریق پر (مکہ سے) نکال اور اپنے پاس سے میرا کوئی مددگار (اور) گواہ مقرر کر۔ " WHERE ayatNumber = 81 AND chapterNumber = 17;
UPDATE verses SET ts = "اور سب لوگوں سے کہہ دے (کہ بس اب) حق آگیا ہے اور باطل بھاگ گیا ہے اور باطل تو ہے ہی بھاگ جانے والا۔ " WHERE ayatNumber = 82 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم قرآن میں آہستہ آہستہ وہ (تعلیم) اتار رہے ہیں جو مومنوں کے لئے (تو) شفا اور رحمت (کا موجب) ہے اور ظالموں کو صرف خسارہ میں بڑھاتی ہے۔ " WHERE ayatNumber = 83 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جب ہم انسان پر انعام کریں تو وہ روگردان ہو جاتا ہے اور اپنے پہلو کو (اس سے) دور کر لیتا ہے اور جب اسے تکلیف پہنچے تو وہ بہت ہی مایوس ہو جاتا ہے۔ " WHERE ayatNumber = 84 AND chapterNumber = 17;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ ہم میں سے) ہر ایک (فریق) اپنے (اپنے) طریق پر عمل کر رہا ہے پس (اپنے رب پر ہی فیصلہ چھوڑ دو کیونکہ) تمہارا رب اسے جو زیادہ صحیح راستہ پر ہے بہتر جانتا ہے (اس لئے اس کا فیصلہ سچے کی سچائی کو ضرور روشن کر دے گا) " WHERE ayatNumber = 85 AND chapterNumber = 17;
UPDATE verses SET ts = "اور وہ تجھ سے روح کے متعلق سوال کرتے ہیں تو (انہیں) کہہ (کہ) روح میرے رب کے حکم سے (پیدا ہوئی) ہے اور تمہیں (اس کے متعلق) علم سے کم ہی (حصہ) دیا گیا ہے۔ " WHERE ayatNumber = 86 AND chapterNumber = 17;
UPDATE verses SET ts = "اوراگر ہم چاہیں تو یقیناً جو (کلام الٰہی) ہم نے تجھ پروحی (کے ذریعہ سے نازل) کیا ہے اسے (دنیا سے) اٹھالیں۔ پھرتواس امرمیں اپنے لئے ہمارے خلاف کوئی کارساز نہیں پاسکے گا۔ " WHERE ayatNumber = 87 AND chapterNumber = 17;
UPDATE verses SET ts = "سوائے اس کے کہ تیرے رب کی (خاص) رحمت ہو (جو اسے واپس لے آئے مگر یہ قرآن مٹ نہیں سکتا کیونکہ) تجھ پر تیرے رب کا یقیناً (بہت) بڑا فضل ہے۔ " WHERE ayatNumber = 88 AND chapterNumber = 17;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) اگر تمام انسان (بھی) اور جن (بھی) اس قرآن کی نظیر لانے کے لئے جمع ہو جائیں تو (پھر بھی) وہ اس کی نظیر نہیں لا سکیں گے خواہ وہ ایک دوسرے کے مددگار (ہی کیوں نہ) بن جائیں۔ " WHERE ayatNumber = 89 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ہم نے اس قرآن میں یقیناً ہر ایک (ضروری) بات کو مختلف پیرایوں سے بیان کیا ہے۔ پھر (بھی) اکثر لوگوں نے (اس کے متعلق) کفر (کی راہ اختیار کرنے) کے سوا ہر بات سے انکار کر دیا ہے۔ " WHERE ayatNumber = 90 AND chapterNumber = 17;
UPDATE verses SET ts = "اور انہوں نے (یہ بھی) کہا ہے (کہ) ہم ہرگز تیری (کوئی) بات نہیں مانیں گے جب تک (ایسا نہ ہو کہ) تو ہمارے لئے زمین سے کوئی چشمہ جاری کرے۔ " WHERE ayatNumber = 91 AND chapterNumber = 17;
UPDATE verses SET ts = "یا تیرا کھجوروں اور انگوروں کا کوئی باغ ہو اور تو اس کے اندر خوب (کثرت سے) نہریں جاری کرے۔ " WHERE ayatNumber = 92 AND chapterNumber = 17;
UPDATE verses SET ts = "یا جیسا کہ تیرا دعویٰ ہے تو ہم پر آسمان کے ٹکڑے گرائے۔ یا اللہ اور فرشتوں کو (ہمارے) آمنے سامنے لاکھڑا کرے۔ " WHERE ayatNumber = 93 AND chapterNumber = 17;
UPDATE verses SET ts = "یا تیرا سونے کا کوئی گھر ہو‘ یا تو آسمان پر چڑھ جائے اور ہم تیرے (آسمان پر) چڑھ جانے پر بھی ایمان نہیں لائیں گے جب تک کہ تو (اوپر جاکر) ہم پر کوئی کتاب (نہ) اتارے جسے ہم (خود) پڑھیں تو (انہیں) کہہ (کہ) میرا رب (ایسی باتوں سے) پاک ہے۔ میں (تو) صرف بشر رسول ہوں۔ " WHERE ayatNumber = 94 AND chapterNumber = 17;
UPDATE verses SET ts = "اور ان لوگوں کو اس ہدایت پر جو ان کے پاس آئی ہے ایمان لانے سے صرف اس بات نے روکا ہے کہ انہوں نے (اپنے دلوں میں) کہا (کہ) کیا اللہ نے ایک بشر کو رسول بنا کر بھیج  دیا ہے۔ " WHERE ayatNumber = 95 AND chapterNumber = 17;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) اگر زمین پر فرشتے (بستے) ہوتے جو زمین پر اطمینان سے چلتے پھرتے تو (اس صورت میں) ہم ضرور ان پر آسمان سے کسی فرشتہ کو (ہی) رسول بنا کر اتارتے۔ " WHERE ayatNumber = 96 AND chapterNumber = 17;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) میرے درمیان اور تمہارے درمیان گواہ کے طور پر اللہ ہی کافی ہے وہ اپنے بندوں کو جاننے والا (اور) دیکھنے والا ہے۔ " WHERE ayatNumber = 97 AND chapterNumber = 17;
UPDATE verses SET ts = "اور جسے اللہ ہدایت دے وہی ہدایت پر ہوتا ہے اور جسے وہ گمراہ کرے تو تو اس کے (یعنی اللہ کے) مقابل پر اس کا کوئی بھی مددگار نہیں پائے گا اور قیامت کے دن ہم انہیں ان کے مقاصد (اور نیتوں) کے مطابق اندھے اور گونگے اور بہرے ہونے کی حالت میں جمع کریں گے ان کا ٹھکانا جہنم ہوگا۔ جب بھی وہ (جہنم ذرا) ٹھنڈی ہوگی تو ان پر عذاب کو شدید کر دیا جائے گا جو (عذاب کہ) بھڑکنے والی آگ کا ہوگا۔ " WHERE ayatNumber = 98 AND chapterNumber = 17;
UPDATE verses SET ts = "یہ (آک) ان (ہی کے اعمال) کی جزا ہوگی۔ کیونکہ انہوں نے ہمارے نشانوں کا انکار کیا اور کہا کہ کیا جب ہم (مرکر) ہڈیاں اور چورا چورا ہو جائیں گے (تو ہمیں ازسر نو زندہ کیا جائے گا اور) کیا واقعی ہمیں ایک نئی مخلوق کی صورت میں اٹھایا جائے گا۔ " WHERE ayatNumber = 99 AND chapterNumber = 17;
UPDATE verses SET ts = "کیا وہ (ابھی تک) سمجھ نہیں سکے کہ وہ (ہستی) جس نے آسمانوں اور زمین کو پیدا کیا ہے اس بات پر بھی قادر ہے کہ وہ ان جیسے (اور لوگ) پیدا کرے اور اس میں کوئی شک نہیں کہ اس نے ان کے لئے ایک میعاد مقرر کر دی ہے پھر (بھی) ان ظالموں نے صرف ناشکری کی راہ اختیار کر لی ہے۔ " WHERE ayatNumber = 100 AND chapterNumber = 17;
