UPDATE verses SET ts = "کیا تو سمجھتا ہے کہ کہف اور رقیم والے (لوگ) ہمارے نشانوں میں سے کوئی اچنبھا (نشان) تھے (جن کی نظیر پھر کبھی نہ پائی جا سکتی ہو) " WHERE ayatNumber = 10 AND chapterNumber = 18;
UPDATE verses SET ts = "جب وہ (چند) نوجوان وسیع غار میں پناہ گزیں ہوئے اور (دعا کرتے ہوئے) انہوں نے کہا (کہ) اے ہمارے رب ہمیں اپنے حضور سے (خاص) رحمت عطا کر اور ہمارے لئے ہمارے (اس) معاملہ میں رشد و ہدایت کا سامان مہیا کر۔ " WHERE ayatNumber = 11 AND chapterNumber = 18;
UPDATE verses SET ts = "جس پر ہم نے اس وسیع غار میں چند گنتی کے سالوں کے لئے انہیں (بیرونی حالات کے) سننے سے محروم کر دیا۔ " WHERE ayatNumber = 12 AND chapterNumber = 18;
UPDATE verses SET ts = "پھر ہم نے انہیں اٹھایا‘ تا کہ ہم جان لیں کہ جتنی مدت وہ (وہاں) ٹھہرے رہے تھے اسے (مسیح کے متبع) دونوں گروہوں میں سے زیادہ محفوظ رکھنے والا کونسا گروہ ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 18;
UPDATE verses SET ts = "(اب) ہم ان کی اہم خبر بالکل صحیح طور پر تیرے پاس بیان کرتے ہیں وہ چند نوجوان تھے جو اپنے رب  پر (حقیقی) ایمان لائے تھے اور انہیں ہم نے ہدایت میں (اور بھی) بڑھایا تھا۔ " WHERE ayatNumber = 14 AND chapterNumber = 18;
UPDATE verses SET ts = "اور جب وہ (اپنے وطن سے نکلنے کے لئے) اٹھے تو ہم نے ان کے دلوں کو مضبوط کر دیا۔ تب انہوں نے (ایک دوسرے سے) کہا (کہ) ہمارا رب (وہ ہے جو) آسمانوں اور زمین کا (بھی) رب ہے ہم اس کے سوا کسی اور معبود کو ہرگز (کبھی) نہیں پکاریں گے ورنہ ہم ایک حق سے دور بات کہنے والے ہوں گے۔ " WHERE ayatNumber = 15 AND chapterNumber = 18;
UPDATE verses SET ts = "ان لوگوں یعنی ہماری قوم نے اس (معبود برحق) کو چھوڑ کر (اپنے لئے) اور معبود بنا لیے ہیں۔ وہ ان کے ثبوت میں کیوں کوئی روشن دلیل نہیں لاتے۔ پھر (وہ کیوں نہیں سمجھتے) کہ جو شخص اللہ پر جھوٹ باندھے اس سے بڑھ کر ظالم (اور) کون (ہوسکتا) ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اس وقت) جب (کہ) تم نے ان سے اور (نیز) اللہ کے سوا جس چیز کی (بھی) وہ پرستش کرتے ہیں (اس سے) کنارہ کشی کرلی ہے تو (اب) تم اس وسیع پہاڑی پناہ گاہ میں بیٹھے رہو (ایسا کرو گے تو) تمہارا رب اپنی رحمت (کی کوئی راہ) تمہارے لئے کھول دے گا اور تمہارے لئے تمہارے اس معاملہ میں کوئی سہولت کا سامان مہیا کر دے گا۔ " WHERE ayatNumber = 17 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اے مخاطب) تو سورج کو دیکھتا ہے کہ جب وہ چڑھتا ہے تو ان کی وسیع جائے پناہ سے دائیں طرف کو ہٹ کر گزرتا ہے اور جب وہ ڈوبتا ہے تو ان سے بائیں طرف کو ہٹ کر گذرتا ہے اور وہ اس غار کے اندر ایک فراخ جگہ میں (رہتے) تھے یہ بات اللہ (کی نصرت) کے نشانوں میں سے (ایک نشان) ہے جسے اللہ (ہدایت کا) راستہ دکھائے وہی ہدایت پر ہوتا ہے اور جسے وہ گمراہ کرے اس کا تو (کبھی) کوئی دوست (اور) راہنما نہیں پائے گا۔ " WHERE ayatNumber = 18 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اے مخاطب) تو انہیں بیدار سمجھتا ہے حالانکہ وہ سوتے ہیں اور ہم انہیں دائیں طرف (بھی) پھرائیں گے اور بائیں طرف (بھی) اور ان کا کتا (بھی ان کے ساتھ ساتھ) صحن میں ہاتھ پھیلائے (موجود) رہے گا۔ اگر تو ان کے حالات سے آگاہ ہو جائے تو تو ان سے بھاگنے کے لئے پیٹھ پھیر لے اور ان کی وجہ سے رعب سے بھر جائے۔ " WHERE ayatNumber = 19 AND chapterNumber = 18;
UPDATE verses SET ts = "اور اسی طرح ہم نے انہیں (بیکسی کی حالت سے) اٹھایا۔ اس پر وہ آپس میں (حیرت سے) ایک دوسرے سے سوال کرنے لگے (اور) ان میں سے ایک کہنے لگا (کہ) تم یہاں کتنی دیر ٹھہرے رہے ہو (جو اس کے مخاطب تھے) انہوں نے کہا (کہ) ہم ایک دن یا دن کا کچھ حصہ ٹھہرے ہیں (تب) انہوں نے (یعنی دوسروں نے) کہا (کہ) جو (عرصہ) تم (یہاں) ٹھہرے رہے ہو اسے تمہارا رب (ہی) بہتر جانتا ہے۔ پس (اس بحث کو چھوڑو اور)  اپنے یہ پرانے سکے دے کر اپنے میں سے کسی ایک کو اس شہر کی طرف بھیجو وہ (جاکر) دیکھے کہ اس (شہر) میں سے کس کا غلہ سب سے اچھا ہے پھر (جس کا غلہ سب سے اچھا ہو) اس سے کچھ کھانے کا سامان لے آئے اور وہ ہوشیاری سے (لوگوں کی) راز کی باتیں معلوم کرنے کی کوشش کرے اور تمہارے متعلق کسی کو ہرگز (کوئی) علم نہ ہونے دے۔ " WHERE ayatNumber = 20 AND chapterNumber = 18;
UPDATE verses SET ts = "کیونکہ اگر وہ تم پر غلبہ پالیں تو یقیناً تمہیں سنگسار کر دیں گے یا (جبراً) تمہیں واپس اپنے مذہب میں داخل کر لیں گے اور اس صورت میں تم کبھی (بھی) کامیاب نہیں ہو سکو گے۔ " WHERE ayatNumber = 21 AND chapterNumber = 18;
UPDATE verses SET ts = "اور اسی طرح ہم نے (لوگوں کو) ان (کے حالات) سے آگاہ کیا ہے تا انہیں معلوم ہو کہ اللہ کا وعدہ پورا ہو کر رہنے والا ہے اور (یہ بھی) کہ اس (موعودہ) گھڑی (کے آنے )میں کچھ بھی شک نہیں (اور اس وقت کو بھی یاد کرو) جب وہ اپنے کام کے متعلق آپس میں گفتگو کرنے لگے اور انہوں نے (ایک دوسرے سے) کہا (کہ) تم ان (کے رہنے کے مقام) پر کوئی عمارت بنائو۔ ان کا رب ان (کے حال) کو سب سے بہتر جانتا تھا (آخر) جنہوں نے اپنے قول میں غلبہ حاصل کر لیا انہوں نے کہا (کہ) ہم (تو) ان  (کے رہنے کے مقام) پر مسجد (ہی) بنائیں گے۔ " WHERE ayatNumber = 22 AND chapterNumber = 18;
UPDATE verses SET ts = "وہ (لوگ جو حقیقت حال سے بے خبر ہیں ضرور) غیب کے متعلق اٹکل پچو باتیں کرتے ہوئے (کبھی) کہیں گے (کہ وہ صرف) تین (آدمی) تھے۔ جن کے ساتھ چوتھا ان کا کتا تھا اور (کبھی یہ) کہیں گے (کہ وہ) پانچ تھے جن کے ساتھ چھٹا ان کا کتا تھا اور (ان میں سے بعض یوں بھی) کہیں گے (کہ وہ) سات تھے اور ان کے ساتھ آٹھواں ان کا کتا تھا۔ تو (انہیں) کہہ (کہ) ان کی صحیح گنتی کو اللہ (ہی) بہتر جانتا ہے۔ (اور) تھوڑے لوگوں کے سوا انہیں کوئی نہیں جانتا۔ پس تو ان کے متعلق مضبوط بحث کے سوا کوئی بحث نہ کر۔ اور ان کے بارہ میں ان میں سے کسی سے حقیقت حال دریافت نہ کر۔ " WHERE ayatNumber = 23 AND chapterNumber = 18;
UPDATE verses SET ts = "اور تو کسی بات کے متعلق (دعویٰ سے) ہرگز نہ کہہ (کہ) میں کل یہ (کام) ضرور کروں گا۔ " WHERE ayatNumber = 24 AND chapterNumber = 18;
UPDATE verses SET ts = "ہاں (صرف اس طرح کروں گا) جس طرح اللہ چاہے گا۔ اور جب (کسی وقت) تو بھول جائے تو (یاد آجانے پر) اپنے رب (کے وعدوں) کو یاد (کرلیا) کر اور کہہ دیا کر (کہ مجھے کامل) امید ہے کہ میرا رب (آخر مجھے کامیاب کردے گا اور) مجھے اس راستہ پر چلائے گا جو ہدایت پانے کے لحاظ سے اس (راستہ) سے (جو میرا تجویز کردہ ہے تکمیل کے) زیادہ قریب ہوگا۔ " WHERE ayatNumber = 25 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (بعض لوگ یہ بھی کہتے ہیں کہ) وہ اپنی وسیع پناہ گاہ میں تین سو سال تک رہے تھے اور (اس عرصہ پر) نو (سال) انہوں نے اور بڑھائے تھے۔ " WHERE ayatNumber = 26 AND chapterNumber = 18;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) جتنا (عرصہ غار میں) وہ ٹھہرے رہے اسے اللہ (ہی) بہتر جانتا ہے۔ آسمانوں اور زمین کا غیب اسی کے لئے ہے وہ خوب ہی دیکھنے والا اور خوب ہی سننے والا ہے۔ ان (لوگوں) کا اس کے سوا کوئی بھی مددگار نہیں ہے اور وہ اپنے حکم (اور فیصلوں) میں کسی کو (اپنا) شریک نہیں بناتا۔ " WHERE ayatNumber = 27 AND chapterNumber = 18;
UPDATE verses SET ts = "اور تیرے رب کی کتاب میں سے جو (حصہ) تجھ پر وحی (کے ذریعہ سے نازل) ہوتا ہے اسے پڑھ (کر لوگوں کو سنا) اس کی باتوں کو کوئی بھی تبدیل کر سکنے والا نہیں ہے اور اسے چھوڑ کر تو کوئی پناہ کی جگہ نہیں پائے گا۔ " WHERE ayatNumber = 28 AND chapterNumber = 18;
UPDATE verses SET ts = "اور اپنے آپ کو ان لوگوں کے ساتھ رکھ جو اپنے رب کو اس کی خوشنودی چاہتے ہوئے صبح و شام پکارتے ہیں اور تیری نظریں ان کو پیچھے چھوڑ کر آگے نہ نکل جائیں (اور اگر تو ایسا کرے گا تو) تو ورلی زندگی کی زینت چاہے گا اور جس کے دل کو ہم نے اپنی یاد سے غافل کر دیا ہو اور اس نے اپنی گری ہوئی خواہش کی پیروی اختیار کی ہو اور اس کا معاملہ حد سے بڑھا ہوا ہو اس کی فرمانبرداری مت کر۔ " WHERE ayatNumber = 29 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (لوگوں کو) کہہ دے (کہ) یہ سچائی تمہارے رب کی طرف سے ہی (نازل ہوئی) پس جو چاہے (اس پر) ایمان لائے اور جو چاہے (اس کا) انکار کر دے (مگر یہ یاد رکھے کہ) ہم نے ظالموں کے لئے یقیناً ایک آگ تیار کی ہے جس کی چار دیواری نے (اب بھی) انہیں گھیرا ہوا ہے اور اگر وہ فریاد کریں گے تو ایسے پانی سے ان کی فریاد رسی کی جائے گی جو پگھلے ہوئے تانبے کی طرح ہوگا (اور) چہروں کو جھلس دے گا۔ وہ بہت بری پینے کی چیز ہوگی۔ اور وہ (آگ) برا ٹھکانا ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 18;
UPDATE verses SET ts = "(ہاں) جو لوگ ایمان لائے اور انہوں نے نیک (اور مناسب حال) عمل کیے ہیں (وہ یقیناً بڑے اجر پائیں گے) جنہوں نے اچھے کام کیے ہوں ہم ان کا اجر ہرگز ضائع نہیں کرتے۔ " WHERE ayatNumber = 31 AND chapterNumber = 18;
UPDATE verses SET ts = "(ہاں ہاں) انہی لوگوں کے لئے دائمی رہائش کے باغات (مقدر) ہیں۔ (ان میں) ان کے (اپنے انتظام کے) نیچے نہریں بہتی ہوں گی۔ ان کے لئے ان میں سونے کے گنگنوں کی قسم کے زیور بنوائے جائیں گے اور وہ باریک ریشم کے اور موٹے ریشم کے سبز کپڑے پہنیں گے (وہ) ان بہشتوں میں آراستہ پلنگوں پر تکیے لگائے (ہوئے بیٹھے) ہوں گے۔ یہ کیا ہی اچھا اجر ہے اور وہ بہت ہی اچھا ٹھکانا ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 18;
UPDATE verses SET ts = "اور تو ان کے سامنے ان دو شخصوں کی حالت بیان کر جن میں سے ایک کو ہم نے انگوروں کے دو باغ دئیے تھے اور انہیں ہم نے کھجوروں کے درختوں سے (ہرطرف سے) گھیر رکھا تھا اور ہم نے ان (دونوں کی مقبوضات) کے درمیان کچھ کھیتی بھی پیدا کی تھی۔ " WHERE ayatNumber = 33 AND chapterNumber = 18;
UPDATE verses SET ts = "ان دونوں باغوں نے (اپنا) اپنا پھل (خوب) دیا اور اس میں سے کچھ (بھی) کم نہ کیا۔ اور ان کے درمیان ہم نے ایک نہر جاری کی تھی۔ " WHERE ayatNumber = 34 AND chapterNumber = 18;
UPDATE verses SET ts = "اور اسے بہت پھل حاصل (ہوتا) تھا۔ اسی وجہ سے اس نے اپنے ساتھی کو اس سے باتیں کرتے ہوئے (فخریہ طور پر) کہا (کہ دیکھ) تیری نسبت میرا مال زیادہ اور جتھا معزز ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (ایک دفعہ) وہ اپنی جان پر ظلم کرتے ہوئے اپنے باغ میں داخل ہوا (اور) اس نے (اپنے ساتھی سے) کہا (کہ) میں نہیں سمجھتا کہ یہ (میرا باغ) کبھی تباہ ہوگا۔ " WHERE ayatNumber = 36 AND chapterNumber = 18;
UPDATE verses SET ts = "اور میں نہیں سمجھتا کہ وہ (موعودہ تباہی کی) گھڑی (کبھی) آنے والی ہے اور اگر (بالفرض) مجھے میرے رب کی طرف لوٹا (بھی) دیا جائے تو میں (وہاں) بھی یقیناً اس سے بہتر ٹھکانا پائوں گا۔ " WHERE ayatNumber = 37 AND chapterNumber = 18;
UPDATE verses SET ts = "اس کے ساتھی نے اس سے سوال و جواب کرتے ہوئے کہا (کہ) کیا تو نے اس (ہستی) کا انکار کر دیا ہے جس نے تجھے (اولاً)مٹی سے (اور) پھر نطفہ سے پیدا کیا اور پھر اس نے تجھے پورا آدمی بنایا۔ " WHERE ayatNumber = 38 AND chapterNumber = 18;
UPDATE verses SET ts = "(تمہارا تو یہ حال ہے) لیکن (میں تو یہ کہتا ہوں کہ) حق تو یہ ہے کہ اللہ ہی میرا رب ہے اور میں کسی کو (بھی) اپنے رب کا شریک نہیں بناتا۔ " WHERE ayatNumber = 39 AND chapterNumber = 18;
UPDATE verses SET ts = "اور جب تو اپنے باغ میں آیا تھا تو کیوں نہ تو نے کہا (کہ وہی ہوگا) جو اللہ چاہے گا (کیونکہ) اللہ ہی (کے فضل) سے ہر ایک قوت (حاصل ہوتی) ہے اگر تو مجھ (ناچیز) کو اپنی نسبت مال اور اولاد میں کم سمجھتا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 18;
UPDATE verses SET ts = "تو بالکل ممکن ہے‘ کہ میرا رب مجھے تیرے باغ سے کوئی بہتر (باغ) دے دے اور اس (تیرے باغ) پر اوپر سے کوئی آگ کا شعلہ گرائے۔ جس کی وجہ سے وہ ایک صاف اور چٹیل میدان ہوجائے۔ " WHERE ayatNumber = 41 AND chapterNumber = 18;
UPDATE verses SET ts = "یا اس کا پانی خشک ہو جائے (اور) پھر تو (یعنی اسلام کا دشمن) اس کی تلاش کی (بھی) طاقت نہ پائے (چنانچہ ایسا ہی ہوا) ۔" WHERE ayatNumber = 42 AND chapterNumber = 18;
UPDATE verses SET ts = "اور اس کے تمام  پھلوں کو تباہ کر دیا گیا اور وہ (یعنی باغ کا مالک) اس حال میں کہ وہ (باغ) اپنے سہاروں پر گرا ہوا تھا اس (مال) کی وجہ سے جو اس نے اس (باغ کی ترقی) کے لئے خرچ کیا تھا اپنے دونوں ہاتھ ملنے لگا اور کہنے لگا (کہ) اے کاش! میں کسی کو اپنے رب کا شریک نہ بناتا۔ " WHERE ayatNumber = 43 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اس وقت) کوئی جماعت بھی اس کے ساتھ نہ ہوئی جو اللہ کے سوا اس کی مدد کرتی۔ اور نہ وہ (اس کا کوئی) انتقام ہی لے سکا۔ " WHERE ayatNumber = 44 AND chapterNumber = 18;
UPDATE verses SET ts = "ایسے موقع پر مدد اللہ کی ہی (مفید) ہوتی ہے جو معبود حقیقی ہے اور وہ بدلہ دینے میں (بھی) سب سے اچھا ہے اور (اچھا) انجام (پیدا کرنے) کی رو سے بھی سب سے اچھا ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 18;
UPDATE verses SET ts = "اور تو ان کے سامنے اس ورلی زندگی کی حالت (بھی) کھول کر بیان کر‘ (کہ وہ) اس پانی کی طرح (ہے) جسے ہم نے بادل سے برسایا‘ پھر اس میں زمین کی روئیدگی مل گئی۔ پھر (آخر) وہ (بھوسے کا) چورا بن گئی۔ جسے ہوائیں اڑاتی (پھرتی) ہیں اور اللہ ہر بات پر قدرت رکھنے والا ہے۔ " WHERE ayatNumber = 46 AND chapterNumber = 18;
UPDATE verses SET ts = "مال اور بیٹے اس ورلی زندگی کی زینت ہیں اور باقی رہنے والے نیک (اور مناسب حال) کام (ہی جو ان چیزوں سے لئے جائیں) تیرے رب کے نزدیک بدلہ کے لحاظ سے (بھی) بہتر ہیں اور امید کے لحاظ سے (بھی) بہتر ہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اس دن بھی ان کے بہتر نتائج نمودار ہوں گے) جس دن ہم ان پہاڑوں کو (اپنی اپنی جگہ سے) چلا دیں گے اور تو سب اہل زمین (کو ایک دوسرے کے مقابلہ میں جنگ کے لئے) نکلتا ہوا دیکھے گا اور ہم ان (سب) کو اکٹھا کریں گے اور ان میں سے کسی کو (بھی باقی) نہیں چھوڑیں گے۔ " WHERE ayatNumber = 48 AND chapterNumber = 18;
UPDATE verses SET ts = "اور وہ صف باندھے ہوئے تیرے رب کے سامنے پیش کئے جائیں گے (اور ان سے کہا جائے گا کہ) دیکھ لو تم اسی طرح کمزوری کی حالت میں) ہمارے پاس آگئے ہو جس حالت میں ہم نے تمہیں پہلی بار پیدا کیا تھا (اور تم یہ امید نہیں رکھتے تھے) بلکہ تمہیں دعویٰ تھا کہ ہم تمہارے لئے کوئی وعدہ (کے پورا ہونے) کی ساعت مقرر نہیں کریں گے۔" WHERE ayatNumber = 49 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (ان کے اعمال کی) کتاب (ان کے سامنے) رکھ دی جائے گی۔ پس (اے مخاطب) تو ان مجرموں کو اس کی وجہ سے جو اس میں (لکھا) ہوگا ڈرتے دیکھے گا اور (اس وقت) وہ کہیں گے کہ اے (افسوس) ہماری تباہی (سامنے کھڑی ہے) اس کتاب کو کیا (ہوا) ہے (کہ) نہ کسی چھوٹی بات کو اس کا احاطہ کیے بغیر چھوڑتی ہے اور نہ کسی بڑی بات کو اور جو کچھ انہوں نے کیا (ہوا) ہوگا اسے اپنے سامنے حاضر پائیں گے اور تیرا رب کسی پر ظلم نہیں کرتا۔ " WHERE ayatNumber = 50 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جب ہم نے فرشتوں سے کہا تھا کہ تم آدم کے ساتھ (مل کر) سجدہ کرو۔ اس پر انہوں نے (تو اس حکم کے مطابق اس کے ساتھ ہوکر) سجدہ کیا مگر ابلیس نے (نہ کیا) وہ جنوں میں سے تھا سو اس نے (اپنی فطرت کے مطابق) اپنے رب کے حکم کی نافرمانی کی۔ (اے میرے بندو)! کیا تم مجھے چھوڑ کر اس (شیطان) کو اور اس کی نسل کو (اپنے) دوست بناتے ہو حالانکہ وہ تمہارے دشمن ہیں وہ (شیطان) ظالموں کے لئے بہت ہی برا بدلہ ثابت ہوا ہے۔ " WHERE ayatNumber = 51 AND chapterNumber = 18;
UPDATE verses SET ts = "میں نے نہ انہیں آسمانوں اور زمین کی پیدائش (کے موقعہ) پر حاضر کیا تھا اور نہ (خود) ان کی (اپنی) جانوں کی پیدائش کے موقعہ پر۔ اور نہ ہی میں گمراہ کرنے والوں کو (اپنا) مددگار بنا سکتا تھا۔ " WHERE ayatNumber = 52 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اس دن کو بھی یاد کرو) جس دن وہ (خدائے برتر مشرکوں کو) کہے گا کہ اب تم میرے (ان) شریکوں کو بلائو جن کے (شریک ہونے کے) متعلق تم دعویٰ کرتے تھے جس پر وہ انہیں بلائیں گے مگر وہ انہیں (کوئی) جواب نہیں دیں گے اور ان کے (اور ان کے تجویز کردہ شریکوں کے) درمیان ہم ایک آڑ (حائل) کر دیں گے۔ " WHERE ayatNumber = 53 AND chapterNumber = 18;
UPDATE verses SET ts = "اور مجرم اس آگ کو دیکھیں گے اور سمجھ جائیں گے وہ اس میں پڑنے والے ہیں اور وہ اس سے پیچھے ہٹنے کی کوئی جگہ نہیں پائیں گے۔ " WHERE ayatNumber = 54 AND chapterNumber = 18;
UPDATE verses SET ts = "اور ہم نے اس قرآن میں لوگوں کے لئے یقیناً ہر ایک (ضروری) بات کو مختلف پیرایوں میں بیان کیا ہے اور (ایسا کیوں نہ کرتے کہ) انسان سب سے بڑھ کر بحث کرنے والا ہے۔ " WHERE ayatNumber = 55 AND chapterNumber = 18;
UPDATE verses SET ts = "اور ان لوگوں کو جب ان کے پاس ہدایت آئی تو (اس پر) ایمان لانے اور اپنے رب سے بخشش چاہنے سے صرف اس بات نے روکا کہ پہلے لوگوں کی سی حالت ان پر (بھی) آئے‘ یا پھر عذاب ان کے سامنے آکھڑا ہو۔ " WHERE ayatNumber = 56 AND chapterNumber = 18;
UPDATE verses SET ts = "اور ہم رسولوں کو صرف بشارت دینے والا اور (عذاب کی آمد سے) آ گاہ کرنے والا بنا کر بھیجتے ہیں۔ اور جن لوگوں نے انکار کیا ہے وہ جھوٹ کے ذریعہ سے اس لئے جھگڑتے ہیں کہ اس کے ذریعہ سے حق کو مٹا دیں اور انہوں نے میرے نشانوں کو (اور) میرے انداز کو ہنسی کا نشانہ بنا لیا ہے۔ " WHERE ayatNumber = 57 AND chapterNumber = 18;
UPDATE verses SET ts = "اور اس شخص سے زیادہ ظالم اور کون (ہوسکتا) ہے۔ جسے اس کے رب کے نشانوں کے ذریعہ سمجھایا گیا (لیکن) پھر (بھی) وہ ان سے روگردان ہو گیا اور جو کچھ اس کے ہاتھوں نے (کما کر) آگے بھیجا تھا اسے اس نے بھلا دیا۔ ان لوگوں کے دلوں پر ہم نے یقیناً کئی پردے ڈال دئے ہیں تا کہ وہ اسے نہ سمجھیں اور ان کے کانوں میں گرانی (پیدا کر دی ہے) اور اگر تو  انہیں ہدایت کی طرف بلائے تو (وہ تجھ سے اس قدر حسد رکھتے ہیں کہ) اس صورت میں وہ ہدایت کو (بھی) کبھی قبول نہیں کریں گے۔ " WHERE ayatNumber = 58 AND chapterNumber = 18;
UPDATE verses SET ts = "اور تیرا رب بہت ہی بخشنے والا (اور بہت ہی) رحمت کرنے والا ہے۔ اگر وہ ان کے (برے) اعمال کی وجہ سے انہیں ہلاک کرنا چاہتا تو وہ ان پر فوراً عذاب نازل کر دیتا۔ (مگر وہ ایسا نہیں کرتا) بلکہ ان کے لئے ایک میعاد (مقرر) ہے جس سے ورے (یعنی پیشتر اس کے وہ عذاب کو بھگت لیں) وہ ہرگز کوئی جائے پناہ نہ پائیں گے۔ " WHERE ayatNumber = 59 AND chapterNumber = 18;
UPDATE verses SET ts = "اور وہ بستیاں جن کو ہم نے ان کے ظلم کی وجہ سے ہلاک کر دیا ہے (ان کے لئے موجب عبرت ہو سکتی تھیں) اور ہم نے ان کی ہلاکت کے لئے پہلے سے ایک میعاد مقرر کر دی تھی (تا وہ چاہیں تو توبہ کر لیں) ۔" WHERE ayatNumber = 60 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جب موسیٰ نے اپنے نوجوان (رفیق) سے کہا تھا (کہ) میں (جس راستے پر جا رہا ہوں اس پر قائم رہنے سے) نہیں ٹلوں گا یہاں تک کہ ان دونوں سمندروں کے اکٹھے ہونے کے مقام پر پہنچ جائوں۔ یا صدیوں تک (آگے ہی آگے) چلتا جائوں۔ " WHERE ayatNumber = 61 AND chapterNumber = 18;
UPDATE verses SET ts = "پس جب وہ (دونوں) ان (دونوں سمندروں) کے باہم ملنے کی جگہ پر (یعنی قرب زمانہ نبوی تک) پہنچے تو وہ اپنی مچھلی (وہاں) بھول گئے۔ جس پر اس (مچھلی) نے تیزی سے بھاگتے ہوئے سمندر میں اپنی راہ لی۔ " WHERE ayatNumber = 62 AND chapterNumber = 18;
UPDATE verses SET ts = "پھر جب وہ (اس جگہ سے) آگے نکل گئے تو اس (یعنی موسیٰ) نے اپنے نوجوان (رفیق) سے کہا (کہ) ہمارا صبح کا کھانا ہمیں دو۔ ہمیں یقیناً اپنے اس سفر کی وجہ سے تھکان ہو گئی ہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 18;
UPDATE verses SET ts = "اس نے کہا (کہ) بتائیے (اب کیا ہوگا) جب ہم (آرام کے لئے) اس چٹان پر ٹھہرے تو میں مچھلی (کا خیال) بھول گیا اور مجھے یہ بات شیطان کے سوا کسی نے نہیں بھلائی کہ میں اس کا خیال رکھتا اور اس نے سمندر میں عجیب طرح سے اپنی راہ لے لی۔ " WHERE ayatNumber = 64 AND chapterNumber = 18;
UPDATE verses SET ts = "اس نے کہا (کہ) یہی وہ (مقام) ہے جس کی ہمیں تلاش تھی۔ پھر وہ اپنے پائوں کے نشان دیکھتے ہوئے واپس لوٹے۔ " WHERE ayatNumber = 65 AND chapterNumber = 18;
UPDATE verses SET ts = "تو انہوں نے ہمارے (برگزیدہ) بندوں میں سے ایک ایسا بندہ (وہاں) پایا جسے ہم نے اپنے حضور سے رحمت (کی سیرت) بخشی تھی اور اسے ہم نے اپنی جناب سے (خاص) علم (بھی) عطا کیا تھا۔ " WHERE ayatNumber = 66 AND chapterNumber = 18;
UPDATE verses SET ts = "موسیٰ نے اس سے کہا (کہ) کیا میں اس (مقصد کے) لئے آپ کے ساتھ چل سکتا ہوں کہ جو علم آپ کو عطا ہوا ہے اس میں سے کچھ رشد (کی باتیں) مجھے بھی سکھائیں۔ " WHERE ayatNumber = 67 AND chapterNumber = 18;
UPDATE verses SET ts = "اس نے کہا (کہ) تو میرے ساتھ رہ کر ہرگز صبر نہیں کر سکے گا۔ " WHERE ayatNumber = 68 AND chapterNumber = 18;
