UPDATE verses SET ts = "پھر وہ (وہاں سے بھی) چل پڑے‘ یہاں تک کہ جب وہ ایک بستی کے لوگوں کے پاس پہنچے تو اس (بستی) کے باشندوں سے انہوں نے کھانا مانگا‘ مگر انہوں نے انہیں (اپنے) مہمان بنانے سے انکار کر دیا۔  پھر انہوں نے اس (بستی) میں ایک ایسی دیوار پائی جو گرنے کو تھی۔ اس (خدا کے بندہ) نے اسے درست کر دیا (اس پر) اس نے (یعنی موسیٰ) نے کہا (کہ) اگر آپ چاہتے تو یقیناً اس کی کچھ نہ (کچھ) اُجرت لے سکتے تھے۔ " WHERE ayatNumber = 78 AND chapterNumber = 18;
UPDATE verses SET ts = "اس (خدا کے برگزیدہ) نے کہا (کہ) یہ میرے درمیان اور تمہارے درمیان جدائی (کا وقت) ہے جس بات پر تو صبر نہیں کر سکا میں ابھی تجھے اس کی حقیقت سے آگاہ کرتا ہوں۔ " WHERE ayatNumber = 79 AND chapterNumber = 18;
UPDATE verses SET ts = "کشتی تو چند مساکین کی تھی‘ جو دریا میں کام کرتے ہیں اور ان کے سامنے (دریا پار) ایک (ظالم) بادشاہ تھا جو ہر ایک کشتی کو زبردستی چھین لیتا تھا‘ اس لئے میں نے چاہا کہ اسے عیب دار کر دوں۔ " WHERE ayatNumber = 80 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (یہ جو) لڑکے (کا واقعہ ہے تو اس) کی حقیقت یہ ہے کہ اس کے ماں باپ دونوں مومن تھے۔ اس پر (اس کی یہ حالت دیکھ کر) ہم ڈرے کہ ایسا نہ ہو (بڑے ہو کر) وہ ان پر سرکشی اور کفر کا الزام لگوادے۔ " WHERE ayatNumber = 81 AND chapterNumber = 18;
UPDATE verses SET ts = "پس ہم نے چاہا کہ ان کا رب ان کو اس (لڑکے) سے پاکیزگی اور رحم و انصاف کے لحاظ سے بہتر (لڑکا بدل کر) دے دے۔ " WHERE ayatNumber = 82 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (وہ) دیوار اس شہر کے دو یتیم لڑکوں کی تھی اور اس کے نیچے ان کا کچھ خزانہ (گڑا ہوا) تھا۔ اور ان کا باپ (ایک) نیک (شخص) تھا۔ اس لئے تیرے رب نے چاہا کہ وہ اپنی مضبوطی کی عمر کو پہنچ جائیں اور (بڑے ہو کر) اپنے خزانہ کو (خود) نکالیں تیرے رب کی طرف سے (ان پر خاص) رحم (ہوا) ہے اور یہ (کام) میں نے اپنے نفس کے حکم سے نہیں کیا۔ یہ اس بات کی حقیقت ہے جس پر تو صبر نہیں کر سکا۔ " WHERE ayatNumber = 83 AND chapterNumber = 18;
UPDATE verses SET ts = "اور وہ تجھ سے ذوالقرنین کے متعلق بھی سوال کرتے ہیں تو (انہیں) کہہ (کہ) میں ضرور اس کے متعلق کچھ ذکر تمہارے سامنے کروں گا۔ " WHERE ayatNumber = 84 AND chapterNumber = 18;
