UPDATE verses SET ts = "(یہ واقعہ ٹھیک) اسی طرح ہے اور ہم نے اس کے تمام حالات کا خوب پتا رکھا ہوا ہے۔ " WHERE ayatNumber = 92 AND chapterNumber = 18;
UPDATE verses SET ts = "پھر وہ ایک راستہ پر چل پڑا۔ " WHERE ayatNumber = 93 AND chapterNumber = 18;
UPDATE verses SET ts = "یہاں تک کہ جب وہ دو پہاڑوں کے درمیان پہنچا‘ تو اس نے ان کے ورے کچھ ایسے لوگ پائے جو بمشکل اس کی بات سمجھتے تھے۔ " WHERE ayatNumber = 94 AND chapterNumber = 18;
UPDATE verses SET ts = "انہوں نے کہا (کہ) اے ذوالقرنین! یاجوج و ماجوج یقیناً اس ملک میں فساد پھیلا رہے ہیں پس کیا ہم (لوگ) آپ کے لئے کچھ خراج اس شرط پر مقرر کر دیں کہ آپ ہمارے درمیان اور ان کے درمیان ایک روک بنا دیں۔ " WHERE ayatNumber = 95 AND chapterNumber = 18;
UPDATE verses SET ts = "اس نے کہا (کہ) اس (قسم کے کاموں) کے متعلق میرے رب نے جو طاقت مجھے بخشی ہے وہ (دشمنوں کے سامانوں سے) بہت بہتر ہے اس لئے تم مجھے مقدور بھر مدد دو تا کہ میں تمہارے درمیان اور ان کے درمیان ایک روک بنادوں۔ " WHERE ayatNumber = 96 AND chapterNumber = 18;
UPDATE verses SET ts = "تم مجھے لوہے کے ٹکڑے دو (چنانچہ وہ روک تیار ہونے لگی) یہاں تک کہ  جب اس نے (پہاڑی کی) ان (دونوں) چوٹیوں کے درمیان برابری پیدا کر دی۔ تو اس نے (ان سے) کہا (کہ اب اس پر آگ) دھونکو۔ حتیٰ کہ جب اس نے اسے (بالکل) آگ (کی طرح) کر دیا تو (ان سے) کہا (کہ اب) مجھے (گلا ہوا) تانبا (لا) دو‘ تا کہ میں (اسے) اس پر ڈال دوں۔ " WHERE ayatNumber = 97 AND chapterNumber = 18;
UPDATE verses SET ts = "پس (جب وہ دیوار تیار ہو گئی تو) وہ (یعنی یاجوج ماجوج) اس پر چڑھ نہ سکے‘ اور نہ اس میں کوئی  سوراخ کرسکے۔ " WHERE ayatNumber = 98 AND chapterNumber = 18;
UPDATE verses SET ts = "(اس پر) اس نے کہا (کہ) یہ (کام محض) میرے رب کے خاص احسان سے (ہوا) ہے پھر جب (عالمگیر عذاب کے متعلق) میرے رب کا وعدہ (پورا ہونے پر) آئے گا تو وہ اس (روک) کو (توڑ کر) زمین سے پیوست شدہ ایک ٹیلہ بنا دے گا۔ اور میرے رب کا وعدہ (ضرور) پورا ہو کر رہنے والا ہے۔ " WHERE ayatNumber = 99 AND chapterNumber = 18;
UPDATE verses SET ts = "اور (جب اس کے پورا ہونے کا وقت آئے گا تو) اُس وقت ہم انہیں ایک دوسرے کے خلاف جوش سے حملہ آور ہوتے ہوئے چھوڑ دیں گے اور بگل بجایا جائے گا تب ہم ان (سب) کو اکٹھا کر دیں گے۔ " WHERE ayatNumber = 100 AND chapterNumber = 18;
UPDATE verses SET ts = "اور ہم اس دن جہنم کو کافروں کے بالکل سامنے لے آئیں گے۔ " WHERE ayatNumber = 101 AND chapterNumber = 18;
UPDATE verses SET ts = "(وہ کافر) جن کی آنکھیں میرے ذکر (یعنی قرآن کریم) کی طرف سے (غفلت کے) پردہ میں تھیں۔ اور وہ سننے کی طاقت (بھی) نہیں رکھتے تھے۔ " WHERE ayatNumber = 102 AND chapterNumber = 18;
UPDATE verses SET ts = "(تو) کیا (یہ سب کچھ دیکھ کر) پھر (بھی) وہ لوگ جنہوں نے کفر (کا طریق) اختیار کیا ہے (یہ) سمجھتے ہیں کہ وہ مجھے چھوڑ کر میرے بندوں کو مددگار بنا سکیں گے۔ ہم نے کافروں کے انعام (یعنی بدلہ) کے طور پر جہنم کو تیار کر رکھا ہے۔ " WHERE ayatNumber = 103 AND chapterNumber = 18;
UPDATE verses SET ts = "ُتو (اُنہیں) کہہ (کہ) کیا ہم تمہیں ان لوگوں سے آگاہ کریں جو اعمال کے لحاظ سے سب سے زیادہ گھاٹا پانے والے ہیں۔ " WHERE ayatNumber = 104 AND chapterNumber = 18;
UPDATE verses SET ts = "(یہ وہ لوگ ہیں) جن کی (تمام تر) کوشش اس ورلی زندگی میں ہی غائب ہو گئی ہے اور (اس کے ساتھ) وہ (یہ بھی) سمجھتے ہیں کہ وہ اچھا کام کر رہے ہیں۔ " WHERE ayatNumber = 105 AND chapterNumber = 18;
UPDATE verses SET ts = "یہ وہ لوگ ہیں جنہوں نے اپنے رب کے نشانوں کا اور اس سے ملنے کا انکار کر دیا ہے اور اس لئے ان کے (تمام) اعمال گر کر (اسی دنیا میں) رہ گئے ہیں‘ چنانچہ قیامت کے دن ہم انہیں کچھ بھی وقعت نہیں دیں گے۔ " WHERE ayatNumber = 106 AND chapterNumber = 18;
UPDATE verses SET ts = "یہ ان کا بدلہ (یعنی) جہنم اس وجہ سے ہوگا کہ انہوں نے کفر (کا طریق) اختیار کیا۔ اور میرے نشانوں اور میرے رسولوں کو (اپنی) ہنسی کا نشانہ بنالیا۔ " WHERE ayatNumber = 107 AND chapterNumber = 18;
UPDATE verses SET ts = "جو لوگ ایمان لائے ہیں اور انہوں نے نیک (اور مناسب حال) عمل کیے ہیں ان کا ٹھکانا یقیناً فردوس نامی بہشت ہوں گے۔ " WHERE ayatNumber = 108 AND chapterNumber = 18;
UPDATE verses SET ts = "وہ ان میں رہتے چلے جائیں گے (اور) ان سے الگ ہونا نہیں چاہیں گے۔ " WHERE ayatNumber = 109 AND chapterNumber = 18;
UPDATE verses SET ts = "تو انہیں کہہ (کہ) اگر (ہرایک) سمندر میرے رب کی باتوں (کے لکھنے) کے لئے روشنائی بن جاتا تو میرے رب کی باتوں کے ختم ہونے سے پہلے (ہر ایک) سمندر (کا پانی) ختم ہو جاتا گو (اسے) زیادہ کرنے کے لئے ہم اتنا (ہی) اور (پانی سمندر میں) لا ڈالتے۔ " WHERE ayatNumber = 110 AND chapterNumber = 18;
UPDATE verses SET ts = "تو (انہیں) کہہ (کہ) میں صرف تمہاری طرح کا ایک بشر ہوں‘ (فرق صرف یہ ہے کہ) میری طرف (یہ) وحی (نازل)  کی جاتی ہے کہ تمہارا معبود ایک ہی (حقیقی) معبود ہے پس جو شخص اپنے رب سے ملنے کی امید رکھتا ہو اسے چاہیے کہ نیک (اور مناسب حال) کام کرے اور اپنے رب کی عبادت میں کسی کو بھی شریک نہ کرے۔ " WHERE ayatNumber = 111 AND chapterNumber = 18;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 19;
UPDATE verses SET ts = "اے عالم اور صادق خدا تو کافی اور ہادی ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 19;
UPDATE verses SET ts = "(اس سورۃ میں) تیرے رب کی (اس) رحمت کا ذکر (ہے) جو اس نے اپنے بندے زکریا پر (اس وقت) کی‘ " WHERE ayatNumber = 3 AND chapterNumber = 19;
UPDATE verses SET ts = "جب اس نے اپنے رب کو آہستہ آواز سے پکارا۔ " WHERE ayatNumber = 4 AND chapterNumber = 19;
UPDATE verses SET ts = "(اور) کہا‘ اے میرے رب! (میری حالت تو یقیناً ایسی ہے کہ) میری تمام ہڈیاں تک کمزور ہو گئی ہیں اور (میرا) سر بڑھاپے کی وجہ سے بھڑک اٹھا ہے اور اے میرے رب! میں کبھی بھی تجھ سے دعائیں مانگنے کی وجہ سے ناکام (ونامراد) نہیں رہا۔ " WHERE ayatNumber = 5 AND chapterNumber = 19;
UPDATE verses SET ts = "اور میں یقیناً اپنے رشتہ داروں سے اپنے (مرنے کے) بعد (کے سلوک سے) ڈرتا ہوں اور میری بیوی بانجھ ہے‘ پس تو مجھے اپنے پاس سے ایک دوست (یعنی بیٹا) عطا فرما۔ " WHERE ayatNumber = 6 AND chapterNumber = 19;
UPDATE verses SET ts = "جو میرا بھی وارث ہو اور آل یعقوب (سے جو دین و تقویٰ ہم کو ورثہ میں ملا ہے اس) کا بھی وارث ہو۔ اور اے میرے رب اس کو (اپنا) پسندیدہ (وجود) بنائیو۔ " WHERE ayatNumber = 7 AND chapterNumber = 19;
UPDATE verses SET ts = "(اس پر اللہ نے فرمایا) اے زکریا! ہم تجھے ایک لڑکے کی خبر دیتے ہیں (جو جوانی کی عمر تک پہنچے گا اور) اس کا نام (خدا کی طرف سے) یحییٰ ہوگا۔ ہم نے اس سے پہلے کسی کو اس نام سے یاد نہیں کیا۔ " WHERE ayatNumber = 8 AND chapterNumber = 19;
UPDATE verses SET ts = "(زکریا نے) کہا۔ اے میرے رب! میرے ہاں لڑکا کیسے ہو سکتا ہے‘ حالانکہ میری بیوی بانجھ ہے اور میں بڑھاپے کی انتہائی حد کو پہنچ چکا ہوں۔ " WHERE ayatNumber = 9 AND chapterNumber = 19;
UPDATE verses SET ts = "(الہام لانے والے فرشتہ نے) کہا (کہ جس طرح تو کہتا ہے واقعہ) اسی طرح (ہے) (مگر) تیرا رب کہتا ہے کہ یہ (بات) مجھ پر آسان ہے اور (دیکھ کہ) میں تجھے اس سے پہلے پیدا کر چکا ہوں حالانکہ تو کچھ بھی نہیں تھا۔ " WHERE ayatNumber = 10 AND chapterNumber = 19;
UPDATE verses SET ts = "(زکریا نے) کہا‘ اے میرے رب! میرے لئے کوئی حکم بخش۔ فرمایا تیرے لئے یہ حکم ہے کہ تو لوگوں سے تین راتیں متواتر کلام نہ کر۔ " WHERE ayatNumber = 11 AND chapterNumber = 19;
UPDATE verses SET ts = "اس کے بعد (زکریا) محراب سے نکل کر اپنی قوم کے پاس گئے اور انہیں آہستہ آواز میں کہا کہ صبح و شام خدا کی تسبیح کرتے رہو۔ " WHERE ayatNumber = 12 AND chapterNumber = 19;
UPDATE verses SET ts = "(اس کے بعد یحییٰ پیدا ہو گیا اور ہم نے اسے کہا) اے یحییٰ تو (الٰہی) کتاب کو مضبوطی سے پکڑ لے اور ہم نے اسے چھوٹی عمر میں ہی (اپنے) حکم سے نوازا تھا۔ " WHERE ayatNumber = 13 AND chapterNumber = 19;
UPDATE verses SET ts = "(اوریہ بات) ہماری طرف سے بطور مہربانی (اور شفقت کے تھی) اور( اسے) پاک کرنے کے لئے (تھی) اور وہ بڑا متقی تھا۔ " WHERE ayatNumber = 14 AND chapterNumber = 19;
UPDATE verses SET ts = "اور وہ اپنے والدین کے ساتھ نیک سلوک کرنے والا تھا اور ظالم اور نافرمان نہیں تھا۔ " WHERE ayatNumber = 15 AND chapterNumber = 19;
UPDATE verses SET ts = "اور جب وہ پیدا ہوا تب بھی اس پر سلامتی تھی‘ اور جب وہ مرے گا اور جب وہ زندہ کرکے اٹھایا جائے گا (تب بھی اس پر سلامتی ہوگی) ۔" WHERE ayatNumber = 16 AND chapterNumber = 19;
UPDATE verses SET ts = "اور تو (اس) کتاب میں مریم کا (جو) ذکر (آتا ہے اسے) بیان کر (خصوصاً اس بات کو کہ) جب وہ اپنے رشتہ داروں سے مشرقی (جانب ایک) جگہ چلی گئی۔ " WHERE ayatNumber = 17 AND chapterNumber = 19;
UPDATE verses SET ts = "اور (اپنے اور) ان (یعنی رشتہ داروں) کے درمیان پردہ ڈال دیا۔ (یعنی ان سے قطع تعلق کرکے اپنے آپ کو چھپا دیا) اس وقت ہم نے اس کی طرف اپنا کلام لانے والا فرشتہ (یعنی جبرائیل) بھیجا اور وہ اس کے سامنے ایک تندرست بشر کی شکل میں ظاہر ہوا۔ " WHERE ayatNumber = 18 AND chapterNumber = 19;
UPDATE verses SET ts = "(مریم نے اس سے) کہا‘ میں تجھ سے رحمان خدا کی پناہ مانگتی ہوں‘ اگر تیرے اندر کچھ بھی تقویٰ ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 19;
UPDATE verses SET ts = "(اس پر اس فرشتہ نے) کہا۔ میں تو صرف تیرے رب کا بھیجا ہوا پیغامبر ہوں تا کہ میں تجھے (وحی کے مطابق) ایک پاک لڑکا دوں (جو جوانی کی عمر تک پہنچے گا)۔ " WHERE ayatNumber = 20 AND chapterNumber = 19;
UPDATE verses SET ts = "(مریم نے) کہا۔ میرے ہاں لڑکا کہاں سے ہوگا۔ حالانکہ اب تک مجھے کسی مرد نے نہیں چھؤا۔ اور میں کبھی بدکاری میں مبتلا نہیں ہوئی۔ " WHERE ayatNumber = 21 AND chapterNumber = 19;
UPDATE verses SET ts = "(فرشتہ نے) کہا (بات) اسی طرح ہے (جس طرح تو نے کہی‘ مگر) تیرے رب نے یہ کہا ہے کہ یہ (کام) مجھ پر آسان ہے اور (ہم اس لیے یہ لڑکا پیدا کریں گے) تا کہ اسے لوگوں کے لئے ایک نشان بنائیں اور اپنی طرف سے رحمت (کا موجب بھی بنائیں) اور یہ (امر) ہماری تقدیر میں طے ہو چکا ہے۔ " WHERE ayatNumber = 22 AND chapterNumber = 19;
UPDATE verses SET ts = "اس پر مریم نے (اپنے پیٹ میں) اس (بچہ) کو اٹھا لیا اور پھر اس کو لے کر ایک دور مکان کی طرف چلی گئی۔ " WHERE ayatNumber = 23 AND chapterNumber = 19;
UPDATE verses SET ts = "پس (جب وہ وہاں پہنچی تو) اسے درد زہ (اٹھی اور اسے) مجبور کرکے ایک کھجور کے تنے کی طرف لے گئی (جب مریم کو یقین ہو گیا کہ اس کے ہاں بچہ ہونے والا ہے تو اس نے دنیا کی انگشت نمائی کا خیال کرکے) کہا۔ اے کاش! میں اس سے پہلے مر جاتی اور میری یاد مٹا دی جاتی۔ " WHERE ayatNumber = 24 AND chapterNumber = 19;
UPDATE verses SET ts = "پس (فرشتہ نے) اس کو نچلی جانب کی طرف سے پکار کر کہا کہ (اے عورت) غم نہ کر‘ اللہ نے تیری نچلی جانب ایک چشمہ بہایا  ہوا ہے (اس کے پاس جا اور اپنی اور بچہ کی صفائی کر)۔ " WHERE ayatNumber = 25 AND chapterNumber = 19;
UPDATE verses SET ts = "اور (وہ) کھجور (جو تیرے قریب ہوگی اس) کی ٹہنی کو پکڑ کر اپنی طرف ہلا وہ تجھ پر تازہ بتازہ پھل پھینکے گی۔ " WHERE ayatNumber = 26 AND chapterNumber = 19;
UPDATE verses SET ts = "پس (ان کو) کھائو اور (چشمہ سے پانی بھی) پیئو اور (خود نہا کر اور بچہ کو نہلا کر) اپنی آنکھیں ٹھنڈی کرو‘ پھر اگر (اس عرصہ) میں تو کسی مرد کو دیکھے تو کہہ دے‘ میں نے رحمن (خدا) کے لئے (ایک) روزے کی نذر کی ہوئی ہے پس آج میں کسی انسان سے بات نہیں کروں گی۔ " WHERE ayatNumber = 27 AND chapterNumber = 19;
UPDATE verses SET ts = "اس کے بعد وہ اس کو لے کر اپنی قوم کے پاس سوار کراکے لائی۔ جنہوں نے کہا اے مریم! تو نے بہت برا کام کیا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 19;
UPDATE verses SET ts = "اے ہارون کی بہن! تیرا باپ تو برا آدمی نہیں تھا‘ اور تیری ماں بھی بدکار نہیں تھی۔ " WHERE ayatNumber = 29 AND chapterNumber = 19;
UPDATE verses SET ts = "اس پر اس نے اس (بچہ) کی طرف اشارہ کیا‘ اس پر لوگوں نے کہا‘ ہم اس سے کس طرح باتیں کریں جو کہ (کل تک) پنگھوڑے میں بیٹھنے والا بچہ تھا۔ " WHERE ayatNumber = 30 AND chapterNumber = 19;
UPDATE verses SET ts = "(یہ سن کر ابن مریم نے) کہا کہ میں اللہ کا بندہ ہوں اور اس نے مجھے کتاب بخشی ہے اور مجھے نبی بنایا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 19;
UPDATE verses SET ts = "اور میں جہاں کہیں بھی ہوں اس نے مجھے بابرکت (وجود) بنایا ہے اور جب تک میں زندہ ہوں مجھے نماز اور زکٰوۃ کی تاکید کی ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 19;
UPDATE verses SET ts = "اور مجھے اپنی والدہ سے نیک سلوک کرنے والا بنایا ہے اور مجھے ظالم اور بدبخت نہیں بنایا۔ " WHERE ayatNumber = 33 AND chapterNumber = 19;
UPDATE verses SET ts = "اور جس دن میں پیدا ہوا تھا اس دن بھی مجھ پر سلامتی نازل ہوئی تھی اور جب میں مروں گا اور جب مجھے زندہ کرکے اٹھایا جائے گا (اس وقت بھی مجھ پر سلامتی نازل کی جائے گی) " WHERE ayatNumber = 34 AND chapterNumber = 19;
UPDATE verses SET ts = "(دیکھو) یہ (حقیقی) عیسیٰ ابن مریم ہے اور یہ (اس کا اصل) سچا واقعہ ہے جس میں وہ (لوگ) اختلاف کر رہے ہیں۔ " WHERE ayatNumber = 35 AND chapterNumber = 19;
UPDATE verses SET ts = "خدا کی شان کے یہ خلاف ہے کہ وہ کوئی بیٹا بنائے۔ وہ اس بات سے پاک ہے وہ جب کبھی کسی بات کا فیصلہ کرتا ہے تو کہتا ہے (ایسا) ہوتا جائے تو ویسا ہی ہونے لگتا ہے (پھر اسے مدد کے لئے بیٹا بنانے کی کیا ضرورت ہے) " WHERE ayatNumber = 36 AND chapterNumber = 19;
UPDATE verses SET ts = "اور اللہ میرا بھی رب ہے اور تمہارا بھی رب ہے۔ اسی کی عبادت کرو یہی سیدھا راستہ ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 19;
UPDATE verses SET ts = "مگر مختلف گروہوں نے آپس میں اختلاف کیا (اور سچائی کو چھوڑ دیا) پس جن لوگوں نے ایک بڑے دن میں حاضر ہونے کا انکار کیا ان پر عذاب نازل ہوگا۔ " WHERE ayatNumber = 38 AND chapterNumber = 19;
UPDATE verses SET ts = "جس دن وہ ہمارے حضور میں حاضر ہونگے اس دن ان کی قوت شنوائی بہت تیز ہوگی اور نظریں بھی بہت تیز ہونگی لیکن وہ ظالم آج بہت بھاری گمراہی میں مبتلا ہیں۔ " WHERE ayatNumber = 39 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ان کو اس دن سے ڈرا جس دن (افسوس اور) مایوسی چھائی ہوئی ہوگی (یعنی قیامت کے دن سے) جب سب معاملات کا فیصلہ ہو جائے گا اور (اب تو) یہ لوگ غفلت میں (پڑے ہوئے) ہیں اور ایمان نہیں لاتے۔ " WHERE ayatNumber = 40 AND chapterNumber = 19;
UPDATE verses SET ts = "ہم یقیناً (ساری) زمین کے بھی وارث ہونگے اور ان لوگوں کے بھی جو اس پر رہتے ہیں اور (آخرکار) سب لوگ ہماری طرف ہی لوٹا کر لائے جائیں گے۔ " WHERE ayatNumber = 41 AND chapterNumber = 19;
UPDATE verses SET ts = "اور تو قرآن کریم کے رو سے ابراہیم کا ذکر کر۔ وہ یقیناً بڑا راستباز تھا اور نبی تھا۔ " WHERE ayatNumber = 42 AND chapterNumber = 19;
UPDATE verses SET ts = "(اور تو اس وقت کو بھی یاد کر۔ اور لوگوں کے سامنے بیان کر) جب ابراہیم نے اپنے باپ سے کہا تھا کہ اے میرے باپ! تو کیوں ان چیزوں کی پرستش کرتا ہے جو نہ سنتی ہیں نہ دیکھتی ہیں اور نہ تیری کسی تکلیف کو دور کرنے پر قادر ہیں۔ " WHERE ayatNumber = 43 AND chapterNumber = 19;
UPDATE verses SET ts = "اے میرے باپ! مجھے ایک خاص علم عطا کیا گیا ہے جو تجھے نہیں ملا۔ پس (باوجود اس کے کہ میں تیرا بیٹا ہوں) تو میری اتباع کر۔ میں تجھے سیدھا راستہ دکھائوں گا۔ " WHERE ayatNumber = 44 AND chapterNumber = 19;
UPDATE verses SET ts = "اے میرے باپ! شیطان کی عبادت نہ کر‘ شیطان یقیناً خدائے رحمن کا نافرمان ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 19;
UPDATE verses SET ts = "اے میرے باپ! میں ڈرتا ہوں کہ تجھے خدائے رحمن کی طرف سے (نافرمانی کی وجہ سے) کوئی عذاب نہ پہنچے‘ جس کے نتیجہ میں تو شیطان کا دوست ہوجائے۔ " WHERE ayatNumber = 46 AND chapterNumber = 19;
UPDATE verses SET ts = "اس پر (ابراہیم کے باپ نے) کہا‘ اے ابراہیم! کیا تو میرے معبودوں سے متنفر ہو رہا ہے؟ اے ابراہیم! اگر تو باز نہ آئے گا تو میں تجھے ضرور سنگسار کر دوں گا اور (بہتر ہے کہ) تو کچھ دیر کے لئے میری نظروں سے اوجھل ہو جا (تا کہ غصہ میں میں کچھ کر نہ بیٹھوں) " WHERE ayatNumber = 47 AND chapterNumber = 19;
UPDATE verses SET ts = "(اس پر ابراہیم نے) کہا اچھا میری طرف سے تجھ پر ہمیشہ سلامتی کی دعا پہنچتی رہے (یعنی میں تجھ سے الگ ہو جاتا ہوں) میں اپنے رب سے تیرے لئے ضرور مغفرت کی دعا کروں گا‘ وہ مجھ پر بہت ہی مہربان ہے۔ " WHERE ayatNumber = 48 AND chapterNumber = 19;
UPDATE verses SET ts = "اور (اے باپ) میں تم کو اور جن (وجودوں) کو تم اللہ کے سوا پکارتے ہو سب کو چھوڑ دوں گا اور صرف اپنے رب کے حضور دعائیں مانگوں گا (اور) یقیناً میں اپنے رب کے حضور دعا کرنے کی وجہ سے بدنصیب نہیں بنوں گا۔ " WHERE ayatNumber = 49 AND chapterNumber = 19;
UPDATE verses SET ts = "پھر جب (ابراہیم) ان (یعنی اپنے لوگوں) سے بھی اور جن کی وہ اللہ کے سوا پوجا کرتے تھے (ان سے بھی) جدا ہو گیا‘ تو ہم نے اسے اسحاق اور (اس کے بعد) یعقوب عطا فرمائے اور ان سب کو ہم نے نبی بنایا۔ " WHERE ayatNumber = 50 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم نے ان کو اپنی رحمت میں سے ایک (وافر) حصہ عطا فرمایا اور ہم نے ان کے لئے ہمیشہ قائم رہنے والا اعلیٰ درجہ کا ذکر خیر مقرر فرمایا۔ " WHERE ayatNumber = 51 AND chapterNumber = 19;
UPDATE verses SET ts = "اور تو قرآن کے مطابق موسیٰ کا بھی ذکر کر‘ وہ ہمارا منتخب بندہ تھا۔ اور رسول (اور) نبی تھا۔ " WHERE ayatNumber = 52 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم نے موسیٰ کو طور کی دائیں طرف سے پکارا اور اس کو اپنے اسرار بتاتے ہوئے اپنے قریب کر لیا۔ " WHERE ayatNumber = 53 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم نے اس (موسیٰ) کو اپنی رحمت سے اس کا بھائی ہارون نبی بنا کر (مددگار کے طور پر) دیا۔ " WHERE ayatNumber = 54 AND chapterNumber = 19;
UPDATE verses SET ts = "اور تو قرآن کے مطابق اسماعیل کا بھی ذکر کر‘ وہ بھی یقیناً سچے وعدوں والا تھا اور رسول (اور) نبی تھا۔ " WHERE ayatNumber = 55 AND chapterNumber = 19;
UPDATE verses SET ts = "اور اپنے اہل کو نماز اور زکٰوۃ کی تا کید کرتا رہتا تھا اور اپنے رب کے نزدیک پسندیدہ (وجود) تھا۔ " WHERE ayatNumber = 56 AND chapterNumber = 19;
UPDATE verses SET ts = "اور تو قرآن کے رو سے ادریس کا بھی ذکر کر‘ وہ بھی صدیق نبی تھا۔ " WHERE ayatNumber = 57 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم نے اسے نہایت اعلیٰ مقام تک پہنچایا تھا۔ " WHERE ayatNumber = 58 AND chapterNumber = 19;
UPDATE verses SET ts = "یہ سب کے سب وہ لوگ تھے‘ جن پر خدا نے نبیوں میں سے انعام کیا تھا۔ ان (نبیوں) میں سے جو آدم کی اولاد تھے اور جو ان لوگوں کی اولاد تھے جن کو ہم نے نوح کے ساتھ کشتی میں بچایا اور ابراہیم اور یعقوب کی (اولاد تھے) اور ان (لوگوں) میں سے تھے جن کو ہم نے ہدایت دی اور اپنے لئے منتخب کر لیا۔ جب ان کے اوپر خدائے رحمن کا کلام پڑھا جاتا تھا‘ تو وہ سجدہ کرتے ہوئے اور روتے ہوئے (زمین پر) گر جاتے تھے۔ " WHERE ayatNumber = 59 AND chapterNumber = 19;
UPDATE verses SET ts = "پھر ان کے بعد ایک ایسی نسل آئی جنہوں نے نماز کو ضائع کر دیا اور نفسانی خواہشات کے پیچھے پڑ گئے۔ پس وہ عنقریب گمراہی کے مقام تک پہنچ جائیں گے۔ " WHERE ayatNumber = 60 AND chapterNumber = 19;
UPDATE verses SET ts = "سوائے اس کہ جو توبہ کر لے گا اور ایمان لائے گا اور نیک عمل کرے گا۔ یہ (لوگ) جنت میں داخل ہوں گے اور ان پر کوئی ظلم نہیں کیا جائے گا۔ " WHERE ayatNumber = 61 AND chapterNumber = 19;
UPDATE verses SET ts = "(یعنی ان جنتوں میں) جو ہمیشہ رہنے والی ہیں اور جن کا (خدائے) رحمن نے اپنے بندوں سے ایسے وقت میں وعدہ کیا ہے جبکہ وہ ان کی نظروں سے ابھی پوشیدہ ہیں۔ یقیناً خدا کا وعدہ پورا ہو کر رہتا ہے۔ " WHERE ayatNumber = 62 AND chapterNumber = 19;
UPDATE verses SET ts = "وہ ان (جنتوں) میں کوئی لغو بات نہیں سنیں گے بلکہ صرف سلامتی (اور امن کی باتیں سنیں گے) اور ان (جنتوں) میں ان کو صبح اور شام رزق ملے گا۔ " WHERE ayatNumber = 63 AND chapterNumber = 19;
UPDATE verses SET ts = "یہ وہ جنت ہے جس کا وارث ہم اپنے بندوں میں سے ان کو کریں گے جو متقی ہوں گے۔ " WHERE ayatNumber = 64 AND chapterNumber = 19;
UPDATE verses SET ts = "اور (فرشتے ان سے کہیں گے کہ) ہم تو صرف تمہارے رب کے حکم سے اترتے ہیں اور جو کچھ ہمارے آگے ہے اور جو کچھ ہمارے پیچھے ہے اور جو کچھ ان دونوں (جہات) کے درمیان میں ہے سب کچھ خدا کا ہے اور تمہارا رب بھولنے والا نہیں۔ " WHERE ayatNumber = 65 AND chapterNumber = 19;
UPDATE verses SET ts = "(وہ) آسمانوں کا (بھی) رب ہے اور زمین کا بھی (رب) اور جو کچھ ان دونوں کے درمیان (ہے) پس (اے مسلمان) اس کی عبادت کر اور اس کی عبادت پر ہمیشہ قائم رہ۔ کیا تو اس کا کوئی ہم صفت جانتا ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 19;
UPDATE verses SET ts = "اور انسان ہمیشہ یہ کہتا رہے گا کہ کیا جب میں مر جائوں گا تو پھر زندہ کرکے اٹھایا جائوں گا؟ " WHERE ayatNumber = 67 AND chapterNumber = 19;
UPDATE verses SET ts = "کیا انسان کو یہ (بات) یاد نہیں کہ ہم نے اس کو اس سے پہلے پیدا کیا تھا اور (اس وقت) وہ کوئی چیز نہیں تھا۔ " WHERE ayatNumber = 68 AND chapterNumber = 19;
UPDATE verses SET ts = "پس تیرے رب کی قسم ہم (جو تیرے رب ہیں) ان لوگوں کو (پھر ایک دفعہ) اٹھائیں گے اور شیطانوں کو بھی (اٹھائیں گے اور) پھر ان سب کو جہنم کے گرد ایسی صورت میں حاضر کریں گے کہ وہ زانوئوں کے بل گرے ہوئے ہوں گے۔ " WHERE ayatNumber = 69 AND chapterNumber = 19;
UPDATE verses SET ts = "پھر ہم ہر ایک گروہ میں سے ایسے لوگوں کو الگ کر لیں گے جو (خدائے) رحمن کے سخت دشمن تھے۔ " WHERE ayatNumber = 70 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم خوب جانتے ہیں کہ ان میں سے کون دوزخ میں جانے کے زیادہ قابل ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 19;
UPDATE verses SET ts = "اور تم میں سے ہر شخص اس (دوزخ) میں جانے والا ہے۔ یہ خدا کا ایسا پکا وعدہ ہے جو ہو کر رہے گا۔ " WHERE ayatNumber = 72 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم متقیوں کو بچالیں گے‘ اور ظالموں کو اس میں گھٹنوں کے بل گرے ہوئے چھوڑ دیں گے۔ " WHERE ayatNumber = 73 AND chapterNumber = 19;
UPDATE verses SET ts = "اور جب انہیں ہماری کھلی کھلی آیات پڑھ کر سنائی جاتی ہیں‘ تو کافر مومنوں سے کہتے ہیں (بتائو تو) ہم دونوں فریق میں سے کونسا فریق درجہ کے لحاظ سے بہتر اور ہم جلیسوں کے لحاظ سے (زیادہ) اچھا ہے۔ " WHERE ayatNumber = 74 AND chapterNumber = 19;
UPDATE verses SET ts = "اور ہم نے ان سے پہلے بہت سے زمانوں کے لوگوں کو ہلاک کیا ہے جو سامانوں کے لحاظ سے اور ظاہری شان و شوکت کے لحاظ سے ان لوگوں سے اچھے تھے۔ " WHERE ayatNumber = 75 AND chapterNumber = 19;
