UPDATE verses SET ts = "وہ ضرور ان کو ایسی جگہ میں داخل کرے گا۔ جسے وہ پسند کریں گے اور اللہ بہت جاننے والا (اور) بہت سمجھ رکھنے والا ہے۔ " WHERE ayatNumber = 60 AND chapterNumber = 22;
UPDATE verses SET ts = "یہ بات اسی طرح ہے اور جو شخص اتنی ہی سزا دے جتنی اسے تکلیف دی گئی تھی مگر باوجود اس کے (اس کا دشمن) الٹا اس پر چڑھ آئے تو اللہ ضرور اس کی مدد کرے گا۔ اللہ یقیناً بہت معاف کرنے والا (اور) بہت بخشنے والا ہے۔ " WHERE ayatNumber = 61 AND chapterNumber = 22;
UPDATE verses SET ts = "یہ (سزاوجزا کا سلسلہ) اس لئے (چلتا) ہے کہ ثابت ہو کہ اللہ رات کو دن میں داخل کر دیتا ہے اور دن کو رات میں داخل کر دیتا ہے۔ اور اللہ یقیناً (بہت دعائیں) سننے والا (اور بہت حالات) دیکھنے والا ہے۔ " WHERE ayatNumber = 62 AND chapterNumber = 22;
UPDATE verses SET ts = "یہ (دعائیں سننا اور حالات سے واقف رہنا) اس لئے ہے کہ اللہ اپنی ذات میں قائم ہے اور دوسری چیزوں کو قائم رکھتا ہے اور اس  لئے کہ جس چیز کو وہ خدا کے سوا پکارتے ہیں وہ تباہ ہونے والی ہے اور اس لئے کہ اللہ ہی سب سے اوپر ہے اور سب سے بڑا ہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 22;
UPDATE verses SET ts = "کیا تو نے دیکھا نہیں کہ اللہ نے آسمان سے پانی اتارا ہے۔ جس سے زمین سرسبز ہو جاتی ہے۔ اللہ یقیناً (اپنے بندوں سے) مہربانی کا سلوک کرنے والا ہے اور (ان کے حالات سے) بہت باخبر ہے۔ " WHERE ayatNumber = 64 AND chapterNumber = 22;
UPDATE verses SET ts = "جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے وہ سب اس کا ہے اور اللہ یقیناً اپنے سوا سب وجودوں کی مدد سے بے نیاز (اور) تعریفوں کا مالک ہے۔ " WHERE ayatNumber = 65 AND chapterNumber = 22;
UPDATE verses SET ts = "کیا تو نے دیکھا نہیں کہ اللہ نے تمہارے کام پر جو کچھ بھی زمین میں ہے اسے بغیر مزدوری کے لگا رکھا ہے اور کشتیاں بھی سمندر میں اس کے حکم سے چلتی ہیں اور اس نے آسمان کو روک رکھا ہے کہ کہیں زمین پر سوائے اس کے حکم کے گر نہ جائے اللہ یقیناً لوگوں سے بہت شفقت کرنے والا (اور ان پر) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 22;
UPDATE verses SET ts = "اور وہی ہے جس نے تم کو زندہ کیا۔ پھر تم کو مارے گا پھر تم کو زندہ کرے گا۔ انسان یقیناً بڑا ناشکرا ہے۔ " WHERE ayatNumber = 67 AND chapterNumber = 22;
UPDATE verses SET ts = "ہم نے ہر امت کے لئے ایک عبادت کا طریق مقرر کیا ہے جس کے مطابق وہ چلتی ہے پس اس طریق (یعنی اسلام) کے متعلق وہ تجھ سے بحث نہ کریں (کیونکہ یہ خدا کا مقرر کردہ ہے) اور تو (انہیں) اپنے رب کی طرف بلا کیونکہ تو سیدھے راستہ پر ہے۔ " WHERE ayatNumber = 68 AND chapterNumber = 22;
UPDATE verses SET ts = "اور اگر وہ تجھ سے بحث کریں تو کہہ دے کہ اللہ تمہارے اعمال سے خوب واقف ہے۔ " WHERE ayatNumber = 69 AND chapterNumber = 22;
UPDATE verses SET ts = "اللہ (تمہارے اور میرے درمیان) قیامت کے دن ان امور میں فیصلہ کرے گا جن میں تم اختلاف رکھتے ہو۔ " WHERE ayatNumber = 70 AND chapterNumber = 22;
UPDATE verses SET ts = "(اے محمد رسول اللہ)! کیا تجھے معلوم نہیں کہ اللہ ہر چیز کو جو آسمان اور زمین میں ہے جانتا ہے۔ یہ سب کچھ ایک کتاب میں لکھا ہوا موجود ہے اور اس طرح (کسی قانون کو محفوظ) کر دینا اللہ کے لئے آسان ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 22;
UPDATE verses SET ts = "اور وہ لوگ اللہ کے سوا ان چیزوں کی عبادت کرتے ہیں جن کے لئے اس نے کوئی دلیل نہیں اتاری اور جن کے متعلق ان کوکسی قسم کا کوئی علم حاصل نہیں۔ اور ظالموں کا کوئی مددگار نہیں ہوگا۔ " WHERE ayatNumber = 72 AND chapterNumber = 22;
UPDATE verses SET ts = "اور جب ان کے سامنے ہماری کھلی کھلی آیات پڑھی جاتی ہیں تو تو منکروں کے چہروں میں (صاف صاف) ناپسندیدگی (کے آثار) دیکھتاہے۔ قریب ہوتا ہے کہ وہ ان لوگوں پر حملہ کر دیں جو ان کو ہماری آیتیں پڑھ کر سنا رہے ہوتے ہیں۔ تو کہہ دے‘ کیا میں تم کو اس حالت سے بھی ایک بری حالت کی خبر دوں؟ (اور وہ) جہنم (میں پڑنا) ہے۔ اللہ نے اس کا وعدہ منکروں سے کیا ہے اور وہ برا ٹھکانا ہے۔ " WHERE ayatNumber = 73 AND chapterNumber = 22;
UPDATE verses SET ts = "اے لوگو! ایک بات تمہیں بتائی جاتی ہے تم اسے غور سے سنو! تم جن کو اللہ کے سوا پکارتے ہو وہ ایک مکھی بھی پیدا نہیں کر سکیں گے خواہ سب کے سب جمع ہو جائیں‘ بلکہ اگر ایک مکھی ان کے آگے سے کوئی چیز اچک کرلے جائے تو وہ اس چیزکو(بھی) چھڑا نہیںسکتے۔ یہ دعائیں مانگنے والا (بھی) اور جس سے دعائیں مانگی جاتی ہیں (وہ بھی) کتنے کمزور ہیں۔ " WHERE ayatNumber = 74 AND chapterNumber = 22;
UPDATE verses SET ts = "ان لوگوں نے اللہ (کی صفات) کا صحیح اندازہ نہیں لگایا۔ اللہ تو یقیناً بڑی طاقت والا (اور) بڑا غالب ہے۔ " WHERE ayatNumber = 75 AND chapterNumber = 22;
