UPDATE verses SET ts = "سوائے ان کے جو بعد میں توبہ کر لیں اور اصلاح کر لیں۔ سو (ایسا کرنے پر) اللہ یقیناً بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 24;
