UPDATE verses SET ts = "اور اس بیوی سے (جس پر اس کا خاوند الزام لگائے) اس کا اللہ کی قسم کھا کر چار گواہیاں دینا کہ وہ (خاوند) جھوٹا ہے عذاب دور کر دے گا۔ " WHERE ayatNumber = 9 AND chapterNumber = 24;
UPDATE verses SET ts = "اور پانچویں (قسم) اس طرح (کھائے) کہ اللہ کا غضب اس (عورت) پر نازل ہو اگر وہ (الزام لگانے والا خاوند) سچا ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 24;
UPDATE verses SET ts = "اور اگر اللہ کا فضل اور اس کی رحمت تم پر نہ ہوتی اور یہ نہ ہوتا کہ اللہ بڑا فضل کرنے والا (اور) بڑی حکمتوں والا ہے (تو تم لوگ تباہی میں پڑجاتے) " WHERE ayatNumber = 11 AND chapterNumber = 24;
UPDATE verses SET ts = "یقیناً وہ لوگ جنہوں نے ایک بڑا اتہام باندھا تھا۔ تمہیں میں سے ایک گروہ ہے تم اس (فعل) کو اپنے لئے برا نہ سمجھو بلکہ وہ تمہارے لئے بہت اچھا تھا (کیونکہ اس کی وجہ سے ایک پرحکمت تعلیم تم کو مل گئی) ان میں سے ہر شخص کو اس نے جتنا گناہ کیا تھا اس کی سزا مل جائے گی اور جو شخص اس گناہ کے بڑے حصے کا ذمہ وار تھا اس کو بہت بڑا عذاب ملے گا۔ " WHERE ayatNumber = 12 AND chapterNumber = 24;
UPDATE verses SET ts = "جب تم نے یہ بات سنی تھی تو کیوں نہ مومن مردوں اور مومن عورتوں نے اپنی قوم کے متعلق نیک گمان کیا اور یہ کہہ دیا کہ یہ تو ایک بہت بڑا جھوٹ ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 24;
