UPDATE verses SET ts = "اور اگر اللہ کا فضل اور رحمت تم پر دنیا اور آخرت میں نہ ہوتے تو تم کو اس کام (کی وجہ) سے جس میں تم پڑ گئے تھے بہت بڑا عذاب پہنچتا۔ " WHERE ayatNumber = 15 AND chapterNumber = 24;
UPDATE verses SET ts = "(اس وجہ سے کہ) تم  اس (بات) کو ایک دوسرے سے لیکر زبان در زبان اچھالتے چلے گئے اور اپنے مونہوں سے وہ بات کہنے لگ گئے جس کا تم کو کوئی علم نہیں تھا (خدا تم پر ناراض ہوا) اور تم اس بات کو معمولی سمجھتے تھے حالانکہ وہ اللہ کے نزدیک بہت بڑی تھی۔ " WHERE ayatNumber = 16 AND chapterNumber = 24;
UPDATE verses SET ts = "اور کیوں نہ ہوا کہ جب تم نے اس بات کو سنا تھا تو فوراً کہہ دیا کہ یہ ہمارا کام نہیں کہ ہم اس بات کو آگے دہرائیں‘ اے خدا تو پاک ہے یہ بہت بڑا بہتان ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 24;
UPDATE verses SET ts = "اللہ تم کو اس قسم کی بات کے دوبارہ کرنے سے ہمیشہ کے لئے روکتا ہے‘ اگر تم مومن ہو۔ " WHERE ayatNumber = 18 AND chapterNumber = 24;
UPDATE verses SET ts = "اور اللہ تمہارے لئے اپنے احکام بیان کرتا ہے اور اللہ بہت جاننے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 24;
