UPDATE verses SET ts = "اور وہ لوگ اللہ کی پکی قسمیں کھاتے ہیں کہ اگر تو ان کو حکم دے تو وہ فوراً گھروں سے نکل کھڑے ہوں گے۔ کہو قسمیں نہ کھائو۔ ہمارا حکم تو تمہیں صرف ایسی اطاعت کا ہے جو عرف عام میں اطاعت سمجھی جاتی ہے اللہ اس سے جو تم کرتے ہو‘ یقیناً خبردار ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 24;
UPDATE verses SET ts = "تو کہہ اللہ کی اطاعت کرو اور اس کے رسول کی اطاعت کرو۔ پس اگر وہ پھر جائیں تو اس (رسول) پر صرف اس کی ذمہ داری ہے جو اس کے ذمہ لگایا گیا ہے اور تم پر اس کی ذمہ واری ہے جو تمہارے ذمہ لگایا گیا ہے اور اگر تم اس کی اطاعت کرو تو ہدایت پا جائو گے اور رسول کے ذمہ تو صرف بات کو کھول کر پہنچا دینا ہے۔ " WHERE ayatNumber = 55 AND chapterNumber = 24;
UPDATE verses SET ts = "اللہ نے تم میں سے ایمان لانے والوں اور مناسب حال عمل کرنے والوں سے وعدہ کیا ہے کہ وہ ان کو زمین میں خلیفہ بنا دے گا۔ جس طرح ان سے پہلے لوگوں کو خلیفہ بنا دیا تھا۔ اور جو دین اس نے ان کے لئے پسند کیا ہے وہ ان کے لئے اُسے مضبوطی سے قائم کر دے گا اور ان کے خوف کی حالت کے بعد وہ ان کے لئے امن کی حالت تبدیل کر دے گا۔ وہ میری عبادت کریں گے (اور) کسی چیز کو میرا شریک نہیں بنائیں گے اور جو لوگ اس کے بعد بھی انکار کریں گے وہ نافرمانوں میں سے قرار دیئے جائیں گے۔ " WHERE ayatNumber = 56 AND chapterNumber = 24;
UPDATE verses SET ts = "اور تم سب نمازوں کو قائم کرو اور زکٰوتیں دو‘ اور اس رسول کی اطاعت کرو تا کہ تم پر رحم کیا جائے۔ " WHERE ayatNumber = 57 AND chapterNumber = 24;
UPDATE verses SET ts = "(اور اے مخاطب) کبھی خیال نہ کر کہ کفار زمین میں ہمیں اپنی تدبیروں سے عاجز کر دیں گے اور ان کا ٹھکانا تو دوزخ ہے اور وہ بہت برا ٹھکانا ہے۔ " WHERE ayatNumber = 58 AND chapterNumber = 24;
UPDATE verses SET ts = "اے مومنو! چاہیئے کہ وہ لوگ جن کے مالک تمہارے دائیں ہاتھ ہیں اور وہ لوگ جو ابھی بلوغت کو نہیں پہنچے وہ تین وقتوں میں اجازت لے کراندر آیا کریں‘ صبح کی نماز سے پہلے‘ اور جب تم دوپہر کے وقت اپنے کپڑے اتارتے ہو‘ اور عشاء کی نماز کے بعد۔ یہ تین وقت تمہارے پردے کے وقت ہیں‘ ان وقتوں کے بعد (اندر آنے جانے پر) نہ تم پر کوئی گناہ ہے اور نہ ان پر کوئی گناہ ہے۔ کیونکہ بعض تم میں سے بعض کے پاس اکثر آتے جاتے ہیں۔ اسی طرح اللہ اپنے احکام کھول کر بیان کرتا ہے اور اللہ بہت علم والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 24;
UPDATE verses SET ts = "اور جب تمہارے بچے بلوغت کو پہنچ جائیں تو اسی طرح اجازت لیا کریں جس طرح ان سے پہلے (یعنی بڑے) لوگ اجازت لیا کرتے تھے۔ اسی طرح اللہ اپنے احکام تمہارے لئے بیان کرتا ہے اور اللہ بہت جاننے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 60 AND chapterNumber = 24;
UPDATE verses SET ts = "اور وہ عورتیں جو کہ بڑھیا ہو گئی ہیں اور نکاح کے قابل نہیں۔ ان پر کوئی گناہ نہیں ہے کہ اپنے کپڑے اتار کر رکھ دیں اس طرح کہ زینت کو ظاہر نہ کیا کریں۔ اور ان کا بچے رہنا ان کے لئے بہتر ہوگا اور اللہ بہت سننے والا (اور) جاننے والا ہے۔ " WHERE ayatNumber = 61 AND chapterNumber = 24;
UPDATE verses SET ts = "نہ اندھوں پر‘ نہ لنگڑوں پر‘ نہ مریض پر‘ نہ تم پر اپنے گھروں سے یا اپنے باپ دادوں کے گھروں سے یا اپنی مائوں (یا ننھیال) کے گھر سے یا اپنے بھائیوں کے گھر سے یا اپنی بہنوں کے گھر سے یا اپنے چچوں کے گھر سے یا اپنی پھوپھیوں کے گھر سے یا اپنے ماموئوں کے گھر سے یا اپنی خالائوں کے گھر سے یا جن کے سامان کے انتظام پر تم مقرر ہو‘ یا اپنے دوستوں کے گھروں سے کوئی چیز لے کر کھا لینے میں کوئی حرج ہے (اسی طرح) تم پر کوئی گناہ نہیں کہ تم سب مل کر کھائو یا الگ الگ کھائو۔ پس جب گھروں میں داخل ہونے لگو تو اپنے عزیزوں یا دوستوں پر سلام  کہہ لیا کرو۔ یہ اللہ کی طرف سے ایک بڑی برکت والی اور پاکیزہ دعا ہے۔ اسی طرح اللہ اپنے احکام تمہیں کھول کر سناتا ہے تا کہ تم عقل سے کام لو۔ " WHERE ayatNumber = 62 AND chapterNumber = 24;
UPDATE verses SET ts = "صرف وہی لوگ مومن کہلانے کے مستحق ہیں جو اللہ اور اس کے رسول پر ایمان لاتے ہیں اور جب کسی قومی کام کے لئے اس (رسول) کے پاس بیٹھے ہوں تو اٹھ کر نہیں جاتے جب تک اس کی اجازت نہ لے لیں۔ وہ لوگ جو کہ اجازت لے کر جاتے ہیں وہی اللہ اور اس کے رسول پر ایمان رکھتے ہیں۔ پس جب وہ اپنے کسی اہم کام کے لئے تجھ سے اجازت لیں تو ان میں سے جن کے متعلق تو چاہے انہیں اجازت دے دے۔ اور اللہ سے ان کے لئے بخشش مانگ۔ اور اللہ یقیناً بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 24;
UPDATE verses SET ts = "(اے مومنو)! یہ نہ سمجھو کہ رسول کا تم میں سے کسی کو بلانا ایسا ہی ہے جیسا کہ تم میں سے بعض کا بعض کو بلانا۔ اللہ ان لوگوں کو جانتا ہے جو کہ تم میں سے پہلو بچا کر (مشورہ کی مجلس سے) بھاگ جاتے ہیں۔ پس چاہیئے کہ جو اس (رسول) کے حکم کی مخالفت کرتے ہیں اس سے ڈریں کہ ان کو خدا کی طرف سے کوئی آفت نہ پہنچ جائے یا ان کو دردناک عذاب نہ پہنچ جائے۔" WHERE ayatNumber = 64 AND chapterNumber = 24;
UPDATE verses SET ts = "سنو‘ جو کچھ آسمانوں اور زمین میں ہے اللہ کا ہی ہے۔ جس (مقام) پر تم (کھڑے) ہو اس کو بھی اللہ ہی جانتا ہے اور جس دن وہ لوگ اللہ کی طرف لوٹائے جائیں گے تو وہ ان کو ان کے اعمال کے حال بتائے گا اور اللہ ہر ایک چیز کو خوب جانتا ہے۔ " WHERE ayatNumber = 65 AND chapterNumber = 24;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 25;
UPDATE verses SET ts = "وہ ذات بڑی برکت والی ہے جس نے فرقان اپنے بندے پر اتارا ہے‘ تا کہ وہ سب جہانوں کے لئے ہوشیار کرنے والا بنے۔ " WHERE ayatNumber = 2 AND chapterNumber = 25;
UPDATE verses SET ts = "وہ (ذات) جس کے قبضہ میں آسمانوں اور زمین کی بادشاہت ہے اور جس نے کوئی بیٹا نہیں بنایا اور جس کی بادشاہت میں کوئی شریک نہیں اور جس نے ہر چیز کو پیدا کیا ہے پھر اس کے لئے ایک اندازہ مقرر کیا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ان لوگوں نے اس (خدا) کے سوا معبود بنا چھوڑے ہیں‘ جو کچھ (بھی) پیدا نہیں کرتے حالانکہ وہ خود پیدا کیے جاتے ہیں اور جو اپنی ذات کے لئے نہ کسی ضرر پر قادر ہیں نہ نفع پر۔ نہ موت کے مالک ہیں اور نہ زندگی کے اور نہ پھر جی اٹھنے کے۔ " WHERE ayatNumber = 4 AND chapterNumber = 25;
UPDATE verses SET ts = "اور کافر کہتے ہیں کہ یہ تو صرف ایک جھوٹ ہے جو اس نے بنا لیا ہے۔ اور اس کے بنانے پر ایک اور قوم نے اس کی مدد کی ہے۔ پس ان لوگوں نے (یہ بات کہہ کر) بہت بڑا ظلم کیا ہے اور بہت بڑا جھوٹ بولا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ یہ (قرآن) تو پہلوں کی باتیں ہیں جو اس نے (کسی سے) لکھوالی ہیں۔ اور اب وہ صبح شام اس کے سامنے پڑھ کر سنائی جاتی ہیں (تا کہ وہ قرآن اچھی طرح لکھ لے)۔ " WHERE ayatNumber = 6 AND chapterNumber = 25;
UPDATE verses SET ts = "تو کہہ دے کہ اس (قرآن) کو تو اُس (خدا) نے اتارا ہے جو آسمانوں اور زمین کے رازوں سے واقف ہے‘ وہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ اس رسول کو کیا ہو گیا ہے کہ وہ کھانا بھی کھاتا ہے اور بازاروں میں بھی چلتا پھرتا ہے۔ کیوں نہ اس پر فرشتہ اتارا گیا‘ جو اس کے ساتھ کھڑا ہو کر لوگوں کو ہوشیار کرتا۔ " WHERE ayatNumber = 8 AND chapterNumber = 25;
UPDATE verses SET ts = "یا اس پر کوئی خزانہ اتارا جاتا‘ یا اس کے پاس کوئی باغ ہوتا‘ جس کے پھل وہ کھاتا‘ اور ظالم کہتے ہیں کہ تم تو ایک ایسے آدمی کے پیچھے چل رہے ہو جس کو کھانا کھلایا جاتا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 25;
UPDATE verses SET ts = "دیکھ! یہ تیرے متعلق کیسی کیسی باتیں بناتے ہیں اور وہ گمراہ ہو چکے ہیں پس ان کو کوئی (صحیح بات کہنے کا) رستہ نہیں ملتا۔ " WHERE ayatNumber = 10 AND chapterNumber = 25;
UPDATE verses SET ts = "بہت برکت والا ہے وہ خدا جو چاہے تو تیرے لئے (ان کے تجویز کردہ) اس (باغ) سے بہت بہتر باغات پیدا کر دے جن (کے سایہ) میں نہریں بہتی ہوں اور تیرے لئے بڑے بڑے محل تیار کر دے۔ " WHERE ayatNumber = 11 AND chapterNumber = 25;
UPDATE verses SET ts = "حق یہ ہے کہ یہ لوگ قیامت کا انکار کر رہے ہیں اور ہم نے اس کے لئے جو قیامت کا منکر ہو بھڑکنے والے عذاب کا انتظام کر چھوڑا ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 25;
UPDATE verses SET ts = "جب وہ (یعنی جہنم) ان کو دور سے دیکھے گی تو وہ اس کے جوش کی اور (آنے والی) مصیبت کی آواز کو سنیں گے۔ " WHERE ayatNumber = 13 AND chapterNumber = 25;
UPDATE verses SET ts = "اور جب وہ اس (دوزخ) کے ایک تنگ حصہ میں مشکیں باندھے ہوئے پھینکے جائیں گے اور وہ اس وقت موت کی آرزو کریں گے۔ " WHERE ayatNumber = 14 AND chapterNumber = 25;
UPDATE verses SET ts = "(تب خدا کے فرشتے ان سے کہیں گے) آج ایک موت کی آرزو نہ کرو بلکہ باربار موت کی خواہش کرو (کیونکہ تم پر باربار عذاب آنے والے ہیں) " WHERE ayatNumber = 15 AND chapterNumber = 25;
UPDATE verses SET ts = "تو ان سے کہہ دے کہ یہ (انجام) بہتر ہے یا دائمی جنت‘ جس کا متقیوں سے وعدہ کیا گیا ہے۔ وہ ان کا (صحیح صحیح) بدلہ اور آخری ٹھکانا ہوگی۔ " WHERE ayatNumber = 16 AND chapterNumber = 25;
UPDATE verses SET ts = "انہیں اس میں جو کچھ چاہیں گے ملے گا۔ وہ اس میں ہمیشہ کے لئے بستے چلے جائیں گے۔ یہ ایک ایسا وعدہ ہے جس کا پورا کرنا تیرے رب پر واجب ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 25;
UPDATE verses SET ts = "اور جب وہ اُن کو اور اُن کے جھوٹے معبودوں کو اپنے حضور میں کھڑا کرے گا اور پھر ان سے کہے گا کیا تم نے میرے ان بندوں کو گمراہ کیا تھا یا وہ آپ ہی سیدھے راستہ سے بھٹک گئے تھے۔ " WHERE ayatNumber = 18 AND chapterNumber = 25;
UPDATE verses SET ts = "(تب) وہ جواب دیں گے تو پاک ہے ہمیں کوئی حق نہ تھا کہ ہم تیرے سوا اور ہستیوں کو اپنا کارساز بناتے لیکن تو نے ان لوگوں کو اور ان کے باپ دادوں کو دنیوی متاع بخشے یہاں تک کہ انہوں نے (تیری) یاد کو ترک کر دیا اور ہلاک ہونے والی قوم بن گئے۔" WHERE ayatNumber = 19 AND chapterNumber = 25;
UPDATE verses SET ts = "پس (کفار سے کہا جائے گا کہ دیکھ لو) ان جھوٹے معبودوں نے تمہاری باتوں کو جھٹلا دیا ہے۔ پس آج تم نہ تو عذاب کو ہٹا سکتے ہو‘ اور نہ کوئی مدد حاصل کر سکتے ہو۔ اور جو کوئی تم میں سے ظالم ہے ہم اسے بڑا عذاب پہنچائیں گے۔ " WHERE ayatNumber = 20 AND chapterNumber = 25;
UPDATE verses SET ts = "اور تجھ سے پہلے ہم نے جتنے بھی رسول بھیجے تھے‘ وہ سب کے سب کھانا کھاتے تھے اور بازاروں میں چلتے تھے۔ اور ہم نے تم میں سے بعض کو بعض کے لئے آزمائش کا ذریعہ بنایا ہے (یہ دیکھنے کے لئے) کہ کیا تم (مسلمان) صبر کرتے ہو (یا نہیں) اور (اے مسلمان) تیرا رب (حالات کو) بہت دیکھنے والا ہے۔ " WHERE ayatNumber = 21 AND chapterNumber = 25;
UPDATE verses SET ts = "اور انہوں نے جو ہماری ملاقات کی امید نہیں کرتے کہہ دیا کہ کیوں ہم پر فرشتے نہیں اتارے گئے؟ یا ہم اپنے رب کو اپنی آنکھوں سے نہیں دیکھتے؟ انہوں نے اپنے دلوں میں اپنے آپ کو بہت بڑا سمجھا ہے اور سرکشی میں بہت آگے نکل گئے ہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 25;
UPDATE verses SET ts = "(کیا یہ لوگ نہیںجانتے کہ) جس دن فرشتوں کو دیکھیں گے اس دن مجرموں کو کوئی خوش خبری نہیں ملے گی اور (وہ گھبرا کر) کہیں گے (ہم سے) پرے ہی رہو۔ " WHERE ayatNumber = 23 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ہم نے ان کے ہر قسم کے عمل کی طرف توجہ کی جو انہوں نے کیا تھا اور اس کو ہوا میں بکھیر کر اڑائے ہوئے ذرات کی طرح کر دیا۔ " WHERE ayatNumber = 24 AND chapterNumber = 25;
UPDATE verses SET ts = "جنتی لوگ اس دن ٹھکانے کے لحاظ سے بھی اچھے ہوں گے اور خواب گاہوں کے لحاظ سے بھی وہ اعلیٰ مقام پر ہوں گے۔ " WHERE ayatNumber = 25 AND chapterNumber = 25;
UPDATE verses SET ts = "اور اس دن (کو یاد کرو) جب آسمان پھٹ جائے گا اور بادل سر پر منڈلا رہے ہوں گے اور فرشتے باربار اتارے جائیں گے۔ " WHERE ayatNumber = 26 AND chapterNumber = 25;
UPDATE verses SET ts = "اس دن بادشاہت سچ مچ رحمن (خدا) کے قبضہ میں نظر آئے گی۔ اور (یہ) دن کافروں پر بڑا سخت ہوگا۔ " WHERE ayatNumber = 27 AND chapterNumber = 25;
UPDATE verses SET ts = "اور اس دن ظالم اپنے ہاتھوں کو کاٹے گا (اور) کہے گا۔ اے کاش! میں رسول کے ساتھ چل پڑتا۔ " WHERE ayatNumber = 28 AND chapterNumber = 25;
UPDATE verses SET ts = "وائے بدبختی! کاش! میں فلاں شخص کو دوست نہ بناتا۔ " WHERE ayatNumber = 29 AND chapterNumber = 25;
UPDATE verses SET ts = "اس نے مجھے خدا کے ذکر سے غافل کر دیا جبکہ وہ (رسول کے ذریعہ سے) میرے پاس آیا تھا۔ اور شیطان آخر انسان کو اکیلا چھوڑ کر چلا جاتا ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 25;
UPDATE verses SET ts = "اور رسول نے کہا‘ اے میرے رب! میری قوم نے تو اس قرآن کو پیٹھ کے پیچھے پھینک دیا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ہم نے اسی طرح مجرموں میں سے سب نبیوں کے دشمن بنائے ہیں‘ اور تیرا رب ہدایت دینے اور مدد کرنے کے لحاظ سے (بالکل) کافی ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 25;
UPDATE verses SET ts = "اور کافروں نے کہا‘ کیوں نہ قرآن اس (نبی) پر ایک ہی دفعہ نازل کر دیا گیا۔ ان کا کہنا بھی ایک طرح ٹھیک ہے (لیکن ہم نے اس کو مختلف سورتوں اور وقتوں میں) اس لئے (اتارا) کہ ہم اس (قرآن) کے ذریعہ سے تیرے دل کو مضبوط کرتے رہیں اور ہم نے اس کو نہایت عمدہ بنایا ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 25;
UPDATE verses SET ts = "اور (تیری تردید کے لئے) وہ کوئی بات نہیں کہتے کہ ہم اس کے جواب میں ایک پختہ بات بیان نہیں کر دیتے۔ اور اس کی اچھی سے اچھی توجیہ نہیں کر دیتے۔ " WHERE ayatNumber = 34 AND chapterNumber = 25;
UPDATE verses SET ts = "جو لوگ اپنے سرداروں سمیت جہنم کی طرف لے جائے جائیں گے ان کا مقام بہت برا ہوگا اور ان کا رستہ بڑی گمراہی کا ہوگا۔ " WHERE ayatNumber = 35 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ہم نے موسیٰ کو ایک (معلوم) کتاب دی تھی اور ہم نے اس کے ساتھ اس کے بھائی ہارون کو بھی نائب بنا کر بھیج دیا تھا۔ " WHERE ayatNumber = 36 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ہم نے ان سے کہا تھا کہ تم دونوں اس قوم کی طرف جائو جنہوں نے ہماری آیتوں کا انکار کر دیا ہے پھر (جب وہ تبلیغ کر چکے) ہم نے ان جھٹلانے والوںکو بالکل تباہ کردیا۔ " WHERE ayatNumber = 37 AND chapterNumber = 25;
UPDATE verses SET ts = "اور قوم نوح کو بھی جب انہوں نے رسولوں کا انکار کیا ہم نے غرق کر دیا اور ہم نے انہیں لوگوں کے لئے ایک نشان بنا دیا اور ہم نے ظالموں کے لئے دردناک عذاب تیار کر چھوڑا ہے۔ " WHERE ayatNumber = 38 AND chapterNumber = 25;
UPDATE verses SET ts = "اور عاد کو بھی اور ثمود کو بھی اور کنوئیں والے لوگوں کوبھی اور ان کے درمیان اور بہت سے قوموں کو بھی (ہم نے تباہ کردیا) " WHERE ayatNumber = 39 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ان میں سے ہر قوم کے لئے ہم نے حقیقت بیان کر دی۔ اور (جب نہ سمجھے تو) سب کو ہلاک کر دیا۔ " WHERE ayatNumber = 40 AND chapterNumber = 25;
UPDATE verses SET ts = "اور یہ (مکہ کے کفار) اس بستی کے پاس سے گذر چکے ہیں جس پر ایک تکلیف دہ بارش نازل کی گئی تھی۔ کیا یہ اس (بستی کے نشانوں) کو نہیں دیکھتے۔ حقیقت یہ ہے کہ یہ دوبارہ اٹھنے کی امید ہی نہیں رکھتے تھے۔ " WHERE ayatNumber = 41 AND chapterNumber = 25;
UPDATE verses SET ts = "اور جب وہ تجھے دیکھتے ہیں تو تجھے صرف ایک ہنسی ٹھٹھے کی چیز سمجھتے ہیں‘ (اور کہتے ہیں) کیا اللہ نے اس شخص کو رسول بنا کر بھیجا ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 25;
UPDATE verses SET ts = "اگر ہم اپنے معبودوں پر قائم نہ رہتے تو یہ (شخص) تو ہم کو ان سے گمراہ کرنے ہی لگا تھا۔ اور جب یہ عذاب کو دیکھیں گے تو ان کو ضرور حقیقت معلوم ہو جائے گی کہ کون اپنے طور و طریق میں زیادہ گمراہ تھا۔ " WHERE ayatNumber = 43 AND chapterNumber = 25;
UPDATE verses SET ts = "(اے رسول)! کیا تو نے اس شخص کا حال بھی معلوم کرلیا جس نے اپنی خواہشات نفسانی کو اپنا معبود بنا لیا۔ کیا تو اس شخص پر نگران ہے (کہ تو اسے جبراً گمراہی سے روکے)۔ " WHERE ayatNumber = 44 AND chapterNumber = 25;
UPDATE verses SET ts = "کیا تو سمجھتا ہے کہ ان میں سے اکثر سنتے یا سمجھتے ہیں؟ وہ تو فقط جانوروں کی طرح ہیں‘ بلکہ رویہ کے لحاظ سے ان سے بھی بدتر۔ " WHERE ayatNumber = 45 AND chapterNumber = 25;
UPDATE verses SET ts = "(اے قرآن کے مخاطب) کیا تجھے معلوم نہیں کہ تیرے رب نے کس طرح سایہ کو لمبا کیا ہے۔ اور اگر وہ چاہتا تو اسے ایک جگہ ٹھہرا ہوا بنا دیتا۔ پھر ہم نے سورج کو اس پر ایک گواہ بنا دیا۔ " WHERE ayatNumber = 46 AND chapterNumber = 25;
UPDATE verses SET ts = "پھر ہم اس کو آہستہ آہستہ اپنی طرف کھینچنا شروع کرتے ہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہی (خدا) ہے جس نے رات کو تمہارے لئے لباس بنایا اورنیند کو آرام کا موجب‘ اور دن کوپھیلنے اورترقی کا ذریعہ۔ " WHERE ayatNumber = 48 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ (خدا) ہی ہے جس نے ہوائوں کو اپنی رحمت سے پہلے بشارت دینے کے لئے بھیجا اور ہم نے بادل سے پاک (وصاف) پانی اتارا ہے۔ " WHERE ayatNumber = 49 AND chapterNumber = 25;
UPDATE verses SET ts = "تا کہ اس کے ذریعہ سے مردہ ملک کو زندہ کریں اور اسی طرح اس (پانی) سے اپنے پیدا کیے ہوئے چارپایوں اور بہت سے انسانوں کو سیراب کریں۔ " WHERE ayatNumber = 50 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ہم نے اس (پانی) کو اُن (انسانوں) میں خوب پھیلا دیا تا کہ وہ نصیحت حاصل کریں لیکن لوگوں میں سے اکثر لوگ کفر کے سوا کسی بات پر راضی نہیں ہوتے۔ " WHERE ayatNumber = 51 AND chapterNumber = 25;
UPDATE verses SET ts = "اور اگر ہم چاہتے تو ہر بستی میں ایک ہوشیار کرنے والا (نبی یا مامور) بھیج دیتے۔ " WHERE ayatNumber = 52 AND chapterNumber = 25;
UPDATE verses SET ts = "پس تو کافروں کی بات نہ مان اور اس (قرآن) کے ذریعہ سے ان سے بڑا جہاد کر۔ " WHERE ayatNumber = 53 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہی ہے جس نے دو سمندروں کو چلایا ہے جن میں سے ایک تو بہت میٹھا ہے‘ اور دوسرا نمکین (اور) کڑوا ہے اور اس (اللہ) نے ان دونوں کے درمیان ایک روک بنا دی ہے اور ایسا سامان بنایا ہے کہ وہ ایک دوسرے کو پرے رکھتے ہیں‘ ملنے نہیں دیتے۔ " WHERE ayatNumber = 54 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ (خدا) ہی ہے جس نے پانی سے انسان بنایا‘ پس اس کو کبھی تو نسب بنایا ہے (یعنی شجرئہ آبائ) اور کبھی صِھْر بنایا ہے (یعنی شجرئہ سسرال) اور تیرا رب ہر چیز پر قادر ہے۔ " WHERE ayatNumber = 55 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ (کافر) اللہ کے سوا ان کی عبادت کرتے ہیں جو نہ انہیں نفع دے سکتے ہیں اور نہ تکلیف پہنچا سکتے ہیں اور کافر ہمیشہ اپنے رب کے (جاری کردہ سلسلوں کے) خلاف ہوتا ہے۔ " WHERE ayatNumber = 56 AND chapterNumber = 25;
UPDATE verses SET ts = "اور ہم نے تو تجھے صرف بشارت دینے والا اور ہوشیار کرنے والا بنایا ہے۔ " WHERE ayatNumber = 57 AND chapterNumber = 25;
UPDATE verses SET ts = "تو ان سے کہہ دے کہ میں تم سے اُس (یعنی خدا کا پیغام پہنچانے) کا کوئی اجر نہیں مانگتا۔ ہاں اگر کوئی شخص اپنی مرضی سے چاہے تو اپنے رب کی طرف جانے والی راہ کو اختیار کرلے (وہی میرا بدلہ ہوگا) " WHERE ayatNumber = 58 AND chapterNumber = 25;
UPDATE verses SET ts = "اور تو اس پر توکل کر جو زندہ ہے (اور سب کو زندہ رکھتا ہے) کبھی نہیں مرتا اور اس کی تعریف کے ساتھ ساتھ اس کی تسبیح بھی کر اور وہ اپنے بندوں کے گناہوں سے خوب واقف ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 25;
UPDATE verses SET ts = "وہ (خدا) جس نے آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے ان سب کو چھ اوقات میں پیدا کیا ہے پھر وہ مضبوطی سے عرش پر قائم ہو گیا۔وہ رحمان ہے پس جب بھی (اے انسان) تو اس کے متعلق کوئی سوال کرے تو خبیر سے سوال کر جو بہت باخبر ہے (اور ٹھیک ٹھیک جواب دے سکتا ہے)۔ " WHERE ayatNumber = 60 AND chapterNumber = 25;
UPDATE verses SET ts = "اور جب ان سے کہا جاتا ہے کہ رحمن کے سامنے سجدہ میں گر جائو‘ تو کہتے ہیں رحمن کیا چیز ہے؟ کیا ہم اس کے آگے سجدہ کریں جس (کے آگے سجدہ کرنے) کا تو حکم دیتا ہے اور یہ بات ان کو نفرت میں اور بھی بڑھا دیتی ہے۔ " WHERE ayatNumber = 61 AND chapterNumber = 25;
UPDATE verses SET ts = "برکت والی ہے وہ ہستی جس نے آسمان میں ستاروں کے ٹھہرنے کے مقام بنائے ہیں اور اس میں چمکتا ہوا چراغ بنایا ہے اور نور دینے والا چاند بنائے ہیں۔ " WHERE ayatNumber = 62 AND chapterNumber = 25;
UPDATE verses SET ts = "وہی ہے جس نے رات کو اور دن کو ایک دوسرے کے پیچھے آنے والا بنایا ہے اس شخص کے (فائدہ کے) لئے جو نصیحت حاصل کرنا چاہے یا شکر گذار بندہ بننا چاہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 25;
UPDATE verses SET ts = "اور رحمن کے (سچے) بندے وہ ہوتے ہیں جو زمین پر آرام سے چلتے ہیں اور جب جاہل لوگ ان سے مخاطب ہوتے ہیں تو وہ (لڑتے نہیں بلکہ) کہتے ہیں کہ ہم توتمہارے لئے سلامتی کی دعا کرتے ہیں۔ " WHERE ayatNumber = 64 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ لوگ بھی جو اپنے رب کے لئے راتیں سجدوں میں اور کھڑے ہو کر گذار دیتے ہیں۔ " WHERE ayatNumber = 65 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ (رحمن کے بندے) کہتے ہیں‘ اے ہمارے رب! ہم سے جہنم کا عذاب ٹلادے۔ اس کا عذاب ایک بہت بڑی تباہی ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 25;
UPDATE verses SET ts = "وہ (دوزخ) عارضی ٹھکانے کے طور پر بھی بری ہے اور مستقل ٹھکانے کے طور پر بھی (بری ہے) ۔" WHERE ayatNumber = 67 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ (اللہ کے بندے) ایسے ہوتے ہیں کہ جب خرچ کرتے ہیں تو فضول خرچی سے کام نہیں لیتے اور نہ بخل کرتے ہیں اور ان کا خرچ ان دونوں حالتوں کے درمیان درمیان ہوتا ہے۔ " WHERE ayatNumber = 68 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ لوگ ایسے ہوتے ہیں کہ اللہ کے سوا کسی اور معبود کو نہیں پکارتے اور نہ کسی جان کو جسے اللہ نے حفاظت بخشی ہو قتل کرتے ہیں سوائے (شرعی) حق کے۔ اور نہ زنا کرتے ہیں اور جو کوئی ایسا کام کرے گا وہ اپنے گناہ کی جزا کو دیکھ لے گا۔ " WHERE ayatNumber = 69 AND chapterNumber = 25;
UPDATE verses SET ts = "قیامت کے دن اس کے لئے عذاب زیادہ کیا جائے گا اور وہ اس میں ذلت کے ساتھ رہتا چلا جائے گا۔ " WHERE ayatNumber = 70 AND chapterNumber = 25;
UPDATE verses SET ts = "سوائے اس کے جس نے توبہ کر لی اور ایمان لایا اور ایمان کے مطابق عمل کیے پس یہ لوگ ایسے ہوں گے کہ اللہ ان کی بدیوں کو نیکیوں سے بدل دے گا اور اللہ بڑا بخشنے والا مہربان ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 25;
UPDATE verses SET ts = "اور جو توبہ کرے اور اس کے مطابق عمل کرے‘ تو وہ شخص حقیقی طور پر اللہ کی طرف جھکتا ہے۔ " WHERE ayatNumber = 72 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ لوگ بھی (اللہ کے بندے ہیں) جو جھوٹی گواہیاں نہیں دیتے اور جب لغو باتوں کے پاس سے گذرتے ہیں تو بزرگانہ طور پر (بغیر ان میں شامل ہونے کے) گذر جاتے ہیں۔ " WHERE ayatNumber = 73 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ لوگ بھی کہ ان کے رب کی آیات جب انہیں یاد دلائی جائیں تو ان سے بہروں اور اندھوں کا معاملہ نہیں کرتے۔ " WHERE ayatNumber = 74 AND chapterNumber = 25;
UPDATE verses SET ts = "اور وہ لوگ بھی (رحمن کے بندے ہیں) جو یہ کہتے رہتے ہیں کہ اے ہمارے رب! ہم کو ہماری بیویوں کی طرف سے اور اولاد کی طرف سے آنکھوں کی ٹھنڈک عطا فرما۔ اور ہمیں متقیوں کا امام بنا۔ " WHERE ayatNumber = 75 AND chapterNumber = 25;
