UPDATE verses SET ts = "اور سلیمان دائود کا وارث بنا اور اس نے کہا اے لوگو! ہمیں پرندوں کی زبان سکھائی گئی ہے اور ہر ضروری چیز (یعنی تعلیم) ہم کو دی گئی ہے یہ کھلا کھلا فضل ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 27;
UPDATE verses SET ts = "اور (ایک دفعہ) سلیمان کے سامنے جنوں اور انسانوں اور پرندوں میں سے اس کے لشکر ترتیب وار اکٹھے کیے گئے (پھر ان کو کوچ کا حکم ملا) ۔" WHERE ayatNumber = 18 AND chapterNumber = 27;
UPDATE verses SET ts = "یہاں تک کہ جب وہ وادی نملہ میں پہنچے تو نملہ قوم میں سے ایک شخص نے کہا‘ اے نملہ قوم اپنے اپنے گھروں میں چلے جائو۔ ایسا نہ ہو کہ سلیمان اور اس کے لشکر (تمہارے حالات کو) نہ جانتے ہوئے تمہیں پیروں کے نیچے مسل دیں۔ " WHERE ayatNumber = 19 AND chapterNumber = 27;
UPDATE verses SET ts = "پس سلیمان اس کی بات سن کر ہنس پڑا اور کہا‘ اے میرے رب! مجھے توفیق دے کہ تیری نعمت کا جو تو نے مجھ پر اور میرے والدین پر کی ہے شکریہ ادا کر سکوں اور ایسا مناسب عمل کروں جسے تو پسند فرمائے اور (اے خدا) اپنے رحم کے ساتھ تو مجھے اپنے بزرگ بندوں میں داخل کر۔ " WHERE ayatNumber = 20 AND chapterNumber = 27;
UPDATE verses SET ts = "اور اس نے سب پرندوں کی حاضری لی۔ پھر کہا مجھے کیا ہوا ہے کہ میں ہدہد کو نہیں دیکھتا‘ یا وہ (جان بوجھ کر) غیر حاضر ہے۔ " WHERE ayatNumber = 21 AND chapterNumber = 27;
UPDATE verses SET ts = "میں اس کو یقیناً سخت سزا دوں گا‘ یا اسے قتل کر دوں گا۔ یا وہ میرے سامنے کوئی کھلی دلیل (اپنی غیر حاضری کی) پیش کرے گا۔ " WHERE ayatNumber = 22 AND chapterNumber = 27;
UPDATE verses SET ts = "پس کچھ دیر وہ ٹھہرا (اتنے میں ہدہد حاضر ہوا) اور اس نے کہا کہ میں نے اس چیز کا علم حاصل کیا ہے جو تجھے حاصل نہیں اور میں سبا (کی قوم کے علاقہ) سے تیرے پاس (آیا ہوںاور)ایک یقینی خبر لایا ہوں۔ " WHERE ayatNumber = 23 AND chapterNumber = 27;
UPDATE verses SET ts = "(جو یہ ہے کہ) میں نے (وہاں) ایک عورت کو دیکھا جو ان (کی ساری قوم) پر حکومت کر رہی ہے اور ہر نعمت اسے حاصل ہے اور اس کا ایک بڑا تخت ہے۔ " WHERE ayatNumber = 24 AND chapterNumber = 27;
UPDATE verses SET ts = "اور میں نے اس کو اور اس کی قوم کو اللہ کے سوا سورج کے آگے سجدہ کرتے دیکھا اور شیطان نے ان کے عمل ان کو خوبصورت کرکے دکھائے ہیں اور ان کے سچے راستہ سے روک دیا ہے جس کی وجہ سے وہ ہدایت نہیں پاتے۔ " WHERE ayatNumber = 25 AND chapterNumber = 27;
UPDATE verses SET ts = "(اور مصر ہیں) کہ اللہ کو سجدہ نہ کریں جو کہ آسمانوں اور زمین کی ہر پوشیدہ تقدیر کو ظاہر کرتا ہے اور جو کچھ تم چھپاتے اور ظاہر کرتے ہو ان (تدبیروں) کو بھی جانتا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 27;
UPDATE verses SET ts = "(حالانکہ) اللہ وہ ہے جس کے سوا کوئی معبود نہیں (وہ) ایک بڑے تخت کا مالک ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 27;
UPDATE verses SET ts = "(اس پر سلیمان نے) کہا کہ ہم دیکھیں گے کہ تو نے سچ بولا ہے یا تو جھوٹوں میں سے ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 27;
UPDATE verses SET ts = "تو میرا یہ خط لے جا اور اسے ان کے (یعنی سبا کی قوم کے) سامنے پھینک دے پھر (ادب سے) پیچھے ہٹ (کر کھڑا ہو) جا اور دیکھ کہ وہ کیا جواب دیتے ہیں۔ " WHERE ayatNumber = 29 AND chapterNumber = 27;
UPDATE verses SET ts = "(جب اس نے ایسا کیا) تو وہ (ملکہ) بولی۔ اے میرے درباریو! میرے سامنے ایک معزز خط رکھا گیا ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 27;
UPDATE verses SET ts = "(جس کا مضمون یہ ہے کہ) یہ خط سلیمان کی طرف سے ہے اور اس میں بتایا گیا ہے کہ اللہ جو بے انتہا رحم کرنے والا (اور) باربار کرم کرنے والا ہے‘ اس کے نام سے ہم شروع کرتے ہیں۔ " WHERE ayatNumber = 31 AND chapterNumber = 27;
UPDATE verses SET ts = "(اور کہتے ہیں) کہ ہم پر زیادتی نہ کرو اور ہمارے حضور میں فرمانبردار بن کر حاضر ہو جائو۔ " WHERE ayatNumber = 32 AND chapterNumber = 27;
UPDATE verses SET ts = "پھر اس (ملکہ) نے کہا۔ اے سردارو! میرے معاملہ میں اپنی پختہ رائے دو۔ کیونکہ میں کبھی کوئی فیصلہ نہیں کرتی۔ جب تک کہ تم میرے پاس حاضر نہ ہو (اور مشورہ نہ دے لو) " WHERE ayatNumber = 33 AND chapterNumber = 27;
UPDATE verses SET ts = "(درباریوں نے) کہا ہم بڑی طاقت والے ہیں اور بڑے جنگجو ہیں اور (آخری) معاملہ آپ کے ہاتھ میں ہے پس غور کر لیں کہ آپ کیا حکم دینا چاہتی ہیں (ہم اس کی اتباع کریں گے) " WHERE ayatNumber = 34 AND chapterNumber = 27;
UPDATE verses SET ts = "اس نے کہا کہ جب بادشاہ کسی ملک میں داخل ہوتے ہیں تو اسے تباہ کردیتے ہیں اور اس کے باشندوں میں سے معزز لوگوں کو ذلیل کر دیا کرتے ہیں اور وہ اسی طرح کرتے چلے آئے ہیں۔ " WHERE ayatNumber = 35 AND chapterNumber = 27;
UPDATE verses SET ts = "اور (میں نے فیصلہ کیا ہے کہ) میں ان کی طرف ایک تحفہ بھیجوں گی۔ پھر دیکھوں گی کہ میرے ایلچی کیا جواب لے کر واپس آتے ہیں۔ " WHERE ayatNumber = 36 AND chapterNumber = 27;
UPDATE verses SET ts = "پھر جب وہ تحفہ سلیمان کے سامنے لاکر رکھا گیا تو اس نے کہا کیا تم مال کے ذریعہ سے میری مدد کرنا چاہتے ہو (اگر یہ بات ہے تو یاد رکھو کہ) اللہ نے جو کچھ مجھے  دیا ہے وہ اس سے بہت بہتر ہے جو تم کو دیا ہے اور (معلوم ہوتا ہے کہ) تم اپنے تحفہ پر بڑے نازاں ہو۔ " WHERE ayatNumber = 37 AND chapterNumber = 27;
UPDATE verses SET ts = "(اے ہدہد)! تو ان کی طرف لوٹ جا اور (ان سے کہدے کہ) میں ایک بڑے لشکر کے ساتھ ان کے پاس آئوں گا ایسا لشکر کہ اس کے مقابلہ کی ان کو طاقت نہ ہوگی اورمیں ان کو اس (ملک) سے (مفتوح ہونے کے بعد) ایسی حالت میں نکال دوں گا کہ (وہ بادشاہت کی) عزت کھوچکے ہوں گے۔ " WHERE ayatNumber = 38 AND chapterNumber = 27;
UPDATE verses SET ts = "(اس کے بعد) اس نے (اپنے درباریوں سے مخاطب ہو کر) کہا۔ اے درباریو! تم میں سے کون اس کے تخت کو میرے پاس لے آئے گا پیشتر اس کے کہ وہ (لوگ) فرمانبردار ہو کر میری خدمت میں حاضر ہوں۔ " WHERE ayatNumber = 39 AND chapterNumber = 27;
UPDATE verses SET ts = "(پہاڑی قوموں میں سے) ایک سرکش سردار نے کہا۔ آپ کے (اس) مقام سے جانے سے پہلے میں وہ (عرش) لے آئوں گا اور میں اس بات پر بڑی قدرت رکھنے والا (اور) امانت دار ہوں۔ " WHERE ayatNumber = 40 AND chapterNumber = 27;
UPDATE verses SET ts = "(اس پر) اس شخص نے جس کو (الٰہی) کتاب کا علم حاصل تھا کہا کہ میں تیرے پاس اس (تخت) کو تیرے آنکھ جھپکنے سے پہلے لے آئوں گا۔ پس جب (سلیمان نے) اس کو پاس رکھا ہوا دیکھا تو اس نے کہا۔ یہ میرے رب کے فضل کی وجہ سے ہوا ہے تا کہ وہ مجھے آزمائے کہ میں شکر کرتا ہوں یا ناشکری کرتا ہوں اور جو شکر کرے وہ اپنی جان کے فائدہ کے لئے ایسا کرتا ہے اور جو ناشکری کرے تو یقیناً میرا رب بے نیاز (اور) بڑی سخاوت کرنے والا ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 27;
UPDATE verses SET ts = "(پھر) اس نے کہا اس (ملکہ) کے لئے اس کا عرش حقیر کرکے دکھائو (ہم دیکھیں گے کہ) کیا وہ ہدایت پاتی ہے یا ان لوگوں میں سے بنتی ہے جو ہدایت نہیں پاتے۔ " WHERE ayatNumber = 42 AND chapterNumber = 27;
UPDATE verses SET ts = "پس جب وہ آگئی تو کہا گیا‘ کیا تیرا تخت ایسا ہی ہے؟ اس پر اس نے کہا۔ کہ یوں معلوم ہوتا ہے یہ وہی ہے اور ہم کو اس سے پہلے ہی علم حاصل ہو چکا تھا اور ہم (تیرے) فرمانبردار بن چکے تھے۔ " WHERE ayatNumber = 43 AND chapterNumber = 27;
UPDATE verses SET ts = "اور سلیمان نے ملکہ کو اللہ کے سوا پرستش کرنے سے روکا۔ وہ یقیناً کافر قوم میں سے تھی۔ " WHERE ayatNumber = 44 AND chapterNumber = 27;
UPDATE verses SET ts = "اور اسے کہا گیا کہ محل میں داخل ہو جائو۔ پس جب اس نے اس (محل) کو دیکھا تو اس کو گہرا پانی سمجھا اور گھبرا گئی۔ تب سلیمان نے کہا یہ تو محل ہے جس میں شیشہ کے ٹکڑے لگائے گئے ہیں۔ تب وہ (ملکہ) بولی۔ اے میرے رب! میں نے اپنی جان پر ظلم کیا اور میں سلیمان کے ساتھ رب العالمین خدا پر ایمان لاتی ہوں۔ " WHERE ayatNumber = 45 AND chapterNumber = 27;
UPDATE verses SET ts = "اور ہم نے ثمود کی طرف ان کے بھائی صالح کو ضرور رسول بنا کر بھیجا تھا‘ (یہ کہتے ہوئے) کہ اللہ کی عبادت کرو۔ پس وہ (سنتے ہی) دو گروہ ہو گئے جو آپس میں جھگڑنے لگے۔ " WHERE ayatNumber = 46 AND chapterNumber = 27;
UPDATE verses SET ts = "اس (صالح) نے کہا‘ اے میری قوم! تم خوشحالی کے آنے سے پہلے خراب حالی کے لئے کیوں جلدی کرتے ہو کیا تم خدا سے اپنے گناہوں پر استغفار نہیں کرتے تا کہ تم پر رحم کیا جائے۔ " WHERE ayatNumber = 47 AND chapterNumber = 27;
UPDATE verses SET ts = "انہوں نے کہا (اے صالح) ہم نے (جتنا سوچا ہے) تجھے اور تیرے ساتھیوں کو منحوس ہی پایا ہے۔ صالح نے کہا تمہاری نحوست کا سبب تو اللہ کے پاس ہے بلکہ حقیقت یہ ہے کہ تم ایک ایسی قوم ہو جس کو آزمائش میں ڈالا گیا ہے۔ " WHERE ayatNumber = 48 AND chapterNumber = 27;
UPDATE verses SET ts = "اور شہر میں نو آدمی تھے جو ملک میں فساد کرتے تھے۔ اور اصلاح نہیں کرتے تھے۔ " WHERE ayatNumber = 49 AND chapterNumber = 27;
UPDATE verses SET ts = "انہوں نے کہا تم سب اس پر اللہ کی قسم کھائو کہ ہم اس کے اور اس کے گھر والوں پر رات کے وقت حملہ کریں گے پھر جو بھی اس کے خون کا مطالبہ کرنے آئے گا ہم اس سے کہیں گے کہ ہم نے اس کے اہل کی ہلاکت (کے واقعہ) کو نہیں دیکھا اور ہم سچے ہیں۔ " WHERE ayatNumber = 50 AND chapterNumber = 27;
UPDATE verses SET ts = "اور انہوں نے ایک تدبیر کی اور ہم نے بھی ایک تدبیر کی اور وہ جانتے نہیں تھے۔" WHERE ayatNumber = 51 AND chapterNumber = 27;
UPDATE verses SET ts = "پھر دیکھ کہ ان کی تدبیر کا نتیجہ کیا نکلا۔ ہم نے ان کو اور ان کی قوم کو سب کو تباہ کرکے رکھ دیا۔ " WHERE ayatNumber = 52 AND chapterNumber = 27;
UPDATE verses SET ts = "پس (دیکھ) یہ ان کے گھر ہیں جو ان کے ظلموں کی وجہ سے گرے ہوئے ہیں۔ اس میں علم والی قوم کے لئے بڑا نشان ہے۔ " WHERE ayatNumber = 53 AND chapterNumber = 27;
UPDATE verses SET ts = "اور ہم نے ان لوگوں کو جو ایمان لائے اور تقویٰ کرتے تھے نجات دی۔ " WHERE ayatNumber = 54 AND chapterNumber = 27;
UPDATE verses SET ts = "اور (ہم نے) لوط کو (بھی رسول بنا کر بھیجا) جبکہ اس نے اپنی قوم سے کہا کیا تم بدیاں کرتے ہوں اور تم دیکھ رہے ہوتے ہو۔ " WHERE ayatNumber = 55 AND chapterNumber = 27;
UPDATE verses SET ts = "کیا تم عورتوں کو چھوڑ کر مردوں کے پاس شہوت کی نیت سے آتے ہو حقیقت یہ ہے کہ تم ایک جاہل قوم ہو۔ " WHERE ayatNumber = 56 AND chapterNumber = 27;
UPDATE verses SET ts = "پس اس قوم کا جواب صرف یہ تھا کہ (اے لوگو) لوط کے خاندان کو اپنے شہر سے نکال دو۔ وہ ایسے لوگ ہیں جو بڑا نیک بننا چاہتے ہیں۔ " WHERE ayatNumber = 57 AND chapterNumber = 27;
UPDATE verses SET ts = "نتیجہ یہ ہوا کہ ہم نے اس (لوط) کو اور اس کے خاندان کے لوگوں کو سوائے اس کی بیوی کے نجات دی۔ ہم نے اس (بیوی) کو پیچھے رہنے والوں میں گن چھوڑا تھا۔ " WHERE ayatNumber = 58 AND chapterNumber = 27;
UPDATE verses SET ts = "اور ہم نے ان پر ایک بارش برسائی اور جن کو عذاب کا پیغام پہنچ چکا ہو‘ ان کی بارش بہت بری ہوتی ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 27;
UPDATE verses SET ts = "تو کہہ دے ہر تعریف کا اللہ (ہی) مستحق ہے اور اس کے وہ بندے جن کو اس نے چن لیا ہو ان پر ہمیشہ سلامتی نازل ہوتی ہے۔ کیا اللہ بہتر ہے یا وہ چیزیں جن کو وہ (اس کا) شریک قرار دیتے ہیں؟ " WHERE ayatNumber = 60 AND chapterNumber = 27;
UPDATE verses SET ts = "(بتائو تو) آسمانوں اور زمین کو کس نے پیدا کیا ہے؟ اور (کس نے) تمہارے لئے بادل سے پانی اتارا ہے پھر اس (پانی) کے ذریعہ سے ہم نے خوبصورت باغ نکالے ہیں تم ان باغوں کے درخت نہیں اگا سکتے تھے۔ کیا اللہ کے ساتھ اور بھی معبود ہے؟ (جو سب کائنات عالم کا انتظام کر رہا ہے) لیکن یہ (کافر) ایسی قوم ہیں جو اس کے شریک بنارہے ہیں۔ " WHERE ayatNumber = 61 AND chapterNumber = 27;
UPDATE verses SET ts = "بتائو تو)  کس نے زمین کو ٹھہرنے کی جگہ بنایا ہے؟ اور اس کے بیچ میں دریا چلائے ہیں اور اس کے (فائدہ کے) لئے پہاڑ بنائے ہیں اور دو سمندروں کے درمیان (جن میں سے ایک میٹھا اور ایک کھاری ہوتا ہے) ایک روک بنائی ہے۔ کیا اللہ کے سوا کوئی اور معبود ہے؟ لیکن حقیقت یہ ہے کہ ان میں سے اکثر جانتے نہیں۔ " WHERE ayatNumber = 62 AND chapterNumber = 27;
UPDATE verses SET ts = "(نیز بتائو تو) کون کسی بے کس کی دعا سنتا ہے۔ جب وہ اس (خدا) سے دعا کرتا ہے اور (اس کی) تکلیف کو دور کر دیتا ہے اور وہ تم (دعا کرنے والے انسانوں) کو (ایک دن) ساری زمین کا وارث بنا دے گا۔ کیا (اس قادر مطلق) اللہ کے سوا کوئی معبود ہے؟ تم بالکل نصیحت حاصل نہیں کرتے۔ " WHERE ayatNumber = 63 AND chapterNumber = 27;
UPDATE verses SET ts = "(بتائو تو) خشکیوں اور سمندروں کی مصیبتوں میں کون تم کو نجات کی راہ دکھاتا ہے اور کون اپنی رحمت (یعنی بارش) سے پہلے خوش خبری کے طور پر ہوائوں کو بھیجتا ہے کیا اللہ کے سوا کوئی اور معبود ہے؟ اللہ تمہاری شرک کی باتوں سے بہت بلند ہے۔ " WHERE ayatNumber = 64 AND chapterNumber = 27;
UPDATE verses SET ts = "(بتائو تو کہ) وہ جو پہلی دفعہ پیدا کرتا ہے اور پھر (پیدائش کے) سلسلہ کو جاری کرتا ہے اور جو بادلوں اور زمین سے تمہیں رزق دیتا ہے۔ کیا (اس قادر مطلق) اللہ کے سوا کوئی اور معبود بھی ہے؟ تو کہہ دے کہ اگر تم سچے ہو تو اپنی دلیل پیش کرو (کہ اس کے ثانی اور بھی ہیں) " WHERE ayatNumber = 65 AND chapterNumber = 27;
UPDATE verses SET ts = "(پھر تو) کہہ دے کہ آسمانوں اور زمین میں جو مخلوق بھی ہے خدا کے سوا (ان میں سے کوئی) غیب کو نہیں جانتی اور ان میں سے کوئی یہ بھی نہیں سمجھتا کہ ان کو کب زندہ کرکے اٹھایا جائے گا۔ " WHERE ayatNumber = 66 AND chapterNumber = 27;
UPDATE verses SET ts = "بلکہ حقیقت یہ ہے کہ اخروی زندگی کے بارہ میں ان کا علم بالکل ختم ہو گیا ہے بلکہ وہ اس کے بارہ میں شک میں پڑے ہوئے ہیں۔ بلکہ وہ اس کے بارہ میں بالکل اندھے ہیں۔ " WHERE ayatNumber = 67 AND chapterNumber = 27;
UPDATE verses SET ts = "اور کافر کہتے ہیں کہ کیا جب ہم اور ہمارے باپ دادے مٹی ہو جائیں گے تو کیا ہم پھر (زمین سے) زندہ کرکے نکالے جائیں گے۔ " WHERE ayatNumber = 68 AND chapterNumber = 27;
UPDATE verses SET ts = "ہم اور ہمارے باپ دادوں سے اس سے پہلے بھی ایسا ہی وعدہ کیا گیا تھا۔ مگریہ صرف پہلے لوگوں کی باتیں ہیں  (جو کبھی پوری نہیں ہوتیں) " WHERE ayatNumber = 69 AND chapterNumber = 27;
UPDATE verses SET ts = "تو کہہ دے کہ زمین میں پھرو اور دیکھو کہ مجرموں کا انجام کیسا ہوا تھا۔ " WHERE ayatNumber = 70 AND chapterNumber = 27;
UPDATE verses SET ts = "اور تو اُن پر غم نہ کھا۔ اور اُن کی تدبیروں کی وجہ سے تنگی محسوس نہ کر۔ " WHERE ayatNumber = 71 AND chapterNumber = 27;
UPDATE verses SET ts = "اور وہ کہتے ہیں اگر تم سچے ہو تو یہ (عذاب کا) وعدہ کب پورا ہوگا؟ " WHERE ayatNumber = 72 AND chapterNumber = 27;
UPDATE verses SET ts = "تو کہہ دے کہ ممکن ہے کہ وہ (عذاب) جس کے لئے تم جلدی کر رہے ہو اس کا کچھ حصہ تمہارے پیچھے پیچھے چلا آرہا ہو۔ " WHERE ayatNumber = 73 AND chapterNumber = 27;
UPDATE verses SET ts = "اور تیرا رب لوگوں پر فضل کرنے والا ہے۔ لیکن ان میں سے اکثر شکر نہیں کرتے۔ " WHERE ayatNumber = 74 AND chapterNumber = 27;
UPDATE verses SET ts = "اور تیرا رب ان چیزوں کو بھی جانتا ہے جن  کو ان کے سینے چھپا رہے ہیں اور جن کو وہ ظاہر کر رہے ہیں۔ " WHERE ayatNumber = 75 AND chapterNumber = 27;
UPDATE verses SET ts = "اور آسمانوں اور زمین میں جو کوئی بھی چھپی ہوئی چیز ہے ایک بیان کرنے والی کتاب میں (محفوظ) ہے۔ " WHERE ayatNumber = 76 AND chapterNumber = 27;
UPDATE verses SET ts = "یہ قرآن بنی اسرائیل کے سامنے اکثر وہ باتیں سناتا ہے جن میں وہ اختلاف کر رہے ہیں۔ " WHERE ayatNumber = 77 AND chapterNumber = 27;
UPDATE verses SET ts = "اور وہ ضرور مومنوں کے لئے ہدایت اور رحمت ہے۔ " WHERE ayatNumber = 78 AND chapterNumber = 27;
UPDATE verses SET ts = "تیرا رب ان (بنی اسرائیل) کے درمیان اپنے حکم (یعنی قرآن) کے ساتھ (سچا) فیصلہ کرتا ہے۔ اور وہ غالب (اور) بہت بڑے علم والا ہے ۔ " WHERE ayatNumber = 79 AND chapterNumber = 27;
UPDATE verses SET ts = "پس اللہ پر توکل کر‘ تو یقیناً ایک مدلل حق پر قائم ہے۔ " WHERE ayatNumber = 80 AND chapterNumber = 27;
UPDATE verses SET ts = "تو ہرگز مردوں کو نہیں سنا سکتا اور نہ بہروں کو ہی (اپنی) آواز سنا سکتا ہے۔ (خصوصاً) جبکہ وہ پیٹھ پھیر کر چلے جاتے ہیں۔ " WHERE ayatNumber = 81 AND chapterNumber = 27;
UPDATE verses SET ts = "اور تو اندھوں کو بھی ان کی گمراہی سے بچا کر ہدایت نہیں دے سکتا۔ تو تو صرف انہی کو سناتا ہے جو ہماری آیتوں پر ایمان لاتے ہیں اور وہ (عملاً بھی) فرمانبردار ہوتے ہیں۔ " WHERE ayatNumber = 82 AND chapterNumber = 27;
UPDATE verses SET ts = "اور جب ان کی تباہی کی پیشگوئی پوری ہو جائے گی تو  ہم ان کے لئے زمین سے ایک کیڑا نکالیں گے جو ان کو کاٹے گا۔ اس وجہ سے کہ لوگ ہمارے نشانات پر یقین نہیں رکھتے تھے۔ " WHERE ayatNumber = 83 AND chapterNumber = 27;
UPDATE verses SET ts = "اور اس دن (کو یاد کرو) جب ہر (اس) قوم میں سے جو ہمارے نشانات کا انکار کرتی رہی ہوگی ہم ایک بڑا گروہ کھڑا کریں گے۔ پھر اس (گروہ) کو (جواب دہی کے لئے) مختلف گروہوں میں تقسیم کر دیا جائے گا۔ " WHERE ayatNumber = 84 AND chapterNumber = 27;
UPDATE verses SET ts = "اور جب وہ اس کے پاس پہنچیں گے۔ وہ ان سے کہے گا۔ کیا تم نے میرے نشانات کا اس کے باوجود انکارکیا تھا کہ تم نے علم کے ذریعہ سے ان کی پوری واقفیت حاصل نہیں کی تھی یایہ بتائوکہ تم (اسلام کے خلاف کیا) کیاسازشیں کیاکرتے تھے۔ " WHERE ayatNumber = 85 AND chapterNumber = 27;
UPDATE verses SET ts = "اور ان کے ظلموں کی وجہ سے ان کے خلاف کی گئی پیشگوئی پوری ہو جائے گی۔ اور وہ کچھ بات نہ کر سکیں گے۔ " WHERE ayatNumber = 86 AND chapterNumber = 27;
UPDATE verses SET ts = "کیا ان کو معلوم نہیں کہ ہم نے رات کو اس لئے بنایا ہے کہ وہ اس میں آرام حاصل کریں اور دن کو دیکھنے کی طاقت دینے والا بنایا۔ اس میں یقیناً مومن قوم کے لئے بڑے نشان ہیں۔ " WHERE ayatNumber = 87 AND chapterNumber = 27;
UPDATE verses SET ts = "اور (اس دن کو بھی یاد کرو) جس دن بگل میں ہوا پھونکی جائے گی۔ جس کے نتیجہ میں آسمانوں اور زمین میں جو کوئی بھی ہے گھبرا اٹھے گا۔ سوائے اس کے جس کے متعلق اللہ چاہے گا (کہ وہ گھبراہٹ سے محفوظ رہے) اور سب کے سب اس (خدا) کے حضور مطیع و فرمانبردار ہو کر آئیں گے۔ " WHERE ayatNumber = 88 AND chapterNumber = 27;
UPDATE verses SET ts = "اور تو پہاڑوں کو اس صورت میں دیکھتا ہے کہ وہ اپنی جگہ ٹھہرے ہوئے ہیں حالانکہ وہ بادلوں کی طرح چل رہے ہیں۔ یہ اللہ کی صنعت ہے جس نے ہر چیز کو مضبوط بنایا ہے وہ تمہارے اعمال سے خوب خبردار ہے ۔" WHERE ayatNumber = 89 AND chapterNumber = 27;
UPDATE verses SET ts = "جو کوئی نیکی کرے گا اس کو اس سے بہتر بدلہ ملے گا اور ایسے لوگ اس دن خوف سے (جس کا ذکر اوپر ہو چکا ہے) محفوظ رہیں گے۔ " WHERE ayatNumber = 90 AND chapterNumber = 27;
UPDATE verses SET ts = "اور جو لوگ برے عمل لے کر خدا کی خدمت میں حاضر ہوں گے ان کے سرداروں کو دوزخ میں اوندھا کرکے گرا دیا جائے گا اور کہا جائے گا کہ کیا تمہاری جزا تمہارے عمل کے مطابق نہیں۔ " WHERE ayatNumber = 91 AND chapterNumber = 27;
UPDATE verses SET ts = "مجھے تو صرف یہ حکم دیا گیا ہے کہ میں اس شہر (مکہ) کے رب کی جس کو اس (رب) نے معزز بنا دیا ہے عبادت کروں اور ہر چیز اسی کے قبضہ میں ہے اور مجھے حکم دیا گیا ہے کہ میں فرمانبرداروں میں سے ہو جائوں۔ " WHERE ayatNumber = 92 AND chapterNumber = 27;
UPDATE verses SET ts = "اور یہ بھی کہ میں قرآن پڑھ کر سنائوں۔ پس جو اسے سن کر ہدایت پاجائے گا تو اس کا ہدایت پانا صرف اسی کی جان کے کام آئے گا اور جو اسے سن کر گمراہ ہو جائے گا تو تو اسے کہہ دے کہ میں صرف ایک ہوشیار کرنے والا (وجود) ہوں۔ " WHERE ayatNumber = 93 AND chapterNumber = 27;
UPDATE verses SET ts = "اور یہ بھی کہہ دے کہ اللہ ہی سب تعریفوں کا مستحق ہے وہ تم کو اپنے نشان دکھائے گا یہاں تک کہ تم ان کو پہچان لو گے اور تمہارا رب تمہارے عمل سے غافل نہیں۔ " WHERE ayatNumber = 94 AND chapterNumber = 27;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 28;
UPDATE verses SET ts = "طاہر (پاک) سمیع (دعائیں سننے والا) مجید (بڑی بزرگی والا خدا اس سورۃ کو نازل کرنے والا ہے) " WHERE ayatNumber = 2 AND chapterNumber = 28;
UPDATE verses SET ts = "یہ (یعنی اس سورۃ کی آیات) ایک مدلل کتاب کی آیات ہیں۔ " WHERE ayatNumber = 3 AND chapterNumber = 28;
UPDATE verses SET ts = "مومن قوم کے فائدہ کے لئے ہم موسیٰ اور فرعون کے صحیح سوانح تیرے سامنے پڑھتے ہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 28;
UPDATE verses SET ts = "فرعون نے (اپنے) ملک میں بڑی تعلی سے کام لیا تھا اور اس کے رہنے والوں کو ٹکڑے ٹکڑے کر دیا تھا۔ وہ ان میں سے ایک گروہ کو کمزور کرنا چاہتا تھا (اس طرح کہ) ان کے بیٹوں کو قتل کرتا تھا اور ان کی لڑکیوں کو زندہ رکھتا تھا اور وہ یقیناً فسادیوں میں سے تھا۔ " WHERE ayatNumber = 5 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم نے ارادہ کر رکھا تھا کہ جن لوگوں کو ملک میں کمزور سمجھا گیا تھا ان پر احسان کریں اور ان کو سردار بنادیں اور ان کو (تمام نعمتوں کا) وارث کر دیں۔ " WHERE ayatNumber = 6 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ان کو ملک میں تمکنت بخشیں اور فرعون اور ہامان اور ان کے لشکروں کو ان سے وہ کچھ دکھائیں جس کا ان کو خوف لگا ہوا تھا۔ " WHERE ayatNumber = 7 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم نے موسیٰ کی ماں کی طرف وحی کی تھی کہ اس کو (یعنی موسیٰ کو) دودھ پلا۔ پس جب تو اس (کی جان) کے متعلق خائف ہو تو اس کو دریا میں ڈال دے اور ڈر نہیں اور نہ کسی پچھلے واقعہ کی وجہ سے غم کر ہم اس کو تیری طرف واپس لائیں گے اور اس کو رسولوں میں سے (ایک رسول) بنائیں گے۔ (چنانچہ موسیٰ کی ماں نے اس وحی کے مطابق عمل کیا اور موسیٰ کو دریا میں ڈال دیا) ۔" WHERE ayatNumber = 8 AND chapterNumber = 28;
UPDATE verses SET ts = "سو اس کے بعد اس (موسیٰ) کو فرعون کے خاندان میں سے ایک نے اٹھا لیا جس کا نتیجہ یہ ہوا کہ ایک دن وہ ان کے لئے دشمن ثابت ہوا اور غم کا موجب بنا۔ فرعون اور ہامان اور ان دونوں کے لشکر غلطی میں مبتلا تھے۔ " WHERE ayatNumber = 9 AND chapterNumber = 28;
UPDATE verses SET ts = "اور فرعون (کے خاندان) کی (ایک) عورت نے کہا‘ یہ تیرے لئے اور میرے لئے آنکھ کی ٹھنڈک کا موجب ہوگا۔ اس کو قتل نہ کرو ممکن ہے کہ ایک دن وہ ہمیں نفع پہنچائے یا ہم اس کو بیٹا بنا لیں اور ان کو اصل حقیقت معلوم نہ تھی۔ " WHERE ayatNumber = 10 AND chapterNumber = 28;
UPDATE verses SET ts = "اور موسیٰ کی ماں کا دل (غم سے) فارغ ہو گیا۔ قریب تھا کہ اگر ہم اس کے دل کو مومن بنانے کے لئے مضبوط نہ کرتے تو وہ اس واقعہ کی سب حقیقت ظاہر کر دیتی۔ " WHERE ayatNumber = 11 AND chapterNumber = 28;
UPDATE verses SET ts = "اور اس (یعنی موسیٰ کی ماں) نے اس (یعنی موسیٰ) کی بہن سے کہا کہ اس کے پیچھے پیچھے جا۔ پس وہ اس کو دُور سے دیکھتی رہی اور وہ (یعنی فرعون کے لوگ) بے خبر تھے۔ " WHERE ayatNumber = 12 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم نے اس (یعنی موسیٰ) پر اس سے پہلے دودھ پلانے والیوں کو حرام کر دیا۔ پس اس (یعنی موسیٰ کی بہن) نے کہا کہ کیا میں تمہیں ایک ایسے گھر والوں کی خبر دوں جو اس کو تمہارے لئے پال دیں اور وہ اس کے خیر خواہ ثابت ہونگے۔ " WHERE ayatNumber = 13 AND chapterNumber = 28;
UPDATE verses SET ts = "اس طرح ہم نے اس کو اس کی ماں کی طرف لوٹا دیا تاکہ اس کی آنکھیں ٹھنڈی ہوں اور وہ غم نہ کرے اور جان لے کہ اللہ کا وعدہ پورا ہوکر رہتا ہے لیکن (منکروں میں سے)  اکثر جانتے نہیں۔ " WHERE ayatNumber = 14 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جب وہ اپنی پختہ جوانی کو پہنچا اور (اپنے اعلیٰ اخلاق پر) مضبوطی سے قائم ہو گیا تو ہم نے اسے حکم اور علم بخشا اور ہم محسنوں کو ایسا ہی بدلہ دیا کرتے ہیں۔ " WHERE ayatNumber = 15 AND chapterNumber = 28;
UPDATE verses SET ts = "اور (ایک دن) وہ شہر میں ایسے وقت میں آیا کہ لوگ غفلت کی حالت میں تھے اس نے شہر میں دو آدمیوں کو دیکھا کہ آپس میں لڑ رہے تھے۔ ایک اس کی قوم سے (تعلق رکھتا) تھا اور ایک اس کے دشمنوں سے۔ پس اس نے جو اس کی جماعت میں سے تھا اس شخص کے خلاف جو اس کے دشمنوں میں سے تھا اس کی مدد طلب کی۔ اس پر موسیٰ نے اس (دشمن) کو ایک گھونسا مارا اور اس (گھونسے) نے اس کا کام تمام کر دیا۔ پھر موسیٰ نے کہا یہ سب واقعہ شیطانی کرتوت سے ہوا ہے۔ وہ (شیطان) (مومن کا) دشمن اور (اسے امن کے راستہ سے) کھلا کھلا بہکانے والا ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 28;
UPDATE verses SET ts = "پھر موسیٰ نے دعا کی کہ اے میرے رب! میں نے اپنی جان کو تکلیف میں ڈال دیا ہے پس تو میرے اس فعل پر پردہ ڈال دے۔ سو اس نے اس فعل پر پردہ ڈال دیا۔ وہ بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 28;
UPDATE verses SET ts = "(موسیٰ نے) کہا اے میرے رب! چونکہ تو نے مجھ پر انعام کیا ہے اس لئے آئندہ کبھی کسی مجرم کا مددگار نہیں بنوں گا۔ " WHERE ayatNumber = 18 AND chapterNumber = 28;
UPDATE verses SET ts = "اس کے بعد وہ شہر میں صبح کے وقت دشمنوں سے خوف کرتا ہوا اور ادھر ادھر دیکھتا ہوا نکلا تو کیا دیکھتا ہے کہ جس نے اس سے کل مدد طلب کی تھی وہ پھر اسے مدد کے لئے بلا رہا ہے اس پر موسیٰ نے اس سے کہا تو یقیناً ایک کھلا کھلا گمراہ ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 28;
UPDATE verses SET ts = "پس جب اس نے ارادہ کیا کہ اس شخص کو پکڑے جو ان دونوں کا دشمن تھا تو اس (شخص) نے کہا کہ اے موسیٰ! کیا تو چاہتا ہے کہ مجھے قتل کر دے جس طرح تو نے کل ایک شخص کو قتل کیا تھا تو صرف یہ چاہتا ہے کہ کمزوروں کو ملک میں دبا دے اور اصلاح کرنے والوں میں شامل ہونا تیری غرض نہیں۔ " WHERE ayatNumber = 20 AND chapterNumber = 28;
UPDATE verses SET ts = "اور اس وقت ایک شخص شہر کے دور کے حصہ سے دوڑتا ہوا آیا اور کہا اے موسیٰ (ملک کے) رئوسا مشورہ کر رہے ہیں کہ تجھے قتل کر دیں۔ پس (میری بات سن اور) اس شہر سے نکل جا‘ میں تیرے خیر خواہوں میں سے ہوں۔ " WHERE ayatNumber = 21 AND chapterNumber = 28;
UPDATE verses SET ts = "تب وہ اس شہر سے ڈرتے ہوئے نکل گیا اور وہ ہوشیاری سے ادھر ادھر دیکھتا جاتا تھا اس وقت اس نے دعا کی اور کہا اے میرے رب! مجھے ظالم قوم سے نجات دے۔ " WHERE ayatNumber = 22 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جب وہ مدین شہر کی طرف چلا‘ تو اس نے کہا مجھے امید ہے کہ میرا رب مجھے سیدھا راستہ دکھا دے گا۔ " WHERE ayatNumber = 23 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جب وہ مدین شہر کے چشمہ کے پاس آیا تو اس نے اس پر لوگوںں کا ایک گروہ کھڑا دیکھا جو (اپنے جانوروں کو) پانی پلا رہے تھے اور ان سے پیچھے ہٹ کر کھڑی ہوئی دو عورتیں دیکھیں جو اپنے جانوروں کو (ہجوم سے پرے) ہٹا رہی تھیں۔ موسیٰ نے ان سے کہا تم دونوں کو کیا اہم کام درپیش ہے۔ اس پر دونوں عورتوں نے کہا‘ ہم پانی نہیں پلا سکتیں جب تک کہ دوسرے چرواہے چلے نہ جائیں اور ہمارا باپ بہت بوڑھا ہے (اس لئے ہمارے ساتھ نہیں آسکا) ۔" WHERE ayatNumber = 24 AND chapterNumber = 28;
UPDATE verses SET ts = "پس اس نے ان دونوں کی خاطر (جانوروں کو) پانی پلایا۔ پھر ایک سایہ کی طرف ہٹ گیا۔ پھر کہا اے میرے رب! اپنی بھلائی میں سے جو کچھ تو مجھ پر نازل کرے میں اس کا محتاج ہوں۔ " WHERE ayatNumber = 25 AND chapterNumber = 28;
UPDATE verses SET ts = "اس کے بعد ان دونوں لڑکیوں میں سے ایک چلتی ہوئی آئی اور وہ شرما رہی تھی اور اس نے کہا میرا باپ تجھے بلاتا ہے تا کہ تجھے ہماری جگہ پر (جانوروں کو) پانی پلانے کا اجر عطا کرے۔ پس جب وہ اس (یعنی لڑکیوں کے باپ) کے پاس آیا اور اس کے آگے (اپنا) سارا واقعہ بیان کیا تو اس نے کہا ڈر نہیں‘ تو اب ظالم قوم کے پنجہ سے نجات پا گیا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 28;
UPDATE verses SET ts = "اس پر ان (دونوں لڑکیوں) میں سے ایک نے کہا‘ اے میرے باپ! اس کو تو ملازم رکھ لے کیونکہ جن کو تو ملازم رکھے ان میں سے بہتر شخص وہی ہوگا جو مضبوط بھی ہو اور امانت دار بھی۔ " WHERE ayatNumber = 27 AND chapterNumber = 28;
UPDATE verses SET ts = "تب وہ شخص بولا (اے موسیٰ) میں چاہتا ہوں کہ اس شرط پر اپنی ان دو بیٹیوں میں سے ایک کا تجھ سے نکاح کر دوں کہ تو آٹھ سال تک میری خدمت کرے پس اگر تو (آٹھ کے عدد کی جگہ پر) دس کے عدد سے اپنے وعدہ کو مکمل کر دے تو یہ تیرا احسان ہوگا اور میں تجھ پر کوئی بوجھ ڈالنا نہیں چاہتا۔ اگر اللہ نے چاہا تو تو مجھے نیک معاملہ کرنے والوں میں سے پائے گا۔ " WHERE ayatNumber = 28 AND chapterNumber = 28;
UPDATE verses SET ts = "(اس پر موسیٰ نے) کہا‘ یہ بات میرے اور تیرے درمیان پختہ ہوگئی۔ ان دونوں مدتوں میں سے جو بھی میں پوری کروں مجھ پر کوئی الزام نہیں ہوگا اور جو کچھ ہم کہتے ہیں‘ اللہ اس پر گواہ ہے۔ " WHERE ayatNumber = 29 AND chapterNumber = 28;
UPDATE verses SET ts = "جب موسیٰ نے وقت مقررہ کو پورا کر لیا اور اپنے گھر والوں کو لے کے چلا تو اس نے طور کی طرف سے ایک آگ دیکھی (اور) اپنے گھر والوں سے کہا تم یہاں ٹھہرو‘ میں نے ایک آگ دیکھی ہے‘ شاید میں وہاں سے تمہارے لئے کوئی (ضروری) خبر لائوں یا کوئی آگ کا انگارہ لائوں تاکہ تم سینکو۔ " WHERE ayatNumber = 30 AND chapterNumber = 28;
UPDATE verses SET ts = "پھر جب وہ اس (آگ) کے پاس پہنچا‘ تو مبارک مقام کے ایک مبارک حصہ کی طرف سے ایک درخت کے پاس سے اسے پکارا گیا کہ اے موسیٰ میں اللہ ہوں سب جہانوں کا رب۔ " WHERE ayatNumber = 31 AND chapterNumber = 28;
UPDATE verses SET ts = "اور یہ کہ تو اپنا عصا پھینک دے۔ پس جب اس نے اُس (عصا) کو حرکت کرتے ہوئے دیکھا گویا کہ وہ ایک چھوٹا سانپ ہے‘ وہ پیٹھ پھیر کر بھاگا اور پیچھے مڑ کر نہ دیکھا (تب اسے کہا گیا) اے موسیٰ! آگے بڑھ اور ڈر نہیں تو سلامتی پانے والے لوگوں میں سے ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 28;
UPDATE verses SET ts = "(اور) اپنے ہاتھ کو اپنے گریبان میں ڈال‘ وہ بغیر کسی بیماری کے سفید نکلے گا اور اپنے بازو کو خوف کی وجہ سے (زور سے) کھینچ کر (اپنے جسم سے) ملالے یہ دو دلیلیں (علاوہ دوسری دلیلوں کے) ہیں جو فرعون اور اس کے درباریوں کی طرف تیرے رب کی طرف سے بھیجی گئی ہیں کیونکہ وہ اطاعت سے نکلنے والے لوگ ہیں۔ " WHERE ayatNumber = 33 AND chapterNumber = 28;
UPDATE verses SET ts = "(موسیٰ نے) کہا‘ اے میرے رب! میں نے فرعون کی قوم میں سے ایک شخص کو قتل کیا تھا۔ پس میں ڈرتا ہوں کہ وہ مجھے قتل نہ کر دیں (اور تیرا پیغام نہ پہنچ سکے) ۔" WHERE ayatNumber = 34 AND chapterNumber = 28;
UPDATE verses SET ts = "اور میرا بھائی ہارون بات کرنے میں مجھ سے زیادہ فصیح ہے۔ پس اس کو میرے ساتھ مددگار کے طور پر بھیج۔ تا کہ وہ میری تصدیق کرے۔ میں ڈرتا ہوں کہ وہ میری تکذیب نہ کریں۔ " WHERE ayatNumber = 35 AND chapterNumber = 28;
UPDATE verses SET ts = "فرمایا‘ ہم تیرے بھائی کے ساتھ تیرے بازو کو مضبوط کریں گے اور تم دونوں کے لئے غلبہ کے سامان پیدا کریں گے۔ پس وہ تم تک نہیں پہنچ سکیں گے تم دونوں اور جو تم دونوں کے متبع ہوں گے ہماری آیات کے ذریعہ سے غالب ہونگے۔ " WHERE ayatNumber = 36 AND chapterNumber = 28;
UPDATE verses SET ts = "پس جب موسیٰ ہماری کھلی کھلی آیتیں لے کر آیا تو فرعون کے لوگوں نے کہا‘ یہ تو ایک فریب ہے جو بنا لیا گیا ہے۔ ہم نے اپنے باپ دادوں سے ایسی  بات کبھی نہیں سنی۔ " WHERE ayatNumber = 37 AND chapterNumber = 28;
UPDATE verses SET ts = "اور موسیٰ نے کہا‘ میرا رب اس کو جو اس کی طرف سے ہدایت لائے خوب جانتا ہے اور اس کو بھی جس کا انجام اچھا ہو۔ حق یہ ہے کہ ظالم کبھی کامیاب نہیں ہوتے۔ " WHERE ayatNumber = 38 AND chapterNumber = 28;
UPDATE verses SET ts = "اور فرعون نے کہا۔ اے درباریو! مجھے اپنے سوا تمہارا کوئی معبود معلوم نہیں۔ پس اے ہامان! میرے لئے گیلی مٹی پر آگ جلا (یعنی اینٹیں بنوا) پھر میرے لئے ایک قلعہ تیار کر۔ شاید اس پر چڑھ کر میں موسیٰ کے خدا کو معلوم کرلوں۔ اور میں تو اس کو جھوٹوں میں سے سمجھتا ہوں۔ " WHERE ayatNumber = 39 AND chapterNumber = 28;
UPDATE verses SET ts = "اور اس نے بھی اور اس کے لشکروں نے بھی ملک میں بغیر کسی حق کے تکبر سے کام لیا اور خیال کیا کہ وہ ہماری طرف لوٹا کر نہیں لائے جائیں گے۔ " WHERE ayatNumber = 40 AND chapterNumber = 28;
UPDATE verses SET ts = "پس ہم نے اس کو بھی اور اس کے لشکروں کو بھی پکڑ لیا اور ان کو سمندر میں پھینک دیا۔ پس دیکھ کہ ظالموں کا انجام کیسا ہوا؟ " WHERE ayatNumber = 41 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم نے ان (فرعونیوں) کو سردار بنایا تھا جو (اپنی سرداری کے غرور میں) لوگوں کو دوزخ کی طرف بلاتے تھے اور قیامت کے دن ان کی کوئی مدد نہیں کی جائے گی۔ " WHERE ayatNumber = 42 AND chapterNumber = 28;
UPDATE verses SET ts = "اور اس دنیا میں بھی ہم نے ان پر لعنت بھیجی اور قیامت کے دن بھی وہ بدحال لوگوں میں سے ہوں گے۔ " WHERE ayatNumber = 43 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم نے موسیٰ کے بعد اس کے کہ ہم نے پہلی قوموں کو ہلاک کر دیا تھا‘ کتاب بخشتی۔ اس کی تعلیم لوگوں کو روحانی بینائی بخشتی تھی۔ اور وہ ہدایت اور رحمت کا موجب تھی (اور) اس غرض سے (دی گئی تھی) کہ وہ نصیحت حاصل کریں۔ " WHERE ayatNumber = 44 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تو (طور کے) مغربی جانب نہیں تھا جب ہم نے موسیٰ کے سپرد (رسالت کا) کام کیا تھا اور نہ تو  (اس وقت) گواہوں میں سے ایک گواہ تھا۔ " WHERE ayatNumber = 45 AND chapterNumber = 28;
UPDATE verses SET ts = "لیکن ہم نے بہت سی قوموں کو پیدا کیا۔ پس ان پر عمر لمبی ہو گئی (اور وہ اپنی پیشگوئیوں کو بھول گئے) اور تو اہل مدین کے ساتھ بھی نہیں رہتا تھا کہ ان کے سامنے ہمارے نشانات پڑھ کر سناتا۔ لیکن ہم ہی رسول بھیجنے والے ہیں۔ " WHERE ayatNumber = 46 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تو طور کی جانب نہیں تھا۔ جب کہ ہم نے (موسیٰ کو) پکارا لیکن یہ سب کچھ تیرے رب کی طرف سے رحمت ہے تا کہ تو اس قوم کو ہوشیار کرے جن کے پاس تجھ سے پہلے کوئی ہوشیار کرنے والا نہیں آیا تھا تا کہ وہ نصیحت حاصل کریں۔ " WHERE ayatNumber = 47 AND chapterNumber = 28;
UPDATE verses SET ts = "اور اگر یہ خیال نہ ہوتا کہ وہ اپنے اعمال کی وجہ سے کسی مصیبت کے آنے پر کہیں گے اے ہمارے رب! تو نے ہماری طرف کوئی رسول نہ بھیجا‘ تا کہ ہم تیری آیتوں کے پیچھے چلتے اور مومنوں میں سے بن جاتے (تو شاید ہم تجھے رسول بنا کر نہ بھیجتے مگر کفار پر حجت قائم کرنا ضروری تھا) " WHERE ayatNumber = 48 AND chapterNumber = 28;
UPDATE verses SET ts = "پس جب ان کے پاس ہماری طرف سے حق آگیا تو انہوں نے کہا کیوں اس (یعنی محمد رسول اللہ) کو ویسی تعلیم نہیں ملی جیسی کہ موسیٰ کو ملی تھی۔ کیا انہوں نے موسیٰ کی تعلیم کا اس سے پہلے انکار نہیں کیا تھا؟ انہوں نے تو کہہ دیا تھا کہ یہ دو بڑے جادو گر ہیں جو ایک دوسرے کی مدد کرتے ہیں اور کہہ دیا تھا کہ ہم ان میں سے ہر ایک کے دعویٰ کے منکر ہیں۔ " WHERE ayatNumber = 49 AND chapterNumber = 28;
UPDATE verses SET ts = "تو کہہ دے کہ (اگر موسیٰ اور ہارون اور محمد رسول اللہ صلی اللہ علیہ وسلم  سب کی باتیں جھوٹی ہیں تو) اگر تم سچے ہو تو اللہ کے پاس سے ایک ایسی کتاب لائو جو ان دونوں کتابوں سے زیادہ ہدایت دیتی ہو تا کہ میں اس کی اتباع کروں۔ " WHERE ayatNumber = 50 AND chapterNumber = 28;
UPDATE verses SET ts = "پھر اگر وہ کوئی جواب نہ دیں‘ تو جان لے کہ وہ صرف اپنی خواہشوں کی پیروی کر رہے ہیں اور اس سے زیادہ کون گمراہ ہے جو اللہ کی ہدایت کو نظر انداز کرکے اپنی خواہش کے پیچھے چلتا ہے اللہ یقیناً ظالم قوم کو کامیابی کا راستہ نہیں دکھاتا۔ " WHERE ayatNumber = 51 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم ان کے لئے پے در پے وحی اتارتے رہے  تاکہ وہ نصیحت حاصل کریں۔ " WHERE ayatNumber = 52 AND chapterNumber = 28;
UPDATE verses SET ts = "وہ لوگ جن کو ہم نے اس (قرآن) سے پہلے کتاب دی تھی وہ اس (قرآن) پر (دل میں) ایمان رکھتے ہیں۔ " WHERE ayatNumber = 53 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جب وہ (قرآن) ان کے سامنے پڑھا جاتا ہے تو وہ کہتے ہیں ہم اس پر ایمان لاتے ہیں۔ یہ ہمارے رب کی طرف سے برحق کلام ہے ہم تو اس دن سے پہلے ہی مسلمان تھے۔ " WHERE ayatNumber = 54 AND chapterNumber = 28;
UPDATE verses SET ts = "ان لوگوں کو ان کا بدلہ ان کے صبر کی وجہ سے دو دفعہ ملے گا اور وہ نیکی سے بدی کا مقابلہ کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس کو خرچ کرتے ہیں۔ " WHERE ayatNumber = 55 AND chapterNumber = 28;
UPDATE verses SET ts = "اور (یہودیوں میں سے مسلمان ہونے والے جب) کوئی لغو بات سنتے ہیں تو اُس سے اعراض کرتے ہیں اور کہتے ہیں اے کافرو ہمارے عمل ہمارے لئے ہیں اور تمہارے عمل تمہارے لئے ہیں۔ تم پر سلامتی نازل ہو (یعنی خدا تمہیں ایمان نصیب کرے) ہم جاہلوں سے تعلق رکھنا پسند نہیں کرتے۔ " WHERE ayatNumber = 56 AND chapterNumber = 28;
UPDATE verses SET ts = "تو جس کو پسند کرے ہدایت نہیں دے سکتا۔ لیکن اللہ جسے چاہے ہدایت دیتا ہے اور وہ ہدایت پانے والوں کو خوب جانتا ہے۔ " WHERE ayatNumber = 57 AND chapterNumber = 28;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ اگر ہم اس ہدایت کی جو تجھ پر نازل ہوتی ہے اتباع کریں تو اپنے ملک سے اُچک لیے جائیں گے (تو کہہ دے) کیا ہم نے ان کو محفوظ اور امن والے مقام میں جگہ نہیں دی۔ جس کی طرف ہر قسم کے پھل لائے جاتے ہیں۔ یہ ہماری طرف سے عطیہ ہے۔ مگر ان میں سے اکثر جانتے نہیں۔ " WHERE ayatNumber = 58 AND chapterNumber = 28;
UPDATE verses SET ts = "اور بہت سے بستیاں ہیں جن کو ہم نے ہلاک کیا جو اپنی معیشت (کے افراط) کی وجہ سے متکبر ہو گئی تھیں۔ پس (دیکھ) یہ ان کی بستیاں ہیں جن میں ان کے بعد کوئی نہیں رہا اور ہم ہی ان کے وارث بنے۔ " WHERE ayatNumber = 59 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تیرا رب جب تک کسی مرکزی بستی میں ایسا رسول نہ بھیج دے جو ان کے سامنے ہماری آیتیں پڑھ کر سنائے ان بستیوں کے مجموعہ (یعنی ملک) کو ہلاک کرنے کا ارتکاب نہیں کر سکتا تھا (کیونکہ یہ انصاف کے خلاف ہے) اور ہم بستیوں کو کبھی ہلاک نہیں کرتے سوائے اس کے کہ ان کے رہنے والے ظالم ہو جائیں۔ " WHERE ayatNumber = 60 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جو کچھ تمہیں دیا جاتا ہے وہ تو صرف ورلی زندگی کا سامان ہے اور اس کی زینت ہے اور جو اللہ کے پاس ہے وہ بہتر اور باقی رہنے والا ہے۔ کیا تم عقل نہیں کرتے۔ " WHERE ayatNumber = 61 AND chapterNumber = 28;
UPDATE verses SET ts = "کیا وہ شخص جس سے ہم نے اچھا (یعنی اُخروی زندگی کی کامیابی کا) وعدہ کیا ہو اور وہ اسے (یقیناً) پا لینے والا ہو اس شخص کی طرح ہو سکتا ہے جس کو ہم نے صرف دنیوی زندگی کا کچھ سامان دیا ہو۔ پھر وہ قیامت کے دن (خدا کے رُوبرو جواب دہی کے لئے) پیش کیا جانے والا ہو۔ " WHERE ayatNumber = 62 AND chapterNumber = 28;
UPDATE verses SET ts = "اور (یاد کرو) جس دن وہ (اللہ) ان کو بلائے گا پھر پوچھے گا میرے وہ شرکاء کہاں ہیں جو تمہارے تصور نے بنا رکھے تھے جن کو تم میرے مقابل پر معبود قرار دیتے تھے۔ " WHERE ayatNumber = 63 AND chapterNumber = 28;
UPDATE verses SET ts = "(تب) جن پر ہمارے عذاب کی خبر پوری ہو چکی ہوگی کہیں گے اے ہمارے رب! یہ وہ لوگ ہیں جن کو ہم نے بہکایا تھا۔ ہم نے ان کو اسی طرح بہکایا تھا جس طرح ہم خود بہک گئے تھے۔ آج ہم تیرے پاس اپنی گمراہی سے برأت ظاہرکرتے ہیں۔ وہ لوگ ہمارے عبادت گزار نہیں تھے (بلکہ اپنے خیالوں کے پیچھے چلتے تھے) ۔" WHERE ayatNumber = 64 AND chapterNumber = 28;
UPDATE verses SET ts = "اور کہا جائے گا‘ تم اپنے شریکوں کو بلائو‘ پس وہ ان کو بلائیں گے مگر وہ ان کا جواب نہ دیں گے اور (مشرک) مقررہ عذاب دیکھ لیں گے۔ کاش کہ وہ ہدایت پاجاتے۔ " WHERE ayatNumber = 65 AND chapterNumber = 28;
UPDATE verses SET ts = "اور اس دن کو بھی یاد کرو جب خدا ان کو پکارے گا اور کہے گا تم نے رسولوں (کے وعظ) کا کیا جواب دیا تھا؟ " WHERE ayatNumber = 66 AND chapterNumber = 28;
UPDATE verses SET ts = "پس اس دن ساری دلیلیں انہیں بھول جائیں گی اور وہ ایک دوسرے سے کوئی سوال نہ کر سکیں گے۔ " WHERE ayatNumber = 67 AND chapterNumber = 28;
UPDATE verses SET ts = "پس جو کوئی توبہ کرے گا اور ایمان لائے گا اور مناسب حال عمل کرے گا قریب ہوگا کہ وہ بامراد لوگوں میں شامل ہوجائے۔ " WHERE ayatNumber = 68 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تیرا رب جو چاہتا ہے پیدا کرتا ہے اور جس کو چاہتا ہے چن لیتا ہے۔ ان کو اس بارہ میں کوئی اختیار حاصل نہیں۔ اللہ پاک ہے اور ان کی مشرکانہ باتوں سے بلند ہے۔ " WHERE ayatNumber = 69 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تیرا رب اس کو بھی جانتا ہے جس کو وہ سینہ میں چھپاتے ہیں اور اسے بھی جسے وہ ظاہر کرتے ہیں۔ " WHERE ayatNumber = 70 AND chapterNumber = 28;
UPDATE verses SET ts = "اور حقیقت یہ ہے کہ اللہ کی ذات ایسی ہے کہ اس کے سوا کوئی معبود نہیں ابتدائے آفرینش میں بھی وہ تعریف کا مستحق تھا۔ اور آخرت میں بھی وہی تعریف کا مستحق ہوگا۔ سب بادشاہت اسی کے قبضہ میں ہے اور تم سب کو اسی کی طرف لوٹ کر جانا ہوگا۔ " WHERE ayatNumber = 71 AND chapterNumber = 28;
UPDATE verses SET ts = "تو (ان سے) کہہ‘ مجھے بتائو تو سہی اگر اللہ تمہارے لئے قیامت کے دن تک رات کو لمبا کر دے تو اللہ کے سوا اور کون ہے جو تمہارے پاس روشنی لائے گا؟ کیا تم سنتے نہیں۔ " WHERE ayatNumber = 72 AND chapterNumber = 28;
UPDATE verses SET ts = "تو کہہ دے مجھے بتائو تو سہی کہ اگر اللہ دن کو قیامت کے دن تک تمہارے لئے لمبا کر دے تو اللہ کے سوا کونسا معبود ہے جو تمہارے پاس رات کو لے آئے جس میں تم سکون پائو۔ کیا تم دیکھتے نہیں۔ " WHERE ayatNumber = 73 AND chapterNumber = 28;
UPDATE verses SET ts = "اور یہ اس کی رحمت ہے کہ اس نے تمہارے لئے رات اور دن بنائے ہیں کہ اس (رات) میں تم سکون حاصل کرو۔ اور (اس دن میں) تم اس کا فضل تلاش کرو۔ تا کہ تم شکر گذار بنو۔ " WHERE ayatNumber = 74 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جس دن وہ ان کو پکارے گا اور کہے گا کہ کہاں ہیں وہ میرے مزعومہ شرکا ء جن کو تم (معبود) خیال کرتے تھے۔ " WHERE ayatNumber = 75 AND chapterNumber = 28;
UPDATE verses SET ts = "اور ہم ہر ایک امت میں سے ایک گواہ نکال کھڑا کریں گے پھر کہیں گے اپنی دلیل لائو۔ تب وہ جان لیں گے کہ کامل حق اللہ ہی کے پاس ہے اور ان کا سب افترا ان سے کھویا جائے گا۔ " WHERE ayatNumber = 76 AND chapterNumber = 28;
UPDATE verses SET ts = "قارون (دراصل) موسیٰ کی قوم میں سے تھا۔ مگر وہ انہی کے خلاف ظلم پر آمادہ ہو گیا اور ہم نے اس کو اتنے خزانے دئیے تھے کہ جن کی کنجیاں ایک مضبوط جماعت کے لئے بھی اٹھانا مشکل تھیں۔ (یاد کر) جب اس کی قوم نے اسے کہا کہ (اتنا) فخر مت کر۔ اللہ فخر کرنے والوں کو یقیناً پسند نہیں کرتا۔ " WHERE ayatNumber = 77 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جو کچھ تجھے اللہ نے دیا ہے اس سے اُخروی زندگی کے گھر کی تلاش کر۔ اور دنیوی زندگی سے تجھے جو حصہ ملا ہے اسے بھی بھول نہیں (ہم تجھے ایک حد تک دنیا کی آسائشوں کے استعمال سے روکتے نہیں) اور جس طرح اللہ نے تجھ پر احسان کیا ہے تو بھی لوگوں پر احسان کر اور ملک میں فساد پھیلانے کی کوشش نہ کر۔ اللہ یقیناً مفسدوں کو پسند نہیں کرتا۔ " WHERE ayatNumber = 78 AND chapterNumber = 28;
UPDATE verses SET ts = "(قارون نے) کہا۔ یہ سب رتبہ مجھے ایک ایسے علم کی وجہ سے ملا ہے‘ جو صرف مجھے حاصل ہے۔ کیا وہ جانتا نہیں تھا کہ اس سے پہلے اللہ نے بہت سی نسلوں کو جو اس سے زیادہ طاقت ور اور اس سے زیادہ مالدار تھیں ہلاک کر دیا تھا اور مجرموں کو (جب عذاب دیا جاتا ہے تو) ان کے گناہوں کے متعلق ان سے پوچھ گچھ نہیں کی جاتی۔ " WHERE ayatNumber = 79 AND chapterNumber = 28;
UPDATE verses SET ts = "(ایک دن ایسا ہوا کہ) وہ اپنی قوم کے سامنے اپنی زینت (یعنی اپنے باڈی گارڈ) کے ساتھ نکلا۔ اس پر وہ لوگ جو کہ دنیا کی زندگی کا سامان چاہتے تھے بول اٹھے‘ اے کاش! ہمیں بھی وہی کچھ ملتا جو قارون کو دیا گیا‘ اس کو تو (دنیا کا ایک) بہت بڑا حصہ ملا ہے۔ " WHERE ayatNumber = 80 AND chapterNumber = 28;
UPDATE verses SET ts = "اور جن لوگوں کو علم دیا گیا تھا وہ بولے‘ تمہارا ستیاناس۔ اللہ کی طرف سے ملنے والی جزا مومن اور ایمان کے مناسب حال عمل کرنے والے کے لئے بہت اچھی ہوتی ہے اور یہ (جزا) صرف صبر کرنے والوں کا گروہ ہی پاتا ہے۔ " WHERE ayatNumber = 81 AND chapterNumber = 28;
UPDATE verses SET ts = "پھر ہم نے اس کو اور اس کے قبیلہ کو مکروہات میں مبتلا کر دیا اور کوئی جماعت ایسی نہ نکلی جو اللہ کے سوا اس کی مدد کرتی۔ اور کسی تدبیر سے بھی وہ (اپنے دشمن سے) بچ نہ سکا۔ " WHERE ayatNumber = 82 AND chapterNumber = 28;
UPDATE verses SET ts = "اور وہ لوگ جو کل تک اس کے مقام پر ہونے کی تمنا کرتے تھے کہنے لگ گئے تجھ پر ہلاکت ہو۔ اللہ ہی یقیناً اپنے بندوں میں سے جس کے لئے چاہتا ہے رزق میں فراخی دیتا ہے اور جس کے لئے چاہتا ہے تنگی کرتا ہے اگر ہم پر اللہ نے احسان نہ کیا ہوتا تو ہمیں بھی مصیبتوں کا شکار کر دیتا۔ تجھ پر ہلاکت ہو (بات یہی ہے کہ) کافر کبھی کامیاب نہیں ہوتے۔ " WHERE ayatNumber = 83 AND chapterNumber = 28;
UPDATE verses SET ts = "یہ (جو) اخروی زندگی (ہے) ہم اسے انہی  کے لئے مخصوص کر دیتے ہیں جو ملک میں ناجائز غلبہ اور فساد نہیں چاہتے۔ اور انجام متقیوں کا ہی (اچھا) ہوتا ہے۔ " WHERE ayatNumber = 84 AND chapterNumber = 28;
UPDATE verses SET ts = "جو شخص پسندیدہ عمل کرے‘ اسے اس سے بہتر بدلہ ملے گا۔ اور جو کوئی برا عمل کرے گا تو برے اعمال کرنے والوں کو ان کے اپنے عمل کے برابر جزا دی جائے گی۔ " WHERE ayatNumber = 85 AND chapterNumber = 28;
UPDATE verses SET ts = "وہ خدا جس نے تجھ پر یہ قرآن فرض کیا ہے اپنی ذات کی قسم کھا کر کہتا ہے کہ وہ تجھے اس مقام کی طرف لوٹا کر لائے گا جس کی طرف لوگ لوٹ لوٹ کر آتے ہیں۔ تو کہہ دے‘ میرا رب (اس کو بھی) خوب جانتا ہے جو ہدایت پر قائم ہوتا ہے اور (اس کو بھی) جو کھلی گمراہی میں مبتلا ہوتا ہے۔ " WHERE ayatNumber = 86 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تو (کوئی) امید نہیں رکھتا تھا کہ تجھ پر ایک مکمل کتاب نازل کی جائے گی مگر تیرے رب کی طرف سے رحمت کے طور پر ایسا ہوا۔ پس تو کافروں کا مددگار کبھی نہ بنیئو۔ " WHERE ayatNumber = 87 AND chapterNumber = 28;
UPDATE verses SET ts = "اور تجھے کوئی شخص اس کے بعد کہ اللہ کی آیتیں تجھ پر اتاری گئیں ان سے روکنے والا نہ بنے اور تو اپنے رب کی طرف (لوگوں کو) بلا اور مشرکوں میں شامل نہ ہو۔ " WHERE ayatNumber = 88 AND chapterNumber = 28;
UPDATE verses SET ts = "اور (اے مخاطب) اللہ کے سوا کسی معبود کو مت پکار۔ اس کے سوا کوئی معبود نہیں۔ ہر ایک چیز ہلاک ہونے والی ہے سوائے اس کے جس کی طرف اس (اللہ) کی توجہ ہو۔ حکم اسی کے اختیار میں ہے اور تم سب اسی کی طرف لوٹا کر لے جائے جائو گے۔ " WHERE ayatNumber = 89 AND chapterNumber = 28;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) ۔" WHERE ayatNumber = 1 AND chapterNumber = 29;
UPDATE verses SET ts = "میں اللہ سب سے زیادہ جاننے والا ہوں۔ " WHERE ayatNumber = 2 AND chapterNumber = 29;
UPDATE verses SET ts = "کیا (اس زمانہ کے) لوگوں نے یہ سمجھ رکھا ہے کہ ان کا یہ کہہ دینا کہ ہم ایمان لے آئے ہیں (کافی ہوگا) اور وہ چھوڑ دیئے جائیں گے اور ان کو آزمایا نہ جائے گا۔ " WHERE ayatNumber = 3 AND chapterNumber = 29;
UPDATE verses SET ts = "حالانکہ جو (لوگ) ان سے پہلے گذر چکے ہیں ان کو ہم نے آزمایا تھا (اور اب بھی وہ ایسا ہی کرے گا) سو اللہ ظاہر کر دے گا ان کو بھی جنہوں نے سچ بولا اور ان کو بھی جنہوں نے جھوٹ بولا۔ " WHERE ayatNumber = 4 AND chapterNumber = 29;
UPDATE verses SET ts = "کیا جو لوگ بدیاں کرتے ہیں۔ وہ خیال کرتے ہیں کہ وہ ہمارے سزا سے بچ جائیں گے۔ اُن کا فیصلہ بہت برا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 29;
UPDATE verses SET ts = "جو شخص اللہ کی ملاقات کی امید رکھتا ہے (اسے معلوم ہونا چاہیئے کہ) اللہ کا مقرر کردہ وقت ضرور آنے والا ہے اور وہ بہت سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 29;
UPDATE verses SET ts = "اور جو شخص خدا کے لئے کوشش کرتا ہے درحقیقت وہ اپنی جان ہی کے لئے کرتا ہے اللہ تمام جہانوں سے بے نیاز ہے (اور ان کی عبادت کا محتاج نہیں)۔ " WHERE ayatNumber = 7 AND chapterNumber = 29;
UPDATE verses SET ts = "اور جو لوگ ایمان لائے ہیں اور ایمان کے مطابق انہوں نے عمل کئے ہم ان کی بدیوں کو ان سے دور کر دیں گے اور جو کام وہ کرتے تھے اس کے مطابق جو بہترین جزا ان کو مل سکتی ہوگی وہ ہم اُن کو دیں گے۔ " WHERE ayatNumber = 8 AND chapterNumber = 29;
UPDATE verses SET ts = "اور ہم نے انسان کو اپنے والدین سے اچھا سلوک کرنے کا حکم دیا ہے اور (کہا ہے کہ) اگر وہ دونوں تجھ سے اس بات میں بحث کریں کہ تو کسی کو میرا شریک قرار دے حالانکہ اس کا تجھے کوئی علم نہیں‘ تو ان دونوں کی فرمانبرداری نہ کر کیونکہ تم سب نے میری طرف ہی لوٹ کر آنا ہے اور میں تمہارے عمل (کی نیکی بدی) سے تم کو واقف کروں گا۔ " WHERE ayatNumber = 9 AND chapterNumber = 29;
UPDATE verses SET ts = "اور جو لوگ ایمان لائے ہیں اور اس کے مطابق انہوں نے عمل بھی کیا ہے ہم ان کو اچھے بندوں میں داخل کریں گے۔ " WHERE ayatNumber = 10 AND chapterNumber = 29;
UPDATE verses SET ts = "اور لوگوں میں سے (بعض ایسے بھی ہوتے ہیں) جو کہتے ہیں کہ ہم اللہ پر ایمان لے آئے ہیں پھر جب اللہ کی وجہ سے ان کو تکلیف دی جاتی ہے وہ لوگوں کے عذاب کو اللہ کے عذاب کی طرح سمجھ لیتے ہیں اور اگر تیرے رب کی طرف سے مدد آتی ہے تو وہ کہتے ہیں (درحقیقت) ہم بھی تمہارے ساتھ تھے کیا دنیا جہان کے لوگوں کے دلوں میں جو کچھ ہے اس کو اللہ اچھی طرح نہیں جانتا۔ " WHERE ayatNumber = 11 AND chapterNumber = 29;
UPDATE verses SET ts = "اور اللہ ضرور ظاہر کر دے گا ان کو بھی جو ایمان لائے اور ان کو بھی جو منافق ہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 29;
UPDATE verses SET ts = "اور کافر مومنوں سے کہتے ہیں‘ تم ہمارے پیچھے چلو۔ ہم تمہارے گناہ اٹھا لیں گے۔ حالانکہ وہ ان کے گناہ (بالکل) نہیں اٹھا سکتے۔ وہ یقیناً جھوٹے ہیں۔ " WHERE ayatNumber = 13 AND chapterNumber = 29;
UPDATE verses SET ts = "حقیقت یہ ہے کہ وہ اپنے بوجھ بھی اٹھائیں گے اور اپنے بوجھوں کے سوا اور لوگوں کے بوجھ بھی اٹھائیں گے (جن کو وہ دھوکا دیتے ہیں) اور قیامت کے دن ان سے اس افترا کے بارے میں سوال کیا جائے گا۔ " WHERE ayatNumber = 14 AND chapterNumber = 29;
UPDATE verses SET ts = "اور ہم نے نوح کو اُس کی قوم کی طرف بھیجا تھا پس وہ ان میں نو سو پچاس سال تک رہا۔ سو اس کی قوم کے لوگوں کو طوفان نے آلیا اور وہ ظالم تھے۔ " WHERE ayatNumber = 15 AND chapterNumber = 29;
UPDATE verses SET ts = "پس ہم نے اس کو اور اس کی کشتی میں بیٹھنے والے ساتھیوں کو نجات دی اور ہم نے اس واقعہ کو تمام جہان کے لوگوں کے لئے ایک نشان بنا دیا۔ " WHERE ayatNumber = 16 AND chapterNumber = 29;
UPDATE verses SET ts = "اور (ہم نے) ابراہیم کو (بھی رسول بناکر بھیجا تھا) جب اس نے اپنی قوم سے کہا تھا کہ اللہ کی عبادت کرو اور اس کا تقویٰ اختیار کرو۔ اگر تم جانتے ہو تو یہ تمہارے لئے بہت اچھا ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 29;
UPDATE verses SET ts = "تم اللہ کے سوا دوسری ہستیوں کی عبادت کرتے ہو اور (مذہب کے بارے میں) جھوٹی باتیں بناتے ہو۔ وہ (ہستیاں) جن کی تم اللہ کے سوا پرستش کرتے ہو تمہیں رزق نہیں دے سکتیں۔ پس اللہ سے اپنا رزق مانگو اور اس کی عبادت کرو اور اس کا شکر ادا کرو۔ تم کو اسی کی طرف لوٹا کر لے جایا جائے گا۔ " WHERE ayatNumber = 18 AND chapterNumber = 29;
UPDATE verses SET ts = "اور اگر تم میری بات کو جھوٹا قرار دو تو (یہ کوئی نئی بات نہیں) تم سے پہلی قوموں نے بھی (اپنے رسولوں کو) جھٹلایا تھا اور رسول کا کام تو صرف کھول کھول کر پہنچانا ہوتا ہے (زبردستی منوانا نہیں ہوتا) " WHERE ayatNumber = 19 AND chapterNumber = 29;
UPDATE verses SET ts = "کیا اُن کو معلوم نہیں کہ اللہ پیدائش عالم کو کس طرح پہلی دفعہ شروع کرتا ہے۔ پھر اس کو باربار لوٹاتا جاتا ہے یہ کام اللہ کے لئے بالکل آسان ہے۔ " WHERE ayatNumber = 20 AND chapterNumber = 29;
UPDATE verses SET ts = "تو کہہ‘ ملک میں چاروں طرف پھرو پھر دیکھو کہ اللہ نے مخلوق کی پیدائش کس طرح شروع کی تھی پھر مرنے کے بعد ان کو دوبارہ زندہ کرتا چلا گیا اللہ ہر ایک چیز پر قادر ہے۔ " WHERE ayatNumber = 21 AND chapterNumber = 29;
UPDATE verses SET ts = "وہ جس کو چاہتا ہے عذاب دیتا ہے اور جس پر چاہتا ہے رحم کرتا ہے اور اسی کی طرف تم کو لوٹا کر لایا جائے گا۔ " WHERE ayatNumber = 22 AND chapterNumber = 29;
UPDATE verses SET ts = "اور تم نہ زمین میں نہ آسمان میں خدا کو اس کی مرضی کے خلاف مجبور کر سکو گے اور خدا کے سوا نہ کوئی تمہارا دوست ہے نہ مددگار۔ " WHERE ayatNumber = 23 AND chapterNumber = 29;
UPDATE verses SET ts = "اور وہ لوگ جو اللہ کے نشانوں کا اور اس سے ملاقات ہونے کا انکار کرتے ہیں وہ لوگ ایسے ہیں جو میری رحمت سے مایوس ہو گئے ہیں اور وہی ہیں جن کو دردناک عذاب ملے گا۔ " WHERE ayatNumber = 24 AND chapterNumber = 29;
UPDATE verses SET ts = "پس اس کی (یعنی ابراہیم کی) قوم کا جواب اس کے سوا کچھ نہ تھا کہ انہوں نے کہا اس کو قتل کر دو یا اس کو جلا دو (چنانچہ انہوں نے اس کو آگ میں ڈال دیا) مگر اللہ نے اس کو آگ سے بچا لیا‘ اس میں یقیناً مومن قوم کے لئے بڑے نشان ہیں۔ " WHERE ayatNumber = 25 AND chapterNumber = 29;
UPDATE verses SET ts = "(ابراہیم نے) کہا‘ تم نے اللہ کے سوا بتوں سے تعلق قائم کر چھوڑا ہے (اور تمہارا یہ فعل) ورلی زندگی میں دوسرے مشرکوں سے محبت بڑھانے کے لئے (ہے) پھر قیامت کے دن تم میں سے بعض بعض کا انکار کریں گے اور تم میں سے بعض بعض پر لعنت ڈالیں گے اور تمہارا ٹھکانا دوزخ ہوگا اور جن کو تم مددگار سمجھتے ہو ان میں سے کوئی تمہاری مدد کو نہ آئے گا۔ " WHERE ayatNumber = 26 AND chapterNumber = 29;
UPDATE verses SET ts = "اس (نصیحت) کے بعد لوط اُس پر ایمان لے آئے اور (ابراہیم نے) کہا‘ میں تو اپنے رب کی طرف ہجرت کرکے جانے والا ہوں وہ یقیناً غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 29;
UPDATE verses SET ts = "اور ہم نے اُسے اسحاق اور یعقوب بخشے‘ اور اُس کی ذریت کے ساتھ نبوت اور کتاب مخصوص کر دی اور ہم نے اس کو دنیا میں بھی اس کا اجر بخشا۔ اور آخرت میں بھی وہ نیک بندوں میں شامل کیا جائے گا۔ " WHERE ayatNumber = 28 AND chapterNumber = 29;
UPDATE verses SET ts = "اور لوط کو بھی (ہم نے رسول بنا کر بھیجا) جبکہ اس نے اپنی قوم سے کہا کہ تم ایک ایسی بدی کرتے ہو جس کا ارتکاب دنیا میں تم سے پہلے کسی نے نہیں کیا تھا۔ " WHERE ayatNumber = 29 AND chapterNumber = 29;
UPDATE verses SET ts = "کیا تم (عورتوں کو چھوڑ کر) مردوں کے پاس آتے ہو اور ڈاکے مارتے ہو۔ اور اپنی مجالس میں ناپسندیدہ حرکتیں کرتے ہو۔ اس پر اس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ انہوں نے کہہ دیا کہ اگر تو سچے لوگوں میں سے ہے تو اللہ کا عذاب ہم پر نازل کر دے۔ " WHERE ayatNumber = 30 AND chapterNumber = 29;
UPDATE verses SET ts = "اِس پر لوط نے کہا‘ اے میرے رب! مفسد قوم کے خلاف میری مدد کر۔ " WHERE ayatNumber = 31 AND chapterNumber = 29;
UPDATE verses SET ts = "اور جب ہمارے رسول ابراہیم کے پاس بشارت لائے۔ تو انہوں نے کہا۔ ہم اس بستی والوں کو ہلاک کرنے والے ہیں۔ کیونکہ اس کے باشندے ظالم ہیں۔ " WHERE ayatNumber = 32 AND chapterNumber = 29;
UPDATE verses SET ts = "(ابراہیم نے جواب میں) کہا کہ اس (بستی) میں تو لوط بھی رہتا ہے انہوں نے کہا ہم اس (بستی) کے رہنے والوں کو خوب جانتے ہیں۔ ہم اس (یعنی لوط) کو اور اس کے گھر والوں کو سوائے اس کی بیوی کے جو پیچھے رہنے والوں میں شامل ہو جائے گی نجات دیں گے۔ " WHERE ayatNumber = 33 AND chapterNumber = 29;
UPDATE verses SET ts = "اور جب ہمارے  رسول لوط کے پاس آئے تو ان کی وجہ سے اُسے دُکھ پہنچا نیز اُن کی وجہ سے اس کا دل تنگ ہو گیا۔ اور (اس کی اس حالت کو دیکھ کر ان پیغام لانے والوں نے ) کہا۔ کسی (آئندہ) بات کا خوف نہ کر اور نہ کسی گزشتہ واقعہ پر افسوس کر۔ ہم تجھ کو اور تیرے گھر والوں کو سوائے تیری بیوی کے جو پیچھے رہنے والوں میں شامل ہوجائے گی‘ نجات دینے والے ہیں۔ " WHERE ayatNumber = 34 AND chapterNumber = 29;
UPDATE verses SET ts = "ہم اس بستی پر ان کی نافرمانی کی وجہ سے عذاب نازل کرنے والے ہیں۔ " WHERE ayatNumber = 35 AND chapterNumber = 29;
UPDATE verses SET ts = "اور ہم نے اس بستی کے (واقعہ) کے ذریعہ سے ایک کھلی عبرت کا سامان عقل والے لوگوں کے لئے پیچھے چھوڑا ہے ۔" WHERE ayatNumber = 36 AND chapterNumber = 29;
UPDATE verses SET ts = "اور مدین کی طرف ہم نے ان کے بھائی شعیب کو رسول بنا کر بھیجا تھا (جب وہ آیا تو) اس نے کہا اے میری قوم! اللہ کی عبادت کرو اور اخروی زندگی کے وقت کو یاد رکھو اور ایسے مفسدانہ کام نہ کرو کہ ملک میں تمہارے کاموں کی وجہ سے فساد پھیل جائے۔ " WHERE ayatNumber = 37 AND chapterNumber = 29;
UPDATE verses SET ts = "اس پر انہوں نے اس کو جھٹلا دیا۔ اور ایک ہلا دینے والے عذاب نے ان کو پکڑ لیا۔ جس کے نتیجہ میں وہ اپنے گھروں میں (زمین سے) چمٹے کے چمٹے رہ گئے۔ " WHERE ayatNumber = 38 AND chapterNumber = 29;
UPDATE verses SET ts = "اور عاد کو بھی اور ثمود کو بھی (ہماری طرف سے ایک ہلا دینے والے عذاب نے پکڑ لیا) اور (اے اہل مکہ)! تم پر ان کی بستیوں کا حال ظاہر ہے اور شیطان نے ان کو ان کے عمل اچھے کرکے دکھائے اور اس (شیطان) نے ان کو (اللہ کے) راستہ سے روکا۔ حالانکہ وہ خوب سمجھتے تھے۔ " WHERE ayatNumber = 39 AND chapterNumber = 29;
UPDATE verses SET ts = "اور قارون اور فرعون اور ہامان کو بھی (ہم نے عذاب میں گرفتار کیا) اور موسیٰ ان کے پاس کھلے کھلے نشان لے کر آئے تھے۔ پھر بھی (وہ نہ مانے بلکہ) انہوں نے ملک میں متکبرانہ رویہ اختیار کیا اور (ہمارے عذاب سے) بھاگ کر بچ نہ سکے۔ " WHERE ayatNumber = 40 AND chapterNumber = 29;
UPDATE verses SET ts = "پس ہم نے ان میں سے ہر ایک کو اس کے گناہ کی وجہ سے پکڑ لیا۔ سو ان میں سے کوئی تو ایسا تھا کہ ہم نے اس پر پتھروں کا مینہ برسایا اور کوئی ایسا تھا کہ اس کو کسی اور سخت عذاب نے پکڑ لیا۔ اور کوئی ایسا تھا کہ ہم نے اس کو ملک میں ذلیل کر دیا۔ اور کوئی ایسا تھا کہ ہم نے اسے غرق کر دیا۔ اور اللہ ان پر ظلم کرنے والا نہیں تھا‘ بلکہ وہ خود ہی اپنی جانوں پر ظلم کرتے تھے۔ " WHERE ayatNumber = 41 AND chapterNumber = 29;
UPDATE verses SET ts = "ان لوگوں کا حال جنہوں نے اللہ کو چھوڑ کر اور دوست بنا لیے‘ مکڑی کا سا حال ہے‘ جس نے (اپنے لئے) ایک گھر تو بنایا لیکن گھروں میں سب سے کمزور گھر مکڑی کا ہی ہوتا ہے کاش! کہ یہ لوگ جانتے۔ " WHERE ayatNumber = 42 AND chapterNumber = 29;
UPDATE verses SET ts = "اللہ ہر اس چیز کو جس کو یہ لوگ اس کے سوا پکارتے ہیں جانتا ہے اور وہ غالب (اور) حکمت والا ہے۔ " WHERE ayatNumber = 43 AND chapterNumber = 29;
UPDATE verses SET ts = "اور یہ مثالیں ہیں جو ہم لوگوں کے لئے بیان کرتے ہیں۔ لیکن عالموں کے سوا کوئی ان کو اپنے پلے نہیں باندھتا۔ " WHERE ayatNumber = 44 AND chapterNumber = 29;
UPDATE verses SET ts = "اللہ نے آسمانوں اور زمین کو خاص مقصد کے لئے پیدا کیا ہے۔ اس میں مومنوں کے لئے ایک بڑا نشان ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 29;
UPDATE verses SET ts = "اس کتاب (یعنی قرآن) میں سے جو کچھ تیری طرف وحی کیا جاتا ہے اُسے پڑھ (اور لوگوں کو پڑھ کر سنا) اور نماز کو (اس کی سب شرائط کے ساتھ) ادا کر۔ یقیناً نماز سب بری اور ناپسندیدہ باتوں سے روکتی ہے اور اللہ کی یاد یقیناً (اور سب کاموں سے) بڑی ہے اور اللہ تمہارے اعمال کو جانتا ہے۔ " WHERE ayatNumber = 46 AND chapterNumber = 29;
UPDATE verses SET ts = "اور اہل کتاب سے کبھی بحث نہ کرو مگر اعلیٰ اور مضبوط دلیل کے ساتھ۔ سوائے ان لوگوں کے جو ان میں سے ظلم کرنے والے ہوں (ان کو الزامی جواب دے سکتے ہو) اور ان سے کہو کہ جو ہم پر نازل ہوا ہے ہم اس پر بھی ایمان لاتے ہیں اور جو تم پر نازل ہوا ہے اس پر بھی۔ اور ہمارا خدا اور تمہارا خدا ایک ہے اور ہم اس کے فرمانبردار ہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 29;
UPDATE verses SET ts = "اور اسی طرح ہم نے تجھ پر یہ مکمل کتاب اتاری ہے پس وہ لوگ جن کو ہم نے یہ کتاب دی ہے وہ اس پر ایمان لاتے ہیں اور ان لوگوں سے (یعنی اہل کتاب میں سے بھی) بعض اس پر ایمان لاتے ہیں اور ہماری آیتوں کا ضد کے ساتھ انکار صرف کافر ہی کرتے ہیں۔ " WHERE ayatNumber = 48 AND chapterNumber = 29;
UPDATE verses SET ts = "اور اس (قرآن) کے نازل ہونے سے پہلے تو کوئی کتاب نہ پڑھتا تھا‘ نہ لوگوں کو سناتا تھا اور نہ اسے اپنے دائیں ہاتھ سے لکھتا تھا۔ اگر ایسا ہوتا تو جھٹلانے والے شبہ میں پڑ جاتے۔" WHERE ayatNumber = 49 AND chapterNumber = 29;
UPDATE verses SET ts = "مگر یہ (قرآن) تو کھلی نشانیاں ہیں ان لوگوں کے دلوں میں جن کو علم دیا گیا ہے اور ہمارے نشانات کا ظالموں کے سوا کوئی انکار نہیں کرتا۔ " WHERE ayatNumber = 50 AND chapterNumber = 29;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ کیوں اس کے رب کی طرف سے اس پر کوئی نشانات نہیں اترے۔ کہو کہ نشانات تو خدا کے پاس ہیں (جب وہ فائدہ دیکھتا ہے اتارتا ہے) اور میں تو ایک کھلا کھلا ہوشیار کرنے والا ہوں۔ " WHERE ayatNumber = 51 AND chapterNumber = 29;
UPDATE verses SET ts = "کیا ان کے لئے (یہ نشان) کافی نہ تھا کہ ہم نے تجھ پر ایک مکمل کتاب (قرآن) کو نازل کیا جو انہیں پڑھ کر سنائی جاتی ہے۔ اس امر میں مومنوں کے لئے تو بڑی رحمت اور نصیحت کے سامان ہیں۔ " WHERE ayatNumber = 52 AND chapterNumber = 29;
UPDATE verses SET ts = "تو کہہ دے میرے اور تمہارے درمیان بطور گواہ فیصلہ کرنے والا اللہ ہی کافی ہے اور جو کچھ آسمانوں میں ہے اور زمین میں ہے اسے وہ جانتا ہے اور جو لوگ باطل پر عمل کرتے ہیں اور اللہ کے احکام کا انکار کرتے ہیں وہی گھاٹے میں پڑنے والے ہیں۔ " WHERE ayatNumber = 53 AND chapterNumber = 29;
UPDATE verses SET ts = "اور وہ تجھ سے عذاب کے جلدی لانے کی خواہش کرتے ہیں اور اگر ایک مقرر وقت نہ ہوتا تو عذاب ان کے پاس آجاتا اور اب بھی وہ ان کے پاس ضرور (آئے گا اور) اچانک آئے گا اس حالت میں کہ وہ جانتے بھی نہ ہوں گے۔ " WHERE ayatNumber = 54 AND chapterNumber = 29;
