UPDATE verses SET ts = "اور آسمانوں اور زمین میں اسی کی تعریف ہے اور بعد دوپہر بھی اس کی تسبیح کرو اور اسی طرح (عین) دوپہر کے وقت بھی۔ " WHERE ayatNumber = 19 AND chapterNumber = 30;
UPDATE verses SET ts = "وہ زندہ کو مردہ سے نکالتا ہے اور مردہ کو زندہ سے نکالتا ہے اور زمین کو اس کے مر جانے کے بعد زندہ کرتا ہے۔ اور اسی طرح تم بھی نکالے جائو گے۔ " WHERE ayatNumber = 20 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانات میں سے (ایک) یہ (نشان) بھی ہے کہ اس نے تم کو مٹی سے پیدا کیا۔ پھر اس پیدائش کے نتیجہ میں تم بشر بن جاتے اور (تمام زمین میں) پھیل جاتے ہو۔ " WHERE ayatNumber = 21 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے (ایک) یہ (نشان) بھی ہے کہ اس نے تمہاری ہی جنس میں سے تمہارے لئے جوڑے بنائے ہیں تاکہ تم ان کی طرف (مائل ہو کر) تسکین حاصل کرو۔ اور تمہارے درمیان پیار اور رحم کا رشتہ پیدا کیا ہے اس میں فکر کرنے والی قوم کے لئے بڑے نشان ہیں۔ " WHERE ayatNumber = 22 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانات میں سے آسمانوں اور زمین کا پیدا کرنا اور تمہاری زبانوں اور تمہارے رنگوں کا اختلاف بھی ہے اس میں تمام جاننے والوں کے لئے بڑے نشان ہیں۔ " WHERE ayatNumber = 23 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے رات کے وقت اور دن کے وقت تمہارا سونا اور اس کے فضل کے حاصل کرنے کے لئے محنت کرنا بھی ہے۔ اس میں سننے والی قوم کے لئے بڑے نشان ہیں۔ " WHERE ayatNumber = 24 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے یہ بھی ہے کہ وہ تم کو بجلی‘ خوف اور طمع پیدا کرنے کے لئے دکھاتا ہے اور بادل سے پانی اتارتا ہے۔ پھر اس کے ذریعہ سے زمین کو اس کے مرجانے کے بعد زندہ کرتا ہے۔ اس میں عقل والے لوگوں کے لئے بڑے نشان ہیں۔ " WHERE ayatNumber = 25 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے آسمانوں اور زمین کا اس کے حکم سے کھڑا رہنا بھی ہے۔ پھر (اس کا یہ نشان بھی ظاہر ہوگا کہ) جب وہ تم کو زمین سے نکلنے کے لئے ایک آواز دے گا تو اچانک تم زمین سے نکلنے لگو گے۔ " WHERE ayatNumber = 26 AND chapterNumber = 30;
UPDATE verses SET ts = "اور آسمانوں اور زمین میں (بسنے والے) تمام وجود اس کے فرمانبردار ہیں۔ " WHERE ayatNumber = 27 AND chapterNumber = 30;
UPDATE verses SET ts = "اور وہ پیدائش (عالم) کو شروع بھی کرتا ہے اور پھر اس کو باربار دہراتا رہتا ہے اور یہ امر اس کے لئے بہت آسان ہے اور آسمانوں اور زمین میں اسی کی شان سب سے بالا ہے اور وہ غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 30;
UPDATE verses SET ts = "اس نے تمہارے سمجھانے کے لئے تمہاری ہی جنس کی ایک حالت بیان کی ہے (جو یہ ہے کہ) جن لوگوں کے تمہارے دائیں ہاتھ مالک ہوتے ہیں۔ کیا ان میں سے کوئی اس (مال) میں جو ہم نے تم کو دیا ہے تمہارا برابر کا شریک بھی ہوتا ہے اس طرح کہ تم سب (مالک و غلام) اس (مال و دولت) میں برابر ہو جاتے ہو اور ان (غلاموں) سے اس طرح ڈرتے ہو جس طرح تم اپنے آپ سے ڈرتے ہو اسی طرح ہم عقل والی قوم کے لئے نشان کھول کھول کر بیان کرتے ہیں۔ " WHERE ayatNumber = 29 AND chapterNumber = 30;
UPDATE verses SET ts = "بلکہ (حقیقت یہ ہے کہ) ظالم لوگ بغیر علم کے اپنی خواہشات کے پیچھے چل پڑے ہیں اور جس کو اللہ گمراہ کرے اسے کون ہدایت دے سکتا ہے (اور) ان (لوگوں) کا کوئی مددگار نہیں ہوگا۔ " WHERE ayatNumber = 30 AND chapterNumber = 30;
UPDATE verses SET ts = "پس تو اپنی ساری توجہ دین کے لئے مخصوص کر دے ایسی صورت میں کہ تجھ میں کوئی کجی نہ ہو (تو) اللہ کی (پیدا کی ہوئی) فطرت کو اختیار کر (وہ فطرت) جس پر اللہ نے لوگوں کو پیدا کیا ہے۔ اللہ کی پیدائش میں کوئی تبدیلی نہیں ہو سکتی۔ یہی قائم رہنے والا دین ہے لیکن اکثر لوگ جانتے نہیں۔ " WHERE ayatNumber = 31 AND chapterNumber = 30;
UPDATE verses SET ts = "(پس) تم سب اس (خدا) کی طرف جھکتے ہوئے دین فطرت اختیار کرو۔ اور اس کا تقویٰ اختیار کرو اور نماز کو اس کی شرائط کے ساتھ ادا کرو اور مشرکوں میں سے مت بنو۔ " WHERE ayatNumber = 32 AND chapterNumber = 30;
UPDATE verses SET ts = "یعنی ان (مشرک) لوگوں میں سے جنہوں نے اپنے دین کو پراگندہ کر دیا۔ اور (دین کو ٹکڑے ٹکڑے کرکے) مختلف فرقوں میں بٹ گئے اور ہر گروہ اس خیال سے خوش ہے کہ جو ٹکڑا ہم نے لیا ہے وہی بہتر ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 30;
UPDATE verses SET ts = "اور جب انسانوں کو کوئی تکلیف پہنچے تو وہ اپنے رب کو اسی کی طرف جھکتے ہوئے پکارتے ہیں پھر جب اس کی طرف سے انہیں کوئی رحمت پہنچتی ہے تو ان میں سے ایک گروہ اپنے رب کے مدمقابل شریک بنانے لگ جاتا ہے۔ " WHERE ayatNumber = 34 AND chapterNumber = 30;
UPDATE verses SET ts = "جس کا نتیجہ یہ ہوتا ہے کہ جو کچھ ہم نے ان کو دیا ہے وہ عملاً اس کا انکار کرنے لگ جاتے ہیں (تب ان سے کہا جاتا ہے۔ دنیا سے) جو کچھ فائدہ اٹھانا ہے اٹھا لو۔ کیونکہ تم جلد ہی اپنا انجام دیکھ لو گے۔ " WHERE ayatNumber = 35 AND chapterNumber = 30;
UPDATE verses SET ts = "کیا ہم نے ان کے لئے کوئی (شرک کی) دلیل اتاری ہے جو ان سے وہ باتیں کہتی ہے جو شرک کے متعلق وہ بیان کرتے ہیں۔ " WHERE ayatNumber = 36 AND chapterNumber = 30;
UPDATE verses SET ts = "جب ہم لوگوں سے رحم کا سلوک کرتے ہیں تو وہ اس سے خوش ہو جاتے ہیں اور اگر انہیں اپنے سابقہ اعمال کی وجہ سے کوئی تکلیف پہنچے تو وہ اچانک مایوس ہو جاتے ہیں۔ " WHERE ayatNumber = 37 AND chapterNumber = 30;
UPDATE verses SET ts = "کیا انہوں نے نہیں دیکھا کہ اللہ جس کے لئے پسند کرتا ہے رزق فراخ کر دیتا ہے اور جس کے لئے پسند کرتا ہے رزق تنگ کر دیتا ہے اس میں ایمان لانے والے لوگوں کے لئے بہت سے نشانات ہیں۔ " WHERE ayatNumber = 38 AND chapterNumber = 30;
UPDATE verses SET ts = "پس چاہیے (کہ جب خدا رزق میں فراخی دے تو اے قرآن کے پڑھنے والے) قریبی‘ مسکین اور مسافر کو اس کا حق بخش۔ یہ بات بہت  بہتر ہے ان لوگوں کے لئے جو اللہ کی رضا حاصل کرنا چاہتے ہیں اور وہی لوگ بامراد ہونے والے ہیں۔ " WHERE ayatNumber = 39 AND chapterNumber = 30;
UPDATE verses SET ts = "اور جو روپیہ تم سود حاصل کرنے کے لئے دیتے ہو‘ تاکہ وہ لوگوں کے مالوں میں بڑھے تو وہ روپیہ اللہ کے حضور میں نہیں بڑھتا اور جو تم اللہ کی رضا حاصل کرنے کے لئے زکٰوۃ کے طور پر دیتے ہو تو یاد رکھو کہ اسی قسم کے لوگ خدا کے ہاں (روپیہ) بڑھا رہے ہیں۔ " WHERE ayatNumber = 40 AND chapterNumber = 30;
UPDATE verses SET ts = "اللہ وہ ہے جس نے تم کو پیدا کیا ہے پھر اس نے تم کو رزق دیا ہے۔ پھر وہ تمہیں مارے گا‘ پھر وہ تمہیں زندہ کرے گا۔ کیا تمہارے (تجویز کردہ) شرکا میں سے بھی کوئی ایسا ہے جو اس کام کا کوئی حصہ بھی کرتا ہو؟ وہ ان کے شرک سے پاک‘ اور بلند شان رکھنے والا ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 30;
UPDATE verses SET ts = "(اس زمانہ میں) خشکی اور تری میں لوگوں کے کاموں کی وجہ سے فساد نمایاں ہو گیا ہے جس کا نتیجہ یہ ہوگا کہ خدا لوگوں کو ان کے عملوں کے کچھ حصے کی سزا (اس دنیا میں) دے گا۔ تاکہ وہ (اپنی نافرمانی سے) لوٹ آئیں۔ " WHERE ayatNumber = 42 AND chapterNumber = 30;
UPDATE verses SET ts = "تو کہہ دے‘ ملک میں پھرو۔ اور دیکھو کہ جو لوگ تم سے پہلے تھے ان کا کیا انجام ہوا تھا۔ ان میں سے اکثر مشرک تھے۔ " WHERE ayatNumber = 43 AND chapterNumber = 30;
UPDATE verses SET ts = "پس تو اپنی توجہ کو قائم رہنے والے دین کی طرف پھیر دے پیشتر اس کے کہ وہ دن آئے جس کو ٹلانے کے لئے اللہ کی طرف سے کوئی علاج نہیں اترا جس دن وہ (مومن اور کافر) ایک دوسرے سے الگ ہو جائیں گے۔ " WHERE ayatNumber = 44 AND chapterNumber = 30;
UPDATE verses SET ts = "جس نے کفر کیا اس پر اس کے کفر کا وبال پڑے گا۔ اور جس نے اپنے ایمان کے مطابق عمل کیا وہ اپنے ہی نفس کے فائدہ کے لئے تیاری کر رہا ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 30;
UPDATE verses SET ts = "تاکہ خدا مومنوں اور اپنے ایمان کے مطابق عمل کرنے والوں کو اپنے فضل سے جزا دے وہ یقیناً منکروں سے محبت نہیں کرتا۔ " WHERE ayatNumber = 46 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے ایک نشان ہوائوں کا بشارت دیتے ہوئے بھیجنا بھی ہے اور وہ اس لئے ایسا کرتا ہے کہ تا وہ تم  کو اپنی رحمت (کا پھل) چکھائے۔ اور کشتیاں اس کے حکم سے چلیں اور تم اس کے فضل کو حاصل کرو اور تم شکر گذار بنو۔ " WHERE ayatNumber = 47 AND chapterNumber = 30;
UPDATE verses SET ts = "اور ہم نے تجھ سے پہلے کئی رسول ان کی قوموں کی طرف بھیجے تھے پس وہ ان کے پاس کھلے کھلے نشان لے کر آئے اور ہم نے مجرموں سے مناسب بدلہ لیا‘ اور مومنوں کی مدد کرنا ہمارا فرض ہے۔ " WHERE ayatNumber = 48 AND chapterNumber = 30;
UPDATE verses SET ts = "اللہ وہ ہے جو ہوائوں کو بھیجتا ہے‘ پھر وہ ہوائیں بادل کی شکل میں بخارات اٹھاتی ہیں۔ پھر وہ اس کو جس طرح چاہتا ہے آسمان میں پھیلا دیتا ہے اور وہ اسے ٹکڑے ٹکڑے کر دیتا ہے اور تو بارش کو دیکھتا ہے کہ اس (بادل) میں سے ٹپکتی ہے پھر جب خدا اس بادل کو اپنے بندوں میں سے جس تک پسند کرے پہنچا دیتا ہے تو اچانک وہ خوش ہو جاتے ہیں۔ " WHERE ayatNumber = 49 AND chapterNumber = 30;
UPDATE verses SET ts = "گو وہ اس (بارش) کے نازل ہونے سے پہلے بالکل ناامید ہوگئے تھے۔ " WHERE ayatNumber = 50 AND chapterNumber = 30;
UPDATE verses SET ts = "پس (اے مخاطب) اللہ کی رحمت کے نشانوں کو دیکھ کہ وہ کس طرح زمین کے مر جانے کے بعد اس کو زندہ کرتا ہے۔ یہی خدا ہے جو قیامت کے دن) مردوں کو زندہ کرے گا اور وہ ہر چیز پر قادر ہے۔ " WHERE ayatNumber = 51 AND chapterNumber = 30;
UPDATE verses SET ts = "اور اگر ہم ہوا چلائیں اور یہ لوگ اس (یعنی کھیتی) کو زرد زرد دیکھیں‘ تو اس نظارہ کے بعد (بجائے نصیحت حاصل کرنے کے) ناشکر گذار بن جائیں گے۔ " WHERE ayatNumber = 52 AND chapterNumber = 30;
UPDATE verses SET ts = "(پس تو ان کو ایسا کرنے دے) کیونکہ نہ تو تو مردوں کو سنا سکتا ہے اور نہ بہروں کو اس وقت (اپنی) آواز سنا سکتا ہے جب وہ پیٹھ پھیر کر لوٹ جائیں۔ " WHERE ayatNumber = 53 AND chapterNumber = 30;
UPDATE verses SET ts = "اور نہ تو اندھوں کو ان کی گمراہی سے ہٹا کر سیدھے راستہ پر لا سکتا ہے تو صرف انہی کو سنا سکتا ہے جو ہماری آیتوں پر ایمان لاتے ہیں اور وہ فرمانبردار بن جاتے ہیں۔ " WHERE ayatNumber = 54 AND chapterNumber = 30;
UPDATE verses SET ts = "اللہ وہی ہے جس نے تم کو اس حالت میں پیدا کیا کہ تمہارے اندر کمزوری پائی جاتی تھی۔ پھر کمزوری کے بعد تم کو قوت بخشی پھر قوت کے بعد ضعف اور بڑھاپا دیا۔ وہ جس چیز کو چاہتا ہے پیدا کرتا ہے اور وہ بڑے علم والا (اور) قدرت والا ہے۔ " WHERE ayatNumber = 55 AND chapterNumber = 30;
UPDATE verses SET ts = "اور جس دن مقررہ گھڑی آجائے گی مجرم قسمیں کھائیں گے کہ وہ ایک چھوٹے وقفہ کے سوا (دنیا میں) نہیں رہے۔ وہ اسی طرح بہکی بہکی باتیں بناتے ہیں۔ " WHERE ayatNumber = 56 AND chapterNumber = 30;
UPDATE verses SET ts = "اور وہ لوگ جن کو علم اور ایمان دیا گیا ہے کہیں گے تم اللہ کے حساب میں اس دن تک ٹھہرے ہو جو تمہارے پھر ابھرنے کے لئے مقرر تھا اور (یاد رکھو کہ) یہی پھر ابھرنے کا دن ہے۔ لیکن تم جانتے نہیں۔ " WHERE ayatNumber = 57 AND chapterNumber = 30;
UPDATE verses SET ts = "پس آج ظالموں کو ان کا کوئی عذر نفع نہیں دے گا اور نہ انہیں معاف کرکے ڈیوڑھی تک آنے کا موقع دیا جائے گا۔ " WHERE ayatNumber = 58 AND chapterNumber = 30;
UPDATE verses SET ts = "اور ہم نے لوگوں کی خاطر اس قرآن میں ہر قسم کی حقیقتیں کھول کھول کر بیان کر دی ہیں اور اگر تو ان کے پاس کوئی نشان لائے تو کافر ضرور کہیں گے کہ تم لوگ تو صرف جھوٹی باتیں پیش کرنے والے ہو۔ " WHERE ayatNumber = 59 AND chapterNumber = 30;
UPDATE verses SET ts = "اسی طرح اللہ بے علم لوگوں کے دلوں پر مہر لگا دیتا ہے۔ " WHERE ayatNumber = 60 AND chapterNumber = 30;
UPDATE verses SET ts = "پس (اے قرآن کے مخاطب) استقلال سے (اپنے ایمان پر) قائم رہ۔ اللہ کا وعدہ ضرور پورا ہو کر رہے گا اور چاہیے کہ جو لوگ یقین نہیں رکھتے وہ تجھے دھوکہ دیکر (اپنی جگہ سے) ہٹا نہ دیں۔ " WHERE ayatNumber = 61 AND chapterNumber = 30;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 31;
UPDATE verses SET ts = "میں اللہ سب سے زیادہ جاننے والا ہوں۔ " WHERE ayatNumber = 2 AND chapterNumber = 31;
UPDATE verses SET ts = "یہ (یعنی اس سورۃ کی آیات) اس کامل کتاب کی آیات ہیں جو بڑی حکمتوں والی ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 31;
UPDATE verses SET ts = "(اور) اپنے فرائض کو صحیح طور پر ادا کرنے والوں کے لئے ہدایت اور رحمت کا موجب ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 31;
UPDATE verses SET ts = "وہ لوگ جو نماز کو (اس کی شرائط کے ساتھ) ادا کرتے ہیں اور (غریبوں اور مسکینوں کو) زکٰوۃ دیتے رہتے ہیں اور اُخروی زندگی پر یقین رکھتے ہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 31;
UPDATE verses SET ts = "یہ لوگ اپنے رب کی طرف سے آنے والی ہدایت پر بھی مضبوطی سے قائم ہیں اور ایسے ہی لوگ (ہر میدان میں) کامیاب ہوں گے۔ " WHERE ayatNumber = 6 AND chapterNumber = 31;
UPDATE verses SET ts = "اور لوگوں میں سے بعض ایسے ہیں جو (اپنا روپیہ ضائع کرکے) کھیل تماشہ کی باتیں لیتے رہتے ہیں تاکہ بغیر علم کے لوگوں کو اللہ کے راستہ سے روکیں اور اس (یعنی اللہ کے راستہ) کو ہنسی کے قابل چیز بنا لیتے ہیں ان لوگوں کے لئے ذلت والا عذاب ہوگا۔ " WHERE ayatNumber = 7 AND chapterNumber = 31;
UPDATE verses SET ts = "اور جب ایسے شخص کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو وہ تکبر کرتے ہوئے پیٹھ پھیر لیتا ہے گویا کہ اس نے ان کو سنا ہی نہیں (وہ اس حد تک بے پروائی کرتا ہے) گویا اس کے کانوں میں بہرہ پن ہے پس تو اس کو ایک دردناک عذاب کی اہم خبر سنا۔ " WHERE ayatNumber = 8 AND chapterNumber = 31;
UPDATE verses SET ts = "یقیناً وہ لوگ جو ایمان لائے اور جنہوں نے اس کے مناسب حال عمل کیے ان کو نعمت والے باغات ملیں گے۔ " WHERE ayatNumber = 9 AND chapterNumber = 31;
UPDATE verses SET ts = "جن میں وہ رہتے چلے جائیں گے۔ یہ اللہ کا کیا ہوا پختہ وعدہ ہے اور وہ غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 31;
UPDATE verses SET ts = "اس نے آسمانوں کو بغیر ستونوں کے پیدا کیا ہے۔ جیسا کہ تم دیکھتے ہو۔ اور زمین میں پہاڑ اس ڈر سے ڈال چھوڑے ہیں کہ وہ تمہارے سمیت شدید زلزلہ میں مبتلا نہ ہو جائے اور اس میں ہر قسم کے جانور پھیلائے اور بادلوں سے پانی اتارا ہے۔ پھر اس میں ہر قسم کے عمدہ جوڑے پیدا کیے ہیں۔ " WHERE ayatNumber = 11 AND chapterNumber = 31;
UPDATE verses SET ts = "یہ اللہ کی مخلوق ہے۔ پس تم مجھے دکھائو کہ اس کے سوا جن کو تم شریک قرار دیتے ہو۔ انہوں نے کیا پیدا کیا ہے (کچھ بھی نہیں) بلکہ حق یہ ہے کہ ظالم کھلی کھلی گمراہی میں ہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 31;
UPDATE verses SET ts = "اور ہم نے لقمان کو حکمت دی تھی اور کہا تھا کہ اللہ کا شکر ادا کر۔ اور جو شخص بھی شکر کرتا ہے اس کے شکر کرنے کا فائدہ اسی کی جان کو پہنچتا ہے اور جو ناشکری کرتا ہے (اسے یاد رکھنا چاہیئے کہ) اللہ سب قسم کے شکروں سے بے نیاز ہے (اور) بڑی حمد والا ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 31;
UPDATE verses SET ts = "اور (یاد کرو) جب لقمان نے اپنے بیٹے کو نصیحت کرتے ہوئے کہا اے میرے بیٹے! اللہ کا شریک کسی کو مت قرار دے شرک یقیناً بہت بڑا ظلم ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 31;
UPDATE verses SET ts = "اور ہم نے یہ کہتے ہوئے کہ میرا اور اپنے والدین کا شکریہ ادا کر‘ انسان کو اپنے والدین کے متعلق (احسان کرنے کا) تاکیدی حکم دیا تھا (اور) اس کی ماں نے اسے کمزوری کے (ایک دور کے) بعد کمزوری (کے دوسرے دور) میں اٹھایا تھا۔ اور اس کا دودھ چھڑانا دو سال کے عرصہ میں تھا (یاد رکھو کہ) میری ہی طرف (تجھ کو) لوٹ کر آنا ہوگا۔ " WHERE ayatNumber = 15 AND chapterNumber = 31;
UPDATE verses SET ts = "اور اگر وہ دونوں تجھ سے بحث کریں کہ تو کسی کو میرا شریک مقرر کر جس کا تجھے کوئی علم نہیں تو ان دونوں کی بات مت مانیو۔ ہاں دنیوی معاملات میں ان کے ساتھ نیک تعلقات قائم رکھیئو اور اس شخص کے پیچھے چلیو جو میری طرف جھکتا ہے اور تم سب کا لوٹنا میری طرف ہی ہوگا۔ اس وقت میں تم کو تمہارے عمل سے خبردار کروں گا۔ " WHERE ayatNumber = 16 AND chapterNumber = 31;
UPDATE verses SET ts = "(لقمان نے کہا کہ) اے میرے بیٹے بات یہ ہے کہ اگر ایک عمل رائی کے دانہ کے برابر بھی ہو پھر وہ (رائی کے برابر عمل کسی) پتھر میں یا آسمانوں میں یا زمین میں چھپا ہوا ہو تو اللہ اس کو ظاہر کر دے گا۔ اللہ باریک سے باریک راز کو پالینے والا اور بہت خبردار ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 31;
UPDATE verses SET ts = "اے میرے بیٹے! نماز کو (تمام شرائط سے) قائم رکھ۔ اور معروف باتوں کا حکم دیتا رہ اور ناپسندیدہ باتوں سے روکتا رہ اور جو (تنگی ترشی) تجھے پہنچے اس پر صبر سے کام لے یہ بات یقیناً نہایت ہی ہمت والے کاموں میں سے ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 31;
UPDATE verses SET ts = "اور اپنے گال لوگوں کے سامنے (غصہ سے) مت پھلا۔ اور زمین میں تکبر سے مت چل۔ اللہ یقیناً ہر شیخی کرنے والے اور فخر کرنے والے سے پیار نہیں کرتا۔ " WHERE ayatNumber = 19 AND chapterNumber = 31;
UPDATE verses SET ts = "اور اپنی رفتار میں میانہ روی اختیار کر اور اپنی آواز کو دھیما رکھا کر (کیونکہ) آوازوں میں سے سب سے زیادہ ناپسندیدہ آواز گدھے کی آواز ہے (جو بہت اونچی ہوتی ہے) ۔" WHERE ayatNumber = 20 AND chapterNumber = 31;
UPDATE verses SET ts = "کیا تم (لوگوں) نے نہیں دیکھا کہ اللہ نے جو کچھ آسمانوں میں ہے اور زمین میں ہے تمہاری خدمت میں لگایا ہوا ہے اور تم پر اپنی نعمتیں خواہ ظاہری ہوں یا باطنی پانی کی طرح بہادی ہیں اور لوگوں میں سے بعض ایسے ہیں جو بغیر علم اور بغیر ہدایت اور بغیر کسی روشن کتاب کی دلیل کے اللہ کے بارہ میں بحث کرتے ہیں۔ " WHERE ayatNumber = 21 AND chapterNumber = 31;
UPDATE verses SET ts = "اور جب انہیں کہا جاتا ہے کہ جو کچھ اللہ نے اتارا ہے اس کی اتباع کرو۔ تو کہتے ہیں‘ نہیں! ہم تو اس کی اتباع کریں گے جس پر ہم نے اپنے باپ دادوں کو پایا ہے۔ کیا اگر شیطان (ان کے باپ دادوں کے ذریعہ سے) ان کو دوزخ کے عذاب کی طرف بلا رہا ہو (پھر بھی وہ ایسا کریں گے) ۔" WHERE ayatNumber = 22 AND chapterNumber = 31;
UPDATE verses SET ts = "اور جو شخص اپنی توجہ کو اللہ کی طرف پھیر دیتا ہے اور وہ اپنے عمل میں بھی پورا محتاط ہے تو وہ ایسا ہے کہ (گویا) اس نے ایک مضبوط کڑے کو پکڑ لیا ہے اور اللہ کی طرف ہی تمام کاموں کا انجام پھرتا ہے۔ " WHERE ayatNumber = 23 AND chapterNumber = 31;
UPDATE verses SET ts = "اور جو لوگ کفر کریں ان کا کفر تجھے غمگین نہ کرے۔ ان کو آخر ہماری طرف ہی لوٹنا پڑے گا۔ پھر ہم ان کو ان کے اعمال کی حقیقت سے خبردار کریں گے۔ اللہ یقیناً سینہ کے اندر کی سب باتوں کو جانتا ہے۔ " WHERE ayatNumber = 24 AND chapterNumber = 31;
UPDATE verses SET ts = "ہم ان کو کچھ عرصہ تک دنیوی فائدہ پہنچائیں گے۔ پھر ہم ان کو مجبور کرکے بڑے سخت عذاب کی طرف لے جائیں گے۔ " WHERE ayatNumber = 25 AND chapterNumber = 31;
UPDATE verses SET ts = "اور اگر تو ان سے پوچھے کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے تو وہ ضرور کہیں گے اللہ نے۔ تو اُن سے کہہ (ٹھیک ہے) سب تعریف اللہ ہی کی ہے مگر ان (منکروں) میں سے اکثر یہ بات نہیں جانتے۔ " WHERE ayatNumber = 26 AND chapterNumber = 31;
UPDATE verses SET ts = "آسمانوں اور زمین میں جو کچھ ہے اللہ ہی کا ہے۔ اللہ ہی یقیناً وہ (ذات) ہے جو (ہر عبادت اور تعریف سے) بے نیاز ہے (مگر ساتھ ہی) وہ سب تعریفوں کا بھی مستحق ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 31;
UPDATE verses SET ts = "اور اگر زمین میں جتنے درخت ہیں ان کی قلمیں بن جائیں اور سمندر سیاہی سے بھرا ہوا ہو۔ اس طرح کہ سات اور سیاہی کے سمندر اس میں ملا دئے جائیں تو بھی اللہ کے نشان ختم نہیں ہوں گے۔ اللہ یقیناً غالب (اور) بڑی حکمتوں والا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 31;
UPDATE verses SET ts = "تم سب کی پیدائش اور تمہارا زندہ کرکے اٹھایا جانا صرف ایک شخص کی پیدائش کی طرح ہے اللہ یقیناً بہت سننے والا (اور) بہت دیکھنے والا ہے۔ " WHERE ayatNumber = 29 AND chapterNumber = 31;
UPDATE verses SET ts = "کیا تو نے دیکھا نہیں کہ اللہ رات کو دن میں داخل کر دیتا ہے اور دن کو رات میں داخل کر دیتا ہے اور سورج اور چاند کو اس نے خدمت پر لگا چھوڑا ہے۔ ان میں سے ہر ایک مدت مقررہ تک کے لئے چلتا چلا جاتا ہے اور اللہ تمہارے اعمال سے اچھی طرح واقف ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 31;
UPDATE verses SET ts = "یہ سب کچھ اس لئے ہو رہا ہے کہ اللہ کی ذات سچی اور قائم رہنے والی ہے اور اس لئے کہ جن کو وہ لوگ اس کے سوا پکارتے ہیں وہ جھوٹے اور فنا ہونے والے ہیں اور اللہ یقیناً بہت بلند اور وسیع شان والا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 31;
UPDATE verses SET ts = "کیا تو نے دیکھا نہیں کہ کشتیاں اللہ کی نعمت کے ساتھ سمندر میں چلتی ہیں تا کہ وہ تم کو اپنے نشانات دکھائے۔ اس میں ہر بڑے صابر اور ہر بڑے شکر گذار بندے کے لئے بہت سے نشان ہیں۔ " WHERE ayatNumber = 32 AND chapterNumber = 31;
UPDATE verses SET ts = "اور جب انہیں کوئی موج سایہ کی طرح ڈھانک لیتی ہے تو وہ عبادت کو صرف اللہ کے لئے مخصوص کرتے ہوئے اس کو پکارتے ہیں۔ پھر جب وہ انہیں خشکی کی طرف نجات دے دیتا ہے تو ان میں سے کچھ لوگ میانہ روی پر قائم رہتے ہیں (اور کچھ پھر وہی ظلم اور شرک کرنے لگ جاتے ہیں) اور ہماری آیتوں کا انکار صرف بدعہد اور ناشکرا ہی کرتا ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 31;
UPDATE verses SET ts = "اے لوگو! اپنے رب کا تقویٰ اختیار کرو اور اس دن سے ڈرو جس دن کوئی باپ بھی اپنے بیٹے کے کام نہ آسکے گا اور نہ کوئی بیٹا اپنے باپ کے کام آسکے گا۔ اللہ کا وعدہ ضرور پورا ہو کر رہتا ہے۔ پس دنیا کی زندگی تم کو دھوکا میں نہ ڈال دے اور نہ دھوکا دینے والا شیطان تم کو اللہ کے بارے میں دھوکا میں ڈالے۔ " WHERE ayatNumber = 34 AND chapterNumber = 31;
UPDATE verses SET ts = "قیامت (یا کسی قوم کے آخری فیصلہ) کا علم اللہ ہی کو ہے اور وہی بارش نازل کرتا ہے اور رحموں میں جو کچھ ہے اسے جانتا ہے اور کوئی شخص نہیں جانتا کہ کل وہ کیا عمل کرے گا اور نہ کوئی شخص جانتا ہے کہ وہ کس زمین میں مرے گا۔ اللہ ہی یقیناً جاننے والا (اور) خبر رکھنے والا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 31;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 32;
UPDATE verses SET ts = "میں اللہ ہوں جو سب سے زیادہ جانتا ہوں۔ " WHERE ayatNumber = 2 AND chapterNumber = 32;
UPDATE verses SET ts = "اس کتاب کا(جس کی یہ ایک سورۃ ہے) رب العالمین کی طرف سے اتارا جانا ایک یقینی بات ہے‘ جس میں کوئی شبہ نہیں۔ " WHERE ayatNumber = 3 AND chapterNumber = 32;
UPDATE verses SET ts = "کیا وہ لوگ کہتے ہیں کہ اس شخص (یعنی محمد رسول اللہ صلی اللہ علیہ وسلم) نے اس کو اپنے پاس سے بنا لیا ہے۔ ایسا نہیں بلکہ یہ کتاب تیرے رب کی طرف سے نازل ہونے والی (اور قائم رہنے والی) کتاب ہے۔ تا کہ تو اس قوم کو ہشیار کرے جن کے پاس تجھ سے پہلے کوئی رسول نہیں آیا تا کہ وہ ہدایت پائیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 32;
UPDATE verses SET ts = "اللہ وہ ہے جس نے آسمانوں اور زمین کو اور جو کچھ ان دونوں کے درمیان ہے‘ اس سب کو چھ وقتوں میں پیدا کیا۔ اس کے بعد وہ عرش حکومت پر مضبوطی سے قائم ہو گیا‘ تمہارا اس (خدا) کے سوا نہ کوئی حقیقی دوست ہے‘ نہ سفارشی کیا تم نصیحت حاصل نہیں کرتے۔ " WHERE ayatNumber = 5 AND chapterNumber = 32;
UPDATE verses SET ts = "وہ آسمان سے زمین تک اپنے حکم کو اپنی تدبیر کے مطابق قائم کرے گا۔ پھر وہ اس کی طرف ایک ایسے وقت میں جس کی مقدار ایسے ہزار سال کی ہے جس کے مطابق تم دنیا میں گنتی کرتے ہو چڑھنا شروع کرے گا۔ " WHERE ayatNumber = 6 AND chapterNumber = 32;
UPDATE verses SET ts = "یہ غیب اور حاضر کا جاننے والا (خدا) ہے جو غالب (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 32;
UPDATE verses SET ts = "جس نے جو کچھ بھی پیدا کیا ہے اعلیٰ طاقتوں سے پیدا کیا ہے اور انسان کو گیلی مٹی سے پیدا کیا ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 32;
UPDATE verses SET ts = "پھر اس کی نسل کو ایک حقیر سیال چیز کے خلاصہ (یعنی نطفہ) سے پیدا کیا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 32;
UPDATE verses SET ts = "پھر اس کو مکمل طاقتیں دیں اور اس میں اپنی طرف سے روح ڈالی اور تمہارے لئے کان‘ اور آنکھ‘ اور دل بنائے۔ مگر تم بالکل شکر نہیں کرتے۔ " WHERE ayatNumber = 10 AND chapterNumber = 32;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ کیا جب ہم زمین میں کھوئے جائیں گے تو ہمیں ایک نئی مخلوق کی شکل میں کھڑا کر دیا جائے گا (یہ لوگ اس نئی پیدائش کے منکر نہیں) بلکہ اپنے رب کی ملاقات کے منکر ہیں۔ " WHERE ayatNumber = 11 AND chapterNumber = 32;
UPDATE verses SET ts = "تو کہہ کہ وہ موت کا فرشتہ جو تم پر مقرر کیا گیا ہے‘ ضرور تمہاری روح قبض کرے گا۔ پھر تم اپنے رب کی طرف لوٹائے جائو گے۔ " WHERE ayatNumber = 12 AND chapterNumber = 32;
UPDATE verses SET ts = "اور اگر تجھے وہ حالت معلوم ہو جائے جب کہ مجرم اپنے رب کے سامنے اپنا سر ڈالے کھڑے ہوں گے اور کہہ رہے ہوں گے اے ہمارے رب! ہم نے (جو کچھ تو نے کہا تھا) دیکھ لیا اور سن لیا۔ پس اب تو ہمیں واپس لوٹا دے تا کہ تیرے حکم کے مطابق عمل کریں۔ ہم اب تیری بات پر پوری طرح یقین کر چکے ہیں۔ " WHERE ayatNumber = 13 AND chapterNumber = 32;
UPDATE verses SET ts = "اور اگر ہم چاہتے تو ہر شخص کو اس کے مناسب حال ہدایت دیتے۔ لیکن میری بات پوری ہوگئی کہ میں ضرور جہنم کو جنوں اور انسانوں سے پر کروں گا۔ " WHERE ayatNumber = 14 AND chapterNumber = 32;
UPDATE verses SET ts = "پس آج کے دن کی ملاقات کو بھول جانے کی وجہ سے اور اپنے عملوں کی وجہ سے دیر پا عذاب کا مزہ چکھو اور (یاد رکھو کہ) ہم نے بھی (آج) تم کو اپنے ذہن سے اتار دیا ہے (یعنی تمہاری پروا کرنی چھوڑ دی ہے) ۔" WHERE ayatNumber = 15 AND chapterNumber = 32;
UPDATE verses SET ts = "ہماری آیتوں پر تو وہی لوگ ایمان لاتے ہیں کہ جب ان کو ان کے متعلق یاد دلایا جاتا ہے تو وہ سجدہ کرتے ہوئے زمین پر گر جاتے ہیں اور اپنے رب کی تعریف اور تسبیح کرتے ہیں اور تکبر نہیں کرتے۔ " WHERE ayatNumber = 16 AND chapterNumber = 32;
UPDATE verses SET ts = "(اور) اُن (مومنوں) کے پہلو ان کے بستروں سے الگ ہو جاتے ہیں (یعنی تہجد کی نماز پڑھنے کے لئے) (اور) وہ اپنے رب کو اس کے عذابوں سے بچنے کے لئے اور اس کی رحمتوں کو حاصل کرنے کے لئے پکارتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے خدا کی راہ میں خرچ کرتے رہتے ہیں۔ " WHERE ayatNumber = 17 AND chapterNumber = 32;
UPDATE verses SET ts = "اور (حقیقت یہ ہے کہ) کوئی شخص نہیں جانتا کہ ان (مومنوں) کے لئے ان کے اعمال کے بدلہ کے طور پر کیا کیا آنکھیں ٹھنڈی کرنے والی چیزیں چھپا کر رکھی گئی ہیں۔ " WHERE ayatNumber = 18 AND chapterNumber = 32;
UPDATE verses SET ts = "کیا جو شخص مومن ہو اس کی طرح ہو سکتا ہے جو اطاعت سے باہر نکل جائے ایسے لوگ ہرگز برابر نہیں ہو سکتے۔ " WHERE ayatNumber = 19 AND chapterNumber = 32;
UPDATE verses SET ts = "وہ لوگ جو ایمان لائے ہیں اور اس کے مطابق انہوں نے عمل کیے ہیں انہیں رہائش کے قابل جنتیں ملیں گی۔ یہ ان کے اعمال کے مطابق مہمان داری ہوگی۔ " WHERE ayatNumber = 20 AND chapterNumber = 32;
UPDATE verses SET ts = "اور جو لوگ اطاعت سے نکل گئے ہیں ان کو رہنے کے لئے دوزخ ملے گی۔ جب کبھی اس سے نکلنے کا ارادہ کریں گے اسی کی طرف واپس کر دئیے جائیں گے اور اُن سے کہا جائے گا کہ اب دوزخ کا وہ عذاب چکھو جس کو تم جھٹلایا کرتے تھے۔ " WHERE ayatNumber = 21 AND chapterNumber = 32;
UPDATE verses SET ts = "اور اس بڑے آنے والے عذاب سے پہلے ہم (اس دنیا کا) چھوٹا عذاب بھی ان کو چکھائیں گے تاکہ وہ لوٹیں (اور توبہ کریں اور اخروی عذاب سے بچ جائیں) ۔" WHERE ayatNumber = 22 AND chapterNumber = 32;
UPDATE verses SET ts = "اور جس کو اس کے رب کی آیات یاد دلائی جائیں پھر بھی وہ ان سے اعراض کرے اس سے زیادہ ظالم کون ہو سکتا ہے۔ ہم یقیناً مجرموں سے بدلہ لیں گے۔ " WHERE ayatNumber = 23 AND chapterNumber = 32;
UPDATE verses SET ts = "اور ہم نے موسیٰ کو کتاب دی تھی پس تو بھی ایک مکمل کتاب کے ملنے کے متعلق شبہ نہ کر اور ہم نے اس (کتاب) کو بنی اسرائیل کے لئے ہدایت بنایا تھا۔ " WHERE ayatNumber = 24 AND chapterNumber = 32;
UPDATE verses SET ts = "اور ہم نے ان میں سے امام بنائے تھے جو کہ ہمارے حکم کے ساتھ لوگوں کو ہدایت دیتے تھے۔ اس وجہ سے کہ انہوں نے استقلال سے کام لیا اور وہ لوگ ہماری آیتوں پر مکمل یقین رکھتے تھے۔ " WHERE ayatNumber = 25 AND chapterNumber = 32;
UPDATE verses SET ts = "تیرا رب تو وہ ہے جو (ان کلام الٰہی کے پہنچانے والوں اور) ان کے (منکروں کے) درمیان قیامت کے دن فیصلہ کرے گا۔ ہر اس امر کے متعلق جس میں وہ اختلاف کیا کرتے تھے۔ " WHERE ayatNumber = 26 AND chapterNumber = 32;
UPDATE verses SET ts = "کیا اُن کو اس بات نے ہدایت نہیں دی کہ ہم نے بہت سے زمانوں کے لوگوں کو ان سے پہلے ہلاک کر دیا تھا (اور اب) یہ لوگ انہی کے گھروں میں چلتے پھرتے ہیں۔ اس میں یقیناً بہت سے نشان ہیں مگر کیا وہ سنتے نہیں؟ " WHERE ayatNumber = 27 AND chapterNumber = 32;
UPDATE verses SET ts = "کیا انہوں نے نہیں دیکھا کہ ہم ایک بنجر زمین کی طرف پانی کو ہانک کر لے جاتے ہیں پھر اس (پانی) کے ذریعہ سے کھیتی نکالتے ہیں جس سے ان کے جانور بھی کھاتے ہیں اور وہ خود بھی کھاتے ہیں کیا وہ دیکھتے نہیں؟ " WHERE ayatNumber = 28 AND chapterNumber = 32;
UPDATE verses SET ts = "اور وہ کہتے ہیں کہ یہ فتح جس کا تم ذکر کرتے ہو اگر تم سچے ہو تو بتائو کہ کب ہوگی۔ " WHERE ayatNumber = 29 AND chapterNumber = 32;
UPDATE verses SET ts = "تو کہہ دے کہ اس فتح کے دن کفار کو ان کا ایمان نفع نہیں دے گا اور نہ ان کو ڈھیل دی جائے گی۔ " WHERE ayatNumber = 30 AND chapterNumber = 32;
UPDATE verses SET ts = "پس تو ان سے اعراض کر اور (ان کے انجام کا) انتظار کر‘ وہ بھی کچھ عرصہ تک ابھی اور انتظار کریں گے۔ " WHERE ayatNumber = 31 AND chapterNumber = 32;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) ۔" WHERE ayatNumber = 1 AND chapterNumber = 33;
UPDATE verses SET ts = "اے نبی! اللہ کا تقویٰ اختیار کر اور کافروں اور منافقوں کی بات نہ مان۔ اللہ یقیناً بہت جاننے والا (اور) بہت حکمت والا ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 33;
UPDATE verses SET ts = "اور (صرف) اُس وحی کی پیروی کر جو تیرے رب کی طرف سے تجھ پر نازل کی جاتی ہے۔ اللہ تمہارے اعمال سے خوب واقف ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 33;
UPDATE verses SET ts = "اور اللہ پر توکل کر اور اللہ کارساز ہونے کے لحاظ سے کافی ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 33;
UPDATE verses SET ts = "اللہ نے کسی مرد کے سینہ میں دو دل نہیں بنائے اور نہ تمہاری بیویوں کو جن کو تم (بعض دفعہ) ماں کہہ بیٹھتے ہو تمہاری مائیں بنایا ہے اور نہ تمہارے لے پالکوں کو تمہارا بیٹا بنایا ہے۔ یہ سب تمہاری زبانی باتیں ہیں اور اللہ ہی سچی بات کہتا ہے اور وہی سیدھا راستہ دکھاتا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 33;
UPDATE verses SET ts = "(چاہیے کہ) ان (لے پالکوں) کو ان کے باپوں کا بیٹا کہہ کر پکارو۔ یہ اللہ کے نزدیک زیادہ منصفانہ فعل ہے اور اگر تم کو معلوم نہ ہو کہ ان کے باپ کون ہیں تو (بہرحال) وہ تمہارے دینی بھائی ہیں اور دینی دوست ہیں اور جو تم غلطی سے پہلے کر چکے ہو اس کے متعلق تم پر کوئی گناہ نہیں‘ لیکن جس بات پر تمہارے دل پختہ ارادہ کر بیٹھے ہوں (وہ قابل سزا ہے) اور اللہ (ہرتوبہ کرنے والے کے لئے) بہت بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 33;
UPDATE verses SET ts = "نبی مومنوں سے ان کی اپنی جانوں کی نسبت بھی زیادہ قریب ہے اور اس کی بیویاں ان کی مائیں ہیں اور رحمی رشتہ داروں میں سے اللہ کی کتاب کے مطابق بعض بعض سے زیادہ قریبی ہیں۔ بہ نسبت (غیر رشتہ دار) مومنوں اور مہاجروں کے ہاں تمہارا اپنے دوستوں سے نیک سلوک کرنا (جائز ہے) یہ بات قرآن میں لکھی جا چکی ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 33;
UPDATE verses SET ts = "اور (یاد کرو) جب کہ ہم نے نبیوں سے ان پر عائد کردہ ایک خاص بات کا وعدہ لیا تھا اور تجھ سے بھی (وعدہ لیا تھا) اور نوح اور ابراہیم اور موسیٰ اور عیسیٰ ابن مریم سے بھی اور ہم نے ان سب سے پختہ عہد لیا تھا۔ " WHERE ayatNumber = 8 AND chapterNumber = 33;
UPDATE verses SET ts = "تا کہ اللہ سچوں سے ان کے سچ کے متعلق سوال کرے اور کافروں کے لئے اس نے ایک دردناک عذاب تیار کیا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 33;
UPDATE verses SET ts = "اے مومنو! اللہ کی اس نعمت کو یاد کرو جو اس نے تم پر اس وقت کی جبکہ تم پر کچھ لشکر چڑھ آئے تھے اور ہم نے ان کی طرف ایک ہوا بھیجی تھی اور ایسے لشکر بھیجے تھے جن کو تم نہیں دیکھتے تھے اور اللہ تمہارے اعمال کو خوب دیکھتا ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 33;
UPDATE verses SET ts = "(ہاں! اس وقت کو یاد کرو) جبکہ تمہارے مخالف تمہاری اوپر کی طرف سے بھی (یعنی پہاڑی کی طرف سے بھی) اور نیچے کی طرف سے بھی (یعنی نشیب کی طرف سے بھی) آگئے تھے اور جبکہ آنکھیں گھبرا کر ٹیڑھی ہو گئی تھیں اور دل دھڑکتے ہوئے حلق تک آگئے تھے اور تم اللہ کے متعلق مختلف شکوک میں مبتلا ہو گئے تھے۔ " WHERE ayatNumber = 11 AND chapterNumber = 33;
UPDATE verses SET ts = "اس وقت مومن ایک (بڑے) ابتلا میں ڈال دئیے گئے تھے اور سخت ہلا دئیے گئے تھے۔ " WHERE ayatNumber = 12 AND chapterNumber = 33;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جبکہ منافق اور جن لوگوں کے دلوں میں بیماری تھی‘ کہنے لگ گئے تھے کہ اللہ اور اس کے رسول نے ہم سے صرف ایک جھوٹا وعدہ کیا تھا۔ " WHERE ayatNumber = 13 AND chapterNumber = 33;
UPDATE verses SET ts = "اور جبکہ ان سے ایک گروہ یہ بھی کہنے لگ گیا تھا کہ اے مدینہ کے لوگو! (اب) تمہارے لئے کوئی ٹھکانا نہیں پس (اسلام سے) مرتد ہو جائو‘ اور ان میں سے ایک گروہ نبی سے اجازت مانگنے لگ گیا تھا اور کہنے لگ گیا تھا کہ ہمارے گھر دشمن کی زد میں ہیں حالانکہ وہ دشمن کی زد میں نہیں تھے وہ صرف بھاگنے کا ارادہ کر رہے تھے۔ " WHERE ayatNumber = 14 AND chapterNumber = 33;
UPDATE verses SET ts = "اور اگر مدینہ کی مختلف طرفوں سے ان پر (فوجیں) داخل کر دی جائیں اور پھر ان سے (کافروں کی طرف سے) ارتداد کا مطالبہ کیا جائے تو یہ ضرور اس مطالبہ کو قبول کر لیں گے۔ لیکن وہ اس (یعنی مدینہ) میں (اس کے بعد) تھوڑا سا ہی رہنے پائیں گے۔ " WHERE ayatNumber = 15 AND chapterNumber = 33;
UPDATE verses SET ts = "اور (حقیقت یہ ہے کہ) اس سے پہلے ان (منافقوں) نے اللہ سے عہد کیا تھا کہ وہ کبھی پیٹھ نہ پھیریں گے (اور ثابت قدم رہیں گے) اور اللہ سے باندھے جانے والے عہد کے متعلق ضرور سوال کیا جائے گا۔ " WHERE ayatNumber = 16 AND chapterNumber = 33;
UPDATE verses SET ts = "تو ان سے کہہ دے کہ اگر تم موت یا قتل سے بھاگو گے تو تمہارا بھاگنا تمہیں ہر گز نفع نہیں دے گا اور اس صورت میں تم کوئی فائدہ نہیں اٹھائو گے۔ " WHERE ayatNumber = 17 AND chapterNumber = 33;
UPDATE verses SET ts = "تو کہہ دے کہ وہ کون ہے جو تمہیں اللہ کی گرفت سے اگر وہ تمہیں سزا دینا چاہے بچائے گا۔ یا اگر وہ تم پر رحم کرنا چاہے (تو اس سے تمہیں محروم کر سکے گا) وہ اللہ کے سوا اپنے لئے نہ کوئی حقیقی دوست پائیں گے اور نہ مددگار۔ (یعنی جب بھی کام آئے گا خدا ہی آئے گا) " WHERE ayatNumber = 18 AND chapterNumber = 33;
UPDATE verses SET ts = "اللہ ان لوگوں کو پوری طرح جانتا ہے جو تم میں سے دوسروں کو (جہاد سے) پیچھے ہٹاتے تھے۔ اور اپنے بھائیوں سے کہتے تھے کہ ہماری طرف آئو (اور جہاد میں شامل نہ ہو) اور وہ خود بھی دشمن سے بالکل جنگ نہ کرتے تھے۔ " WHERE ayatNumber = 19 AND chapterNumber = 33;
UPDATE verses SET ts = "وہ تمہارے متعلق بہت بخل رکھنے والے ہیں (یعنی کبھی نہیں چاہتے کہ تمہیں کوئی خیر پہنچے) اور جب ان پر کوئی خوف کا وقت آئے تو تو انہیں دیکھے گا کہ وہ تیری طرف اس طرح دیکھتے ہیں کہ ان کے ڈیلے چکر کھا رہے ہوتے ہیں بالکل اس شخص کی طرح جو موت کی غشی میں مبتلا ہو۔ پھر جب خوف کا وقت جاتا رہتا ہے تو وہ تم پر تلوار کی طرح کاٹنے والی زبان چلاتے ہیں وہ ہر خیر کے متعلق بخیل ہیں (اور چاہتے ہیں کہ تم کو نہ ملے) یہ لوگ (درحقیقت) ایمان لائے ہی نہ تھے۔ سو اللہ نے ان کی سب کوششیں ضائع کر دیں‘ (جو وہ اسلام کے خلاف کرتے تھے) اور اللہ پر یہ بات بالکل آسان تھی۔ " WHERE ayatNumber = 20 AND chapterNumber = 33;
UPDATE verses SET ts = "(اور) یہ لوگ تو اب بھی امید رکھتے ہیں کہ (کاش) (کفار کا) لشکر ابھی گیا نہ ہو حالانکہ اگر (کفار کا) لشکر واپس آجائے تو (بجائے ان کے آنے پر خوش ہونے کے) یہ لوگ خواہش کریں گے کہ کاش وہ جنگلی لوگوں میں رہ رہے ہوتے (اور اے مومنو)! تمہارے متعلق (لوگوں سے) دریافت کر رہے ہوتے (کہ ابھی زندہ ہیں یا تباہ ہو چکے ہیں) اور اگر یہ (ضرورت کے موقعہ پر) تمہارے ساتھ رہتے تو تب بھی تمہاری طرف سے بالکل نہ لڑتے۔ " WHERE ayatNumber = 21 AND chapterNumber = 33;
UPDATE verses SET ts = "تمہارے لئے (یعنی ان لوگوں کے لئے) جو اللہ اور اخروی دن سے ملنے کی امید رکھتے ہیں اور اللہ کا بہت ذکر کرتے ہیں۔ اللہ کے رسول میں ایک اعلیٰ نمونہ ہے (جس کی انہیں پیروی کرنی چاہیئے) ۔" WHERE ayatNumber = 22 AND chapterNumber = 33;
UPDATE verses SET ts = "چنانچہ (دیکھو) جب حقیقی مومنوں نے حملہ آور لشکروں کو دیکھا تو کہا یہ تو وہی (لشکر) ہیں جن کا اللہ اور اس کے رسول نے ہم سے وعدہ کیا تھا اور اللہ اور اس کے رسول نے بالکل سچ بولا تھا اور ان کو اس واقعہ نے ایمان اور اطاعت میں ہی بڑھایا (کمزور نہیں کیا) ۔" WHERE ayatNumber = 23 AND chapterNumber = 33;
UPDATE verses SET ts = "ان مومنوں سے کچھ لوگ ایسے ہیں جنہوں نے اس وعدہ کو جو انہوں نے اللہ سے کیا تھا سچا کر دیا۔ پس بعض تو ایسے ہیں جنہوں نے اپنی نیت کو پورا کر دیا (یعنی لڑتے لڑتے مارے گئے)  اور ان میں سے بعض ایسے بھی ہیں جو ابھی انتظار کر رہے ہیں اور اپنے ارادہ میں کوئی تزلزل انہوں نے نہیں آنے دیا۔ " WHERE ayatNumber = 24 AND chapterNumber = 33;
UPDATE verses SET ts = "اس کا نتیجہ یہ ہوگا کہ اللہ ایسے صادقوں کو ان کے صدق کا بدلہ دے گا اور اگر چاہے گا تو منافقوں کو عذاب دے گا۔ یا ان پر رحم کرے گا اور اللہ یقیناً بڑا بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 25 AND chapterNumber = 33;
UPDATE verses SET ts = "اور (حقیقت یہ ہے کہ) اللہ نے کفار کو ان کے سب غصہ سمیت (مدینہ سے) واپس لوٹا دیا اور کسی قسم کا فائدہ ان کو نہیں پہنچا‘ اور اللہ نے مومنوں کی طرف سے خود لڑائی کی اور اللہ بڑا طاقت ور (اور) غالب ہے ۔" WHERE ayatNumber = 26 AND chapterNumber = 33;
UPDATE verses SET ts = "اور اس (اللہ) نے اُن اہل کتاب کو جنہوں نے ان (حملہ آور مشرکوں) کی مدد کی تھی‘ اپنے قلعوں سے اتار دیا اور ان کے دلوں میں رُعب داخل کر دیا۔ یہاں تک کہ تم ان میں سے ایک حصہ کو قتل کرنے پر اور ایک حصہ کو قید کرنے پر قادر ہوگئے۔ " WHERE ayatNumber = 27 AND chapterNumber = 33;
UPDATE verses SET ts = "اور ان کی زمینوں اور ان کے گھروں اور ان کے مالوں کا تم کو وارث کر دیا نیز اس زمین کا بھی جس پر ابھی تمہارے قدم نہیں پڑے اور اللہ ہر ایک چیز پر قادر ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 33;
UPDATE verses SET ts = "اے نبی! اپنی بیویوں سے کہہ کہ اگر تم دنیا اور اس کی زینت چاہتی ہو تو آئو میں تمہیں کچھ دنیوی سامان دے دیتا ہوں اور تم کو نیک طریق سے رخصت کر دیتا ہوں۔ " WHERE ayatNumber = 29 AND chapterNumber = 33;
UPDATE verses SET ts = "اور اگر تم اللہ اور اس کے رسول اور اُخروی زندگی کے گھر کو چاہتی ہو تو اللہ نے تم میں سے پوری طرح اسلام پر قائم رہنے والیوں کے لئے بہت بڑا انعام تجویز کر رکھا ہے۔ " WHERE ayatNumber = 30 AND chapterNumber = 33;
UPDATE verses SET ts = "اے نبی کی بیویو! اگر تم میں سے کوئی اعلیٰ ایمان کے خلاف بات کرے‘ تو اس کا عذاب دُگنا کیا جائے گا۔ اور یہ بات اللہ پر آسان ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 33;
UPDATE verses SET ts = "اور تم میں سے جو کوئی اللہ اور اس کے رسول کی فرمانبرداری کرے گی اور اس (فرمانبرداری کی شان) کے مطابق کام بھی کرے گی تو ہم اسے انعام بھی دگنا دیں گے اور ہم نے ہر ایسی (بیوی) کے لئے معزز رزق تیار کیا ہوا ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 33;
UPDATE verses SET ts = "اے نبی کی بیویو! اگر تم اپنے مقام کو سمجھو تو تم عام عورتوں کی طرح نہیں ہو۔ پس چبا کر (یعنی نازونخرے کے ساتھ) بات نہ کیا کرو۔ تا ایسا نہ ہو کہ جس کے دل میں مرض ہے وہ تمہارے متعلق کوئی بدارادہ کرے اور ہمیشہ لوگوں کو نیک باتیں کہا کرو۔ " WHERE ayatNumber = 33 AND chapterNumber = 33;
UPDATE verses SET ts = "اور اپنے گھروں میں بیٹھی رہا کرو اور پرانے زمانے کی جاہلیت (کے طریقوں) کی طرح اپنے زینت (غیر لوگوں پر) ظاہر نہ  کیا کرو اور نماز کو (باشرائط) ادا کیا کرو۔ اور زکٰوۃ دیا کرو۔ اور اللہ اور اس کے رسول کی اطاعت کیا کرو۔ اے اہل بیت! اللہ تم میں سے ہر قسم کی گندگی دور کرنا اور تم کو کامل طور پر پاک کرنا چاہتا ہے۔ " WHERE ayatNumber = 34 AND chapterNumber = 33;
UPDATE verses SET ts = "اور جو کچھ تمہارے گھروں میں اللہ کی آیتوں اور حکمت کی باتوں کی تلاوت کی جاتی ہے اس کو یاد رکھو اللہ بہت مہربانی کرنے والا (اور) خبر رکھنے والا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 33;
UPDATE verses SET ts = "یقیناً کامل مسلمان مرد اور کامل مسلمان عورتیں اور کامل مومن مرد اور کامل مومن عورتیں اور کامل فرمانبردار مرد اور کامل فرمانبردار عورتیں اور کامل راست گو مرد اور کامل راست گو عورتیں اور کامل صبر کرنے والے مرد اور کامل صبر کرنے والی عورتیں اور کامل عجز دکھانے والے مرد اور کامل عجز دکھانے والی عورتیں اور کامل صدقہ کرنے والے مرد اور کامل صدقہ کرنے والی عورتیں اور کامل روزہ گذار مرد اور کامل روزہ گذار عورتیں اور پوری طرح اپنی شرم گاہوں کی حفاظت کرنے والے مرد اور پوری طرح اپنی شرم گاہوں کی حفاظت کرنے والی عورتیں اور اللہ کا بہت ذکر کرنے والے مرد اور اللہ کا بہت ذکر کرنے والی عورتیں ان سب کے لئے اللہ نے بخشش کا سامان اور بڑا انعام تیار کر رکھا ہے۔ " WHERE ayatNumber = 36 AND chapterNumber = 33;
UPDATE verses SET ts = "اور کسی مومن مرد یا مومن عورت کے لئے جائز نہیں کہ جب اللہ اور اس کا رسول کسی معاملہ میں کوئی فیصلہ کر دیں تو وہ (پھر بھی) اپنے معاملے کا اپنی مرضی سے فیصلہ کریں اور جو کوئی اللہ اور اس کے رسول کی نافرمانی کرتا ہے وہ کھلی کھلی گمراہی میں پڑجاتا ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 33;
UPDATE verses SET ts = "اور (اے نبی! یاد کر) جب تو اس شخص کو جس پر اللہ نے اور تو نے انعام کیا تھا‘ کہتا تھا کہ اپنی بیوی کو روک رکھ (اور طلاق نہ دے) اور اللہ کا تقویٰ اختیار کر اور تو اپنے دل میں وہ بات چھپاتا تھا جس کو اللہ ظاہر کرنے والا تھا۔ اور تو لوگوں سے ڈرتا تھا‘ حالانکہ اللہ زیادہ حق دار ہے کہ تو اس سے ڈرے۔ پس جب زید نے اس (عورت) کے بارے میں اپنی خواہش پوری کر لی (یعنی طلاق دے دی) ہم نے اس (عورت) کا تجھ سے بیاہ کر دیا تاکہ مومنوں کے دلوں میں اپنے لے پالکوں کی بیویوں سے نکاح کرنے کے متعلق ان کو طلاق مل جانے کی صورت میں کوئی خلش نہ رہے اور خدا کا فیصلہ بہرحال پورا ہو کر رہنا تھا۔ " WHERE ayatNumber = 38 AND chapterNumber = 33;
UPDATE verses SET ts = "نبی پر اس معاملے میں جو اس پر اللہ نے فرض کیا ہے کوئی الزام نہیں۔ یہی طریق اللہ نے پہلے لوگوں میں جاری کیا تھا اور خدا کا حکم تو ایک فیصلہ شدہ چیز ہے (کوئی اسے ٹلا نہیں سکتا)۔ " WHERE ayatNumber = 39 AND chapterNumber = 33;
UPDATE verses SET ts = "(یہی سنت پہلے گذرے ہوئے پیغمبروں میں جاری تھی) جو اللہ کے پیغام لوگوں کو پہنچا دیتے تھے اور اس سے ڈرتے تھے اور اللہ کے سوا کسی سے نہیں ڈرتے تھے اور حساب لینے میں اللہ کافی ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 33;
UPDATE verses SET ts = "نہ محمد تم میں سے کسی مرد کے باپ تھے نہ ہیں (نہ ہونگے) لیکن اللہ کے رسول ہیں بلکہ (اس سے بھی بڑھ کر) نبیوں کی مہر ہیں اور اللہ ہر ایک چیز سے خوب آگاہ ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 33;
