UPDATE verses SET ts = "اور نیز اللہ کے حکم سے اس کی طرف بلانے والا اور ایک چمکتا ہوا سورج بنا کر (بھیجا ہے) ۔" WHERE ayatNumber = 47 AND chapterNumber = 33;
UPDATE verses SET ts = "اور مومنوں کو بشارت دے دے کہ ان کو اللہ کی طرف سے بہت بڑا فضل ملے گا۔ " WHERE ayatNumber = 48 AND chapterNumber = 33;
UPDATE verses SET ts = "اور کافروں اور منافقوں کی بات ہرگز نہ مان‘ اور ان کی ایذا دہی کو نظر انداز کر دے اور اللہ پر توکل کر اور اللہ کارسازی میں کافی ہے۔ " WHERE ayatNumber = 49 AND chapterNumber = 33;
UPDATE verses SET ts = "اے مومنو! جب تم مومن عورتوں سے شادی کرو‘ پھر ان کو ان کے چھونے سے پہلے طلاق دے دو تو تم کو کوئی حق نہیں کہ ان سے عدت کا مطالبہ کرو‘ پس (چاہیئے کہ) ان کو کچھ دنیوی نفع پہنچا دو اور ان کو عمدگی کے ساتھ رخصت کر دو۔ " WHERE ayatNumber = 50 AND chapterNumber = 33;
UPDATE verses SET ts = "اے نبی! تیری موجودہ بیویوں میں سے وہ جن کو تو نے ان کا حق مہر ادا کر دیا ہے ہم نے تیرے لئے حلال کر دی ہیں۔ اسی طرح آئندہ کے لئے وہ عورتیں جن کے مالک تیرے داہنے ہاتھ ہوئے ہیں جن کو اللہ تیرے قبضہ میں جنگ کے بعد لایا ہے اور اسی طرح تیرے چچے کی بیٹیاں اور تیری پھوپھی کی بیٹیاں اور تیرے ماموں کی بیٹیاں اور تیری خالائوں کی بیٹیاں جنہوں نے تیرے ساتھ ہجرت کی ہے اور ایسی مومن عورت بھی جس نے اپنی جان کو نبی کے لئے ہبہ کر دیا۔ بشرطیکہ نبی اس سے نکاح کا ارادہ کرے۔ یہ حکم خاص تیرے لئے ہے۔ باقی مومنوں کے لئے نہیں ہم اس کو جو ہم نے ان (مسلمانوں) پر ان کی بیویوں کے متعلق اور ان کی لونڈیوں کے متعلق فرض کیا ہے خوب جانتے ہیں۔ تا کہ تجھ پر کوئی ناواجب قید نہ ہو۔ اور اللہ بہت بخشنے والا (اور) باربار کرم کرنے والا ہے۔ " WHERE ayatNumber = 51 AND chapterNumber = 33;
UPDATE verses SET ts = "تو ان (بیویوں) میں سے جس کو چاہے الگ کر دے اور جس کو چاہے اپنے پاس رکھ لے اور ان عورتوں میں سے جن کو تو نے الگ کر دیا ہے کسی کو اپنے پاس لے آئے تو تجھ پر کوئی گناہ نہیں۔ یہ امر اس بات کے بہت زیادہ قریب ہے کہ ان کی آنکھیں ٹھنڈی ہوں اور وہ غم نہ کریں اور جو کچھ تو نے ان کو دیا ہے اس پر سب کی سب راضی ہو جائیں اور اللہ تمہارے دلوں کی ہر بات کو جانتا ہے اور وہ بہت جاننے والا (اور) بہت سمجھ بوجھ سے کام لینے والا ہے۔ " WHERE ayatNumber = 52 AND chapterNumber = 33;
UPDATE verses SET ts = "تیرے لئے (مزید) عورتیں اس کے بعد حلال نہیں اور نہ یہ حلال ہے کہ ان موجودہ بیویوں کو بدل کر اور بیویاں کر لے خواہ ان کا حسن تجھے کتنا ہی پسند ہو۔ سوائے ان عورتوں کے جن کے مالک تیرے دائیں ہاتھ ہوئے اور اللہ ہر ایک چیز کا نگران ہے۔ " WHERE ayatNumber = 53 AND chapterNumber = 33;
UPDATE verses SET ts = "اے مومنو! نبی کے گھروں میں سوائے اس کے کہ تمہیں کھانے کے لئے بلایا جائے ہرگز داخل نہ ہوا کرو۔ وہ بھی اس شرط سے کہ کھانا پکنے کے انتظار میںنہ بیٹھے رہا کرو۔ اور نہ باتیں کرنے کے شوق میں بیٹھے رہا کرو۔ ہاں! جب تم کو بلایا جائے تو پھر ضرور چلے جایا کرو۔ پھر جب کھانا کھا چکو تو اپنے اپنے گھروں کو چلے جایا کرو۔ یہ امر (یعنی بے فائدہ بیٹھے رہنا یا پہلے آجانا) نبی کو تکلیف دیتا تھا۔ مگر وہ (تمہارے جذبات کا خیال کرکے) تم (کو منع کرنے) سے حیا کرتا تھا۔ مگر اللہ سچی بات بیان کرنے سے (لوگوں کے خیالات کی وجہ سے) باز نہیں رہتا اور (چاہیے کہ) جب تم ان (یعنی نبی کی بیویوں) سے کوئی گھر کی چیز مانگو تو پردہ کے پیچھے سے مانگا کرو۔ یہ بات تمہارے دلوں اور ان کے دلوں کے لئے بہت اچھی ہے اور اللہ کے رسول کو تکلیف دینا تمہارے لئے جائز نہیں اور نہ یہ جائز ہے کہ تم اس کے بعد اس کی بیویوں سے کبھی بھی شادی کرو۔ یہ بات اللہ کے فیصلہ کے مطابق بہت بری ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 33;
UPDATE verses SET ts = "اگر تم کسی چیز کو ظاہر کرو یا چھپائو‘ تو اللہ ہر چیز کو خوب جانتا ہے۔ " WHERE ayatNumber = 55 AND chapterNumber = 33;
UPDATE verses SET ts = "ان (یعنی ازواج نبی کریم صلی اللہ علیہ وسلم) پر اپنے باپوں یا اپنے بیٹوں  یا اپنے بھائیوں یا اپنے بھتیجوں یا اپنے بھانجوں یا اپنی ہم کفو عورتوں یا اپنی زیر تصرف لونڈیوں کے سامنے ہونے میں کوئی پابندی نہیں اور اللہ کا تقویٰ اختیار کرو۔ اللہ ہر ایک چیز کو دیکھ رہا ہے۔ " WHERE ayatNumber = 56 AND chapterNumber = 33;
UPDATE verses SET ts = "اللہ یقیناً اس نبی پر اپنی رحمت نازل کر رہا ہے اور اس کے فرشتے بھی (یقیناً اس کے لئے دعائیں کر رہے ہیں پس) اے مومنو! تم بھی اس نبی پر درود بھیجتے اور ان کے لئے دعائیں کرتے رہا کرو اور (خوب جوش و خروش سے) ان کے لئے سلامتی مانگتے رہا کرو۔ " WHERE ayatNumber = 57 AND chapterNumber = 33;
UPDATE verses SET ts = "وہ لوگ جو کہ اللہ اور اس کے رسول کو تکلیف دیتے ہیں اللہ ان کو (اس) دنیا میں اور آخرت میں اپنے قرب سے محروم کر دیتا ہے اور اس نے ان کے لئے رسوا کرنے والا عذاب تیار کر چھوڑا ہے۔ " WHERE ayatNumber = 58 AND chapterNumber = 33;
UPDATE verses SET ts = "وہ لوگ جو کہ مومن مردوں اور مومن عورتوں کو بغیر اس کے کہ انہوں نے کوئی قصور کیا ہو تکلیف دیتے ہیں ان لوگوں نے خطرناک جھوٹ اور کھلے کھلے گناہ کا بوجھ اپنے اوپر اٹھا لیا ہے۔ " WHERE ayatNumber = 59 AND chapterNumber = 33;
UPDATE verses SET ts = "اے نبی! اپنی بیویوں اور اپنی بیٹیوں اور مومنوں کی بیویوں سے کہہ دے کہ (جب وہ باہر نکلیں) اپنی بڑی چادروں کو سروں پر سے گھسیٹ کر اپنے سینوں تک لے آیا کریں یہ امر اس بات کو ممکن بنا دیتا ہے کہ وہ پہچانی جائیں اور ان کو تکلیف نہ دی جائے اور اللہ بڑا بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 60 AND chapterNumber = 33;
UPDATE verses SET ts = "اگر منافق مرد اور وہ لوگ جن کے دلوں میں مرض ہے اور وہ لوگ جو مدینہ میں جھوٹی افواہیں پھیلاتے پھرتے ہیں باز نہ آئیں گے تو ہم تجھے ان لوگوں کے خلاف (ایک دن) کھڑا کر دیں گے پھر وہ تیرے ساتھ اس شہر میں بہت ہی تھوڑی مدت تک ہمسائیگت میں بسر کریں گے۔ " WHERE ayatNumber = 61 AND chapterNumber = 33;
UPDATE verses SET ts = "وہ جہاں کہیں بھی تمہارے قابو میں آئیں چاہیے کہ پکڑے جائیں اور قتل کر دئیے جائیں کیونکہ وہ خدا کے رحم سے محروم کر دئیے گئے ہیں۔ " WHERE ayatNumber = 62 AND chapterNumber = 33;
UPDATE verses SET ts = "اللہ کی اس سنت کو (اختیار کرو) جو ان لوگوں میں جاری ہوئی تھی جو تم سے پہلے گذر چکے ہیں اور تو کبھی بھی اللہ کی سنت میں تبدیلی نہیں پائے گا۔ " WHERE ayatNumber = 63 AND chapterNumber = 33;
UPDATE verses SET ts = "لوگ تجھ سے خاتمہ کر دینے والی گھڑی کے متعلق پوچھتے ہیں تو کہہ دے کہ اس کا علم صرف اللہ کو ہے اور تم کو یہ بات کیا معلوم کہ شاید وہ گھڑی قریب ہی ہو۔ " WHERE ayatNumber = 64 AND chapterNumber = 33;
UPDATE verses SET ts = "اللہ نے یقیناً کافروں کو اپنے قرب سے محروم کر دیا ہے اور ان کے لئے ایک بھڑکنے والا عذاب تیار کیا ہے۔ " WHERE ayatNumber = 65 AND chapterNumber = 33;
UPDATE verses SET ts = "جس میں وہ ہمیشہ رہتے چلے جائیں گے (اور وہاں) نہ کوئی دوست پائیں گے اور نہ مددگار۔ " WHERE ayatNumber = 66 AND chapterNumber = 33;
UPDATE verses SET ts = "جس دن ان کے بڑے بڑے آدمیوں کو آگ پر الٹایا پلٹایا جائے گا۔ اور وہ کہیں گے اے کاش! ہم اللہ اور اس کے رسول کی اطاعت کر لیتے۔ " WHERE ayatNumber = 67 AND chapterNumber = 33;
