UPDATE verses SET ts = "اے ہمارے رب! ان کو دہرا عذاب دے اور ان کو اپنے رحم سے دور کر دے۔ " WHERE ayatNumber = 69 AND chapterNumber = 33;
UPDATE verses SET ts = "اے مومنو! تم ان لوگوں کی طرح نہ بنو۔ جنہوں نے موسیٰ کو تکلیف دی تھی۔ جس پر اللہ نے اس کو ان کے اقوال سے بری قرار دیا تھا اور وہ اللہ کے حضور میں بڑی شان رکھتا ہے۔ " WHERE ayatNumber = 70 AND chapterNumber = 33;
UPDATE verses SET ts = "اے مومنو! اللہ کا تقویٰ اختیار  کرو اور وہ بات کہو جو پیچدار نہ ہو‘ (بلکہ سچی ہو) " WHERE ayatNumber = 71 AND chapterNumber = 33;
UPDATE verses SET ts = "(اگر تم ایسا کرو گے) تو اللہ تمہارے اعمال کو درست کر دے گا۔ اور تمہارے گناہوں کو معاف کر دے گا اور جو شخص اللہ اور اس کے رسول کی اطاعت کرے وہ بڑی کامیابی حاصل کرتا ہے۔ " WHERE ayatNumber = 72 AND chapterNumber = 33;
UPDATE verses SET ts = "ہم نے کامل امانت (یعنی شریعت) کو آسمانوں اور زمین اور پہاڑوں کے سامنے پیش کیا تھا۔ لیکن اس کے اٹھانے سے انہوں نے انکار کر دیا اور اس سے ڈر گئے لیکن انسان نے اس کو اٹھالیا۔ وہ یقیناً بہت ظلم کرنے والا (اور) عواقب سے بے پروا تھا۔ " WHERE ayatNumber = 73 AND chapterNumber = 33;
UPDATE verses SET ts = "(ہمارے اس شریعت کے بوجھ لادنے کا) نتیجہ یہ ہوا کہ منافق مردوں اور منافق عورتوں اور مشرک مردوں اور مشرک عورتوں کو اللہ نے عذاب دیا۔ اور مومن مردوں اور مومن عورتوں پر فضل کیا اور اللہ ہے ہی بڑا بخشش کرنے والا (اور) باربار کرم کرنے والا۔ " WHERE ayatNumber = 74 AND chapterNumber = 33;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 34;
UPDATE verses SET ts = "سب تعریف اللہ ہی کی ہے (اور) جو آسمانوں اور زمین میں ہے‘ (سب) اسی کا ہے اور (جس طرح ابتدا میں وہ حمد کا مستحق ہے) انجام کار بھی وہی حمد کا مستحق ہوتا ہے اور وہ بڑی حکمتوں کا مالک ہے (اور) ہر چیز سے خبردار ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 34;
UPDATE verses SET ts = "جو کچھ زمین میں داخل ہوتا ہے اسے بھی وہ جانتا ہے اور جو کچھ اس سے نکلتا ہے‘ اسے بھی جانتا ہے نیز اسے بھی جو آسمان سے اترتا ہے اور اسے بھی جو آسمان کی طرف چڑھتا ہے اور وہ باربار رحم کرنے والا (اور) بہت بخشنے والا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 34;
UPDATE verses SET ts = "اور کافروں نے کہا کہ ہماری قوم کی تباہی کی آخری گھڑی ہم نہیں دیکھیں گے تو کہہ دے ایسا نہیں‘ مجھے اپنے (اس) رب کی قسم جو غیب کا جاننے والا ہے اور آسمانوں اور زمین میں سے کوئی چیز خواہ ایک سرخ چیونٹی کے برابر ہو یا اس سے بھی چھوٹی یا اس سے بڑی ہو اس کی نظروں سے غائب نہیں رہتی۔ یہ گھڑی تم ضرور دیکھو گے کیونکہ وہ ایک ظاہر کر دینے والی تحریر میں محفوظ ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 34;
UPDATE verses SET ts = "(اس گھڑی کا آنا اس لئے ضروری ہے) تا کہ اللہ مومنوں اور ایمان کے مطابق عمل کرنے والوں کو ان کے ایمان اور عمل کے مطابق جزا دے۔ وہ یقیناً ایسے ہیں کہ ان کو خدا کی طرف سے مغفرت اور معزز رزق ہمیشہ پہنچتا رہے گا۔ " WHERE ayatNumber = 5 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ لوگ جو ہمارے نشانوں کے بارے میں (ہم کو) عاجز کرنے کی کوشش کرتے ہیں ان کو ان کے گناہ کی وجہ سے دردناک عذاب پہنچے گا۔ " WHERE ayatNumber = 6 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ لوگ جن کو علم دیا گیا ہے اس چیز کو جو تیری طرف تیرے رب کی طرف سے نازل ہوئی ہے حق جانتے ہیں اور یہ (بھی جانتے ہیں) کہ (وہ تعلیم) غالب اور حمد والے خدا کی طرف لے جاتی ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ (لوگ) جنہوں نے کفر کیا ہے کہتے ہیں کہ (اے لوگو)! کیا ہم تم کو ایک ایسے شخص کا پتہ دیں جو تم کو یہ بتاتا ہے کہ جب تم (مرنے کے بعد) ذرہ ذرہ کر دئیے جائو گے تو پھر جڑ کر تم ایک نئی مخلوق کی صورت میں بدل جائو گے۔ " WHERE ayatNumber = 8 AND chapterNumber = 34;
UPDATE verses SET ts = "کیا وہ شخص اللہ پر جھوٹ باندھ رہا ہے یا اس کو جنون ہے یوں نہیں بلکہ جو لوگ آخرت پر ایمان نہیں لاتے وہ (ایسے برے خیالات کی وجہ سے ابھی سے) عذاب اور خطرناک گمراہی میں مبتلا ہو گئے ہیں۔ " WHERE ayatNumber = 9 AND chapterNumber = 34;
UPDATE verses SET ts = "کیا انہوں نے اس چیز کی طرف نہیں دیکھا جو آسمان اور زمین سے ان کے آگے کی طرف سے اور ان کے پیچھے کی طرف سے ان کو گھیرے ہوئے ہے اگر ہم چاہیں تو ان کو انہی کے ملک میں مصائب میں مبتلا کر دیں یا ان پر بادل کے کچھ ٹکڑے نازل کریں اس میں خدا کی طرف جھکنے والے ہر بندہ کے لئے ایک نشان ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ہم نے  دائود کو اپنی طرف سے فضل عطا کیا تھا (اور کہا تھا کہ) اے پہاڑوں کے رہنے والو! تم بھی اور اے پرندو تم بھی اس کے ساتھ خدا کی تسبیح کرو اور ہم نے اس کے لئے لوہے کو نرم کر دیا تھا۔ " WHERE ayatNumber = 11 AND chapterNumber = 34;
UPDATE verses SET ts = "(اور کہا تھا کہ) پورے سائز کی زرہیں بنائو اور ان کے حلقے چھوٹے بنانا اور (اے دائود کے ساتھیو)! اپنے ایمان کے مطابق عمل کرو‘ میں تمہارے عمل کو دیکھ رہا ہوں۔ " WHERE ayatNumber = 12 AND chapterNumber = 34;
UPDATE verses SET ts = "اور (ہم نے) سلیمان کو ایسی ہوا (عطا کی تھی) جس کا صبح کا چلنا ایک مہینہ کے برابر ہوتا تھا اور شام کا چلنا بھی ایک مہینہ کے برابر ہوتا تھا اور ہم نے اس کے لئے تانبہ کا چشمہ پگھلا دیا تھا اور ہم نے اس کو جنوں کی ایک جماعت بھی عطا کی تھی جو اس کے رب کے حکم سے اس کے تابع فرمان عمل کرتی تھی اور (یہ بھی کہہ دیا تھا کہ) ان میں سے جو کوئی ہمارے حکم سے کجروی اختیار کرے گا ہم اس کو بھڑکتا ہوا عذاب پہنچائیں گے۔ " WHERE ayatNumber = 13 AND chapterNumber = 34;
UPDATE verses SET ts = "وہ جو کچھ چاہتا تھا جن (یعنی سرکش قوموں کے سردار) اس کے لئے بناتے تھے یعنی مساجد اور ڈھلے ہوئے مجسمے اور بڑے بڑے لگن جو حوضوں کے برابر ہوتے تھے اور بھاری بھاری دیگیں جو ہر وقت چولھوں پر دھری رہتی تھیں (اور ہم نے کہا) اے دائود کے خاندان کے لوگو! شکر گذاری کے ساتھ عمل کرو اور میرے بندوں میں سے بہت کم لوگ شکر گذار ہوتے ہیں۔ " WHERE ayatNumber = 14 AND chapterNumber = 34;
UPDATE verses SET ts = "پھر جب ہم نے اس کے لئے موت کے وارد ہونے کا فیصلہ کیا تو ان (یعنی سرکش قوموں) کو اس کی موت کی صرف ایک زمین کے کیڑے نے خبر دی جو اس کے عصاء (حکومت) کو کھا رہا تھا پھر جب وہ گر گیا‘ تو جنوں پر ظاہر ہو گیا کہ اگر ان کو غیب کا علم ہوتا تو وہ ذلت والے عذاب میں پڑے نہ رہتے۔ " WHERE ayatNumber = 15 AND chapterNumber = 34;
UPDATE verses SET ts = "سبا کے لئے ان کے اپنے ملک میں ایک بڑی نشانی موجود تھی (اور وہ) دو باغوں کی صورت میں (تھی) جن میں سے ایک دائیں طرف تھا اور ایک بائیں طرف (تھا) (اور ہم نے ان سے کہہ رکھا تھا کہ) اپنے رب کے رزق کو کھائو اور اس کا شکر ادا کرو (تمہارا) شہر ایک خوبصورت شہر ہے اور (تمہارا) رب بہت بخشنے والا ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 34;
UPDATE verses SET ts = "پھر بھی انہوں نے (حق سے) پیٹھ پھیر لی تب ہم نے (ان کے حق پانے سے مایوس ہو کر) ان پر ایسا سیلاب بھیج دیا جو ہر چیز کو تباہ کرتا جاتا تھا اور ہم نے ان کے دو اعلیٰ درجہ کے باغوں کی جگہ ان کو دو ایسے باغ دئیے جن کے پھل بدمزہ تھے‘ اور جن میں جھائو پایا جاتا تھا یا کچھ تھوڑی سی بیریاں تھیں۔ " WHERE ayatNumber = 17 AND chapterNumber = 34;
UPDATE verses SET ts = "یہ سزا ہم نے ان کو ان کی ناشکری کی وجہ سے دی تھی اور ناشکروں کو ہی ہم ایسا بدلہ دیا کرتے ہیں۔ " WHERE ayatNumber = 18 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ہم نے ان کے درمیان اور ان بستیوں کے درمیان جن کو ہم نے برکت دی تھی‘ (یعنی فلسطین جو سلیمان کا ملک تھا) بہت بستیاں آباد کردی تھیں جو ایک دوسرے کے سامنے اور قریب (قریب) تھیں اور ہم نے ایک بستی سے دوسری بستی تک جانے کا سفر چھوٹا کر دیا تھا اور کہا تھا کہ راتوں کو بھی اور دنوں کو بھی ان میں امن کے ساتھ سفر کرو۔ " WHERE ayatNumber = 19 AND chapterNumber = 34;
UPDATE verses SET ts = "(مگر بجائے شکریہ ادا کرنے کے) انہوں نے (یہ) کہا کہ اے ہمارے رب! ہمارے سفروں کو لمبا کر دے (تا کہ سیرو سفر کا مزہ آئے) اور انہوں نے اپنی جانوں پر ظلم کیا پس ہم نے ان کا نام مٹا دیا اور ان کو قدیم زمانہ کے افسانے بنا دیا‘ اور تباہ کرکے ذرہ ذرہ کر دیا۔ اس میں ہر صبر کرنے والے اور بہت شکر کرنے والے بندہ کے لئے بڑے نشانات ہیں۔ " WHERE ayatNumber = 20 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ابلیس نے ان کے بارہ میں اپنے خیال کو سچا ثابت کر دیا اور مومنوں کے ایک گروہ کو چھوڑ کر بقیہ لوگوں (یعنی کفار) نے اس کی پیروی کی۔ " WHERE ayatNumber = 21 AND chapterNumber = 34;
UPDATE verses SET ts = "حالانکہ اس (یعنی شیطان) کو ان پر کوئی قبضہ حاصل نہیں تھا یہ صرف اس لئے ہوا کہ تا ہم ان کو جو آخرت پر ایمان لانے والے ہیں ان کے مقابلہ میں جو آخرت پر شک رکھتے ہیں ممتاز کرکے ان کی اندرونی حقیقت کھول دیں۔ اور تیرا رب ہر چیز کا نگران ہے۔ " WHERE ayatNumber = 22 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے کہ جن لوگوں کو تم اپنے خیال کے مطابق اللہ کے سوا پکارتے ہو ان کو (اپنی مدد کے لئے) بلا لو وہ آسمانوں اور زمین میں ایک چیونٹی کے برابر بھی کسی چیز کے ہرگز مالک نہیں اور ان دونوں (کی ملکیت) میں ان کو (کوئی) شراکت حاصل نہیں اور نہ اس کا ان میں سے کوئی مددگار ہے۔ " WHERE ayatNumber = 23 AND chapterNumber = 34;
UPDATE verses SET ts = "اور اس (اللہ) کے حضور اس شخص کی شفاعت کے سوا جس کو اللہ نے اجازت دی ہو (یا جس شخص کے حق میں اللہ نے شفاعت کرنے کی اجازت دی ہو) کسی شخص کی شفاعت نفع نہیں بخشتی۔ یہاں تک کہ جب ان (شفاعت کی اجازت پانے والے گروہ) کے دلوں پر سے رعب جاتا رہتا ہے تو دوسرے لوگ ان سے کہتے ہیں کہ ابھی تمہارے رب نے تم سے کیا کہا تھا۔ اس پر وہ جواب دیتے ہیں کہ جو کچھ کہا تھا‘ سچ ہی کہا تھا اور وہ بلند شان اور بڑے اختیارات والا ہے۔ " WHERE ayatNumber = 24 AND chapterNumber = 34;
UPDATE verses SET ts = "(اور) تو کہہ دے (کہ اے لوگو)! آسمانوں اور زمین میں سے تمہیں کون رزق دیتا ہے (اور پھر خود ہی اس سوال کے جواب میں) کہہ اللہ (اور کون) اور میں اور تم یا تو ہدایت پر قائم ہیں یا کھلی کھلی گمراہی میں مبتلا ہیں۔ " WHERE ayatNumber = 25 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے (اگر ہم تمہارے خیال کے مطابق مجرم ہیں تو) ہمارے جرموں کے متعلق تم سے نہیں پوچھا جائے گا اور نہ ہم سے تمہارے اعمال کے متعلق پوچھا جائے گا۔ " WHERE ayatNumber = 26 AND chapterNumber = 34;
UPDATE verses SET ts = "اور یہ بھی کہہ دے کہ ہمارا رب (ایک دن) تم کو اورہم کو (ایک جنگ کے میدان میں) جمع کرے گا پھرحق اورانصاف کے ساتھ ہم دونوں کے درمیان فیصلہ کردے گا اور وہ سب سے بڑا (اور) اچھا فیصلہ کرنے والا ہے اور ہر بات کو خوب جانتا ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 34;
UPDATE verses SET ts = "کہو ان کو مجھے دکھائو تو سہی جن کو تم شریک بنا کر اس کے ساتھ ملاتے ہو (اس کا کوئی شریک بنانا) ہرگز درست نہیں بلکہ حق یہ ہے کہ اللہ غالب (اور) بڑی حکمتوں والا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ہم نے تجھ کو تمام بنی نوع انسان کی طرف (جن میں سے ایک بھی تیرے حلقئہ رسالت سے باہر نہ رہے ایسا) رسول بنا کر بھیجا ہے جو (مومنوں کو) خوشخبری دیتا اور (کافروں کو) ہوشیار کرتا ہے لیکن انسانوں میں سے اکثر اس حقیقت سے واقف نہیں۔ " WHERE ayatNumber = 29 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ (جوش میں آکر) کہہ دیتے ہیں یہ (ساری دنیا تک رسالت کا پیغام دینے کا) وعدہ اگر تم سچے ہو کب پورا ہوگا؟ " WHERE ayatNumber = 30 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے کہ تمہارے لئے ایک دن کی میعاد مقرر ہے نہ تو تم اس سے ایک گھڑی پیچھے رہ سکو گے‘ نہ ایک گھڑی آگے بڑھ سکو گے۔ " WHERE ayatNumber = 31 AND chapterNumber = 34;
UPDATE verses SET ts = "اور کافر کہتے ہیں کہ ہم نہ تو اس قرآن کو مانتے ہیں اور نہ ان پیشگوئیوں کو جو اس کے متعلق کی گئی ہیں اور اگر تو ظالموں کو اس وقت دیکھے جب کہ وہ اپنے رب کے سامنے کھڑے کیے جائیں گے۔ اور ان میں سے بعض بعض سے بحث کر رہے ہوں گے اور کمزور طبقہ متکبر طبقہ سے کہہ رہا ہوگا کہ اگر تم نہ ہوتے تو ہم ضرور ایمان لے آتے۔ " WHERE ayatNumber = 32 AND chapterNumber = 34;
UPDATE verses SET ts = "(تب) متکبر طبقہ جواب میں کمزور طبقہ سے کہے گا‘ کہ جب ہدایت تمہارے پاس آگئی تھی تو کیا ہم نے تمہیں اس سے روکا تھا؟ (ہرگز نہیں) بلکہ تم خود ہی مجرم تھے۔ " WHERE ayatNumber = 33 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ لوگ جن کو ضعیف سمجھا گیا تھا‘ ان لوگوں کو جو متکبر تھے کہیں گے (ہم یونہی مجرم نہیں بن گئے تھے) بلکہ تمہاری رات اور دن کی تدبیروں نے ہم کو ایسا بنا دیا جبکہ تم ہم کو اللہ کے احکام کا انکار کرنے اور اس کا شریک بنانے کی تعلیم دیا کرتے تھے اور جب (ان سرکشیوں کے نتیجہ میں) انہوں نے خطرناک عذاب دیکھا۔ تو (جو لوگ اپنے آپ کو بڑا سمجھتے تھے) انہوں نے (اپنے دل کی) شرمندگی کو چھپانا شروع کیا۔ اور ہم ان میں سے جو پکے کافر ہوں گے ان کی گردنوں میں طوق ڈالیں گے۔ (اور یہ سزا ان کی بدعملیوں سے زیادہ نہیں ہوگی‘ کیا وہ سمجھ نہیں سکتے کہ) ان کو جو سزا دی جائے گی وہ ان کے عمل کے مطابق ہوگی۔ " WHERE ayatNumber = 34 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ہم نے کسی بستی میں کوئی ہوشیار کرنے والا (نبی) نہیں بھیجا کہ اس کے مالداروں نے یہ نہ کہا ہو کہ (اے رسولو)! ہم تمہاری رسالت کے منکر ہیں۔ " WHERE ayatNumber = 35 AND chapterNumber = 34;
UPDATE verses SET ts = "نیز وہ یہ بھی کہتے رہے ہیں کہ ہم تم سے مال اور اولاد میں زیادہ ہیں اور ہم پر کبھی بھی عذاب نازل نہیں ہوگا۔ " WHERE ayatNumber = 36 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے‘ میرا رب جس کے لئے چاہتا ہے رزق (کا دروازہ) کھول دیتا ہے اور جس کے لئے چاہتا ہے تنگ کر دیتا ہے لیکن لوگوں میں سے اکثر جانتے نہیں۔ " WHERE ayatNumber = 37 AND chapterNumber = 34;
UPDATE verses SET ts = "اور تمہارے مال اور تمہاری اولادیں ایسی چیز نہیں کہ تم کو ہمارا مقرب بنا دیں‘ ہاں جو ایمان لاتا ہے اور اس کے مناسب حال عمل کرتا ہے (وہی ہمارا مقرب ہوتا ہے) اور ایسے ہی  لوگوں کوان کے اچھے اعمال کی وجہ سے بڑھ بڑھ کر بدلے ملیں گے اور وہ بالاخانوں میں امن سے زندگی بسر کریں گے۔ " WHERE ayatNumber = 38 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ لوگ جو کہ ہمارے نشانوں کے متعلق ہم کو عاجز کرنے کے لئے کوششیں کرتے ہیں وہ لوگ سخت عذاب کے سامنے پیش کیے جائیں گے۔ " WHERE ayatNumber = 39 AND chapterNumber = 34;
UPDATE verses SET ts = "(اور) تو کہہ دے میرا رب اپنے بندوں میں سے جس کے رزق کو چاہتا ہے وسیع کرتا ہے اور جس کے رزق کو چاہتا ہے تنگ کر دیتا ہے اور جو کچھ بھی تم خرچ کرو‘ وہ اس کا نتیجہ ضرور نکالے گا۔ وہ سب رزق دینے والوں میں سے اچھا (اور کامل) ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 34;
UPDATE verses SET ts = "اور جس دن وہ (اللہ) ان سب کو اکٹھا کرے گا پھر وہ فرشتوں سے کہے گا کیا یہ لوگ تمہاری عبادت کرتے تھے؟ " WHERE ayatNumber = 41 AND chapterNumber = 34;
UPDATE verses SET ts = "وہ کہیں گے کہ تو پاک ہے۔ ان کو چھوڑ کر فقط تو ہمارا دوست ہے (پس یوں نہیں جس طرح کفار کہیں گے) بلکہ حقیقت یہ ہے کہ وہ خیالی اور مخفی ہستیوں کی عبادت کرتے تھے اور ان میں سے اکثر ان ہستیوں پر ایمان لاتے تھے۔ " WHERE ayatNumber = 42 AND chapterNumber = 34;
UPDATE verses SET ts = "پس ان سے کہا جائے گا‘ آج تم میں سے بعض بعض کو نہ نفع پہنچا سکیں گے‘ نہ ضرر۔ اور ہم ظالموں سے کہیں گے‘ کہ اس دوزخ کا عذاب چکھو۔ جس کو تم جھٹلاتے تھے۔ " WHERE ayatNumber = 43 AND chapterNumber = 34;
UPDATE verses SET ts = "اور جب ان کے سامنے ہمارے کھلے کھلے نشانات پڑھ کر سنائے جاتے ہیں تو وہ کہتے ہیں کہ یہ شخص صرف ایسا آدمی ہے جو تم کو ان (ہستیوں) کی عبادت سے روکنا چاہتا ہے‘ جن کی تمہارے باپ دادے عبادت کرتے تھے اور کہتے ہیں یہ (قرآن صرف) جھوٹ ہے جو اپنے نفس سے بنا لیا گیا ہے اور جب کامل سچائی ان کے پاس آجاتی ہے تو کافر مجبور ہو کر یوں کہتے ہیں کہ یہ تو ایک کھلا کھلا فریب ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ہم نے ان کو کوئی (آسمانی) صحیفے نہیں دئیے جن کو وہ پڑھتے ہوں (اور ان میں ایسی بے ہودہ باتیں لکھی ہوں) اور نہ ہم نے ان کی طرف تجھ سے پہلے کوئی (ایسا) ڈرانے والا بھیجا ہے (جس نے ایسی بیہودہ باتیں انہیں سکھائی ہوں) ۔" WHERE ayatNumber = 45 AND chapterNumber = 34;
UPDATE verses SET ts = "اور جو لوگ ان سے پہلے تھے انہوں نے بھی (اپنے رسولوں کو) جھٹلایا ہی تھا اور اس زمانہ کے لوگوں کو اس طاقت کا دسواں حصہ بھی نہیں ملا جو ہم نے ان سے پہلے لوگوں کو دی تھی لیکن ان لوگوں نے (بھی باوجود پہلوں سے ذلیل ہونے کے اور ان کی سزا کو دیکھ لینے کے) ہمارے رسولوں کا انکار ہی کیا۔ سو (اب دیکھ لیں کہ ان حالات میں) میرے انکار کا نتیجہ (یعنی عذاب) کیسا ہوتا ہے۔ " WHERE ayatNumber = 46 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے‘ میں تم کو ایک بات کی نصیحت کرتا ہوں (کم سے کم وہ تو مانو) وہ یہ ہے کہ اللہ کے سامنے دو دو ہو کر اور اکیلے اکیلے کھڑے ہو جائو۔ پھر غور کرو (تو لازماً یہی نتیجہ نکلے گا) کہ تمہارا یہ رسول مجنون نہیں وہ صرف تمہیں آئندہ آنے والے سخت عذاب سے ہوشیار کرنے والا (شخص) ہے۔ " WHERE ayatNumber = 47 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے میں نے تم سے (تبلیغ رسالت کے بدلہ میں) جو بھی اجرت مانگی ہو‘ وہ تم ہی لے لو۔ میرا حق صرف اللہ پر ہے اور وہ ہر چیز کو دیکھ رہا ہے۔ " WHERE ayatNumber = 48 AND chapterNumber = 34;
UPDATE verses SET ts = "(پھر اُن سے) کہہ دے کہ میرا رب یقیناً حق کے ذریعہ سے (جھوٹ کو) ٹکڑے ٹکڑے کر رہا ہے اور وہ ہر غیب کو اچھی طرح جانتا ہے۔ " WHERE ayatNumber = 49 AND chapterNumber = 34;
UPDATE verses SET ts = "(اور) کہہ دے کہ کامل حق آ گیا ہے اور باطل کوئی چیز پیدا نہیں کرتا اور نہ کسی ہلاک شدہ چیز کو واپس لا سکتا ہے۔ " WHERE ayatNumber = 50 AND chapterNumber = 34;
UPDATE verses SET ts = "تو کہہ دے کہ اگر میں گمراہ ہوں تو میری گمراہی کا وبال صرف میری ذات پر پڑے گا اور اگر میں ہدایت پر ہوں تو صرف اس وحی کی وجہ سے ہوں جو میرے رب نے مجھ پر نازل کی ہے وہ یقیناً بہت دعائیں سننے والا (اور) بندے کے پاس ہی موجود رہنے والا ہے۔ " WHERE ayatNumber = 51 AND chapterNumber = 34;
UPDATE verses SET ts = "اور اگر تو اس حالت کو دیکھے جبکہ یہ (خدا کے عذابوں کی وجہ سے) گھبرا جائیں گے اور انہیں بھاگنے کا کوئی رستہ نہ ملے گا اور ایک قریب کے مکان سے وہ پکڑ لئے جائیں گے (تو تجھے معلوم ہو جائے کہ یہ کیسے تھڑدلے ہیں) ۔" WHERE ayatNumber = 52 AND chapterNumber = 34;
UPDATE verses SET ts = "اور کہیں گے ہم اس (کلام) پر ایمان لے آئے لیکن ان کو اس (یعنی ایمان) کا حاصل کرنا اتنی دور جاکر کس طرح نصیب ہو سکتا ہے۔ " WHERE ayatNumber = 53 AND chapterNumber = 34;
UPDATE verses SET ts = "اور وہ اس سے پہلے اس کا انکار کر چکے ہیں اور دور بیٹھے بغیر غور و فکر کے اٹکل پچو اعتراض کرتے رہے ہیں۔ " WHERE ayatNumber = 54 AND chapterNumber = 34;
UPDATE verses SET ts = "اور ان کے درمیان اور ان کی خواہشات کے درمیان روکیں ڈال دی گئیں۔ اسی طرح جس طرح ان کے ہم جنس لوگوں سے ان سے پہلے کیا گیا تھا۔ وہ بھی ایک ایسے شک میں مبتلا تھے جو بے چینی پیدا کرنے والا تھا۔ (اور یہ بھی) ۔" WHERE ayatNumber = 55 AND chapterNumber = 34;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) ۔" WHERE ayatNumber = 1 AND chapterNumber = 35;
UPDATE verses SET ts = "اللہ ہی کی سب تعریف ہے جو آسمانوں اور زمین کو (ایک نئے نظام کے مطابق) پیدا کرنے والا ہے (اور) فرشتوں کو ایسی حالت میں رسول بنا کر بھیجنے والا ہے جبکہ کبھی تو ان کے دو دو پر ہوتے ہیں‘ کبھی تین تین اور کبھی چار چار۔ (اور ان فرشتوں کے پروں کی) پیدائش میں وہ (اللہ) جتنی چاہتا ہے زیادتی بخشتا ہے اللہ ہر ایک چیز پر بہت قادر ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 35;
UPDATE verses SET ts = "جو رحمت کا سامان اللہ لوگوں کے لئے کھولے اسے کوئی بند کرنے والا نہیں‘ اور جس رحمت کے سامان کو وہ بند کرے اس کو خدا کے فعل کے بعد کوئی جاری کرنے والا نہیں۔ اور وہ بڑا غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 35;
UPDATE verses SET ts = "اے لوگو! اللہ کی جو نعمت تم پر نازل ہوئی ہے‘ اس کو یاد کرو۔ کیا اللہ کے سوا کوئی خالق ہے جو آسمان اور زمین سے تم کو رزق دیتا ہو؟ اس کے سوا کوئی معبود نہیں۔ پھر تم کدھر بہکا کر لے جائے جا رہے ہو۔ " WHERE ayatNumber = 4 AND chapterNumber = 35;
UPDATE verses SET ts = "اگر یہ لوگ تجھے جھٹلاتے ہیں تو (کیا حرج ہے) تجھ سے پہلے جتنے رسول گذرے ہیں ان کو بھی جھٹلایا گیا تھا اور تمام معاملات (فیصلہ کے لئے) اللہ کے حضور  ہی پیش کیے جائیں گے۔ " WHERE ayatNumber = 5 AND chapterNumber = 35;
UPDATE verses SET ts = "اے لوگو! اللہ کا وعدہ یقیناً سچا ہے۔ پس تم کو ورلی زندگی دھوکہ میں نہ ڈالے اور کوئی دھوکہ دینے والا تم کو اللہ کے بارہ میں دھوکہ نہ دے۔ " WHERE ayatNumber = 6 AND chapterNumber = 35;
UPDATE verses SET ts = "شیطان تمہارا یقیناً دشمن ہے پس اس کو دشمن ہی سمجھو۔ وہ اپنے ساتھیوں کو صرف اس لئے بلاتا ہے کہ وہ دوزخی بن جائیں۔ " WHERE ayatNumber = 7 AND chapterNumber = 35;
UPDATE verses SET ts = "وہ لوگ جنہوں نے کفر کیا ہے ان کے لئے سخت عذاب مقدر ہے اور وہ لوگ جو ایمان لائے اور ایمان کے مطابق انہوں نے کام بھی کیے‘ ان کے حصے میں بخشش اور بہت بڑا انعام ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 35;
UPDATE verses SET ts = "کیا جس شخص کے لئے اس کی بداعمالی خوبصورت کرکے دکھائی گئی ہو اور وہ اس کو اچھا سمجھتا ہو (وہ ہدایت پا سکتا ہے؟) پھر یاد رکھو کہ اللہ جس کو چاہتا ہے (یعنی اس کے قابل پاتا ہے) اسے ہلاک کر دیتا ہے اور جس کو چاہتا ہے (یعنی قابل پاتا ہے) اس کو کامیابی کا راستہ دکھا دیتا ہے۔ پس تیری جان ان کی وجہ سے حسرت و غم کے باعث ہلاک نہ ہو جائے اللہ ان کے اعمال کو خوب جانتا ہے (پس خدا کی سزا عمل کے عین مطابق ہوتی ہے بلاوجہ نہیں ہوتی) ۔" WHERE ayatNumber = 9 AND chapterNumber = 35;
UPDATE verses SET ts = "اور اللہ وہ ہے جو ہوائیں بھیجتا ہے جو بادل کو اٹھاتی ہیں۔ پھر ہم اس کو ایک مردہ ملک کی طرف ہانک کر لے جاتے ہیں اور اس کے ذریعہ سے زمین کو اس کی ویرانی کے بعد آباد کر دیتے ہیں اسی طرح موت کے بعد اٹھنے کا قانون مقرر ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 35;
UPDATE verses SET ts = "جو کوئی عزت چاہتا ہے اسے یاد رہے کہ عزت سب اللہ کے ہاتھ میں ہے‘ پاک باتیں اسی کی طرف چڑھ کر جاتی ہیں اور ایمان کے مطابق عمل ان کو اٹھاتا ہے اور جو لوگ (تمہارے خلاف) بری تدبیریں کرتے ہیں ان کے لئے سخت عذاب (مقدر) ہے اور ان لوگوں کی تدبیر ہی تباہ ہونے والے شے ہے (نہ کہ تم) ۔" WHERE ayatNumber = 11 AND chapterNumber = 35;
UPDATE verses SET ts = "اور اللہ نے تم کو مٹی سے پیدا کیا ہے اس کے بعد نطفہ سے۔ پھر جوڑوں کی شکل میں بنایا ہے۔ اور کسی عورت کو حمل نہیں ہوتا اور نہ وہ بچہ جنتی ہے مگر وہ خدا کے علم کے مطابق ہوتا ہے اور نہ کوئی بڑی عمر والا بڑی عمر پاتا ہے اور نہ کسی کی عمر میں کوئی کمی کی جاتی ہے  مگر وہ ایک قانون کے مطابق ہے۔ یہ بات اللہ کے لئے بالکل آسان ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 35;
UPDATE verses SET ts = "اور ایسے دو سمندر (یا دریا) ہرگز برابر نہیں ہو سکتے کہ (ان میں سے) ایک تو میٹھا اور پسندیدہ ہو اور اس کا پانی آسانی سے گلے سے اتر جاتا ہو اور ایک (دوسرا) نمکین گلا جلا دینے والا ہو اور تم (اس کے باوجود) ان دونوں سمندروں (یا دریائوں) سے تازہ بتازہ گوشت کھاتے ہو اور ان میں سے زیور کے طور پر استعمال ہونے والی چیزیں نکالتے ہو۔ (یعنی موتی وغیرہ) جن کو تم پہنتے ہو اور تم کشتیوں کو دیکھتے ہو کہ ان میں لہروں کو چیرتی ہوئی چلی جاتی ہیں تا کہ تم اس (خدا) کے فضل کی تلاش کرو اور تاکہ تم شکر گذار بنو۔ " WHERE ayatNumber = 13 AND chapterNumber = 35;
UPDATE verses SET ts = "وہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور سورج اور چاند کو اس نے (تمام مخلوق کی) خدمت میں لگایا ہوا ہے ان میں سے ہر ایک مقررہ مدت کے لئے چلتا چلا جا رہا ہے۔ یہ ہے تمہارا رب۔ بادشاہت اسی کی ہے اور جن کو تم اس کے سوا پکارتے ہو وہ کھجور کی گٹھلی کے درمیانی چھلکے کے برابر بھی کسی چیز کے مالک نہیں۔ " WHERE ayatNumber = 14 AND chapterNumber = 35;
UPDATE verses SET ts = "اگر تم ان کو بلائو تو وہ کبھی بھی تمہاری دعا نہیں سنیں گے اور اگر سن بھی لیں تو تمہارے فائدے کے لئے اسے قبول نہیں کر سکیں گے اور پھر قیامت کے دن تمہارے شرک کا انکار کر دیں گے اور تجھ کو واقف کار سے زیادہ اچھی خبر کوئی نہیں دے سکتا۔ " WHERE ayatNumber = 15 AND chapterNumber = 35;
UPDATE verses SET ts = "اے لوگو! تم اللہ کے محتاج ہو اور اللہ کسی کا محتاج نہیں‘ بلکہ سب تعریفوں کا مالک ہے (یعنی دوسروں کی حاجتیں پوری کرتا ہے) ۔" WHERE ayatNumber = 16 AND chapterNumber = 35;
UPDATE verses SET ts = "اگر وہ چاہے تو تم سب کو تباہ کر دے اور ایک نئی مخلوق پیدا کر دے۔ " WHERE ayatNumber = 17 AND chapterNumber = 35;
UPDATE verses SET ts = "اور اللہ پر یہ بات ہرگز دشوار نہیں۔ " WHERE ayatNumber = 18 AND chapterNumber = 35;
UPDATE verses SET ts = "اور کوئی بوجھ اٹھانے والی جان دوسرے کا بوجھ نہیں اٹھا سکتی اور اگر کوئی بوجھ سے دبا ہوا اپنے بوجھ کے اٹھانے کے لئے کسی کو پکارے تو اس کا ذرا سا بھی بوجھ نہیں اٹھایا جائے گا خواہ وہ کتنا ہی قریبی کیوں نہ ہو تو تو صرف ان لوگوں کو ہوشیار کرتا ہے جو اپنے رب سے علیحدگی میں بھی ڈرتے ہیں اور نمازیں (باشرائط) ادا کرتے ہیں اور جو شخص پاک ہوتا ہے وہ صرف اپنی جان کے فائدہ کے لئے پاک ہوتا ہے اور آخرکارسب کو اللہ ہی کی طرف لوٹ کر جانا ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 35;
UPDATE verses SET ts = "اور اندھا اور آنکھوں والا برابر نہیں ہو سکتے۔ " WHERE ayatNumber = 20 AND chapterNumber = 35;
UPDATE verses SET ts = "اور نہ اندھیرے اور نور (برابر ہو سکتے ہیں) ۔" WHERE ayatNumber = 21 AND chapterNumber = 35;
UPDATE verses SET ts = "اور نہ سایہ اور دھوپ (برابر ہو سکتے ہیں) ۔" WHERE ayatNumber = 22 AND chapterNumber = 35;
UPDATE verses SET ts = "اور نہ زندے اور مردے (برابر ہو سکتے ہیں) اللہ یقیناً جس کو چاہتا ہے سنا سکتا ہے مگر تو ان کو نہیں سنا سکتا‘ جو قبروں میں دبے ہوئے ہیں۔ " WHERE ayatNumber = 23 AND chapterNumber = 35;
UPDATE verses SET ts = "تو تو صرف ایک ہوشیار کرنے والا ہے۔ " WHERE ayatNumber = 24 AND chapterNumber = 35;
UPDATE verses SET ts = "ہم نے تجھے ایک قائم رہنے والی صداقت کے ساتھ ایک خوش خبری دینے والا اور ہوشیار کرنے والا بنا کر بھیجا ہے اور کوئی قوم ایسی نہیں جس میں (خدا کی طرف سے) کوئی ہوشیار کرنے والا نہ آیا ہو۔ " WHERE ayatNumber = 25 AND chapterNumber = 35;
UPDATE verses SET ts = "اور اگر یہ لوگ تجھے جھٹلاتے ہیں تو ان لوگوں نے بھی جو ان سے پہلے گذرے تھے (اپنے وقت کے رسولوں کو) جھٹلایا تھا۔ ان کے پاس بھی ان کے زمانہ کے رسول دلائل کے ساتھ اور بعض تعلیمات کے ساتھ آئے تھے اور (ان میں سے بعض) روشن کرنے والی کتاب کے ساتھ (بھی آئے تھے) ۔" WHERE ayatNumber = 26 AND chapterNumber = 35;
UPDATE verses SET ts = "پھر میں نے کافروں کو ان کے جھٹلانے کے سبب (عذاب سے) پکڑ لیا۔ سو (اب دیکھ لیں کہ ان حالات میں) میرے انکار کا نتیجہ (یعنی عذاب) کیسا ہوتا ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 35;
UPDATE verses SET ts = "کیا تو نے دیکھا نہیں کہ اللہ نے بادل سے پانی اتارا ہے پھر ہم نے اس سے مختلف رنگ کے پھل پیدا کیے ہیں اور پہاڑوں میں سے مختلف رنگوں کے پہاڑ ہوتے ہیں (جو ایک دوسرے سے ممتاز ہوتے ہیں) بعض سفید بعض سرخ مختلف رنگوں کے اور کالے سیاہ بھی۔ " WHERE ayatNumber = 28 AND chapterNumber = 35;
UPDATE verses SET ts = "اور لوگوں اور چارپایوں اور جانوروں میں سے بھی بعض ایسے ہوتے ہیں کہ ان میں سے ہر ایک کا رنگ دوسرے سے جدا ہوتا ہے۔ حقیقت اسی طرح ہے (اور) اللہ کے بندوں میں سے صرف علماء اس سے ڈرتے ہیں۔ اللہ بڑا غالب (اور) بہت بخشنے والا ہے۔ " WHERE ayatNumber = 29 AND chapterNumber = 35;
UPDATE verses SET ts = "وہ لوگ جو اللہ کی کتاب کو پڑھتے ہیں اور نماز (باجماعت) ادا کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے خفیہ بھی اور ظاہر بھی خرچ کرتے ہیں وہی درحقیقت ایسی تجارت کی جستجو میں لگے ہوئے ہیں جو کبھی تباہ نہیں ہوگی۔ " WHERE ayatNumber = 30 AND chapterNumber = 35;
UPDATE verses SET ts = "کیونکہ اس کا نتیجہ یہ ہوگا کہ اللہ ان کو ان کے اعمال کے پورے پورے اجر دے گا اور ان کو (ان کے اعمال کے اجر کے علاوہ) اپنے فضل سے اور بھی زیادہ دے گا (جس کی وجہ سے ان کی حالت اس دنیا سے ہزاروں گنے بہتر ہوگی) اس لئے کہ وہ (خدا) بہت بخشنے والا (اور) بہت قدر کرنے والا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 35;
UPDATE verses SET ts = "اور وہ بات جو ہم نے اس قرآن میں سے تجھ پر وحی کے ذریعہ سے بھیجی ہے بالکل سچی ہے اور پوری ہو کر رہنے والی ہے اور اس سے پہلے جو وحی آچکی ہے اس کو پورا کرنے والی ہے اللہ اپنے بندوں سے یقیناً باخبر ہے (اور) ان کی حالت کو دیکھتا ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 35;
UPDATE verses SET ts = "پھر (حقیقت یہ ہے کہ نزول وحی کے بعد) ہم نے اپنی کتاب کا (ہمیشہ) انہی لوگوں کو وارث بنایا ہے جن کو ہم نے اپنے بندوں میں سے چن لیا‘ پس ان میں سے کوئی کوئی آدمی تو ایسا ثابت ہوا جو اپنی جان پر انتہائی ظلم کرنے والا تھا اور کوئی ان میں سے ایسا نکلا جو درمیانی چال چلنے والا تھا اور کوئی کوئی ایسا نکلا جو نیکیوں میں اللہ کے حکم سے دوسروں سے آگے بڑھنے والا تھا۔ یہ (اللہ کا) بڑا فضل ہے۔ " WHERE ayatNumber = 33 AND chapterNumber = 35;
UPDATE verses SET ts = "(اس مقام کا بدلہ) ہمیشہ رہنے والی وہ جنتیں ہونگی جن میں وہ (لوگ) داخل ہونگے (اور) ان میں ان کو سونے کے کنگن پہنائے جائیں گے اور موتی (کے زیور) بھی اور ان کا لباس اس میں ریشم (کا بنا ہوا) ہوگا۔ " WHERE ayatNumber = 34 AND chapterNumber = 35;
UPDATE verses SET ts = "اور وہ کہیں گے کہ سب تعریف اللہ ہی کی ہے جس نے ہمارا غم دور کر دیا ہمارا رب یقیناً بہت بخشنے والا (اور) بہت قدر دان ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 35;
UPDATE verses SET ts = "وہ (خدا) جس نے اپنے فضل سے ہم کو ٹھہرنے کے لئے ایک ایسی جگہ پر رکھا ہے کہ جس میں نہ تو ہم کو کوئی تکلیف ہوتی ہے اور نہ  تھکان۔ " WHERE ayatNumber = 36 AND chapterNumber = 35;
UPDATE verses SET ts = "اور وہ لوگ جنہوں نے کفر کیا ہے انہیں جہنم کی آگ جلائے گی۔ نہ تو ان پر موت کا فیصلہ صادر ہوگا کہ وہ مر جائیں اور نہ جہنم کے عذاب میں ان کی خاطر کوئی کمی کی جائے گی۔ ہم ہر ناشکرے کے ساتھ ایسا ہی معاملہ کیا کرتے ہیں۔ " WHERE ayatNumber = 37 AND chapterNumber = 35;
UPDATE verses SET ts = "اور وہ اس (جہنم) میں چلائیں گے (اور کہیں گے) اے ہمارے رب! ہم کو اس جہنم سے نکال دے تو ہم (مومنوں کی طرح) نیک کام کریں گے‘ ان سے مختلف جو ہم پہلی زندگی میں کیا کرتے تھے (ہم ان سے کہیں گے) کیا ہم نے تم کو اتنی عمر نہیں دی تھی جس میں نصیحت حاصل کرنے کا خواہش مند نصیحت حاصل کر لیتا ہے اور تمہارے پاس ہوشیار کرنے والے (بھی) تو آئے تھے (پھر تم نے ان کی بات کیوں نہ مانی) پس (اب یہ) عذاب چکھو کہ ظالموں کا کوئی مددگار نہیں ہوتا۔ " WHERE ayatNumber = 38 AND chapterNumber = 35;
UPDATE verses SET ts = "اللہ آسمانوں اور زمین کے غیب کو جاننے والا ہے۔ وہ دلوں کی باتوں کو بھی خوب اچھی طرح جانتا ہے۔ " WHERE ayatNumber = 39 AND chapterNumber = 35;
UPDATE verses SET ts = "وہی ہے جس نے تم کو دنیا میں (پہلے لوگوں کا) قائم مقام بنایا۔ پس جو انکار کرے گا اس کے انکار کی سزا اسی پر پڑے گی اور کافروں کا کفر انہیں ان کے رب کی نظر میں صرف خفگی میں ہی بڑھاتا ہے اور کافروں کا کفر ان کو صرف گھاٹے میں ہی بڑھاتا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 35;
UPDATE verses SET ts = "تو کہہ دے مجھے بتائو تو سہی اپنے (مزعومہ) شریک جن کو تم اللہ کے سوا پکارتے ہو (اور اگر وہ نظر نہیں آتے تو) مجھے ان کی وہ مخلوق دکھائو جو انہوں نے زمین میں پیدا کی ہے‘ یا کیا ان کی آسمانوں کے بنانے میں کوئی شراکت ہے؟ کیا ہم نے ان کو کوئی کتاب دی ہے؟ کہ وہ اس کی بیان کردہ دلیل اپنے ساتھ رکھتے ہیں  (کہو ایسا ہرگز نہیں)بلکہ ظالم آپس میں ایک دوسرے سے صرف دھوکہ دینے والے وعدے کرتے ہیں۔ " WHERE ayatNumber = 41 AND chapterNumber = 35;
UPDATE verses SET ts = "اللہ ہی نے آسمانوں اور زمین کو اس بات سے روک رکھا ہے کہ وہ اپنی جگہ سے  ٹل جائیں اور اگر وہ ٹل جائیں تو اس کے بعد کوئی ان کو (تباہ ہونے سے) روک نہیں سکے گا یقیناً وہ بڑی سمجھ بوجھ والا (اور) بہت معاف کرنے والا ہے۔ " WHERE ayatNumber = 42 AND chapterNumber = 35;
UPDATE verses SET ts = "اور وہ اللہ کی پختہ قسمیں کھاتے ہیں کہ اگر ان کے پاس کوئی نبی آجائے تو وہ تمام امتوں میں سے ہر ایک سے زیادہ ہدایت پانے والے بن جائیں گے مگر جب ان کے پاس نبی آیا تو اس کا آنا انہیں صرف نفرت میں بڑھانے کا موجب ہوا۔ " WHERE ayatNumber = 43 AND chapterNumber = 35;
UPDATE verses SET ts = "کیونکہ وہ زمین میں بڑا بننا چاہتے تھے اور بری تدبیریں کرنا چاہتے تھے اور بری تدبیر اس کے کرنے والوں کو ہی ہلاک کیا کرتی ہے۔ پس کیا وہ صرف پہلوں کی سنت (یعنی عذاب) کا تو انتظار نہیں کر رہے (اور اگر تو غور کرے) تو اللہ کی سنت میں کبھی تبدیلی نہیں پائے گا اور نہ تو کبھی سنت اللہ کو ٹلتے ہوئے دیکھے گا۔ " WHERE ayatNumber = 44 AND chapterNumber = 35;
UPDATE verses SET ts = "کیا وہ زمین میں نہیں پھرے (اگر ایسا کرتے) تو دیکھتے کہ ان سے پہلے لوگوں کا کیا انجام ہوا؟ حالانکہ وہ ان سے طاقت میں زیادہ تھے۔ اور کوئی چیز اللہ کو آسمانوں میں اور زمین میں اپنے ارادہ میں ناکام نہیں کر سکتی۔ وہ یقیناً بہت جاننے والا (اور) بڑی قدرت والا ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 35;
UPDATE verses SET ts = "اور اگر اللہ لوگوں کو ان کے کاموں کی وجہ سے پکڑنا شروع کر دیتا تو زمین کی سطح پر کسی جان دار کو باقی نہ چھوڑتا۔ لیکن وہ ان کو ایک مدت تک ڈھیل دیتا ہے پھر جب ان کی مقررہ مدت آجاتی ہے تو (ثابت ہو جاتا ہے کہ) اللہ اپنے بندوں کو خوب دیکھ رہا تھا۔ " WHERE ayatNumber = 46 AND chapterNumber = 35;
