UPDATE verses SET ts = "اس غرض سے کہ تو اس قوم کو ہوشیار کرے جن کے باپ دادوں کو ہوشیار نہیں کیا گیا تھا اور وہ غافل پڑے تھے۔ " WHERE ayatNumber = 7 AND chapterNumber = 36;
