UPDATE verses SET ts = "ہم نے ان کی گردنوں میں طوق ڈالے ہوئے ہیں اور وہ ان کی ٹھوڑیوں تک چڑھ گئے ہیں اور وہ (دکھ سے بچنے کے لئے اپنی) گردنیں اونچی کر رہے ہیں۔ " WHERE ayatNumber = 9 AND chapterNumber = 36;
UPDATE verses SET ts = "اور ہم نے ان کے آگے بھی ایک روک بنا دی ہے اور ان کے پیچھے بھی ایک روک بنا دی ہے اور ان کو ڈھانک دیا ہے۔ پس وہ دیکھ نہیں سکتے۔ " WHERE ayatNumber = 10 AND chapterNumber = 36;
UPDATE verses SET ts = "اور تیرا ڈرانا یا نہ ڈرانا ان کے لئے برابر ہے وہ (جب تک اپنے دل میں تبدیلی پیدا نہ کریں گے) ایمان نہیں لائیں گے۔ " WHERE ayatNumber = 11 AND chapterNumber = 36;
UPDATE verses SET ts = "تو تو صرف اس شخص کو ہوشیار کر سکتا ہے جو یاددہانی کو مان لیا کرتا ہے اور رحمن (خدا) سے علیحدگی میں بھی ڈرتا ہے۔ پس ایسے شخص کو تو ایک عظم الشان معافی اور ایک عزت والے بدلہ کی خبر سنا دے۔ " WHERE ayatNumber = 12 AND chapterNumber = 36;
UPDATE verses SET ts = "ہم ہی مردوں کو زندہ کرتے ہیں اور جو کچھ وہ آئندہ زندگی کے لئے آگے بھیجتے ہیں اس کو بھی محفوظ رکھتے ہیں اور جو اعمال انہوں نے دنیا میں کیے تھے ان کے جو نتائج نکلیں گے ان کو بھی ہم محفوظ رکھتے ہیں اور ہر چیز کو ہم نے ایک کھلی کتاب میں گن رکھا ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 36;
UPDATE verses SET ts = "اور تو ان کے سامنے ایک گائوں والوں کی حالت بیان کر جب کہ ان کے پاس ان کے رسول آئے۔ " WHERE ayatNumber = 14 AND chapterNumber = 36;
UPDATE verses SET ts = "یعنی جس وقت ہم نے پہلے تو ان کی طرف دو رسول بھیجے لیکن انہوں نے ان دونوں کا انکار کر دیا‘ اس پر ہم نے پہلے دو رسولوں کو ایک تیسرا رسول بھیج کر طاقت بخشی پھر ان سب نے مل کر اپنی قوم سے کہا کہ ہم تمہاری طرف ایک پیغام لیکر تمہارے رب کی طرف سے بھیجے گئے ہیں۔ " WHERE ayatNumber = 15 AND chapterNumber = 36;
UPDATE verses SET ts = "انہوں نے (جواب میں) کہا کہ تم تو ہمارے جیسے آدمی ہو اور رحمن خدا نے کوئی چیز (بطور الہام کے) نہیں اتاری‘ تم صرف جھوٹ بول رہے ہو۔ " WHERE ayatNumber = 16 AND chapterNumber = 36;
UPDATE verses SET ts = "انہوں نے کہا‘ کہ ہمارا رب اس بات کو جانتا ہے کہ ہم تمہاری طرف رسول بنا کر بھیجے گئے ہیں۔ " WHERE ayatNumber = 17 AND chapterNumber = 36;
UPDATE verses SET ts = "اور ہمارا کام صرف یہ ہے کہ ہم کھلی کھلی تبلیغ کریں۔ " WHERE ayatNumber = 18 AND chapterNumber = 36;
UPDATE verses SET ts = "اس پر کفار نے کہا‘ ہم تو تمہارا اپنی طرف آنا منحوس قرار دیتے ہیں اگر تم اپنی باتوں سے رکو گے نہیں‘ تو ہم تم کو سنگسار کر دیں گے اور تم کو ہماری طرف سے دردناک عذاب پہنچے گا۔ " WHERE ayatNumber = 19 AND chapterNumber = 36;
UPDATE verses SET ts = "انہوں نے کہا کہ تمہارا عمل تو تمہارے ساتھ ہے (تم جہاں بھی ہوگے تمہارے عملوں کا بدنتیجہ نکلتا رہے گا) کیا تم یہ بات اس لئے کہتے ہو کہ ہم تم کو اچھے کام یاد دلاتے ہیں بلکہ حق یہ ہے کہ تم حد سے گذرنے والی قوم ہو (اس لئے لازما سزا پائو گے) " WHERE ayatNumber = 20 AND chapterNumber = 36;
