UPDATE verses SET ts = "اور اسی (گروہ) کے مشابہ کچھ اور گروہ بھی ہونگے (جن کے اعمال آپس میں ملتے جلتے ہونگے) ۔" WHERE ayatNumber = 59 AND chapterNumber = 38;
UPDATE verses SET ts = "(ان میں سے ایک جماعت ایک پہلی جماعت کی طرف اشارہ کرکے کہے گی) یہ بھی ایک گروہ ہے جو تمہارے ساتھ جہنم میں داخل ہوگا (اور) ان کو کوئی خوش آمدید کہنے والا نہ ہوگا وہ ضرور جہنم میں داخل ہونگے۔ " WHERE ayatNumber = 60 AND chapterNumber = 38;
UPDATE verses SET ts = "(اس پر) وہ گروہ (جس سے یہ بات کہی جائے گی) کہے گا‘ ہمارا ذکر چھوڑو۔ تم بھی ایسے لوگ ہو کہ جن کو خوش آمدید کہنے والا کوئی نہیں تم نے (ہم کو بہکا بہکا کر) اس (دوزخ) کو ہمارے لئے آگے بھیجا تھا اور وہ بہت برا ٹھکانا ہے ۔" WHERE ayatNumber = 61 AND chapterNumber = 38;
UPDATE verses SET ts = "(اس پر) وہ گروہ (جس سے وہ یہ بات کہیں گے) کہے گا۔ اے ہمارے رب! جس کسی نے (بھی تیری نظر میں) ہمارے لئے اس دوزخ کو آگے بھیجا ہے (یعنی ہمارے لئے ممکن بنایا ہے) تو اس کو آگ میں زیادہ سے زیادہ عذاب دے۔ " WHERE ayatNumber = 62 AND chapterNumber = 38;
UPDATE verses SET ts = "اور (اس وقت دوزخی) کہیں گے ہمیں کیا ہوا کہ ہم آج ان لوگوں کو نہیں دیکھتے جن کو ہم برا قرار دیا کرتے تھے۔ " WHERE ayatNumber = 63 AND chapterNumber = 38;
UPDATE verses SET ts = "کیا ہم ان کو (یونہی اپنے دلی خیال کی وجہ سے) حقیر سمجھتے تھے یا اس وقت ہماری آنکھیں کج ہو گئی ہیں (اور وہ ہمیں نظر نہیں آتے) ۔" WHERE ayatNumber = 64 AND chapterNumber = 38;
UPDATE verses SET ts = "دوزخیوں کا یہ جھگڑا ایک سچی بات ہے اور ایسا ہی ہو کر رہے گا۔ " WHERE ayatNumber = 65 AND chapterNumber = 38;
UPDATE verses SET ts = "تو (ان سے) کہہ دے کہ میں تو صرف ایک ڈرانے والا ہوں۔ اللہ کے سوا کوئی معبود نہیں‘ وہ اکیلا (اور) غالب ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 38;
