UPDATE verses SET ts = "اور جزا سزا کے وقت تک تجھ پر ہماری دھتکار پڑتی رہے گی۔ " WHERE ayatNumber = 79 AND chapterNumber = 38;
UPDATE verses SET ts = "اس پر اس نے کہا اے میرے رب! (اگر ایسا ہی کرنا ہے) تو مجھے اس وقت تک مہلت دے جب تک ان کو زندہ کرکے پھر کھڑا کر دیا جائے۔ " WHERE ayatNumber = 80 AND chapterNumber = 38;
