UPDATE verses SET ts = "کہ میں جہنم کو تجھ سے اور ان میں سے جو تیری اتباع کریں گے‘ سب سے بھر دوں گا۔ " WHERE ayatNumber = 86 AND chapterNumber = 38;
UPDATE verses SET ts = "تو کہہ دے کہ میں اس (تبلیغ) پر تم سے کوئی اجر نہیں مانگتا اور نہ میں تکلف سے بات کرنے کا عادی ہوں۔ " WHERE ayatNumber = 87 AND chapterNumber = 38;
UPDATE verses SET ts = "یہ (قرآن) تو سب جہانوں کے لئے ایک نصیحت کی کتاب ہے۔ " WHERE ayatNumber = 88 AND chapterNumber = 38;
UPDATE verses SET ts = "اور تم کچھ عرصہ کے بعد اس کی خبر کو سن لو گے۔ " WHERE ayatNumber = 89 AND chapterNumber = 38;
