UPDATE verses SET ts = "اس کتاب کا اترنا اللہ کی طرف سے ہے جو غالب (اور) سب کام حکمتوں کے ماتحت کرنے والاہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 39;
UPDATE verses SET ts = "ہم نے تیری طرف یہ کتاب (یعنی قرآن) کامل سچائیوں پر مشتمل اتاری ہے‘ پس تو اطاعت کو اللہ کے لئے خالص کرتے ہوئے اس کی عبادت کر۔ " WHERE ayatNumber = 3 AND chapterNumber = 39;
UPDATE verses SET ts = "سنو‘ اطاعت خالص اللہ ہی کا حق ہے اور جو لوگ اس کے سوا اور ہستیوں کو دوست بناتے ہیں (یہ کہتے ہوئے) کہ ہم ان کی عبادت صرف اس لئے کرتے ہیں کہ وہ ہمیں اللہ کے قریب کر دیں۔ اللہ ان کے درمیان ان باتوں کے متعلق جن میں وہ اختلاف رکھتے ہیں فیصلہ کرے گا۔ اللہ یقیناً جھوٹے اور ناشکر گذار کو کامیابی کا راستہ نہیں دکھاتا۔ " WHERE ayatNumber = 4 AND chapterNumber = 39;
UPDATE verses SET ts = "اگر اللہ بیٹا بنانے کا ارادہ رکھتا تو اپنی مخلوق میں سے جس کو چاہتا چن لیتا وہ (حقیقی بیٹے کے نقص سے) پاک ہے۔ بات یہ ہے کہ اللہ ایک ہے (اور) غالب ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 39;
UPDATE verses SET ts = "اس نے آسمانوں اور زمین کو ایک کامل مصلحت کے ساتھ پیدا کیا ہے وہ رات کو دن پر اڑھا دیتا ہے اور دن کو رات پر اُڑھا دیتا ہے اور سورج اور چاند کو اس نے ایک (قانون کا پابند بنا کر) کام پر لگا رکھا ہے دونوں (میں سے ہر ایک) مقررہ مدت کے لئے ایک راستہ پر چل رہے ہیں۔ سنو! وہ بڑا غالب اور بڑا بخشنے والا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 39;
UPDATE verses SET ts = "تم کو اس نے ایک جان سے پیدا کیا ہے۔ پھر اُس نے اسی جنس میں سے اُس کا جوڑا بنایا اور تمہارے لئے چار پایوں میں سے اس نے آٹھ جوڑے بنائے ہیں وہ تم کو تمہاری مائوں کے پیٹوں میں پیدا کرتا ہے (یعنی) ایک پیدائش کے بعد دوسری پیدائش میں بدلتے ہوئے تین ظلمتوں میں سے گذار کر۔ یہ اللہ تمہارا رب ہے‘ بادشاہت اسی کے قبضہ میں ہے صرف وہی ایک معبود ہے پس تم کہاں پھیرے جاتے ہو۔ " WHERE ayatNumber = 7 AND chapterNumber = 39;
UPDATE verses SET ts = "اگر تم کفر کرو تو اللہ تمہارا محتاج نہیں‘ اور وہ اپنے بندوں کے لئے کفر کو کبھی پسند نہیں کرتا‘ اور اگر شکر کرو تو وہ اس کو تمہارے لئے پسند کرتا ہے اور کوئی بوجھ اٹھانے والی ہستی دوسرے کے بوجھ کو نہیں اٹھا سکتی اور تم سب کو اپنے رب کی طرف ہی لوٹ کر جانا ہوگا۔ تب وہ تمہیں تمہارے اعمال سے خبردار کرے گا وہ سینوں کے اندر کی چھپی ہوئی باتوں کو جاننے والا ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جب کبھی انسان کو کوئی تکلیف پہنچتی ہے وہ اپنے رب کی طرف متوجہ ہوتے ہوئے اسے پکارتا ہے پھر جب وہ انسان کو اپنی طرف سے نعمت عطا کر دیتا ہے تو انسان اس غرض کو بھول جاتا ہے جس کے لئے وہ خدا کو پکارتا تھا اور خدا کے شریک مقرر کر دیتا ہے تا کہ اس کے راستہ سے (لوگوں کو) گمراہ کر دے۔ تو کہہ دے (اے انسان) کچھ دیر اپنے کفر کی وجہ سے فائدہ اٹھالے۔ آخر کار تو دوزخ میں پڑنے والا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا جو شخص رات کی گھڑیوں میں سجدہ اور قیام کی صورت میں فرمانبرداری کا نمونہ دکھاتا ہے اور آخرت سے ڈرتا ہے اور اپنے رب کی رحمت کی امید رکھتا ہے (وہ نافرمان کی طرح ہو سکتا ہے) تو کہہ دے کیا علم والے لوگ اور جاہل برابر ہو سکتے ہیں؟ نصیحت تو صرف عقل مند لوگ حاصل کیا کرتے ہیں۔ " WHERE ayatNumber = 10 AND chapterNumber = 39;
UPDATE verses SET ts = "(اسی طرح) کہہ دے کہ اے میرے مومن بندو! اپنے رب کا تقویٰ اختیار کرو۔ وہ لوگ جو اس دنیا میں (الٰہی) احکام کو پوری طرح ادا کرتے ہیں ان کے لئے (اگلے جہان میں) نیک بدلہ مقرر ہے اور اللہ کی زمین بہت وسیع ہے۔ صابروں کو ان کا اجر بغیر حساب کے پورا پورا دیا جائے گا۔ " WHERE ayatNumber = 11 AND chapterNumber = 39;
UPDATE verses SET ts = "تو کہہ دے کہ مجھے حکم دیا گیا ہے کہ میں اللہ کی اس طرح عبادت کروں کہ اطاعت صرف اسی کے لئے مخصوص کردوں۔ " WHERE ayatNumber = 12 AND chapterNumber = 39;
UPDATE verses SET ts = "اور مجھے یہ حکم دیا گیا ہے کہ میں سب سے بڑا فرمانبردار بنوں۔ " WHERE ayatNumber = 13 AND chapterNumber = 39;
UPDATE verses SET ts = "کہہ‘ اگر میں اپنے رب کی نافرمانی کروں‘ تو میں ایک بڑے دن کے عذاب سے ڈرتاہوں۔ " WHERE ayatNumber = 14 AND chapterNumber = 39;
UPDATE verses SET ts = "(اور پھر) کہہ دے کہ میں اللہ کی عبادت اپنی اطاعت کو صرف اس کے لئے وابستہ کرتے ہوئے کرتا ہوں۔ " WHERE ayatNumber = 15 AND chapterNumber = 39;
UPDATE verses SET ts = "(باقی رہے تم) سو تم اللہ کے سوا جس کی چاہو عبادت کرو (اور یہ بھی) کہہ دے کہ پوری طرح گھاٹے میں پڑنے والے لوگ وہی ہیں جنہوں نے اپنے آپ کو بھی اور اپنے رشتہ داروں کو بھی قیامت کے دن گھاٹے میں ڈال دیا۔ سنو! یہی کھلا کھلا گھاٹا ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 39;
UPDATE verses SET ts = "اُن کے اُوپر بھی آگ کے سائے ہونگے اور نیچے بھی آگ کے سائے ہوں گے یہ وہ چیز ہے جس سے اللہ اپنے بندوں کو ڈراتا ہے۔ اے میرے بندو! میرا تقویٰ اختیار کرو۔ " WHERE ayatNumber = 17 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جو لوگ سرکش ہستیوں کی فرمانبرداری سے بچتے ہیں اور اللہ کی طرف جھکتے ہیں ان کے لئے بڑی خوشخبری ہے۔ پس تو میرے ان بندوں کو خوش خبری دے۔ " WHERE ayatNumber = 18 AND chapterNumber = 39;
UPDATE verses SET ts = "جو ہماری بات کو سنتے ہیں اور پھر اس میں سے سب سے بہتر حکم کی اتباع کرتے ہیں وہی لوگ ہیں جنہیں اللہ نے ہدایت دی اور وہی لوگ عقل مند ہیں۔ " WHERE ayatNumber = 19 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا وہ شخص جس پر عذاب کی پیشگوئی پوری ہوگئی (اُسے کوئی  بچا سکتا ہے؟ اور) کیا تو آگ میں پڑنے والوں کو بچا سکتا ہے۔ " WHERE ayatNumber = 20 AND chapterNumber = 39;
UPDATE verses SET ts = "لیکن وہ لوگ جو اپنے رب کا تقویٰ اختیار کرتے ہیں ان کو کئی منزل والے مکان عطا ہونگے جن کے اوپر اور منزلیں مضبوطی کے ساتھ بنی ہوں گی۔ ان کے نیچے نہریں بہتی ہونگی یہ اللہ کا ایک پختہ عہد ہے (اور) اللہ اپنے وعدوں کو توڑا نہیں کرتا۔ " WHERE ayatNumber = 21 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا تم نے نہیں دیکھا کہ اللہ نے آسمان سے پانی اتارا ہے۔ پھر اس کو زمین میں سے چشمے بنا کر چلایا ہے۔ پھر وہ اس کے ذریعہ سے مختلف رنگوں کی کھیتی اگاتا ہے۔ پھر وہ پکنے پر آجاتی ہے تو تو اسے زرد زرد دیکھتا ہے۔ پھر اللہ اس کو خس و خاشاک کی طرح کر دیتا ہے اس میں عقل مندوں کے لئے بڑی نصیحت ہے۔ " WHERE ayatNumber = 22 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا جس کا سینہ اللہ فرمانبرداری کے لئے کھول دے اور اس کو اپنے رب کی طرف سے نور بھی ملا ہو (وہ اس کے برابر ہو سکتا ہے جو ایسا نہیں) پس افسوس ان پر جن کے دل اللہ کے ذکر سے سختی محسوس کرتے ہیں۔ وہ بڑی کھلی گمراہی میں ہیں۔ " WHERE ayatNumber = 23 AND chapterNumber = 39;
UPDATE verses SET ts = "اللہ وہ ہے جس نے بہتر سے بہتر بات یعنی وہ کتاب اتاری ہے‘ جو متشابہ بھی ہے۔ اور اس کے مضمون نہایت اعلیٰ ہیں۔ جو لوگ اپنے رب سے ڈرتے ہیں ان کے جسموں کے رونگٹے اس کے پڑھنے سے کھڑے ہو جاتے ہیں۔ پھر ان کے چمڑے اور دل نرم ہو کر اللہ کے ذکر کی طرف جھک جاتے ہیں۔ یہ (قرآن) اللہ کی ہدایت ہے (یعنی قرآن جس ہدایت کا مالک ہے وہ اللہ کی ہدایت ہے) جس کے ذریعہ سے وہ جس کو چاہتا ہے ہدایت دیتا ہے اور جس کو اللہ گمراہ قرار دے دے اس کو کوئی ہدایت نہیں دے سکتا۔ " WHERE ayatNumber = 24 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا وہ شخص جو اپنے منہ کو قیامت کے دن سخت عذاب (یعنی دوزخ) سے بچنے کے لئے ڈھال بناتا ہے (وہ جنت میں جانے والے کے برابر ہو سکتا ہے) اور ظالموں سے کہا جائے گا کہ اپنے عملوں کا نتیجہ چکھو۔ " WHERE ayatNumber = 25 AND chapterNumber = 39;
UPDATE verses SET ts = "اُن سے پہلے لوگ بھی جھٹلا چکے ہیں (پھر اس کے نتیجہ میں) اُن کے اوپر عذاب ایسی ایسی طرفوں سے آیا جن کو وہ سمجھتے نہیں تھے۔ " WHERE ayatNumber = 26 AND chapterNumber = 39;
UPDATE verses SET ts = "اور اللہ نے ان کو دنیوی زندگی کی ذلت بھی دی اور آخرت میں جو عذاب آئے گا اگر وہ سمجھیں تو وہ اس سے بھی بڑا ہوگا۔ " WHERE ayatNumber = 27 AND chapterNumber = 39;
UPDATE verses SET ts = "اور ہم نے اس قرآن میں ہر قسم کی باتیں بیان کر دی ہیں‘ تاکہ وہ (یعنی کفار) نصیحت حاصل کریں۔ " WHERE ayatNumber = 28 AND chapterNumber = 39;
UPDATE verses SET ts = "ہم نے اس کو قرآن بنایا ہے جو اپنا مطلب کھول کر بیان کرنے والا ہے اس میں کوئی کجی نہیں (یہ اس لئے اترا ہے) تا لوگ تقویٰ اختیار کریں۔ " WHERE ayatNumber = 29 AND chapterNumber = 39;
UPDATE verses SET ts = "اللہ اس شخص کی حالت کو (عبرت کے لئے) بیان کرتا ہے جس کے کئی مالک ہیں جو آپس میں اختلاف بھی رکھتے ہیں اور ایک شخص پورا کا پورا ایک آدمی کی ملکیت ہے۔ کیا یہ دونوں شخص اپنے حال میں برابر ہو سکتے ہیں؟ سب تعریف اللہ ہی کی ہے لیکن ان میں سے اکثر جانتے نہیں۔ " WHERE ayatNumber = 30 AND chapterNumber = 39;
UPDATE verses SET ts = "تو بھی ایک دن مرنے والا ہے اور وہ بھی مرنے والے ہیں۔ " WHERE ayatNumber = 31 AND chapterNumber = 39;
UPDATE verses SET ts = "پھر تم سب کے سب قیامت کے دن اپنے رب کے سامنے اپنے اپنے عقائد اور اعمال کو پیش کرو گے۔ " WHERE ayatNumber = 32 AND chapterNumber = 39;
UPDATE verses SET ts = "اور اس سے زیادہ ظالم کون ہو سکتا ہے جو اللہ پر جھوٹ باندھے اور (خدا کی طرف سے آنے والے) سچ کو جب وہ اس کے پاس آئے جھٹلائے۔ کیا کافروں کا ٹھکانا جہنم نہیں ہے؟ " WHERE ayatNumber = 33 AND chapterNumber = 39;
UPDATE verses SET ts = "اور (ہر) وہ شخص جو خدا کی طرف سے سچی تعلیم لائے اور (ہر) وہ شخص جو ایسی تعلیم کی تصدیق کرے ایسے لوگ ہی متقی ہوتے ہیں۔ " WHERE ayatNumber = 34 AND chapterNumber = 39;
UPDATE verses SET ts = "وہ جو کچھ چاہیں گے ان کو اپنے رب کے ہاں مل جائے گا۔ محسنوں کا بدلہ ایسا ہی ہوتا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 39;
UPDATE verses SET ts = "تا کہ اللہ ان کے اعمال کے بدپہلوئوں کو ڈھانک دے اور اُن کا بدلہ اُن کے اعمال میں سے جو سب سے اچھے اعمال ہوں ان کے مطابق دے دے۔ " WHERE ayatNumber = 36 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا اللہ اپنے بندہ کے لئے کافی نہیں؟ اور وہ لوگ تجھے ان سے ڈراتے ہیں جو اس (خدا) کے علاوہ ہیں اور جس کو اللہ گمراہ سمجھے اس کو ہدایت دینے والا کوئی نہیں۔ " WHERE ayatNumber = 37 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جس کو اللہ ہدایت دے اس کو کوئی گمراہ نہیں کر سکتا۔ کیا اللہ غالب (اور) بدلہ لینے پر قادر نہیں ہے؟ " WHERE ayatNumber = 38 AND chapterNumber = 39;
UPDATE verses SET ts = "اور اگر تو ان سے پوچھے کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے؟ تو وہ ضرور کہیں گے کہ اللہ نے۔ تو کہہ دے کیا تم کو معلوم ہے کہ تم اللہ کے سوا کس کو پکارتے ہو؟ اگر اللہ مجھے نقصان پہنچانا چاہے تو کیا وہ  (جھوٹے معبود) اس کے نقصان کو دور کر سکتے ہیں؟ یا اگر اللہ مجھے رحمت پہنچانا چاہے تو کیا وہ (معبود) اس کی رحمت کو روک سکتے ہیں؟ تو کہہ دے مجھے اللہ کافی ہے۔ سب (سچے) متوکل اسی پر توکل کرتے ہیں۔ " WHERE ayatNumber = 39 AND chapterNumber = 39;
UPDATE verses SET ts = "تو کہہ دے‘ اے میری قوم! تم اپنی اپنی جگہوں پر عمل کرو‘ میں بھی (اپنی جگہ پر) عمل کروں گا۔ پس تم ضرور جان لو گے۔ " WHERE ayatNumber = 40 AND chapterNumber = 39;
UPDATE verses SET ts = "(کہ) کس کو ایسا عذاب آتا ہے جو اُسے رسوا کر دیتا ہے اور کس پر وہ عذاب آتا ہے جو دائمی ہوتا ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 39;
UPDATE verses SET ts = "ہم نے تجھ پر (یہ) کتاب یقیناً لوگوں کے فائدہ کے لئے حق اور حکمت کے ساتھ اتاری ہے سو جس نے ہدایت پالی اس کا نفع اسی کی جان کو حاصل ہوگا اور جو گمراہ ہو گیا اس کی گمراہی اسی پر پڑے گی اور تو اُن پر کارساز کے طور پر مقرر نہیں کیا گیا۔ " WHERE ayatNumber = 42 AND chapterNumber = 39;
UPDATE verses SET ts = "اللہ ہر شخص کی روح اس کی موت کے وقت قبض کرتا ہے اور جس کی موت نہیں آئی (اس کی روح) اس کی نیند کے وقت (قبض کرتا ہے) پھر وہ جس کی موت کا حکم جاری کر چکا ہوتا ہے اس کی روح کو روکے رکھتا ہے اور دوسری کو ایک مدت مقررہ کے لئے واپس کر دیتا ہے  اس میں فکر کرنے والی قوم کے لئے نشانات ہیں۔ " WHERE ayatNumber = 43 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا انہوں نے اللہ کے سوا کچھ شفیع قرار دے چھوڑے ہیں؟ کہہ دے گو ان کے قبضہ میں کوئی طاقت نہ ہو اور نہ ان کو (کسی قسم) کی سمجھ بوجھ ہو (پھر بھی تم انہیں شفیع قرار دو گے) ۔" WHERE ayatNumber = 44 AND chapterNumber = 39;
UPDATE verses SET ts = "تو کہہ دے کہ شفاعت کا اختیار سب کا سب اللہ کے ہاتھ میں ہے (جس کو چاہتا ہے یہ اختیار دیتا ہے) آسمانوں اور زمین کی بادشاہت اسی کی ہے اور تم اسی کی طرف لوٹائے جائو گے۔ " WHERE ayatNumber = 45 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جب خدائے واحد کا ذکر کیا جاتا ہے تو وہ لوگ جو آخرت پر ایمان نہیں لاتے ان کے دل (ایسے وعظ سے) نفرت کرنے لگ جاتے ہیں اور جب اُن (بتوں) کا جو خدا کے مقابلہ میں بالکل حقیر ہیں ذکر کیا جاتا ہے تو وہ یکدم خوش ہونے لگ جاتے ہیں۔ " WHERE ayatNumber = 46 AND chapterNumber = 39;
UPDATE verses SET ts = "تو کہہ دے اے اللہ! (اے) آسمانوں اور زمین کو پیدا کرنے والے‘ غیب اور ظاہر کا علم رکھنے والے! تو ہی اپنے بندوں کے درمیان ان تمام چیزوں کا فیصلہ کرنے والا ہے جن میں وہ اختلاف کرتے ہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 39;
UPDATE verses SET ts = "اور اگر ظالم لوگ زمین میں جو کچھ ہے اس کے مالک ہوتے بلکہ اتنا ہی اور بھی ان کے پاس ہوتا تو وہ قیامت کے دن عذاب سے بچنے کے لئے اسے فدیہ کے طور پر دے دیتے اور اللہ کی طرف سے ان پر وہ کچھ ظاہر ہو جائے گا۔ جس کا وہ گمان بھی نہیں رکھتے تھے۔ " WHERE ayatNumber = 48 AND chapterNumber = 39;
UPDATE verses SET ts = "اور اُن پر اُن کے اعمال کی برائیاں ظاہر ہو جائیں گی اور جس عذاب سے وہ ہنسی کیا کرتے تھے (وہ بھی) اُن کو ہر طرف سے گھیر لے گا۔ " WHERE ayatNumber = 49 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جب انسان کو کوئی نقصان پہنچتا ہے تو وہ ہمیں پکارنے لگتا ہے۔ پھر جب ہم اس کو اپنے پاس سے کوئی نعمت دیتے ہیں تو وہ کہتا ہے مجھے یہ نعمت اپنے علم کی بنا پر ملی ہے (یہ درست نہیں) بلکہ اس کا ملنا ایک آزمائش ہے لیکن اِن میں سے اکثر جانتے نہیں۔ " WHERE ayatNumber = 50 AND chapterNumber = 39;
UPDATE verses SET ts = "یہی (بات) ان سے پہلے لوگ بھی کہہ چکے ہیں۔ پھر بھی اُن کے اعمال اُن کے کام نہ آئے۔ " WHERE ayatNumber = 51 AND chapterNumber = 39;
UPDATE verses SET ts = "بلکہ ان کے اعمال کی بدیوں (کی جزا) نے ان کو آپکڑا اور اس زمانہ کے لوگوں میں سے جو ظالم ہیں ان کو بھی ان کے اعمال کی بدیاں پکڑ لیں گی اور وہ خدا کو اس کے ارادہ سے روک نہ سکیں گے۔ " WHERE ayatNumber = 52 AND chapterNumber = 39;
UPDATE verses SET ts = "کیا ان کو معلوم نہیں کہ اللہ جس کے لئے چاہتا ہے رزق میں فراخی دے دیتا ہے اور (جس کے لئے چاہتا ہے رزق میں) تنگی کر دیتا ہے۔ اور اس میں مومن قوم کے لئے بڑے بڑے نشان ہیں۔ " WHERE ayatNumber = 53 AND chapterNumber = 39;
UPDATE verses SET ts = "تو (ان کو ہماری طرف سے) کہدے اے میرے بندو! جنہوں نے اپنی جان پر (گناہ کرکے) ظلم کیا ہے اللہ کی رحمت سے مایوس نہ ہو۔ اللہ سب گناہ بخش دیتا ہے۔ وہ بخشنے والا (اور) باربار رحم کرنے والا ہے۔ " WHERE ayatNumber = 54 AND chapterNumber = 39;
UPDATE verses SET ts = "اور تم سب اپنے رب کی طرف جھکو اور پیشتر اس کے کہ تم پر ایسا عذاب نازل ہو جس کے نزول کے بعد تمہاری مدد کے لئے کوئی نہ پہنچ سکے اس کے پورے فرمانبردار بن جائو۔ " WHERE ayatNumber = 55 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جو کچھ تمہاری طرف اُترا ہے اس میں سے (اپنے مطابق حال) سب سے بہتر حکم کی پیروی کرو۔ پیشتر اس کے کہ تم پر اچانک عذاب آجائے اور تم جانتے بھی نہ ہو۔ " WHERE ayatNumber = 56 AND chapterNumber = 39;
UPDATE verses SET ts = "تا ایسا نہ ہو کہ تم میں سے کوئی شخص یہ کہنے لگے کہ جو کچھ اللہ کے بارے میں میں نے کمی کی ہے اس کی بنا پر مجھ پر افسوس! اور میں تو (وحی الٰہی) کو حقیر سمجھتا رہا تھا۔ " WHERE ayatNumber = 57 AND chapterNumber = 39;
UPDATE verses SET ts = "یا (کوئی) یہ کہنے لگے کہ اگر اللہ مجھے (جبرا) ہدایت دے دیتا تو میں بھی متقیوں میں سے ہو جاتا۔ " WHERE ayatNumber = 58 AND chapterNumber = 39;
UPDATE verses SET ts = "یا جب عذاب کو دیکھے تو کہنے لگے کہ اگر مجھے واپس جانے کا موقعہ ملتا‘ تو میں محسنوں میں شامل ہو جاتا۔ " WHERE ayatNumber = 59 AND chapterNumber = 39;
UPDATE verses SET ts = "(ایسا) ہرگز نہیں (ہوسکتا) بلکہ تیرے پاس ہمارے نشانات آچکے تھے پھر بھی تو نے ان کا انکار کیا اور تکبر سے پیش آیا اور کافروں میں شامل ہو گیا۔ " WHERE ayatNumber = 60 AND chapterNumber = 39;
UPDATE verses SET ts = "اور قیامت کے دن تو ان لوگوں کو دیکھے گا جنہوں نے اللہ پر جھوٹ باندھا تھا۔ کہ اُن کے منہ کالے ہوں گے کیا متکبروں کی جگہ جہنم میں نہیں ہے۔ " WHERE ayatNumber = 61 AND chapterNumber = 39;
UPDATE verses SET ts = "اور اللہ متقیوں کو اُن کی شان کے مطابق کامیابی عطا کرکے ہر قسم کے غم سے نجات بخشے گا نہ تو تکلیف ان کو پہنچے گی اور نہ وہ (کسی گزشتہ کوتاہی پر) غم کریں گے۔ " WHERE ayatNumber = 62 AND chapterNumber = 39;
UPDATE verses SET ts = "اللہ ہر چیز کا پیدا کرنے والا ہے اور وہ ہر ایک کام کے لئے کافی ہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 39;
UPDATE verses SET ts = "آسمانوں اور زمین کی کنجیاں اسی کے ہاتھ میں ہیں اور جو لوگ اللہ کی آیات کا انکار کرتے ہیں‘ وہی گھاٹے میں پڑتے ہیں۔ " WHERE ayatNumber = 64 AND chapterNumber = 39;
UPDATE verses SET ts = "تو کہہ دے‘ اے جاہلو! کیا تم مجھے حکم دیتے ہو کہ اللہ کے سوا میں کسی اور (ہستی) کی عبادت کروں۔ " WHERE ayatNumber = 65 AND chapterNumber = 39;
UPDATE verses SET ts = "حالانکہ خود خدا کی طرف سے تیری طرف اور تجھ سے پہلے (نبیوں) کی طرف وحی کی گئی ہے (اور ہر ایک نبی کو کہا گیا تھا کہ) اگر تو شرک کرے گا تو تیرے سارے اعمال ضائع ہو جائیں گے اور تو ان لوگوں میں سے ہو جائے گا جو گھاٹے میں پڑ جاتے ہیں۔ " WHERE ayatNumber = 66 AND chapterNumber = 39;
UPDATE verses SET ts = "(پس ایسا نہ کر) بلکہ اللہ کی عبادت کر اور شکر گذار بندوں  میں شامل ہو جا۔ " WHERE ayatNumber = 67 AND chapterNumber = 39;
UPDATE verses SET ts = "اور ان لوگوں نے اللہ کی صفات کا صحیح اندازہ نہیں لگایا حالانکہ زمین سب کی سب اس کی مملوکہ ہے اور آسمان (اور زمین دونوں) قیامت کے دن اس کے دائیں ہاتھ میں لپٹے ہوئے ہونگے وہ پاک ہے اور ان کے شرکیہ عقیدوں سے بہت بالا ہے۔ " WHERE ayatNumber = 68 AND chapterNumber = 39;
UPDATE verses SET ts = "اور بگل میں پھونکا جائے گا تو آسمانوں اور زمین میں جو کوئی بھی ہے اس پر ایک بے ہوشی طاری ہو جائے گی سوائے اس (شخص) کے جس کو اللہ چاہے گا (کہ بچالے) پھر دوسری دفعہ بگل میں پھونکا جائے گا اور اچانک وہ سب (اپنے متعلق فیصلہ کا) انتظار کرتے ہوئے کھڑے ہو جائیں گے۔ " WHERE ayatNumber = 69 AND chapterNumber = 39;
UPDATE verses SET ts = "اور زمین اپنے رب کے نور سے روشن ہو جائے گی۔ اور کتاب سامنے رکھ دی جائے گی اور نبیوں اور دوسرے گواہوں کو حاضر کیا جائے گا۔ پھر ان تمام انسانوں کے درمیان پورا پورا سچائی کے مطابق فیصلہ کر دیا جائے گا۔ اور ان پر ظلم نہیں کیا جائے گا۔ " WHERE ayatNumber = 70 AND chapterNumber = 39;
UPDATE verses SET ts = "اور ہر نفس نے جو کیا ہوگا اس کے مطابق اسے پورا پورا بدلہ دیا جائے گا اور وہ (اللہ) ان کے اعمال سے پوری طرح واقف ہے۔ " WHERE ayatNumber = 71 AND chapterNumber = 39;
UPDATE verses SET ts = "اور کافروں کو جہنم کی طرف گروہ در گروہ بنا کر ہنکایا جائے گا یہاں تک کہ جب وہ اس کے پاس پہنچ جائیں گے تو اس کے دروازے کھول دیئے جائیں گے اور ان (کافروں) سے اس (دوزخ) کے داروغے کہیں گے۔ کیا تمہاری طرف تمہاری ہی قوم میں سے رسول نہیں آئے تھے جو تمہارے سامنے تمہارے رب کی آیات پڑھ کر سناتے تھے اور آج کے دن کی ملاقات سے تم کو ہوشیار کرتے تھے؟ وہ کہیں گے ہاں! ایسا ہی ہوا تھا۔ لیکن کافروں پر عذاب کی پیشگوئی پوری ہونی ہی تھی (یعنی ہم کافر تھے ہم رسولوں کو کس طرح مان سکتے تھے) ۔" WHERE ayatNumber = 72 AND chapterNumber = 39;
UPDATE verses SET ts = "اُن سے کہا جائے گا کہ جہنم کے دروازوں میں داخل ہو جائو‘ اس صورت میں کہ ایک لمبا عرصہ تمہیں اس میں رہنا پڑے گا۔ پس متکبروں کا ٹھکانا بہت برا ہے۔ " WHERE ayatNumber = 73 AND chapterNumber = 39;
UPDATE verses SET ts = "اور جو لوگ تقویٰ کرتے تھے ان کو جنت کی طرف گروہ در گروہ دھکیل کر لے جایا جائے گا۔ یہاں تک کہ جب وہ جنت تک پہنچ جائیں گے تو اس کے دروازے کھول دیئے جائیں گے اور اس کے داروغے ان سے کہیں گے تم پر ہمیشہ سلامتی ہو تم بڑی اچھی حالت کو پہنچے ہو۔ پس اس (جنت) میں داخل ہو جائو اس فیصلہ کے ساتھ کہ تم ہمیشہ اس میں رہو گے۔ " WHERE ayatNumber = 74 AND chapterNumber = 39;
UPDATE verses SET ts = "اور وہ کہیں گے‘ اللہ ہی کی سب تعریف ہے جس نے ہم سے اپنا وعدہ پور اکر دیا اور اس جگہ کا ہم کو وارث بنا دیا۔ ہم جنت میں جہاں چاہیں گے رہیں گے سو (ثابت ہوا کہ) پوری طرح عمل کرنے والوں کا انعام بہت ہی اچھا ہوتا ہے۔ " WHERE ayatNumber = 75 AND chapterNumber = 39;
UPDATE verses SET ts = "اور تو اُس دن ملائکہ کو دیکھے گا کہ عرش کے گرد حلقہ باندھے ہوئے اپنے رب کی حمد کے ساتھ ساتھ تسبیح بھی کر رہے ہیں اور ان (لوگوں) کے درمیان پورے پورے انصاف کے ساتھ فیصلہ کر دیا جائے گا اور کہا جائے گا کہ سب تعریف اللہ ہی کو حاصل ہے جو سب جہانوں کا رب ہے۔ " WHERE ayatNumber = 76 AND chapterNumber = 39;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) ۔" WHERE ayatNumber = 1 AND chapterNumber = 40;
UPDATE verses SET ts = "یہ سورۃخدا کی حمد اور اس کی بزرگی کو ثابت کرتی ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 40;
UPDATE verses SET ts = "یہ کتاب غالب (اور) علم والے اللہ کی طرف سے نازل ہوئی ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 40;
UPDATE verses SET ts = "جو گناہوں کا بخشنے والا اور توبہ قبول کرنے والا ہے (اور اس وجہ سے حمد کا مستحق ہے) سزا دینے میں سخت ہے بڑا احسان کرنے والا ہے۔ اس کے سوا کوئی معبود نہیں‘ اسی کی طرف لوٹ کر جانا ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 40;
UPDATE verses SET ts = "کافروں کے سوا اللہ کی آیات کے متعلق کوئی شخص مجادلانہ بحث نہیں کرتا۔ پس ان کا مختلف ملکوں میں (تجارت وغیرہ کے لئے آزادی سے) گھومتے پھرنا تجھ کو دھوکہ میں نہ ڈالے (کیونکہ ان کے اعمال ان کی ہی تباہی کا باعث ہیں)۔ " WHERE ayatNumber = 5 AND chapterNumber = 40;
UPDATE verses SET ts = "ان سے پہلی قوموں نے بھی (اپنے رسولوں کا) انکار کیا تھا۔ نوح کی قوم نے بھی اور ان کے بعد اور مختلف قوموں نے (جنہوں نے اپنے نبیوں کے خلاف جتھے بنا لئے) اور ہر قوم نے اپنے رسول کے متعلق ارادہ کر لیا کہ اس کو گرفتار کر لیں اور جھوٹی دلیلیوں کے ساتھ بحث کرنی شروع کر دی تا کہ اپنی کج بحثی سے حق کو اس کے مقام سے ہٹا دیں پس میں نے ان کو پکڑ لیا۔ اب بتائو‘ میری سزا کیسی ہوئی؟ " WHERE ayatNumber = 6 AND chapterNumber = 40;
UPDATE verses SET ts = "اور اس طرح تیرے رب کا فیصلہ کافروں کے متعلق پورا ہو گیا (یعنی یہ) کہ وہ دوزخی ہیں۔ " WHERE ayatNumber = 7 AND chapterNumber = 40;
UPDATE verses SET ts = "وہ فرشتے جو عرش کو اٹھائے ہوئے ہیں اور جو اس کے اردگرد ہیں وہ اپنے رب کی تعریف کے ساتھ ساتھ اس کی پاکیزگی بھی بیان کرتے ہیں اور اس (حمد) پر پورا ایمان رکھتے ہیں اور مومنوں کے لئے استغفار کرتے ہیں (اور کہتے ہیں) اے ہمارے رب! ہر ایک چیز کا تو نے اپنی رحمت اور علم سے احاطہ کیا ہوا ہے پس توبہ کرنے والوں کو اور اپنے راستے کے اوپر چلنے والوں کو معاف فرما اور ان کو دوزخ کے عذاب سے بچالے۔ " WHERE ayatNumber = 8 AND chapterNumber = 40;
UPDATE verses SET ts = "اے ہمارے رب! اور ان کو اور ان کے باپ دادوں اور ان کی بیویوں اور ان کی اولاد میں سے جو نیک ہوں ان کو بھی دائمی جنتوں میں داخل کر‘ جن کا تو نے ان سے وعدہ کیا ہوا ہے تو غالب (اور) بڑی حکمت والا ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 40;
UPDATE verses SET ts = "اور تو ان کو تمام تکلیفوں سے بچا۔ اور جس کو تو اس دن تکلیفوں سے محفوظ رکھے تو یقیناً تو نے اس پر رحم کیا۔ اور یہ بڑی کامیابی ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 40;
UPDATE verses SET ts = "اور کافروں کو یقیناً (موت کے بعد) کہا جائے گا کہ آج تم جتنا بھی اپنی جانوں کو نفرت کی نگاہ سے دیکھتے ہو اللہ کی نفرت (جو وہ تم سے کرتا ہے) اس سے زیادہ ہے (یاد کرو) جب تمہیں ایمان کی طرف پکارا جاتا تھا تو تم کفر کرتے تھے۔ " WHERE ayatNumber = 11 AND chapterNumber = 40;
UPDATE verses SET ts = "وہ کہیں گے کہ اے ہمارے رب! تو نے ہم کو دو دفعہ مارا اور دو دفعہ زندہ کیا۔ سو ہم اپنی خطائوں کا اقرار کرتے ہیں۔ پس (تو بتا کہ) اب (اس گرفت سے) نکلنے کی کوئی صورت بھی ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 40;
UPDATE verses SET ts = "تمہارا یہ حال اس لئے ہے کہ جب صرف اللہ کو پکارا جاتا تھا تو تم انکار کر دیتے تھے اور جب اس کا کوئی شریک مقرر کیا جاتا تھا تو تم ایمان لے آتے تھے پس (آج ثابت ہو گیا کہ) سب حکومت عالی شان وسیع مملکت والے اللہ ہی کی ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 40;
UPDATE verses SET ts = "وہی تم کو اپنے نشان دکھاتا ہے اور آسمان سے تمہارے لئے رزق اتارتا ہے اور نصیحت صرف وہی حاصل کرتا ہے جو خدا کی طرف جھکتا ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 40;
UPDATE verses SET ts = "پس اپنی اطاعت کو خالص اسی کے لئے کرتے ہوئے اللہ کو پکارو‘ خواہ کفار اسے ناپسند ہی کریں۔ " WHERE ayatNumber = 15 AND chapterNumber = 40;
UPDATE verses SET ts = "(وہ) اونچے درجوں والا (ہے) عرش کا مالک ہے اور اپنے حکم سے اپنے بندوں میں سے جس پر چاہتا ہے اپنا کلام نازل کرتا ہے تا کہ وہ (بندہ خدا کی) ملاقات کے دن سے لوگوں کو ڈرائے۔ " WHERE ayatNumber = 16 AND chapterNumber = 40;
UPDATE verses SET ts = "جس دن وہ سب کے سب لوگ خدا کے سامنے آ حاضر ہوں گے‘ ان کی کوئی چیز بھی اللہ پر مخفی نہ ہوگی اس دن بادشاہت کس کو حاصل ہوگی؟ (صرف) اللہ کو جو اکیلا ہے (اور) سب پر غالب ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 40;
UPDATE verses SET ts = "اس دن ہر جان کو اس کے عمل کی جزا دی جائے گی اور اس دن کسی قسم کا ظلم نہیں ہوگا۔ اللہ بہت جلدی حساب لے لیتا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 40;
UPDATE verses SET ts = "اور تو ان کو اس دن سے ڈرا جو قریب سے قریب تر ہوتا جاتا ہے۔ جبکہ دل غم سے بھرے ہوئے (ہونگے اور) منہ تک آجائیں گے۔ ظالموں کا اس دن نہ کوئی دوست ہوگا نہ کوئی شفیع (ہوگا) جس کی بات مانی جائے۔ " WHERE ayatNumber = 19 AND chapterNumber = 40;
UPDATE verses SET ts = "اللہ آنکھوں کی خیانت تک کو جانتا ہے اور اسے بھی جس کو سینے یا دل چھپائے ہوئے ہیں۔ " WHERE ayatNumber = 20 AND chapterNumber = 40;
UPDATE verses SET ts = "اور اللہ ہمیشہ انصاف کے مطابق فیصلہ کرتا ہے اور جن کو یہ لوگ خدا کے سوا پکارتے ہیں‘ وہ کوئی بھی فیصلہ نہیں کرتے۔ اللہ یقیناً بہت سننے والا (اور) بہت دیکھنے والا ہے۔ " WHERE ayatNumber = 21 AND chapterNumber = 40;
UPDATE verses SET ts = "کیا یہ لوگ زمین میں نہیں پھرے تا دیکھتے کہ ان سے پہلے لوگوں کا انجام کیا ہوا تھا؟ وہ لوگ ان سے طاقت اور پیچھے چھوڑے ہوئے نشانوں کے لحاظ سے بڑے تھے۔ سو اللہ نے ان کو ان کے گناہوں کی وجہ سے تباہ کر دیا اور کوئی بھی اللہ کی گرفت سے ان کو بچانے والا پیدا نہ ہوا۔ " WHERE ayatNumber = 22 AND chapterNumber = 40;
UPDATE verses SET ts = "یہ (بے کسی) اس وجہ سے تھی کہ ان کے پاس ان کے رسول نشانات لے کر آتے رہے اور انہوں نے (ہمیشہ) انکار سے کام لیا پس اللہ نے بھی ان کو ہلاک کردیا وہ بڑا قوی ہے (اور) اس کا عذاب سخت ہے۔ " WHERE ayatNumber = 23 AND chapterNumber = 40;
UPDATE verses SET ts = "اور ہم نے موسیٰ کو بھی اپنے نشانات کے ساتھ اور کھلے کھلے غلبہ کے ساتھ فرعون اور ہامان اور قارون کی طرف بھیجا تھا مگر انہوں نے کہا یہ شخص دھوکہ دینے والا (اور) جھوٹا ہے۔" WHERE ayatNumber = 24 AND chapterNumber = 40;
UPDATE verses SET ts = "فرعون اور ہامان اور قارون کی طرف بھیجا تھا مگر انہوں نے کہا یہ شخص دھوکہ دینے والا (اور) جھوٹا ہے۔ " WHERE ayatNumber = 25 AND chapterNumber = 40;
UPDATE verses SET ts = "پس جب وہ (موسیٰ) ہماری طرف سے حق لے کر ان کے پاس آیا تو انہوں نے کہا‘ جو لوگ ایمان لا کر اس کے ساتھ شامل ہو گئے ہیں۔ ان کے بیٹوں کو ہلاک کر دو۔ اور ان کی عورتوں کو زندہ رکھو اور کافروں کی تدبیر ہمیشہ الٹی ہی پڑتی ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 40;
UPDATE verses SET ts = "اور فرعون نے کہا مجھے چھوڑو تا میں موسٰی کو قتل کروں اور چاہیئے کہ وہ اپنے رب کو پکارے میں اس بات سے ڈرتا ہوں کہ وہ تمہارے دین کو بدل نہ دے یا ملک میں کوئی فساد نہ پیدا کر دے۔ " WHERE ayatNumber = 27 AND chapterNumber = 40;
UPDATE verses SET ts = "(اس پر) موسیٰ نے کہا‘ میں اپنے رب سے اور تمہارے رب سے ہر متکبر کی شرارت سے جو یوم حساب پر ایمان نہیں لاتا پناہ مانگتا ہوں۔ " WHERE ayatNumber = 28 AND chapterNumber = 40;
UPDATE verses SET ts = "اور آل فرعون میں سے ایک شخص جو ایمان دار تھا‘ مگر اپنا ایمان چھپاتا تھا اس نے کہا اے لوگو! کیا تم ایک آدمی کو صرف اس لئے مارتے ہو کہ وہ کہتا ہے کہ اللہ میرا رب ہے اور وہ تمہارے رب کی طرف سے نشانات بھی لایا ہے اور اگر وہ جھوٹا ہے تو اس کے جھوٹ کا وبال اسی پر پڑے گا اور اگر وہ سچا ہے تو اس کی کی ہوئی بعض (انذاری) پیشگوئیاں تمہارے متعلق پوری ہو جائیں گی۔ اللہ حد سے بڑھے ہوئے اور بہت جھوٹ بولنے والے کو کبھی کامیاب نہیں کرتا۔ " WHERE ayatNumber = 29 AND chapterNumber = 40;
UPDATE verses SET ts = "اے میری قوم! آج تمہاری ایسی حکومت ہے کہ تم ملک پر غالب ہو‘ پس بتائو کہ اللہ کے عذاب کے مقابلہ میں اگر وہ ہم پر نازل ہو گیا تو ہماری مدد کون کرے گا؟ فرعون نے کہا‘ میں تمہیں وہی بتاتا ہوں جو خود مجھے ٹھیک نظر آتا ہے اور میں تمہیں صرف ہدایت کا راستہ بتاتا ہوں۔ " WHERE ayatNumber = 30 AND chapterNumber = 40;
UPDATE verses SET ts = "اور وہ شخص جو (درحقیقت) ایمان لا چکا تھا اس نے کہا کہ اے میری قوم! گزشتہ بڑی بڑی قوموں کی ہلاکت کے دن کی طرح میں تمہاری ہلاکت کے دن سے بھی ڈرتا ہوں۔ " WHERE ayatNumber = 31 AND chapterNumber = 40;
UPDATE verses SET ts = "جیسا کہ نوح کی قوم اور عاد اور ثمود پر گذرا۔ اور جو لوگ ان کے بعد گذرے۔ اور اللہ اپنے بندوں پر ظلم نہیں کرنا چاہتا۔ " WHERE ayatNumber = 32 AND chapterNumber = 40;
UPDATE verses SET ts = "اور اے میری قوم! میں تم پر اس دن سے ڈرتا ہوں جس دن لوگ ایک دوسرے کو مدد کے لئے پکاریں گے۔" WHERE ayatNumber = 33 AND chapterNumber = 40;
UPDATE verses SET ts = "جس دن تم پیٹھ پھیر کر (خدائی لشکروں کے سامنے سے) بھاگ جائو گے اور اللہ کے مقابلہ میں کوئی تم کو بچانے والا نہیں ہوگا اور جس کو اللہ گمراہ قرار دے اس کو کوئی ہدایت نہیں دے سکتا۔ " WHERE ayatNumber = 34 AND chapterNumber = 40;
UPDATE verses SET ts = "اور یوسف اس سے پہلے دلائل کے ساتھ تمہارے پاس آچکا ہے مگر تم جو کچھ وہ تمہارے پاس لایا تھا اس کے بارہ میں شک ہی میں رہے‘ یہاں تک کہ جب وہ فوت ہو گیا تو تم نے (مایوسی سے) کہنا شروع کیا کہ اللہ اس کے بعد کوئی رسول مبعوث نہیں کرے گا اسی طرح اللہ ہر حد سے گذرنے والے (اور) شبہ کرنے والے کو گمراہ قرار دیتا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 40;
UPDATE verses SET ts = "جو لوگ اللہ کی آیتوں میں بغیر کسی دلیل کے جو ان کے پاس (اللہ کی طرف سے) آئی ہو بحثیں کرتے ہیں (ان کو معلوم ہونا چایئے کہ) یہ اللہ کے نزدیک اور مومنوں کے نزدیک بہت برا ہے اسی طرح اللہ متکبر انسان کے پورے پورے دل پر مہر لگا دیتا ہے۔ " WHERE ayatNumber = 36 AND chapterNumber = 40;
UPDATE verses SET ts = "اور فرعون نے کہا‘ اے ہامان! میرے لئے ایک محل بنوا۔ تا کہ میں ان راستوں پر جا پہنچوں‘ " WHERE ayatNumber = 37 AND chapterNumber = 40;
UPDATE verses SET ts = "جو آسمان کے راستے ہیں اور اس طرح موسیٰ کے خدا پر آگاہ ہو جائوں۔ کیونکہ میں اسے جھوٹا سمجھتا ہوں۔ اور اس طرح فرعون کی نظر میں اس کے اعمال کی بدیاں خوبصورت کرکے دکھائی گئی تھیں اور وہ حقیقی راستہ سے (اپنی نافرمانیوں کی وجہ سے) روکا گیا تھا اور فرعون کی تدبیر ناکامی کی صورت میں ہی ظاہر ہونے والی تھی۔ " WHERE ayatNumber = 38 AND chapterNumber = 40;
UPDATE verses SET ts = "اور وہ شخص جو ایمان لایا تھا‘ اس نے کہا‘ اے میری قوم! میری اتباع کرو‘ میں تم کو ہدایت کا راستہ دکھائوں گا۔ " WHERE ayatNumber = 39 AND chapterNumber = 40;
UPDATE verses SET ts = "اے میری قوم! یہ ورلی زندگی صرف ایک چند روزہ فائدہ ہے‘ اور اُخروی زندگی ہی یقیناً پائیدار ٹھکانا ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 40;
UPDATE verses SET ts = "جو برا عمل کرے گا۔ اس کو اسی کے مطابق نتیجہ ملے گا اور جو کوئی ایمان کے مطابق عمل کرے گا خواہ مرد ہو یا عورت‘ بشرطیکہ وہ ایمان میں سچا ہو وہ اور اس کے ہم مشرب لوگ جنت میں داخل ہوں گے اور ان کو اس میں بغیر حساب کے انعام دیا جائے گا۔ " WHERE ayatNumber = 41 AND chapterNumber = 40;
UPDATE verses SET ts = "اور اے میری قوم! میرا بھی کیا عجیب حال ہے کہ میں تو تم کو نجات کی طرف بلاتا ہوں‘ اور تم مجھے دوزخ کی طرف بلاتے ہو۔ " WHERE ayatNumber = 42 AND chapterNumber = 40;
UPDATE verses SET ts = "تم مجھے اس غرض سے بلاتے ہو کہ میں اللہ کا کفر کروں اور اس کے شریک ان کو قرار دوں جن کے شریک ہونے کے متعلق مجھے کوئی علم نہیں اور میں تم کو ایک غالب (اور) بخشنے والی ہستی کی طرف بلاتا ہوں۔ " WHERE ayatNumber = 43 AND chapterNumber = 40;
UPDATE verses SET ts = "اس میں کوئی شبہ نہیں کہ تم مجھے اس ہستی کی طرف بلاتے ہو جس کی کوئی (مؤثر) آواز نہ اس دنیا میں ہے نہ اگلے جہان میں۔ اور (حقیقت یہ ہے کہ) ہم سب کو اللہ ہی کی طرف لوٹ کر جانا ہے اور یہ کہ حد سے گذرنے والے لوگ دوزخی ہیں۔ " WHERE ayatNumber = 44 AND chapterNumber = 40;
UPDATE verses SET ts = "پس میں جو کچھ تمہیں نصیحت کرتا ہوں اس کو تم جلدی ہی یاد کرو گے اور (میں تمہاری دھمکیوں سے ڈرتا نہیں) میں اپنا معاملہ اللہ کے سپرد کرتا ہوں۔ اللہ یقیناً (اپنے) بندوں کا نگران ہے۔ " WHERE ayatNumber = 45 AND chapterNumber = 40;
UPDATE verses SET ts = "اس پر اللہ نے اس (مومن شخص) کو اُن (کفار) کی تدبیروں کے بدنتائج سے بچا لیا اور آل فرعون کو دردناک عذاب نے چاروں طرف سے گھیر لیا۔ " WHERE ayatNumber = 46 AND chapterNumber = 40;
UPDATE verses SET ts = "یعنی آگ نے جس کے سامنے وہ صبح اور شام پیش کیے جاتے ہیں اور جب مقررہ گھڑی آئے گی (فرشتوں سے کہا جائے گا کہ) فرعون کے ساتھیوں کو سخت عذاب میں داخل کرو۔ " WHERE ayatNumber = 47 AND chapterNumber = 40;
UPDATE verses SET ts = "اور (اس وقت کو بھی یاد کرو) جب کہ (فرعون کے ساتھی) آگ میں بحث کر رہے ہوں گے۔ اور ان میں سے کمزور بڑے لوگوں سے کہہ رہے ہوں گے کہ ہم تمہارے متبع تھے پس کیا آج تم ہم کو آگ کے عذاب کے کسی حصہ سے بچا سکتے ہو؟ " WHERE ayatNumber = 48 AND chapterNumber = 40;
UPDATE verses SET ts = "بڑے لوگ کہیں گے‘ ہم سب اس عذاب میں مبتلا ہیں۔ اللہ نے اپنے بندوں کے درمیان جو فیصلہ کرنا تھا کر دیا۔ " WHERE ayatNumber = 49 AND chapterNumber = 40;
UPDATE verses SET ts = "اور دوزخی لوگ دوزخ کے داروغوں سے کہیں گے‘ تم اپنے رب کو پکارو کہ عذاب کا کچھ وقت تو ہم سے کم کرے۔ " WHERE ayatNumber = 50 AND chapterNumber = 40;
UPDATE verses SET ts = "وہ کہیں گے کہ کیا تمہارے پاس تمہارے رسول دلائل لے کر نہیں آئے تھے وہ کہیں گے ہاں۔ کیوں نہیں! اس پر وہ (دوزخ کے داروغے) کہیں گے‘ اب تم (جتنا چاہو) پکارتے جائو۔ اور کافروں کی دعا رائیگاں ہی جاتی ہے۔ " WHERE ayatNumber = 51 AND chapterNumber = 40;
UPDATE verses SET ts = "ہم اپنے رسولوں کی اور ان پر ایمان لانے والوں کی اس دنیا میں بھی ضرور مدد کریں گے اور اس دن بھی جبکہ گواہ کھڑے ہونگے۔ " WHERE ayatNumber = 52 AND chapterNumber = 40;
UPDATE verses SET ts = "جس دن ظالموں کو ان کا عذر کوئی نفع نہیں دے گا اور ان پر (خداتعالیٰ کی) لعنت نازل ہوگی۔ اور ان کو برا گھر (رہنے کو) ملے گا۔ " WHERE ayatNumber = 53 AND chapterNumber = 40;
UPDATE verses SET ts = "اور ہم نے یقیناً موسیٰ کو ہدایت دی تھی اور بنی اسرائیل کو (اس) کتاب (یعنی توراۃ) کا وارث بنا دیا تھا۔ " WHERE ayatNumber = 54 AND chapterNumber = 40;
UPDATE verses SET ts = "جو عقل مندوں کے لئے ہدایت اور نصیحت کا موجب تھی۔ " WHERE ayatNumber = 55 AND chapterNumber = 40;
UPDATE verses SET ts = "پس تو صبر کر۔ اللہ کا وعدہ ضرور پورا ہو کر رہے گا اور جو تیرے گناہ لوگوں نے کیے ہیں ان کی بھی ان کے حق میں بخشش مانگتا رہ اور اپنے رب کی شام اور صبح حمد کے ساتھ ساتھ تسبیح بھی کرتا رہا کر۔ " WHERE ayatNumber = 56 AND chapterNumber = 40;
UPDATE verses SET ts = "وہ لوگ جو کہ اللہ کی آیتوں کے بارے میں بغیر کسی دلیل کے جو ان کے پاس (خدا کی طرف سے) آئی ہو بحث میں لگے رہتے ہیں‘ ان کے دلوں میں بڑی بڑی خواہشیں ہیں جن کو وہ کبھی نہ پہنچیں گے۔ پس اللہ کی پناہ مانگتا رہ۔ وہ بہت سننے والا (اور) بہت دیکھنے والا ہے۔ " WHERE ayatNumber = 57 AND chapterNumber = 40;
UPDATE verses SET ts = "آسمانوں اور زمین کی پیدائش انسانوں کی پیدائش سے بڑا کام ہے۔ مگر اکثر انسان جانتے نہیں۔ " WHERE ayatNumber = 58 AND chapterNumber = 40;
UPDATE verses SET ts = "اور اندھے اور آنکھوں والے برابر نہیں ہو سکتے اور جو لوگ ایمان لے آئے اور ایمان کے مطابق انہوں نے کام کیے۔ وہ بدکار لوگوں کے (برابر نہیں ہو سکتے) تم لوگ بالکل نصیحت حاصل نہیں کرتے۔ " WHERE ayatNumber = 59 AND chapterNumber = 40;
UPDATE verses SET ts = "اور اس میں کوئی شک نہیں کہ تباہی کی گھڑی ضرور آنے والی ہے‘ لیکن اکثر لوگ ایمان نہیں لاتے۔ " WHERE ayatNumber = 60 AND chapterNumber = 40;
UPDATE verses SET ts = "اور تمہارا رب کہتا ہے مجھے پکارو‘ میں تمہاری دعا سنوں گا۔ جو لوگ ہماری عبادت کے معاملہ میں تکبر سے کام لیتے ہیں وہ ضرور جہنم میں رسوا ہو کر داخل ہونگے۔ " WHERE ayatNumber = 61 AND chapterNumber = 40;
UPDATE verses SET ts = "اللہ وہ ہے جس نے تمہارے لئے رات بنائی تاکہ تم اس میں آرام حاصل کرو‘ اور دن کو دکھانے والا بنایا ہے۔ اللہ لوگوں پر بڑا فضل کرنے والا ہے لیکن لوگوں میں سے اکثر شکر نہیں کرتے۔ " WHERE ayatNumber = 62 AND chapterNumber = 40;
UPDATE verses SET ts = "یہ اللہ تمہارا وہ رب ہے جو ہر چیز کا پیدا کرنے والا ہے اس کے سوا کوئی معبود نہیں۔ پس بتائو تو سہی تمہیں کس طرف پھیر کر لے جایا جارہا ہے۔ " WHERE ayatNumber = 63 AND chapterNumber = 40;
UPDATE verses SET ts = "اسی طرح ان لوگوں کو کم عقلی کی باتوں کی طرف پھیر کر لے جایا جاتا ہے جو اللہ کی آیتوں کا بضد انکار کرتے ہیں۔ " WHERE ayatNumber = 64 AND chapterNumber = 40;
UPDATE verses SET ts = "(حالانکہ) اللہ وہ ہے جس نے زمین کو تمہارے لئے قرار کی جگہ  بنایا ہے اور آسمان کو ایک مکان کی صورت میں (حفاظت کے لئے بنایا ہے) اور اس نے تم کو مختلف طاقتیں بخشی ہیں اور پھر ان طاقتوں کو نہایت اعلیٰ درجہ کا اور مضبوط بنایا ہے اور تم کو پاکیزہ رزق بخشا ہے۔ یہ اللہ ہے جو تمہارا رب بھی ہے۔ پس بہت برکت والا ہے اللہ جو سب جہانوں کا رب ہے۔" WHERE ayatNumber = 65 AND chapterNumber = 40;
UPDATE verses SET ts = "وہ زندہ (اور دوسروں کو زندگی دینے والا) ہے۔ اس کے سوا کوئی معبود نہیں‘ اور اللہ کے لئے عبادت کو خالص کرتے ہوئے اس کو پکارو‘ سب تعریف اللہ ہی کی ہے جو سب جہانوں کا رب ہے۔ " WHERE ayatNumber = 66 AND chapterNumber = 40;
UPDATE verses SET ts = "تو کہہ دے کہ مجھے اس سے روکا گیا ہے کہ میں ان کی عبادت کروں جن کو تم اللہ کے سوا پکارتے ہو۔ خصوصاً جبکہ میرے پاس میرے رب کی طرف سے کھلی نشانیاں بھی آچکی ہیں اور مجھے حکم دیا گیا ہے کہ میں صرف سب جہانوں کے رب کی اطاعت کروں۔ " WHERE ayatNumber = 67 AND chapterNumber = 40;
UPDATE verses SET ts = "وہی ہے جس نے تم کو (پہلے دور میں) مٹی سے پیدا کیا پھر (دوسرے دور میں) نطفہ سے‘ پھر (تیسرے دور میں) ایک خون کے لوتھڑے سے۔ پھر (اس سے) تم کو ایک بچہ بنا کر نکالتا ہے۔ پھر اس کا نتیجہ یہ ہوتا ہے کہ تم اپنی جوانی کو پہنچ جاتے ہو پھر اس کا نتیجہ یہ ہوتا ہے کہ تم اپنے بڑھاپے کو پہنچ جاتے ہو اور تم میں سے کسی کی روح اس (عمر کو پہنچنے) سے پہلے (یعنی بڑھاپے کی عمر سے پہلے) قبض کر لی جاتی ہے اور وہ ایسا اس لئے کرتا جاتا ہے کہ تم اس مدت کو پہنچ جائو جو تمہارے لئے مقرر کی گئی ہے اور تا کہ تم (اس ڈھیل کے زمانہ سے فائدہ اٹھا کر) عقل سے کام لے لو۔ " WHERE ayatNumber = 68 AND chapterNumber = 40;
UPDATE verses SET ts = "وہ خدا ہی ہے جو زندہ کرتا ہے اور مارتا ہے پس جب وہ کسی بات کا فیصلہ کر دیتا ہے تو اس کے بارہ میں کہتا ہے ہو جائے‘ سو وہ ہو جاتی ہے۔ " WHERE ayatNumber = 69 AND chapterNumber = 40;
UPDATE verses SET ts = "کیا تو نے ان لوگوں کو نہیں دیکھا جو اللہ کی آیتوں کے متعلق جھگڑتے رہتے ہیں وہ کدھر پھیر کر لے جائے جاتے ہیں۔ " WHERE ayatNumber = 70 AND chapterNumber = 40;
UPDATE verses SET ts = "یہ (اسی قسم کے) لوگ ہیں جنہوں نے ہماری کتاب کا بھی اور ہمارے رسول جو پیغام لائے ہیں ان کا بھی انکار کر دیا ہے پس اب یہ جلدی اپنے انجام کو دیکھ لیں گے۔ " WHERE ayatNumber = 71 AND chapterNumber = 40;
UPDATE verses SET ts = "جبکہ طوق اور زنجیریں ان کی گردنوں میں ہونگی اور وہ (ان زنجیروں کے ذریعہ سے جلانے والے) ۔" WHERE ayatNumber = 72 AND chapterNumber = 40;
