UPDATE verses SET ts = "پھر ان سے کہا جائے گا‘ وہ (معبود) کہاں ہیں جن کو تم اللہ کے سوا (خدائی کا) شریک بناتے تھے‘ وہ کہیں گے اس وقت وہ ہم سے کھوئے گئے ہیں (پھر کہیں گے) یوں نہیں حقیقت یہ ہے کہ ہم اس سے پہلے کسی چیز کو خدا کا شریک بناتے ہی نہیں تھے‘ اسی طرح اللہ کافروں کو گمراہ کرتا ہے۔" WHERE ayatNumber = 74 AND chapterNumber = 40;
UPDATE verses SET ts = "اللہ کے سوا (خدائی کا) شریک بناتے تھے‘ وہ کہیں گے اس وقت وہ ہم سے کھوئے گئے ہیں (پھر کہیں گے) یوں نہیں حقیقت یہ ہے کہ ہم اس سے پہلے کسی چیز کو خدا کا شریک بناتے ہی نہیں تھے‘ اسی طرح اللہ کافروں کو گمراہ کرتا ہے۔ " WHERE ayatNumber = 75 AND chapterNumber = 40;
UPDATE verses SET ts = "یہی (حقیقت) ہے جس کے باعث تم بغیر کسی دلیل کے خوشیاں منایا کرتے تھے اور جس کی وجہ سے تم بلاوجہ اترایا کرتے تھے۔ " WHERE ayatNumber = 76 AND chapterNumber = 40;
UPDATE verses SET ts = "(اب جاکر) جہنم کے دروازوں میں داخل ہو جائو (کیونکہ تمہارے لئے فیصلہ ہو چکا ہے کہ) تم اس میں رہتے چلے جائو گے اور متکبروں کا ٹھکانا بہت برا ہے۔ " WHERE ayatNumber = 77 AND chapterNumber = 40;
UPDATE verses SET ts = "پس صبر کر اللہ کا وعدہ ضرور پورا ہو کر رہے گا اور اگر ہم تجھ کو ان باتوں میں سے جن کا ان سے وعدہ کیا جاتا ہے۔ بعض (تیری وفات تک) دکھا دیں (تو بعض پیشگوئیاں تیری وفات کے بعد پوری ہونگی اور اس میں کوئی حرج نہیں) کیونکہ وہ ہماری طرف لوٹائے جائیں گے (اور وہاں ان کا انجام ان پر کھل جائے گا) ۔" WHERE ayatNumber = 78 AND chapterNumber = 40;
UPDATE verses SET ts = "اور ہم نے تجھ سے پہلے کئی رسول بھیجے تھے بعض کا ذکر ہم نے تیرے سامنے کر دیا ہے اور بعض کا ذکر ہم نے تیرے سامنے نہیں کیا اور کسی رسول کی یہ طاقت نہیں کہ خدا کے حکم کے بغیر کوئی کلام لے آئے اور جب اللہ کا حکم آجاتا ہے تو حق کے مطابق فیصلہ کر دیا جاتا ہے اور جھوٹ بولنے والے لوگ گھاٹے میں پڑ جاتے ہیں۔ " WHERE ayatNumber = 79 AND chapterNumber = 40;
UPDATE verses SET ts = "اللہ وہی ہے جس نے تمہارے لئے چارپائے پیدا کیے ہیں تا کہ ان میں سے بعض پر تم سواری کرو اور ان میں سے بعض (کا گوشت) کھائو۔ " WHERE ayatNumber = 80 AND chapterNumber = 40;
UPDATE verses SET ts = "اور ان چارپایوں میں تمہارے لئے اور بھی کئی فائدے ہیں اور یہ (غرض بھی ہے) کہ تم ان پر باربرداری وغیرہ کرکے اپنے دلوں کی حاجت پوری کر لیا کرو۔ اور ان (چارپایوں) پر اور کشتیوں پر تم کو سوار کیا جاتا ہے۔ " WHERE ayatNumber = 81 AND chapterNumber = 40;
UPDATE verses SET ts = "اور وہ (یعنی خدا) تم کو اپنے نشان دکھاتا ہے۔  پس تم اللہ کے نشانوں میں سے کس نشان کا انکار کر سکتے ہو۔ " WHERE ayatNumber = 82 AND chapterNumber = 40;
UPDATE verses SET ts = "کیا یہ زمین میں نہیں پھرے کہ دیکھتے کہ ان سے پہلوں کا انجام کیسا ہوا؟ وہ ملک میں ان سے تعداد اور طاقت میں بھی زیادہ تھے اور عمارت وغیرہ کے فنون میں بھی زیادہ (ماہر) تھے۔ لیکن ان کے اعمال نے ان کو کوئی نفع نہیں دیا تھا۔ " WHERE ayatNumber = 83 AND chapterNumber = 40;
UPDATE verses SET ts = "اور جب اُن کے پاس اُن کے رسول نشانات لے کر آئے‘ تو ان کے پاس جو تھوڑا بہت علم تھا اس پر فخر کرنے لگے اور جس (عذاب) کی وہ ہنسی اڑاتے تھے اسی نے ان کو گھیر لیا۔ " WHERE ayatNumber = 84 AND chapterNumber = 40;
UPDATE verses SET ts = "پھر جب اُنہوں نے ہمارا عذاب دیکھا تو کہہ اٹھے ہم تو اللہ کو ایک قرار دیتے ہوئے اس پر ایمان لاتے ہیں اور اس کے ساتھ جن چیزوں کو ہم شریک قرار دیا کرتے تھے ان کا ہم انکار کرتے ہیں۔ " WHERE ayatNumber = 85 AND chapterNumber = 40;
UPDATE verses SET ts = "پس جب انہوں نے ہمارا عذاب دیکھ لیا تو ان کے ایمان نے ان کو کوئی فائدہ نہیں پہنچایا۔ یہی اللہ کی مقررہ سنت ہے جو اس کے بندوں میں جاری چلی آتی ہے اور اس وقت کافر گھاٹے میں پڑ گئے۔ " WHERE ayatNumber = 86 AND chapterNumber = 40;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 41;
UPDATE verses SET ts = "حمید اور مجید (خد اکی صفات اس سورۃ میں بیان کی گئی ہیں) " WHERE ayatNumber = 2 AND chapterNumber = 41;
UPDATE verses SET ts = "یہ قرآن بے انتہا رحم کرنے والے اور بار بار کرم کرنے والے (خدا) کی طرف سے نازل ہوا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 41;
UPDATE verses SET ts = "(اور) ایسی کتاب ہے جس کی آیات خوب تفصیل کے ساتھ بیان کی گئی ہیں اور جو (کتاب) خوب پڑھی جائے گی اور (وہ ایسی زبان میں ہے جو) اپنا مطلب آپ کھول کر بیان کرتی ہے مگر (یہ کتاب) انہی کو فائدہ دیتی ہے جو (روحانی) علم رکھتے ہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 41;
UPDATE verses SET ts = "(نیکوں کو) خوشخبری دینے والی اور (بدوں کو) ہوشیار کرنے والی ہے۔ پھر (بھی) ان میں سے اکثر نے اعراض کیا اور وہ اس کے سننے کو بھی تیار نہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 41;
UPDATE verses SET ts = "اور کہتے ہیں جس چیز کی طرف تم ہمیں بلاتے ہو اس کے ماننے سے ہمارے دل پردوں میں ہیں (یعنی وہ ہمارے دلوں پر اثر نہیں کر سکتی) اور ہمارے کانوں میں بہرا پن ہے (جس کی وجہ سے ہم تمہاری بات سن ہی نہیں سکتے) اور ہمارے اور تمہارے درمیان ایک پردہ ہے۔ پس تو (اپنے عقیدہ کے مطابق) کام کر‘ ہم (اپنے عقیدہ کے مطابق) کام کریں گے۔ " WHERE ayatNumber = 6 AND chapterNumber = 41;
UPDATE verses SET ts = "تو کہہ دے‘ میں بھی تمہاری طرح کا ایک آدمی ہوں‘ میری طرف وحی کی جاتی ہے کہ تمہارا صرف ایک خدا ہے۔ پس اس کا خیال کرکے استقلال دکھائو۔ اور اس سے استغفار کرتے رہو (کہ پائوں نہ پھسل جائے) اور (یاد رکھو کہ) مشرکوں کے لئے عذاب (مقدر) ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 41;
UPDATE verses SET ts = "وہ مشرک جو نہ زکٰوۃ دیتے ہیں اور نہ آخرت پر ایمان لاتے ہیں۔ " WHERE ayatNumber = 8 AND chapterNumber = 41;
UPDATE verses SET ts = "یقیناً وہ (لوگ) جو ایمان بھی لائے اور اس کے مطابق انہوں نے عمل بھی کیے‘ ان کے لئے ایک نہ ختم ہونے والا اجر (مقدر) ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 41;
UPDATE verses SET ts = "تو کہہ دے‘ کیا تم اس خدا کا انکار کرتے ہو‘ جس نے زمین کو دو دور میں پیدا کیا ہے‘ اور اس کے شریک مقرر کرتے ہو۔ یہ (خدا تو) سب جہانوں کا رب ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 41;
UPDATE verses SET ts = "اور اس نے زمین میں اس کے اوپر پہاڑ بنائے ہیں اور اس میں بڑی برکت رکھی ہے اور اس میں رہنے والوں کے کھانے پینے کے لئے ہر چیز کو اندازہ کے مطابق بنا دیا ہے یہ سب کچھ چار دور میں کیا ہے۔ یہ بات سب پوچھنے والوں کے لئے برابر ہے۔ " WHERE ayatNumber = 11 AND chapterNumber = 41;
UPDATE verses SET ts = "پھر آسمان کی طرف متوجہ ہوا۔ اور وہ محض ایک کہر کی طرح تھا۔ اور اس نے اس سے اور زمین سے کہا دونوں اپنی مرضی سے یا مجبور ہو کر میری فرمانبرداری کے لئے آجائو۔ ان دونوں نے کہا ہم فرمانبردار ہو کر آگئے ہیں۔ " WHERE ayatNumber = 12 AND chapterNumber = 41;
UPDATE verses SET ts = "پھر اُن کو سات آسمانوں کی صورت میں دو دور میں بنایا اور ہر آسمان میں جو کچھ ہونا تھا اس کی طاقت اس میں ودیعت کر دی اور ہم نے نچلے آسمان کو روشنیوں کے ساتھ خوبصورت بنایا اور (علاوہ خوبصورت بنانے کے) حفاظت کے لئے بھی (اس میں سامان پیدا کیا) (اور) یہ غالب (اور) جاننے والے خدا کی تقدیر ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 41;
UPDATE verses SET ts = "پھر اگر وہ اعراض کریں تو ان سے کہہ دے کہ میں نے تم کو اس عذاب سے جو عاد اور ثمود کا سا عذاب ہے ہوشیار کر دیا ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 41;
UPDATE verses SET ts = "یعنی جب اُن کے پاس اُن کے آگے سے بھی اور اُن کے پیچھے سے بھی رسول آئے (یعنی قومی زندگی کے دوران میں متواتر رسول آئے) یہ کہتے ہوئے کہ اللہ کے سوا کسی کی عبادت نہ کرو‘ تو انہوں نے ان کو جواب دیا کہ اگر ہمارا رب چاہتا تو ہم پر خود فرشتے نازل کر دیتا۔ پس ہم اس تعلیم کا جس کے ساتھ تم کو بھیجا گیا ہے انکار کرتے ہیں۔ " WHERE ayatNumber = 15 AND chapterNumber = 41;
UPDATE verses SET ts = "اور عاد کی قوم نے زمین میں بغیر کسی حق کے تکبر سے کام لیا اور کہا‘ ہم سے زیادہ قوت میں کون ہے کیا انہوں نے دیکھا نہیں کہ جس خدا نے ان کو پیدا کیا ہے۔ وہ ان سے زیادہ طاقتور ہے اور وہ ہماری آیتوں کا بضد انکار کرتے تھے۔ " WHERE ayatNumber = 16 AND chapterNumber = 41;
UPDATE verses SET ts = "پس ہم نے ان پر ایک ایسی ہوا بھیجی جو بڑی تیز تھی اور منحوس دنوں میں آئی تھی تاکہ ہم انہیں اس دنیا میں رسوائی کا عذاب چکھائیں اور اخروی زندگی کا عذاب اس سے بھی بڑھ کر رسوائی کا ہے اور (وہاں) ان کی کسی رنگ میں مدد نہ کی جائے گی۔ " WHERE ayatNumber = 17 AND chapterNumber = 41;
UPDATE verses SET ts = "اور ثمود (کا یہ حال تھا کہ) ہم نے اُن کو ہدایت کا راستہ دکھایا تھا لیکن انہوں نے گمراہی کو ہدایت پر ترجیح دی۔ پس اُن کو ایک رسوائی کے عذاب نے اُن کے اعمال کی وجہ سے آگھیرا۔ " WHERE ayatNumber = 18 AND chapterNumber = 41;
UPDATE verses SET ts = "اور جو متقی تھے اور مومن تھے ان کو ہم نے نجات دے دی۔ " WHERE ayatNumber = 19 AND chapterNumber = 41;
UPDATE verses SET ts = "اور جس دن اللہ کے دشمن (یعنی کافر) زندہ کرکے آگ کی طرف لے جائے جائیں گے پھر ان کو مختلف درجوں میں تقسیم کر دیا جائے گا۔ " WHERE ayatNumber = 20 AND chapterNumber = 41;
UPDATE verses SET ts = "یہاں تک کہ جب وہ دوزخ کے پاس پہنچ جائیں گے ان کے کان اور ان کی آنکھیں اور ان کے چمڑے ان کے عمل کی وجہ سے ان کے خلاف گواہی دیں گے۔ " WHERE ayatNumber = 21 AND chapterNumber = 41;
UPDATE verses SET ts = "اور وہ اپنے چمڑوں سے کہیں گے تم نے ہمارے خلاف گواہی کیوں دی؟ اور ان کے چمڑے جواب میں کہیں گے‘ ہم سے اسی خدا نے کلام کروایا ہے جس نے ہر چیز سے کلام کروایا ہے اور اس نے تم کو پہلی دفعہ بھی پیدا کیا تھا اور پھر بھی تم اس کی طرف لوٹائے جائو گے۔ " WHERE ayatNumber = 22 AND chapterNumber = 41;
UPDATE verses SET ts = "اور تم اپنے عیب اس خوف سے نہیں چھپایا کرتے تھے کہ کہیں تمہارے کان اور تمہاری آنکھیں اور تمہارے چمڑے تمہارے خلاف گواہی نہ دے دیں (بلکہ دوسرے لوگوں کی انگشت نمائی کے ڈر سے ایسا کرتے تھے) بلکہ تم کو یقین تھا‘ کہ اللہ کو تو بہت سی تمہاری باتوں کا علم ہی نہیں۔ " WHERE ayatNumber = 23 AND chapterNumber = 41;
UPDATE verses SET ts = "اور یہی وہ بدظنی تھی جو تم نے اپنے رب کے متعلق کی جس نے تم کو ہلاک کر دیا اور تم ہر طرح نقصان اٹھانے والوں میں سے ہو گئے۔ " WHERE ayatNumber = 24 AND chapterNumber = 41;
UPDATE verses SET ts = "پس اگر یہ لوگ صبر سے کام لیں تو آگ ان کا ٹھکانا ہے اور اگر یہ خدا کے حضور میں حاضر ہونا چاہیں تو ان کو خدا کے حضور میں حاضر ہونے کی اجازت نہیں دی جائے گی۔ " WHERE ayatNumber = 25 AND chapterNumber = 41;
UPDATE verses SET ts = "اور ہم نے ان کے ساتھ کچھ ایسے ہم نشین لگا دئیے جنہوں نے ان کے اعمال کو خوبصورت کرکے دکھایا۔ اور اُن کے اوپر وہی حکم جاری ہو گیا جو جنوں اور انسانوں میں سے ان سے پہلے گذری ہوئی قوموں پر جاری ہوا تھا (یعنی یہ) کہ وہ گھاٹا پانے والوں میں سے ہو جائیں گے۔ " WHERE ayatNumber = 26 AND chapterNumber = 41;
UPDATE verses SET ts = "اور کفار نے کہا‘ اس قرآن کی تعلیم مت سنو اور اس کے سنانے کے وقت شور مچا دو۔ تا کہ اس طرح تم غالب آجائو۔ " WHERE ayatNumber = 27 AND chapterNumber = 41;
UPDATE verses SET ts = "سو اس کے نتیجہ میں ہم کفار کو ضرور سخت عذاب کا مزہ چکھائیں گے اور ان کے بدترین اعمال کی جزا ان کو دیں گے۔ " WHERE ayatNumber = 28 AND chapterNumber = 41;
UPDATE verses SET ts = "(اور) اللہ کے دشمنوں کی جزا یہی ہے یعنی آگ‘ اس میں ایک لمبے عرصہ تک رہنے والا گھر ان کو ملے گا یہ جزا انہیں اس لئے ملے گی کہ وہ ہماری آیتوں کا بضد انکار کرتے تھے۔ " WHERE ayatNumber = 29 AND chapterNumber = 41;
UPDATE verses SET ts = "اور کفار کہیں گے کہ اے ہمارے رب! تو ہمیں جن و انس میں سے وہ لوگ دکھا جو ہمیں گمراہ کیا کرتے تھے۔ تا کہ ہم ان کو اپنے پیروں کے نیچے مسلیں اور اس کے نتیجہ میں وہ ذلیل ترین لوگ بن جائیں۔ " WHERE ayatNumber = 30 AND chapterNumber = 41;
UPDATE verses SET ts = "وہ لوگ جنہوں نے کہا کہ اللہ ہمارا رب ہے پھر مستقل مزاجی سے اس عقیدہ پر قائم ہو گئے‘ اُن پر فرشتے اتریں گے یہ کہتے ہوئے کہ ڈرو نہیں اور کسی پچھلی غلطی کا غم نہ کرو اور اس جنت کے ملنے سے خوش ہو جائو‘ جس کا تم سے وعدہ کیا گیا تھا۔ " WHERE ayatNumber = 31 AND chapterNumber = 41;
UPDATE verses SET ts = "ہم دنیا میں بھی تمہارے دوست ہیں اور آخرت میں بھی تمہارے دوست رہیں گے اور اس (جنت) میں جو کچھ تمہارے جی چاہیں گے تم کو ملے گا اور جو کچھ تم مانگو گے وہ بھی تم کو اس میں ملے گا۔ " WHERE ayatNumber = 32 AND chapterNumber = 41;
UPDATE verses SET ts = "یہ بخشنے والے (اور) بے انتہا کرم کرنے والے خدا کی طرف سے مہمانی کے طور پر ہوگا۔ " WHERE ayatNumber = 33 AND chapterNumber = 41;
UPDATE verses SET ts = "اور اس سے زیادہ اچھی بات کس کی ہوگی جو کہ اللہ کی طرف لوگوں کو بلاتا ہے اور اپنے ایمان کے مطابق عمل کرتا ہے اور کہتا ہے کہ میں تو فرمانبرداروں میں سے ہوں۔ " WHERE ayatNumber = 34 AND chapterNumber = 41;
UPDATE verses SET ts = "اور نیکی اور بدی برابر نہیں ہو سکتی۔ اور تو برائی کا جواب نہایت نیک سلوک سے دے اس کا نتیجہ یہ ہوگا کہ وہ شخص کہ اس کے اور تیرے درمیان عداوت پائی جاتی ہے‘ وہ تیرے حسن سلوک کو دیکھ کر ایک گرم جوش دوست بن جائے گا۔ " WHERE ayatNumber = 35 AND chapterNumber = 41;
UPDATE verses SET ts = "اور (باوجود ظلموں کے سہنے کے) اس (قسم کے سلوک) کی توفیق صرف انہی کو ملتی ہے جو بڑے صبر کرنے والے ہیں اور یا پھر ان کو ملتی ہے جن کو (خدا کی طرف سے نیکی کا) ایک بہت بڑا حصہ ملا ہو۔ " WHERE ayatNumber = 36 AND chapterNumber = 41;
UPDATE verses SET ts = "اور اگر شیطان (یعنی حق سے دور ہستی) تجھے تکلیف پہنچائے تو (فوراً  اس کا بدلہ لینے کے لئے تیار نہ ہو جایا کر بلکہ) اللہ سے پناہ مانگا کر (کہ وہ تجھے اس ادنیٰ درجہ کے خلق سے بچائے) اللہ یقیناً بہت سننے والا (اور) بہت جاننے والا ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 41;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے رات بھی ہے اور دن بھی اور سورج بھی ہے اور چاند بھی۔ نہ سورج کو سجدہ کرو نہ چاند کو۔ بلکہ صرف اللہ کو جس نے ان دونوں کو پیدا کیا ہے‘ سجدہ کرو اگر تم پکے موحد ہو۔ " WHERE ayatNumber = 38 AND chapterNumber = 41;
UPDATE verses SET ts = "پھر اگر یہ لوگ تکبر کریں‘ تو یاد رکھ کہ وہ لوگ جو تیرے رب کے مقرب ہیں وہ رات اور دن اسی کی تسبیح کرتے ہیں اور وہ کبھی نہیں تھکتے۔ " WHERE ayatNumber = 39 AND chapterNumber = 41;
UPDATE verses SET ts = "اور اس کے نشانوں میں سے (ایک نشان) یہ بھی ہے کہ تو زمین کو (بعض وقتوں میں) ویران دیکھتا ہے پھر جب ہم اس پر پانی اتارتے ہیں تو وہ ایک نئی زندگی پا لیتی ہے اور (سبزی کو) خوب بڑھاتی ہے۔ وہ (خدا) جس نے اس (زمین) کو زندہ کیا ہے وہ یقیناً مردوں کو بھی زندہ کرے گا۔ وہ ہر ایک چیز پر قادر ہے۔ " WHERE ayatNumber = 40 AND chapterNumber = 41;
UPDATE verses SET ts = "وہ لوگ جو ہماری آیات میں سے پیچ در پیچ غلط باتیں نکالتے رہتے ہیں وہ ہم سے مخفی نہیں۔ کیا وہ شخص جو دوزخ میں ڈالا جائے زیادہ اچھا ہے یا وہ جو قیامت کے دن امن کے ساتھ (ہمارے پاس) آئے۔ (اے لوگو) جو کچھ چاہو کرو۔ اللہ تمہارے اعمال کو اچھی طرح دیکھ رہا ہے۔ " WHERE ayatNumber = 41 AND chapterNumber = 41;
UPDATE verses SET ts = "وہ لوگ جو کہ اس ذکر (یعنی قرآن) کا جب وہ ان کے پاس آیا انکار کرتے ہیں حالانکہ وہ ایک بڑی عزت والی کتاب ہے (وہ اپنی تباہی کا سامان اپنے ہاتھوں کر رہے ہیں) ۔" WHERE ayatNumber = 42 AND chapterNumber = 41;
UPDATE verses SET ts = "باطل نہ اس کے آگے سے آسکتا ہے نہ اس کے پیچھے سے‘ بڑی حکمتوں والے اور بڑی تعریفوں والے خدا کی طرف سے وہ اترا ہے۔ " WHERE ayatNumber = 43 AND chapterNumber = 41;
UPDATE verses SET ts = "تجھ سے صرف وہی باتیں کہی جاتی ہیں۔ جو تجھ سے پہلے رسولوں سے کہی گئی تھیں۔ تیرا رب بڑی بخشش والا ہے اور اس کا عذاب بھی دردناک ہوتا ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 41;
UPDATE verses SET ts = "اگر ہم اس قرآن کو اعجمی بناتے (یعنی عربی زبان کے سوا کسی اور زبان میں اتارتے) تو وہ (مکے والے) کہہ سکتے تھے کہ اس کی آیتیں کھول کر کیوں نہیں بیان کی گئیں۔ کیا اعجمی زبان اور عربی نبی کوئی بھی مشابہت رکھتے ہیں؟ تو کہہ دے وہ مومنوں کے لئے ہدایت اور شفا رکھتا ہے (یعنی عربی میں اتارا گیا ہے اور مکہ کے لوگ اسے سمجھ سکتے ہیں) اور جو ایمان ہی نہیں لاتے ان کے کانوں میں تو بہرا پن ہے اور وہ (یعنی اس کی حقیقت) ان پر مخفی ہے وہ لوگ ایسے ہی ہیں جیسے کسی کو دور کی جگہ سے پکارا جائے۔ " WHERE ayatNumber = 45 AND chapterNumber = 41;
UPDATE verses SET ts = "اور ہم نے موسیٰ کو بھی ایک اعلیٰ کتاب دی تھی اور اس کے بارہ میں بھی اختلاف کیا گیا تھا اور اگر ایک بات تیرے رب کی طرف سے پہلے گذر نہ چکی ہوتی تو ان لوگوں کا کبھی کا فیصلہ ہو چکا ہوتا۔ وہ اس (قرآن) کے بارے میں ایک ایسے شک میں پڑے ہوئے ہیں جو ان کے امن کو کاٹ دیتا ہے (یعنی انتہائی شکوک میں مبتلا رہتے ہیں اور اس پر غور نہیں کرتے) ۔" WHERE ayatNumber = 46 AND chapterNumber = 41;
UPDATE verses SET ts = "جو شخص ایمان کے مطابق عمل کرے تو اس کا فائدہ اس کی اپنی جان کو پہنچتا ہے اور جو بداعمالی کرے اُس کا عذاب بھی اسی پر نازل ہوتا ہے اور تیرا رب اپنے بندوں پر کوئی ظلم نہیں کرتا۔ " WHERE ayatNumber = 47 AND chapterNumber = 41;
UPDATE verses SET ts = "قیامت کا علم اسی کی طرف لوٹایا جاتا ہے (یعنی قیامت کا کامل علم اسی کو حاصل ہے) اور گابھوں میں سے (بھی) کوئی پھل نہیں نکلتے اور عورتیں بھی اپنے پیٹ میں کچھ نہیں اٹھاتیں اور نہ جنتی ہیں مگر وہ اللہ کو معلوم ہوتا ہے جس دن وہ لوگوں کو پکارے گا اور کہے گا کہاں ہیں میرے شریک؟ وہ کہیں گے ہم نے تجھے کھول کر کہہ دیا ہے‘ کہ ہم میں سے کوئی اس بات کا گواہ نہیں۔ " WHERE ayatNumber = 48 AND chapterNumber = 41;
UPDATE verses SET ts = "اور جن (معبودوں) کو وہ اس سے پہلے پکارتے تھے وہ ان سے کھوئے جائیں گے اور ان کو یقین ہو جائے گا کہ اب ان کے لئے کہیں بھاگ کر جانے کی جگہ نہیں۔ " WHERE ayatNumber = 49 AND chapterNumber = 41;
UPDATE verses SET ts = "انسان اچھی چیزوں کے مانگنے سے کبھی نہیں تھکتا۔ لیکن اگر اسے کبھی کوئی تکلیف پہنچ جائے تو (پہلی حالت کو بھول کر) مایوس ہو جاتا ہے۔ " WHERE ayatNumber = 50 AND chapterNumber = 41;
UPDATE verses SET ts = "اور اگر ہم اس کو دکھ پہنچنے کے بعد رحمت کا مزہ چکھائیں تو وہ کہتا ہے یہ تو میرا حق ہی ہے اور مجھے یقین ہے کہ قیامت نہیں آئے گی اور اگر مجھے میرے رب کی طرف لوٹایا بھی گیا تو اس کے پاس میرے لئے (اس دنیا سے) اعلیٰ انعامات موجود ہوں گے اور ہم کافروں کو ضرور ان کے اعمال کے متعلق خبر دیں گے اور ان کو ایک سخت عذاب چکھائیں گے۔ " WHERE ayatNumber = 51 AND chapterNumber = 41;
UPDATE verses SET ts = "اور جب ہم انسان پر انعام کرتے ہیں تو وہ منہ پھیر لیتا ہے اور ہم سے ایک طرف ہو جاتا ہے اور جب اسے کوئی تکلیف پہنچتی ہے تو بڑی لمبی لمبی دعائیں کرتا ہے۔ " WHERE ayatNumber = 52 AND chapterNumber = 41;
UPDATE verses SET ts = "تو ان سے کہہ دے مجھے بتائو تو سہی اگر یہ (قرآن) اللہ کی طرف سے ہو اور تم اس کا انکار کر دو۔ تو اس شخص سے جو حق سے بہت دور بھاگ جائے کون زیادہ گمراہ ہوگا؟ " WHERE ayatNumber = 53 AND chapterNumber = 41;
UPDATE verses SET ts = "ہم ان لوگوں کو تمام اطراف عالم میں بھی ضرور اپنے نشان دکھائیں گے اور خود ان کی جانوں (اور خاندانوں) میں بھی۔ یہاں تک کہ یہ (امر) ان کے لئے بالکل ظاہر ہو جائے گا کہ یہ (قرآن) حق ہے کیا تیرے رب کا ہر چیز پر نگران ہونا ان کے لئے کافی نہیں؟ " WHERE ayatNumber = 54 AND chapterNumber = 41;
UPDATE verses SET ts = "خبردار! یہ لوگ اپنے رب کی ملاقات کے بارہ میں شبہ میں پڑے ہوئے ہیں کان کھول کر سنو! اللہ ہر چیز پر حاوی (اور اس کے تباہ کرنے پر قادر) ہے۔ " WHERE ayatNumber = 55 AND chapterNumber = 41;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) ۔" WHERE ayatNumber = 1 AND chapterNumber = 42;
UPDATE verses SET ts = "(یہ سورۃ) حمید (و) مجید ۔" WHERE ayatNumber = 2 AND chapterNumber = 42;
UPDATE verses SET ts = "علیم‘ سمیع (اور) قدیر (خدا نے اُتاری ہے) ۔" WHERE ayatNumber = 3 AND chapterNumber = 42;
UPDATE verses SET ts = "اسی طرح اللہ تجھ پر بھی وحی کرتا ہے اور اُن پر بھی (وحی کر چکا ہے) جو (لوگ) تجھ سے پہلے گذر چکے ہیں وہ غالب بھی ہے (اور) حکمت والا بھی ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 42;
UPDATE verses SET ts = "آسمانوں اور زمین میں جو کچھ ہے سب اسی کا ہے۔ اور وہ بلند شان ہے۔ اور اس کی حکومت ہر طبقہ کی مخلوق پر پھیلی ہوئی ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 42;
UPDATE verses SET ts = "قریب ہے کہ آسمان اپنے اوپر (غالب ذات کے حکم کی وجہ) سے پھٹ کر گر جائیں باوجود اس کے کہ ملائکہ اپنے رب کی حمد بھی کررہے ہیں اور تسبیح بھی کر رہے ہیں اور جو لوگ زمین میں ہیں ان کے لئے استغفار بھی کر رہے ہیں (پھر بھی خدا اپنے عذاب کو روکے گا نہیں تا کہ اس کی غفاری اور رحیمیت ظاہر ہو) سنو اللہ بہت بخشنے والا (اور) بے انتہا رحم کرنے والا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 42;
UPDATE verses SET ts = "وہ لوگ جو اللہ کے سوا کسی کو اپنا پناہ دینے والا بناتے ہیں اللہ نے ان کے خلاف پڑنے والے سب اعمال کو محفوظ کر چھوڑا ہے اور تو ان پر نگران نہیں (خدا ہی نگران ہے) ۔" WHERE ayatNumber = 7 AND chapterNumber = 42;
UPDATE verses SET ts = "اور اسی طرح (یعنی اپنے نگران ہونے کے ثبوت میں) ہم نے قرآن کو عربی زبان میں تیری طرف اتارا ہے تا کہ تو ملک کے مرکز کے لوگوں کو اور اس کے اردگرد کے رہنے والوں کو ہوشیار کرے اور تا کہ تو اس وقت سے ہوشیار کرے جب تمام لوگ جمع کیے جائیں گے جس کے آنے میں کوئی شبہ نہیں اس دن ایک گروہ تو جنت میں جائے گا اور ایک گروہ دوزخ میں جائے گا۔ " WHERE ayatNumber = 8 AND chapterNumber = 42;
UPDATE verses SET ts = "اور اگر اللہ اپنی مرضی سے کام لیتا (یعنی جبر کرتا) تو ان سب کو ایک جماعت بنا دیتا لیکن وہ جس کو چاہتا ہے اپنی رحمت میں داخل کرتا ہے اور ظالموں کا نہ کوئی پناہ دہندہ ہوتا ہے نہ مددگار۔ " WHERE ayatNumber = 9 AND chapterNumber = 42;
UPDATE verses SET ts = "کیا انہوں نے اللہ کے سوا کسی دوسرے کو پناہ دینے والا تجویز کیا ہے؟ پس (یاد رہے کہ) اللہ ہی پناہ دینے والا ہے اور وہی مردے زندہ کرتا ہے اور وہ اپنے ہر ارادہ کو پورا کرنے پر قادر ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جس کسی (امر) میں تم اختلاف کرو اس کا آخری فیصلہ اللہ ہی کے قبضہ میں ہے یہ ہے تمہارا اللہ جو میرا بھی رب ہے۔ میں نے اسی پر توکل کیا ہے اور اُسی کی طرف میں جھکتا ہوں۔ " WHERE ayatNumber = 11 AND chapterNumber = 42;
UPDATE verses SET ts = "وہ آسمانوں اور زمین کا پیدا کرنے والا ہے اس نے تمہاری ہی جنس سے تمہارے ساتھی بنائے ہیں اور چارپایوں کے بھی جوڑے بنائے ہیں اور اس طرح وہ تم کو زمین میں بڑھاتا ہے اس جیسی کوئی چیز نہیں اور وہ بہت سننے والا (اور) دیکھنے والا ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 42;
UPDATE verses SET ts = "آسمانوں کی کنجیاں بھی اس کے ہاتھ میں ہیں اور زمین کی بھی وہ جس کے لئے چاہتا ہے رزق میں فراخی دے دیتا ہے اور (جس کے لئے چاہتا ہے رزق میں) تنگی کر دیتا ہے وہ ہر ایک امر کو خوب اچھی طرح جانتا ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 42;
UPDATE verses SET ts = "اس (اللہ) نے تجھ کو (اصولی طور پر) وہی دین دیا ہے جس کی تاکید اس نے نوح کو کی تھی اور جو ہم نے اب تجھ پر (قرآن کے ذریعہ سے) اتارا ہے اور جس کی تا کید ہم نے ابراہیم اور موسیٰ اور عیسیٰ کو کی تھی اور وہ یہ تھا کہ (اللہ کی) اطاعت کو دنیا میں قائم کرو‘ اور اس (دین) کے بارہ میں اختلاف کبھی نہ کیا کرو۔ مشرکوں پر وہ (تعلیم بڑی) گراں گذرتی ہے جس کی طرف تو ان کو بلاتا ہے حالانکہ اللہ کی نظر میں جو پسندیدہ ہوتا ہے وہ اسے اس (دین) کے لئے چن لیتا ہے اور یہ تعلیم اسی کو ملتی ہے جو خدا کی طرف ہمیشہ جھکا رہتا ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 42;
UPDATE verses SET ts = "اور کفار نے دین کے معاملہ میں اختلاف نہیں کیا مگر اس کے بعد کہ ان کے پاس ایک کامل علم (یعنی قرآن) آگیا اور یہ اختلاف انہوں نے (کسی عقلی دلیل کی وجہ سے نہیں کیا بلکہ) باہمی بغض و حسد کی وجہ سے کیا ہے اور اگر تیرے رب کی طرف سے ایک مدت مقررہ تک کے لئے ایک بات کا فیصلہ نہ ہو چکا ہوتا تو ان کفار کی تباہی کبھی کی ہو چکی ہوتی اور وہ لوگ جن کو ان (پہلے لوگوں) کے بعد کتاب کا وارث کیا گیا ہے وہ اس کتاب کی وجہ سے ایک ایسے شک میں مبتلا ہیں جو ان کے دلوں میں قلق پیدا کر رہا ہے۔ " WHERE ayatNumber = 15 AND chapterNumber = 42;
UPDATE verses SET ts = "پس تو (اسی دین کی طرف) لوگوں کو پکار۔ اور تو (اسی طرح دین پر) استقلال سے قائم رہ جس طرح تجھے کہا گیا ہے اور ان کی خواہشوں کی پیروی نہ کر اور کہہ دے کہ اللہ نے اپنی کتاب سے جو کچھ اتارا ہے میں اس پر ایمان لاتا ہوں اور مجھے حکم دیا گیا ہے کہ میں تمہارے درمیان عدل سے فیصلہ کروں۔ اللہ ہمارا بھی رب ہے اور تمہارا بھی رب (ہے) ہمارے اعمال ہمارے ساتھ ہیں اور تمہارے اعمال تمہارے ساتھ (ہیں) تمہارے اور ہمارے درمیان کوئی جھگڑا نہیں۔ اللہ ہمیں (ایک دن) جمع کر دے گا اور اسی کی طرف ہم سب کو لوٹ کر جانا ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 42;
UPDATE verses SET ts = "اور وہ لوگ جو اللہ کے بارہ میں بحثیں کرتے ہیں جبکہ (لوگوں کی ایک بڑی تعداد) اس کی آواز کو قبول کر چکی ہے ان کی دلیل ان کے رب کے حضور توڑی جانے والی ہے ان پر غضب نازل ہوگا اور ان کے لئے سخت عذاب (مقدر) ہے۔ " WHERE ayatNumber = 17 AND chapterNumber = 42;
UPDATE verses SET ts = "اللہ وہی ہے جس نے حق کے ساتھ اس کامل کتاب (قرآن) اور میزان کو اتارا ہے اور تم کو کس (بات) نے بتایا ہے کہ مقررہ وقت قریب آگیا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 42;
UPDATE verses SET ts = "جو لوگ اس پر ایمان نہیں لاتے وہ اس کے جلد لانے کے خواہاں ہیں اور مومن تو اس سے ڈرتے رہتے ہیں اور یقین رکھتے ہیں کہ وہ واقع ہو کر رہنے والی ہے۔ سنو! جو لوگ قیامت میں شبہ کرتے ہیں‘ وہ ایک لاعلاج گمراہی میں مبتلا ہیں۔ " WHERE ayatNumber = 19 AND chapterNumber = 42;
UPDATE verses SET ts = "اللہ اپنے بندوں کے پوشیدہ رازوں کو جانتا ہے جس کو چاہتا ہے رزق وافر دیتا ہے اور جس کے متعلق چاہتا ہے رزق میں کمی کر دیتا ہے۔ وہ بڑی طاقت والا (اور) غالب ہے۔ " WHERE ayatNumber = 20 AND chapterNumber = 42;
UPDATE verses SET ts = "جو کوئی آخرت کی کھیتی چاہتا ہے ہم اس کی (آخرت کی) کھیتی کو اس کے لئے بڑھاتے چلے جاتے ہیں اور جو کوئی اس دنیا کی کھیتی چاہتا ہے ہم اس (کو اسی دنیا کی کھیتی) میں سے اس کا حصہ دے دیتے ہیں (یعنی دنیوی متاع اسے مل جاتا ہے) اور آخرت میں اس کا کوئی حصہ نہیں ہوتا۔ " WHERE ayatNumber = 21 AND chapterNumber = 42;
UPDATE verses SET ts = "کیا ان کے لئے کوئی ایسے شریک ہیں جنہوں نے ان کے لئے ایسی دینی تعلیم تجویز کی ہے جس کا اللہ نے حکم نہیں دیا اور اگر (اللہ تعالیٰ کی طرف سے) آخری فیصلہ نہ ہو چکا ہوتا تو ان کے درمیان فیصلہ کر دیا جاتا اور ظالموں کو یقیناً دردناک عذاب پہنچے گا۔ " WHERE ayatNumber = 22 AND chapterNumber = 42;
UPDATE verses SET ts = "تو ظالموں کو دیکھتا ہے کہ وہ اپنے عمل کی وجہ سے ڈر رہے ہیں حالانکہ وہ (موعود عذاب) ان پر ضرور نازل ہونے والا ہے اور وہ لوگ جو ایمان لائے اور ایمان کے مطابق عمل کیا‘ وہ گھنے باغوں میں ہوں گے (اور) اُن کے رب کے پاس ان کے لئے وہ سب کچھ موجود ہوگا (جس کی ان کو خواہش ہوگی) یہی بڑا فضل ہے۔ " WHERE ayatNumber = 23 AND chapterNumber = 42;
UPDATE verses SET ts = "یہی وہ چیز ہے جس کی اللہ اپنے مومن بندوں کو بشارت دیتا ہے‘ ایسے مومن بندوں کو جو ایمان کے مطابق عمل بھی کرتے ہیں۔ تو کہہ دے میں اپنی خدمت کے بدلہ میں تم سے کوئی اجر نہیں مانگتا‘ سوائے اس محبت کے جو اپنے قریب ترین رشتہ داروں سے کی جاتی ہے اور جو شخص کوئی نیکی کا کام کرتا ہے ہم اس کی نیکی کو اس کے لئے اور زیادہ حسین بنا دیتے ہیں اللہ بہت بخشنے والا (اور) قدر دان ہے۔ " WHERE ayatNumber = 24 AND chapterNumber = 42;
UPDATE verses SET ts = "کیا وہ کہتے ہیں کہ اس شخص (یعنی محمد رسول اللہ صلی اللہ علیہ وسلم) نے خدا پر جھوٹ باندھا ہے۔ پس اگر اللہ چاہے تو تیرے دل پر مہر لگا دے۔ اور اللہ ہمیشہ باطل کو مٹا دیتا ہے اور حق کو اپنے نشانوں کے ذریعہ قائم کر دیتا ہے۔ وہ دلوں کی باتوں کو جانتا ہے۔ " WHERE ayatNumber = 25 AND chapterNumber = 42;
UPDATE verses SET ts = "اور وہی ہے جو اپنے بندوں کی توبہ کو قبول کرتا ہے اور ان کی غلطیوں کو معاف کرتا ہے اور جو کچھ تم کرتے ہو اسے جانتا ہے۔ " WHERE ayatNumber = 26 AND chapterNumber = 42;
UPDATE verses SET ts = "اور مومنوں اور ایمان کے مطابق عمل کرنے والوں کی دعائوں کو قبول کرتا ہے اور اپنے فضل سے (جتنے بدلہ کے وہ مستحق ہوتے ہیں اس سے بھی) زیادہ ان کو دیتا ہے‘ اور کافروں کے لئے سخت عذاب مقرر ہے۔ " WHERE ayatNumber = 27 AND chapterNumber = 42;
UPDATE verses SET ts = "اور اگر اللہ اپنے بندوں کے لئے رزق کو بہت وسیع کر دیتا تو وہ ملک میں بہت سرکشی کرنے لگ جاتے‘ لیکن وہ جو کچھ چاہتا ہے اندازہ کے مطابق اتارتا ہے وہ اپنے بندوں کے حالات سے باخبر (اور) ان کے حالات کو دیکھنے والا ہے۔ " WHERE ayatNumber = 28 AND chapterNumber = 42;
UPDATE verses SET ts = "اور وہی ہے جو مایوسی کے بعد بارش اتارتا ہے اور اپنی رحمت کو پھیلا دیتا ہے‘ اور وہی (سچا) پناہ دینے والا (اور) سب تعریفوں کا مالک ہے۔ " WHERE ayatNumber = 29 AND chapterNumber = 42;
UPDATE verses SET ts = "اور آسمانوں اور زمین کی پیدائش اور جو کچھ ان دونوں کے درمیان جانداروں کی قسم سے اس نے پھیلایا ہے اس کے نشانوں میں سے ہے اور جب وہ چاہے گا ان سب کے جمع کرنے پر قادر ہوگا۔ " WHERE ayatNumber = 30 AND chapterNumber = 42;
UPDATE verses SET ts = "اور ہر مصیبت جو تم کو پہنچے وہ تمہارے اعمال کے سبب سے ہوتی ہے اور اللہ تمہارے اکثر قصوروں کو معاف کرتا ہے۔ " WHERE ayatNumber = 31 AND chapterNumber = 42;
UPDATE verses SET ts = "اور تم (اللہ کو) اس کے ارادے میں کہیں بھی زمین پر ناکام نہیں کر سکتے اور اللہ کے سوا تمہیں کوئی پناہ دینے والا نہیں‘ نہ کوئی تمہارا مددگار ہے۔ " WHERE ayatNumber = 32 AND chapterNumber = 42;
UPDATE verses SET ts = "اور اس کے نشانات میں سے پہاڑوں کی طرح سمندر میں چلنے والی کشتیاں ہیں (یعنی جو بہت اونچی اونچی ہوتی ہیں) " WHERE ayatNumber = 33 AND chapterNumber = 42;
UPDATE verses SET ts = "اگر وہ چاہے تو ہوا کو ٹھہرا دے اور وہ سمندر کی سطح پر کھڑی کی کھڑی رہ جائیں۔ اس میں ہر صبر کرنے والے قدر دان کے لئے نشانات ہیں۔ " WHERE ayatNumber = 34 AND chapterNumber = 42;
UPDATE verses SET ts = "یا اگر وہ چاہے تو کشتی والوں کو ان کے اعمال کی وجہ سے ہلاک کر دے اور وہ بہت سے گناہوں کو معاف کر دیتا ہے۔ " WHERE ayatNumber = 35 AND chapterNumber = 42;
UPDATE verses SET ts = "اور تا وہ لوگ جو ہمارے نشانوں میں بحث کرتے ہیں جان لیں کہ ان کے لئے بھاگنے کی کوئی جگہ نہیں۔ " WHERE ayatNumber = 36 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جو کچھ بھی تم کو دیا گیا ہے وہ ورلی زندگی کا سامان ہے اور جو اللہ کے پاس ہے وہ مومنوں اور اپنے رب پر توکل کرنے والوں کے لئے زیادہ اچھا اور زیادہ باقی رہنے والا ہے۔ " WHERE ayatNumber = 37 AND chapterNumber = 42;
UPDATE verses SET ts = "اور (ان کے لئے) جو بڑے گناہوں اور بدکاری سے بچتے ہیں اور جب ان کو غصہ آتا ہے تو معاف کر دیتے ہیں۔ " WHERE ayatNumber = 38 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جو اپنے رب کی آواز کو قبول کر لیتے ہیں اور نمازیں باجماعت ادا کرتے ہیں اور ان کا طریق یہ ہے کہ اپنے ہر معاملہ کو باہمی مشورہ سے طے کرتے ہیں اور جو کچھ ہم نے ان کودیا ہے اس میں سے خرچ کرتے ہیں۔ " WHERE ayatNumber = 39 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جب ان پر کوئی ظلم ہوتا ہے تو وہ بدلہ تو لے لیتے ہیں (مگر یہ یاد رکھتے ہیں) ۔" WHERE ayatNumber = 40 AND chapterNumber = 42;
UPDATE verses SET ts = "کہ بدی کا بدلہ اتنی ہی بدی ہوتی ہے اور جو معاف کرے اور اصلاح کو مدنظر رکھے تو اس کو بدلہ دینا اللہ کے ذمے ہوتا ہے وہ (اللہ) ظالموں کو پسند نہیں کرتا۔ " WHERE ayatNumber = 41 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جو لوگ اپنی ذات پر ظلم کیے جانے کے بعد مناسب بدلہ لیتے ہیں۔ ان پر کسی قسم کا اعتراض نہیں ہو سکتا۔ " WHERE ayatNumber = 42 AND chapterNumber = 42;
UPDATE verses SET ts = "اعتراض صرف ان لوگوں پر ہوتا ہے جو لوگوں پر ظلم (اور حملہ) میں ابتدا کرتے ہیں اور زمین میں بغیر کسی حق کے زیادتی کرنے لگ جاتے ہیں ایسے لوگوں کو دردناک عذاب ملے گا۔ " WHERE ayatNumber = 43 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جس نے صبر کیا اور معاف کیا تو (اس کا) یہ (کام) بڑی ہمت والے کاموں میں سے ہے۔ " WHERE ayatNumber = 44 AND chapterNumber = 42;
UPDATE verses SET ts = "اور جس کو اللہ گمراہ قرار دے تو اس (اللہ) کو چھوڑ کر اس کا کوئی مددگار نہیں ہو سکتا اور تو ظالموں کو دیکھے گا کہ جب وہ عذاب آتا دیکھیں گے تو کہیں گے کہ کیا اس عذاب کو لوٹانے کی کوئی تدبیر بھی ہے! " WHERE ayatNumber = 45 AND chapterNumber = 42;
UPDATE verses SET ts = "اور تو ان کو دیکھے گا کہ وہ اس عذاب کے سامنے پیش کیے جائیں گے اور ذلت کی وجہ سے سر جھکائے ہوئے نیچی نگاہوں سے دیکھ رہے ہوں گے اور مومن کہیں گے کہ اصل گھاٹا پانے والے وہی ہیں جنہوں نے اپنی جانوں اور اہل و عیال کو قیامت کے دن گھاٹے میں ڈالا۔ کان کھول کر سن لو! ظالم لوگ ایک قائم رہنے والے عذاب میں ہوں گے۔ " WHERE ayatNumber = 46 AND chapterNumber = 42;
UPDATE verses SET ts = "اور ان کو پناہ دینے والے کوئی نہیں ہوں گے جو ان کی اللہ کے مقابلہ میں مدد کر سکیں اور جس کو اللہ گمراہ سمجھتا ہے اس کو راستہ پر لانے کی کوئی تدبیر نہیں۔ " WHERE ayatNumber = 47 AND chapterNumber = 42;
UPDATE verses SET ts = "تم اپنے رب کی آواز کا (اس پر لبیک کہتے ہوئے) جواب دو۔ پیشتر اس کے کہ وہ وقت آجائے جس کو اللہ کے مقابلہ میں لوٹانے والا کوئی نہیں۔ اس دن تمہارے لئے کوئی پناہ کی جگہ نہیں ہوگی اور تمہارے لئے انکار کی کوئی گنجائش نہیں ہوگی۔ " WHERE ayatNumber = 48 AND chapterNumber = 42;
