UPDATE verses SET ts = "اور کسی آدمی کی یہ حیثیت نہیں کہ اللہ اس سے وحی کے سوا یا پردے کے پیچھے بولنے کے سوا کسی اور صورت سے کلام کرے یا (اس کی طرف فرشتوں میں سے) رسول (بناکر) بھیجے جو اس کے حکم سے جو کچھ وہ کہے بات پہنچا دیں۔ وہ بڑی شان والا (اور) حکمتوں کا واقف ہے۔ " WHERE ayatNumber = 52 AND chapterNumber = 42;
UPDATE verses SET ts = "اور اسی طرح ہم نے تیری طرف اپنے حکم سے کلام نازل کیا ہے تو نہیں جانتا تھا کہ کتاب کیا چیز ہے اور نہ یہ جانتا تھا کہ ایمان کیا چیز ہے لیکن ہم نے اس (تیری وحی) کو نور بنا دیا ہے اس کے ذریعہ سے اپنے بندوں میں سے جس کو چاہتے ہیں ہم ہدایت دے دیتے ہیں اور تو یقیناً لوگوں کو سیدھے راستہ کی طرف لا رہا ہے ۔" WHERE ayatNumber = 53 AND chapterNumber = 42;
UPDATE verses SET ts = "اللہ کے راستہ کی طرف جو اس کا بھی مالک ہے جو آسمانوں میں ہے اور اس کا بھی جو زمین میں ہے۔ سنو! سب باتیں خدا ہی کی طرف جاتی ہیں (یعنی تمام باتوں کی ابتدا اور انجام خدا ہی کے ہاتھ میں ہیں۔) " WHERE ayatNumber = 54 AND chapterNumber = 42;
