UPDATE verses SET ts = "ہم اس کتاب کی قسم کھاتے ہیں (یعنی اسے بطور شہادت کے پیش کرتے ہیں) جو اپنے مضمون کو کھول کر بیان کرنے والی ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 43;
