UPDATE verses SET ts = "جن لوگوں پر تورات کی اطاعت واجب کی گئی ہے مگر باوجود اس کے انہوں نے اس پر عمل نہیں کیا‘ ان کی مثال گدھے کی ہے‘ جس نے بہت سی کتابیں اٹھائی ہوتی ہیں (مگر جانتا کچھ نہیں) اللہ کے احکام کا انکار کرنے والی قوم کی حالت بہت بری ہوتی ہے اور اللہ ظالم قوم کو کبھی کامیابی کا منہ نہیں دکھاتا۔ " WHERE ayatNumber = 6 AND chapterNumber = 62;
UPDATE verses SET ts = "تو کہہ دے‘ اے یہودیو! اگر تمہارا یہ دعویٰ ہے کہ تم باقی دنیا کو چھوڑ کر اللہ کے دوست ہو اور اس کی پناہ میں ہو تو اگر تم (اس دعویٰ میں) سچے ہو‘ تو موت کی تمنا کرو۔ (یعنی رسول کریم صلی اللہ علیہ وسلم سے مباہلہ کرو) " WHERE ayatNumber = 7 AND chapterNumber = 62;
UPDATE verses SET ts = "مگر وہ اپنے گذشتہ اعمال کی وجہ سے کبھی بھی مباہلہ کے لئے تیار نہ ہوں گے اور اللہ ظالموں سے خوب واقف ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 62;
UPDATE verses SET ts = "تو کہہ دے کہ وہ موت جس سے تم بھاگتے ہو‘ ایک دن یقیناً تم کو آپکڑے گی۔ پھر تم پوشیدہ اور ظاہر کے جاننے والے خدا کی طرف لوٹائے جائو گے اور وہ تم کو تمہارے اعمال کی خبر دے گا۔ " WHERE ayatNumber = 9 AND chapterNumber = 62;
