UPDATE verses SET ts = "جب تیرے پاس منافق آتے ہیں تو وہ کہتے ہیں کہ ہم قسم کھا کر گواہی دیتے ہیں کہ تو اللہ کا رسول ہے۔ اور اللہ جانتا ہے کہ تو اس کا رسول ہے۔ مگر (ساتھ ہی) اللہ قسم کھا کر گواہی دیتا ہے کہ منافق جھوٹے ہیں۔ " WHERE ayatNumber = 2 AND chapterNumber = 63;
UPDATE verses SET ts = "انہوں نے اپنی قسموں کو (تیری گرفت سے بچنے کے لئے) ڈھال بنا لیا ہے اور وہ اللہ کے رستہ سے لوگوں کو روکتے ہیں‘ جو کچھ وہ کرتے ہیں بہت برا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 63;
UPDATE verses SET ts = "یہ کام وہ اس لئے کرتے ہیں کہ وہ پہلے ایمان لائے پھر انہوں نے انکار کر دیا۔ جس کے نتیجہ میں ان کے دلوں پر مہر لگا دی گئی اور اب وہ سمجھتے نہیں۔ " WHERE ayatNumber = 4 AND chapterNumber = 63;
UPDATE verses SET ts = "جب تو ان کو دیکھتا ہے تو ان کے مضبوط جسم تجھے خوب بھاتے ہیں۔ اور اگر وہ کوئی بات کرتے ہیں (تو ایسے شاندار طریقے سے کہ) تو ان کی بات کو (باوجود ان کے حالات جاننے کے) سن لیتا ہے (وہ مجلس میں اس طرح بیٹھے ہوئے ہوتے ہیں) گویا کہ وہ بڑے بڑے شہتیر ہیں جو (دیوار سے) اٹکائے ہوئے ہیں۔ جب کسی عذاب کی خبر (قرآن میں) نازل ہو تو وہ سمجھتے ہیں کہ یہ ہمارے ہی متعلق ہے۔ وہ پکے دشمن ہیں پس تو ان سے ہوشیار رہ۔ اللہ ان کو ہلاک کرے‘ وہ کہاں سے پھرا کر (راہ حق سے دور) لے جائے جاتے ہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 63;
UPDATE verses SET ts = "اور جب ان سے کہا جاتا ہے کہ آئو! اللہ کا رسول تمہارے لئے استغفار کرے تو وہ اپنے سر (تکبر اور انکار سے) پھیر لیتے ہیں اور تو ان کو دیکھتا ہے کہ وہ راہ حق سے لوگوں کو پھرا رہے ہیں اور وہ تکبر کی مرض میں مبتلا ہیں۔ " WHERE ayatNumber = 6 AND chapterNumber = 63;
UPDATE verses SET ts = "تو ان کے لئے استغفار کرے یا استغفار نہ کرے ان کے حق میں سب برابر ہے کیونکہ اللہ ان کو کبھی معاف نہیں کرے گا (جب تک وہ خود توبہ نہ کریں) اللہ اطاعت سے نکل جانے والی قوم کو کامیابی کا منہ نہیں دکھاتا۔ " WHERE ayatNumber = 7 AND chapterNumber = 63;
UPDATE verses SET ts = "یہی لوگ ہیں جو کہتے ہیں کہ اللہ کے رسول کے پاس جو لوگ رہتے ہیں ان پر خرچ نہ کرو‘ یہاں تک کہ وہ (فاقوں سے تنگ آکر) بھاگ جائیں۔ حالانکہ آسمان اور زمین کے خزانے اللہ کے پاس ہیں‘ لیکن منافق سمجھتے نہیں۔ " WHERE ayatNumber = 8 AND chapterNumber = 63;
UPDATE verses SET ts = "وہ کہتے ہیں کہ اگر ہم مدینہ کی طرف لوٹ کر گئے تو جو مدینہ کا سب سے معزز آدمی ہے وہ مدینہ کے سب سے ذلیل آدمی کو اس سے نکال دے گا۔ اور عزت اللہ اور اس کے رسول اور مومنوں کو ہی حاصل ہے لیکن منافق جانتے نہیں۔ " WHERE ayatNumber = 9 AND chapterNumber = 63;
UPDATE verses SET ts = "اے مومنو! تم کو تمہارے مال اور تمہاری اولادیں اللہ کے ذکر سے غافل نہ کر دیں اور جو لوگ ایسا کریں گے‘ وہی گھاٹا پانے والے ہوں گے۔ " WHERE ayatNumber = 10 AND chapterNumber = 63;
UPDATE verses SET ts = "اور (اے مومنو)! جو کچھ ہم نے تمہیں دیا ہے اس میں سے موت آنے سے پہلے خرچ کرو‘ تا ایسا نہ ہو کہ (جب موت آئے تو خرچ نہ کرنے والے کو) کہنا پڑے کہ اے میرے رب! تو نے مجھے کیوں تھوڑی سی ڈھیل نہیں دی تا کہ میں اس (ڈھیل) میں کچھ صدقہ کر لیتا اور نیک بن جاتا۔ " WHERE ayatNumber = 11 AND chapterNumber = 63;
UPDATE verses SET ts = "لیکن جب کسی نفس کی موت آجاتی ہے تو اللہ اسے ڈھیل نہیں دیا کرتا‘ اور اللہ تمہارے اعمال سے واقف ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 63;
UPDATE verses SET ts = "(۔میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 64;
UPDATE verses SET ts = "آسمانوں اور زمین میں جو کچھ ہے اللہ کی تسبیح کر رہا ہے۔ بادشاہت بھی اسی کی ہے اور تعریف بھی اسی کی ہے اور وہ ہر چیز پر قادر ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 64;
UPDATE verses SET ts = "وہی ہے جس نے تم کو پیدا کیا ہے۔ پس تم میں سے کوئی تو کافر بن جاتا ہے اور کوئی مومن بن جاتا ہے۔ اور اللہ تمہارے اعمال کو دیکھ رہا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 64;
UPDATE verses SET ts = "آسمانوں اور زمین کو اس نے ایک خاص غرض کے ماتحت پیدا کیا ہے اور اسی نے تمہاری صورتیں بنائی ہیں اور تمہاری صورتوں کو بہت اچھا بنایا ہے اور اسی کی طرف تم نے لوٹ کر جانا ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 64;
UPDATE verses SET ts = "آسمانوں اور زمین میں جو کچھ ہے وہ اسے جانتا ہے۔ اور اس عمل کو بھی جانتا ہے جسے تم چھپاتے ہو یا ظاہر کرتے ہو۔ اور اللہ دل کی باتوں کو بھی جانتا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 64;
UPDATE verses SET ts = "کیا تم کو اپنے سے پہلے کافروں کی خبریں نہیں پہنچیں۔ انہوں نے اپنے اعمال کی سزا بھگتی اور ان کے لئے دردناک عذاب (مقدر) ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 64;
UPDATE verses SET ts = "یہ اس لئے ہوا کہ ان کے پاس ان کے رسول دلائل لیکر آتے رہے مگر وہ یہی جواب دیتے رہے کہ کیا انسانی شکل میں آنے والے لوگ ہی ہم کو ہدایت دیں گے (اگر ہدایت دینی ہوتی تو فرشتے آسمان سے اترتے) سو انہوں نے کفر کیا اور پیٹھ پھیر لی۔ اور اللہ نے بھی ان کے ایمان سے بے نیازی ظاہر کی اور اللہ ہر ایک سے بے احتیاج (اور) بڑی تعریفوں کا مستحق ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 64;
UPDATE verses SET ts = "کافر خیال کرتے ہیں کہ کبھی بھی وہ (زندہ کرکے) نہیں اٹھائے جائیں گے تو کہہ دے جس طرح تم خیال کرتے ہو۔ ایسا نہیں ہوگا۔ بلکہ مجھ کو اپنے رب کی قسم تم کو زندہ کرکے اٹھایا جائے گا‘ پھر تم کو تمہارے اعمال سے واقف کیا جائے گا اور یہ بات اللہ پر آسان ہے۔ " WHERE ayatNumber = 8 AND chapterNumber = 64;
UPDATE verses SET ts = "پس اے لوگو! اللہ اور اس کے رسول پر ایمان لائو اور اس نور (یعنی قرآن) پر بھی جو ہم نے اتارا ہے اور اللہ تمہارے اعمال سے خبردار ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 64;
UPDATE verses SET ts = "جس دن تم سب کو اکٹھا کرنے کے دن (یعنی قیامت کے دن زندہ کرکے) جمع کرے گا‘ یہ ہار جیت کے فیصلہ کا دن ہوگا اور جو کوئی اللہ پر ایمان لاتا ہے اور اس کے مطابق عمل کرتا ہے اللہ بھی اس کی بدیوں کو ڈھانپ دیتا ہے اور اس کو ایسی جنتوں میں داخل کرتا ہے جن کے نیچے نہریں بہتی ہیں۔ وہ ہمیشہ کے لئے ان میں رہتے چلے جائیں گے‘ یہ بہت بڑی کامیابی ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 64;
UPDATE verses SET ts = "اور جنہوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا‘ وہ دوزخی ہیں (اور) وہ دوزخ میں رہتے چلے جائیں گے اور وہ بہت برا ٹھکانا ہے۔ " WHERE ayatNumber = 11 AND chapterNumber = 64;
UPDATE verses SET ts = "کوئی مصیبت نہیں آتی‘ مگر اللہ کے حکم سے۔ اور جو اللہ پر ایمان لاتا ہے‘ وہ اس کے دل کو کامیابی کے طریقوں کی طرف پھیر دیتا ہے اور اللہ ہر چیز کو جانتا ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 64;
UPDATE verses SET ts = "اور اللہ اور رسول کی اطاعت کرو۔ لیکن اگر تم پھر جائو‘ تو ہمارے رسول پر صرف کھول کر بات پہنچا دینا ہی فرض ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 64;
UPDATE verses SET ts = "اللہ کے سوا کوئی معبود نہیں اور مومنوں کو اللہ پر ہی توکل کرنا چاہیے۔ " WHERE ayatNumber = 14 AND chapterNumber = 64;
UPDATE verses SET ts = "اے مومنو! تمہاری بیویوں اور اولادوں میں سے بعض ایسے ہیں جو تمہارے دشمن ہیں۔ پس ان سے ہوشیار رہو اور اگر تم عفو سے کام لو اور درگذر کرو‘ اور معاف کرو۔ تو اللہ بڑا معاف کرنے والا (اور) بے انتہا کرم کرنے والا ہے۔ " WHERE ayatNumber = 15 AND chapterNumber = 64;
UPDATE verses SET ts = "تمہارے مال اور تمہاری اولادیں صرف ایک آزمائش کا ذریعہ ہیں۔ اور اللہ کے پاس بڑا اجر ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 64;
UPDATE verses SET ts = "پس جتنا ہو سکے اللہ کا تقویٰ اختیار کرو‘ اور اس کی بات سنو اور اس کی اطاعت کرو‘ اور اپنے مال اس کی راہ میں خرچ کرتے رہو۔ یہ تمہاری جانوں کے لئے بہتر ہوگا اور جو لوگ اپنے دل کے بخل سے بچائے جاتے ہیں وہ کامیاب ہوتے ہیں۔ " WHERE ayatNumber = 17 AND chapterNumber = 64;
UPDATE verses SET ts = "اگر تم اللہ کے لئے اپنے مالوں میں سے ایک اچھا حصہ کاٹ کر الگ کر دو‘ تو وہ اس حصہ کو تمہارے لئے بڑھائے گا اور تمہارے لئے بخشش کے سامان پیدا کرے گا اور اللہ بہت قدر دان (اور) ہر بات کو سمجھنے والا ہے۔ " WHERE ayatNumber = 18 AND chapterNumber = 64;
UPDATE verses SET ts = "وہ حاضر اور غائب کو جانتا ہے اور غالب (اور) حکمت والا ہے۔ " WHERE ayatNumber = 19 AND chapterNumber = 64;
UPDATE verses SET ts = "(۔میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 65;
UPDATE verses SET ts = "اے نبی! (اور اس کے ماننے والو) جب تم بیویوں کو طلاق دو تو ان کو مقررہ وقت کے مطابق طلاق دو اور (طلاق کے بعد) وقت کا اندازہ رکھو اور اللہ کا جو تمہارا رب ہے تقویٰ اختیار کرو‘ ان کو ان کے گھروں سے نہ نکالو اور نہ وہ خود نکلیں سوائے اس کے کہ وہ ایک کھلے گناہ کی مرتکب ہوں۔ اور یہ اللہ کی حدیں ہیں اور جو کوئی اللہ کی حدوں کو توڑ دیتا ہے وہ اپنی جان پر ظلم کرتا ہے (اے طلاق دینے والے)! تجھے معلوم نہیں کہ شاید اللہ اس واقعہ کے بعد کچھ اور ظاہر کرے۔ " WHERE ayatNumber = 2 AND chapterNumber = 65;
UPDATE verses SET ts = "پھر جب عورتیں عدت کی آخری حد کو پہنچ جائیں تو انہیں مناسب طریق پر روک لو یا انہیں مناسب طریق پر فارغ کردو۔ اور اپنے میں سے دو منصف گواہ مقرر کرو۔ اور خدا کے لئے سچی گواہی دو۔ تم میں سے جو کوئی اللہ اور یوم آخر پر ایمان لاتا ہے اس کو یہ نصیحت کی جاتی ہے اور جو شخص اللہ کا تقویٰ اختیار کرے گا اللہ اس کے لئے کوئی نہ کوئی رستہ نکال دے گا۔ " WHERE ayatNumber = 3 AND chapterNumber = 65;
UPDATE verses SET ts = "اور اس کو وہاں سے رزق دے گا جہاں سے رزق آنے کا اسے خیال بھی نہیں ہوگا اور جو کوئی اللہ پر توکل کرتا ہے وہ (اللہ) اس کے لئے کافی ہے اللہ یقیناً اپنے مقصد کو پورا کرکے چھوڑتا ہے اللہ نے ہر چیز کا اندازہ مقرر کر چھوڑا ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 65;
UPDATE verses SET ts = "اور (تمہاری بیویوں میں سے) وہ (عورتیں) جو حیض سے مایوس ہو چکی ہوں اگر (ان کی عدت کے متعلق) تمہیں شبہ ہو تو ان کی عدت تین مہینے ہے اور اسی طرح ان کی بھی جن کو حیض نہیں آرہا۔ اور جن عورتوں کو حمل ہو ان کی عدت وضع حمل تک ہے اور جو کوئی اللہ کا تقویٰ اختیار کرے اللہ ضرور اس کے معاملہ میں آسانی پیدا کر دیتا ہے۔ " WHERE ayatNumber = 5 AND chapterNumber = 65;
UPDATE verses SET ts = "یہ اللہ کا حکم ہے جو اس نے تمہاری طرف اتارا ہے اور جو کوئی اللہ کا تقویٰ اختیار کرے وہ (خدا) اس کی بدیاں مٹا دیتا ہے اور اس کے اجر کو بڑھا دیتا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 65;
UPDATE verses SET ts = "(اے مسلمانو! مطلقہ عورتوں کے حق کو نہ بھولو) ان کو وہیں رکھو جہاں تم اپنی طاقت کے مطابق رہتے ہو۔ اور ان کو کسی قسم کا ضرر نہ دو اس لئے  کہ ان کو تنگ کرکے (گھر سے) نکال دو۔ اور اگر وہ حمل والی ہوں‘ تو اس وقت تک ان پر خرچ کرو جب تک وضع حمل ہو جائے اور اگر وہ تمہارے لئے بچوں کو دودھ پلائیں‘ تو ان کو معقول اجرت دو اور اس کا فیصلہ حسب دستور باہمی مشورہ سے کرو اور اگر آپس میں کسی فیصلہ پر اکٹھے نہ ہو سکو تو کوئی اور عورت اس بچے کو دودھ پلائے۔ " WHERE ayatNumber = 7 AND chapterNumber = 65;
UPDATE verses SET ts = "چاہیے کہ مال دار مرد (دودھ پلانے والی عورت پر) اپنی طاقت کے مطابق خرچ کرے۔ اور جو مالدار نہیں ہے وہ اللہ کے دئے کے مطابق خرچ کرے اللہ کسی نفس کو ایسے احکام نہیں دیتا جو اس کی طاقت سے بڑھ کر ہوں بلکہ ایسے ہی احکام دیتا ہے جن کے پورا کرنے کی توفیق بھی ان کو بخشی ہو۔ چنانچہ اگر کوئی شخص خدا کے حکم پر عمل کرتے ہوئے دودھ پلانے والی عورت کی مزدوری صحیح طور پر دے گا تو اگر وہ تنگی کی حالت میں بھی ہے تو اللہ اس کے بعد اس کے لئے فراخی کی حالت پیدا کر دے گا۔ " WHERE ayatNumber = 8 AND chapterNumber = 65;
UPDATE verses SET ts = "اور بہت سی بستیاں ہیں جنہوں نے اپنے رب کے حکم کا اور رسولوں کا انکار کیا۔ اس پر ہم نے ان سے بڑی سختی سے حساب لیا اور ان پر بڑا عذاب نازل کیا۔ " WHERE ayatNumber = 9 AND chapterNumber = 65;
UPDATE verses SET ts = "اور انہوں نے اپنے کیے کی سزا بھگت لی۔ اور ان کا انجام گھاٹا ہی نکلا۔ " WHERE ayatNumber = 10 AND chapterNumber = 65;
UPDATE verses SET ts = "ایسے لوگوں کے لئے اللہ نے سخت عذاب مقرر کیا ہے۔ پس اے مومنوں میں سے عقل مندو! اللہ کا تقویٰ اختیار کرو۔ اللہ نے تمہارے لئے شرف کا سامان یعنی رسول اتارا ہے " WHERE ayatNumber = 11 AND chapterNumber = 65;
UPDATE verses SET ts = "یعنی رسول اتارا ہے جو تم کو اللہ کی ایسی آیات سناتا ہے جو (ہر نیکی اور بدی کو) کھول دیتی ہیں جس کا نتیجہ یہ ہوتا ہے کہ مومن لوگ جو اپنے ایمان کے مطابق عمل کرتے ہیں‘ وہ اندھیروں سے نکل کر نور میں آجاتے ہیں اور  جو بھی اللہ پر ایمان لائے‘ اور اس کے مطابق عمل کرے وہ اس کو ایسی جنتوں میں داخل کرے گا‘ جن کے نیچے نہریں بہتی ہونگی۔ وہ اس میں ہمیشہ رہتے چلے جائیں گے اللہ نے ایسے شخص کو بڑا پسندیدہ روزی عطا فرمائی ہے۔ " WHERE ayatNumber = 12 AND chapterNumber = 65;
UPDATE verses SET ts = "اللہ وہی ہے جس نے سات آسمان پیدا کیے ہیں اور زمینیں بھی آسمانوں کے عدد کے مطابق (پیدا کی ہیں) ان (آسمانوں اور ان زمینوں) کے درمیان اس کا حکم نازل ہوتا رہتا ہے تا کہ تم جان لو کہ اللہ ہر ایک چیز پر قادر ہے۔ اور اسی طرح اللہ ہر چیز کا علم سے احاطہ کیے ہوئے ہے۔ " WHERE ayatNumber = 13 AND chapterNumber = 65;
UPDATE verses SET ts = "(۔میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 66;
UPDATE verses SET ts = "اے نبی! تو اس (چیز) کو کیوں حرام کرتا ہے جس کو اللہ نے تیرے لئے حلال کیا ہے تو اپنی بیویوں کی خوشی چاہتا ہے اور اللہ بہت بخشنے والا (اور) بے انتہا کرم کرنے والا ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 66;
UPDATE verses SET ts = "اللہ نے تم پر ایسی قسموں کے کھولنے کو واجب کیا ہے (جن سے فتنہ پیدا ہو) اور اللہ تمہارا آقا ہے اور وہ بہت جاننے والا (اور) حکمت والا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 66;
UPDATE verses SET ts = "اور یاد کرو جب نبی نے اپنی بیویوں میں سے کسی ایک سے ایک مخفی بات کہی۔ پھر جب اس نے وہ بات کسی کو بتا دی اور اللہ نے اس (کمزوری) کی خبر اس (یعنی نبی) کو دے دی‘ تو اس نے (بات کا) کچھ حصہ بیوی کو بتا دیا اور کچھ حصہ سے پردہ پوشی کی۔ پھر جب اس نے امر واقعہ کی خبر اس (بیوی) کو دی تو اس نے کہا کہ آپ کو یہ خبر کس نے دی ہے اس پر اس (یعنی نبی) نے کہا کہ مجھے بڑے علم والے (اور) واقف کار خدا نے خبر دی ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 66;
UPDATE verses SET ts = "پھر اس نے کہا کہ اے دونوں بیویو! اگر تم اپنی غلطی پر اللہ سے توبہ کرو تو تم دونوں کے دل تو پہلے ہی اس بات کی طرف جھکے ہوئے ہیں (اور توبہ کے لئے تیار ہیں) اور اگر تم دونوں ایک دوسرے کی پشت پناہی کے لئے کھڑی ہو جائو تو یاد رکھو کہ اللہ اس رسول کا دوست ہے‘ اسی طرح جبریل اور سب مومن اور علاوہ اس کے فرشتے بھی نبی کی پشت پر ہیں۔ " WHERE ayatNumber = 5 AND chapterNumber = 66;
UPDATE verses SET ts = "اس کے رب کے لئے بالکل ممکن ہے کہ اگر وہ تم کو طلاق دے دے تو وہ تم سے زیادہ اچھی بیویاں بدل کر اس کو دے دے جو مسلمان بھی ہوں مومن بھی ہوں فرمانبردار بھی ہوں توبہ کرنے والی‘ عبادت کرنے والی‘ روزہ رکھنے والی‘ بیوہ بھی ہوں اور کنواریاں بھی۔ " WHERE ayatNumber = 6 AND chapterNumber = 66;
UPDATE verses SET ts = "اے مومنو! اپنے اہل کو بھی اور اپنی جانوں کو بھی دوزخ سے بچائو جس کا ایندھن خاص لوگ (یعنی کافر) ہونگے اور اسی طرح پتھر (جن سے بت بنے) اس (دوزخ) پر ایسے ملائکہ مقرر ہیں جو کسی کی منت سماجت سننے والے نہیں بلکہ اپنے فرض کے ادا کرنے میں بڑے سخت ہیں اور اللہ نے ان کو جو حکم دیا ہے اس کی وہ نافرمانی نہیں کرتے اور جو کچھ کہا جاتا ہے وہی کرتے ہیں۔ " WHERE ayatNumber = 7 AND chapterNumber = 66;
UPDATE verses SET ts = "اے کافرو! آج عذر نہ کرو۔ تم کو تمہارے اعمال کے مطابق جزا ملے گی۔ " WHERE ayatNumber = 8 AND chapterNumber = 66;
UPDATE verses SET ts = "اے مومنو! اللہ کی طرف خالص طور پر رجوع کرو (منافقت کی کوئی ملاوٹ نہ ہو) کوئی تعجب نہیں کہ تمہارا رب تمہاری بدیوں کو مٹا دے اور تم کو ایسی جنتوں میں داخل کرے جن کے نیچے نہریں بہتی ہیں اس دن جس دن اللہ اپنے نبی کو رسوا نہیں کرے گا اور نہ ان لوگوں کو جو اس کے ساتھ ایمان لائے ہیں ان کا نور ان کے آگے آگے بھی بھاگتا جائے گا اور دائیں پہلو کے ساتھ ساتھ بھی‘ وہ کہیں گے کہ اے ہمارے رب! ہمارا نور ہمارے فائدہ کے لئے پورا کر دے اور ہمیں معاف فرما‘ تو ہر چیز پر قادر ہے۔ " WHERE ayatNumber = 9 AND chapterNumber = 66;
UPDATE verses SET ts = "اے نبی! کافروں اور منافقوں کے خلاف خوب تبلیغ کر اور ان کا کوئی اثر قبول نہ کر‘ اور (سمجھ لے کہ) ان کا ٹھکانا جہنم ہے اور وہ بہت برا ٹھکانا ہے۔ " WHERE ayatNumber = 10 AND chapterNumber = 66;
UPDATE verses SET ts = "اللہ کافروں کی حالت نوح اور لوط کی بیویوں کی مانند بیان کرتا ہے۔ وہ دونوں ہمارے نیک بندوں کے نکاح میں تھیں۔ مگر ان دونوں نے ان دونوں (بندوں) کی خیانت کی تھی اور وہ دونوں الٰہی عذاب کے وقت ان (بیویوں) کے کسی کام نہ آسکے۔ اور ان دونوں عورتوں سے کہا گیا تھا کہ جہنم میں جانے والوں کے ساتھ تم بھی جہنم میں چلی جائو۔ " WHERE ayatNumber = 11 AND chapterNumber = 66;
UPDATE verses SET ts = "اور مومنوں کی حالت اللہ فرعون کی بیوی کی مانند بیان کرتا ہے جبکہ اس نے اپنے رب سے کہا‘ کہ اے خدا! تو اپنے پاس ایک گھر جنت میں میرے لئے بھی بنا دے اور مجھ کو فرعون اور اس کی بداعمالیوں سے بچا اور اسی طرح (اس کی) ظالم قوم سے نجات دے۔ " WHERE ayatNumber = 12 AND chapterNumber = 66;
UPDATE verses SET ts = "اور پھر اللہ مومنوں کی حالت مریم کی طرح بیان کرتا ہے جو عمران کی بیٹی تھی۔ جس نے اپنے ناموس کی حفاظت کی اور ہم نے اس میں اپنا کلام ڈال دیا تھا اور اس نے اس کلام کی جو اس کے رب نے اس پر نازل کیا تھا تصدیق کر دی تھی۔ اور اس (خدا) کی کتابوں پر بھی ایمان لائی تھی اور (ہوتے ہوتے ایسی حالت پکڑ لی تھی کہ) اس نے فرمانبرداروں کا مقام حاصل کر لیا تھا۔ " WHERE ayatNumber = 13 AND chapterNumber = 66;
UPDATE verses SET ts = "(میں) اللہ کا نام لے کر جو بے حد کرم کرنے والا (اور) باربار رحم کرنے والا ہے (پڑھتا ہوں) " WHERE ayatNumber = 1 AND chapterNumber = 67;
UPDATE verses SET ts = "بہت برکت والا ہے وہ (خدا) جس کے قبضہ میں بادشاہت ہے اور وہ ہر ایک ارادہ کے پورا کرنے پر قادر ہے۔ " WHERE ayatNumber = 2 AND chapterNumber = 67;
UPDATE verses SET ts = "اس نے موت اور زندگی کو اس لئے پیدا کیا ہے کہ وہ تم کو آزمائے کہ تم میں سے کون زیادہ اچھا عمل کرنے والا ہے‘ اور وہ غالب (اور) بہت بخشنے والا ہے۔ " WHERE ayatNumber = 3 AND chapterNumber = 67;
UPDATE verses SET ts = "وہی ہے جس نے سات آسمان درجہ بدرجہ بنائے ہیں (اور) تو رحمن (خدا) کی پیدائش میں کوئی رخنہ نہیں دیکھتا۔ اور تو اپنی آنکھ کو (ادھر ادھر) پھیر کر اچھی طرح سے دیکھ لے! کیا تجھے (خدا کی مخلوق میں کسی جگہ بھی) کوئی رخنہ نظر آتا ہے۔ " WHERE ayatNumber = 4 AND chapterNumber = 67;
UPDATE verses SET ts = "پھر باربار نظر کو چکر دے‘ وہ آخر تیری طرف ناکام ہو کر لوٹ آئے گی‘ اور وہ تھکی ہوئی ہوگی (اور کوئی رخنہ نظر نہ آئے گا)۔ " WHERE ayatNumber = 5 AND chapterNumber = 67;
UPDATE verses SET ts = "اور ہم نے نچلے آسمان کو چراغوں کے ساتھ زینت دی ہے اور ان (چراغوں) کو شیطانوں کے لئے پتھرائو کا باعث بنایا ہے اور ہم نے ان (شیطانوں) کے لئے ایک بھڑکنے والا عذاب مقرر کیا ہے۔ " WHERE ayatNumber = 6 AND chapterNumber = 67;
UPDATE verses SET ts = "اور جنہوں نے اپنے رب کا انکار کیا ہے انہیں جہنم کا عذاب پہنچے گا اور وہ بہت برا ٹھکانا ہے۔ " WHERE ayatNumber = 7 AND chapterNumber = 67;
UPDATE verses SET ts = "جب وہ اس (جہنم) میں ڈالے جائیں گے تو وہ اس میں اس کی ایک بڑی چیخ سنیں گے (جیسے گدھے کے رینگنے کی آواز) اور وہ (جہنم بڑے) جوش میں ہوگی۔ " WHERE ayatNumber = 8 AND chapterNumber = 67;
UPDATE verses SET ts = "قریب ہوگا کہ وہ غصہ کے مارے پھٹ پڑے۔ جب بھی اس میں کوئی جماعت ڈالی جائے گی‘ اس (جہنم) کے دربان ان سے پوچھیں گے کہ کیا تمہارے پاس کوئی نبی نہیں آیا تھا۔ " WHERE ayatNumber = 9 AND chapterNumber = 67;
UPDATE verses SET ts = "وہ کہیں گے‘ ہاں ہمارے پاس نبی ضرور آیا تھا پر ہم نے اسے جھٹلا دیا تھا اور اس سے کہا تھا کہ اللہ نے کچھ بھی نہیں اتارا (سب تمہارا جھوٹ ہے) تم ایک کھلی گمراہی میں مبتلا ہو (اور اپنے خیالات کو الہام سمجھتے ہو) " WHERE ayatNumber = 10 AND chapterNumber = 67;
UPDATE verses SET ts = "اور ان لوگوں نے یہ بھی کہا کہ اگر ہم سننے کی طاقت رکھتے یا عقل رکھتے تو کبھی دوزخی نہ بنتے۔ " WHERE ayatNumber = 11 AND chapterNumber = 67;
UPDATE verses SET ts = "پس انہوں نے اپنے گناہوں کا اعتراف کر لیا‘ سو (اے فرشتو)! دوزخیوں کے لئے لعنت مقدر کردو۔ " WHERE ayatNumber = 12 AND chapterNumber = 67;
UPDATE verses SET ts = "یقیناً وہ لوگ جو کہ اپنے رب سے علیحدگی میں ڈرتے ہیں‘ ان کو مغفرت اور بڑا اجر ملے گا۔ " WHERE ayatNumber = 13 AND chapterNumber = 67;
UPDATE verses SET ts = "اور (اے لوگو) تم اپنی بات کو چھپائو یا ظاہر کرو‘ وہ (خدا) دلوں کی بات کو بھی خوب جانتا ہے۔ " WHERE ayatNumber = 14 AND chapterNumber = 67;
UPDATE verses SET ts = "کیا ایسا ہو سکتا ہے کہ جس نے پیدا کیا ہو وہی اندر کے حال نہ جانے‘ حالانکہ وہ مخفی سے مخفی رازوں کا واقف اور بہت خبردار ہے۔ " WHERE ayatNumber = 15 AND chapterNumber = 67;
UPDATE verses SET ts = "وہی ہے جس نے تمہارے لئے زمین کو رہنے کے قابل بنایا ہے پس اس کی وادیوں میں جائو اور اس (خدا) کے رزق سے کھائو اور اسی کی طرف لوٹ کے جانا ہے۔ " WHERE ayatNumber = 16 AND chapterNumber = 67;
UPDATE verses SET ts = "کیا تم آسمان میں رہنے والی ہستی سے اس بات سے امن میں آگئے ہو کہ وہ تم کو دنیا میں ذلیل کر دے اور تم دیکھ لو کہ وہ (زمین) چکر کھا رہی ہے (اور تم تباہی کے قریب ہو) " WHERE ayatNumber = 17 AND chapterNumber = 67;
UPDATE verses SET ts = "کیا آسمان میں رہنے والی ہستی سے تم اس بات سے امن میں آگئے‘ کہ وہ تم پر پتھروں کا مینہ برسا دے‘ سو (جس شکل میں بھی میرا غضب نازل ہوگا) تم جان لو گے کہ میرا ڈرانا کیسا سخت تھا۔ " WHERE ayatNumber = 18 AND chapterNumber = 67;
UPDATE verses SET ts = "اور ان سے پہلے لوگوں نے بھی رسولوں کو جھٹلایا تھا۔ پھر (انہوں نے دیکھا یا نہ دیکھا کہ) میرا عذاب کیسا سخت تھا۔ " WHERE ayatNumber = 19 AND chapterNumber = 67;
