UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 1;
UPDATE verses SET es = "Toda alabanza pertenece a Al-lah <em>sólo, </em>Señor de todos los mundos," WHERE ayatNumber = 2 AND chapterNumber = 1;
UPDATE verses SET es = "El Clemente, el Misericordioso," WHERE ayatNumber = 3 AND chapterNumber = 1;
UPDATE verses SET es = "Dueño del Día del Juicio." WHERE ayatNumber = 4 AND chapterNumber = 1;
UPDATE verses SET es = "A Ti sólo te adoramos y a Ti sólo imploramos ayuda." WHERE ayatNumber = 5 AND chapterNumber = 1;
UPDATE verses SET es = "Dirígenos por el camino recto," WHERE ayatNumber = 6 AND chapterNumber = 1;
UPDATE verses SET es = "El camino de aquellos a quienes Tú has concedido <em>Tus </em> bendiciones, de los que no han incurrido en <em>el </em>enojo y de los que no se han extraviado." WHERE ayatNumber = 7 AND chapterNumber = 1;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 2;
UPDATE verses SET es = "Alif Lam Mim." WHERE ayatNumber = 2 AND chapterNumber = 2;
UPDATE verses SET es = "Éste es un Libro perfecto; no hay duda en él; es una guía para los justos," WHERE ayatNumber = 3 AND chapterNumber = 2;
UPDATE verses SET es = "Quienes creen en lo invisible y observan la Oración y gastan de lo que les hemos provisto;" WHERE ayatNumber = 4 AND chapterNumber = 2;
UPDATE verses SET es = "Y creen en lo que te ha sido revelado y en lo que fue revelado con anterioridad a ti y tienen una fe firme en lo que aún está por venir." WHERE ayatNumber = 5 AND chapterNumber = 2;
UPDATE verses SET es = "Son los que siguen la guía de su Señor y son los que alcanzarán la felicidad." WHERE ayatNumber = 6 AND chapterNumber = 2;
UPDATE verses SET es = "Los que son incrédulos –les es igual que les adviertas o no– no creerán." WHERE ayatNumber = 7 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah ha puesto un sello en sus corazones y en sus oídos; y un velo cubre sus ojos; y para ellos hay <em>reservado </em>un grave castigo." WHERE ayatNumber = 8 AND chapterNumber = 2;
UPDATE verses SET es = "Y entre los hombres hay algunos que dicen: “creemos en Al-lah y en el Último Día”; pero no son en absoluto creyentes." WHERE ayatNumber = 9 AND chapterNumber = 2;
UPDATE verses SET es = "Tratan de engañar a Al-lah y a los que creen, y no engañan a nadie sino a ellos mismos; pero no se dan cuenta <em>de ello.</em>" WHERE ayatNumber = 10 AND chapterNumber = 2;
UPDATE verses SET es = "Había una enfermedad en sus corazones, y Al-lah les ha agravado la enfermedad; y tendrán un penoso castigo porque solían mentir." WHERE ayatNumber = 11 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando se les dice: “no creéis el desorden en la tierra”, responden: “Sólo trabajamos por la paz”." WHERE ayatNumber = 12 AND chapterNumber = 2;
UPDATE verses SET es = "¡Cuidado!; son ciertamente ellos los que crean el desorden, pero no se dan cuenta <em>de ello.</em>" WHERE ayatNumber = 13 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando se les dice: “Creed como han creído <em>otras </em> personas”, responden: “¿Acaso hemos de creer como han creído los insensatos?” ¡Cuidado! ellos son ciertamente los insensatos, pero no lo saben." WHERE ayatNumber = 14 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando se reúnen con los que creen, dicen: “Creemos”; pero cuando están solos con sus cabecillas, dicen: “En realidad estamos con vosotros; únicamente nos burlamos”." WHERE ayatNumber = 15 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah les castigará por su burla y les dejará que continúen en su trasgresión, vagando ciegamente." WHERE ayatNumber = 16 AND chapterNumber = 2;
UPDATE verses SET es = "Son los que han preferido el camino erróneo al agrado de Al-lah; pero su comercio no les ha reportado beneficio, ni están rectamente guiados." WHERE ayatNumber = 17 AND chapterNumber = 2;
UPDATE verses SET es = "Les ocurre como a la persona que encendió un fuego, y cuando lo iluminaba todo a su alrededor, Al-lah se llevó la luz y le dejó en la más negra oscuridad;<em> así</em> no pueden ver." WHERE ayatNumber = 18 AND chapterNumber = 2;
UPDATE verses SET es = "<em>Son</em> sordos, mudos y ciegos; por eso no volverán." WHERE ayatNumber = 19 AND chapterNumber = 2;
UPDATE verses SET es = "O <em>es</em> como un fuerte chaparrón que cae de las nubes, con oscuras tinieblas, truenos y relámpagos; se introducen los dedos en los oídos, a causa de los truenos y por temor a la muerte; y Al-lah pone un cerco alrededor de los incrédulos." WHERE ayatNumber = 20 AND chapterNumber = 2;
UPDATE verses SET es = "Los relámpagos casi les dejan ciegos: Cuando brillan ante ellos, caminan a su luz; y cuando queda todo oscuro a su alrededor se detienen. Y si Al-lah quisiera, podría dejarlos sin oído y sin vista; ciertamente, Al-lah tiene poder para hacer todo lo que quiera." WHERE ayatNumber = 21 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh hombres! adorad a vuestro Señor que os ha creado a vosotros y a vuestros antepasados para que lleguéis a ser piadosos." WHERE ayatNumber = 22 AND chapterNumber = 2;
UPDATE verses SET es = "Que convirtió la tierra en lecho para vosotros y los cielos en techo, e hizo que cayera agua de las nubes y de ese modo hizo crecer frutos para vuestro sustento. No pongáis pues a nadie a la altura de Al-lah puesto que ya sabéis." WHERE ayatNumber = 23 AND chapterNumber = 2;
UPDATE verses SET es = "Y si dudáis sobre lo que hemos revelado a Nuestro siervo, entonces presentad un capítulo como éste, y llamad a quienes os ayudan, prescindiendo de Al-lah, si es que sois veraces." WHERE ayatNumber = 24 AND chapterNumber = 2;
UPDATE verses SET es = "Pero si no <em>lo</em> hacéis – y nunca <em>lo </em>haréis – temed entonces al Fuego, cuyo combustible son hombres y piedras, <em>que está</em> preparado para los impíos." WHERE ayatNumber = 25 AND chapterNumber = 2;
UPDATE verses SET es = "Y anuncia la buena nueva a los que creen y hacen buenas obras, que para ellos hay jardines bajo los cuales corren arroyos. Cada vez que reciban los frutos producidos en ellos dirán: “Esto es lo que se nos había dado antes”, aunque solo se les entregarán dones similares. Y tendrán allí parejas, totalmente puras; y allí pondrán su morada." WHERE ayatNumber = 26 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah no se avergüenza de usar como parábola un mosquito o incluso algo más pequeño. Los que creen saben que ésa es la verdad que viene de su Señor, mientras que los descreídos dicen: “¿Qué pretende Al-lah con esta parábola?”. Con ella juzga a muchos como extraviados y a otros muchos los guía, pero a nadie juzga como descarriados, excepto a los desobedientes," WHERE ayatNumber = 27 AND chapterNumber = 2;
UPDATE verses SET es = "Quienes rompen el pacto con Al-lah después de haberlo aceptado, y separan con ello lo que Al-lah ha ordenado que se mantenga unido, y crean el desorden en la tierra; ellos son los perdedores." WHERE ayatNumber = 28 AND chapterNumber = 2;
UPDATE verses SET es = "¿Cómo podéis no creer en Al-lah? Cuando no teníais vida Él os dio la vida, y después os hará morir y os devolverá de nuevo la vida, y más tarde a Él seréis restituidos." WHERE ayatNumber = 29 AND chapterNumber = 2;
UPDATE verses SET es = "Él es quien creó para vosotros todo lo que hay en la tierra; después se volvió al Cielo y lo dispuso en siete cielos; y Él sabe todas las cosas." WHERE ayatNumber = 30 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando tu Señor dijo a los ángeles: “Estoy a punto de designar un vicario en la tierra,” dijeron: “¿Vas a situar en ella a quien cree el desorden y derrame la sangre? y nosotros Te glorificamos con Tus alabanzas y exaltamos Tu santidad”. Él respondió: “Yo sé lo que vosotros no sabéis.”" WHERE ayatNumber = 31 AND chapterNumber = 2;
UPDATE verses SET es = "Y enseñó a Adán todos los nombres, y acto seguido puso <em>los objetos de</em> estos <em>nombres</em> ante los ángeles y dijo: “Decidme sus nombres, si sois veraces”." WHERE ayatNumber = 32 AND chapterNumber = 2;
UPDATE verses SET es = "Ellos dijeron: “¡Santo eres! No sabemos nada, excepto lo que Tú nos has enseñado; ciertamente, Tú eres el Omnisciente, el Sabio”." WHERE ayatNumber = 33 AND chapterNumber = 2;
UPDATE verses SET es = "Dijo: “Oh Adán, diles sus nombres”; y cuando les hubo dicho los nombres, dijo: “¿No os he manifestado que conozco los secretos de los cielos y de la tierra, y sé lo que mostráis y lo que ocultáis?”" WHERE ayatNumber = 34 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>recordad</em> cuando dijimos a los ángeles: “Someteos a Adán” y <em>todos </em>se sometieron excepto Iblis. Se negó y actuó con arrogancia; y fue uno de los incrédulos." WHERE ayatNumber = 35 AND chapterNumber = 2;
UPDATE verses SET es = "Y dijimos: “Oh Adán, habitad tú y tu esposa en el jardín, y comed de él en abundancia en cualquier lugar que deseéis, pero no os acerquéis a este árbol, no sea que os convirtáis en transgresores.”" WHERE ayatNumber = 36 AND chapterNumber = 2;
UPDATE verses SET es = "Pero Satanás hizo, por su medio, que ambos cometieran un desliz y les expulsó del <em>estado</em> en que se encontraban. Y les dijimos: “Salid; algunos sois enemigos de otros, y para vosotros hay una morada en la tierra y provisiones durante algún tiempo.”" WHERE ayatNumber = 37 AND chapterNumber = 2;
UPDATE verses SET es = "Y Adán aprendió de su Señor ciertas palabras <em>de oración. </em>Y de ese modo Él volvió a <em>mirarlo</em> con misericordia. En verdad, Él es Remisorio <em>con compasión</em>, <em>y es </em>Misericordioso." WHERE ayatNumber = 38 AND chapterNumber = 2;
UPDATE verses SET es = "Dijimos: “Salid todos de aquí. Y si os llega de mi parte una orientación, entonces <em>no sobrecogerá </em>el temor a quien quiera que siga mi orientación, ni sufrirá aflicción.”" WHERE ayatNumber = 39 AND chapterNumber = 2;
UPDATE verses SET es = "Pero los que no crean y traten de falaces nuestros Signos, ésos serán huéspedes del Fuego; y en él tendrán su morada." WHERE ayatNumber = 40 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh Hijos de Israel! acordaos de los favores que os concedí y sed fieles a vuestro pacto conmigo; yo seré fiel a mi alianza con vosotros, y sólo a Mí debéis temer." WHERE ayatNumber = 41 AND chapterNumber = 2;
UPDATE verses SET es = "Y creed en lo que he revelado, que cumple lo que ya tenéis, y no seáis de los primeros en no creer en ello, ni troquéis Mis Signos por algo de bajo precio, y acogeos únicamente a Mi protección." WHERE ayatNumber = 42 AND chapterNumber = 2;
UPDATE verses SET es = "Y no confundáis la verdad con la falsedad ni escondáis a sabiendas la verdad." WHERE ayatNumber = 43 AND chapterNumber = 2;
UPDATE verses SET es = "Y cumplid la Oración y pagad el <em>Zakat, </em>e inclinaos con los que se inclinan." WHERE ayatNumber = 44 AND chapterNumber = 2;
UPDATE verses SET es = "¿Obligáis a otros a hacer buenas obras y vosotros lo olvidáis, mientras leéis el Libro? ¿No entenderéis, pues?" WHERE ayatNumber = 45 AND chapterNumber = 2;
UPDATE verses SET es = "Y solicitad ayuda con paciencia y Oración; algo realmente difícil excepto para los humildes de espíritu," WHERE ayatNumber = 46 AND chapterNumber = 2;
UPDATE verses SET es = "Quienes saben con certeza que encontrarán a su Señor, y que a Él volverán." WHERE ayatNumber = 47 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh, Hijos de Israel! recordad los favores que Os concedí y que Os ensalcé por encima de los <em>demás</em> pueblos." WHERE ayatNumber = 48 AND chapterNumber = 2;
UPDATE verses SET es = "Y temed el día en que ningún alma podrá sustituir en absoluto a otra, ni se aceptará intersección alguna por ella, ni se recibirá rescate de ella; ni recibirán ayuda alguna." WHERE ayatNumber = 49 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando Os libramos de las gentes del Faraón, que os afligían con crueles tormentos, matando a vuestros hijos y dejando vivir a vuestras mujeres. En eso había una gran prueba para vosotros por parte de vuestro Señor." WHERE ayatNumber = 50 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando separamos el mar para vosotros, os salvamos y ahogamos a las gentes del Faraón, mientras observabais." WHERE ayatNumber = 51 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando hicimos a Moisés una promesa de cuarenta noches; y entonces tomasteis el becerro <em>para adorarlo</em> y fuisteis transgresores." WHERE ayatNumber = 52 AND chapterNumber = 2;
UPDATE verses SET es = "Posteriormente, os perdonamos, para que pudierais ser agradecidos." WHERE ayatNumber = 53 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando dimos a Moisés el Libro y el Discernimiento, para que pudierais ser guiados rectamente." WHERE ayatNumber = 54 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos </em>de cuando Moisés dijo a su pueblo: “Oh, pueblo mío, en verdad habéis obrado mal con vosotros mismos tomando al becerro <em>para adorarlo</em>; volved pues a vuestro Creador y practicad la automortificación; eso será lo mejor para vosotros ante vuestro Creador.” Entonces Él volvió a vosotros <em>con compasión</em>. En verdad, Él es Remisorio <em>con compasión </em>y es Misericordioso." WHERE ayatNumber = 55 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando dijisteis: “Oh, Moisés, no creeremos en ti en absoluto mientras no veamos a Al-lah cara a cara;” entonces el rayo os arrebató mientras mirabais." WHERE ayatNumber = 56 AND chapterNumber = 2;
UPDATE verses SET es = "Acto seguido, os resucitamos después de vuestra muerte, para que fuerais agradecidos." WHERE ayatNumber = 57 AND chapterNumber = 2;
UPDATE verses SET es = "E hicimos que las nubes os cubrieran con su sombra y os enviamos el Manna y las Salwa, <em>diciendo: </em>“Comed de las cosas buenas que os hemos proporcionado.” Y no nos hicieron daño a Nos, sino a sí mismos." WHERE ayatNumber = 58 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos </em>de cuando dijimos: “Entrad en este pueblo y comed de sus bienes –donde queráis- hasta saciaros; y entrad humildemente por la puerta y decid: “¡Dios! Perdónanos nuestros pecados.” Os perdonaremos vuestros pecados y aumentaremos Nuestros favores a quienes hagan buenas obras.”" WHERE ayatNumber = 59 AND chapterNumber = 2;
UPDATE verses SET es = "Los transgresores<em> la</em> cambiaron por una palabra contraria a la que se les había dicho. Por tanto enviamos desde los cielos un castigo a los injustos, porque fueron desobedientes." WHERE ayatNumber = 60 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos </em>cuando Moisés pidió agua para su pueblo y dijimos: “Golpea la roca con tu bastón”, y de ella brotaron doce fuentes, a <em>fin de que </em>cada tribu supiera en qué lugar debía beber. “Comed y bebed de lo que Al-lah ha proporcionado, y no cometáis iniquidades en la tierra, creando el desorden.”" WHERE ayatNumber = 61 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando dijisteis: “Oh, Moisés, ciertamente no soportaremos una sola <em>clase de</em> comida; ruega, pues, a tu Señor por nosotros para que haga brotar a nuestro favor lo que la tierra produce: sus hierbas, sus pepinos, su trigo, sus lentejas y sus cebollas”. Él dijo: “¿Acaso vais a cambiar lo inferior por lo superior? Bajad a alguna ciudad y allí tendréis lo que pedís.” Y fueron golpeados con el abatimiento y la pobreza, e incurrieron en la cólera de Al-lah: así ocurrió porque rechazaron los Signos de Al-lah y mataron injustamente a los Profetas; y fue porque se rebelaron y fueron transgresores." WHERE ayatNumber = 62 AND chapterNumber = 2;
UPDATE verses SET es = "Ciertamente, los creyentes, los judíos, los cristianos y los sabeos –y cualquiera <em>de entre aquellos</em> que crea <em>verdaderamente</em> en Al-lah y el Último Día y haga buenas obras tendrán su recompensa con su Señor y no les <em>sobrecogerá</em> el temor, ni se afligirán." WHERE ayatNumber = 63 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando aceptamos vuestra alianza y elevamos sobre vosotros el Monte, <em>diciendo:</em> “Sujetad con fuerza lo que os hemos dado y recordad lo que contiene, a fin de que os salvéis.”" WHERE ayatNumber = 64 AND chapterNumber = 2;
UPDATE verses SET es = "Pero, después de esto, os alejasteis; y de no haber sido por la gracia de Al-lah para vosotros y por Su misericordia, ciertamente os contaríais entre los desventurados." WHERE ayatNumber = 65 AND chapterNumber = 2;
UPDATE verses SET es = "Y ciertamente, habéis conocido <em>la suerte </em>de aquellos de vosotros que violaron lo relacionado con el Sabbath. Por eso les dijimos, “Volveos monos despreciables.”" WHERE ayatNumber = 66 AND chapterNumber = 2;
UPDATE verses SET es = "Así lo convertimos en un ejemplo para sus contemporáneos y para los que vinieron después, y en una lección para los que temen a Dios." WHERE ayatNumber = 67 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando Moisés dijo a su pueblo: “Al-lah os ordena sacrificar una vaca”, y ellos dijeron: “¿Acaso pretendes burlarte de nosotros?” Dijo: “Busco refugio en Al-lah para no ser uno de los ignorantes.”" WHERE ayatNumber = 68 AND chapterNumber = 2;
UPDATE verses SET es = "Ellos dijeron: “Ruega por nosotros a tu Señor para que nos indique cómo es”. Respondió: “<em>Dios</em> dice: es una vaca, ni vieja ni joven, ya crecida, de edad intermedia entre las dos; y ahora haced lo que se os manda.”" WHERE ayatNumber = 69 AND chapterNumber = 2;
UPDATE verses SET es = "Dijeron: “Ruega por nosotros a tu Señor para que nos indique de qué color es”. Él respondió: “<em>Dios</em> dice que es una vaca de color pardo, <em>de tono</em> puro y rico; que agrada a cuantos la miran.”" WHERE ayatNumber = 70 AND chapterNumber = 2;
UPDATE verses SET es = "Dijeron: “Ruega por nosotros a tu Señor para que nos indique cómo es, ya que para nosotros todas esas vacas se asemejan.” Y si Al-lah quiere, ciertamente, seremos guiados." WHERE ayatNumber = 71 AND chapterNumber = 2;
UPDATE verses SET es = "Respondió: “<em>Dios</em> dice que es una vaca no agotada a fuerza de arar la tierra o regar los cultivos; un animal sin tacha; de un solo color”. Dijeron: “Ahora has traído la verdad”. Entonces la sacrificaron, aunque a punto estuvieron de no hacerlo." WHERE ayatNumber = 72 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando matasteis a una persona y disputasteis entre vosotros por ese motivo, y Al-lah sacó a la luz lo que ocultasteis." WHERE ayatNumber = 73 AND chapterNumber = 2;
UPDATE verses SET es = "Entonces dijimos: “Castigadlo severamente (al asesino) por una parte <em>de la ofensa realizada contra </em>él” (la víctima). Así Al-lah da vida a los <em>espiritualmente</em> muertos y os muestra Sus Signos para que entendáis." WHERE ayatNumber = 74 AND chapterNumber = 2;
UPDATE verses SET es = "Luego, posteriormente, se endurecieron vuestros corazones, hasta ser como piedras o aún más duros; pues en cuanto a las piedras hay, en verdad, algunas de las que brotan ríos y algunas de las que, una vez partidas, sale agua. Y en verdad, entre ellas hay otras que se humillan por temor a Al-lah. Y Al-lah no ignora lo que hacéis." WHERE ayatNumber = 75 AND chapterNumber = 2;
UPDATE verses SET es = "¿Mantenéis la esperanza de que os crean cuando una parte de ellos ha pervertido conscientemente la palabra de Al-lah después de que la hubieran escuchado y la hubieran entendido y la conocieran perfectamente?" WHERE ayatNumber = 76 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando encuentran a los creyentes, dicen: “Creemos”, pero cuando se reúnen en privado, unos con otros, dicen: “¿Les informáis de lo que Al-lah os ha revelado, para que puedan así discutir con vosotros ante vuestro Señor?”. ¿No comprenderéis entonces?" WHERE ayatNumber = 77 AND chapterNumber = 2;
UPDATE verses SET es = "¿Acaso no saben que Al-lah conoce lo que ocultan y lo que revelan?" WHERE ayatNumber = 78 AND chapterNumber = 2;
UPDATE verses SET es = "Y algunos de ellos son analfabetos; no conocen el Libro sino <em>sus propias</em> falsas concepciones, y <em>no hacen más</em> que conjeturas." WHERE ayatNumber = 79 AND chapterNumber = 2;
UPDATE verses SET es = "¡Ay, pues, de aquellos que escriben el Libro con sus propias manos y después dicen: “Esto viene de Al-lah”, para poder pedir por él un precio ridículo! ¡Ay, pues, de ellos por lo que han escrito sus manos, y ¡Ay! de ellos por lo que ganaron!" WHERE ayatNumber = 80 AND chapterNumber = 2;
UPDATE verses SET es = "Y dicen: “El Fuego no nos tocará sino durante un número <em>reducido</em> de días” Diles: “¿Habéis recibido una promesa de Al-lah? Si es así, Al-lah nunca incumplirá Su promesa. O bien ¿afirmáis de Al-lah lo que desconocéis?”." WHERE ayatNumber = 81 AND chapterNumber = 2;
UPDATE verses SET es = "¡Ay de aquél que hace el mal y está rodeado de sus pecados!: Ésos son los prisioneros del Fuego; en él morarán." WHERE ayatNumber = 82 AND chapterNumber = 2;
UPDATE verses SET es = "Pero los que creen y hacen buenas obras: ésos son los habitantes del Cielo; en él morarán eternamente." WHERE ayatNumber = 83 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando aceptamos una alianza de los Hijos de Israel: “No adoréis sino a Al-lah, sed buenos con los padres, los parientes, los huérfanos y los pobres; hablad bondadosamente a los hombres, cumplid la Oración y pagad el <em>Zakat,</em>”; entonces volvisteis la espalda alejándoos, excepto unos cuantos." WHERE ayatNumber = 84 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando acepté la alianza con vosotros: “No derramaréis vuestra propia sangre ni expulsaréis a los vuestros de vuestras casas”; luego <em>lo</em> confirmasteis; y habéis sido testigos <em>de ello</em>." WHERE ayatNumber = 85 AND chapterNumber = 2;
UPDATE verses SET es = "No obstante, sois de los que matan a sus propios hermanos y expulsan a parte de vuestro pueblo de sus casas, ayudándoos mutuamente contra ellos con pecado y trasgresión. Y si os llegan como prisioneros, los rescatáis, aunque <em>el hecho mismo de</em> su expulsión sea ilegal para vosotros. ¿Acaso creéis en <em>una </em>parte del Libro y no creéis en <em>otra</em> parte? No hay pues recompensa alguna para aquellos de vosotros que así actúen, salvo la desgracia en esta vida; y en el Día del Juicio serán conducidos al más terrible castigo; y, en verdad, Al-lah no ignora lo que hacéis." WHERE ayatNumber = 86 AND chapterNumber = 2;
UPDATE verses SET es = "Son éstos los que han preferido la presente vida a la Futura. Su castigo no será pues, aliviado, ni serán auxiliados <em>de ninguna otra manera.</em>" WHERE ayatNumber = 87 AND chapterNumber = 2;
UPDATE verses SET es = "Y en verdad Dimos a Moisés el Libro y enviamos a Mensajeros tras él, para que siguieran sus pasos; y a Jesús, hijo de María, dimos Signos manifiestos y lo fortalecimos con el Espíritu de santidad. ¿Es que cada vez que os llegue un Mensajero con lo que no deseéis, os comportaréis con arrogancia, trataréis a unos de mentirosos y mataréis a otros?" WHERE ayatNumber = 88 AND chapterNumber = 2;
UPDATE verses SET es = "Dijeron, “Nuestros corazones están rodeados por una envoltura”. ¡No!, Al-lah los ha maldecido por su incredulidad. En muy pocas cosas creen." WHERE ayatNumber = 89 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando les llegó un Libro enviado por Al-lah, cumpliendo lo que ya tenían, –y antes de eso habían implorado la victoria sobre los incrédulos-, no obstante, cuando les llegó lo que ya sabían, lo rechazaron. ¡Que la maldición de Dios caiga sobre los incrédulos!" WHERE ayatNumber = 90 AND chapterNumber = 2;
UPDATE verses SET es = "Por maldad es por lo que vendieron sus almas: no creyeron en lo que Al-lah ha revelado, quejándose, por envidia, de que Al-lah enviara su gracia a aquellos de Sus siervos que deseara. De este modo, incurrieron en cólera sobre cólera; y hay un ignominioso castigo aguardando a los incrédulos." WHERE ayatNumber = 91 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando se les dice: “Creed en lo que Al-lah ha revelado”, responden: “Creemos en lo que se nos ha revelado”; pero no creen en lo que <em>ha sido revelado</em> posteriormente, aunque es la Verdad, que cumple lo que ya tienen. Diles: “¿Por qué, pues, intentasteis matar a los Profetas de Al-lah con anterioridad, si erais creyentes?”." WHERE ayatNumber = 92 AND chapterNumber = 2;
UPDATE verses SET es = "Y Moisés vino a vosotros con Signos manifiestos, pero tomasteis en su ausencia al becerro <em>para adorarlo,</em> y fuisteis pecadores." WHERE ayatNumber = 93 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando aceptamos vuestra alianza y elevamos sobre vosotros el Monte, <em>diciendo: </em>“Sujetad firmemente lo que os hemos dado y escuchad”; ellos dijeron: “Escuchamos pero desobedecemos;” y sus corazones se inundaron de <em>amor por el</em> becerro, debido a su incredulidad. Diles: “¡Mala cosa es lo que os impone vuestra fe, si es que tenéis alguna fe!”." WHERE ayatNumber = 94 AND chapterNumber = 2;
UPDATE verses SET es = "Diles: “Si la morada del Más Allá, junto a Al-lah, es únicamente para vosotros, excluidos <em>todos</em> los demás pueblos, entonces desead la muerte, si sois veraces”." WHERE ayatNumber = 95 AND chapterNumber = 2;
UPDATE verses SET es = "Pero nunca la desearán, a causa de lo que les precede, causado por sus propias manos; y Al-lah conoce bien a los que hacen el mal." WHERE ayatNumber = 96 AND chapterNumber = 2;
UPDATE verses SET es = "Y encontrarás en verdad que son, entre todas las gentes, los más ávidos de vivir, incluso <em>más</em> que los que elevan a otros a la altura <em>de Dios.</em> Todos desean que se les conceda una vida de mil años, pero aunque se les concediera <em>tal </em>vida eso no les ahorraría el castigo; y Al-lah ve todo lo que hacen." WHERE ayatNumber = 97 AND chapterNumber = 2;
UPDATE verses SET es = "Diles: “Quien sea enemigo de Gabriel –porque él fue quien lo hizo bajar a tu corazón, por mandato de Al-lah, lo que cumple todo lo anterior, y es una guía y buena nueva para los creyentes-.”" WHERE ayatNumber = 98 AND chapterNumber = 2;
UPDATE verses SET es = "“Quien sea enemigo de Al-lah y de sus Ángeles, y de sus Mensajeros, de Gabriel y de Miguel, <em>sepa que</em>, en verdad, Al-lah es enemigo de <em>tales</em> incrédulos.”" WHERE ayatNumber = 99 AND chapterNumber = 2;
UPDATE verses SET es = "Y en verdad, te hemos revelado Signos manifiestos, y nadie se niega a creer en ellos, excepto los desobedientes." WHERE ayatNumber = 100 AND chapterNumber = 2;
UPDATE verses SET es = "¡Cómo! ¿cada vez que concluyen un pacto ha de ser rechazado por un grupo de ellos?. No, la mayoría no tienen fe." WHERE ayatNumber = 101 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>ahora,</em> cuando les ha llegado un Mensajero de Al-lah confirmando lo que ya tienen, un grupo del pueblo al que se le dio el Libro han arrojado el Libro de Al-lah a sus espaldas, como si no <em>lo</em> conocieran." WHERE ayatNumber = 102 AND chapterNumber = 2;
UPDATE verses SET es = "Y caminan por <em>la senda </em>que recorrieron <em>los hombres </em>rebeldes durante el reinado de Salomón. Y Salomón no fue un incrédulo, fueron incrédulos los rebeldes, enseñando a las gentes la falsedad y el engaño. Y <em>persiguen</em> lo que fue revelado a los dos ángeles de Babilonia, Harut y Marut. Pero estos dos no enseñaron a nadie sin antes decirles: “No somos más que una prueba, no dejes de creer”. Así los hombres aprendieron de ellos aquello en lo que se diferencia un hombre de su mujer, y no hicieron con ello mal a nadie, excepto por mandato de Al-lah; <em>por el contrario,</em> estas gentes aprenden lo que les perjudica y no les es de utilidad. Y han sabido ciertamente que el que comercia con ello no participará en las delicias de la Vida Futura; y, en verdad, por la maldad es por lo que vendieron sus almas; ¡si lo hubiesen sabido!" WHERE ayatNumber = 103 AND chapterNumber = 2;
UPDATE verses SET es = "Si hubiesen creído y actuado santamente, mejor hubiera sido en verdad la recompensa de Al-lah; ¡si lo hubiesen sabido!" WHERE ayatNumber = 104 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes!, no digáis <em>al Profeta,</em> “escúchanos”, sino “obsérvanos” y escuchad. Y para los incrédulos hay un doloroso castigo." WHERE ayatNumber = 105 AND chapterNumber = 2;
UPDATE verses SET es = "Los que no creen de entre el Pueblo del Libro, o de entre aquellos que asocian otros dioses <em>a Al-lah</em>, no desean que se os envíe nada bueno de parte de vuestro Señor; pero Al-lah elige para Su misericordia a quien le place; y Al-lah es de extrema generosidad." WHERE ayatNumber = 106 AND chapterNumber = 2;
UPDATE verses SET es = "Si abrogamos o hacemos que se olvide cualquier Signo, traemos otro mejor o similar a él. ¿No sabes acaso que Al-lah tiene el poder de hacer todo lo que le place?" WHERE ayatNumber = 107 AND chapterNumber = 2;
UPDATE verses SET es = "¿No sabes acaso que el Reino de los Cielos y la Tierra pertenece a Al-lah sólo? Y fuera de Al-lah no tienes protector ni defensor alguno." WHERE ayatNumber = 108 AND chapterNumber = 2;
UPDATE verses SET es = "¿Queréis interrogar al Mensajero enviado a vosotros como antes fue interrogado Moisés? Y quien cambia la fe por la incredulidad evidentemente se ha apartado del camino recto." WHERE ayatNumber = 109 AND chapterNumber = 2;
UPDATE verses SET es = "Muchos del Pueblo del Libro desearían, por la <em>mera</em> envidia que sienten sus almas, convertiros en incrédulos después de que habéis creído, tras habérseles hecho evidente la verdad. Mas perdonadlos y alejaos de <em>ellos,</em> hasta que Al-lah traiga su decreto. En verdad, Al-lah tiene el poder de hacer todo lo que le place." WHERE ayatNumber = 110 AND chapterNumber = 2;
UPDATE verses SET es = "Y observad la Oración y pagad el <em>Zakat; </em>y todo el bien que hayáis practicado antes por vosotros mismos, lo encontraréis con Al-lah. En verdad, Al-lah ve todo lo que hacéis." WHERE ayatNumber = 111 AND chapterNumber = 2;
UPDATE verses SET es = "Y dicen: “Nadie entrará en el Cielo a menos que sea judío o cristiano.” Estos son sus vanos deseos. Decidles: “Probadlo, si sois veraces.”" WHERE ayatNumber = 112 AND chapterNumber = 2;
UPDATE verses SET es = "¡No!, quien se someta completamente a Al-lah, siendo excelente en conducta, tendrá su recompensa con su Señor. Ningún temor les <em>sobrecogerá</em> ni sufrirán aflicción." WHERE ayatNumber = 113 AND chapterNumber = 2;
UPDATE verses SET es = "Y los judíos dicen: “Los cristianos no se apoyan en nada”; y los cristianos dicen: “Los judíos no se apoyan en nada”; aunque <em>ambos</em> leen el <em>mismo</em> Libro. Y lo mismo que ellos dicen, dijeron los que no tenían conocimiento. Pero Al-lah juzgará entre ellos, el Día de la Resurrección, en relación con sus diferencias." WHERE ayatNumber = 114 AND chapterNumber = 2;
UPDATE verses SET es = "Y ¿hay alguien más injusto que quien prohíbe que el nombre de Al-lah sea glorificado en los templos de Al-lah y busca su ruina? No era apropiado que tales hombres entraran en ellos sino con temor. Sobre ellos caerá la ignominia en este mundo; y les está reservado un duro castigo en el próximo." WHERE ayatNumber = 115 AND chapterNumber = 2;
UPDATE verses SET es = "A Al-lah pertenecen el Oriente y el Occidente; por ello, a donde quiera que os volváis, allí estará el rostro de Al-lah. En verdad, Al-lah es Generoso, Omnisciente." WHERE ayatNumber = 116 AND chapterNumber = 2;
UPDATE verses SET es = "Y dicen: “Al-lah ha tomado <em>para Sí</em> un hijo” ¡Santo sea!”. No, cuanto hay en los cielos y en la tierra a Él pertenece. A Él todo le obedece." WHERE ayatNumber = 117 AND chapterNumber = 2;
UPDATE verses SET es = "<em> Él es </em>el Originador de los cielos y de la tierra. Cuando decreta una cosa, sólo es preciso que diga “¡Sea!”, y es." WHERE ayatNumber = 118 AND chapterNumber = 2;
UPDATE verses SET es = "Y los que no tienen conocimiento dicen: “¿Por qué no nos habla Al-lah o viene a nosotros un Signo?”. Lo mismo dijeron los que los precedieron, similar a lo que dicen. Sus corazones se asemejan. Ciertamente, hemos hecho patentes los Signos para un pueblo que cree firmemente." WHERE ayatNumber = 119 AND chapterNumber = 2;
UPDATE verses SET es = "Te hemos enviado con la Verdad, como portador de buenas nuevas y para que les adviertas. Y no se te preguntará sobre los moradores del infierno." WHERE ayatNumber = 120 AND chapterNumber = 2;
UPDATE verses SET es = "Y los judíos no estarán en absoluto satisfechos contigo, ni tampoco los cristianos, salvo que sigas su religión. Diles: “en verdad, la orientación de Al-lah es la única orientación”. Y si sigues sus perversos deseos después del conocimiento que te ha venido, no tendrás en Al-lah ni amigo ni protector." WHERE ayatNumber = 121 AND chapterNumber = 2;
UPDATE verses SET es = "Aquellos, a los que hemos dado el Libro, lo siguen como debe ser seguido; son ésos los que creen en él. Y quienes no crean en él, ésos serán los perdedores." WHERE ayatNumber = 122 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh, hijos de Israel! acordaos de los favores que os concedí, y de que os levanté por encima de todos los pueblos." WHERE ayatNumber = 123 AND chapterNumber = 2;
UPDATE verses SET es = "Y temed el día en que ningún alma podrá en absoluto ocupar el lugar de otra alma, ni se aceptará rescate alguno de ella, ni le valdrá intercesión alguna, ni serán socorridas." WHERE ayatNumber = 124 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando su Señor puso a prueba a Abraham con ciertas órdenes que él cumplió. Dijo; “te convertiré en Guía de los hombres”. <em>Abraham</em> preguntó: “¿Y de entre mi descendencia?”. Él dijo: “Mi pacto no alcanza a los transgresores.”" WHERE ayatNumber = 125 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando hicimos de la Casa un lugar de reunión para la humanidad y <em>un lugar</em> de seguridad; y ocupad vosotros el sitio de Abraham como lugar de Oración. Y dimos una orden a Abraham y a Ismael, <em>diciendo</em>: “Purificad Mi Casa para los que hacen el circuito y <em>para</em> los que permanecen <em>en ella</em> por devoción y <em>para</em> los que se inclinan y se postran <em>en Oración”.</em>" WHERE ayatNumber = 126 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando Abraham dijo: “Mi Señor, convierte a ésta en ciudad de paz y proporciona frutos a aquellos de sus habitantes que crean en Al-lah y en el Último Día”. Él dijo: “e incluso a quien no crea le concederé también mis beneficios durante algún tiempo; luego le empujaré al castigo del Fuego, ¡un destino desdichado!”." WHERE ayatNumber = 127 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando Abraham e Ismael levantaron los cimientos de la Casa, <em>pidiendo:</em> “Señor Nuestro acepta <em>esto </em>de nosotros; porque Tú eres el que todo lo oye, el que todo lo sabe”;" WHERE ayatNumber = 128 AND chapterNumber = 2;
UPDATE verses SET es = "“Señor Nuestro, haznos sumisos a Ti, y <em>haz</em> de nuestra descendencia un pueblo sumiso a Ti. Y enséñanos nuestros lugares de culto y vuélvete a nosotros con misericordia; porque Tú eres Remisorio <em>con compasión</em> y Misericordioso”;" WHERE ayatNumber = 129 AND chapterNumber = 2;
UPDATE verses SET es = "“Y, Señor Nuestro, suscita de entre ellos un Mensajero, que les recite Tus Signos y les enseñe el Libro y la Sabiduría y les purifique; en verdad, Tú eres el Poderoso, el Sabio”." WHERE ayatNumber = 130 AND chapterNumber = 2;
UPDATE verses SET es = "Y ¿quién se apartará de la religión de Abraham sino el insensato? A él lo escogimos en este mundo, y en el futuro estará ciertamente entre los justos." WHERE ayatNumber = 131 AND chapterNumber = 2;
UPDATE verses SET es = "Cuando su Señor le dijo “sométete”, él contestó: “Me he sometido al Señor de los mundos”." WHERE ayatNumber = 132 AND chapterNumber = 2;
UPDATE verses SET es = "Lo mismo impuso Abraham a sus hijos –y <em>así hizo</em> Jacob- <em>diciendo</em>: “¡Oh, hijos míos!, en verdad, Al-lah ha escogido esta religión para vosotros; así pues, no dejéis que la muerte caiga sobre vosotros a menos que estéis en estado de sumisión”." WHERE ayatNumber = 133 AND chapterNumber = 2;
UPDATE verses SET es = "¿Estuvisteis presentes cuando vino la muerte a Jacob y dijo a sus hijos: “¿qué adoraréis después de mí”? Ellos respondieron: “Adoraremos a tu Dios, el Dios de tus padres, Abraham e Ismael e Isaac, el Dios Único; y a Él nos sometemos”." WHERE ayatNumber = 134 AND chapterNumber = 2;
UPDATE verses SET es = "Son un pueblo que ha pasado; para ellos lo que ganaron, y para vosotros será lo que ganéis; y no seréis interrogados sobre lo que ellos hicieron." WHERE ayatNumber = 135 AND chapterNumber = 2;
UPDATE verses SET es = "Y dicen: “Sed judíos o cristianos para que seáis bien guiados”. Decid: “No, <em>seguid</em> la religión de Abraham que siempre se inclinó<em> ante</em> <em>Dios;</em> no fue de aquellos que elevan dioses <em>a la altura de Dios”.</em>" WHERE ayatNumber = 136 AND chapterNumber = 2;
UPDATE verses SET es = "Decid: “Creemos en Al-lah y en lo que nos ha sido revelado, y en lo que fue revelado a Abraham e Ismael, e Isaac y Jacob y <em>sus </em> hijos, y en lo que fue dado a Moisés y Jesús, y en lo que fue dado a <em>todos los demás</em> Profetas por su Señor. Nosotros no hacemos diferencias entre ninguno de ellos; y a Él nos sometemos.”" WHERE ayatNumber = 137 AND chapterNumber = 2;
UPDATE verses SET es = "Y si creen como vosotros habéis creído, están, en verdad, bien guiados; pero si vuelven la espalda, entonces <em>no hacen más que </em>crear un cisma, y Al-lah te bastará ciertamente contra ellos, porque Él es el que todo lo oye, El Omnisciente." WHERE ayatNumber = 138 AND chapterNumber = 2;
UPDATE verses SET es = "<em> Decid</em>: “<em>Nosotros adoptaremos</em> la religión de Al-lah; ¿quién posee mejores atributos que Al-lah?, y a Él sólo adoramos”." WHERE ayatNumber = 139 AND chapterNumber = 2;
UPDATE verses SET es = "Di: ¿Disputaréis con nosotros acerca de Al-lah, siendo así que Él es nuestro Señor y vuestro Señor? Y para nosotros son nuestras obras, y para vosotros las vuestras; y sólo de Él somos sinceros devotos”." WHERE ayatNumber = 140 AND chapterNumber = 2;
UPDATE verses SET es = "¿Decís que Abraham, Ismael, Isaac, Jacob y <em>sus</em> hijos eran judíos o cristianos? Di: “¿Sabéis más que Al-lah? ¿Y hay alguien más injusto que quien oculta el testimonio que ha recibido de Al-lah? Y Al-lah no ignora lo que hacéis”." WHERE ayatNumber = 141 AND chapterNumber = 2;
UPDATE verses SET es = "Son un pueblo que ha desaparecido; para ellos lo que ganaron y para vosotros será lo que ganéis; y no se os interrogará sobre lo que hicieron." WHERE ayatNumber = 142 AND chapterNumber = 2;
UPDATE verses SET es = "Los insensatos de entre el pueblo dirán: “¿Qué les ha hecho apartarse de la Kibla que seguían?”. Diles: “A Al-lah pertenece el Oriente y el Occidente. Él guía, a quienes le place, por el camino recto”." WHERE ayatNumber = 143 AND chapterNumber = 2;
UPDATE verses SET es = "Y así os convertimos en una nación ensalzada, para que pudierais ser guardianes de los hombres, y para que el Mensajero <em>de Dios</em> pudiera ser vuestro guardián. Y no establecimos la Kibla que seguisteis, sino para distinguir entre aquel que sigue al Mensajero <em>de Dios</em> y el que vuelve sobre sus talones. Y esto es ciertamente difícil, salvo para aquellos a quienes Al-lah ha guiado. Y no corresponde a Al-lah hacer que perdáis vuestra fe; ciertamente, Al-lah es Compasivo y Misericordioso con los hombres." WHERE ayatNumber = 144 AND chapterNumber = 2;
UPDATE verses SET es = "En verdad, vemos cómo vuelves a menudo tu rostro hacia el cielo; ciertamente, te haremos volver a la Kibla que más te agrade. Vuelve, pues, tu rostro hacia la Mezquita Sagrada; y donde quiera que estéis, volved vuestros rostros hacia ella. Y aquellos a quienes ha sido entregado el Libro saben que ésta es la verdad de su Señor; y Al-lah no ignora lo que hacen." WHERE ayatNumber = 145 AND chapterNumber = 2;
UPDATE verses SET es = "Y aunque mostraras todos los Signos a quienes se dio el Libro, nunca seguirán tu Kibla; ni tú seguirás la Kibla de ellos; ni tampoco ninguno de ellos seguiría la Kibla de los demás. Y si siguieras sus deseos después del conocimiento que te ha sido dado, entonces te contarías ciertamente entre los transgresores." WHERE ayatNumber = 146 AND chapterNumber = 2;
UPDATE verses SET es = "Aquellos a quienes hemos entregado el Libro lo reconocen <em> como la verdad</em>, igual que reconocen a sus hijos, pero algunos de ellos ocultan a sabiendas la verdad." WHERE ayatNumber = 147 AND chapterNumber = 2;
UPDATE verses SET es = "Es la verdad de tu Señor; no seas, por tanto, de los que dudan." WHERE ayatNumber = 148 AND chapterNumber = 2;
UPDATE verses SET es = "Y cada uno tiene una meta hacia la que se dirige. Rivalizad, pues, entre vosotros en las buenas obras. Dondequiera que estéis, Al-lah os reunirá a todos. Ciertamente, Al-lah tiene poder para hacer todo lo que desea." WHERE ayatNumber = 149 AND chapterNumber = 2;
UPDATE verses SET es = "Y salgas de donde salgas, vuelve tu rostro hacia la Mezquita Sagrada, porque ésta es, en efecto, la verdad que procede de tu Señor. Y Al-lah no ignora lo que hacéis." WHERE ayatNumber = 150 AND chapterNumber = 2;
UPDATE verses SET es = "Y vengas de donde vengas, vuelve tu rostro hacia la Mezquita Sagrada; y dondequiera que estéis, volved vuestros rostros hacia ella, para que la gente no pueda presentar argumentos en contra vuestra, excepto los que son injustos –por lo que no debéis temerles a ellos, sino a Mí-, y para que perfeccione mis dones para vosotros, y seáis guiados por el camino recto." WHERE ayatNumber = 151 AND chapterNumber = 2;
UPDATE verses SET es = "Y así os enviamos un Mensajero de entre vosotros mismos, el cual os lee Nuestros Signos, y os purifica y os enseña el Libro y la Sabiduría, y os enseña lo que no sabíais." WHERE ayatNumber = 152 AND chapterNumber = 2;
UPDATE verses SET es = "Acordaos, pues, de Mí, <em>y yo</em> me acordaré de vosotros; y sed agradecidos y no ingratos Conmigo." WHERE ayatNumber = 153 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis!, buscad ayuda con paciencia y Oración; ciertamente Al-lah está con los constantes." WHERE ayatNumber = 154 AND chapterNumber = 2;
UPDATE verses SET es = "Y no digáis de los que cayeron por la causa de Al-lah que están muertos; no, están vivos, pero no os dais cuenta." WHERE ayatNumber = 155 AND chapterNumber = 2;
UPDATE verses SET es = "Y os pondremos a prueba con algo de temor y hambre, y con pérdida de riquezas, de vidas y de frutos; pero dad la buena nueva a los pacientes." WHERE ayatNumber = 156 AND chapterNumber = 2;
UPDATE verses SET es = "Aquellos que, cuando les aflige una desgracia, dicen: “en verdad, a Al-lah pertenecemos y a Él volveremos”." WHERE ayatNumber = 157 AND chapterNumber = 2;
UPDATE verses SET es = "Para éstos son las bendiciones de su Señor y su misericordia, y son ellos los guiados por el camino recto." WHERE ayatNumber = 158 AND chapterNumber = 2;
UPDATE verses SET es = "Ciertamente, Al-Safa y Al-Marwa están entre los Signos de Al-lah. No hay, por tanto, pecado, para quien va en peregrinación a la Casa o realiza la ´Umrah, en dar vueltas alrededor de ambas. Y quienes practican el bien más allá de lo que es obligatorio, <em>sepan</em> que en verdad Al-lah es Apreciador y Omnisciente." WHERE ayatNumber = 159 AND chapterNumber = 2;
UPDATE verses SET es = "Aquellos que ocultan lo que hemos hecho descender en forma de Signos y de guía, después de haberlo dejado muy claro para los hombres en el Libro, a ésos son a quienes Al-lah maldice; y <em>así</em> los maldigan quienes maldicen." WHERE ayatNumber = 160 AND chapterNumber = 2;
UPDATE verses SET es = "Pero los que se arrepienten y se corrigen y declaran abiertamente <em>la verdad,</em> a ésos Me volveré con mi perdón, pues soy El que perdona constantemente <em>con compasión</em> y El Misericordioso." WHERE ayatNumber = 161 AND chapterNumber = 2;
UPDATE verses SET es = "Los que son incrédulos y mueren en la incredulidad, sobre ellos caerá la maldición de Al-lah, de los ángeles y de los hombres, a un mismo tiempo." WHERE ayatNumber = 162 AND chapterNumber = 2;
UPDATE verses SET es = "Permanecerán bajo ella. No se les aligerará el castigo ni se les concederá respiro." WHERE ayatNumber = 163 AND chapterNumber = 2;
UPDATE verses SET es = "Y vuestro Dios es un Dios Único; no hay otro Dios que no sea Él, el Clemente, el Misericordioso." WHERE ayatNumber = 164 AND chapterNumber = 2;
UPDATE verses SET es = "En verdad, en la creación de los cielos y la tierra y en la sucesión de la noche y el día, y en los barcos que surcan los mares con todo lo que es útil a los hombres, y en el agua que Al-lah hace descender del cielo con la que resucita la tierra después de su muerte, distribuyendo por ella toda clase de bestias, y en el cambio de los vientos y en las nubes sometidas a su servicio entre el cielo y la tierra... en todo ello hay Signos para las gentes que comprenden." WHERE ayatNumber = 165 AND chapterNumber = 2;
UPDATE verses SET es = "Y entre los hombres hay algunos que toman para sí objetos de culto distintos de Al-lah, amándolos como deberían amar a Al-lah. Pero los creyentes son más fuertes en <em>su </em>amor por Al-lah. Y si los pecadores pudieran contemplar <em>ahora</em> el momento en que verán el castigo, <em>comprenderían</em> que todo el poder pertenece a Al-lah y que Al-lah es severo en el castigo." WHERE ayatNumber = 166 AND chapterNumber = 2;
UPDATE verses SET es = "<em> En verdad que comprenderían, si pudieran contemplar el momento </em> en que los que eran seguidos repudiarán a sus seguidores y contemplarán el castigo y sus medios de escape quedarán rotos." WHERE ayatNumber = 167 AND chapterNumber = 2;
UPDATE verses SET es = "Y los que seguían dirán: “Si al menos pudiéramos volver, los repudiaríamos como nos han repudiado a nosotros”. Así Al-lah les mostrará sus obras con gran congoja para ellos, y no saldrán del Fuego." WHERE ayatNumber = 168 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh humanos! comed de lo que es lícito y saludable en la tierra; y no sigáis las huellas de Satanás; en verdad, él es para vosotros un enemigo declarado." WHERE ayatNumber = 169 AND chapterNumber = 2;
UPDATE verses SET es = "Él sólo os impone lo que es malo y lo que es abominable, y que digáis de Al-lah lo que desconocéis." WHERE ayatNumber = 170 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando se les dice: “Seguid lo que Al-lah os ha enviado”, dicen: “No, seguiremos lo que encontramos en nuestros padres”. ¡Cómo! ¿aunque sus padres fueran insensatos y alejados del buen camino?" WHERE ayatNumber = 171 AND chapterNumber = 2;
UPDATE verses SET es = "Y el caso de los que no creen es como el del que grita a otro cuando éste no oye más que gritos y voceríos. <em>Son</em> sordos, mudos y ciegos: por eso no razonan." WHERE ayatNumber = 172 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! Comed de las cosas buenas que os hemos proporcionado y dad gracias a Al-lah, si es a Él a quien adoráis." WHERE ayatNumber = 173 AND chapterNumber = 2;
UPDATE verses SET es = "Él os ha prohibido únicamente lo que muere por sí mismo y la sangre y la carne de cerdo, y aquello sobre lo que haya sido invocado un nombre distinto del de Al-lah. Pero el que se vea movido por la necesidad sin ser desobediente ni trasgresor, ése estará libre de pecado. En verdad, Al-lah es el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 174 AND chapterNumber = 2;
UPDATE verses SET es = "Quienes ocultan lo que Al-lah ha hecho descender en el Libro y reciben a cambio de ello un precio mísero, no llenarán sus vientres sino de fuego. Al-lah no les hablará el Día de la Resurrección, ni les purificará. Y para ellos hay un horrible castigo." WHERE ayatNumber = 175 AND chapterNumber = 2;
UPDATE verses SET es = "Son los que cambian el buen camino por el error y el perdón por el castigo. ¡Con qué gran paciencia deberán resistir el Fuego!" WHERE ayatNumber = 176 AND chapterNumber = 2;
UPDATE verses SET es = "Eso es porque Al-lah ha hecho descender el Libro con la verdad; y ciertamente, quienes se muestran en desacuerdo respecto al Libro han ido demasiado lejos en su enemistad." WHERE ayatNumber = 177 AND chapterNumber = 2;
UPDATE verses SET es = "La piedad no consiste en que volváis vuestros rostros a Oriente o a Occidente, el <em>verdaderamente</em> justo es aquel que cree en Al-lah y en el Último Día, y en los ángeles y en el Libro y en los Profetas, y otorga su dinero por amor a Él a los parientes y a los huérfanos, al necesitado y al viajero, y a los que piden por <em>caridad;</em> y para <em>el rescate de</em> los cautivos; y quien cumple la Oración y paga el <em>Zakat;</em> y los que cumplen su promesa cuando la hacen, y el paciente en la pobreza y en la desgracia, y el <em>constante</em> y <em>firme</em> en tiempo de guerra; éstos son quienes se han mostrado veraces y quienes sienten temor de Dios." WHERE ayatNumber = 178 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! se os prescribe una ley del talión equitativa <em>en caso de</em> homicidio; el hombre libre por el hombre libre, y el esclavo por el esclavo, y la mujer por la mujer. Pero si a alguien se le perdona por su propio hermano, entonces <em>el pleito para determinar la indemnización</em> se llevará con equidad, y <em>el asesino</em> le pagará la indemnización con generosidad. Esto es un alivio <em>por parte</em> de vuestro Señor y una gracia. Y quien lo infrinja posteriormente, sufrirá un horrible castigo." WHERE ayatNumber = 179 AND chapterNumber = 2;
UPDATE verses SET es = "En <em>la ley del</em> talión hay vida para vosotros. ¡Oh hombres de entendimiento!, para que podáis disfrutar de seguridad." WHERE ayatNumber = 180 AND chapterNumber = 2;
UPDATE verses SET es = "Se os prescribe que, cuando llegue la muerte a uno de vosotros, si deja muchos bienes, <em>haga</em> testamento a favor de sus padres y parientes cercanos, actuando con equidad, esto es un deber para los que temen a Dios." WHERE ayatNumber = 181 AND chapterNumber = 2;
UPDATE verses SET es = "Y quien lo modifique después de haberlo oído, el pecado así cometido recaerá ciertamente sobre los que lo han modificado. En verdad, Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 182 AND chapterNumber = 2;
UPDATE verses SET es = "Pero quien descubre en un testador una parcialidad o error, y restablece la paz entre ellas (las partes afectadas), estará libre de pecado. En verdad, Al-lah es el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 183 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! se os prescribe el ayuno, como fue prescrito a los que os precedieron, para que seáis justos." WHERE ayatNumber = 184 AND chapterNumber = 2;
UPDATE verses SET es = "<em> El ayuno prescrito</em> debe durar un número fijo de días, pero aquel de entre vosotros que esté enfermo o de viaje <em>ayunará</em> el mismo número de días con posterioridad; y para los que <em>sólo</em> pueden ayunar con gran dificultad hay una expiación: alimentar a un pobre. Y quien realiza una buena obra con obediencia voluntaria, mejor para él. Y el ayuno es bueno para vosotros, ¡si lo supierais!" WHERE ayatNumber = 185 AND chapterNumber = 2;
UPDATE verses SET es = "El mes de Ramadán es aquél en que se hizo descender el Corán como guía de la humanidad, con pruebas claras de dirección y discernimiento. Por tanto, quienquiera de vosotros que se encuentre <em>en casa</em> durante este mes, que ayune allí. Pero quien esté enfermo o de viaje<em>ayunará</em> el mismo número de otros días. Al-lah desea <em>daros</em> facilidades y no desea para vosotros lo difícil, y que completéis el número <em>de días </em>y ensalcéis a Al-lah por haberos guiado y para que seáis agradecidos." WHERE ayatNumber = 186 AND chapterNumber = 2;
UPDATE verses SET es = "Cuando mis siervos te pregunten por Mí, <em>diles: </em>“Estoy cerca. Respondo a la plegaria del que suplica cuando Me invoca. Por tanto, deben escucharme y creer en Mí, para que puedan seguir el camino recto.”" WHERE ayatNumber = 187 AND chapterNumber = 2;
UPDATE verses SET es = "Durante la noche del ayuno, os es lícita la reunión con vuestras esposas. Ellas son una vestidura para vosotros y vosotros una vestidura para ellas. Al-lah sabe que habéis estado actuando injustamente para con vosotros mismos, por lo que ha vuelto a vosotros con misericordia y os ha proporcionado alivio. Por lo tanto, ahora podéis cohabitar con ellas y buscar lo que Al-lah os ha ordenado; y comed y bebed hasta que el hilo blanco de la aurora se distinga del hilo negro de la noche. Entonces completad el ayuno hasta la noche y no cohabitéis con ellas, mientras permanezcáis en las mezquitas por devoción. Éstos son los límites <em>fijados</em> por Al-lah, por lo que no debéis acercaros a ellas. De este modo manifiesta Al-lah con claridad Sus mandamientos a los hombres, para que puedan estar seguros contra el mal." WHERE ayatNumber = 188 AND chapterNumber = 2;
UPDATE verses SET es = "Y no devoréis mutuamente vuestros bienes con falsedad, ni los ofrezcáis <em>como soborno</em> a las autoridades para apropiaros conscientemente y con injusticia del dinero público." WHERE ayatNumber = 189 AND chapterNumber = 2;
UPDATE verses SET es = "Te preguntan por los novilunios. Diles: “Son medios para medir el tiempo, para el <em>bien general de</em> la humanidad y para la Peregrinación.” Y la santidad no consiste en que entréis en las casas por la parte trasera; sino que el <em>realmente</em> justo es quien teme a Dios. Y debéis entrar en vuestras casas por sus puertas; y temed a Dios para que prosperéis." WHERE ayatNumber = 190 AND chapterNumber = 2;
UPDATE verses SET es = "Y luchad en la causa de Al-lah contra los que luchan contra vosotros, pero no seáis transgresores. En verdad, Al-lah no ama a los transgresores." WHERE ayatNumber = 191 AND chapterNumber = 2;
UPDATE verses SET es = "Y matadlos en donde los encontréis y expulsadlos de donde os hayan expulsado; ya que la persecución es peor que el homicidio. Y no luchéis contra ellos <em>ni dentro ni</em> cerca de la Mezquita Sagrada mientras no os ataquen allí. Pero si os atacan, combatidles: ésa es la retribución para los incrédulos." WHERE ayatNumber = 192 AND chapterNumber = 2;
UPDATE verses SET es = "Pero si desisten, sepan que Al-lah es, en verdad, el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 193 AND chapterNumber = 2;
UPDATE verses SET es = "Y luchad contra ellos hasta que cese la persecución, y<em>se profese libremente</em> la religión de Al-lah. Pero si desisten, <em>recordad</em> que no se permite hostilidad alguna excepto contra los agresores." WHERE ayatNumber = 194 AND chapterNumber = 2;
UPDATE verses SET es = "La <em>violación de un Mes Sagrado debe ser vengada</em> en el Mes Sagrado; y para <em>todas</em> las cosas sagradas está <em>la ley del </em>talión. Así, a quien se porte mal con vosotros, castigadlo por su mal comportamiento en la medida en que os haya atacado. Y temed a Al-lah y sabed que Al-lah está con quienes Le temen." WHERE ayatNumber = 195 AND chapterNumber = 2;
UPDATE verses SET es = "Y gastad en la causa de Al-lah y no os lancéis a la perdición con vuestras propias manos, y haced el bien; en verdad, Al-lah ama a quienes hacen el bien." WHERE ayatNumber = 196 AND chapterNumber = 2;
UPDATE verses SET es = "Y cumplid el Hall y el ´Umrah en honor a Al-lah; pero si estáis impedidos, entonces <em>haced</em> cualquier ofrenda que os sea fácilmente disponible; y no os rasuréis la cabeza hasta que la ofrenda llegue a su destino, y quien de entre vosotros esté enfermo o tenga un mal en la cabeza, <em>debe realizar</em> una expiación por medio de ayunos, de limosna o con un sacrificio. Pero cuando estéis a salvo, quien se acoja al ´Umrah junto con el Hall, <em>debe realizar</em> cualquier ofrenda que pueda obtener fácilmente. Sin embargo, aquel <em>de vosotros</em> que no pueda encontrar <em>una ofrenda</em> debe ayunar tres días durante la Peregrinación y siete cuando regrese a casa; esto hace diez días completos. Esto es para aquél cuya familia no habita cerca de la Mezquita Sagrada. Y temed a Al-lah y sabed que Al-lah es severo en el castigo." WHERE ayatNumber = 197 AND chapterNumber = 2;
UPDATE verses SET es = "Los meses del <em>Hall</em> son bien conocidos; así pues, quien decida realizar la Peregrinación en esos meses <em>debe recordar</em> que no ha de <em>haber</em> conversaciones groseras, pecados ni discusiones durante la Peregrinación. Y todo el bien que hagáis, Al-lah lo conoce. Y procuraos las provisiones <em>necesarias, </em>aunque, en verdad, la piedad es la mejor provisión. Y temedme sólo a Mí, Oh hombres de conocimiento." WHERE ayatNumber = 198 AND chapterNumber = 2;
UPDATE verses SET es = "No es pecado que pidáis la generosidad de vuestro Señor. Pero cuando volváis en multitud desde ´Arafat, recordad a Al-lah en Mash´ar al-Haram, y acordaos de Él porque os ha guiado cuando antes os contabais entre los extraviados." WHERE ayatNumber = 199 AND chapterNumber = 2;
UPDATE verses SET es = "Luego volved en conjunto de donde la gente viene así, y pedid perdón a Al-lah; en verdad, Dios es el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 200 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando hayáis cumplido los actos de culto que se os han prescrito, celebrad las alabanzas de Al-lah como lo hacéis con las alabanzas de vuestros padres, o aún más. Y entre los hombres hay algunos que dicen: “Señor Nuestro, concédenos las <em>cosas buenas</em> en este mundo”; pero éstos no tendrán parte en el Más Allá." WHERE ayatNumber = 201 AND chapterNumber = 2;
UPDATE verses SET es = "Y entre ellos hay algunos que dicen: “Señor Nuestro, concédenos el bien de este mundo y así mismo el bien en el otro, y presérvanos del tormento del Fuego”." WHERE ayatNumber = 202 AND chapterNumber = 2;
UPDATE verses SET es = "Para ellos habrá una <em>buena</em> parte por lo que han ganado. Y Al-lah es rápido en las cuentas." WHERE ayatNumber = 203 AND chapterNumber = 2;
UPDATE verses SET es = "Y acordaos de Al-lah durante el número de días señalados, pero no hay pecado para quien se apresura <em>a marcharse</em> en dos días; tampoco lo hay para quien se queda atrás. Esto es para quien teme a Dios. Y temed a Al-lah y sabed que seréis reunidos ante Él." WHERE ayatNumber = 204 AND chapterNumber = 2;
UPDATE verses SET es = "Y hay alguno entre los hombres cuya charla sobre esta vida te gustaría, y que pone a Dios por testigo de lo que hay en su corazón, y sin embargo es el más belicoso de los pendencieros." WHERE ayatNumber = 205 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando tiene autoridad, corre por la tierra para crear el desorden en ella y destruir las cosechas y la progenie <em>del hombre; </em>y a Al-lah no le gusta el desorden." WHERE ayatNumber = 206 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando se le dice: “Teme a Al-lah”, el orgullo le incita a pecar <em>de nuevo.</em> Así, el Infierno será su recompensa suficiente; y en verdad es un desagradable lugar de descanso." WHERE ayatNumber = 207 AND chapterNumber = 2;
UPDATE verses SET es = "Y entre los hombres hay quien se vendería a sí mismo para intentar agradar a Al-lah; y Al-lah es Compasivo para <em>Sus</em> siervos." WHERE ayatNumber = 208 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh, creyentes! aceptad plenamente la sumisión y no sigáis los pasos de Satanás; ciertamente, él es vuestro enemigo declarado." WHERE ayatNumber = 209 AND chapterNumber = 2;
UPDATE verses SET es = "Pero si resbaláis después de los Signos claros que os han llegado, entonces sabed que Al-lah es Todopoderoso, Sabio." WHERE ayatNumber = 210 AND chapterNumber = 2;
UPDATE verses SET es = "¿Están acaso esperando que Al-lah venga a ellos en las sombras de las nubes con ángeles, y se decida el asunto? Y a Al-lah es a quien todo vuelve." WHERE ayatNumber = 211 AND chapterNumber = 2;
UPDATE verses SET es = "Pregunta a los Hijos de Israel cuántos Signos claros les otorgamos. Pero quien cambie el don de Al-lah después de haberlo recibido, sepa que, en verdad, Al-lah es Severo en el castigo." WHERE ayatNumber = 212 AND chapterNumber = 2;
UPDATE verses SET es = "Se hace que la vida de este mundo <em>aparezca</em> atractiva para los incrédulos; y se burlan de los que creen. Pero los temerosos <em>de Dios</em> estarán por encima de ellos en el Día de la Resurrección; y Al-lah concede <em>Sus dones</em> a quien le place, sin echar cuentas." WHERE ayatNumber = 213 AND chapterNumber = 2;
UPDATE verses SET es = "La humanidad participaba de la misma suerte moral, por lo que Al-lah suscitó Profetas como portadores de la buena nueva y como anunciadores, e hizo bajar con ellos el Libro que contenía la verdad, para poder juzgar entre los hombres <em>sobre</em> aquello en lo que diferían. <em>Pero ahora han comenzado a diferir en cuanto al Libro; </em>y nadie mostró divergencias respecto a él excepto aquellos a quienes se les entregó, después de que les llegaron Signos claros, sólo por envidia entre unos y otros. Ahora Al-lah, con su mandamiento, ha guiado a los creyentes a la verdad respecto a aquello en que (los descreídos) diferían; y Al-lah guía a quien le place por el camino recto." WHERE ayatNumber = 214 AND chapterNumber = 2;
UPDATE verses SET es = "¿Pensáis que entraréis en el cielo sin antes pasar por la situación de quienes os precedieron? La pobreza y las calamidades les alcanzaron, y fueron violentamente sacudidos hasta el punto que el Mensajero y los que con él creían dijeron: “¿Cuándo <em>vendrá</em> la ayuda de Al-lah? Sí, en verdad, la ayuda de Al-lah está cercana." WHERE ayatNumber = 215 AND chapterNumber = 2;
UPDATE verses SET es = "Te preguntan acerca de qué deben gastar. Diles: “Cualquier riqueza buena y abundante que empleéis debe ser para los padres y los parientes cercanos, para los huérfanos, los necesitados y el viajero. Y cualquier bien que hagáis, Al-lah ciertamente lo conoce bien”." WHERE ayatNumber = 216 AND chapterNumber = 2;
UPDATE verses SET es = "Se os ordena la lucha, aunque os repugne; pues es posible que os disguste algo que os es bueno, y es posible que os guste algo que es malo para vosotros. Al-lah sabe <em>todas las cosas</em> y vosotros no." WHERE ayatNumber = 217 AND chapterNumber = 2;
UPDATE verses SET es = "Te preguntan sobre el combate en el Mes Sagrado. Diles: “Combatir<em>en él </em>es <em>un</em> grave <em>pecado, </em>pero apartar<em>a los hombres</em> del camino de Al-lah, y ser desagradecido con Él <em>y alejar a los hombres de</em> la Mezquita Sagrada, y expulsar a su pueblo de ella, es <em>un pecado</em> más grave a los ojos de Al-lah; y la persecución es peor que la muerte”. Y ellos no dejarán de combatiros hasta que os hayan apartado de vuestra religión, si pudieran. Y aquél de vosotros que abandone su fe y muera como incrédulo, será de aquellos cuyas obras serán vanas en este mundo y en el otro. Son los moradores del Fuego y allí vivirán." WHERE ayatNumber = 218 AND chapterNumber = 2;
UPDATE verses SET es = "Quienes creen, y quienes emigran y trabajan duramente por la causa de Al-lah, son los que pueden esperar la misericordia de Al-lah; y Al-lah es el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 219 AND chapterNumber = 2;
UPDATE verses SET es = "Te preguntan sobre el vino y los juegos de azar. “En ambas cosas hay un gran pecado y también <em>algunas</em> ventajas para los hombres; pero su mal es mayor que sus ventajas”. Y te preguntan sobre lo que deben gastar. Diles “Lo que podáis ahorrar”. Así hace Al-lah sus mandamientos claros para vosotros, a fin de que podáis reflexionar" WHERE ayatNumber = 220 AND chapterNumber = 2;
UPDATE verses SET es = "Sobre este mundo y el futuro. Y te preguntan sobre los huérfanos. Diles: “La defensa de su bienestar es <em>un acto de </em>la máxima bondad. Y si os mezcláis con ellos, son vuestros hermanos. Y Al-lah distingue al que obra mal del que obra bien. Y si Al-lah lo quisiera podría poneros a dura prueba. En verdad, Al-lah es Todopoderoso, Sabio”." WHERE ayatNumber = 221 AND chapterNumber = 2;
UPDATE verses SET es = "Y no os caséis con mujeres idólatras mientras no crean. Incluso una esclava creyente es mejor que una idólatra, aunque ésta os agrade<em>mucho.</em> Y no entreguéis en matrimonio <em>vuestras mujeres creyentes </em>a los idólatras mientras éstos no crean; incluso un esclavo creyente es mejor que un idólatra, aunque éste os agrade <em>mucho.</em> Éstos invitan al Fuego, mientras que Al-lah invita al Cielo y al perdón con Su mandamiento. Y Él clarifica Sus Signos a los hombres, para que recuerden." WHERE ayatNumber = 222 AND chapterNumber = 2;
UPDATE verses SET es = "Y te preguntan sobre la menstruación. Diles: “Es una indisposición, por lo que debéis manteneros alejados de las mujeres durante la menstruación y no cohabitar con ellas hasta que estén limpias. Pero cuando se hayan purificado, acercaos a ellas como Al-lah os ha ordenado. Al-lah ama a los que se vuelven a Él y ama a los que se conservan limpios.”" WHERE ayatNumber = 223 AND chapterNumber = 2;
UPDATE verses SET es = "Vuestras mujeres son un campo labrado para vosotros; así pues, acercaos a vuestro campo cuando y como deseéis, y haceos preceder con <em>algún bien</em>; y temed a Al-lah y sabed que Le encontraréis; y dad la buena nueva a los que obedecen." WHERE ayatNumber = 224 AND chapterNumber = 2;
UPDATE verses SET es = "No hagáis al Al-lah objeto de vuestros juramentos, de modo que os <em>abstengáis</em> por ese motivo de hacer el bien y de actuar santamente, y de establecer la paz entre los hombres. Y Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 225 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah no os pedirá cuentas de aquellos juramentos que son vanos, pero os pedirá cuentas de lo que vuestros corazones hayan adquirido. Y Al-lah es Quien más perdona, el Indulgente." WHERE ayatNumber = 226 AND chapterNumber = 2;
UPDATE verses SET es = "Para quienes hacen voto de <em>abstinencia</em> de sus mujeres, el período <em>máximo</em> de espera es de cuatro meses; pero si se retractan del voto, sepan que, en verdad Al-lah es el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 227 AND chapterNumber = 2;
UPDATE verses SET es = "Y si se deciden por el divorcio, entonces, sepan que, en verdad, Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 228 AND chapterNumber = 2;
UPDATE verses SET es = "Y las mujeres divorciadas esperarán por su parte tres menstruaciones; y no les es lícito que oculten lo que Al-lah ha creado en su matriz, si creen en Al-lah y en el Último Día; y sus esposos tienen preferencia para recogerlas durante ese tiempo, siempre y cuando deseen la reconciliación. Y ellas (las mujeres) tienen derechos similares a los que ellos tienen (los hombres) en justicia; aunque los hombres poseen <em>un rango superior</em> a ellas. Y Al-lah es Poderoso, Sabio." WHERE ayatNumber = 229 AND chapterNumber = 2;
UPDATE verses SET es = "Ese divorcio debe <em>pronunciarse</em> dos veces; después conservad<em>las</em> de manera apropiada o despedid<em>las</em> con amabilidad. Y no es lícito que toméis nada de lo que les habéis dado (a vuestras esposas) salvo que ambos temáis que no puedan respetar los límites <em>prescritos</em> por Al-lah. Y si teméis que no puedan observar los límites <em>prescritos</em> por Al-lah, entonces, no habrá pecado para ninguno de ellos en lo que ella entregue para conseguir su libertad. Éstos son los límites <em>prescritos</em> por Al-lah, por tanto no los sobrepaséis; y quienes sobrepasen los límites <em>prescritos</em> por Al-lah, sepan que son malvados." WHERE ayatNumber = 230 AND chapterNumber = 2;
UPDATE verses SET es = "Y si la divorcia por <em>tercera vez,</em> ya no le será lícita después, mientras no se haya casado con otro marido; pero si él <em>también</em> se divorcia de ella, entonces no habrá pecado en que vuelvan el uno al otro, a condición de que estén seguros de poder respetar los límites <em>prescritos</em> por Al-lah, que Él aclara para las personas que tienen conocimiento." WHERE ayatNumber = 231 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando os divorciéis de vuestras esposas y ellas se acerquen al término del período <em>fijado,</em> entonces conservadlas de manera apropiada; o despedidlas de manera adecuada; pero no las retengáis por la fuerza, convirtiéndoos así en transgresores. Y quien haga eso, ciertamente, se hace daño a sí mismo. Y no os burléis de los mandamientos de Al-lah y recordad el favor de Al-lah para con vosotros y el Libro y la Sabiduría que Él hizo descender para vosotros, con los cuales Él os amonesta. Y temed a Al-lah y sabed que Al-lah conoce bien todas las cosas." WHERE ayatNumber = 232 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando os divorciéis de las mujeres y lleguen al término de su período, no les impidáis que contraigan matrimonio con sus maridos, si llegan a un acuerdo mutuo y honrado. Ésta es una amonestación para aquél de entre vosotros que crea en Al-lah y en el Último Día. Es lo más puro y más limpio para vosotros; y Al-lah sabe pero vosotros ignoráis." WHERE ayatNumber = 233 AND chapterNumber = 2;
UPDATE verses SET es = "Y las madres darán el pecho a sus hijos durante dos años completos; <em>esto es</em> para quienes desean completar la lactancia. Y el hombre al que pertenece el hijo será el responsable del alimento y vestido de ellas (de las madres), según la costumbre. Pero no se impone a nadie una carga superior a su capacidad. La madre no hará sufrir al padre a causa de su hijo, ni aquél a quien pertenece el hijo hará sufrir a <em>la madre</em> a causa de su hijo, y lo mismo corresponde al heredero. Si ambos deciden destetar <em>al hijo</em>, por acuerdo y consentimiento mutuos, no cometen falta. Y si deseáis contratar a una nodriza para vuestros hijos, no cometéis falta, a condición de que le paguéis lo estipulado, honradamente. Y temed a Al-lah y sabed que Al-lah ve lo que hacéis." WHERE ayatNumber = 234 AND chapterNumber = 2;
UPDATE verses SET es = "Y aquellos de vosotros que muráis dejando esposas, éstas (las viudas) esperarán por su parte cuatro meses y diez <em>días</em>. Y cuando hayan alcanzado el final de este período, no habrá pecado para vosotros en nada de lo que hagan respecto a sus personas según lo que es justo. Y Al-lah conoce lo que hacéis." WHERE ayatNumber = 235 AND chapterNumber = 2;
UPDATE verses SET es = "Y no cometeréis falta si hacéis una insinuación a <em>estas</em> mujeres sobre una propuesta de matrimonio, o si mantenéis <em>el deseo</em> oculto en vuestras mentes. Al-lah sabe que pensaréis en ellas <em>a este respecto</em>. Y no realicéis ningún acuerdo con ellas en secreto, sino que debéis decir la palabra adecuada. Y no decidáis sobre los lazos del matrimonio mientras no haya llegado a su fin el período prescrito. Y sabed que Al-lah conoce lo que hay en vuestras mentes; así pues, cuidaos de ello. Y sabed que Al-lah es el Sumo Indulgente, el Paciente." WHERE ayatNumber = 236 AND chapterNumber = 2;
UPDATE verses SET es = "No cometeréis pecado si os divorciáis de mujeres a las que no habéis tocado, ni les habéis fijado la dote. Pero proveed su sustento: el rico según sus medios y el pobre según sus medios; una provisión de manera digna, una obligación para el virtuoso." WHERE ayatNumber = 237 AND chapterNumber = 2;
UPDATE verses SET es = "Y si os divorciáis de ellas antes de haberlas tocado, pero habiéndoles fijado una dote, entonces <em>debéis entregar</em> la mitad de lo que habéis fijado, a menos que ellas renuncien, o que renuncie quien tenga en sus manos los lazos del matrimonio. Y la renuncia por vuestra parte está más cercana a la santidad. Y no olvidéis haceros mutuamente el bien. En verdad, Al-lah ve lo que hacéis." WHERE ayatNumber = 238 AND chapterNumber = 2;
UPDATE verses SET es = "Cumplid las Oraciones, y la Oración intermedia, y permaneced ante Al-lah con sumisión." WHERE ayatNumber = 239 AND chapterNumber = 2;
UPDATE verses SET es = "Si os encontráis <em>en estado de</em> temor, entonces decid vuestra Oración a pie o montados, pero cuando estéis a salvo, acordaos de Al-lah, pues Él os ha enseñado lo que vosotros no sabíais." WHERE ayatNumber = 240 AND chapterNumber = 2;
UPDATE verses SET es = "Y aquellos de vosotros que mueran dejando esposas, legarán a sus mujeres provisiones para un año <em>sin que </em>sean expulsadas. Pero si ellas <em>mismas</em> deciden irse, no habrá culpa en vosotros respecto a cualquier cosa adecuada que hagan por sí mismas. Y Al-lah es Poderoso, Sabio." WHERE ayatNumber = 241 AND chapterNumber = 2;
UPDATE verses SET es = "Y para las mujeres divorciadas debe haber <em>también</em> una provisión según lo que es justo; es una obligación para los temerosos de Dios." WHERE ayatNumber = 242 AND chapterNumber = 2;
UPDATE verses SET es = "Así Al-lah os aclara Sus mandamientos, para que entendáis." WHERE ayatNumber = 243 AND chapterNumber = 2;
UPDATE verses SET es = "¿No has oído hablar de aquellos que salieron de sus casas, a millares, por temor a la muerte? Y Al-lah les dijo: “¡Morid!”, y después les devolvió la vida. En verdad, Al-lah es Generoso en grado sumo con los hombres, pero la mayoría de los hombres no son agradecidos." WHERE ayatNumber = 244 AND chapterNumber = 2;
UPDATE verses SET es = "Y combatid por la causa de Al-lah y sabed que Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 245 AND chapterNumber = 2;
UPDATE verses SET es = "¿Quién es el que hará un préstamo magnífico a Al-lah para que Él se lo multiplique muchas veces? Y Al-lah recibe y aumenta, y a Él seréis devueltos." WHERE ayatNumber = 246 AND chapterNumber = 2;
UPDATE verses SET es = "¿No has oído hablar de los jefes de los hijos de Israel, después de Moisés, cuando dijeron a un Profeta suyo: “Nómbranos un rey para que podamos combatir por la causa de Al-lah”? Él dijo: “¿No es bastante probable que no luchéis si se os ordena la lucha?”. Ellos dijeron: “¿Qué motivos tendríamos para dejar de luchar por la causa de Al-lah, siendo así que hemos sido expulsados de nuestras casas y separados de nuestros hijos?”. Pero cuando se les ordenó combatir, volvieron la espalda, excepto un pequeño número. Y Al-lah conoce bien a los pecadores." WHERE ayatNumber = 247 AND chapterNumber = 2;
UPDATE verses SET es = "Y su Profeta les dijo: “Al-lah os ha nombrado a Talut como rey”. Dijeron: ¿Cómo puede ser soberano nuestro si nosotros tenemos más derecho a la soberanía que él, y no le han dado <em>ni siquiera</em> riquezas en abundancia?”. Él dijo: “En verdad, Al-lah lo ha elegido sobre vosotros y lo ha hecho muy superior en cuanto conocimientos y fuerza”. Y Al-lah entrega la soberanía a quien le place, y Al-lah es Generoso, Omnisciente." WHERE ayatNumber = 248 AND chapterNumber = 2;
UPDATE verses SET es = "Y su Profeta les dijo: “La señal de su soberanía es que se os dará un corazón en el que reinará el arca de la alianza de vuestro Señor y un legado <em>de bien</em> dejado por la familia de Moisés y la familia de Aarón: los ángeles lo traerán. En verdad," WHERE ayatNumber = 249 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando Talut partió con las tropas dijo: “En verdad, Al-lah os probará con un río. Así, quien beba de él no será de los míos; y quien no lo pruebe será ciertamente de los míos, excepto quien tome un poco de agua con la mano”. Pero bebieron de él, excepto unos pocos. Y cuando lo cruzaron –él y quienes creían junto con él- dijeron: “Hoy no tenemos uerza contra Yalut y sus fuerzas. Pero los que sabían con certeza que <em>un día</em> encontrarían a Al-lah, dijeron: ¡Cuántas veces un pequeño grupo ha triunfado sobre un gran ejército bajo la orden de Dios! Y Al-lah está con los que perseveran”." WHERE ayatNumber = 250 AND chapterNumber = 2;
UPDATE verses SET es = "Y cuando avanzaron para <em>salir al encuentro de</em> Yalut y sus tropas, dijeron: “¡Oh, Señor nuestro, danos perseverancia, haz firmes nuestros pasos y ayúdanos contra los incrédulos!”." WHERE ayatNumber = 251 AND chapterNumber = 2;
UPDATE verses SET es = "Con el permiso de Dios los pusieron en fuga; y David mató a Yalut, y Al-lah le dio soberanía y sabiduría, enseñándole lo que Él quiso. Y de no haber sido porque Al-lah rechazó a los hombres, a algunos de ellos por medio de otros, la tierra se hubiera hundido en el desorden. Pero Al-lah es Magnánimo con <em>todos</em> los pueblos." WHERE ayatNumber = 252 AND chapterNumber = 2;
UPDATE verses SET es = "Éstos son los Signos de Al-lah; te los recitamos con verdad. Ciertamente, tú eres <em>uno</em> de los Mensajeros." WHERE ayatNumber = 253 AND chapterNumber = 2;
UPDATE verses SET es = "Éstos son los Mensajeros. Hemos hecho que algunos de ellos superen a otros a los cuales Al-lah habló; y <em>Al-lah</em> hizo que uno de ellos superara a los demás. Y dimos a Jesús, hijo de María, pruebas laras y lo fortalecimos con el Espíritu de santidad. Y si Al-lah <em>lo</em> hubiese querido, los <em>que vinieron</em> después de ellos no hubiesen luchado entre sí después de los Signos claros que les llegaron; pero discreparon. Entre ellos hubo quienes creyeron y otros que no creyeron, y si Al-lah <em>lo</em> hubiese querido, no habrían luchado entre sí; pero Al-lah hace lo que quiere." WHERE ayatNumber = 254 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! gastad <em>en caridad</em> de lo que os hemos concedido antes de que venga el día en el que no habrá compra ni venta, i amistad, ni intercesión; y son los incrédulos los que se perjudican <em>a ellos mismos.</em>" WHERE ayatNumber = 255 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah: no hay mas Dios que Él, el Viviente, el que Subsiste por Sí mismo y sostiene todo. No se apoderan de Él ni el sopor ni el sueño. A Él pertenece cuanto hay en los cielos y cuanto hay en la tierra. ¿Quién es capaz de interceder ante Él si no es con Su permiso? Él conoce lo que está delante y detrás de ellos; y ellos no abarcan nada de Su ciencia, salvo lo que Él quiere. Su Trono se extiende sobre los cielos y la tierra; y su cuidado no le supone preocupación alguna; y Él es el Altísimo, el Grande." WHERE ayatNumber = 256 AND chapterNumber = 2;
UPDATE verses SET es = "No ha de existir coacción en la religión. Ciertamente, lo recto ha quedado separado de lo erróneo; así, quien se niegue a ser conducido por los pecadores, y crea en Al-lah, ha agarrado con seguridad una empuñadura fuerte, que no tiene grietas. Y Al-lah es quien todo lo oye, Omnisciente." WHERE ayatNumber = 257 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah es el amigo de los que creen: los saca de <em>toda clase de</em> tinieblas a la luz. Y respecto a los que no reen, sus amigos son los pecadores que los llevan desde la luz a <em>toda clase de</em> tinieblas. Éstos son los moradores del Fuego; allí habitarán." WHERE ayatNumber = 258 AND chapterNumber = 2;
UPDATE verses SET es = "¿No has oído hablar de aquel que disputó con Abraham respecto a su Señor, porque Al-lah le había dado el reino? Cuando Abraham dijo: “Mi eñor es quien da la vida y causa la muerte”, él dijo: “Yo <em>también</em> doy la vida y causo la muerte”. Abraham dijo: “Bien, Al-lah trae el sol desde Oriente, tráelo tú desde Occidente”. El infiel quedó confundido. Y Al-lah no guía a la gente injusta." WHERE ayatNumber = 259 AND chapterNumber = 2;
UPDATE verses SET es = "O como aquel que pasó junto a una ciudad derruida y exclamó: “¿Cuándo le devolverá la vida Al-lah después de su destrucción?”. Entonces Al-lah le hizo morir durante cien años, luego le resucitó y dijo: “¿Cuánto tiempo has permanecido <em>en este estado</em>?”. Él respondió: “He permanecido un día o parte de un día”. Él dijo: “No, has permanecido <em>en este estado</em> durante cien años. Mira tu comida y u bebida; no se han estropeado. Y mira tu asno. Y <em>hemos hecho esto</em> para convertirte en un Signo para los hombres. Y mira los huesos, como los hemos dispuesto y después cubierto de carne”. Y cuando <em>todo</em> esto le quedó claro, dijo: “Sé que Al-lah tiene poder para hacer todo lo que quiere”." WHERE ayatNumber = 260 AND chapterNumber = 2;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando Abraham dijo: “Mi Señor; muéstrame cómo le devuelves la vida a los muertos”. Él dijo: “¿Es que no has creído?” Él dijo: “Sí, pero <em>pregunto esto</em> para que mi corazón se tranquilice”. Respondió: “Toma cuatro pájaros y haz que se vinculen a ti. A continuación pon a cada uno de ellos en un monte; llámalos después y verás cómo acuden a ti apresuradamente. Y sabe que Al-lah es Poderoso, Sabio.”" WHERE ayatNumber = 261 AND chapterNumber = 2;
UPDATE verses SET es = "Los que emplean sus bienes en la causa de Al-lah son semejantes a un grano <em>de trigo</em> que da siete espigas, y en cada espiga hay cien granos. Y Al-lah <em>lo </em>multiplica aún más para quien Le place; y Al-lah es Magnánimo, Omnisciente." WHERE ayatNumber = 262 AND chapterNumber = 2;
UPDATE verses SET es = "Quienes emplean sus bienes en la causa de Al-lah, y a continuación no hacen que lo empleado vaya seguido de burlas o agravios, son los que tendrán la recompensa con su Señor, y no tendrán temor ni se afligirán." WHERE ayatNumber = 263 AND chapterNumber = 2;
UPDATE verses SET es = "Una palabra amable y el perdón son mejores que una limosna seguida de agravios. Y Al-lah es Suficiente por Sí mismo, Indulgente." WHERE ayatNumber = 264 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! no hagáis vanas vuestras limosnas con reproches y agravios, como aquél que emplea su riqueza para ser visto por los hombres, y no cree en Al-lah ni en el Último Día. Su caso es similar a una roca <em>lisa</em>, cubierta con tierra, sobre la que cae el aguacero, dejándola desnuda, llana y dura. Éstos no conseguirán mantener nada de lo que han ganado. Y Al-lah no guía a la gente incrédula." WHERE ayatNumber = 265 AND chapterNumber = 2;
UPDATE verses SET es = "Y los que emplean sus riquezas para buscar el placer de Al-lah y fortalecer sus almas son semejantes a un jardín sobre un terreno elevado. Basta que la lluvia abundante caiga sobre él para que se upliquen sus frutos. Y si no cae sobre él un fuerte aguacero, <em>le basta</em> con una lluvia ligera. Y Al-lah ve lo que hacéis." WHERE ayatNumber = 266 AND chapterNumber = 2;
UPDATE verses SET es = "¿A quién de vosotros le gustaría tener un jardín de palmeras y vid, con arroyos corriendo por él, y con toda clase de frutos cuando le ha llegado la vejez y tiene una descendencia débil e indefensa, y que e pronto una terrible tormenta cayera sobre él y lo quemara <em>todo</em>? Así os hace Al-lah patentes Sus Signos, para que podáis reflexionar." WHERE ayatNumber = 267 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! gastad de las cosas buenas que habéis ganado y de lo que hacemos brotar de la tierra para vosotros; y no busquéis lo malo para entregarlo, cuando no lo cogeríais para vosotros mismos salvo bajando la mirada por vergüenza. Y sabed que Al-lah es Suficiente en Sí mismo, Merecedor de toda alabanza." WHERE ayatNumber = 268 AND chapterNumber = 2;
UPDATE verses SET es = "Satanás os amenaza con la pobreza y os ordena lo que es inmundo, mientras que Al-lah os promete el perdón de Sí mismo y la magnanimidad. Y Al-lah es Bondadoso, Omnisciente." WHERE ayatNumber = 269 AND chapterNumber = 2;
UPDATE verses SET es = "Él concede la sabiduría a quien le place. Y a quien se le da la sabiduría se le concede un bien excelente; y nadie se acuerda excepto los dotados de conocimiento." WHERE ayatNumber = 270 AND chapterNumber = 2;
UPDATE verses SET es = "Y sea cual fuere lo que gastéis y el voto que formuléis, Al-lah ciertamente lo conoce; y para los malvados no habrá defensores." WHERE ayatNumber = 271 AND chapterNumber = 2;
UPDATE verses SET es = "Si dais limosnas en público, está bien y <em>es bueno</em>; pero si las ocultáis y las dais al pobre, mejor para vosotros; y Él os uitará <em>muchos</em> de vuestros pecados. Y Al-lah sabe <em>perfectamente</em> lo que hacéis." WHERE ayatNumber = 272 AND chapterNumber = 2;
UPDATE verses SET es = "No es responsabilidad tuya hacer que sigan el camino recto; pero Al-lah guía a quien Le place. Y cualquier riqueza que empleéis es para vosotros mismos; mientras gastéis, únicamente, para conseguir la gracia de Al-lah. Y cualquier riqueza que empleéis se os devolverá íntegra y no sufriréis perjuicio alguno." WHERE ayatNumber = 273 AND chapterNumber = 2;
UPDATE verses SET es = "<em> Estas limosnas son</em> para los pobres que se encuentran detenidos en el camino de Al-lah y que no pueden moverse por la tierra. l hombre ignorante piensa que carecen de necesidades porque <em>se</em> abstienen <em>de pedir.</em> Tú los conocerás por su aspecto; no piden a los hombres de modo inoportuno. Y cualquier riqueza que empleéis, sabed que Al-lah la conoce perfectamente." WHERE ayatNumber = 274 AND chapterNumber = 2;
UPDATE verses SET es = "Los que emplean caritativamente sus bienes de noche y de día, secreta y abiertamente, tienen su recompensa junto a su Señor; no caerá el temor sobre ellos, ni serán afligidos." WHERE ayatNumber = 275 AND chapterNumber = 2;
UPDATE verses SET es = "Los que consumen del interés no se levantarán, salvo como se levanta el que ha sido derribado por Satanás con la locura. Eso es porque dicen: “El comercio es como la usura”; cuando Al-lah ha hecho lícito el comercio e ilícito el interés. Así pues, a quien le llega la advertencia de su Señor y desiste, será suyo <em>lo que recibió</em> en el pasado; y su caso está en manos de Al-lah. Pero los que vuelvan a ello, serán los moradores del Fuego; allí habitarán." WHERE ayatNumber = 276 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah elimina el interés y hace que aumente la caridad. Y Al-lah no ama a quien es incrédulo y pecador declarado." WHERE ayatNumber = 277 AND chapterNumber = 2;
UPDATE verses SET es = "En verdad, los que creen y hacen buenas obras, cumplen la Oración y pagan el <em>Zakat</em>, tendrán su recompensa de su Señor y no les sobrecogerá ningún temor, ni serán afligidos." WHERE ayatNumber = 278 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! temed a Al-lah y abandonad lo que os quede de usura, si es que creéis." WHERE ayatNumber = 279 AND chapterNumber = 2;
UPDATE verses SET es = "Pero si no lo hacéis, entonces esperad la guerra de Al-lah y Su ensajero; pero si os arrepentís, tendréis vuestras sumas originales; <em>así</em> no perjudicaréis ni seréis perjudicados." WHERE ayatNumber = 280 AND chapterNumber = 2;
UPDATE verses SET es = "Y si cualquier deudor se encuentra en dificultades, <em>concededle</em> un plazo hasta que vengan tiempos mejores. Y si se lo perdonáis como limosna será mejor para vosotros, ¡si lo supierais!" WHERE ayatNumber = 281 AND chapterNumber = 2;
UPDATE verses SET es = "Y temed el día en el que regresaréis a Al-lah; entonces se pagará a cada alma la totalidad de lo que se ha ganado; y no se les perjudicará." WHERE ayatNumber = 282 AND chapterNumber = 2;
UPDATE verses SET es = "¡Oh creyentes! cuando os otorguéis un préstamo entre vosotros por un período fijo, ponedlo por escrito. Y haced que un escribano lo transcriba fielmente en vuestra presencia; y ningún escribano deberá negarse a escribir, porque Al-lah le ha enseñado; por tanto, hacedle escribir y que dicte el que incurre en responsabilidad; y éste debe ser temeroso de Al-lah, su Señor, y no disminuir nada. Pero si la persona que incurre en responsabilidad es de pocos conocimientos, o es débil o incapaz de dictar, haced que dicte con justicia alguien que pueda defender sus intereses. Y llamad a dos testigos de entre vuestros hombres; y si no hay dos hombres <em>disponibles,</em> entonces a un hombre y dos mujeres que os agraden como testigos, de manera que si una de las mujeres yerra <em>en la memoria</em>, la otra pueda hacerla recordar. Y los testigos no deben negarse cuando son llamados. Y no os sintáis molestos por tener que escribirlo, sea pequeño o grande, junto con la fecha fijada para <em>la devolución.</em> Esto es más justo a los ojos de Al-lah y hace que el testimonio sea más seguro y os evite, probablemente, las dudas; por tanto, <em>no dejéis de escribirlo,</em> salvo que se trate de mercancías que entregáis o recibís en propia mano, en cuyo caso no pecáis si no lo escribís. Y tened testigos cuando ejerzáis la venta entre vosotros; y no permitid que se perjudique al escribano o testigo. Pues si lo hacéis, ciertamente incurriréis en desobediencia por vuestra parte. Y temed a Al-lah. Y Al-lah os concede el conocimiento y Al-lah conoce perfectamente todas las cosas." WHERE ayatNumber = 283 AND chapterNumber = 2;
UPDATE verses SET es = "Y si estáis de viaje y no encontráis escribano, entonces tomad una prenda como posesión. Y si uno de vosotros confía algo a otro, que el que lo haya recibido devuelva su depósito y que tema a Al-lah, su Señor. Y no ocultéis el testimonio; y quien lo oculte, sepa que su corazón está ciertamente lleno de pecado. Y Al-lah sabe muy bien lo que hacéis." WHERE ayatNumber = 284 AND chapterNumber = 2;
UPDATE verses SET es = "A Al-lah pertenece cuanto hay en los cielos y cuanto hay en la tierra; y tanto si reveláis lo que hay en vuestras mentes como si lo mantenéis oculto. Al-lah os pedirá cuentas por ello; después perdonará a quien Le plazca y castigará a quien Le plazca; y Al-lah tiene poder para hacer todo lo que desea." WHERE ayatNumber = 285 AND chapterNumber = 2;
UPDATE verses SET es = "Este Mensajero <em>Nuestro</em> cree en lo que ha sido revelado por su Señor, y <em>así hacen</em> los creyentes; todos <em>ellos</em> reen en Al-lah, y en Sus ángeles y en Sus Libros y en Sus Mensajeros, <em>diciendo: </em>“No hacemos distinción alguna entre ninguno de Sus Mensajeros; y dicen: “Oímos y obedecemos. <em>Imploramos</em> Tu perdón, Oh Señor Nuestro, y hacia Ti es el regreso”." WHERE ayatNumber = 286 AND chapterNumber = 2;
UPDATE verses SET es = "Al-lah no impone cargas a ningún alma más allá de su capacidad. Tendrá <em>la recompensa</em> que gane y recibirá <em>el castigo</em> que merezca. ¡Señor Nuestro!, no nos castigues si olvidamos o caemos en el error; ¡Señor Nuestro!, no nos impongas un peso como el que impusiste a quienes nos precedieron. ¡Señor Nuestro!, no nos cargues on lo que no tengamos fuerza para llevar; y borra nuestros <em>pecados</em> y concédenos el perdón y ten misericordia de nosotros; Tú eres nuestro Dueño; ayúdanos, pues, contra la gente incrédula." WHERE ayatNumber = 287 AND chapterNumber = 2;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 3;
UPDATE verses SET es = "Alif Lam Mim." WHERE ayatNumber = 2 AND chapterNumber = 3;
UPDATE verses SET es = "Al-lah es Aquél fuera del cual no hay otro Dios, el Viviente, el que Subsiste por Sí mismo y todo lo sostiene." WHERE ayatNumber = 3 AND chapterNumber = 3;
UPDATE verses SET es = "Él ha hecho descender sobre ti el Libro que contiene la verdad y confirma lo que le precede; y Él hizo descender la Torah y el Evangelio antes de <em>esto,</em> como guía del pueblo; y Él hizo descender la verdad incontrovertible." WHERE ayatNumber = 4 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, aquellos que no crean en los Signos de Al-lah tendrán un severo castigo. Y Al-lah es Poderoso, poseedor del poder de retribución." WHERE ayatNumber = 5 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, no hay nada en la tierra ni en el cielo oculto para Al-lah." WHERE ayatNumber = 6 AND chapterNumber = 3;
UPDATE verses SET es = "Él es quien os moldea en los senos maternos como desea; no hay otro Dios sino Él, el Poderoso, el Sabio." WHERE ayatNumber = 7 AND chapterNumber = 3;
UPDATE verses SET es = "Él es Quien ha hecho que descienda a ti el Libro; en él hay versículos decisivos en su significado –ellos son la esencia del Libro- y hay otros que permiten diferentes interpretaciones. Pero aquellos en cuyos corazones anida la maldad siguen los que permiten diversas nterpretaciones, buscando la discordia y la interpretación<em>errónea</em> de los mismos. Y nadie conoce su interpretación <em>correcta </em>sino Al-lah y quienes están firmemente basados en el conocimiento; dicen: “Creemos en él; todo él procede de nuestro Señor”. –Y ninguno reflexiona excepto los dotados de entendimiento-." WHERE ayatNumber = 8 AND chapterNumber = 3;
UPDATE verses SET es = "“Señor nuestro, no permitas que nuestros corazones se perviertan después de que nos has guiado; y concédenos Tu misericordia; en verdad, sólo Tú eres el Donador por excelencia." WHERE ayatNumber = 9 AND chapterNumber = 3;
UPDATE verses SET es = "“Señor nuestro, reunirás ciertamente a la humanidad en el Día sobre el cual no hay duda alguna; en verdad, Al-lah no incumple <em>Sus</em> promesas.”" WHERE ayatNumber = 10 AND chapterNumber = 3;
UPDATE verses SET es = "En cuanto a los incrédulos, ni sus posesiones ni sus hijos les servirán frente a Al-lah; y son ellos el combustible del Fuego." WHERE ayatNumber = 11 AND chapterNumber = 3;
UPDATE verses SET es = "<em> Con ellos ocurre</em> como con las gentes del Faraón y de quienes les precedieron; rechazaron Nuestros Signos; y, así, Al-lah los castigó por sus pecados, y Al-lah es severo en el castigo." WHERE ayatNumber = 12 AND chapterNumber = 3;
UPDATE verses SET es = "Di a los que no creen: “Seréis vencidos y reunidos en el infierno; y es un mal lugar de reposo”." WHERE ayatNumber = 13 AND chapterNumber = 3;
UPDATE verses SET es = "Ciertamente hubo un Signo para vosotros en los dos ejércitos que se nfrentaron: uno que luchaba por la causa de Al-lah y el <em>otro formado por</em> incrédulos, quienes les vieron con sus propios ojos en número doble del suyo. Así Al-lah fortalece con Su ayuda a quien Le place. Ahí hay en verdad una lección para los clarividentes." WHERE ayatNumber = 14 AND chapterNumber = 3;
UPDATE verses SET es = "Se ha hecho hermoso para los hombres el amor por las cosas deseadas: mujeres e hijos, montones atesorados de oro y plata, caballos que pastan, rebaños y cultivos. Ésas son las provisiones para esta vida; pero es junto a Al-lah donde se encuentra una morada excelente." WHERE ayatNumber = 15 AND chapterNumber = 3;
UPDATE verses SET es = "Diles: “¿Puedo informaros de algo mejor que eso?” Para los que temen a Dios hay jardines junto a su Señor por los cuales corren ríos; allí morarán; también <em>habrán</em> cónyuges, purificados por Al-lah, y el agrado de Al-lah. Y Al-lah tiene siempre presentes a <em>Sus</em> siervos." WHERE ayatNumber = 16 AND chapterNumber = 3;
UPDATE verses SET es = "Los que dicen: “Señor nuestro, ciertamente creemos; perdónanos, pues, nuestros pecados y sálvanos del castigo del Fuego”;" WHERE ayatNumber = 17 AND chapterNumber = 3;
UPDATE verses SET es = "Los constantes, los sinceros, los humildes y los que gastan en la causa de Dios, y los que piden perdón en la última parte de la noche." WHERE ayatNumber = 18 AND chapterNumber = 3;
UPDATE verses SET es = "Al-lah es testigo de que no hay dios sino Él, –y <em>también lo son</em> los ángeles y quienes poseen sabiduría- Mantenedor de la justicia; no hay más Dios que Él, el Poderoso, el Sabio." WHERE ayatNumber = 19 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, ante Al-lah la <em>verdadera</em> religión es el Islam (sumisión completa). Y aquellos a quienes se entregó el Libro no discreparon sino después de llegarles el conocimiento, por pura envidia entre sí. Y quien niegue los Signos de Al-lah, sepa que en verdad, Al-lah es rápido en rendir cuentas." WHERE ayatNumber = 20 AND chapterNumber = 3;
UPDATE verses SET es = "Mas si disputan contigo, diles: “Me he sometido a Al-lah, como <em>también</em> los que me siguen”. Y di a quienes se les ha entregado el Libro y a los ignorantes: “¿Os habéis sometido?”. Si se someten, serán ciertamente guiados, pero si vuelven sobre sus pasos, debes saber que tu única obligación es la de transmitir el mensaje. Y Al-lah está siempre atento a <em>Sus</em> siervos." WHERE ayatNumber = 21 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, a quienes niegan los Signos de Al-lah y traman matar injustamente a los Profetas y procuran matar a los hombres que predican la justicia: anúnciales un doloroso castigo." WHERE ayatNumber = 22 AND chapterNumber = 3;
UPDATE verses SET es = "Son aquellos cuyas acciones serán nulas en este mundo y en el futuro, y no tendrán defensores." WHERE ayatNumber = 23 AND chapterNumber = 3;
UPDATE verses SET es = "¿No conoces a quienes se les ha dado <em>su</em> parte del Libro? Son llamados al Libro de Al-lah para que el Libro juzgue entre ellos, pero una parte de ellos se aparta con aversión." WHERE ayatNumber = 24 AND chapterNumber = 3;
UPDATE verses SET es = "Eso es porque dicen: “El Fuego sólo nos tocará durante un número reducido de días.” Y lo que solían falsificar les ha engañado respecto a su religión." WHERE ayatNumber = 25 AND chapterNumber = 3;
UPDATE verses SET es = "¿Cómo <em>les irá</em> cuando los reunamos en el Día acerca del cual no hay duda alguna; y cuando se pague a cada alma todo lo que ha ganado, y no sean perjudicados?" WHERE ayatNumber = 26 AND chapterNumber = 3;
UPDATE verses SET es = "Di: “¡Oh Al-lah, Señor de la soberanía!”, Tú das la autoridad a quien Te place y retiras el poder de quien quieres; Tú ensalzas a quien deseas y humillas a quien Te place. En Tu mano está todo el bien. Tú, en verdad, tienes el poder de hacer todas las cosas." WHERE ayatNumber = 27 AND chapterNumber = 3;
UPDATE verses SET es = "“Tú haces que la noche se convierta en día y que el día se convierta en noche. Y Tú haces salir lo vivo de lo muerto y lo muerto de lo vivo. Y Tú das a quien Te agrada, sin medida.”" WHERE ayatNumber = 28 AND chapterNumber = 3;
UPDATE verses SET es = "Que los creyentes no tomen por amigos a los incrédulos, prefiriéndolos a los creyentes –y quien haga eso no tiene relación alguna con Al-lah- salvo que os protejáis prudentemente de ellos. Y Al-lah os advierte que no debéis tomaros libertades contra Él; y a Al-lah será el retorno." WHERE ayatNumber = 29 AND chapterNumber = 3;
UPDATE verses SET es = "Diles. “Tanto si ocultáis lo que hay en vuestros corazones como si lo reveláis, Al-lah lo conoce; y Él sabe todo lo que hay en los cielos y en la tierra. Y Al-lah tiene poder para hacer todas las cosas”." WHERE ayatNumber = 30 AND chapterNumber = 3;
UPDATE verses SET es = "No olvidéis el Día en el que cada alma se encontrará frente a <em>todo</em> el bien que ha hecho y <em>todo</em> el mal que ha ometido. Desearía que hubiera una gran distancia entre ella y ese <em>mal</em>. Y Al-lah os previene de Su castigo. Y Al-lah es el Sumo Compasivo con <em>Sus</em> siervos" WHERE ayatNumber = 31 AND chapterNumber = 3;
UPDATE verses SET es = "Diles: “Si amáis a Al-lah, seguidme; <em>entonces</em> Al-lah os amará y os perdonará vuestros pecados. Y Al-lah es el Sumo Indulgente, el Misericordioso.”" WHERE ayatNumber = 32 AND chapterNumber = 3;
UPDATE verses SET es = "Diles: “Obedeced a Al-lah y al Mensajero;” pero si vuelven la espalda , <em>recordad que</em> Al-lah no ama a los incrédulos." WHERE ayatNumber = 33 AND chapterNumber = 3;
UPDATE verses SET es = "Al-lah escogió a Adán, a Noé, a la familia de Abraham y a la familia de ‘Imrán por encima de todos los pueblos." WHERE ayatNumber = 34 AND chapterNumber = 3;
UPDATE verses SET es = "Una raza mutuamente relacionada. Y Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 35 AND chapterNumber = 3;
UPDATE verses SET es = "Y <em>acordaos de</em> cuando la mujer de ´Imrán dijo: “Mi Señor, Te he prometido lo que hay en mi vientre para que se consagre a Tu servicio. Acéptamelo, pues. En verdad, sólo Tú eres el que todo lo oye, el Omnisciente." WHERE ayatNumber = 36 AND chapterNumber = 3;
UPDATE verses SET es = "Pero cuando hubo dado a luz, dijo: “Mi Señor, he dado a luz una niña” –aunque Al-lah sabía mejor <em>que ella </em>lo que había parido que el varón <em>en que ella pensaba</em> no era como la hembra <em>que dio a luz-</em> “y le he puesto por nombre María, y pongo a ella y a su progenie bajo Tu protección contra Satanás, el rechazado.”" WHERE ayatNumber = 37 AND chapterNumber = 3;
UPDATE verses SET es = "Y su Señor la aceptó con benevolencia y la hizo crecer con hermoso crecimiento, y convirtió a Zacarías en su tutor. Cada vez que Zacarías la visitaba en su habitación, la encontraba con provisiones. Dijo: “¡Oh, María! ¿De dónde viene todo esto?”. Ella respondió: “Procede de Al-lah.” En verdad, Al-lah da sin medida a quien Le place." WHERE ayatNumber = 38 AND chapterNumber = 3;
UPDATE verses SET es = "Allí y <em>entonces </em>rogó Zacarías a Su Señor, diciendo: “Mi Señor, concédeme de Ti mismo una descendencia pura; en verdad, Tú eres quien escucha la oración”." WHERE ayatNumber = 39 AND chapterNumber = 3;
UPDATE verses SET es = "Y los ángeles lo llamaron mientras oraba en el aposento: “Al-lah te da la buena nueva de Yahyá, quien dará testimonio de la verdad de una palabra de Al-lah -noble y casto- y un Profeta, de entre los justos.”" WHERE ayatNumber = 40 AND chapterNumber = 3;
UPDATE verses SET es = "Dijo: “¡Mi Señor!, ¿cómo voy a tener un hijo, si la vejez se ha poderado de mí, y mi mujer es estéril?”. Él respondió: “Así es <em>el designio de</em> Al-lah; Él hace lo que quiere”." WHERE ayatNumber = 41 AND chapterNumber = 3;
UPDATE verses SET es = "Dijo: “Mi Señor, señálame una prueba”. Él respondió: “Tú prueba será que no hablarás a los hombres durante tres días a no ser con gestos. Y recuerda mucho a Tu Señor y glorifícale en el ocaso y en el alba”." WHERE ayatNumber = 42 AND chapterNumber = 3;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando los ángeles dijeron: “¡Oh, María!, Al-lah te ha elegido y purificado y te ha escogido sobre las mujeres de todos los pueblos”." WHERE ayatNumber = 43 AND chapterNumber = 3;
UPDATE verses SET es = "“¡Oh, María! sé obediente a tu Señor y póstrate y adora a <em>Dios sólo</em> con los que l<em>e</em> adoran”." WHERE ayatNumber = 44 AND chapterNumber = 3;
UPDATE verses SET es = "Éste es uno de los relatos de las cosas ocultas que te revelamos. Y tú no estabas con ellos cuando lanzaron sus flechas, a fin de saber cuál de ellos sería el tutor de María, ni estabas con ellos cuando disputaron entre sí." WHERE ayatNumber = 45 AND chapterNumber = 3;
UPDATE verses SET es = "Cuando los ángeles dijeron: “¡Oh, María! Al-lah te anuncia la buena nueva de una palabra procedente de Él; su nombre <em>será</em> el Mesías, Jesús, hijo de María, honrado en este mundo y en el otro, y que será de aquellos a quienes se concede la proximidad <em>a Dios;</em>" WHERE ayatNumber = 46 AND chapterNumber = 3;
UPDATE verses SET es = "“Y hablará a los hombres desde la cuna y en la madurez, y estará entre los justos.”" WHERE ayatNumber = 47 AND chapterNumber = 3;
UPDATE verses SET es = "Ella dijo: “Mi Señor, ¿cómo voy a tener un hijo si ningún hombre me ha tocado?”. Él dijo: “Así <em>es el designio de </em>Al-lah; Él crea lo que Le place. Cuando decreta una cosa, le dice: “¡Sé!” y comienza a ser, <em>y finalmente es;</em>" WHERE ayatNumber = 48 AND chapterNumber = 3;
UPDATE verses SET es = "“Y Él le enseñará el Libro, la Sabiduría, la Torah y el Evangelio”;" WHERE ayatNumber = 49 AND chapterNumber = 3;
UPDATE verses SET es = "“Y <em>lo haremos</em> Mensajero para los Hijos de Israel (y dirá): “Vengo a vosotros con un Signo de vuestro Señor, <em>que es éste</em>: modelaré para vosotros, con barro, una criatura con forma de pájaro; luego le insuflaré un <em>nuevo espíritu</em> y se convertirá en un ser volador por mandato de Al-lah; y curaré al ciego y al leproso, y daré vida al muerto, por mandato de Al-lah; y os anunciaré lo que comeréis y lo que almacenaréis en vuestras casas. En verdad, he ahí un Signo para vosotros, si sois creyentes." WHERE ayatNumber = 50 AND chapterNumber = 3;
UPDATE verses SET es = "“Y <em>vengo</em> a cumplir lo que me ha precedido, esto es, la Torah; y para permitiros parte de lo que os fue prohibido; y vengo a vosotros con un Signo de vuestro Señor; temed pues a Al-lah y obedecedme”;" WHERE ayatNumber = 51 AND chapterNumber = 3;
UPDATE verses SET es = "“En verdad, Al-lah es mi Señor y vuestro Señor; adoradle, pues: éste es el camino recto”." WHERE ayatNumber = 52 AND chapterNumber = 3;
UPDATE verses SET es = "Mas cuando Jesús se dio cuenta de su incredulidad, dijo: “¿Quiénes serán mis colaboradores en la causa de Al-lah?”. Los discípulos respondieron: “Nosotros somos los colaboradores de Al-lah. Hemos creído en Al-lah y te ponemos por testigo de que somos obedientes;" WHERE ayatNumber = 53 AND chapterNumber = 3;
UPDATE verses SET es = "“Señor nuestro, creemos en lo que Tú has revelado y seguimos a este Mensajero. Inscríbenos, pues, entre los que son testigos.”" WHERE ayatNumber = 54 AND chapterNumber = 3;
UPDATE verses SET es = "E intrigaron, y Al-lah <em>también</em> hizo Sus planes; pues Al-lah es el que mejor sabe hacer planes." WHERE ayatNumber = 55 AND chapterNumber = 3;
UPDATE verses SET es = "Cuando Al-lah dijo: “¡Oh, Jesús! te haré morir<em>de muerte natural y</em> te ensalzaré hasta Mí mismo, librándote <em>de las acusaciones</em> de los incrédulos, y colocaré a los que te siguen por encima de los incrédulos, hasta el Día de la Resurrección; entonces volveréis a Mí y Yo os juzgaré respecto a vuestras diferencias." WHERE ayatNumber = 56 AND chapterNumber = 3;
UPDATE verses SET es = "“Y en cuanto a los incrédulos, los castigaré con un severo castigo en este mundo y en el futuro, y no tendrán quien les ayude.”" WHERE ayatNumber = 57 AND chapterNumber = 3;
UPDATE verses SET es = "“Y a los que creen y hacen buenas obras, Él les pagará toda su recompensa. Pues Al-lah no ama a los injustos”." WHERE ayatNumber = 58 AND chapterNumber = 3;
UPDATE verses SET es = "Esto es lo que te recitamos de los Signos y del sabio Recordatorio." WHERE ayatNumber = 59 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, lo que ocurrió con Jesús ante Al-lah es como lo que ocurrió con Adán. Él lo creó del polvo, y le dijo entonces: “¡Sé!” y fue." WHERE ayatNumber = 60 AND chapterNumber = 3;
UPDATE verses SET es = "<em> Ésta es</em> la verdad de tu Señor, para que no seas de los que dudan." WHERE ayatNumber = 61 AND chapterNumber = 3;
UPDATE verses SET es = "Pero si alguien disputa contigo respecto a él, después de haberte llegado el conocimiento, <em>dile:</em> “Venid, llamemos a nuestros hijos y a los vuestros, a nuestras mujeres y a las vuestras, a nuestras gentes y a las vuestras; entonces oremos fervientemente e invoquemos la maldición de Al-lah sobre los que mienten.”" WHERE ayatNumber = 62 AND chapterNumber = 3;
UPDATE verses SET es = "Ciertamente éste es el relato verídico. No hay nadie digno de ser adorado sino Al-lah; y en verdad Al-lah es el Poderoso, el Sabio." WHERE ayatNumber = 63 AND chapterNumber = 3;
UPDATE verses SET es = "Pero si vuelven la espalda, <em>recordad que</em> Al-lah conoce bien a los malvados." WHERE ayatNumber = 64 AND chapterNumber = 3;
UPDATE verses SET es = "Diles: “¡Oh, gente del Libro! venid a una palabra que es igual entre nosotros y vosotros: que no adoremos a nadie que no sea Al-lah, y no asociemos a nadie con Él, y que ninguno de nosotros tomemos a nadie por Señor aparte de Al-lah.” Pero si vuelven la espalda, diles: “Sed testigos de que nos hemos sometido <em>a Dios.</em>”" WHERE ayatNumber = 65 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, gente del Libro! ¿Por qué discutís acerca de Abraham, si la Torah y el Evangelio no fueron revelados sino después de él? ¿Acaso no comprenderéis?" WHERE ayatNumber = 66 AND chapterNumber = 3;
UPDATE verses SET es = "¡Mirad! sois de los que discutíais sobre lo que ya sabíais. ¿Por ué, pues, discutís <em>ahora</em> de lo que desconocéis <em>en absoluto</em>? Al-lah sabe y vosotros no sabéis." WHERE ayatNumber = 67 AND chapterNumber = 3;
UPDATE verses SET es = "Abraham no era ni judío ni cristiano, pero fue siempre sumiso <em> a Dios y</em> obediente <em>a él, </em>y no fue de los que asocian otros dioses<em> a Dios</em>." WHERE ayatNumber = 68 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, los más cercanos a Abraham son los hombres que lo siguieron <em>como también</em> este Profeta y los que creen. Y Al-lah es el amigo de los creyentes." WHERE ayatNumber = 69 AND chapterNumber = 3;
UPDATE verses SET es = "A una parte de la gente del Libro les encantaría extraviaros; pero sólo se han extraviado a sí mismos, y no se dan cuenta." WHERE ayatNumber = 70 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, gente del Libro! ¿por qué negáis los Signos de Al-lah, si sois sus testigos?" WHERE ayatNumber = 71 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, gente del Libro! ¿por qué confundís la verdad con la falsedad y ocultáis la verdad a sabiendas?" WHERE ayatNumber = 72 AND chapterNumber = 3;
UPDATE verses SET es = "Y una parte de la gente del Libro dice: “Creed al comienzo del día en lo que ha sido revelado a los creyentes y no creáis al final del mismo; tal vez así regresen”;" WHERE ayatNumber = 73 AND chapterNumber = 3;
UPDATE verses SET es = "“Y no obedezcáis sino a quien sigue vuestra religión”. Di, <em>Oh, Profeta</em>: “En verdad, la guía es la guía de Al-lah, <em>la que El desee”</em>. Lo que es esencial es que a cada uno se le conceda <em>una enseñanza</em> similar a la que fue concedida anteriormente. De otra manera tendrían derecho a disputar con vosotros en presencia de vuestro Señor. Diles: “Toda la generosidad está en las manos de Al-lah y Él la concede a quien Le place. Y Al-lah es Magnánimo, Omnisciente;" WHERE ayatNumber = 74 AND chapterNumber = 3;
UPDATE verses SET es = "“Él elige para Su misericordia a quien Le place. Pues Al-lah es Señor de suma generosidad”." WHERE ayatNumber = 75 AND chapterNumber = 3;
UPDATE verses SET es = "Entre la gente del Libro hay alguno que, si le confías un tesoro, te lo devolverá; y hay entre ellos alguno que, si le confías un dinar, no te lo devolverá, a menos que le apremies insistentemente. Y es porque dicen: “No se nos reprochará nada en lo que se refiere a los ignorantes”; y profieren a sabiendas una mentira contra Al-lah." WHERE ayatNumber = 76 AND chapterNumber = 3;
UPDATE verses SET es = "No, pero quien cumple su promesa y teme a Dios, <em>sepa que,</em> en verdad, Al-lah ama a los temerosos de Dios." WHERE ayatNumber = 77 AND chapterNumber = 3;
UPDATE verses SET es = "<em> En cuanto a</em> los que cobran un precio ridículo a cambio de <em>su</em> pacto con Al-lah y sus juramentos, no tendrán parte alguna en la vida futura, y Al-lah no les hablará ni les mirará el Día de la Resurrección, ni tampoco los purificará; y sufrirán un severo castigo." WHERE ayatNumber = 78 AND chapterNumber = 3;
UPDATE verses SET es = "Y en verdad, entre ellos hay una parte que tuercen la lengua mientras recitan el Libro; para que penséis que <em>es parte</em> del Libro, aunque no lo es. Y dicen: “Esto procede de Al-lah”, siendo así que no procede de Él; y profieren a sabiendas una mentira contra Al-lah." WHERE ayatNumber = 79 AND chapterNumber = 3;
UPDATE verses SET es = "Es inadmisible que un hombre al que Al-lah le haya dado el Libro, la sabiduría y el Profetazgo, diga después a los hombres: “Sed siervos míos y no de Al-lah”; por el contrario <em>debería decir:</em> “Consagraos únicamente al Señor porque enseñáis el Libro y <em>lo</em> estudiáis.”" WHERE ayatNumber = 80 AND chapterNumber = 3;
UPDATE verses SET es = "Y es <em>igualmente inadmisible</em> que <em>dicho hombre</em> os ordene tomar a los ángeles y a los Profetas como “Señores”. ¿Cómo puede obligaros a ser incrédulos si ya os habéis sometido <em>a Dios?</em>" WHERE ayatNumber = 81 AND chapterNumber = 3;
UPDATE verses SET es = "Y <em>acordaos de</em> cuando Al-lah estableció una alianza con el <em>pueblo a través de</em> los Profetas, <em>diciendo:</em> “Cuando os dé el Libro y la Sabiduría y os llegue después un Mensajero, cumpliendo lo que ya tenéis, creed en él y ayudadle”. Y Él dijo: “¿Estáis de acuerdo y aceptáis la responsabilidad que os impongo a este respecto?”. Dijeron: “Aceptamos”. Él dijo: “Dad, pues, testimonio y Yo estaré con vosotros entre los testigos.”" WHERE ayatNumber = 82 AND chapterNumber = 3;
UPDATE verses SET es = "Mas quienes se aparten después de esto serán de los inicuos." WHERE ayatNumber = 83 AND chapterNumber = 3;
UPDATE verses SET es = "¿Acaso buscan una religión distinta de la de Al-lah, cuando a Él está sometido todo lo que hay en los cielos y en la tierra, voluntaria o involuntariamente, y a Él serán devueltos?" WHERE ayatNumber = 84 AND chapterNumber = 3;
UPDATE verses SET es = "Di: “Creemos en Al-lah y en lo que nos ha sido revelado, y en lo revelado a Abraham, Ismael, Isaac, Jacob y a las tribus, y en lo que fue dado por su Señor a Moisés, Jesús y <em>otros</em> Profetas. No hacemos distinción entre ninguno de ellos y a Él nos sometemos.”" WHERE ayatNumber = 85 AND chapterNumber = 3;
UPDATE verses SET es = "Y a quien busque una religión distinta del Islam, no le será aceptada, y en la vida futura estará entre los perdedores." WHERE ayatNumber = 86 AND chapterNumber = 3;
UPDATE verses SET es = "¿Cómo guiará Al-lah a unas gentes que han dejado de creer después de haber creído, de haber sido testigos de que el Mensajero era auténtico y de haber recibido pruebas claras? Pues Al-lah no guía a los injustos." WHERE ayatNumber = 87 AND chapterNumber = 3;
UPDATE verses SET es = "Su recompensa será la maldición de Al-lah, de los ángeles y de los hombres, al mismo tiempo." WHERE ayatNumber = 88 AND chapterNumber = 3;
UPDATE verses SET es = "Y bajo ella vivirán. Su castigo no les será aliviado ni serán indultados;" WHERE ayatNumber = 89 AND chapterNumber = 3;
UPDATE verses SET es = "Excepto los que después se arrepientan y se enmienden. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 90 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, a quienes han dejado de creer después de haber creído y aumentan acto seguido su incredulidad, no se les aceptará su arrepentimiento, y serán ellos los descarriados." WHERE ayatNumber = 91 AND chapterNumber = 3;
UPDATE verses SET es = "<em>En cuanto a</em> los que no han creído y mueren en la incredulidad, no se aceptará de ninguno de ellos ni siquiera todo el oro de la tierra, aunque lo ofrezcan como rescate. Sufrirán un doloroso castigo y no tendrán defensores." WHERE ayatNumber = 92 AND chapterNumber = 3;
UPDATE verses SET es = "Jamás alcanzaréis la piedad a menos que gastéis de lo que amáis. Y Al-lah conoce con seguridad cualquier cosa que deis." WHERE ayatNumber = 93 AND chapterNumber = 3;
UPDATE verses SET es = "Todo alimento era lícito para los hijos de Israel, excepto lo que Israel se prohibió a sí mismo, antes de que la Torah fuese revelada. Diles: “Traed, pues, la Torah y recitadla, si sois sinceros.”" WHERE ayatNumber = 94 AND chapterNumber = 3;
UPDATE verses SET es = "Mas quienes, después de esto, fragüen una mentira contra Al-lah, se contarán entre los injustos." WHERE ayatNumber = 95 AND chapterNumber = 3;
UPDATE verses SET es = "Y diles: “Al-lah ha dicho la verdad; seguid, pues, la religión de Abraham, <em>quien estuvo</em> siempre sometido <em>a Dios;</em> y no fue de los que asocian otros dioses<em> a Dios</em>.”" WHERE ayatNumber = 96 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, la primera Casa fundada para la humanidad es la de Bakka, abundante en bendiciones y guía para todos los pueblos." WHERE ayatNumber = 97 AND chapterNumber = 3;
UPDATE verses SET es = "En ella hay Signos evidentes; es el lugar de Abraham; y quien entra en ella entra en la paz. Y la peregrinación a la Casa es un deber que los hombres –quienes sean capaces de encontrar el camino a ella– deben a Al-lah. Mas a quien no crea, <em>recuérdale</em> que Al-lah es ciertamente independiente de todas las criaturas." WHERE ayatNumber = 98 AND chapterNumber = 3;
UPDATE verses SET es = "Diles: “¡Oh, Pueblo del Libro! ¿por qué negáis los Signos de Al-lah, si Al-lah es testigo de lo que hacéis?”." WHERE ayatNumber = 99 AND chapterNumber = 3;
UPDATE verses SET es = "Diles: “¡Oh, Pueblo del Libro! ¿por qué desviáis a los creyentes del camino de Al-lah, intentando hacerlo tortuoso, si sois <em>sus</em> testigos?. Pero Al-lah no está desatento a lo que hacéis.”" WHERE ayatNumber = 100 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! si obedecéis a cualquier grupo de los que recibieron el Libro, os convertirán de nuevo en incrédulos después de haber sido creyentes." WHERE ayatNumber = 101 AND chapterNumber = 3;
UPDATE verses SET es = "¿Cómo podéis renegar si os han recitado los Signos de Al-lah y su Mensajero está presente entre vosotros? Mas quien se aferre fuertemente a Al-lah es en verdad conducido por el camino recto." WHERE ayatNumber = 102 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! temed a Al-lah como debe ser temido; y no permitáis que os llegue la muerte si no estáis sometidos." WHERE ayatNumber = 103 AND chapterNumber = 3;
UPDATE verses SET es = "Y aferraos fuertemente todos juntos, con la cuerda de Al-lah y no os dividáis; y recordad el favor de Al-lah que Él os concedió cuando erais enemigos y Él unió vuestros corazones en el amor, para que con su gracia os hicierais <em>como</em> hermanos; estabais al borde de un abismo de fuego y Él os salvó. Así os explica Al-lah sus mandamientos, para que seáis guiados." WHERE ayatNumber = 104 AND chapterNumber = 3;
UPDATE verses SET es = "Y que surja de entre vosotros un grupo de hombres que invite a la bondad, imponga la justicia y prohíba la maldad. Ellos serán los bienaventurados." WHERE ayatNumber = 105 AND chapterNumber = 3;
UPDATE verses SET es = "Y no seáis como aquellos que se separaron y disputaron <em>entre sí</em> después de que les llegaran pruebas evidentes. Y sobre ellos caerá un gran castigo." WHERE ayatNumber = 106 AND chapterNumber = 3;
UPDATE verses SET es = "El día en que unos rostros serán blancos y otros negros. Y a cuantos tengan el rostro negro<em> se les dirá:</em> “¿Dejasteis de creer después de haber creído? Probad, pues, el castigo de vuestra incredulidad.”" WHERE ayatNumber = 107 AND chapterNumber = 3;
UPDATE verses SET es = "Y en cuanto a los que tengan el rostro blanco, estarán en la misericordia de Al-lah; y en ella morarán eternamente." WHERE ayatNumber = 108 AND chapterNumber = 3;
UPDATE verses SET es = "Éstos son los Signos de Al-lah. Te los recitamos porque aúnan la Verdad. Pues Al-lah no desea nada malo a Sus criaturas." WHERE ayatNumber = 109 AND chapterNumber = 3;
UPDATE verses SET es = "Y a Al-lah pertenece cuanto hay en los cielos y en la tierra; y a Al-lah se someterán todos los asuntos." WHERE ayatNumber = 110 AND chapterNumber = 3;
UPDATE verses SET es = "Sois el mejor pueblo, exaltado para el bien de la humanidad; ordenáis lo bueno, prohibís lo malo y creéis en Al-lah. Y si el Pueblo del Libro hubiese creído, en verdad hubiese sido mejor para ellos. Algunos de ellos son creyentes, pero en su mayoría son desobedientes." WHERE ayatNumber = 111 AND chapterNumber = 3;
UPDATE verses SET es = "No os perjudicarán, salvo con ligero daño; y si os combaten os volverán la espalda. Entonces quedarán sin ayuda." WHERE ayatNumber = 112 AND chapterNumber = 3;
UPDATE verses SET es = "Serán afligidos con la humillación donde quiera que se encuentren, a menos que tengan la protección de Al-lah o la protección de los hombres. Han incurrido en la cólera de Al-lah y sufrieron la miseria. Todo ello por haber rechazado los Signos de Al-lah y matado injustamente a los Profetas. Todo ello por haberse rebelado y ser pecadores." WHERE ayatNumber = 113 AND chapterNumber = 3;
UPDATE verses SET es = "No <em>todos</em> ellos son iguales. Entre las gentes del Libro hay un grupo <em>que </em>cumple con firmeza<em> su alianza;</em> ecitan la palabra de Al-lah durante las horas de la noche y se postran <em>ante Él.</em>" WHERE ayatNumber = 114 AND chapterNumber = 3;
UPDATE verses SET es = "Creen en Al-lah y en el Último Día, ordenan lo que es bueno y prohíben lo malo y rivalizan entre ellos en las buenas obras. Y éstos están entre los justos." WHERE ayatNumber = 115 AND chapterNumber = 3;
UPDATE verses SET es = "Y de sus buenas acciones no se les negará la debida recompensa; y Al-lah conoce bien a los que Le temen." WHERE ayatNumber = 116 AND chapterNumber = 3;
UPDATE verses SET es = "Y en cuanto a los que no creen, de nada les valdrá ante Al-lah ni su hacienda ni sus hijos; éstos serán los habitantes del Fuego; allí tendrán su morada." WHERE ayatNumber = 117 AND chapterNumber = 3;
UPDATE verses SET es = "Lo que gastan en la presente vida es parecido a un viento con frío glacial, que azota los cultivos de un pueblo que se ha perjudicado a sí mismo, y los destruye. Y no es Al-lah quien les hace daño, sino ellos mismos quienes se lo hacen." WHERE ayatNumber = 118 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! no toméis como amigos íntimos a <em>otros</em> que no sean de los vuestros; porque ellos no dejarán de corromperos. Les agrada veros en problemas. El odio se ha manifestado ya por <em>lo que profieren</em> sus bocas, y es aún peor lo que ocultan en sus corazones. Os hemos aclarado Nuestro mandamiento, si es que comprendéis." WHERE ayatNumber = 119 AND chapterNumber = 3;
UPDATE verses SET es = "He ahí que vosotros los amáis, pero ellos no os aman. Y vosotros creéis en todo el Libro. Cuando se reúnen con vosotros, dicen: “Creemos”; pero cuando están solos se muerden las yemas de los dedos de rabia contra vosotros. Diles: “Morid en vuestra rabia. En verdad, Al-lah conoce bien lo que está oculto en <em>vuestros</em> corazones”." WHERE ayatNumber = 120 AND chapterNumber = 3;
UPDATE verses SET es = "Si os ocurre algo bueno, eso les molesta; y si os ocurre algo malo se regocijan. Pero si sois perseverantes y piadosos, sus designios no os perjudicarán en absoluto; en verdad, Al-lah conoce <em>todo</em> lo que hacen." WHERE ayatNumber = 121 AND chapterNumber = 3;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando abandonaste tu casa al amanecer, a fin de asignar a los creyentes sus posiciones para la batalla. Pues Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 122 AND chapterNumber = 3;
UPDATE verses SET es = "Cuando dos de vuestros grupos flaquearon aunque Al-lah era su Amigo. Y los creyentes deben apoyarse en Al-lah." WHERE ayatNumber = 123 AND chapterNumber = 3;
UPDATE verses SET es = "Y Al-lah os había ayudado <em>ya</em> en Badr cuando erais débiles. Tomad pues a Al-lah como vuestro Protector para que seáis agradecidos." WHERE ayatNumber = 124 AND chapterNumber = 3;
UPDATE verses SET es = "Cuando dijiste a los creyentes: “¿No será suficiente para nosotros on que vuestro Señor os ayude con tres mil ángeles enviados <em>desde lo alto?”.</em>" WHERE ayatNumber = 125 AND chapterNumber = 3;
UPDATE verses SET es = "Cierto <em>que</em> si sois perseverantes y piadosos y ellos os atacan de inmediato y por sorpresa, vuestro Señor os ayudará con cinco mil ángeles, atacando valientemente." WHERE ayatNumber = 126 AND chapterNumber = 3;
UPDATE verses SET es = "Y Al-lah no lo ha hecho sino como una buena nueva para vosotros y para que vuestros corazones se tranquilicen; y la ayuda sólo procede de Al-lah, el Poderoso, el Sabio." WHERE ayatNumber = 127 AND chapterNumber = 3;
UPDATE verses SET es = "<em> Dios lo hará así</em> para poder eliminar a parte de los incrédulos o derribarlos a fin de que retrocedan desengañados." WHERE ayatNumber = 128 AND chapterNumber = 3;
UPDATE verses SET es = "A ti nada te incumbe en el asunto: Él puede volverse hacia ellos con misericordia o castigarlos, porque son malvados." WHERE ayatNumber = 129 AND chapterNumber = 3;
UPDATE verses SET es = "Y a Al-lah pertenece cuanto hay en los cielos y en la tierra. Él perdona a quien le place y castiga a quien le place, pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 130 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! no devoréis intereses que llevan inherente su multiplicación; y temed a Al-lah para que prosperéis." WHERE ayatNumber = 131 AND chapterNumber = 3;
UPDATE verses SET es = "Y temed al Fuego preparado para los incrédulos." WHERE ayatNumber = 132 AND chapterNumber = 3;
UPDATE verses SET es = "Y obedeced a Al-lah y al Mensajero, para que se os muestre misericordia." WHERE ayatNumber = 133 AND chapterNumber = 3;
UPDATE verses SET es = "Y rivalizad mutuamente pidiendo el perdón de vuestro Señor, y pidiendo el paraíso cuya recompensa son los cielos y la tierra, preparado para los temerosos de Dios." WHERE ayatNumber = 134 AND chapterNumber = 3;
UPDATE verses SET es = "Los que gastan en la prosperidad y en la adversidad, y los que reprimen su cólera y perdonan a los hombres; pues Al-lah ama a los que hacen el bien;" WHERE ayatNumber = 135 AND chapterNumber = 3;
UPDATE verses SET es = "Y aquellos que, cuando cometen una mala acción o se perjudican, se acuerdan de Al-lah e imploran el perdón de sus pecados -¿y quién puede perdonar los pecados sino Al-lah?- y no persisten a sabiendas en lo que han hecho." WHERE ayatNumber = 136 AND chapterNumber = 3;
UPDATE verses SET es = "Son éstos cuya recompensa es el perdón de su Señor, y jardines por los cuales corren ríos, en donde morarán eternamente; ¡qué hermosa es la recompensa de los que trabajan!" WHERE ayatNumber = 137 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad hubo muchos designios antes de vosotros; recorred, pues, a tierra y comprobad qué malo fue el fin de los que trataron <em>a los Profetas</em> de embusteros." WHERE ayatNumber = 138 AND chapterNumber = 3;
UPDATE verses SET es = "Éste (el Corán), es una declaración evidente para los hombres, una guía y exhortación para los temerosos de Dios." WHERE ayatNumber = 139 AND chapterNumber = 3;
UPDATE verses SET es = "No os desaniméis ni os aflijáis; y tendréis ciertamente la posibilidad más ventajosa, si es que sois creyentes." WHERE ayatNumber = 140 AND chapterNumber = 3;
UPDATE verses SET es = "Si habéis recibido alguna herida, en verdad que los <em>incrédulos</em> han recibido <em>ya</em> una herida similar. Y acemos que esos días se sucedan alternativamente entre los hombres <em>para que sean exhortados,</em> y para que Al-lah pueda distinguir a los que creen y escoja testigos de entre vosotros; pues Al-lah no ama a los injustos." WHERE ayatNumber = 141 AND chapterNumber = 3;
UPDATE verses SET es = "Y para que Al-lah pueda purificar a los creyentes y destruir a los incrédulos." WHERE ayatNumber = 142 AND chapterNumber = 3;
UPDATE verses SET es = "¿Suponéis que vais a entrar en el cielo cuando Al-lah no ha istinguido aún a aquellos de vosotros que se sacrifican <em>en el camino de</em> Al-lah ni tampoco ha distinguido a los perseverantes?" WHERE ayatNumber = 143 AND chapterNumber = 3;
UPDATE verses SET es = "Solíais desear esta muerte antes de encontrarla; ahora que inalmente la habéis visto, permanecéis observando como si <em>estuvierais paralizados</em>." WHERE ayatNumber = 144 AND chapterNumber = 3;
UPDATE verses SET es = "Y Mohammad no es más que un Mensajero y antes que él han pasado <em>todos</em> los Mensajeros. Pero si muere o es asesinado, ¿volveríais sobre vuestros pasos? Quien vuelve sobre sus pasos no perjudicará en absoluto a Al-lah. Y Al-lah recompensa a los agradecidos." WHERE ayatNumber = 145 AND chapterNumber = 3;
UPDATE verses SET es = "Y ningún alma puede morir si no es con permiso de Al-lah: Una sentencia a plazo fijo. Y a quien desee la recompensa de este mundo, se la daremos; y a quien desee la recompensa del mundo futuro se la daremos; pues en verdad recompensaremos a los agradecidos." WHERE ayatNumber = 146 AND chapterNumber = 3;
UPDATE verses SET es = "¡Cuántos Profetas ha habido a cuyo lado lucharon muchos hombres piadosos! No desfallecieron por lo que les sucedió en el camino de Al-lah, ni flaquearon ni se humillaron ante el enemigo. Y Al-lah ama a los perseverantes." WHERE ayatNumber = 147 AND chapterNumber = 3;
UPDATE verses SET es = "Y no profirieron palabra alguna salvo para decir: “Señor nuestro, perdónanos nuestros errores y los excesos de nuestra conducta, haz firmes nuestros pasos y ayúdanos contra los incrédulos.”" WHERE ayatNumber = 148 AND chapterNumber = 3;
UPDATE verses SET es = "Así les dio Al-lah la recompensa de este mundo como también una recompensa excelente en el futuro; pues Al-lah ama a quienes hacen el bien." WHERE ayatNumber = 149 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! si obedecéis a quienes no creen, os harán volver sobre vuestros pasos y seréis perdedores." WHERE ayatNumber = 150 AND chapterNumber = 3;
UPDATE verses SET es = "No; Al-lah es vuestro Protector y es el mejor de los defensores." WHERE ayatNumber = 151 AND chapterNumber = 3;
UPDATE verses SET es = "Infundiremos el temor en los corazones de los incrédulos por haber asociado con Al-lah, a quienes Él no dio autoridad alguna. Su morada es el Fuego; y mala es la mansión de los malvados." WHERE ayatNumber = 152 AND chapterNumber = 3;
UPDATE verses SET es = "Y en verdad Al-lah cumplió con vosotros Su promesa cuando los poníais en fuga y los derrotabais con Su permiso; hasta que, cuando laqueasteis <em>en lo concerniente a la obediencia al Santo Profeta</em> y comenzasteis a disputar entre vosotros sobre la verdadera intención de la orden y desobedecisteis después de que Él os hubiera concedido lo ue vuestro corazón deseaba <em>en forma de victoria</em>, <em>os retiró Su ayuda.</em> Entre vosotros había quienes deseaban el presente mundo y otros que deseaban el futuro. Entonces os alejó de ellos para poder probaros –y en verdad Él ya os ha perdonado, y Al-lah es Clemente para los creyentes-" WHERE ayatNumber = 153 AND chapterNumber = 3;
UPDATE verses SET es = "Cuando huíais sin volver la cabeza a nadie, mientras el Mensajero os llamaba desde atrás, entonces Él os alivió de una aflicción menor, creando el temor a una aflicción mayor, que no era real, para que no os lamentarais. Pues Al-lah sabe perfectamente lo que hacéis." WHERE ayatNumber = 154 AND chapterNumber = 3;
UPDATE verses SET es = "Después, tras la aflicción, Él hizo descender la paz sobre vosotros –un sopor que envolvió a una parte de vosotros- mientras que los de la otra parte se sentían inquietos respecto a sí mismos, pensando equivocadamente de Al-lah con pensamientos propios de los gnorantes. Dijeron: “¿Tenemos algo que decir en este <em>asunto?”.</em> Diles: “Todas las decisiones corresponden a Al-lah”. Ellos ocultan en sus mentes lo que no te revelan. Dicen: “Si hubiésemos tenido parte alguna en la dirección del <em>asunto</em> no nos habrían matado aquí”. Diles: “Aún cuando hubieseis permanecido en vuestros hogares, en verdad que aquellos a quienes se hubo ordenado luchar se abrían dirigido a sus lechos de muerte, <em>para que Al-lah llevara a efecto Su decreto </em>y probara lo que había en vuestros pechos, y purificara lo que había en vuestros corazones. Pues Al-lah sabe perfectamente lo que hay en las mentes;" WHERE ayatNumber = 155 AND chapterNumber = 3;
UPDATE verses SET es = "Aquellos de vosotros que volvieron la espalda el día en que se encontraron las dos tropas, fue en realidad <em>porque</em> Satanás los quiso inducir a tropezar por algunas de sus acciones. Pero ciertamente Al-lah ya los ha perdonado, Al-lah es, en verdad, el Sumo Indulgente, Paciente." WHERE ayatNumber = 156 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! no seáis como los incrédulos, que dicen de sus hermanos cuando viajan por todo el país o se dirigen a la guerra: “Si hubiesen estado con nosotros, no habrían muerto ni hubiesen sido matados”. <em>Esto es así</em> para que Al-lah lo convierta en pena en sus corazones. Pues Al-lah da la vida y causa la muerte y Al-lah está al tanto de lo que hacéis." WHERE ayatNumber = 157 AND chapterNumber = 3;
UPDATE verses SET es = "Y si sois matados o morís por la causa de Al-lah, en verdad el perdón de Al-lah y Su misericordia serán mejores que cuanto puedan atesorar." WHERE ayatNumber = 158 AND chapterNumber = 3;
UPDATE verses SET es = "Y si vosotros morís o sois matados en verdad que seréis reunidos junto a Al-lah." WHERE ayatNumber = 159 AND chapterNumber = 3;
UPDATE verses SET es = "Y por la <em>gran</em> misericordia de Al-lah has sido amable con ellos; y si hubieses sido rudo y desconsiderado, ciertamente se habrían apartado de ti. Perdónalos, pues; pide el perdón para ellos y consúltales en asuntos importantes; y cuando estés decidido, pon tu confianza en Al-lah. En verdad, Al-lah ama a quien pone <em>en Él</em> su confianza." WHERE ayatNumber = 160 AND chapterNumber = 3;
UPDATE verses SET es = "Si Al-lah os ayuda, nadie podrá venceros; pero si os abandona, ¿quién podrá ayudaros aparte de Él? ¡Que los creyentes pongan, pues, en Al-lah su confianza!" WHERE ayatNumber = 161 AND chapterNumber = 3;
UPDATE verses SET es = "No es posible que un Profeta actúe deshonestamente, pues quien así actúe llevará <em>consigo</em> aquello en lo que ha sido deshonesto en el Día de la Resurrección. Entonces cada alma recibirá el pago completo de lo que ha merecido; y no serán tratados injustamente." WHERE ayatNumber = 162 AND chapterNumber = 3;
UPDATE verses SET es = "Quien busca el agrado de Al-lah ¿será acaso como aquel que atrae sobre sí la cólera de Al-lah y cuya morada es el infierno? ¡Terrible refugio es ése!" WHERE ayatNumber = 163 AND chapterNumber = 3;
UPDATE verses SET es = "Poseen <em>diferentes</em> grados <em>de gracia</em> ante Al-lah; pues Al-lah ve lo que hacen." WHERE ayatNumber = 164 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, Al-lah ha concedido un gran don a los creyentes suscitando de entre ellos un Mensajero que les recita Sus Signos, los purifica y les enseña el Libro y la Sabiduría; aunque antes de eso estaban ciertamente en un error manifiesto." WHERE ayatNumber = 165 AND chapterNumber = 3;
UPDATE verses SET es = "¡Cómo! cuando sufrís una desgracia –aunque vosotros les causasteis una doble- decís: ¿De dónde viene esto? Diles: “De vosotros mismos”. En verdad, Al-lah tiene poder sobre todas las cosas." WHERE ayatNumber = 166 AND chapterNumber = 3;
UPDATE verses SET es = "Y lo que os sucedió el día en que las tropas se encontraron fue por mandato de Al-lah; y <em>fue así</em> para que Él distinguiera a los creyentes;" WHERE ayatNumber = 167 AND chapterNumber = 3;
UPDATE verses SET es = "Y distinguiera a los hipócritas. Pues a ellos se les dijo: “Venid luchar por la causa de Al-lah y rechazad <em>el ataque del enemigo;</em>” y respondieron: “Si supiéramos combatir, ciertamente os seguiríamos.” En aquel día, estaban más cerca de la incredulidad que de la fe. Profieren con sus bocas lo que no está en sus corazones. Pero Al-lah sabe <em>muy</em> bien lo que ocultan." WHERE ayatNumber = 168 AND chapterNumber = 3;
UPDATE verses SET es = "<em> Son éstos</em> quienes decían de sus hermanos, mientras <em>ellos</em> permanecían detrás: “Si nos hubieran obedecido, no habrían sido matados”. Diles: “Apartad la muerte de vosotros, si sois sinceros.”" WHERE ayatNumber = 169 AND chapterNumber = 3;
UPDATE verses SET es = "No penséis que los que han muerto por la causa de Al-lah están muertos. No, ellos viven en la presencia de su Señor, <em>y</em> están provistos de todo." WHERE ayatNumber = 170 AND chapterNumber = 3;
UPDATE verses SET es = "Con gran regocijo por lo que Al-lah les ha dado de Su bondad; y alegres por aquellos que quedan atrás, que aún no se les han unido, porque no les sobrecogerá el temor, ni serán afligidos." WHERE ayatNumber = 171 AND chapterNumber = 3;
UPDATE verses SET es = "Se regocijan por el favor de Al-lah y <em>Su</em> bondad, y porque Al-lah no permite que los creyentes queden sin recompensa." WHERE ayatNumber = 172 AND chapterNumber = 3;
UPDATE verses SET es = "En cuanto a los que respondieron a la llamada de Al-lah y del Mensajero después de que sufrieran una herida <em>sepan que </em> quienes de entre ellos hagan el bien y actúen piadosamente tendrán una magnífica recompensa;" WHERE ayatNumber = 173 AND chapterNumber = 3;
UPDATE verses SET es = "A quienes dijeron los hombres: “La gente se ha reunido contra vosotros; temedles, pues,” pero esto <em>no hizo más que</em> aumentar su fe, y dijeron: “Nos basta con Al-lah; ¡qué excelente guardián es Él!”." WHERE ayatNumber = 174 AND chapterNumber = 3;
UPDATE verses SET es = "Regresaron, pues, con el <em>poderoso</em> favor de Al-lah y Su <em>gran</em> magnanimidad, sin sufrir daño alguno; y siguieron a la voluntad de Al-lah, pues Al-lah es el Señor de la suma magnanimidad." WHERE ayatNumber = 175 AND chapterNumber = 3;
UPDATE verses SET es = "Sólo Satanás asusta a sus amigos, no les temáis, por tanto, sino sólo temedme a Mí, si sois creyentes." WHERE ayatNumber = 176 AND chapterNumber = 3;
UPDATE verses SET es = "Y no permitas que te aflijan quienes se precipitan en la incredulidad; ciertamente, no pueden perjudicar a Al-lah en absoluto. Al-lah no desea reservarles una parte en la vida futura; y sufrirán un severo castigo." WHERE ayatNumber = 177 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, quienes compraron la incredulidad a costa de la fe no pueden perjudicar a Al-lah en absoluto; y tendrán un grave castigo." WHERE ayatNumber = 178 AND chapterNumber = 3;
UPDATE verses SET es = "Y que no piensen los incrédulos que el respiro que les concedemos es un bien para ellos; <em>el resultado de</em> que les concedamos tal respiro sólo será que acrecentarán sus pecados; y sufrirán un humillante castigo." WHERE ayatNumber = 179 AND chapterNumber = 3;
UPDATE verses SET es = "Al-lah no abandonará a los creyentes en la condición en la que os encontráis, mientras no haya separado al malvado del bueno. Ni tampoco os revelará Al-lah lo desconocido. Mas Al-lah elige de sus Mensajeros a quien Le place. Creed, pues, en Al-lah y en Sus Mensajeros. Si creéis y sois piadosos, tendréis una gran recompensa." WHERE ayatNumber = 180 AND chapterNumber = 3;
UPDATE verses SET es = "Y que no piensen los tacaños, respecto a lo que Al-lah con Su magnanimidad les ha concedido, que eso es bueno para ellos. No, les resultará peor. En el Día de la Resurrección llevarán como un collar, alrededor de su cuello, aquello en lo que fueron tacaños. Pues a Al-lah pertenece la herencia de los cielos y la tierra, y Al-lah conoce perfectamente lo que hacéis." WHERE ayatNumber = 181 AND chapterNumber = 3;
UPDATE verses SET es = "Y en verdad, Al-lah ha oído las palabras de quienes dijeron: “Al-lah es pobre y nosotros ricos”. Tomaremos nota de lo que dijeron y de sus intentos por matar injustamente a los Profetas; y diremos: “¡Gustad el castigo del fuego!”." WHERE ayatNumber = 182 AND chapterNumber = 3;
UPDATE verses SET es = "Eso es por lo que <em>os</em> prepararon vuestras propias manos, ues <em>lo cierto es</em> que Al-lah no es injusto, en absoluto, con <em>Sus</em> Siervos." WHERE ayatNumber = 183 AND chapterNumber = 3;
UPDATE verses SET es = "Quienes dicen: “Al-lah nos ha encomendado que no creamos a ningún Mensajero mientras no nos presente una ofrenda que devore el fuego”. Diles: “Antes de mi os han llegado Mensajeros con Signos claros y con lo que acabáis de decir. ¿Por qué, pues, procurasteis matarlos, si sois veraces?”." WHERE ayatNumber = 184 AND chapterNumber = 3;
UPDATE verses SET es = "Y si te acusan de mentiroso, también fueron acusados de mentirosos los Mensajeros que te precedieron, quienes vinieron con Signos evidentes, libros de sabiduría y el Libro que ilumina." WHERE ayatNumber = 185 AND chapterNumber = 3;
UPDATE verses SET es = "Toda alma probará el sabor de la muerte. Y vuestra recompensa total sólo se os pagará el Día de la Resurrección. Por tanto, quien sea apartado del Fuego e introducido en el Cielo, ha alcanzado en verdad su meta. Pues la vida en este mundo no es más que un placer ilusorio." WHERE ayatNumber = 186 AND chapterNumber = 3;
UPDATE verses SET es = "En verdad, seréis probados en vuestros bienes y en vuestra ersona, y ciertamente oiréis muchas cosas perniciosas de <em>parte de</em> quienes recibieron el Libro antes que vosotros y de quienes asocian <em>a Dios</em> otras divinidades. Pero si mostráis fortaleza y actuáis con justicia, es desde luego <em>un acto</em> de enérgica decisión." WHERE ayatNumber = 187 AND chapterNumber = 3;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando Al-lah estableció un pacto con los que recibieron el Libro, <em>diciendo: </em>“Dad a conocer este Libro a las gentes y no lo ocultéis.” Pero lo tiraron a sus espaldas y lo vendieron a un precio ridículo. Lo que compraron fue el mal." WHERE ayatNumber = 188 AND chapterNumber = 3;
UPDATE verses SET es = "No creas que los que se alegran por lo que han hecho y desean ser alabados por lo que no han hecho: no creas que están a salvo del castigo. Sufrirán un castigo humillante." WHERE ayatNumber = 189 AND chapterNumber = 3;
UPDATE verses SET es = "Pues a Al-lah pertenece el reino de los cielos y la tierra; y Al-lah tiene poder sobre todas las cosas." WHERE ayatNumber = 190 AND chapterNumber = 3;
UPDATE verses SET es = "En la creación de los cielos y la tierra y en la sucesión de la noche y el día hay sin duda Signos para los hombres sensatos;" WHERE ayatNumber = 191 AND chapterNumber = 3;
UPDATE verses SET es = "Que se acuerdan de Al-lah cuando están de pie, sentados y <em>tumbados</em> sobre su costado, y meditan en la creación de los cielos y la tierra: “Señor nuestro, Tú no has creado esto en vano; no ¡Santificado eres Tú!; ¡sálvanos, pues, del castigo del Fuego!" WHERE ayatNumber = 192 AND chapterNumber = 3;
UPDATE verses SET es = "“Señor nuestro, a quienquiera que hagas entrar en el Fuego lo cubres en verdad de oprobio. Pues los malvados no tendrán defensores”." WHERE ayatNumber = 193 AND chapterNumber = 3;
UPDATE verses SET es = "“Señor nuestro, hemos oído a un Pregonero llamarnos a la fe: “Creed en vuestro Señor”, y hemos creído. Señor nuestro, perdónanos, pues, nuestros <em>pecados</em>, borra nuestras malas acciones y únenos a los justos a la hora de morir”." WHERE ayatNumber = 194 AND chapterNumber = 3;
UPDATE verses SET es = "“Señor nuestro, danos lo que nos has prometido por medio de Tus Mensajeros; y no nos afrontes en el Día de la Resurrección. En verdad, Tú no incumples tu promesa”." WHERE ayatNumber = 195 AND chapterNumber = 3;
UPDATE verses SET es = "Entonces su Señor respondió a sus <em>plegarias, diciendo:</em> “No permitiré que se pierda el trabajo de ninguno de vosotros, sea hombre o mujer. Procedéis unos de otros. Por tanto, a quienes emigraron, fueron expulsados de sus hogares, fueron perseguidos por Mi causa, lucharon y fueron matados, en verdad les perdonaré sus malas acciones y los introduciré en unos jardines por los que corren ríos: una recompensa de Al-lah, pues junto a Al-lah está la mejor de las recompensas.”" WHERE ayatNumber = 196 AND chapterNumber = 3;
UPDATE verses SET es = "No permitas que te engañe el ir y venir de los incrédulos por la tierra." WHERE ayatNumber = 197 AND chapterNumber = 3;
UPDATE verses SET es = "<em> Es</em> una ventaja pequeña y <em>breve,</em> ya que después el Infierno será su morada. ¡Qué espantoso lugar de reposo!" WHERE ayatNumber = 198 AND chapterNumber = 3;
UPDATE verses SET es = "Pero quienes temen a su Señor tendrán jardines por los que corren ríos; allí habitarán -como huéspedes de Al-lah-. Y lo que está junto a Al-lah es aún mejor para los piadosos." WHERE ayatNumber = 199 AND chapterNumber = 3;
UPDATE verses SET es = "Y entre el pueblo del Libro hay en verdad quienes creen en Al-lah, en lo que se te ha revelado y en lo que les fue revelado, humillándose ante Al-lah. No malvenden los Signos de Al-lah por un precio ridículo. Son ellos quienes tendrán su recompensa junto a su Señor. En verdad, Al-lah es rápido en ajustar las cuentas." WHERE ayatNumber = 200 AND chapterNumber = 3;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! sed pacientes, luchad por sobresalir en la perseverancia, estad en alerta y temed a Al-lah para que prosperéis." WHERE ayatNumber = 201 AND chapterNumber = 3;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh, hombres! temed a vuestro Señor quien os creó de un solo ser, del cual creó a su pareja, y de los dos hizo descender a muchos hombres mujeres; y temed a Al-lah, en cuyo nombre recurrís unos a otros, y <em>temedle particularmente respetando</em> los lazos del parentesco. En verdad, Al-lah os observa." WHERE ayatNumber = 2 AND chapterNumber = 4;
UPDATE verses SET es = "Y entregad a los huérfanos sus bienes, no cambiéis lo malo por lo bueno, ni devoréis sus bienes mediante los vuestros. En verdad, eso es un gran pecado." WHERE ayatNumber = 3 AND chapterNumber = 4;
UPDATE verses SET es = "Y si teméis que <em>la sociedad</em> falle en hacer justicia en suntos referentes a los huérfanos la <em>al finalizar la</em> <em>guerra</em>, entonces casaros con mujeres de vuestra elección, dos, res o cuatro; pero si teméis no poder mantener la equidad, <em>casaos sólo</em> con una o con lo que posea vuestra diestra. Éste es el <em>camino</em> más seguro para que evitéis la injusticia." WHERE ayatNumber = 4 AND chapterNumber = 4;
UPDATE verses SET es = "Y entregad de buen grado su dote a las mujeres. Pero si ellas, por su propia voluntad, os devuelven una parte de ella, disfrutadla como algo agradable y edificante." WHERE ayatNumber = 5 AND chapterNumber = 4;
UPDATE verses SET es = "Y no entreguéis la custodia de los bienes que os pertenecen, que Al-lah os ha dado como medio de subsistencia, a los que son mentalmente incapaces de manejarlos adecuadamente. Por tanto, alimentadles y vestidles convenientemente y dirigidles palabras amables." WHERE ayatNumber = 6 AND chapterNumber = 4;
UPDATE verses SET es = "Y probad <em>el entendimiento</em> de los huérfanos hasta que alcancen <em>la edad del</em> matrimonio; entonces, si los encontráis de sano juicio, entregadles sus bienes; y no los gastéis en extravagancias y deprisa antes de que ellos crezcan; quien sea rico que se abstenga; y el pobre que tome <em>de ellos</em> con moderación. Mas cuando les entreguéis sus bienes, llamad a unos testigos ante ellos. Y Al-lah es suficiente como Juez." WHERE ayatNumber = 7 AND chapterNumber = 4;
UPDATE verses SET es = "A los hombres les corresponde una parte de lo que dejan sus padres y parientes cercanos; a las mujeres les corresponde <em>también</em> una parte de lo que dejan sus padres y parientes cercanos, sea poco o mucho: una parte determinada." WHERE ayatNumber = 8 AND chapterNumber = 4;
UPDATE verses SET es = "Y cuando en la distribución <em>de la herencia</em> estén presentes<em>otros</em> familiares, huérfanos y menesterosos, dadles <em>algo</em> de ella y dirigidles palabras amables." WHERE ayatNumber = 9 AND chapterNumber = 4;
UPDATE verses SET es = "Y teman <em>a Dios</em> quienes, si dejaran tras ellos una descendencia débil, se sentirían angustiados por ella. Que teman a Al-lah y digan la palabra justa." WHERE ayatNumber = 10 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, quienes devoran injustamente los bienes de los huérfanos no hacen más que tragar fuego en sus entrañas, pues arderán en un fuego llameante." WHERE ayatNumber = 11 AND chapterNumber = 4;
UPDATE verses SET es = "Al-lah os ordena en relación con vuestros hijos: el varón tendrá la arte de dos mujeres; pero si <em>sólo</em> hay mujeres, en<em>número</em> superior a dos, tendrán dos tercios de lo que deja <em>el difunto; </em>y si hay una, recibirá la mitad. Y sus padres tendrán cada uno la sexta parte de la herencia si tuviera un hijo; pero si <em>el difunto</em> no tiene hijos y sus padres son sus herederos, la madre recibirá un tercio; y si tiene hermanos y hermanas, su madre recibirá la sexta parte, después <em>de pagado</em> cualquier legado que hubiera dispuesto o las deudas. En cuanto a vuestros padres y vuestros hijos, desconocéis cuáles de ellos representan un mayor beneficio para vosotros. <em>Esta</em> fijación <em>de partes</em> procede de Al-lah. En verdad, Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 12 AND chapterNumber = 4;
UPDATE verses SET es = "Y vosotros recibiréis la mitad de lo que dejen vuestras esposas, si no tienen hijos; pero si tienen un hijo, recibiréis la cuarta parte de lo que dejen, después <em>de pagado</em> cualquier legado que hubieran dispuesto o las deudas. Si no tenéis hijos, ellas recibirán la cuarta parte de lo que dejéis, pero si los tenéis, recibirán la octava parte de lo que dejéis, después <em>de pagado</em> cualquier legado que hubierais dispuesto o las deudas. Y si hubiera un hombre o una mujer cuya herencia ha de ser dividida y no tiene padre ni hijos, pero tiene un hermano o una hermana, cada uno de ellos recibirá una sexta parte. Pero si fuesen más de dos, recibirán <em>a partes iguales</em> un tercio, después <em>de pagado</em> cualquier legado que hubieran dispuesto o las deudas, sin <em>intentar causar</em> sufrimiento a nadie. <em>Esto es </em>un mandato de Al-lah, pues Al-lah es Omnisciente, Tolerante." WHERE ayatNumber = 13 AND chapterNumber = 4;
UPDATE verses SET es = "Éstos son límites <em>fijados</em> por Al-lah; y a quien obedezca a Al-lah y a Su Mensajero, Él lo introducirá en jardines por los que corren ríos; allí morarán y será un gran triunfo." WHERE ayatNumber = 14 AND chapterNumber = 4;
UPDATE verses SET es = "Pero quien desobedezca a Al-lah y a Su Mensajero y viole Sus límites, Él lo introducirá en el Fuego; allí morará, y sufrirá un castigo ignominioso." WHERE ayatNumber = 15 AND chapterNumber = 4;
UPDATE verses SET es = "Sobre aquéllas de vuestras mujeres que sean culpables de adulterio –llamad a cuatro testigos de los vuestros; y si lo confirman, confinadlas en sus casas hasta que les llegue la muerte o Al-lah les abra otro camino." WHERE ayatNumber = 16 AND chapterNumber = 4;
UPDATE verses SET es = "Y si dos de vuestros hombres son culpables de ello, castigad a ambos. Pero si se arrepienten y se enmiendan, dejadlos solos; en verdad, Al-lah es Remisorio <em>con compasión y es</em> Misericordioso." WHERE ayatNumber = 17 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, Al-lah sólo se compromete a aceptar el arrepentimiento de los que hacen el mal por ignorancia y se arrepienten pronto. Hacia éstos se vuelve a Al-lah con misericordia; pues Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 18 AND chapterNumber = 4;
UPDATE verses SET es = "Pero no se aceptará el arrepentimiento de los que persisten haciendo el mal hasta que la muerte se presenta ante uno de ellos y dice: “Ahora me arrepiento”; ni de los que mueren en la incredulidad. Para ellos hemos preparado un doloroso castigo." WHERE ayatNumber = 19 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! no os es lícito heredar a vuestras mujeres contra su voluntad; ni retenerlas injustamente para retirarles parte de lo que les habéis dado, a menos que sean culpables de una maldad manifiesta; y convivid con ellas con benevolencia, pues si no os gustan, es posible que no os guste algo en lo que Dios ha puesto un gran bien." WHERE ayatNumber = 20 AND chapterNumber = 4;
UPDATE verses SET es = "Y si deseáis tomar a una esposa en lugar de otra y habéis entregado una posesión a una de ellas, no se la quitéis. ¿Seríais capaces de quitárselo mintiendo y con iniquidad manifiesta?" WHERE ayatNumber = 21 AND chapterNumber = 4;
UPDATE verses SET es = "Y ¿cómo podríais quitárselo si habéis convivido a solas el uno con el otro, y ellas (las mujeres) han recibido vuestra solemne promesa?" WHERE ayatNumber = 22 AND chapterNumber = 4;
UPDATE verses SET es = "Y no os caséis con las mujeres que vuestros padres han tenido por esposas, salvo que se trate de un hecho consumado. Es algo abominable y odioso y un pésimo camino." WHERE ayatNumber = 23 AND chapterNumber = 4;
UPDATE verses SET es = "Os están prohibidas vuestras madres, vuestras hijas, vuestras hermanas, las hermanas de vuestros padres y las de vuestras madres, las hijas de un hermano y las de una hermana, vuestras nodrizas que os hayan amamantado, vuestras hermanas de leche y las madres de vuestras esposas, vuestras ahijadas, que son vuestras pupilas por ser hijas de las mujeres con las que hayáis cohabitado –pero si no habéis cohabitado con ellas, no cometéis pecado- y las esposas de vuestros hijos carnales; y <em>os está prohibido contraer matrimonio</em> a la vez con dos hermanas, salvo que se trate de un hecho consumado; en verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 24 AND chapterNumber = 4;
UPDATE verses SET es = "Y <em>os están prohibidas</em> las mujeres casadas, excepto lo que posean vuestras diestras. Esto os ha ordenado Al-lah. Y os está permitido lo que no queda mencionado, que podáis buscar gracias a vuestros bienes, desposándolas decentemente y evitando la fornicación. Y por el beneficio que recibís de ellas, entregadles sus dotes, según lo estipulado, y no pecaréis en lo que acordéis mutuamente después de fijada <em>la dote</em>. Ciertamente, Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 25 AND chapterNumber = 4;
UPDATE verses SET es = "Y aquel de vosotros que no pueda permitirse contraer matrimonio con mujeres creyentes libres, <em>que </em>se case con lo que posean vuestras diestras, es decir, vuestras siervas creyentes. Porque Al-lah conoce bien vuestra fe; sois <em>todos</em> uno del otro; casaos, pues, con el permiso de sus tutores y entregadles sus dotes según lo que es justo, si son castas, no cometen fornicación ni tienen amantes secretos. Y si después de casadas, son culpables de adulterio, sufrirán la mitad del castigo prescrito para las mujeres libres. Esto es para aquel de vosotros que tema caer en el pecado. Pero si os abstenéis será mejor para vosotros; y Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 26 AND chapterNumber = 4;
UPDATE verses SET es = "Al-lah desea mostraros con claridad y guiaros por los caminos de quienes os precedieron, y volver a vosotros con misericordia. Y Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 27 AND chapterNumber = 4;
UPDATE verses SET es = "Y Al-lah desea volver a vosotros con misericordia, pero quienes siguen <em>sus </em>bajos deseos ansían extraviaros lejos." WHERE ayatNumber = 28 AND chapterNumber = 4;
UPDATE verses SET es = "Al-lah desea aligerar vuestra carga, porque el hombre ha sido creado débil." WHERE ayatNumber = 29 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! no devoréis vuestros bienes entre vosotros por medios ilícitos, excepto que lo ganéis por el comercio y por mutuo consentimiento. Y no os matéis vosotros mismos. En verdad, Al-lah es Misericordioso con vosotros." WHERE ayatNumber = 30 AND chapterNumber = 4;
UPDATE verses SET es = "Mas a quien lo haga con iniquidad e injusticia, lo haremos entrar en el Fuego: es cosa fácil para Al-lah." WHERE ayatNumber = 31 AND chapterNumber = 4;
UPDATE verses SET es = "Si os alejáis de las peores cosas de las que os están prohibidas, os absolveremos de vuestras faltas <em>menores</em> y os admitiremos en un lugar de gran honra." WHERE ayatNumber = 32 AND chapterNumber = 4;
UPDATE verses SET es = "Y no ambicionéis aquello en lo que Al-lah ha hecho que algunos superéis a otros. Los hombres tendrán una parte de lo que han ganado y las mujeres parte de lo que también han ganado. Pedid a Al-lah de Su magnanimidad. En verdad, Al-lah conoce perfectamente todas las cosas." WHERE ayatNumber = 33 AND chapterNumber = 4;
UPDATE verses SET es = "Y hemos instituido a cada uno heredero de lo que dejan sus padres y familiares, y <em>también</em> aquellos con quienes vuestros juramentos ratificaron un contrato. Dadles, pues, su parte. En verdad, Al-lah vigila todas las cosas." WHERE ayatNumber = 34 AND chapterNumber = 4;
UPDATE verses SET es = "Los hombres son protectores de las mujeres porque Al-lah ha hecho que algunos de ellos sobresalgan sobre otros y porque ellos (los hombres) gastan de sus bienes. Así pues, las mujeres virtuosas son las que son obedientes y guardan los secretos <em>de sus maridos</em> con la protección de Al-lah. En cuanto a aquéllas de las que temáis desobediencia, amonestadlas, dejadlas solas en sus lechos y castigadlas. Pero si después os obedecen, no busquéis otro camino contra ellas. Ciertamente, Al-lah es Excelso, Grande." WHERE ayatNumber = 35 AND chapterNumber = 4;
UPDATE verses SET es = "Y si teméis una ruptura entre ellos, nombrad a un árbitro de la gente de él y a otro de la de ella. Si desean la reconciliación, Al-lah la realizará entre ellos. En verdad, Al-lah es Omnisciente, el que Todo lo Sabe." WHERE ayatNumber = 36 AND chapterNumber = 4;
UPDATE verses SET es = "Y adorad a Al-lah y no asociéis nada a Él y <em>mostrad</em> bondad a los padres, a los parientes, a los huérfanos y necesitados, al vecino afín a vosotros y al extraño, al compañero que está <em>a vuestro</em> lado, al viajero y a los que poseen vuestras diestras. En verdad, Al-lah no ama a los orgullosos <em>ni</em> a los jactanciosos;" WHERE ayatNumber = 37 AND chapterNumber = 4;
UPDATE verses SET es = "Que son avaros y ordenan a los hombres ser tacaños, y ocultan lo que Al-lah les ha dado de Su magnanimidad. Pues para los incrédulos hemos preparado un castigo ignominioso;" WHERE ayatNumber = 38 AND chapterNumber = 4;
UPDATE verses SET es = "Y también para quienes gastan su dinero para ser vistos por los hombres y no creen en Al-lah ni en el Último Día. Y quien tenga a Satanás por compañero, <em>recordadle</em> qué pésimo compañero tiene." WHERE ayatNumber = 39 AND chapterNumber = 4;
UPDATE verses SET es = "Pero ¿qué daño les habría sobrevenido si hubiesen creído en Al-lah y en el Último Día y gastado de lo que Al-lah les ha dado? Y Al-lah les conoce muy bien." WHERE ayatNumber = 40 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, Al-lah no es injusto <em>con nadie ni</em> en el peso de un átomo. Y si se realizan buenas obras, Él las multiplica y concede por Su parte una magnífica recompensa." WHERE ayatNumber = 41 AND chapterNumber = 4;
UPDATE verses SET es = "Y ¿qué suerte correrán cuando traigamos a un testigo de cada pueblo y te presentemos a ti como testigo contra ellos?" WHERE ayatNumber = 42 AND chapterNumber = 4;
UPDATE verses SET es = "En ese día los que no creyeron al Mensajero y lo desobedecieron desearían que se les tragara la tierra, pero no <em>podrán</em> esconder nada a Al-lah." WHERE ayatNumber = 43 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! no os acerquéis a la Oración con la mente confusa hasta que sepáis lo que decís, ni cuando estéis impuros, y no recéis hasta que no os bañéis, excepto si estáis de viaje o nfermos. Mas si estáis enfermos o de viaje y <em>no estáis limpios</em>, o <em>si</em> uno de vosotros acaba de hacer sus necesidades o cohabitado con su mujer y no encuentra agua, recurrid a la arena pura y frotaos con ella la cara y las manos. En verdad, Al-lah es el Sumo Indulgente, Perdonador." WHERE ayatNumber = 44 AND chapterNumber = 4;
UPDATE verses SET es = "¿No has visto a quienes se entregó una parte del Libro? Compran el error y desean que vosotros os desviéis <em>también</em> del camino." WHERE ayatNumber = 45 AND chapterNumber = 4;
UPDATE verses SET es = "Pero Al-lah conoce muy bien a vuestros enemigos. Y Al-lah es suficiente como Amigo, y suficiente como Socorredor." WHERE ayatNumber = 46 AND chapterNumber = 4;
UPDATE verses SET es = "Hay entre los judíos quienes cambian las palabras de su lugar<em>adecuado</em> y dicen: “Oímos y desobedecemos”, “escucha <em>tú</em> sin ser oído” y “nuestro Pastor”, profiriendo con sus lenguas <em>lo que hay en sus mentes e intentando</em> injuriar la fe. Y si hubiesen dicho “oímos y obedecemos”, “escucha <em>tú” </em>y “Unzurna”, habría sido mejor y más recto para ellos. Pero Al-lah los maldijo por su incredulidad; porque no creen sino poco." WHERE ayatNumber = 47 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, Pueblo del Libro! creed en lo que hemos revelado, en cumplimiento de lo que tenéis antes de que inflijamos humillación a vuestros jefes y los hagamos volver sobre sus talones o los maldigamos omo hicimos con el pueblo del Sabbath. Y la sentencia de Al-lah <em>está destinada</em> a que se cumpla." WHERE ayatNumber = 48 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, Al-lah no perdonará que se le asocie nadie a Él; pero sí perdonará, exceptuado esto, a quien Le plazca, en lo que respecta a faltas menores. Pues quien asocia otras <em>divinidades</em> a Al-lah ha inventado, en verdad, un gravísimo pecado." WHERE ayatNumber = 49 AND chapterNumber = 4;
UPDATE verses SET es = "¿No has visto a los que se presentan a sí mismos como puros? No, es Al-lah quien purifica a quien le place, y no serán frustrados lo más mínimo." WHERE ayatNumber = 50 AND chapterNumber = 4;
UPDATE verses SET es = "¡Mira cómo traman una mentira contra Al-lah! Eso es suficiente como pecado manifiesto." WHERE ayatNumber = 51 AND chapterNumber = 4;
UPDATE verses SET es = "¿No has visto a quienes se entregó una parte del Libro? Creen en cosas malvadas, <em>siguen</em> a los pecadores y dicen de los incrédulos: “Son mejor guiados que los que creen en la religión”." WHERE ayatNumber = 52 AND chapterNumber = 4;
UPDATE verses SET es = "A ésos es a quienes Al-lah ha maldecido, y no podrás encontrar defensor para el que haya sido maldecido por Al-lah." WHERE ayatNumber = 53 AND chapterNumber = 4;
UPDATE verses SET es = "¿Tienen acaso parte en el reino? Aunque la tuvieran no darían a los hombres ni el pequeño hueco de un hueso de dátil." WHERE ayatNumber = 54 AND chapterNumber = 4;
UPDATE verses SET es = "¿O envidian a los hombres por lo que Al-lah les ha concedido de Su magnanimidad? <em>Si así es, </em>en verdad, dimos el Libro y la abiduría <em>también</em> a los hijos de Abraham, y les dimos <em>además</em> un gran reino." WHERE ayatNumber = 55 AND chapterNumber = 4;
UPDATE verses SET es = "Algunos de ellos creyeron en él; y otros se apartaron de él. Mas el Infierno bastará con su fuego llameante." WHERE ayatNumber = 56 AND chapterNumber = 4;
UPDATE verses SET es = "A quienes no crean en Nuestros Signos los introduciremos pronto en el Fuego. En cuanto se les queme la piel, les daremos una piel nueva para que sientan el castigo. En verdad, Al-lah es Poderoso, Sabio." WHERE ayatNumber = 57 AND chapterNumber = 4;
UPDATE verses SET es = "Pero a quienes crean y hagan buenas obras los introduciremos en jardines por los que corren ríos, para que habiten en ellos para siempre; allí tendrán conyuges purificadas <em>por Nosotros</em>; y los admitiremos en un <em>lugar</em> con una agradable y densa sombra." WHERE ayatNumber = 58 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, Al-lah os ordena devolver lo depositado a sus propietarios y que, cuando juzguéis entre hombres, lo hagáis con justicia. ¡Ciertamente es excelente aquello a lo que Al-lah os exhorta! Al-lah es quien todo lo oye y todo lo ve." WHERE ayatNumber = 59 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! obedeced a Al-lah, a <em>Su</em> Mensajero y a los que tienen autoridad sobre vosotros. Y si disputáis respecto a cualquier asunto, sometedlo a Al-lah y al Mensajero, si sois creyentes en Al-lah y en el Último Día. Esto es al final lo mejor y más recomendable." WHERE ayatNumber = 60 AND chapterNumber = 4;
UPDATE verses SET es = "¿No has visto a quienes pretenden creer en lo que te ha sido revelado y en lo revelado antes que a ti? Desean que los rebeldes dicten sentencia, aunque se les ordenó que no los obedecieran. Pero Satanás desea extraviarlos muy lejos." WHERE ayatNumber = 61 AND chapterNumber = 4;
UPDATE verses SET es = "Y cuando se les dice: “Volved a lo que Al-lah ha revelado y a <em>Su</em> Mensajero”, ves cómo los hipócritas se apartan de ti con desdén." WHERE ayatNumber = 62 AND chapterNumber = 4;
UPDATE verses SET es = "Si es así, ¿por qué cuando se abate la desgracia sobre ellos, debido a lo que sus propias manos hicieron antes, acuden a ti jurando por Al-lah <em>y diciendo: </em>“No queríamos más que un acto de amabilidad y de concordia”?" WHERE ayatNumber = 63 AND chapterNumber = 4;
UPDATE verses SET es = "En cuanto a ellos, Al-lah conoce muy bien los secretos de sus corazones. Apártate pues de ellos, exhórtales y dirígeles palabras persuasivas respecto a si mismos." WHERE ayatNumber = 64 AND chapterNumber = 4;
UPDATE verses SET es = "Y no hemos enviado a ningún Mensajero, salvo para que fuera obedecido por mandato de Al-lah. Y si hubieran acudido a ti cuando dañaron sus propias almas, pidiendo el perdón de Al-lah; y <em>si</em> el Mensajero hubiera pedido <em>también</em> perdón por ellos, iertamente hubieran encontrado a Al-lah Remisorio <em>con compasión</em> y Misericordioso." WHERE ayatNumber = 65 AND chapterNumber = 4;
UPDATE verses SET es = "Pero no, por tu Señor, no serán creyentes mientras no te hagan juez de todo lo que les separa y no encuentren en sus corazones objeción alguna a lo que tú decidas y se sometan con plena sumisión." WHERE ayatNumber = 66 AND chapterNumber = 4;
UPDATE verses SET es = "Pero si les hubiésemos ordenado: “Sacrificad vuestras vidas o abandonad vuestros hogares”, no lo habrían hecho excepto unos pocos de ellos; y, si hubiesen hecho lo que se les ordena, ciertamente hubiese sido mejor para ellos y les hubiera dado una mayor fuerza;" WHERE ayatNumber = 67 AND chapterNumber = 4;
UPDATE verses SET es = "Entonces les habríamos dado en verdad una magnífica recompensa Nuestra;" WHERE ayatNumber = 68 AND chapterNumber = 4;
UPDATE verses SET es = "Y los habríamos guiado, ciertamente, por el camino recto." WHERE ayatNumber = 69 AND chapterNumber = 4;
UPDATE verses SET es = "Pues quien obedece a Al-lah y a este Mensajero <em>suyo, </em> estará entre aquellos a quienes Al-lah ha concedido sus bendiciones; a saber: los Profetas, los Veraces, los Mártires y los Justos. ¡Qué excelente compañeros son éstos!" WHERE ayatNumber = 70 AND chapterNumber = 4;
UPDATE verses SET es = "Esta gracia procede de Al-lah y suficiente es Al-lah, el Omnisciente." WHERE ayatNumber = 71 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! tomad vuestras precauciones; y acto seguido avanzad en grupos separados o en bloque." WHERE ayatNumber = 72 AND chapterNumber = 4;
UPDATE verses SET es = "Mas hay entre vosotros quien se quedará rezagado, y si sufrís un revés, dirá: “En verdad, Al-lah ha sido generoso conmigo, ya que no estuve con ellos”." WHERE ayatNumber = 73 AND chapterNumber = 4;
UPDATE verses SET es = "Pero si os llega un favor procedente de Al-lah, dice, como si no existiera amor entre vosotros y él: “Ojalá hubiese estado con ellos, pues habría conseguido un gran éxito”." WHERE ayatNumber = 74 AND chapterNumber = 4;
UPDATE verses SET es = "Que combatan pues, por la causa de Al-lah quienes estén dispuestos a sacrificar la presente vida por el Más Allá. Y a quien luche por la causa de Al-lah, tanto si muere como si sale victorioso, le daremos pronto una recompensa." WHERE ayatNumber = 75 AND chapterNumber = 4;
UPDATE verses SET es = "Y ¿qué os ocurre <em>que </em>no combatís por la causa de Al-lah y de los débiles -hombres, mujeres y niños- que dicen: “Señor nuestro, sácanos de esta ciudad cuyos habitantes son opresores y consíguenos de Tu parte, un defensor”?" WHERE ayatNumber = 76 AND chapterNumber = 4;
UPDATE verses SET es = "Los creyentes combaten por la causa de Al-lah y los incrédulos por la causa del Maligno. ¡Combatid, pues, contra los amigos de Satanás; pues en verdad la estrategia de Satanás es débil!" WHERE ayatNumber = 77 AND chapterNumber = 4;
UPDATE verses SET es = "¿No has visto a quienes se les dijo: “Contened vuestras manos, cumplid la Oración y pagad el Zakat”? Mas cuando se les prescribió el combate, ¡mirad! una parte de ellos temen a los hombres como deberían temer a Al-lah e incluso con mayor temor; y dicen: “Señor nuestro, ¿por qué nos has prescrito el combate? ¿Por qué no nos concedes un poco más de tregua?” Diles: “El provecho de este mundo es mínimo y el Más Allá será mejor para quien teme <em>a Dios;</em> y no seréis perjudicados en lo más mínimo”." WHERE ayatNumber = 78 AND chapterNumber = 4;
UPDATE verses SET es = "Dondequiera que estéis os alcanzará la muerte, aunque os encontréis en torres inexpugnables. Si les sucede algo bueno dicen: “Esto procede de Al-lah”, pero si es una desgracia dicen: “Esto procede de ti”. Diles: “Todo procede de Al-lah”. ¿Qué le ha sucedido a esa gente que apenas comprende nada?" WHERE ayatNumber = 79 AND chapterNumber = 4;
UPDATE verses SET es = "Todo el bien que te acontece procede de Al-lah; y el mal que te aflige procede de ti mismo. Y te hemos enviado a la humanidad como Mensajero. Pues Al-lah basta como Testigo." WHERE ayatNumber = 80 AND chapterNumber = 4;
UPDATE verses SET es = "Quien obedece al Mensajero obedece ciertamente a Al-lah; en cuanto a los que vuelven la espalda: no te hemos enviado como guardián de ellos." WHERE ayatNumber = 81 AND chapterNumber = 4;
UPDATE verses SET es = "Y dicen: “La obediencia <em>es el principio que nos guía”;</em> pero cuando se retiran de tu presencia, una parte de ellos pasa la noche intrigando contra lo que tú dices. Mas Al-lah toma nota de cuanto traman por la noche. Aléjate, pues, de ellos y pon <em>tu</em> confianza en Al-lah. Al-lah es suficiente como Ordenador de asuntos." WHERE ayatNumber = 82 AND chapterNumber = 4;
UPDATE verses SET es = "¿Acaso no meditarán en el Corán? Si procediera de alguien que no fuera Al-lah, ciertamente hallarían en él muchas contradicciones." WHERE ayatNumber = 83 AND chapterNumber = 4;
UPDATE verses SET es = "Y cuando conjeturan <em>rumores respecto</em> a un asunto de paz o de alarma, los divulgan; mientras que si los hubieran transmitido al Mensajero y a quienes detentan la autoridad, en verdad <em>que</em> quienes hubieran examinado críticamente el tema, podrían saber la verdad. De no ser por la gracia de Al-lah para vosotros y por Su misericordia, habríais seguido a Satanás, salvo unos pocos." WHERE ayatNumber = 84 AND chapterNumber = 4;
UPDATE verses SET es = "Combatid, pues, por la causa de Al-lah –tú solo eres responsable de ti mismo- y exhorta a los creyentes. Tal vez Al-lah reprima el poder de los incrédulos; pues Al-lah es más fuerte en poder y más fuerte a la hora de infligir el castigo." WHERE ayatNumber = 85 AND chapterNumber = 4;
UPDATE verses SET es = "Quien interceda en una buena causa tendrá parte en ella, y quien lo haga en una causa inicua, compartirá sus <em>malas consecuencias</em>; pues Al-lah es Poderoso sobre todas las cosas." WHERE ayatNumber = 86 AND chapterNumber = 4;
UPDATE verses SET es = "Si recibís una expresión de amistad devolvedla en la misma medida, o incluso mayor. Ciertamente, Al-lah tiene en cuenta todas las cosas." WHERE ayatNumber = 87 AND chapterNumber = 4;
UPDATE verses SET es = "¡Al-lah! fuera de Él no existe nada que merezca adoración. Él <em>continuará</em> ciertamente congregándoos hasta el Día de la Resurrección, sobre el cual no hay duda alguna. Pues ¿quién es más verídico en su palabra que Al-lah?" WHERE ayatNumber = 88 AND chapterNumber = 4;
UPDATE verses SET es = "¿Qué os ha ocurrido que estáis divididos en dos bandos respecto a los hipócritas? Al-lah los derrocó por lo que habían merecido. ¿Os gustaría guiar a quien Al-lah ha hecho perecer? Y no encontraréis camino para quien Al-lah ha hecho perecer." WHERE ayatNumber = 89 AND chapterNumber = 4;
UPDATE verses SET es = "Desean que dejéis de creer, como dejaron de creer ellos, para que seáis todos iguales. No os haréis, pues, amigos de ninguno de ellos hasta que no hayan emigrado en el camino de Al-lah. Y si se alejan, capturadlos y matadlos dondequiera que los encontréis; y no toméis por amigo o por defensor a ninguno de ellos;" WHERE ayatNumber = 90 AND chapterNumber = 4;
UPDATE verses SET es = "Excepto los relacionados con un pueblo entre el cual y vosotros haya una alianza, o quienes vinieren a vosotros con sus corazones arrepentidos por haber combatido contra vosotros o contra su propia gente. Si Al-lah lo hubiera querido, les habría dado poder sobre vosotros, y ciertamente os habrían combatido. Por tanto, si se antienen alejados de vosotros, no os combaten y os ofrecen la paz,<em>recordad que</em> Al-lah no os permite ningún modo <em>de agresión</em> en su contra." WHERE ayatNumber = 91 AND chapterNumber = 4;
UPDATE verses SET es = "Encontraréis a otros que desean estar seguros respecto a vosotros y a su propia gente. Cada vez que se les hace renunciar a la hostilidad, caen de cabeza en ella. Así pues, si no se mantienen alejados de vosotros, no os ofrecen la paz ni retienen sus manos, capturadlos y matadlos dondequiera que los encontréis. Contra éstos os damos autoridad absoluta." WHERE ayatNumber = 92 AND chapterNumber = 4;
UPDATE verses SET es = "No es propio de un creyente matar a otro creyente, salvo que sea por error. Y quien mate a un creyente por error pondrá en libertad a un esclavo creyente, y <em>pagará</em> una indemnización para que sea entregada a sus herederos, a menos que éstos lo perdonen por caridad. ero si<em> la víctima</em> fuese de un pueblo enemigo, y es creyente, <em>el culpable</em> pondrá en libertad a un esclavo creyente, y si uese de un pueblo con el que tenéis un pacto, el <em>culpable pagará</em> una indemnización que será entregada a sus herederos y pondrá en libertad a un esclavo creyente. Mas quien no encuentre <em>uno,</em> ayunará durante dos meses consecutivos: como penitencia impuesta por Al-lah-. Pues Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 93 AND chapterNumber = 4;
UPDATE verses SET es = "Y quien mate intencionadamente a un creyente, su recompensa será el Infierno, y en él morará. Y Al-lah le mostrará Su ira, lo maldecirá y le preparará un severo castigo." WHERE ayatNumber = 94 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! cuando avancéis por la causa de Al-lah, informaos cuidadosamente y no digáis a quien os saluda con el saludo de la paz: “Tú no eres creyente”. Buscáis las cosas buenas de este mundo, pero <em>es </em>con Al-lah <em>donde </em>hay abundancia e cosas buenas. Así erais antes, pero Al-lah os otorgó Su favor <em>especial;</em> informaos pues cuidadosamente. En verdad, Al-lah es perfectamente consciente de lo que hacéis." WHERE ayatNumber = 95 AND chapterNumber = 4;
UPDATE verses SET es = "No son iguales los creyentes que se <em>quedan </em>sentados, exceptuados los incapacitados, que los que se sacrifican por la causa de Al-lah con sus bienes y personas. Al-lah ha exaltado en rango a uienes se sacrifican con sus bienes y personas respecto a los que se <em>quedan</em> sentados. Y aunque Al-lah ha prometido lo mejor a todos, ha exaltado a los que se sacrifican por encima de los que permanecen sentados, con una gran recompensa," WHERE ayatNumber = 96 AND chapterNumber = 4;
UPDATE verses SET es = "<em> Es decir, con</em> distintos grados de excelencia <em>concedidos</em> por Él, y con <em>especial </em>perdón y misericordia<em>;</em> pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 97 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, a quienes los ángeles hacen morir mientras están perjudicando sus propias almas, ellos (los ángeles) <em>les</em> dirán: “¿Qué buscabais?”. Responderán: “Fuimos tratados como débiles en la tierra”. Dirán: “¿No era la tierra de Al-lah suficientemente amplia para que pudierais emigrar?”. Éstos son los que morarán en el Infierno; ¡qué pésimo destino!;" WHERE ayatNumber = 98 AND chapterNumber = 4;
UPDATE verses SET es = "Excepto los hombres, mujeres y niños verdaderamente débiles, que son incapaces de preparar un plan o hallar algún camino." WHERE ayatNumber = 99 AND chapterNumber = 4;
UPDATE verses SET es = "Tal vez Al-lah suprima los pecados de éstos; porque Al-lah es Remisorio <em>y es </em>el Sumo Indulgente." WHERE ayatNumber = 100 AND chapterNumber = 4;
UPDATE verses SET es = "Mas quien emigre de su país por la causa de Al-lah, encontrará en la tierra un amplio lugar de refugio y abundancia. Y quien abandone su hogar, emigrando por la causa de Al-lah y de Su Mensajero, y le sorprenda la muerte, tendrá su recompensa en Al-lah, pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 101 AND chapterNumber = 4;
UPDATE verses SET es = "Y cuando viajéis por la tierra, no pecaréis si abreviáis la Oración por temor a que os hostiguen los incrédulos. En verdad, los incrédulos son vuestros enemigos declarados." WHERE ayatNumber = 102 AND chapterNumber = 4;
UPDATE verses SET es = "Y cuando estés entre ellos y les dirijas la Oración, que una parte de ellos permanezca de pie contigo y que tomen sus armas. Y cuando hayan realizado las postraciones, que se retiren detrás de ti y que otro grupo, que aún no haya orado, se adelante y ore contigo; y haz que tomen sus medios de defensa y sus armas. Los incrédulos desean que descuidéis vuestras armas y bagajes para poder caer sobre vosotros por sorpresa. Y no pecareis si dejáis vuestras armas porque os molesta la lluvia o estáis enfermos. Pero portad <em>siempre</em> vuestros medios de defensa. En verdad, Al-lah ha preparado un castigo ignominioso para los incrédulos." WHERE ayatNumber = 103 AND chapterNumber = 4;
UPDATE verses SET es = "Y cuando hayáis finalizado la Oración, recordad a Al-lah ya estéis de pie, sentados o <em>tumbados</em> sobre vuestros costados. Y cuando stéis fuera <em>de peligro,</em> cumplid la Oración <em>de la forma prescrita;</em> ciertamente la Oración se ordena a los creyentes <em>para que sea practicada</em> en los momentos prescritos." WHERE ayatNumber = 104 AND chapterNumber = 4;
UPDATE verses SET es = "Y no desfallezcáis cuando veáis a esas gentes. Si sufrís, ellos sufren tanto como vosotros. Pero vosotros esperáis de Al-lah lo que ellos no esperan. Pues Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 105 AND chapterNumber = 4;
UPDATE verses SET es = "Te hemos revelado ciertamente el Libro que contiene la verdad, para que juzgues entre los hombres por lo que Al-lah te ha enseñado. Y no defiendas la causa de los que traicionan la confianza." WHERE ayatNumber = 106 AND chapterNumber = 4;
UPDATE verses SET es = "Y pide perdón a Al-lah. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 107 AND chapterNumber = 4;
UPDATE verses SET es = "No abogues por quienes son deshonestos con ellos mismos. En verdad, Al-lah no ama a quien es pérfido y un gran pecador." WHERE ayatNumber = 108 AND chapterNumber = 4;
UPDATE verses SET es = "Procuran ocultarse de los hombres, pero no se cuidan de ocultarse de Al-lah; y Él está con ellos cuando pasan la noche tramando sobre asuntos que Él no aprueba. Pues Al-lah abarca todo cuanto hacen." WHERE ayatNumber = 109 AND chapterNumber = 4;
UPDATE verses SET es = "¡Mirad! sois vosotros los que abogasteis por ellos en esta vida. Pero ¿quién abogará por ellos ante Al-lah en el Día de la Resurrección, o quién será su defensor?" WHERE ayatNumber = 110 AND chapterNumber = 4;
UPDATE verses SET es = "Mas quien hace el mal o perjudica a su propia alma, y pide después perdón a Al-lah, comprobará <em>ciertamente</em> que Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 111 AND chapterNumber = 4;
UPDATE verses SET es = "Y quien comete un pecado sólo lo hace en perjuicio de su propia alma. Pues Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 112 AND chapterNumber = 4;
UPDATE verses SET es = "Y quien comete una falta o un pecado, y se lo imputa a un inocente, ciertamente soporta la carga de una calumnia y de un pecado flagrante." WHERE ayatNumber = 113 AND chapterNumber = 4;
UPDATE verses SET es = "Y si no fuera por la gracia de Al-lah contigo y por Su isericordia, un grupo de ellos había decidido extraviarte, <em>pero</em> <em>El frustró sus designios</em>. De hecho, no extravían a nadie salvo a si mismos y no pueden perjudicarte de modo alguno. Al-lah te ha revelado el Libro y la Sabiduría y te ha enseñado lo que desconocías, pues grande es la gracia de Dios para contigo." WHERE ayatNumber = 114 AND chapterNumber = 4;
UPDATE verses SET es = "No hay nada bueno que salga de sus reuniones secretas salvo cuando deciden gastar por la causa del pobre, en el bienestar público o en promover la reconciliación o reforma de las gentes. Y a quien así actúe, buscando el agrado de Al-lah, le concederemos pronto una magnífica recompensa." WHERE ayatNumber = 115 AND chapterNumber = 4;
UPDATE verses SET es = "En cuanto a quien se opone al Mensajero después de que se le ha mostrado claramente la guía y sigue un camino distinto al de los creyentes, lo dejaremos continuar por el camino que ha elegido y lo arrojaremos al Infierno; ¡qué pésimo destino!" WHERE ayatNumber = 116 AND chapterNumber = 4;
UPDATE verses SET es = "Al-lah no perdonará que se Le asocie ningún otro copartícipe, pero perdonará cualquier falta inferior a ésta a quien desee. Pues quien ponga otra cosa al nivel de Al-lah, en verdad se ha extraviado muy lejos." WHERE ayatNumber = 117 AND chapterNumber = 4;
UPDATE verses SET es = "No invocan, aparte de Él, mas que a falsas diosas; <em>en realidad</em> no invocan más que a Satanás, el rebelde," WHERE ayatNumber = 118 AND chapterNumber = 4;
UPDATE verses SET es = "A quien Al-lah ha maldecido. Él dijo: “Con toda seguridad me apoderaré de una parte determinada de Tus siervos”;" WHERE ayatNumber = 119 AND chapterNumber = 4;
UPDATE verses SET es = "“Y con toda seguridad los extraviaré y despertaré en ellos vanos deseos, y ciertamente les ordenaré que corten las orejas de los camellos <em>y otro tipo de ganado</em>, y ciertamente los incitaré y modificarán la creación de Al-lah”. Y quien tome a Satanás por amigo, en lugar de Al-lah, ciertamente sufrirá una enorme pérdida." WHERE ayatNumber = 120 AND chapterNumber = 4;
UPDATE verses SET es = "Les hace promesas y despierta en ellos vanos deseos, pero Satanás no les promete sino cosas vanas." WHERE ayatNumber = 121 AND chapterNumber = 4;
UPDATE verses SET es = "Son los que tendrán como morada el Infierno, y no encontrarán medio de salir de él." WHERE ayatNumber = 122 AND chapterNumber = 4;
UPDATE verses SET es = "En cambio, a quienes creen y hacen buenas obras los admitiremos en ardines por los que fluyen ríos, y habitarán allí para siempre. <em>Ésta es</em> la promesa indefectible de Al-lah; y ¿quién es más sincero que Al-lah en <em>Su</em> palabra?" WHERE ayatNumber = 123 AND chapterNumber = 4;
UPDATE verses SET es = "No será según vuestros deseos ni según los deseos del pueblo del Libro. Quien haga el mal recibirá su merecido; y fuera de Al-lah no encontrará amigo ni defensor para él." WHERE ayatNumber = 124 AND chapterNumber = 4;
UPDATE verses SET es = "Pero quien hace buenas obras, sea hombre o mujer, y es creyente, entrará en el Cielo y no será perjudicado ni tan siquiera en lo que ocupa el pequeño hueco que hay detrás de un hueso de dátil." WHERE ayatNumber = 125 AND chapterNumber = 4;
UPDATE verses SET es = "Y ¿quién es mejor en la fe que aquel que se somete a Al-lah, practica el bien y sigue la religión de Abraham, el recto? Pues Al-lah tomó a Abraham como amigo íntimo." WHERE ayatNumber = 126 AND chapterNumber = 4;
UPDATE verses SET es = "Y a Al-lah pertenece todo lo que hay en la tierra; pues Al-lah abarca todo." WHERE ayatNumber = 127 AND chapterNumber = 4;
UPDATE verses SET es = "Y te piden la decisión <em>de la Ley</em> a propósito de las mujeres. Diles: “Al-lah os da Su decisión respecto a ellas. E igual hace con lo que se os explica en el Libro sobre las huérfanas a las que o dais lo prescrito para ellas y con quien deseáis casaros, y <em>acerca </em>de los niños desamparados. Pues <em>Él os exhorta</em> a que tratéis con equidad a los huérfanos. Y todo el bien que hagáis, Al-lah en verdad lo conoce." WHERE ayatNumber = 128 AND chapterNumber = 4;
UPDATE verses SET es = "Y si una mujer teme malos tratos o indiferencia por parte de su marido, no hay ningún mal en que se reconcilien efectivamente. Pues la reconciliación es lo mejor. Cierto que las gentes son inclinadas a la varicia por naturaleza. Pero si practicáis el bien y sois piadosos, <em>sabed que</em>, en verdad, Al-lah conoce muy bien lo que hacéis." WHERE ayatNumber = 129 AND chapterNumber = 4;
UPDATE verses SET es = "Por mucho que lo deseéis, no podéis mantener una equidad <em>perfecta</em> entre <em>vuestras </em>mujeres a pesar de vuestras mejores intenciones. Pero no os inclinéis totalmente <em>hacia una</em> dejando a las demás en estado de incertidumbre, desatendidas y sin afecto. Y si os enmendáis y os reconciliáis, en verdad Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 130 AND chapterNumber = 4;
UPDATE verses SET es = "Mas si se separan, Al-lah los hará independientes a ambos, gracias a Su abundancia; pues Al-lah es Magnánimo, Sabio." WHERE ayatNumber = 131 AND chapterNumber = 4;
UPDATE verses SET es = "Y a Al-lah pertenece cuanto hay en los cielos y en la tierra. Y tanto a quienes se les entregó el Libro antes que a vosotros, como a vosotros mismos, os hemos ordenado temer a Al-lah. Pero si dejáis de creer, <em>recordad que</em> a Al-lah pertenece cuanto hay en los cielos y en la tierra, y Al-lah es Autosuficiente, Loable." WHERE ayatNumber = 132 AND chapterNumber = 4;
UPDATE verses SET es = "Pues a Al-lah pertenece cuanto hay en los cielos y en la tierra, y basta Al-lah como Protector." WHERE ayatNumber = 133 AND chapterNumber = 4;
UPDATE verses SET es = "Si a Él Le place, puede expulsaros, ¡Oh, humanos!, y poner a otros en <em>vuestro lugar;</em> porque Al-lah tiene pleno poder para hacer eso." WHERE ayatNumber = 134 AND chapterNumber = 4;
UPDATE verses SET es = "Quien desee la recompensa de este mundo, <em>que recuerde que</em> en Al-lah está la recompensa de este mundo y la del futuro; pues Al-lah es Quien todo lo Oye y todo lo Ve." WHERE ayatNumber = 135 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! sed estrictos en la observancia de la justicia, actuando de testigos por la causa de Al-lah, aunque sea contra vosotros mismos, vuestros padres y familiares. Sea rico o pobre, Al-lah está más atento a ambos <em>que vosotros.</em> No sigáis pues los bajos deseos para <em>que podáis</em> actuar con equidad. Y si ocultáis <em>la verdad o la</em> eludís, <em>recordad que</em> Al-lah conoce muy bien todo lo que hacéis." WHERE ayatNumber = 136 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! creed en Al-lah y en Su Mensajero, n el Libro que Él ha revelado a Su Mensajero, y en el que reveló antes <em>de este.</em> Pues quien no cree en Al-lah, Sus ángeles, Sus Libros, Sus Mensajeros y en el Último Día, ciertamente se ha extraviado muy lejos." WHERE ayatNumber = 137 AND chapterNumber = 4;
UPDATE verses SET es = "A quienes creen, dejan de creer, creen de nuevo, otra vez dejan de creer y luego aumentan en su incredulidad, Al-lah nunca los perdonará ni los guiará al camino recto." WHERE ayatNumber = 138 AND chapterNumber = 4;
UPDATE verses SET es = "Dad a los hipócritas la noticia de que sufrirán un espantoso castigo." WHERE ayatNumber = 139 AND chapterNumber = 4;
UPDATE verses SET es = "Quienes toman por amigos a incrédulos en lugar de creyentes ¿buscan acaso dignidad alguna de ellos? Pues <em>que recuerden que</em> toda dignidad pertenece a Al-lah." WHERE ayatNumber = 140 AND chapterNumber = 4;
UPDATE verses SET es = "Él ya os ha revelado en el Libro que, cuando oigáis que son negados y escarnecidos los Signos de Al-lah, no os sentéis con ellos mientras no inicien otra conversación; ya que, de no hacerlo, seríais como ellos. En verdad, Al-lah reunirá juntos, en el Infierno, a hipócritas e incrédulos;" WHERE ayatNumber = 141 AND chapterNumber = 4;
UPDATE verses SET es = "<em> Aquellos que esperan noticias</em> acerca de vosotros. Si obtenéis una victoria procedente de Al-lah, dicen: “¿No estábamos acaso on vosotros?”. Y si los incrédulos participan <em>en ella</em>, <em> les</em> dicen: “¿No recogimos lo mejor de vosotros y os protegimos contra los creyentes?”. Al-lah juzgará entre vosotros en el Día de la Resurrección; y Al-lah no concederá a los incrédulos ningún medio para prevalecer contra los creyentes." WHERE ayatNumber = 142 AND chapterNumber = 4;
UPDATE verses SET es = "Los hipócritas pretenden engañar a Dios, pero Él hará que se engañen a ellos mismos. Cuando se levantan para la Oración, lo hacen con indolencia y para ser" WHERE ayatNumber = 143 AND chapterNumber = 4;
UPDATE verses SET es = "Vacilando entre <em>esto</em> y aquello, sin <em>pertenecer</em> a estos ni a aquellos. Y si Al-lah hace que alguien se pierda, no encontrarás para él camino alguno." WHERE ayatNumber = 144 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! no toméis por amigos a los incrédulos, prefiriéndolos a los creyentes. ¿Queréis dar a Al-lah una prueba manifiesta contra vosotros mismos?" WHERE ayatNumber = 145 AND chapterNumber = 4;
UPDATE verses SET es = "Los hipócritas estarán con seguridad en lo más profundo del Fuego; y no podrás encontrarles defensor." WHERE ayatNumber = 146 AND chapterNumber = 4;
UPDATE verses SET es = "Excepto los que se arrepienten, se enmiendan, se aferran a Al-lah y son sinceros en su obediencia a Al-lah. Éstos están entre los creyentes. Y Al-lah concederá pronto una magnífica recompensa a los creyentes." WHERE ayatNumber = 147 AND chapterNumber = 4;
UPDATE verses SET es = "¿Por qué os habría de castigar Al-lah si sois agradecidos y creéis? Pues Al-lah es Agradecido, Omnisciente." WHERE ayatNumber = 148 AND chapterNumber = 4;
UPDATE verses SET es = "A Al-lah no le agrada que se profieran palabras impropias en público, excepto <em>por parte de</em> quien esté <em>siendo</em> perjudicado. En verdad, Al-lah lo Oye todo, <em>es </em>Omnisciente." WHERE ayatNumber = 149 AND chapterNumber = 4;
UPDATE verses SET es = "Tanto si ponéis de manifiesto como si ocultáis una buena obra, o si perdonáis el mal, <em>sabed que</em> Al-lah es ciertamente el Perdonador de pecados <em>y es</em> Omnipotente." WHERE ayatNumber = 150 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, quienes no creen en Dios ni en Sus Mensajeros y desean hacer distinción entre Al-lah y Sus Mensajeros, dicen: “Creemos en unos y no creemos en otros”, y quieren tomar un camino intermedio;" WHERE ayatNumber = 151 AND chapterNumber = 4;
UPDATE verses SET es = "Éstos son, en efecto, verdaderos incrédulos, y para los incrédulos hemos preparado un castigo infamante." WHERE ayatNumber = 152 AND chapterNumber = 4;
UPDATE verses SET es = "Y en cuanto a quienes creen en Al-lah y en Sus Mensajeros y no hacen distinción entre ninguno de ellos, es a éstos a quien Él dará pronto sus recompensas. Pues Al-lah es Sumo Indulgente, Misericordioso." WHERE ayatNumber = 153 AND chapterNumber = 4;
UPDATE verses SET es = "El pueblo del Libro te pide que hagas que les descienda un Libro desde el Cielo. Ya pidieron a Moisés algo más grande que esto cuando dijeron: “Muéstranos a Al-lah cara a cara”. Y al punto les sobrevino un astigo demoledor por su grave pecado. Entonces tomaron el becerro <em>para adorarlo</em> después de haberles llegado Signos evidentes, pero <em>incluso</em> eso les perdonamos. Y dimos a Moisés un argumento claro y decisivo." WHERE ayatNumber = 154 AND chapterNumber = 4;
UPDATE verses SET es = "E hicimos que el Monte se elevara sobre ellos mientras pactábamos una alianza, y les dijimos: Entrad por la puerta con sumisión”, y les dijimos: “No profanéis el Sabbath”. Y aceptamos su firme pacto." WHERE ayatNumber = 155 AND chapterNumber = 4;
UPDATE verses SET es = "Luego, por haber roto su pacto, por negar los Signos de Al-lah, por intentar matar injustamente a los Profetas y por decir: “Nuestros corazones están cubiertos”, -¡No!: Al-lah los ha sellado por su incredulidad, por no creer sino poco-" WHERE ayatNumber = 156 AND chapterNumber = 4;
UPDATE verses SET es = "Y debido a su incredulidad y por haber dirigido contra María una gravísima calumnia," WHERE ayatNumber = 157 AND chapterNumber = 4;
UPDATE verses SET es = "Y por decir: “Hemos matado al Mesías, Jesús, hijo de María, el Mensajero de Al-lah”; cuando en realidad no lo asesinaron, ni lo ataron por crucifixión, sino que apareció ante ellos como <em>crucificado;</em> pero los que discrepan al respecto se encuentran, en verdad, en duda sobre esta cuestión; no tienen un conocimiento directo de ella, sino que sólo se apoyan en conjeturas; pero ciertamente ellos no lo mataron." WHERE ayatNumber = 158 AND chapterNumber = 4;
UPDATE verses SET es = "Por el contrario, Al-lah lo exaltó hacia Él. Pues Al-lah es Poderoso, Sabio." WHERE ayatNumber = 159 AND chapterNumber = 4;
UPDATE verses SET es = "Y no hay secta o grupo de entre el pueblo del Libro que no crea en él antes de su muerte; y en el Día de la Resurrección, él (Jesús) será un testigo en su contra." WHERE ayatNumber = 160 AND chapterNumber = 4;
UPDATE verses SET es = "Así, debido a la trasgresión de los judíos, les prohibimos cosas puras que les habían sido permitidas, y <em>también</em> por haber extraviado a muchos <em>hombres </em>del camino de Al-lah," WHERE ayatNumber = 161 AND chapterNumber = 4;
UPDATE verses SET es = "Y <em>por</em> cobrar intereses, aunque se les había prohibido, y <em>por </em>devorar injustamente los bienes de las gentes. Pero para los incrédulos hemos preparado un doloroso castigo." WHERE ayatNumber = 162 AND chapterNumber = 4;
UPDATE verses SET es = "Pero aquellos de entre ellos que están firmemente apoyados en el conocimiento, y los creyentes, creen lo que te ha sido revelado y en lo que fue revelado antes de ti; y<em> especialmente</em> aquellos que cumplen la Oración, los que pagan el <em>Zakat</em> y los que creen en Al-lah y en el Último Día. En verdad, a éstos les daremos una gran recompensa." WHERE ayatNumber = 163 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, te hemos mandado la revelación, como revelamos a Noé y a los Profetas que le siguieron; y enviamos la revelación a Abraham, Ismael, Isaac, Jacob y <em>sus </em>hijos; y a Jesús, Job, Jonás, Aarón y Salomón, y dimos a David los Salmos." WHERE ayatNumber = 164 AND chapterNumber = 4;
UPDATE verses SET es = "Y <em>enviamos algunos</em> Mensajeros que te hemos mencionado y otros que no te hemos mencionado –y Al-lah habló a Moisés transmitiéndole los Mandamientos-" WHERE ayatNumber = 165 AND chapterNumber = 4;
UPDATE verses SET es = "Mensajeros, portadores de buenas nuevas y advertencias, para que as gentes no tuvieran argumento contra Al-lah después <em>del advenimiento</em> de los Mensajeros. Pues Al-lah es Poderoso, Sabio." WHERE ayatNumber = 166 AND chapterNumber = 4;
UPDATE verses SET es = "Pero Al-lah da testimonio, por medio de <em>la revelación</em> que te envió, que El la hizo descender <em>cargada</em> de Su ciencia, y los ángeles también son testigos; aunque Al-lah basta como Testigo." WHERE ayatNumber = 167 AND chapterNumber = 4;
UPDATE verses SET es = "Los incrédulos que apartan a <em>otros</em> del camino de Al-lah, ciertamente se han extraviado muy lejos." WHERE ayatNumber = 168 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, Al-lah no perdonará a los incrédulos que han actuado inicuamente, ni les guiará por camino alguno." WHERE ayatNumber = 169 AND chapterNumber = 4;
UPDATE verses SET es = "Excepto el camino del Infierno, en donde morarán un larguísimo período. Pues eso es fácil para Al-lah." WHERE ayatNumber = 170 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh, humanos! el Mensajero ha venido a vosotros con la Verdad de vuestro Señor; creed, pues; <em>será</em> mejor para vosotros. Pero si no creéis, <em>sabed que,</em> en verdad, a Al-lah pertenece cuanto hay en los cielos y en la tierra. Pues Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 171 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh pueblo del Libro! no rebaséis los límites de vuestra religión y no digáis de Al-lah más que la verdad. En verdad, el Mesías, Jesús, ijo de María, fue sólo un Mensajero de Al-lah y <em>el cumplimiento de </em>Su palabra que reveló a María, y una gracia procedente de Él. Creed, <em>pues,</em> en Al-lah y en Sus Mensajeros y no digáis: “<em>Son</em> tres”. Desistid; <em>será </em>mejor para vosotros. En verdad, Al-lah es el sólo Dios Único. Lejos está de Su Santidad que haya de tener un hijo. A Él pertenece cuanto hay en los cielos y en la tierra. Y Al-lah es suficiente como Protector." WHERE ayatNumber = 172 AND chapterNumber = 4;
UPDATE verses SET es = "En verdad, el Mesías no desdeñará nunca ser siervo de Al-lah, como tampoco los ángeles que están cerca de <em>Dios;</em> mas quienes desdeñan adorarle y caen en el orgullo, <em>sepan que</em> los reunirá a todos junto a Él." WHERE ayatNumber = 173 AND chapterNumber = 4;
UPDATE verses SET es = "En cambio, a los que han creído y hecho buenas obras, les dará su recompensa completa y les entregará más de Su magnanimidad; pero a los desdeñosos y orgullosos los castigará con un doloroso castigo. Y fuera de Al-lah no encontrarán amigo ni defensor alguno." WHERE ayatNumber = 174 AND chapterNumber = 4;
UPDATE verses SET es = "¡Oh humanos!, ciertamente os ha llegado una prueba manifiesta de vuestro Señor, y os hemos revelado una Luz evidente." WHERE ayatNumber = 175 AND chapterNumber = 4;
UPDATE verses SET es = "En cuanto a quienes creen en Al-lah y se aferran a Él, ciertamente Él los admitirá a Su misericordia y gracia, y los guiará por el camino recto <em>que lleva</em> hasta Él mismo." WHERE ayatNumber = 176 AND chapterNumber = 4;
UPDATE verses SET es = "Te piden instrucciones. Diles: “Al-lah os da Sus instrucciones respecto al ´Kalalah. Si un hombre muere sin hijos y tiene una hermana, ella recibirá la mitad de cuanto deje; y él heredará de ella si ésta no tiene hijos. Pero si hubiese dos hermanas, recibirán dos tercios de cuanto deje. Y si <em>los herederos</em> son hermanos –hombres y mujeres- entonces el varón recibirá la parte de dos hembras. Al-lah os explica <em>esto</em> para que no os extraviéis, pues Al-lah conoce perfectamente todas las cosas”." WHERE ayatNumber = 177 AND chapterNumber = 4;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Cumplid <em>vuestros</em> pactos. Se os permiten los cuadrúpedos de <em>la clase</em> del ganado, excepto los que se os comunica; pero no debéis considerar lícita la caza mientras estáis en estado de peregrinación; en verdad, Al-lah ordena lo que desea." WHERE ayatNumber = 2 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! No profanéis lo que Al-lah ha santificado, ni el Monte Sagrado, ni los animales llevados como ofrenda, ni <em>los animales para el sacrificio que lleven</em> collares, ni a quienes se dirigen a la Casa Sagrada, buscando la gracia de su Señor y su agrado. Pero cuando os quitéis las vestiduras de peregrinos <em>y estéis fuera del Territorio Sagrado, </em>podéis cazar. Y que la enemistad de un pueblo, que os puso trabas para entrar en la Mezquita Sagrada, no os incite a transgredir. Más bien ayudaos mutuamente en justicia y piedad; pero no en el pecado y la trasgresión. Y temed a Al-lah; en verdad, Al-lah es severo en el castigo." WHERE ayatNumber = 3 AND chapterNumber = 5;
UPDATE verses SET es = "Se os prohíbe <em>la carne de un animal</em> que muera por sí mismo y la sangre y la carne del cerdo; la de los <em>sacrificados</em> invocando un nombre distinto del de Al-lah; la del que ha sido estrangulado, la del que ha sido golpeado hasta la muerte; la del que ha muerto a causa de una caída y del corneado hasta la muerte; la del que ha sido parcialmente devorado por una fiera, salvo que lo hayáis sacrificado adecuadamente; y los <em>animales</em> sacrificados en un altar. Y <em>se os prohíbe asimismo</em> que intentéis conocer vuestra uerte mediante flechas adivinatorias. <em>Todo ello</em> es un<em>acto de</em> desobediencia. Hoy los incrédulos se desesperan por <em>no poder perjudicar</em> a vuestra religión. No los temáis pues; más bien temedme a Mí. Hoy os he perfeccionado vuestra religión y he completado Mi gracia con vosotros y os he elegido el Islam como religión. Pero quien se vea obligado por el hambre, sin tener intención de pecar, <em>sepa que</em>, en verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 4 AND chapterNumber = 5;
UPDATE verses SET es = "Te preguntan sobre qué les es lícito. Diles: “Todas las cosas buenas s son lícitas; y lo que habéis enseñado a <em>cobrar para vosotros</em> a los animales y pájaros de presa, adiestrándolos para cazar y enseñándoles lo que Al-lah os ha enseñado. Comed, pues, de lo que ellos cacen para vosotros, pero invocad sobre ellos el nombre de Al-lah. Y temed a Al-lah. En verdad, Al-lah es rápido en las cuentas." WHERE ayatNumber = 5 AND chapterNumber = 5;
UPDATE verses SET es = "Hoy todas las cosas buenas os son lícitas. Y los alimentos del Pueblo del Libro son lícitos para vosotros, y vuestros alimentos lo son para ellos. Y <em>os están permitidas</em> las mujeres creyentes castas y las mujeres castas de los que recibieron el Libro antes que vosotros, si les entregáis sus dotes, contraéis un matrimonio válido y no cometéis fornicación ni mantenéis amantes secretos. Mas quien rechace la fe, sus obras quedarán, sin duda alguna, reducidas a la nada, y en el Más Allá, estará entre los perdedores." WHERE ayatNumber = 6 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Cuando os levantéis para la Oración, lavaos la cara y las manos hasta los codos, frotaos la cabeza con las manos <em>mojadas</em> y <em>lavaos</em> los pies hasta los tobillos. Y si estáis impuros, purificaos <em>con el baño.</em> Y si estáis enfermos o de viaje <em>en estado impuro, </em>o uno de vosotros sale del retrete, o habéis tocado a las mujeres y no encontráis agua, recurrid a la arena pura y frotaos con ella las caras y las manos. Al-lah no desea poneros en dificultades, sino que desea purificaros y completar Su gracia sobre vosotros, para que seáis agradecidos." WHERE ayatNumber = 7 AND chapterNumber = 5;
UPDATE verses SET es = "Y recordad el favor de Al-lah para con vosotros y el pacto que estableció con vosotros, cuando dijisteis: “Oímos y obedecemos”. Y temed a Al-lah. En verdad, Al-lah conoce bien cuanto hay en las mentes." WHERE ayatNumber = 8 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Sed perseverantes en la causa de Al-lah en calidad de testigos justos; y que la enemistad de un pueblo no os incite a actuar con injusticia. Sed <em>siempre</em> justos, porque eso está más cerca de la virtud. Y temed a Al-lah. En verdad, Al-lah es consciente de lo que hacéis." WHERE ayatNumber = 9 AND chapterNumber = 5;
UPDATE verses SET es = "Al-lah ha prometido a quienes creen y hacen buenas obras que obtendrán el perdón y una magnífica recompensa." WHERE ayatNumber = 10 AND chapterNumber = 5;
UPDATE verses SET es = "En <em>cuanto a </em>los que no creen y rechazan nuestros Signos, ellos son el pueblo del Infierno." WHERE ayatNumber = 11 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! recordad el favor de Al-lah para con vosotros cuando un pueblo intentó poneros las manos encima, pero Él apartó sus manos de vosotros; y temed a Al-lah. Pues en Al-lah deben confiar los creyentes." WHERE ayatNumber = 12 AND chapterNumber = 5;
UPDATE verses SET es = "En verdad Al-lah estableció un pacto con los hijos de Israel; y suscitamos de entre ellos a doce portavoces. Al-lah dijo: “Ciertamente estoy con vosotros. Si cumplís la Oración, pagáis el <em>Zakat, </em> creéis en mis Mensajeros y los apoyáis, y hacéis a Al-lah una ofrenda generosa, os borraré vuestras faltas y os admitiré en jardines por los que corren ríos. Pero si alguno de vosotros deja de creer después de esto, se aparta ciertamente del camino recto”." WHERE ayatNumber = 13 AND chapterNumber = 5;
UPDATE verses SET es = "Así, por el hecho de romper su pacto, los hemos maldecido y hemos ndurecido sus corazones. Ellos cambian las palabras de su lugar <em>correcto</em> y han olvidado una <em>buena</em> parte de aquello con lo que fueron exhortados. Y tú no dejarás de descubrir traiciones suyas, salvo<em> de</em> unos pocos de ellos. Perdónalos, pues, y ten paciencia con ellos. En verdad, Al-lah ama a quienes hacen el bien." WHERE ayatNumber = 14 AND chapterNumber = 5;
UPDATE verses SET es = "Y <em>también</em> a los que dicen: “somos cristianos”, les restablecimos su alianza, pero han olvidado <em>igualmente buena</em> parte de aquello con lo que fueron exhortados. Así pues, hicimos que la enemistad y el odio mutuo les acompañaran hasta el Día de la Resurrección. Y Al-lah les hará conocer pronto lo que han estado haciendo." WHERE ayatNumber = 15 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh, pueblo del Libro! ha llegado a vosotros Nuestro Mensajero que os explica gran parte de lo que habíais mantenido oculto del Libro y os perdona mucho. Ciertamente os ha llegado de Al-lah una Luz y un Libro lúcido." WHERE ayatNumber = 16 AND chapterNumber = 5;
UPDATE verses SET es = "Por el cual Al-lah guía a los que buscan Su agrado por los caminos de la paz, los saca de toda <em>clase de</em> tinieblas a la luz con Sus bendiciones y los guía al camino recto." WHERE ayatNumber = 17 AND chapterNumber = 5;
UPDATE verses SET es = "Ciertamente han caído en la incredulidad los que dicen: “En verdad, Al-lah no es otro que el Mesías, hijo de María”. Diles: “¿Quién tendría poder alguno contra Al-lah, si Él deseara aniquilar al Mesías, hijo de María, a su madre y a todos los que están en la tierra?”. Porque a Al-lah pertenecen el reino de los cielos, la tierra y cuanto hay entre ellos. Él crea lo que le place, pues Al-lah tiene poder para hacer todas las cosas." WHERE ayatNumber = 18 AND chapterNumber = 5;
UPDATE verses SET es = "Los judíos y los cristianos dicen: “Somos hijos de Al-lah y Sus predilectos”. Diles: “¿Por qué, pues, Él os castiga por vuestros pecados? No, <em>sólo</em> sois seres humanos <em>de</em> entre los creados por Él”. Y Él perdona a quien Le place y castiga a quien desea; pues a Al-lah pertenecen el reino de los cielos, la tierra y cuanto hay entre ellos, y a Él habrán de volver." WHERE ayatNumber = 19 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh, pueblo del Libro! ha llegado a vosotros Nuestro Mensajero, después de una interrupción en <em>la serie</em> de Mensajeros, para aclararos las <em>cosas,</em> no sea que digáis: “no nos ha llegado ningún portador de buenas nuevas ni amonestador alguno”. Por tanto os ha llegado en verdad un portador de buenas nuevas y un amonestador. Pues Al-lah tiene poder para hacer todas las cosas." WHERE ayatNumber = 20 AND chapterNumber = 5;
UPDATE verses SET es = "Y <em>acordaos</em> de cuando Moisés dijo a su pueblo: “¡Oh, pueblo mío!, acordaos del favor de Al-lah para con vosotros cuando nombró Profetas de entre vosotros, os hizo reyes y os dio lo que no había dado a ningún otro pueblo de todo el mundo." WHERE ayatNumber = 21 AND chapterNumber = 5;
UPDATE verses SET es = "“¡Oh, pueblo mío! entrad en la Tierra Santa que Al-lah os ha preparado y no os deis la vuelta porque entonces seríais como los desventurados”." WHERE ayatNumber = 22 AND chapterNumber = 5;
UPDATE verses SET es = "Dijeron: “¡Oh, Moisés!”, hay en esa <em>tierra</em> un pueblo cruel y poderoso, y no entraremos en ella mientras no se hayan ido. Si la abandonan, nosotros entraremos." WHERE ayatNumber = 23 AND chapterNumber = 5;
UPDATE verses SET es = "En cuyo momento dos hombres de los que temían a <em>su Señor, </em> y a quienes Al-lah había concedido Su favor, dijeron: “entrad por la puerta, <em>avanzando</em> contra ellos; <em>una vez</em> que hayáis entrado seréis sin duda victoriosos. Y poned vuestra confianza en Al-lah si sois creyentes”." WHERE ayatNumber = 24 AND chapterNumber = 5;
UPDATE verses SET es = "Ellos dijeron: “¡Oh, Moisés!, no entraremos nunca mientras permanezcan en ella. Por tanto, id tú y tu Señor a luchar y nosotros permaneceremos aquí sentados”." WHERE ayatNumber = 25 AND chapterNumber = 5;
UPDATE verses SET es = "<em> Moisés</em> dijo: “Señor mío, sólo tengo poder sobre mí mismo y sobre mi hermano; sepáranos, pues, de ese pueblo rebelde”." WHERE ayatNumber = 26 AND chapterNumber = 5;
UPDATE verses SET es = "<em> Dios</em> dijo: “En verdad, les estará prohibida durante cuarenta años; vagarán errantes por la tierra. No te atormentes, pues, por ese pueblo rebelde”." WHERE ayatNumber = 27 AND chapterNumber = 5;
UPDATE verses SET es = "Y relátales verazmente la historia de los dos hijos de Adán, cuando <em>cada uno de </em>ellos ofreció una ofrenda, siendo aceptada la de no y no la del otro. Éste dijo: “ciertamente que te mataré”. <em>El primero</em> respondió: “Al-lah sólo acepta la <em>ofrenda</em> de los piadosos”." WHERE ayatNumber = 28 AND chapterNumber = 5;
UPDATE verses SET es = "“Si levantas tu mano para matarme, yo no levantaré la mía para matarte. Temo ciertamente a Al-lah, el Señor del Universo;" WHERE ayatNumber = 29 AND chapterNumber = 5;
UPDATE verses SET es = "“Desearía, más bien, que cargaras con mi pecado además de cargar con el tuyo, para que seas uno de los moradores del Fuego, pues ésa es la recompensa de los inicuos”." WHERE ayatNumber = 30 AND chapterNumber = 5;
UPDATE verses SET es = "Pero su conciencia le hizo aceptable matar a su hermano, y lo mató y se convirtió en <em>uno</em> de los desventurados." WHERE ayatNumber = 31 AND chapterNumber = 5;
UPDATE verses SET es = "Entonces envió Al-lah un cuervo que se puso a escarbar en la tierra para mostrarle cómo esconder el cadáver de su hermano. Dijo: “¡Ay de mí! ¿no seré capaz siquiera de ser como este cuervo, y ocultar el cadáver de mi hermano?”. Entonces se sintió arrepentido." WHERE ayatNumber = 32 AND chapterNumber = 5;
UPDATE verses SET es = "Por este motivo, prescribimos a los hijos de Israel que quien matara a una persona –salvo que fuera por <em>asesinar</em> a otra persona o por sembrar la discordia en el país- sería como si hubiese matado a toda la humanidad; mas quien diera la vida a uno sería como si hubiese dado la vida a toda la humanidad. Y nuestros Mensajeros llegaron a ellos con Signos claros, pero a pesar de eso muchos de ellos cometen excesos en la tierra." WHERE ayatNumber = 33 AND chapterNumber = 5;
UPDATE verses SET es = "La retribución de quienes hacen la guerra contra Al-lah y Su Mensajero e intentan sembrar la discordia en la tierra <em>sólo</em> será <em>ésta:</em> que sean ajusticiados o crucificados, o que se les corte las manos y los pies alternativos o que sean expulsados del país. Será una desventura para ellos en este mundo, y en el Futuro sufrirán un gran castigo;" WHERE ayatNumber = 34 AND chapterNumber = 5;
UPDATE verses SET es = "Excepto quienes se arrepientan antes de caer en vuestras manos. Sabed, pues, que Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 35 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! temed a Al-lah y buscad la manera de acercaros a Él y luchad por Su causa para que prosperéis." WHERE ayatNumber = 36 AND chapterNumber = 5;
UPDATE verses SET es = "Por cierto que si los incrédulos tuviesen todo lo que hay en la tierra, e <em>incluso</em> otro tanto, para rescatarse con ello del castigo el Día de la Resurrección, no se les aceptaría; y sufrirán un castigo doloroso." WHERE ayatNumber = 37 AND chapterNumber = 5;
UPDATE verses SET es = "Querrán salir del Fuego, pero no podrán salir, y sufrirán un castigo duradero." WHERE ayatNumber = 38 AND chapterNumber = 5;
UPDATE verses SET es = "Y en <em>cuanto al</em> ladrón y a la ladrona, cortadle las manos en pago de su delito, como castigo ejemplar de Al-lah. Pues Al-lah es Poderoso, Sabio." WHERE ayatNumber = 39 AND chapterNumber = 5;
UPDATE verses SET es = "Pero quien se arrepiente después de su trasgresión y se enmienda, Al-lah en verdad se volverá hacia él con misericordia; ciertamente, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 40 AND chapterNumber = 5;
UPDATE verses SET es = "¿Ignoras acaso que Al-lah es Aquel a Quien pertenece el Reino de los cielos y la tierra? Él castiga a quien desea y perdona a quien Le place; pues Al-lah tiene poder para hacer todas las cosas." WHERE ayatNumber = 41 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh, Mensajero! que no te aflijan quienes caen rápidamente en la incredulidad; los que dicen con sus bocas: “creemos”, pero sus corazones no creen. Y hay <em>también</em> entre los judíos quienes están ansiosos por escuchar mentiras –y te escuchan simplemente para ponerlo en conocimiento de los que no han acudido a ti.- Tergiversan las palabras después de puestas en su lugar <em>adecuado</em> y dicen: “Si os dan esto, aceptadlo, pero si no ¡cuidado!”. En cuanto a quien Al-lah desea probar, tú no podrás hacer nada por él contra Al-lah. Son aquellos cuyos corazones Al-lah no desea purificar; tendrán la deshonra en este mundo, y en el Más Allá un terrible castigo." WHERE ayatNumber = 42 AND chapterNumber = 5;
UPDATE verses SET es = "Son los que habitualmente prestan oídos a la mentira y disfrutan con los bienes ilícitos. Si acuden a ti <em>para que sentencies,</em> juzga entre ellos o aléjate de ellos. Si te apartas de ellos, no pueden perjudicarte en absoluto. Pero si los juzgas, hazlo con justicia. En verdad, Al-lah ama a los justos." WHERE ayatNumber = 43 AND chapterNumber = 5;
UPDATE verses SET es = "Pero ¿cómo van a tomarte por juez si tienen con ellos la Torah, en donde está el decreto de Dios? No obstante, a pesar de eso, vuelven la espalda; y ciertamente no creerán." WHERE ayatNumber = 44 AND chapterNumber = 5;
UPDATE verses SET es = "En verdad, revelamos la Torah, en la que había guía y luz. Los Profetas que <em>Nos</em> obedecían juzgaron con ella a los judíos, al igual que los hombres piadosos y los doctos <em>en la Ley</em>; porque e les había encomendado la preservación del Libro de Al-lah y <em>porque</em> eran sus custodios. No temáis, pues, a los hombres, sino a Mí; y no vendáis mis Signos a un precio ridículo. Mas quienes no juzguen de acuerdo con lo que Al-lah ha revelado, ésos se contarán entre los incrédulos." WHERE ayatNumber = 45 AND chapterNumber = 5;
UPDATE verses SET es = "Y en ella les prescribimos: vida por vida, ojo por ojo, nariz por nariz, oreja por oreja y diente por diente, y para <em>otras</em> heridas una retribución justa; pero quien renuncie a su derecho, le servirá como expiación por sus <em>pecados</em>; mas quienes no juzguen de acuerdo con lo que Al-lah ha revelado, ésos se contarán entre los inicuos." WHERE ayatNumber = 46 AND chapterNumber = 5;
UPDATE verses SET es = "E hicimos que Jesús, hijo de María, siguiera sus huellas, cumpliendo lo que había sido <em>revelado</em> en la Torah antes de él; y le dimos el Evangelio que encerraba guía y luz, cumpliendo lo que había sido <em>revelado</em> en la Torah antes de él, y como guía y exhortación para los que temen a Dios." WHERE ayatNumber = 47 AND chapterNumber = 5;
UPDATE verses SET es = "Que el pueblo del Evangelio juzgue según lo que Al-lah ha revelado en él, y quienes no juzguen de acuerdo con lo que Al-lah ha revelado, ésos se contarán entre los rebeldes." WHERE ayatNumber = 48 AND chapterNumber = 5;
UPDATE verses SET es = "Y te hemos revelado el Libro que contiene la verdad y cumple lo revelado antes de él en el Libro, y como guardián suyo. Juzga, pues, entre ellos de acuerdo con lo que Al-lah ha revelado y no sigas sus malas inclinaciones <em>desviándote</em> de la verdad que te ha sido entregada. A cada uno de vosotros prescribimos una Ley espiritual diáfana y un camino. Y si Al-lah hubiese ejecutado Su voluntad, os abría reunido a <em>todos</em> en una sola nación, pero <em>Él desea</em> probaros de acuerdo con lo que os ha entregado. Competid pues mutuamente, en las buenas obras. Todos volveréis a Al-lah y Él os hará saber en qué discrepabais." WHERE ayatNumber = 49 AND chapterNumber = 5;
UPDATE verses SET es = "Y<em> te hemos revelado el Libro ordenándote</em> que juzgues entre ellos de acuerdo con lo que Al-lah te ha revelado, y no sigas sus malas inclinaciones, y para que estés en guardia contra ellos, no sea que te hagan caer en aflicción debido a una parte de lo que Al-lah ya te ha revelado. Pero si se alejan, sabe entonces que Al-lah quiere castigarlos por algunos de sus pecados. Porque en realidad un gran número de hombres son desobedientes." WHERE ayatNumber = 50 AND chapterNumber = 5;
UPDATE verses SET es = "¿Buscan acaso el juicio de <em>los días de </em>la Ignorancia? Mas ¿quién es mejor que Al-lah como juez de un pueblo que se mantiene firme en la fe?" WHERE ayatNumber = 51 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! no toméis por amigos a judíos ni a cristianos. Ellos son amigos entre sí. Mas si alguno de vosotros los toma por amigos, es, en verdad, uno de ellos. Ciertamente, Al-lah no guía a los injustos." WHERE ayatNumber = 52 AND chapterNumber = 5;
UPDATE verses SET es = "Y verás a los que tienen el corazón enfermo, apresurarse hacia ellos diciendo: “tememos que nos alcance una desgracia”. Tal vez Al-lah traiga la victoria o algún <em>otro</em> acontecimiento procedente de Él. Entonces se arrepentirán de lo que ocultaron en sus mentes." WHERE ayatNumber = 53 AND chapterNumber = 5;
UPDATE verses SET es = "Y los creyentes dirán: “¿Son éstos los que juraron por Al-lah, con su juramento más solemne, que de verdad estaban con vosotros?”. Sus trabajos son inútiles y se han convertido en perdedores." WHERE ayatNumber = 54 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! quienes de vosotros renieguen de su religión, <em>que sepan que, en su lugar,</em> Al-lah traerá pronto a un pueblo al que Él amará y que le amará a Él, y <em>que serán</em> amables y humildes con los creyentes, pero duros y firmes con los incrédulos. Se sacrificarán por la causa de Al-lah y no temerán el reproche del que se dedica a censurar. Ésa es la gracia de Al-lah; Él la concede a quien le place; pues Al-lah es Magnánimo, Omnisciente." WHERE ayatNumber = 55 AND chapterNumber = 5;
UPDATE verses SET es = "Vuestro amigo es sólo Al-lah y Su Mensajero y los creyentes que cumplen a oración y pagan el Zakat, inclinándose ante El <em>con absoluta sinceridad.</em>" WHERE ayatNumber = 56 AND chapterNumber = 5;
UPDATE verses SET es = "Y quienes toman a Al-lah, a Su Mensajero y a los creyentes por amigos, <em>pueden tener la seguridad de</em> que es el partido de Al-lah el que ha de triunfar." WHERE ayatNumber = 57 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! No toméis por amigos a quienes hacen de vuestra religión motivo de chanzas y burlas de entre los que recibieron el Libro antes que vosotros, ni a los incrédulos. Mas temed a Al-lah si sois creyentes." WHERE ayatNumber = 58 AND chapterNumber = 5;
UPDATE verses SET es = "Ni a <em>quien</em>, cuando llamáis a <em>las gentes</em> a la Oración, la toman como objeto de chanzas y burlas. Eso es porque son gentes que no entienden." WHERE ayatNumber = 59 AND chapterNumber = 5;
UPDATE verses SET es = "Diles: “¡Oh Pueblo del Libro! ¿Nos censuráis porque creemos en Al-lah, en lo que nos ha sido revelado y en lo que fue revelado anteriormente? ¿O <em>es acaso</em> porque la mayoría de vosotros no obedecéis <em>a Dios</em>?”." WHERE ayatNumber = 60 AND chapterNumber = 5;
UPDATE verses SET es = "Diles: “¿Queréis que os hable de aquellos cuya retribución con Al-lah es peor que ésa? <em>Son</em> los que Al-lah ha maldecido y sobre quienes ha caído Su cólera, los que Él ha convertido en simios y cerdos y quienes adoran al Maligno. En efecto, éstos están en una situación peor, y aún más lejos del camino recto." WHERE ayatNumber = 61 AND chapterNumber = 5;
UPDATE verses SET es = "Y cuando acuden a ti, dicen: “creemos”, aunque entraron con incredulidad y salieron con ella; y Al-lah es quien mejor conoce lo que ocultan." WHERE ayatNumber = 62 AND chapterNumber = 5;
UPDATE verses SET es = "Y verás a muchos de ellos apresurarse hacia el pecado, la transgresión y la ingesta de cosas prohibidas. Malo es en verdad lo que practican." WHERE ayatNumber = 63 AND chapterNumber = 5;
UPDATE verses SET es = "¿Por qué los sacerdotes y los doctores en la Ley no les prohíben proferir falsedades y comer cosas prohibidas? Malo es en verdad lo que hacen." WHERE ayatNumber = 64 AND chapterNumber = 5;
UPDATE verses SET es = "Y los judíos dicen: “la mano de Al-lah está atada”. Pero son <em>sus </em>manos las que están atadas y serán maldecidos por lo que dicen. ¡No! Sus dos manos están extendidas y Él dispensa como desea. Y lo que te ha sido revelado por tu Señor aumentará ciertamente la rebeldía y la incredulidad de muchos de ellos. Y hemos infundido en ellos la enemistad y el odio hasta el Día de la Resurrección. Siempre que encienden un fuego de guerra, Al-lah lo apaga. Y luchan por sembrar la discordia en la tierra, cuando Al-lah no ama a los que siembran la discordia." WHERE ayatNumber = 65 AND chapterNumber = 5;
UPDATE verses SET es = "Mas si el Pueblo del Libro hubiese creído y hubiese sido piadoso, ciertamente les habríamos librado de sus males y los habríamos introducido en Jardines de Felicidad." WHERE ayatNumber = 66 AND chapterNumber = 5;
UPDATE verses SET es = "Y si hubiesen cumplido la Torah y el Evangelio y lo que <em>ahora</em> les ha sido revelado por su Señor, en verdad habrían comido de <em>las cosas buenas</em> que existen sobre sus cabezas o bajos sus pies. Hay entre ellos un grupo de hombres que son moderados; pero la mayoría ciertamente hace el mal." WHERE ayatNumber = 67 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh, Mensajero! transmite a las <em>gentes</em> lo que te ha sido revelado por tu Señor; pues si no lo hicieres no habrías proclamado Su Mensaje <em>en absoluto.</em> Y Al-lah te protegerá de los hombres. En verdad, Al-lah no guía a las gentes incrédulas." WHERE ayatNumber = 68 AND chapterNumber = 5;
UPDATE verses SET es = "Diles: “¡Oh, Pueblo del Libro! en nada <em>os apoyáis</em> mientras no cumpláis la Torah, el Evangelio y los que <em>ahora</em> os ha sido revelado por vuestro Señor”. Y ciertamente lo que te ha sido revelado por tu Señor aumentará la rebeldía y la incredulidad de muchos de ellos; no te apenes, pues, por los hombres incrédulos." WHERE ayatNumber = 69 AND chapterNumber = 5;
UPDATE verses SET es = "En verdad, los que han creído a y los judíos, sabeos y cristianos –todos los que creen en Al-lah y en el Último Día y hacen buenas obras-, no <em>les sobrecogerá</em> el temor, ni serán afligidos." WHERE ayatNumber = 70 AND chapterNumber = 5;
UPDATE verses SET es = "Ciertamente hemos aceptado un pacto de los hijos de Israel, y les hemos enviado mensajeros. Pero siempre que le llegaba un Mensajero con lo que no deseaban sus corazones, a unos los trataban de embusteros y a otros intentaron matarles." WHERE ayatNumber = 71 AND chapterNumber = 5;
UPDATE verses SET es = "Y pensaron que de ello no se derivaría perjuicio alguno, por lo que quedaron ciegos y sordos. Y Al-lah se volvió a ellos con misericordia; y a pesar de esto, muchos de ellos permanecieron voluntariamente ciegos y sordos; y Al-lah vigila lo que hacen." WHERE ayatNumber = 72 AND chapterNumber = 5;
UPDATE verses SET es = "Son verdaderamente incrédulos quienes dicen: “en verdad, Al-lah no es sino el Mesías, el hijo de María”, aun cuando el <em>mismo</em> Mesías dijo: “Oh, hijos de Israel, adorad a Al-lah, que es mi Señor y vuestro Señor”. Ciertamente que quien asocie otros dioses a Al-lah, Él le prohibirá la entrada en el Cielo y su morada será el Fuego. Y los malvados no tendrán defensores." WHERE ayatNumber = 73 AND chapterNumber = 5;
UPDATE verses SET es = "Son ciertamente incrédulos quienes dicen: “Al-lah es uno de la trinidad”; <em>cuando</em> no hay Dios sino el Dios Único. Y si no desisten de lo que dicen, un severo castigo caerá ciertamente sobre los que no crean de entre ellos." WHERE ayatNumber = 74 AND chapterNumber = 5;
UPDATE verses SET es = "¿No se volverán a Al-lah y pedirán Su perdón, siendo así que Al-lah es el Sumo Indulgente, Misericordioso?" WHERE ayatNumber = 75 AND chapterNumber = 5;
UPDATE verses SET es = "El Mesías, hijo de María, no fue más que un Mensajero. Todos los Mensajeros anteriores a él fallecieron. Y su madre fue piadosa. Ambos comían alimentos. Observa cómo explicamos los Signos para su bien y observa cómo se desvían." WHERE ayatNumber = 76 AND chapterNumber = 5;
UPDATE verses SET es = "Diles: “¿Adorasteis en lugar de Al-lah a lo que no tiene poder para haceros ni mal ni bien?” Pues Al-lah es quien todo lo oye, Omnisciente." WHERE ayatNumber = 77 AND chapterNumber = 5;
UPDATE verses SET es = "Diles: “¡Oh, Pueblo del Libro! no vayáis más allá de los límites en <em>el asunto de</em> vuestra religión, ni sigáis las perversas inclinaciones de un pueblo que se extravió antes, hizo que otros se extraviaran y <em>él mismo</em> se apartó del camino recto." WHERE ayatNumber = 78 AND chapterNumber = 5;
UPDATE verses SET es = "Los incrédulos de entre los hijos de Israel fueron maldecidos por boca de David y de Jesús, hijo de María. Eso fue porque desobedecieron y fueron transgresores." WHERE ayatNumber = 79 AND chapterNumber = 5;
UPDATE verses SET es = "No se prohibieron mutuamente la conducta ofensiva que practicaban. Malo es en verdad lo que solían hacer." WHERE ayatNumber = 80 AND chapterNumber = 5;
UPDATE verses SET es = "Verás a muchos de ellos tomar por amigos a los incrédulos. Ciertamente es malo para ellos lo que ellos mismos se enviaron por delante; con el <em>resultado de</em> que Al-lah se ha indignado con ellos, y en <em>este</em> castigo morarán." WHERE ayatNumber = 81 AND chapterNumber = 5;
UPDATE verses SET es = "Y si hubiesen creído en Al-lah, en este Profeta y <em>en</em> lo que a él le fue revelado, no los habrían tomado como amigos; pero muchos de ellos son desobedientes." WHERE ayatNumber = 82 AND chapterNumber = 5;
UPDATE verses SET es = "Comprobarás ciertamente que los judíos y los que asocian otros dioses <em>a Dios</em> son los más encarnizados de los hombres en su enemistad contra los creyentes. Y encontrarás seguramente que quienes dicen: “somos cristianos”, son los más cercanos de ellos en amor a los creyentes. Esto es porque entre ellos hay sacerdotes y monjes y porque no son orgullosos." WHERE ayatNumber = 83 AND chapterNumber = 5;
UPDATE verses SET es = "Y cuando oyen lo que ha sido revelado a <em>este</em> Mensajero, verás cómo las lágrimas brotan de sus ojos debido a la verdad que han reconocido. Y dicen: “Señor nuestro, creemos: inscríbenos entre los que dan testimonio”;" WHERE ayatNumber = 84 AND chapterNumber = 5;
UPDATE verses SET es = "“¿Y por qué no habríamos de creer en Al-lah y en la verdad que nos ha sido dada, si ansiamos vivamente que nuestro Señor nos incluya entre los justos?”." WHERE ayatNumber = 85 AND chapterNumber = 5;
UPDATE verses SET es = "Así, Al-lah los recompensó por lo que dijeron, con Jardines por los que corren ríos. En ellos morarán eternamente; y ésa es la recompensa de los que hacen el bien." WHERE ayatNumber = 86 AND chapterNumber = 5;
UPDATE verses SET es = "Pero quienes han dejado de creer y rechazado nuestros Signos, ésos serán los moradores del Infierno." WHERE ayatNumber = 87 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! No hagáis ilícitas las cosas buenas que Al-lah os ha hecho lícitas y no pequéis; porque Al-lah no ama a los transgresores." WHERE ayatNumber = 88 AND chapterNumber = 5;
UPDATE verses SET es = "Y comed de lo que Al-lah os ha proporcionado, de lo lícito y bueno. Y temed a Al-lah en Quien creéis." WHERE ayatNumber = 89 AND chapterNumber = 5;
UPDATE verses SET es = "Al-lah no os pedirá cuentas de vuestros juramentos vanos, pero sí de los juramentos serios que hayáis proferido. La expiación de esto consistirá en alimentar a diez pobres con <em>comida de</em> la calidad media con que alimentáis a vuestras familias, o vestirlos, o liberar a un esclavo. Mas quien no encuentre <em>los medios,</em> que ayune durante tres días. Ésta es la expiación de vuestros juramentos si habéis jurado <em>cumplirlos.</em> Cumplid vuestros juramentos. Así os explica Al-lah Sus Signos para que seáis agradecidos." WHERE ayatNumber = 90 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Los intoxicantes, el juego de azar, los ídolos y las flechas de la suerte no son más que una abominación de las obras de Satanás. Absteneos, pues, de <em>cada una de</em> ellas para que prosperéis." WHERE ayatNumber = 91 AND chapterNumber = 5;
UPDATE verses SET es = "Satanás sólo busca crear enemistad y odio entre vosotros mediante los intoxicantes y los juegos de azar, y apartaros del recuerdo de Al-lah y de la Oración. ¿No os vais, pues, a abstener?" WHERE ayatNumber = 92 AND chapterNumber = 5;
UPDATE verses SET es = "Obedeced a Al-lah y al Mensajero y manteneos en guardia. Pero si os alejáis, sabed que a nuestro Mensajero sólo corresponde la transmisión clara del Mensaje." WHERE ayatNumber = 93 AND chapterNumber = 5;
UPDATE verses SET es = "En cuanto a los que creen y hacen buenas obras, no pecarán por lo que coman, siempre y cuando teman <em>a Dios,</em> crean y hagan buenas bras, y de nuevo: teman <em>a Dios</em> y crean, y una vez más: teman <em>a Dios</em> y practiquen el bien. Pues Al-lah ama a quienes hacen el bien." WHERE ayatNumber = 94 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Al-lah ciertamente os pondrá a prueba en un tema <em>nimio</em>: la caza que pueden alcanzar vuestras manos y lanzas, para que descubra a quienes Le temen en secreto. Por tanto, quien cometa una transgresión después de esto, sufrirá un penoso castigo." WHERE ayatNumber = 95 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! No matéis la caza mientras estéis en peregrinación. Quien de vosotros la matare intencionadamente, su compensación será un cuadrúpedo similar al que ha matado, según determinen dos hombres justos de entre vosotros, que será llevado a la a´bah como ofrenda; o bien ofrecerá como expiación alimentar a <em>un número de </em>pobres, o ayunar un número equivalente de días, para que pruebe la sanción de su acción. <em>En cuanto a </em>lo pasado, Al-lah <em>lo</em> perdonará; pero si alguien reincide, Al-lah le castigará por <em>su ofensa. </em>Pues Al-lah es Poderoso, Señor de la Retribución." WHERE ayatNumber = 96 AND chapterNumber = 5;
UPDATE verses SET es = "Se os permite la pesca y comer de ella como provisión para vosotros y los viajeros; pero se os prohíbe la caza en tierra mientras estéis en estado de peregrinación. Y temed a Al-lah, <em>pues</em> junto a El seréis reunidos." WHERE ayatNumber = 97 AND chapterNumber = 5;
UPDATE verses SET es = "Al-lah ha hecho la Ka´bah, la Casa inviolable, como medio de apoyo y elevación de la humanidad, así como también el Mes Sagrado, las ofrendas y los <em>animales con collares</em>. Y es así para que sepáis que Al-lah conoce todo lo que hay en los cielos y en la tierra, pues Al-lah conoce perfectamente todas las cosas." WHERE ayatNumber = 98 AND chapterNumber = 5;
UPDATE verses SET es = "Sabed que Al-lah es severo en el castigo y que <em>también</em> es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 99 AND chapterNumber = 5;
UPDATE verses SET es = "Al Mensajero corresponde sólo la transmisión del Mensaje. Y Al-lah conoce lo que reveláis y lo que ocultáis." WHERE ayatNumber = 100 AND chapterNumber = 5;
UPDATE verses SET es = "Diles: “lo malo y lo bueno no son equiparables, aunque la abundancia de lo malo despierte tu extrañeza. Temed, pues, a Al-lah, hombres sensatos, para que prosperéis." WHERE ayatNumber = 101 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! no preguntéis por las cosas que, si se os revelaran os resultarían desagradables; aunque si preguntáis por ellas cuando está siendo revelado el Corán, os serán reveladas. Al-lah las ha dejado fuera <em>por indulgencia.</em> Pues Al-lah es el Sumo Indulgente, Tolerante." WHERE ayatNumber = 102 AND chapterNumber = 5;
UPDATE verses SET es = "Gentes anteriores a vosotros preguntaron sobre tales <em>cosas pero</em> luego no creyeron en ellas." WHERE ayatNumber = 103 AND chapterNumber = 5;
UPDATE verses SET es = "Al-lah no ha ordenado nada referente a ninguna “Bahira”, “Sa´iba”, “Wasila” o “Hami”, pero los incrédulos inventan una mentira contra Al-lah, pues en su mayoría son insensatos." WHERE ayatNumber = 104 AND chapterNumber = 5;
UPDATE verses SET es = "Y cuando se les dice: “Venid a lo que Al-lah ha revelado, y al Mensajero”, dicen: “nos basta lo que al respecto encontramos en nuestros padres”. ¡Cómo! ¿a pesar de que sus padres no tenían conocimiento ni guía?" WHERE ayatNumber = 105 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Permaneced en guardia respecto a vosotros mismos. Quien se extravía no puede perjudicaros si estáis bien guiados. Todos volveréis a Al-lah; y entonces Él os descubrirá lo que acostumbrabais a hacer." WHERE ayatNumber = 106 AND chapterNumber = 5;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! cuando se presente la muerte a uno e vosotros, en el momento de otorgar un legado, el testimonio <em> adecuado</em> será el de dos hombres justos de entre vosotros; o de otros dos que no sean de los vuestros si estáis de viaje por el país y os alcanza la desgracia de la muerte. Retendréis a ambos después de la Oración para que <em>den testimonio; </em>y si dudáis, ambos deberán jurar por Al-lah, diciendo: “no hemos cobrado nada a cambio de esto, unque él sea familiar cercano nuestro, ni ocultamos el testimonio <em>ordenado</em> por Al-lah; porque en ese caso, en verdad, nos contaríamos entre los pecadores”." WHERE ayatNumber = 107 AND chapterNumber = 5;
UPDATE verses SET es = "Pero si se hace patente que han cometido pecado <em>de perjurio</em>, entonces, los otros dos contra cuyos intereses habían dado testimonio ocuparán su lugar. Luego, habiendo jurado en el nombre de Al-lah, darán testimonio contra los dos primeros, afirmando: Nuestro testimonio es más verdadero que el de los dos anteriores <em>,</em> y no hemos transgredido. En caso de que fueramos falsos, nos contaríamos entre los transgresores <em>a los ojos de Al-lah</em>”." WHERE ayatNumber = 108 AND chapterNumber = 5;
UPDATE verses SET es = "Es más probable que se defienda así la causa de la verdad en el estimonio, <em>porque creará en las mentes de los primeros testigos</em> el temor a que su testimonio pueda ser rechazado en razón del prestado por los otros. Temed a Al-lah y escuchad; pues Al-lah no guía a los desobedientes." WHERE ayatNumber = 109 AND chapterNumber = 5;
UPDATE verses SET es = "<em> Piensa en</em> el día en que Al-lah reunirá a los Mensajeros y dirá “¿Qué se os ha respondido?”, y ellos dirán: “nosotros carecemos de conocimiento; sólo Tú eres el Poseedor del más amplio conocimiento de las cosas ocultas”." WHERE ayatNumber = 110 AND chapterNumber = 5;
UPDATE verses SET es = "Cuando Al-lah dirá: “¡Oh Jesús, hijo de María!”, acuérdate de Mi favor para ti y para tu madre; cuando te fortalecí con el Espíritu de santidad <em>para que</em> hablaras a las gentes en la niñez y en la edad madura; y cuando te enseñé el Libro y la Sabiduría, la Torah y el Evangelio; y acuérdate de cuando tú modelabas con barro una criatura, on forma de pájaro, siguiendo Mi mandato, entonces le insuflabas un <em>nuevo espíritu</em> y se convertía en un ser volador por Mi mandato; y tú curabas al ciego y al leproso por Mi mandato; y de cuando dabas vida a los muertos por Mi mandato; y de cuando impedí que los hijos de Israel te <em>dieran muerte</em> cuando llegaste a ellos con Signos manifiestos; y los incrédulos de entre ellos dijeron: “evidentemente esto no es más que pura magia”." WHERE ayatNumber = 111 AND chapterNumber = 5;
UPDATE verses SET es = "Y de cuando inspiré a los discípulos <em>de Jesús</em> para que creyeran en Mí y en Mi Mensajero, y dijeron: “creemos; sé Tú testigo de que nos hemos sometido”." WHERE ayatNumber = 112 AND chapterNumber = 5;
UPDATE verses SET es = "Y de cuando dijeron los discípulos: “Oh Jesús, hijo de María ¿puede tu Señor enviarnos desde el cielo una masa servida de manjares?”. Él dijo: “temed a Al-lah, si sois creyentes”." WHERE ayatNumber = 113 AND chapterNumber = 5;
UPDATE verses SET es = "Dijeron: “deseamos comer de ella para que nuestros corazones se tranquilicen, sepamos que nos has dicho la verdad y seamos testigos de ello”." WHERE ayatNumber = 114 AND chapterNumber = 5;
UPDATE verses SET es = "Dijo Jesús, hijo de María: “Oh Al-lah, Señor nuestro, envíanos desde el cielo una mesa servida de manjares que sea para nosotros una fiesta, desde el primero al último de nosotros, y un Signo de Ti; y provéenos del sustento, pues Tú eres el Mejor de los Proveedores”." WHERE ayatNumber = 115 AND chapterNumber = 5;
UPDATE verses SET es = "Al-lah dijo: “En verdad os la enviaré, pero si alguno de vosotros deja de creer después de eso, le impondré un castigo como el que no daré a ningún otro pueblo”." WHERE ayatNumber = 116 AND chapterNumber = 5;
UPDATE verses SET es = "Y cuando Al-lah diga: “Oh Jesús, hijo de María, ¿has dicho a los hombres: “Tomadme a mí y a mi madre por dos dioses además de Al-lah?”, él responderá: “¡Santificado seas! Nunca podría decir nada a lo que no tuviera derecho. Si lo hubiera dicho, Tú ciertamente lo habrías sabido. Tú sabes lo que hay en mi pensamiento, pero yo no sé lo que hay en el Tuyo. Sólo Tú eres el Conocedor de las cosas ocultas;" WHERE ayatNumber = 117 AND chapterNumber = 5;
UPDATE verses SET es = "“No les he dicho sino lo que Tú me ordenaste: “adorad a Al-lah, mi Señor y vuestro Señor”. Y fui testigo ante ellos mientras permanecí entre ellos, pero desde que me hiciste morir, Tú has sido su Vigilante; y Tú eres Testigo ante todas las cosas;" WHERE ayatNumber = 118 AND chapterNumber = 5;
UPDATE verses SET es = "“Si Tú los castigas, siervos tuyos son; y si Tú los perdonas, ciertamente eres el Poderoso, el Sabio”." WHERE ayatNumber = 119 AND chapterNumber = 5;
UPDATE verses SET es = "Al-lah dirá: “Éste es el día en el que <em>sólo</em> los veraces se beneficiarán de su veracidad. Para ellos hay Jardines por los que corren ríos; en ellos morarán para siempre. Al-lah se complacerá con ellos, y ellos con Él; en verdad, ése es el gran triunfo”." WHERE ayatNumber = 120 AND chapterNumber = 5;
UPDATE verses SET es = "A Al-lah pertenece el Reino de los cielos y la tierra y todo cuanto hay en ellos; pues Él tiene poder sobre todas las cosas." WHERE ayatNumber = 121 AND chapterNumber = 5;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 6;
UPDATE verses SET es = "Toda alabanza corresponde a Al-lah, quien creó los cielos y la tierra y originó toda <em>clase de</em> tinieblas y luz; sin embargo, los incrédulos ponen a otros al mismo nivel que su Señor." WHERE ayatNumber = 2 AND chapterNumber = 6;
UPDATE verses SET es = "Quien os creó del barro y os fijó después un plazo. Y <em>hay otro</em> plazo fijado junto a Él. ¡Y a pesar de esto dudáis!" WHERE ayatNumber = 3 AND chapterNumber = 6;
UPDATE verses SET es = "Pues Él es Al-lah, <em>Dios, tanto</em> en los cielos como en la tierra. Él conoce vuestros secretos así como lo que es aparente. Y sabe lo que ganáis." WHERE ayatNumber = 4 AND chapterNumber = 6;
UPDATE verses SET es = "Y no les llega ninguno de los Signos de su Señor, sin que se aparten de Él." WHERE ayatNumber = 5 AND chapterNumber = 6;
UPDATE verses SET es = "Rechazaron pues la verdad cuando les llegó; pero pronto les llegará la nueva de aquello de lo que se burlaron." WHERE ayatNumber = 6 AND chapterNumber = 6;
UPDATE verses SET es = "¿No ven acaso a cuantas épocas diferentes hemos destruido antes de ellos? Los establecimos en la tierra como no os hemos establecido a vosotros, y enviamos sobre ellos nubes que soltaron lluvias abundantes; e hicimos que a sus pies corrieran ríos; pero los destruimos por sus pecados e hicimos surgir tras ellos a otro pueblo." WHERE ayatNumber = 7 AND chapterNumber = 6;
UPDATE verses SET es = "Aunque te hubiésemos revelado un escrito en pergamino y ellos lo tocaran con las manos, los incrédulos habrían dicho ciertamente: “esto no es más que pura magia”." WHERE ayatNumber = 8 AND chapterNumber = 6;
UPDATE verses SET es = "Y dicen: “¿por qué no se le ha enviado un ángel?”. Pero si hubiésemos enviado un ángel, la cuestión habría quedado zanjada y no se les habría concedido un respiro." WHERE ayatNumber = 9 AND chapterNumber = 6;
UPDATE verses SET es = "Y si hubiésemos nombrado <em>como Mensajero</em> a un ángel, lo habríamos hecho <em>aparecer como</em> un hombre; y <em>por tanto</em> abríamos hecho confuso para ellos lo que ya están confundiendo <em>ellos mismos</em>." WHERE ayatNumber = 10 AND chapterNumber = 6;
UPDATE verses SET es = "Pues en verdad se han burlado de otros Mensajeros antes de ti, pero aquello de lo que se burlaron, atrapó a quienes se mofaron." WHERE ayatNumber = 11 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Recorred la tierra y ved cuál fue la suerte de los que trataron de mentirosos <em>a los Profetas”</em>." WHERE ayatNumber = 12 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿A quien pertenece cuanto existe en los cielos y en la ierra?”. Diles: “a Al-lah”. Él se impuso a Sí mismo <em>el mostrar</em> misericordia. Él ciertamente <em>continuará</em> reuniéndoos hasta el Día de la Resurrección. Sobre esto no hay duda alguna. Pero los que se han arruinado a si mismos no creerán." WHERE ayatNumber = 13 AND chapterNumber = 6;
UPDATE verses SET es = "A Él pertenece cuanto mora en el día y la noche. Pues Él es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 14 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Tomaré protector alguno distinto de Al-lah, Creador de los cielos y la tierra, que alimenta y no es alimentado?”. Diles: “se me ha ordenado ser el primero que se someta”. Y no seas de los que asocian partícipes <em>con Dios</em>." WHERE ayatNumber = 15 AND chapterNumber = 6;
UPDATE verses SET es = "Di: “Si desobedezco a mi Señor, temo el castigo de un día enorme”." WHERE ayatNumber = 16 AND chapterNumber = 6;
UPDATE verses SET es = "Quien se libre ese día, es porque <em>Dios</em> verdaderamente ha tenido misericordia de él. Ése será un triunfo manifiesto." WHERE ayatNumber = 17 AND chapterNumber = 6;
UPDATE verses SET es = "Si Al-lah te toca con un mal, nadie podrá librarte sino Él; pero si te bendice con la buena suerte es porque Él tiene poder para hacer todo lo que desea." WHERE ayatNumber = 18 AND chapterNumber = 6;
UPDATE verses SET es = "Pues Él es absolutamente Poderoso sobre Sus criaturas: la humanidad; y es el Sabio, el Consciente de todo." WHERE ayatNumber = 19 AND chapterNumber = 6;
UPDATE verses SET es = "Pregúntales: “¿Qué tiene más peso como testimonio?” Diles: “Al-lah es Testigo entre vosotros y yo. Pues este Corán me ha sido revelado para que con él pueda advertiros a vosotros y a quienes alcance. ¡Cómo! ¿Pretendéis testimoniar que hay otros dioses además de Al-lah?”. Di: “No doy testimonio de <em>eso” </em>y añade: “Él es el Dios Único, y me absuelvo totalmente de lo que asociáis a El”." WHERE ayatNumber = 20 AND chapterNumber = 6;
UPDATE verses SET es = "Aquellos a quienes dimos el Libro lo reconocen como reconocen a sus hijos. Pero quienes arruinan sus almas no creerán." WHERE ayatNumber = 21 AND chapterNumber = 6;
UPDATE verses SET es = "Pues ¿hay alguien más injusto que quien inventa una mentira contra Al-lah o tacha de mentiras sus Signos? En verdad, los impíos no prosperarán." WHERE ayatNumber = 22 AND chapterNumber = 6;
UPDATE verses SET es = "Y <em>acuérdate del </em>día en el que los reuniremos a todos; entonces diremos a quienes asocian otros partícipes <em>a Dios:</em> “¿Dónde están esos otros partícipes de quienes hablabais, aquellos en los que creíais?”." WHERE ayatNumber = 23 AND chapterNumber = 6;
UPDATE verses SET es = "Entonces el <em>fin</em> de su maldad será la nada, salvo que dirán: “ Por Al-lah, Señor Nuestro, no éramos idólatras”." WHERE ayatNumber = 24 AND chapterNumber = 6;
UPDATE verses SET es = "Mira cómo mienten contra sí mismos. Y lo que inventaron no les sirvió de nada." WHERE ayatNumber = 25 AND chapterNumber = 6;
UPDATE verses SET es = "Y hay entre ellos quienes te escuchan; pero hemos puesto velos sobre sus corazones para que no entiendan y hemos hecho sordos sus oídos. Y <em>aunque</em> vean todos los Signos no creerán en ellos, hasta el punto de que, cuando vienen a ti, discutiendo contigo, los incrédulos dicen: “no son más que fábulas de los antiguos”." WHERE ayatNumber = 26 AND chapterNumber = 6;
UPDATE verses SET es = "Prohíben a <em>otros creer</em> en ello y <em>ellos mismos</em> también se apartan. Mas no hacen sino perderse; aunque no se dan cuenta." WHERE ayatNumber = 27 AND chapterNumber = 6;
UPDATE verses SET es = "¡Si al menos pudieras ver cuando sean colocados ante el Fuego! Dirán: “¡Oh, ojalá pudiéramos regresar! <em>Entonces</em> no trataríamos de mentiras los Signos de Nuestro Señor y nos contaríamos entre los creyentes”." WHERE ayatNumber = 28 AND chapterNumber = 6;
UPDATE verses SET es = "No, lo que antes solían ocultar se les ha hecho evidente <em> ahora</em>. Y si fuesen devueltos, seguramente volverían a lo que les fue prohibido. Pues son ciertamente embusteros." WHERE ayatNumber = 29 AND chapterNumber = 6;
UPDATE verses SET es = "Dicen: “No existe nada excepto nuestra vida presente, y no seremos resucitados”." WHERE ayatNumber = 30 AND chapterNumber = 6;
UPDATE verses SET es = "¡Si al menos pudieras ver cuando se les haga comparecer ante su Señor! Él les dirá: “¿No es esta <em>segunda vida</em> una realidad?”. Responderán: “Sí, Señor Nuestro”. Pero Él les dirá: probad, pues, el castigo por no haber creído”." WHERE ayatNumber = 31 AND chapterNumber = 6;
UPDATE verses SET es = "Serán ciertamente de los perdedores aquellos que nieguen el encuentro con Al-lah, hasta el punto de que, cuando les llegue la hora por sorpresa, dirán: ¡Desgraciados de nosotros por haber descuidado esta <em>Hora</em>!”. Pero llevarán su fardo sobre sus espaldas. En verdad, malo será lo que cargarán." WHERE ayatNumber = 32 AND chapterNumber = 6;
UPDATE verses SET es = "La vida mundana no es sino juego y diversión; pero ciertamente la morada del Más Allá es mejor para los justos. ¿No entenderéis?" WHERE ayatNumber = 33 AND chapterNumber = 6;
UPDATE verses SET es = "Sabemos <em>perfectamente</em> que te apena lo que dicen; pues, ciertamente, no es a ti a quien acusan de falsedad, sino que son los Signos de Al-lah lo que rechazan los inicuos." WHERE ayatNumber = 34 AND chapterNumber = 6;
UPDATE verses SET es = "Y en verdad, antes de ti <em>otros </em>Mensajeros fueron rechazados; pero <em>a pesar de ser rechazados y perseguidos, </em> resistieron con paciencia hasta que les llegó Nuestra ayuda. No hay nada que pueda cambiar las palabras de Al-lah. Y ya te han llegado noticias de los <em>anteriores</em> Mensajeros." WHERE ayatNumber = 35 AND chapterNumber = 6;
UPDATE verses SET es = "Y si te apena su alejamiento, entonces, aunque trates de encontrar un túnel en la tierra o una escala que suba al cielo, para traerles un igno, <em>no podrás hacerlo.</em> Pues si Al-lah hubiese <em>ejecutado</em> su voluntad, en verdad los hubiera reunido en el camino recto. No seas pues de los ignorantes." WHERE ayatNumber = 36 AND chapterNumber = 6;
UPDATE verses SET es = "Sólo responden quienes escuchan con sinceridad. En <em>cuanto a</em> los muertos Al-lah les devolverá <em>la vida</em> y a Él serán devueltos." WHERE ayatNumber = 37 AND chapterNumber = 6;
UPDATE verses SET es = "Pero dicen: “¿Por qué no le fue enviado un Signo de su Señor?”. Diles: “en verdad, Al-lah tiene poder para enviar un Signo, pero la mayoría de ellos no lo sabe”." WHERE ayatNumber = 38 AND chapterNumber = 6;
UPDATE verses SET es = "No existe un animal <em>que se arrastre</em> por la tierra, ni un pájaro que vuele con sus dos alas, que no tenga comunidades como las vuestras. Nada hemos omitido en el Libro. Después serán reunidos ante su Señor." WHERE ayatNumber = 39 AND chapterNumber = 6;
UPDATE verses SET es = "Quienes han rechazado nuestros Signos están sordos y mudos en las tinieblas más completas. A quien Al-lah así lo estima <em>oportuno</em> , permite que se extravíe y a quien así decide lo sitúa en el camino correcto." WHERE ayatNumber = 40 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Cuál pensáis que <em>será vuestra respuesta</em> si os azota el castigo de Al-lah o si os llega la Hora <em>destinada</em>?, ¿invocaréis acaso a alguien que no sea Al-lah, si sois sinceros?" WHERE ayatNumber = 41 AND chapterNumber = 6;
UPDATE verses SET es = "No, a Él sólo invocaréis. Entonces Él os librará de lo que le habéis pedido <em>que os libere,</em> si así lo quiere, y olvidaréis todo lo que asociasteis <em>a Él”</em>." WHERE ayatNumber = 42 AND chapterNumber = 6;
UPDATE verses SET es = "En verdad, antes de ti enviamos a <em>otros Mensajeros</em> a los hombres; después los abrumamos con la aflicción y la adversidad, para que se hicieran más humildes." WHERE ayatNumber = 43 AND chapterNumber = 6;
UPDATE verses SET es = "¿Por qué, pues, cuando les llegó nuestro castigo no se hicieron más umildes? Mas sus corazones se endurecieron y Satanás hizo que <em>les pareciera</em> correcto cuanto hicieron." WHERE ayatNumber = 44 AND chapterNumber = 6;
UPDATE verses SET es = "Luego, cuando olvidaron aquello con lo que se les amonestó, les abrimos las puertas de todas las cosas hasta que se envanecieron por lo que se les había dado; entonces caímos de repente sobre ellos y ¡helos ahí!, hundidos en la desesperación." WHERE ayatNumber = 45 AND chapterNumber = 6;
UPDATE verses SET es = "Así fueron cortadas las mismas raíces de los que obraron mal; y toda alabanza corresponde a Al-lah, el Señor de todos los mundos." WHERE ayatNumber = 46 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Qué os parece? si Al-lah os retirara el oído y la vista y sellara vuestros corazones, ¿qué otro dios que no fuera Al-lah podría devolvéroslo? Mira cómo variamos los Signos y, sin embargo, vuelven la espalda." WHERE ayatNumber = 47 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Qué os parece? si el castigo de Al-lah os llegara repentina o abiertamente, ¿quiénes serán destruidos sino los malvados?”." WHERE ayatNumber = 48 AND chapterNumber = 6;
UPDATE verses SET es = "No enviamos a los Mensajeros sino como portadores de la buena nueva y como amonestadores. Por tanto, a quienes crean y <em>se</em> reformen no <em>les sobrecogerá</em> el temor ni se afligirán." WHERE ayatNumber = 49 AND chapterNumber = 6;
UPDATE verses SET es = "Pero a quienes rechacen nuestros Signos, les sobrevendrá Nuestro castigo, por haber desobedecido." WHERE ayatNumber = 50 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “No os digo: “poseo los tesoros de Al-lah” ni tampoco conozco lo desconocido; ni os digo: “soy un ángel”; sigo únicamente lo que me ha sido revelado”. Diles: “¿pueden ser iguales el ciego y el que ve?” ¿No recapacitaréis entonces?" WHERE ayatNumber = 51 AND chapterNumber = 6;
UPDATE verses SET es = "Advierte con ello a quienes temen, que serán reunidos junto a su Señor, y que no tendrán amigo ni intercesor aparte de Él, a fin de que se vuelvan justos." WHERE ayatNumber = 52 AND chapterNumber = 6;
UPDATE verses SET es = "No rechaces a quienes invocan a su Señor mañana y tarde buscando Su semblante. Tú no eres en absoluto responsable de ellos ni ellos lo son de ti, porque si los rechazas serás de los injustos." WHERE ayatNumber = 53 AND chapterNumber = 6;
UPDATE verses SET es = "Y de igual manera probamos a algunos de ellos con otros para que pudieran decir: “¿Son éstos a quienes Dios ha favorecido de entre nosotros?” ¿Acaso no conoce Al-lah mejor a los agradecidos?" WHERE ayatNumber = 54 AND chapterNumber = 6;
UPDATE verses SET es = "Cuando vengan a ti quienes creen en Nuestros Signos, diles: “¡que la paz sea con vosotros!”. Vuestro Señor se ha impuesto a Sí mismo mostrar misericordia para ser -con aquellos de vosotros que hagan el mal por ignorancia y después se arrepientan y se reformen- el Sumo Indulgente <em>y </em>Misericordioso”." WHERE ayatNumber = 55 AND chapterNumber = 6;
UPDATE verses SET es = "Así exponemos los Signos para que aparezca evidente el camino de los pecadores." WHERE ayatNumber = 56 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Se me ha prohibido adorar a quienes invocáis en vez de Al-lah”. Diles: “No seguiré vuestros deseos vanos porque, de hacerlo, me extraviaría y no estaría entre los guiados”." WHERE ayatNumber = 57 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: <em>“Me apoyo</em> en una prueba clara de mi Señor y osotros la rechazáis. Lo que pretendéis apresurar no está en <em>mi poder.</em> La decisión <em>sólo</em> corresponde a Al-lah. Él explica la verdad y es el Mejor de los Jueces”." WHERE ayatNumber = 58 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Si estuviera en mi poder lo que deseáis que se apresure, en verdad la cuestión se decidiría entre vosotros y yo. Pero Al-lah conoce mejor a los injustos”." WHERE ayatNumber = 59 AND chapterNumber = 6;
UPDATE verses SET es = "Pues Él posee las claves de lo desconocido; nadie las conoce excepto Él. Y Él sabe todo lo que hay en la tierra y <em>en</em> el mar. Y no cae una hoja sin que Él lo sepa; ni hay un solo grano en las inieblas de la tierra, ni nada húmedo o seco que no esté <em>registrado</em> en un Libro Manifiestamente claro." WHERE ayatNumber = 60 AND chapterNumber = 6;
UPDATE verses SET es = "Pues Él es quien recoge vuestras almas por la noche y sabe cuanto hacéis durante el día; y más tarde os reanima de nuevo para que se cumpla el plazo establecido. Luego a Él volveréis y os informará de cuanto hayáis hecho." WHERE ayatNumber = 61 AND chapterNumber = 6;
UPDATE verses SET es = "Él es Soberano sobre sus siervos y envía protectores <em>para vigilarlos,</em> hasta que, cuando os llegue la muerte a uno de vosotros, nuestros mensajeros recojan su alma; y no fallarán." WHERE ayatNumber = 62 AND chapterNumber = 6;
UPDATE verses SET es = "Luego son devueltos a Al-lah, su verdadero Señor. En verdad, suyo es el juicio. Y Él es el más Experto en la retribución." WHERE ayatNumber = 63 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Quién os libra de las calamidades de la tierra y el mar,<em>cuando</em> lo invocáis con humildad y en secreto, <em>diciendo</em>: “Si Él nos libra de esto, seremos en verdad de los agradecidos?”." WHERE ayatNumber = 64 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Al-lah os libra de ellas y de toda aflicción, y sin embargo asociáis otros partícipes <em>con Él”</em>." WHERE ayatNumber = 65 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Él tiene poder para enviaros un castigo desde lo alto o desde debajo de vuestros pies, o para hacer de vuestro destino que os dividáis en sectas <em>mutuamente hostiles</em> y que sufráis la violencia de los demás” ¡Mira cómo exponemos los Signos de distintas formas para que comprendan!" WHERE ayatNumber = 66 AND chapterNumber = 6;
UPDATE verses SET es = "Mas tu pueblo lo ha rechazado a pesar de ser la verdad. Diles: “¡No soy vuestro guardián!”." WHERE ayatNumber = 67 AND chapterNumber = 6;
UPDATE verses SET es = "Para cada profeta hay un plazo establecido; y pronto llegaréis a saberlo." WHERE ayatNumber = 68 AND chapterNumber = 6;
UPDATE verses SET es = "Cuando veas a quienes juegan con Nuestros Signos, apártate de ellos hasta que no empiecen a tratar otro tema. Y si Satanás te hace olvidar, no te sientes -después de caer en la cuenta- con los hombres impíos." WHERE ayatNumber = 69 AND chapterNumber = 6;
UPDATE verses SET es = "Pues los justos no son en absoluto responsables de ellos, aunque <em>su deber es</em> amonestarlos, para que teman <em>a Dios</em>." WHERE ayatNumber = 70 AND chapterNumber = 6;
UPDATE verses SET es = "Abandona a quienes tomen su religión como juego y pasatiempo y a uienes ha seducido la vida mundana. Pero exhorta de ese modo <em>a los hombres</em> no sea que un alma sea arrojada a la perdición por lo que ha cometido. No tendrá abogado ni intercesor fuera de Al-lah; ni aunque ofrezca cualquier rescate, se le aceptará. Ésos son los que han sido entregados a la destrucción por sus propios actos. Tendrán por bebida agua hirviente y <em>sufrirán </em>un doloroso castigo, por no haber creído." WHERE ayatNumber = 71 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Invocaremos, en lugar de Al-lah a lo que no puede beneficiarnos ni perjudicarnos, y volveremos sobre nuestros talones después de que Al-lah nos ha guiado, como aquél a quien los malvados educen, <em>dejándolo</em> perdido en la tierra, <em>a pesar de que</em> tiene compañeros que lo llaman al buen camino, diciéndole: “ven a nosotros”?”. Diles: “en verdad, la guía de Al-lah es la única guía, y se nos ha ordenado que nos sometamos al Señor de todos los mundos”." WHERE ayatNumber = 72 AND chapterNumber = 6;
UPDATE verses SET es = "<em> “Se nos ha dado la orden: </em>“Cumplid la Oración y temedle”; pues junto a Él seréis reunidos”." WHERE ayatNumber = 73 AND chapterNumber = 6;
UPDATE verses SET es = "Pues Él es quien creó los cielos y la tierra de la manera correcta; y el día que Él diga “¡Sea!”, será. Su Palabra es la verdad, y Suyo será el Reino el día en que sea tocada la trompeta. Él es quien conoce lo invisible y lo visible. Pues Él es el Sabio, el Omnisapiente." WHERE ayatNumber = 74 AND chapterNumber = 6;
UPDATE verses SET es = "Y <em>acuérdate</em> de cuando Abraham dijo a Azar, su padre: “¿Tomas a los ídolos por dioses? En verdad, os veo a ti y a tu pueblo en un error manifiesto”." WHERE ayatNumber = 75 AND chapterNumber = 6;
UPDATE verses SET es = "Y así mostramos a Abraham el Reino de los cielos y la tierra <em>para que fuera rectamente guiado</em> y se contara entre los que tienen la certeza de la fe." WHERE ayatNumber = 76 AND chapterNumber = 6;
UPDATE verses SET es = "Cuando la noche lo cubrió, vio a una estrella. Dijo: “¡Éste es mi Señor!”. Mas cuando desapareció, dijo: “No me gustan los que desaparecen”." WHERE ayatNumber = 77 AND chapterNumber = 6;
UPDATE verses SET es = "Cuando vio a la luna levantarse con su clara luz, dijo: “éste es mi Señor”. Pero cuando se puso, dijo: “Si mi Señor no me guía me contaré ciertamente entre los hombres que se extravían”." WHERE ayatNumber = 78 AND chapterNumber = 6;
UPDATE verses SET es = "Y cuando vio al sol levantarse con su esplendorosa luz, dijo: “éste es mi Señor, éste es el más grande”. Pero cuando se puso, dijo: “¡Oh, ueblo mío!, no soy en verdad responsable de lo que asociáis <em>a Dios”</em>;" WHERE ayatNumber = 79 AND chapterNumber = 6;
UPDATE verses SET es = "“He dirigido mi rostro hacia Él, el Creador de los cielos y la tierra, estando siempre inclinado <em>ante Dios</em> y no siendo de los que asocian otros dioses <em>a Dios.”</em>" WHERE ayatNumber = 80 AND chapterNumber = 6;
UPDATE verses SET es = "Y su pueblo discutió con él. Y dijo: “¿Discutís conmigo respecto a Al-lah, siendo así que Él me ha guiado por el camino recto? Mas yo no temo a lo que asociáis a Él, a menos que mi Señor quiera algo. Mi Señor abarca todas las cosas en Su conocimiento ¿no estaréis, pues, prevenidos?”." WHERE ayatNumber = 81 AND chapterNumber = 6;
UPDATE verses SET es = "“Y ¿por qué he de temer a lo que asociáis <em>a Dios</em>, si vosotros no teméis asociar a Al-lah aquello para lo que no os ha dado Él autoridad alguna?”. ¿Cuál de las dos partes tiene mayor derecho a la seguridad, si es que en verdad lo sabéis?" WHERE ayatNumber = 82 AND chapterNumber = 6;
UPDATE verses SET es = "Quienes creen y no disfrazan su fe con la injusticia: son los que tendrán la paz y serán correctamente guiados." WHERE ayatNumber = 83 AND chapterNumber = 6;
UPDATE verses SET es = "Ése es el argumento que dimos a Abraham frente a su gente. Pues exaltamos en distintos rangos de dignidad a quien Nos place. Tu Señor es ciertamente Sabio, Omnisciente." WHERE ayatNumber = 84 AND chapterNumber = 6;
UPDATE verses SET es = "Y le dimos a Isaac y a Jacob; guiamos rectamente a cada uno de ellos como guiamos anteriormente a Noé y, de su descendencia, a David y Salomón, a Job y José, a Moisés y Aarón. Así recompensamos a los que hacen el bien." WHERE ayatNumber = 85 AND chapterNumber = 6;
UPDATE verses SET es = "Y <em>guiamos</em> a Zacarías y Juan, a Jesús y Elías, cada <em>uno de ellos</em> estaba entre los virtuosos." WHERE ayatNumber = 86 AND chapterNumber = 6;
UPDATE verses SET es = "Y <em>guiamos también</em> a Ismael y Eliseo, a Jonás y Lot; a cada uno de ellos lo ensalzamos sobre los hombres." WHERE ayatNumber = 87 AND chapterNumber = 6;
UPDATE verses SET es = "Y <em>ensalzamos</em> a algunos de sus padres, sus hijos y sus hermanos; los elegimos y los guiamos por el camino recto." WHERE ayatNumber = 88 AND chapterNumber = 6;
UPDATE verses SET es = "Ésa es la guía de Al-lah. Mediante ella encamina a aquellos de sus siervos que Le place. Pero si hubiesen adorado a cualquier cosa fuera de Él, en verdad todo lo que hicieron no les habría servido de nada." WHERE ayatNumber = 89 AND chapterNumber = 6;
UPDATE verses SET es = "Éstos son a quienes entregamos el Libro, la facultad de juzgar y la rofecía. Pero si estos <em>hombres</em> son ingratos con ellos, <em>no importa,</em> pues ya los hemos confiado a un pueblo que no es desagradecido con ellos." WHERE ayatNumber = 90 AND chapterNumber = 6;
UPDATE verses SET es = "Éstos son aquellos a quienes Al-lah guió rectamente; seguid, pues, sus instrucciones. Diles: “No os pido recompensa alguna por ello. Esto no es más que una exhortación para toda la humanidad”." WHERE ayatNumber = 91 AND chapterNumber = 6;
UPDATE verses SET es = "Mas fracasaron en respetar a Al-lah como se merece cuando dijeron: “Al-lah no ha revelado nada a ningún hombre”. Diles: “¿Quién reveló el Libro que trajo Moisés, luz y guía para los hombres – aunque lo tratáis como pedazos de papel que mostráis, al mismo tiempo que ocultáis gran parte; y se os ha enseñado lo que no sabíais ni vosotros ni vuestros padres?”-. Diles: “¡Al-lah! Y déjalos luego que se diviertan con sus vanas discusiones." WHERE ayatNumber = 92 AND chapterNumber = 6;
UPDATE verses SET es = "Pues éste es un Libro que hemos revelado, lleno de bendiciones, para cumplir lo que le precedió y para permitirte advertir a la madre de las ciudades y a todos los de su alrededor. Y quienes creen en el Más Allá creen en él y cumplen atentamente su Oración." WHERE ayatNumber = 93 AND chapterNumber = 6;
UPDATE verses SET es = "¿Quién es más injusto que quien inventa una mentira contra Al-lah o dice: “me ha sido revelado”, cuando nada le fue revelado; y quien dice: “haré descender algo similar a lo que Al-lah ha hecho descender”. ¡Ojalá pudieras ver a los malhechores en la agonía de la muerte y a los ángeles extender sus manos <em>diciendo</em>: “¡entregad vuestras almas! Este día seréis pagados con el castigo de la desgracia, por lo que dijisteis en falso contra Al-lah y por haberos apartado con desdén de Sus Signos”." WHERE ayatNumber = 94 AND chapterNumber = 6;
UPDATE verses SET es = "Ahora venís a Nos uno a uno, como os creamos al principio; habéis dejado tras vosotros lo que os concedimos, y no vemos con vosotros a sos intercesores vuestros que asegurabais estaban asociados <em>a Dios</em> en vuestros <em>asuntos.</em> Ahora habéis sido separados unos de otros y os ha fallado lo que dabais por sentado." WHERE ayatNumber = 95 AND chapterNumber = 6;
UPDATE verses SET es = "En verdad, es Al-lah quien hace que germinen el grano y el hueso de dátil. Él hace surgir a lo vivo de lo muerto y es quien extrae lo muerto de lo vivo. Así es Al-lah; ¿cómo os alejáis, pues?" WHERE ayatNumber = 96 AND chapterNumber = 6;
UPDATE verses SET es = "Él origina la aurora; e hizo la noche para el reposo, y el sol y la luna para contar el tiempo. Ése es el plan del poderoso, el Sabio." WHERE ayatNumber = 97 AND chapterNumber = 6;
UPDATE verses SET es = "Él es Quien hizo las estrellas para que pudierais seguir, con su ayuda, la orientación correcta entre las tinieblas de la tierra y del mar. Hemos explicado los Signos con detalle para la gente que posee conocimiento." WHERE ayatNumber = 98 AND chapterNumber = 6;
UPDATE verses SET es = "Él es Quien os creó a partir de una sola fuente y <em>os</em> preparó un hogar y un alojamiento. Hemos explicado los Signos con detalle para la gente que entiende." WHERE ayatNumber = 99 AND chapterNumber = 6;
UPDATE verses SET es = "Él es quien hace descender el agua de la nube; de ese modo hacemos que germinen toda clase de plantas; en ellas hacemos brotar hojas verdes en las que producimos granos apiñados. Y de las palmeras, de sus ainas, <em>salen</em> racimos que cuelgan bajo. Y así <em>hacemos brotar</em> huertos de viñedos, olivos y granados –semejantes y diferentes-. Observad su fruto cuando lo tienen y su maduración. En verdad que en todo ello hay Signos para los hombres que creen." WHERE ayatNumber = 100 AND chapterNumber = 6;
UPDATE verses SET es = "Mas sostienen que los yinn están asociados a Al-lah, a pesar de que Él los creó, y Le adjudican falsamente hijos e hijas sin conocimiento. ¡Santo es Él y glorificado sea, <em>muy</em> por encima de lo que <em>Le</em> atribuyen!" WHERE ayatNumber = 101 AND chapterNumber = 6;
UPDATE verses SET es = "¡Originador de los cielos y de la tierra! ¿Cómo podría tener un hijo si no tiene compañera, <em>si</em> lo ha creado todo y tiene conocimiento de todas las cosas?" WHERE ayatNumber = 102 AND chapterNumber = 6;
UPDATE verses SET es = "Así es Al-lah, vuestro Señor. No hay otro Dios sino Él, el Creador de todas las cosas. Adoradle, pues; ya que es el Guardián de todo." WHERE ayatNumber = 103 AND chapterNumber = 6;
UPDATE verses SET es = "Las miradas no pueden alcanzarle pero Él alcanza las miradas. Él es el Inconmensurable, el Omnisapiente." WHERE ayatNumber = 104 AND chapterNumber = 6;
UPDATE verses SET es = "Ciertamente os ha llegado una luz manifiesta de vuestro Señor; por tanto, quien la ve lo hace para su propio bien; pero quien se ciega lo hace para su propio perjuicio. Pues yo no soy vuestro guardián." WHERE ayatNumber = 105 AND chapterNumber = 6;
UPDATE verses SET es = "Así variamos los Signos para que <em>llegue a brillar la verdad,</em> pero <em>el resultado es</em> que llos dicen: “tú has aprendido <em>mucho”;</em> y <em>variamos los Signos</em> para explicarlos a los hombres que tienen conocimiento." WHERE ayatNumber = 106 AND chapterNumber = 6;
UPDATE verses SET es = "Sigue lo que te ha sido revelado por tu Señor; no hay dios sino Él; y aléjate de los idólatras." WHERE ayatNumber = 107 AND chapterNumber = 6;
UPDATE verses SET es = "Y si Al-lah hubiese <em>ejecutado</em> Su voluntad, no habrían asociado otros dioses a Él. Pero no te hemos hecho su protector ni eres su guardián." WHERE ayatNumber = 108 AND chapterNumber = 6;
UPDATE verses SET es = "No insultéis a quienes ellos invocan en lugar de Al-lah, no sea que ellos, por despecho, insulten a Al-lah en <em>su</em> ignorancia. Así hicimos que cada pueblo <em>considerara</em> justas sus acciones. Pero después volverán a su Señor y Él les informará de cuanto hayan hecho." WHERE ayatNumber = 109 AND chapterNumber = 6;
UPDATE verses SET es = "Y juran por Al-lah con sus más solemnes juramentos que si les hubiera llegado un Signo, verdaderamente habrían creído en él. Diles: “en verdad los Signos corresponden a Al-lah. Pero ¿qué os haría comprender que cuando llegaran los signos no creerían?" WHERE ayatNumber = 110 AND chapterNumber = 6;
UPDATE verses SET es = "Y haremos que sus corazones y sus facultades visuales reviertan al mismo estado en que se encontraban cuando rechazaron inicialmente Nuestros Signos, y los abandonaremos en su trasgresión, para que vaguen aturdidos." WHERE ayatNumber = 111 AND chapterNumber = 6;
UPDATE verses SET es = "Pues aunque les enviáramos a los ángeles, les hablaran los muertos y reuniéramos frente a ellos todas las cosas, no creerían a menos que Al-lah <em>ejecutara</em> Su Voluntad. Pero la mayoría de ellos se comportan como ignorantes." WHERE ayatNumber = 112 AND chapterNumber = 6;
UPDATE verses SET es = "Y de igual manera opusimos a cada Profeta un enemigo, seres malvados de entre los hombres y los Yinn. Se dirigen mutuamente alabras cubiertas de oropel para engañar –y si tu Señor hubiera <em>ejecutado</em> Su voluntad, no lo habrían hecho-; ¡déjalos, pues, solos con lo que maquinan!" WHERE ayatNumber = 113 AND chapterNumber = 6;
UPDATE verses SET es = "Para que los corazones de los que no creen en el Más Allá se inclinen hacia ello, les agrade y puedan <em>seguir</em> ganando lo que están mereciendo." WHERE ayatNumber = 114 AND chapterNumber = 6;
UPDATE verses SET es = "¿Voy acaso a buscar un juez distinto de Al-lah, si es Él quien os ha revelado el Libro, claramente explicado? Mas aquellos a quienes entregamos el Libro saben que ha sido revelado por tu Señor con la verdad; no seas, pues, de los que dudan." WHERE ayatNumber = 115 AND chapterNumber = 6;
UPDATE verses SET es = "Pues la palabra de tu Señor se ha cumplido en verdad y justicia. Nadie puede cambiar Sus palabras, pues Él es quien Todo lo Oye, el Omnisapiente." WHERE ayatNumber = 116 AND chapterNumber = 6;
UPDATE verses SET es = "Pero si obedeces a la mayoría de los de que están en la tierra, te extraviarán del camino de Al-lah. No siguen sino <em>meras</em> conjeturas y no hacen más que mentir." WHERE ayatNumber = 117 AND chapterNumber = 6;
UPDATE verses SET es = "En verdad, tu Señor conoce bien a los que se extravían de Su camino; y conoce bien a los que están correctamente guiados." WHERE ayatNumber = 118 AND chapterNumber = 6;
UPDATE verses SET es = "Comed, pues, de aquello sobre lo que ha sido invocado el nombre de Al-lah, si creéis en Sus Signos." WHERE ayatNumber = 119 AND chapterNumber = 6;
UPDATE verses SET es = "¿Qué motivo tenéis para no comer de aquello sobre lo que ha sido invocado el nombre de Al-lah, si Él os ha explicado ya lo que os ha prohibido – a menos que os veáis obligados-?. Y ciertamente muchos desvían <em>a otros</em> con sus malos deseos por falta de conocimiento. En verdad, tu Señor conoce <em>muy</em> bien a los transgresores." WHERE ayatNumber = 120 AND chapterNumber = 6;
UPDATE verses SET es = "Evitad los pecados, tanto los aparentes como los ocultos. Cierto es que quienes ganen el pecado serán retribuidos por lo que han ganado." WHERE ayatNumber = 121 AND chapterNumber = 6;
UPDATE verses SET es = "Y no comáis de aquello sobre lo que no ha sido invocado el nombre de Al-lah porque, en verdad, es una desobediencia. Y ciertamente los malvados inspiran a sus amigos para que discutan con vosotros. Pero si es obedecéis, ciertamente estaréis levantando a otros dioses a la <em>altura de Dios</em>." WHERE ayatNumber = 122 AND chapterNumber = 6;
UPDATE verses SET es = "Quien estaba muerto y le devolvimos la vida, dándole una luz con la que anda entre los hombres, ¿puede ser acaso como quien está en las más profundas tinieblas de las que no puede salir? Así se hizo que las acciones de los incrédulos les parecieran correctas a ellos." WHERE ayatNumber = 123 AND chapterNumber = 6;
UPDATE verses SET es = "Y de este modo permitimos a los cabecillas de los pecadores de cada ciudad a que tramaran en ellas <em>contra la verdad</em>; pero no traman más que contra sus propias almas; aunque no caen en la cuenta." WHERE ayatNumber = 124 AND chapterNumber = 6;
UPDATE verses SET es = "Y cuando les llega un Signo dicen: “No creeremos mientras no se nos dé algo semejante a lo que se ha dado a los Mensajeros de Al-lah”. Al-lah sabe mejor <em>que nadie</em> donde colocar su Mensaje. En verdad, la humillación ante Al-lah y un severo castigo afligirán a los pecadores por lo que tramaron." WHERE ayatNumber = 125 AND chapterNumber = 6;
UPDATE verses SET es = "Así, a quien Al-lah desea guiar le abre el corazón para <em>que acepte</em> el Islam; y a quien desea <em>dejar </em>que se extravíe le estrecha el corazón, como si tratara de subir cuesta arriba. Así impone Al-lah el castigo a quienes no creen." WHERE ayatNumber = 126 AND chapterNumber = 6;
UPDATE verses SET es = "Mas éste es el camino de tu Señor, <em>que lleva</em> recto <em>a Él.</em> En verdad, hemos explicado con detalle los Signos para un pueblo que ha sido advertido." WHERE ayatNumber = 127 AND chapterNumber = 6;
UPDATE verses SET es = "Para ellos es la morada de paz con su Señor, y Él es su Amigo por lo que hicieron." WHERE ayatNumber = 128 AND chapterNumber = 6;
UPDATE verses SET es = "Mas el día en que los reúna a todos <em>dirá:</em> “¡Oh, asamblea de los yinn! ¡Habéis intentado someter a <em>vosotros</em> a un gran número de los hombres!”. Y sus amigos de entre los hombres dirán: ¡Señor Nuestro! nos aprovechamos los unos de los otros, pero <em>ahora</em> hemos alcanzado el término que Tú nos fijaste”. Él dirá: “El Fuego es vuestra morada, en la que habitaréis mucho tiempo, salvo que Al-lah quiera <em>otra cosa”.</em> En verdad, tu Señor es Sabio, Omnisciente." WHERE ayatNumber = 129 AND chapterNumber = 6;
UPDATE verses SET es = "Y de igual manera dimos poder a algunos injustos sobre otros, a causa de lo que ganaron." WHERE ayatNumber = 130 AND chapterNumber = 6;
UPDATE verses SET es = "“¡Oh asamblea de yinns y hombres! ¿Acaso no os llegaron Mensajeros de entre vosotros mismos que os relataron Mis Signos y os advirtieron del encuentro de este día vuestro?”. Dirán: “somos testigos contra nosotros mismos”. Pues la vida mundanal los sedujo. Y atestiguarán contra sí mismos que fueron incrédulos." WHERE ayatNumber = 131 AND chapterNumber = 6;
UPDATE verses SET es = "Esto es porque tu Señor nunca destruiría injustamente las ciudades sin antes advertir a sus habitantes." WHERE ayatNumber = 132 AND chapterNumber = 6;
UPDATE verses SET es = "Y para todos habrá niveles de rango de acuerdo con lo que hayan hecho, y tu Señor no ignora lo que hacen." WHERE ayatNumber = 133 AND chapterNumber = 6;
UPDATE verses SET es = "Y tu Señor es Autosuficiente, pleno de misericordia. Si se le antoja, puede haceros desaparecer y que os suceda lo que le plazca, de igual modo a como os hizo nacer de la descendencia de otros pueblos." WHERE ayatNumber = 134 AND chapterNumber = 6;
UPDATE verses SET es = "En verdad, lo que os ha prometido sucederá y no podréis impedirlo." WHERE ayatNumber = 135 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¡Oh, pueblo mío! obrad del mejor modo que podáis. Yo actúo también. Pronto sabréis a quién corresponde la recompensa final de la morada”. Ciertamente, los injustos no prosperarán." WHERE ayatNumber = 136 AND chapterNumber = 6;
UPDATE verses SET es = "Ellos asignan a Al-lah una parte de los cultivos y del ganado que Él ha producido, y dicen: “esto es para Al-lah”, según piensan ellos, “y esto es para nuestros ídolos”. Pero lo que es para sus ídolos no llega a Al-lah, mientras que lo que es para Al-lah llega a sus ídolos. ¡Qué malo es lo que concluyen!" WHERE ayatNumber = 137 AND chapterNumber = 6;
UPDATE verses SET es = "De la misma manera sus asociados hicieron que el asesinato de sus hijos <em>fuera considerado</em> justo por muchos de los idólatras para rruinarlos y confundirlos en su religión. Y si Al-lah hubiese <em>ejecutado</em> su Voluntad, no habrían hecho esto; dejadlos pues solos con lo que inventan." WHERE ayatNumber = 138 AND chapterNumber = 6;
UPDATE verses SET es = "Mas dicen: “Estos y aquellos ganados y cultivos están prohibidos. Nadie los comerá salvo quien queramos” – así alegan– y hay ganado cuyos lomos están prohibidos y ganado sobre el que no invocan el nombre de Al-lah urdiendo una mentira contra Él. Pronto les dará su merecido por lo que han tramado." WHERE ayatNumber = 139 AND chapterNumber = 6;
UPDATE verses SET es = "Y dicen: “Lo que está en el vientre de éste o aquel ganado está <em>reservado</em> exclusivamente para nuestros varones y está rohibido para nuestras esposas; pero si naciera muerto, entonces <em>todos</em> pueden participar de él. Él les dará su merecido por su afirmación <em>sin fundamento.</em> En verdad, Él es Sabio, Omnisciente." WHERE ayatNumber = 140 AND chapterNumber = 6;
UPDATE verses SET es = "Son ciertamente perdedores quienes matan neciamente a sus hijos por falta de conocimiento, y declaran ilícito lo que Al-lah les ha proporcionado, urdiendo una mentira contra Al-lah. En verdad se han extraviado y no están guiados rectamente." WHERE ayatNumber = 141 AND chapterNumber = 6;
UPDATE verses SET es = "Él es quien crea jardines, con emparrados y sin ellos, así como palmeras y trigales, con frutos de diversas clases, olivos y granados, similares y distintos. Comed de los frutos de cada uno cuando estén en sazón, pero pagad lo debido a Él el día de la recolección y no superéis los límites. En verdad Al-lah no ama a quienes se exceden." WHERE ayatNumber = 142 AND chapterNumber = 6;
UPDATE verses SET es = "Y del ganado, <em>a unos los creó</em> para carga y <em>a otros</em> para el sacrificio. Comed de lo que Al-lah os ha proporcionado, y no sigáis los pasos de Satanás. En verdad, él es vuestro enemigo declarado." WHERE ayatNumber = 143 AND chapterNumber = 6;
UPDATE verses SET es = "Afirman<em> que Al-lah ha creado </em>ocho parejas en conjunto <em>compuestas</em> por dos de ovinos y dos de caprinos; diles: “¿son los dos machos los que Él ha prohibido, o las dos hembras, o lo que éstas llevan en sus vientres? Comunicádmelo con conocimiento, si sois veraces”." WHERE ayatNumber = 144 AND chapterNumber = 6;
UPDATE verses SET es = "Y dos de los camélidos y dos de los bovinos. Diles: “¿son los dos machos los que Él ha prohibido, o las dos hembras o lo que éstas llevan en sus vientres? ¿Estabais presentes cuando Al-lah os impuso esto? ¿quién es más injusto que quien urde una mentira contra Al-lah para hacer que los hombres se extravíen sin conocimiento? Ciertamente, Al-lah no guía a los injustos." WHERE ayatNumber = 145 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “En lo que me ha sido revelado no encuentro nada prohibido para <em>quien desee</em> comerlo, excepto lo que ha muerto solo, la sangre derramada, la carne de cerdo – porque <em>todo</em> ello es impuro – o lo profano, sobre lo que ha sido invocado un nombre distinto del de Al-lah. Pero para quien se vea empujado por la desesperación, sin ser desobediente ni superar <em>el límite</em>, sepa que tu Señor es ciertamente el Sumo Indulgente, Misericordioso”." WHERE ayatNumber = 146 AND chapterNumber = 6;
UPDATE verses SET es = "En cuanto a los judíos, les prohibimos todos los animales con garra; y de los bovinos, ovinos y caprinos, les prohibimos sus grasas, salvo las que tienen sus lomos, los intestinos o lo que está mezclado con el hueso. Ésa es la recompensa que les dimos por su rebelión. Pues en verdad somos veraces." WHERE ayatNumber = 147 AND chapterNumber = 6;
UPDATE verses SET es = "Mas si te acusan de falsedad, diles: “Vuestro Señor posee la más amplia misericordia, pero su cólera no se alejará de los hombres culpables”." WHERE ayatNumber = 148 AND chapterNumber = 6;
UPDATE verses SET es = "Los que asocian partícipes a Al-lah dirán: “si Al-lah hubiese querido no habríamos podido asociar <em>a Él</em> otros dioses, ni lo habrían hecho nuestros padres; ni habríamos podido hacer nada ilícito”. e la misma manera quienes les precedieron acusaron de falsedad a <em>los Mensajeros de Dios</em>, hasta que probaron nuestra cólera. Diles: “¿Tenéis algún conocimiento? Presentádnoslo. No seguís más que conjeturas y no hacéis más que especular”." WHERE ayatNumber = 149 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “El argumento conclusivo lo tiene Al-lah. Si hubiese <em>ejecutado</em> su Voluntad, ciertamente os habría guiado a todos”." WHERE ayatNumber = 150 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Presentad vuestros testigos, los que testifican que Al-lah ha prohibido esto”. Si prestan testimonio, no testifiques tú con ellos ni sigas las malvadas inclinaciones de quienes tratan nuestros Signos de mentiras o de quienes no creen en el Más Allá y elevan a otros a la altura de su Señor." WHERE ayatNumber = 151 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Venid, os recitaré lo que Al-lah os ha hecho inviolable: que no asociéis a nada como partícipe con Él, que tratéis a vuestros adres con extrema bondad, que no matéis a vuestros hijos por <em>temor</em> a la pobreza. – Nosotros somos quienes proveemos para vosotros y para ellos–, que no os acerquéis a las malas acciones, ni manifiestas ni ocultas; y que no destruyáis la vida que Al-lah ha hecho sagrada, salvo por justicia. Esto es lo que os ha ordenado, para que entendáis." WHERE ayatNumber = 152 AND chapterNumber = 6;
UPDATE verses SET es = "“No os acerquéis a los bienes del huérfano, excepto en lo que sea apropiado, hasta que alcance la madurez. Y dad la medida completa y el peso con equidad. No obligamos a nadie, salvo según su capacidad. Y uando habléis observad la justicia, aunque la <em>persona interesada</em> sea un pariente, y cumplid el pacto de Al-lah . Esto es lo que Él os ordena, para que recordéis”." WHERE ayatNumber = 153 AND chapterNumber = 6;
UPDATE verses SET es = "Y diles: “Éste es mi camino que lleva rectamente. Seguidlo, pues, y no sigáis <em>otros</em> caminos, no sea que os alejen de Su camino. sto es lo que Él os ordena, para que <em>podáis</em> protegeros <em>contra los males”.</em>" WHERE ayatNumber = 154 AND chapterNumber = 6;
UPDATE verses SET es = "De nuevo, dimos a Moisés el Libro, que cumple íntegramente las exigencias de quien es excelente en conducta, explica todo con el máximo detalle y es una guía y una merced para que lleguen a creer en el encuentro con su Señor." WHERE ayatNumber = 155 AND chapterNumber = 6;
UPDATE verses SET es = "Y éste es un Libro que hemos revelado; <em>está</em> lleno de bendiciones. Seguidlo pues, y guardaos contra <em>el pecado</em> para que se os conceda misericordia." WHERE ayatNumber = 156 AND chapterNumber = 6;
UPDATE verses SET es = "Para que no digáis: “El Libro sólo fue revelado a dos pueblos antes que a nosotros, pero nosotros desconocíamos en verdad su lectura”;" WHERE ayatNumber = 157 AND chapterNumber = 6;
UPDATE verses SET es = "O para que no digáis: “Si se nos hubiese revelado el Libro, ciertamente hubiésemos sido mejor guiados que ellos.” <em>Ahora</em> os ha llegado una prueba manifiesta de vuestro Señor, una guía y una merced. ¿Quién es, pues, más injusto que quien rechaza los Signos de Al-lah y se aparta de ellos? Daremos a quienes se aparten de nuestros Signos un castigo severo por haberse alejado." WHERE ayatNumber = 158 AND chapterNumber = 6;
UPDATE verses SET es = "¿Acaso no esperan sino que los ángeles vengan a ellos o que venga tu Señor o que vengan algunos de los Signos de tu Señor? El día en el que vengan algunos de los Signos de tu Señor, creer en ellos no servirá a un alma que no haya ganado nada bueno con su fe. Diles: “Esperad; nosotros <em>también</em> esperamos”." WHERE ayatNumber = 159 AND chapterNumber = 6;
UPDATE verses SET es = "En<em> cuanto</em> <em>a</em> quienes escindieron su religión y quedaron <em>divididos</em> en sectas, no tienes que preocuparte en absoluto por ellos. En verdad, a Al-lah corresponde decidir sobre su causa, y luego les informará de lo que hicieron." WHERE ayatNumber = 160 AND chapterNumber = 6;
UPDATE verses SET es = "Quien realice una buena acción recibirá diez veces su valor, pero quien haga una mala acción tendrá sólo un merecido similar; y no serán defraudados." WHERE ayatNumber = 161 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “En cuanto a mí, mi Señor me ha guiado por el camino recto – una religión recta, la religión de Abraham, que siempre optaba por el camino recto. Que no fue de quienes asocian dioses <em>a Dios -.</em>" WHERE ayatNumber = 162 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “Mi Oración, mi sacrificio, mi vida y mi muerte son <em>todos</em> para Al-lah, el Señor de los mundos”;" WHERE ayatNumber = 163 AND chapterNumber = 6;
UPDATE verses SET es = "“Él no tiene a nadie asociado. Así se me ha ordenado y soy el primero de los que se someten”." WHERE ayatNumber = 164 AND chapterNumber = 6;
UPDATE verses SET es = "Diles: “¿Buscaré acaso un Señor distinto de Al-lah, cuando Él es el Señor de todas las cosas? Ningún alma gana su parte <em>de mal</em> sin llevar su carga sobre si misma. Y ningún portador llevará la carga de otro. Luego volveréis a vuestro Señor y Él os informará de aquello en lo que discrepabais." WHERE ayatNumber = 165 AND chapterNumber = 6;
UPDATE verses SET es = "Pues Él es quien os hizo virreyes en la tierra y elevó a algunos de vosotros sobre los demás en grados <em>de rango</em> para probaros con lo que os entregó. En verdad, tu Señor es resuelto en el castigo; y ciertamente es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 166 AND chapterNumber = 6;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 7;
UPDATE verses SET es = "Alif Lam Mim Sad." WHERE ayatNumber = 2 AND chapterNumber = 7;
UPDATE verses SET es = "<em>Éste </em>es un Libro revelado a ti – que no haya strechez en tu pecho respecto a él – para que puedas advertir con él y <em>sea</em> una exhortación para los creyentes." WHERE ayatNumber = 3 AND chapterNumber = 7;
UPDATE verses SET es = "Seguid lo que os ha sido revelado por vuestro Señor, y no sigáis a otros protectores que no sean Él. ¡Qué poco recordáis!" WHERE ayatNumber = 4 AND chapterNumber = 7;
UPDATE verses SET es = "¡Cuántas ciudades hemos destruido! Nuestro castigo les llegó de noche o mientras descansaban a mediodía." WHERE ayatNumber = 5 AND chapterNumber = 7;
UPDATE verses SET es = "Así, cuando les llegó nuestro castigo, su clamor consistió únicamente en decir: “¡fuimos realmente inicuos!”." WHERE ayatNumber = 6 AND chapterNumber = 7;
UPDATE verses SET es = "Mas preguntaremos ciertamente a quienes fueron enviados <em>los Mensajeros</em> y preguntaremos ciertamente a los Mensajeros." WHERE ayatNumber = 7 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces les enumeraremos indudablemente <em>sus acciones</em> con conocimiento, ya que nunca estuvimos ausentes." WHERE ayatNumber = 8 AND chapterNumber = 7;
UPDATE verses SET es = "Y la medida de peso en ese día será justa. Y aquellos cuyas balanzas sean más pesadas, son los que prosperarán." WHERE ayatNumber = 9 AND chapterNumber = 7;
UPDATE verses SET es = "Pero aquellos cuyas balanzas sean ligeras, serán los que habrán arruinado sus almas por haber sido injustos con nuestros Signos." WHERE ayatNumber = 10 AND chapterNumber = 7;
UPDATE verses SET es = "Os establecimos en la tierra y os proporcionamos en ella los medios de subsistencia. ¡Qué poco agradecidos sois!" WHERE ayatNumber = 11 AND chapterNumber = 7;
UPDATE verses SET es = "Os creamos <em>y</em> os dimos forma; entonces dijimos a los ángeles: “someteos a Adán;” y <em>todos</em> ellos se sometieron, excepto Iblis, que <em>no lo hizo;</em> no quiso ser de los que se someten." WHERE ayatNumber = 12 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Dios</em> dijo: “¿qué te impidió someterte cuando te lo ordené?”. Él dijo: “Soy mejor que él. Tú me creaste de fuego, mientras que a él lo creaste de barro”." WHERE ayatNumber = 13 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Dios</em> dijo: “Entonces sal fuera de inmediato; no te corresponde aquí ser arrogante. Sal; ciertamente eres de los que han sido degradados”." WHERE ayatNumber = 14 AND chapterNumber = 7;
UPDATE verses SET es = "Él dijo: “Concédeme un respiro hasta el día en que sean resucitados”." WHERE ayatNumber = 15 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Dios</em> dijo: “Serás de aquellos a los que se conceda un plazo”." WHERE ayatNumber = 16 AND chapterNumber = 7;
UPDATE verses SET es = "Dijo: “Y ahora, ya que Tú me has juzgado como perdido, me quedaré ciertamente a su espera en Tu camino recto”." WHERE ayatNumber = 17 AND chapterNumber = 7;
UPDATE verses SET es = "“Entonces caeré ciertamente sobre ellos, por delante y por detrás, y por su derecha y su izquierda, y encontrarás que la mayoría de ellos no son agradecidos”." WHERE ayatNumber = 18 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Dios</em> dijo: “¡Sal de aquí, despreciado y desterrado! Si alguno de ellos te sigue, sepa que indudablemente llenaré el Infierno con todos vosotros”." WHERE ayatNumber = 19 AND chapterNumber = 7;
UPDATE verses SET es = "“Y ¡Oh, Adán!, habitad tú y tu esposa en el Jardín y comed de él en donde queráis, pero no os acerquéis a este árbol, no sea que os contéis entre los malvados”." WHERE ayatNumber = 20 AND chapterNumber = 7;
UPDATE verses SET es = "Pero Satanás les susurró <em>insinuaciones</em> a fin de darles a conocer sus carencias, que les habían permanecido ocultas, respecto a su vergüenza, y dijo: “Al-lah no os habría prohibido este árbol, excepto que <em>Él deseara</em> que no os convirtáis en ángeles ni seáis de quienes son inmortales”." WHERE ayatNumber = 21 AND chapterNumber = 7;
UPDATE verses SET es = "Y él les juró <em>diciéndoles</em>: “En verdad, soy un consejero sincero para vosotros”." WHERE ayatNumber = 22 AND chapterNumber = 7;
UPDATE verses SET es = "De este modo les hizo caer en <em>la desobediencia</em> mediante el engaño. Y cuando gustaron del árbol, su vergüenza se les hizo manifiesta y ambos empezaron a colocarse sobre si mismos las hojas del jardín. Mas su Señor los llamó diciendo: “¿No os prohibí ese árbol y os dije: en verdad, Satanás es vuestro enemigo declarado?”." WHERE ayatNumber = 23 AND chapterNumber = 7;
UPDATE verses SET es = "Ellos dijeron: “Señor Nuestro, nos hemos hecho daño a nosotros mismos; y si Tú no nos perdonas y no tienes misericordia de nosotros, estaremos ciertamente entre los perdidos”." WHERE ayatNumber = 24 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Él</em> dijo: “¡Marchad!; algunos de vosotros seréis enemigos de los otros. Pues para vosotros hay una morada en la tierra y provisiones durante algún tiempo”." WHERE ayatNumber = 25 AND chapterNumber = 7;
UPDATE verses SET es = "Dijo: “en ella viviréis, en ella moriréis y de ella seréis retornados.”" WHERE ayatNumber = 26 AND chapterNumber = 7;
UPDATE verses SET es = "¡Oh, hijos de Adán! En verdad os hemos entregado vestiduras para ubrir vuestra vergüenza y como vestido elegante; pero<em>sabed que </em>la vestidura de la justicia es la mejor. Ése es <em>uno</em> de los Signos de Al-lah, para que recuerden." WHERE ayatNumber = 27 AND chapterNumber = 7;
UPDATE verses SET es = "¡Oh, hijos de Adán! que no os seduzca Satanás, como expulsó a vuestros padres del Jardín, despojándolos de sus vestiduras para mostrarles su vergüenza. En verdad él os ve -él y su tribu- desde donde vosotros no lo veis. Ciertamente, hemos hecho a los demonios amigos de los que no creen." WHERE ayatNumber = 28 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando cometen una acción ilícita, dicen: “Hemos visto a nuestros padres cometerla y Al-lah nos la impone”. Diles: “Al-lah nunca impone acciones ilícitas. ¿Decís de Al-lah lo que no sabéis?”." WHERE ayatNumber = 29 AND chapterNumber = 7;
UPDATE verses SET es = "Diles: Mi Señor ha ordenado la justicia. Dirigid vuestros rostros a la dirección adecuada en cada lugar de adoración, llamadle, siendo sinceros ante Él en la religión. Pues Él os dio el ser y a Él volveréis." WHERE ayatNumber = 30 AND chapterNumber = 7;
UPDATE verses SET es = "Él ha guiado a algunos y, en <em>cuanto a</em> otros el error ha sido su merecido. Han tomado como amigos a los malvados, excluyendo a Al-lah, y piensan que están correctamente guiados." WHERE ayatNumber = 31 AND chapterNumber = 7;
UPDATE verses SET es = "¡Oh, hijos de Adán! Cuidad de vuestras galas en cada <em>momento y </em>lugar de adoración, y comed y bebed, pero no superéis los límites; en verdad, Él no ama a quienes superan los límites." WHERE ayatNumber = 32 AND chapterNumber = 7;
UPDATE verses SET es = "Diles: “¿Quién ha prohibido las galas de Al-lah que Él produjo para Sus siervos, y las cosas buenas que Él proporciona?”. Diles: “Son para os creyentes en la vida actual <em>y </em>exclusivamente para <em>ellos</em> el Día de la Resurrección. Así explicamos los Signos a los hombres que tienen conocimiento." WHERE ayatNumber = 33 AND chapterNumber = 7;
UPDATE verses SET es = "Diles: “Mi Señor sólo ha prohibido las malas acciones, públicas o secretas, el pecado, y rebelarse sin justificación; que asociéis a Al-lah aquello a lo que Él no ha concedido potestad, y que digáis de Al-lah lo que no sabéis”." WHERE ayatNumber = 34 AND chapterNumber = 7;
UPDATE verses SET es = "Pues para cada pueblo hay un plazo, y cuando se cumple su plazo, no pueden quedarse atrás un solo momento, ni pueden adelantar<em>lo.</em>" WHERE ayatNumber = 35 AND chapterNumber = 7;
UPDATE verses SET es = "¡Oh, hijos de Adán! si os vienen Mensajeros de entre vosotros mismos, explicándoos Mis Signos, sabed entonces que a quienes teman a Dios y hagan buenas obras, no les <em>sobrecogerá</em> el temor ni serán afligidos." WHERE ayatNumber = 36 AND chapterNumber = 7;
UPDATE verses SET es = "Pero quienes rechacen Nuestros Signos y se aparten de ellos con desdén, serán los habitantes del Fuego; en él morarán durante un tiempo largo." WHERE ayatNumber = 37 AND chapterNumber = 7;
UPDATE verses SET es = "¿Quién es, pues, más injusto que quien urde una mentira contra Al-lah o tacha de falsedades sus Signos? Son éstos quienes correrán la suerte que se les ordenó hasta que Nuestros Mensajeros los visiten, para llevarse sus almas, y digan: “¿En dónde está lo que solíais invocar aparte de Al-lah?”. Responderán: “están completamente perdidos para nosotros”; y serán testigos contra ellos mismos de haber sido incrédulos." WHERE ayatNumber = 38 AND chapterNumber = 7;
UPDATE verses SET es = "Él dirá: “Entrad en el Fuego entre las naciones de yinn y hombres que murieron antes que vosotros”. Cada vez que una nación entre, maldecirá a su nación hermana, hasta que, cuando hayan llegado sucesivamente todas, la última de ellas diga de la primera: “¡Señor nuestro, éstos nos extraviaron, imponles pues un doble castigo del uego!” Él dirá: “para cada <em>grupo precedente</em> habrá un doble <em>castigo,</em> pero vosotros no lo sabéis”." WHERE ayatNumber = 39 AND chapterNumber = 7;
UPDATE verses SET es = "Mas los primeros de ellos dirán a los últimos: “No tenéis superioridad sobre nosotros; probad pues el castigo por todo lo que hicisteis”." WHERE ayatNumber = 40 AND chapterNumber = 7;
UPDATE verses SET es = "A quienes rechazan Nuestros Signos y se apartan de ellos con desprecio no se les abrirán las puertas del cielo, ni entrarán en él mientras un camello no pase a través del ojo de una aguja. Y así premiamos a los pecadores." WHERE ayatNumber = 41 AND chapterNumber = 7;
UPDATE verses SET es = "Tendrán el Infierno por lecho y se taparán con cubiertas <em>de lo mismo.</em> Pues de este modo recompensamos al injusto." WHERE ayatNumber = 42 AND chapterNumber = 7;
UPDATE verses SET es = "En <em>cuanto a </em>los que creen y hacen buenas obras – <em> y </em>no imponemos a nadie tarea alguna que supere su capacidad – éstos son los habitantes del Cielo; en él morarán." WHERE ayatNumber = 43 AND chapterNumber = 7;
UPDATE verses SET es = "Retiraremos cualquier rencor que pueda haber en sus corazones. Por debajo de ellos correrán ríos. Y dirán: “toda alabanza corresponde a Al-lah quien nos ha guiado hasta aquí. Y no hubiéramos encontrado guía si Al-lah no nos hubiera encaminado. Los Mensajeros de Nuestro Señor trajeron verdaderamente la verdad”. Y se les proclamará: éste es el Cielo que se os ha dado por herencia <em>como recompensa</em> de lo que hicisteis”." WHERE ayatNumber = 44 AND chapterNumber = 7;
UPDATE verses SET es = "Y los moradores del Cielo gritarán a los del Infierno: “En verdad hemos comprobado que lo que Nuestro Señor nos prometió es cierto. ¿Habéis comprobado también ser cierta la promesa de vuestro Señor?”. llos dirán: “Sí”. Luego, un pregonero anunciará entre ellos, <em>diciendo:</em> “La maldición de Al-lah cae sobre los injustos”." WHERE ayatNumber = 45 AND chapterNumber = 7;
UPDATE verses SET es = "“Quienes apartan <em>a los hombres</em> del camino de Al-lah e intentan hacerlo tortuoso, y no creen en el Más Allá”." WHERE ayatNumber = 46 AND chapterNumber = 7;
UPDATE verses SET es = "Entre ambos habrá un muro, y en los lugares elevados habrá hombres que conocerán a todos por sus marcas. Y gritarán a los hombres del Cielo: “la paz sea con vosotros”. Éstos <em>aún</em> no habrán entrado en él, aunque tendrán la esperanza de <em>hacerlo.</em>" WHERE ayatNumber = 47 AND chapterNumber = 7;
UPDATE verses SET es = "Mas cuando sus miradas se dirijan hacia los hombres del Fuego, dirán: “Señor nuestro, no nos coloques entre las gentes injustas”." WHERE ayatNumber = 48 AND chapterNumber = 7;
UPDATE verses SET es = "Y los ocupantes de los lugares elevados gritarán a los hombres a quienes conocerán por sus marcas; <em>y</em> dirán: “vuestra muchedumbre no os ha servido de nada, ni tampoco vuestra arrogancia”." WHERE ayatNumber = 49 AND chapterNumber = 7;
UPDATE verses SET es = "“¿Son éstos los hombres de quienes jurasteis que Al-lah no les concedería Su misericordia?”. A ellos se les ha dicho: “Entrad en el Paraíso; ningún temor os <em>sobrecogerá,</em> ni seréis afligidos”." WHERE ayatNumber = 50 AND chapterNumber = 7;
UPDATE verses SET es = "Y los moradores del Fuego gritarán a los moradores del Cielo: “Verted sobre nosotros un poco de agua o algo de lo que Al-lah os ha proporcionado”. Responderán: “En verdad, Al-lah ha prohibido ambas cosas a los incrédulos”." WHERE ayatNumber = 51 AND chapterNumber = 7;
UPDATE verses SET es = "“Quienes tomaron su religión como juego y pasatiempo, y fueron seducidos por la vida de este mundo”. Este día, pues, los olvidaremos como ellos olvidaron el encuentro de este día suyo, y por negar Nuestros Signos." WHERE ayatNumber = 52 AND chapterNumber = 7;
UPDATE verses SET es = "Ciertamente les trajimos un Libro que les expusimos con conocimiento; una guía y una misericordia para las gentes que creen." WHERE ayatNumber = 53 AND chapterNumber = 7;
UPDATE verses SET es = "¿Esperan acaso sólo el cumplimiento de sus <em>advertencias?</em> El día en el que venga su cumplimiento, los que lo hayan olvidado dirán: “En verdad los Mensajeros de Nuestro Señor trajeron la verdad. ¿Tendremos algún mediador que interceda por nosotros? O ¿no podríamos ser devueltos para que realizáramos <em>acciones</em> distintas a las que hicimos?”. En verdad han arruinado sus almas y lo que maquinaron les ha fallado." WHERE ayatNumber = 54 AND chapterNumber = 7;
UPDATE verses SET es = "En verdad vuestro Señor es Al-lah, Quien creó los cielos y la tierra en seis períodos; a continuación se instaló en el Trono. Él hace que la noche cubra al día, que la sigue rápidamente. <em>Y Él creó</em> el sol, la luna y las estrellas <em>todos ellos</em> sometidos a Su mandato. Suyos son, en verdad, la creación y el mandato. ¡Bendito sea Al-lah, el Señor de los mundos!" WHERE ayatNumber = 55 AND chapterNumber = 7;
UPDATE verses SET es = "Invocad a vuestro Señor con humildad y en secreto. En verdad, Él no ama a los transgresores." WHERE ayatNumber = 56 AND chapterNumber = 7;
UPDATE verses SET es = "Y no creéis el desorden en la tierra después de haber sido puesta en orden; mas invocadle en el temor y en la esperanza. En verdad, la misericordia de Al-lah está cerca de quienes hacen el bien." WHERE ayatNumber = 57 AND chapterNumber = 7;
UPDATE verses SET es = "Pues Él es Quien envía los vientos como buena nueva por delante de Su misericordia, hasta que, cuando llevan una nube pesada, la llevamos a un territorio árido, después hacemos que descarguen el agua y producimos en ella toda clase de frutos. De igual manera devolvemos la vida a los muertos, para que recordéis." WHERE ayatNumber = 58 AND chapterNumber = 7;
UPDATE verses SET es = "Y en <em>cuanto a</em> la buena tierra, su vegetación brota<em> en abundancia</em> por mandato de su Señor; pero si es mala, <em>su vegetación</em> sólo produce cosas carentes de valor. De la misma manera variamos los Signos para la gente que es agradecida." WHERE ayatNumber = 59 AND chapterNumber = 7;
UPDATE verses SET es = "Enviamos a Noé a su pueblo y dijo: “¡Oh, pueblo mío! Adorad a Al-lah, no tenéis otro Dios sino Él. En verdad, temo para vosotros el castigo del gran día”." WHERE ayatNumber = 60 AND chapterNumber = 7;
UPDATE verses SET es = "Los jefes de su pueblo respondieron: “Ciertamente te vemos en un error evidente”." WHERE ayatNumber = 61 AND chapterNumber = 7;
UPDATE verses SET es = "Él dijo: “¡Oh, pueblo mío! no hay error en mí, pues soy Mensajero del Señor de los mundos”." WHERE ayatNumber = 62 AND chapterNumber = 7;
UPDATE verses SET es = "Os transmito los mensajes de mi Señor y os doy mi más sincero consejo, ya que sé por Al-lah lo que vosotros no sabéis." WHERE ayatNumber = 63 AND chapterNumber = 7;
UPDATE verses SET es = "“¿Os admiráis de que os haya llegado una exhortación de vuestro Señor a través de un hombre de los vuestros, que pueda amonestaros para que seáis piadosos y se os muestre misericordia?”." WHERE ayatNumber = 64 AND chapterNumber = 7;
UPDATE verses SET es = "Pero lo acusaron de falsedad, por lo que lo salvamos a él y a quienes con él se encontraban en el Arca y ahogamos a quienes rechazaron Nuestros Signos. Era en verdad un pueblo ciego." WHERE ayatNumber = 65 AND chapterNumber = 7;
UPDATE verses SET es = "Y a los ‘Ad <em>enviamos</em> a su hermano Hud. Él dijo: “Oh, pueblo mío, adorad a Al-lah, no tenéis otro Dios sino Él. ¿No seréis pues temerosos de Dios?”." WHERE ayatNumber = 66 AND chapterNumber = 7;
UPDATE verses SET es = "Los jefes incrédulos de su pueblo respondieron: “ciertamente te vemos <em>perdido</em> en la necedad, y pensamos, en verdad, que eres uno de los mentirosos”." WHERE ayatNumber = 67 AND chapterNumber = 7;
UPDATE verses SET es = "Respondió: ¡Oh, pueblo mío, no hay necedad en mí, ya que soy Mensajero del Señor de los mundos!" WHERE ayatNumber = 68 AND chapterNumber = 7;
UPDATE verses SET es = "“Os transmito los mensajes de mi Señor y soy vuestro sincero y fiel consejero”." WHERE ayatNumber = 69 AND chapterNumber = 7;
UPDATE verses SET es = "“¿Os admiráis de que os haya llegado una exhortación de vuestro eñor a través de un hombre de los vuestros para advertiros? Y <em>acordaos</em> de cuando os hizo herederos de Sus favores después el Pueblo de Noé, y os aumentó con generosidad en constitución <em>física</em>. Acordaos, pues, de los favores de Al-lah para que prosperéis”." WHERE ayatNumber = 70 AND chapterNumber = 7;
UPDATE verses SET es = "Ellos respondieron: “¿Has venido a nosotros para que adoremos a Al-lah solamente y abandonemos lo que adoraban nuestros padres? Tráenos, pues, aquello con lo que nos amenazas, si eres veraz”." WHERE ayatNumber = 71 AND chapterNumber = 7;
UPDATE verses SET es = "Respondió: “En verdad, <em>ya</em> han caído sobre vosotros el castigo y la ira de vuestro Señor. ¿Disputáis conmigo sobre nombres que habéis mencionado – vosotros y vuestros padres – y para los que Al-lah no ha dado autoridad alguna? Esperad, pues, estoy con vosotros entre los que esperan”." WHERE ayatNumber = 72 AND chapterNumber = 7;
UPDATE verses SET es = "Y lo salvamos a él y a quienes estaban con él, por Nuestra misericordia, y destruimos los últimos restos de los que rechazaron Nuestros Signos. Pues no eran creyentes." WHERE ayatNumber = 73 AND chapterNumber = 7;
UPDATE verses SET es = "Y a los Zamud<em> enviamos</em> a su hermano Salih. Dijo: “Oh, pueblo mío, adorad a Al-lah; no tenéis otro Dios fuera de Él. En verdad, os ha llegado una prueba clara de vuestro Señor: ésta camella de Al-lah , un Signo para vosotros. Dejad pues que se alimente en la tierra de Al-lah y no hacedle daño, no sea que os alcance un castigo doloroso;" WHERE ayatNumber = 74 AND chapterNumber = 7;
UPDATE verses SET es = "“Y acordaos <em>de la época</em> cuando Él os hizo herederos de <em>Sus favores</em> después de los Ad, y os asignó una morada en la tierra; construís palacios en sus llanuras y horadáis casas en las montañas. Acordaos, pues, de los favores de Al-lah y no cometáis iniquidad en la tierra provocando el desorden”." WHERE ayatNumber = 75 AND chapterNumber = 7;
UPDATE verses SET es = "Los jefes de su pueblo, que eran arrogantes, dijeron a los onsiderados débiles – a quienes creían de entre ellos -: “¿Sabéis <em>con certeza</em> que Salih es un enviado de su Señor?”. Respondieron: “ciertamente, creemos en aquello con lo que ha sido enviado”." WHERE ayatNumber = 76 AND chapterNumber = 7;
UPDATE verses SET es = "Los arrogantes dijeron: “En verdad, no creemos en lo que vosotros creéis”." WHERE ayatNumber = 77 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces desjarretaron a la camella y se rebelaron contra el mandato de su Señor, diciendo: “Oh, Salih, tráenos eso con lo que nos amenazas, si eres <em>en realidad</em> uno de los Mensajeros”." WHERE ayatNumber = 78 AND chapterNumber = 7;
UPDATE verses SET es = "A continuación, el terremoto les atrapó y se convirtieron en cadáveres postrados en sus moradas." WHERE ayatNumber = 79 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces Salih se apartó de ellos y dijo: “Oh, pueblo mío, os entregué el Mensaje de mi Señor y os aconsejé sinceramente, pero no amáis a los consejeros sinceros." WHERE ayatNumber = 80 AND chapterNumber = 7;
UPDATE verses SET es = "Y<em> enviamos</em> a Lot, -cuando dijo a su pueblo-: “¿Cometéis una abominación como nadie en el mundo cometió antes que vosotros?" WHERE ayatNumber = 81 AND chapterNumber = 7;
UPDATE verses SET es = "“Os acercáis a los hombres con lujuria en vez de a las mujeres. ¡No!, sois un pueblo que ha excedido <em>todos</em> los límites”." WHERE ayatNumber = 82 AND chapterNumber = 7;
UPDATE verses SET es = "Pero la respuesta de su pueblo no consistió más que en decir: “Expulsarlos de vuestra ciudad, ya que son hombres que quieren conservarse puros”." WHERE ayatNumber = 83 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces le salvamos, a él y a su familia, excepto a su mujer: fue una de las que se quedaron detrás." WHERE ayatNumber = 84 AND chapterNumber = 7;
UPDATE verses SET es = "E hicimos caer sobre ellos una lluvia. ¡Mirad ahora cuál fue el fin de los pecadores!" WHERE ayatNumber = 85 AND chapterNumber = 7;
UPDATE verses SET es = "Y a Madián enviamos a su hermano Shu’aib. Dijo: “Oh, pueblo mío, adorad a Al-lah; no tenéis otro Dios sino Él. En verdad os ha llegado un Signo claro de vuestro Señor. Entregad pues la medida completa y todo el peso, y no deis a las gentes menos de lo que les pertenece por derecho, ni creéis el desorden en la tierra después deque ha sido pacificada. Esto es mejor para vosotros si sois creyentes." WHERE ayatNumber = 86 AND chapterNumber = 7;
UPDATE verses SET es = "“No os apostéis en cualquier camino amenazando y apartando del camino de Al-lah a los que creen en Él, e intentando hacerlo tortuoso. Acordaos de cuando erais pocos y Él os multiplicó. ¡Mirad cuál fue el fin de los que sembraron la discordia!" WHERE ayatNumber = 87 AND chapterNumber = 7;
UPDATE verses SET es = "“Mas si hay un grupo entre vosotros que cree en aquello con lo que he sido enviado, y un grupo que no cree, tened paciencia hasta que Al-lah juzgue entre nosotros. Él es el Mejor de los jueces”." WHERE ayatNumber = 88 AND chapterNumber = 7;
UPDATE verses SET es = "Los jefes de su pueblo, que eran arrogantes, dijeron: “Ciertamente e expulsaremos de nuestra ciudad a ti, oh Shu’aib y a los creyentes <em>que están</em> contigo, a menos que volváis a nuestra religión”. Él respondió: “¿Aunque no estuviésemos dispuestos?”." WHERE ayatNumber = 89 AND chapterNumber = 7;
UPDATE verses SET es = "“En verdad que habríamos estado urdiendo una mentira contra Al-lah, si <em>ahora</em> volvemos a vuestra religión después de que Al-lah nos salvara de ella. Y no nos corresponde a nosotros volver a ella, salvo que Al-lah, nuestro Señor, <em>así</em> lo deseara. En Conocimiento Nuestro Señor alcanza todas las cosas. En Al-lah ponemos nuestra confianza. <em>Así pues,</em> Oh Señor nuestro, decide Tú entre nosotros y entre nuestro pueblo con la verdad, pues Tú eres el Mejor de los que deciden”." WHERE ayatNumber = 90 AND chapterNumber = 7;
UPDATE verses SET es = "Y los jefes de su pueblo que no creían dijeron: “Si seguís a Shu’aib seréis ciertamente de los perdedores”." WHERE ayatNumber = 91 AND chapterNumber = 7;
UPDATE verses SET es = "Así, el terremoto los fulminó y quedaron en sus casas postrados sobre la tierra." WHERE ayatNumber = 92 AND chapterNumber = 7;
UPDATE verses SET es = "Quienes acusaron a Shu’aib de mentir quedaron como si jamás ubiesen vivido en ellas. Quienes acusaron a Shu’aib de mentir fueron, <em>en realidad, </em>los perdedores." WHERE ayatNumber = 93 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces él se apartó de ellos y dijo: “Oh, pueblo mío, en verdad os entregué los mensajes de mi Señor y os di un consejo sincero. ¿Cómo he de sentir pena por un pueblo incrédulo?" WHERE ayatNumber = 94 AND chapterNumber = 7;
UPDATE verses SET es = "Y nunca enviamos un Profeta a ninguna ciudad sin antes afligir a sus gentes con la adversidad y el sufrimiento, para que se hicieran humildes." WHERE ayatNumber = 95 AND chapterNumber = 7;
UPDATE verses SET es = "Después cambiamos <em>su </em>mala <em>condición</em> en otra buena hasta que crecieron en <em>riqueza y número,</em> y dijeron: “el sufrimiento y la felicidad tocaron <em>también</em> a nuestros padres”. Entonces los sorprendimos de repente, sin que se dieran cuenta." WHERE ayatNumber = 96 AND chapterNumber = 7;
UPDATE verses SET es = "Mas si el pueblo <em>de esas</em> ciudades hubiese creído y sido piadoso, ciertamente hubiésemos abierto para ellos las bendiciones del cielo y de la tierra; pero no creyeron, por lo que los atrapamos a causa de lo que ganaron." WHERE ayatNumber = 97 AND chapterNumber = 7;
UPDATE verses SET es = "¿Están, pues, las gentes de<em> estas</em> ciudades seguras frente a la llegada de Nuestro castigo sobre ellos por la noche, mientras están dormidos?" WHERE ayatNumber = 98 AND chapterNumber = 7;
UPDATE verses SET es = "Y ¿están seguras las gentes de <em>estas</em> ciudades frente a la aparición de Nuestro castigo sobre ellos en la primera parte de la tarde, mientras están ocupados en la diversión?" WHERE ayatNumber = 99 AND chapterNumber = 7;
UPDATE verses SET es = "¿Están, pues, seguros ante el designio de Al-lah? Nadie se siente seguro del designio de Al-lah, salvo las gentes <em>que perecen.</em>" WHERE ayatNumber = 100 AND chapterNumber = 7;
UPDATE verses SET es = "¿No sirve de guía a quienes han heredado la tierra sucediendo a sus <em>anteriores</em> habitantes, saber que si queremos podemos castigarlos por sus pecados y sellar sus corazones para que no oigan?" WHERE ayatNumber = 101 AND chapterNumber = 7;
UPDATE verses SET es = "Así eran las ciudades de las que te hemos relatado algunas de sus noticias. Y sus Mensajeros en verdad llegaron a ellas con Signos claros. Pero no quisieron creer en lo que habían desmentido antes. De este modo Al-lah sella los corazones de los incrédulos." WHERE ayatNumber = 102 AND chapterNumber = 7;
UPDATE verses SET es = "Encontramos que la mayoría no respetaba <em>el cumplimiento</em> del pacto y, en verdad, comprobamos que la mayoría practicaban el mal." WHERE ayatNumber = 103 AND chapterNumber = 7;
UPDATE verses SET es = "Luego, después de ellos, enviamos a Moisés con Nuestros Signos al Faraón y a sus jefes, pero ellos los <em>rechazaron</em> injustamente. ¡Mirad, pues, cuál fue el fin de quienes crearon la discordia!" WHERE ayatNumber = 104 AND chapterNumber = 7;
UPDATE verses SET es = "Moisés dijo: “Oh, Faraón, <em>en verdad,</em> soy un Mensajero del Señor de los mundos”." WHERE ayatNumber = 105 AND chapterNumber = 7;
UPDATE verses SET es = "“No es correcto que diga de Al-lah nada que no sea la verdad. He venido a ti con un Signo claro de tu Señor; por tanto, deja que los hijos de Israel vengan conmigo”." WHERE ayatNumber = 106 AND chapterNumber = 7;
UPDATE verses SET es = "El Faraón replicó: “Si has venido en efecto con un Signo, preséntalo, si eres de los veraces”." WHERE ayatNumber = 107 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces, tiró su cayado y he aquí que se convirtió en una serpiente claramente visible." WHERE ayatNumber = 108 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces sacó la mano y he ahí que aparecía blanca a quienes la contemplaban." WHERE ayatNumber = 109 AND chapterNumber = 7;
UPDATE verses SET es = "Los jefes del pueblo del Faraón dijeron: “Ciertamente es un mago muy hábil”." WHERE ayatNumber = 110 AND chapterNumber = 7;
UPDATE verses SET es = "“Desea expulsaros de vuestra tierra. ¿Qué es lo que aconsejáis?”." WHERE ayatNumber = 111 AND chapterNumber = 7;
UPDATE verses SET es = "Respondieron: “Expúlsalos a él y a su hermano durante <em>algún tiempo,</em> y envía a las ciudades reclutadores”." WHERE ayatNumber = 112 AND chapterNumber = 7;
UPDATE verses SET es = "“Que te traigan a todos los magos eminentes”." WHERE ayatNumber = 113 AND chapterNumber = 7;
UPDATE verses SET es = "Y los magos llegaron al Faraón <em>y</em> dijeron: “Naturalmente tendremos una recompensa si ganamos”." WHERE ayatNumber = 114 AND chapterNumber = 7;
UPDATE verses SET es = "Él les dijo: “Sí, y seréis <em>también</em> de entre los que estarán cerca <em>de mí”.</em>" WHERE ayatNumber = 115 AND chapterNumber = 7;
UPDATE verses SET es = "Dijeron: “Oh, Moisés, arroja tú <em>primero</em> o bien lo hacemos nosotros”." WHERE ayatNumber = 116 AND chapterNumber = 7;
UPDATE verses SET es = "Él respondió: “Arrojadlo vosotros” y cuando lo hicieron fascinaron los ojos de los presentes, sobrecogiéndolos con temor y presentándose con una magia extraordinaria." WHERE ayatNumber = 117 AND chapterNumber = 7;
UPDATE verses SET es = "Mas inspiramos a Moisés, <em>diciéndole:</em> “Arroja tu cayado” y he ahí que devoró todo lo que ellos habían falsificado." WHERE ayatNumber = 118 AND chapterNumber = 7;
UPDATE verses SET es = "Así prevaleció la Verdad y sus obras resultaron ser vanas." WHERE ayatNumber = 119 AND chapterNumber = 7;
UPDATE verses SET es = "De este modo fueron allí vencidos, y se fueron humillados." WHERE ayatNumber = 120 AND chapterNumber = 7;
UPDATE verses SET es = "Los magos se sintieron impulsados a caer postrados." WHERE ayatNumber = 121 AND chapterNumber = 7;
UPDATE verses SET es = "Y dijeron: “Creemos en el Señor de los mundos." WHERE ayatNumber = 122 AND chapterNumber = 7;
UPDATE verses SET es = "El Señor de Moisés y Aarón”." WHERE ayatNumber = 123 AND chapterNumber = 7;
UPDATE verses SET es = "El Faraón dijo: “Habéis creído en él antes de que os diera mi permiso. En verdad, ésta es una treta que habéis maquinado en la ciudad ara poder expulsar de ella a sus habitantes, pero muy pronto sabréis <em>las consecuencias”.</em>" WHERE ayatNumber = 124 AND chapterNumber = 7;
UPDATE verses SET es = "“En verdad os cortaré las manos y los pies de lados opuestos. A continuación, ciertamente, os crucificaré a todos juntos”." WHERE ayatNumber = 125 AND chapterNumber = 7;
UPDATE verses SET es = "Ellos respondieron:<em> “Entonces</em> volveremos a nuestro Señor”." WHERE ayatNumber = 126 AND chapterNumber = 7;
UPDATE verses SET es = "Mas tú sólo te vengas de nosotros porque hemos creído en los Signos de nuestro Señor, cuando nos han llegado. ¡Señor nuestro!, otórganos la paciencia y haz que muramos resignados a Ti”." WHERE ayatNumber = 127 AND chapterNumber = 7;
UPDATE verses SET es = "Y los jefes del pueblo del Faraón dijeron: “¿Dejarás que Moisés y su pueblo creen la confusión en el país, y te abandonen a ti y a tus dioses?”. Él respondió: “Mataremos sin piedad a sus hijos y dejaremos vivas a sus mujeres. En verdad los dominamos”." WHERE ayatNumber = 128 AND chapterNumber = 7;
UPDATE verses SET es = "Moisés dijo a su pueblo: “Pedid ayuda a Al-lah y tened paciencia. En verdad, la tierra es de Al-lah. Él la da como herencia a quien desea de Sus siervos, y al final <em>prevalecerán</em> los que temen a Dios”." WHERE ayatNumber = 129 AND chapterNumber = 7;
UPDATE verses SET es = "Respondieron: “Fuimos perseguidos antes de que tú llegaras a nosotros e <em>incluso</em> después de que llegaras”. Él dijo: “Vuestro Señor está a punto de destruir a vuestros enemigos y haceros gobernantes en el país, <em>para</em> así ver cómo os comportáis”." WHERE ayatNumber = 130 AND chapterNumber = 7;
UPDATE verses SET es = "Y castigamos al pueblo del Faraón con sequía y escasez de frutos, para que quedaran advertidos." WHERE ayatNumber = 131 AND chapterNumber = 7;
UPDATE verses SET es = "Pero cuando les llegó la prosperidad, dijeron: “Esto es para nosotros”. Y si les acaecía la desgracia, achacaban su mala fortuna a Moisés y a los que lo acompañaban. En verdad, la <em>causa de</em> su mala fortuna está en Al-lah. Pero la mayoría no lo saben." WHERE ayatNumber = 132 AND chapterNumber = 7;
UPDATE verses SET es = "Y dijeron: “Cualquiera que sea el Signo que puedas traernos para embrujarnos con él, no creeremos en él”." WHERE ayatNumber = 133 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces enviamos sobre ellos la tormenta y la langosta, los piojos, las ranas y la sangre –Signos claros- pero se comportaron con soberbia y fueron un pueblo pecador." WHERE ayatNumber = 134 AND chapterNumber = 7;
UPDATE verses SET es = "Mas cuando cayó sobre ellos el castigo, dijeron: “Oh, Moisés, implora por nosotros a tu Señor según lo que Él te ha prometido. Si retiras de nosotros el castigo, ciertamente creeremos en ti y enviaremos en verdad contigo a los hijos de Israel”." WHERE ayatNumber = 135 AND chapterNumber = 7;
UPDATE verses SET es = "Mas cuando les retiramos el castigo durante el plazo que debían alcanzar, he ahí que violaron su promesa." WHERE ayatNumber = 136 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces nos vengamos de ellos y los ahogamos en el mar, porque trataron Nuestros Signos como mentiras e hicieron caso omiso de ellos." WHERE ayatNumber = 137 AND chapterNumber = 7;
UPDATE verses SET es = "E hicimos que el pueblo considerado débil heredara las partes orientales del país y sus partes occidentales, que bendijimos. Y se cumplió la palabra misericordiosa de tu Señor para los hijos de Israel, porque fueron perseverantes; y destruimos todo cuanto el Faraón y su pueblo habían edificado y todo lo que habían erigido." WHERE ayatNumber = 138 AND chapterNumber = 7;
UPDATE verses SET es = "Y llevamos a los hijos de Israel a través del mar, y llegaron a un pueblo que adoraba a sus ídolos. Dijeron: “Oh, Moisés, haznos un dios de forma similar a como ellos los tienen”. Respondió: “En verdad, sois un pueblo ignorante”." WHERE ayatNumber = 139 AND chapterNumber = 7;
UPDATE verses SET es = "<em> “En cuanto a</em> éstos, en verdad será destruido todo aquello a lo que están dedicados y todo lo que hagan será vano”." WHERE ayatNumber = 140 AND chapterNumber = 7;
UPDATE verses SET es = "Dijo: “¿Debo acaso buscaros un dios distinto de Al-lah, si Él es Quien os ha ensalzado por encima de todos los pueblos?”." WHERE ayatNumber = 141 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Acordaos de</em> cuando os libramos del pueblo del Faraón que os afligía con un doloroso tormento, matando a vuestros hijos y dejando con vida a vuestras mujeres. Era para vosotros una gran prueba de vuestro Señor." WHERE ayatNumber = 142 AND chapterNumber = 7;
UPDATE verses SET es = "E hicimos a Moisés una promesa de treinta noches que complementamos con otras diez. Así quedó completado el tiempo señalado por su Señor –cuarenta noches-. Y Moisés dijo a su hermano, Aarón: “Actúa en mi lugar ante mi pueblo durante mi ausencia, trátalos bien y no sigas el camino de los que causan la discordia”." WHERE ayatNumber = 143 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando Moisés llegó al tiempo que le habíamos fijado y su Señor le habló, dijo: “Señor mío, muéstrate <em>Tú mismo</em> a mí para que pueda contemplarte”. Él respondió: “No me verás, pero mira hacia el monte: si permanece en su lugar, entonces Me verás”. Y cuando su Señor se manifestó en el monte, lo hizo pedazos, y Moisés cayó desvanecido. Cuando se recuperó, dijo: “Santo eres Tú, vuelvo a Ti y soy el primero de los creyentes”." WHERE ayatNumber = 144 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Dios</em> dijo: “Oh, Moisés, te he elegido por encima de los hombres <em>de tu época</em> con Mis Mensajes y con Mis Palabras. Toma, pues, lo que te he dado y sé de los agradecidos." WHERE ayatNumber = 145 AND chapterNumber = 7;
UPDATE verses SET es = "Entonces escribimos para él en las Tablas acerca de todo, una exhortación y una explicación de todas las cosas. “Mantenlas, pues, firmemente, y encomienda a tu pueblo que siga lo mejor de ellas. Pronto os mostraré la morada de los transgresores." WHERE ayatNumber = 146 AND chapterNumber = 7;
UPDATE verses SET es = "En breve alejaré de Mis Signos a quienes se comportaron orgullosamente en el país y de manera injusta; pues aunque vean todos los Signos, no creerán en ellos; y si ven el camino de la justicia, no lo adoptarán como <em>su</em> camino; pero si ven el camino del error, lo seguirán como el <em>suyo.</em> Esto es porque trataron a Nuestros Signos como mentiras e hicieron caso omiso de ellos." WHERE ayatNumber = 147 AND chapterNumber = 7;
UPDATE verses SET es = "Y los que no creen en Nuestros Signos y en el encuentro del Más llá, <em>sepan que </em>sus obras son vanas. ¿Pueden acaso <em>esperar</em> ser recompensados por <em>algo</em> distinto a lo que hicieron?" WHERE ayatNumber = 148 AND chapterNumber = 7;
UPDATE verses SET es = "Las gentes de Moisés, en su ausencia, hicieron de sus aderezos un becerro - un cuerpo que producía un sonido de mugido. ¿Acaso no veían ue no les hablaba ni los guiaba por ningún camino? Lo utilizaron <em>para adorarlo</em> y fueron transgresores." WHERE ayatNumber = 149 AND chapterNumber = 7;
UPDATE verses SET es = "Mas cuando fueron castigados con el remordimiento y vieron que en efecto se habían extraviado, dijeron: “Si nuestro Señor no se apiada de nosotros y no nos perdona, estaremos ciertamente entre los perdedores”." WHERE ayatNumber = 150 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando Moisés volvió a su pueblo, indignado y entristecido, dijo: “es abominable lo que hicisteis en mi lugar y en mi ausencia. ¿Os apresurasteis <em>a buscar un camino para vosotros sin esperar</em> la orden de vuestro Señor?”. Entonces arrojó las Tablas y asió la cabeza de su hermano, atrayéndolo hacia sí. Él (Aarón) dijo: “Hijo de mi madre, el pueblo en verdad me consideró débil y estuvieron a punto de atarme. No hagas, pues, que los enemigos se alegren de <em>mi desgracia</em> ni me coloques entre los injustos”." WHERE ayatNumber = 151 AND chapterNumber = 7;
UPDATE verses SET es = "Moisés dijo: “Señor Mío, perdónanos a mí y a mi hermano y admítenos en Tu misericordia, ya que Tú eres el más Misericordioso de cuantos muestran misericordia”." WHERE ayatNumber = 152 AND chapterNumber = 7;
UPDATE verses SET es = "<em> En cuanto a</em> quienes tomaron el becerro <em>para adorarlo,</em> la cólera de su Señor se abatirá sobre ellos, así como la humillación en la vida presente. De este modo recompensamos a quienes urden mentiras." WHERE ayatNumber = 153 AND chapterNumber = 7;
UPDATE verses SET es = "Pero para quienes cometieron malas obras y posteriormente se arrepintieron y creyeron, sepan que en verdad tu Señor es, después de eso, el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 154 AND chapterNumber = 7;
UPDATE verses SET es = "Cuando se apaciguó la ira de Moisés, tomó las Tablas en cuya escritura estaba la guía y la misericordia para los que temen a su Señor." WHERE ayatNumber = 155 AND chapterNumber = 7;
UPDATE verses SET es = "Y Moisés eligió de su pueblo a setenta hombres para Nuestra cita. Pero cuando el terremoto cayó sobre ellos, dijo: “Señor mío, si se Te ubiera antojado, podrías haberlos destruido antes <em>de esto, y también</em> a mí. ¿Vas a destruirnos por lo que han hecho los necios de entre nosotros? Esto no es más que una prueba tuya. Tú haces que en ella perezcan quienes quieres y guías a quienes deseas. Tú eres nuestro Protector; perdónanos y ten misericordia de nosotros, pues Tú eres el Mejor de los que perdonan”." WHERE ayatNumber = 156 AND chapterNumber = 7;
UPDATE verses SET es = "“Y ordena para nosotros el bien en este mundo y en el futuro: hemos vuelto a Ti<em> con arrepentimiento”. Dios</em> respondió: “Azoto con Mi castigo a quien me place; pero Mi misericordia abarca todas las cosas; por ello la decretaré para los que actúan justamente, pagan el Zakat y para los que creen en Nuestros Signos." WHERE ayatNumber = 157 AND chapterNumber = 7;
UPDATE verses SET es = "“Quienes siguen al Mensajero, el Profeta, el Iletrado, a quien encuentran mencionado en la Torah y en el Evangelio que <em>están</em> con ellos. Él les ordena el bien y les prohíbe el mal, y hace lícitas para ellos las cosas buenas e ilícitas las cosas malas, retirando de ellos las cargas y los grilletes que les aprisionaban. Por tanto, quienes crean en él, lo honren y apoyen, lo ayuden y sigan la luz que ha sido revelada con él: ésos serán los que prosperen.”" WHERE ayatNumber = 158 AND chapterNumber = 7;
UPDATE verses SET es = "Diles: “¡Oh, humanidad! En verdad soy un Mensajero enviado a todos vosotros por Al-lah, a Quien pertenece el reino de los cielos y la tierra. No existe otro dios sino Él. Él da la vida y causa la muerte. Creed pues en Al-lah y en Su Mensajero, el Profeta Iletrado, que cree en Al-lah y en Sus Palabras; y seguidlo para que seáis bien guiados”." WHERE ayatNumber = 159 AND chapterNumber = 7;
UPDATE verses SET es = "Y entre el pueblo de Moisés hay un grupo que guía con la verdad y establece la justicia." WHERE ayatNumber = 160 AND chapterNumber = 7;
UPDATE verses SET es = "Y los dividimos en doce tribus de pueblos <em>distintos.</em> Y evelamos a Moisés, cuando su pueblo le pidió de beber, <em>diciéndole</em> “golpea la roca con tu cayado”, y de ella brotaron doce manantiales; cada tribu sabía su lugar donde beber; e hicimos que las nubes los cubrieran, enviándoles miel y codornices: “comed de las cosas buenas que os hemos proporcionado”. Mas no fue a Nosotros a quien perjudicaron, sino que fue a sí mismos a quienes se perjudicaban." WHERE ayatNumber = 161 AND chapterNumber = 7;
UPDATE verses SET es = "Y<em> recuerda</em> cuando se les dijo: “Habitad en esta ciudad y omed de ella en donde queráis, y decid: “¡<em>Dios</em>! aligera <em> nuestra</em> carga”; y entrad por la puerta con humildad, ya que os perdonaremos vuestros pecados y haremos que prosperen aquellos que hagan el bien”." WHERE ayatNumber = 162 AND chapterNumber = 7;
UPDATE verses SET es = "Pero los transgresores de entre ellos <em>la </em>cambiaron por una palabra distinta a la que se les dijo. Por ello les enviamos un castigo desde el cielo, a causa de sus malas acciones." WHERE ayatNumber = 163 AND chapterNumber = 7;
UPDATE verses SET es = "Pregúntales sobre la ciudad que se levantaba junto al mar. Cuando violaron el Sabbath; cuando los peces les llegaban en su día del Sabbath, apareciendo en la superficie <em>del agua</em>, pero no les llegaban si no guardaban el Sabbath. De este modo los probamos porque eran rebeldes." WHERE ayatNumber = 164 AND chapterNumber = 7;
UPDATE verses SET es = "Y <em>recuerda</em> cuando un grupo de ellos dijeron: “¿por qué predicáis a un pueblo al que Al-lah va a destruir o castigar con un severo castigo?”. Respondieron: “para ser absueltos a la vista de vuestro Señor, y por si acaso temieran <em>a Al-lah</em>”." WHERE ayatNumber = 165 AND chapterNumber = 7;
UPDATE verses SET es = "Mas cuando olvidaron todo aquello con lo que habían sido exhortados, salvamos a quienes prohibían el mal e impusimos a los transgresores un severo castigo a causa de las malas acciones que solían cometer." WHERE ayatNumber = 166 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando se rebelaron insolentemente contra lo que les había sido prohibido, les dijimos: “¡Sed simios despreciables!”." WHERE ayatNumber = 167 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Acuérdate de</em> cuando tu Señor proclamó que levantaría ciertamente contra ellos, hasta el Día de la Resurrección, a quienes les impondrían un doloroso tormento. En verdad, tu Señor es rápido en la retribución, y es <em>también</em> el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 168 AND chapterNumber = 7;
UPDATE verses SET es = "Y los dividimos en la tierra en pueblos <em>separados.</em> Entre ellos los hay justos y otros que no lo son. Mas los probamos con cosas buenas y cosas malas para que volvieran." WHERE ayatNumber = 169 AND chapterNumber = 7;
UPDATE verses SET es = "Después vino tras ellos una generación <em>malvada</em> que heredó el Libro. Toman los bienes despreciables de este<em> mundo</em> vil y dicen: “se nos perdonará”. Pero si les llegaran de nuevo bienes similares, los tomarían. ¿Acaso no se estableció con ellos el pacto del Libro, para que no dijeran de Al-lah <em>nada</em> que no fuera la verdad? Y han estudiado lo que hay en él. Mas la morada del Más Allá es mejor para los justos. ¿No podéis pues comprenderlo?" WHERE ayatNumber = 170 AND chapterNumber = 7;
UPDATE verses SET es = "En <em>cuanto a</em> quienes se aferran al Libro y cumplen la ración, en verdad que no permitiremos que se pierda la recompensa de <em>esos hombres</em> justos." WHERE ayatNumber = 171 AND chapterNumber = 7;
UPDATE verses SET es = "Y recuerda cuando hicimos que el monte colgara sobre ellos como si uera una sombra, y creyeron que iba a caer sobre ellos; <em>dijimos:</em> “Aferraos a lo que os hemos entregado, y recordad lo que hay allí para que os salvéis”." WHERE ayatNumber = 172 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando tu Señor crea de los hijos de Adán –de su misma carne-, su descendencia y los convierte en testigos con­tra ellos mismos diciendo: “¿Acaso no soy vuestro Señor?”. Ellos responden: “Sí, somos testigos de ello”. <em>Esto lo hace Él</em> no sea que digáis en el Día de la Resurre­cción: “En verdad lo desconocíamos”." WHERE ayatNumber = 173 AND chapterNumber = 7;
UPDATE verses SET es = "O <em>no sea que</em> digáis: “sólo nuestros padres fueron los que atribuyeron copartícipes <em>a Dios</em> en el pasado, y nosotros fuimos <em>simplemente</em> su descendencia. ¿Nos vas a destruir por lo que hicieron los mentirosos?”." WHERE ayatNumber = 174 AND chapterNumber = 7;
UPDATE verses SET es = "Pero de este modo dejamos bien claros los Signos, para que <em>sean advertidos</em> y puedan volver<em> a Nosotros.</em>" WHERE ayatNumber = 175 AND chapterNumber = 7;
UPDATE verses SET es = "Nárrales la historia de aquél a quien dimos Nuestros Signos, pero e apartó de ellos; por lo que Satanás lo persiguió y se convirtió <em>en uno</em> de los que se extravían." WHERE ayatNumber = 176 AND chapterNumber = 7;
UPDATE verses SET es = "Si hubiésemos querido, lo hubiéramos exaltado con ellos; pero se inclinó a la tierra y siguió sus inclinaciones malvadas. Su caso es imilar al de un perro que saca la lengua por agotamiento <em>mientras te ladra</em>. Tanto si haces el movimiento de arrojarle una piedra como si lo dejas en paz, continúa sacando la lengua. Así ocurre con las gentes que no creen en Nuestros Signos. Nárrales, pues, os episodios <em>de la historia</em>, para que reflexionen <em>y extraigan lecciones de ellos</em>." WHERE ayatNumber = 177 AND chapterNumber = 7;
UPDATE verses SET es = "Abominable es el ejemplo de las gentes que tratan Nuestros Signos como mentiras. Fue a sí mismos a quienes se perjudicaron." WHERE ayatNumber = 178 AND chapterNumber = 7;
UPDATE verses SET es = "A quien Al-lah guía está en el camino recto. Y a quienes considera extraviados, ellos serán los perdedores." WHERE ayatNumber = 179 AND chapterNumber = 7;
UPDATE verses SET es = "¡En verdad, hemos creado a muchos Yinn y hombres cuyo fin será el nfierno! Tienen corazón <em>pero</em> no entienden con él, tienen ojos <em>pero</em> no ven con ellos y tienen oídos <em>pero</em> no oyen con ellos. Son como el ganado; ¡no! están <em>aún</em> más extraviados. En verdad que son <em>totalmente</em> incautos." WHERE ayatNumber = 180 AND chapterNumber = 7;
UPDATE verses SET es = "<em> Sólo</em> a Al-lah pertenecen <em>todos</em> los atributos perfectos. Invocadle, pues, mediante ellos. Y dejad solos a los que se desvían del camino recto en cuanto a Sus atributos. Serán retribuidos por lo que hacen." WHERE ayatNumber = 181 AND chapterNumber = 7;
UPDATE verses SET es = "Entre los que hemos creado hay gentes que guían a <em>los hombres</em> con la verdad y hacen justicia con ella." WHERE ayatNumber = 182 AND chapterNumber = 7;
UPDATE verses SET es = "Pero a quienes rechazan Nuestros Signos, los llevaremos a <em>la destrucción</em> paso a paso, de manera que no se den cuenta." WHERE ayatNumber = 183 AND chapterNumber = 7;
UPDATE verses SET es = "Les daré rienda suelta; en verdad, Mi plan es poderoso." WHERE ayatNumber = 184 AND chapterNumber = 7;
UPDATE verses SET es = "¿No se dan cuenta <em>de que</em> no hay locura alguna en su compañero? Él no es más que un sincero Amonestador." WHERE ayatNumber = 185 AND chapterNumber = 7;
UPDATE verses SET es = "Pues ¿no han contemplado acaso el reino de los cielos y la tierra y todas las cosas que Al-lah ha creado? Y ¿no <em>ven acaso</em> que su propio plazo quizás está ya cerca de su final? Entonces ¿en qué creerán después?" WHERE ayatNumber = 186 AND chapterNumber = 7;
UPDATE verses SET es = "A quien Al-lah declara extraviado no puede tener guía alguna. Y Él lo deja en su transgresión, vagando sin rumbo." WHERE ayatNumber = 187 AND chapterNumber = 7;
UPDATE verses SET es = "Te preguntan sobre la Hora: “¿Cuándo va a suceder?”. Diles: “su conocimiento sólo corresponde a mi Señor. Nadie puede revelarla en su momento sino Él. Gravita como un peso sobre los cielos y la tierra. No os llegará sino de improviso”. Te preguntan como si tú la conocieses bien. Diles: “su conocimiento sólo corresponde a Al-lah; aunque la mayoría de los hombres lo desconocen”." WHERE ayatNumber = 188 AND chapterNumber = 7;
UPDATE verses SET es = "Diles: No tengo poder para beneficiarme o perjudicarme a mí mismo, salvo que Al-lah lo quiera. Y si conociera lo desconocido, habría conseguido abundancia de bienes, y el mal no me habría tocado. No soy más que un Amonestador y un portador de la buena nueva para las gentes que creen”." WHERE ayatNumber = 189 AND chapterNumber = 7;
UPDATE verses SET es = "Él es quien os creó de un solo ser e hizo de aquel su compañera, para que encontrara consuelo en ella. Y cuando la ha cubierto, ella concibe y porta una carga ligera con la que camina. Y cuando se hace más pesada, ambos rezan a Al-lah: “Si nos das <em>un hijo</em> sano y bueno, seremos en verdad de los agradecidos”." WHERE ayatNumber = 190 AND chapterNumber = 7;
UPDATE verses SET es = "Mas cuando Él les da <em>un hijo</em> bueno, Le atribuyen socios respecto a lo que Él les ha dado. Pero Al-lah está muy por encima de todo lo que <em>Le</em> asocian." WHERE ayatNumber = 191 AND chapterNumber = 7;
UPDATE verses SET es = "¿Acaso <em>Le</em> asocian como partícipes a aquellos que no crean nada sino que ellos mismos son creados?" WHERE ayatNumber = 192 AND chapterNumber = 7;
UPDATE verses SET es = "No pueden prestarles ayuda alguna, ni siquiera ayudarse a sí mismos." WHERE ayatNumber = 193 AND chapterNumber = 7;
UPDATE verses SET es = "Si les llamas para guiarlos, no te seguirán. Lo mismo da que los llames o que permanezcas callado." WHERE ayatNumber = 194 AND chapterNumber = 7;
UPDATE verses SET es = "En verdad, a quienes invocáis en lugar de Al-lah son <em>simples</em> siervos como vosotros. Invocadlos pues y que os contesten si sois veraces." WHERE ayatNumber = 195 AND chapterNumber = 7;
UPDATE verses SET es = "¿Tienen acaso pies con los que andar, manos con las que sostener, ojos con los que ver u oídos con los que oír? Diles: “Invocad a los ocios que asociáis <em>a Dios,</em> conspirad después <em>todos</em> <em>vosotros </em>contra mí, y no me deis tiempo”." WHERE ayatNumber = 196 AND chapterNumber = 7;
UPDATE verses SET es = "“En verdad, mi Protector es Al-lah, Quien reveló el Libro. Él protege al justo”." WHERE ayatNumber = 197 AND chapterNumber = 7;
UPDATE verses SET es = "“Mas aquellos a los que invocáis en lugar de Él no tienen poder para ayudaros, ni pueden ayudarse a sí mismos”." WHERE ayatNumber = 198 AND chapterNumber = 7;
UPDATE verses SET es = "Si los invitáis a la guía, no os escuchan. Y ves que te miran, pero no te ven." WHERE ayatNumber = 199 AND chapterNumber = 7;
UPDATE verses SET es = "Practica el perdón, ordena el bien y apártate de los ignorantes." WHERE ayatNumber = 200 AND chapterNumber = 7;
UPDATE verses SET es = "Y si te asalta una sugerencia malvada de Satanás, busca refugio en Al-lah, Él es el que todo lo oye, el Omnisciente." WHERE ayatNumber = 201 AND chapterNumber = 7;
UPDATE verses SET es = "<em> En cuanto a </em>quienes son justos, cuando les asalta una sugerencia malvada de Satanás, se acuerdan <em>de Dios</em>; y he ahí que empiezan a ver <em>las cosas rectamente</em>." WHERE ayatNumber = 202 AND chapterNumber = 7;
UPDATE verses SET es = "Pero sus hermanos les hacen continuar en el error, y no ceden." WHERE ayatNumber = 203 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando no les muestras un Signo, dicen: “¿Por qué no lo inventas?”. Diles: “Yo sigo únicamente lo que me ha revelado Mi Señor. Éstos son Signos luminosos de vuestro Señor, guía y bendición para las gentes que creen”." WHERE ayatNumber = 204 AND chapterNumber = 7;
UPDATE verses SET es = "Y cuando se recite el Corán, prestadle oído y guardad silencio, a fin de que se os muestre misericordia." WHERE ayatNumber = 205 AND chapterNumber = 7;
UPDATE verses SET es = "Acuérdate de tu Señor en tu mente con humildad y temor, sin alzar la voz, por las mañanas y por las tardes; y no seas de los negligentes." WHERE ayatNumber = 206 AND chapterNumber = 7;
UPDATE verses SET es = "En verdad, quienes están cerca de tu Señor, no se apartan con orgullo de Su adoración, sino que Le glorifican y se postran ante Él." WHERE ayatNumber = 207 AND chapterNumber = 7;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 8;
UPDATE verses SET es = "Te preguntan sobre el botín <em>de guerra</em>. Diles: “El botín pertenece a Al-lah y al Mensajero. Temed pues a Al-lah, arreglad bien las cosas entre vosotros, y obedeced a Al-lah y a Su Mensajero, si sois creyentes." WHERE ayatNumber = 2 AND chapterNumber = 8;
UPDATE verses SET es = "Sólo son <em>verdaderos</em> creyentes aquellos cuyos corazones se estremecen cuando se menciona <em>el nombre de</em> Al-lah, que aumentan su fe cuando se les recitan Sus Signos y ponen su confianza en su Señor." WHERE ayatNumber = 3 AND chapterNumber = 8;
UPDATE verses SET es = "Quienes cumplen la Oración y gastan de lo que les hemos proporcionado." WHERE ayatNumber = 4 AND chapterNumber = 8;
UPDATE verses SET es = "Éstos son los verdaderos creyentes. Tienen grados <em>elevados</em> ante su Señor, así como el perdón y una generosa provisión." WHERE ayatNumber = 5 AND chapterNumber = 8;
UPDATE verses SET es = "Ya que <em>fue</em> tu Señor quien, legítimamente, te hizo salir de u casa aunque una parte de los creyentes se mostraba contraria, <em>así Él te ayudó contra tu enemigo.</em>" WHERE ayatNumber = 6 AND chapterNumber = 8;
UPDATE verses SET es = "Ellos disputan contigo sobre la verdad después de que se ha hecho vidente, como si fuesen arrastrados a la muerte mientras realmente <em>la</em> ven." WHERE ayatNumber = 7 AND chapterNumber = 8;
UPDATE verses SET es = "Y <em>acordaos de</em> cuando Al-lah os prometió que uno de los dos grupos sería vuestro, y vosotros deseabais que lo fuera el que no tenía peligro, pero Al-lah deseó demostrar la verdad con sus palabras y exterminar de raíz a los incrédulos." WHERE ayatNumber = 8 AND chapterNumber = 8;
UPDATE verses SET es = "Para establecer la verdad y reducir a la nada lo que es falso, aunque no les agrade a los culpables." WHERE ayatNumber = 9 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando implorasteis la ayuda de vuestro Señor, y Él os respondió, <em>diciendo:</em> “Os ayudaré con mil ángeles, uno tras otro”." WHERE ayatNumber = 10 AND chapterNumber = 8;
UPDATE verses SET es = "Y Al-lah no hizo esto sino como una buena nueva, para que vuestros corazones se calmaran con ella. Pero la ayuda sólo procede de Al-lah; en verdad, Al-lah es Poderoso, Sabio." WHERE ayatNumber = 11 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando hizo que os llegara el sueño como <em>signo de</em> seguridad Suyo, y os envió agua desde las nubes, para purificaros, y eliminar de vosotros la inmundicia de Satanás, y fortalecer vuestros corazones, y hacer <em>vuestros</em> pasos firmes con todo ello." WHERE ayatNumber = 12 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando tu Señor reveló a los ángeles <em>lo siguiente:</em> “Estoy con vosotros; dad firmeza a quienes creen. Yo infundiré el terror en los corazones de los incrédulos. Golpeadles, pues, en la parte superior de sus cuellos, y golpeadles en las yemas de los dedos”." WHERE ayatNumber = 13 AND chapterNumber = 8;
UPDATE verses SET es = "Esto es por haberse opuesto a Al-lah y a Su Mensajero. Pues para quien se opone a Al-lah y a Su Mensajero, Al-lah es ciertamente severo en Su castigo." WHERE ayatNumber = 14 AND chapterNumber = 8;
UPDATE verses SET es = "Ése <em>es vuestro castigo,</em> probadlo, pues; y <em>sabed</em> que, para los incrédulos, está el castigo del Fuego." WHERE ayatNumber = 15 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Cuando encontréis a los incrédulos que avanzan con fuerza, no les volváis la espalda." WHERE ayatNumber = 16 AND chapterNumber = 8;
UPDATE verses SET es = "Pues quien les vuelva la espalda en ese día, salvo que esté maniobrando para la batalla o vuelva para <em>unirse a otro</em> grupo, atraerá hacia sí la ira de Al-lah y el Infierno será su morada. ¡Qué mal lugar de descanso es!" WHERE ayatNumber = 17 AND chapterNumber = 8;
UPDATE verses SET es = "Así pues, vosotros no los matasteis, sino que fue Al-lah quien los mató. Y no fuiste tú el que tirabas cuando tiraste algo, sino que fue Al-lah quien tiró, <em>para vencer a los incrédulos</em> y dar a los creyentes una prueba –una prueba bondadosa- de Sí mismo. En verdad, Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 18 AND chapterNumber = 8;
UPDATE verses SET es = "Esto <em>es lo que sucedió;</em> y <em>sabed</em> que Al-lah es quien debilita la estrategia de los incrédulos." WHERE ayatNumber = 19 AND chapterNumber = 8;
UPDATE verses SET es = "Si buscabais un juicio, la sentencia os ha llegado ciertamente. Y i desistís, será mejor para vosotros; pero si volvéis a la <em>lucha</em> volveremos <em>también</em> y de nada os servirá vuestro ejército por numeroso que sea, pues <em>sabed</em> que Al-lah está con los creyentes." WHERE ayatNumber = 20 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Obedeced a Al-lah y a Su Mensajero, y no os apartéis de él mientras <em>le</em> oigáis hablar." WHERE ayatNumber = 21 AND chapterNumber = 8;
UPDATE verses SET es = "Y no seáis como quienes dicen: “escuchamos”, pero no escuchan." WHERE ayatNumber = 22 AND chapterNumber = 8;
UPDATE verses SET es = "En verdad, a los ojos de Al-lah, las peores bestias son los sordos <em> y</em> los mudos, que no entenderán." WHERE ayatNumber = 23 AND chapterNumber = 8;
UPDATE verses SET es = "Y si Al-lah hubiese reconocido algo bueno en ellos, en verdad les habría hecho oír. Mas si Él les hace oír <em>ahora,</em> se apartarán con aversión." WHERE ayatNumber = 24 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! responded a Al-lah y al Mensajero cuando él os llama para daros la vida, y sabed que Al-lah está más cerca del hombre que su propio corazón, y que junto a Él seréis reunidos." WHERE ayatNumber = 25 AND chapterNumber = 8;
UPDATE verses SET es = "Mas estad atentos ante una aflicción que no golpeará exclusivamente a aquellos de vosotros que han obrado mal. Y sabed que Al-lah es severo en el castigo." WHERE ayatNumber = 26 AND chapterNumber = 8;
UPDATE verses SET es = "Y acordaos de cuando erais muy pocos <em>y</em> considerados débiles en la tierra, y temíais que las gentes os exterminaran, pero Él os protegió y fortaleció con Su ayuda, y os proporcionó cosas buenas para que fueseis agradecidos." WHERE ayatNumber = 27 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh, vosotros los que creéis! no traicionéis a Al-lah y al Mensajero, cuando <em>a menudo </em>traicionáis vuestros compromisos a sabiendas." WHERE ayatNumber = 28 AND chapterNumber = 8;
UPDATE verses SET es = "Mas sabed que vuestras riquezas y vuestros hijos no son más que una prueba y que en Al-lah es donde tenéis una gran recompensa." WHERE ayatNumber = 29 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh, vosotros, los que creéis! si teméis a Al-lah, Él os concederá una distinción, os librará de vuestros males y os perdonará; pues Al-lah es <em>Señor</em> de gran magnificencia." WHERE ayatNumber = 30 AND chapterNumber = 8;
UPDATE verses SET es = "Y <em>acordaos de</em> cuando los incrédulos tramaron contra ti para hacerte prisionero, matarte o expulsarte. Pero ellos hicieron planes y Al-lah <em>también</em> hizo un plan, y Al-lah es el Mejor de los que planifican." WHERE ayatNumber = 31 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando se les recitan nuestros versículos dicen: “Hemos oído. Si quisiéramos, podríamos ciertamente decir cosas similares. Éstos no son más que <em>simples</em> cuentos de los antiguos”." WHERE ayatNumber = 32 AND chapterNumber = 8;
UPDATE verses SET es = "Y <em>acordaos de</em> cuando dijeron: “Oh Al-lah, si ésta es ciertamente la verdad procedente de Ti, haz que lluevan sobre nosotros piedras desde el cielo o mándanos un doloroso castigo”." WHERE ayatNumber = 33 AND chapterNumber = 8;
UPDATE verses SET es = "Pero Al-lah no los castigaría mientras tú estuvieras entre ellos, ni Al-lah los castigaría mientras imploraran perdón." WHERE ayatNumber = 34 AND chapterNumber = 8;
UPDATE verses SET es = "Mas ¿qué excusa tienen <em>ahora</em> para que Al-lah no los castigue, si impiden a <em>los hombres</em> la entrada en la Mezquita agrada y no son sus <em>verdaderos</em> guardianes? Sus <em>verdaderos</em> guardianes son únicamente los justos, pero la mayoría de ellos no lo sabe." WHERE ayatNumber = 35 AND chapterNumber = 8;
UPDATE verses SET es = "Y su Oración en la Casa <em>Sagrada</em> no consiste más que en silbidos y aplausos. “Probad el castigo por haber sido incrédulos”." WHERE ayatNumber = 36 AND chapterNumber = 8;
UPDATE verses SET es = "En verdad, los incrédulos emplean su riqueza en apartar a la gente del camino de Al-lah. Ciertamente continuarán gastándola; pero después o les producirá <em>mas que</em> en llantos<em>y lamentos ante su completo fracaso</em>, y entonces serán <em>rotundamente</em> vencidos. Y los incrédulos serán reunidos en el Infierno;" WHERE ayatNumber = 37 AND chapterNumber = 8;
UPDATE verses SET es = "Para que Al-lah separe a los malos de los buenos, ponga los malos unos sobre otros, apilándolos todos juntos <em>y</em> los arroje después al Infierno. Éstos son en verdad los perdedores." WHERE ayatNumber = 38 AND chapterNumber = 8;
UPDATE verses SET es = "Diles a los incrédulos, si desisten, que lo pasado se les erdonará; pero si vuelven a <em>ello</em>, entonces, en verdad, tienen <em>ante ellos</em> el ejemplo de los pueblos que les precedieron." WHERE ayatNumber = 39 AND chapterNumber = 8;
UPDATE verses SET es = "Y combatidlos hasta que cese la persecución y la religión sea totalmente para Al-lah. Pero si desisten, <em>sepan que </em>en verdad Al-lah vigila todo lo que hacen." WHERE ayatNumber = 40 AND chapterNumber = 8;
UPDATE verses SET es = "Si vuelven la espalda, sabed que Al-lah es vuestro Protector. ¡Qué magnífico Protector y qué magnífico Defensor!" WHERE ayatNumber = 41 AND chapterNumber = 8;
UPDATE verses SET es = "Y sabed que de cualquier cosa que toméis como botín <em>en la guerra,</em> la quinta parte pertenecerá a Al-lah, al Mensajero, a los parientes y a los huérfanos, a los necesitados y a los viajeros, si creéis en Al-lah y en lo que revelamos a Nuestro siervo el Día de la Distinción, -el día en que se enfrentaron los dos ejércitos– pues Al-lah tiene el poder para hacer todas las cosas." WHERE ayatNumber = 42 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando estabais en la ladera más próxima <em>del valle</em>, y ellos en la más alejada, con la caravana debajo de vosotros. Si vosotros, <em>las dos partes beligerantes</em>, tuvieseis que decidir l momento del encuentro, habríais disputado en cuanto al tiempo <em>para favorecer vuestros propios intereses</em>. Pero estaba destinado que Al-lah decidiera el momento, para cumplir lo que estaba decretado; para que perecieran los condenados a perecer con justificación clara y sobrevivieran los que merecían sobrevivir con el apoyo de una justificación manifiesta. Pues en verdad Al-lah es quien todo lo oye, el Omnisciente." WHERE ayatNumber = 43 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando Al-lah, en tu sueño, te los hizo ver como poco numerosos; ya que si te los hubiera mostrado en gran número, ciertamente habríais vacilado y estado mutuamente en desacuerdo sobre la cuestión; pero Al-lah<em> os</em> salvó. En verdad, Él conoce perfectamente lo que hay en vuestros corazones." WHERE ayatNumber = 44 AND chapterNumber = 8;
UPDATE verses SET es = "Y acordaos del momento de vuestro enfrentamiento, cuando Él hizo que aparecieran como pocos ante vuestros ojos, y a vosotros como pocos ante los ojos de ellos, para que Al-lah cumpliese lo que estaba ecretado. Y a Al-lah se someten todos los asuntos para su <em>decisión final</em>." WHERE ayatNumber = 45 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! cuando encontréis un ejército, permaneced firmes, y acordaos mucho de Al-lah para que triunféis." WHERE ayatNumber = 46 AND chapterNumber = 8;
UPDATE verses SET es = "Y obedeced a Al-lah y a Su Mensajero y no disputéis mutuamente, no sea que flaqueéis y perdáis el poder. Sed perseverantes; <em>pues </em> en verdad, Al-lah está con los perseverantes." WHERE ayatNumber = 47 AND chapterNumber = 8;
UPDATE verses SET es = "No seáis como los que salieron de sus casas jactándose de sus obras, exhibiéndolas a la gente y evitando que<em> la gente</em> alcanzara el camino de Al-lah, pues Al-lah abarca todo lo que hacen." WHERE ayatNumber = 48 AND chapterNumber = 8;
UPDATE verses SET es = "Y de cuando Satanás les hizo que sus obras <em>les parecieran</em> justas ante sus ojos, y les dijo: “ninguno de entre los hombres os vencerá en este día, ya que yo soy vuestro protector”. Mas cuando los dos ejércitos se vieron mutuamente, volvió sobre sus talones y dijo: “en verdad, no tengo nada que ver con vosotros; yo veo lo que vosotros no veis. Ciertamente temo a Al-lah; y Al-lah es severo en el castigo”." WHERE ayatNumber = 49 AND chapterNumber = 8;
UPDATE verses SET es = "Cuando los hipócritas y los de corazón enfermo dijeron: “su religión ha ofuscado <em>a estos hombres”</em>. Pero quien pone su confianza en Al-lah, verá que en verdad Al-lah es Poderoso, Sabio." WHERE ayatNumber = 50 AND chapterNumber = 8;
UPDATE verses SET es = "Ojalá pudieras ver cuando los ángeles se llevan las almas de los ncrédulos, golpeándolos en los rostros y en las espaldas, <em>diciendo:</em> “¡Probad el castigo del fuego!”." WHERE ayatNumber = 51 AND chapterNumber = 8;
UPDATE verses SET es = "“Esto es por lo que vuestras manos hicieron anteriormente, pues <em>sabed que</em> Al-lah no es en absoluto injusto con Sus siervos”." WHERE ayatNumber = 52 AND chapterNumber = 8;
UPDATE verses SET es = "Su <em>ejemplo es </em>como el del pueblo del Faraón y los que los precedieron: no creyeron en los Signos de Al-lah. Así pues, Al-lah los castigó por sus pecados. En verdad, Al-lah es Poderoso y Severo en el castigo." WHERE ayatNumber = 53 AND chapterNumber = 8;
UPDATE verses SET es = "Esto es porque Al-lah nunca cambiaría una gracia concedida a un pueblo mientras no cambiaran su propia condición, pues <em>sabed</em> que Al-lah es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 54 AND chapterNumber = 8;
UPDATE verses SET es = "<em> Su ejemplo es</em> como el del pueblo del Faraón y los que los precedieron: rechazaron los Signos de su Señor, por lo que los destruimos por sus pecados. Y ahogamos a las gentes del Faraón porque todos eran transgresores." WHERE ayatNumber = 55 AND chapterNumber = 8;
UPDATE verses SET es = "En verdad, ante Al-lah, las peores de las criaturas son los ingratos, y no creerán." WHERE ayatNumber = 56 AND chapterNumber = 8;
UPDATE verses SET es = "Aquellos con quienes cerraste un pacto. En cada ocasión rompen su pacto; y no temen a Dios." WHERE ayatNumber = 57 AND chapterNumber = 8;
UPDATE verses SET es = "Por eso, si los apresas en la guerra, infunde <em>con su derrota</em> el temor en los que están tras ellos, para que sean advertidos." WHERE ayatNumber = 58 AND chapterNumber = 8;
UPDATE verses SET es = "Y si temes la traición de un pueblo, devuélveles <em>su pacto</em> con equidad. En verdad, Al-lah no ama a los traidores." WHERE ayatNumber = 59 AND chapterNumber = 8;
UPDATE verses SET es = "Y que los incrédulos no piensen que <em>Nos</em> han vencido. En verdad, no pueden vencer el<em> designio de Dios.</em>" WHERE ayatNumber = 60 AND chapterNumber = 8;
UPDATE verses SET es = "Mas preparad contra ellos cuanto podáis de fuerza <em>armada</em> y de piquetes de caballería en la frontera, para que asustéis al enemigo de Al-lah y enemigo vuestro, y a los demás que están tras ellos y a quienes desconocéis, <em>pero</em> que son conocidos de Al-lah. Pues todo lo que gastéis por la causa de Al-lah os será reembolsado del todo y no seréis perjudicados." WHERE ayatNumber = 61 AND chapterNumber = 8;
UPDATE verses SET es = "Pero si se inclinan hacia la paz, inclínate tú también hacia ella y deposita tu confianza en Al-lah. En verdad, Él es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 62 AND chapterNumber = 8;
UPDATE verses SET es = "Pero si pretenden engañarte, en verdad Al-lah te basta. Él es Quien te ha fortalecido con Su ayuda y con los creyentes;" WHERE ayatNumber = 63 AND chapterNumber = 8;
UPDATE verses SET es = "Pues Él ha puesto el afecto entre sus corazones. Si hubieses gastado todo lo que hay en la tierra, no hubieras podido poner afecto entre sus corazones, pero Al-lah lo ha puesto entre ellos. En verdad, Él es Poderoso, Sabio." WHERE ayatNumber = 64 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh, Profeta! Al-lah es suficiente para ti y para quienes te siguen de entre los creyentes." WHERE ayatNumber = 65 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh, Profeta! apremia a los creyentes al combate. Si hubiese veinte de vosotros perseverantes, podrían vencer a doscientos; si hubiese cien de vosotros, vencerían a un millar de los incrédulos, porque son gente que no entienden." WHERE ayatNumber = 66 AND chapterNumber = 8;
UPDATE verses SET es = "Ahora Al-lah ha aligerado vuestra carga, pues Él sabe que hay debilidad en vosotros. Así, si hubiese entre vosotros un centenar de perseverantes, vencerían a dos centenares; y si hubiese un millar de vosotros, vencerían a dos millares por mandato de Al-lah. Y Al-lah está con quienes son perseverantes." WHERE ayatNumber = 67 AND chapterNumber = 8;
UPDATE verses SET es = "No corresponde a un Profeta tener prisioneros mientras no haya iniciado una batalla regular en el país. Vosotros deseáis los bienes del mundo, mientras que Al-lah desea <em>para vosotros</em> el Más Allá. Pues Al-lah es Poderoso, Sabio." WHERE ayatNumber = 68 AND chapterNumber = 8;
UPDATE verses SET es = "Si no hubiera habido un decreto de Al-lah dictado previamente, en erdad os habría acaecido una gran desgracia en <em>relación con lo </em>que tomasteis." WHERE ayatNumber = 69 AND chapterNumber = 8;
UPDATE verses SET es = "Comed pues de lo que habéis ganado <em>en la guerra</em> como lícito y bueno, y temed a Al-lah. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 70 AND chapterNumber = 8;
UPDATE verses SET es = "¡Oh, Profeta! di a los cautivos que tenéis en vuestro poder: “si Al-lah descubriera bondad en vuestros corazones, os daría algo mejor de lo que os ha tomado, y os perdonaría. Pues Al-lah es el Sumo Indulgente, Misericordioso”." WHERE ayatNumber = 71 AND chapterNumber = 8;
UPDATE verses SET es = "Y si intentan traicionar tu confianza, ya traicionaron antes la confianza de Al-lah, pero Él les dejó indefensos. Pues Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 72 AND chapterNumber = 8;
UPDATE verses SET es = "En verdad quienes han creído y han abandonado sus hogares, luchando on sus bienes y sus personas por la causa de Al-lah, y quienes <em>les</em> han prestado cobijo y ayuda: éstos son, en verdad, mutuamente amigos. Mas de quienes han creído pero no han dejado sus casas, no eres en absoluto responsable de su protección mientras no abandonen sus hogares. Pero si buscan tu ayuda en <em>materias de</em> religión, es tu deber ayudarles, excepto contra un pueblo con el que tengáis una alianza. Pues Al-lah ve lo que hacéis." WHERE ayatNumber = 73 AND chapterNumber = 8;
UPDATE verses SET es = "Y quienes no creen: son <em>también</em> mutuamente amigos. Si no lo hacéis, habrá perturbaciones en el país y un gran desorden." WHERE ayatNumber = 74 AND chapterNumber = 8;
UPDATE verses SET es = "En cuanto a quienes han creído y han dejado sus casas, luchando por la causa de Al-lah, y aquellos que <em>les</em> han dado protección y ayuda: éstos son en verdad los verdaderos creyentes. Para ellos será el perdón y una generosa provisión." WHERE ayatNumber = 75 AND chapterNumber = 8;
UPDATE verses SET es = "Mas quienes han creído desde entonces, y han abandonado sus casas, luchando por la <em>causa de Al-lah</em> junto con vosotros: éstos son de los vuestros; y <em>en cuanto a</em> los parientes de sangre, están más cercanos entre sí, en el Libro de Al-lah. En verdad, Al-lah conoce perfectamente todas las cosas." WHERE ayatNumber = 76 AND chapterNumber = 8;
UPDATE verses SET es = "<em> Ésta es</em> una declaración de <em>completa</em> absolución por parte de Al-lah y Su Mensajero de toda <em>obligación</em> para con los idólatras a quienes habéis hecho promesas." WHERE ayatNumber = 1 AND chapterNumber = 9;
UPDATE verses SET es = "Viajad, pues, por el país durante cuatro meses, y sabed que no podréis frustrar<em> el plan</em> de Al-lah y que Al-lah humillará a los incrédulos." WHERE ayatNumber = 2 AND chapterNumber = 9;
UPDATE verses SET es = "Y <em>ésta es</em> una declaración de Al-lah y Su Mensajero al pueblo, en el día de la Gran Peregrinación, de que Al-lah no es responsable de los idólatras, como tampoco Su Mensajero. Así, si os arrepentís, será mejor para vosotros; pero si os apartáis, sabed que no podéis desbaratar <em>los planes</em> de Al-lah. Y dad la noticia de un doloroso castigo para los incrédulos." WHERE ayatNumber = 3 AND chapterNumber = 9;
UPDATE verses SET es = "Exceptuados aquellos de los idólatras con los que habéis concluido un tratado y que no os hayan fallado en el cumplimiento de sus obligaciones ni ayudado a nadie contra vosotros. Cumplid pues estos tratados que <em>habéis</em> establecido con ellos hasta su término. En verdad, Al-lah ama a los justos." WHERE ayatNumber = 4 AND chapterNumber = 9;
UPDATE verses SET es = "Y cuando hayan transcurrido los meses sagrados, matad a los dólatras dondequiera los encontréis, tomadlos <em>como prisioneros, </em>sitiadlos y permaneced a su espera emboscados en cualquier lugar. Pero si se arrepienten, cumplen la Oración y pagan el Zakat, dejadles que sigan libremente su camino. Al-lah, en verdad, es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 5 AND chapterNumber = 9;
UPDATE verses SET es = "Mas si alguno de los idólatras te pide protección, concédesela para que oiga la palabra de Al-lah. Llévalo después a su lugar seguro. Esto es porque se trata de gentes que no saben." WHERE ayatNumber = 6 AND chapterNumber = 9;
UPDATE verses SET es = "¿Cómo puede haber un tratado entre <em>estos</em> idólatras y Al-lah y Su Mensajero, excepto con aquellos con quienes has cerrado un pacto en la Mezquita Sagrada? Por ello, mientras permanezcan sinceros con vosotros, sed sinceros con ellos. En verdad, Al-lah ama a los justos." WHERE ayatNumber = 7 AND chapterNumber = 9;
UPDATE verses SET es = "¿Cómo <em>puede ser así</em> si, en caso de venceros no respetarían ningún lazo de parentesco o pacto respecto a vosotros? Os agradarían con sus palabras pero sus corazones lo rechaza­rían, ya que la mayoría de ellos son pérfidos." WHERE ayatNumber = 8 AND chapterNumber = 9;
UPDATE verses SET es = "Cambian a un precio ínfimo los Signos de Al-lah y apartan <em>a los hombres</em> de Su camino. ¡Qué malvado es en verdad lo que hacen!" WHERE ayatNumber = 9 AND chapterNumber = 9;
UPDATE verses SET es = "No respetan ningún lazo de parentesco o pacto respecto a quien confía <em>en ellos</em>. Son ellos los transgresores." WHERE ayatNumber = 10 AND chapterNumber = 9;
UPDATE verses SET es = "Pero si se arrepienten, cumplen la Oración y pagan el Zakat, entonces son vuestros hermanos en la fe. Pues explicamos los Signos a un pueblo que tiene conocimiento." WHERE ayatNumber = 11 AND chapterNumber = 9;
UPDATE verses SET es = "Mas si violan sus juramentos después de su pacto, e injurian vuestra religión, entonces combatid a <em>esos</em> jefes de la incredulidad – en verdad, no respetan sus juramentos – para que desistan." WHERE ayatNumber = 12 AND chapterNumber = 9;
UPDATE verses SET es = "¿No combatiríais a gentes que han roto sus juramentos y que aquinaron para expulsar al Mensajero, y fueron los primeros en iniciar <em>las hostilidades</em> contra vosotros? ¿Les tenéis miedo? No, Al-lah es más digno de que Le temáis, si sois creyentes." WHERE ayatNumber = 13 AND chapterNumber = 9;
UPDATE verses SET es = "Combatidlos, para que Al-lah los castigue por medio de vuestras manos; los humille, y os ayude a conseguir<em> la victoria</em> sobre ellos, y alivie las mentes de los hombres que creen;" WHERE ayatNumber = 14 AND chapterNumber = 9;
UPDATE verses SET es = "Y para que expulse la cólera de sus corazones. Pues Al-lah se dirige con misericordia a quien Le place. Y Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 15 AND chapterNumber = 9;
UPDATE verses SET es = "¿Creéis que se os dejaría solos, cuando Al-lah aún no ha conocido a aquellos de vosotros que luchan por la causa de<em> Al-lah</em> y no toman<em> a nadie</em> por amigo íntimo salvo a Al-lah, a su Mensajero y a los creyentes? Al-lah se entera perfectamente lo que hacéis." WHERE ayatNumber = 16 AND chapterNumber = 9;
UPDATE verses SET es = "No son los idolatras quienes hacen justicia a los objetivos por los que se construyen las mezquitas ni <em>pueden</em> mantenerlas adecuadamente mientras permanecen como testigos de su propia incredulidad. Son aquellos cuyas obras serán vanas y morarán en el Fuego." WHERE ayatNumber = 17 AND chapterNumber = 9;
UPDATE verses SET es = "En verdad que sólo es digno de conservar las Mezquitas de Al-lah quien cree en Al-lah y en el Último Día, cumple la Oración, paga el Zakat y no teme a nadie sino a Al-lah; son éstos los que tienen la mayor posibilidad de <em>contarse</em> entre los guiados." WHERE ayatNumber = 18 AND chapterNumber = 9;
UPDATE verses SET es = "¿Pensáis acaso que dar bebida a los peregrinos y mantener la Sagrada Mezquita es <em>igual a las obras de</em> quien cree en Al-lah en el Último Día y se esfuerza en el camino de Al-lah? <em>Todos ellos</em> no son iguales ante Al-lah. Y Al-lah no guía a los injustos." WHERE ayatNumber = 19 AND chapterNumber = 9;
UPDATE verses SET es = "Quienes creen y emigran <em>de sus casas por la causa de Dios</em> y se esfuerzan en la causa de Al-lah con sus bienes y sus personas son los que ocupan el puesto más alto ante Al-lah. Son ellos quienes triunfarán." WHERE ayatNumber = 20 AND chapterNumber = 9;
UPDATE verses SET es = "Su Señor les da la buena noticia de Su misericordia y de Su agrado, y de los jardines en los que habrá una felicidad eterna para ellos;" WHERE ayatNumber = 21 AND chapterNumber = 9;
UPDATE verses SET es = "Allí morarán para siempre. En verdad, junto a Al-lah está la recompensa más grande." WHERE ayatNumber = 22 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! No toméis por amigos a vuestros padres o a vuestros hermanos, si prefieren la incredulidad a la fe. Pues aquellos de vosotros que se hagan amigos de ellos se contarán entre los inicuos." WHERE ayatNumber = 23 AND chapterNumber = 9;
UPDATE verses SET es = "Diles: “Si vuestros padres, vuestros hijos y vuestros hermanos, vuestras mujeres y vuestras gentes, y la riqueza que habéis adquirido, y el negocio cuya ruina teméis y las viviendas que amáis os son más queridos que Al-lah y Su Mensajero y que los esfuerzos por Su causa, entonces esperad que Al-lah venga con Su juicio; pues Al-lah no guía a las gentes desobedientes." WHERE ayatNumber = 24 AND chapterNumber = 9;
UPDATE verses SET es = "En verdad, Al-lah os ayudó en muchos campos de batalla y en el Día de Hunain, cuando vuestro gran número os hizo orgullosos pero no os sirvió de nada; y la tierra, con <em>toda</em> su amplitud se os quedó estrecha,<em> y </em>entonces volvisteis la espalda en retirada." WHERE ayatNumber = 25 AND chapterNumber = 9;
UPDATE verses SET es = "Entonces Al-lah hizo que la tranquilidad descendiera sobre Su Mensajero y sobre los creyentes, y envió huestes que vosotros no visteis y castigó a los incrédulos. Pues ésta es la recompensa de los incrédulos." WHERE ayatNumber = 26 AND chapterNumber = 9;
UPDATE verses SET es = "Después de eso, Al-lah se dirigirá con compasión a quien Le plazca; pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 27 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! En verdad los idólatras son impuros. Por ello, no se acercarán a la Sagrada Mezquita después de éste, su año. Y si teméis la pobreza, Al-lah os enriquecerá de Su magnanimidad, si Le place. En verdad, Al-lah es Omnisciente, Sabio." WHERE ayatNumber = 28 AND chapterNumber = 9;
UPDATE verses SET es = "Combatid a aquellos de entre el Pueblo del Libro que no creen en Al-lah ni en el Último Día ni consideran ilícito lo que Al-lah y Su Mensajero han declarado ilícito, ni siguen la verdadera religión, hasta que paguen el tributo <em>sumisamente, </em><em>con su propia</em> mano y reconozcan su estado de sometimiento." WHERE ayatNumber = 29 AND chapterNumber = 9;
UPDATE verses SET es = "Y los judíos dicen: “Esdrás es el hijo de Al-lah” y los cristianos dicen: “el Mesías es el hijo de Al-lah”; no son más que palabras de sus bocas. Imitan la afirmación de quienes no creyeron antes que ellos. ¡Que Al-lah los maldiga! ¡Qué lejos se han desviado!" WHERE ayatNumber = 30 AND chapterNumber = 9;
UPDATE verses SET es = "Han tomado a sus sabios y monjes como señores en lugar de Al-lah. Y <em>de igual modo han tomado</em> al Mesías, hijo de María. Mas sólo se les ordenó adorar al Único Dios. No hay otro Dios sino Él. ¡Santo es Él por encima de lo que <em>Le</em> asocian!" WHERE ayatNumber = 31 AND chapterNumber = 9;
UPDATE verses SET es = "Desean apagar la luz de Al-lah con sus bocas; pero Al-lah no permitirá nada salvo el perfeccionamiento de Su luz, aunque no les guste a los incrédulos." WHERE ayatNumber = 32 AND chapterNumber = 9;
UPDATE verses SET es = "Él es Quien envió a Su Mensajero con la guía y la religión de la verdad, para hacerla prevalecer sobre cualquier <em>otra</em> religión, aunque a los idólatras no les guste." WHERE ayatNumber = 33 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! En verdad, muchos de los sacerdotes y onjes devoran las riquezas de los hombres con medios falsos y apartan <em>a los hombres</em> del camino de Al-lah. Pero a quienes atesoran el oro y la plata y no lo emplean en el camino de Al-lah, dales la noticia de un doloroso castigo." WHERE ayatNumber = 34 AND chapterNumber = 9;
UPDATE verses SET es = "En el día en que se calentará el fuego del Infierno, y sus frentes, us costados y sus espaldas sean marcados con él, <em>diciéndoseles: </em>“esto es lo que atesorasteis para vosotros; probad ahora lo que habéis amontonado”." WHERE ayatNumber = 35 AND chapterNumber = 9;
UPDATE verses SET es = "El recuento de los meses junto a Al-lah ha sido de doce meses, por decreto de Al-lah, desde el día en que creó los cielos y la tierra. De ellos, cuatro son sagrados. Ésta es la religión que permanece. No os perjudiquéis, pues, en ello. Combatid a los idólatras todos juntos como ellos os combaten unidos; y sabed que Al-lah está con el justo." WHERE ayatNumber = 36 AND chapterNumber = 9;
UPDATE verses SET es = "En verdad, el aplazamiento de <em>un mes sagrado</em> es un aditamento para la incredulidad. Los que no creen se extravían con ello. Lo permiten un año y lo prohíben otro año para ponerse de acuerdo en el número de <em>meses</em> que Al-lah ha hecho sagrados, y poder hacer así lícito lo que Dios ha prohibido. La maldad de sus acciones se les hace <em>parecer</em> justa a sus ojos. Pues Al-lah no guía a las gentes incrédulas." WHERE ayatNumber = 37 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! ¿Qué os ocurre cuando se os dice: “salid por la causa de Al-lah”, que os quedáis clavados en la tierra? ¿Os contentaréis con la vida presente, prefiriéndola al Más Allá? Mas la ganancia de la vida actual no parecerá sino pequeña e insignificante en el Más Allá." WHERE ayatNumber = 38 AND chapterNumber = 9;
UPDATE verses SET es = "Si no partís <em>al combate,</em> Él os castigará con un castigo doloroso, y elegirá en vuestro lugar a un pueblo distinto a vosotros, y no podréis perjudicarle en absoluto. Pues Al-lah tiene pleno poder sobre todas las cosas." WHERE ayatNumber = 39 AND chapterNumber = 9;
UPDATE verses SET es = "Si no le ayudáis, <em>sabed que</em> Al-lah le ayudó incluso cuando los incrédulos lo desterraron, siendo uno de los dos que estaban en la caverna, cuando dijo a su compañero: “No te aflijas, pues Al-lah está con nosotros”. Entonces Al-lah le envió Su paz, y lo fortaleció con huestes que vosotros no visteis y humilló la palabra de los incrédulos, pues sólo la palabra de Al-lah es la que es suprema, ya que Al-lah es Poderoso, Sabio." WHERE ayatNumber = 40 AND chapterNumber = 9;
UPDATE verses SET es = "Salid, ligeros y pesados, y esforzaos con vuestros bienes y vuestras personas por la causa de Al-lah. Esto es lo mejor para vosotros, ¡si lo supierais!" WHERE ayatNumber = 41 AND chapterNumber = 9;
UPDATE verses SET es = "Si hubiese habido una ganancia inmediata y un viaje corto, ciertamente te habrían seguido, pero el viaje duro les pareció demasiado largo. Sin embargo, jurarán por Al-lah diciendo: “Si hubiésemos podido, en verdad, hubiéramos avanzado contigo”. Arruinan sus almas, y Al-lah sabe que son mentirosos." WHERE ayatNumber = 42 AND chapterNumber = 9;
UPDATE verses SET es = "Al-lah hace desaparecer tus <em>preocupaciones</em>. ¿Por qué les permitiste quedarse <em>detrás</em> hasta que los que dijeron la verdad fueron conocidos por ti y <em>hasta que</em> identificaste a los mentirosos?" WHERE ayatNumber = 43 AND chapterNumber = 9;
UPDATE verses SET es = "Quienes creen en Al-lah y en el Último Día no te pedirán permiso <em>para quedar exentos</em> de esforzarse con sus bienes y sus personas. Pues Al-lah conoce perfectamente al justo." WHERE ayatNumber = 44 AND chapterNumber = 9;
UPDATE verses SET es = "Sólo te pedirán permiso para <em>quedar exentos</em> quienes no creen en Al-lah y en el Último Día y cuyos corazones están llenos de dudas; y en su duda vacilan." WHERE ayatNumber = 45 AND chapterNumber = 9;
UPDATE verses SET es = "Pues si hubiesen pensado acudir, ciertamente hubiesen hecho algunos preparativos para ello; pero Al-lah era contrario a su partida. Por ello los mantuvo atrás; y se les dijo: “Sentaos <em>en casa</em> con los que se sientan”." WHERE ayatNumber = 46 AND chapterNumber = 9;
UPDATE verses SET es = "Si hubiesen avanzado contigo, no te ha­brían dado sino problemas, abrían avanzado y retrocedido en medio de los otros, intentando <em>crear</em> la discordia entre todos. Y entre vosotros hay algunos que suelen <em>escucharles</em>. Pero Al-lah conoce perfectamente a los malvados." WHERE ayatNumber = 47 AND chapterNumber = 9;
UPDATE verses SET es = "Intentaron <em>crear</em> el desorden incluso antes de esto, y prepararon maquinaciones contra ti hasta que llegó la verdad y prevaleció el designio de Al-lah, aunque a ellos no les gustó." WHERE ayatNumber = 48 AND chapterNumber = 9;
UPDATE verses SET es = "Y entre ellos hay quien te dice: “Permíteme <em>quedarme detrás</em> y no me pongas a prueba”. En verdad, han caído ya en la prueba. Pues verdaderamente el Infierno rodeará a los incrédulos." WHERE ayatNumber = 49 AND chapterNumber = 9;
UPDATE verses SET es = "Si te sucede algo bueno, eso les aflige, pero si te sucede una desgracia, dicen: “Habíamos tomado ya nuestras precauciones”. Y vuelven la espalda con regocijo." WHERE ayatNumber = 50 AND chapterNumber = 9;
UPDATE verses SET es = "Diles: “No nos sucederá nada salvo lo que Al-lah haya ordenado para nosotros. Él es nuestro Protector y en Al-lah deben, pues, poner su confianza los creyentes”." WHERE ayatNumber = 51 AND chapterNumber = 9;
UPDATE verses SET es = "Diles: “No esperáis nada para nosotros excepto una de las dos cosas buenas, mientras que para vosotros esperamos que Al-lah os aflija con n castigo Suyo o por nuestras manos. Esperad, pues, nosotros <em>también</em> esperamos con vosotros”." WHERE ayatNumber = 52 AND chapterNumber = 9;
UPDATE verses SET es = "Diles: “Aunque gastéis voluntaria o involuntariamente, no se os aceptará. Sois, en realidad, un pueblo desobediente”." WHERE ayatNumber = 53 AND chapterNumber = 9;
UPDATE verses SET es = "Y nada les ha privado de la aceptación de sus contribuciones excepto el no creer en Al-lah y en Su Mensajero. Pues sólo acuden a la Oración perezosamente y no hacen sus contribuciones sino a disgusto." WHERE ayatNumber = 54 AND chapterNumber = 9;
UPDATE verses SET es = "Por tanto, que no te admiren ni sus riquezas ni sus hijos. Al-lah sólo pretende castigarlos con ellos en la vida presente y que sus almas expiren mientras siguen siendo incrédulos." WHERE ayatNumber = 55 AND chapterNumber = 9;
UPDATE verses SET es = "Juran por Al-lah que son realmente de los vuestros, cuando no lo son, ya que son unas gentes cobardes." WHERE ayatNumber = 56 AND chapterNumber = 9;
UPDATE verses SET es = "Si pudieran encontrar un lugar en el que refugiarse, o cavernas, o <em> incluso</em> un agujero en el que entrar, ciertamente irían a él, corriendo a toda prisa." WHERE ayatNumber = 57 AND chapterNumber = 9;
UPDATE verses SET es = "Y entre ellos están quienes encuentran falta en ti en <em>el asunto de</em> las limosnas. Si se les da de ellas se sienten satisfechos; pero si no se les da, he ahí que se muestran descontentos." WHERE ayatNumber = 58 AND chapterNumber = 9;
UPDATE verses SET es = "Si hubiesen estado satisfechos con lo que Al-lah y Su Mensajero les dieron y dijeran: “Para nosotros nos basta con Al-lah y Su Mensajero; ciertamente Al-lah nos dará de Su magnanimidad; a Al-lah nos dirigimos suplicantes”, <em>habría sido mejor para ellos.</em>" WHERE ayatNumber = 59 AND chapterNumber = 9;
UPDATE verses SET es = "Las limosnas son únicamente para los pobres y necesitados, para los empleados en relación con ellos, para aquellos cuyos corazones deben reconciliarse, para la <em>liberación de</em> los esclavos, para los que tienen deudas, para la causa de Al-lah y para el viajero: he aquí una orden de Al-lah. Pues Al-lah es el Omnisciente, Sabio." WHERE ayatNumber = 60 AND chapterNumber = 9;
UPDATE verses SET es = "Y entre ellos están quienes ofenden al Profeta, diciendo: “Él es todo oídos”. Diles: “Su disposición a escuchar <em>a todos</em> es excelente para vosotros: él cree en Al-lah y tiene confianza en los creyentes, y es una misericordia para aquellos de vosotros que creen”. Pero quienes molesten al Mensajero de Al-lah tendrán un doloroso castigo." WHERE ayatNumber = 61 AND chapterNumber = 9;
UPDATE verses SET es = "Os juran por Al-lah para complaceros; pero Al-lah y Su Mensajero son más dignos de que les agraden a él y <em>a Dios,</em> si son creyentes." WHERE ayatNumber = 62 AND chapterNumber = 9;
UPDATE verses SET es = "¿No saben acaso que para quien se opone a Al-lah y a Su Mensajero, está el Fuego del Infierno, en donde morará? Ésa es la humillación más grande." WHERE ayatNumber = 63 AND chapterNumber = 9;
UPDATE verses SET es = "Los hipócritas muestran temor (fingido) de que se revele un <em>Surah</em> sobre ellos, comunicándoles lo que hay en sus corazones. Diles: “¡Burlaos! en verdad Al-lah sacará a plena luz lo que teméis”." WHERE ayatNumber = 64 AND chapterNumber = 9;
UPDATE verses SET es = "Pero si los interrogas, seguramente dirán: “hablábamos de cosas frívolas y bromeábamos”. Diles: “¿Os burlabais de Al-lah, de Sus Signos y de Su Mensajero?" WHERE ayatNumber = 65 AND chapterNumber = 9;
UPDATE verses SET es = "“No os excuséis. Ciertamente dejasteis de creer después de haber creído. Si perdonáramos a un grupo de vosotros, castigaremos a otro grupo, porque han sido culpables”." WHERE ayatNumber = 66 AND chapterNumber = 9;
UPDATE verses SET es = "Los hipócritas, hombres y mujeres, están <em>todos ellos relacionados</em> entre sí. Ordenan el mal y prohíben el bien, y mantienen la mano cerrada. Olvidaron a Al-lah, por lo que Él los ha olvidado. En verdad, los hipócritas son los desobedientes." WHERE ayatNumber = 67 AND chapterNumber = 9;
UPDATE verses SET es = "Al-lah promete a los hipócritas -hombres y mujeres- y a los incrédulos, el Fuego del Infierno, en el que morarán. Eso les bastará. Pues Al-lah los ha maldecido y tendrán un castigo duradero." WHERE ayatNumber = 68 AND chapterNumber = 9;
UPDATE verses SET es = "Al igual que los que os precedieron. Eran superiores a vosotros en poder y más ricos en posesiones e hijos. Disfrutaron de su suerte durante un breve tiempo y vosotros disfrutasteis de vuestra suerte como los que os precedieron disfrutaron de la suya. Y os dedicasteis a conversaciones inútiles igual que ellos se dedicaron al mismo tipo de conversaciones. Son aquellos cuyas obras no les servirán de nada en este mundo ni en el Más Allá. Ellos son los perdedores." WHERE ayatNumber = 69 AND chapterNumber = 9;
UPDATE verses SET es = "¿No les ha llegado noticia de los que los precedieron: el pueblo de Noé, Ad y Zamud, y el pueblo de Abraham, los habitantes de Madián y las ciudades que fueron destruidas? Sus Mensajeros llegaron a ellos con signos claros. Así pues, no fue Al-lah quien los perjudicó sino que se perjudicaron ellos mismos." WHERE ayatNumber = 70 AND chapterNumber = 9;
UPDATE verses SET es = "Y los creyentes, hombres y mujeres, son mutuamente amigos. Ordenan el bien y prohíben el mal, cumplen la Oración, pagan el Zakat y obedecen a Al-lah y a Su Mensajero. Con ellos es con quienes Al-lah tendrá misericordia. En verdad, Al-lah es Poderoso, Sabio." WHERE ayatNumber = 71 AND chapterNumber = 9;
UPDATE verses SET es = "Al-lah ha prometido a los creyentes, hombres y mujeres, Jardines por los que corren ríos, en los que habitarán; y moradas deliciosas en los Jardines de la Eternidad. Pero el agrado de Al-lah es lo más grande de todo. Es el triunfo supremo." WHERE ayatNumber = 72 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh, Profeta! esfuérzate contra los incrédulos y los hipócritas. Sé severo con ellos. Su morada es el Infierno, ¡qué horrible destino!" WHERE ayatNumber = 73 AND chapterNumber = 9;
UPDATE verses SET es = "Juran por Al-lah que no dijeron nada. Pero en verdad usaron un lenguaje blasfemo, y no creyeron después de haber abrazado el Islam. Meditaron sobre lo que <em>posteriormente</em> fracasaron en conseguir. Y alimentaron su odio únicamente porque Al-lah y Su Mensajero los habían enriquecido por su magnanimidad. Por tanto, si se arrepienten, mejor sería para ellos; pero si se apartan, Al-lah les inflingirá un castigo doloroso en este mundo y en el Más Allá, y no tendrán ni amigo ni abogado en la tierra." WHERE ayatNumber = 74 AND chapterNumber = 9;
UPDATE verses SET es = "Entre ellos están quienes concluyeron un pacto con Al-lah, <em>diciendo:</em> “si Él nos da de Su magnanimidad, nosotros ciertamente daremos limosnas y seremos virtuosos”." WHERE ayatNumber = 75 AND chapterNumber = 9;
UPDATE verses SET es = "Pero cuando Él les dio de Su magnanimidad, se hicieron avaros con ella, y volvieron la espalda, alejándose." WHERE ayatNumber = 76 AND chapterNumber = 9;
UPDATE verses SET es = "Por ello, Él les castigó con la hipocresía <em>que durará</em> en sus corazones hasta el día que se reúnan con Él; por romper su promesa a Al-lah y por haber mentido." WHERE ayatNumber = 77 AND chapterNumber = 9;
UPDATE verses SET es = "¿No saben que Al-lah conoce sus secretos y sus deliberaciones encubiertas, y que Al-lah es Quien mejor conoce todo lo invisible?" WHERE ayatNumber = 78 AND chapterNumber = 9;
UPDATE verses SET es = "Aquellos que encuentran faltas en los creyentes que dan limosnas por su propia voluntad, y en los que no tienen nada <em>para dar</em> excepto<em> lo que ganan</em> con su propio esfuerzo. Se ríen de ellos. Al-lah los castigará por su burla, y para ellos habrá un doloroso castigo." WHERE ayatNumber = 79 AND chapterNumber = 9;
UPDATE verses SET es = "Implores o no el perdón para ellos, y aunque pidas perdón para ellos setenta veces, Al-lah nunca los perdonará. Eso es porque no creyeron en Al-lah ni en Su Mensajero. Y Al-lah no guía a las personas malvadas." WHERE ayatNumber = 80 AND chapterNumber = 9;
UPDATE verses SET es = "Los que <em>se las ingeniaron para</em> quedarse atrás se alegraron por haberse quedado rezagados, contradiciendo al Mensajero de Al-lah, y se negaron a esforzarse con sus bienes y sus personas por la causa de Al-lah. Dijeron: “No<em> salgáis</em> durante el calor”. Diles: “El Fuego del Infierno tiene un calor más intenso”. ¡Si pudieran entender!" WHERE ayatNumber = 81 AND chapterNumber = 9;
UPDATE verses SET es = "Tendrán que reír poco y llorar mucho como recompensa por lo que ganaron." WHERE ayatNumber = 82 AND chapterNumber = 9;
UPDATE verses SET es = "Y si Al-lah te devuelve a un grupo de ellos, y éstos te piden permiso para partir<em> al combate,</em> diles: “Nunca partiréis conmigo y jamás combatiréis a un enemigo conmigo. La primera vez optasteis por quedaros sentados <em>en casa;</em> permaneced ahora, por tanto, con los que se quedan detrás”." WHERE ayatNumber = 83 AND chapterNumber = 9;
UPDATE verses SET es = "Nunca reces por ninguno de ellos cuando muera, ni ores sobre sus tumbas, porque no creyeron en Al-lah ni en Su Mensajero y murieron en estado de desobediencia." WHERE ayatNumber = 84 AND chapterNumber = 9;
UPDATE verses SET es = "No te dejes seducir por sus bienes ni por sus hijos; Al-lah sólo pretende castigarlos con ellos en este mundo y que sus almas se separen de ellos mientras son incrédulos." WHERE ayatNumber = 85 AND chapterNumber = 9;
UPDATE verses SET es = "Y cuando se revela un Surah que <em>ordena:</em> “Creed en Al-lah y esforzaos<em> por la causa de Al-lah</em> en compañía de Su Mensajero” los más acomodados de entre ellos te piden permiso y dicen: “Déjanos estar con los que se quedan <em>en casa”.</em>" WHERE ayatNumber = 86 AND chapterNumber = 9;
UPDATE verses SET es = "Se alegran de estar con las mujeres que quedaron detrás, y sus corazones están sellados, de manera que no entienden." WHERE ayatNumber = 87 AND chapterNumber = 9;
UPDATE verses SET es = "Pero el Mensajero y quienes creen con él se esfuerzan <em>por la causa de Al-lah</em> con sus bienes y personas, y son ellos quienes recibirán cosas buenas, y quienes prosperarán." WHERE ayatNumber = 88 AND chapterNumber = 9;
UPDATE verses SET es = "Al-lah ha preparado para ellos Jardines por los que corren ríos; en ellos morarán. Ése es el triunfo supremo." WHERE ayatNumber = 89 AND chapterNumber = 9;
UPDATE verses SET es = "Quienes se excusan de entre los árabes del desierto, vinieron para que se le concediera la exención. Mas los falsos para Al-lah y Su Mensajero se quedaron <em>en casa.</em> Un penoso castigo caerá sobre los incrédulos." WHERE ayatNumber = 90 AND chapterNumber = 9;
UPDATE verses SET es = "No hay que reprochar nada a los débiles, a los enfermos, ni a los que no tienen nada que gastar, si son sinceros con Al-lah y Su Mensajero. No hay motivo de reproche contra los que hacen buenas obras; pues Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 91 AND chapterNumber = 9;
UPDATE verses SET es = "Tampoco contra aquellos que, cuando llegaron a ti para que les proporcionases una montura, les dijiste: “No puedo encontrar cabalgadura para vosotros”; se volvieron, con sus ojos inundados de lágrimas, por la pena de no poder encontrar nada que emplear." WHERE ayatNumber = 92 AND chapterNumber = 9;
UPDATE verses SET es = "Sólo hay motivo de reproche contra quienes te piden ser eximidos a pesar de ser ricos. Se sienten contentos de quedarse con las mujeres. Pero Al-lah ha puesto un sello en sus corazones para que no lo sepan." WHERE ayatNumber = 93 AND chapterNumber = 9;
UPDATE verses SET es = "Os presentarán excusas cuando volváis a ellos. Diles: “No os excuséis; no os creeremos. Al-lah nos ha informado ya de todo lo eferente a vosotros. Y Al-lah observará vuestra conducta, <em>como también</em> Su Mensajero; más tarde seréis devueltos a Él, a Quien conoce lo invisible y lo visible, y Él os dirá todo lo que solíais hacer”." WHERE ayatNumber = 94 AND chapterNumber = 9;
UPDATE verses SET es = "Os jurarán por Al-lah, cuando volváis a ellos, que podéis dejarlos solos. Dejadlos pues solos. En verdad, son indecentes y su morada es el Infierno, una recompensa <em>adecuada</em> para lo que solían ganar." WHERE ayatNumber = 95 AND chapterNumber = 9;
UPDATE verses SET es = "Os jurarán para que estéis contentos con ellos. Pero <em>aun cuando</em> estuvierais contentos con ellos, Al-lah no lo estará con las gentes rebeldes." WHERE ayatNumber = 96 AND chapterNumber = 9;
UPDATE verses SET es = "Los moradores del desierto son los peores en incredulidad e ipocresía, y la mayoría no son aptos para conocer los decretos de la <em>Revelación</em> que Al-lah ha revelado a Su Mensajero. Pues Al-lah es el Omnisciente, Sabio." WHERE ayatNumber = 97 AND chapterNumber = 9;
UPDATE verses SET es = "Y entre los beduinos están quienes consideran lo que gastan a <em>favor de Dios</em> como un castigo y esperan <em>ansiosamente</em> que las desgracias caigan sobre vosotros. ¡Que el mal acaezca sobre ellos!. Pues Al-lah es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 98 AND chapterNumber = 9;
UPDATE verses SET es = "Y entre los moradores del Desierto están quienes creen en Al-lah y en el Último Día y consideran que lo que gastan es un medio para acercarse a Al-lah y <em>recibir</em> las bendiciones del Profeta. Sin duda que es para ellos un medio para acercarse a <em>Dios.</em> Al-lah muy pronto los recibirá en Su misericordia. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 99 AND chapterNumber = 9;
UPDATE verses SET es = "En cuanto a los más destacados <em>de entre los creyentes, </em> los primeros Emigrantes y Socorredores, y los que les siguieron de manera excelente, Al-lah se siente contento con ellos y ellos contentos con Él. Y Él les ha preparado Jardines por los que corren ríos. En ellos habitarán para siempre. Ése es el triunfo supremo." WHERE ayatNumber = 100 AND chapterNumber = 9;
UPDATE verses SET es = "Y de entre los moradores del desierto que os rodean, <em>algunos</em> son hipócritas; y <em>también</em> los hay en las entes de Medina. Persisten en la hipocresía. Tú no los conoces <em>pero</em> Nosotros los conocemos. Los castigaremos dos veces; y después se les infligirá un gran castigo." WHERE ayatNumber = 101 AND chapterNumber = 9;
UPDATE verses SET es = "Pero <em>hay</em> otros que han reconocido sus faltas. Mezclaron una buena obra con otra mala. Tal vez Al-lah vuelva a ellos con compasión. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 102 AND chapterNumber = 9;
UPDATE verses SET es = "Toma limosnas de su riqueza para que los limpies y purifiques con llas. Y ora por ellos; tu oración es, en verdad, <em>una fuente de</em> tranquilidad para ellos. Pues Al-lah es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 103 AND chapterNumber = 9;
UPDATE verses SET es = "¿No saben que Al-lah es el que acepta el arrepentimiento de Sus iervos y reconoce las limosnas, y que Al-lah es el Sumo Remisorio <em> con compasión, y es</em> Misericordioso?" WHERE ayatNumber = 104 AND chapterNumber = 9;
UPDATE verses SET es = "Diles: “Haced lo que podáis, Al-lah vigilará ciertamente vuestras obras, como también lo harán Su Mensajero y los creyentes. Y se os hará volver al Conocedor de lo visible y lo invisible; entonces Él os dirá lo que solíais hacer”." WHERE ayatNumber = 105 AND chapterNumber = 9;
UPDATE verses SET es = "Pero <em>hay</em> otros a la espera del decreto de Al-lah. El decidirá si los castiga o si vuelve a ellos con compasión. Pues Al-lah es el Omnisciente, Sabio." WHERE ayatNumber = 106 AND chapterNumber = 9;
UPDATE verses SET es = "Y <em>entre los hipócritas hay</em> quienes construyeron una mezquita para perjudicar al Islam, extender la incredulidad, crear la disensión entre los creyentes y proporcionar un lugar de ocultamiento a aquellos que hicieron la guerra a Al-lah y Su Mensajero. En verdad jurarán: “Sólo pretendíamos el bien”; pero Al-lah es testigo de que son realmente mentirosos." WHERE ayatNumber = 107 AND chapterNumber = 9;
UPDATE verses SET es = "Nunca permanezcas allí <em>para rezar.</em> Una mezquita fundada en la piedad desde sus primeros días es más digna de que permanezcas en ella <em>para orar.</em> Allí hay hombres que desean ser purificados, y Al-lah ama a quienes se purifican a sí mismos." WHERE ayatNumber = 108 AND chapterNumber = 9;
UPDATE verses SET es = "¿No es, pues, mejor, quien fundó sus cimientos sobre el temor de Al-lah y <em>buscando</em> Su agrado, que quien fundó sus cimientos al borde de una orilla tambaleante, socavada por el agua, que se hundió con él en el fuego del infierno? Pues Al-lah no guía a los hombres transgresores." WHERE ayatNumber = 109 AND chapterNumber = 9;
UPDATE verses SET es = "Este edificio suyo, que han erigido, quedará siempre como fuente de recelo e incertidumbre en sus corazones, hasta que sus corazones se dividan y se partan en trozos. Pues Al-lah es el Omnisciente, Sabio." WHERE ayatNumber = 110 AND chapterNumber = 9;
UPDATE verses SET es = "En verdad, Al-lah ha adquirido de los creyentes sus personas y sus bienes a cambio del Jardín que tendrán; combaten por la causa de Al-lah, matan y son matados: una promesa que <em>Él ha hecho</em> suya en la Torah, el Evangelio y el Corán. Mas ¿quién es más fiel a su promesa que Al-lah? Alegraos, pues, del trato que habéis concluido con Él; en eso está el triunfo supremo." WHERE ayatNumber = 111 AND chapterNumber = 9;
UPDATE verses SET es = "<em> Son quienes</em> se vuelven <em>a Dios con arrepentimiento,</em> Lo adoran, Lo alaban, ayunan, se inclinan <em>ante El,</em> se postran <em>en la Oración,</em> ordenan el bien y prohíben el mal y respetan el límite<em> establecido</em> por Al-lah. Y da buenas noticias a los creyentes." WHERE ayatNumber = 112 AND chapterNumber = 9;
UPDATE verses SET es = "No corresponde al Profeta y a quienes creen, pedir <em>a Dios</em> perdón por los idólatras, aunque sean sus parientes, después de ver claramente que son los moradores del Infierno." WHERE ayatNumber = 113 AND chapterNumber = 9;
UPDATE verses SET es = "Y si Abraham pidió el perdón para su padre fue únicamente por una promesa que él le había hecho, pero cuando vio con claridad que era enemigo de Al-lah, se separó de él. En verdad, Abraham era sumamente compasivo <em>y</em> tolerante." WHERE ayatNumber = 114 AND chapterNumber = 9;
UPDATE verses SET es = "Y no corresponde a Al-lah hacer que un pueblo se pierda después de que Él los haya guiado mientras no les haya explicado claramente de lo que deben guardarse. En verdad, Al-lah conoce perfectamente todas las cosas." WHERE ayatNumber = 115 AND chapterNumber = 9;
UPDATE verses SET es = "Ciertamente, es a Al-lah a Quien pertenece el Reino de los cielos y de la tierra. Él da la vida y causa la muerte. No tenéis amigo ni abogado fuera de Al-lah." WHERE ayatNumber = 116 AND chapterNumber = 9;
UPDATE verses SET es = "Al-lah se ha vuelto en verdad con misericordia hacia el Profeta y hacia los Emigrantes y Socorredores que lo siguieron en la hora de angustia, después de que los corazones de un grupo de ellos estuvieran a punto de desfallecer. Y de nuevo volvió a ellos con misericordia. En verdad, es para ellos Compasivo, Misericordioso." WHERE ayatNumber = 117 AND chapterNumber = 9;
UPDATE verses SET es = "Y a los tres <em>que permanecieron</em> atrás hasta que la tierra les pareció demasiado estrecha a pesar de <em>toda</em> su extensión, y sus almas les fueron <em>también </em>constreñidas<em>, </em>quedando convencidos de que no había refugio ante Al-lah salvo en Él mismo. ntonces volvió a ellos con misericordia para que pudieran retornar <em>a Él.</em> En verdad, Al-lah es Remisorio con compasión y es Misericordioso." WHERE ayatNumber = 118 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! temed a Al-lah y permaneced entre los veraces." WHERE ayatNumber = 119 AND chapterNumber = 9;
UPDATE verses SET es = "No había razón para que las gentes de Medina y de sus alrededores de entre los moradores del desierto permanecieran detrás del Mensajero de Al-lah, o que hubieran preferido sus propias vidas a la de él. Esto es porque en el camino de Al-lah no les aflige la sed, la fatiga o el hambre, ni toman una senda que moleste a los incrédulos, ni ganan ventaja al enemigo, sin que se les contara como una buena obra en razón de todo ello. En verdad, Al-lah no permite que se pierda la recompensa de los que hacen el bien." WHERE ayatNumber = 120 AND chapterNumber = 9;
UPDATE verses SET es = "Pues no gastan ninguna suma, grande o pequeña, ni atraviesan un valle, sin que quede escrito para ellos, para que Al-lah pueda darles la mejor de las recompensas por lo que hicieron." WHERE ayatNumber = 121 AND chapterNumber = 9;
UPDATE verses SET es = "No es posible para los creyentes partir todos juntos. ¿Por qué, pues, no parte un grupo de cada sección de ellos para que consigan un mejor conocimiento de la religión y puedan advertir a su pueblo cuando vuelvan a ellos, a fin de que puedan guardarse <em>del mal?</em>" WHERE ayatNumber = 122 AND chapterNumber = 9;
UPDATE verses SET es = "¡Oh vosotros, los que creéis! Combatid a los incrédulos que estén cerca de vosotros y haced que encuentren una firmeza inquebrantable en vosotros; y sabed que Al-lah está con los justos." WHERE ayatNumber = 123 AND chapterNumber = 9;
UPDATE verses SET es = "Pues siempre que se revela un Surah, hay alguno de ellos que dice: “¿A cuál de vosotros ha aumentado en la fe este Surah? Pero en lo que respecta a los creyentes, aumenta su fe y se alegran por ello." WHERE ayatNumber = 124 AND chapterNumber = 9;
UPDATE verses SET es = "Pero para quienes tienen sus corazones enfermos, añade <em>una nueva inmundicia</em> a su inmundicia <em>actual</em>, y mueren como incrédulos." WHERE ayatNumber = 125 AND chapterNumber = 9;
UPDATE verses SET es = "¿Acaso no ven que son puestos a prueba una o dos veces al año? Y sin embargo no se arrepienten ni quieren ser advertidos." WHERE ayatNumber = 126 AND chapterNumber = 9;
UPDATE verses SET es = "Cuando se revela un Surah, se miran entre sí, <em>diciéndose: </em>“¿Os ve alguien?”. Luego se retiran. Al-lah ha hecho que sus corazones se alejen porque son un pueblo que no quiere entender." WHERE ayatNumber = 127 AND chapterNumber = 9;
UPDATE verses SET es = "En verdad, os ha llegado un Mensajero de entre vosotros; es penoso ara él que sufráis el infortunio; desea ardientemente vuestro <em>bienestar; y es</em> compasivo y misericordioso con los creyentes." WHERE ayatNumber = 128 AND chapterNumber = 9;
UPDATE verses SET es = "Mas si ellos se alejan, diles: “Al-lah me basta. No hay Dios sino Él. En Él pongo mi confianza, y Él es el Señor del Trono poderoso”." WHERE ayatNumber = 129 AND chapterNumber = 9;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 10;
UPDATE verses SET es = "Alif Lam Ra. Éstos son los versículos del Libro que está lleno de sabiduría." WHERE ayatNumber = 2 AND chapterNumber = 10;
UPDATE verses SET es = "¿Es acaso sorprendente para la gente que hayamos inspirado a un hombre de ellos, <em>diciendo:</em> “Advierte a la humanidad y da a los que creen la buena noticia de que tendrán una elevada dignidad<em> de verdad</em> ante su Señor?”. Los incrédulos dicen: “En verdad, es un hechicero manifiesto”." WHERE ayatNumber = 3 AND chapterNumber = 10;
UPDATE verses SET es = "En verdad, vuestro Señor es Al-lah, Quien creó los cielos y la tierra en seis períodos, y después Se sentó en el Trono; Él lo organiza todo. No existe intercesor <em>ante Él</em> salvo con su permiso. Así es Al-lah, vuestro Señor; adoradle pues. ¿No queréis ser advertidos?" WHERE ayatNumber = 4 AND chapterNumber = 10;
UPDATE verses SET es = "A Él volveréis todos. La promesa de Al-lah es cierta. En verdad, Él origina la creación; después Él la reproduce, para poder recompensar a quienes creen y hacen buenas obras, con equidad; y en <em>cuanto a</em> quienes no creen, beberán agua hirviente y recibirán un doloroso castigo por no haber creído." WHERE ayatNumber = 5 AND chapterNumber = 10;
UPDATE verses SET es = "Él es Quien hizo el sol radiante y la luna brillante y ordenó sus fases, para que conocierais el número de años y el cómputo <em>del tiempo</em>. Al-lah no los creó sino con verdad. Él explica los Signos a los hombres que tienen conocimiento." WHERE ayatNumber = 6 AND chapterNumber = 10;
UPDATE verses SET es = "En verdad, en la sucesión de la noche y el día, y en todo lo que Al-lah ha creado en los cielos y en la tierra hay Signos para las gentes que temen a Dios." WHERE ayatNumber = 7 AND chapterNumber = 10;
UPDATE verses SET es = "Quienes no buscan el encuentro con Nosotros y están contentos con la vida de este mundo sintiéndose tranquilos con ella, y quienes no hacen caso de Nuestros Signos," WHERE ayatNumber = 8 AND chapterNumber = 10;
UPDATE verses SET es = "Son aquellos cuya morada es el Fuego, por lo que han ganado." WHERE ayatNumber = 9 AND chapterNumber = 10;
UPDATE verses SET es = "Pero<em> en cuanto a</em> quienes creen, y hacen buenas obras, su Señor los guiará gracias a su fe. Correrán ríos debajo de ellos en los Jardines de la Felicidad." WHERE ayatNumber = 10 AND chapterNumber = 10;
UPDATE verses SET es = "Su oración en ellos será: “¡Gloria a Ti, oh Al-lah!”, y su saludo en ellos será: “¡Paz!”. Y la conclusión de su oración será: “Toda alabanza sea para Al-lah, el Señor de los mundos”." WHERE ayatNumber = 11 AND chapterNumber = 10;
UPDATE verses SET es = "Pero si Al-lah tuviera que apresurar para los hombres el mal<em>que se han ganado, </em>como ellos se apresuran a la <em>adquisición de</em> la riqueza, les habría llegado ya el <em>fin</em> de su plazo <em>de vida.</em> Mas dejamos que quienes no esperan el encuentro con Nosotros vaguen distraídamente en su transgresión." WHERE ayatNumber = 12 AND chapterNumber = 10;
UPDATE verses SET es = "Y cuando la desgracia cae sobre un hombre, Nos invoca, echado sobre su costado, sentado o de pie; pero cuando le libramos de su desgracia, se aleja como si nunca hubiera Nos hubiera suplicado para<em> salvarlo</em> de la desdicha que le afligía. De este modo es como las acciones de los extravagantes se les hace aparecer justas ante sus ojos." WHERE ayatNumber = 13 AND chapterNumber = 10;
UPDATE verses SET es = "Y destruimos a muchas generaciones que os precedieron cuando transgredieron; a pesar de que <em>anteriormente</em> les llegaran Mensajeros con Signos claros, pero no quisieron creer­­­. De este modo castigamos a las gentes culpables." WHERE ayatNumber = 14 AND chapterNumber = 10;
UPDATE verses SET es = "A continuación, os convertimos en sucesores en la tierra, tras ellos, para poder observar cómo os comportabais." WHERE ayatNumber = 15 AND chapterNumber = 10;
UPDATE verses SET es = "Y cuando se les recitan Nuestros Signos claros, los que no esperan la reunión con Nosotros, dicen: “Trae un Corán distinto a éste o cámbialo”. Diles: “No me corresponde a mí cambiarlo por mi propia voluntad. Yo sólo sigo lo que se me ha revelado. En verdad, si desobedezco a mi Señor, temo el castigo del día Inmenso”." WHERE ayatNumber = 16 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “Si Al-lah hubiese querido, no os lo habría recitado ni os lo habría dado a conocer. En verdad, he vivido entre vosotros una vida antes de esto. ¿No queréis comprender?”." WHERE ayatNumber = 17 AND chapterNumber = 10;
UPDATE verses SET es = "¿Quién viola la justicia de manera mas flagrante que quien trama una mentira contra Al-lah o quien trata a Sus Signos como mentiras? En verdad, el culpable nunca prosperará." WHERE ayatNumber = 18 AND chapterNumber = 10;
UPDATE verses SET es = "Mas en lugar de Al-lah, oran a quienes ni les perjudican ni les benefician; y dicen: “éstos son nuestros intercesores ante Al-lah”. Diles: “¿acaso informaríais a Al-lah de algo que Él desconozca en los cielos y en la tierra?”. ¡Glorioso es Él! Está elevado muy por encima de lo que Le asocian." WHERE ayatNumber = 19 AND chapterNumber = 10;
UPDATE verses SET es = "La humanidad no era más que una sola comunidad, pero más tarde discreparon<em>,</em> y de no haber sido por una palabra que había sido enviada antes por tu Señor, su suerte habría quedado marcada de acuerdo con todo lo que disintieron." WHERE ayatNumber = 20 AND chapterNumber = 10;
UPDATE verses SET es = "Y dicen: “¿Por qué no se le ha enviado un Signo de su Señor?”. Diles: “a Al-lah pertenece lo invisible. Esperad, pues, estoy con vosotros entre los que esperan”." WHERE ayatNumber = 21 AND chapterNumber = 10;
UPDATE verses SET es = "En el momento en que hacemos probar Nuestra misericordia a la gente que ha sido afligida con la adversidad, comienzan inmediatamente a tramar contra Nuestros Signos. Diles: “Al-lah es más rápido en hacer proyectos”. En verdad, Nuestros Mensajeros toman nota de lo que conspiráis." WHERE ayatNumber = 22 AND chapterNumber = 10;
UPDATE verses SET es = "Él es Quien os permite viajar por tierra y por mar hasta que, cuando estáis <em>a bordo</em> de los buques y éstos navegan con una brisa agradable y se alegran con ella entonces les alcanza un viento tempestuoso las olas caen sobre ellos de todas partes y piensan haber sido rodeados, <em>entonces</em> invocan a Al-lah, prometiendo la sinceridad de su fe en Él, <em>diciendo:</em> “Si nos libras de esto, ciertamente seremos de los agradecidos”." WHERE ayatNumber = 23 AND chapterNumber = 10;
UPDATE verses SET es = "Pero cuando Él los ha librado, he ahí que empiezan a cometer excesos injustos en la tierra. ¡Oh humanos! vuestros excesos sólo son contra vosotros mismos: un mero placer de la vida presente. Después volveréis a Nosotros; y os informaremos de lo que solíais hacer." WHERE ayatNumber = 24 AND chapterNumber = 10;
UPDATE verses SET es = "El ejemplo de la vida presente es como el del agua que hacemos descender del cielo; con ella se mezcla la vegetación de la tierra, de la que participan los hombres y el ganado. Así continúa ocurriendo hasta que la tierra florece plenamente y madura en belleza. Entonces quienes la poseen se sienten dueños de ella; pero entonces viene Nuestro designio, de noche o de día, y la convertimos en un campo segado, como si nada hubiese existido en ella el día anterior. Así exponemos los Signos para las gentes que reflexionan." WHERE ayatNumber = 25 AND chapterNumber = 10;
UPDATE verses SET es = "Pues Al-lah llama a la morada de la paz, y guía a quien desea por el camino recto." WHERE ayatNumber = 26 AND chapterNumber = 10;
UPDATE verses SET es = "Para quienes hacen buenas obras, habrá la mejor<em> recompensa</em> y <em>bendiciones</em> aún mayores<em>.</em> Y sus rostros no quedarán cubiertos por las sombras ni serán humillados. Son éstos los moradores del Cielo; en él habitarán." WHERE ayatNumber = 27 AND chapterNumber = 10;
UPDATE verses SET es = "<em> En cuanto a </em>quienes cometan malas acciones, el castigo de la maldad será similar a ella, y quedarán cubiertos por la humillación. No tendrán a nadie que les proteja contra Al-lah.<em> Y parecerá</em> como si sus rostros hubiesen sido cubiertos con lóbregos jirones de la noche. Éstos son los habitantes del Fuego; en él morarán." WHERE ayatNumber = 28 AND chapterNumber = 10;
UPDATE verses SET es = "Cuidado con el día en el que reunamos a todos, cuando digamos a los que asocian partícipes<em> a Dios: “</em>Quedaos en vuestros lugares, vosotros y vuestros dioses asociados”. Entonces los separaremos, y aquellos a quienes consideraban sus socios dirán: “No es a nosotros a quienes adorabais”." WHERE ayatNumber = 29 AND chapterNumber = 10;
UPDATE verses SET es = "<em> “</em>Así, Al-lah es Testigo suficiente entre nosotros y vosotros. Desconocíamos totalmente vuestra devoción”." WHERE ayatNumber = 30 AND chapterNumber = 10;
UPDATE verses SET es = "Allí cada alma caerá en la cuenta de lo que tuvo antes. Serán devueltas a Al-lah, su verdadero Maestro, y perderán todo lo que solían tramar." WHERE ayatNumber = 31 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “¿Quién os proporciona el sustento desde el cielo y la tierra? O ¿quién es el que tiene el poder sobre los oídos y los ojos? Pues ¿quién hace surgir a lo vivo de lo muerto y saca lo muerto de lo vivo? Y ¿quién rige todos los asuntos?”. Ellos responderán: “Al-lah”. Diles entonces: “¿No desistiréis entonces <em>de vuestras erradas acciones</em>?”." WHERE ayatNumber = 32 AND chapterNumber = 10;
UPDATE verses SET es = "Así es Al-lah, vuestro verdadero Señor. Por ello, ¿qué queda después de a verdad excepto el error manifiesto? ¿cómo, pues, os apartáis <em>de la verdad?</em>" WHERE ayatNumber = 33 AND chapterNumber = 10;
UPDATE verses SET es = "Así se demostró cierta la palabra de tu Señor contra los que se rebelan, para que no crean." WHERE ayatNumber = 34 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “¿Hay alguno de vuestros socios que origine la creación y después la repita?”. Diles: “<em>Sólo</em> Al-lah es Quien origina la creación y después la repite. ¿Cómo podéis dejaros extraviar?" WHERE ayatNumber = 35 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “¿Hay alguno de vuestros dioses asociados que conduzca a la verdad?”. Diles: “Al-lah es Quien lleva a la verdad. ¿Acaso no es más digno e ser seguido Quien lleva a la verdad que quien no encuentra el camino <em> por sí mismo</em> a menos que sea guiado? ¿Qué os pasa pues? ¿Cómo juzgáis?" WHERE ayatNumber = 36 AND chapterNumber = 10;
UPDATE verses SET es = "Y la mayoría de ellos no siguen sino conjeturas. Ciertamente, las conjeturas no pueden sustituir a la verdad en lo más mínimo. En verdad, Al-lah conoce perfectamente lo que hacen." WHERE ayatNumber = 37 AND chapterNumber = 10;
UPDATE verses SET es = "Y este Corán no ha podido ser inventado por nadie excepto Al-lah. Por l contrario, cumple lo que hubo antes de él y es una exposición de la Ley <em> de Dios.</em> No hay duda alguna sobre él. <em>Procede</em> del Señor de los mundos." WHERE ayatNumber = 38 AND chapterNumber = 10;
UPDATE verses SET es = "Dicen: “¿Él lo ha inventado?”. Diles: “Traed un Surah semejante a éste y pedid ayuda a todos los que podáis, aparte de Al-lah, si sois sinceros”." WHERE ayatNumber = 39 AND chapterNumber = 10;
UPDATE verses SET es = "No, pero han rechazado aquello cuyo conocimiento no abarcan y cuyo <em>verdadero</em> significado no se les alcanza. De igual manera rechazaron<em> la verdad</em> los que les precedieron. Pero ¡Ved cuál fue el fin de los que hicieron el mal!" WHERE ayatNumber = 40 AND chapterNumber = 10;
UPDATE verses SET es = "Mas entre ellos hay<em> algunos</em> que creen en ella y <em>otros</em> que no creen en ella, y tu Señor conoce perfectamente a los que crean la discordia." WHERE ayatNumber = 41 AND chapterNumber = 10;
UPDATE verses SET es = "Pero si te acusan de mentir, diles: “Yo debo dar cuenta de mis acciones y vosotros de las vuestras; no sois responsables de lo que hago, y yo no soy responsable de lo que hacéis”." WHERE ayatNumber = 42 AND chapterNumber = 10;
UPDATE verses SET es = "Y entre ellos hay <em>algunos</em> que te escuchan. Mas ¿puedes hacer que los sordos oigan aunque no entiendan?" WHERE ayatNumber = 43 AND chapterNumber = 10;
UPDATE verses SET es = "Y entre ellos hay <em>algunos</em> que te miran. Mas ¿puedes guiar a los ciegos, aunque no vean?" WHERE ayatNumber = 44 AND chapterNumber = 10;
UPDATE verses SET es = "En verdad, Al-lah no perjudica en absoluto a los hombres sino que son ellos mismos quienes perjudican a sus propias almas." WHERE ayatNumber = 45 AND chapterNumber = 10;
UPDATE verses SET es = "Mas en el día en el que reunirá a todos,<em> les parecerá</em> como si sólo hubiesen permanecido en el mundo una hora de un día. Se reconocerán los unos a los otros. En verdad, los perdedores son quienes niegan el encuentro con Al-lah, y no serán guiados." WHERE ayatNumber = 46 AND chapterNumber = 10;
UPDATE verses SET es = "Y si te hacemos testigo de parte de lo que les prometimos o si te hacemos morir <em>antes de que ello </em>ocurra, a Nosotros será su retorno; luego Al-lah se erigirá como testigo en contra de lo que hacen." WHERE ayatNumber = 47 AND chapterNumber = 10;
UPDATE verses SET es = "Hay un Mensajero para cada pueblo. Por ello, cuando llega su Mensajero, se juzga entre ellos con equidad, y no son perjudicados." WHERE ayatNumber = 48 AND chapterNumber = 10;
UPDATE verses SET es = "Mas dicen: “¿Cuándo<em> se cumplirá</em> esta promesa, si sois sinceros?”." WHERE ayatNumber = 49 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “No tengo poder por mí mismo ni para perjudicar ni para eneficiar, excepto en lo que Al-lah desee. Para cada pueblo <em> incrédulo</em> hay un plazo fijado. Cuando llega a su término, no pueden retrasarlo un solo momento ni adelantarlo”." WHERE ayatNumber = 50 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “Decidme, si Su castigo os llega de noche o de día, ¿cómo podrá escapar el culpable por mucha prisa que se dé?”." WHERE ayatNumber = 51 AND chapterNumber = 10;
UPDATE verses SET es = "Y cuando haya caído sobre vosotros, ¿Creeréis entonces en él? ¡Cómo! ¡Ahora! ¿Solíais pedir<em> antes de esto</em> que viniera rápidamente?”." WHERE ayatNumber = 52 AND chapterNumber = 10;
UPDATE verses SET es = "Entonces se dirá a los injustos: “Probad el castigo duradero. Sólo se os recompensa por lo que habéis ganado”." WHERE ayatNumber = 53 AND chapterNumber = 10;
UPDATE verses SET es = "Y te preguntan: “¿Es cierto esto?”. Diles: “Sí, por mi Señor, es ciertamente verdad, y no podéis frustrarlo”." WHERE ayatNumber = 54 AND chapterNumber = 10;
UPDATE verses SET es = "Pues si cada alma que hace el mal poseyera todo lo que hay en la ierra, seguro que lo ofrecería para rescatarse con ello. Ocultarán <em> su</em> remordimiento cuando vean el castigo. Y la sentencia se dictará contra ellos con equidad, pues no serán perjudicados." WHERE ayatNumber = 55 AND chapterNumber = 10;
UPDATE verses SET es = "¡Sabedlo! a Al-lah, en verdad, pertenece todo lo que hay en el cielo y en la tierra. ¡Sabed que la promesa de Al-lah es cierta! Pero la mayoría de ellos no lo comprenden." WHERE ayatNumber = 56 AND chapterNumber = 10;
UPDATE verses SET es = "Él es quien da la vida y causa la muerte, y a Él seréis devueltos." WHERE ayatNumber = 57 AND chapterNumber = 10;
UPDATE verses SET es = "¡Oh, humanidad! En verdad os ha llegado una exhortación de vuestro Señor y un remedio para cualquier<em> enfermedad</em> que haya en los corazones, así como una guía y misericordia para los creyentes." WHERE ayatNumber = 58 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: <em>“Todo esto es</em> por la gracia de Al-lah y por Su misericordia; que se alegren, pues por ella. Esto es mejor que lo que atesoran”." WHERE ayatNumber = 59 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “¿Os habéis dado cuenta de que Al-lah os envió provisiones y uego hicisteis que<em> algunas</em> de ellas fuesen ilícitas y <em> otras</em> lícitas?”. Diles: “¿Os ha permitido Al-lah<em> eso</em> o inventáis mentiras contra Al-lah?”." WHERE ayatNumber = 60 AND chapterNumber = 10;
UPDATE verses SET es = "¿Qué piensan quienes inventan mentiras contra Al-lah sobre el Día de la Resurrección? En verdad, Al-lah es Clemente para la humanidad, pero la mayoría de ellos no son agradecidos." WHERE ayatNumber = 61 AND chapterNumber = 10;
UPDATE verses SET es = "Y no emprendes ninguna obra, ni recitas de Él parte alguna del Corán, ni haces nada sin que te observemos cuando estás ocupado en ello. Y nada hay oculto a la vista de tu Señor, ni siquiera lo que pesa como una artícula, o más o menos que ella, en la tierra o en el cielo, que no esté <em>registrado</em> en un Libro explícito." WHERE ayatNumber = 62 AND chapterNumber = 10;
UPDATE verses SET es = "He ahí que los amigos de Al-lah no tendrán, en verdad, temor, ni serán afligidos." WHERE ayatNumber = 63 AND chapterNumber = 10;
UPDATE verses SET es = "Quienes creyeron y siempre actuaron con justicia," WHERE ayatNumber = 64 AND chapterNumber = 10;
UPDATE verses SET es = "Para ellos hay buenas nuevas en la vida presente y<em> también</em> en el Más Allá, pues la palabra de Al-lah no cambia; ése es en verdad el triunfo supremo." WHERE ayatNumber = 65 AND chapterNumber = 10;
UPDATE verses SET es = "Que sus palabras no te entristezcan. En verdad toda la majestad corresponde a Al-lah. Él es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 66 AND chapterNumber = 10;
UPDATE verses SET es = "He ahí que a Al-lah pertenece todo lo que hay en los cielos y todo lo que hay en la tierra. Quienes rezan a otros socios distintos de Al-lah no les siguen<em> realmente;</em> sólo siguen a la ficción y no hacen sino conjeturas." WHERE ayatNumber = 67 AND chapterNumber = 10;
UPDATE verses SET es = "Él es Quien hizo la noche para que en ella descanséis, y el día radiante. En verdad, en ello hay Signos para las gentes que escuchan." WHERE ayatNumber = 68 AND chapterNumber = 10;
UPDATE verses SET es = "Dicen: “Al-lah ha tomado para Sí un hijo”. ¡Santo es Él! Él es Autosuficiente. A Él pertenece cuanto hay en los cielos y en la tierra. No tenéis autoridad para esto. ¿Decís acaso contra Al-lah lo que desconocéis?" WHERE ayatNumber = 69 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “Quienes inventan mentiras contra Al-lah no prosperarán”." WHERE ayatNumber = 70 AND chapterNumber = 10;
UPDATE verses SET es = "<em> Tendrán </em>una pequeña ganancia temporal en este mundo. Después volverán a nosotros. Entonces les haremos probar un severo castigo, porque no quisieron creer." WHERE ayatNumber = 71 AND chapterNumber = 10;
UPDATE verses SET es = "Recítales la historia de Noé, cuando dijo a su pueblo: “Oh, pueblo mío, i os ofenden mi permanencia <em>con Dios</em> y que os haya recordado <em> vuestra obligación</em> por medio de los Signos de Al-lah –y en Al-lah ongo mi confianza– reunid <em>todos</em> vuestros designios, <em>vosotros</em> y vuestros “socios”; que vuestra manera de actuar no sea oscura para vosotros; a continuación ejecutad<em> vuestros propósitos</em> contra mí y no me deis ningún respiro." WHERE ayatNumber = 72 AND chapterNumber = 10;
UPDATE verses SET es = "“Pero si os dais la vuelta,<em> recordad</em> que no os he pedido recompensa alguna. Mi recompensa está sólo en Al-lah y se me ha ordenado ser de aquellos que se someten <em>a Él”</em>." WHERE ayatNumber = 73 AND chapterNumber = 10;
UPDATE verses SET es = "Pero le rechazaron, por lo que le salvamos a él y a quienes estaban con él en el Arca. Y les hicimos herederos <em>de la tierra</em>, a la vez que ahogamos a los que rechazaron Nuestros Signos. ¡Ved, pues, cómo fue el fin de quienes habían sido advertidos!" WHERE ayatNumber = 74 AND chapterNumber = 10;
UPDATE verses SET es = "Después enviamos tras él a <em>otros </em>Mensajeros a sus pueblos <em> respectivos,</em> quienes les llevaron pruebas claras. Pero no creyeron <em>en ellos,</em> porque anteriormente les habían rechazado. De este modo sellamos los corazones de los transgresores." WHERE ayatNumber = 75 AND chapterNumber = 10;
UPDATE verses SET es = "Luego enviamos, tras ellos, a Moisés y a Aarón al Faraón y a sus jefes con Nuestros Signos, pero se comportaron con arrogancia. Era un pueblo pecador." WHERE ayatNumber = 76 AND chapterNumber = 10;
UPDATE verses SET es = "Y cuando les llegó la verdad Nuestra, dijeron: “es ciertamente una brujería manifiesta”." WHERE ayatNumber = 77 AND chapterNumber = 10;
UPDATE verses SET es = "Moisés dijo: “¿Decís <em>esto</em> de la verdad cuando os ha llegado? ¿Es esto brujería? Pues los brujos nunca prosperan”;" WHERE ayatNumber = 78 AND chapterNumber = 10;
UPDATE verses SET es = "Dijeron: “¿Has venido a nosotros para apartarnos de lo que comprobamos que seguían nuestros padres, y para que la grandeza del país os pertenezca a los dos? Pero no creeremos en ninguno de vosotros”." WHERE ayatNumber = 79 AND chapterNumber = 10;
UPDATE verses SET es = "Entonces el Faraón dijo: “Traedme a los magos más hábiles”." WHERE ayatNumber = 80 AND chapterNumber = 10;
UPDATE verses SET es = "Y cuando llegaron los magos, Moisés les dijo: “Arrojad lo que tengáis que arrojar”." WHERE ayatNumber = 81 AND chapterNumber = 10;
UPDATE verses SET es = "Y cuando hubieron arrojado, Moisés dijo: “lo que habéis traído es simple brujería. En verdad, Al-lah la hará inútil. Ciertamente Al-lah no permite que prosperen las obras de los corruptores." WHERE ayatNumber = 82 AND chapterNumber = 10;
UPDATE verses SET es = "Pues Al-lah establece la verdad con Sus palabras, aunque los culpables se muestren contrarios a ellas”." WHERE ayatNumber = 83 AND chapterNumber = 10;
UPDATE verses SET es = "Pero nadie obedeció a Moisés salvo una generación de su pueblo, por temor al Faraón y a sus jefes, no fuera que los persiguieran. Y verdaderamente, el Faraón era un déspota en el país y era ciertamente uno de los transgresores." WHERE ayatNumber = 84 AND chapterNumber = 10;
UPDATE verses SET es = "Moisés dijo: “Oh, pueblo mío, si habéis creído en Al-lah, poned en Él vuestra confianza, si en efecto os sometéis a <em>Su voluntad”.</em>" WHERE ayatNumber = 85 AND chapterNumber = 10;
UPDATE verses SET es = "Ellos dijeron: “En Al-lah ponemos nuestra confianza. Señor nuestro, no nos conviertas en prueba de las gentes injustas”." WHERE ayatNumber = 86 AND chapterNumber = 10;
UPDATE verses SET es = "“Mas líbranos, por Tu misericordia, de los incrédulos”." WHERE ayatNumber = 87 AND chapterNumber = 10;
UPDATE verses SET es = "Entonces hablamos a Moisés y a su hermano,<em> diciéndoles:</em> “Construid casas para vuestras gentes en<em> la</em> ciudad; haced que estas casas miren a la misma dirección, y cumplid la Oración. Y dad la buena nueva a los creyentes”." WHERE ayatNumber = 88 AND chapterNumber = 10;
UPDATE verses SET es = "Moisés dijo: “Señor Nuestro, Tú has concedido al Faraón y a sus jefes ornamentos y riquezas en la vida presente, con el resultado, Señor Nuestro, de que están apartando a <em>la gente</em> de Tu camino. ¡Señor Nuestro! estruye sus riquezas y trata con severidad sus corazones, pues <em>parece que</em> no creerán mientras no vean un doloroso castigo”." WHERE ayatNumber = 89 AND chapterNumber = 10;
UPDATE verses SET es = "Dijo: “Tu oración ha sido escuchada, sed pues perseverantes y no sigáis el camino de los que no conocen”." WHERE ayatNumber = 90 AND chapterNumber = 10;
UPDATE verses SET es = "Entonces hicimos que los hijos de Israel cruzaran el Mar; el Faraón y sus huestes los persiguieron con enemistad y malvadas intenciones, hasta que, cuando le sucedió <em>la desgracia de</em> estar a punto de ahogarse dijo: “Creo que no hay otro dios sino Aquel en Quien creen los hijos de Israel, y soy de los que se someten <em>a Él”</em>." WHERE ayatNumber = 91 AND chapterNumber = 10;
UPDATE verses SET es = "¡Cómo! ¡Ahora! cuando antes fuiste desobediente y eras de los que creaban desorden." WHERE ayatNumber = 92 AND chapterNumber = 10;
UPDATE verses SET es = "Por tanto, en este día salvaremos <em>sólo</em> tu cuerpo para que te conviertas en un Signo para los que vengan después de ti. En verdad, muchos de los hombres no hacen caso de Nuestros Signos." WHERE ayatNumber = 93 AND chapterNumber = 10;
UPDATE verses SET es = "Y deparamos a los hijos de Israel una morada excelente, les proporcionamos buenas cosas y no discutieron hasta que recibieron el conocimiento. En verdad, tu Señor les juzgará en el Día de la Resurrección a propósito de lo que discutieron." WHERE ayatNumber = 94 AND chapterNumber = 10;
UPDATE verses SET es = "Mas si tienes alguna duda sobre lo que te hemos revelado, pregunta a quienes han estado leyendo el Libro antes que tú. En efecto, la verdad ha llegado a ti desde tu Señor; no seas, por tanto, de los que dudan." WHERE ayatNumber = 95 AND chapterNumber = 10;
UPDATE verses SET es = "No seas tampoco de los que rechazan los Signos de Al-lah, pues te contarías entre los perdedores." WHERE ayatNumber = 96 AND chapterNumber = 10;
UPDATE verses SET es = "En verdad, aquellos contra quienes se ha cumplido la palabra de tu Señor no creerán." WHERE ayatNumber = 97 AND chapterNumber = 10;
UPDATE verses SET es = "Aunque les lleguen todos los Signos, mientras no vean el terrible castigo." WHERE ayatNumber = 98 AND chapterNumber = 10;
UPDATE verses SET es = "¿Por qué no hubo<em> otro</em> pueblo que creyese salvo el pueblo de Jonás, para que les hubiera beneficiado su fe? Cuando creyeron, les retiramos el castigo de la humillación en la presente vida y les dimos provisiones para algún tiempo." WHERE ayatNumber = 99 AND chapterNumber = 10;
UPDATE verses SET es = "Mas si tu Señor hubiese <em>impuesto</em> Su voluntad, en verdad <em>que</em> todos los que están en la tierra habrían creído juntos. ¿Obligarás por tanto, a los hombres a hacerse creyentes?" WHERE ayatNumber = 100 AND chapterNumber = 10;
UPDATE verses SET es = "Ningún alma puede creer salvo con permiso de Al-lah. Pues Él hace que Su cólera <em>descienda</em> sobre aquellos que no utilizan la razón." WHERE ayatNumber = 101 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “Considerad lo que está <em>sucediendo</em> en los cielos y en la tierra”. Pero los Signos y los Amonestadores no sirven de nada a las gentes incrédulas”." WHERE ayatNumber = 102 AND chapterNumber = 10;
UPDATE verses SET es = "“¿Qué esperan ellos salvo algo similar a los días <em>de castigo que sufrieron</em> los que murieron antes que ellos? Diles: “Esperad, pues yo estoy con vosotros entre quienes esperan”." WHERE ayatNumber = 103 AND chapterNumber = 10;
UPDATE verses SET es = "Entonces salvaremos a Nuestros Mensajeros y a los creyentes. Así <em>sucede siempre;</em> nos corresponde a Nosotros salvar a los creyentes." WHERE ayatNumber = 104 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “Oh vosotros, hombres: si tenéis dudas sobre mi religión, entonces <em>sabed que</em> no adoro a quienes vosotros adoráis aparte de Al-lah, sino que <em>sólo</em> adoro a Al-lah, Quien os hace morir; y se me ha ordenado ser de los creyentes." WHERE ayatNumber = 105 AND chapterNumber = 10;
UPDATE verses SET es = "“Y <em>se me ha ordenado también que diga:</em> “Dirige firmemente tu objetivo hacia la fe como el que siempre se inclina ante Dios, y no seas de los que asocian otros partícipes<em> con Él”.</em>" WHERE ayatNumber = 106 AND chapterNumber = 10;
UPDATE verses SET es = "“Y aparte de Al-lah, no invoques a ningún otro que no pueda ni beneficiarte ni perjudicarte. Si lo haces, serás ciertamente de los injustos”." WHERE ayatNumber = 107 AND chapterNumber = 10;
UPDATE verses SET es = "Si Al-lah te toca con algún mal, no hay nadie que pueda retirártelo excepto Él; y si Él desea algo bueno para ti, nadie podrá alejar de ti Su gracia. Él hace que <em>ésta </em>alcance a quien El quiere de entre Sus siervos. Pues Él es el Sumo Indulgente, el Misericordioso." WHERE ayatNumber = 108 AND chapterNumber = 10;
UPDATE verses SET es = "Diles: “Oh vosotros, hombres: ahora os ha llegado la verdad de vuestro Señor. Así, quien siga la guía la sigue únicamente por el bien de su propia alma, y quien yerra, yerra sólo contra ella. Pues yo no soy vuestro guardián." WHERE ayatNumber = 109 AND chapterNumber = 10;
UPDATE verses SET es = "Y sigue lo que te ha sido revelado y persevera hasta que Al-lah dicte Su sentencia. Pues Él es el Mejor de los jueces." WHERE ayatNumber = 110 AND chapterNumber = 10;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 11;
UPDATE verses SET es = "Alif Lam Ra. Éste es un Libro cuyos versículos han sido fortalecidos creados sin defecto, y luego expuestos con detalle. <em>Procede</em> del Único Sabio y Conocedor de todo." WHERE ayatNumber = 2 AND chapterNumber = 11;
UPDATE verses SET es = "<em> Enseña</em> que no debéis adorar a nadie sino a Al-lah. Sólo soy para vosotros un Amonestador y portador de buenas nuevas de Él;" WHERE ayatNumber = 3 AND chapterNumber = 11;
UPDATE verses SET es = "Para que pidáis el perdón de vuestro Señor y volváis después a Él. Él os proporcionará una provisión generosa hasta el plazo fijado. Y concederá Su gracia a quien la merezca. Pero si os apartáis temo para vosotros la sanción del día grandioso." WHERE ayatNumber = 4 AND chapterNumber = 11;
UPDATE verses SET es = "Hacia Al-lah regresaréis, y Él tiene poder sobre todas las cosas." WHERE ayatNumber = 5 AND chapterNumber = 11;
UPDATE verses SET es = "Ahora, en verdad, repliegan sus pechos para esconderse de Él. <em> Pero aunque</em> se cubran con sus vestiduras, Él sabe lo que ocultan y lo que revelan. En verdad, Él conoce perfectamente lo que hay en <em>sus </em>corazones." WHERE ayatNumber = 6 AND chapterNumber = 11;
UPDATE verses SET es = "Pues no existe criatura que se mueva en la tierra sin que sea Al-lah Quien le proporcione su sustento. Él conoce su lugar de residencia temporal y permanente. Todo<em> esto</em> está <em>registrado</em> en un Libro diáfano." WHERE ayatNumber = 7 AND chapterNumber = 11;
UPDATE verses SET es = "Él es Quien creó los cielos y la tierra en seis períodos y Su Trono se apoya en el agua, para probaros y <em>mostrar</em> cuál de vosotros observa mejor conducta. Y si dices: “Seréis resucitados después de la muerte”, los incrédulos dirán ciertamente. “esto no es más que un engaño manifiesto”." WHERE ayatNumber = 8 AND chapterNumber = 11;
UPDATE verses SET es = "Pero si les retrasamos el castigo hasta el tiempo calculado, ciertamente dirían: “¿qué es lo que lo retiene?”. Pues bien, en el día en que les llegue, en verdad no les será apartado, y aquello de lo que solían burlarse, les rodeará." WHERE ayatNumber = 9 AND chapterNumber = 11;
UPDATE verses SET es = "Mas si hacemos que el hombre pruebe Nuestra misericordia, y después se la retiramos, en verdad se siente desesperado, desagradecido." WHERE ayatNumber = 10 AND chapterNumber = 11;
UPDATE verses SET es = "Y si después de que ha sufrido la desgracia le hacemos probar la prosperidad, dirá ciertamente: “¡los males se han apartado de mí!”. Helo ahí exultante, lleno de jactancia." WHERE ayatNumber = 11 AND chapterNumber = 11;
UPDATE verses SET es = "Salvo los perseverantes y los que hacen buenas obras: son ellos quienes tendrán el perdón y una gran recompensa." WHERE ayatNumber = 12 AND chapterNumber = 11;
UPDATE verses SET es = "<em> Se imaginan</em> que tal vez vayas a abandonar ahora parte de lo que te ha sido revelado; y que tu pecho se va a estrechar por ello, porque dicen: “¿Por qué no se le ha enviado un tesoro desde lo alto o ha venido con él un ángel?”. En verdad, tú sólo eres un Amonestador, y Al-lah es el Guardián de todas las cosas." WHERE ayatNumber = 13 AND chapterNumber = 11;
UPDATE verses SET es = "¿Por ventura dicen: “él lo ha inventado”? Diles: “Presentad entonces diez Surahs como éste, inventados, y acudid a quien podáis aparte de Al-lah, si sois veraces”." WHERE ayatNumber = 14 AND chapterNumber = 11;
UPDATE verses SET es = "Y si ellos no te responden sabed entonces que ha sido revelado <em>lleno</em> del conocimiento de Al-lah y que no hay otro Dios fuera de Él. ¿Os someteréis entonces?" WHERE ayatNumber = 15 AND chapterNumber = 11;
UPDATE verses SET es = "A quienes deseen la vida presente y su ornato, les pagaremos plenamente por sus obras en esta <em>vida</em> y no serán perjudicados." WHERE ayatNumber = 16 AND chapterNumber = 11;
UPDATE verses SET es = "Son quienes no tendrán nada en el Más Allá salvo el Fuego; y lo que forjaron en esta vida se reducirá a la nada, y vano será lo que solían hacer." WHERE ayatNumber = 17 AND chapterNumber = 11;
UPDATE verses SET es = "¿ Puede <em>ser</em> acaso <em>un impostor</em> quien <em>tiene</em> una prueba clara de su Señor, y a quien le sigue un testigo Suyo para dar <em>testimonio</em> de <em>su verdad</em>, y que fue precedido del Libro de Moisés, una guía y una <em>fuente de</em> misericordia? Ellos creen en él; y quienquiera que, de las diferentes sectas, le rechace, tendrá el Fuego como su lugar fijado. No dudes pues sobre ello. Ciertamente es la verdad de tu Señor; aunque la mayoría de la gente no cree." WHERE ayatNumber = 18 AND chapterNumber = 11;
UPDATE verses SET es = "Mas ¿quién es más injusto que quien trama una mentira contra Al-lah? Los que actúen así serán presentados ante su Señor y los testigos dirán: “Éstos son quienes mintieron contra su Señor”. En verdad, la maldición de Al-lah cae sobre el injusto," WHERE ayatNumber = 19 AND chapterNumber = 11;
UPDATE verses SET es = "Quienes apartan <em>a los hombres</em> del camino de Al-lah e intentan hacerlo tortuoso. Son éstos quienes no creen en el Más Allá." WHERE ayatNumber = 20 AND chapterNumber = 11;
UPDATE verses SET es = "Tales personas no podrán nunca desbaratar los <em> planes de Dios</em> en la tierra, ni tendrán amigo alguno prescindiendo de Al-lah. A ellos se les duplicará el castigo. No pueden oír ni ver." WHERE ayatNumber = 21 AND chapterNumber = 11;
UPDATE verses SET es = "Son éstos quienes han arruinado sus almas, y lo que inventaron les fallará." WHERE ayatNumber = 22 AND chapterNumber = 11;
UPDATE verses SET es = "Ciertamente ellos serán los mayores perdedores en el Más Allá." WHERE ayatNumber = 23 AND chapterNumber = 11;
UPDATE verses SET es = "En verdad, quienes creen y hacen buenas obras, humillándose ante su Señor, serán los habitantes del Cielo; en él morarán." WHERE ayatNumber = 24 AND chapterNumber = 11;
UPDATE verses SET es = "El ejemplo de los dos grupos es como <em>el del</em> ciego y el sordo, y el que ve y oye. ¿Son los dos iguales? ¿No comprenderéis pues?" WHERE ayatNumber = 25 AND chapterNumber = 11;
UPDATE verses SET es = "Mas enviamos a Noé a su pueblo, y <em>dijo:</em> “en verdad, soy para vosotros un claro Amonestador”." WHERE ayatNumber = 26 AND chapterNumber = 11;
UPDATE verses SET es = "“Para que no adoréis sino a Al-lah. En verdad, temo para vosotros el castigo de un día doloroso”." WHERE ayatNumber = 27 AND chapterNumber = 11;
UPDATE verses SET es = "Los jefes de su pueblo, que no creían, respondieron: “Sólo vemos en ti un hombre como nosotros mismos, y es comúnmente sabido que nadie te ha seguido excepto nuestros inferiores. No vemos que tengáis superioridad alguna sobre nosotros; no, pensamos que sois embusteros”." WHERE ayatNumber = 28 AND chapterNumber = 11;
UPDATE verses SET es = "Dijo: “Oh pueblo mío, dime: si me apoyo en una prueba clara de mi Señor y Él me ha concedido de Sí mismo una gran merced que ha sido hecha oscura para vosotros, ¿hemos de forzaros a admitirla, si sois contrarios a ella?" WHERE ayatNumber = 29 AND chapterNumber = 11;
UPDATE verses SET es = "“Oh pueblo mío, no te pido riqueza alguna a cambio de ella. Mi recompensa sólo la espero de Al-lah. Pues no extraviaré a quienes creen. Ellos ciertamente se reunirán con su Señor. Pero os considero un pueblo que actúa con ignorancia”;" WHERE ayatNumber = 30 AND chapterNumber = 11;
UPDATE verses SET es = "“Oh pueblo mío, ¿quién me ayudaría contra Al-lah si quisiera extraviarlos? ¿no os dais cuenta?" WHERE ayatNumber = 31 AND chapterNumber = 11;
UPDATE verses SET es = "“Pues no os digo: “poseo los tesoros de Al-lah”, ni conozco lo invisible, ni digo: “soy un ángel”. Ni digo de aquellos que son despreciados por vuestros ojos: “Al-lah no les concederá ningún bien – Al-lah sabe mejor lo que hay en sus mentes -. En verdad, sería entonces de los injustos”." WHERE ayatNumber = 32 AND chapterNumber = 11;
UPDATE verses SET es = "Respondieron: “Oh, Noé, en verdad has disputado con nosotros durante<em> mucho tiempo</em> y has discutido con nosotros muchas veces; tráenos ahora aquello con lo que nos amenazas, si eres de los que dicen la verdad”." WHERE ayatNumber = 33 AND chapterNumber = 11;
UPDATE verses SET es = "Él dijo: “Sólo Al-lah os lo traerá, si Le place, y no podéis frustrar el <em>objetivo de</em> Dios”." WHERE ayatNumber = 34 AND chapterNumber = 11;
UPDATE verses SET es = "“Mi amonestación no os aprovechará por mucho que quiera advertiros, si Al-lah decide que permanezcáis extraviados. Él es vuestro Señor y a Él se os hará retornar”." WHERE ayatNumber = 35 AND chapterNumber = 11;
UPDATE verses SET es = "¿Dicen acaso: “él lo ha inventado”? Diles: “Si yo lo he inventado, mía será la culpa, pero soy inocente de los pecados que vosotros cometéis”." WHERE ayatNumber = 36 AND chapterNumber = 11;
UPDATE verses SET es = "Y se reveló a Noé: “nadie de tu pueblo creerá excepto quienes han creído <em>ya;</em> no te aflijas, por tanto, por lo que han estado haciendo”." WHERE ayatNumber = 37 AND chapterNumber = 11;
UPDATE verses SET es = "“Construye el Arca ante Nuestros ojos y según <em>te ordenó</em> Nuestra revelación. Y no me preguntes sobre los impíos. Ciertamente serán ahogados”." WHERE ayatNumber = 38 AND chapterNumber = 11;
UPDATE verses SET es = "Empezó a fabricar el Arca; y cada vez que los jefes de su pueblo pasaban a su lado, se burlaban de él. Dijo: “si ahora os burláis de nosotros, llegará el <em>momento en que</em> nosotros nos burlaremos de vosotros igual que os burláis <em>ahora”.</em>" WHERE ayatNumber = 39 AND chapterNumber = 11;
UPDATE verses SET es = "“Entonces sabréis quién es aquel sobre el que caerá el castigo que lo cubrirá de ignominia, y sobre quien caerá el castigo duradero”." WHERE ayatNumber = 40 AND chapterNumber = 11;
UPDATE verses SET es = "Hasta que llegó Nuestra orden y las fuentes <em>de la tierra</em> empezaron a manar, y dijimos: “embarca en ella a dos de cada especie, macho y hembra, y a tu familia, excepto contra quienes ha sido ya roferida la palabra, y a los creyentes”. Pero no creyeron ni <em>vivieron</em> con él excepto unos pocos." WHERE ayatNumber = 41 AND chapterNumber = 11;
UPDATE verses SET es = "Dijo: “Embarcad en ella. En nombre de Al-lah sea su curso y su fondeo. Mi Señor es ciertamente el Sumo Indulgente, Misericordioso”." WHERE ayatNumber = 42 AND chapterNumber = 11;
UPDATE verses SET es = "Entonces empezó a moverse junto con ellos sobre olas como montañas. Noé gritó a su hijo, que se mantenía apartado: “Hijo mío, embarca con nosotros y no permanezcas con los incrédulos”." WHERE ayatNumber = 43 AND chapterNumber = 11;
UPDATE verses SET es = "Él respondió: “Me subiré pronto a una montaña que me protegerá del agua”. Él dijo: “no hay protección para <em>nadie</em> en este día contra el decreto de Al-lah, excepto aquellos a quienes Él muestre misericordia”. Y una ola los separó; por lo que estuvo entre los ahogados." WHERE ayatNumber = 44 AND chapterNumber = 11;
UPDATE verses SET es = "Y se dijo: “Oh, tierra, traga tu agua, y Oh, cielo, que cese<em> la lluvia.</em> El agua empezó a bajar y terminó el trance. Y el <em>Arca</em> quedó apoyada en Al-Yudi. Y se dijo: “Malditos sean los hombres malvados”." WHERE ayatNumber = 45 AND chapterNumber = 11;
UPDATE verses SET es = "Noé clamó a su Señor y dijo: “Señor mío, en verdad mi hijo es de mi familia y ciertamente Tu promesa es verdadera, y Tú eres el Más Justo de los jueces”." WHERE ayatNumber = 46 AND chapterNumber = 11;
UPDATE verses SET es = "Él dijo: “Oh, Noé, en verdad no es de tu familia; él es en realidad un <em>hombre de </em>conducta impía. No me pidas pues aquello de lo que no tienes conocimiento. Te aconsejo para que no seas <em>uno</em> de los ignorantes”." WHERE ayatNumber = 47 AND chapterNumber = 11;
UPDATE verses SET es = "Él dijo: “Señor mío, te ruego que me protejas para que no te pregunte sobre aquello de lo que no tengo conocimiento. Pues si no me perdonas y no tienes misericordia de mí, estaré entre los perdedores”." WHERE ayatNumber = 48 AND chapterNumber = 11;
UPDATE verses SET es = "Se dijo: “Oh, Noé, baja pues, con la paz Nuestra y con las bendiciones sobre ti y sobre las naciones que <em>nacerán</em> de los que están contigo. Y habrán<em> otros</em> pueblos a quienes concederemos provisiones durante<em> un tiempo, </em>y después les alcanzará un doloroso castigo procedente de Nosotros”." WHERE ayatNumber = 49 AND chapterNumber = 11;
UPDATE verses SET es = "Esto corresponde a las noticias de lo desconocido que te revelamos. Tú no las sabías, ni tampoco tu pueblo, antes de esto. Sé pues paciente; ya que el fin es para los que temen a Dios." WHERE ayatNumber = 50 AND chapterNumber = 11;
UPDATE verses SET es = "Y <em>enviamos</em> a ‘Ad a su hermano Hud. Dijo: “Oh pueblo mío, adorad <em>sólo</em> a Al-lah. No tenéis otro Dios fuera de Él. No sois más que forjadores de mentiras." WHERE ayatNumber = 51 AND chapterNumber = 11;
UPDATE verses SET es = "“¡Oh pueblo mío! no pido de ti recompensa alguna. Mi recompensa sólo corresponde a Quien me ha modelado. ¿No queréis comprender?" WHERE ayatNumber = 52 AND chapterNumber = 11;
UPDATE verses SET es = "“¡Oh pueblo mío! pide perdón a tu Señor y vuelve después a Él; Él os enviará nubes que soltarán lluvias abundantes y añadirá fuerza a vuestras fuerzas. No os apartéis como culpables”." WHERE ayatNumber = 53 AND chapterNumber = 11;
UPDATE verses SET es = "Respondieron: “Oh Hud, tú no nos has traído ninguna prueba clara, y no vamos a prescindir de nuestros dioses<em> simplemente</em> por lo que dices, ni vamos a creer en ti”." WHERE ayatNumber = 54 AND chapterNumber = 11;
UPDATE verses SET es = "“Sólo podemos decir que algunos de nuestros dioses te han afligido para humillarte”. Él respondió: “En verdad, pongo a Al-lah por testigo, y vosotros sois también testigos de que no tengo culpa de que asociéis otros partícipes<em> a Dios.</em>" WHERE ayatNumber = 55 AND chapterNumber = 11;
UPDATE verses SET es = "“Además de Él. Tramad pues planes contra mí, todos vosotros, y no me deis respiro”;" WHERE ayatNumber = 56 AND chapterNumber = 11;
UPDATE verses SET es = "“En verdad he puesto mi confianza en Al-lah, Señor mío y Señor vuestro. No existe criatura que se mueva <em>en la tierra</em> a la que l no sujete por el mechón de la frente. En verdad, mi Señor <em> permanece</em> en el camino recto”." WHERE ayatNumber = 57 AND chapterNumber = 11;
UPDATE verses SET es = "“Pero si os alejáis, os he transmitido ya aquello con lo que fui enviado a vosotros, y mi Señor hará que otro pueblo ocupe vuestro lugar. No podéis perjudicarle en absoluto. En verdad, mi Señor es Guardián de todas las cosas”." WHERE ayatNumber = 58 AND chapterNumber = 11;
UPDATE verses SET es = "Cuando llegó Nuestra orden, salvamos a Hud y a los que creyeron con él, por Nuestra misericordia <em>especial</em>. Y los salvamos de un severo tormento." WHERE ayatNumber = 59 AND chapterNumber = 11;
UPDATE verses SET es = "Y así fueron los Ad. Negaron los Signos de su Señor y desobedecieron a Sus Mensajeros y siguieron las órdenes de los enemigos encarnizados <em>de la verdad.</em>" WHERE ayatNumber = 60 AND chapterNumber = 11;
UPDATE verses SET es = "Y se hizo que les persiguiera una maldición en este mundo y en el Día de la Resurrección. He ahí que <em>la tribu de los</em> Ad no creyó en su Señor. ¡Mirad! ¡malditos sean los Ad, el pueblo de Hud!" WHERE ayatNumber = 61 AND chapterNumber = 11;
UPDATE verses SET es = "Y a <em>la tribu de</em> Zamud <em>enviamos</em> a su hermano Salih. Él dijo: “Oh pueblo mío, adorad a Al-lah; no tenéis más dios que Él. Él os ensalzó de la tierra, y os asentó en ella. Pedidle pues perdón y volved a Él de <em>todo corazón.</em> En verdad, mi Señor está cerca <em>y</em> responde<em> a las oraciones”.</em>" WHERE ayatNumber = 62 AND chapterNumber = 11;
UPDATE verses SET es = "Respondieron: “Oh Salih, fuiste aquel entre nosotros en quien pusimos nuestras esperanzas. ¿Nos prohíbes adorar a lo que adoraron nuestros padres? Estamos en verdad en una gran duda acerca de lo que nos propones”." WHERE ayatNumber = 63 AND chapterNumber = 11;
UPDATE verses SET es = "Él dijo: “Oh pueblo mío, decidme: si me apoyo en una prueba evidente de mi Señor, y Él me ha concedido Su misericordia, ¿quién me ayudará pues contra Al-lah si lo desobedezco? Vosotros no haríais sino aumentar mi destrucción”;" WHERE ayatNumber = 64 AND chapterNumber = 11;
UPDATE verses SET es = "“Oh pueblo mío, ésta es la camella de Al-lah como Signo para vosotros, dejadla sola para que pueda pacer en la tierra de Al-lah, y no la toquéis haciéndole daño, no sea que os alcance pronto el castigo”." WHERE ayatNumber = 65 AND chapterNumber = 11;
UPDATE verses SET es = "Pero ellos la desjarretaron. Entonces dijo: “disfrutad en vuestras casas durante tres días. Esta promesa no quedará sin cumplir”." WHERE ayatNumber = 66 AND chapterNumber = 11;
UPDATE verses SET es = "Así, cuando llegó a Nuestra orden, salvamos a Salih y a quienes con él creyeron por Nuestra Misericordia <em>especial</em> y los salvamos de la ignominia de ese día. En verdad, tu Señor es Omnipotente, Poderoso." WHERE ayatNumber = 67 AND chapterNumber = 11;
UPDATE verses SET es = "Y un golpe atronador abatió a quienes habían hecho el mal, y cuando amaneció aparecieron postrados en sus casas." WHERE ayatNumber = 68 AND chapterNumber = 11;
UPDATE verses SET es = "Como si nunca hubiesen morado en ellas. He aquí que los Zamud se omportaron con desagradecimiento hacia su Señor; ¡maldita sea <em>la tribu de</em> Zamud!" WHERE ayatNumber = 69 AND chapterNumber = 11;
UPDATE verses SET es = "En verdad, Nuestros Mensajeros llegaron a Abraham con buenas noticias. Dijeron:<em> “Te ofrecemos</em> la paz”. Él respondió: “la paz <em>sea con vosotros”</em> y no tardó mucho en traer un ternero asado." WHERE ayatNumber = 70 AND chapterNumber = 11;
UPDATE verses SET es = "Pero cuando vio que sus manos no se acercaban a él, los tomó por extraños <em>poco amistosos</em> y sintió temor ante ellos. Dijeron: “No temas, porque hemos sido enviados a las gentes de Lot”." WHERE ayatNumber = 71 AND chapterNumber = 11;
UPDATE verses SET es = "Y su esposa, que estaba de pie <em>al lado,</em> sonrió, y le dimos las buenas noticias del nacimiento de Isaac y, después, el de Jacob." WHERE ayatNumber = 72 AND chapterNumber = 11;
UPDATE verses SET es = "Ella dijo: “¡Desdichada de mí! ¿Cómo voy a tener un hijo si soy una mujer vieja, y éste mi marido es un hombre viejo? ¡Cosa extraña es ésta, en verdad!”." WHERE ayatNumber = 73 AND chapterNumber = 11;
UPDATE verses SET es = "Ellos dijeron: “¿Te sorprende el designio de Al-lah? La misericordia de Al-lah y Sus bendiciones están con vosotros, Oh gente de la Casa. En verdad, Él merece todas las alabanzas y toda la gloria”." WHERE ayatNumber = 74 AND chapterNumber = 11;
UPDATE verses SET es = "Y cuando el temor abandonó a Abraham, y le llegó la buena nueva, empezó a interceder ante Nosotros por el pueblo de Lot." WHERE ayatNumber = 75 AND chapterNumber = 11;
UPDATE verses SET es = "En efecto, Abraham era indulgente, compasivo, y volvía siempre hacia Dios." WHERE ayatNumber = 76 AND chapterNumber = 11;
UPDATE verses SET es = "“Oh Abraham, apártate de esto. En verdad, la orden de tu Señor ha sido ya dada y ciertamente vendrá sobre ellos un castigo que no puede ser evitado”." WHERE ayatNumber = 77 AND chapterNumber = 11;
UPDATE verses SET es = "Y cuando Nuestros Mensajeros llegaron a Lot, se afligió por ellos y se sintió impotente para ayudarles y dijo: “Éste es un día duro”." WHERE ayatNumber = 78 AND chapterNumber = 11;
UPDATE verses SET es = "Sus gentes llegaron hacia él corriendo y temblando <em>de rabia;</em> y antes de esto también habían cometido el mal. Él dijo: “Oh, mi gente, estas son mis hijas; son absolutamente castas para vosotros. Por tanto, temed a Al-lah y no me avergoncéis ante mis invitados. ¿No hay entre vosotros un hombre recto?”" WHERE ayatNumber = 79 AND chapterNumber = 11;
UPDATE verses SET es = "Respondieron: “Bien sabes que no tenemos derecho alguno sobre tus hijas y sabes en verdad lo que deseamos”." WHERE ayatNumber = 80 AND chapterNumber = 11;
UPDATE verses SET es = "Él dijo: “Ojalá tuviera poder para <em>enfrentarme</em> con osotros, o pudiera recurrir a un apoyo potente <em> buscando refugio”.</em>" WHERE ayatNumber = 81 AND chapterNumber = 11;
UPDATE verses SET es = "Los <em>mensajeros</em> dijeron: “Oh Lot, somos los mensajeros de tu Señor. De ningún modo podrán alcanzarte. Sal pues con tu familia durante la noche, y no dejes que nadie de vosotros mire hacia atrás, exceptuada tu mujer. En verdad, lo que les va a suceder a ellos le pasará <em>también</em> a ella. Ciertamente, el momento fijado es la mañana ¿no está cerca la mañana?”" WHERE ayatNumber = 82 AND chapterNumber = 11;
UPDATE verses SET es = "Así, cuando llegó Nuestra orden, pusimos la ciudad de arriba abajo e hicimos llover sobre ella piedras de arcilla, capa sobre capa." WHERE ayatNumber = 83 AND chapterNumber = 11;
UPDATE verses SET es = "Señaladas <em>para ellos</em> en el decreto de tu Señor. Y un<em>castigo</em> similar no está lejos de los injustos de <em>esta época.</em>" WHERE ayatNumber = 84 AND chapterNumber = 11;
UPDATE verses SET es = "A Madián <em>enviamos</em> a su hermano Shu’aib. Él dijo: “Oh pueblo mío, adorad a Al-lah. No tenéis otro dios fuera de Él. No disminuyáis ni la medida ni el peso. Os veo en un <em>estado de</em> prosperidad y temo para vosotros el castigo de un día destructor”;" WHERE ayatNumber = 85 AND chapterNumber = 11;
UPDATE verses SET es = "¡Oh, pueblo mío! colmad la medida y el peso con equidad y no privéis a las gentes de lo que les corresponde <em>por derecho</em>, ni cometáis iniquidad en la tierra, causando el desorden." WHERE ayatNumber = 86 AND chapterNumber = 11;
UPDATE verses SET es = "“Lo que Al-lah <em>os</em> ha dejado es mejor para vosotros, si sois creyentes. Pues yo no soy vuestro guardián”." WHERE ayatNumber = 87 AND chapterNumber = 11;
UPDATE verses SET es = "Respondieron: “Oh Shu’aib, ¿te ordena por ventura tu Oración que abandonemos lo que adoraron nuestros padres, o que dejemos de hacer con nuestros bienes lo que nos plazca? En verdad, <em>eres</em> muy inteligente y recto”." WHERE ayatNumber = 88 AND chapterNumber = 11;
UPDATE verses SET es = "Dijo: “Oh pueblo mío, dime: si me apoyo en pruebas claras de mi eñor y Él me ha proporcionado de Sí mismo una abundante provisión,<em>¿qué respuesta Le daréis?</em> No deseo hacer contra vosotros <em> la misma </em>cosa que os pido que no hagáis. Sólo pretendo reformaros en la medida de mis posibilidades. No hay poder en mí, salvo a través de Al-lah. En Él confío y a Él retornaré." WHERE ayatNumber = 89 AND chapterNumber = 11;
UPDATE verses SET es = "“Oh pueblo mío, que<em> vuestra</em> hostilidad contra mí no os conduzca<em> hasta el punto de</em> que ocurra algo similar a lo que ocurrió al pueblo de Noé o al pueblo de Hud, o al de Salih; pues las gentes de Lot no están lejos de vosotros”;" WHERE ayatNumber = 90 AND chapterNumber = 11;
UPDATE verses SET es = "“Pedid perdón a vuestro Señor; luego volved a Él de <em>todo corazón.</em> En verdad, mi Señor es Misericordioso, el más Amante”." WHERE ayatNumber = 91 AND chapterNumber = 11;
UPDATE verses SET es = "Respondieron: “Oh Shu’aib, no entendemos mucho de lo que dices y en verdad, vemos que eres débil entre nosotros. Si no fuera por tu tribu, ciertamente te lapidaríamos. Pues tú no tienes poder sobre nosotros”." WHERE ayatNumber = 92 AND chapterNumber = 11;
UPDATE verses SET es = "Dijo: “Oh pueblo mío, ¿acaso mi tribu es más poderosa para vosotros que Al-lah? Mas vosotros Le habéis dado vuestras espaldas y Le habéis olvidado. En verdad, mi Señor alcanza todo lo que hacéis." WHERE ayatNumber = 93 AND chapterNumber = 11;
UPDATE verses SET es = "“Oh pueblo mío, actuad del mejor modo posible, pues yo <em>también</em> actúo. Pronto sabréis sobre quien caerá un castigo que lo afrentará, y quién es el mentiroso. Esperad; en verdad, yo espero con vosotros”." WHERE ayatNumber = 94 AND chapterNumber = 11;
UPDATE verses SET es = "Mas cuando vino Nuestra orden, salvamos a Shu’aib y a quienes habían creído con él por Nuestra misericordia <em>especial;</em> y el castigo se abatió sobre los que habían obrado mal, por lo que quedaron de bruces en sus casas." WHERE ayatNumber = 95 AND chapterNumber = 11;
UPDATE verses SET es = "Como si nunca hubiesen morado en ellas. Mirad cómo fueron aldecidos los de Madián, como <em>también </em>lo fueron los Zamud <em>antes de ellos.</em>" WHERE ayatNumber = 96 AND chapterNumber = 11;
UPDATE verses SET es = "En verdad, enviamos a Moisés con Nuestros Signos y una autoridad manifiesta," WHERE ayatNumber = 97 AND chapterNumber = 11;
UPDATE verses SET es = "Al Faraón y a sus jefes; pero siguieron la orden del Faraón, y la orden del Faraón no era justa." WHERE ayatNumber = 98 AND chapterNumber = 11;
UPDATE verses SET es = "Encabezará a su pueblo en el Día de la Resurrección y los guiará al Fuego, <em>igual que se lleva al ganado a los bebederos.</em> ¡Malo es el destino al que llegarán!" WHERE ayatNumber = 99 AND chapterNumber = 11;
UPDATE verses SET es = "Están perseguidos por una maldición en <em>esta vida</em> y en el Día de la Resurrección. ¡Qué detestable es el regalo y quienes lo han recibido!" WHERE ayatNumber = 100 AND chapterNumber = 11;
UPDATE verses SET es = "Éstas son las historias de las ciudades <em>arrasadas</em> que te elatamos. <em>Algunas</em> de ellas permanecen de pie y<em>otras</em> han sido segadas, <em>como si se tratara de la cosecha.</em>" WHERE ayatNumber = 101 AND chapterNumber = 11;
UPDATE verses SET es = "Nosotros no les perjudicamos, sino que se lo hicieron a sí mismos; pues sus dioses, a quienes invocaban en lugar de Al-lah, no les sirvieron de nada cuando llegó la orden de tu Señor; y no hicieron más que sumarse a su ruina." WHERE ayatNumber = 102 AND chapterNumber = 11;
UPDATE verses SET es = "Así castiga tu Señor, cuando se apodera de las ciudades mientras están haciendo el mal. En verdad, Su asimiento es doloroso <em>y</em> severo." WHERE ayatNumber = 103 AND chapterNumber = 11;
UPDATE verses SET es = "En esto hay ciertamente un Signo para quien teme el castigo del Más Allá. Es un día en el que<em> toda</em> la humanidad será ongregada y un día <em>cuyos acontecimientos </em>serán presenciados <em>por todos.</em>" WHERE ayatNumber = 104 AND chapterNumber = 11;
UPDATE verses SET es = "Y no lo retrasaremos salvo durante un plazo calculado." WHERE ayatNumber = 105 AND chapterNumber = 11;
UPDATE verses SET es = "El día en el que llegue no hablará alma alguna excepto con Su permiso; entonces <em>algunas</em> de ellas resultarán ser desgraciadas y <em>otras</em> afortunadas." WHERE ayatNumber = 106 AND chapterNumber = 11;
UPDATE verses SET es = "En cuanto a los que resulten desafortunados, estarán en el Fuego, allí habrá para ellos sollozos y suspiros." WHERE ayatNumber = 107 AND chapterNumber = 11;
UPDATE verses SET es = "Y allí morarán mientras subsistan los cielos y la tierra, salvo que tu Señor quiera <em>otra cosa.</em> En verdad, tu Señor hace cuanto desea." WHERE ayatNumber = 108 AND chapterNumber = 11;
UPDATE verses SET es = "Pero en cuanto a los afortunados, estarán en el Cielo, morando en l mientras duren los Cielos y la Tierra, salvo que tu Señor quiera <em>otra cosa</em>, una gracia que no se les quitará." WHERE ayatNumber = 109 AND chapterNumber = 11;
UPDATE verses SET es = "No dudes, pues, en cuanto a lo que adoran estos <em>pueblos.</em> Sólo adoran lo que adoraron antes sus padres, y Nosotros les pagaremos ciertamente lo que les corresponde, sin rebaja." WHERE ayatNumber = 110 AND chapterNumber = 11;
UPDATE verses SET es = "Ciertamente entregamos a Moisés el Libro; pero sobre él surgieron diferencias; y de no haber sido por una palabra pronunciada ya por tu Señor, el asunto habría sido, en verdad, decidido entre ellos mucho antes; y <em>ahora</em> estas <em>gentes</em> están en una duda inquietante respecto a él." WHERE ayatNumber = 111 AND chapterNumber = 11;
UPDATE verses SET es = "Pues en verdad las <em>obras de</em> todos <em>éstos</em> aún no <em>han sido recompensadas, pero</em> tu Señor las pagará plenamente, según sus obras. En verdad Él conoce perfectamente lo que hacen." WHERE ayatNumber = 112 AND chapterNumber = 11;
UPDATE verses SET es = "Permanece, pues, recto, como se te ha ordenado, <em>al igual que</em> quienes han vuelto <em>hacia Dios</em> contigo; y no superéis los límites, Oh <em>creyentes;</em> pues, ciertamente, Él ve lo que hacéis." WHERE ayatNumber = 113 AND chapterNumber = 11;
UPDATE verses SET es = "Y no os inclinéis hacia quienes actúan inicuamente, no sea que os alcance el Fuego. Y no tendréis amigos fuera de Al-lah, ni seréis ayudados." WHERE ayatNumber = 114 AND chapterNumber = 11;
UPDATE verses SET es = "Cumple la Oración en los dos límites del día y en la parte de la noche <em>más cercana al día.</em> En verdad, las virtudes alejan los males. Esto es una advertencia para los que quieren recordar." WHERE ayatNumber = 115 AND chapterNumber = 11;
UPDATE verses SET es = "Sé perseverante; en verdad Al-lah no permite que se pierda la recompensa de los justos." WHERE ayatNumber = 116 AND chapterNumber = 11;
UPDATE verses SET es = "¿Por qué, pues, no hubo entre las generaciones que os precedieron personas poseídas de conocimiento que prohibieran la corrupción en la tierra, excepto unos pocos de aquellos a quienes salvamos de entre ellos? Mas los malvados siguieron aquello que les procuraba facilidad y comodidad, y se hicieron culpables." WHERE ayatNumber = 117 AND chapterNumber = 11;
UPDATE verses SET es = "Mas tu Señor no destruiría injustamente las ciudades si sus moradores fuesen justos." WHERE ayatNumber = 118 AND chapterNumber = 11;
UPDATE verses SET es = "Pues si tu Señor hubiese <em>ejecutado</em> Su voluntad, ciertamente habría convertido a la humanidad en una sola nación; pero no dejarían de discrepar." WHERE ayatNumber = 119 AND chapterNumber = 11;
UPDATE verses SET es = "Salvo aquellos con quienes tu Señor ha tenido misericordia, y para esto los creó. Pero la palabra de tu Señor se cumplirá: “En verdad, llenaré el Infierno con los Yinn y hombres <em>desobedientes</em>, todos juntos”." WHERE ayatNumber = 120 AND chapterNumber = 11;
UPDATE verses SET es = "Y te relatamos todas las noticias de los Mensajeros con las que confortamos tu corazón. Pues aquí te ha llegado la verdad y una exhortación y <em>una</em> advertencia para los creyentes." WHERE ayatNumber = 121 AND chapterNumber = 11;
UPDATE verses SET es = "Diles a quienes no creen: “Actuad lo mejor que podáis, ya que nosotros actuamos<em> también”</em>." WHERE ayatNumber = 122 AND chapterNumber = 11;
UPDATE verses SET es = "“Y esperad, que nosotros esperamos <em>también”</em>." WHERE ayatNumber = 123 AND chapterNumber = 11;
UPDATE verses SET es = "Mas a Al-lah pertenecen las cosas ocultas de los cielos y la tierra y a Él se someterá todo el asunto. Adórale pues, y pon tu confianza <em>sólo</em> en Él<em>,</em> pues tu Señor no está desatento a lo que hacéis." WHERE ayatNumber = 124 AND chapterNumber = 11;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 12;
UPDATE verses SET es = "Alif Lam Ra. Éstos son versículos del Libro diáfano." WHERE ayatNumber = 2 AND chapterNumber = 12;
UPDATE verses SET es = "Lo hemos revelado: El Corán,<em>un Libro que se recita frecuentemente, </em>en árabe, <em>un</em> <em>lenguaje claro y elocuente</em>, para que entendáis." WHERE ayatNumber = 3 AND chapterNumber = 12;
UPDATE verses SET es = "Te relatamos la más hermosa narración revelándote este Corán, ya que antes de él, te contabas entre los que no conocían." WHERE ayatNumber = 4 AND chapterNumber = 12;
UPDATE verses SET es = "<em> Acuérdate de cuando</em> José dijo a su padre: “Oh, padre mío, vi en un <em>sueño</em> once estrellas, el sol y la luna, y los vi prosternados ante mí”." WHERE ayatNumber = 5 AND chapterNumber = 12;
UPDATE verses SET es = "Él dijo: “Oh, mi querido hijo, no relates tus sueños a tus hermanos, no sea que tramen una intriga contra ti; pues Satanás es enemigo declarado del hombre”;" WHERE ayatNumber = 6 AND chapterNumber = 12;
UPDATE verses SET es = "“Y así <em>será como tú has visto,</em> tu Señor te elegirá y te enseñará la interpretación de los asuntos narrados y te concederá Su gracia, a ti y a la familia de Jacob, como se la concedió a dos de tus antepasados: Abraham e Isaac. En verdad, vuestro Señor es Omnisciente, Sabio”." WHERE ayatNumber = 7 AND chapterNumber = 12;
UPDATE verses SET es = "En verdad, en José y sus hermanos hay Signos para los que los buscan." WHERE ayatNumber = 8 AND chapterNumber = 12;
UPDATE verses SET es = "Cuando ellos dijeron: “En verdad, José y su hermano son más queridos por nuestro padre que nosotros, a pesar de que somos un grupo fuerte. En verdad, nuestro padre está en un error manifiesto”." WHERE ayatNumber = 9 AND chapterNumber = 12;
UPDATE verses SET es = "“Matad a José o desterradlo a algún país <em>lejano</em> para que el favor de vuestro padre sea exclusivamente vuestro y podáis ser, después, gentes piadosas”." WHERE ayatNumber = 10 AND chapterNumber = 12;
UPDATE verses SET es = "Uno de ellos dijo: “No matéis a José, mas si debéis hacer algo, arrojadlo al fondo de un pozo profundo; alguno de los viajeros lo sacará”." WHERE ayatNumber = 11 AND chapterNumber = 12;
UPDATE verses SET es = "Dijeron: “Oh, padre nuestro, ¿por qué no confías en nosotros respecto a José, si ciertamente lo queremos bien?" WHERE ayatNumber = 12 AND chapterNumber = 12;
UPDATE verses SET es = "“Envíalo mañana con nosotros para que disfrute y juegue, pues nosotros ciertamente cuidaremos de él”." WHERE ayatNumber = 13 AND chapterNumber = 12;
UPDATE verses SET es = "Él dijo: “Me apena el que os lo llevéis, pues temo que el lobo lo devore mientras estáis descuidados”." WHERE ayatNumber = 14 AND chapterNumber = 12;
UPDATE verses SET es = "Respondieron: “En verdad, si el lobo lo devorara a pesar de que somos un grupo fuerte, seríamos entonces los <em>mayores</em> perdedores”." WHERE ayatNumber = 15 AND chapterNumber = 12;
UPDATE verses SET es = "Así, mientras se lo llevaban, acordando echarlo al fondo de un pozo profundo, Nosotros le revelamos<em> lo siguiente: </em>“Un <em>día</em> iertamente les hablarás de este asunto suyo y ellos no conocerán <em>tu identidad</em>”." WHERE ayatNumber = 16 AND chapterNumber = 12;
UPDATE verses SET es = "Y volvieron a su padre al anochecer, llorando." WHERE ayatNumber = 17 AND chapterNumber = 12;
UPDATE verses SET es = "Dijeron: “Oh, padre nuestro, competíamos en carreras unos con los otros y dejamos a José con nuestras cosas y el lobo lo devoró, aunque no nos creerás pese a que digamos la verdad”." WHERE ayatNumber = 18 AND chapterNumber = 12;
UPDATE verses SET es = "Y llegaron con sangre falsa en su túnica. Dijo: “No, aunque uestras mentes hayan hecho que este acto pecaminoso os sea atractivo.<em> Ahora</em> la bella paciencia es buena <em>para mí.</em> Pues <em>sólo</em> a Al-lah debe pedirse ayuda contra lo que afirmáis”." WHERE ayatNumber = 19 AND chapterNumber = 12;
UPDATE verses SET es = "Y ocurrió que llegó una caravana de viajeros y enviaron a su aguador. Él echó su cubo <em>al pozo</em>. “¡Albricias!” exclamó, “¡aquí hay un joven!”. Y lo ocultaron como mercancía. Aunque Al-lah sabía perfectamente lo que hicieron." WHERE ayatNumber = 20 AND chapterNumber = 12;
UPDATE verses SET es = "Y lo vendieron a un precio ínfimo, unos pocos dirhams, pues no estaban interesados en sacar gran provecho de él." WHERE ayatNumber = 21 AND chapterNumber = 12;
UPDATE verses SET es = "Y el hombre de Egipto que lo compró dijo a su mujer: “Haz su estancia agradable. Tal vez nos sea de beneficio o lo adoptemos como hijo”. Y así establecimos a José en el país y<em> lo hicimos</em> para poder enseñarle <em>también</em> la interpretación de los hechos narrados<em>.</em> Pues Al-lah tiene poder absoluto sobre Sus designios, pero la mayoría de los hombres no<em> lo </em>saben." WHERE ayatNumber = 22 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando alcanzó la <em>edad de</em> la madurez, le concedimos discernimiento y conocimiento. Así recompensamos a quienes hacen el bien." WHERE ayatNumber = 23 AND chapterNumber = 12;
UPDATE verses SET es = "La mujer en cuya casa estaba, intentó seducirlo contra su voluntad. Cerró las puertas y dijo: “Ven aquí”. Él respondió: “Busco refugio en Al-lah, Él es mi Señor. Él hizo que mi estancia <em>con vosotros</em> fuese digna. En verdad, los malvados nunca prosperan”." WHERE ayatNumber = 24 AND chapterNumber = 12;
UPDATE verses SET es = "Mas ella ciertamente lo deseaba y él también<em> la habría </em> deseado de no haber visto el Signo manifiesto de su Señor. Así ocurrió ara que apartásemos de él el mal y la obscenidad. En verdad, él era <em>uno</em> de Nuestros siervos elegidos." WHERE ayatNumber = 25 AND chapterNumber = 12;
UPDATE verses SET es = "Ambos corrieron entonces hacia la puerta, y ella le rasgó la túnica por detrás, encontrando los dos a su amo junto a la puerta. Ella dijo: “¿Qué pena merece quien ha intentado deshonrar a tu mujer, salvo la prisión o un terrible castigo?”." WHERE ayatNumber = 26 AND chapterNumber = 12;
UPDATE verses SET es = "Él dijo: “Fue ella quien intentó seducirme contra mi voluntad”. Y un testigo de la casa de ella testificó<em> diciendo</em>: “Si su túnica está rasgada por delante ella ha dicho la verdad y él es el mentiroso”." WHERE ayatNumber = 27 AND chapterNumber = 12;
UPDATE verses SET es = "“Pero si la túnica está rasgada por detrás, ella ha mentido y él es sincero”." WHERE ayatNumber = 28 AND chapterNumber = 12;
UPDATE verses SET es = "Así, cuando él vio la túnica rasgada por detrás, dijo: “En verdad, ésta es una treta vuestra, mujeres. Vuestras artimañas son ciertamente grandes”." WHERE ayatNumber = 29 AND chapterNumber = 12;
UPDATE verses SET es = "“Oh, José, apártate de esto, y tú, <em>Oh,</em><em> mujer,</em> pide perdón por tu pecado. En verdad, eres de las culpables”." WHERE ayatNumber = 30 AND chapterNumber = 12;
UPDATE verses SET es = "Y las mujeres de la ciudad dijeron: “la esposa del Aziz intenta seducir a su esclavo contra su voluntad. Él la ha vuelto loca de amor. En verdad, la vemos en error manifiesto”." WHERE ayatNumber = 31 AND chapterNumber = 12;
UPDATE verses SET es = "Mas cuando ella se enteró de su <em>astuto</em> propósito, envió a uscarlas y preparó un festín, dándoles a cada una un cuchillo, y dijo <em>entonces a José:</em> “Preséntate ante ellas”. Y cuando le vieron ensaron mucho en él y se cortaron las manos, diciendo: “¡Que Al-lah <em>lo</em> proteja <em>de todo mal</em>! No es un ser humano; no es sino un ángel noble”." WHERE ayatNumber = 32 AND chapterNumber = 12;
UPDATE verses SET es = "Ella dijo: “He aquí aquel por el que me censurabais. Intenté seducirlo contra su voluntad, pero él se preservó <em>del pecado.</em> Pero si no hace ahora lo que le ordeno, ciertamente será encarcelado y se convertirá en <em>uno</em> de los humillados”." WHERE ayatNumber = 33 AND chapterNumber = 12;
UPDATE verses SET es = "Él dijo: “Oh, mi Señor, prefiero la prisión a aquello a lo que me invitan; pues si no apartas de mí sus engaños, me inclinaré ante ellas y me contaré entre los ignorantes”." WHERE ayatNumber = 34 AND chapterNumber = 12;
UPDATE verses SET es = "Así pues, su Señor escuchó su plegaria y le salvó de sus designios. En verdad, Él es Quien todo lo oye, el Omnisciente." WHERE ayatNumber = 35 AND chapterNumber = 12;
UPDATE verses SET es = "Entonces se les ocurrió (a los hombres), después de haber visto las eñales <em>de su inocencia,</em> que, para <em>preservar su buen nombre,</em> debían encarcelarlo durante algún tiempo." WHERE ayatNumber = 36 AND chapterNumber = 12;
UPDATE verses SET es = "Y con él entraron en la prisión dos jóvenes. Uno de ellos dijo: “Me he visto <em>en un sueño</em> prensando vino”. El otro dijo: “Me he visto <em>en un sueño</em> llevando pan en mi cabeza del que comían los pájaros. Infórmanos de su interpretación, ya que vemos que te cuentas entre los justos”." WHERE ayatNumber = 37 AND chapterNumber = 12;
UPDATE verses SET es = "Él respondió: “No os llegará <em>a tiempo </em>la comida que se os entrega, pero os informaré de su interpretación antes de que os llegue. He aquí lo que me ha enseñado mi Señor. He renunciado a la religión de los hombres que no creen en Al-lah y que no creen en el Más Allá”;" WHERE ayatNumber = 38 AND chapterNumber = 12;
UPDATE verses SET es = "“He seguido la religión de mis padres, Abraham, Isaac y Jacob. En efecto, no podemos asociar a nadie con Al-lah. Ésta es la gracia de Al-lah con nosotros y con la humanidad, pero la mayoría de los hombres son desagradecidos”." WHERE ayatNumber = 39 AND chapterNumber = 12;
UPDATE verses SET es = "“Oh, mis dos compañeros de prisión, ¿qué es preferible, <em>tener</em> diversos señores o Al-lah, el Único, el Supremo?”." WHERE ayatNumber = 40 AND chapterNumber = 12;
UPDATE verses SET es = "“No adoráis a nada en lugar de Al-lah sino <em>meros</em> nombres que habéis inventado vosotros y vuestros padres; Al-lah no ha revelado autoridad alguna para ello. La decisión corresponde únicamente a Al-lah. Él os ha ordenado que no adoréis a nada salvo a Él. Ésa es la fe de la verdad eterna, pero la mayoría de los hombres no <em>la </em> conocen”;" WHERE ayatNumber = 41 AND chapterNumber = 12;
UPDATE verses SET es = "“Oh, mis dos compañeros de prisión, en cuanto a uno de vosotros, servirá vino para que beba su señor; y, en cuanto al otro, será crucificado y los pájaros le picotearán la cabeza. Ya está resuelta la cuestión sobre la que me preguntabais”." WHERE ayatNumber = 42 AND chapterNumber = 12;
UPDATE verses SET es = "Y de los dos, le dijo a aquel que en su opinión se salvaría: “Recuérdame ante tu señor”. Pero Satanás le hizo olvidar mencionarlo a su señor, por lo que 7permaneció en prisión durante algunos años." WHERE ayatNumber = 43 AND chapterNumber = 12;
UPDATE verses SET es = "El Rey dijo: “he visto <em>en un sueño</em> siete vacas gordas a as que siete vacas flacas devoraban; y siete espigas verdes y otras <em>siete</em> secas. Oh, vosotros, jefes, explicadme el significado de mi sueño, si es que podéis interpretarlo”." WHERE ayatNumber = 44 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos respondieron: “Son sueños confusos y desconocemos la interpretación de este tipo de sueños confusos”." WHERE ayatNumber = 45 AND chapterNumber = 12;
UPDATE verses SET es = "Y aquel de los dos que se había librado, y que <em>ahora</em> lo recordó después de algún tiempo, dijo: “Os daré a conocer su interpretación, enviadme pues”." WHERE ayatNumber = 46 AND chapterNumber = 12;
UPDATE verses SET es = "“¡José! Oh tú, hombre veraz, explícanos el significado de siete acas gordas que devoran siete flacas y de siete espigas verdes y otras <em>siete</em> secas; para que pueda volver a esa gente y lo conozcan”." WHERE ayatNumber = 47 AND chapterNumber = 12;
UPDATE verses SET es = "Respondió: “Sembraréis durante siete años, trabajando duro y continuamente, mas dejad lo que seguéis en la espiga, excepto lo poco que comáis”." WHERE ayatNumber = 48 AND chapterNumber = 12;
UPDATE verses SET es = "“Luego vendrán tras ellos siete años duros que consumirán todo lo que hayáis reservado de antemano para ellos, excepto el poco que podáis preservar”." WHERE ayatNumber = 49 AND chapterNumber = 12;
UPDATE verses SET es = "“Después seguirá un año en el que la gente disfrutará de lluvia abundante con la que tendrán <em>plenitud de frutos y semillas </em>que prensarán<em> para obtener zumos y aceite”</em>." WHERE ayatNumber = 50 AND chapterNumber = 12;
UPDATE verses SET es = "El Rey dijo: “Traédmelo”. Pero cuando el mensajero llegó a él, le dijo: “Vuelve a tu señor y pregúntale cuál fue la suerte de las mujeres que se cortaron las manos; pues mi Señor conoce perfectamente su astuto propósito”." WHERE ayatNumber = 51 AND chapterNumber = 12;
UPDATE verses SET es = "Él ( el Rey) dijo <em>a las mujeres:</em> “¿Qué os pasó cuando intentasteis seducir a José contra su voluntad?”. Ellas respondieron: “Glorificado sea Al-lah por crear un hombre así: no conocemos nada malo contra él”. La mujer del Aziz dijo: “ahora se ha hecho patente la verdad. Fui yo quien intenté seducirlo contra su voluntad, y en verdad él es el veraz”." WHERE ayatNumber = 52 AND chapterNumber = 12;
UPDATE verses SET es = "<em> José dijo: “He hecho la pregunta</em> para que él (el ‘Aziz) upiera que no traicioné su confianza <em>durante su</em> ausencia y <em>también para que se sepa</em> que Al-lah no permite que tengan éxito los engaños de los que no son honestos”." WHERE ayatNumber = 53 AND chapterNumber = 12;
UPDATE verses SET es = "“Y no me absuelvo de debilidad; pues el alma está ciertamente inclinada a instigar al mal, salvo aquello sobre lo que mi Señor tiene misericordia. En verdad, mi Señor es el Sumo Indulgente, Misericordioso”." WHERE ayatNumber = 54 AND chapterNumber = 12;
UPDATE verses SET es = "El Rey dijo: “Traédmelo que quiero dedicarlo exclusivamente a mi servicio”. Y cuando le hubo hablado, dijo: “hoy eres<em> un hombre</em> de posición respetada y cuentas con nuestra confianza”." WHERE ayatNumber = 55 AND chapterNumber = 12;
UPDATE verses SET es = "Él respondió: “Encárgame de los tesoros del país, ya que soy un buen custodio, <em>y </em>tengo conocimientos”." WHERE ayatNumber = 56 AND chapterNumber = 12;
UPDATE verses SET es = "Y así establecimos a José en el país. Vivió en él en donde quiso. Nosotros concedemos Nuestra Misericordia a quien nos agrada y no permitimos que se pierda la recompensa de los justos." WHERE ayatNumber = 57 AND chapterNumber = 12;
UPDATE verses SET es = "Pues, en verdad, la recompensa del Más Allá es mejor para quienes creen y temen a Dios." WHERE ayatNumber = 58 AND chapterNumber = 12;
UPDATE verses SET es = "Llegaron los hermanos de José, presentándose ante él; <em>y </em>él los reconoció, pero ellos lo consideraron un extraño." WHERE ayatNumber = 59 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando les hubo provisto de provisiones, dijo: “Traedme a vuestro hermano del lado de vuestro padre. ¿No veis que os entrego una medida colmada y que soy el mejor de los anfitriones?" WHERE ayatNumber = 60 AND chapterNumber = 12;
UPDATE verses SET es = "“Pero si no me lo traéis, no habrá medida alguna <em>de grano</em> para vosotros ni os acercaréis a mí”." WHERE ayatNumber = 61 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos respondieron: “Intentaremos convencer a su padre para que renuncie a él y <em>lo</em> haremos ciertamente”." WHERE ayatNumber = 62 AND chapterNumber = 12;
UPDATE verses SET es = "Dijo a sus criados: “Poned <em>también</em> su dinero en sus alforjas para que puedan reconocerlo cuando vuelvan a su familia; quizás regresen”." WHERE ayatNumber = 63 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando regresaron ante su padre dijeron: “Oh, padre nuestro, se nos ha negado una medida <em>adicional de grano, </em>envíanos pues a nuestro hermano para que obtengamos nuestra medida, pues ciertamente cuidaremos de él”." WHERE ayatNumber = 64 AND chapterNumber = 12;
UPDATE verses SET es = "Él respondió: “No os lo puedo confiar, como os confié antes a su hermano. Pero Al-lah es el mejor Protector, y Él es el Más Misericordioso de entre los que muestran misericordia”." WHERE ayatNumber = 65 AND chapterNumber = 12;
UPDATE verses SET es = "Mas cuando abrieron las mercancías, encontraron su dinero devuelto. Dijeron: “Oh, padre nuestro, ¿Qué más podemos desear? He aquí nuestro dinero devuelto. Llevaremos provisiones para nuestra familia, cuidaremos de nuestro hermano y recibiremos en añadidura la medida de na <em>carga</em> de camello. Ésta es una medida fácil <em> de obtener”</em>." WHERE ayatNumber = 66 AND chapterNumber = 12;
UPDATE verses SET es = "Respondió: “No lo enviaré con vosotros, a menos que reciba de vosotros la promesa solemne, en el nombre de Al-lah, de devolvérmelo con seguridad, salvo que os veáis impedidos”. Y cuando le hicieron su promesa solemne, él dijo: “Al-lah vigila lo que decimos”." WHERE ayatNumber = 67 AND chapterNumber = 12;
UPDATE verses SET es = "Y dijo: “Oh hijos míos, no entréis por una sola puerta, sino por puertas diferentes; pues no os puedo ayudar en nada contra Al-lah. La decisión corresponde sólo a Al-lah. En Él pongo ciertamente mi confianza y que en Él pongan su confianza todos los que confían”." WHERE ayatNumber = 68 AND chapterNumber = 12;
UPDATE verses SET es = "Cuando entraron de la manera en que su padre les había ordenado, no les sirvió de nada contra Al-lah, salvo <em>el hecho de</em> que Jacob tenía un deseo intuitivo que satisfizo de ese modo<em>;</em> pues él poseía, en verdad, un <em>gran</em> conocimiento; porque Nosotros se lo habíamos enseñado, aunque la mayoría de los hombres lo ignoran." WHERE ayatNumber = 69 AND chapterNumber = 12;
UPDATE verses SET es = "Mas cuando se presentaron ante José, éste hospedó con él a su ermano.<em> Y</em> dijo: “Soy tu hermano; no te apenes pues, <em>ahora,</em> por lo que hayan estado haciendo”." WHERE ayatNumber = 70 AND chapterNumber = 12;
UPDATE verses SET es = "Cuando les entregó sus provisiones, puso la copa en la alforja de u hermano. A continuación un pregonero gritó: “Oh vosotros, hombres <em>de la</em> caravana, sois culpables de robo”." WHERE ayatNumber = 71 AND chapterNumber = 12;
UPDATE verses SET es = "Respondieron, volviéndose ha­cia ellos: “¿Qué echáis en falta?”." WHERE ayatNumber = 72 AND chapterNumber = 12;
UPDATE verses SET es = "Contestaron: “Echamos en falta la copa de medida del Rey, y quien la traiga tendrá una carga de camello, pues yo soy garante de ello”." WHERE ayatNumber = 73 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos contestaron: “Por Al-lah, sabéis muy bien que no hemos venido para actuar con malicia en el país, ni somos ladrones”." WHERE ayatNumber = 74 AND chapterNumber = 12;
UPDATE verses SET es = "Dijeron, “¿Cuál será pues el castigo si se<em> comprueba que</em> habéis dicho una mentira?”." WHERE ayatNumber = 75 AND chapterNumber = 12;
UPDATE verses SET es = "Respondieron: “El castigo por ello <em>consistirá en que</em> aquel en cuya alforja se encuentre, será él mismo la sanción. De este modo castigamos a los injustos”." WHERE ayatNumber = 76 AND chapterNumber = 12;
UPDATE verses SET es = "Entonces empezó la <em>búsqueda</em> entre sus bolsas antes de la bolsa de su hermano; luego la sacó de la bolsa de su hermano. Así fue nuestro plan para José. No podría haber detenido a su hermano según la ley del Rey salvo que Al-lah <em>así</em> lo hubiera deseado. Pues Nosotros elevamos en grados de<em> dignidad</em> a quien Nos place; y sobre cualquier poseedor de conocimiento está el Único, el Sumo Sabedor." WHERE ayatNumber = 77 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos dijeron: “Si él ha robado, un hermano suyo cometió también un robo antes”. Pero José lo mantuvo en secreto en su corazón y no se lo reveló a ellos. Les dijo <em>simplemente:</em> “Parece que estáis en la peor situación; pues Al-lah sabe mejor lo que alegáis”." WHERE ayatNumber = 78 AND chapterNumber = 12;
UPDATE verses SET es = "Dijeron; “Excelencia, tiene un padre muy anciano, por tanto, toma a uno de nosotros en su lugar; porque vemos que te cuentas entre los que hacen el bien”." WHERE ayatNumber = 79 AND chapterNumber = 12;
UPDATE verses SET es = "Él respondió: “Al-lah prohibe que tomemos <em>a nadie</em> salvo a aquel en quien encontramos nuestra propiedad; porque de otra manera seríamos ciertamente injustos”." WHERE ayatNumber = 80 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando desesperaron de él, se retiraron, hablando entre ellos en privado. Su <em>hermano</em> mayor dijo: “¿Ignoráis acaso que vuestro padre hubo recibido de vosotros una promesa solemne en el nombre de Al-lah? y recordad la injusticia que cometisteis respecto a José; por lo tanto, no abandonaré la tierra mientras mi padre no me lo permita o Al-lah decida por mí. Pues Él es el Mejor de los Jueces;" WHERE ayatNumber = 81 AND chapterNumber = 12;
UPDATE verses SET es = "“Volved a vuestro padre y decidle: ‘Oh padre nuestro, tu hijo ha robado y sólo hemos afirmado lo que sabemos pues no podemos ser custodios de lo desconocido”." WHERE ayatNumber = 82 AND chapterNumber = 12;
UPDATE verses SET es = "“E indaga <em>entre las personas de</em> la ciudad en la que estábamos, y entre la caravana con la que vinimos; ciertamente decimos la verdad”." WHERE ayatNumber = 83 AND chapterNumber = 12;
UPDATE verses SET es = "Él respondió: “No, vuestras mentes os han engañado al presentároslo como algo bueno. Por lo tanto, <em>vuelvo</em> a la paciencia digna. Tal vez Al-lah me los traiga a todos; pues Él es el Omnisciente, el Sabio”." WHERE ayatNumber = 84 AND chapterNumber = 12;
UPDATE verses SET es = "Y se apartó de ellos diciendo: “¡Qué pena siento por José!” y sus jos se llenaron de lágrimas por la pena que sufría, pues reprimía <em> su dolor”.</em>" WHERE ayatNumber = 85 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos dijeron: “Por Al-lah, no dejarás de hablar de José hasta que enfermes o incluso te mueras”." WHERE ayatNumber = 86 AND chapterNumber = 12;
UPDATE verses SET es = "Respondió: “Sólo me lamento ante Al-lah por mi pena y mi dolor, y sé de Al-lah lo que vosotros ignoráis”." WHERE ayatNumber = 87 AND chapterNumber = 12;
UPDATE verses SET es = "“Oh, hijos míos, id y buscad a José y a su hermano y no desesperéis de la misericordia de Al-lah; porque nadie desespera de la misericordia de Al-lah salvo las gentes incrédulas”." WHERE ayatNumber = 88 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando llegaron ante él (José) dijeron: “Excelencia, la pobreza nos ha afligido a nosotros y a nuestras familias, y traemos una suma escasa de dinero; danos pues la medida completa y sé caritativo con nosotros. En verdad, Al-lah recompensa al caritativo”." WHERE ayatNumber = 89 AND chapterNumber = 12;
UPDATE verses SET es = "Él dijo: “¿Sabéis lo que hicisteis a José y a su hermano, cuando erais ignorantes?”." WHERE ayatNumber = 90 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos respondieron: “¿Eres tú realmente José?”. Él dijo: “Sí, soy José y éste es mi hermano. Al-lah en verdad ha sido Clemente con los dos. En verdad, quien es justo y perseverante, <em>sepa que </em>Al-lah no permite que se pierda la recompensa de los que hacen buenas obras”." WHERE ayatNumber = 91 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos respondieron: “¡Por Al-lah! en verdad Al-lah te ha preferido sobre nosotros y hemos sido, en efecto, pecadores”." WHERE ayatNumber = 92 AND chapterNumber = 12;
UPDATE verses SET es = "Él dijo: “No caerán reproches sobre vosotros en este día; ¡que Al-lah os perdone! Pues Él es el Sumo Misericordioso de entre los que muestran misericordia." WHERE ayatNumber = 93 AND chapterNumber = 12;
UPDATE verses SET es = "“Id con esta túnica mía y extendedla ante mi padre; él lo comprenderá todo. Y luego traedme a toda vuestra familia”." WHERE ayatNumber = 94 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando la caravana hubo partido, su padre dijo: “En verdad, siento el perfume de José, aunque penséis que deliro”." WHERE ayatNumber = 95 AND chapterNumber = 12;
UPDATE verses SET es = "Ellos respondieron: “Por Al-lah, que continúas ciertamente en tu antiguo error”." WHERE ayatNumber = 96 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando llegó el portador de la buena nueva, la extendió ante él y quedó iluminado. <em>Entonces</em> dijo: “¿Acaso no os dije que sé de Al-lah lo que vosotros ignoráis?”." WHERE ayatNumber = 97 AND chapterNumber = 12;
UPDATE verses SET es = "Dijeron: “Oh, padre nuestro, pide a Dios perdón por nosotros, por nuestros pecados; en verdad hemos sido pecadores”." WHERE ayatNumber = 98 AND chapterNumber = 12;
UPDATE verses SET es = "Respondió: “Ciertamente pediré vuestro perdón a mi Señor. En verdad, Él es el Sumo Indulgente, Misericordioso”." WHERE ayatNumber = 99 AND chapterNumber = 12;
UPDATE verses SET es = "Y cuando llegaron a José, alojó consigo a sus padres, y dijo: “entrad en Egipto y vivid en paz, si así lo quiere Al-lah”." WHERE ayatNumber = 100 AND chapterNumber = 12;
UPDATE verses SET es = "Y elevó a sus padres hasta el trono y <em>todos</em> ellos cayeron postrados <em>ante Dios</em> por él. Y él dijo: “Oh, padre mío, éste es el cumplimiento de mi antiguo sueño. Mi Señor lo ha convertido en realidad. Él me concedió Su gracia cuando me sacó de la prisión y os trajo del desierto después de que Satanás hubiera sembrado la discordia entre mis hermanos y yo. En verdad, mi Señor es Benigno con quien le place; pues Él es el Omnisciente, el Sabio”." WHERE ayatNumber = 101 AND chapterNumber = 12;
UPDATE verses SET es = "“Oh, Señor mío, me has bendecido con una parte de la soberanía y me has enseñado la interpretación de las cosas. Oh, Creador de los cielos y la tierra, Tú eres mi Protector en este mundo y en el Más llá. Haz que la muerte me sorprenda en estado de sumisión a <em>Tu voluntad</em> y úneme a los justos”." WHERE ayatNumber = 102 AND chapterNumber = 12;
UPDATE verses SET es = "Éstas son las noticias de lo desconocido,<em> que</em> te revelamos. Pues tú no estabas con ellos cuando tramaron su plan mientras maquinaban." WHERE ayatNumber = 103 AND chapterNumber = 12;
UPDATE verses SET es = "Y la mayoría de los hombres no creerán aunque tú <em>lo</em> desees ardientemente." WHERE ayatNumber = 104 AND chapterNumber = 12;
UPDATE verses SET es = "Y tú no les pides recompensa alguna por ello. No es más que una exhortación para toda la humanidad." WHERE ayatNumber = 105 AND chapterNumber = 12;
UPDATE verses SET es = "¡Cuántos signos hay en los Cielos y en la tierra, que desprecian, apartándose de ellos!" WHERE ayatNumber = 106 AND chapterNumber = 12;
UPDATE verses SET es = "Pues la mayoría de ellos no creen en Al-lah sin, además, atribuirle partícipes." WHERE ayatNumber = 107 AND chapterNumber = 12;
UPDATE verses SET es = "¿Se sienten, pues, seguros de que no vendrá sobre ellos un terrible castigo de Al-lah, o la llegada repentina de la Hora sin que se den cuenta?" WHERE ayatNumber = 108 AND chapterNumber = 12;
UPDATE verses SET es = "Diles: “Éste es mi camino: Convoco hacia Al-lah. Ocupo una posición de conocimiento manifiesto como también <em>lo hacen</em> quienes me siguen. Pues Al-lah es Santo; y no soy de quienes asocian otros dioses <em>a Dios”.</em>" WHERE ayatNumber = 109 AND chapterNumber = 12;
UPDATE verses SET es = "Pues antes de ti no enviamos <em>como mensajeros</em> sino a hombres, a quienes inspiramos de entre los habitantes de las ciudades. ¿Acaso no han recorrido la tierra y han visto cuál fue el fin de los que les precedieron? En verdad, la morada del Más Allá es mejor para los que temen a Dios. ¿Acaso no comprenderéis?" WHERE ayatNumber = 110 AND chapterNumber = 12;
UPDATE verses SET es = "Hasta el instante en el que los Mensajeros se desesperaban y percibían haber sido <em>tomados</em> por mentirosos; entonces, de repente les llegó Nuestra ayuda, y salvamos a quien quisimos. Y Nuestro castigo no puede ser apartado de las gentes pecadoras." WHERE ayatNumber = 111 AND chapterNumber = 12;
UPDATE verses SET es = "Ciertamente, en su historia hay una lección para los hombres de conocimiento. No es algo que haya sido inventado, sino el cumplimiento de lo que existía antes, y una exposición detallada de todas las cosas, así como una guía y misericordia para los hombres que creen." WHERE ayatNumber = 112 AND chapterNumber = 12;
UPDATE verses SET es = "En el Nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 13;
UPDATE verses SET es = "Alif Lam Mim Ra. Éstos son los versículos del Libro. Y lo que te ha sido revelado por tu Señor es la verdad, pero la mayoría de los hombres no lo creen." WHERE ayatNumber = 2 AND chapterNumber = 13;
UPDATE verses SET es = "Al-lah es Quien levantó los cielos sin pilares visibles para vosotros. Después se asentó Él mismo en el Trono y sometió el sol y la luna: cada uno de los cuales sigue su curso hasta el plazo fijado. Él rige las leyes de la <em>naturaleza</em>. Explica claramente los Signos, para que creáis firmemente en el encuentro con vuestro Señor." WHERE ayatNumber = 3 AND chapterNumber = 13;
UPDATE verses SET es = "Él es Quien dilató la tierra y estableció en ella montañas y ríos. Los frutos los hizo a pares, masculinos y femeninos. Hace que la noche cubra el día. Aquí hay, en verdad, signos para las gentes que reflexionan." WHERE ayatNumber = 4 AND chapterNumber = 13;
UPDATE verses SET es = "Y en la tierra hay parcelas <em>diversas</em> limítrofes unas con otras, jardines de vid, campos de grano y palmeras, que crecen juntos a partir de una raíz y <em>otros</em> que no crecen de esta manera; son regados con la misma agua, y sin embargo hacemos que algunos de ellos superen a otros en sus frutos. Aquí hay Signos para las gentes que entienden." WHERE ayatNumber = 5 AND chapterNumber = 13;
UPDATE verses SET es = "Y si te asombras, lo más asombroso es en verdad lo que dicen: ¡Cómo! Cuando nos convirtamos en polvo ¿estaremos entonces en <em>un</em> <em>estado de</em> nueva creación?”. Son éstos quienes no creen en su Señor y son éstos quienes tendrán argollas alrededor de sus cuellos y <em>serán </em>los moradores del Fuego, en donde habitarán." WHERE ayatNumber = 6 AND chapterNumber = 13;
UPDATE verses SET es = "Y están impacientes exigiéndote que les traigas el mal antes que el bien<em>,</em> cuando <em>ya</em> han acontecido castigos ejemplares anteriormente a ellos. Y en verdad, tu Señor está lleno de perdón hacia la humanidad a pesar de sus iniquidades; y ciertamente tu Señor <em>también</em> es estricto en el castigo merecido." WHERE ayatNumber = 7 AND chapterNumber = 13;
UPDATE verses SET es = "Y los incrédulos dicen: “¿Por qué su Señor no le ha enviado un Signo?”. En verdad eres un Amonestador. Y hay una Guía para cada pueblo." WHERE ayatNumber = 8 AND chapterNumber = 13;
UPDATE verses SET es = "Al-lah sabe lo que lleva cada hembra, lo que disminuye los vientres lo que los hace crecer. Pues con Él todo tiene su medida <em>apropiada.</em>" WHERE ayatNumber = 9 AND chapterNumber = 13;
UPDATE verses SET es = "<em> Él es el</em> Conocedor de lo invisible y de lo visible, el Incomparablemente Grande, el Sublime." WHERE ayatNumber = 10 AND chapterNumber = 13;
UPDATE verses SET es = "<em> Ante Su vista</em> son iguales aquel de vosotros que oculta<em> su</em> palabra y el que la expresa abiertamente; como<em>también</em> quien se oculta por la noche y sale <em>a las claras</em> durante el día." WHERE ayatNumber = 11 AND chapterNumber = 13;
UPDATE verses SET es = "Para él (el Mensajero) hay una sucesión <em>de ángeles</em> delante y detrás de él, que lo guardan por mandato de Al-lah. En verdad, Al-lah no cambia el estado de un pueblo mientras ellos no cambien lo que hay en sus corazones. Cuando Al-lah desea castigar a un pueblo, nadie puede impedirlo, ni tienen socorredor alguno fuera de Él." WHERE ayatNumber = 12 AND chapterNumber = 13;
UPDATE verses SET es = "Él es Quien os muestra el relámpago <em>para inspirar</em> temor y esperanza, Quien hace surgir las nubes pesadas." WHERE ayatNumber = 13 AND chapterNumber = 13;
UPDATE verses SET es = "El trueno Lo glorifica con Su alabanza y <em>lo mismo hacen</em> los ángeles por temor a Él; y Él envía los rayos, golpeando con ellos a quien desea; y sin embargo disputan sobre Al-lah, a pesar de que es severo en el castigo." WHERE ayatNumber = 14 AND chapterNumber = 13;
UPDATE verses SET es = "La verdadera oración es la dirigida sólo a Él. Aquellos a quienes nvocan en lugar de Él no les responden en absoluto, salvo como <em>es respondido</em> aquel que extiende sus dos manos hacia el agua para que llegue a su boca, pero nunca llega. Pues la oración de los incrédulos no es más que una pérdida de tiempo." WHERE ayatNumber = 15 AND chapterNumber = 13;
UPDATE verses SET es = "Mas a Al-lah se someten quienes están en los cielos y en la tierra, de grado o a la fuerza, e <em>igualmente lo hacen</em> sus sombras, al amanecer y al atardecer." WHERE ayatNumber = 16 AND chapterNumber = 13;
UPDATE verses SET es = "Diles: “¿Quién es el Señor de los cielos y la tierra?”. Responde: “Al-lah”. Diles: ¿Habéis tomado, pues, en lugar de Él, socorredores que o tienen poder para hacer ningún bien ni ningún mal ni <em>siquiera</em> a ellos mismos?”. Diles: “¿Pueden equipararse el ciego y el vidente? o ¿pueden ser las tinieblas iguales a la luz? o ¿asignan socios a Al-lah que hayan creado algo similar a Su Creación hasta el punto de que aparezcan similares las <em>dos</em> creaciones ante ellos?”. Diles: “<em>Sólo</em> Al-lah es el Creador de todas las cosas, Él es el Único, el Supremo”." WHERE ayatNumber = 17 AND chapterNumber = 13;
UPDATE verses SET es = "Él hace descender el agua del cielo, para que los ríos recorran los valles según la medida, y la corriente lleve en su superficie una spuma flotante. Y de lo que calientan al fuego, con la intención <em>de fabricar</em> adornos o utensilios, emana una espuma similar a ella. Así ilustra Al-lah la verdad y la falsedad. Ahora bien, en cuanto a la espuma, se desvanece como basura, mas aquello que beneficia a los hombres, permanece en la tierra. Así Al-lah explica las parábolas." WHERE ayatNumber = 18 AND chapterNumber = 13;
UPDATE verses SET es = "Para quienes responden a su Señor existe el bien <em>eterno; </em>y <em>en cuanto</em> <em>a </em>quienes no Le responden, si poseyeran uanto hay en la tierra y otro tanto igual añadido, lo darían <em>fácilmente</em> como rescate de sí mismos. Son éstos quienes tendrán una pésima cuenta y cuya morada es el Infierno. ¡Qué mal lugar de descanso!" WHERE ayatNumber = 19 AND chapterNumber = 13;
UPDATE verses SET es = "¿El que sabe que lo que te ha sido revelado por tu Señor es la verdad, es acaso comparable al que está ciego? <em>Pero</em> sólo reflexionarán los dotados de entendimiento;" WHERE ayatNumber = 20 AND chapterNumber = 13;
UPDATE verses SET es = "Quienes cumplen el pacto de Al-lah y no incumplen la promesa;" WHERE ayatNumber = 21 AND chapterNumber = 13;
UPDATE verses SET es = "Y quienes unen lo que Al‑lah ha ordenado que sea unido, temen a su Señor, y sienten temor por la cuenta aciaga;" WHERE ayatNumber = 22 AND chapterNumber = 13;
UPDATE verses SET es = "Y quienes perseveran buscan­do el favor de su Señor, observan la Oración, gastan de lo que les hemos provisto, secreta y abiertamente, y echazan el mal con el bien. Son ellos quienes tendrán la <em>mejor</em> recompensa de la Morada <em>final­</em>:" WHERE ayatNumber = 23 AND chapterNumber = 13;
UPDATE verses SET es = "Jardines de Eternidad. Entrarán en ellos, como también los justos de entre sus padres, sus esposas y sus hijos. Y los ángeles penetrarán en ellos por todas sus puertas, <em>diciendo:</em>" WHERE ayatNumber = 24 AND chapterNumber = 13;
UPDATE verses SET es = "\"La paz sea con vosotros, porque fuisteis perseverantes, ¡mirad que excelente es la recompensa de la Morada <em>final</em>!\" " WHERE ayatNumber = 25 AND chapterNumber = 13;
UPDATE verses SET es = "Mas para quienes rompen el pacto de Al‑lah después de establecerlo y cortan con ello lo que Al‑lah ha ordenado que sea unido, actuando con corrupción en la tierra, sobre ellos caerá la maldición y tendrán una morada deplorable." WHERE ayatNumber = 26 AND chapterNumber = 13;
UPDATE verses SET es = "Al‑lah prodiga <em>Su</em> provisión a quien le place y la restringe<strong> </strong>a <em>quién quiere. </em>Ellos se regocijan n la vida presente, aunque esta vida no es más que un goce <em>temporal </em>comparada con la que ha de venir." WHERE ayatNumber = 27 AND chapterNumber = 13;
UPDATE verses SET es = "Y los incrédulos dicen: \"¿Por qué no se le ha revelado un Signo de su Señor?\". Diles: \"Al‑lah deja que se extravíen quienes El quiere y guía a Sí mismo a quienes acuden <em>a El;</em>" WHERE ayatNumber = 28 AND chapterNumber = 13;
UPDATE verses SET es = "Quienes creen y cuyos cora­zones hallan sosiego en el recuerdo de Al‑lah. ¡Ay! En el recuerdo de Al‑lah es en lo que los corazones pueden hallar sosiego;" WHERE ayatNumber = 29 AND chapterNumber = 13;
UPDATE verses SET es = "\"Para quienes creen y hacen buenas obras: suya será la felicidad y un excelente lugar de retorno.\"" WHERE ayatNumber = 30 AND chapterNumber = 13;
UPDATE verses SET es = "Así te enviamos a un pueblo, antes del cual otros pueblos antecedieron, para que le relates lo que te hemos revelado; <u>y </u> sin embargo, no creen en el <em>Dios</em> <em>Clemente. </em>Diles: \"El es mi Señor; no hay otro Dios sino El. En El pongo mi confianza y hacia El será mi retorno.\"" WHERE ayatNumber = 31 AND chapterNumber = 13;
UPDATE verses SET es = "Pues aunque hubiese un Corán por el que se movieran las montañas, por el que la tierra se partiera en pedazos, o pudiera hablarse a los muertos, <em>no creerían en él</em>. No, el asunto está totalmente en manos de Al‑lah. <em>¿Aún </em>no se han dado cuenta los creyentes que si Al‑lah hubiese <em>ejecutado </em>Su<em> vo</em>luntad, habría guiado ciertamente a toda la humanidad? En cuanto a los incrédulos, las calamidades no dejarán de azotarlos por lo que han forjado, ni dejará de caer cerca de sus hogares, hasta que se cumpla la promesa de Al‑lah. En verdad, Al‑lah no falla en <em>Su</em> promesa." WHERE ayatNumber = 32 AND chapterNumber = 13;
UPDATE verses SET es = "En verdad, los Mensajeros anteriores a ti fueron objeto de burlas; mas Yo concedí un respiro a los incrédulos. Después caí sobre ellos y ¡cómo fue entonces Mi castigo!" WHERE ayatNumber = 33 AND chapterNumber = 13;
UPDATE verses SET es = "¿Acaso Quien está sobre cada alma <em>para anotar lo </em>que se merece, <em>los va a dejar sin castigo? Y </em>sin embargo, adscriben partícipes a Al‑lah. Diles: “¡Nombradlos!”<strong> </strong>¿Podéis informarle de algo que desconozca de la tierra? ¿Se trata simplemente de una forma de hablar sin sentido? No, pero se ha hecho que la intriga de los incrédulos <em>apareciera </em>favorable ante sus ojos y han sido apartados del camino <em>recto. </em>Pues aquél a quien Al‑lah deja que se extravíe no tendrá guía." WHERE ayatNumber = 34 AND chapterNumber = 13;
UPDATE verses SET es = "Para ellos <em>hay reservado </em>un castigo en la vida presente; mas en verdad el castigo del Más Allá es más duro, y no tendrán defensor contra Al‑lah." WHERE ayatNumber = 35 AND chapterNumber = 13;
UPDATE verses SET es = "La definición del Cielo prometido a los que temen a Dios es que por l corren ríos: sus frutos son permanentes, como <em>también </em>su sombra. Esta es la recompensa de los justos; mas la recompensa de los incrédulos es el Fuego." WHERE ayatNumber = 36 AND chapterNumber = 13;
UPDATE verses SET es = "Mas aquellos a quienes concedimos el Libro se regocijan de lo que e ha sido revelado. Y entre los diferentes grupos hay <em>algunos </em>que niegan una parte de él. Diles: \"Sólo se me ha ordenado adorar a Al‑lah y no elevar partícipes iguales a El. A El invoco y hacia El será mi retorno.\"" WHERE ayatNumber = 37 AND chapterNumber = 13;
UPDATE verses SET es = "De este modo lo revelamos como juicio evidente. Si sigues sus malvados deseos después de que te ha llegado el conocimiento, no tendrás amigo ni defensor contra Al‑lah." WHERE ayatNumber = 38 AND chapterNumber = 13;
UPDATE verses SET es = "En verdad enviamos a Mensajeros antes que a ti, y les dimos esposas e hijos. Y no es posible que un Mensajero traiga una Señal, salvo por orden de Al‑lah. Para cada plazo hay un decreto <em>divino.</em>" WHERE ayatNumber = 39 AND chapterNumber = 13;
UPDATE verses SET es = "Al‑lah borra lo que quiere y confirma <em>lo que desea, </em>pues en El está la fuente de <em>todos </em>los decretos." WHERE ayatNumber = 40 AND chapterNumber = 13;
UPDATE verses SET es = "Tanto si te hacemos ver el <em>cumplimiento de </em>algunas de las osas con las que les amenazamos como si te hacemos morir, <em>poca importancia tiene, </em>ya que a ti corresponde únicamente la entrega del Mensaje y a Nosotros la cuenta." WHERE ayatNumber = 41 AND chapterNumber = 13;
UPDATE verses SET es = "¿Acaso no estamos reduciendo <em>su</em> terreno por sus fronteras?. Mas Al‑lah juzga: nadie puede revocar Su sentencia. Pues El es Rápido en la cuenta." WHERE ayatNumber = 42 AND chapterNumber = 13;
UPDATE verses SET es = "Quienes existieron antes de ellos idearon <em>también </em>planes, pero toda preparación <em>efectiva </em>de planes pertenece a Al‑lah. El sabe lo que se merece cada alma, y los incrédulos sabrán pronto de quién será la recompensa final de <em>esta </em>morada." WHERE ayatNumber = 43 AND chapterNumber = 13;
UPDATE verses SET es = "Mas los incrédulos dicen: \"Tú no eres Mensajero\". Diles: \"Me basta con Al‑lah como Testigo entre vosotros y yo, <em>así como </em>quien posee el conocimiento del Libro.\"" WHERE ayatNumber = 44 AND chapterNumber = 13;
UPDATE verses SET es = "En el nombre de Al‑lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 14;
UPDATE verses SET es = "Alif Lam Ra. Este es un Libro que te hemos revelado para que saques a la humanidad de todo <em>tipo </em><em>de </em>tinieblas a la luz por andato de Su Señor, al camino del Poderoso, el Que merece Alabanza <strong>.</strong>" WHERE ayatNumber = 2 AND chapterNumber = 14;
UPDATE verses SET es = "A Al‑lah pertenece cuanto hay en los cielos y cuanto hay en la tierra. Y, ¡ay de los incrédulos por el terrible castigo!:" WHERE ayatNumber = 3 AND chapterNumber = 14;
UPDATE verses SET es = "Quienes prefieren la vida presente al Más Allá, desvían <em>a los</em> <em>hombres </em>del camino de Al‑lah e intentan hacerlo tortuoso. Son éstos quienes han ido muy lejos en el error." WHERE ayatNumber = 4 AND chapterNumber = 14;
UPDATE verses SET es = "Mas no hemos enviado a ningún Mensajero salvo con el idioma de su pueblo para que les aclarara <em>todas las cosas</em>, Al‑lah deja, pues, que se extravíe quien El quiere, y guía a quien desea. Pues El es el Poderoso, el Sabio." WHERE ayatNumber = 5 AND chapterNumber = 14;
UPDATE verses SET es = "Enviamos a Moisés con Nuestros Signos, <em>diciendo: </em>\"Saca a tu pueblo de toda <em>clase </em>de tinieblas a la luz, y recuérdales los días de Al‑lah. En verdad, en ello hay Signos para toda persona paciente y agradecida." WHERE ayatNumber = 6 AND chapterNumber = 14;
UPDATE verses SET es = "<em>Y acordaos de </em>cuando Moisés dijo a su pueblo: \"Recordad las gracias de Al‑lah para vosotros cuando os libró de las gentes del Faraón que os afligían con un tormento doloroso, matando a vuestros hijos y dejando con vida a vuestras mujeres; en eso había una gran prueba para vosotros de vuestro Señor.\"" WHERE ayatNumber = 7 AND chapterNumber = 14;
UPDATE verses SET es = "<em>Y acordaos también de </em>cuando vuestro Señor declaró: \"si sois agra­decidos, en verdad os concederé más <em>favores; </em>pero si sois desagrade­cidos, <em>sabed entonces </em>que Mi castigo es ciertamente severo.\"" WHERE ayatNumber = 8 AND chapterNumber = 14;
UPDATE verses SET es = "Y Moisés dijo: \"Si no creéis, vosotros y quienes están en la tierra, todos juntos, <em>no podréis perjudicar a Dios</em>; en verdad Al‑lah es Auto‑Suficiente, Merecedor de Alabanza." WHERE ayatNumber = 9 AND chapterNumber = 14;
UPDATE verses SET es = "¿No os han llegado las noticias de quienes os precedieron: las gentes de Noé, y las <em>tribus de </em>Ad y Zamud y de los que los sucedieron? Nadie los conoce <em>ahora </em>salvo Al‑lah. Sus Mensajeros llegaron a ellos con Signos evidentes, pero se pusieron las manos en la boca, diciendo: \"No creemos en lo que te ha sido revelado y en verdad estamos en una gran duda respecto a aquello a lo que nos convocáis.\"" WHERE ayatNumber = 10 AND chapterNumber = 14;
UPDATE verses SET es = "Sus Mensajeros dijeron: \"¿Tenéis acaso alguna duda sobre Al‑lah, Creador de los cielos y de la tierra?. El os convoca para perdonaros vuestros pecados y concederos respiro hasta un plazo fijado.\" Respondieron: \"No sois más que hombres como nosotros; queréis que nos apartemos de lo que nuestros padres solían adorar. Presentadnos, pues, una prueba evidente.\"" WHERE ayatNumber = 11 AND chapterNumber = 14;
UPDATE verses SET es = "Los Mensajeros les dijeron: \"En verdad sólo somos hombres como vosotros, pero AI‑lah concede <em>Sus</em> gracias a quien quiere de entre Sus siervos. Y no nos corresponde presentaros una prueba excepto por orden de Al‑lah. Y <em>sólo</em> en Al‑lah deben poner los creyentes su confianza." WHERE ayatNumber = 12 AND chapterNumber = 14;
UPDATE verses SET es = "\"Mas ¿por qué no hemos de poner nuestra confianza en Al‑lah, si El nos ha mostrado el camino? En verdad soportaremos cualquier persecución de vuestras manos. ¡Que confíen en Al‑lah, pues, quienes confían!\"" WHERE ayatNumber = 13 AND chapterNumber = 14;
UPDATE verses SET es = "Mas los incrédulos dijeron a sus Mensajeros: \"Ciertamente os expulsaremos de nuestro país a menos que volváis a nuestra religión\". Entonces Su Señor les envió la revelación: \"En verdad destruiremos a los incrédulos.\"" WHERE ayatNumber = 14 AND chapterNumber = 14;
UPDATE verses SET es = "\"Y ciertamente os haremos morar en el país después de ellos. Esto es para aquel que se sienta sobrecogido por Mi rango y preste atención a Mis advertencias.\"" WHERE ayatNumber = 15 AND chapterNumber = 14;
UPDATE verses SET es = "Imploraron la victoria y, <em>como</em><em> resultado de ello, los </em>arrogantes enemigos <em>de la verdad </em>quedaron reducidos a la nada." WHERE ayatNumber = 16 AND chapterNumber = 14;
UPDATE verses SET es = "Ante él está el Infierno; y se le hará beber agua hirviendo." WHERE ayatNumber = 17 AND chapterNumber = 14;
UPDATE verses SET es = "La beberá trago a trago, a regañadientes, incapaz de deglutir. Y la muerte le asaltará de todas las esquinas, <em>pero </em>sin llegar a morir. Y además sufrirá un severo castigo." WHERE ayatNumber = 18 AND chapterNumber = 14;
UPDATE verses SET es = "El caso de quienes no creen en su Señor es que sus obras son semejantes a cenizas sobre las que el viento sopla violentamente en un día de tormenta. No tendrán poder sobre lo que han ganado. Esa es, en verdad, una completa destrucción." WHERE ayatNumber = 19 AND chapterNumber = 14;
UPDATE verses SET es = "¿No ves que Al‑lah creó los cielos y la tierra con la verdad?. Si le agrada, puede haceros desaparecer y producir una nueva creación." WHERE ayatNumber = 20 AND chapterNumber = 14;
UPDATE verses SET es = "Pues eso no es <em>en absoluto </em>difícil para Al‑lah." WHERE ayatNumber = 21 AND chapterNumber = 14;
UPDATE verses SET es = "Comparecerán todos ante Al-lah; entonces los débiles dirán a quienes se comportaron orgullosamente: \"en verdad éramos vuestros seguidores; ¿no podéis pues servirnos de algo contra el castigo de Al‑lah?” Responderán: \"Si Al‑lah nos hubiese guiado, ciertamente os habríamos guiado a vosotros. <em>Pero ahora lo </em>mismo da que mostremos impaciencia o permanezcamos pacientes; no tenemos escapatoria.\"" WHERE ayatNumber = 22 AND chapterNumber = 14;
UPDATE verses SET es = "Y cuando se decida el asunto, Satanás dirá: \"Al‑lah os hizo una promesa de verdad, pero yo os pro­metí y falté a mi promesa. Pues no tenía poder sobre vosotros salvo que os convoqué y me obedecisteis. No me acu­séis pues, sino acusaros vosotros mismos. Ni puedo socorreros ni osotros podéis socorrerme. He rechazado ya que me hayáis asociado <em>a Dios</em>. Y para los inicuos habrá, en verdad, un doloroso castigo.\"" WHERE ayatNumber = 23 AND chapterNumber = 14;
UPDATE verses SET es = "Quienes creen y hacen buenas obras serán admitidos en Jar­dines por los que corren ríos, en los que habitarán por mandato de su Señor. Allí su saludo será 'Paz\"." WHERE ayatNumber = 24 AND chapterNumber = 14;
UPDATE verses SET es = "¿No ves con qué compara Al‑lah una buena palabra? <em>Es</em> como un buen árbol, cuya raíz es firme y cuyas ramas <em>llegan </em>al cielo?" WHERE ayatNumber = 25 AND chapterNumber = 14;
UPDATE verses SET es = "Produce sus frutos en todas las estaciones por mandato de su Señor. Pues Al‑lah presenta parábolas a los hombres para que reflexionen." WHERE ayatNumber = 26 AND chapterNumber = 14;
UPDATE verses SET es = "Mas una palabra mala se asemeja a un árbol malo, cuyas raíces se han salido de la tierra y no tiene estabilidad." WHERE ayatNumber = 27 AND chapterNumber = 14;
UPDATE verses SET es = "Al‑lah fortalece a los creyentes con la palabra firmemente establecida, <em>tanto </em>en la vida presente <em>como</em> en el Más Allá; y Al‑lah permite que los injustos se extravíen. Al‑lah hace lo que quiere." WHERE ayatNumber = 28 AND chapterNumber = 14;
UPDATE verses SET es = "¿No ves a quienes cambiaron la gracia de Al‑lah por ingratitud y arrastraron a sus gentes a la morada de perdición­" WHERE ayatNumber = 29 AND chapterNumber = 14;
UPDATE verses SET es = "<em> Que es </em>el Infierno. En él arderán; ¡qué mal lugar de descanso es ése!" WHERE ayatNumber = 30 AND chapterNumber = 14;
UPDATE verses SET es = "Inventaron competidores con Al‑lah para apartar a <em>las</em> <em>gentes </em>de Su camino. Diles: \"gozad ahora algún tiempo, pues después, en verdad vuestro destino es <em>ir</em> al Fuego.\"" WHERE ayatNumber = 31 AND chapterNumber = 14;
UPDATE verses SET es = "Di a Mis siervos creyentes que deben cumplir la Oración y gastar de lo que les hemos proporcionado, secreta y manifiestamente, antes de que llegue un día en el que no habrá tratos ni amistad." WHERE ayatNumber = 32 AND chapterNumber = 14;
UPDATE verses SET es = "Al-lah es Quien creó los cielos y la tierra e hizo que el agua cayera de las nubes, produciendo así frutos para vuestro sustento; Él os ha sometido por los buques para que naveguen por el mar siguiendo Su mandato, e <em>igualmente</em> os sometió los ríos." WHERE ayatNumber = 33 AND chapterNumber = 14;
UPDATE verses SET es = "Él ha puesto a vuestro servicio el sol y la luna que se mueven constantemente. También ha sometido la noche y el día para que os sirvan." WHERE ayatNumber = 34 AND chapterNumber = 14;
UPDATE verses SET es = "Pues Él os dio todo lo que le habéis pedido; y si <em>intentáis</em> contar los favores de Al-lah, no podréis enumerarlos. En verdad, el hombre es muy injusto, muy desagradecido." WHERE ayatNumber = 35 AND chapterNumber = 14;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando Abraham dijo: “Señor mío, haz de esta ciudad <em>una ciudad</em> de paz, y presérvanos a mí y a mis hijos de la adoración de los ídolos," WHERE ayatNumber = 36 AND chapterNumber = 14;
UPDATE verses SET es = "“Señor mío, en verdad han extraviado a muchos de la humanidad. Por ello, quien me siga será ciertamente de los míos; y quien me desobedezca <em>sepa que</em> Tú eres, en verdad el Sumo Indulgente, Misericordioso”." WHERE ayatNumber = 37 AND chapterNumber = 14;
UPDATE verses SET es = "“Señor nuestro, he establecido a algunos de mis hijos en un valle incultivable cerca de Tu Sagrada Casa, Señor Nuestro, para que cumplan la Oración. Haz pues que los corazones de los hombres se inclinen hacia ellos y proporciónales frutos, para que sean agradecidos”." WHERE ayatNumber = 38 AND chapterNumber = 14;
UPDATE verses SET es = "“Señor Nuestro, en verdad Tú sabes lo que ocultamos y lo que manifestamos. Pues nada hay oculto en absoluto para Al-lah, ni en la tierra ni en el cielo." WHERE ayatNumber = 39 AND chapterNumber = 14;
UPDATE verses SET es = "“Toda alabanza corresponde a Al-lah, Quien me ha dado, a pesar <em> de mi</em> edad avanzada, a Ismael y a Isaac. Ciertamente mi Señor es el que escucha la Oración”," WHERE ayatNumber = 40 AND chapterNumber = 14;
UPDATE verses SET es = "“Señor mío, hazme practicar la Oración y <em>también</em> a mis hijos. ¡Señor nuestro! acepta mi oración”." WHERE ayatNumber = 41 AND chapterNumber = 14;
UPDATE verses SET es = "“Señor nuestro, perdónanos a mí y a mis padres y a los creyentes el día en que se hagan las cuentas”." WHERE ayatNumber = 42 AND chapterNumber = 14;
UPDATE verses SET es = "Y no pienses que Al-lah desconoce lo que hacen los injustos. Él sólo les da un respiro hasta el día en que los ojos observen fijamente," WHERE ayatNumber = 43 AND chapterNumber = 14;
UPDATE verses SET es = "Corriendo asustados, levantando las cabezas, sin que la mirada les vuelva y con sus mentes espantosamente vacías." WHERE ayatNumber = 44 AND chapterNumber = 14;
UPDATE verses SET es = "Advierte a los hombres a propósito del día en que les llegará el castigo<em> prometido,</em> cuando los inicuos dirán: “Señor nuestro, concédenos un respiro durante un breve plazo. Responderemos a Tu lamada y seguiremos a los Mensajeros”. “¿No jurasteis antes de esto <em>que </em>no caeríais?”." WHERE ayatNumber = 45 AND chapterNumber = 14;
UPDATE verses SET es = "“Mas moráis en las viviendas de quienes se perjudicaron a sí mismos, y habéis comprendido claramente cómo los tratamos; pues os hemos presentado parábolas <em>claras”</em>." WHERE ayatNumber = 46 AND chapterNumber = 14;
UPDATE verses SET es = "Ellos tramaron todo el engaño que pudieron pero <em>el resultado</em> de su engaño depende de Al-lah, aun cuando sus estratagemas fueran capaces de mover las montañas." WHERE ayatNumber = 47 AND chapterNumber = 14;
UPDATE verses SET es = "No penséis entonces que Al-lah dejará de cumplir Su promesa a Sus Mensajeros. En verdad Al-lah es Todopoderoso, Señor de la retribución," WHERE ayatNumber = 48 AND chapterNumber = 14;
UPDATE verses SET es = "El día en que esta tierra cambie por otra tierra, <em>como también</em> los cielos; y <em>todos</em> comparezcan ante Al-lah, el Único, el Supremo." WHERE ayatNumber = 49 AND chapterNumber = 14;
UPDATE verses SET es = "En ese día verás a los culpables atados con cadenas." WHERE ayatNumber = 50 AND chapterNumber = 14;
UPDATE verses SET es = "Sus ropas serán de alquitrán y el fuego envolverá sus rostros." WHERE ayatNumber = 51 AND chapterNumber = 14;
UPDATE verses SET es = "<em> Esto será así</em> para que Al-lah retribuya a cada alma por lo que ha hecho. En verdad, Al-lah es rápido en el cálculo." WHERE ayatNumber = 52 AND chapterNumber = 14;
UPDATE verses SET es = "Este es un mensaje difundido con claridad para beneficio de la humanidad, para que sean exhortados por ella, para que sepan que Él es el solo Dios Único y para que puedan reflexionar los que posean conocimiento." WHERE ayatNumber = 53 AND chapterNumber = 14;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 15;
UPDATE verses SET es = "Alif Lam Ra. Éstos son versículos del Libro y del Corán manifiesto." WHERE ayatNumber = 2 AND chapterNumber = 15;
UPDATE verses SET es = "Con frecuencia, los incrédulos desearán haber sido musulmanes." WHERE ayatNumber = 3 AND chapterNumber = 15;
UPDATE verses SET es = "Déjalos solos para que coman y disfruten y la esperanza vana les engañe; mas pronto sabrán." WHERE ayatNumber = 4 AND chapterNumber = 15;
UPDATE verses SET es = "Pues nunca hemos destruido ciudad alguna sin que existiera para ella un decreto conocido." WHERE ayatNumber = 5 AND chapterNumber = 15;
UPDATE verses SET es = "Ningún pueblo puede anticipar el tiempo que le ha sido fijado, ni retrasarlo." WHERE ayatNumber = 6 AND chapterNumber = 15;
UPDATE verses SET es = "Y dijeron: “Oh tú, a quien ha sido revelada esta Exhortación, sin duda eres un loco," WHERE ayatNumber = 7 AND chapterNumber = 15;
UPDATE verses SET es = "“¿Por qué no nos envías unos ángeles, si eres de los veraces?”." WHERE ayatNumber = 8 AND chapterNumber = 15;
UPDATE verses SET es = "Nosotros no enviamos ángeles sino con la verdad, y entonces no se les concede respiro alguno." WHERE ayatNumber = 9 AND chapterNumber = 15;
UPDATE verses SET es = "En verdad, Nosotros mismos hemos revelado esta Exhortación, y ciertamente seremos su Guardián." WHERE ayatNumber = 10 AND chapterNumber = 15;
UPDATE verses SET es = "Pues enviamos <em>Mensajeros</em> antes de ti de varias confesiones de pueblos anteriores." WHERE ayatNumber = 11 AND chapterNumber = 15;
UPDATE verses SET es = "Pero nunca les llegó Mensajero alguno sin que se burlaran de él." WHERE ayatNumber = 12 AND chapterNumber = 15;
UPDATE verses SET es = "Así hicimos que esta<em> costumbre de burlarse</em> entrara en los corazones de los pecadores;" WHERE ayatNumber = 13 AND chapterNumber = 15;
UPDATE verses SET es = "No creen en él, aunque el ejemplo de los pueblos anteriores les <em>precedió.</em>" WHERE ayatNumber = 14 AND chapterNumber = 15;
UPDATE verses SET es = "Y aunque les abriésemos una puerta del Cielo, y empezaran a subir por ella," WHERE ayatNumber = 15 AND chapterNumber = 15;
UPDATE verses SET es = "Dirían ciertamente: “Es que nuestros ojos están deslumbrados; más bien somos un pueblo embrujado”." WHERE ayatNumber = 16 AND chapterNumber = 15;
UPDATE verses SET es = "Mas en verdad hemos creado constelaciones en el firmamento y las hemos adornado para quienes contemplan." WHERE ayatNumber = 17 AND chapterNumber = 15;
UPDATE verses SET es = "Y lo hemos protegido contra todo Satanás rechazado." WHERE ayatNumber = 18 AND chapterNumber = 15;
UPDATE verses SET es = "Mas si alguien escucha furtivamente, le persigue una llama brillante." WHERE ayatNumber = 19 AND chapterNumber = 15;
UPDATE verses SET es = "Pues hemos extendido la tierra, poniendo en ella montañas firmes, y hemos hecho que todo germinara en ella en la proporción adecuada." WHERE ayatNumber = 20 AND chapterNumber = 15;
UPDATE verses SET es = "Y en ella dispusimos medios de subsistencia para vosotros, y <em>también</em> para <em>todos</em> aquellos a quienes no sustentáis." WHERE ayatNumber = 21 AND chapterNumber = 15;
UPDATE verses SET es = "Y no existe cosa alguna cuyos tesoros no estén en Nosotros, y sólo los enviamos en medida conocida." WHERE ayatNumber = 22 AND chapterNumber = 15;
UPDATE verses SET es = "Y enviamos los vientos fecundantes, y después hacemos que caiga el agua de las nubes, entregándoosla para que bebáis; pero no sois los que la almacenáis." WHERE ayatNumber = 23 AND chapterNumber = 15;
UPDATE verses SET es = "Y en verdad, somos Nosotros quienes damos la vida y causamos la muerte; pues sólo Nosotros somos el <em>único</em> Heredero." WHERE ayatNumber = 24 AND chapterNumber = 15;
UPDATE verses SET es = "Conocemos a los que van delante de vosotros y conocemos a los que vienen detrás." WHERE ayatNumber = 25 AND chapterNumber = 15;
UPDATE verses SET es = "Y en verdad, es tu Señor quien los reunirá a todos. En verdad, Él es Sabio, Omnisciente." WHERE ayatNumber = 26 AND chapterNumber = 15;
UPDATE verses SET es = "En verdad, creamos al hombre de arcilla seca sonora, <em>modelado </em>de barro estancado." WHERE ayatNumber = 27 AND chapterNumber = 15;
UPDATE verses SET es = "Y anteriormente habíamos creado a los Yinn del fuego de vientos llameantes." WHERE ayatNumber = 28 AND chapterNumber = 15;
UPDATE verses SET es = "Y <em>acuérdate</em> de cuando tu Señor dijo a los ángeles: “Estoy a punto de crear al hombre de arcilla seca sonora, de barro negro moldeado en su forma”;" WHERE ayatNumber = 29 AND chapterNumber = 15;
UPDATE verses SET es = "“Así, cuando lo haya moldeado <em>con perfección</em> y le haya insuflado Mi Espíritu, postraos en señal de sumisión a Él”." WHERE ayatNumber = 30 AND chapterNumber = 15;
UPDATE verses SET es = "Los ángeles se sometieron pues, todos ellos juntos." WHERE ayatNumber = 31 AND chapterNumber = 15;
UPDATE verses SET es = "Excepto Iblis; se negó a ser de los que se someten." WHERE ayatNumber = 32 AND chapterNumber = 15;
UPDATE verses SET es = "Dios dijo: “Oh, Iblis, ¿qué te ocurre que no estás entre los que se someten?”." WHERE ayatNumber = 33 AND chapterNumber = 15;
UPDATE verses SET es = "Él respondió: “No voy a someterme al hombre a quien has creado de arcilla seca sonora, de barro negro moldeado en su forma”." WHERE ayatNumber = 34 AND chapterNumber = 15;
UPDATE verses SET es = "<em>Dios</em> dijo: “Sal de aquí, porque en verdad has sido rechazado”." WHERE ayatNumber = 35 AND chapterNumber = 15;
UPDATE verses SET es = "<em> “</em>Pues en verdad sobre ti caerá Mi maldición, hasta el Día del Juicio”." WHERE ayatNumber = 36 AND chapterNumber = 15;
UPDATE verses SET es = "Él dijo: “Señor mío, concédeme un respiro hasta el día en que sean resucitados”." WHERE ayatNumber = 37 AND chapterNumber = 15;
UPDATE verses SET es = "<em> Dios</em> dijo: “Eres de aquellos a los que se les concede un respiro”." WHERE ayatNumber = 38 AND chapterNumber = 15;
UPDATE verses SET es = "<em> “</em>Hasta que llegue el día del tiempo fijado”." WHERE ayatNumber = 39 AND chapterNumber = 15;
UPDATE verses SET es = "Él respondió: “Señor mío, ya que Me has juzgado como perdido, haré ciertamente que <em>el mal</em> les <em>aparezca</em> bello en la tierra, y extraviaré a todos ellos”." WHERE ayatNumber = 40 AND chapterNumber = 15;
UPDATE verses SET es = "“Excepto a Tus siervos elegidos de entre ellos”." WHERE ayatNumber = 41 AND chapterNumber = 15;
UPDATE verses SET es = "<em> Dios</em> dijo: “Éste es un camino<em> que lleva</em> recto hasta Mí;”" WHERE ayatNumber = 42 AND chapterNumber = 15;
UPDATE verses SET es = "“En verdad, no tendrás poder sobre Mis siervos, excepto aquellos de los perversos que te sigan”." WHERE ayatNumber = 43 AND chapterNumber = 15;
UPDATE verses SET es = "Pues en verdad, el Infierno es el lugar prometido para todos ellos." WHERE ayatNumber = 44 AND chapterNumber = 15;
UPDATE verses SET es = "Tiene siete puertas;<em> y</em> a cada puerta se le asigna un grupo de ellos." WHERE ayatNumber = 45 AND chapterNumber = 15;
UPDATE verses SET es = "Ciertamente el justo estará <em>colocado</em> entre jardines y fuentes." WHERE ayatNumber = 46 AND chapterNumber = 15;
UPDATE verses SET es = "“Entrad en ella en paz y seguridad”." WHERE ayatNumber = 47 AND chapterNumber = 15;
UPDATE verses SET es = "Haremos desaparecer cualquier rencor que pueda haber en sus pechos <em>de manera que se vuelvan</em> como hermanos, reclinados en divanes, unos frente a otros;" WHERE ayatNumber = 48 AND chapterNumber = 15;
UPDATE verses SET es = "La fatiga no les alcanzará, ni serán <em>nunca</em> expulsados de allí." WHERE ayatNumber = 49 AND chapterNumber = 15;
UPDATE verses SET es = "Di a Mis siervos que Yo soy ciertamente el Sumo Perdonador, el Misericordioso;" WHERE ayatNumber = 50 AND chapterNumber = 15;
UPDATE verses SET es = "Y <em>también</em> que Mi castigo es el castigo doloroso." WHERE ayatNumber = 51 AND chapterNumber = 15;
UPDATE verses SET es = "E infórmales de los huéspedes de Abraham." WHERE ayatNumber = 52 AND chapterNumber = 15;
UPDATE verses SET es = "Cuando se presentaron ante él, diciéndole: “Paz”, él respondió: “En verdad sentimos miedo de vosotros”." WHERE ayatNumber = 53 AND chapterNumber = 15;
UPDATE verses SET es = "Respondieron: “No temas, pues venimos a darte la buena nueva de un hijo<em> que estará</em> dotado de conocimiento”." WHERE ayatNumber = 54 AND chapterNumber = 15;
UPDATE verses SET es = "Él dijo: “¿Me dais esa buena nueva a pesar de que la vejez me ha alcanzado? ¿Sobre qué pues me dais la buena nueva?”." WHERE ayatNumber = 55 AND chapterNumber = 15;
UPDATE verses SET es = "Respondieron: “No te hemos dado sino la buena nueva basada en la verdad; no seas pues de los que desesperan”." WHERE ayatNumber = 56 AND chapterNumber = 15;
UPDATE verses SET es = "Dijo: “¿Mas quién puede desesperar de la misericordia de su Señor salvo los extraviados?”." WHERE ayatNumber = 57 AND chapterNumber = 15;
UPDATE verses SET es = "Y añadió: “¿Cuál es vuestra misión<em> actual,</em> Oh mensajeros?”." WHERE ayatNumber = 58 AND chapterNumber = 15;
UPDATE verses SET es = "Respondieron: “Hemos sido enviados a un pueblo culpable”," WHERE ayatNumber = 59 AND chapterNumber = 15;
UPDATE verses SET es = "“Exceptuada la familia de Lot. Salvaremos a todos ellos”," WHERE ayatNumber = 60 AND chapterNumber = 15;
UPDATE verses SET es = "“Excepto a su mujer. Suponemos que estará entre los que se queden detrás”." WHERE ayatNumber = 61 AND chapterNumber = 15;
UPDATE verses SET es = "Mas cuando los mensajeros se presentaron ante la familia de Lot," WHERE ayatNumber = 62 AND chapterNumber = 15;
UPDATE verses SET es = "Él dijo: “En verdad, sois un grupo de desconocidos”." WHERE ayatNumber = 63 AND chapterNumber = 15;
UPDATE verses SET es = "Respondieron: “No; sólo hemos venido a ti con aquello sobre lo que dudaron”;" WHERE ayatNumber = 64 AND chapterNumber = 15;
UPDATE verses SET es = "“Y hemos venido a ti con la verdad, pues ciertamente somos veraces”." WHERE ayatNumber = 65 AND chapterNumber = 15;
UPDATE verses SET es = "“Sal pues con tu familia en la <em>última</em> parte de la noche, y sigue tú detrás de ellos. Que ninguno de vosotros mire atrás, y dirigios <em>ahora</em> a donde se os ordene”." WHERE ayatNumber = 66 AND chapterNumber = 15;
UPDATE verses SET es = "Y le comunicamos este decreto: de que hasta el último de ellos sería exterminado por la mañana." WHERE ayatNumber = 67 AND chapterNumber = 15;
UPDATE verses SET es = "Mas la gente de la ciudad acudieron regocijados." WHERE ayatNumber = 68 AND chapterNumber = 15;
UPDATE verses SET es = "Él dijo: “Éstos son mis invitados, no me avergoncéis”." WHERE ayatNumber = 69 AND chapterNumber = 15;
UPDATE verses SET es = "“Temed a Al-lah y no me cubráis de vergüenza”." WHERE ayatNumber = 70 AND chapterNumber = 15;
UPDATE verses SET es = "Respondieron: “¿Acaso no te prohibimos <em>recibir</em> a todo <em>tipo de</em> gentes?”." WHERE ayatNumber = 71 AND chapterNumber = 15;
UPDATE verses SET es = "Dijo: “Mis hijas <em>también</em> están <em>presentes</em> aquí. <em>Tenedlo en cuenta</em> si os sentís inclinados a hacer algo”." WHERE ayatNumber = 72 AND chapterNumber = 15;
UPDATE verses SET es = "Por tu vida, éstos <em>también</em> vagan distraídos en su <em>loca</em> embriaguez." WHERE ayatNumber = 73 AND chapterNumber = 15;
UPDATE verses SET es = "Pero entonces el castigo se abatió sobre ellos al despuntar el sol." WHERE ayatNumber = 74 AND chapterNumber = 15;
UPDATE verses SET es = "Le dimos <em>completamente</em> la vuelta, e hicimos llover sobre ellos piedras arcillosas." WHERE ayatNumber = 75 AND chapterNumber = 15;
UPDATE verses SET es = "En verdad, en todo esto hay señales para quien sepa leer <em>los signos.</em>" WHERE ayatNumber = 76 AND chapterNumber = 15;
UPDATE verses SET es = "Pues se encuentra en un camino que <em>todavía</em> existe." WHERE ayatNumber = 77 AND chapterNumber = 15;
UPDATE verses SET es = "En verdad, en esto hay un Signo para los creyentes." WHERE ayatNumber = 78 AND chapterNumber = 15;
UPDATE verses SET es = "Y <em>los de </em>el Pueblo del Bosque, fueron también, ciertamente, inicuos." WHERE ayatNumber = 79 AND chapterNumber = 15;
UPDATE verses SET es = "Por ello les castigamos. Y ambos yacen <em>enterrados</em> en un camino notorio." WHERE ayatNumber = 80 AND chapterNumber = 15;
UPDATE verses SET es = "Las Gentes del Hillr trataron <em>también</em> a los Mensajeros como mentirosos." WHERE ayatNumber = 81 AND chapterNumber = 15;
UPDATE verses SET es = "Les dimos nuestros Signos, pero se apartaron de ellos." WHERE ayatNumber = 82 AND chapterNumber = 15;
UPDATE verses SET es = "Solían excavar sus casas en las montañas, para más seguridad." WHERE ayatNumber = 83 AND chapterNumber = 15;
UPDATE verses SET es = "Pero el castigo cayó sobre ellos al amanecer," WHERE ayatNumber = 84 AND chapterNumber = 15;
UPDATE verses SET es = "Y no les sirvió de nada todo lo que habían ganado." WHERE ayatNumber = 85 AND chapterNumber = 15;
UPDATE verses SET es = "Pues no hemos creado los cielos y la tierra y todo lo que hay entre ellos sino de acuerdo con la verdad; y es seguro que vendrá la Hora. Aléjate, pues, <em>de ellos, </em>apartándote con cortesía." WHERE ayatNumber = 86 AND chapterNumber = 15;
UPDATE verses SET es = "En verdad, tu Señor es el Gran Creador, el Omnisciente." WHERE ayatNumber = 87 AND chapterNumber = 15;
UPDATE verses SET es = "Y, en efecto, te hemos dado los siete<em> versículos</em>, que se repiten a menudo y el Gran Corán." WHERE ayatNumber = 88 AND chapterNumber = 15;
UPDATE verses SET es = "No dirijas tu mirada <em>con codicia</em> al placer temporal que hemos proporcionado a algunos grupos de ellos, ni te aflijas por ellos; más bien cubre con tu ala <em>de misericordia</em> a los creyentes." WHERE ayatNumber = 89 AND chapterNumber = 15;
UPDATE verses SET es = "Diles: “Soy ciertamente un simple Amonestador”." WHERE ayatNumber = 90 AND chapterNumber = 15;
UPDATE verses SET es = "Igual que siempre, enviaremos el castigo a quienes se dividieron en sectas." WHERE ayatNumber = 91 AND chapterNumber = 15;
UPDATE verses SET es = "<em>Y</em> a quienes dividirán al Corán en fragmentos." WHERE ayatNumber = 92 AND chapterNumber = 15;
UPDATE verses SET es = "Así pues, por tu Señor, en verdad que interrogaremos a todos ellos" WHERE ayatNumber = 93 AND chapterNumber = 15;
UPDATE verses SET es = "Sobre lo que solían hacer." WHERE ayatNumber = 94 AND chapterNumber = 15;
UPDATE verses SET es = "Declara pues abiertamente lo que se te ha ordenado y aléjate de quienes adscriben partícipes <em>a Dios.</em>" WHERE ayatNumber = 95 AND chapterNumber = 15;
UPDATE verses SET es = "En verdad, te bastaremos contra quienes se burlan;" WHERE ayatNumber = 96 AND chapterNumber = 15;
UPDATE verses SET es = "Quienes elevan a otro dios a la altura de Al-lah, aunque pronto lo sabrán." WHERE ayatNumber = 97 AND chapterNumber = 15;
UPDATE verses SET es = "Pues, en verdad, sabemos que tu pecho se encoge por lo que dicen." WHERE ayatNumber = 98 AND chapterNumber = 15;
UPDATE verses SET es = "Y glorifica a tu Señor alabándole, y sé de los que se postran <em>ante Él.</em>" WHERE ayatNumber = 99 AND chapterNumber = 15;
UPDATE verses SET es = "Y continúa adorando a tu Señor hasta que te llegue la muerte." WHERE ayatNumber = 100 AND chapterNumber = 15;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 16;
UPDATE verses SET es = "El decreto de Al-lah está cerca; no seáis impacientes acerca de él. Santo es Él, y exaltado por encima de todo lo que Le asocian." WHERE ayatNumber = 2 AND chapterNumber = 16;
UPDATE verses SET es = "Él envía a los ángeles con la revelación por Su mandato, a quien le place de Sus siervos, <em>diciendo:</em> “Advertid <em>a la gente</em> ue no hay más dios que Yo, por lo que debéis temerme <em>sólo a Mí”</em>." WHERE ayatNumber = 3 AND chapterNumber = 16;
UPDATE verses SET es = "Él creó los cielos y la tierra de acuerdo con los requisitos de la verdad. Exaltado sea Él por encima de todo lo que Le asocian." WHERE ayatNumber = 4 AND chapterNumber = 16;
UPDATE verses SET es = "Él ha creado al hombre de una gota de líquido, pero he ahí que se convierte en un opositor declarado." WHERE ayatNumber = 5 AND chapterNumber = 16;
UPDATE verses SET es = "Él ha creado también el ganado; en ellos encontráis calor y <em>muchos</em> <em>otros</em> beneficios; y de algunos de ellos coméis." WHERE ayatNumber = 6 AND chapterNumber = 16;
UPDATE verses SET es = "Y en ellos encontráis belleza para vosotros cuando <em>los</em> lleváis a casa al atardecer, y cuando <em>los</em> sacáis a pastar por la mañana." WHERE ayatNumber = 7 AND chapterNumber = 16;
UPDATE verses SET es = "Y ellos transportan vuestras cargas hasta una tierra a la que no podríais llegar a no ser a costa de gran esfuerzo por vuestra parte. En verdad, vuestro Señor es Compasivo, Misericordioso." WHERE ayatNumber = 8 AND chapterNumber = 16;
UPDATE verses SET es = "<em> Él ha creado</em> caballos, mulas y asnos para que podáis montarlos, y <em>como fuente de</em> belleza. Y Él creará lo que aún desconocéis." WHERE ayatNumber = 9 AND chapterNumber = 16;
UPDATE verses SET es = "A Al-lah corresponde <em>mostrar el</em> camino recto, pues hay aminos que se desvían­ <em>del recto.</em> Mas si Él hubiese <em>ejecutado</em> Su Voluntad, os habría guiado a todos." WHERE ayatNumber = 10 AND chapterNumber = 16;
UPDATE verses SET es = "Él es quien os envía el agua de las nubes; de ella tenéis vuestra bebida y a partir de ella <em>crecen</em> las plantas en las que hacéis pastar<em> a vuestro ganado.</em>" WHERE ayatNumber = 11 AND chapterNumber = 16;
UPDATE verses SET es = "En ella hace germinar para vosotros <em>toda clase</em> de cultivos, el olivo y la palmera, la vid y toda clase de frutos. En verdad, aquí hay un Signo para las gentes que reflexionan." WHERE ayatNumber = 12 AND chapterNumber = 16;
UPDATE verses SET es = "Y sometió a vuestro servicio la noche y el día, el sol y la luna; las estrellas <em>también</em> fueron puestas a vuestro servicio por orden Suya. En verdad, aquí hay Signos para las gentes que utilizan la razón." WHERE ayatNumber = 13 AND chapterNumber = 16;
UPDATE verses SET es = "Y él <em>sometió a vuestro servicio</em> las cosas que creó para vosotros en la tierra, de colores variados. En verdad, aquí hay un Signo para las gentes que prestan atención." WHERE ayatNumber = 14 AND chapterNumber = 16;
UPDATE verses SET es = "Él es quien<em> os</em> sometió el mar para que comierais de él carne fresca, y sacarais de él ciertos ornamentos con los que os ngalanáis. Veis a los buques surcándolo, <em>para que de ese modo podáis viajar</em> y para que procuréis Su Magnanimidad y seáis agradecidos." WHERE ayatNumber = 15 AND chapterNumber = 16;
UPDATE verses SET es = "Él ha puesto en la tierra montañas firmes para sosteneros, y ríos y rutas para que podáis seguir el camino adecuado." WHERE ayatNumber = 16 AND chapterNumber = 16;
UPDATE verses SET es = "Y <em>también puso otras</em> señales; <em>con ellas</em> y con las estrellas siguen la dirección correcta." WHERE ayatNumber = 17 AND chapterNumber = 16;
UPDATE verses SET es = "¿Acaso Quien crea es como quien no crea? ¿No prestaréis atención?" WHERE ayatNumber = 18 AND chapterNumber = 16;
UPDATE verses SET es = "Pero si <em>intentáis</em> contar los favores de Al-lah, no podréis enumerarlos. En verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 19 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah sabe lo que ocultáis y lo que manifestáis." WHERE ayatNumber = 20 AND chapterNumber = 16;
UPDATE verses SET es = "Mas aquellos a quienes invocan en vez de Al-lah no crean nada, sino que ellos mismos son creados." WHERE ayatNumber = 21 AND chapterNumber = 16;
UPDATE verses SET es = "<em>Son</em> muertos, no vivos; e ignoran cuándo serán resucitados." WHERE ayatNumber = 22 AND chapterNumber = 16;
UPDATE verses SET es = "Vuestro Dios es el Dios Único. Y en cuanto a quienes no creen en el Más Allá, sus corazones son contrarios <em>a la verdad,</em> y están llenos de orgullo." WHERE ayatNumber = 23 AND chapterNumber = 16;
UPDATE verses SET es = "Indudablemente Al-lah sabe lo que ocultan y lo que manifiestan. El, en verdad, no ama a los orgullosos." WHERE ayatNumber = 24 AND chapterNumber = 16;
UPDATE verses SET es = "Mas cuando se les dice: “¿Qué <em>pensáis vosotros</em> de lo que vuestro Señor ha revelado?”. Responden: “Historias de los antiguos”." WHERE ayatNumber = 25 AND chapterNumber = 16;
UPDATE verses SET es = "Para que lleven sus cargas completas el Día de la Resurrección, y <em>también</em> una parte de las cargas de aquellos a quienes extraviaron sin conocimiento. ¡Qué mala es la carga que soportan!" WHERE ayatNumber = 26 AND chapterNumber = 16;
UPDATE verses SET es = "Quienes los precedieron se confabularon <em>también</em>, mas Al-lah derribó sus edificaciones hasta sus mismos cimientos, de manera que el techo se desplomó sobre ellos; y el castigo les vino de donde menos pensaban." WHERE ayatNumber = 27 AND chapterNumber = 16;
UPDATE verses SET es = "Luego, en el Día de la Resurrección, los afrentará diciendo: “¿Dónde están Mis “partícipes” en cuyo nombre acostumbrabais a oponeros a <em>los Profetas?”</em>. Los dotados de conocimiento dirán: “Este día, la desgracia y la aflicción <em>caerán</em> ciertamente sobre los incrédulos”." WHERE ayatNumber = 28 AND chapterNumber = 16;
UPDATE verses SET es = "Aquellos a quienes los ángeles causarán la muerte mientras estén aciendo daño a sus propias almas. Entonces ofrecerán su sumisión <em>diciendo:</em> “No solíamos hacer ningún mal”. No; en verdad Al-lah sabe muy bien lo que solíais hacer." WHERE ayatNumber = 29 AND chapterNumber = 16;
UPDATE verses SET es = "Así pues, franquead las puertas del Infierno, para morar en él. ¡Mala es en verdad la morada del orgulloso!" WHERE ayatNumber = 30 AND chapterNumber = 16;
UPDATE verses SET es = "Y <em>de cuando</em> se dice a los justos: “¿Qué <em>pensáis vosotros</em> de lo que ha revelado vuestro Señor?”, ellos responden: “Lo mejor”. Para quienes hacen el bien hay un beneficio en este mundo, pero el hogar del Más Allá es aún mejor. ¡Qué buena es, en verdad, la morada de los justos!" WHERE ayatNumber = 31 AND chapterNumber = 16;
UPDATE verses SET es = "Jardines de Eternidad, en los que entrarán, por los que corren ríos. Tendrán en ellos lo que deseen. Así Al-lah recompensa a los justos," WHERE ayatNumber = 32 AND chapterNumber = 16;
UPDATE verses SET es = "Aquellos a quienes los ángeles causarán la muerte mientras son puros. Dirán: “¡La paz sea con vosotros! Entrad en el Cielo por lo que solíais hacer”." WHERE ayatNumber = 33 AND chapterNumber = 16;
UPDATE verses SET es = "¿Esperan acaso otra cosa salvo que los ángeles vengan sobre ellos o que se alcance el designio de tu Señor? Lo mismo hicieron los que les precedieron. Al-lah no les ha perjudicado, sino que se perjudicaron a sí mismos." WHERE ayatNumber = 34 AND chapterNumber = 16;
UPDATE verses SET es = "De ese modo, la perversa <em>consecuencia</em> de lo que hicieron, recayó sobre ellos y aquello de lo que solían mofarse les acorraló." WHERE ayatNumber = 35 AND chapterNumber = 16;
UPDATE verses SET es = "Quienes elevan <em>partícipes</em> iguales <em>a Dios</em> dicen: “Si Al-lah <em>lo</em> hubiese querido, no habríamos adorado a nada en vez de a Él, ni nosotros ni nuestros padres, ni habríamos prohibido nada sin una <em>orden</em> Suya”. Lo mismo <em>dijeron</em> quienes los precedieron. Pero ¿son responsables los Mensajeros de algo salvo de la simple entrega del Mensaje?" WHERE ayatNumber = 36 AND chapterNumber = 16;
UPDATE verses SET es = "Y suscitamos de entre cada pueblo un Mensajero, <em> que predicó</em>: “Adorad a Al-lah y rechazad al Maligno”. Pero ntre ellos hubo <em>algunos</em> a quienes Al-lah guió y <em>otros</em> que merecieron la perdición. ¡Recorred pues la tierra, y omprobad cuál fue el fin de los que trataron de mentirosos <em>a los Profetas!</em>" WHERE ayatNumber = 37 AND chapterNumber = 16;
UPDATE verses SET es = "Si deseas ardientemente guiarlos, <em>sabe</em> entonces que Al-lah no guía ciertamente a quienes extravían <em>a otros.</em> Para ellos no habrá socorredores." WHERE ayatNumber = 38 AND chapterNumber = 16;
UPDATE verses SET es = "Mas juran por Al-lah con sus juramentos más solemnes, que Al-lah no resucitará a quienes mueren. No, <em>Él ciertamente los resucitará</em> , una promesa que<em> Él ha hecho</em> obligándose a Sí Mismo, pero la mayoría de los hombres lo ignoran." WHERE ayatNumber = 39 AND chapterNumber = 16;
UPDATE verses SET es = "<em> Él los resucitará</em> para explicarles aquello en lo que discreparon, y para que los incrédulos sepan que fueron mentirosos." WHERE ayatNumber = 40 AND chapterNumber = 16;
UPDATE verses SET es = "Cuando queremos que algo exista, Nuestra palabra es solamente decirle: “¡Sé!”, y es." WHERE ayatNumber = 41 AND chapterNumber = 16;
UPDATE verses SET es = "<em> En cuanto </em>a los que abandonaron sus hogares por la causa de Al-lah después de haber sido vejados, ciertamente les daremos una digna morada en este mundo; mas, en verdad, la recompensa del Más Allá es mayor, si lo supieran," WHERE ayatNumber = 42 AND chapterNumber = 16;
UPDATE verses SET es = "Quienes son perseverantes y ponen su confianza en su Señor." WHERE ayatNumber = 43 AND chapterNumber = 16;
UPDATE verses SET es = "No enviamos <em>como Mensajeros</em> antes de ti sino a hombres a quienes enviamos la revelación; preguntad, pues, a quienes poseen la custodia de las escrituras divinas, si no lo sabéis." WHERE ayatNumber = 44 AND chapterNumber = 16;
UPDATE verses SET es = "Los enviamos con Signos evidentes y Escrituras. Y te revelamos el Recordatorio, para que explicaras a la humanidad lo que se les había revelado, y para que reflexionaran." WHERE ayatNumber = 45 AND chapterNumber = 16;
UPDATE verses SET es = "¿Se sentirán pues seguros quienes inventaron planes malvados, de que Al-lah no hará que se los trague la tierra, o de que el castigo no vendrá sobre ellos de donde no lo esperan?" WHERE ayatNumber = 46 AND chapterNumber = 16;
UPDATE verses SET es = "¿O de que <em>Él</em> no los sorprenderá en sus desplazamientos de modo que no puedan frustrar <em>los planes de Dios?</em>" WHERE ayatNumber = 47 AND chapterNumber = 16;
UPDATE verses SET es = "¿O de que Él no los alcanzará con <em>un proceso</em> de aniquilación gradual? Vuestro Señor es en verdad Compasivo, Misericordioso." WHERE ayatNumber = 48 AND chapterNumber = 16;
UPDATE verses SET es = "¿Acaso no han comprobado que las sombras de todo lo que Al-lah ha creado se mueven desde la derecha y<em> desde</em> la izquierda, postrándose ante Al-lah al tiempo que se humillan?" WHERE ayatNumber = 49 AND chapterNumber = 16;
UPDATE verses SET es = "Pues todo lo que hay en los cielos y cada criatura que hay en la tierra se somete<em> humildemente</em> a Al-lah, y <em>también</em> los ángeles, que no se comportan orgullosamente." WHERE ayatNumber = 50 AND chapterNumber = 16;
UPDATE verses SET es = "Temen a su Señor que está encima de ellos, y hacen lo que se les ordena." WHERE ayatNumber = 51 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah ha dicho: “No toméis a dos dioses <em>para adorarlos.</em> No hay más que un Dios Único. Así pues, Temedme solamente a Mí”." WHERE ayatNumber = 52 AND chapterNumber = 16;
UPDATE verses SET es = "A Él pertenece todo lo que hay en los cielos y en la tierra y a Él pertenece eternamente <em>el derecho a determinar</em> el camino. ¿Temeréis pues a alguien que no sea Al-lah?" WHERE ayatNumber = 53 AND chapterNumber = 16;
UPDATE verses SET es = "Pues todas las bendiciones que tenéis proceden de Al-lah. Y cuando s aflige la desgracia, es a Él a quien clamáis <em>pidiendo ayuda.</em>" WHERE ayatNumber = 54 AND chapterNumber = 16;
UPDATE verses SET es = "Luego, cuando os retira la desgracia, he ahí que un grupo de entre vosotros <em>empieza</em> a atribuir iguales a su Señor," WHERE ayatNumber = 55 AND chapterNumber = 16;
UPDATE verses SET es = "Hasta el punto de que niegan cuanto les hemos concedido. Pues bien, gozad un poco; pues pronto sabréis." WHERE ayatNumber = 56 AND chapterNumber = 16;
UPDATE verses SET es = "Y apartan para <em>los falsos dioses</em> de los que no saben nada, una parte de lo que les hemos concedido. Por Al-lah, ciertamente se os pedirá cuenta de todo lo que habéis fraguado." WHERE ayatNumber = 57 AND chapterNumber = 16;
UPDATE verses SET es = "Y atribuyen hijas a Al-lah - ¡Santo es Él! – mientras <em>ellos mismos</em> tienen lo que desean." WHERE ayatNumber = 58 AND chapterNumber = 16;
UPDATE verses SET es = "Mas cuando se anuncia a uno de ellos la buena nueva <em>del nacimiento de</em> una hembra, su semblante se ensombrece, y sofoca la angustia <em>en su interior.</em>" WHERE ayatNumber = 59 AND chapterNumber = 16;
UPDATE verses SET es = "Se oculta de las gentes por la mala noticia que ha recibido: “¿La conservará a pesar de la deshonra o la enterrará en el polvo?”. En verdad, ¡cuán malo es lo que juzgan!" WHERE ayatNumber = 60 AND chapterNumber = 16;
UPDATE verses SET es = "A quienes no creen en el Más Allá se ajusta la peor semblanza; mientras que a Al-lah pertenece lo más sublime y Él es el Poderoso, el Sabio." WHERE ayatNumber = 61 AND chapterNumber = 16;
UPDATE verses SET es = "Y si Al-lah tuviera que castigar a los hombres por sus malas acciones, no dejaría una criatura viva, pero Él les concede un respiro hasta un plazo fijado; y cuando les llega el plazo, no pueden retrasarse una sola hora, ni adelantar<em>lo</em>." WHERE ayatNumber = 62 AND chapterNumber = 16;
UPDATE verses SET es = "Y atribuyen a Al-lah lo que no les gusta <em>para ellos mismos</em> sus lenguas profieren la mentira de que tendrán lo mejor <em>de todo.</em> Indudablemente, suyo será el Fuego, y <em>en él</em> serán abandonados." WHERE ayatNumber = 63 AND chapterNumber = 16;
UPDATE verses SET es = "Por Al-lah, enviamos Mensajeros a los pueblos antes de ti; pero Satanás hizo que sus obras les <em>aparecieran</em> bellas a sus ojos. Así pues, él es su patrón en este día, y sufrirán un doloroso castigo." WHERE ayatNumber = 64 AND chapterNumber = 16;
UPDATE verses SET es = "Pues solamente te hemos revelado el Libro para que les expliques lo relativo a sus discordias, y como misericordia para un pueblo que cree." WHERE ayatNumber = 65 AND chapterNumber = 16;
UPDATE verses SET es = "Y Al-lah ha enviado agua desde el cielo y ha resucitado con ella la tierra después de su muerte. En verdad, aquí hay un Signo para las gentes que quieren oír." WHERE ayatNumber = 66 AND chapterNumber = 16;
UPDATE verses SET es = "Y ciertamente en el ganado hay <em>también</em> una lección para vosotros. Os damos a beber lo que hay en sus entrañas, de entre las heces y la sangre, leche pura y agradable para quienes <em>la </em> beben." WHERE ayatNumber = 67 AND chapterNumber = 16;
UPDATE verses SET es = "Y de los frutos de las palmeras y la vid, de donde obtenéis una bebida embriagadora y alimentos nutritivos. En verdad, en ello hay un Signo para las gentes que hacen uso de su razón." WHERE ayatNumber = 68 AND chapterNumber = 16;
UPDATE verses SET es = "Y tu Señor inspiró a la abeja, <em>diciéndole:</em> “Hazte casas en las colinas, en los árboles y en los enrejados que ellos construyen”." WHERE ayatNumber = 69 AND chapterNumber = 16;
UPDATE verses SET es = "“Come luego de <em>todo tipo de</em> fruta, y sigue humildemente los caminos prescritos por tu Señor”. De sus vientres sale una bebida de diversas tonalidades. En ella hay una cura para el hombre. En verdad, en ello hay un Signo para las gentes que reflexionan." WHERE ayatNumber = 70 AND chapterNumber = 16;
UPDATE verses SET es = "Y Al-lah os crea y después os hace morir; pero hay<em>algunos </em>de entre vosotros que llegan a la senectud,<em>en la situación</em> de no saber nada después de <em> haber tenido</em> conocimiento. En verdad, Al-lah es el Omnisciente, el Poderoso." WHERE ayatNumber = 71 AND chapterNumber = 16;
UPDATE verses SET es = "Y Al-lah ha favorecido a algunos de vosotros sobre los demás en cuanto a provisiones. Pero los más favorecidos no devolverán parte alguna de sus provisiones a los que poseen en su mano derecha, a fin de que los <em>compartan</em> a partes iguales. ¿Denegarán pues la gracia de Al-lah?" WHERE ayatNumber = 72 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah os ha creado consortes de entre vosotros mismos, y os ha dado, de vuestros consortes, hijos y nietos, y os ha proporcionado cosas buenas. ¿Creerán pues en cosas vanas y negarán la merced de Al-lah?" WHERE ayatNumber = 73 AND chapterNumber = 16;
UPDATE verses SET es = "En vez de Al-lah, adoran a quienes no tienen poder para concederles don alguno de los cielos o de la tierra, ni <em>nunca</em> tendrán tal poder." WHERE ayatNumber = 74 AND chapterNumber = 16;
UPDATE verses SET es = "No comparéis pues a nadie con Al-lah. En verdad, Al-lah sabe y vosotros ignoráis." WHERE ayatNumber = 75 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah propone como parábola a un esclavo, posesión de otro, que no tiene poder sobre nada; y <em>un hombre libre</em> a quien hemos proporcionado una provisión adecuada de Nosotros Mismos, y de la que él emplea oculta y manifiestamente. ¿Acaso son iguales? ¡Alabado sea Al-lah! Pero la mayoría de ellos no lo saben." WHERE ayatNumber = 76 AND chapterNumber = 16;
UPDATE verses SET es = "Y Al-lah propone <em>otra</em> parábola de dos hombres: uno de ellos es mudo, no tiene poder sobre nada, y resulta una carga para su amo; a dondequiera que lo envíe, no le trae nada bueno. ¿Puede compararse con quien ordena la justicia y se encuentra él mismo en el camino recto?" WHERE ayatNumber = 77 AND chapterNumber = 16;
UPDATE verses SET es = "A Al-lah pertenece lo invisible de los cielos y la tierra; y el asunto de la Hora está a un abrir y cerrar de ojos o aún más cerca. En verdad, Al-lah tiene poder sobre todas las cosas." WHERE ayatNumber = 78 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah os sacó de los vientres de vuestras madres mientras no sabíais nada, y os dio oídos, ojos y corazones, para que fuerais agradecidos." WHERE ayatNumber = 79 AND chapterNumber = 16;
UPDATE verses SET es = "¿No ven acaso a los pájaros que se mantienen <em>en lo alto</em> a mitad del cielo? Nadie los sostiene <em>arriba</em> excepto Al-lah. En verdad, en esto hay Signos para las gentes que creen." WHERE ayatNumber = 80 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah ha hecho vuestras casas: un lugar de reposo para vosotros; y de las pieles del ganado os ha dado moradas que encontráis ligeras cuando viajáis y en el momento en que os detenéis; y de su lana, de sus pieles y de su pelo <em>os ha proporcionado</em> enseres y artículos domésticos para utilizarlos durante un tiempo." WHERE ayatNumber = 81 AND chapterNumber = 16;
UPDATE verses SET es = "Y Al-lah ha hecho para vosotros, de lo que ha creado, <em>cosas que dan</em> sombra; y en la montaña os ha creado lugares de refugio; ha hecho para vosotros prendas que os protegen del calor y cotas de malla que os protegen en vuestras guerras. De este modo completa Su favor hacia vosotros, para que os sometáis <em>a Él.</em>" WHERE ayatNumber = 82 AND chapterNumber = 16;
UPDATE verses SET es = "Pero si se apartan, tú sólo eres responsable de comunicar simplemente el Mensaje." WHERE ayatNumber = 83 AND chapterNumber = 16;
UPDATE verses SET es = "Ellos conocen bien el favor de Al-lah, <em>puesto que lo ven</em>, aunque lo nieguen; y la mayoría son desagradecidos" WHERE ayatNumber = 84 AND chapterNumber = 16;
UPDATE verses SET es = "Acordaos del día en que suscitaremos un testigo de cada pueblo, entonces no se permitirá <em>demandar</em> a los incrédulos<em>,</em> ni su súplica será aceptada." WHERE ayatNumber = 85 AND chapterNumber = 16;
UPDATE verses SET es = "Mas cuando los que actuaron inicuamente vean <em>en realidad</em> el castigo, no se les hará ligero, ni se les concederá respiro." WHERE ayatNumber = 86 AND chapterNumber = 16;
UPDATE verses SET es = "Y cuando los que asocian partícipes <em>a Dios</em> vean a sus divinidades asociadas, dirán: “Señor Nuestro, éstas son nuestras divinidades asociadas a las que solíamos invocar en vez de Ti”. A lo que ellos les responderán con las palabras: “En verdad, sois mentirosos”." WHERE ayatNumber = 87 AND chapterNumber = 16;
UPDATE verses SET es = "Ese día ofrecerán su sumisión a Al-lah, y todo lo que acostumbraban a inventar les fallará." WHERE ayatNumber = 88 AND chapterNumber = 16;
UPDATE verses SET es = "<em> En cuanto a</em> los incrédulos que apartan <em>a los hombres</em> del camino de Al-lah, añadiremos un castigo a su castigo por haber actuado con corrupción." WHERE ayatNumber = 89 AND chapterNumber = 16;
UPDATE verses SET es = "<em> Y</em> <em>acordaos </em>del día en que suscitaremos en cada pueblo un testigo contra ellos de entre si mismos, y te presentaremos como testigo contra éstos. Pues te hemos revelado el Libro para explicar todo, y como guía, misericordia, y buena nueva para los que se someten <em>a Dios.</em>" WHERE ayatNumber = 90 AND chapterNumber = 16;
UPDATE verses SET es = "En verdad, Al-lah os ordena permanecer en la justicia y <em>dispensar</em> un trato amable, y dar como se da a los parientes; y prohíbe la obscenidad, la maldad manifiesta y la transgresión. Él os exhorta para que caigáis en la cuenta." WHERE ayatNumber = 91 AND chapterNumber = 16;
UPDATE verses SET es = "Cumplid el pacto con Al-lah cuando lo hayáis hecho; y no rompáis los juramentos después de haberlos realizado solemnemente, si tomáis a Al-lah como vuestro fiador. En verdad, Al-lah sabe lo que hacéis." WHERE ayatNumber = 92 AND chapterNumber = 16;
UPDATE verses SET es = "No seáis como aquella que, después de haberlo hecho fuerte, rompe su hilado en trozos. Hacéis de vuestros juramentos un medio de engaño mutuo, <em>por temor a que</em> un pueblo se haga más poderoso que otro. En verdad, Al-lah os prueba con ello, y en el Día de la Resurrección os manifestará en qué discrepabais." WHERE ayatNumber = 93 AND chapterNumber = 16;
UPDATE verses SET es = "Mas si Al-lah hubiese <em>ejecutado</em> Su Voluntad, ciertamente os habría convertido <em>a todos</em> en un solo pueblo; pero Él deja que se extravíe quien <em>lo</em> desee, y guía a quien <em>lo</em> desea; y en verdad seréis interrogados sobre cuanto hayáis hecho." WHERE ayatNumber = 94 AND chapterNumber = 16;
UPDATE verses SET es = "No convirtáis a vuestros juramentos en un medio de engaño entre vosotros; <em>vuestro</em> pie resbalará después de pisar firmemente, y saborearéis el mal porque habéis apartado <em>a las gentes</em> del camino de Al-lah, y tendréis un severo castigo." WHERE ayatNumber = 95 AND chapterNumber = 16;
UPDATE verses SET es = "Y no vendáis el pacto de Al-lah a un precio ridículo. En verdad, lo que está junto a Al-lah es mejor para vosotros, ¡si lo supierais!" WHERE ayatNumber = 96 AND chapterNumber = 16;
UPDATE verses SET es = "Lo que poseéis se acabará pero lo que está junto a Al-lah durará <em>para siempre</em>. En verdad daremos a los perseverantes su recompensa según lo mejor de sus obras." WHERE ayatNumber = 97 AND chapterNumber = 16;
UPDATE verses SET es = "A quien actúa con justicia, sea hombre o mujer, y sea creyente, le concederemos en verdad una vida pura; y ciertamente le daremos su recompensa de acuerdo con lo mejor de sus obras." WHERE ayatNumber = 98 AND chapterNumber = 16;
UPDATE verses SET es = "Y cuando recites el Corán busca refugio en Al-lah contra Satanás, el rechazado." WHERE ayatNumber = 99 AND chapterNumber = 16;
UPDATE verses SET es = "En verdad, él no tiene poder sobre los que creen y ponen su confianza en su Señor." WHERE ayatNumber = 100 AND chapterNumber = 16;
UPDATE verses SET es = "Su poder se extiende únicamente sobre los que se hacen amigos suyos y asocian otros dioses a Él." WHERE ayatNumber = 101 AND chapterNumber = 16;
UPDATE verses SET es = "Cuando traemos un Signo en lugar de otro – y Al-lah es quien mejor sabe lo que revela – dicen: “No eres más que un impostor”. No, pero la mayoría de ellos no lo saben." WHERE ayatNumber = 102 AND chapterNumber = 16;
UPDATE verses SET es = "Diles: “El Espíritu de santidad lo ha traído de tu Señor con la verdad, para que Él fortalezca a los creyentes, y como guía y buena nueva para los musulmanes”." WHERE ayatNumber = 103 AND chapterNumber = 16;
UPDATE verses SET es = "Pues en verdad sabemos que dicen que no es más que un hombre quien e enseña. <em>Pero</em> la lengua de aquel a quien se inclinan <em>injustamente al hacer esta insinuación</em> es extranjera, mientras que ésta es la lengua árabe, lisa y pura." WHERE ayatNumber = 104 AND chapterNumber = 16;
UPDATE verses SET es = "<em> En cuanto a</em> quienes no creen en los Signos de Al-lah, en verdad Al-lah no los guiará, y tendrán un castigo doloroso." WHERE ayatNumber = 105 AND chapterNumber = 16;
UPDATE verses SET es = "Sólo quienes no creen en los Signos de Al-lah son los que inventan falsedades, y son ellos los mentirosos." WHERE ayatNumber = 106 AND chapterNumber = 16;
UPDATE verses SET es = "Quien deja de creer en Al-lah después de haber creído, –salvo uien se vea obligado <em>a ello por encima del límite de su tolerancia</em> y mientras su corazón permanece firme en la fe–; aquellos cuyos corazones están contentos con el rechazo: sobre ellos caerá la cólera de Al-lah; y sufrirán un severo castigo." WHERE ayatNumber = 107 AND chapterNumber = 16;
UPDATE verses SET es = "Esto es porque han preferido la vida presente al Más Allá, y porque Al-lah no guía a la gente incrédula." WHERE ayatNumber = 108 AND chapterNumber = 16;
UPDATE verses SET es = "Son aquellos en cuyos corazones, oídos y ojos Al-lah ha puesto un sello. Y son los desatentos." WHERE ayatNumber = 109 AND chapterNumber = 16;
UPDATE verses SET es = "Indudablemente serán los perdedores en el Más Allá." WHERE ayatNumber = 110 AND chapterNumber = 16;
UPDATE verses SET es = "Luego, en verdad, tu Señor –para aquellos que abandonaron <em>sus hogares</em> después de haber sido perseguidos y después se sacrificaron duramente <em>por la causa de Al-lah</em> y permanecieron perseverantes– ¡Sí!, sepan que, ciertamente, después de eso, tu Señor es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 111 AND chapterNumber = 16;
UPDATE verses SET es = "En el día en que cada alma abogará por sí misma, y cada alma será plenamente recompensada por lo que hizo, y no serán defraudadas." WHERE ayatNumber = 112 AND chapterNumber = 16;
UPDATE verses SET es = "Al-lah <em>os</em> propone la parábola de una ciudad que disfrutaba de seguridad y paz; sus provisiones llegaban a ella abundantes desde todas partes; pero rechazó las mercedes de Al-lah, por o que Al-lah les dio a probar una vida de hambre y temor que <em>los cercó</em> por lo que solían hacer." WHERE ayatNumber = 113 AND chapterNumber = 16;
UPDATE verses SET es = "Pues en verdad les ha llegado un Mensajero de entre ellos mismos, pero le trataron como mentiroso, por lo que el castigo cayó sobre ellos mientras eran malvados." WHERE ayatNumber = 114 AND chapterNumber = 16;
UPDATE verses SET es = "Comed pues de las cosas lícitas y buenas que Al-lah os ha proporcionado; y sed agradecidos ante la magnanimidad de Al-lah, si es a Él a Quien adoráis." WHERE ayatNumber = 115 AND chapterNumber = 16;
UPDATE verses SET es = "Él sólo os ha hecho ilícito que comáis la carne de aquellos animales que mueren de muerte natural, la sangre y la carne de cerdo y aquellos sobre los que ha sido invocado el nombre de alguien distinto de Al-lah. Pero quien se vea obligado por la necesidad extrema, careciendo de deseo y ánimo de transgredir, <em>sepa que</em>, en verdad, Al-lah es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 116 AND chapterNumber = 16;
UPDATE verses SET es = "No digáis –por la falsedad que profieren vuestras lenguas-: “Esto es lícito y aquello ilícito”, forjando una mentira contra Al-lah. En verdad, quienes forjan mentiras contra Al-lah no prosperarán." WHERE ayatNumber = 117 AND chapterNumber = 16;
UPDATE verses SET es = "<em> Después de</em> una ganancia temporal. Hay para ellos un doloroso castigo." WHERE ayatNumber = 118 AND chapterNumber = 16;
UPDATE verses SET es = "<em> Y</em> <em>también</em> a quienes son judíos, les prohibimos antes<em> de esto</em> todo lo que te hemos referido. Y no les perjudicamos, sino que se perjudicaron ellos mismos." WHERE ayatNumber = 119 AND chapterNumber = 16;
UPDATE verses SET es = "Pero en verdad, tu Señor –a quienes hacen el mal por ignorancia y después se arrepienten y se enmiendan– <em>sepan que,</em> ciertamente, después de eso, tu Señor es el Sumo Indulgente, Misericordioso." WHERE ayatNumber = 120 AND chapterNumber = 16;
UPDATE verses SET es = "Abraham fue una nación en sí mismo, siempre obediente a Al-lah, siempre inclinado <em>ante Él,</em> y ciertamente no fue de los idólatras." WHERE ayatNumber = 121 AND chapterNumber = 16;
UPDATE verses SET es = "Siempre agradecido por Sus mercedes; Él lo eligió y lo guió hacia el camino recto." WHERE ayatNumber = 122 AND chapterNumber = 16;
UPDATE verses SET es = "Nosotros le concedimos el bien en este mundo, y en el Más Allá estará ciertamente entre los justos." WHERE ayatNumber = 123 AND chapterNumber = 16;
UPDATE verses SET es = "<em> Ahora</em> Te hemos revelado<em> diciéndote: </em>“Sigue el amino de Abraham, <em>quien estuvo</em> siempre inclinado<em>ante Dios</em> y no fue de quienes engrandecen a otros a la altura <em> de Él”.</em>" WHERE ayatNumber = 124 AND chapterNumber = 16;
UPDATE verses SET es = "El Sabbath fue impuesto a quienes habían discrepado respecto <em>a Abraham y su religión,</em> mas tu Señor juzgará ciertamente entre ellos en el Día de la Resurrección sobre aquello en lo que discreparon." WHERE ayatNumber = 125 AND chapterNumber = 16;
UPDATE verses SET es = "Llama al camino de tu Señor con sabiduría y una bella exhortación, y discute con ellos de la mejor manera posible. En verdad, tu Señor sabe mejor quién se ha extraviado de Su camino; y Él conoce a los que están rectamente guiados." WHERE ayatNumber = 126 AND chapterNumber = 16;
UPDATE verses SET es = "Si <em>deseas</em> castigar a los <em>opresores,</em> castígalos en la medida en que hayas sido perjudicado; pero si muestras paciencia, entonces, es en verdad lo mejor para los pacientes." WHERE ayatNumber = 127 AND chapterNumber = 16;
UPDATE verses SET es = "Y resiste con paciencia; ciertamente tu paciencia sólo <em>es posible</em> con <em>la ayuda de</em> Al-lah. Y no te aflijas por ellos, ni sientas angustia por sus maquinaciones." WHERE ayatNumber = 128 AND chapterNumber = 16;
UPDATE verses SET es = "En verdad, Al-lah está con los justos y con quienes practican el bien." WHERE ayatNumber = 129 AND chapterNumber = 16;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 17;
UPDATE verses SET es = "Glorificado sea Aquel que llevó a Su siervo durante la noche, desde la Mezquita Sagrada a la Mezquita Lejana, cuyos alrededores hemos bendecido, para mostrarle <em>algunos</em> de Nuestros Signos. En verdad, sólo Él es el Oyente, el Vidente." WHERE ayatNumber = 2 AND chapterNumber = 17;
UPDATE verses SET es = "Dimos a Moisés el Libro, y lo hicimos una guía para los hijos de Israel, <em>diciéndoles:</em> “No toméis guardián alguno fuera de Mí”," WHERE ayatNumber = 3 AND chapterNumber = 17;
UPDATE verses SET es = "“Oh vosotros, descendientes de aquellos a los que llevamos <em>en el Arca</em> con Noé”. Fue en verdad un siervo agradecido." WHERE ayatNumber = 4 AND chapterNumber = 17;
UPDATE verses SET es = "Y revelamos a los hijos de Israel en el Libro, <em>diciéndoles:</em> “En verdad cometeréis dos veces una gran perversidad en la tierra, y ciertamente os haréis orgullosos en extremo”." WHERE ayatNumber = 5 AND chapterNumber = 17;
UPDATE verses SET es = "Así, cuando llegó el tiempo de cumplimiento de la primera de las dos advertencias, enviamos contra vosotros a <em>algunos</em> siervos Nuestros que poseían una gran fuerza para la guerra, y que penetraron en <em>las partes más recónditas de vuestras</em> casas; y fue una advertencia que estaba destinada a cumplirse." WHERE ayatNumber = 6 AND chapterNumber = 17;
UPDATE verses SET es = "A continuación os devolvimos el poder contra ellos, y os ayudamos con riqueza e hijos, aumentando vuestro número." WHERE ayatNumber = 7 AND chapterNumber = 17;
UPDATE verses SET es = "Si actuáis bien, lo haréis para beneficio de vuestras propias almas; pero si actuáis mal, lo haréis en perjuicio de ellas. Por ello, cuando llegue la hora prometida de los últimos días, intentarán causaros aflicción, y entrar en la mezquita como entraron en ella la primera vez: destruyendo todo cuanto habían conquistado con la más completa destrucción." WHERE ayatNumber = 8 AND chapterNumber = 17;
UPDATE verses SET es = "Tal vez vuestro Señor tenga <em>ahora</em> misericordia de vosotros; ero si volvéis a <em>vuestro estado anterior,</em> Nosotros volveremos <em>también,</em> y hemos hecho del Infierno una cárcel para los incrédulos." WHERE ayatNumber = 9 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad, este Corán guía hacia lo que es más recto; y comunica a los creyentes que realizan buenas obras la buena nueva de que tendrán una gran recompensa." WHERE ayatNumber = 10 AND chapterNumber = 17;
UPDATE verses SET es = "Para quienes no creen en lo que vendrá más tarde, hemos preparado un doloroso castigo." WHERE ayatNumber = 11 AND chapterNumber = 17;
UPDATE verses SET es = "Pero el hombre ruega por el mal como si estuviera pidiendo por el bien; y el hombre es impaciente." WHERE ayatNumber = 12 AND chapterNumber = 17;
UPDATE verses SET es = "Y hemos hecho que la noche y el día sean dos Signos, y extinguimos el Signo de la noche <em>reemplazándolo con el del día</em>, y al Signo del día lo hicimos luminoso, para que busquéis la magnanimidad de uestro Señor, y conozcáis el cálculo de los años y <em> la ciencia del</em> cómputo. Pues todo lo hemos explicado detalladamente." WHERE ayatNumber = 13 AND chapterNumber = 17;
UPDATE verses SET es = "Y hemos ajustado al cuello de cada hombre el recuento de sus acciones; y en el Día de la Resurrección sacaremos para él un libro que encontrará totalmente abierto." WHERE ayatNumber = 14 AND chapterNumber = 17;
UPDATE verses SET es = "“Lee tu libro. Hoy te bastará tu propia alma para juzgarte a ti mismo”." WHERE ayatNumber = 15 AND chapterNumber = 17;
UPDATE verses SET es = "Quien sigue el camino recto lo hace únicamente por <em> el bien de</em> su <em>propia</em> alma: y quien se extravía, se extravía únicamente para su <em>propia</em> pérdida. Y nadie que soporte una carga llevará la carga de otro. Nosotros nunca castigamos mientras no hayamos enviado un Mensajero." WHERE ayatNumber = 16 AND chapterNumber = 17;
UPDATE verses SET es = "Y cuando pensamos destruir una ciudad, permitimos que sus gentes más acomodadas <em>hagan lo que deseen</em>. Así, se entregan en ella a todo tipo de pecado, hasta que el decreto queda justificado que se le aplique, y entonces la destruimos completamente." WHERE ayatNumber = 17 AND chapterNumber = 17;
UPDATE verses SET es = "¡Cuántas generaciones hemos destruido después de Noé! Mas tu Señor basta como Conocedor y Observador de los pecados de Sus siervos." WHERE ayatNumber = 18 AND chapterNumber = 17;
UPDATE verses SET es = "A quien desea la presente vida, nos apresuraremos a darle en ella lo que deseamos -para <em>aquellos</em> que nos plazca-; luego le asignamos el Infierno; en él se quemará, condenado y rechazado." WHERE ayatNumber = 19 AND chapterNumber = 17;
UPDATE verses SET es = "Mas quien desea el Más Allá y se sacrifica por él como debe hacerse, y es creyente: éstos son aquellos cuyo sacrificio encontrará el favor <em>ante Dios.</em>" WHERE ayatNumber = 20 AND chapterNumber = 17;
UPDATE verses SET es = "Ayudamos a todos –tanto a éstos como a aquellos– como merced de tu Señor. Y el don de tu Señor no está limitado." WHERE ayatNumber = 21 AND chapterNumber = 17;
UPDATE verses SET es = "Observa cómo hemos exaltado a algunos de ellos sobre otros <em>en la vida presente;</em> pero en verdad, el Más Allá será mayor en rangos <em>elevados</em> y mayor en excelencia." WHERE ayatNumber = 22 AND chapterNumber = 17;
UPDATE verses SET es = "No encumbres a otro dios juntamente con Al-lah para que no te quedes sentado, despreciado <em>y</em> abandonado." WHERE ayatNumber = 23 AND chapterNumber = 17;
UPDATE verses SET es = "Tu Señor ha ordenado: “No adoréis a nadie sino a Él, <em>y mostrad </em>bondad a vuestros padres. Si uno o los dos alcanzan la ancianidad contigo, no les digas nunca ninguna palabra que exprese disgusto ni les reproches, mas bien dirígete a ellos con palabras amables." WHERE ayatNumber = 24 AND chapterNumber = 17;
UPDATE verses SET es = "Y haz descender sobre ellos el ala de la humildad y de la ternura. i: “Señor mío, ten misericordia de ellos al igual que ellos me criaron <em>en mi</em> niñez”." WHERE ayatNumber = 25 AND chapterNumber = 17;
UPDATE verses SET es = "Vuestro Señor conoce mejor que nadie lo que hay en vuestras mentes; si sois virtuosos, Él es en verdad el Sumo Indulgente para los que acuden <em>a Él </em>una y otra vez." WHERE ayatNumber = 26 AND chapterNumber = 17;
UPDATE verses SET es = "Y dale al pariente lo que se le debe, así como al menesteroso y al viajero, y no malgastes tus <em>bienes</em> con extravagancia." WHERE ayatNumber = 27 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad, los extravagantes son hermanos de los satanicos, y Satanás es desagradecido con su Señor." WHERE ayatNumber = 28 AND chapterNumber = 17;
UPDATE verses SET es = "Mas si has de apartarte de ellos buscando la misericordia que esperas de tu Señor, <em>incluso entonces</em> háblales con palabras amables." WHERE ayatNumber = 29 AND chapterNumber = 17;
UPDATE verses SET es = "Y no mantengas tu mano encadenada al cuello <em>por tacañería extrema, </em>ni la abras completamente con extravagancia; pues acabarás totalmente condenado <em>y</em> no servirá de nada." WHERE ayatNumber = 30 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad, tu Señor amplía Sus provisiones a quien Le place, y las escatima <em>a quien desea.</em> Ciertamente, Él conoce y ve perfectamente bien a Sus siervos." WHERE ayatNumber = 31 AND chapterNumber = 17;
UPDATE verses SET es = "No matéis a vuestros hijos por temor a la pobreza. Somos Nosotros Quienes proveemos para ellos y para vosotros. En verdad, su muerte es un grave pecado." WHERE ayatNumber = 32 AND chapterNumber = 17;
UPDATE verses SET es = "No os acerquéis al adulterio; en verdad, es una obscenidad y un mal camino." WHERE ayatNumber = 33 AND chapterNumber = 17;
UPDATE verses SET es = "No matéis al ser que Al-lah os ha prohibido salvo por causa justa. Y quien es matado injustamente, ciertamente hemos dado a su heredero autoridad <em>para exigir represalia,</em> pero sin que supere los límites <em>prescritos</em> al matar; pues <em>para ello</em> cuenta con la ayuda <em>de la ley.</em>" WHERE ayatNumber = 34 AND chapterNumber = 17;
UPDATE verses SET es = "Y no os acerquéis a los bienes del huérfano, excepto de la mejor manera posible, hasta que alcance la madurez; y cumplid el pacto; pues se pedirá cuenta del pacto." WHERE ayatNumber = 35 AND chapterNumber = 17;
UPDATE verses SET es = "Cuando midáis dad la medida completa y pesad con una balanza justa; esto es lo mejor y lo más recomendable en última instancia." WHERE ayatNumber = 36 AND chapterNumber = 17;
UPDATE verses SET es = "No sigas lo que no conoces. En verdad, el oído, el ojo y el corazón, serán todos llamados para dar cuenta." WHERE ayatNumber = 37 AND chapterNumber = 17;
UPDATE verses SET es = "No andes por la tierra con jactancia, porque <em>así</em> no puedes hendir la tierra ni igualar a las montañas en altura." WHERE ayatNumber = 38 AND chapterNumber = 17;
UPDATE verses SET es = "La maldad de todo esto es detestable a la Vista de tu Señor." WHERE ayatNumber = 39 AND chapterNumber = 17;
UPDATE verses SET es = "Esto es<em> parte</em> de esa sabiduría que te ha revelado tu Señor. No levantes pues ningún otro dios a la altura de Al-lah, para que no seas arrojado al Infierno, condenado <em>y </em>rechazado." WHERE ayatNumber = 40 AND chapterNumber = 17;
UPDATE verses SET es = "¿Os ha distinguido, pues, vuestro Señor y ha tomado para Él hembras de entre los ángeles? En verdad, decís algo terrible." WHERE ayatNumber = 41 AND chapterNumber = 17;
UPDATE verses SET es = "Hemos explicado <em>la verdad</em> en este Corán de diversas maneras para que sean advertidos, pero esto no hace más que aumentar su aversión." WHERE ayatNumber = 42 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Si hubiesen existido otros dioses con Él, como dicen ellos (los idólatras) habrían ciertamente buscado un camino para llegar al Soberano del Trono”." WHERE ayatNumber = 43 AND chapterNumber = 17;
UPDATE verses SET es = "¡Santo es Él y Exaltado muy por encima de lo que dicen!" WHERE ayatNumber = 44 AND chapterNumber = 17;
UPDATE verses SET es = "Los siete cielos, la tierra y los que en ella están, ensalzan Su gloria; y no hay ni una sola cosa que no Lo glorifique con Su alabanza; mas vosotros no comprendéis su glorificación. En verdad, Él es Tolerante, el Sumo Indulgente." WHERE ayatNumber = 45 AND chapterNumber = 17;
UPDATE verses SET es = "Cuando recitas el Corán, interponemos un velo oculto entre ti y aquellos que no creen en el Más Allá;" WHERE ayatNumber = 46 AND chapterNumber = 17;
UPDATE verses SET es = "Y colocamos cubiertas sobre sus corazones, de manera que no lo comprenden, y provocamos la sordera en sus oídos. Y cuando mencionas en el Corán a tu Señor el Único, vuelven las espaldas desdeñosamente." WHERE ayatNumber = 47 AND chapterNumber = 17;
UPDATE verses SET es = "Sabemos muy bien lo que escuchan, cuando te oyen y cuando charlan en privado, cuando dicen los inicuos: “No seguís más que a un hombre víctima del engaño”." WHERE ayatNumber = 48 AND chapterNumber = 17;
UPDATE verses SET es = "Mira cómo inventan semejanzas para ti, mas de este modo se han extraviado, por lo que no podrán encontrar el camino." WHERE ayatNumber = 49 AND chapterNumber = 17;
UPDATE verses SET es = "Pues dicen: “Cuando nos convirtamos en huesos y partículas, ¿seremos realmente resucitados como <em>nueva</em> criatura?”." WHERE ayatNumber = 50 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Aunque fueseis piedras o hierro”," WHERE ayatNumber = 51 AND chapterNumber = 17;
UPDATE verses SET es = "“O materia creada de <em>cualquier tipo</em> de lo que parezca más ifícil para vuestras mentes, <em>incluso entonces seréis resucitados”.</em> Entonces preguntarán: “¿quién nos devolverá la vida?”. Diles: “Quien os creó la primera vez”. Entonces moverán sus cabezas hacia ti y dirán: “¿cuándo será eso?”. Diles: “Tal vez esté cerca”," WHERE ayatNumber = 52 AND chapterNumber = 17;
UPDATE verses SET es = "“<em>Será</em> en el día en el que Él os llame; entonces responderéis, glorificándole y pensaréis que sólo os habéis detenido poco tiempo”." WHERE ayatNumber = 53 AND chapterNumber = 17;
UPDATE verses SET es = "Di a Mis siervos que deben hablar lo mejor. En verdad, Satanás siembra la discordia entre ellos. Ciertamente Satanás es un enemigo declarado del hombre." WHERE ayatNumber = 54 AND chapterNumber = 17;
UPDATE verses SET es = "Vuestro Señor os conoce muy bien. Si Le place, tendrá misericordia de vosotros; o si quiere, os castigará. Pero no te hemos enviado como guardián de ellos." WHERE ayatNumber = 55 AND chapterNumber = 17;
UPDATE verses SET es = "Y tu Señor conoce muy bien a quienes están en los cielos y en la tierra. Hemos exaltado a algunos de los Profetas sobre los otros, y entregamos a David los Zabur." WHERE ayatNumber = 56 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Invocad a quienes pensáis<em> que son dioses</em> en lugar de Él; entonces <em>sabréis</em> <em>que</em> no tienen poder para libraros de la adversidad ni apartarla de vosotros." WHERE ayatNumber = 57 AND chapterNumber = 17;
UPDATE verses SET es = "Aquellos a quienes invocan buscan <em>ellos</em> <em>mismos</em> la cercanía a su Señor –i<em>ncluso</em> los que están más cerca– y esperan Su misericordia y temen Su castigo. En verdad, el castigo de tu Señor es una cosa temible." WHERE ayatNumber = 58 AND chapterNumber = 17;
UPDATE verses SET es = "No existe ciudad alguna a la que no destruyamos antes del Día de la Resurrección, o a la que castiguemos con un severo castigo. Esto está escrito en el Libro." WHERE ayatNumber = 59 AND chapterNumber = 17;
UPDATE verses SET es = "Y nada pudo impedirnos el envío de los Signos, salvo que los ueblos anteriores los rechazaron, <em>aunque esto no constituyó un obstáculo</em>. Y dimos la camella a los Zamud como Signo claro, pero ellos la trataron injustamente. Y sólo enviamos los Signos para amonestarlos." WHERE ayatNumber = 60 AND chapterNumber = 17;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando te dijimos: “En verdad, tu Señor ha rodeado a las gentes”. La visión que te mostramos sólo fue una prueba para los hombres, como también <em>lo fue </em>el árbol maldito en el Corán. Y les exhortamos, pero eso no hace más que acrecentar en ellos su gran rebeldía." WHERE ayatNumber = 61 AND chapterNumber = 17;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando dijimos a los ángeles: “Someteos a Adán”, y <em>todos</em> ellos se sometieron, excepto Iblis. Dijo: “¿Acaso debo someterme a quien creaste de barro?”." WHERE ayatNumber = 62 AND chapterNumber = 17;
UPDATE verses SET es = "<em> Y</em> dijo: “¿Qué piensas Tú? Éste es aquel a quien preferiste sobre mí. Si me concedes un respiro hasta el Día de la Resurrección, ciertamente pondré a sus descendientes bajo mi dominio, excepto a unos pocos”." WHERE ayatNumber = 63 AND chapterNumber = 17;
UPDATE verses SET es = "Dijo: “¡Vete!: tú y quienes te sigan de entre ellos; el Infierno será en verdad la recompensa de todos vosotros, una abundante recompensa”;" WHERE ayatNumber = 64 AND chapterNumber = 17;
UPDATE verses SET es = "“Y seduce a quienes puedas de ellos con tu voz, y lanza contra ellos tus caballos y tus hombres y asóciate con ellos en la riqueza y en los hijos, y hazles promesas”. Mas Satanás no promete sino el engaño." WHERE ayatNumber = 65 AND chapterNumber = 17;
UPDATE verses SET es = "<em> En cuanto a</em> Mis siervos, ciertamente no tendrás poder sobre ellos, pues te basta tu Señor como Guardián." WHERE ayatNumber = 66 AND chapterNumber = 17;
UPDATE verses SET es = "Vuestro Señor es Quien os conduce los buques por el mar, para que busquéis de Su magnanimidad. En verdad, Él es Misericordioso con vosotros." WHERE ayatNumber = 67 AND chapterNumber = 17;
UPDATE verses SET es = "Y cuando os azota la adversidad en el mar, <em>todos</em> aquellos a quienes invocáis, excepto Él, desaparecen <em>para vosotros</em>. Pero cuando Él os lleva seguros a tierra, os apartáis; pues el hombre es muy desagradecido." WHERE ayatNumber = 68 AND chapterNumber = 17;
UPDATE verses SET es = "¿Estáis seguros de que Él<em> no</em> hará que os hundáis cerca de la tierra o enviará contra vosotros una violenta tormenta de arena, sin que entonces podáis encontrar protector?" WHERE ayatNumber = 69 AND chapterNumber = 17;
UPDATE verses SET es = "¿O estáis seguros de que no os devolverá allí una segunda vez, enviando entonces contra vosotros un huracán que os haga naufragar por vuestra incredulidad? Allí no encontraréis entonces quien os socorra contra Nosotros." WHERE ayatNumber = 70 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad, hemos honrado a los hijos de Adán, llevándolos por tierra y por mar; les dimos cosas buenas y los ensalzamos muy por encima de muchos de los que hemos creado." WHERE ayatNumber = 71 AND chapterNumber = 17;
UPDATE verses SET es = "<em> Acuérdate</em> del día en el que convoquemos a cada hombre con su Jefe. Entonces, a quienes se les entregue el libro en su mano derecha ésos leerán su libro y no serán defraudados en lo más mínimo." WHERE ayatNumber = 72 AND chapterNumber = 17;
UPDATE verses SET es = "Pero quien sea ciego en este mundo, será ciego en el Más Allá, incluso más extraviado del camino." WHERE ayatNumber = 73 AND chapterNumber = 17;
UPDATE verses SET es = "Mas estuvieron a punto de causarte <em>la más grave</em> aflicción en razón de lo que Te revelamos, para que inventaras contra Nosotros algo distinto a eso; y ciertamente entonces te habrían considerado un amigo<em> especial.</em>" WHERE ayatNumber = 74 AND chapterNumber = 17;
UPDATE verses SET es = "Mas si no te hubiésemos fortalecido <em>con el Corán,</em> te habrías inclinado un poco hacia ellos." WHERE ayatNumber = 75 AND chapterNumber = 17;
UPDATE verses SET es = "En ese caso, te habríamos hecho probar <em>aflicciones</em> imilares en la vida y <em>aflicciones</em> similares en la muerte, <em>y </em>no habrías encontrado para ti socorredor alguno contra Nosotros." WHERE ayatNumber = 76 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad, están a punto de moverte de la tierra para poder expulsarte de ella; pero en ese caso, <em>ellos mismos</em> no habrían permanecido después de ti excepto por poco tiempo." WHERE ayatNumber = 77 AND chapterNumber = 17;
UPDATE verses SET es = "<em> Ésta ha sido Nuestra</em> actitud con Nuestros Mensajeros a quienes enviamos antes de ti, y no encontrarás cambio alguno en Nuestra actitud." WHERE ayatNumber = 78 AND chapterNumber = 17;
UPDATE verses SET es = "Cumple la Oración al declinar el sol y hasta la oscuridad de la oche, y recita <em>el Corán</em> en el alba. En verdad, la recitación<em>del Corán</em> al alba <em>es especialmente</em> aceptable <em>para Dios</em>." WHERE ayatNumber = 79 AND chapterNumber = 17;
UPDATE verses SET es = "Y despiértate para recitarlo (el Corán) <em>durante la oración de Tahayyud</em> de la noche como servicio supererogatorio tuyo. Podría ser que tu Señor te elevara a un puesto loable." WHERE ayatNumber = 80 AND chapterNumber = 17;
UPDATE verses SET es = "Y di: “Oh mi Señor, haz que mi entrada sea una entrada buena y mi salida una salida buena. Y concédeme, de Ti <em>mismo</em>, un poder inquebrantable”." WHERE ayatNumber = 81 AND chapterNumber = 17;
UPDATE verses SET es = "Di <em>también:</em> “Ha llegado la Verdad y se ha desvanecido la falsedad. En verdad, la falsedad se desvanece <em>rápidamente”</em>." WHERE ayatNumber = 82 AND chapterNumber = 17;
UPDATE verses SET es = "Y estamos revelando <em>gradualmente</em> el Corán, que es curación y misericordia para los creyentes; aunque no hace más que aumentar la pérdida de los inicuos." WHERE ayatNumber = 83 AND chapterNumber = 17;
UPDATE verses SET es = "Cuando concedemos favores al hombre, se aparta y nos da de lado; mas cuando lo azota la adversidad, se entrega a la desesperación." WHERE ayatNumber = 84 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Cada cual actúa según su propia forma, y tu Señor sabe muy bien quién está rectamente guiado”." WHERE ayatNumber = 85 AND chapterNumber = 17;
UPDATE verses SET es = "Te preguntan acerca del alma. Diles: “El alma es por mandato de mi Señor; y de <em>su</em> conocimiento se os ha dado una pequeña parte”." WHERE ayatNumber = 86 AND chapterNumber = 17;
UPDATE verses SET es = "Si lo hubiésemos querido, ciertamente hubiéramos podido retirarte o que te hemos revelado <em>y </em>no encontrarías <em>al respecto</em> guardián alguno que te protegiera contra Nosotros," WHERE ayatNumber = 87 AND chapterNumber = 17;
UPDATE verses SET es = "Salvo la misericordia de tu Señor. En verdad, Su gracia para contigo es grande." WHERE ayatNumber = 88 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Si la humanidad y los Yinn se reunieran para producir algo semejante a este Corán, jamás harían nada parecido, aunque se ayudaran mutuamente”." WHERE ayatNumber = 89 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad, hemos presentado a la humanidad, de diversas maneras, toda clase de ejemplos en este Corán, pero la mayoría de los hombres rechazan todo excepto la incredulidad." WHERE ayatNumber = 90 AND chapterNumber = 17;
UPDATE verses SET es = "Dicen: “Nunca creeremos en ti hasta que no nos hagas brotar un manantial de la tierra”;" WHERE ayatNumber = 91 AND chapterNumber = 17;
UPDATE verses SET es = "“O a menos que poseas un jardín de palmeras y vid y hagas que broten corrientes abundantes de agua en mitad del mismo”;" WHERE ayatNumber = 92 AND chapterNumber = 17;
UPDATE verses SET es = "“O a menos que hagas que el cielo caiga sobre nosotros en pedazos, como has afirmado, o que traigas a Al-lah y a los ángeles cara a cara ante nosotros”;" WHERE ayatNumber = 93 AND chapterNumber = 17;
UPDATE verses SET es = "“O poseas una casa de oro o subas a los cielos; y no creeremos en tu ascensión hasta que no nos envíes un libro que podamos leer”. Diles: “¡Santo es mi Señor! No soy sino un hombre <em>enviado como</em> Mensajero”." WHERE ayatNumber = 94 AND chapterNumber = 17;
UPDATE verses SET es = "Y nada ha impedido a los hombres creer cuando les ha llegado la uía, excepto que dijeron: “¿Ha enviado Al-lah a un hombre <em>como</em> Mensajero?”." WHERE ayatNumber = 95 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Si hubiera en la tierra ángeles que anduvieran en paz y ranquilos, ciertamente les hubiéramos enviado un ángel del cielo <em>como</em> Mensajero”." WHERE ayatNumber = 96 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Me basta con Al-lah como Testigo entre vosotros y yo; en verdad, Él ve y conoce perfectamente a Sus siervos”." WHERE ayatNumber = 97 AND chapterNumber = 17;
UPDATE verses SET es = "Y a quien Al-lah guía es el <em>único</em> correctamente guiado; en cambio, <em>para</em> quienes permanecen perdidos a la vista de Al-lah, no encontrarás socorredor para ellos fuera de Él. Y en el Día de la Resurrección, los congregaremos sobre sus rostros, ciegos, mudos y sordos. Su morada será el Infierno; cada vez que se extinga, avivaremos para ellos la llama." WHERE ayatNumber = 98 AND chapterNumber = 17;
UPDATE verses SET es = "Ésa es su recompensa, por haber rechazado Nuestros Signos y haber dicho: “¡Cómo! Cuando quedemos reducidos a huesos y partículas rotas, ¿seremos realmente resucitados como una nueva criatura?" WHERE ayatNumber = 99 AND chapterNumber = 17;
UPDATE verses SET es = "¿Acaso no ven que Al-lah, que creó los cielos y la tierra, tiene poder para crear semejantes a ellos? Él les ha fijado un plazo; sobre esto no hay duda alguna. Pero los inicuos lo rechazan todo excepto la incredulidad." WHERE ayatNumber = 100 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Aunque poseyerais los tesoros de la misericordia de mi Señor, ciertamente los guardaríais por temor a gastarlos, ya que el hombre es avariento”." WHERE ayatNumber = 101 AND chapterNumber = 17;
UPDATE verses SET es = "En verdad dimos a Moisés nueve Signos manifiestos. Preguntad <em>pues</em> a los hijos de Israel. Cuando vino a ellos, el Faraón le dijo: “Creo, Oh Moisés, que eres víctima del engaño”." WHERE ayatNumber = 102 AND chapterNumber = 17;
UPDATE verses SET es = "Él respondió: “Tú sabes bien <em>que </em>nadie ha revelado estos<em>Signos</em> salvo el Señor de los cielos y la tierra para hacer <em>te</em> ver la luz; y en verdad pienso que tú, Oh Faraón, eres un hombre perdido”." WHERE ayatNumber = 103 AND chapterNumber = 17;
UPDATE verses SET es = "Así pues, decidió expulsarlos del país, pero <em>Nosotros</em> lo ahogamos, a él y a cuantos estaban con él, todos juntos." WHERE ayatNumber = 104 AND chapterNumber = 17;
UPDATE verses SET es = "Y después de él, dijimos a los hijos de Israel: “Habitad la tierra; y cuando llegue <em>la hora de</em> la promesa de los últimos días, os reuniremos una vez más." WHERE ayatNumber = 105 AND chapterNumber = 17;
UPDATE verses SET es = "Pues con verdad lo hemos revelado y descendió con la verdad. Y sólo te hemos enviado como portador de la buena nueva, y como Amonestador." WHERE ayatNumber = 106 AND chapterNumber = 17;
UPDATE verses SET es = "Y dividimos al Corán en partes para que puedas leerlo a la humanidad a intervalos, y lo revelamos gradualmente." WHERE ayatNumber = 107 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Creáis o no en él, a quienes se les ha dado el conocimiento antes de él, caen postrados sobre sus rostros cuando se les recita”." WHERE ayatNumber = 108 AND chapterNumber = 17;
UPDATE verses SET es = "Y dicen: “Santo es nuestro Señor. En verdad, la promesa de nuestro Señor debe cumplirse”." WHERE ayatNumber = 109 AND chapterNumber = 17;
UPDATE verses SET es = "Y caen de bruces llorando, y esto aumenta su humildad." WHERE ayatNumber = 110 AND chapterNumber = 17;
UPDATE verses SET es = "Diles: “Invocad a Al-lah o invocad a Al-Rahman<em>; </em> cualquiera que sea el nombre con el que <em>Le</em> oréis, los Suyos son los nombres más sublimes”. Y no pronuncies tu oración en voz alta, ni la musites <em>demasiado</em> bajo, más bien procura un tono intermedio." WHERE ayatNumber = 111 AND chapterNumber = 17;
UPDATE verses SET es = "Y di: “Toda alabanza corresponde a Al-lah, que no ha tomado hijo alguno para Sí, que no tiene socio alguno en <em>Su</em> Reino, ni tiene a nadie que <em>Le</em> ayude por debilidad”. Y ensalzad Su Gloria con toda alabanza." WHERE ayatNumber = 112 AND chapterNumber = 17;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 18;
UPDATE verses SET es = "Toda alabanza corresponde a Al-lah, Quien ha revelado el Libro a Su siervo, y no ha utilizado tergiversación alguna en su creación." WHERE ayatNumber = 2 AND chapterNumber = 18;
UPDATE verses SET es = "<em> Él lo ha convertido</em> en guardián, para advertir de un doloroso castigo Suyo, y para dar a los creyentes que hacen buenas obras, la buena noticia de que tendrán una maravillosa recompensa," WHERE ayatNumber = 3 AND chapterNumber = 18;
UPDATE verses SET es = "En la que morarán para siempre;" WHERE ayatNumber = 4 AND chapterNumber = 18;
UPDATE verses SET es = "Y para advertir a quienes dicen: “Al-lah ha tomado para Sí un hijo”." WHERE ayatNumber = 5 AND chapterNumber = 18;
UPDATE verses SET es = "No tienen el menor conocimiento de ello, ni <em>lo tuvieron</em> sus padres. Es una maldad la palabra que sale de sus bocas. No dicen más que mentiras." WHERE ayatNumber = 6 AND chapterNumber = 18;
UPDATE verses SET es = "Es posible que te aflijas hasta la muerte por la pena que sientes por ellos si no creen en este mensaje." WHERE ayatNumber = 7 AND chapterNumber = 18;
UPDATE verses SET es = "En verdad, hemos hecho todo lo que hay en la tierra como adorno suyo, para probarlos y comprobar cuáles de ellos se comportan mejor." WHERE ayatNumber = 8 AND chapterNumber = 18;
UPDATE verses SET es = "Mas convertiremos todo cuanto existe en ella en terreno árido." WHERE ayatNumber = 9 AND chapterNumber = 18;
UPDATE verses SET es = "¿Crees que el Pueblo de la Caverna y la Inscripción eran una maravilla entre Nuestros Signos?" WHERE ayatNumber = 10 AND chapterNumber = 18;
UPDATE verses SET es = "Cuando los jóvenes buscaron refugio en la Caverna diciendo: “Señor Nuestro, concédenos Tu misericordia, y guíanos rectamente en nuestro asunto”." WHERE ayatNumber = 11 AND chapterNumber = 18;
UPDATE verses SET es = "De ese modo les privamos de escuchar <em>las noticias del mundo exterior</em> durante una serie de años." WHERE ayatNumber = 12 AND chapterNumber = 18;
UPDATE verses SET es = "Más tarde les despertamos para saber cuál de los dos grupos conservaba un cálculo mejor del tiempo durante el cual habían permanecido." WHERE ayatNumber = 13 AND chapterNumber = 18;
UPDATE verses SET es = "Te relataremos su historia con veracidad: Eran jóvenes que creían en su Señor por lo que les aumentamos su guía." WHERE ayatNumber = 14 AND chapterNumber = 18;
UPDATE verses SET es = "Y fortalecimos sus corazones cuando se levantaron y dijeron: “Nuestro Señor es el Señor de los cielos y la tierra. Nunca invocaremos a dios alguno en lugar de Él; <em>si lo hiciéramos</em>, habríamos proferido en verdad una atrocidad”;" WHERE ayatNumber = 15 AND chapterNumber = 18;
UPDATE verses SET es = "“Éstos, nuestras gentes, han tomado <em>para adorar a otros</em> dioses en lugar de Él. ¿Por qué no presentan entonces una prueba evidente de ellos? Pues ¿quién es más injusto que quien inventa una mentira sobre Al-lah?" WHERE ayatNumber = 16 AND chapterNumber = 18;
UPDATE verses SET es = "“Y <em>ahora,</em> cuando os habéis retirado de ellos y de aquello a lo que adoran en lugar de Al-lah, buscad refugio en la Caverna; vuestro Señor extenderá sobre vosotros Su misericordia y os proporcionará consuelo en <em>este</em> asunto vuestro”." WHERE ayatNumber = 17 AND chapterNumber = 18;
UPDATE verses SET es = "Pudisteis ver el sol, cuando subió, retirarse de su Caverna a la derecha, y cuando se puso, pasar sobre ellos a su izquierda; y ellos se encontraban en el amplio espacio intermedio. Éste es uno de los Signos de Al-lah. A quien Al-lah guía, está encaminado correctamente; pero a quien Él declara extraviado, no encontrarás quien le socorra ni le guíe." WHERE ayatNumber = 18 AND chapterNumber = 18;
UPDATE verses SET es = "Podrías considerarlos despiertos, cuando están dormidos; pero Nosotros haremos que se muevan a la derecha y a la izquierda; con su perro estirando sus patas delanteras en el umbral. Si los hubieras contemplado, ciertamente te habrías alejado de ellos atemorizado, y en verdad te hubieras sentido aterrado ante ellos." WHERE ayatNumber = 19 AND chapterNumber = 18;
UPDATE verses SET es = "Y así los despertamos para que pudieran interrogarse unos a otros. Uno de ellos dijo: “¿Cuánto tiempo habéis permanecido?”. Contestaron: “Estuvimos un día o parte de un día”. <em>Otros</em> dijeron. “Vuestro Señor sabe muy bien <em>el tiempo</em> que habéis permanecido. Enviad ahora a uno de vosotros a la ciudad con estas vuestras monedas de plata; y que vea cuál de sus <em>moradores</em> tiene la comida más pura, y que os traiga provisiones de ella. Y que sea amable y que no informe a nadie sobre vosotros”." WHERE ayatNumber = 20 AND chapterNumber = 18;
UPDATE verses SET es = "“Porque si se os impusieran, os lapidarían u os harían volver a su religión, y entonces nunca prosperaríais”." WHERE ayatNumber = 21 AND chapterNumber = 18;
UPDATE verses SET es = "Y de este modo los descubrimos<em> a las gentes</em> para que conocieran que la promesa de Al-lah era verdadera y que, en cuanto a la Hora, no había duda alguna respecto a ella. <em>Y acordaos de</em> uando las gentes discutieron entre sí respecto a ellos y <em>algunos</em> dijeron: “Edificad sobre ellos un edificio”. Su Señor los conocía perfectamente. Aquellos cuya opinión prevaleció dijeron: “En verdad, edificaremos un lugar de oración sobre ellos”." WHERE ayatNumber = 22 AND chapterNumber = 18;
UPDATE verses SET es = "<em> Algunos</em> dicen: “Eran tres, el cuarto era su perro” y <em>otros</em> dicen: “Eran cinco, el sexto era su perro”, conjeturando al azar. Y <em>algunos otros dicen:</em> “Eran siete, el octavo era su perro”. Diles: “Mi Señor conoce perfectamente cuál era su número real. Nadie los conoce salvo unos pocos”. No discutáis, pues, respecto a ellos, salvo que se trate de una conversación casual, ni busquéis información sobre los mismos de ninguno de ellos." WHERE ayatNumber = 23 AND chapterNumber = 18;
UPDATE verses SET es = "No digas nunca de ninguna cosa: “La haré mañana”," WHERE ayatNumber = 24 AND chapterNumber = 18;
UPDATE verses SET es = "A menos que Al-lah lo quiera. Recuerda a tu Señor cuando olvides y di: “Espero que mi Señor me guíe a lo que está <em>aún</em> más cerca del camino recto que esto”." WHERE ayatNumber = 25 AND chapterNumber = 18;
UPDATE verses SET es = "Mas permanecieron en su Caverna trescientos años, y <em>a eso</em> añadieron nueve." WHERE ayatNumber = 26 AND chapterNumber = 18;
UPDATE verses SET es = "Diles: “Al-lah sabe perfecta­mente cuánto tiempo permanecieron allí”. A Él pertenecen lo invisible de los cielos y la tierra. ¡Qué Vista y qué Oído tiene! No tienen amigos aparte de Él, y Él no permite que nadie comparta Su autoridad." WHERE ayatNumber = 27 AND chapterNumber = 18;
UPDATE verses SET es = "“Recita lo que te ha sido revelado del Libro de tu Señor. No hay nadie que pueda cambiar Sus palabras, y no hallarás refugio fuera de Él”." WHERE ayatNumber = 28 AND chapterNumber = 18;
UPDATE verses SET es = "Mantente unido a quienes invocan a su Señor, mañana y tarde, buscando Su agrado; no permitas que tus ojos se aparten de ellos, buscando las galas de la vida del mundo; y no obedezcas a aquel cuyo corazón lo hemos hecho distraído de Nuestro recuerdo ni a quien sigue sus inclinaciones malvadas, superando en su conducta todos los límites." WHERE ayatNumber = 29 AND chapterNumber = 18;
UPDATE verses SET es = "Diles: <em>“Es</em> la verdad de vuestro Señor; por tanto, el que quiera creer, que crea, y el que no quiera creer, no crea”. En verdad, ara los malvados hemos preparado un fuego cuyo dosel <em> llameante</em> los rodeará. Y si gritan pidiendo ayuda, recibirán agua como plomo fundido que les quemará los rostros. ¡Qué horrible bebida y qué espantoso es el <em>Fuego como</em> lugar de reposo!" WHERE ayatNumber = 30 AND chapterNumber = 18;
UPDATE verses SET es = "En verdad, para quienes creen y hacen buenas obras, <em>sabed </em> ciertamente que no permitiremos que se pierda la recompensa de quienes hacen buenas obras." WHERE ayatNumber = 31 AND chapterNumber = 18;
UPDATE verses SET es = "Son ellos quienes tendrán Jardines de Eternidad por los cuales correrán ríos. Serán adornados allí con brazaletes de oro, y llevarán vestidos verdes de fina seda y grueso brocado, recostándose allí en divanes elevados. ¡Qué buena recompensa y qué excelente lugar de reposo!" WHERE ayatNumber = 32 AND chapterNumber = 18;
UPDATE verses SET es = "Preséntales la parábola de dos hombres: a uno de ellos le proporcionamos dos huertos de vid y, rodeándolos, palmeras, y entre ellos pusimos campos de cereales." WHERE ayatNumber = 33 AND chapterNumber = 18;
UPDATE verses SET es = "Ambos huertos produjeron su fruto <em>en abundancia</em>, sin fallar nada en ellos. Y entre ellos hicimos que fluyera un río." WHERE ayatNumber = 34 AND chapterNumber = 18;
UPDATE verses SET es = "Tuvo frutos <em>en abundancia.</em> Y dijo a su compañero, cuando conversaba con él: “soy más rico que tú en riquezas y más fuerte en seguidores”." WHERE ayatNumber = 35 AND chapterNumber = 18;
UPDATE verses SET es = "Y entró en su huerto mientras perjudicaba a su alma. Dijo: “No creo que esto desaparezca nunca”;" WHERE ayatNumber = 36 AND chapterNumber = 18;
UPDATE verses SET es = "“Y no creo que llegue<em> nunca</em> la Hora. Y aunque fuese alguna vez devuelto a mi Señor, ciertamente encontraré un lugar mejor que éste”." WHERE ayatNumber = 37 AND chapterNumber = 18;
UPDATE verses SET es = "Mientras conversaba con él, su compañero le dijo: “¿Acaso no crees en Aquel que te creó del polvo, después de una gota de semen y por último te modeló en un hombre <em>perfecto?”</em>." WHERE ayatNumber = 38 AND chapterNumber = 18;
UPDATE verses SET es = "<em> “En cuanto a mí, creo que</em> Al-lah sólo es mi Señor, y no asociaré a nadie a mi Señor”;" WHERE ayatNumber = 39 AND chapterNumber = 18;
UPDATE verses SET es = "“¿Y por qué no dijiste cuando entraste en tu huerto: “Que se haga la voluntad de Al-lah; no hay poder excepto en Al-lah?”, si me ves menor a ti en riqueza y descendencia." WHERE ayatNumber = 40 AND chapterNumber = 18;
UPDATE verses SET es = "“Es posible que mi Señor me dé algo mejor que tu huerto, y envíe sobre él (tu huerto) un rayo del cielo que lo convierta en un terreno estéril y resbaladizo”;" WHERE ayatNumber = 41 AND chapterNumber = 18;
UPDATE verses SET es = "“O que su agua se hunda<em> en la tierra</em> hasta el punto de que no puedas encontrarla”." WHERE ayatNumber = 42 AND chapterNumber = 18;
UPDATE verses SET es = "Y sus frutos quedaron <em>realmente</em> destruidos, y empezó a retorcerse las manos por lo que había gastado en él, pues<em> todo</em> él se había derrumbado sobre sus emparrados. Y dijo: “¡Ojalá no hubiera asociado a nadie a mi Señor!”." WHERE ayatNumber = 43 AND chapterNumber = 18;
UPDATE verses SET es = "Y no hubo nadie que lo ayudara con­tra Al-lah ni pudo defenderse a sí mismo." WHERE ayatNumber = 44 AND chapterNumber = 18;
UPDATE verses SET es = "En esos momentos el apoyo <em>sólo viene</em> de Al-lah, el Verdadero. Él es el Mejor en cuanto a recompensa y el Mejor en cuanto a consecuencias." WHERE ayatNumber = 45 AND chapterNumber = 18;
UPDATE verses SET es = "Preséntales el ejemplo de la vida de este mundo: es como el agua que enviamos del cielo, la vegetación de la tierra se mezcla con ella y después se convierte en hierba seca rota en trozos que desparraman los vientos. Y Al-lah tiene poder sobre todas las cosas." WHERE ayatNumber = 46 AND chapterNumber = 18;
UPDATE verses SET es = "La riqueza y los hijos son las galas de la vida de este mundo. Pero la perseverancia en las buenas obras es mejor, a la vista de tu Señor, en cuanto a la recompensa <em>inmediata</em> y mejor en cuanto a la esperanza <em>futura.</em>" WHERE ayatNumber = 47 AND chapterNumber = 18;
UPDATE verses SET es = "Y <em>reflexiona en</em> el día en que derrumbaremos las montañas y erás a las <em>naciones de la</em> tierra marchar <em> unas contra otras;</em> y las reuniremos a todas sin dejar a ninguna de ellas detrás." WHERE ayatNumber = 48 AND chapterNumber = 18;
UPDATE verses SET es = "Serán presentadas a tu Señor, <em>dispuestas</em> en filas: “Ahora comparecéis ante Nosotros como os creamos al principio. Aunque ensasteis que no fijaríamos un plazo para el cumplimiento de <em>Nuestra</em> promesa <em>hecha </em>a vosotros”." WHERE ayatNumber = 49 AND chapterNumber = 18;
UPDATE verses SET es = "Y se colocará el Libro <em>ante ellos,</em> y verás a los culpables atemorizados por su contenido; y dirán: “¡Ay de nosotros! ¿Qué clase de Libro es éste? No omite nada, ni grande ni pequeño, sino que lo registra todo”. Y encontrarán ante <em>ellos</em> cuanto hicieron, pues tu Señor no perjudica a nadie." WHERE ayatNumber = 50 AND chapterNumber = 18;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando dijimos a los ángeles: “Someteos a dán” y <em>todos</em> ellos se sometieron, excepto Iblis. Era <em>uno</em> de los Yinn; y desobedeció la orden de su Señor. ¿Tomaréis acaso a él y a su progenie como amigos en lugar de a Mí, a pesar de que son vuestros enemigos? Malo es el intercambio para los inicuos." WHERE ayatNumber = 51 AND chapterNumber = 18;
UPDATE verses SET es = "No los hice testigos de la creación de los cielos y de la tierra, ni de su propia creación; ni podría tomar como ayudantes a quienes extravían <em>a las gentes.</em>" WHERE ayatNumber = 52 AND chapterNumber = 18;
UPDATE verses SET es = "Y <em>acuérdate</em> del día en que Él dirá: “Llamad a quienes considerabais Mis socios”. Entonces ellos les invocarán, pero no recibirán respuesta; y colocaremos una barrera entre ellos." WHERE ayatNumber = 53 AND chapterNumber = 18;
UPDATE verses SET es = "Los culpables verán el Fuego y comprenderán que van a caer a él; y no encontrarán modo alguno de escapar de él." WHERE ayatNumber = 54 AND chapterNumber = 18;
UPDATE verses SET es = "En verdad, hemos explicado de diversas formas en este Corán, para <em>el bien de</em> la humanidad, todo<em> tipo de</em> ejemplos, pero de todas las cosas, el hombre es el más pendenciero." WHERE ayatNumber = 55 AND chapterNumber = 18;
UPDATE verses SET es = "Mas nada impidió a las gentes creer y pedir perdón a su Señor, cuando les llegó la guía, pero eligieron seguir el camino de los pueblos <em>anteriores con las mismas consecuencias</em> o esperaron que el castigo <em>de Al-lah</em> les llegara abiertamente." WHERE ayatNumber = 56 AND chapterNumber = 18;
UPDATE verses SET es = "Mas sólo enviamos a los Mensajeros como portadores de la buena nueva y como Amonestadores. Y quienes no creen argumentan con la falsedad a fin de refutar con ella la verdad. Y toman Mis Signos y las advertencias que hay en ellos,<em> únicamente</em> como una broma." WHERE ayatNumber = 57 AND chapterNumber = 18;
UPDATE verses SET es = "¿Habrá alguien más injusto que aquel a quien se le recuerdan los Signos de su Señor, y se aparta de ellos, olvidando lo que sus manos han cometido antes? En verdad, hemos puesto unos velos sobre sus corazones para que no lo entiendan, y sordera en sus oídos. Y si los invitas a la guía, nunca la aceptarán." WHERE ayatNumber = 58 AND chapterNumber = 18;
UPDATE verses SET es = "Mas tu Señor es el Sumo Indulgente, lleno de Misericordia. Si tuviera que atraparlos por lo que cometieron, ciertamente habría apresurado su castigo. Pero tienen un plazo fijado contra el que no encontrarán refugio alguno." WHERE ayatNumber = 59 AND chapterNumber = 18;
UPDATE verses SET es = "En cuanto a estas ciudades, las destruimos cuando cometieron iniquidades. Y fijamos un plazo para su destrucción." WHERE ayatNumber = 60 AND chapterNumber = 18;
UPDATE verses SET es = "Y <em>acuérdate de</em> cuando Moisés dijo a su joven <em>compañero:</em> “No me detendré hasta que no llegue a la confluencia de los dos mares, aunque tenga que andar años y años”." WHERE ayatNumber = 61 AND chapterNumber = 18;
UPDATE verses SET es = "Pero cuando llegaron a la confluencia de los dos <em>mares</em>, lvidaron su pescado, y éste emprendió rápidamente su camino al mar, <em>alejándose.</em>" WHERE ayatNumber = 62 AND chapterNumber = 18;
UPDATE verses SET es = "Y cuando hubieron avanzado aún más, dijo a su joven <em>compañero:</em> “Tráenos nuestro almuerzo. En verdad, hemos sufrido muchas fatigas a consecuencia de este nuestro viaje”." WHERE ayatNumber = 63 AND chapterNumber = 18;
UPDATE verses SET es = "Respondió: “¿No has visto que cuando nos refugiamos junto a la roca <em>para descansar,</em> y olvidé el pescado – y nadie sino Satanás me hizo olvidar mencionártelo – tomó rumbo hacia el mar de forma extraña?”." WHERE ayatNumber = 64 AND chapterNumber = 18;
UPDATE verses SET es = "Dijo: “Eso es lo que hemos estado buscando”. Y los dos volvieron sobre sus pasos." WHERE ayatNumber = 65 AND chapterNumber = 18;
UPDATE verses SET es = "Encontraron entonces a uno de Nuestros siervos al que habíamos concedido Nuestra misericordia, y a quien le habíamos enseñado Nuestro conocimiento." WHERE ayatNumber = 66 AND chapterNumber = 18;
UPDATE verses SET es = "Moisés le dijo: “¿Puedo seguirte a condición de que me enseñes la guía que te ha sido enseñada?”." WHERE ayatNumber = 67 AND chapterNumber = 18;
UPDATE verses SET es = "Respondió: “No puedes hacerme compañía con paciencia”;" WHERE ayatNumber = 68 AND chapterNumber = 18;
UPDATE verses SET es = "“¿Y cómo podrías ser paciente sobre cosas cuyo conocimiento no abarcas?”." WHERE ayatNumber = 69 AND chapterNumber = 18;
UPDATE verses SET es = "Dijo: “Me encontrarás paciente, si es la voluntad de Al-lah, y no te desobedeceré en nada”." WHERE ayatNumber = 70 AND chapterNumber = 18;
UPDATE verses SET es = "Dijo: “Bien, si me has de seguir, no me preguntes acerca de nada hasta que yo mismo te hable sobre ello”." WHERE ayatNumber = 71 AND chapterNumber = 18;
UPDATE verses SET es = "Así pues, ambos partieron hasta que, después de subir a una barca, él la taladró. <em>Moisés</em> dijo: “¿La has taladrado para ahogar a quienes están en ella? En verdad, has cometido una mala acción." WHERE ayatNumber = 72 AND chapterNumber = 18;
UPDATE verses SET es = "Él respondió: “¿No <em>te</em> dije que no podrías hacerme compañía con paciencia?”." WHERE ayatNumber = 73 AND chapterNumber = 18;
UPDATE verses SET es = "<em>Moisés</em> dijo: “No me reprendas por haberme olvidado y no seas demasiado duro por este<em> fallo</em> mío”." WHERE ayatNumber = 74 AND chapterNumber = 18;
UPDATE verses SET es = "Y ambos continuaron el viaje hasta que, habiendo encontrado a un joven, él lo mató. <em>Moisés</em> dijo: “¿Has matado a un inocente sin que <em>él hubiera matado</em> a nadie? ¡En verdad, has cometido una acción malvada!”." WHERE ayatNumber = 75 AND chapterNumber = 18;
UPDATE verses SET es = "Él respondió: “¿No te dije que no podrías hacerme compañía con paciencia?”." WHERE ayatNumber = 76 AND chapterNumber = 18;
UPDATE verses SET es = "<em> Moisés</em> dijo: “Si te pregunto sobre algo después de esto, no permitas que continúe en tu compañía, pues <em>entonces</em> habrás recibido suficientes excusas mías”." WHERE ayatNumber = 77 AND chapterNumber = 18;
UPDATE verses SET es = "<em> C</em>ontinuaron, pues, hasta que, habiendo llegado a las gentes de una ciudad, pidieron comida a sus moradores, pero se negaron a considerarles <em>sus</em> huéspedes. Mas encontraron en ella un muro que estaba a punto de derrumbarse y él lo reparó. <em>Moisés</em> dijo: “Si hubieses querido, habrías podido pedir una retribución por ello”." WHERE ayatNumber = 78 AND chapterNumber = 18;
UPDATE verses SET es = "Dijo: “Aquí se separan <em>los caminos</em> tuyo y mío. Te diré ahora el significado de lo que no has podido soportar con paciencia”;" WHERE ayatNumber = 79 AND chapterNumber = 18;
UPDATE verses SET es = "“En cuanto a la barca, pertenecía a unas pobres gentes que trabajaban en el mar; y quise dañarla porque detrás había un rey que incautaba por la fuerza todas las embarcaciones”;" WHERE ayatNumber = 80 AND chapterNumber = 18;
UPDATE verses SET es = "“En cuanto al joven, sus padres eran creyentes, y temíamos que les causara graves problemas por su rebelión y su incredulidad”;" WHERE ayatNumber = 81 AND chapterNumber = 18;
UPDATE verses SET es = "“Deseamos pues que su Señor les diera a cambio de él un<em>hijo</em> mejor que él en cuanto a pureza y con más afecto <em>filial;</em>" WHERE ayatNumber = 82 AND chapterNumber = 18;
UPDATE verses SET es = "“Y en cuanto al muro, pertenecía a dos huérfanos de la ciudad, y ebajo había un tesoro que les pertenecía, y su padre había sido un <em>hombre</em> justo, por lo que tu Señor deseó que alcanzaran su edad adulta y encontraran su tesoro, como favor de tu Señor; y no lo hice por mi propia voluntad. Ésta es la explicación de lo que no has podido soportar con paciencia”." WHERE ayatNumber = 83 AND chapterNumber = 18;
UPDATE verses SET es = "Te preguntan sobre Dhu´l Qarnain. Diles: “En verdad, os narraré <em>algo </em>de su historia”." WHERE ayatNumber = 84 AND chapterNumber = 18;
UPDATE verses SET es = "Lo establecimos en la tierra y le dimos los medios para <em>realizar</em> cualquier cosa." WHERE ayatNumber = 85 AND chapterNumber = 18;
UPDATE verses SET es = "Entonces él siguió <em>un determinado</em> camino." WHERE ayatNumber = 86 AND chapterNumber = 18;
UPDATE verses SET es = "Hasta que, cuando llegó al poniente del sol, lo encontró poniéndose en un charco de aguas oscuras, y cerca encontró una población. Dijimos: “Oh, Dhu´l Qarnain, castígalos o trátalos bondadosamente”." WHERE ayatNumber = 87 AND chapterNumber = 18;
UPDATE verses SET es = "Respondió: “En cuanto a quien haga el mal, ciertamente lo castigaré; después será devuelto a su Señor, Quien lo castigará con un castigo terrible”;" WHERE ayatNumber = 88 AND chapterNumber = 18;
UPDATE verses SET es = "“Pero en cuanto a quien crea y actúe justamente, le daremos una buena recompensa y le dirigiremos <em>las palabras</em> más suaves de Nuestros mandamientos”." WHERE ayatNumber = 89 AND chapterNumber = 18;
UPDATE verses SET es = "Entonces,<em> en verdad,</em> siguió <em>otro</em> camino." WHERE ayatNumber = 90 AND chapterNumber = 18;
UPDATE verses SET es = "Hasta que, cuando llegó al saliente del sol, encontró que salía sobre un pueblo al que no habíamos dado protección contra él." WHERE ayatNumber = 91 AND chapterNumber = 18;
UPDATE verses SET es = "Así <em>fue en verdad.</em> Ciertamente abarcamos con Nuestro conocimiento <em>todo</em> lo que poseía." WHERE ayatNumber = 92 AND chapterNumber = 18;
UPDATE verses SET es = "Luego siguió <em>otro</em> camino." WHERE ayatNumber = 93 AND chapterNumber = 18;
UPDATE verses SET es = "Hasta que, cuando llegó al lugar situado entre las dos montañas, encontró bajo ellas a un pueblo que apenas podía comprender una palabra." WHERE ayatNumber = 94 AND chapterNumber = 18;
UPDATE verses SET es = "Dijeron: “Oh, Dhu´l Qarnain, en verdad Gog y Magog están creando el desorden en la tierra; ¿podemos pagarte un tributo a condición de que levantes una barrera entre ellos y nosotros?”." WHERE ayatNumber = 95 AND chapterNumber = 18;
UPDATE verses SET es = "Respondió: “El poder con el que me ha dotado mi Señor a este respecto es mejor, pero podéis ayudarme con vuestra fuerza física; levantaré entre vosotros y ellos una muralla”." WHERE ayatNumber = 96 AND chapterNumber = 18;
UPDATE verses SET es = "“Traedme bloques de hierro”. <em>Así lo hicieron</em> hasta que, una vez que hubo nivelado el espacio entre las dos laderas de la montaña, dijo: “Soplad<em> ahora con vuestros fuelles”. Soplaron</em> hasta que, una vez que lo hubo puesto <em>al rojo vivo, </em>dijo: “Traedme cobre fundido para echarlo encima”." WHERE ayatNumber = 97 AND chapterNumber = 18;
UPDATE verses SET es = "De este modo ellos (Gog y Magog) no pudieron escalarla ni socavarla." WHERE ayatNumber = 98 AND chapterNumber = 18;
UPDATE verses SET es = "<em> Tras lo cual</em> dijo: “Ésta es una gracia de mi Señor. Pero cuando se cumpla la promesa de mi Señor, Él la romperá en trozos. Pues la promesa de mi Señor es <em>ciertamente</em> infalible”." WHERE ayatNumber = 99 AND chapterNumber = 18;
UPDATE verses SET es = "Y en aquel día dejaremos que algunos de ellos se levanten contra otros, y sonará la trompeta. Entonces los congregaremos a todos." WHERE ayatNumber = 100 AND chapterNumber = 18;
UPDATE verses SET es = "Y en ese día mostraremos el infierno, cara a cara, a los incrédulos." WHERE ayatNumber = 101 AND chapterNumber = 18;
UPDATE verses SET es = "Cuyos ojos se encontraban cubiertos por un velo <em>para que no escucharan</em> mi advertencia ni tampoco oyeran." WHERE ayatNumber = 102 AND chapterNumber = 18;
UPDATE verses SET es = "¿Creen los incrédulos que pueden tomar a Mis siervos como protectores en vez de a Mí? En verdad, hemos preparado el Infierno como morada para los incrédulos." WHERE ayatNumber = 103 AND chapterNumber = 18;
UPDATE verses SET es = "Diles: “¿Queréis que os informe de aquellos que son los mayores perdedores en cuanto a sus obras?”," WHERE ayatNumber = 104 AND chapterNumber = 18;
UPDATE verses SET es = "“Aquellos cuyo trabajo se pierde <em> todo en búsqueda de las cosas correspondientes a</em> la vida de este mundo, aunque piensan que están haciendo buenas obras”." WHERE ayatNumber = 105 AND chapterNumber = 18;
UPDATE verses SET es = "Son ellos quienes no creen en los Signos de su Señor ni en la reunión con Él. Sus obras son, pues, vanas y en el Día de la Resurrección no les daremos mérito alguno." WHERE ayatNumber = 106 AND chapterNumber = 18;
UPDATE verses SET es = "Ésa es su recompensa: el Infierno; por no haber creído y haberse burlado de Mis Signos y de Mis Mensajeros." WHERE ayatNumber = 107 AND chapterNumber = 18;
UPDATE verses SET es = "En verdad, quienes creen y hacen buenas obras tendrán como morada los Jardines del Paraíso." WHERE ayatNumber = 108 AND chapterNumber = 18;
UPDATE verses SET es = "En el habitarán; no desearán que su suerte cambie en modo alguno." WHERE ayatNumber = 109 AND chapterNumber = 18;
UPDATE verses SET es = "Diles: “Si el Océano se convirtiera en tinta para las palabras de mi Señor, en verdad que el Océano se agotaría antes de que se terminaran las palabras de mi Señor, aunque trajéramos otro tanto como ayuda <em>suplementaria”.</em>" WHERE ayatNumber = 110 AND chapterNumber = 18;
UPDATE verses SET es = "Diles: “No soy más que un hombre como vosotros; <em>pero</em> he recibido la revelación de que vuestro Dios es sólo un Único Dios. Así pues, quien espere reunirse con su Señor que haga buenas obras y no asocie a nadie en el culto de su Señor”." WHERE ayatNumber = 111 AND chapterNumber = 18;
UPDATE verses SET es = "En el nombre de Al-lah, el Clemente, el Misericordioso." WHERE ayatNumber = 1 AND chapterNumber = 19;
UPDATE verses SET es = "Kaf Ha Ya Ain Sad." WHERE ayatNumber = 2 AND chapterNumber = 19;
UPDATE verses SET es = "<em> He aquí</em> el relato de la misericordia de tu Señor mostrada con Su Siervo, Zacarías." WHERE ayatNumber = 3 AND chapterNumber = 19;
UPDATE verses SET es = "Cuando invocó a su Señor con una invocación secreta." WHERE ayatNumber = 4 AND chapterNumber = 19;
UPDATE verses SET es = "Diciendo: “Señor mío, los huesos se me han debilitado y la cabeza me blanquea de canicie, pero nunca, mi Señor, he dejado de ser bendecido en mi súplica a Ti”;" WHERE ayatNumber = 5 AND chapterNumber = 19;
UPDATE verses SET es = "“Desconfío de mis parientes que me siguen, y mi mujer es estéril. Concédeme pues, un sucesor de Ti mismo”," WHERE ayatNumber = 6 AND chapterNumber = 19;
UPDATE verses SET es = "“Que sea heredero mío y de la Casa de Jacob. Y hazlo, Señor mío, agradable a Ti”." WHERE ayatNumber = 7 AND chapterNumber = 19;
UPDATE verses SET es = "“Oh, Zacarías, te damos la buena nueva de un hijo cuyo nombre <em>será</em> Yahya. No hemos dado ese nombre a nadie antes de él”." WHERE ayatNumber = 8 AND chapterNumber = 19;
UPDATE verses SET es = "Dijo: “Señor mío, ¿cómo podré tener un hijo si mi mujer es estéril y yo he alcanzado el <em>límite</em> extremo de la vejez?”." WHERE ayatNumber = 9 AND chapterNumber = 19;
UPDATE verses SET es = "Él dijo: “Así es”. Pero tu Señor dice: “Es fácil para Mí, pues, en efecto, te creé a ti antes, cuando no eras nada”." WHERE ayatNumber = 10 AND chapterNumber = 19;
UPDATE verses SET es = "Dijo: “Señor mío, dame una señal”. <em>Dios</em> dijo: “Tu señal es que no hablarás a nadie durante tres <em>días y</em> noches completos”." WHERE ayatNumber = 11 AND chapterNumber = 19;
UPDATE verses SET es = "Tras esto, se presentó a sus gentes saliendo del santuario y les indicó por señas que glorificaran a Dios día y noche." WHERE ayatNumber = 12 AND chapterNumber = 19;
UPDATE verses SET es = "“Oh Yahya, sujeta con fuerza el Libro”. Y le dimos la sabiduría cuando aún era un niño," WHERE ayatNumber = 13 AND chapterNumber = 19;
UPDATE verses SET es = "la ternura <em>de corazón,</em> de Nosotros mismos, y la pureza. Pues era piadoso." WHERE ayatNumber = 14 AND chapterNumber = 19;
UPDATE verses SET es = "Y respetuoso con sus padres. Y no era arrogante <em>ni </em> rebelde." WHERE ayatNumber = 15 AND chapterNumber = 19;
UPDATE verses SET es = "La paz fue con él el día en que nació y el día en que murió, y la <em>paz será con él</em> el día en que <em>de nuevo</em> sea resucitado." WHERE ayatNumber = 16 AND chapterNumber = 19;
UPDATE verses SET es = "Y relata <em>la historia de</em> María <em>tal como se menciona</em> en el Libro. Cuando se retiró de sus gentes a un lugar situado al Este;" WHERE ayatNumber = 17 AND chapterNumber = 19;
UPDATE verses SET es = "Y se ocultó de ellos con un velo, entonces le enviamos a Nuestro ángel y se le apareció en forma de hombre perfecto." WHERE ayatNumber = 18 AND chapterNumber = 19;
UPDATE verses SET es = "Le dijo: “Busco refugio contra ti en el <em>Dios</em> Misericordioso si es que en verdad <em>Le</em> temes”." WHERE ayatNumber = 19 AND chapterNumber = 19;
UPDATE verses SET es = "Él respondió: “No soy más que un Mensajero de tu Señor, para concederte un hijo justo”." WHERE ayatNumber = 20 AND chapterNumber = 19;
UPDATE verses SET es = "Ella dijo: “¿Cómo podría tener un hijo si ningún hombre me ha tocado, ni he perdido nunca la castidad?”." WHERE ayatNumber = 21 AND chapterNumber = 19;
UPDATE verses SET es = "Él respondió: “Así es”. Pero dice tu Señor: “Es fácil para Mí; y <em>lo haremos</em> <em>así</em> para que lo convirtamos en un Signo para los hombres, y en una misericordia Nuestra, pues es algo decidido”." WHERE ayatNumber = 22 AND chapterNumber = 19;
UPDATE verses SET es = "Así pues, ella lo concibió, y se retiró con él a un lugar apartado." WHERE ayatNumber = 23 AND chapterNumber = 19;
UPDATE verses SET es = "Los dolores del parto la llevaron hasta el tronco de una palmera. Ella dijo: “¡Oh!”, ojalá hubiera muerto antes de esto y me hubiera convertido en una cosa totalmente olvidada!”." WHERE ayatNumber = 24 AND chapterNumber = 19;
UPDATE verses SET es = "Entonces él (el ángel) la llamó de debajo de ella, <em>diciéndole:</em> “No te aflijas. Tu Señor ha puesto un riachuelo a tus pies”;" WHERE ayatNumber = 25 AND chapterNumber = 19;
UPDATE verses SET es = "“Agita hacia ti el tronco de la palmera; eso hará que caigan sobre ti dátiles frescos y maduros”;" WHERE ayatNumber = 26 AND chapterNumber = 19;
UPDATE verses SET es = "“Come pues y bebe, y refréscate los ojos. Y si ves a algún humano, dile: “He hecho voto de ayunar al<em> Dios</em> Misericordioso; no hablaré por tanto hoy a ningún ser humano”;" WHERE ayatNumber = 27 AND chapterNumber = 19;
UPDATE verses SET es = "Entonces le llevó a sus gentes, portándole <em>en brazos</em>. Ellos dijeron: “¡Oh María, nos has traído algo extraordinario!”." WHERE ayatNumber = 28 AND chapterNumber = 19;
UPDATE verses SET es = "“¡Oh, hermana de Aarón, tu padre no fue un hombre malo ni tu madre una mujer impura!”." WHERE ayatNumber = 29 AND chapterNumber = 19;
UPDATE verses SET es = "Entonces ella le señaló a él. Dijeron: “¿Cómo hablaremos a quien no es más que un niño en su cuna?”." WHERE ayatNumber = 30 AND chapterNumber = 19;
UPDATE verses SET es = "Él dijo: “Soy siervo de Al-lah. Él me ha dado el Libro y me ha hecho Profeta”;" WHERE ayatNumber = 31 AND chapterNumber = 19;
UPDATE verses SET es = "“Él me ha hecho bendito dondequiera que me encuentre, y me ha prescrito la Oración y la limosna mientras viva”;" WHERE ayatNumber = 32 AND chapterNumber = 19;
UPDATE verses SET es = "“<em>Me ha hecho</em> piadoso para con mi madre, y no me ha hecho soberbio<em> ni</em> desafortunado”;" WHERE ayatNumber = 33 AND chapterNumber = 19;
UPDATE verses SET es = "“Y la paz fue conmigo el día en que nací, y la<em>paz seguirá conmigo</em> el día en que muera, y el día en que sea <em>de nuevo</em> devuelto a la vida”." WHERE ayatNumber = 34 AND chapterNumber = 19;
UPDATE verses SET es = "Así era Jesús, el hijo de María. <em>Ésta es</em> una declaración de la verdad de la cual dudan." WHERE ayatNumber = 35 AND chapterNumber = 19;
UPDATE verses SET es = "No corresponde a <em>la Majestad de</em> Al-lah que tome para Él un hijo. Santo es Él. Cuando decreta algo, le dice: “¡Sé!”, y es." WHERE ayatNumber = 36 AND chapterNumber = 19;
UPDATE verses SET es = "<em> Dijo Jesús:</em> “En verdad, Al-lah es mi Señor y vuestro Señor. Adoradlo pues a Él <em>sólo; </em>éste es el camino recto”." WHERE ayatNumber = 37 AND chapterNumber = 19;
UPDATE verses SET es = "Pero los grupos discutieron entre sí; ¡Ay de quienes no creen a causa de la reunión del gran día!" WHERE ayatNumber = 38 AND chapterNumber = 19;
UPDATE verses SET es = "¡Qué maravillosa será su audición y su visión el día en que comparezcan ante Nosotros! Pero hoy los inicuos se encuentran en error manifiesto." WHERE ayatNumber = 39 AND chapterNumber = 19;
UPDATE verses SET es = "Adviérteles del día del lamento, cuando se decida la cuestión. Aunque ahora están en <em>estado de </em>indiferencia, por lo que no creen." WHERE ayatNumber = 40 AND chapterNumber = 19;
UPDATE verses SET es = "Somos Nosotros quienes heredaremos la tierra y <em>a todos</em> quienes estén en ella; y a Nosotros serán <em>todos</em> ellos devueltos." WHERE ayatNumber = 41 AND chapterNumber = 19;
UPDATE verses SET es = "Nárrales <em>la historia de </em>Abraham <em>como se menciona </em> en el Libro. Fue<em> un hombre </em>veraz <em>y </em>un Profeta." WHERE ayatNumber = 42 AND chapterNumber = 19;
UPDATE verses SET es = "Cuando dijo a su padre: “Oh padre mío, ¿por qué adoras a lo que ni oye, ni ve, ni puede servirte de nada?”." WHERE ayatNumber = 43 AND chapterNumber = 19;
UPDATE verses SET es = "“Oh, padre mío, en verdad ha llegado a mí un conocimiento que tú no has recibido; sígueme y te guiaré a un camino recto”;" WHERE ayatNumber = 44 AND chapterNumber = 19;
UPDATE verses SET es = "“Oh, padre mío, no sirvas a Satanás; en verdad Satanás es rebelde contra el <em>Dios</em> Clemente”." WHERE ayatNumber = 45 AND chapterNumber = 19;
UPDATE verses SET es = "“Oh, padre mío, en verdad temo que caiga sobre ti un castigo del <em>Dios</em> Clemente, y que te conviertas en colega de Satanás”." WHERE ayatNumber = 46 AND chapterNumber = 19;
UPDATE verses SET es = "Respondió: “¿Te alejas de mis dioses, Oh, Abraham? Si no desistes, en verdad cortaré todas las relaciones contigo. Ahora déjame solo durante mucho tiempo”." WHERE ayatNumber = 47 AND chapterNumber = 19;
UPDATE verses SET es = "<em> Abraham</em> dijo: “La paz sea contigo. Pediré para ti el perdón de mi Señor. En verdad Él es Clemente conmigo”." WHERE ayatNumber = 48 AND chapterNumber = 19;
UPDATE verses SET es = "<em> “</em>Y me mantendré alejado de ti y de aquello a lo que tú invocas en vez de Al-lah; y oraré a mi Señor; es posible que, orando a mi Señor, no me sienta frustrado”." WHERE ayatNumber = 49 AND chapterNumber = 19;
