UPDATE verses SET es = "<em>Cuidado con </em>el día en que el inicuo se roerá las manos <em>en completo desamparo</em> y dirá: \"¡Ojalá hubiera seguido el mismo ca­mino junto al Mensajero!\"." WHERE ayatNumber = 28 AND chapterNumber = 25;
UPDATE verses SET es = "\"¡Ah, triste de mí! ¡Ojalá no hubiese tomado a uno así por amigo!" WHERE ayatNumber = 29 AND chapterNumber = 25;
UPDATE verses SET es = "\"Él me desvió del Recorda­torio después de que me llegara.\" Pues Satanás es el mayor traidor para el hombre." WHERE ayatNumber = 30 AND chapterNumber = 25;
UPDATE verses SET es = "Y el Mensajero dirá: \"Oh, mi Señor, mi pueblo trató en verdad este Corán como <em>algo </em>desechado\"." WHERE ayatNumber = 31 AND chapterNumber = 25;
UPDATE verses SET es = "Así destinamos a cada Pro­feta un enemigo de entre los peca­dores; pues suficiente es tu Señor como Guía y Socorredor." WHERE ayatNumber = 32 AND chapterNumber = 25;
