UPDATE verses SET es = "No te ponen ningún ejemplo, mas Nosotros te proporcionamos la verdad y una excelente explica­ción." WHERE ayatNumber = 34 AND chapterNumber = 25;
UPDATE verses SET es = "Quienes serán reunidos de bruces y conducidos al Infierno, serán los que se encuentren en peor situación y los más extraviados del camino <em>recto.</em>" WHERE ayatNumber = 35 AND chapterNumber = 25;
