UPDATE verses SET es = "Y éstos deben haber visitado la ciudad sobre la que cayó una lluvia efasta. ¿Acaso no la han visto? No, no esperan ser resu­citados <em>después de la muerte.</em>" WHERE ayatNumber = 41 AND chapterNumber = 25;
UPDATE verses SET es = "Y cuando te ven, sólo<strong> </strong>te ha­cen objeto de burla: \"¿Es éste a quien Al‑lah ha enviado <em>como </em>Mensajero?" WHERE ayatNumber = 42 AND chapterNumber = 25;
UPDATE verses SET es = "\"En verdad nos habría apar­tado de nuestros dioses, si no hu­biésemos sido firmemente fieles a ellos”. Y sabrán, cuando vean el castigo, quién está más desviado del camino <em>recto.</em>" WHERE ayatNumber = 43 AND chapterNumber = 25;
